INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'7765FA91F34E46498B098907810E0E3C', X'A47D07E8919871499666DDE04A736EDE', X'A47D07E8919871499666DDE04A736EDE', 0, 'https://grillgirl.com/2019/07/cast-iron-grilled-mussels-in-a-white-wine-bacon-herb-butter-sauce/', 'Cast Iron Grilled Mussels in a White Wine, Bacon Herb Butter Sauce', 2, NULL, 0, 'Cast Iron Grilled Mussels in a White Wine, Bacon Herb Butter Sauce', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/07/DSC_016172-150x150.jpg', '', '', '5 minutes', '15 minutes', '20 minutes', '3', 'Ingredients?#&?#&1 dozen mussels?#&2/3 cup Beringer Main and Vine Pinot Grigio?#&3 tbsp butter?#&1 strip bacon, partially cooked?#&1 small bunch of fresh thyme sprigs?#&4 cloves garlic, minced?#&Salt, to taste?#&1 tsp pepper', 'American, French', 'https://grillgirl.com/2019/07/cast-iron-grilled-mussels-in-a-white-wine-bacon-herb-butter-sauce/', 'grill, cast iron', 'how to grill mussels, cast iron grilled mussels', 'Jul 26, 2019', 'american, grilled', 'Instructions?#&?#&Precook a slice of bacon so it is ¾ of the way cooked- this can easily be done in the microwave. Next, add the wine, butter, garlic, bacon (remove fatty parts of the bacon and chop before adding), thyme, salt and pepper.?#&?#&Add the mussels to the cast iron skillet and grill on direct heat over Kingsford Charcoal until the mussels open up- about 15 minutes or less. Serve with grilled or toasted crusty bread like ciabatta or French bread to sop up the white wine sauce.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'AA1F8260A13C7E40AE6B780271939CA6', X'42DBAB8B01084046A95D738192B14F21', X'42DBAB8B01084046A95D738192B14F21', 0, 'https://grillgirl.com/2019/06/sheet-pan-dinner-harissa-orange-salmon-broccolini/', 'Sheet Pan Dinner: Harissa Orange Salmon and Broccolini', 2, NULL, 0, 'Sheet Pan Dinner: Harissa Orange Salmon and Veggies', 'Amber Hanson', 'https://grillgirl.com/wp-content/uploads/2019/06/Harissa-Salmon-on-Grill-150x150.jpeg', '', '', '10 minutes', '30 minutes', '40 minutes', '4', 'Ingredients?#&?#&1 lb. sweet potatoes, cut into ¼-inch thick slices?#&3 Tbsp. avocado oil, divided?#&12 oz. broccolini, trimmed?#&2 Tbsp. harissa sauce (spicy or mild)?#&Zest of 1 orange?#&4 (5-oz.) wild caught salmon filets?#&Salt & Pepper', '', 'https://grillgirl.com/2019/06/sheet-pan-dinner-harissa-orange-salmon-broccolini/', '', 'pellet smoker, harissa, salmon, broccolini, sweet potatoes, sheet pan, smoked salmon, orange salmon, orange', 'Jun 17, 2019', '', 'Instructions?#&?#&Preheat your pellet smoker to 425° F.?#&Toss sweet potatoes with 1 ½ tablespoons avocado oil and season with salt and pepper. Spread evenly on a rimmed baking sheet.?#&Place sheet pan on the grill; cook for 10 minutes.?#&Meanwhile, toss broccolini with remaining oil and season with salt and pepper.?#&Move sweet potato slices to one side of the sheet pan and place broccolini on the other. Cook for 10 minutes.?#&Stir together harissa and orange zest. Season salmon with salt and pepper.?#&Carefully move the sweet potatoes and broccolini to make room for salmon filets. Spread harissa sauce evenly over each piece. Close lid and continue cooking for 10-14 minutes or until salmon flakes easily and internal temperature reaches 145°F.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'A85B803D2A2A3D49A30B2842E0DFED98', X'7C9A5FF429F65C4D86CC33C311FA65F5', X'7C9A5FF429F65C4D86CC33C311FA65F5', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'6E9EB98A65570544B7B01E762FE6A379', X'B1A2A036EA324E4CBE3AE6E39B4F605B', X'B1A2A036EA324E4CBE3AE6E39B4F605B', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'4C4642F5614FEA4FAB73163C374E6E1F', X'913158D78E9E344991D808F0681EA368', X'913158D78E9E344991D808F0681EA368', 0, 'https://grillgirl.com/2015/07/grilled-pulpo-octopus-tacos/', 'Grilled Octopus Tacos', 2, NULL, 0, 'Grilled Pulpo {Octopus} Tacos', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/07/GrilledPulpoTacos_MichelleLara_CupcakesCocktailsAndKids_Horizontal-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Octopus?#&1 orange?#&1 red onion?#&3–4 bay leaves?#&Salt + Pepper {well seasoned, to taste}?#&1/2 cup of olive oil?#&1/4 cup of cilantro {for garnish}?#&pico de gallo?#&flour tortillas', '', 'https://grillgirl.com/2015/07/grilled-pulpo-octopus-tacos/', '', '', 'Jul 16, 2015', '', 'Instructions?#&?#&In a large deep pot bring water to a boil with  all the ingredients {except the cilantro and pico de gallo}, once the water is boiling add the pulpo. Keep the flame on medium and let it boil for 30 minutes.?#&?#&While that is boiling prep your grill and wooden skewers by submerging them in water to prevent them burning wile on the grill. Once the pulpo is cooled down cut it up into about 1 – 2 inch sizes. Keep the size consistent so they grill evenly.  Skewer the pulpo and brush with a little bit of olive oil. Grill them on direct heat for just a minute or two per side. If you’re using a charcoal grill the flavor will be even more intense!?#&?#&Grill the flour tortillas and serve with some fresh pico de gallo and a few lime wedges!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2E5D8F20E7178A44804E09F71833D327', X'5142CF87EE322647A68400FD311A5ED4', X'5142CF87EE322647A68400FD311A5ED4', 0, 'https://grillgirl.com/2015/06/herb-marinated-grilled-swordfish-steaks/', 'Herb Marinated Grilled Swordfish Steaks', 2, NULL, 0, 'Herb Marinated Grilled Swordfish Steaks', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/06/Grilled-Sword-Fish_Featured_MichelleLara_GrillGirl-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&for marinade:?#&?#&1/2 cup of extra virgin olive oil?#&the juice of 2 limes {or lemons} – reserve the zest of 1 lime and a few slices for the garnish?#&3 teaspoons of fresh oregano?#&3 teaspoons of fresh thyme?#&sea salt + fresh cracked pepper to taste?#&?#&fish:?#&?#&2 6 – 8 oz sword fish steaks', '', 'https://grillgirl.com/2015/06/herb-marinated-grilled-swordfish-steaks/', '', '', 'Jun 3, 2015', '', 'Instructions?#&?#&Combine all the marinade ingredients in a bowl.  After cleaning and drying your fish allow it to marinade for 2 – 3 hours {1 hour minimum} in the refrigerator.?#&?#&Prepare your grill on high and brush the grates with some olive oil. Place your swordfish steaks on direct heat and cook for about 7 minutes per side. Steaks are done once they’ve reached an internal temperature of 145 degrees (I use an internal read thermometer like the thermapen to test internal temp).Once they’re cooked let them rest before serving them.');
