INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2AE626ED88432A4EACA0E1E49338CBA4', X'A168E4F941241042A41F9D02104F8C54', X'A168E4F941241042A41F9D02104F8C54', 0, 'https://grillgirl.com/2019/07/cast-iron-grilled-mussels-in-a-white-wine-bacon-herb-butter-sauce/', 'Cast Iron Grilled Mussels in a White Wine, Bacon Herb Butter Sauce', 2, NULL, 0, 'Cast Iron Grilled Mussels in a White Wine, Bacon Herb Butter Sauce', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/07/DSC_016172-150x150.jpg', '', '', '5 minutes', '15 minutes', '20 minutes', '3', 'Ingredients?#&?#&1 dozen mussels?#&2/3 cup Beringer Main and Vine Pinot Grigio?#&3 tbsp butter?#&1 strip bacon, partially cooked?#&1 small bunch of fresh thyme sprigs?#&4 cloves garlic, minced?#&Salt, to taste?#&1 tsp pepper', 'American, French', 'https://grillgirl.com/2019/07/cast-iron-grilled-mussels-in-a-white-wine-bacon-herb-butter-sauce/', 'grill, cast iron', 'how to grill mussels, cast iron grilled mussels', 'Jul 26, 2019', 'american, grilled', 'Instructions?#&?#&Precook a slice of bacon so it is ¾ of the way cooked- this can easily be done in the microwave. Next, add the wine, butter, garlic, bacon (remove fatty parts of the bacon and chop before adding), thyme, salt and pepper.?#&?#&Add the mussels to the cast iron skillet and grill on direct heat over Kingsford Charcoal until the mussels open up- about 15 minutes or less. Serve with grilled or toasted crusty bread like ciabatta or French bread to sop up the white wine sauce.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'66776C442F443741AD561EF089B373F4', X'664033A007ED9343A48BCC3E8A31F544', X'664033A007ED9343A48BCC3E8A31F544', 0, 'https://grillgirl.com/2019/06/sheet-pan-dinner-harissa-orange-salmon-broccolini/', 'Sheet Pan Dinner: Harissa Orange Salmon and Broccolini', 2, NULL, 0, 'Sheet Pan Dinner: Harissa Orange Salmon and Veggies', 'Amber Hanson', 'https://grillgirl.com/wp-content/uploads/2019/06/Harissa-Salmon-on-Grill-150x150.jpeg', '', '', '10 minutes', '30 minutes', '40 minutes', '4', 'Ingredients?#&?#&1 lb. sweet potatoes, cut into ¼-inch thick slices?#&3 Tbsp. avocado oil, divided?#&12 oz. broccolini, trimmed?#&2 Tbsp. harissa sauce (spicy or mild)?#&Zest of 1 orange?#&4 (5-oz.) wild caught salmon filets?#&Salt & Pepper', '', 'https://grillgirl.com/2019/06/sheet-pan-dinner-harissa-orange-salmon-broccolini/', '', 'pellet smoker, harissa, salmon, broccolini, sweet potatoes, sheet pan, smoked salmon, orange salmon, orange', 'Jun 17, 2019', '', 'Instructions?#&?#&Preheat your pellet smoker to 425° F.?#&Toss sweet potatoes with 1 ½ tablespoons avocado oil and season with salt and pepper. Spread evenly on a rimmed baking sheet.?#&Place sheet pan on the grill; cook for 10 minutes.?#&Meanwhile, toss broccolini with remaining oil and season with salt and pepper.?#&Move sweet potato slices to one side of the sheet pan and place broccolini on the other. Cook for 10 minutes.?#&Stir together harissa and orange zest. Season salmon with salt and pepper.?#&Carefully move the sweet potatoes and broccolini to make room for salmon filets. Spread harissa sauce evenly over each piece. Close lid and continue cooking for 10-14 minutes or until salmon flakes easily and internal temperature reaches 145°F.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'674DF2B8EE05214C9DE9A1E5FA66E9F0', X'CD95E04443B91440909F20D0650867D5', X'CD95E04443B91440909F20D0650867D5', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'BE56006EFF369B48AC68E05A5569223E', X'8AB6F5985C6F424DA02C04B354E652D7', X'8AB6F5985C6F424DA02C04B354E652D7', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'0365F759B6A4AE43BE3052ACC6AA30C8', X'9523B8D6FC891441A8F20515D9AAC7C2', X'9523B8D6FC891441A8F20515D9AAC7C2', 0, 'https://grillgirl.com/2015/07/grilled-pulpo-octopus-tacos/', 'Grilled Octopus Tacos', 2, NULL, 0, 'Grilled Pulpo {Octopus} Tacos', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/07/GrilledPulpoTacos_MichelleLara_CupcakesCocktailsAndKids_Horizontal-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Octopus?#&1 orange?#&1 red onion?#&3–4 bay leaves?#&Salt + Pepper {well seasoned, to taste}?#&1/2 cup of olive oil?#&1/4 cup of cilantro {for garnish}?#&pico de gallo?#&flour tortillas', '', 'https://grillgirl.com/2015/07/grilled-pulpo-octopus-tacos/', '', '', 'Jul 16, 2015', '', 'Instructions?#&?#&In a large deep pot bring water to a boil with  all the ingredients {except the cilantro and pico de gallo}, once the water is boiling add the pulpo. Keep the flame on medium and let it boil for 30 minutes.?#&?#&While that is boiling prep your grill and wooden skewers by submerging them in water to prevent them burning wile on the grill. Once the pulpo is cooled down cut it up into about 1 – 2 inch sizes. Keep the size consistent so they grill evenly.  Skewer the pulpo and brush with a little bit of olive oil. Grill them on direct heat for just a minute or two per side. If you’re using a charcoal grill the flavor will be even more intense!?#&?#&Grill the flour tortillas and serve with some fresh pico de gallo and a few lime wedges!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'FEBA04675BFCB74C9942020B4F9D672F', X'BAA52ADFCC7B704791443AB7D3D45938', X'BAA52ADFCC7B704791443AB7D3D45938', 0, 'https://grillgirl.com/2015/06/herb-marinated-grilled-swordfish-steaks/', 'Herb Marinated Grilled Swordfish Steaks', 2, NULL, 0, 'Herb Marinated Grilled Swordfish Steaks', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/06/Grilled-Sword-Fish_Featured_MichelleLara_GrillGirl-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&for marinade:?#&?#&1/2 cup of extra virgin olive oil?#&the juice of 2 limes {or lemons} – reserve the zest of 1 lime and a few slices for the garnish?#&3 teaspoons of fresh oregano?#&3 teaspoons of fresh thyme?#&sea salt + fresh cracked pepper to taste?#&?#&fish:?#&?#&2 6 – 8 oz sword fish steaks', '', 'https://grillgirl.com/2015/06/herb-marinated-grilled-swordfish-steaks/', '', '', 'Jun 3, 2015', '', 'Instructions?#&?#&Combine all the marinade ingredients in a bowl.  After cleaning and drying your fish allow it to marinade for 2 – 3 hours {1 hour minimum} in the refrigerator.?#&?#&Prepare your grill on high and brush the grates with some olive oil. Place your swordfish steaks on direct heat and cook for about 7 minutes per side. Steaks are done once they’ve reached an internal temperature of 145 degrees (I use an internal read thermometer like the thermapen to test internal temp).Once they’re cooked let them rest before serving them.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'DAD52DB3A5402146A78D704239E48E27', X'8B64332CF84A8E418745545EE6F2E9AB', X'8B64332CF84A8E418745545EE6F2E9AB', 0, 'https://grillgirl.com/2015/04/grilled-soy-ginger-wild-caught-ocean-prawns-with-asian-slaw-and-coconut-rice/', 'Grilled Soy Ginger Wild Caught Ocean Prawns with Asian Slaw and Coconut Rice', 2, NULL, 0, 'Grilled Soy Ginger Wild Caught Ocean Prawns with Asian Slaw and Coconut RiceINSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'DB6992E60254464FA997CD7343C1DF58', X'8EBB3AC34F9E6A4282DA1E7331C7111F', X'8EBB3AC34F9E6A4282DA1E7331C7111F', 0, 'https://grillgirl.com/2015/03/grilled-whole-red-snapper/', 'Grilled Whole Red Snapper', 2, NULL, 0, 'Grilled Whole Red Snapper', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/03/GrilledLimeCilantroRedSnapper_MichelleLara_CupcakesCocktailsAndKids_1_cck-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Medium sized whole red snapper {ours was about 2.5 lbs which served 4}?#&3 tbs of good quality olive or canola oil?#&salt + pepper to taste?#&juice of 3 limes?#&2–3 sliced limes?#&3 crushed garlic cloves?#&1/2 cup of chopped cilantro?#&parchment paper', '', 'https://grillgirl.com/2015/03/grilled-whole-red-snapper/', '', '', 'Mar 9, 2015', '', 'Instructions?#&?#&Preheat your grill. Rinse and dry the fish well {I recommend having your fish monger clean and scale it for you}. Season both sides of the snapper with the salt, pepper and oil, don’t forget to season the inside as well. Stuff the cavity with the cilantro, crushed garlic and lime juice as well as a few slices of limes {keep the rest to garnish}. All remaining lime juice and minced garlic pour over the top of the fish.  Place the fish on top of parchment paper that’s been slightly brushed with oil. Grill in indirect heat for about 15 – 20 minutes, turn the fish mid way into cooking. The last 3-5 minutes of cooking time place the fish on direct heat  – to crisp up the skin. Once the tempature reaches 135 F remove it and let it rest for a few minutes before serving it.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'1F6544DBC3B07340A8AB7EA4EBF6F28B', X'41C7A37286267D48909B41073741B4CB', X'41C7A37286267D48909B41073741B4CB', 0, 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', 'Rum Pineapple Pork Ribs are Sweet and Tangy', 2, NULL, 0, 'Rum Pineapple Pork Ribs are Sweet and Tangy', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/08/rum-pineapple-ribs-150x150.jpeg', '', '', '20 minutes', '5 hours', '5 hours 20 minutes', '', 'Ingredients?#&?#&1 slab baby back ribs?#&Mustard?#&3 tbsp smoked all purpose rub ?#&1 pineapple, cored and sliced into 1/2” rings – should make about 9 rings total – 3 of which are used in the sauce, save juice for sauce (if using canned look for a corn syrup free, reduced sugar version)?#&?#&Texas Crutch?#&?#&1/4 cup apple juice?#&2 tbsp pineapple preserves?#&?#&Pineapple Rum BBQ Sauce?#&?#&1/3 cup BBQ sauce or your favorite store bought variety?#&3 tbsp pineapple preserves?#&1/4 cup Myers rum?#&3 tbsp pineapple juice?#&Dash allspice?#&3 slice pineapple, chopped', '', 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', '2-2-1 Method', 'pork ribs, baby back ribs, 2-2-1 ribs, smoked BBQ ribs, texas crutch, pork ribs recipe', 'Aug 29, 2019', 'Pork Ribs', 'Instructions?#&?#&Smoke Temp: 225F?#&?#&Smoking Woods: FRUIT WOODS- (apple, pecan, pear) OR mix with OAK, PECAN, MAPLE?#&?#&The ribs are going to use the 2-2-1 method: 2 hours in the smoker, 2 hours in foil, 1 hour letting the sauce glaze.?#&?#&Start by removing the membrane from the ribs. This is easier with a paper towel for gripping and a butter knife for separating from the meat.?#&Prepare your smoker for 225 degrees- this recipe can be made on any kind of smoker (and was originally created for my Healthy Electric Smoker Cookbook).?#&Next, slather with mustard so the rub can adhere. Apply the rub and smoke the meat for 2 hours.?#&After 2 hours, remove from the smoker and do the “Texas Crutch,” i.e., “wrap in foil”. Mix the apple juice and pineapple preserve and add to the foil packet. Let smoke another two hours.?#&Meanwhile, as the meat is smoking make your rum barbecue sauce. Combine the BBQ sauce, pineapple preserves, rum, chopped pineapple, pineapple juice and allspice. Let reduce down for 20 minutes on medium simmer.?#&During the last hour, remove the ribs from foil and glaze with the pineapple rum BBQ sauce.?#&Layer slices of pineapple on top of the ribs and drizzle more sauce over the pineapple.?#&Let smoke for an additional hour to let the sauce set. Serve immediately.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'4E709AE66BCCE94DBB7BFB26EAAEABB0', X'D7769306FF360F41A31E643E94F626FE', X'D7769306FF360F41A31E643E94F626FE', 0, 'https://grillgirl.com/2019/06/gorgonzola-thyme-stuffed-pork-chops-with-bourbon-grilled-apples/', 'Gorgonzola Thyme Stuffed Pork Chops with Bourbon Grilled Apples', 2, NULL, 0, 'Gorgonzola Thyme Stuffed Pork Chops with Bourbon Grilled Apples', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/06/Stuffed-Pork-Chops-with-bourbone-grilled-apples-72-dpi-150x150.jpg', '', '', '20', '8', '28', '4 stuffed pork chops', 'Ingredients?#&?#&4 Smithfield Brown Sugar Boneless Center Cut Pork Chops – these are available at select grocery stores and retailers nationwide, including: Walmart, ShopRite, Food Lion, Publix, Albertson’s/Safeway and Save a Lot.?#&2 tbsp gorgonzola?#&2 tbsp chopped walnuts?#&2 tbsp chopped dried cherries?#&2 tbsp fresh thyme?#&toothpicks?#&?#&Grilled Bourbon Apples:?#&?#&4 apples, sliced with seeds removed?#&dash cinnamon?#&1 tbsp maple syrup?#&2 tbsp bourbon', 'American', 'https://grillgirl.com/2019/06/gorgonzola-thyme-stuffed-pork-chops-with-bourbon-grilled-apples/', 'Grill', 'Grilled Pork Chops, Grilled Center Cut Pork Chops, Stuffed Pork Chops with Grilled Apples, Gorgonzola Thyme Stuffed Pork Chops, Bourbon Grilled Apples, How to Grill Pork Chops', 'Jun 27, 2019', 'Pork, Grilled Pork Chops', 'Instructions?#&?#&Prepare your grill direct for heat grilling at 350-400 degrees. While the grill is heating up, go ahead and mix your gorgonzola, walnuts, cherries and thyme.?#&?#&Take a very sharp knife and cut a slit into the pork chops where the stuffing will go, using your finger to expand the opening. Stuff the pork chops with the cheese mixture and use a tooth pick to secure the opening so the stuffing doesn’t come out while grilling.?#&?#&Next, cut your apples. Combine the maple syrup and bourbon together in a cup. Sprinkle the apples with the cinnamon and then drizzle the maple bourbon syrup on top of the apples.?#&?#&Oil your grates with a high heat oil like avocado oil or vegetable oil so your food doesn’t stick to the grill grates. Grill the pork chops on direct heat, flipping halfway through, until the internal temp reaches 140 degrees with an internal read thermometer, this should take about 5-8 minutes depending on your grill. While grilling the pork chops, add the apple slices and let them grill until they develop char marks and have begun to caramelize on the grill.?#&?#&Once the pork chops have reached an internal temp of 140 degrees, pull them off the grill (they will continue to cook and reach 145 degrees after removed from the grill). Let them rest under foil for 5 minutes.?#&?#&Serve the pork chops with the grilled apples for sweet-salty pork pairing perfection!?#&?#&What is your favorite way to enjoy Pork Chops? Check out my recent Cuban inspired Mojo Pork, coming straight to you from my Miami Kitchen!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'29E973130CFA50439BD22DB6228C9577', X'245AFA714EEF734D8DD428D04E30ED4F', X'245AFA714EEF734D8DD428D04E30ED4F', 0, 'https://grillgirl.com/2019/03/colombian-style-hot-dogs/', 'Colombian Style Hot Dogs', 2, NULL, 0, 'Colombian Style Hot Dogs', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/03/Colombian-Style-Hot-Dogs-RL-150x150.jpg', '', '', '5 minutes', '10 minutes', '15 minutes', '8 hot dogs- 2 per person', 'Ingredients?#&?#&8 hot dogs- I use use Nitrate Free Hot dogs?#&8 pack hot dog buns?#&1/4 cup Mayo?#&1/4 cup Mustard?#&¼ cup Ketchup?#&¼ cup Pineapple Preserves?#&2 cups Crushed Potato Chips (I like Ruffles chopped up bc they are extra crunchy)', '', 'https://grillgirl.com/2019/03/colombian-style-hot-dogs/', 'Grill', 'Colombian Style Hot Dogs', 'Mar 12, 2019', 'Latin, American', 'Instructions?#&?#&Grill hot dogs on Kingsford Charcoal until they are nice and charred on all sides. Next, spread the buns out and toast for a few minutes on the grill on the cooler side, being careful not to burn.?#&?#&Mix mayo, mustard and ketchup to make a “Pink sauce”. **I like to put this in a squeeze bottle to squeeze on top but whatever works for you!?#&?#&Place the hot dog in the bun and add a tbsp of pineapple preserves. Next, add pink sauce and then finish with crushed potato chips.?#&?#&This is an unexpectedly tasty combo of sweet, salty, smoky and crunch all in one bite!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'672B725A1E148941AE6003D34D93A19B', X'73BA27EED312784798805CABC00B4A15', X'73BA27EED312784798805CABC00B4A15', 0, 'https://grillgirl.com/2018/12/grilled-shrimp-and-grits/', 'Grilled Shrimp and Grits', 2, NULL, 0, 'Grilled Shrimp and Grits', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/12/Grilled-Shrimp-and-Grits-150x150.jpeg', '', '', '5 minutes', '30-40 minutes', '45 minutes', '8 servings', 'Ingredients?#&?#&1 ½ lbs shrimp, deveined and shells removed ?#&?#&1 lb andouille sausage, chopped into ¼” pieces ?#&?#&1 8-oz bag red, yellow, green mini peppers ?#&?#&2 tbsp old bay seasoning ?#&?#&2 cups Grits- I am really loving Bob’s Red Mill Corn Grits/Polenta- very creamy and cook quickly?#&?#&1 qt organic reduced sodium chicken stock (or homemade!), plus 2 cups water ?#&?#&1/3 cup whole milk ?#&?#&1 tbsp butter ?#&?#&1 tbsp sea salt- or to taste ?#&?#&1 tsbp fresh ground pepper- or to taste ?#&?#&1 tbsp smoked paprika ?#&?#&1 cup cheddar, grated?#&?#&Chopped parsley, for garnish, optional?#&?#&Crystal Hot sauce', 'Southern', 'https://grillgirl.com/2018/12/grilled-shrimp-and-grits/', 'Grilling', 'Shrimp and Grits, easy shrimp and grits, low country style shrimp and grits, Southern Shrimp and Grits, Grilled Shrimp and Grits, Kingsford Charcoal, Robyn Lindars, GrillGirl', 'Dec 18, 2018', 'Brunch/Seafood', 'Instructions?#&?#&In a bowl, add olive oil, old bay and the shrimp and toss to coat.?#&?#&Meanwhile, start your grits: Heat quart of chicken broth and water in a large pot and bring to a boil. Add 2 cups of grits gradually over 2 minutes stirring constantly (grits will clump easily-also easily stick to bottom of pot!). Let the grits cook down on a medium simmer, stirring often, about 30 minutes.  While the grits are cooking, prepare your grill for cooking on direct heat. I recommend using your charcoal grill with Kingsford Charcoal to get smoky flavor.?#&?#&While your charcoal is heating up, continue to watch and stir your grits. Once they have reduced down, keep them on a very low simmer.?#&?#&Grill the shrimp, sausage and peppers until cooked through. The shrimp will cook most quickly- remove them as soon as they begin to turn pink. Remove the sausage once an internal temp of 145 degrees with a Thermapen has been reached and the peppers can be removed once they have developed char marks.?#&?#&Once the shrimp, peppers and sausage have cooked on the grill, remove them and set aside on a cutting board to let cool slightly so they can be chopped. Meanwhile, add the milk, cheese and butter to the grits and stir to combine and let thicken over a low simmer. Add salt, pepper, and smoked paprika to the grits.  Chop the peppers and sausage and remove the tails off the shrimp. Ladle grits ½ cup at a time into bowls. Add shrimp, peppers and sausage and garnish with chopped parsley.?#&?#&Serve with your favorite hot sauce, and additional salt and pepper. I hope you and your family enjoy this Southern inspired dish as much as mine does! Cheers!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'862C58490888BD4FB6B350BA0844A0C2', X'5CC97EF4539B264198B46F163BF74B98', X'5CC97EF4539B264198B46F163BF74B98', 0, 'https://grillgirl.com/2018/11/pork-belly-burnt-ends-recipe/', 'Pork Belly Burnt Ends Recipe', 2, NULL, 0, 'Pork Belly Burnt Ends', 'Robyn Lindars', 'https://grillgirl.com/wp-content/uploads/2018/11/pork-belly-150x150.jpg', '', '', '30 min', '5 hours', '5 hours 30 minutes', '', 'Ingredients?#&?#&Large aluminum foil pan?#&5 lbs pork belly, cubed into 1 ½” pieces (I used Snake River Farms Kurobuta Pork Belly)?#&1 tbsp olive oil?#&1 cup All Purpose BBQ Rub (Jon Solberg’s rub is fantastic!)?#&?#&Pan Sauce:?#&?#&1 cup BBQ Sauce (Try this Jamaican Jerk BBQ Sauce recipe or just pick up some Stubb’s!)?#&1 tbsp bourbon?#&2 tbsp molasses?#&Dash hot sauce?#&3 tbsp butter', '', 'https://grillgirl.com/2018/11/pork-belly-burnt-ends-recipe/', '', '', 'Nov 13, 2018', '', 'Instructions?#&?#&Slice the pork belly into 1 ½” pieces. Place in a large bowl and toss in olive oil, then add rub and make sure all pieces are covered in it.?#&Smoke the pork belly pieces at 250 for 2 hours or until all the fat has rendered off and a crust is beginning to form.?#&Place the pork belly burnt ends in the foil pan. Combine the bbq sauce, bourbon, molasses, and hot sauce and pour over the burnt ends.?#&Smoke for another 2 hours covered in foil.?#&After 2 hours, remove the foil and let cook uncovered for another 30 minutes to thicken up the sauce.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'4B0FBE0628565D40BFAC02C91279E3ED', X'7050BBE677458545B4714BEAAAB9B15C', X'7050BBE677458545B4714BEAAAB9B15C', 0, 'https://grillgirl.com/2018/07/big-green-egg-series-making-pulled-pork-in-your-egg/', 'Big Green Egg Series: Making Pulled Pork in Your Egg!', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2018/07/big-green-egg-series-making-pulled-pork-in-your-egg/', '', '', 'Jul 9, 2018', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'8DD3E19DC10A124DAE7585B2E446C6E6', X'7D43FE075383CE4CAB6FDF18CA08B51C', X'7D43FE075383CE4CAB6FDF18CA08B51C', 0, 'https://grillgirl.com/2017/10/smoked-bologna-pit-barrel-cooker/', 'Smoked Bologna on the Pit Barrel Cooker', 2, NULL, 0, 'Smoked Bologna on the Pit Barrel Cooker', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/10/smoked-bologna-on-pitbarrel-cooker-edited-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One 3–5 lb chubb of beef bologna?#&PBC Beef & Game Pit Rub?#&2 tsp mustard (for rub binder)?#&1 loaf of Wonder Bread Texas Toast?#&1 Heirloom tomato?#&1 head of iceberg lettuce?#&1 Vidalia onion?#&1 jar of Dukes Mayonnaise?#&?#&BBQ GLAZE?#&?#& 2 Cups of KC Masterpiece Classic BBQ sauce?#&2/3 cups of apple juice?#&?#& In a small bowl mix the bbq sauce and apple juice until incorporated', '', 'https://grillgirl.com/2017/10/smoked-bologna-pit-barrel-cooker/', '', '', 'Oct 20, 2017', '', 'Instructions?#&?#&Remove exterior wrapper from the chubb of bologna. Take the chubb and lay it on its side. Take a paring knife and score diagonally about ¼” to 1/2”  deep in a criss cross method. This will give the meat some texture. Apply approximately 2 TBSP of mustard (amount depends on the size of your chubb) and massage into the meat.  Season with a moderate dusting of Beef & Game Pit Rub to all sides of the bologna.?#&?#&Light your Pit Barrel Cooker (PBC) according to the instructions. Oil the grate so the meat will not stick. Place the meat on its side on the grate. Add your preferred wood for smoke- I added cherry wood. Cook until you reach an internal temperature of 140 degrees as measured with an internal read thermometer (I recommend the Thermapen for accurate internal temperature readings). This should take approximately 2 ½ hrs depending on the weather conditions.?#&?#&Remove the bologna from the grate. Glaze with the BBQ glaze. Don’t overdo the sauce; use just enough to coat the bologna. Put the bologna back on the PBC and cook for 10-15 minutes. Just enough to set the glaze.?#&?#&Remove and loosely cover the bologna with aluminum foil while you prep for your bologna sandwich. Take two pieces of Wonder Texas Toast, lie them on the grate and cook with the lid off until you see some grill marks.  Slice the bologna to your desired thickness and then put mayonnaise, tomato, sliced onion and lettuce on your sandwich and enjoy.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'1E7795857ED1A440818481A8D0C2BC38', X'FA7B5BAC2F86A345B1700C5D115954CE', X'FA7B5BAC2F86A345B1700C5D115954CE', 0, 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 2, NULL, 0, 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/10/Bacon-Pork-Guinness-Cassoulet-edited-150x100.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Dutch Oven or large aluminum pan to put on the grill?#&Smithfield Applewood Smoked Bacon Marinated Fresh Pork Loin Filet, sliced into 1/2” thick pieces for grilling?#&1/2 lb bacon, chopped into 1/2 inch pieces?#&2 15 oz cans navy beans, drained and rinsed?#&1 15 oz can kidney beans?#&1 medium sweet onion, sliced into rings for grilling?#&4 medium sized (I used Campari) tomatoes, sliced in half?#&2 carrots, chopped?#&2 celery sticks, chopped?#&2 cloves garlic, chopped?#&1 large bunch thyme?#&1–2 Guinness Beers or another Porter Style Beer?#&2 tbsp brown sugar?#&French Bread?#&Compound Butter (herbed butter)', '', 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', '', '', 'Oct 7, 2017', '', 'Instructions?#&?#&Heat your grill up, preparing it for medium direct cooking- at a temperature of approximately 350 degrees. This means you will be cooking directly over the fire!?#&?#&While the grill heats up go ahead and prepare your ingredients for the grill; chop the bacon, carrots and celery and garlic. Slice the tenderloin into 1/2 thick pieces, and slice onion into rings for the grill, cut the tomatoes in half. Get all your other ingredients ready to take outside- I usually put everything on a big cutting board and carry it outside and then get another plate for switching out.?#&?#&Once the grill is heated up, place the cast iron on one half of the grill and add the bacon and let it reduce. Meanwhile, with your existing grill surface area, go ahead and grill the pork tenderloin. This will only take a few minutes per side, remove once char marks have begun to form (remember these will also go into the stew and continue to cook). Next, grill the tomatoes and onions until char marks have formed, then remove from the grill.?#&?#&In the dutch oven, once the bacon fat has rendered, add the carrots and celery and let them cook in the bacon fat. Meanwhile, chop the onions and tomatoes and add to the dutch oven, stirring frequently to ensure no bacon is burning on the bottom. Now add the garlic. Next, add the first Guinness beer and deglaze the bottom of the pan. Add the sugar and stir. Now, chop the pork into small bite sized pieces and add to the dutch oven. Last but not least, add the beans and thyme and let the cassoulet simmer for 10 minutes or until the liquid has reduced. If you like a more soup like stew, add another 1/2 to full beer, depending on how much time you have to let simmer.?#&?#&While the Cassoulet simmers on the grill, slice the French bread into 1” thick pieces and slather with butter on both sides. Grill very quickly – no more than a minute- to get grill marks, ensuring you don’t let it burn.?#&?#&Serve the Cassoulet with the grilled bread and be prepared for big smiles on the faces of your family- like I said, this recipe is pure comfort food and expect to add this to your list of recipes you make again and again. Also great for freezing and giving to those in need of an easy meal- like friends and family going through an illness or just home with a new baby.?#&?#&If you give this recipe a try, please let me know your thoughts in the comments below! Also, when reheating you’ll notice that the beans soak up a lot of the liquid. You can add more beer if you want more stew like consistency before reheating.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'792BFD675AA2A344AC75D76980153307', X'1A2BAA5E0AA2C449A71F022C3D1CF00B', X'1A2BAA5E0AA2C449A71F022C3D1CF00B', 0, 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 2, NULL, 0, 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/Hawaiian-Pork-Tenderloin-Sliders_1-150x150.jpg', '', '', '', '', '', 'Serves 4, 2 sliders per person', 'Ingredients?#&?#&1 Smithfield Roasted Garlic and Cracked Black Pepper Marinated Fresh Pork Tenderloin1 large pack Kings Hawaiian Rolls?#&1 small bag mini peppers?#&1 pineapple, cored and sliced into rings (you can also use canned pineapple)?#&1/4 cup mayo?#&1 tbsp Sriracha', '', 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', '', '', 'Jun 22, 2017', '', 'Instructions?#&?#&Preheat your grill to 350 degrees preparing a direct and indirect zone. While the grill is heating up, slice your pork tenderloin into 3/4” filets.?#&?#&Coat your grill grates with a little oil so the food does not stick (make sure to do this especially before grilling the pork filets).?#&?#&Grill the pineapple and peppers until char marks have formed and they have begun to caramelize. Remove and let cool.?#&?#&Next, grill the pork tenderloin filets- about 5 minutes max, or until the internal temp reads 145 degrees with an internal read thermometer (I recommend the thermapen).?#&On the indirect side, toast the rolls very quickly being careful to not let them burn.?#&?#&Slice the peppers and then combine the mayo with the sriracha and spread on the buns. Now, assemble your sliders with the tenderloin, pineapple and peppers.?#&?#&The sweetness of the pineapple and peppers in this recipe really compliment the spice of the Black Pepper marinated pork tenderloin, don’t be surprised that you’ll go back for seconds or thirds!?#&?#&What are your favorite recipes for getting dinner on the table in less than 30 minutes? Please share in the comments below!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'D7EC8148882B1E46B22124E9FAA448E1', X'1676588881A80146B4788CD0E13BC1C5', X'1676588881A80146B4788CD0E13BC1C5', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'7712CF581CE70E4EB8CC3983DCDBE21F', X'5A9F3C8024E45A48B469AFBFA7C61DCE', X'5A9F3C8024E45A48B469AFBFA7C61DCE', 0, 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', 'Rum Pineapple Pork Ribs are Sweet and Tangy', 2, NULL, 0, 'Rum Pineapple Pork Ribs are Sweet and Tangy', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/08/rum-pineapple-ribs-150x150.jpeg', '', '', '20 minutes', '5 hours', '5 hours 20 minutes', '', 'Ingredients?#&?#&1 slab baby back ribs?#&Mustard?#&3 tbsp smoked all purpose rub ?#&1 pineapple, cored and sliced into 1/2” rings – should make about 9 rings total – 3 of which are used in the sauce, save juice for sauce (if using canned look for a corn syrup free, reduced sugar version)?#&?#&Texas Crutch?#&?#&1/4 cup apple juice?#&2 tbsp pineapple preserves?#&?#&Pineapple Rum BBQ Sauce?#&?#&1/3 cup BBQ sauce or your favorite store bought variety?#&3 tbsp pineapple preserves?#&1/4 cup Myers rum?#&3 tbsp pineapple juice?#&Dash allspice?#&3 slice pineapple, chopped', '', 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', '2-2-1 Method', 'pork ribs, baby back ribs, 2-2-1 ribs, smoked BBQ ribs, texas crutch, pork ribs recipe', 'Aug 29, 2019', 'Pork Ribs', 'Instructions?#&?#&Smoke Temp: 225F?#&?#&Smoking Woods: FRUIT WOODS- (apple, pecan, pear) OR mix with OAK, PECAN, MAPLE?#&?#&The ribs are going to use the 2-2-1 method: 2 hours in the smoker, 2 hours in foil, 1 hour letting the sauce glaze.?#&?#&Start by removing the membrane from the ribs. This is easier with a paper towel for gripping and a butter knife for separating from the meat.?#&Prepare your smoker for 225 degrees- this recipe can be made on any kind of smoker (and was originally created for my Healthy Electric Smoker Cookbook).?#&Next, slather with mustard so the rub can adhere. Apply the rub and smoke the meat for 2 hours.?#&After 2 hours, remove from the smoker and do the “Texas Crutch,” i.e., “wrap in foil”. Mix the apple juice and pineapple preserve and add to the foil packet. Let smoke another two hours.?#&Meanwhile, as the meat is smoking make your rum barbecue sauce. Combine the BBQ sauce, pineapple preserves, rum, chopped pineapple, pineapple juice and allspice. Let reduce down for 20 minutes on medium simmer.?#&During the last hour, remove the ribs from foil and glaze with the pineapple rum BBQ sauce.?#&Layer slices of pineapple on top of the ribs and drizzle more sauce over the pineapple.?#&Let smoke for an additional hour to let the sauce set. Serve immediately.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'154F02E75AA82E498D2450371E02946A', X'F5FD6CE27517F34F944DB327D0AE217E', X'F5FD6CE27517F34F944DB327D0AE217E', 0, 'https://grillgirl.com/2019/06/gorgonzola-thyme-stuffed-pork-chops-with-bourbon-grilled-apples/', 'Gorgonzola Thyme Stuffed Pork Chops with Bourbon Grilled Apples', 2, NULL, 0, 'Gorgonzola Thyme Stuffed Pork Chops with Bourbon Grilled Apples', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/06/Stuffed-Pork-Chops-with-bourbone-grilled-apples-72-dpi-150x150.jpg', '', '', '20', '8', '28', '4 stuffed pork chops', 'Ingredients?#&?#&4 Smithfield Brown Sugar Boneless Center Cut Pork Chops – these are available at select grocery stores and retailers nationwide, including: Walmart, ShopRite, Food Lion, Publix, Albertson’s/Safeway and Save a Lot.?#&2 tbsp gorgonzola?#&2 tbsp chopped walnuts?#&2 tbsp chopped dried cherries?#&2 tbsp fresh thyme?#&toothpicks?#&?#&Grilled Bourbon Apples:?#&?#&4 apples, sliced with seeds removed?#&dash cinnamon?#&1 tbsp maple syrup?#&2 tbsp bourbon', 'American', 'https://grillgirl.com/2019/06/gorgonzola-thyme-stuffed-pork-chops-with-bourbon-grilled-apples/', 'Grill', 'Grilled Pork Chops, Grilled Center Cut Pork Chops, Stuffed Pork Chops with Grilled Apples, Gorgonzola Thyme Stuffed Pork Chops, Bourbon Grilled Apples, How to Grill Pork Chops', 'Jun 27, 2019', 'Pork, Grilled Pork Chops', 'Instructions?#&?#&Prepare your grill direct for heat grilling at 350-400 degrees. While the grill is heating up, go ahead and mix your gorgonzola, walnuts, cherries and thyme.?#&?#&Take a very sharp knife and cut a slit into the pork chops where the stuffing will go, using your finger to expand the opening. Stuff the pork chops with the cheese mixture and use a tooth pick to secure the opening so the stuffing doesn’t come out while grilling.?#&?#&Next, cut your apples. Combine the maple syrup and bourbon together in a cup. Sprinkle the apples with the cinnamon and then drizzle the maple bourbon syrup on top of the apples.?#&?#&Oil your grates with a high heat oil like avocado oil or vegetable oil so your food doesn’t stick to the grill grates. Grill the pork chops on direct heat, flipping halfway through, until the internal temp reaches 140 degrees with an internal read thermometer, this should take about 5-8 minutes depending on your grill. While grilling the pork chops, add the apple slices and let them grill until they develop char marks and have begun to caramelize on the grill.?#&?#&Once the pork chops have reached an internal temp of 140 degrees, pull them off the grill (they will continue to cook and reach 145 degrees after removed from the grill). Let them rest under foil for 5 minutes.?#&?#&Serve the pork chops with the grilled apples for sweet-salty pork pairing perfection!?#&?#&What is your favorite way to enjoy Pork Chops? Check out my recent Cuban inspired Mojo Pork, coming straight to you from my Miami Kitchen!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'91BAE52924066B4D8C23E6681BF39BB3', X'A2D30D979AD1B34C8C5CCC0DCA6EA4BC', X'A2D30D979AD1B34C8C5CCC0DCA6EA4BC', 0, 'https://grillgirl.com/2019/03/colombian-style-hot-dogs/', 'Colombian Style Hot Dogs', 2, NULL, 0, 'Colombian Style Hot Dogs', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/03/Colombian-Style-Hot-Dogs-RL-150x150.jpg', '', '', '5 minutes', '10 minutes', '15 minutes', '8 hot dogs- 2 per person', 'Ingredients?#&?#&8 hot dogs- I use use Nitrate Free Hot dogs?#&8 pack hot dog buns?#&1/4 cup Mayo?#&1/4 cup Mustard?#&¼ cup Ketchup?#&¼ cup Pineapple Preserves?#&2 cups Crushed Potato Chips (I like Ruffles chopped up bc they are extra crunchy)', '', 'https://grillgirl.com/2019/03/colombian-style-hot-dogs/', 'Grill', 'Colombian Style Hot Dogs', 'Mar 12, 2019', 'Latin, American', 'Instructions?#&?#&Grill hot dogs on Kingsford Charcoal until they are nice and charred on all sides. Next, spread the buns out and toast for a few minutes on the grill on the cooler side, being careful not to burn.?#&?#&Mix mayo, mustard and ketchup to make a “Pink sauce”. **I like to put this in a squeeze bottle to squeeze on top but whatever works for you!?#&?#&Place the hot dog in the bun and add a tbsp of pineapple preserves. Next, add pink sauce and then finish with crushed potato chips.?#&?#&This is an unexpectedly tasty combo of sweet, salty, smoky and crunch all in one bite!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'9B6F84776924BA4E832E322A8D1A34F5', X'33B82CE6B40E9C40B19D2FAF8A49D38B', X'33B82CE6B40E9C40B19D2FAF8A49D38B', 0, 'https://grillgirl.com/2018/12/grilled-shrimp-and-grits/', 'Grilled Shrimp and Grits', 2, NULL, 0, 'Grilled Shrimp and Grits', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/12/Grilled-Shrimp-and-Grits-150x150.jpeg', '', '', '5 minutes', '30-40 minutes', '45 minutes', '8 servings', 'Ingredients?#&?#&1 ½ lbs shrimp, deveined and shells removed ?#&?#&1 lb andouille sausage, chopped into ¼” pieces ?#&?#&1 8-oz bag red, yellow, green mini peppers ?#&?#&2 tbsp old bay seasoning ?#&?#&2 cups Grits- I am really loving Bob’s Red Mill Corn Grits/Polenta- very creamy and cook quickly?#&?#&1 qt organic reduced sodium chicken stock (or homemade!), plus 2 cups water ?#&?#&1/3 cup whole milk ?#&?#&1 tbsp butter ?#&?#&1 tbsp sea salt- or to taste ?#&?#&1 tsbp fresh ground pepper- or to taste ?#&?#&1 tbsp smoked paprika ?#&?#&1 cup cheddar, grated?#&?#&Chopped parsley, for garnish, optional?#&?#&Crystal Hot sauce', 'Southern', 'https://grillgirl.com/2018/12/grilled-shrimp-and-grits/', 'Grilling', 'Shrimp and Grits, easy shrimp and grits, low country style shrimp and grits, Southern Shrimp and Grits, Grilled Shrimp and Grits, Kingsford Charcoal, Robyn Lindars, GrillGirl', 'Dec 18, 2018', 'Brunch/Seafood', 'Instructions?#&?#&In a bowl, add olive oil, old bay and the shrimp and toss to coat.?#&?#&Meanwhile, start your grits: Heat quart of chicken broth and water in a large pot and bring to a boil. Add 2 cups of grits gradually over 2 minutes stirring constantly (grits will clump easily-also easily stick to bottom of pot!). Let the grits cook down on a medium simmer, stirring often, about 30 minutes.  While the grits are cooking, prepare your grill for cooking on direct heat. I recommend using your charcoal grill with Kingsford Charcoal to get smoky flavor.?#&?#&While your charcoal is heating up, continue to watch and stir your grits. Once they have reduced down, keep them on a very low simmer.?#&?#&Grill the shrimp, sausage and peppers until cooked through. The shrimp will cook most quickly- remove them as soon as they begin to turn pink. Remove the sausage once an internal temp of 145 degrees with a Thermapen has been reached and the peppers can be removed once they have developed char marks.?#&?#&Once the shrimp, peppers and sausage have cooked on the grill, remove them and set aside on a cutting board to let cool slightly so they can be chopped. Meanwhile, add the milk, cheese and butter to the grits and stir to combine and let thicken over a low simmer. Add salt, pepper, and smoked paprika to the grits.  Chop the peppers and sausage and remove the tails off the shrimp. Ladle grits ½ cup at a time into bowls. Add shrimp, peppers and sausage and garnish with chopped parsley.?#&?#&Serve with your favorite hot sauce, and additional salt and pepper. I hope you and your family enjoy this Southern inspired dish as much as mine does! Cheers!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'81803B514F12C54D9522CF9B74AF4132', X'D8DD00009309A647A6D4C6E05CA472FC', X'D8DD00009309A647A6D4C6E05CA472FC', 0, 'https://grillgirl.com/2018/11/pork-belly-burnt-ends-recipe/', 'Pork Belly Burnt Ends Recipe', 2, NULL, 0, 'Pork Belly Burnt Ends', 'Robyn Lindars', 'https://grillgirl.com/wp-content/uploads/2018/11/pork-belly-150x150.jpg', '', '', '30 min', '5 hours', '5 hours 30 minutes', '', 'Ingredients?#&?#&Large aluminum foil pan?#&5 lbs pork belly, cubed into 1 ½” pieces (I used Snake River Farms Kurobuta Pork Belly)?#&1 tbsp olive oil?#&1 cup All Purpose BBQ Rub (Jon Solberg’s rub is fantastic!)?#&?#&Pan Sauce:?#&?#&1 cup BBQ Sauce (Try this Jamaican Jerk BBQ Sauce recipe or just pick up some Stubb’s!)?#&1 tbsp bourbon?#&2 tbsp molasses?#&Dash hot sauce?#&3 tbsp butter', '', 'https://grillgirl.com/2018/11/pork-belly-burnt-ends-recipe/', '', '', 'Nov 13, 2018', '', 'Instructions?#&?#&Slice the pork belly into 1 ½” pieces. Place in a large bowl and toss in olive oil, then add rub and make sure all pieces are covered in it.?#&Smoke the pork belly pieces at 250 for 2 hours or until all the fat has rendered off and a crust is beginning to form.?#&Place the pork belly burnt ends in the foil pan. Combine the bbq sauce, bourbon, molasses, and hot sauce and pour over the burnt ends.?#&Smoke for another 2 hours covered in foil.?#&After 2 hours, remove the foil and let cook uncovered for another 30 minutes to thicken up the sauce.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'CC9AD328624F3648881BEE8104594D65', X'F43494BED4E0CB4E9D18BD7DBB064C50', X'F43494BED4E0CB4E9D18BD7DBB064C50', 0, 'https://grillgirl.com/2018/07/big-green-egg-series-making-pulled-pork-in-your-egg/', 'Big Green Egg Series: Making Pulled Pork in Your Egg!', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2018/07/big-green-egg-series-making-pulled-pork-in-your-egg/', '', '', 'Jul 9, 2018', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'7AE60CEBAAA34847B5395AA23C709EAE', X'85D8A97F2437BE4AB8A850295FF9670A', X'85D8A97F2437BE4AB8A850295FF9670A', 0, 'https://grillgirl.com/2017/10/smoked-bologna-pit-barrel-cooker/', 'Smoked Bologna on the Pit Barrel Cooker', 2, NULL, 0, 'Smoked Bologna on the Pit Barrel Cooker', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/10/smoked-bologna-on-pitbarrel-cooker-edited-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One 3–5 lb chubb of beef bologna?#&PBC Beef & Game Pit Rub?#&2 tsp mustard (for rub binder)?#&1 loaf of Wonder Bread Texas Toast?#&1 Heirloom tomato?#&1 head of iceberg lettuce?#&1 Vidalia onion?#&1 jar of Dukes Mayonnaise?#&?#&BBQ GLAZE?#&?#& 2 Cups of KC Masterpiece Classic BBQ sauce?#&2/3 cups of apple juice?#&?#& In a small bowl mix the bbq sauce and apple juice until incorporated', '', 'https://grillgirl.com/2017/10/smoked-bologna-pit-barrel-cooker/', '', '', 'Oct 20, 2017', '', 'Instructions?#&?#&Remove exterior wrapper from the chubb of bologna. Take the chubb and lay it on its side. Take a paring knife and score diagonally about ¼” to 1/2”  deep in a criss cross method. This will give the meat some texture. Apply approximately 2 TBSP of mustard (amount depends on the size of your chubb) and massage into the meat.  Season with a moderate dusting of Beef & Game Pit Rub to all sides of the bologna.?#&?#&Light your Pit Barrel Cooker (PBC) according to the instructions. Oil the grate so the meat will not stick. Place the meat on its side on the grate. Add your preferred wood for smoke- I added cherry wood. Cook until you reach an internal temperature of 140 degrees as measured with an internal read thermometer (I recommend the Thermapen for accurate internal temperature readings). This should take approximately 2 ½ hrs depending on the weather conditions.?#&?#&Remove the bologna from the grate. Glaze with the BBQ glaze. Don’t overdo the sauce; use just enough to coat the bologna. Put the bologna back on the PBC and cook for 10-15 minutes. Just enough to set the glaze.?#&?#&Remove and loosely cover the bologna with aluminum foil while you prep for your bologna sandwich. Take two pieces of Wonder Texas Toast, lie them on the grate and cook with the lid off until you see some grill marks.  Slice the bologna to your desired thickness and then put mayonnaise, tomato, sliced onion and lettuce on your sandwich and enjoy.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'286CA9486CA76047A320BA7D55291A30', X'760A4CA3C4207F4C903B53FE0E622084', X'760A4CA3C4207F4C903B53FE0E622084', 0, 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 2, NULL, 0, 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/10/Bacon-Pork-Guinness-Cassoulet-edited-150x100.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Dutch Oven or large aluminum pan to put on the grill?#&Smithfield Applewood Smoked Bacon Marinated Fresh Pork Loin Filet, sliced into 1/2” thick pieces for grilling?#&1/2 lb bacon, chopped into 1/2 inch pieces?#&2 15 oz cans navy beans, drained and rinsed?#&1 15 oz can kidney beans?#&1 medium sweet onion, sliced into rings for grilling?#&4 medium sized (I used Campari) tomatoes, sliced in half?#&2 carrots, chopped?#&2 celery sticks, chopped?#&2 cloves garlic, chopped?#&1 large bunch thyme?#&1–2 Guinness Beers or another Porter Style Beer?#&2 tbsp brown sugar?#&French Bread?#&Compound Butter (herbed butter)', '', 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', '', '', 'Oct 7, 2017', '', 'Instructions?#&?#&Heat your grill up, preparing it for medium direct cooking- at a temperature of approximately 350 degrees. This means you will be cooking directly over the fire!?#&?#&While the grill heats up go ahead and prepare your ingredients for the grill; chop the bacon, carrots and celery and garlic. Slice the tenderloin into 1/2 thick pieces, and slice onion into rings for the grill, cut the tomatoes in half. Get all your other ingredients ready to take outside- I usually put everything on a big cutting board and carry it outside and then get another plate for switching out.?#&?#&Once the grill is heated up, place the cast iron on one half of the grill and add the bacon and let it reduce. Meanwhile, with your existing grill surface area, go ahead and grill the pork tenderloin. This will only take a few minutes per side, remove once char marks have begun to form (remember these will also go into the stew and continue to cook). Next, grill the tomatoes and onions until char marks have formed, then remove from the grill.?#&?#&In the dutch oven, once the bacon fat has rendered, add the carrots and celery and let them cook in the bacon fat. Meanwhile, chop the onions and tomatoes and add to the dutch oven, stirring frequently to ensure no bacon is burning on the bottom. Now add the garlic. Next, add the first Guinness beer and deglaze the bottom of the pan. Add the sugar and stir. Now, chop the pork into small bite sized pieces and add to the dutch oven. Last but not least, add the beans and thyme and let the cassoulet simmer for 10 minutes or until the liquid has reduced. If you like a more soup like stew, add another 1/2 to full beer, depending on how much time you have to let simmer.?#&?#&While the Cassoulet simmers on the grill, slice the French bread into 1” thick pieces and slather with butter on both sides. Grill very quickly – no more than a minute- to get grill marks, ensuring you don’t let it burn.?#&?#&Serve the Cassoulet with the grilled bread and be prepared for big smiles on the faces of your family- like I said, this recipe is pure comfort food and expect to add this to your list of recipes you make again and again. Also great for freezing and giving to those in need of an easy meal- like friends and family going through an illness or just home with a new baby.?#&?#&If you give this recipe a try, please let me know your thoughts in the comments below! Also, when reheating you’ll notice that the beans soak up a lot of the liquid. You can add more beer if you want more stew like consistency before reheating.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'707F72348CDA0540B93E7EFB80652728', X'DA89ED47DF2133418B17A91A62E1DB9F', X'DA89ED47DF2133418B17A91A62E1DB9F', 0, 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 2, NULL, 0, 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/Hawaiian-Pork-Tenderloin-Sliders_1-150x150.jpg', '', '', '', '', '', 'Serves 4, 2 sliders per person', 'Ingredients?#&?#&1 Smithfield Roasted Garlic and Cracked Black Pepper Marinated Fresh Pork Tenderloin1 large pack Kings Hawaiian Rolls?#&1 small bag mini peppers?#&1 pineapple, cored and sliced into rings (you can also use canned pineapple)?#&1/4 cup mayo?#&1 tbsp Sriracha', '', 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', '', '', 'Jun 22, 2017', '', 'Instructions?#&?#&Preheat your grill to 350 degrees preparing a direct and indirect zone. While the grill is heating up, slice your pork tenderloin into 3/4” filets.?#&?#&Coat your grill grates with a little oil so the food does not stick (make sure to do this especially before grilling the pork filets).?#&?#&Grill the pineapple and peppers until char marks have formed and they have begun to caramelize. Remove and let cool.?#&?#&Next, grill the pork tenderloin filets- about 5 minutes max, or until the internal temp reads 145 degrees with an internal read thermometer (I recommend the thermapen).?#&On the indirect side, toast the rolls very quickly being careful to not let them burn.?#&?#&Slice the peppers and then combine the mayo with the sriracha and spread on the buns. Now, assemble your sliders with the tenderloin, pineapple and peppers.?#&?#&The sweetness of the pineapple and peppers in this recipe really compliment the spice of the Black Pepper marinated pork tenderloin, don’t be surprised that you’ll go back for seconds or thirds!?#&?#&What are your favorite recipes for getting dinner on the table in less than 30 minutes? Please share in the comments below!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'A4A82EBA98790346A7AA09350CB54CE8', X'CF0F702D51D4AF4EBBF91D6BB33A1988', X'CF0F702D51D4AF4EBBF91D6BB33A1988', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'3F6CF3F763426A498386698FAE6429EB', X'CC754DE28C05A04481FA9E7C161FC0D8', X'CC754DE28C05A04481FA9E7C161FC0D8', 0, 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', 'Rum Pineapple Pork Ribs are Sweet and Tangy', 2, NULL, 0, 'Rum Pineapple Pork Ribs are Sweet and Tangy', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/08/rum-pineapple-ribs-150x150.jpeg', '', '', '20 minutes', '5 hours', '5 hours 20 minutes', '', 'Ingredients?#&?#&1 slab baby back ribs?#&Mustard?#&3 tbsp smoked all purpose rub ?#&1 pineapple, cored and sliced into 1/2” rings – should make about 9 rings total – 3 of which are used in the sauce, save juice for sauce (if using canned look for a corn syrup free, reduced sugar version)?#&?#&Texas Crutch?#&?#&1/4 cup apple juice?#&2 tbsp pineapple preserves?#&?#&Pineapple Rum BBQ Sauce?#&?#&1/3 cup BBQ sauce or your favorite store bought variety?#&3 tbsp pineapple preserves?#&1/4 cup Myers rum?#&3 tbsp pineapple juice?#&Dash allspice?#&3 slice pineapple, chopped', '', 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', '2-2-1 Method', 'pork ribs, baby back ribs, 2-2-1 ribs, smoked BBQ ribs, texas crutch, pork ribs recipe', 'Aug 29, 2019', 'Pork Ribs', 'Instructions?#&?#&Smoke Temp: 225F?#&?#&Smoking Woods: FRUIT WOODS- (apple, pecan, pear) OR mix with OAK, PECAN, MAPLE?#&?#&The ribs are going to use the 2-2-1 method: 2 hours in the smoker, 2 hours in foil, 1 hour letting the sauce glaze.?#&?#&Start by removing the membrane from the ribs. This is easier with a paper towel for gripping and a butter knife for separating from the meat.?#&Prepare your smoker for 225 degrees- this recipe can be made on any kind of smoker (and was originally created for my Healthy Electric Smoker Cookbook).?#&Next, slather with mustard so the rub can adhere. Apply the rub and smoke the meat for 2 hours.?#&After 2 hours, remove from the smoker and do the “Texas Crutch,” i.e., “wrap in foil”. Mix the apple juice and pineapple preserve and add to the foil packet. Let smoke another two hours.?#&Meanwhile, as the meat is smoking make your rum barbecue sauce. Combine the BBQ sauce, pineapple preserves, rum, chopped pineapple, pineapple juice and allspice. Let reduce down for 20 minutes on medium simmer.?#&During the last hour, remove the ribs from foil and glaze with the pineapple rum BBQ sauce.?#&Layer slices of pineapple on top of the ribs and drizzle more sauce over the pineapple.?#&Let smoke for an additional hour to let the sauce set. Serve immediately.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'BA00FB9D965761469A17EC4CD8220A88', X'E8298C141BB12D43BCB1A24CA41B4395', X'E8298C141BB12D43BCB1A24CA41B4395', 0, 'https://grillgirl.com/2019/06/gorgonzola-thyme-stuffed-pork-chops-with-bourbon-grilled-apples/', 'Gorgonzola Thyme Stuffed Pork Chops with Bourbon Grilled Apples', 2, NULL, 0, 'Gorgonzola Thyme Stuffed Pork Chops with Bourbon Grilled Apples', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/06/Stuffed-Pork-Chops-with-bourbone-grilled-apples-72-dpi-150x150.jpg', '', '', '20', '8', '28', '4 stuffed pork chops', 'Ingredients?#&?#&4 Smithfield Brown Sugar Boneless Center Cut Pork Chops – these are available at select grocery stores and retailers nationwide, including: Walmart, ShopRite, Food Lion, Publix, Albertson’s/Safeway and Save a Lot.?#&2 tbsp gorgonzola?#&2 tbsp chopped walnuts?#&2 tbsp chopped dried cherries?#&2 tbsp fresh thyme?#&toothpicks?#&?#&Grilled Bourbon Apples:?#&?#&4 apples, sliced with seeds removed?#&dash cinnamon?#&1 tbsp maple syrup?#&2 tbsp bourbon', 'American', 'https://grillgirl.com/2019/06/gorgonzola-thyme-stuffed-pork-chops-with-bourbon-grilled-apples/', 'Grill', 'Grilled Pork Chops, Grilled Center Cut Pork Chops, Stuffed Pork Chops with Grilled Apples, Gorgonzola Thyme Stuffed Pork Chops, Bourbon Grilled Apples, How to Grill Pork Chops', 'Jun 27, 2019', 'Pork, Grilled Pork Chops', 'Instructions?#&?#&Prepare your grill direct for heat grilling at 350-400 degrees. While the grill is heating up, go ahead and mix your gorgonzola, walnuts, cherries and thyme.?#&?#&Take a very sharp knife and cut a slit into the pork chops where the stuffing will go, using your finger to expand the opening. Stuff the pork chops with the cheese mixture and use a tooth pick to secure the opening so the stuffing doesn’t come out while grilling.?#&?#&Next, cut your apples. Combine the maple syrup and bourbon together in a cup. Sprinkle the apples with the cinnamon and then drizzle the maple bourbon syrup on top of the apples.?#&?#&Oil your grates with a high heat oil like avocado oil or vegetable oil so your food doesn’t stick to the grill grates. Grill the pork chops on direct heat, flipping halfway through, until the internal temp reaches 140 degrees with an internal read thermometer, this should take about 5-8 minutes depending on your grill. While grilling the pork chops, add the apple slices and let them grill until they develop char marks and have begun to caramelize on the grill.?#&?#&Once the pork chops have reached an internal temp of 140 degrees, pull them off the grill (they will continue to cook and reach 145 degrees after removed from the grill). Let them rest under foil for 5 minutes.?#&?#&Serve the pork chops with the grilled apples for sweet-salty pork pairing perfection!?#&?#&What is your favorite way to enjoy Pork Chops? Check out my recent Cuban inspired Mojo Pork, coming straight to you from my Miami Kitchen!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B03E27DFAAC35B44BDF20BFA146472E0', X'9147ED4AAF410F4BA39915493BE2DDE0', X'9147ED4AAF410F4BA39915493BE2DDE0', 0, 'https://grillgirl.com/2019/03/colombian-style-hot-dogs/', 'Colombian Style Hot Dogs', 2, NULL, 0, 'Colombian Style Hot Dogs', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/03/Colombian-Style-Hot-Dogs-RL-150x150.jpg', '', '', '5 minutes', '10 minutes', '15 minutes', '8 hot dogs- 2 per person', 'Ingredients?#&?#&8 hot dogs- I use use Nitrate Free Hot dogs?#&8 pack hot dog buns?#&1/4 cup Mayo?#&1/4 cup Mustard?#&¼ cup Ketchup?#&¼ cup Pineapple Preserves?#&2 cups Crushed Potato Chips (I like Ruffles chopped up bc they are extra crunchy)', '', 'https://grillgirl.com/2019/03/colombian-style-hot-dogs/', 'Grill', 'Colombian Style Hot Dogs', 'Mar 12, 2019', 'Latin, American', 'Instructions?#&?#&Grill hot dogs on Kingsford Charcoal until they are nice and charred on all sides. Next, spread the buns out and toast for a few minutes on the grill on the cooler side, being careful not to burn.?#&?#&Mix mayo, mustard and ketchup to make a “Pink sauce”. **I like to put this in a squeeze bottle to squeeze on top but whatever works for you!?#&?#&Place the hot dog in the bun and add a tbsp of pineapple preserves. Next, add pink sauce and then finish with crushed potato chips.?#&?#&This is an unexpectedly tasty combo of sweet, salty, smoky and crunch all in one bite!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'E9BDDD3BF6980F45B8B48BF1A8747508', X'09D3B4DA69F3C346AD8EB67AABCE1655', X'09D3B4DA69F3C346AD8EB67AABCE1655', 0, 'https://grillgirl.com/2018/12/grilled-shrimp-and-grits/', 'Grilled Shrimp and Grits', 2, NULL, 0, 'Grilled Shrimp and Grits', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/12/Grilled-Shrimp-and-Grits-150x150.jpeg', '', '', '5 minutes', '30-40 minutes', '45 minutes', '8 servings', 'Ingredients?#&?#&1 ½ lbs shrimp, deveined and shells removed ?#&?#&1 lb andouille sausage, chopped into ¼” pieces ?#&?#&1 8-oz bag red, yellow, green mini peppers ?#&?#&2 tbsp old bay seasoning ?#&?#&2 cups Grits- I am really loving Bob’s Red Mill Corn Grits/Polenta- very creamy and cook quickly?#&?#&1 qt organic reduced sodium chicken stock (or homemade!), plus 2 cups water ?#&?#&1/3 cup whole milk ?#&?#&1 tbsp butter ?#&?#&1 tbsp sea salt- or to taste ?#&?#&1 tsbp fresh ground pepper- or to taste ?#&?#&1 tbsp smoked paprika ?#&?#&1 cup cheddar, grated?#&?#&Chopped parsley, for garnish, optional?#&?#&Crystal Hot sauce', 'Southern', 'https://grillgirl.com/2018/12/grilled-shrimp-and-grits/', 'Grilling', 'Shrimp and Grits, easy shrimp and grits, low country style shrimp and grits, Southern Shrimp and Grits, Grilled Shrimp and Grits, Kingsford Charcoal, Robyn Lindars, GrillGirl', 'Dec 18, 2018', 'Brunch/Seafood', 'Instructions?#&?#&In a bowl, add olive oil, old bay and the shrimp and toss to coat.?#&?#&Meanwhile, start your grits: Heat quart of chicken broth and water in a large pot and bring to a boil. Add 2 cups of grits gradually over 2 minutes stirring constantly (grits will clump easily-also easily stick to bottom of pot!). Let the grits cook down on a medium simmer, stirring often, about 30 minutes.  While the grits are cooking, prepare your grill for cooking on direct heat. I recommend using your charcoal grill with Kingsford Charcoal to get smoky flavor.?#&?#&While your charcoal is heating up, continue to watch and stir your grits. Once they have reduced down, keep them on a very low simmer.?#&?#&Grill the shrimp, sausage and peppers until cooked through. The shrimp will cook most quickly- remove them as soon as they begin to turn pink. Remove the sausage once an internal temp of 145 degrees with a Thermapen has been reached and the peppers can be removed once they have developed char marks.?#&?#&Once the shrimp, peppers and sausage have cooked on the grill, remove them and set aside on a cutting board to let cool slightly so they can be chopped. Meanwhile, add the milk, cheese and butter to the grits and stir to combine and let thicken over a low simmer. Add salt, pepper, and smoked paprika to the grits.  Chop the peppers and sausage and remove the tails off the shrimp. Ladle grits ½ cup at a time into bowls. Add shrimp, peppers and sausage and garnish with chopped parsley.?#&?#&Serve with your favorite hot sauce, and additional salt and pepper. I hope you and your family enjoy this Southern inspired dish as much as mine does! Cheers!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'C93ECA525FADBD4DBF15263CD6FECAE8', X'CA02AE1994CF6B4F9340A6299DE966F4', X'CA02AE1994CF6B4F9340A6299DE966F4', 0, 'https://grillgirl.com/2018/11/pork-belly-burnt-ends-recipe/', 'Pork Belly Burnt Ends Recipe', 2, NULL, 0, 'Pork Belly Burnt Ends', 'Robyn Lindars', 'https://grillgirl.com/wp-content/uploads/2018/11/pork-belly-150x150.jpg', '', '', '30 min', '5 hours', '5 hours 30 minutes', '', 'Ingredients?#&?#&Large aluminum foil pan?#&5 lbs pork belly, cubed into 1 ½” pieces (I used Snake River Farms Kurobuta Pork Belly)?#&1 tbsp olive oil?#&1 cup All Purpose BBQ Rub (Jon Solberg’s rub is fantastic!)?#&?#&Pan Sauce:?#&?#&1 cup BBQ Sauce (Try this Jamaican Jerk BBQ Sauce recipe or just pick up some Stubb’s!)?#&1 tbsp bourbon?#&2 tbsp molasses?#&Dash hot sauce?#&3 tbsp butter', '', 'https://grillgirl.com/2018/11/pork-belly-burnt-ends-recipe/', '', '', 'Nov 13, 2018', '', 'Instructions?#&?#&Slice the pork belly into 1 ½” pieces. Place in a large bowl and toss in olive oil, then add rub and make sure all pieces are covered in it.?#&Smoke the pork belly pieces at 250 for 2 hours or until all the fat has rendered off and a crust is beginning to form.?#&Place the pork belly burnt ends in the foil pan. Combine the bbq sauce, bourbon, molasses, and hot sauce and pour over the burnt ends.?#&Smoke for another 2 hours covered in foil.?#&After 2 hours, remove the foil and let cook uncovered for another 30 minutes to thicken up the sauce.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'833F817213EC40408FF583C34D077515', X'9CEDF54376EF6E4BAB30B8330274D05F', X'9CEDF54376EF6E4BAB30B8330274D05F', 0, 'https://grillgirl.com/2018/07/big-green-egg-series-making-pulled-pork-in-your-egg/', 'Big Green Egg Series: Making Pulled Pork in Your Egg!', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2018/07/big-green-egg-series-making-pulled-pork-in-your-egg/', '', '', 'Jul 9, 2018', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'FE853F3E0BF5854C8A1E4B62236A5D7C', X'824479FE996B5A458436830E2273E95A', X'824479FE996B5A458436830E2273E95A', 0, 'https://grillgirl.com/2017/10/smoked-bologna-pit-barrel-cooker/', 'Smoked Bologna on the Pit Barrel Cooker', 2, NULL, 0, 'Smoked Bologna on the Pit Barrel Cooker', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/10/smoked-bologna-on-pitbarrel-cooker-edited-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One 3–5 lb chubb of beef bologna?#&PBC Beef & Game Pit Rub?#&2 tsp mustard (for rub binder)?#&1 loaf of Wonder Bread Texas Toast?#&1 Heirloom tomato?#&1 head of iceberg lettuce?#&1 Vidalia onion?#&1 jar of Dukes Mayonnaise?#&?#&BBQ GLAZE?#&?#& 2 Cups of KC Masterpiece Classic BBQ sauce?#&2/3 cups of apple juice?#&?#& In a small bowl mix the bbq sauce and apple juice until incorporated', '', 'https://grillgirl.com/2017/10/smoked-bologna-pit-barrel-cooker/', '', '', 'Oct 20, 2017', '', 'Instructions?#&?#&Remove exterior wrapper from the chubb of bologna. Take the chubb and lay it on its side. Take a paring knife and score diagonally about ¼” to 1/2”  deep in a criss cross method. This will give the meat some texture. Apply approximately 2 TBSP of mustard (amount depends on the size of your chubb) and massage into the meat.  Season with a moderate dusting of Beef & Game Pit Rub to all sides of the bologna.?#&?#&Light your Pit Barrel Cooker (PBC) according to the instructions. Oil the grate so the meat will not stick. Place the meat on its side on the grate. Add your preferred wood for smoke- I added cherry wood. Cook until you reach an internal temperature of 140 degrees as measured with an internal read thermometer (I recommend the Thermapen for accurate internal temperature readings). This should take approximately 2 ½ hrs depending on the weather conditions.?#&?#&Remove the bologna from the grate. Glaze with the BBQ glaze. Don’t overdo the sauce; use just enough to coat the bologna. Put the bologna back on the PBC and cook for 10-15 minutes. Just enough to set the glaze.?#&?#&Remove and loosely cover the bologna with aluminum foil while you prep for your bologna sandwich. Take two pieces of Wonder Texas Toast, lie them on the grate and cook with the lid off until you see some grill marks.  Slice the bologna to your desired thickness and then put mayonnaise, tomato, sliced onion and lettuce on your sandwich and enjoy.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'4492E49B903FFD4F8812D97F6A7DB5B8', X'8482ABFBF0B8F146BF25369BEE51FDD5', X'8482ABFBF0B8F146BF25369BEE51FDD5', 0, 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 2, NULL, 0, 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/10/Bacon-Pork-Guinness-Cassoulet-edited-150x100.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Dutch Oven or large aluminum pan to put on the grill?#&Smithfield Applewood Smoked Bacon Marinated Fresh Pork Loin Filet, sliced into 1/2” thick pieces for grilling?#&1/2 lb bacon, chopped into 1/2 inch pieces?#&2 15 oz cans navy beans, drained and rinsed?#&1 15 oz can kidney beans?#&1 medium sweet onion, sliced into rings for grilling?#&4 medium sized (I used Campari) tomatoes, sliced in half?#&2 carrots, chopped?#&2 celery sticks, chopped?#&2 cloves garlic, chopped?#&1 large bunch thyme?#&1–2 Guinness Beers or another Porter Style Beer?#&2 tbsp brown sugar?#&French Bread?#&Compound Butter (herbed butter)', '', 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', '', '', 'Oct 7, 2017', '', 'Instructions?#&?#&Heat your grill up, preparing it for medium direct cooking- at a temperature of approximately 350 degrees. This means you will be cooking directly over the fire!?#&?#&While the grill heats up go ahead and prepare your ingredients for the grill; chop the bacon, carrots and celery and garlic. Slice the tenderloin into 1/2 thick pieces, and slice onion into rings for the grill, cut the tomatoes in half. Get all your other ingredients ready to take outside- I usually put everything on a big cutting board and carry it outside and then get another plate for switching out.?#&?#&Once the grill is heated up, place the cast iron on one half of the grill and add the bacon and let it reduce. Meanwhile, with your existing grill surface area, go ahead and grill the pork tenderloin. This will only take a few minutes per side, remove once char marks have begun to form (remember these will also go into the stew and continue to cook). Next, grill the tomatoes and onions until char marks have formed, then remove from the grill.?#&?#&In the dutch oven, once the bacon fat has rendered, add the carrots and celery and let them cook in the bacon fat. Meanwhile, chop the onions and tomatoes and add to the dutch oven, stirring frequently to ensure no bacon is burning on the bottom. Now add the garlic. Next, add the first Guinness beer and deglaze the bottom of the pan. Add the sugar and stir. Now, chop the pork into small bite sized pieces and add to the dutch oven. Last but not least, add the beans and thyme and let the cassoulet simmer for 10 minutes or until the liquid has reduced. If you like a more soup like stew, add another 1/2 to full beer, depending on how much time you have to let simmer.?#&?#&While the Cassoulet simmers on the grill, slice the French bread into 1” thick pieces and slather with butter on both sides. Grill very quickly – no more than a minute- to get grill marks, ensuring you don’t let it burn.?#&?#&Serve the Cassoulet with the grilled bread and be prepared for big smiles on the faces of your family- like I said, this recipe is pure comfort food and expect to add this to your list of recipes you make again and again. Also great for freezing and giving to those in need of an easy meal- like friends and family going through an illness or just home with a new baby.?#&?#&If you give this recipe a try, please let me know your thoughts in the comments below! Also, when reheating you’ll notice that the beans soak up a lot of the liquid. You can add more beer if you want more stew like consistency before reheating.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'0DD0E090C1667B488B11DFA84C00CC5C', X'91EA8A64E7AC1743A018A573E5E4B21D', X'91EA8A64E7AC1743A018A573E5E4B21D', 0, 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 2, NULL, 0, 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/Hawaiian-Pork-Tenderloin-Sliders_1-150x150.jpg', '', '', '', '', '', 'Serves 4, 2 sliders per person', 'Ingredients?#&?#&1 Smithfield Roasted Garlic and Cracked Black Pepper Marinated Fresh Pork Tenderloin1 large pack Kings Hawaiian Rolls?#&1 small bag mini peppers?#&1 pineapple, cored and sliced into rings (you can also use canned pineapple)?#&1/4 cup mayo?#&1 tbsp Sriracha', '', 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', '', '', 'Jun 22, 2017', '', 'Instructions?#&?#&Preheat your grill to 350 degrees preparing a direct and indirect zone. While the grill is heating up, slice your pork tenderloin into 3/4” filets.?#&?#&Coat your grill grates with a little oil so the food does not stick (make sure to do this especially before grilling the pork filets).?#&?#&Grill the pineapple and peppers until char marks have formed and they have begun to caramelize. Remove and let cool.?#&?#&Next, grill the pork tenderloin filets- about 5 minutes max, or until the internal temp reads 145 degrees with an internal read thermometer (I recommend the thermapen).?#&On the indirect side, toast the rolls very quickly being careful to not let them burn.?#&?#&Slice the peppers and then combine the mayo with the sriracha and spread on the buns. Now, assemble your sliders with the tenderloin, pineapple and peppers.?#&?#&The sweetness of the pineapple and peppers in this recipe really compliment the spice of the Black Pepper marinated pork tenderloin, don’t be surprised that you’ll go back for seconds or thirds!?#&?#&What are your favorite recipes for getting dinner on the table in less than 30 minutes? Please share in the comments below!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'C603167A82E10449B40282467775933B', X'1DC5301FA828D945B72AFAEF3A78D6CB', X'1DC5301FA828D945B72AFAEF3A78D6CB', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'827E1B64C1181047B2E51B9719508E2E', X'3C62856AB9F9E449948AFAC3E7CFF094', X'3C62856AB9F9E449948AFAC3E7CFF094', 0, 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', 'Rum Pineapple Pork Ribs are Sweet and Tangy', 2, NULL, 0, 'Rum Pineapple Pork Ribs are Sweet and Tangy', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/08/rum-pineapple-ribs-150x150.jpeg', '', '', '20 minutes', '5 hours', '5 hours 20 minutes', '', 'Ingredients?#&?#&1 slab baby back ribs?#&Mustard?#&3 tbsp smoked all purpose rub ?#&1 pineapple, cored and sliced into 1/2” rings – should make about 9 rings total – 3 of which are used in the sauce, save juice for sauce (if using canned look for a corn syrup free, reduced sugar version)?#&?#&Texas Crutch?#&?#&1/4 cup apple juice?#&2 tbsp pineapple preserves?#&?#&Pineapple Rum BBQ Sauce?#&?#&1/3 cup BBQ sauce or your favorite store bought variety?#&3 tbsp pineapple preserves?#&1/4 cup Myers rum?#&3 tbsp pineapple juice?#&Dash allspice?#&3 slice pineapple, chopped', '', 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', '2-2-1 Method', 'pork ribs, baby back ribs, 2-2-1 ribs, smoked BBQ ribs, texas crutch, pork ribs recipe', 'Aug 29, 2019', 'Pork Ribs', 'Instructions?#&?#&Smoke Temp: 225F?#&?#&Smoking Woods: FRUIT WOODS- (apple, pecan, pear) OR mix with OAK, PECAN, MAPLE?#&?#&The ribs are going to use the 2-2-1 method: 2 hours in the smoker, 2 hours in foil, 1 hour letting the sauce glaze.?#&?#&Start by removing the membrane from the ribs. This is easier with a paper towel for gripping and a butter knife for separating from the meat.?#&Prepare your smoker for 225 degrees- this recipe can be made on any kind of smoker (and was originally created for my Healthy Electric Smoker Cookbook).?#&Next, slather with mustard so the rub can adhere. Apply the rub and smoke the meat for 2 hours.?#&After 2 hours, remove from the smoker and do the “Texas Crutch,” i.e., “wrap in foil”. Mix the apple juice and pineapple preserve and add to the foil packet. Let smoke another two hours.?#&Meanwhile, as the meat is smoking make your rum barbecue sauce. Combine the BBQ sauce, pineapple preserves, rum, chopped pineapple, pineapple juice and allspice. Let reduce down for 20 minutes on medium simmer.?#&During the last hour, remove the ribs from foil and glaze with the pineapple rum BBQ sauce.?#&Layer slices of pineapple on top of the ribs and drizzle more sauce over the pineapple.?#&Let smoke for an additional hour to let the sauce set. Serve immediately.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'78604040852BCD47B73045BEE11F8B10', X'09B4A1C8AEBA1945A4A1AA4C66025761', X'09B4A1C8AEBA1945A4A1AA4C66025761', 0, 'https://grillgirl.com/2019/06/gorgonzola-thyme-stuffed-pork-chops-with-bourbon-grilled-apples/', 'Gorgonzola Thyme Stuffed Pork Chops with Bourbon Grilled Apples', 2, NULL, 0, 'Gorgonzola Thyme Stuffed Pork Chops with Bourbon Grilled Apples', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/06/Stuffed-Pork-Chops-with-bourbone-grilled-apples-72-dpi-150x150.jpg', '', '', '20', '8', '28', '4 stuffed pork chops', 'Ingredients?#&?#&4 Smithfield Brown Sugar Boneless Center Cut Pork Chops – these are available at select grocery stores and retailers nationwide, including: Walmart, ShopRite, Food Lion, Publix, Albertson’s/Safeway and Save a Lot.?#&2 tbsp gorgonzola?#&2 tbsp chopped walnuts?#&2 tbsp chopped dried cherries?#&2 tbsp fresh thyme?#&toothpicks?#&?#&Grilled Bourbon Apples:?#&?#&4 apples, sliced with seeds removed?#&dash cinnamon?#&1 tbsp maple syrup?#&2 tbsp bourbon', 'American', 'https://grillgirl.com/2019/06/gorgonzola-thyme-stuffed-pork-chops-with-bourbon-grilled-apples/', 'Grill', 'Grilled Pork Chops, Grilled Center Cut Pork Chops, Stuffed Pork Chops with Grilled Apples, Gorgonzola Thyme Stuffed Pork Chops, Bourbon Grilled Apples, How to Grill Pork Chops', 'Jun 27, 2019', 'Pork, Grilled Pork Chops', 'Instructions?#&?#&Prepare your grill direct for heat grilling at 350-400 degrees. While the grill is heating up, go ahead and mix your gorgonzola, walnuts, cherries and thyme.?#&?#&Take a very sharp knife and cut a slit into the pork chops where the stuffing will go, using your finger to expand the opening. Stuff the pork chops with the cheese mixture and use a tooth pick to secure the opening so the stuffing doesn’t come out while grilling.?#&?#&Next, cut your apples. Combine the maple syrup and bourbon together in a cup. Sprinkle the apples with the cinnamon and then drizzle the maple bourbon syrup on top of the apples.?#&?#&Oil your grates with a high heat oil like avocado oil or vegetable oil so your food doesn’t stick to the grill grates. Grill the pork chops on direct heat, flipping halfway through, until the internal temp reaches 140 degrees with an internal read thermometer, this should take about 5-8 minutes depending on your grill. While grilling the pork chops, add the apple slices and let them grill until they develop char marks and have begun to caramelize on the grill.?#&?#&Once the pork chops have reached an internal temp of 140 degrees, pull them off the grill (they will continue to cook and reach 145 degrees after removed from the grill). Let them rest under foil for 5 minutes.?#&?#&Serve the pork chops with the grilled apples for sweet-salty pork pairing perfection!?#&?#&What is your favorite way to enjoy Pork Chops? Check out my recent Cuban inspired Mojo Pork, coming straight to you from my Miami Kitchen!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'39A01A5F1DF38645AAB08D632799A6CD', X'D3E22426C53B3B47B47BCD1D3A3197D8', X'D3E22426C53B3B47B47BCD1D3A3197D8', 0, 'https://grillgirl.com/2019/03/colombian-style-hot-dogs/', 'Colombian Style Hot Dogs', 2, NULL, 0, 'Colombian Style Hot Dogs', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/03/Colombian-Style-Hot-Dogs-RL-150x150.jpg', '', '', '5 minutes', '10 minutes', '15 minutes', '8 hot dogs- 2 per person', 'Ingredients?#&?#&8 hot dogs- I use use Nitrate Free Hot dogs?#&8 pack hot dog buns?#&1/4 cup Mayo?#&1/4 cup Mustard?#&¼ cup Ketchup?#&¼ cup Pineapple Preserves?#&2 cups Crushed Potato Chips (I like Ruffles chopped up bc they are extra crunchy)', '', 'https://grillgirl.com/2019/03/colombian-style-hot-dogs/', 'Grill', 'Colombian Style Hot Dogs', 'Mar 12, 2019', 'Latin, American', 'Instructions?#&?#&Grill hot dogs on Kingsford Charcoal until they are nice and charred on all sides. Next, spread the buns out and toast for a few minutes on the grill on the cooler side, being careful not to burn.?#&?#&Mix mayo, mustard and ketchup to make a “Pink sauce”. **I like to put this in a squeeze bottle to squeeze on top but whatever works for you!?#&?#&Place the hot dog in the bun and add a tbsp of pineapple preserves. Next, add pink sauce and then finish with crushed potato chips.?#&?#&This is an unexpectedly tasty combo of sweet, salty, smoky and crunch all in one bite!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'0E68A5D6AF77CC49A55435C550C2E263', X'BBA20270F87182488DE41220A57FF5C8', X'BBA20270F87182488DE41220A57FF5C8', 0, 'https://grillgirl.com/2018/12/grilled-shrimp-and-grits/', 'Grilled Shrimp and Grits', 2, NULL, 0, 'Grilled Shrimp and Grits', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/12/Grilled-Shrimp-and-Grits-150x150.jpeg', '', '', '5 minutes', '30-40 minutes', '45 minutes', '8 servings', 'Ingredients?#&?#&1 ½ lbs shrimp, deveined and shells removed ?#&?#&1 lb andouille sausage, chopped into ¼” pieces ?#&?#&1 8-oz bag red, yellow, green mini peppers ?#&?#&2 tbsp old bay seasoning ?#&?#&2 cups Grits- I am really loving Bob’s Red Mill Corn Grits/Polenta- very creamy and cook quickly?#&?#&1 qt organic reduced sodium chicken stock (or homemade!), plus 2 cups water ?#&?#&1/3 cup whole milk ?#&?#&1 tbsp butter ?#&?#&1 tbsp sea salt- or to taste ?#&?#&1 tsbp fresh ground pepper- or to taste ?#&?#&1 tbsp smoked paprika ?#&?#&1 cup cheddar, grated?#&?#&Chopped parsley, for garnish, optional?#&?#&Crystal Hot sauce', 'Southern', 'https://grillgirl.com/2018/12/grilled-shrimp-and-grits/', 'Grilling', 'Shrimp and Grits, easy shrimp and grits, low country style shrimp and grits, Southern Shrimp and Grits, Grilled Shrimp and Grits, Kingsford Charcoal, Robyn Lindars, GrillGirl', 'Dec 18, 2018', 'Brunch/Seafood', 'Instructions?#&?#&In a bowl, add olive oil, old bay and the shrimp and toss to coat.?#&?#&Meanwhile, start your grits: Heat quart of chicken broth and water in a large pot and bring to a boil. Add 2 cups of grits gradually over 2 minutes stirring constantly (grits will clump easily-also easily stick to bottom of pot!). Let the grits cook down on a medium simmer, stirring often, about 30 minutes.  While the grits are cooking, prepare your grill for cooking on direct heat. I recommend using your charcoal grill with Kingsford Charcoal to get smoky flavor.?#&?#&While your charcoal is heating up, continue to watch and stir your grits. Once they have reduced down, keep them on a very low simmer.?#&?#&Grill the shrimp, sausage and peppers until cooked through. The shrimp will cook most quickly- remove them as soon as they begin to turn pink. Remove the sausage once an internal temp of 145 degrees with a Thermapen has been reached and the peppers can be removed once they have developed char marks.?#&?#&Once the shrimp, peppers and sausage have cooked on the grill, remove them and set aside on a cutting board to let cool slightly so they can be chopped. Meanwhile, add the milk, cheese and butter to the grits and stir to combine and let thicken over a low simmer. Add salt, pepper, and smoked paprika to the grits.  Chop the peppers and sausage and remove the tails off the shrimp. Ladle grits ½ cup at a time into bowls. Add shrimp, peppers and sausage and garnish with chopped parsley.?#&?#&Serve with your favorite hot sauce, and additional salt and pepper. I hope you and your family enjoy this Southern inspired dish as much as mine does! Cheers!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'8EDDCC40F48DD14099BA823A447EB52D', X'93FA4ACC71ECE04B9F83646723DB728F', X'93FA4ACC71ECE04B9F83646723DB728F', 0, 'https://grillgirl.com/2018/11/pork-belly-burnt-ends-recipe/', 'Pork Belly Burnt Ends Recipe', 2, NULL, 0, 'Pork Belly Burnt Ends', 'Robyn Lindars', 'https://grillgirl.com/wp-content/uploads/2018/11/pork-belly-150x150.jpg', '', '', '30 min', '5 hours', '5 hours 30 minutes', '', 'Ingredients?#&?#&Large aluminum foil pan?#&5 lbs pork belly, cubed into 1 ½” pieces (I used Snake River Farms Kurobuta Pork Belly)?#&1 tbsp olive oil?#&1 cup All Purpose BBQ Rub (Jon Solberg’s rub is fantastic!)?#&?#&Pan Sauce:?#&?#&1 cup BBQ Sauce (Try this Jamaican Jerk BBQ Sauce recipe or just pick up some Stubb’s!)?#&1 tbsp bourbon?#&2 tbsp molasses?#&Dash hot sauce?#&3 tbsp butter', '', 'https://grillgirl.com/2018/11/pork-belly-burnt-ends-recipe/', '', '', 'Nov 13, 2018', '', 'Instructions?#&?#&Slice the pork belly into 1 ½” pieces. Place in a large bowl and toss in olive oil, then add rub and make sure all pieces are covered in it.?#&Smoke the pork belly pieces at 250 for 2 hours or until all the fat has rendered off and a crust is beginning to form.?#&Place the pork belly burnt ends in the foil pan. Combine the bbq sauce, bourbon, molasses, and hot sauce and pour over the burnt ends.?#&Smoke for another 2 hours covered in foil.?#&After 2 hours, remove the foil and let cook uncovered for another 30 minutes to thicken up the sauce.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'C2A685F920E01448A8A0973F50E3A42B', X'E73A1102D543784A89A831CAB20A65E5', X'E73A1102D543784A89A831CAB20A65E5', 0, 'https://grillgirl.com/2018/07/big-green-egg-series-making-pulled-pork-in-your-egg/', 'Big Green Egg Series: Making Pulled Pork in Your Egg!', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2018/07/big-green-egg-series-making-pulled-pork-in-your-egg/', '', '', 'Jul 9, 2018', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'5CACF74A3735D342B6D6825F9CCBF0CB', X'CCB467F004749B47A4B55AB11CE03E31', X'CCB467F004749B47A4B55AB11CE03E31', 0, 'https://grillgirl.com/2017/10/smoked-bologna-pit-barrel-cooker/', 'Smoked Bologna on the Pit Barrel Cooker', 2, NULL, 0, 'Smoked Bologna on the Pit Barrel Cooker', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/10/smoked-bologna-on-pitbarrel-cooker-edited-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One 3–5 lb chubb of beef bologna?#&PBC Beef & Game Pit Rub?#&2 tsp mustard (for rub binder)?#&1 loaf of Wonder Bread Texas Toast?#&1 Heirloom tomato?#&1 head of iceberg lettuce?#&1 Vidalia onion?#&1 jar of Dukes Mayonnaise?#&?#&BBQ GLAZE?#&?#& 2 Cups of KC Masterpiece Classic BBQ sauce?#&2/3 cups of apple juice?#&?#& In a small bowl mix the bbq sauce and apple juice until incorporated', '', 'https://grillgirl.com/2017/10/smoked-bologna-pit-barrel-cooker/', '', '', 'Oct 20, 2017', '', 'Instructions?#&?#&Remove exterior wrapper from the chubb of bologna. Take the chubb and lay it on its side. Take a paring knife and score diagonally about ¼” to 1/2”  deep in a criss cross method. This will give the meat some texture. Apply approximately 2 TBSP of mustard (amount depends on the size of your chubb) and massage into the meat.  Season with a moderate dusting of Beef & Game Pit Rub to all sides of the bologna.?#&?#&Light your Pit Barrel Cooker (PBC) according to the instructions. Oil the grate so the meat will not stick. Place the meat on its side on the grate. Add your preferred wood for smoke- I added cherry wood. Cook until you reach an internal temperature of 140 degrees as measured with an internal read thermometer (I recommend the Thermapen for accurate internal temperature readings). This should take approximately 2 ½ hrs depending on the weather conditions.?#&?#&Remove the bologna from the grate. Glaze with the BBQ glaze. Don’t overdo the sauce; use just enough to coat the bologna. Put the bologna back on the PBC and cook for 10-15 minutes. Just enough to set the glaze.?#&?#&Remove and loosely cover the bologna with aluminum foil while you prep for your bologna sandwich. Take two pieces of Wonder Texas Toast, lie them on the grate and cook with the lid off until you see some grill marks.  Slice the bologna to your desired thickness and then put mayonnaise, tomato, sliced onion and lettuce on your sandwich and enjoy.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'BA60E0A4B2592B4F8DC85270F863C4B2', X'0725524F6A92C8499E733D5D4237EA9F', X'0725524F6A92C8499E733D5D4237EA9F', 0, 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 2, NULL, 0, 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/10/Bacon-Pork-Guinness-Cassoulet-edited-150x100.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Dutch Oven or large aluminum pan to put on the grill?#&Smithfield Applewood Smoked Bacon Marinated Fresh Pork Loin Filet, sliced into 1/2” thick pieces for grilling?#&1/2 lb bacon, chopped into 1/2 inch pieces?#&2 15 oz cans navy beans, drained and rinsed?#&1 15 oz can kidney beans?#&1 medium sweet onion, sliced into rings for grilling?#&4 medium sized (I used Campari) tomatoes, sliced in half?#&2 carrots, chopped?#&2 celery sticks, chopped?#&2 cloves garlic, chopped?#&1 large bunch thyme?#&1–2 Guinness Beers or another Porter Style Beer?#&2 tbsp brown sugar?#&French Bread?#&Compound Butter (herbed butter)', '', 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', '', '', 'Oct 7, 2017', '', 'Instructions?#&?#&Heat your grill up, preparing it for medium direct cooking- at a temperature of approximately 350 degrees. This means you will be cooking directly over the fire!?#&?#&While the grill heats up go ahead and prepare your ingredients for the grill; chop the bacon, carrots and celery and garlic. Slice the tenderloin into 1/2 thick pieces, and slice onion into rings for the grill, cut the tomatoes in half. Get all your other ingredients ready to take outside- I usually put everything on a big cutting board and carry it outside and then get another plate for switching out.?#&?#&Once the grill is heated up, place the cast iron on one half of the grill and add the bacon and let it reduce. Meanwhile, with your existing grill surface area, go ahead and grill the pork tenderloin. This will only take a few minutes per side, remove once char marks have begun to form (remember these will also go into the stew and continue to cook). Next, grill the tomatoes and onions until char marks have formed, then remove from the grill.?#&?#&In the dutch oven, once the bacon fat has rendered, add the carrots and celery and let them cook in the bacon fat. Meanwhile, chop the onions and tomatoes and add to the dutch oven, stirring frequently to ensure no bacon is burning on the bottom. Now add the garlic. Next, add the first Guinness beer and deglaze the bottom of the pan. Add the sugar and stir. Now, chop the pork into small bite sized pieces and add to the dutch oven. Last but not least, add the beans and thyme and let the cassoulet simmer for 10 minutes or until the liquid has reduced. If you like a more soup like stew, add another 1/2 to full beer, depending on how much time you have to let simmer.?#&?#&While the Cassoulet simmers on the grill, slice the French bread into 1” thick pieces and slather with butter on both sides. Grill very quickly – no more than a minute- to get grill marks, ensuring you don’t let it burn.?#&?#&Serve the Cassoulet with the grilled bread and be prepared for big smiles on the faces of your family- like I said, this recipe is pure comfort food and expect to add this to your list of recipes you make again and again. Also great for freezing and giving to those in need of an easy meal- like friends and family going through an illness or just home with a new baby.?#&?#&If you give this recipe a try, please let me know your thoughts in the comments below! Also, when reheating you’ll notice that the beans soak up a lot of the liquid. You can add more beer if you want more stew like consistency before reheating.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'0C2988F1DFED4C4985714B1784017C1D', X'A9E9F304B5EEC84899172DC5CDA2ECFA', X'A9E9F304B5EEC84899172DC5CDA2ECFA', 0, 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 2, NULL, 0, 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/Hawaiian-Pork-Tenderloin-Sliders_1-150x150.jpg', '', '', '', '', '', 'Serves 4, 2 sliders per person', 'Ingredients?#&?#&1 Smithfield Roasted Garlic and Cracked Black Pepper Marinated Fresh Pork Tenderloin1 large pack Kings Hawaiian Rolls?#&1 small bag mini peppers?#&1 pineapple, cored and sliced into rings (you can also use canned pineapple)?#&1/4 cup mayo?#&1 tbsp Sriracha', '', 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', '', '', 'Jun 22, 2017', '', 'Instructions?#&?#&Preheat your grill to 350 degrees preparing a direct and indirect zone. While the grill is heating up, slice your pork tenderloin into 3/4” filets.?#&?#&Coat your grill grates with a little oil so the food does not stick (make sure to do this especially before grilling the pork filets).?#&?#&Grill the pineapple and peppers until char marks have formed and they have begun to caramelize. Remove and let cool.?#&?#&Next, grill the pork tenderloin filets- about 5 minutes max, or until the internal temp reads 145 degrees with an internal read thermometer (I recommend the thermapen).?#&On the indirect side, toast the rolls very quickly being careful to not let them burn.?#&?#&Slice the peppers and then combine the mayo with the sriracha and spread on the buns. Now, assemble your sliders with the tenderloin, pineapple and peppers.?#&?#&The sweetness of the pineapple and peppers in this recipe really compliment the spice of the Black Pepper marinated pork tenderloin, don’t be surprised that you’ll go back for seconds or thirds!?#&?#&What are your favorite recipes for getting dinner on the table in less than 30 minutes? Please share in the comments below!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'FAF6BCE2D53ED54A8CF90F6A55282E12', X'2DE82756CC54B149BA16A44D9D20371E', X'2DE82756CC54B149BA16A44D9D20371E', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'AE8105AF2BCE7F4292CD6BA838F4D333', X'9CF877B80B6A76428F44AB7C45B26787', X'9CF877B80B6A76428F44AB7C45B26787', 0, 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', 'Rum Pineapple Pork Ribs are Sweet and Tangy', 2, NULL, 0, 'Rum Pineapple Pork Ribs are Sweet and Tangy', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/08/rum-pineapple-ribs-150x150.jpeg', '', '', '20 minutes', '5 hours', '5 hours 20 minutes', '', 'Ingredients?#&?#&1 slab baby back ribs?#&Mustard?#&3 tbsp smoked all purpose rub ?#&1 pineapple, cored and sliced into 1/2” rings – should make about 9 rings total – 3 of which are used in the sauce, save juice for sauce (if using canned look for a corn syrup free, reduced sugar version)?#&?#&Texas Crutch?#&?#&1/4 cup apple juice?#&2 tbsp pineapple preserves?#&?#&Pineapple Rum BBQ Sauce?#&?#&1/3 cup BBQ sauce or your favorite store bought variety?#&3 tbsp pineapple preserves?#&1/4 cup Myers rum?#&3 tbsp pineapple juice?#&Dash allspice?#&3 slice pineapple, chopped', '', 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', '2-2-1 Method', 'pork ribs, baby back ribs, 2-2-1 ribs, smoked BBQ ribs, texas crutch, pork ribs recipe', 'Aug 29, 2019', 'Pork Ribs', 'Instructions?#&?#&Smoke Temp: 225F?#&?#&Smoking Woods: FRUIT WOODS- (apple, pecan, pear) OR mix with OAK, PECAN, MAPLE?#&?#&The ribs are going to use the 2-2-1 method: 2 hours in the smoker, 2 hours in foil, 1 hour letting the sauce glaze.?#&?#&Start by removing the membrane from the ribs. This is easier with a paper towel for gripping and a butter knife for separating from the meat.?#&Prepare your smoker for 225 degrees- this recipe can be made on any kind of smoker (and was originally created for my Healthy Electric Smoker Cookbook).?#&Next, slather with mustard so the rub can adhere. Apply the rub and smoke the meat for 2 hours.?#&After 2 hours, remove from the smoker and do the “Texas Crutch,” i.e., “wrap in foil”. Mix the apple juice and pineapple preserve and add to the foil packet. Let smoke another two hours.?#&Meanwhile, as the meat is smoking make your rum barbecue sauce. Combine the BBQ sauce, pineapple preserves, rum, chopped pineapple, pineapple juice and allspice. Let reduce down for 20 minutes on medium simmer.?#&During the last hour, remove the ribs from foil and glaze with the pineapple rum BBQ sauce.?#&Layer slices of pineapple on top of the ribs and drizzle more sauce over the pineapple.?#&Let smoke for an additional hour to let the sauce set. Serve immediately.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'BEAAC342172E0B46AFBC774AC1D58BFD', X'48B73AA5B457FA49B39BE2CF4BD4FE88', X'48B73AA5B457FA49B39BE2CF4BD4FE88', 0, 'https://grillgirl.com/2019/06/gorgonzola-thyme-stuffed-pork-chops-with-bourbon-grilled-apples/', 'Gorgonzola Thyme Stuffed Pork Chops with Bourbon Grilled Apples', 2, NULL, 0, 'Gorgonzola Thyme Stuffed Pork Chops with Bourbon Grilled Apples', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/06/Stuffed-Pork-Chops-with-bourbone-grilled-apples-72-dpi-150x150.jpg', '', '', '20', '8', '28', '4 stuffed pork chops', 'Ingredients?#&?#&4 Smithfield Brown Sugar Boneless Center Cut Pork Chops – these are available at select grocery stores and retailers nationwide, including: Walmart, ShopRite, Food Lion, Publix, Albertson’s/Safeway and Save a Lot.?#&2 tbsp gorgonzola?#&2 tbsp chopped walnuts?#&2 tbsp chopped dried cherries?#&2 tbsp fresh thyme?#&toothpicks?#&?#&Grilled Bourbon Apples:?#&?#&4 apples, sliced with seeds removed?#&dash cinnamon?#&1 tbsp maple syrup?#&2 tbsp bourbon', 'American', 'https://grillgirl.com/2019/06/gorgonzola-thyme-stuffed-pork-chops-with-bourbon-grilled-apples/', 'Grill', 'Grilled Pork Chops, Grilled Center Cut Pork Chops, Stuffed Pork Chops with Grilled Apples, Gorgonzola Thyme Stuffed Pork Chops, Bourbon Grilled Apples, How to Grill Pork Chops', 'Jun 27, 2019', 'Pork, Grilled Pork Chops', 'Instructions?#&?#&Prepare your grill direct for heat grilling at 350-400 degrees. While the grill is heating up, go ahead and mix your gorgonzola, walnuts, cherries and thyme.?#&?#&Take a very sharp knife and cut a slit into the pork chops where the stuffing will go, using your finger to expand the opening. Stuff the pork chops with the cheese mixture and use a tooth pick to secure the opening so the stuffing doesn’t come out while grilling.?#&?#&Next, cut your apples. Combine the maple syrup and bourbon together in a cup. Sprinkle the apples with the cinnamon and then drizzle the maple bourbon syrup on top of the apples.?#&?#&Oil your grates with a high heat oil like avocado oil or vegetable oil so your food doesn’t stick to the grill grates. Grill the pork chops on direct heat, flipping halfway through, until the internal temp reaches 140 degrees with an internal read thermometer, this should take about 5-8 minutes depending on your grill. While grilling the pork chops, add the apple slices and let them grill until they develop char marks and have begun to caramelize on the grill.?#&?#&Once the pork chops have reached an internal temp of 140 degrees, pull them off the grill (they will continue to cook and reach 145 degrees after removed from the grill). Let them rest under foil for 5 minutes.?#&?#&Serve the pork chops with the grilled apples for sweet-salty pork pairing perfection!?#&?#&What is your favorite way to enjoy Pork Chops? Check out my recent Cuban inspired Mojo Pork, coming straight to you from my Miami Kitchen!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2072F69FCDA54144A9FA94AE84DA8E1C', X'E1FA9CE96E18814C92EF5AE47104AF54', X'E1FA9CE96E18814C92EF5AE47104AF54', 0, 'https://grillgirl.com/2019/03/colombian-style-hot-dogs/', 'Colombian Style Hot Dogs', 2, NULL, 0, 'Colombian Style Hot Dogs', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/03/Colombian-Style-Hot-Dogs-RL-150x150.jpg', '', '', '5 minutes', '10 minutes', '15 minutes', '8 hot dogs- 2 per person', 'Ingredients?#&?#&8 hot dogs- I use use Nitrate Free Hot dogs?#&8 pack hot dog buns?#&1/4 cup Mayo?#&1/4 cup Mustard?#&¼ cup Ketchup?#&¼ cup Pineapple Preserves?#&2 cups Crushed Potato Chips (I like Ruffles chopped up bc they are extra crunchy)', '', 'https://grillgirl.com/2019/03/colombian-style-hot-dogs/', 'Grill', 'Colombian Style Hot Dogs', 'Mar 12, 2019', 'Latin, American', 'Instructions?#&?#&Grill hot dogs on Kingsford Charcoal until they are nice and charred on all sides. Next, spread the buns out and toast for a few minutes on the grill on the cooler side, being careful not to burn.?#&?#&Mix mayo, mustard and ketchup to make a “Pink sauce”. **I like to put this in a squeeze bottle to squeeze on top but whatever works for you!?#&?#&Place the hot dog in the bun and add a tbsp of pineapple preserves. Next, add pink sauce and then finish with crushed potato chips.?#&?#&This is an unexpectedly tasty combo of sweet, salty, smoky and crunch all in one bite!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'EF6A6222DE968740ACEEB75C8022EE6A', X'A1215FDAAD118542A71B99F9BABFBFEE', X'A1215FDAAD118542A71B99F9BABFBFEE', 0, 'https://grillgirl.com/2018/12/grilled-shrimp-and-grits/', 'Grilled Shrimp and Grits', 2, NULL, 0, 'Grilled Shrimp and Grits', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/12/Grilled-Shrimp-and-Grits-150x150.jpeg', '', '', '5 minutes', '30-40 minutes', '45 minutes', '8 servings', 'Ingredients?#&?#&1 ½ lbs shrimp, deveined and shells removed ?#&?#&1 lb andouille sausage, chopped into ¼” pieces ?#&?#&1 8-oz bag red, yellow, green mini peppers ?#&?#&2 tbsp old bay seasoning ?#&?#&2 cups Grits- I am really loving Bob’s Red Mill Corn Grits/Polenta- very creamy and cook quickly?#&?#&1 qt organic reduced sodium chicken stock (or homemade!), plus 2 cups water ?#&?#&1/3 cup whole milk ?#&?#&1 tbsp butter ?#&?#&1 tbsp sea salt- or to taste ?#&?#&1 tsbp fresh ground pepper- or to taste ?#&?#&1 tbsp smoked paprika ?#&?#&1 cup cheddar, grated?#&?#&Chopped parsley, for garnish, optional?#&?#&Crystal Hot sauce', 'Southern', 'https://grillgirl.com/2018/12/grilled-shrimp-and-grits/', 'Grilling', 'Shrimp and Grits, easy shrimp and grits, low country style shrimp and grits, Southern Shrimp and Grits, Grilled Shrimp and Grits, Kingsford Charcoal, Robyn Lindars, GrillGirl', 'Dec 18, 2018', 'Brunch/Seafood', 'Instructions?#&?#&In a bowl, add olive oil, old bay and the shrimp and toss to coat.?#&?#&Meanwhile, start your grits: Heat quart of chicken broth and water in a large pot and bring to a boil. Add 2 cups of grits gradually over 2 minutes stirring constantly (grits will clump easily-also easily stick to bottom of pot!). Let the grits cook down on a medium simmer, stirring often, about 30 minutes.  While the grits are cooking, prepare your grill for cooking on direct heat. I recommend using your charcoal grill with Kingsford Charcoal to get smoky flavor.?#&?#&While your charcoal is heating up, continue to watch and stir your grits. Once they have reduced down, keep them on a very low simmer.?#&?#&Grill the shrimp, sausage and peppers until cooked through. The shrimp will cook most quickly- remove them as soon as they begin to turn pink. Remove the sausage once an internal temp of 145 degrees with a Thermapen has been reached and the peppers can be removed once they have developed char marks.?#&?#&Once the shrimp, peppers and sausage have cooked on the grill, remove them and set aside on a cutting board to let cool slightly so they can be chopped. Meanwhile, add the milk, cheese and butter to the grits and stir to combine and let thicken over a low simmer. Add salt, pepper, and smoked paprika to the grits.  Chop the peppers and sausage and remove the tails off the shrimp. Ladle grits ½ cup at a time into bowls. Add shrimp, peppers and sausage and garnish with chopped parsley.?#&?#&Serve with your favorite hot sauce, and additional salt and pepper. I hope you and your family enjoy this Southern inspired dish as much as mine does! Cheers!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'22F11ABF4A1F5940AA79CB3F6682FBC9', X'F54B72F18F3213459F9DBBAA3FA31E47', X'F54B72F18F3213459F9DBBAA3FA31E47', 0, 'https://grillgirl.com/2018/11/pork-belly-burnt-ends-recipe/', 'Pork Belly Burnt Ends Recipe', 2, NULL, 0, 'Pork Belly Burnt Ends', 'Robyn Lindars', 'https://grillgirl.com/wp-content/uploads/2018/11/pork-belly-150x150.jpg', '', '', '30 min', '5 hours', '5 hours 30 minutes', '', 'Ingredients?#&?#&Large aluminum foil pan?#&5 lbs pork belly, cubed into 1 ½” pieces (I used Snake River Farms Kurobuta Pork Belly)?#&1 tbsp olive oil?#&1 cup All Purpose BBQ Rub (Jon Solberg’s rub is fantastic!)?#&?#&Pan Sauce:?#&?#&1 cup BBQ Sauce (Try this Jamaican Jerk BBQ Sauce recipe or just pick up some Stubb’s!)?#&1 tbsp bourbon?#&2 tbsp molasses?#&Dash hot sauce?#&3 tbsp butter', '', 'https://grillgirl.com/2018/11/pork-belly-burnt-ends-recipe/', '', '', 'Nov 13, 2018', '', 'Instructions?#&?#&Slice the pork belly into 1 ½” pieces. Place in a large bowl and toss in olive oil, then add rub and make sure all pieces are covered in it.?#&Smoke the pork belly pieces at 250 for 2 hours or until all the fat has rendered off and a crust is beginning to form.?#&Place the pork belly burnt ends in the foil pan. Combine the bbq sauce, bourbon, molasses, and hot sauce and pour over the burnt ends.?#&Smoke for another 2 hours covered in foil.?#&After 2 hours, remove the foil and let cook uncovered for another 30 minutes to thicken up the sauce.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'E595E49F7502EE48B3DF4559B19A484C', X'B4E655846026804CB3B4592ECA3AD14B', X'B4E655846026804CB3B4592ECA3AD14B', 0, 'https://grillgirl.com/2018/07/big-green-egg-series-making-pulled-pork-in-your-egg/', 'Big Green Egg Series: Making Pulled Pork in Your Egg!', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2018/07/big-green-egg-series-making-pulled-pork-in-your-egg/', '', '', 'Jul 9, 2018', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'CED064FA71B36942BF7771AFFC6E9A53', X'7EF8F515229C5F47969C187D9E892E4D', X'7EF8F515229C5F47969C187D9E892E4D', 0, 'https://grillgirl.com/2017/10/smoked-bologna-pit-barrel-cooker/', 'Smoked Bologna on the Pit Barrel Cooker', 2, NULL, 0, 'Smoked Bologna on the Pit Barrel Cooker', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/10/smoked-bologna-on-pitbarrel-cooker-edited-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One 3–5 lb chubb of beef bologna?#&PBC Beef & Game Pit Rub?#&2 tsp mustard (for rub binder)?#&1 loaf of Wonder Bread Texas Toast?#&1 Heirloom tomato?#&1 head of iceberg lettuce?#&1 Vidalia onion?#&1 jar of Dukes Mayonnaise?#&?#&BBQ GLAZE?#&?#& 2 Cups of KC Masterpiece Classic BBQ sauce?#&2/3 cups of apple juice?#&?#& In a small bowl mix the bbq sauce and apple juice until incorporated', '', 'https://grillgirl.com/2017/10/smoked-bologna-pit-barrel-cooker/', '', '', 'Oct 20, 2017', '', 'Instructions?#&?#&Remove exterior wrapper from the chubb of bologna. Take the chubb and lay it on its side. Take a paring knife and score diagonally about ¼” to 1/2”  deep in a criss cross method. This will give the meat some texture. Apply approximately 2 TBSP of mustard (amount depends on the size of your chubb) and massage into the meat.  Season with a moderate dusting of Beef & Game Pit Rub to all sides of the bologna.?#&?#&Light your Pit Barrel Cooker (PBC) according to the instructions. Oil the grate so the meat will not stick. Place the meat on its side on the grate. Add your preferred wood for smoke- I added cherry wood. Cook until you reach an internal temperature of 140 degrees as measured with an internal read thermometer (I recommend the Thermapen for accurate internal temperature readings). This should take approximately 2 ½ hrs depending on the weather conditions.?#&?#&Remove the bologna from the grate. Glaze with the BBQ glaze. Don’t overdo the sauce; use just enough to coat the bologna. Put the bologna back on the PBC and cook for 10-15 minutes. Just enough to set the glaze.?#&?#&Remove and loosely cover the bologna with aluminum foil while you prep for your bologna sandwich. Take two pieces of Wonder Texas Toast, lie them on the grate and cook with the lid off until you see some grill marks.  Slice the bologna to your desired thickness and then put mayonnaise, tomato, sliced onion and lettuce on your sandwich and enjoy.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'DAD0CB613A718C409261F9BF8D0ABE48', X'4FFE3471F4877547B49061E87A023940', X'4FFE3471F4877547B49061E87A023940', 0, 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 2, NULL, 0, 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/10/Bacon-Pork-Guinness-Cassoulet-edited-150x100.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Dutch Oven or large aluminum pan to put on the grill?#&Smithfield Applewood Smoked Bacon Marinated Fresh Pork Loin Filet, sliced into 1/2” thick pieces for grilling?#&1/2 lb bacon, chopped into 1/2 inch pieces?#&2 15 oz cans navy beans, drained and rinsed?#&1 15 oz can kidney beans?#&1 medium sweet onion, sliced into rings for grilling?#&4 medium sized (I used Campari) tomatoes, sliced in half?#&2 carrots, chopped?#&2 celery sticks, chopped?#&2 cloves garlic, chopped?#&1 large bunch thyme?#&1–2 Guinness Beers or another Porter Style Beer?#&2 tbsp brown sugar?#&French Bread?#&Compound Butter (herbed butter)', '', 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', '', '', 'Oct 7, 2017', '', 'Instructions?#&?#&Heat your grill up, preparing it for medium direct cooking- at a temperature of approximately 350 degrees. This means you will be cooking directly over the fire!?#&?#&While the grill heats up go ahead and prepare your ingredients for the grill; chop the bacon, carrots and celery and garlic. Slice the tenderloin into 1/2 thick pieces, and slice onion into rings for the grill, cut the tomatoes in half. Get all your other ingredients ready to take outside- I usually put everything on a big cutting board and carry it outside and then get another plate for switching out.?#&?#&Once the grill is heated up, place the cast iron on one half of the grill and add the bacon and let it reduce. Meanwhile, with your existing grill surface area, go ahead and grill the pork tenderloin. This will only take a few minutes per side, remove once char marks have begun to form (remember these will also go into the stew and continue to cook). Next, grill the tomatoes and onions until char marks have formed, then remove from the grill.?#&?#&In the dutch oven, once the bacon fat has rendered, add the carrots and celery and let them cook in the bacon fat. Meanwhile, chop the onions and tomatoes and add to the dutch oven, stirring frequently to ensure no bacon is burning on the bottom. Now add the garlic. Next, add the first Guinness beer and deglaze the bottom of the pan. Add the sugar and stir. Now, chop the pork into small bite sized pieces and add to the dutch oven. Last but not least, add the beans and thyme and let the cassoulet simmer for 10 minutes or until the liquid has reduced. If you like a more soup like stew, add another 1/2 to full beer, depending on how much time you have to let simmer.?#&?#&While the Cassoulet simmers on the grill, slice the French bread into 1” thick pieces and slather with butter on both sides. Grill very quickly – no more than a minute- to get grill marks, ensuring you don’t let it burn.?#&?#&Serve the Cassoulet with the grilled bread and be prepared for big smiles on the faces of your family- like I said, this recipe is pure comfort food and expect to add this to your list of recipes you make again and again. Also great for freezing and giving to those in need of an easy meal- like friends and family going through an illness or just home with a new baby.?#&?#&If you give this recipe a try, please let me know your thoughts in the comments below! Also, when reheating you’ll notice that the beans soak up a lot of the liquid. You can add more beer if you want more stew like consistency before reheating.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'82B5861A5C8E704F88CD488BC553FACE', X'760211085F62D54898A8EC5C40BF3F5C', X'760211085F62D54898A8EC5C40BF3F5C', 0, 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 2, NULL, 0, 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/Hawaiian-Pork-Tenderloin-Sliders_1-150x150.jpg', '', '', '', '', '', 'Serves 4, 2 sliders per person', 'Ingredients?#&?#&1 Smithfield Roasted Garlic and Cracked Black Pepper Marinated Fresh Pork Tenderloin1 large pack Kings Hawaiian Rolls?#&1 small bag mini peppers?#&1 pineapple, cored and sliced into rings (you can also use canned pineapple)?#&1/4 cup mayo?#&1 tbsp Sriracha', '', 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', '', '', 'Jun 22, 2017', '', 'Instructions?#&?#&Preheat your grill to 350 degrees preparing a direct and indirect zone. While the grill is heating up, slice your pork tenderloin into 3/4” filets.?#&?#&Coat your grill grates with a little oil so the food does not stick (make sure to do this especially before grilling the pork filets).?#&?#&Grill the pineapple and peppers until char marks have formed and they have begun to caramelize. Remove and let cool.?#&?#&Next, grill the pork tenderloin filets- about 5 minutes max, or until the internal temp reads 145 degrees with an internal read thermometer (I recommend the thermapen).?#&On the indirect side, toast the rolls very quickly being careful to not let them burn.?#&?#&Slice the peppers and then combine the mayo with the sriracha and spread on the buns. Now, assemble your sliders with the tenderloin, pineapple and peppers.?#&?#&The sweetness of the pineapple and peppers in this recipe really compliment the spice of the Black Pepper marinated pork tenderloin, don’t be surprised that you’ll go back for seconds or thirds!?#&?#&What are your favorite recipes for getting dinner on the table in less than 30 minutes? Please share in the comments below!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'C454E2C28E103A4AAA77D11120A1CE00', X'75C00512D7A7724DAF0AD55C76B75210', X'75C00512D7A7724DAF0AD55C76B75210', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'079BDF075590C44CB7D0A9119E40C470', X'00F579872983F2448E2A434BB6EF68BE', X'00F579872983F2448E2A434BB6EF68BE', 0, 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', 'Rum Pineapple Pork Ribs are Sweet and Tangy', 2, NULL, 0, 'Rum Pineapple Pork Ribs are Sweet and Tangy', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/08/rum-pineapple-ribs-150x150.jpeg', '', '', '20 minutes', '5 hours', '5 hours 20 minutes', '', 'Ingredients?#&?#&1 slab baby back ribs?#&Mustard?#&3 tbsp smoked all purpose rub ?#&1 pineapple, cored and sliced into 1/2” rings – should make about 9 rings total – 3 of which are used in the sauce, save juice for sauce (if using canned look for a corn syrup free, reduced sugar version)?#&?#&Texas Crutch?#&?#&1/4 cup apple juice?#&2 tbsp pineapple preserves?#&?#&Pineapple Rum BBQ Sauce?#&?#&1/3 cup BBQ sauce or your favorite store bought variety?#&3 tbsp pineapple preserves?#&1/4 cup Myers rum?#&3 tbsp pineapple juice?#&Dash allspice?#&3 slice pineapple, chopped', '', 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', '2-2-1 Method', 'pork ribs, baby back ribs, 2-2-1 ribs, smoked BBQ ribs, texas crutch, pork ribs recipe', 'Aug 29, 2019', 'Pork Ribs', 'Instructions?#&?#&Smoke Temp: 225F?#&?#&Smoking Woods: FRUIT WOODS- (apple, pecan, pear) OR mix with OAK, PECAN, MAPLE?#&?#&The ribs are going to use the 2-2-1 method: 2 hours in the smoker, 2 hours in foil, 1 hour letting the sauce glaze.?#&?#&Start by removing the membrane from the ribs. This is easier with a paper towel for gripping and a butter knife for separating from the meat.?#&Prepare your smoker for 225 degrees- this recipe can be made on any kind of smoker (and was originally created for my Healthy Electric Smoker Cookbook).?#&Next, slather with mustard so the rub can adhere. Apply the rub and smoke the meat for 2 hours.?#&After 2 hours, remove from the smoker and do the “Texas Crutch,” i.e., “wrap in foil”. Mix the apple juice and pineapple preserve and add to the foil packet. Let smoke another two hours.?#&Meanwhile, as the meat is smoking make your rum barbecue sauce. Combine the BBQ sauce, pineapple preserves, rum, chopped pineapple, pineapple juice and allspice. Let reduce down for 20 minutes on medium simmer.?#&During the last hour, remove the ribs from foil and glaze with the pineapple rum BBQ sauce.?#&Layer slices of pineapple on top of the ribs and drizzle more sauce over the pineapple.?#&Let smoke for an additional hour to let the sauce set. Serve immediately.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'1F5BAEEF18B8564494140804B0812368', X'58D72A49C7A6914FBEAEBB1E469E9879', X'58D72A49C7A6914FBEAEBB1E469E9879', 0, 'https://grillgirl.com/2019/06/gorgonzola-thyme-stuffed-pork-chops-with-bourbon-grilled-apples/', 'Gorgonzola Thyme Stuffed Pork Chops with Bourbon Grilled Apples', 2, NULL, 0, 'Gorgonzola Thyme Stuffed Pork Chops with Bourbon Grilled Apples', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/06/Stuffed-Pork-Chops-with-bourbone-grilled-apples-72-dpi-150x150.jpg', '', '', '20', '8', '28', '4 stuffed pork chops', 'Ingredients?#&?#&4 Smithfield Brown Sugar Boneless Center Cut Pork Chops – these are available at select grocery stores and retailers nationwide, including: Walmart, ShopRite, Food Lion, Publix, Albertson’s/Safeway and Save a Lot.?#&2 tbsp gorgonzola?#&2 tbsp chopped walnuts?#&2 tbsp chopped dried cherries?#&2 tbsp fresh thyme?#&toothpicks?#&?#&Grilled Bourbon Apples:?#&?#&4 apples, sliced with seeds removed?#&dash cinnamon?#&1 tbsp maple syrup?#&2 tbsp bourbon', 'American', 'https://grillgirl.com/2019/06/gorgonzola-thyme-stuffed-pork-chops-with-bourbon-grilled-apples/', 'Grill', 'Grilled Pork Chops, Grilled Center Cut Pork Chops, Stuffed Pork Chops with Grilled Apples, Gorgonzola Thyme Stuffed Pork Chops, Bourbon Grilled Apples, How to Grill Pork Chops', 'Jun 27, 2019', 'Pork, Grilled Pork Chops', 'Instructions?#&?#&Prepare your grill direct for heat grilling at 350-400 degrees. While the grill is heating up, go ahead and mix your gorgonzola, walnuts, cherries and thyme.?#&?#&Take a very sharp knife and cut a slit into the pork chops where the stuffing will go, using your finger to expand the opening. Stuff the pork chops with the cheese mixture and use a tooth pick to secure the opening so the stuffing doesn’t come out while grilling.?#&?#&Next, cut your apples. Combine the maple syrup and bourbon together in a cup. Sprinkle the apples with the cinnamon and then drizzle the maple bourbon syrup on top of the apples.?#&?#&Oil your grates with a high heat oil like avocado oil or vegetable oil so your food doesn’t stick to the grill grates. Grill the pork chops on direct heat, flipping halfway through, until the internal temp reaches 140 degrees with an internal read thermometer, this should take about 5-8 minutes depending on your grill. While grilling the pork chops, add the apple slices and let them grill until they develop char marks and have begun to caramelize on the grill.?#&?#&Once the pork chops have reached an internal temp of 140 degrees, pull them off the grill (they will continue to cook and reach 145 degrees after removed from the grill). Let them rest under foil for 5 minutes.?#&?#&Serve the pork chops with the grilled apples for sweet-salty pork pairing perfection!?#&?#&What is your favorite way to enjoy Pork Chops? Check out my recent Cuban inspired Mojo Pork, coming straight to you from my Miami Kitchen!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'BB1658461EBE6549A78259EF3C4CBE8E', X'E84FE3BF4AC608429A2F75E4A89D2251', X'E84FE3BF4AC608429A2F75E4A89D2251', 0, 'https://grillgirl.com/2019/03/colombian-style-hot-dogs/', 'Colombian Style Hot Dogs', 2, NULL, 0, 'Colombian Style Hot Dogs', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/03/Colombian-Style-Hot-Dogs-RL-150x150.jpg', '', '', '5 minutes', '10 minutes', '15 minutes', '8 hot dogs- 2 per person', 'Ingredients?#&?#&8 hot dogs- I use use Nitrate Free Hot dogs?#&8 pack hot dog buns?#&1/4 cup Mayo?#&1/4 cup Mustard?#&¼ cup Ketchup?#&¼ cup Pineapple Preserves?#&2 cups Crushed Potato Chips (I like Ruffles chopped up bc they are extra crunchy)', '', 'https://grillgirl.com/2019/03/colombian-style-hot-dogs/', 'Grill', 'Colombian Style Hot Dogs', 'Mar 12, 2019', 'Latin, American', 'Instructions?#&?#&Grill hot dogs on Kingsford Charcoal until they are nice and charred on all sides. Next, spread the buns out and toast for a few minutes on the grill on the cooler side, being careful not to burn.?#&?#&Mix mayo, mustard and ketchup to make a “Pink sauce”. **I like to put this in a squeeze bottle to squeeze on top but whatever works for you!?#&?#&Place the hot dog in the bun and add a tbsp of pineapple preserves. Next, add pink sauce and then finish with crushed potato chips.?#&?#&This is an unexpectedly tasty combo of sweet, salty, smoky and crunch all in one bite!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'FFC13A95BD22DD429B9AAA7357FF7266', X'1819976C8639D34E854AAEEA5DC60397', X'1819976C8639D34E854AAEEA5DC60397', 0, 'https://grillgirl.com/2018/12/grilled-shrimp-and-grits/', 'Grilled Shrimp and Grits', 2, NULL, 0, 'Grilled Shrimp and Grits', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/12/Grilled-Shrimp-and-Grits-150x150.jpeg', '', '', '5 minutes', '30-40 minutes', '45 minutes', '8 servings', 'Ingredients?#&?#&1 ½ lbs shrimp, deveined and shells removed ?#&?#&1 lb andouille sausage, chopped into ¼” pieces ?#&?#&1 8-oz bag red, yellow, green mini peppers ?#&?#&2 tbsp old bay seasoning ?#&?#&2 cups Grits- I am really loving Bob’s Red Mill Corn Grits/Polenta- very creamy and cook quickly?#&?#&1 qt organic reduced sodium chicken stock (or homemade!), plus 2 cups water ?#&?#&1/3 cup whole milk ?#&?#&1 tbsp butter ?#&?#&1 tbsp sea salt- or to taste ?#&?#&1 tsbp fresh ground pepper- or to taste ?#&?#&1 tbsp smoked paprika ?#&?#&1 cup cheddar, grated?#&?#&Chopped parsley, for garnish, optional?#&?#&Crystal Hot sauce', 'Southern', 'https://grillgirl.com/2018/12/grilled-shrimp-and-grits/', 'Grilling', 'Shrimp and Grits, easy shrimp and grits, low country style shrimp and grits, Southern Shrimp and Grits, Grilled Shrimp and Grits, Kingsford Charcoal, Robyn Lindars, GrillGirl', 'Dec 18, 2018', 'Brunch/Seafood', 'Instructions?#&?#&In a bowl, add olive oil, old bay and the shrimp and toss to coat.?#&?#&Meanwhile, start your grits: Heat quart of chicken broth and water in a large pot and bring to a boil. Add 2 cups of grits gradually over 2 minutes stirring constantly (grits will clump easily-also easily stick to bottom of pot!). Let the grits cook down on a medium simmer, stirring often, about 30 minutes.  While the grits are cooking, prepare your grill for cooking on direct heat. I recommend using your charcoal grill with Kingsford Charcoal to get smoky flavor.?#&?#&While your charcoal is heating up, continue to watch and stir your grits. Once they have reduced down, keep them on a very low simmer.?#&?#&Grill the shrimp, sausage and peppers until cooked through. The shrimp will cook most quickly- remove them as soon as they begin to turn pink. Remove the sausage once an internal temp of 145 degrees with a Thermapen has been reached and the peppers can be removed once they have developed char marks.?#&?#&Once the shrimp, peppers and sausage have cooked on the grill, remove them and set aside on a cutting board to let cool slightly so they can be chopped. Meanwhile, add the milk, cheese and butter to the grits and stir to combine and let thicken over a low simmer. Add salt, pepper, and smoked paprika to the grits.  Chop the peppers and sausage and remove the tails off the shrimp. Ladle grits ½ cup at a time into bowls. Add shrimp, peppers and sausage and garnish with chopped parsley.?#&?#&Serve with your favorite hot sauce, and additional salt and pepper. I hope you and your family enjoy this Southern inspired dish as much as mine does! Cheers!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'02CBB3B0CAD7E645A7C4307C42E8681A', X'B164C21EB5E701419E25D14EDC2B29B1', X'B164C21EB5E701419E25D14EDC2B29B1', 0, 'https://grillgirl.com/2018/11/pork-belly-burnt-ends-recipe/', 'Pork Belly Burnt Ends Recipe', 2, NULL, 0, 'Pork Belly Burnt Ends', 'Robyn Lindars', 'https://grillgirl.com/wp-content/uploads/2018/11/pork-belly-150x150.jpg', '', '', '30 min', '5 hours', '5 hours 30 minutes', '', 'Ingredients?#&?#&Large aluminum foil pan?#&5 lbs pork belly, cubed into 1 ½” pieces (I used Snake River Farms Kurobuta Pork Belly)?#&1 tbsp olive oil?#&1 cup All Purpose BBQ Rub (Jon Solberg’s rub is fantastic!)?#&?#&Pan Sauce:?#&?#&1 cup BBQ Sauce (Try this Jamaican Jerk BBQ Sauce recipe or just pick up some Stubb’s!)?#&1 tbsp bourbon?#&2 tbsp molasses?#&Dash hot sauce?#&3 tbsp butter', '', 'https://grillgirl.com/2018/11/pork-belly-burnt-ends-recipe/', '', '', 'Nov 13, 2018', '', 'Instructions?#&?#&Slice the pork belly into 1 ½” pieces. Place in a large bowl and toss in olive oil, then add rub and make sure all pieces are covered in it.?#&Smoke the pork belly pieces at 250 for 2 hours or until all the fat has rendered off and a crust is beginning to form.?#&Place the pork belly burnt ends in the foil pan. Combine the bbq sauce, bourbon, molasses, and hot sauce and pour over the burnt ends.?#&Smoke for another 2 hours covered in foil.?#&After 2 hours, remove the foil and let cook uncovered for another 30 minutes to thicken up the sauce.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'9F15463F2CCAAC4E91C8C295E6E3A72B', X'378396A0398D8E41A75482E02FBC9755', X'378396A0398D8E41A75482E02FBC9755', 0, 'https://grillgirl.com/2018/07/big-green-egg-series-making-pulled-pork-in-your-egg/', 'Big Green Egg Series: Making Pulled Pork in Your Egg!', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2018/07/big-green-egg-series-making-pulled-pork-in-your-egg/', '', '', 'Jul 9, 2018', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'67CE1B0752A6F54FBE4CC983CE4E1140', X'E155D948FE584440962D42FFB037DEEB', X'E155D948FE584440962D42FFB037DEEB', 0, 'https://grillgirl.com/2017/10/smoked-bologna-pit-barrel-cooker/', 'Smoked Bologna on the Pit Barrel Cooker', 2, NULL, 0, 'Smoked Bologna on the Pit Barrel Cooker', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/10/smoked-bologna-on-pitbarrel-cooker-edited-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One 3–5 lb chubb of beef bologna?#&PBC Beef & Game Pit Rub?#&2 tsp mustard (for rub binder)?#&1 loaf of Wonder Bread Texas Toast?#&1 Heirloom tomato?#&1 head of iceberg lettuce?#&1 Vidalia onion?#&1 jar of Dukes Mayonnaise?#&?#&BBQ GLAZE?#&?#& 2 Cups of KC Masterpiece Classic BBQ sauce?#&2/3 cups of apple juice?#&?#& In a small bowl mix the bbq sauce and apple juice until incorporated', '', 'https://grillgirl.com/2017/10/smoked-bologna-pit-barrel-cooker/', '', '', 'Oct 20, 2017', '', 'Instructions?#&?#&Remove exterior wrapper from the chubb of bologna. Take the chubb and lay it on its side. Take a paring knife and score diagonally about ¼” to 1/2”  deep in a criss cross method. This will give the meat some texture. Apply approximately 2 TBSP of mustard (amount depends on the size of your chubb) and massage into the meat.  Season with a moderate dusting of Beef & Game Pit Rub to all sides of the bologna.?#&?#&Light your Pit Barrel Cooker (PBC) according to the instructions. Oil the grate so the meat will not stick. Place the meat on its side on the grate. Add your preferred wood for smoke- I added cherry wood. Cook until you reach an internal temperature of 140 degrees as measured with an internal read thermometer (I recommend the Thermapen for accurate internal temperature readings). This should take approximately 2 ½ hrs depending on the weather conditions.?#&?#&Remove the bologna from the grate. Glaze with the BBQ glaze. Don’t overdo the sauce; use just enough to coat the bologna. Put the bologna back on the PBC and cook for 10-15 minutes. Just enough to set the glaze.?#&?#&Remove and loosely cover the bologna with aluminum foil while you prep for your bologna sandwich. Take two pieces of Wonder Texas Toast, lie them on the grate and cook with the lid off until you see some grill marks.  Slice the bologna to your desired thickness and then put mayonnaise, tomato, sliced onion and lettuce on your sandwich and enjoy.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'DC0ADD207E1CE145858496EEA04D0D43', X'00FC67D4B624B243814982BBB530BE49', X'00FC67D4B624B243814982BBB530BE49', 0, 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 2, NULL, 0, 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/10/Bacon-Pork-Guinness-Cassoulet-edited-150x100.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Dutch Oven or large aluminum pan to put on the grill?#&Smithfield Applewood Smoked Bacon Marinated Fresh Pork Loin Filet, sliced into 1/2” thick pieces for grilling?#&1/2 lb bacon, chopped into 1/2 inch pieces?#&2 15 oz cans navy beans, drained and rinsed?#&1 15 oz can kidney beans?#&1 medium sweet onion, sliced into rings for grilling?#&4 medium sized (I used Campari) tomatoes, sliced in half?#&2 carrots, chopped?#&2 celery sticks, chopped?#&2 cloves garlic, chopped?#&1 large bunch thyme?#&1–2 Guinness Beers or another Porter Style Beer?#&2 tbsp brown sugar?#&French Bread?#&Compound Butter (herbed butter)', '', 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', '', '', 'Oct 7, 2017', '', 'Instructions?#&?#&Heat your grill up, preparing it for medium direct cooking- at a temperature of approximately 350 degrees. This means you will be cooking directly over the fire!?#&?#&While the grill heats up go ahead and prepare your ingredients for the grill; chop the bacon, carrots and celery and garlic. Slice the tenderloin into 1/2 thick pieces, and slice onion into rings for the grill, cut the tomatoes in half. Get all your other ingredients ready to take outside- I usually put everything on a big cutting board and carry it outside and then get another plate for switching out.?#&?#&Once the grill is heated up, place the cast iron on one half of the grill and add the bacon and let it reduce. Meanwhile, with your existing grill surface area, go ahead and grill the pork tenderloin. This will only take a few minutes per side, remove once char marks have begun to form (remember these will also go into the stew and continue to cook). Next, grill the tomatoes and onions until char marks have formed, then remove from the grill.?#&?#&In the dutch oven, once the bacon fat has rendered, add the carrots and celery and let them cook in the bacon fat. Meanwhile, chop the onions and tomatoes and add to the dutch oven, stirring frequently to ensure no bacon is burning on the bottom. Now add the garlic. Next, add the first Guinness beer and deglaze the bottom of the pan. Add the sugar and stir. Now, chop the pork into small bite sized pieces and add to the dutch oven. Last but not least, add the beans and thyme and let the cassoulet simmer for 10 minutes or until the liquid has reduced. If you like a more soup like stew, add another 1/2 to full beer, depending on how much time you have to let simmer.?#&?#&While the Cassoulet simmers on the grill, slice the French bread into 1” thick pieces and slather with butter on both sides. Grill very quickly – no more than a minute- to get grill marks, ensuring you don’t let it burn.?#&?#&Serve the Cassoulet with the grilled bread and be prepared for big smiles on the faces of your family- like I said, this recipe is pure comfort food and expect to add this to your list of recipes you make again and again. Also great for freezing and giving to those in need of an easy meal- like friends and family going through an illness or just home with a new baby.?#&?#&If you give this recipe a try, please let me know your thoughts in the comments below! Also, when reheating you’ll notice that the beans soak up a lot of the liquid. You can add more beer if you want more stew like consistency before reheating.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'8A709F3322465E4686368A97BA0A51AD', X'C06215978041904CA93B5C0B0B3A7093', X'C06215978041904CA93B5C0B0B3A7093', 0, 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 2, NULL, 0, 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/Hawaiian-Pork-Tenderloin-Sliders_1-150x150.jpg', '', '', '', '', '', 'Serves 4, 2 sliders per person', 'Ingredients?#&?#&1 Smithfield Roasted Garlic and Cracked Black Pepper Marinated Fresh Pork Tenderloin1 large pack Kings Hawaiian Rolls?#&1 small bag mini peppers?#&1 pineapple, cored and sliced into rings (you can also use canned pineapple)?#&1/4 cup mayo?#&1 tbsp Sriracha', '', 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', '', '', 'Jun 22, 2017', '', 'Instructions?#&?#&Preheat your grill to 350 degrees preparing a direct and indirect zone. While the grill is heating up, slice your pork tenderloin into 3/4” filets.?#&?#&Coat your grill grates with a little oil so the food does not stick (make sure to do this especially before grilling the pork filets).?#&?#&Grill the pineapple and peppers until char marks have formed and they have begun to caramelize. Remove and let cool.?#&?#&Next, grill the pork tenderloin filets- about 5 minutes max, or until the internal temp reads 145 degrees with an internal read thermometer (I recommend the thermapen).?#&On the indirect side, toast the rolls very quickly being careful to not let them burn.?#&?#&Slice the peppers and then combine the mayo with the sriracha and spread on the buns. Now, assemble your sliders with the tenderloin, pineapple and peppers.?#&?#&The sweetness of the pineapple and peppers in this recipe really compliment the spice of the Black Pepper marinated pork tenderloin, don’t be surprised that you’ll go back for seconds or thirds!?#&?#&What are your favorite recipes for getting dinner on the table in less than 30 minutes? Please share in the comments below!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B7CB3E189C533B4AB3303FCF5DB0E575', X'4F2150AD412FED479998396E7A90E988', X'4F2150AD412FED479998396E7A90E988', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'392A414C1CFD3240A8FC9095758D2C45', X'FBD9C4D52734DA46BBD74BD96D1D3845', X'FBD9C4D52734DA46BBD74BD96D1D3845', 0, 'https://grillgirl.com/2014/07/grilled-denver-lamb-ribs/', 'Grilled Denver Lamb Ribs', 2, NULL, 0, 'Grilled Denver Lamb Ribs', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/07/Grilled-Denver-Lamb-Ribs-edited.jpg-150x150.jpg', '', '', '', '', '', '', 'ingredients below?#&honey- to drizzle at the end- I used Bees Knees spicy honey from mixemade.com?#&?#&Make your own curry:?#&?#&½ tbsp. cardamom?#&1 tbsp turmeric?#&2 tbsp cumin?#&2 tbsp coriander?#&1 tbsp dry mustard?#&1 tsp chipotle powder (or your favorite pepper)?#&1 tbsp smoked paprika', '', 'https://grillgirl.com/2014/07/grilled-denver-lamb-ribs/', '', '', 'Jul 17, 2014', '', 'Instructions?#&?#&Combine all ingredients until uniformly mixed. Next, prepare your ribs by removing the membrane on the underneath side. This can be done by using a butter knife and inserting it underneath the membrane.?#&?#&Apply olive oil to the ribs and then generously coat them on both sides with the rub. Let them marinate the fridge for 30 minutes while you prepare your grill.?#&?#&I used “Bees Knees” spicy honey to finish these ribs with a hint of sweet and spicy.?#&?#&Prepare your grill for 350 degrees with a direct and indirect zone. Grill the lamb on indirect heat for 20 minutes each side, or until an internal read thermometer reads 140 degrees.?#&?#&Grill the lamb for another 5 minutes (or until at 145 degrees internal temp), meaty side down, to obtain a nice char and caramelization on the grill.?#&?#&Pull the ribs off and finish with a drizzle of honey to give that “gooey, finger licking good” effect and a hint of sweetness. Tent under foil for 10 minutes, then slice and serve.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'C53D8D23371CC24F9B111FDE773AEC12', X'729DC28EF5B3894B9975EBF408B68EDB', X'729DC28EF5B3894B9975EBF408B68EDB', 0, 'https://grillgirl.com/2014/07/grilled-lamb-kofta-kebabs/', 'Grilled Lamb Kofta Kebabs', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2014/07/grilled-lamb-kofta-kebabs/', '', '', 'Jul 3, 2014', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'CFAF13FACB1DCE478E784F729A7B42C8', X'985EE13D93B66348AF5345B415487AEE', X'985EE13D93B66348AF5345B415487AEE', 0, 'https://grillgirl.com/2014/07/lamb-kofta-kebabs/', 'Lamb Kofta Kebabs', 2, NULL, 0, 'Lamb Kofta Kebabs', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/07/Turkish-Kofka-Kebabs-150x150.jpg', '', '', '', '', '', '', 'ingredients:?#&?#&1 cup. Plain greek yogurt?#&1tbsp Sriracha?#&large bunch mint', '', 'https://grillgirl.com/2014/07/lamb-kofta-kebabs/', '', '', 'Jul 3, 2014', '', 'Instructions?#&?#&Combine all ingredients in a large mixing bowl. Then, form little patties, the size of a large meatball, in the palm of your hand. Roll them out to be oval little balls and then place them on a tray. After You’ve rolled all the balls out, insert (pre soaked) bamboo skewers into them.?#&?#&Grill the koftas on a medium high heat grill, about 350 degrees, for 3 minutes per side, or until an internal read thermometer (I recommend the Thermapen) reads 160 degrees. Make sure you oil your grill grates so the meat does not stick. After you pull them off, let them rest under foil for 10 minutes. Meanwhile, grill your pita flat bread for a few minutes to get warm. Serve the koftas with the warm pita bread and greek yogurt dipping sauce. Use chopped mint as a garnish before serving if you like.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'64D292A2C069E74C9FDFE19C3B295B39', X'F45540A469379340B787E48065C27188', X'F45540A469379340B787E48065C27188', 0, 'https://grillgirl.com/2014/06/grilled-rosemary-lemon-boneless-leg-lamb/', 'Grilled Rosemary Lemon Boneless Leg of Lamb', 2, NULL, 0, 'Grilled Rosemary Lemon Boneless Leg of Lamb', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/06/lamb-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 boneless leg of lamb?#&1 large bunch rosemary?#&1/8 cup olive oil?#&juice of 2 lemons?#&Zest of 2 lemons?#&1 large bunch rosemary?#&2 teaspoon salt?#&1 tbsp pepper', '', 'https://grillgirl.com/2014/06/grilled-rosemary-lemon-boneless-leg-lamb/', '', '', 'Jun 26, 2014', '', 'Instructions?#&?#&Combine marinade ingredients in the food processor until uniform in consistency.?#&?#&Next, slather the marinade on the lamb and let marinate for 30 minutes or more in the refrigerator.?#&?#&Grilling Directions:?#&?#&Prepare your grill for medium high heat at 350 degrees, and create direct and indirect zones.?#&?#&Grill the leg of lamb on indirect heat for 45 minutes or until an internal read thermometer registers 135 degrees. Now, transfer the leg to the direct side and let it continue to cook for another 5 minutes on each side. Use a brick covered in foil to help flatten the meat on the grill which will also help the outside get crispy.?#&?#&Once an internal read thermometer like the Thermapen reads 145 degrees, pull the leg off the grill and let it rest for 10 minutes under foil so the juices can redistribute.?#&?#&Slice against the grain for a beautiful presentation. How do you like to prepare lamb? I look forward to hearing your ideas!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'7103EABE20430B478450734B0A88990C', X'C896B12C7E4A954B83C09C9D10AF3759', X'C896B12C7E4A954B83C09C9D10AF3759', 0, 'https://grillgirl.com/2017/01/goose-big-green-egg/', 'Smoked Goose on the Big Green Egg', 2, NULL, 0, 'Smoked Goose on the Big Green Egg', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/01/goose-on-the-big-green-egg-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1  8-10 lb Goose?#&?#&Rub/Slather:?#&?#&1–2 tbsp olive oil?#&1 tbsp fresh ground pepper?#&1 tbsp sea salt?#&1 tsp smoked paprika?#&?#&For stuffing the cavity:?#&?#&Citrus – a few oranges/lemons/limes for stuffing the cavity?#&Bunch of fresh herbs – rosemary and thyme are some of my favorite for fowl?#&Pecan Wood (or wood of your choice) for smoking?#&Aluminum tray for catching the Goose grease', '', 'https://grillgirl.com/2017/01/goose-big-green-egg/', '', '', 'Jan 10, 2017', '', 'Instructions?#&?#&If your bird is frozen, make sure to go ahead and thaw it overnight.?#&Remove the gizzards from the inside. Save them for smoking them or you can boil them to make a stock for gravy.?#&Stuff the cavity with the citrus and herbs.?#&Combine the olive oil, salt, pepper and smoked paprika to make a slather that will help the rub adhere to the bird. Rub this generously all over the bird leaving no place dry.?#&Slice the citrus and place inside the cavity along with the fresh herbs. Take a few short skewers or toothpicks to close the opening up so the contents don’t fall out while smoking.?#&?#&Preheat your Big Green Egg or Kamado Smoker for 350 degrees. You will need to put the place setter in and also make sure to put in an aluminum tray on the place setter to catch the rendered fat. Add your wood to create smoke. IMPORTANT: Don’t forget to check on the rendered fat and make sure to remove a few times during the cook!?#&Place the goose on the grates (above the plate setter) with the breast up. Let the goose smoke for 2 1/2-3 hours or until the breast reads 160 degrees with an internal read thermometer like the Thermapen– it will continue to cook when you take it off so you know it will get to 165 which is the USDA recommended internal temp for poultry).?#&Pull the goose off the smoker and let it rest for a few minutes, then carve the breasts lengthwise like you would a brisket, you can also carve the legs for serving.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'18FBADE043013D4FB2AD7E822B4FF5B7', X'EF351C12B78B4A479856FD3F66C20049', X'EF351C12B78B4A479856FD3F66C20049', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'CFF39A8296ACC84C88879EE48420D770', X'52CF23F431CB004CB5BB6E7576FB59AC', X'52CF23F431CB004CB5BB6E7576FB59AC', 0, 'https://grillgirl.com/2016/06/bacon-blue-cheese-bbq-bison-burgers/', 'Bacon, Blue Cheese and BBQ Bison Burgers', 2, NULL, 0, 'Bacon, Blue Cheese and BBQ Bison Burgers', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/06/Bacon-Blue-Cheese-BBQ-Bison-Burger-150x150.jpg', '', '', '', '', '', 'Makes 8 1/4lb burgers', 'Ingredients?#&?#&2 lbs ground bison meat (*ground beef can be substituted)?#&1/2 bottle Stubb’s Sticky Sweet Bar-B-Q Sauce?#&½ jar Stubb’s Steak Rub– use ½ tsp on each burger?#&1 tbsp apple cider vinegar?#&dash Worcestershire sauce?#&2 garlic cloves, minced?#&1 tbsp butter?#&1 medium sized red onion?#&8 oz blue cheese?#&8 slices bacon, pre-cooked?#&1 jar high quality dill pickles?#&8 hamburger buns', '', 'https://grillgirl.com/2016/06/bacon-blue-cheese-bbq-bison-burgers/', '', '', 'Jun 29, 2016', '', 'Instructions?#&?#&Mix the bison meat with the minced garlic and add the butter a dash of Worcestershire sauce (bison meat is lean so the butter adds fat= moisture). Form the meat into 8 burgers. With your thumb, create an indentation in the middle of the burger- this will help the burger cook evenly. Coat each burger with approximately ½-1tsp.?#&?#&Next, prepare your grill for medium heat, 350 degrees- creating a direct and indirect zone (coals on one side).While the grill is heating up, slice the onion into thin slices. Take 2 large pieces of foil as a double layer and create a “hobo packet” where you create a packet that is sealed on all sides that will cook the onions on the grill. Before sealing the foil packet, add the sliced onions, 1 tbsp. of cider vinegar and ½ cup of BBQ Sauce.?#&?#&First, oil your grill grates so your meat does not stick, then place your burgers and the hobo packet on direct heat. If your burgers are cooking too quickly you can put them indirect so they continue to cook without getting burned. Cook until the internal temperate reads 140 degrees for medium burgers, (I recommend using an internal read thermometer such as the Thermapen) flipping halfway through. When the burgers are getting almost done, add the cheese to the burgers.?#&?#&Next, place the sliced buns on the indirect side and let them toast on the grill for a few minutes. Pull off the hobo packet at this point.?#&?#&Assemble your burger:?#&?#&Place your burger on the toasted bun and add bacon, pickles and a giant heap of BBQ sauce caramelized onions, plus a little extra sauce to the burger. Prepare for burger ecstasy! If you want some greens on your burger, a little peppery arugula is perfect.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'BA09B2B3AD111348A37E279058B3E583', X'FE9F363FC97881439CBEF37EBC2FB2F1', X'FE9F363FC97881439CBEF37EBC2FB2F1', 0, 'https://grillgirl.com/2015/02/rosemary-lemon-grilled-quail/', 'Rosemary Lemon Grilled Quail', 2, NULL, 0, 'Rosemary Lemon Grilled Quail', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/02/rosemary-lemon-grilled-quail-1-150x150.jpg', '', '', '', '', '', 'serves 4', 'Ingredients?#&?#&4 quail, butterflied (spatchcocked- ie, cut the backbone out so they will late flat on the grill)?#&zest of 2 lemons?#&Juice of one lemon?#&3 tbsp olive oil?#&3 sprigs rosemary, leaves removed?#&1 1/2 tsp sea salt?#&2 tsp fresh ground pepper', '', 'https://grillgirl.com/2015/02/rosemary-lemon-grilled-quail/', '', '', 'Feb 15, 2015', '', 'Instructions?#&?#&Cut the backbone of the quail in half so you can spread the birds flat on the grill. Next, combine the next ingredients with an immersion blender to form a seasoning paste. Using your index finger, gently separate the skin from the muscle, being careful not to tear the skin. Now, slather the seasoning paste generously under and over the skin of the quail. Let the quail marinate in the fridge for at least an hour before grilling.?#&?#&Prepare your grill for 350 degrees, direct heat (ie, over the coals/gas). Oil your grill grates with canola or vegetable oil so your quail will not stick to the grill grates- don’t forget this step, Very Important! Grill the quail for 6-8 minutes PER SIDE or until an internal read thermometer reads 170 degrees. Remove the quail and let rest under foil for 10 minutes.?#&?#&Pair with your favorite sides! This recipe is perfect for date night or having friends over- the quail are so pretty on the plate and they grill up in no time!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'731F6E36CC7E2D42BA5110700AC3B664', X'163322DCF52AE742BDB07D40BA09297F', X'163322DCF52AE742BDB07D40BA09297F', 0, 'https://grillgirl.com/2014/07/grilled-lamb-kofta-kebabs/', 'Grilled Lamb Kofta Kebabs', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2014/07/grilled-lamb-kofta-kebabs/', '', '', 'Jul 3, 2014', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'8AA18A02355FA64FB13581D88F836A8C', X'A093571145B7EB40A7EB868D56023C0E', X'A093571145B7EB40A7EB868D56023C0E', 0, 'https://grillgirl.com/2014/07/lamb-kofta-kebabs/', 'Lamb Kofta Kebabs', 2, NULL, 0, 'Lamb Kofta Kebabs', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/07/Turkish-Kofka-Kebabs-150x150.jpg', '', '', '', '', '', '', 'ingredients:?#&?#&1 cup. Plain greek yogurt?#&1tbsp Sriracha?#&large bunch mint', '', 'https://grillgirl.com/2014/07/lamb-kofta-kebabs/', '', '', 'Jul 3, 2014', '', 'Instructions?#&?#&Combine all ingredients in a large mixing bowl. Then, form little patties, the size of a large meatball, in the palm of your hand. Roll them out to be oval little balls and then place them on a tray. After You’ve rolled all the balls out, insert (pre soaked) bamboo skewers into them.?#&?#&Grill the koftas on a medium high heat grill, about 350 degrees, for 3 minutes per side, or until an internal read thermometer (I recommend the Thermapen) reads 160 degrees. Make sure you oil your grill grates so the meat does not stick. After you pull them off, let them rest under foil for 10 minutes. Meanwhile, grill your pita flat bread for a few minutes to get warm. Serve the koftas with the warm pita bread and greek yogurt dipping sauce. Use chopped mint as a garnish before serving if you like.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'AC2643DF5D6D5B4D9BCD195B90113D8C', X'7CB8E8658590454C9470B813B2EA5A2A', X'7CB8E8658590454C9470B813B2EA5A2A', 0, 'https://grillgirl.com/2013/05/goat-sliders/', 'Goat Sliders With Wine Reduction and Blue Cheese Sauce', 2, NULL, 0, 'Goat Sliders With Wine Reduction and Blue Cheese Sauce', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2013/05/goat-sliders-Eric-Chester-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Goat Leg?#&3–5 lbs of bacon?#&6 cloves of garlic minced?#&1 softened stick of unsalted butter?#&7–8 leaves of fresh mint shredded.?#&4 bottles of Guinness or stout of your choice?#&2 Tbsp of sugar?#&1 Tbsp of sea salt?#&3 cups of cold water?#&?#&Cracked Pepper Red Wine Glaze?#&?#&2–3 cups of Red Wine (Choose a red wine that you would drink, I prefer a Cabernet Sauvignon)?#&1 tsp onion powder?#&1tsp minced garlic?#&½ tsp of cayenne?#& Fresh ground black pepper to your taste.?#&?#&Blue Cheese Sauce?#&?#&½ of a white onion chopped?#&1 red bell pepper – fire roasted and chopped?#&9 oz blue cheese?#&1 tsp of minced garlic?#&1 pint of heavy cream', '', 'https://grillgirl.com/2013/05/goat-sliders/', '', '', 'May 29, 2013', '', 'Instructions?#&?#&To begin cooking extravaganza trim away any excess fat from the goat leg. Expose as much of the meat as possible the set it aside. Goat can be a rather tough meat if prepared incorrectly. The best way to tenderize the meat is a good brine. Take the first 2 bottles of Guinness and pour them into a small stock pot. Open the 3rd bottle and enjoy. Add the 2 tbsp of sugar and 1 tbsp of salt to the Guinness and simmer until dissolved. Add 3 cups of water to the mixture and allow it to cool. Place the goat leg in a container large enough that it can lay flat and pour the Guinness brine over the leg.  Leave in the brine for 3-5 hours rotating the leg every 30 mins. While we brine the leg…may as well have that other bottle of Guinness.?#&?#&This would also be a great time to start working on the next step – BACON WEAVNG. Take a large sheet of aluminum foil (Large enough to cover the goat leg) and begin weaving. Think back to elementary school and making mats out of construction paper….it is the same, but with tasty, tasty bacon. If you aren’t a master bacon weaver, don’t worries you can lay out the bacon in overlapping strips.  This would also be a good time to bring your smoker up to 225 degrees.?#&?#&After 3-5 hrs, remove the goat leg from the brine and pat dry with a paper towel. Rub the entire leg down with the softened butter and minced garlic. Once rubbed down, sprinkle the shredded mint leaves over the leg. Now wrap the bacon around the leg. Make sure you leave no gaps…add more bacon. Now that it is happily wrapped in bacon wrap the leg in aluminum foil. Place the leg in the smoker and cook 12-14 hrs (depending on the size of the leg) at 225 degrees.?#&?#&Take a nap…have a drink or two, like Robyn’s Grilled Margaritas.?#&?#&An hour or so before the meat is done start working on your Cracked Pepper Red Wine Glaze and Blue Cheese Sauce.?#&?#&Cracked Pepper Red Wine Glaze: Pour the wine into a medium sauce pan. Add the onion powder, garlic, cayenne and black pepper. Bring the mix to boil for 2-3 min then bring the heat down to a simmer for 10- 15 min.  Remove from heat and set to the side.  Pour yourself a glass or two of wine. It’s not too early is it??#&?#&Blue Cheese Sauce: Add the garlic, onions, and fire-roasted pepper to a large skillet and heat until the onions begin to sweat. Add the blue cheese and heavy cream to the mix and continue stirring until the cheese is blended smooth.?#&?#&Now that the goat has been on for the 12-14 hours remove it from the smoker and allow it to rest 15 – 20 mins in the foil. Unwrap the foil and the bacon…but don’t discard the bacon…its bacon…eat it…It’s awesome. Pull the meat from the leg like you would a pork shoulder.  Place the meat on toasted wheat rolls.  Drizzle the cracked pepper wine glaze over the pulled goat.  Spoon the blue cheese sauce over top of the meat. Add a piece of lettuce or spinach and Viola!  Pour yourself a drink and Bon Appetite!”');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'9F70FD00959FE84AA244F67E52CFF264', X'AC9A7F8B9B6F2D4E9228570AA3634C08', X'AC9A7F8B9B6F2D4E9228570AA3634C08', 0, 'https://grillgirl.com/2018/07/the-perfect-skinny-margarita/', 'The Perfect Skinny Margarita', 2, NULL, 0, 'The Perfect Skinny Margarita', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/07/skinny-margarita-paleo-friendly-edited-1024x684-150x150.jpg', '', '', '', '', '', '2 cocktails', 'Ingredients?#&?#&4 oz tequila?#&2 teaspoons agave syrup (ie, one teaspoons per drink)?#&Juice of two large limes, plus an additional lime for garnish.?#&1–1/2 cans or 12 ounces of lime flavored sparkling water', '', 'https://grillgirl.com/2018/07/the-perfect-skinny-margarita/', '', '', 'Jul 7, 2018', '', 'Instructions?#&?#&Combine the tequila, agave syrup, lime juice and sparkling water along with some ice in a cocktail shaker and shake profusely to help dissolve the agave syrup. Pour into a Collins glass filled with ice and garnish with a lime wedge.?#&?#&Each margarita has only 85 calories! Yay! If you feel like you want your ‘rita a bit sweeter, add a touch of stevia or monk fruit sweetener to keep the calories low. By starting with a base of agave, it keeps it tasting natural, not artificial.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'EEE590BB34E63342A72F979FC6791C56', X'C9F423681A640F47AB944C7559C53354', X'C9F423681A640F47AB944C7559C53354', 0, 'https://grillgirl.com/2016/11/smoked-old-fashioned/', 'Smoked Old Fashioned', 2, NULL, 0, 'Smoked Old Fashioned', 'Robyn aka GrillGirl', 'https://grillgirl.com/wp-content/uploads/2016/11/smoked-old-fashioned-72-grillgirlrobyn-150x150.jpg', '', '', '5 minutes', 'none', '5 minutes', '1 cocktail', 'Ingredients?#&?#&• 2 oz good quality bourbon?#&• 2 dashes Angostura bitters, or to taste?#&• 1 large segment orange peel?#&• 1 teaspoon simple syrup – (simple syrup is a 1/1 ration sugar/water. Cook with rosemary sprig to infuse flavor into the syrup. Can be stored in the refrigerator for a week for making cocktails).?#&• 1 maraschino cherry', '', 'https://grillgirl.com/2016/11/smoked-old-fashioned/', '', 'smoked bourbon, how to make smoked cocktails, smoked ice, smoked old fashioned cocktail, how to smoke bourbon, how to make smoked cocktails, smoked old fashioned recipe', 'Nov 23, 2016', '', 'Instructions?#&?#&In a decanter or large glass pitcher, add the bourbon, simple syrup, and bitters. Next, turn on your smoking gun and infuse the pitcher with smoke for 1-2 minutes; close the container with a piece of saran wrap so the smoke is trapped into the pitcher. Swirl the bourbon around and then pour it into a old fashioned glass filled with ice. Take the orange peel and swirl around the rim of the glass and then add to the drink. Garnish with a sprig of rosemary. Enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'358554228C5CFD42BD10E88703D46045', X'440FDA388D728C43B5C4921E838E5AD3', X'440FDA388D728C43B5C4921E838E5AD3', 0, 'https://grillgirl.com/2016/08/coconut-mojito/', 'Coconut Mojito', 2, NULL, 0, 'Coconut Mojito', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/08/Coconut-Mojito_Michelle-Lara_A-150x150.jpg', '', '', '', '', '', 'makes 2 cocktails', 'Ingredients?#&?#&3 oz white rum?#&12–14 mint leaves?#&1 tsp of sugar?#&2 oz of fresh lime juice?#&2 tsp of coconut cream {I used this brand}?#&club soda', '', 'https://grillgirl.com/2016/08/coconut-mojito/', '', '', 'Aug 18, 2016', '', 'Instructions?#&?#&Muddle the mint leaves, sugar and lime juice in a cocktail glass. Add ice and pour the rum, and coconut cream – give it a good shake. Pour over ice and add a splash of club soda and mint garnish – enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'12C2FD83BD50AC4186CDC6A6A5AEC638', X'ADE6FB479F2DDE44BFD2ACE34449A7A6', X'ADE6FB479F2DDE44BFD2ACE34449A7A6', 0, 'https://grillgirl.com/2016/06/strawberry-honeydew-mojito/', 'Summer Cocktail: Strawberry & Honeydew Mojito', 2, NULL, 0, 'Summer Cocktail: Strawberry & Honeydew Mojito', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/06/AdobeStock_209818084-150x150.jpg', '', '', '', '', '', '{makes 6 - 8 cocktails, 1 pitcher}', 'Ingredients?#&?#&Juice of 2 limes cut into quarters {seeds removed}?#&8 – 12 strawberries?#&3 – 4 honeydew wedges cut in cubes?#&1 1/2 cups of white rum?#&7 cups of club soda?#&1/2 cup of fresh mint?#&1/2 cup of granulated sugar?#&Additional lime, strawberries, and mint, for garnish', '', 'https://grillgirl.com/2016/06/strawberry-honeydew-mojito/', '', '', 'Jun 27, 2016', '', 'Instructions?#&?#&Boil 1/2 cup water + dissolve the sugar stirring slowly to make the simple syrup. Set aside and let cool. In a blender add your honeydew and about 1 tsp of the simple syrup and pulse until it’s a liquid consistency – you might need to add some more simple syrup so it can blend properly.?#&Using a muddler or wooden spoon mash the strawberries and mint leaves in the bottom of your pitcher, add the liquefied honeydew. Add in the lime juice and rum and 1/4 cup of simple syrup {can add more if you want it sweeter taste test it first}.?#&Right before serving pour in the club soda.Gently stir to combine and pour over iced glasses and garnish with fresh mint sprigs, lime and strawberries if you’d like.');
