INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'C2134CA27CE86644AF8DA9B0BA560913', X'BBC8F7FECD327A47870AFD1AA06C132F', X'BBC8F7FECD327A47870AFD1AA06C132F', 0, 'https://grillgirl.com/2015/07/grilled-watermelon-cucumber-cosmo/', 'GRILLED WATERMELON + CUCUMBER COSMO', 2, NULL, 0, 'GRILLED WATERMELON + CUCUMBER COSMO', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/07/Watermelon_Cucumber_Cosmo_MichelleLara_GrillGirl-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Here’s what you’ll need to make 1 cocktail:?#&?#&1.5 oz Hendricks gin?#&.5 oz lime juice?#&1 oz white cranberry juice?#&1–2 mint leaves?#&3 cucumber slices?#&2–3 grilled watermelon wedges?#&.5 oz Triple Sec?#&ice', '', 'https://grillgirl.com/2015/07/grilled-watermelon-cucumber-cosmo/', '', '', 'Jul 29, 2015', '', 'Instructions?#&?#&In a blender mix the lime juice, 2 cucumber slices and cubed grilled watermelon slices.?#&In a cocktail shaker add the ingredients along with gin, triple sec, cranberry juice and mint leaves.?#&Shake for about a minute and strain into a martini glass.?#&Garnish with the leftover cucumber slice.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'97144CADCDD13E4198F269939BA6853F', X'5772A946A4DD8247A4E964F5DD714F05', X'5772A946A4DD8247A4E964F5DD714F05', 0, 'https://grillgirl.com/2015/04/grilled-grapefruit-ginger-fizz/', 'Grilled Grapefruit & Ginger Fizz', 2, NULL, 0, 'Grilled Grapefruit & Ginger Fizz', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/04/Grilled-Grapefruit-Gin-Fizz_Michelle-Lara-for-GrillGirl.com_650-150x150.jpg', '', '', '', '', '', '2 cocktails', 'Ingredients?#&?#&2 1/2 ounces of good quality gin?#&1 ½ ounces of St. Germain Elderflower Liqueur?#&3 ½ ounces of Onli Grapefruit & Pomelo Peach {or fresh squeezed Grapefruit juice, if you want it to fizz you’ll need a splash of club soda}?#&1 ½ teaspoons of ginger simple syrup?#&4 slices of thickly cut grilled grapefruit wedges?#&Ice', '', 'https://grillgirl.com/2015/04/grilled-grapefruit-ginger-fizz/', '', '', 'Apr 21, 2015', '', 'Instructions?#&?#&Combine the gin, St. Germain, grapefruit juice and simple syrup in a cocktail shaker filled halfway with ice. Shake vigorously and strain into a cocktail glass or tumbler. Garnish with the grilled grapefruit. Add a splash of Onli Grapefruit & Pomelo Peach right before you serve to add some more fizz.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'3D5B3D8E93CC6B45B7A789BE04A87923', X'FFDE9EC6AA95294592F4E6AFD0A49E96', X'FFDE9EC6AA95294592F4E6AFD0A49E96', 0, 'https://grillgirl.com/2014/06/watermelon-agua-fresca-cocktail/', 'Watermelon Agua Fresca Cocktail', 2, NULL, 0, 'Watermelon Agua Fresca Cocktail', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/06/watermelon-aqua-fresca-cocktail1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 cups of cubed chilled seedless watermelon?#&1/4 cup of water?#&2 – 3 tbsp. simple syrup or agave?#&2–3 tbsp. fresh lime juice?#&2 oz of vodka?#&ice', '', 'https://grillgirl.com/2014/06/watermelon-agua-fresca-cocktail/', '', '', 'Jun 19, 2014', '', 'Instructions?#&?#&In a blender add your watermelon and water and puree.  In a cocktail shaker add 2 cups of the agua fresca, simple syrup {or agave}, lime juice, vodka and ice. Shake vigorously.  Place a strainer over your cocktail glass and pour. Garnish with a slice of lime and a watermelon wedge.');
