INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B016144B80F24A4897BB5882432B34BF', X'F97F998BB3056A4381C20A721FB79678', X'F97F998BB3056A4381C20A721FB79678', 0, 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Kathy Pullin’s Turkey Lasagna Wonton Cups', 2, NULL, 0, 'Turkey Lasagna Wonton Cups', 'Kathy Pullin', 'https://grillgirl.com/wp-content/uploads/2020/05/Kathy-Pullin-Turkey-Lasagna-Wonton-Cups-150x150.jpg', '', '', '15 min', '45 min', '1 hour', '48', 'Ingredients?#&?#&1 pound of ground turkey?#&6 ounces of diced pancetta?#&1/4 cup of diced onion?#&1/2 teaspoon Italian seasoning – pick your favorite?#&1/2 teaspoon of basil flakes?#&1/2 teaspoon parsley flakes?#&1 clove of garlic?#&8 ounces of ricotta cheese?#&8 ounces of shredded mozzarella?#&8 ounces of shredded Italian cheese?#&2 cups of marinara?#&Salt and pepper?#&Lots of wonton wrappers', '', 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Baked', 'turkey, lasagna, wonton, cheese, italian', 'May 6, 2020', 'Turkey', 'Instructions?#&?#&First, grab your ground turkey, pancetta and onion. Once you’ve got those together, put them in a cast iron skillet and let it all sauté over hot coals to get that turkey nice and brown.?#&Once that turkey starts taking to a tan, mince up that garlic clove and toss it into the skillet with the Italian seasoning, 1/4 teaspoon of basil and 1/4 teaspoon of parsley. When that’s simmering, add just a touch of salt and pepper.?#&Is that turkey nice and brown? Toss in the marina and set it all aside.?#&Now is where things get cheesy. Bust out all that ricotta and 6 ounces of mozzarella and another 6 ounces of the shredded Italian cheese. Mix all that cheese and the rest of the basil and parsley in a separate bowl.?#&Grease your favorite cupcake pan and place wonton wrappers in the center of each cupcake holder.?#&Scoop out a teaspoon of the meat mixture, toss that on the wonton wrapper and then top it with a teaspoon of the cheese mix. Room to spare? That’s by design. Add in another teaspoon of meat mix and top it again with shredded cheese. Repeat and then repeat some more.?#&Now it’s time to get hot. Bake all these savory wontons over hot coals at 350 degrees and set your timer for 20 minutes until they’re finished cooking.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'4E94FA262281584C8B4EDB1B1577C917', X'932F3CD4A4C0954084530CBB69A9B44C', X'932F3CD4A4C0954084530CBB69A9B44C', 0, 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', 'Grilled Watermelon Pizza', 2, NULL, 0, 'Grilled Watermelon Pizza', 'Captain Ron', 'https://grillgirl.com/wp-content/uploads/2019/10/Watermelon-Pizza-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Whole Watermelon (seedless is preferred) ?#&4 Oz package crumbled Feta Cheese ?#&2 Tbsp chopped fresh mint leaves ?#&1 Tbsp olive oil', '', 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', '', '', 'Oct 30, 2019', '', 'Instructions?#&?#&Prepare your grill for direct grilling over medium high heat ?#&Cut your watermelon into discs and coat them lightly with olive oil ?#&Place your oiled watermelon disks directly on the hot grates until there are visible grill marks underneath?#&Flip the watermelon over and top it with your crumbled feta cheese. Cover the grill and let it cook for a few more minutes. The cheese will soften but will not melt. ?#&Remove the watermelon pizza and sprinkle it with the chopped mint Slice your pizza into wedges just as you would cut a regular pizza pie ?#&?#&At this point, we like to drizzle it with a little bit of balsamic glaze for an extra burst of flavor. Let your imagination run wild!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'8EE888B6386F564DBA1C8BE3F309AB8B', X'02FA338981C34942A6F5C461894E2C70', X'02FA338981C34942A6F5C461894E2C70', 0, 'https://grillgirl.com/2019/01/top-5-keto-friendly-apps-for-the-super-bowl/', 'Top 5 Keto Friendly Apps for the Super Bowl', 2, NULL, 0, 'Bacon Jalapeno Deviled EggsINSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'93CF41D01723E44981B33F034A851678', X'C5CF15A26C85F54C8B8ECF619B224488', X'C5CF15A26C85F54C8B8ECF619B224488', 0, 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', 'Cedar Planked Pizza on the Grill', 2, NULL, 0, 'Cedar Planked Pizza on the Grill', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/04/cedar-planked-pizzas-on-grill-edited-2-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One store bought pizza crust from the deli, divided into 4 balls OR a tube of thin crust pizza dough from the refrigerator section (this is easier to get to roll thin than the stuff from the deli!)?#&Toppings of your choice', '', 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', '', '', 'Apr 30, 2018', '', 'Instructions?#&?#&Soak your wood planks for an hour before using. They need to be soaked so they don’t catch fire while grilling.?#&?#&Roll the pizza balls into long rectangles, trying to get as thin as possible and matching the size of the 5”X11” plank. Drizzle the plank with a bit of olive oil and spread around, then add cornmeal- this is done so your pizza is less likely to stick. Place the dough onto the plank. Try to get your dough as thin as possible.?#&?#&Have all of your toppings ready to be added by the grill before you get started.?#&?#&(adsbygoogle = window.adsbygoogle || ``).push({});?#&?#&Heat your grill up to approximately 400 degrees, creating direct and indirect zones. Place the cedar planked pizzas on the indirect side and let the dough cook for about 5-7 minutes so the top has a chance to bake before adding toppings. Next, add the toppings and let continue to cook for another 10 minutes until the cheese has melted and the crust is golden brown.?#&?#&Remove from the grill and slice into pieces- you can use the cedar planks as a serving tray for presentation if you like after they have slightly cooled.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'6C18AAC523381242B104A78A149DAA92', X'E06E26A8F547364D93C017D5E3076673', X'E06E26A8F547364D93C017D5E3076673', 0, 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 2, NULL, 0, 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/Sri-lankan-chicken-wings-up-close-EDITED-150x150.jpg', '', '', '', '', '', '', 'ingredients for marinade)', '', 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', '', '', 'Dec 8, 2017', '', 'Instructions?#&?#&The making of the spice paste!?#&?#&Combine olive oil, lemongrass/or zest, cilantro, garlic, turmeric, sea salt and pepper in the food processor and pulse until uniformly mixed- you are looking to make a seasoning “paste”. Next, reserve about 2 tbsp of the marinade to drizzle before serving. Marinate the chicken wings for 30 minutes before putting on the grill.?#&?#&Prepare your grill for medium direct heat at 350 degrees. I used my Green Mountain Grill Pellet Smoker and set it for 375 degrees with the smoker holes wide open. Grill your wings, turning halfway during the process, for approximately 15-18 minutes OR until the internal temperature has reached 170 degrees with an internal read thermometer.?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#& ?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#&Once you’ve reached 170 degrees (I recommend using an internal read thermometer such as the thermapen to check internal temp), pull the wings off and drizzle with the remaining marinade. Prepare for out of this world flavor combination! Enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'4D927C01FD4C1D4087B6F56F37589F7B', X'B8C7B26CE0767248A4FDC65F53522B23', X'B8C7B26CE0767248A4FDC65F53522B23', 0, 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', 'Rosemary Lemon Shrimp Skewers', 2, NULL, 0, 'Rosemary Lemon Shrimp Skewers', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/rosemary-lemon-shrimp-skewers-EDITED-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 lb jumbo or extra jumbo size shrimp- (buy the largest you can find, they will work the best for threading onto the Rosemary skewers)  shells and tails removed?#&1–2 tbsp olive oil?#&7–8 rosemary stems plus a few extra sprigs of rosemary for the marinade (1/4 cup rosemary needles are needed for the marinade)?#&8 lemons, use for 2 tbsp zest then slice thinly?#&3 cloves garlic?#&1 tbsp course Smoked sea salt (if you can’t find smoked sea salt, regular sea salt is okay)?#&1/2 tbsp fresh ground pepper?#&?#&***If you don’t have rosemary skewers, just use 6-8 bamboo skewers and buy a large bunch of rosemary for the marinade?#&?#&The rosemary stem makes for a fun skewer for the shrimp. Shrimp grills quickly so be careful to not overcook!', '', 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', '', '', 'Dec 2, 2017', '', 'Instructions?#&?#&If you have the rosemary stems, remove the rosemary needles in the middle and set aside. If you don’t have the stems, take your bunch of bunch of rosemary, reserving a few sprigs to throw on the grill, and remove the needles- you will need approximately 1/4 cup of rosemary needles for the marinade.?#&Next, take the rosemary needles/leaves and add to a bowl that will be used for making the marinade.?#&Add the olive oil, garlic and 2 tbsp of lemon zest then combine with a stick blender to create a seasoning paste or “slather”.?#&Let the shrimp marinate in the slather for 30 minutes.?#&Meanwhile, you can go prepare your grill while they marinate.?#&While the coals are doing their thing in the chimney starter, use a very sharp knife to thinly slice the lemon into 1/8” slices. The thinner the better as they will be more malleable to go onto the skewers.?#&Next, thread the shrimp onto the rosemary skewers and alternate shrimp with lemon slices that are folded in half. If you don’t have the rosemary skewers, just use regular bamboo skewers (don’t forget to soak them in water beforehand).?#&Prepare your PitBarrel Cooker for cooking on the grill grates.?#&Once your charcoal is ready to go and you are ready to put your skewers on, throw a few rosemary sprigs on the coals for some additional smoke. This will add some nice additional flavor to the shrimp.?#&Get the grill nice and hot and grill the shrimp only a few minutes on each side, looking for them to turn pink and being careful not to overcook.?#&While grilling the shrimp, throw the remaining lemons on the grill until they get charmarks (These will be served with the shrimp and make for great presentation, plus the grill makes them extra juicy for squeezing on the shrimp right before serving)?#&Once they’ve turned pink, take them off and enjoy a flavorful dish that is a true crowd pleaser!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'49FFA2A3A15CC34D85D55E44E28DAAA8', X'47E1CBDE5B49974EA3EB88342B06254C', X'47E1CBDE5B49974EA3EB88342B06254C', 0, 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', 'Smoked Watermelon Gazpacho', 2, NULL, 0, 'Smoked Watermelon Gazpacho', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/09/smoked-watermelon-gazpacho-green-mountain-grills-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Produce:?#&?#&1/2 large watermelon, sliced in wedges?#&3 large cucumbers, skinned and sliced lengthwise?#&1 lb mini bell peppers?#&2 medium sized shallots?#&5 cloves garlic, put them on a skewer so they don’t fall through the grill grates?#&4 medium beefsteak tomatoes, cut in half?#&1 LARGE bunch of Basil, plus more for garnishEtcetera:?#&2 tbsp balsamic vinegar?#&3 tbsp olive oil?#&4 tbsp water?#&1 heaping tbsp. smoked sea salt (I prefer smoked sea salt if you have it!)?#&1 tsp fresh ground pepper (or to taste)', '', 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', '', '', 'Sep 10, 2017', '', 'Instructions?#&?#&Preheat your Green Mountain Grill or your other grill to 350 degrees. While getting your grill heated up, finish slicing your veggies. One your grill is at temperature, put your veggies on. Check your veggies every so often to ensure they are not burning- the garlic and peppers will cook the quickest- don’t let the garlic burn, just put it on long enough to soften and get some smoke. As char marks form, turn the veggies over and let the other side cook. Ideally you are looking for good char marks and the veggies and watermelon to caramelize on the grill. NOTE, the watermelon will not get as charred as the rest of the veggies unless you sliced it extremely thin and that is okay. The inherent nature of this fruit having so much water content takes it longer to get char marks- be patient. As veggies develop the char marks, remove them and let them cool in a big bowl.?#&?#&Once all veggies have been remove, place them in the largest pot you have and get out your stick blender. Now, go ahead and add your additional ingredients: balsamic, olive oil, water, sea salt and pepper and the the basil leaves. Pulse the content with your immersion blender until it is pureed. Don’t over puree it, you want it to have the consistency of a fine salsa.?#&?#&Let chill in the refrigerator for at least an hour before serving. Garnish with additional basil. Great served with grilled bread as an appetizer, also great for a light lunch. Goat cheese or sliced avocado are also a great addition for garnish!?#&?#&Enjoy being the creative cook on your block- I promise your friends and family will rave over this recipe!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'BF103EA30CE1B94CA280816EC3199927', X'2FD57EABD10A3042992F0A9B0FE37CFE', X'2FD57EABD10A3042992F0A9B0FE37CFE', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'8B947716F4F90D4F9E7C0E8EDF904821', X'1B5AD82ACB51334EBCA9CA3D25C64638', X'1B5AD82ACB51334EBCA9CA3D25C64638', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'8C4137A41F201D409D7315636684990E', X'72DF38FF5045614DB5FD6ABF625CDD1D', X'72DF38FF5045614DB5FD6ABF625CDD1D', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'F96B90BC9B1EF94EA9070D8D555C0594', X'13558490386AC342BDBBABDC4DEEFC0E', X'13558490386AC342BDBBABDC4DEEFC0E', 0, 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Kathy Pullin’s Turkey Lasagna Wonton Cups', 2, NULL, 0, 'Turkey Lasagna Wonton Cups', 'Kathy Pullin', 'https://grillgirl.com/wp-content/uploads/2020/05/Kathy-Pullin-Turkey-Lasagna-Wonton-Cups-150x150.jpg', '', '', '15 min', '45 min', '1 hour', '48', 'Ingredients?#&?#&1 pound of ground turkey?#&6 ounces of diced pancetta?#&1/4 cup of diced onion?#&1/2 teaspoon Italian seasoning – pick your favorite?#&1/2 teaspoon of basil flakes?#&1/2 teaspoon parsley flakes?#&1 clove of garlic?#&8 ounces of ricotta cheese?#&8 ounces of shredded mozzarella?#&8 ounces of shredded Italian cheese?#&2 cups of marinara?#&Salt and pepper?#&Lots of wonton wrappers', '', 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Baked', 'turkey, lasagna, wonton, cheese, italian', 'May 6, 2020', 'Turkey', 'Instructions?#&?#&First, grab your ground turkey, pancetta and onion. Once you’ve got those together, put them in a cast iron skillet and let it all sauté over hot coals to get that turkey nice and brown.?#&Once that turkey starts taking to a tan, mince up that garlic clove and toss it into the skillet with the Italian seasoning, 1/4 teaspoon of basil and 1/4 teaspoon of parsley. When that’s simmering, add just a touch of salt and pepper.?#&Is that turkey nice and brown? Toss in the marina and set it all aside.?#&Now is where things get cheesy. Bust out all that ricotta and 6 ounces of mozzarella and another 6 ounces of the shredded Italian cheese. Mix all that cheese and the rest of the basil and parsley in a separate bowl.?#&Grease your favorite cupcake pan and place wonton wrappers in the center of each cupcake holder.?#&Scoop out a teaspoon of the meat mixture, toss that on the wonton wrapper and then top it with a teaspoon of the cheese mix. Room to spare? That’s by design. Add in another teaspoon of meat mix and top it again with shredded cheese. Repeat and then repeat some more.?#&Now it’s time to get hot. Bake all these savory wontons over hot coals at 350 degrees and set your timer for 20 minutes until they’re finished cooking.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'0856E29CEC8E2948B3BF23B53BF3723E', X'7A111E84FD4BC14EA9C908A35F5782EB', X'7A111E84FD4BC14EA9C908A35F5782EB', 0, 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', 'Grilled Watermelon Pizza', 2, NULL, 0, 'Grilled Watermelon Pizza', 'Captain Ron', 'https://grillgirl.com/wp-content/uploads/2019/10/Watermelon-Pizza-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Whole Watermelon (seedless is preferred) ?#&4 Oz package crumbled Feta Cheese ?#&2 Tbsp chopped fresh mint leaves ?#&1 Tbsp olive oil', '', 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', '', '', 'Oct 30, 2019', '', 'Instructions?#&?#&Prepare your grill for direct grilling over medium high heat ?#&Cut your watermelon into discs and coat them lightly with olive oil ?#&Place your oiled watermelon disks directly on the hot grates until there are visible grill marks underneath?#&Flip the watermelon over and top it with your crumbled feta cheese. Cover the grill and let it cook for a few more minutes. The cheese will soften but will not melt. ?#&Remove the watermelon pizza and sprinkle it with the chopped mint Slice your pizza into wedges just as you would cut a regular pizza pie ?#&?#&At this point, we like to drizzle it with a little bit of balsamic glaze for an extra burst of flavor. Let your imagination run wild!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'73DA4B598035C64C9EDEADB022E8739D', X'A4C407F0A4C5C14396E1C910C0240C8F', X'A4C407F0A4C5C14396E1C910C0240C8F', 0, 'https://grillgirl.com/2019/01/top-5-keto-friendly-apps-for-the-super-bowl/', 'Top 5 Keto Friendly Apps for the Super Bowl', 2, NULL, 0, 'Bacon Jalapeno Deviled EggsINSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'68995AB01C0AF04E9A6F77B16F54B7B6', X'C7FF98584EA3614FAC4F5AD819A1C3C8', X'C7FF98584EA3614FAC4F5AD819A1C3C8', 0, 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', 'Cedar Planked Pizza on the Grill', 2, NULL, 0, 'Cedar Planked Pizza on the Grill', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/04/cedar-planked-pizzas-on-grill-edited-2-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One store bought pizza crust from the deli, divided into 4 balls OR a tube of thin crust pizza dough from the refrigerator section (this is easier to get to roll thin than the stuff from the deli!)?#&Toppings of your choice', '', 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', '', '', 'Apr 30, 2018', '', 'Instructions?#&?#&Soak your wood planks for an hour before using. They need to be soaked so they don’t catch fire while grilling.?#&?#&Roll the pizza balls into long rectangles, trying to get as thin as possible and matching the size of the 5”X11” plank. Drizzle the plank with a bit of olive oil and spread around, then add cornmeal- this is done so your pizza is less likely to stick. Place the dough onto the plank. Try to get your dough as thin as possible.?#&?#&Have all of your toppings ready to be added by the grill before you get started.?#&?#&(adsbygoogle = window.adsbygoogle || ``).push({});?#&?#&Heat your grill up to approximately 400 degrees, creating direct and indirect zones. Place the cedar planked pizzas on the indirect side and let the dough cook for about 5-7 minutes so the top has a chance to bake before adding toppings. Next, add the toppings and let continue to cook for another 10 minutes until the cheese has melted and the crust is golden brown.?#&?#&Remove from the grill and slice into pieces- you can use the cedar planks as a serving tray for presentation if you like after they have slightly cooled.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2B5E2EBCF3E14C4D814B6988039AFA15', X'5E8EB3DC1080DC41B19F0DE5950AF19A', X'5E8EB3DC1080DC41B19F0DE5950AF19A', 0, 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 2, NULL, 0, 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/Sri-lankan-chicken-wings-up-close-EDITED-150x150.jpg', '', '', '', '', '', '', 'ingredients for marinade)', '', 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', '', '', 'Dec 8, 2017', '', 'Instructions?#&?#&The making of the spice paste!?#&?#&Combine olive oil, lemongrass/or zest, cilantro, garlic, turmeric, sea salt and pepper in the food processor and pulse until uniformly mixed- you are looking to make a seasoning “paste”. Next, reserve about 2 tbsp of the marinade to drizzle before serving. Marinate the chicken wings for 30 minutes before putting on the grill.?#&?#&Prepare your grill for medium direct heat at 350 degrees. I used my Green Mountain Grill Pellet Smoker and set it for 375 degrees with the smoker holes wide open. Grill your wings, turning halfway during the process, for approximately 15-18 minutes OR until the internal temperature has reached 170 degrees with an internal read thermometer.?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#& ?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#&Once you’ve reached 170 degrees (I recommend using an internal read thermometer such as the thermapen to check internal temp), pull the wings off and drizzle with the remaining marinade. Prepare for out of this world flavor combination! Enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B6818102FC4CA1458EC46DA561F302C5', X'38C874344D53BC4EB649E7FC7D49BB2E', X'38C874344D53BC4EB649E7FC7D49BB2E', 0, 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', 'Rosemary Lemon Shrimp Skewers', 2, NULL, 0, 'Rosemary Lemon Shrimp Skewers', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/rosemary-lemon-shrimp-skewers-EDITED-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 lb jumbo or extra jumbo size shrimp- (buy the largest you can find, they will work the best for threading onto the Rosemary skewers)  shells and tails removed?#&1–2 tbsp olive oil?#&7–8 rosemary stems plus a few extra sprigs of rosemary for the marinade (1/4 cup rosemary needles are needed for the marinade)?#&8 lemons, use for 2 tbsp zest then slice thinly?#&3 cloves garlic?#&1 tbsp course Smoked sea salt (if you can’t find smoked sea salt, regular sea salt is okay)?#&1/2 tbsp fresh ground pepper?#&?#&***If you don’t have rosemary skewers, just use 6-8 bamboo skewers and buy a large bunch of rosemary for the marinade?#&?#&The rosemary stem makes for a fun skewer for the shrimp. Shrimp grills quickly so be careful to not overcook!', '', 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', '', '', 'Dec 2, 2017', '', 'Instructions?#&?#&If you have the rosemary stems, remove the rosemary needles in the middle and set aside. If you don’t have the stems, take your bunch of bunch of rosemary, reserving a few sprigs to throw on the grill, and remove the needles- you will need approximately 1/4 cup of rosemary needles for the marinade.?#&Next, take the rosemary needles/leaves and add to a bowl that will be used for making the marinade.?#&Add the olive oil, garlic and 2 tbsp of lemon zest then combine with a stick blender to create a seasoning paste or “slather”.?#&Let the shrimp marinate in the slather for 30 minutes.?#&Meanwhile, you can go prepare your grill while they marinate.?#&While the coals are doing their thing in the chimney starter, use a very sharp knife to thinly slice the lemon into 1/8” slices. The thinner the better as they will be more malleable to go onto the skewers.?#&Next, thread the shrimp onto the rosemary skewers and alternate shrimp with lemon slices that are folded in half. If you don’t have the rosemary skewers, just use regular bamboo skewers (don’t forget to soak them in water beforehand).?#&Prepare your PitBarrel Cooker for cooking on the grill grates.?#&Once your charcoal is ready to go and you are ready to put your skewers on, throw a few rosemary sprigs on the coals for some additional smoke. This will add some nice additional flavor to the shrimp.?#&Get the grill nice and hot and grill the shrimp only a few minutes on each side, looking for them to turn pink and being careful not to overcook.?#&While grilling the shrimp, throw the remaining lemons on the grill until they get charmarks (These will be served with the shrimp and make for great presentation, plus the grill makes them extra juicy for squeezing on the shrimp right before serving)?#&Once they’ve turned pink, take them off and enjoy a flavorful dish that is a true crowd pleaser!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'C017890D60870748987356F9FFBFA3CF', X'6F64C82CD950784AB0A353C012DB79D1', X'6F64C82CD950784AB0A353C012DB79D1', 0, 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', 'Smoked Watermelon Gazpacho', 2, NULL, 0, 'Smoked Watermelon Gazpacho', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/09/smoked-watermelon-gazpacho-green-mountain-grills-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Produce:?#&?#&1/2 large watermelon, sliced in wedges?#&3 large cucumbers, skinned and sliced lengthwise?#&1 lb mini bell peppers?#&2 medium sized shallots?#&5 cloves garlic, put them on a skewer so they don’t fall through the grill grates?#&4 medium beefsteak tomatoes, cut in half?#&1 LARGE bunch of Basil, plus more for garnishEtcetera:?#&2 tbsp balsamic vinegar?#&3 tbsp olive oil?#&4 tbsp water?#&1 heaping tbsp. smoked sea salt (I prefer smoked sea salt if you have it!)?#&1 tsp fresh ground pepper (or to taste)', '', 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', '', '', 'Sep 10, 2017', '', 'Instructions?#&?#&Preheat your Green Mountain Grill or your other grill to 350 degrees. While getting your grill heated up, finish slicing your veggies. One your grill is at temperature, put your veggies on. Check your veggies every so often to ensure they are not burning- the garlic and peppers will cook the quickest- don’t let the garlic burn, just put it on long enough to soften and get some smoke. As char marks form, turn the veggies over and let the other side cook. Ideally you are looking for good char marks and the veggies and watermelon to caramelize on the grill. NOTE, the watermelon will not get as charred as the rest of the veggies unless you sliced it extremely thin and that is okay. The inherent nature of this fruit having so much water content takes it longer to get char marks- be patient. As veggies develop the char marks, remove them and let them cool in a big bowl.?#&?#&Once all veggies have been remove, place them in the largest pot you have and get out your stick blender. Now, go ahead and add your additional ingredients: balsamic, olive oil, water, sea salt and pepper and the the basil leaves. Pulse the content with your immersion blender until it is pureed. Don’t over puree it, you want it to have the consistency of a fine salsa.?#&?#&Let chill in the refrigerator for at least an hour before serving. Garnish with additional basil. Great served with grilled bread as an appetizer, also great for a light lunch. Goat cheese or sliced avocado are also a great addition for garnish!?#&?#&Enjoy being the creative cook on your block- I promise your friends and family will rave over this recipe!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'656657E2BCD90848B3BA1835FEB4F5C7', X'9C9FCCA51B40BD449C950FEEC68A0AB3', X'9C9FCCA51B40BD449C950FEEC68A0AB3', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'186BD5A8FF4AB84FB06FA780504ECFB7', X'640CBBCE49505E4AACE9EDF06D3DAAD2', X'640CBBCE49505E4AACE9EDF06D3DAAD2', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'9BB0E17B323B894A9B19EC3FD3633966', X'D1022AFCCCF5D948B566FACC304C5F17', X'D1022AFCCCF5D948B566FACC304C5F17', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'206C571E3970434982A8D560C94FF45C', X'9F2ED22C90EE3A4C831C09AB50BFBF42', X'9F2ED22C90EE3A4C831C09AB50BFBF42', 0, 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Kathy Pullin’s Turkey Lasagna Wonton Cups', 2, NULL, 0, 'Turkey Lasagna Wonton Cups', 'Kathy Pullin', 'https://grillgirl.com/wp-content/uploads/2020/05/Kathy-Pullin-Turkey-Lasagna-Wonton-Cups-150x150.jpg', '', '', '15 min', '45 min', '1 hour', '48', 'Ingredients?#&?#&1 pound of ground turkey?#&6 ounces of diced pancetta?#&1/4 cup of diced onion?#&1/2 teaspoon Italian seasoning – pick your favorite?#&1/2 teaspoon of basil flakes?#&1/2 teaspoon parsley flakes?#&1 clove of garlic?#&8 ounces of ricotta cheese?#&8 ounces of shredded mozzarella?#&8 ounces of shredded Italian cheese?#&2 cups of marinara?#&Salt and pepper?#&Lots of wonton wrappers', '', 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Baked', 'turkey, lasagna, wonton, cheese, italian', 'May 6, 2020', 'Turkey', 'Instructions?#&?#&First, grab your ground turkey, pancetta and onion. Once you’ve got those together, put them in a cast iron skillet and let it all sauté over hot coals to get that turkey nice and brown.?#&Once that turkey starts taking to a tan, mince up that garlic clove and toss it into the skillet with the Italian seasoning, 1/4 teaspoon of basil and 1/4 teaspoon of parsley. When that’s simmering, add just a touch of salt and pepper.?#&Is that turkey nice and brown? Toss in the marina and set it all aside.?#&Now is where things get cheesy. Bust out all that ricotta and 6 ounces of mozzarella and another 6 ounces of the shredded Italian cheese. Mix all that cheese and the rest of the basil and parsley in a separate bowl.?#&Grease your favorite cupcake pan and place wonton wrappers in the center of each cupcake holder.?#&Scoop out a teaspoon of the meat mixture, toss that on the wonton wrapper and then top it with a teaspoon of the cheese mix. Room to spare? That’s by design. Add in another teaspoon of meat mix and top it again with shredded cheese. Repeat and then repeat some more.?#&Now it’s time to get hot. Bake all these savory wontons over hot coals at 350 degrees and set your timer for 20 minutes until they’re finished cooking.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'F54A21092608CF418E445F4A8988B5F5', X'210F528AF23BDA4C8655EA86716FA8D5', X'210F528AF23BDA4C8655EA86716FA8D5', 0, 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', 'Grilled Watermelon Pizza', 2, NULL, 0, 'Grilled Watermelon Pizza', 'Captain Ron', 'https://grillgirl.com/wp-content/uploads/2019/10/Watermelon-Pizza-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Whole Watermelon (seedless is preferred) ?#&4 Oz package crumbled Feta Cheese ?#&2 Tbsp chopped fresh mint leaves ?#&1 Tbsp olive oil', '', 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', '', '', 'Oct 30, 2019', '', 'Instructions?#&?#&Prepare your grill for direct grilling over medium high heat ?#&Cut your watermelon into discs and coat them lightly with olive oil ?#&Place your oiled watermelon disks directly on the hot grates until there are visible grill marks underneath?#&Flip the watermelon over and top it with your crumbled feta cheese. Cover the grill and let it cook for a few more minutes. The cheese will soften but will not melt. ?#&Remove the watermelon pizza and sprinkle it with the chopped mint Slice your pizza into wedges just as you would cut a regular pizza pie ?#&?#&At this point, we like to drizzle it with a little bit of balsamic glaze for an extra burst of flavor. Let your imagination run wild!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'7554C52FA49EBE4F97AC2CAE60247C83', X'E12C40AFB0D7E74D805B24385008A275', X'E12C40AFB0D7E74D805B24385008A275', 0, 'https://grillgirl.com/2019/01/top-5-keto-friendly-apps-for-the-super-bowl/', 'Top 5 Keto Friendly Apps for the Super Bowl', 2, NULL, 0, 'Bacon Jalapeno Deviled EggsINSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'8B348D5C709FA640893F7F069D160C43', X'8AC809B1DF25E242AC21D69E90772A70', X'8AC809B1DF25E242AC21D69E90772A70', 0, 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', 'Cedar Planked Pizza on the Grill', 2, NULL, 0, 'Cedar Planked Pizza on the Grill', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/04/cedar-planked-pizzas-on-grill-edited-2-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One store bought pizza crust from the deli, divided into 4 balls OR a tube of thin crust pizza dough from the refrigerator section (this is easier to get to roll thin than the stuff from the deli!)?#&Toppings of your choice', '', 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', '', '', 'Apr 30, 2018', '', 'Instructions?#&?#&Soak your wood planks for an hour before using. They need to be soaked so they don’t catch fire while grilling.?#&?#&Roll the pizza balls into long rectangles, trying to get as thin as possible and matching the size of the 5”X11” plank. Drizzle the plank with a bit of olive oil and spread around, then add cornmeal- this is done so your pizza is less likely to stick. Place the dough onto the plank. Try to get your dough as thin as possible.?#&?#&Have all of your toppings ready to be added by the grill before you get started.?#&?#&(adsbygoogle = window.adsbygoogle || ``).push({});?#&?#&Heat your grill up to approximately 400 degrees, creating direct and indirect zones. Place the cedar planked pizzas on the indirect side and let the dough cook for about 5-7 minutes so the top has a chance to bake before adding toppings. Next, add the toppings and let continue to cook for another 10 minutes until the cheese has melted and the crust is golden brown.?#&?#&Remove from the grill and slice into pieces- you can use the cedar planks as a serving tray for presentation if you like after they have slightly cooled.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'A93433BDE41A714C8DD51A87320A6C6C', X'882EC0CED893354AB7E7D0560ADA50C7', X'882EC0CED893354AB7E7D0560ADA50C7', 0, 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 2, NULL, 0, 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/Sri-lankan-chicken-wings-up-close-EDITED-150x150.jpg', '', '', '', '', '', '', 'ingredients for marinade)', '', 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', '', '', 'Dec 8, 2017', '', 'Instructions?#&?#&The making of the spice paste!?#&?#&Combine olive oil, lemongrass/or zest, cilantro, garlic, turmeric, sea salt and pepper in the food processor and pulse until uniformly mixed- you are looking to make a seasoning “paste”. Next, reserve about 2 tbsp of the marinade to drizzle before serving. Marinate the chicken wings for 30 minutes before putting on the grill.?#&?#&Prepare your grill for medium direct heat at 350 degrees. I used my Green Mountain Grill Pellet Smoker and set it for 375 degrees with the smoker holes wide open. Grill your wings, turning halfway during the process, for approximately 15-18 minutes OR until the internal temperature has reached 170 degrees with an internal read thermometer.?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#& ?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#&Once you’ve reached 170 degrees (I recommend using an internal read thermometer such as the thermapen to check internal temp), pull the wings off and drizzle with the remaining marinade. Prepare for out of this world flavor combination! Enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'652B23758D02974B985790503130BB2F', X'AE3B15F9B47BB04CA6B65366A9F24437', X'AE3B15F9B47BB04CA6B65366A9F24437', 0, 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', 'Rosemary Lemon Shrimp Skewers', 2, NULL, 0, 'Rosemary Lemon Shrimp Skewers', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/rosemary-lemon-shrimp-skewers-EDITED-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 lb jumbo or extra jumbo size shrimp- (buy the largest you can find, they will work the best for threading onto the Rosemary skewers)  shells and tails removed?#&1–2 tbsp olive oil?#&7–8 rosemary stems plus a few extra sprigs of rosemary for the marinade (1/4 cup rosemary needles are needed for the marinade)?#&8 lemons, use for 2 tbsp zest then slice thinly?#&3 cloves garlic?#&1 tbsp course Smoked sea salt (if you can’t find smoked sea salt, regular sea salt is okay)?#&1/2 tbsp fresh ground pepper?#&?#&***If you don’t have rosemary skewers, just use 6-8 bamboo skewers and buy a large bunch of rosemary for the marinade?#&?#&The rosemary stem makes for a fun skewer for the shrimp. Shrimp grills quickly so be careful to not overcook!', '', 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', '', '', 'Dec 2, 2017', '', 'Instructions?#&?#&If you have the rosemary stems, remove the rosemary needles in the middle and set aside. If you don’t have the stems, take your bunch of bunch of rosemary, reserving a few sprigs to throw on the grill, and remove the needles- you will need approximately 1/4 cup of rosemary needles for the marinade.?#&Next, take the rosemary needles/leaves and add to a bowl that will be used for making the marinade.?#&Add the olive oil, garlic and 2 tbsp of lemon zest then combine with a stick blender to create a seasoning paste or “slather”.?#&Let the shrimp marinate in the slather for 30 minutes.?#&Meanwhile, you can go prepare your grill while they marinate.?#&While the coals are doing their thing in the chimney starter, use a very sharp knife to thinly slice the lemon into 1/8” slices. The thinner the better as they will be more malleable to go onto the skewers.?#&Next, thread the shrimp onto the rosemary skewers and alternate shrimp with lemon slices that are folded in half. If you don’t have the rosemary skewers, just use regular bamboo skewers (don’t forget to soak them in water beforehand).?#&Prepare your PitBarrel Cooker for cooking on the grill grates.?#&Once your charcoal is ready to go and you are ready to put your skewers on, throw a few rosemary sprigs on the coals for some additional smoke. This will add some nice additional flavor to the shrimp.?#&Get the grill nice and hot and grill the shrimp only a few minutes on each side, looking for them to turn pink and being careful not to overcook.?#&While grilling the shrimp, throw the remaining lemons on the grill until they get charmarks (These will be served with the shrimp and make for great presentation, plus the grill makes them extra juicy for squeezing on the shrimp right before serving)?#&Once they’ve turned pink, take them off and enjoy a flavorful dish that is a true crowd pleaser!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'7CADE4AB504D904D9C2EA626A921D2F9', X'B178EB457967884AA4AA5EEC18B90B39', X'B178EB457967884AA4AA5EEC18B90B39', 0, 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', 'Smoked Watermelon Gazpacho', 2, NULL, 0, 'Smoked Watermelon Gazpacho', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/09/smoked-watermelon-gazpacho-green-mountain-grills-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Produce:?#&?#&1/2 large watermelon, sliced in wedges?#&3 large cucumbers, skinned and sliced lengthwise?#&1 lb mini bell peppers?#&2 medium sized shallots?#&5 cloves garlic, put them on a skewer so they don’t fall through the grill grates?#&4 medium beefsteak tomatoes, cut in half?#&1 LARGE bunch of Basil, plus more for garnishEtcetera:?#&2 tbsp balsamic vinegar?#&3 tbsp olive oil?#&4 tbsp water?#&1 heaping tbsp. smoked sea salt (I prefer smoked sea salt if you have it!)?#&1 tsp fresh ground pepper (or to taste)', '', 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', '', '', 'Sep 10, 2017', '', 'Instructions?#&?#&Preheat your Green Mountain Grill or your other grill to 350 degrees. While getting your grill heated up, finish slicing your veggies. One your grill is at temperature, put your veggies on. Check your veggies every so often to ensure they are not burning- the garlic and peppers will cook the quickest- don’t let the garlic burn, just put it on long enough to soften and get some smoke. As char marks form, turn the veggies over and let the other side cook. Ideally you are looking for good char marks and the veggies and watermelon to caramelize on the grill. NOTE, the watermelon will not get as charred as the rest of the veggies unless you sliced it extremely thin and that is okay. The inherent nature of this fruit having so much water content takes it longer to get char marks- be patient. As veggies develop the char marks, remove them and let them cool in a big bowl.?#&?#&Once all veggies have been remove, place them in the largest pot you have and get out your stick blender. Now, go ahead and add your additional ingredients: balsamic, olive oil, water, sea salt and pepper and the the basil leaves. Pulse the content with your immersion blender until it is pureed. Don’t over puree it, you want it to have the consistency of a fine salsa.?#&?#&Let chill in the refrigerator for at least an hour before serving. Garnish with additional basil. Great served with grilled bread as an appetizer, also great for a light lunch. Goat cheese or sliced avocado are also a great addition for garnish!?#&?#&Enjoy being the creative cook on your block- I promise your friends and family will rave over this recipe!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'16A8438E1816394DA1EE442652FCB14C', X'5A310F6129A9134AB5B9C2F96A27C7B9', X'5A310F6129A9134AB5B9C2F96A27C7B9', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'21942BB30BDF1B4CAD28BAEDB279CE30', X'C3E90A386E068E4893584DCE97838374', X'C3E90A386E068E4893584DCE97838374', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'62E71D899FA55A498E374BC6DCD8C395', X'57A0B01006D68647BB38C34DA8188BC2', X'57A0B01006D68647BB38C34DA8188BC2', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2F553D4E6DF9CB4889AB88B3D11EA4F7', X'14CEF155B3F94244B9FD53F9BBC6EB2D', X'14CEF155B3F94244B9FD53F9BBC6EB2D', 0, 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Kathy Pullin’s Turkey Lasagna Wonton Cups', 2, NULL, 0, 'Turkey Lasagna Wonton Cups', 'Kathy Pullin', 'https://grillgirl.com/wp-content/uploads/2020/05/Kathy-Pullin-Turkey-Lasagna-Wonton-Cups-150x150.jpg', '', '', '15 min', '45 min', '1 hour', '48', 'Ingredients?#&?#&1 pound of ground turkey?#&6 ounces of diced pancetta?#&1/4 cup of diced onion?#&1/2 teaspoon Italian seasoning – pick your favorite?#&1/2 teaspoon of basil flakes?#&1/2 teaspoon parsley flakes?#&1 clove of garlic?#&8 ounces of ricotta cheese?#&8 ounces of shredded mozzarella?#&8 ounces of shredded Italian cheese?#&2 cups of marinara?#&Salt and pepper?#&Lots of wonton wrappers', '', 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Baked', 'turkey, lasagna, wonton, cheese, italian', 'May 6, 2020', 'Turkey', 'Instructions?#&?#&First, grab your ground turkey, pancetta and onion. Once you’ve got those together, put them in a cast iron skillet and let it all sauté over hot coals to get that turkey nice and brown.?#&Once that turkey starts taking to a tan, mince up that garlic clove and toss it into the skillet with the Italian seasoning, 1/4 teaspoon of basil and 1/4 teaspoon of parsley. When that’s simmering, add just a touch of salt and pepper.?#&Is that turkey nice and brown? Toss in the marina and set it all aside.?#&Now is where things get cheesy. Bust out all that ricotta and 6 ounces of mozzarella and another 6 ounces of the shredded Italian cheese. Mix all that cheese and the rest of the basil and parsley in a separate bowl.?#&Grease your favorite cupcake pan and place wonton wrappers in the center of each cupcake holder.?#&Scoop out a teaspoon of the meat mixture, toss that on the wonton wrapper and then top it with a teaspoon of the cheese mix. Room to spare? That’s by design. Add in another teaspoon of meat mix and top it again with shredded cheese. Repeat and then repeat some more.?#&Now it’s time to get hot. Bake all these savory wontons over hot coals at 350 degrees and set your timer for 20 minutes until they’re finished cooking.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'188E5A1078EE9047B001941446C38EFA', X'C29D63048A9BB8468C29FD41B98CE2E8', X'C29D63048A9BB8468C29FD41B98CE2E8', 0, 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', 'Grilled Watermelon Pizza', 2, NULL, 0, 'Grilled Watermelon Pizza', 'Captain Ron', 'https://grillgirl.com/wp-content/uploads/2019/10/Watermelon-Pizza-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Whole Watermelon (seedless is preferred) ?#&4 Oz package crumbled Feta Cheese ?#&2 Tbsp chopped fresh mint leaves ?#&1 Tbsp olive oil', '', 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', '', '', 'Oct 30, 2019', '', 'Instructions?#&?#&Prepare your grill for direct grilling over medium high heat ?#&Cut your watermelon into discs and coat them lightly with olive oil ?#&Place your oiled watermelon disks directly on the hot grates until there are visible grill marks underneath?#&Flip the watermelon over and top it with your crumbled feta cheese. Cover the grill and let it cook for a few more minutes. The cheese will soften but will not melt. ?#&Remove the watermelon pizza and sprinkle it with the chopped mint Slice your pizza into wedges just as you would cut a regular pizza pie ?#&?#&At this point, we like to drizzle it with a little bit of balsamic glaze for an extra burst of flavor. Let your imagination run wild!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'30B29D85BDC87048BE449DB06DF6B550', X'67CAFF1A4BBC2A489C72A3C52E58E668', X'67CAFF1A4BBC2A489C72A3C52E58E668', 0, 'https://grillgirl.com/2019/01/top-5-keto-friendly-apps-for-the-super-bowl/', 'Top 5 Keto Friendly Apps for the Super Bowl', 2, NULL, 0, 'Bacon Jalapeno Deviled EggsINSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'0339846DB446D648A9415AC61545DFDE', X'7E6F7C596769F54B93D76DE86C928FD5', X'7E6F7C596769F54B93D76DE86C928FD5', 0, 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', 'Cedar Planked Pizza on the Grill', 2, NULL, 0, 'Cedar Planked Pizza on the Grill', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/04/cedar-planked-pizzas-on-grill-edited-2-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One store bought pizza crust from the deli, divided into 4 balls OR a tube of thin crust pizza dough from the refrigerator section (this is easier to get to roll thin than the stuff from the deli!)?#&Toppings of your choice', '', 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', '', '', 'Apr 30, 2018', '', 'Instructions?#&?#&Soak your wood planks for an hour before using. They need to be soaked so they don’t catch fire while grilling.?#&?#&Roll the pizza balls into long rectangles, trying to get as thin as possible and matching the size of the 5”X11” plank. Drizzle the plank with a bit of olive oil and spread around, then add cornmeal- this is done so your pizza is less likely to stick. Place the dough onto the plank. Try to get your dough as thin as possible.?#&?#&Have all of your toppings ready to be added by the grill before you get started.?#&?#&(adsbygoogle = window.adsbygoogle || ``).push({});?#&?#&Heat your grill up to approximately 400 degrees, creating direct and indirect zones. Place the cedar planked pizzas on the indirect side and let the dough cook for about 5-7 minutes so the top has a chance to bake before adding toppings. Next, add the toppings and let continue to cook for another 10 minutes until the cheese has melted and the crust is golden brown.?#&?#&Remove from the grill and slice into pieces- you can use the cedar planks as a serving tray for presentation if you like after they have slightly cooled.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'CA17AD5C6D4ED347A6A33D838E1FB4B9', X'81FADCA552359345A332FC60444541BF', X'81FADCA552359345A332FC60444541BF', 0, 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 2, NULL, 0, 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/Sri-lankan-chicken-wings-up-close-EDITED-150x150.jpg', '', '', '', '', '', '', 'ingredients for marinade)', '', 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', '', '', 'Dec 8, 2017', '', 'Instructions?#&?#&The making of the spice paste!?#&?#&Combine olive oil, lemongrass/or zest, cilantro, garlic, turmeric, sea salt and pepper in the food processor and pulse until uniformly mixed- you are looking to make a seasoning “paste”. Next, reserve about 2 tbsp of the marinade to drizzle before serving. Marinate the chicken wings for 30 minutes before putting on the grill.?#&?#&Prepare your grill for medium direct heat at 350 degrees. I used my Green Mountain Grill Pellet Smoker and set it for 375 degrees with the smoker holes wide open. Grill your wings, turning halfway during the process, for approximately 15-18 minutes OR until the internal temperature has reached 170 degrees with an internal read thermometer.?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#& ?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#&Once you’ve reached 170 degrees (I recommend using an internal read thermometer such as the thermapen to check internal temp), pull the wings off and drizzle with the remaining marinade. Prepare for out of this world flavor combination! Enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'F7BBBEE0C879E44DA32C5680127FC74F', X'EC571C39B2FB53458506C87D217B4A80', X'EC571C39B2FB53458506C87D217B4A80', 0, 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', 'Rosemary Lemon Shrimp Skewers', 2, NULL, 0, 'Rosemary Lemon Shrimp Skewers', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/rosemary-lemon-shrimp-skewers-EDITED-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 lb jumbo or extra jumbo size shrimp- (buy the largest you can find, they will work the best for threading onto the Rosemary skewers)  shells and tails removed?#&1–2 tbsp olive oil?#&7–8 rosemary stems plus a few extra sprigs of rosemary for the marinade (1/4 cup rosemary needles are needed for the marinade)?#&8 lemons, use for 2 tbsp zest then slice thinly?#&3 cloves garlic?#&1 tbsp course Smoked sea salt (if you can’t find smoked sea salt, regular sea salt is okay)?#&1/2 tbsp fresh ground pepper?#&?#&***If you don’t have rosemary skewers, just use 6-8 bamboo skewers and buy a large bunch of rosemary for the marinade?#&?#&The rosemary stem makes for a fun skewer for the shrimp. Shrimp grills quickly so be careful to not overcook!', '', 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', '', '', 'Dec 2, 2017', '', 'Instructions?#&?#&If you have the rosemary stems, remove the rosemary needles in the middle and set aside. If you don’t have the stems, take your bunch of bunch of rosemary, reserving a few sprigs to throw on the grill, and remove the needles- you will need approximately 1/4 cup of rosemary needles for the marinade.?#&Next, take the rosemary needles/leaves and add to a bowl that will be used for making the marinade.?#&Add the olive oil, garlic and 2 tbsp of lemon zest then combine with a stick blender to create a seasoning paste or “slather”.?#&Let the shrimp marinate in the slather for 30 minutes.?#&Meanwhile, you can go prepare your grill while they marinate.?#&While the coals are doing their thing in the chimney starter, use a very sharp knife to thinly slice the lemon into 1/8” slices. The thinner the better as they will be more malleable to go onto the skewers.?#&Next, thread the shrimp onto the rosemary skewers and alternate shrimp with lemon slices that are folded in half. If you don’t have the rosemary skewers, just use regular bamboo skewers (don’t forget to soak them in water beforehand).?#&Prepare your PitBarrel Cooker for cooking on the grill grates.?#&Once your charcoal is ready to go and you are ready to put your skewers on, throw a few rosemary sprigs on the coals for some additional smoke. This will add some nice additional flavor to the shrimp.?#&Get the grill nice and hot and grill the shrimp only a few minutes on each side, looking for them to turn pink and being careful not to overcook.?#&While grilling the shrimp, throw the remaining lemons on the grill until they get charmarks (These will be served with the shrimp and make for great presentation, plus the grill makes them extra juicy for squeezing on the shrimp right before serving)?#&Once they’ve turned pink, take them off and enjoy a flavorful dish that is a true crowd pleaser!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'660B56DD50744940845C3DAE9C62DBEB', X'9E0586522EFDE945A1D4B98A0C83C722', X'9E0586522EFDE945A1D4B98A0C83C722', 0, 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', 'Smoked Watermelon Gazpacho', 2, NULL, 0, 'Smoked Watermelon Gazpacho', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/09/smoked-watermelon-gazpacho-green-mountain-grills-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Produce:?#&?#&1/2 large watermelon, sliced in wedges?#&3 large cucumbers, skinned and sliced lengthwise?#&1 lb mini bell peppers?#&2 medium sized shallots?#&5 cloves garlic, put them on a skewer so they don’t fall through the grill grates?#&4 medium beefsteak tomatoes, cut in half?#&1 LARGE bunch of Basil, plus more for garnishEtcetera:?#&2 tbsp balsamic vinegar?#&3 tbsp olive oil?#&4 tbsp water?#&1 heaping tbsp. smoked sea salt (I prefer smoked sea salt if you have it!)?#&1 tsp fresh ground pepper (or to taste)', '', 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', '', '', 'Sep 10, 2017', '', 'Instructions?#&?#&Preheat your Green Mountain Grill or your other grill to 350 degrees. While getting your grill heated up, finish slicing your veggies. One your grill is at temperature, put your veggies on. Check your veggies every so often to ensure they are not burning- the garlic and peppers will cook the quickest- don’t let the garlic burn, just put it on long enough to soften and get some smoke. As char marks form, turn the veggies over and let the other side cook. Ideally you are looking for good char marks and the veggies and watermelon to caramelize on the grill. NOTE, the watermelon will not get as charred as the rest of the veggies unless you sliced it extremely thin and that is okay. The inherent nature of this fruit having so much water content takes it longer to get char marks- be patient. As veggies develop the char marks, remove them and let them cool in a big bowl.?#&?#&Once all veggies have been remove, place them in the largest pot you have and get out your stick blender. Now, go ahead and add your additional ingredients: balsamic, olive oil, water, sea salt and pepper and the the basil leaves. Pulse the content with your immersion blender until it is pureed. Don’t over puree it, you want it to have the consistency of a fine salsa.?#&?#&Let chill in the refrigerator for at least an hour before serving. Garnish with additional basil. Great served with grilled bread as an appetizer, also great for a light lunch. Goat cheese or sliced avocado are also a great addition for garnish!?#&?#&Enjoy being the creative cook on your block- I promise your friends and family will rave over this recipe!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'C8AF3DCD56A6654C8848A96BC0A39192', X'A1BC495746D303409010032E92E1142A', X'A1BC495746D303409010032E92E1142A', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'E03AC2B571C9C340A53E64DF9EC6C324', X'68F363F832EB01418249FBCF88341C9F', X'68F363F832EB01418249FBCF88341C9F', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'F8862C2DAE40394AAF727300C213C2B1', X'B3BB0E90D751DD4188FC35E3C1BE334C', X'B3BB0E90D751DD4188FC35E3C1BE334C', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'7B12DD6077BC3447A8C907D020023219', X'795A6AD1772BF44FBD43389DA90FAB55', X'795A6AD1772BF44FBD43389DA90FAB55', 0, 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Kathy Pullin’s Turkey Lasagna Wonton Cups', 2, NULL, 0, 'Turkey Lasagna Wonton Cups', 'Kathy Pullin', 'https://grillgirl.com/wp-content/uploads/2020/05/Kathy-Pullin-Turkey-Lasagna-Wonton-Cups-150x150.jpg', '', '', '15 min', '45 min', '1 hour', '48', 'Ingredients?#&?#&1 pound of ground turkey?#&6 ounces of diced pancetta?#&1/4 cup of diced onion?#&1/2 teaspoon Italian seasoning – pick your favorite?#&1/2 teaspoon of basil flakes?#&1/2 teaspoon parsley flakes?#&1 clove of garlic?#&8 ounces of ricotta cheese?#&8 ounces of shredded mozzarella?#&8 ounces of shredded Italian cheese?#&2 cups of marinara?#&Salt and pepper?#&Lots of wonton wrappers', '', 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Baked', 'turkey, lasagna, wonton, cheese, italian', 'May 6, 2020', 'Turkey', 'Instructions?#&?#&First, grab your ground turkey, pancetta and onion. Once you’ve got those together, put them in a cast iron skillet and let it all sauté over hot coals to get that turkey nice and brown.?#&Once that turkey starts taking to a tan, mince up that garlic clove and toss it into the skillet with the Italian seasoning, 1/4 teaspoon of basil and 1/4 teaspoon of parsley. When that’s simmering, add just a touch of salt and pepper.?#&Is that turkey nice and brown? Toss in the marina and set it all aside.?#&Now is where things get cheesy. Bust out all that ricotta and 6 ounces of mozzarella and another 6 ounces of the shredded Italian cheese. Mix all that cheese and the rest of the basil and parsley in a separate bowl.?#&Grease your favorite cupcake pan and place wonton wrappers in the center of each cupcake holder.?#&Scoop out a teaspoon of the meat mixture, toss that on the wonton wrapper and then top it with a teaspoon of the cheese mix. Room to spare? That’s by design. Add in another teaspoon of meat mix and top it again with shredded cheese. Repeat and then repeat some more.?#&Now it’s time to get hot. Bake all these savory wontons over hot coals at 350 degrees and set your timer for 20 minutes until they’re finished cooking.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'178CCD68D461C54EBB1F415FE0212A81', X'18FB37E1C2021C47A7A4F30CBAF70D3D', X'18FB37E1C2021C47A7A4F30CBAF70D3D', 0, 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', 'Grilled Watermelon Pizza', 2, NULL, 0, 'Grilled Watermelon Pizza', 'Captain Ron', 'https://grillgirl.com/wp-content/uploads/2019/10/Watermelon-Pizza-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Whole Watermelon (seedless is preferred) ?#&4 Oz package crumbled Feta Cheese ?#&2 Tbsp chopped fresh mint leaves ?#&1 Tbsp olive oil', '', 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', '', '', 'Oct 30, 2019', '', 'Instructions?#&?#&Prepare your grill for direct grilling over medium high heat ?#&Cut your watermelon into discs and coat them lightly with olive oil ?#&Place your oiled watermelon disks directly on the hot grates until there are visible grill marks underneath?#&Flip the watermelon over and top it with your crumbled feta cheese. Cover the grill and let it cook for a few more minutes. The cheese will soften but will not melt. ?#&Remove the watermelon pizza and sprinkle it with the chopped mint Slice your pizza into wedges just as you would cut a regular pizza pie ?#&?#&At this point, we like to drizzle it with a little bit of balsamic glaze for an extra burst of flavor. Let your imagination run wild!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'13B6AB0A1DF7EF44B84F86C79CEBAEF4', X'F286EA3F6F58644B979ABA3808197FEB', X'F286EA3F6F58644B979ABA3808197FEB', 0, 'https://grillgirl.com/2019/01/top-5-keto-friendly-apps-for-the-super-bowl/', 'Top 5 Keto Friendly Apps for the Super Bowl', 2, NULL, 0, 'Bacon Jalapeno Deviled EggsINSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'822EF623E590304CA8FAA188434019D7', X'36E836DE27C48E4C866E67EC4B0B2C6D', X'36E836DE27C48E4C866E67EC4B0B2C6D', 0, 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', 'Cedar Planked Pizza on the Grill', 2, NULL, 0, 'Cedar Planked Pizza on the Grill', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/04/cedar-planked-pizzas-on-grill-edited-2-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One store bought pizza crust from the deli, divided into 4 balls OR a tube of thin crust pizza dough from the refrigerator section (this is easier to get to roll thin than the stuff from the deli!)?#&Toppings of your choice', '', 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', '', '', 'Apr 30, 2018', '', 'Instructions?#&?#&Soak your wood planks for an hour before using. They need to be soaked so they don’t catch fire while grilling.?#&?#&Roll the pizza balls into long rectangles, trying to get as thin as possible and matching the size of the 5”X11” plank. Drizzle the plank with a bit of olive oil and spread around, then add cornmeal- this is done so your pizza is less likely to stick. Place the dough onto the plank. Try to get your dough as thin as possible.?#&?#&Have all of your toppings ready to be added by the grill before you get started.?#&?#&(adsbygoogle = window.adsbygoogle || ``).push({});?#&?#&Heat your grill up to approximately 400 degrees, creating direct and indirect zones. Place the cedar planked pizzas on the indirect side and let the dough cook for about 5-7 minutes so the top has a chance to bake before adding toppings. Next, add the toppings and let continue to cook for another 10 minutes until the cheese has melted and the crust is golden brown.?#&?#&Remove from the grill and slice into pieces- you can use the cedar planks as a serving tray for presentation if you like after they have slightly cooled.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'C86B8E9920D98C4887DA2C20ED19CC63', X'8ADA1E1AC0891F439BC2EB7BCBF5F1EE', X'8ADA1E1AC0891F439BC2EB7BCBF5F1EE', 0, 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 2, NULL, 0, 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/Sri-lankan-chicken-wings-up-close-EDITED-150x150.jpg', '', '', '', '', '', '', 'ingredients for marinade)', '', 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', '', '', 'Dec 8, 2017', '', 'Instructions?#&?#&The making of the spice paste!?#&?#&Combine olive oil, lemongrass/or zest, cilantro, garlic, turmeric, sea salt and pepper in the food processor and pulse until uniformly mixed- you are looking to make a seasoning “paste”. Next, reserve about 2 tbsp of the marinade to drizzle before serving. Marinate the chicken wings for 30 minutes before putting on the grill.?#&?#&Prepare your grill for medium direct heat at 350 degrees. I used my Green Mountain Grill Pellet Smoker and set it for 375 degrees with the smoker holes wide open. Grill your wings, turning halfway during the process, for approximately 15-18 minutes OR until the internal temperature has reached 170 degrees with an internal read thermometer.?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#& ?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#&Once you’ve reached 170 degrees (I recommend using an internal read thermometer such as the thermapen to check internal temp), pull the wings off and drizzle with the remaining marinade. Prepare for out of this world flavor combination! Enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'3C853B47A84E644F9E251F9645AD14D2', X'72A7EA2BD002184EBFF790D0F475EA75', X'72A7EA2BD002184EBFF790D0F475EA75', 0, 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', 'Rosemary Lemon Shrimp Skewers', 2, NULL, 0, 'Rosemary Lemon Shrimp Skewers', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/rosemary-lemon-shrimp-skewers-EDITED-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 lb jumbo or extra jumbo size shrimp- (buy the largest you can find, they will work the best for threading onto the Rosemary skewers)  shells and tails removed?#&1–2 tbsp olive oil?#&7–8 rosemary stems plus a few extra sprigs of rosemary for the marinade (1/4 cup rosemary needles are needed for the marinade)?#&8 lemons, use for 2 tbsp zest then slice thinly?#&3 cloves garlic?#&1 tbsp course Smoked sea salt (if you can’t find smoked sea salt, regular sea salt is okay)?#&1/2 tbsp fresh ground pepper?#&?#&***If you don’t have rosemary skewers, just use 6-8 bamboo skewers and buy a large bunch of rosemary for the marinade?#&?#&The rosemary stem makes for a fun skewer for the shrimp. Shrimp grills quickly so be careful to not overcook!', '', 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', '', '', 'Dec 2, 2017', '', 'Instructions?#&?#&If you have the rosemary stems, remove the rosemary needles in the middle and set aside. If you don’t have the stems, take your bunch of bunch of rosemary, reserving a few sprigs to throw on the grill, and remove the needles- you will need approximately 1/4 cup of rosemary needles for the marinade.?#&Next, take the rosemary needles/leaves and add to a bowl that will be used for making the marinade.?#&Add the olive oil, garlic and 2 tbsp of lemon zest then combine with a stick blender to create a seasoning paste or “slather”.?#&Let the shrimp marinate in the slather for 30 minutes.?#&Meanwhile, you can go prepare your grill while they marinate.?#&While the coals are doing their thing in the chimney starter, use a very sharp knife to thinly slice the lemon into 1/8” slices. The thinner the better as they will be more malleable to go onto the skewers.?#&Next, thread the shrimp onto the rosemary skewers and alternate shrimp with lemon slices that are folded in half. If you don’t have the rosemary skewers, just use regular bamboo skewers (don’t forget to soak them in water beforehand).?#&Prepare your PitBarrel Cooker for cooking on the grill grates.?#&Once your charcoal is ready to go and you are ready to put your skewers on, throw a few rosemary sprigs on the coals for some additional smoke. This will add some nice additional flavor to the shrimp.?#&Get the grill nice and hot and grill the shrimp only a few minutes on each side, looking for them to turn pink and being careful not to overcook.?#&While grilling the shrimp, throw the remaining lemons on the grill until they get charmarks (These will be served with the shrimp and make for great presentation, plus the grill makes them extra juicy for squeezing on the shrimp right before serving)?#&Once they’ve turned pink, take them off and enjoy a flavorful dish that is a true crowd pleaser!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'4AFC6B58B03B3C41A8DC968171AFD063', X'62A87F39FB53EA4881336C54B947F118', X'62A87F39FB53EA4881336C54B947F118', 0, 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', 'Smoked Watermelon Gazpacho', 2, NULL, 0, 'Smoked Watermelon Gazpacho', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/09/smoked-watermelon-gazpacho-green-mountain-grills-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Produce:?#&?#&1/2 large watermelon, sliced in wedges?#&3 large cucumbers, skinned and sliced lengthwise?#&1 lb mini bell peppers?#&2 medium sized shallots?#&5 cloves garlic, put them on a skewer so they don’t fall through the grill grates?#&4 medium beefsteak tomatoes, cut in half?#&1 LARGE bunch of Basil, plus more for garnishEtcetera:?#&2 tbsp balsamic vinegar?#&3 tbsp olive oil?#&4 tbsp water?#&1 heaping tbsp. smoked sea salt (I prefer smoked sea salt if you have it!)?#&1 tsp fresh ground pepper (or to taste)', '', 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', '', '', 'Sep 10, 2017', '', 'Instructions?#&?#&Preheat your Green Mountain Grill or your other grill to 350 degrees. While getting your grill heated up, finish slicing your veggies. One your grill is at temperature, put your veggies on. Check your veggies every so often to ensure they are not burning- the garlic and peppers will cook the quickest- don’t let the garlic burn, just put it on long enough to soften and get some smoke. As char marks form, turn the veggies over and let the other side cook. Ideally you are looking for good char marks and the veggies and watermelon to caramelize on the grill. NOTE, the watermelon will not get as charred as the rest of the veggies unless you sliced it extremely thin and that is okay. The inherent nature of this fruit having so much water content takes it longer to get char marks- be patient. As veggies develop the char marks, remove them and let them cool in a big bowl.?#&?#&Once all veggies have been remove, place them in the largest pot you have and get out your stick blender. Now, go ahead and add your additional ingredients: balsamic, olive oil, water, sea salt and pepper and the the basil leaves. Pulse the content with your immersion blender until it is pureed. Don’t over puree it, you want it to have the consistency of a fine salsa.?#&?#&Let chill in the refrigerator for at least an hour before serving. Garnish with additional basil. Great served with grilled bread as an appetizer, also great for a light lunch. Goat cheese or sliced avocado are also a great addition for garnish!?#&?#&Enjoy being the creative cook on your block- I promise your friends and family will rave over this recipe!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'E989FD873465224DAD1AD2C423524149', X'9305CA9EEE2133418BB448255ED7119C', X'9305CA9EEE2133418BB448255ED7119C', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B8379AE37214E64C94136E262B39277A', X'1305F6E90040DB4B99FAD9D447862D10', X'1305F6E90040DB4B99FAD9D447862D10', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'3FC97130A13A1D4FAD345CBA18D7647E', X'F0FBF4BD5A7BEB40BF4F149F43EE3EC6', X'F0FBF4BD5A7BEB40BF4F149F43EE3EC6', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'A20078852E8492409E209C67014C3727', X'71BA686CBDEA01408FE7D65C3BD93CDA', X'71BA686CBDEA01408FE7D65C3BD93CDA', 0, 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Kathy Pullin’s Turkey Lasagna Wonton Cups', 2, NULL, 0, 'Turkey Lasagna Wonton Cups', 'Kathy Pullin', 'https://grillgirl.com/wp-content/uploads/2020/05/Kathy-Pullin-Turkey-Lasagna-Wonton-Cups-150x150.jpg', '', '', '15 min', '45 min', '1 hour', '48', 'Ingredients?#&?#&1 pound of ground turkey?#&6 ounces of diced pancetta?#&1/4 cup of diced onion?#&1/2 teaspoon Italian seasoning – pick your favorite?#&1/2 teaspoon of basil flakes?#&1/2 teaspoon parsley flakes?#&1 clove of garlic?#&8 ounces of ricotta cheese?#&8 ounces of shredded mozzarella?#&8 ounces of shredded Italian cheese?#&2 cups of marinara?#&Salt and pepper?#&Lots of wonton wrappers', '', 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Baked', 'turkey, lasagna, wonton, cheese, italian', 'May 6, 2020', 'Turkey', 'Instructions?#&?#&First, grab your ground turkey, pancetta and onion. Once you’ve got those together, put them in a cast iron skillet and let it all sauté over hot coals to get that turkey nice and brown.?#&Once that turkey starts taking to a tan, mince up that garlic clove and toss it into the skillet with the Italian seasoning, 1/4 teaspoon of basil and 1/4 teaspoon of parsley. When that’s simmering, add just a touch of salt and pepper.?#&Is that turkey nice and brown? Toss in the marina and set it all aside.?#&Now is where things get cheesy. Bust out all that ricotta and 6 ounces of mozzarella and another 6 ounces of the shredded Italian cheese. Mix all that cheese and the rest of the basil and parsley in a separate bowl.?#&Grease your favorite cupcake pan and place wonton wrappers in the center of each cupcake holder.?#&Scoop out a teaspoon of the meat mixture, toss that on the wonton wrapper and then top it with a teaspoon of the cheese mix. Room to spare? That’s by design. Add in another teaspoon of meat mix and top it again with shredded cheese. Repeat and then repeat some more.?#&Now it’s time to get hot. Bake all these savory wontons over hot coals at 350 degrees and set your timer for 20 minutes until they’re finished cooking.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'76E76BB1B9B1934C858AFFCF0ABA8666', X'AB9CCC901E8B6F49A8628288B4F32FCC', X'AB9CCC901E8B6F49A8628288B4F32FCC', 0, 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', 'Grilled Watermelon Pizza', 2, NULL, 0, 'Grilled Watermelon Pizza', 'Captain Ron', 'https://grillgirl.com/wp-content/uploads/2019/10/Watermelon-Pizza-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Whole Watermelon (seedless is preferred) ?#&4 Oz package crumbled Feta Cheese ?#&2 Tbsp chopped fresh mint leaves ?#&1 Tbsp olive oil', '', 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', '', '', 'Oct 30, 2019', '', 'Instructions?#&?#&Prepare your grill for direct grilling over medium high heat ?#&Cut your watermelon into discs and coat them lightly with olive oil ?#&Place your oiled watermelon disks directly on the hot grates until there are visible grill marks underneath?#&Flip the watermelon over and top it with your crumbled feta cheese. Cover the grill and let it cook for a few more minutes. The cheese will soften but will not melt. ?#&Remove the watermelon pizza and sprinkle it with the chopped mint Slice your pizza into wedges just as you would cut a regular pizza pie ?#&?#&At this point, we like to drizzle it with a little bit of balsamic glaze for an extra burst of flavor. Let your imagination run wild!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'9A0BE66380F163498A5A5568DD115D59', X'6E5078F93F8FBE4B8BC8D8EC8BDBBB50', X'6E5078F93F8FBE4B8BC8D8EC8BDBBB50', 0, 'https://grillgirl.com/2019/01/top-5-keto-friendly-apps-for-the-super-bowl/', 'Top 5 Keto Friendly Apps for the Super Bowl', 2, NULL, 0, 'Bacon Jalapeno Deviled EggsINSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'D594D848FEB18942A851F462E19ED894', X'06CBFA95F733534998209C4C763CCA40', X'06CBFA95F733534998209C4C763CCA40', 0, 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', 'Cedar Planked Pizza on the Grill', 2, NULL, 0, 'Cedar Planked Pizza on the Grill', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/04/cedar-planked-pizzas-on-grill-edited-2-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One store bought pizza crust from the deli, divided into 4 balls OR a tube of thin crust pizza dough from the refrigerator section (this is easier to get to roll thin than the stuff from the deli!)?#&Toppings of your choice', '', 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', '', '', 'Apr 30, 2018', '', 'Instructions?#&?#&Soak your wood planks for an hour before using. They need to be soaked so they don’t catch fire while grilling.?#&?#&Roll the pizza balls into long rectangles, trying to get as thin as possible and matching the size of the 5”X11” plank. Drizzle the plank with a bit of olive oil and spread around, then add cornmeal- this is done so your pizza is less likely to stick. Place the dough onto the plank. Try to get your dough as thin as possible.?#&?#&Have all of your toppings ready to be added by the grill before you get started.?#&?#&(adsbygoogle = window.adsbygoogle || ``).push({});?#&?#&Heat your grill up to approximately 400 degrees, creating direct and indirect zones. Place the cedar planked pizzas on the indirect side and let the dough cook for about 5-7 minutes so the top has a chance to bake before adding toppings. Next, add the toppings and let continue to cook for another 10 minutes until the cheese has melted and the crust is golden brown.?#&?#&Remove from the grill and slice into pieces- you can use the cedar planks as a serving tray for presentation if you like after they have slightly cooled.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'769955664278AD409FDF32443B2BC961', X'26153CDFD370894A8B865E593611ED42', X'26153CDFD370894A8B865E593611ED42', 0, 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 2, NULL, 0, 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/Sri-lankan-chicken-wings-up-close-EDITED-150x150.jpg', '', '', '', '', '', '', 'ingredients for marinade)', '', 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', '', '', 'Dec 8, 2017', '', 'Instructions?#&?#&The making of the spice paste!?#&?#&Combine olive oil, lemongrass/or zest, cilantro, garlic, turmeric, sea salt and pepper in the food processor and pulse until uniformly mixed- you are looking to make a seasoning “paste”. Next, reserve about 2 tbsp of the marinade to drizzle before serving. Marinate the chicken wings for 30 minutes before putting on the grill.?#&?#&Prepare your grill for medium direct heat at 350 degrees. I used my Green Mountain Grill Pellet Smoker and set it for 375 degrees with the smoker holes wide open. Grill your wings, turning halfway during the process, for approximately 15-18 minutes OR until the internal temperature has reached 170 degrees with an internal read thermometer.?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#& ?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#&Once you’ve reached 170 degrees (I recommend using an internal read thermometer such as the thermapen to check internal temp), pull the wings off and drizzle with the remaining marinade. Prepare for out of this world flavor combination! Enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'71103C5C3DCC064F926D153FADC7D178', X'6106E8CB49363643905708AF7D0C87B2', X'6106E8CB49363643905708AF7D0C87B2', 0, 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', 'Rosemary Lemon Shrimp Skewers', 2, NULL, 0, 'Rosemary Lemon Shrimp Skewers', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/rosemary-lemon-shrimp-skewers-EDITED-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 lb jumbo or extra jumbo size shrimp- (buy the largest you can find, they will work the best for threading onto the Rosemary skewers)  shells and tails removed?#&1–2 tbsp olive oil?#&7–8 rosemary stems plus a few extra sprigs of rosemary for the marinade (1/4 cup rosemary needles are needed for the marinade)?#&8 lemons, use for 2 tbsp zest then slice thinly?#&3 cloves garlic?#&1 tbsp course Smoked sea salt (if you can’t find smoked sea salt, regular sea salt is okay)?#&1/2 tbsp fresh ground pepper?#&?#&***If you don’t have rosemary skewers, just use 6-8 bamboo skewers and buy a large bunch of rosemary for the marinade?#&?#&The rosemary stem makes for a fun skewer for the shrimp. Shrimp grills quickly so be careful to not overcook!', '', 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', '', '', 'Dec 2, 2017', '', 'Instructions?#&?#&If you have the rosemary stems, remove the rosemary needles in the middle and set aside. If you don’t have the stems, take your bunch of bunch of rosemary, reserving a few sprigs to throw on the grill, and remove the needles- you will need approximately 1/4 cup of rosemary needles for the marinade.?#&Next, take the rosemary needles/leaves and add to a bowl that will be used for making the marinade.?#&Add the olive oil, garlic and 2 tbsp of lemon zest then combine with a stick blender to create a seasoning paste or “slather”.?#&Let the shrimp marinate in the slather for 30 minutes.?#&Meanwhile, you can go prepare your grill while they marinate.?#&While the coals are doing their thing in the chimney starter, use a very sharp knife to thinly slice the lemon into 1/8” slices. The thinner the better as they will be more malleable to go onto the skewers.?#&Next, thread the shrimp onto the rosemary skewers and alternate shrimp with lemon slices that are folded in half. If you don’t have the rosemary skewers, just use regular bamboo skewers (don’t forget to soak them in water beforehand).?#&Prepare your PitBarrel Cooker for cooking on the grill grates.?#&Once your charcoal is ready to go and you are ready to put your skewers on, throw a few rosemary sprigs on the coals for some additional smoke. This will add some nice additional flavor to the shrimp.?#&Get the grill nice and hot and grill the shrimp only a few minutes on each side, looking for them to turn pink and being careful not to overcook.?#&While grilling the shrimp, throw the remaining lemons on the grill until they get charmarks (These will be served with the shrimp and make for great presentation, plus the grill makes them extra juicy for squeezing on the shrimp right before serving)?#&Once they’ve turned pink, take them off and enjoy a flavorful dish that is a true crowd pleaser!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'F76BEB7707D1264495E8C91980A2110F', X'B084435E193542408BCF280BC84E3D8D', X'B084435E193542408BCF280BC84E3D8D', 0, 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', 'Smoked Watermelon Gazpacho', 2, NULL, 0, 'Smoked Watermelon Gazpacho', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/09/smoked-watermelon-gazpacho-green-mountain-grills-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Produce:?#&?#&1/2 large watermelon, sliced in wedges?#&3 large cucumbers, skinned and sliced lengthwise?#&1 lb mini bell peppers?#&2 medium sized shallots?#&5 cloves garlic, put them on a skewer so they don’t fall through the grill grates?#&4 medium beefsteak tomatoes, cut in half?#&1 LARGE bunch of Basil, plus more for garnishEtcetera:?#&2 tbsp balsamic vinegar?#&3 tbsp olive oil?#&4 tbsp water?#&1 heaping tbsp. smoked sea salt (I prefer smoked sea salt if you have it!)?#&1 tsp fresh ground pepper (or to taste)', '', 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', '', '', 'Sep 10, 2017', '', 'Instructions?#&?#&Preheat your Green Mountain Grill or your other grill to 350 degrees. While getting your grill heated up, finish slicing your veggies. One your grill is at temperature, put your veggies on. Check your veggies every so often to ensure they are not burning- the garlic and peppers will cook the quickest- don’t let the garlic burn, just put it on long enough to soften and get some smoke. As char marks form, turn the veggies over and let the other side cook. Ideally you are looking for good char marks and the veggies and watermelon to caramelize on the grill. NOTE, the watermelon will not get as charred as the rest of the veggies unless you sliced it extremely thin and that is okay. The inherent nature of this fruit having so much water content takes it longer to get char marks- be patient. As veggies develop the char marks, remove them and let them cool in a big bowl.?#&?#&Once all veggies have been remove, place them in the largest pot you have and get out your stick blender. Now, go ahead and add your additional ingredients: balsamic, olive oil, water, sea salt and pepper and the the basil leaves. Pulse the content with your immersion blender until it is pureed. Don’t over puree it, you want it to have the consistency of a fine salsa.?#&?#&Let chill in the refrigerator for at least an hour before serving. Garnish with additional basil. Great served with grilled bread as an appetizer, also great for a light lunch. Goat cheese or sliced avocado are also a great addition for garnish!?#&?#&Enjoy being the creative cook on your block- I promise your friends and family will rave over this recipe!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'3028727414426945BD0E1109639A1145', X'A7DA77D032D7FA4D9F60274C65AD0741', X'A7DA77D032D7FA4D9F60274C65AD0741', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'AEAEF8269947B745998D18032D6F9F9D', X'B46B2BBA0C9E524699ABF3596DF35AA5', X'B46B2BBA0C9E524699ABF3596DF35AA5', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'9C0AF245C1A4594EB20DBFDD2DF892AC', X'8264413F9FE0EE4DAB586D3AF74C71AC', X'8264413F9FE0EE4DAB586D3AF74C71AC', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'F838E57A078C334EB6D845BCB8C34C26', X'0FA8C2B7DC547943AC2313D333D775E1', X'0FA8C2B7DC547943AC2313D333D775E1', 0, 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Kathy Pullin’s Turkey Lasagna Wonton Cups', 2, NULL, 0, 'Turkey Lasagna Wonton Cups', 'Kathy Pullin', 'https://grillgirl.com/wp-content/uploads/2020/05/Kathy-Pullin-Turkey-Lasagna-Wonton-Cups-150x150.jpg', '', '', '15 min', '45 min', '1 hour', '48', 'Ingredients?#&?#&1 pound of ground turkey?#&6 ounces of diced pancetta?#&1/4 cup of diced onion?#&1/2 teaspoon Italian seasoning – pick your favorite?#&1/2 teaspoon of basil flakes?#&1/2 teaspoon parsley flakes?#&1 clove of garlic?#&8 ounces of ricotta cheese?#&8 ounces of shredded mozzarella?#&8 ounces of shredded Italian cheese?#&2 cups of marinara?#&Salt and pepper?#&Lots of wonton wrappers', '', 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Baked', 'turkey, lasagna, wonton, cheese, italian', 'May 6, 2020', 'Turkey', 'Instructions?#&?#&First, grab your ground turkey, pancetta and onion. Once you’ve got those together, put them in a cast iron skillet and let it all sauté over hot coals to get that turkey nice and brown.?#&Once that turkey starts taking to a tan, mince up that garlic clove and toss it into the skillet with the Italian seasoning, 1/4 teaspoon of basil and 1/4 teaspoon of parsley. When that’s simmering, add just a touch of salt and pepper.?#&Is that turkey nice and brown? Toss in the marina and set it all aside.?#&Now is where things get cheesy. Bust out all that ricotta and 6 ounces of mozzarella and another 6 ounces of the shredded Italian cheese. Mix all that cheese and the rest of the basil and parsley in a separate bowl.?#&Grease your favorite cupcake pan and place wonton wrappers in the center of each cupcake holder.?#&Scoop out a teaspoon of the meat mixture, toss that on the wonton wrapper and then top it with a teaspoon of the cheese mix. Room to spare? That’s by design. Add in another teaspoon of meat mix and top it again with shredded cheese. Repeat and then repeat some more.?#&Now it’s time to get hot. Bake all these savory wontons over hot coals at 350 degrees and set your timer for 20 minutes until they’re finished cooking.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'D9D48A639BECBD4185AA08A107D29772', X'EE00E672DB3FE3418C631703E86D2558', X'EE00E672DB3FE3418C631703E86D2558', 0, 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', 'Grilled Watermelon Pizza', 2, NULL, 0, 'Grilled Watermelon Pizza', 'Captain Ron', 'https://grillgirl.com/wp-content/uploads/2019/10/Watermelon-Pizza-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Whole Watermelon (seedless is preferred) ?#&4 Oz package crumbled Feta Cheese ?#&2 Tbsp chopped fresh mint leaves ?#&1 Tbsp olive oil', '', 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', '', '', 'Oct 30, 2019', '', 'Instructions?#&?#&Prepare your grill for direct grilling over medium high heat ?#&Cut your watermelon into discs and coat them lightly with olive oil ?#&Place your oiled watermelon disks directly on the hot grates until there are visible grill marks underneath?#&Flip the watermelon over and top it with your crumbled feta cheese. Cover the grill and let it cook for a few more minutes. The cheese will soften but will not melt. ?#&Remove the watermelon pizza and sprinkle it with the chopped mint Slice your pizza into wedges just as you would cut a regular pizza pie ?#&?#&At this point, we like to drizzle it with a little bit of balsamic glaze for an extra burst of flavor. Let your imagination run wild!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'C6AB564FFB171B479CB75965AF5778E0', X'F26CF9AE02EAC44CAB745689ED40F817', X'F26CF9AE02EAC44CAB745689ED40F817', 0, 'https://grillgirl.com/2019/01/top-5-keto-friendly-apps-for-the-super-bowl/', 'Top 5 Keto Friendly Apps for the Super Bowl', 2, NULL, 0, 'Bacon Jalapeno Deviled EggsINSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'1D22EE393B9DEE4681F37A225339C211', X'70984AB3A0B58C43A0347DFEE16BB9D3', X'70984AB3A0B58C43A0347DFEE16BB9D3', 0, 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', 'Cedar Planked Pizza on the Grill', 2, NULL, 0, 'Cedar Planked Pizza on the Grill', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/04/cedar-planked-pizzas-on-grill-edited-2-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One store bought pizza crust from the deli, divided into 4 balls OR a tube of thin crust pizza dough from the refrigerator section (this is easier to get to roll thin than the stuff from the deli!)?#&Toppings of your choice', '', 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', '', '', 'Apr 30, 2018', '', 'Instructions?#&?#&Soak your wood planks for an hour before using. They need to be soaked so they don’t catch fire while grilling.?#&?#&Roll the pizza balls into long rectangles, trying to get as thin as possible and matching the size of the 5”X11” plank. Drizzle the plank with a bit of olive oil and spread around, then add cornmeal- this is done so your pizza is less likely to stick. Place the dough onto the plank. Try to get your dough as thin as possible.?#&?#&Have all of your toppings ready to be added by the grill before you get started.?#&?#&(adsbygoogle = window.adsbygoogle || ``).push({});?#&?#&Heat your grill up to approximately 400 degrees, creating direct and indirect zones. Place the cedar planked pizzas on the indirect side and let the dough cook for about 5-7 minutes so the top has a chance to bake before adding toppings. Next, add the toppings and let continue to cook for another 10 minutes until the cheese has melted and the crust is golden brown.?#&?#&Remove from the grill and slice into pieces- you can use the cedar planks as a serving tray for presentation if you like after they have slightly cooled.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'4A2E8B4E48D7A54E8D503F848A8C4EEE', X'33824CA09CFEAD4E94FB458F916C185B', X'33824CA09CFEAD4E94FB458F916C185B', 0, 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 2, NULL, 0, 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/Sri-lankan-chicken-wings-up-close-EDITED-150x150.jpg', '', '', '', '', '', '', 'ingredients for marinade)', '', 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', '', '', 'Dec 8, 2017', '', 'Instructions?#&?#&The making of the spice paste!?#&?#&Combine olive oil, lemongrass/or zest, cilantro, garlic, turmeric, sea salt and pepper in the food processor and pulse until uniformly mixed- you are looking to make a seasoning “paste”. Next, reserve about 2 tbsp of the marinade to drizzle before serving. Marinate the chicken wings for 30 minutes before putting on the grill.?#&?#&Prepare your grill for medium direct heat at 350 degrees. I used my Green Mountain Grill Pellet Smoker and set it for 375 degrees with the smoker holes wide open. Grill your wings, turning halfway during the process, for approximately 15-18 minutes OR until the internal temperature has reached 170 degrees with an internal read thermometer.?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#& ?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#&Once you’ve reached 170 degrees (I recommend using an internal read thermometer such as the thermapen to check internal temp), pull the wings off and drizzle with the remaining marinade. Prepare for out of this world flavor combination! Enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'6290EAB000EE13468AAC64300BB08D9B', X'48E618272E2BAC4C9E8CDF1F3A4209DF', X'48E618272E2BAC4C9E8CDF1F3A4209DF', 0, 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', 'Rosemary Lemon Shrimp Skewers', 2, NULL, 0, 'Rosemary Lemon Shrimp Skewers', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/rosemary-lemon-shrimp-skewers-EDITED-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 lb jumbo or extra jumbo size shrimp- (buy the largest you can find, they will work the best for threading onto the Rosemary skewers)  shells and tails removed?#&1–2 tbsp olive oil?#&7–8 rosemary stems plus a few extra sprigs of rosemary for the marinade (1/4 cup rosemary needles are needed for the marinade)?#&8 lemons, use for 2 tbsp zest then slice thinly?#&3 cloves garlic?#&1 tbsp course Smoked sea salt (if you can’t find smoked sea salt, regular sea salt is okay)?#&1/2 tbsp fresh ground pepper?#&?#&***If you don’t have rosemary skewers, just use 6-8 bamboo skewers and buy a large bunch of rosemary for the marinade?#&?#&The rosemary stem makes for a fun skewer for the shrimp. Shrimp grills quickly so be careful to not overcook!', '', 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', '', '', 'Dec 2, 2017', '', 'Instructions?#&?#&If you have the rosemary stems, remove the rosemary needles in the middle and set aside. If you don’t have the stems, take your bunch of bunch of rosemary, reserving a few sprigs to throw on the grill, and remove the needles- you will need approximately 1/4 cup of rosemary needles for the marinade.?#&Next, take the rosemary needles/leaves and add to a bowl that will be used for making the marinade.?#&Add the olive oil, garlic and 2 tbsp of lemon zest then combine with a stick blender to create a seasoning paste or “slather”.?#&Let the shrimp marinate in the slather for 30 minutes.?#&Meanwhile, you can go prepare your grill while they marinate.?#&While the coals are doing their thing in the chimney starter, use a very sharp knife to thinly slice the lemon into 1/8” slices. The thinner the better as they will be more malleable to go onto the skewers.?#&Next, thread the shrimp onto the rosemary skewers and alternate shrimp with lemon slices that are folded in half. If you don’t have the rosemary skewers, just use regular bamboo skewers (don’t forget to soak them in water beforehand).?#&Prepare your PitBarrel Cooker for cooking on the grill grates.?#&Once your charcoal is ready to go and you are ready to put your skewers on, throw a few rosemary sprigs on the coals for some additional smoke. This will add some nice additional flavor to the shrimp.?#&Get the grill nice and hot and grill the shrimp only a few minutes on each side, looking for them to turn pink and being careful not to overcook.?#&While grilling the shrimp, throw the remaining lemons on the grill until they get charmarks (These will be served with the shrimp and make for great presentation, plus the grill makes them extra juicy for squeezing on the shrimp right before serving)?#&Once they’ve turned pink, take them off and enjoy a flavorful dish that is a true crowd pleaser!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'57A7B5EC5CC83F45A61DF9619940CF15', X'57D04F901CBA2B4F86402ED8213C21A6', X'57D04F901CBA2B4F86402ED8213C21A6', 0, 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', 'Smoked Watermelon Gazpacho', 2, NULL, 0, 'Smoked Watermelon Gazpacho', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/09/smoked-watermelon-gazpacho-green-mountain-grills-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Produce:?#&?#&1/2 large watermelon, sliced in wedges?#&3 large cucumbers, skinned and sliced lengthwise?#&1 lb mini bell peppers?#&2 medium sized shallots?#&5 cloves garlic, put them on a skewer so they don’t fall through the grill grates?#&4 medium beefsteak tomatoes, cut in half?#&1 LARGE bunch of Basil, plus more for garnishEtcetera:?#&2 tbsp balsamic vinegar?#&3 tbsp olive oil?#&4 tbsp water?#&1 heaping tbsp. smoked sea salt (I prefer smoked sea salt if you have it!)?#&1 tsp fresh ground pepper (or to taste)', '', 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', '', '', 'Sep 10, 2017', '', 'Instructions?#&?#&Preheat your Green Mountain Grill or your other grill to 350 degrees. While getting your grill heated up, finish slicing your veggies. One your grill is at temperature, put your veggies on. Check your veggies every so often to ensure they are not burning- the garlic and peppers will cook the quickest- don’t let the garlic burn, just put it on long enough to soften and get some smoke. As char marks form, turn the veggies over and let the other side cook. Ideally you are looking for good char marks and the veggies and watermelon to caramelize on the grill. NOTE, the watermelon will not get as charred as the rest of the veggies unless you sliced it extremely thin and that is okay. The inherent nature of this fruit having so much water content takes it longer to get char marks- be patient. As veggies develop the char marks, remove them and let them cool in a big bowl.?#&?#&Once all veggies have been remove, place them in the largest pot you have and get out your stick blender. Now, go ahead and add your additional ingredients: balsamic, olive oil, water, sea salt and pepper and the the basil leaves. Pulse the content with your immersion blender until it is pureed. Don’t over puree it, you want it to have the consistency of a fine salsa.?#&?#&Let chill in the refrigerator for at least an hour before serving. Garnish with additional basil. Great served with grilled bread as an appetizer, also great for a light lunch. Goat cheese or sliced avocado are also a great addition for garnish!?#&?#&Enjoy being the creative cook on your block- I promise your friends and family will rave over this recipe!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'D19B56543385F5448649F43A71E92653', X'78753DA06314124D92FF6D6354B47D0D', X'78753DA06314124D92FF6D6354B47D0D', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'69392935979BA44DA58FC8BD28033F3F', X'CB7FBBA93B6CCC42869B9D003957486F', X'CB7FBBA93B6CCC42869B9D003957486F', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'77EB4286C240964BA3B2B347017C5042', X'247224442755BE42ADBE56B361BAF1D0', X'247224442755BE42ADBE56B361BAF1D0', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'F3A829C744206A47AEBE647908B6AA92', X'97D69E24272E084CBF05E86097E357A4', X'97D69E24272E084CBF05E86097E357A4', 0, 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Kathy Pullin’s Turkey Lasagna Wonton Cups', 2, NULL, 0, 'Turkey Lasagna Wonton Cups', 'Kathy Pullin', 'https://grillgirl.com/wp-content/uploads/2020/05/Kathy-Pullin-Turkey-Lasagna-Wonton-Cups-150x150.jpg', '', '', '15 min', '45 min', '1 hour', '48', 'Ingredients?#&?#&1 pound of ground turkey?#&6 ounces of diced pancetta?#&1/4 cup of diced onion?#&1/2 teaspoon Italian seasoning – pick your favorite?#&1/2 teaspoon of basil flakes?#&1/2 teaspoon parsley flakes?#&1 clove of garlic?#&8 ounces of ricotta cheese?#&8 ounces of shredded mozzarella?#&8 ounces of shredded Italian cheese?#&2 cups of marinara?#&Salt and pepper?#&Lots of wonton wrappers', '', 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Baked', 'turkey, lasagna, wonton, cheese, italian', 'May 6, 2020', 'Turkey', 'Instructions?#&?#&First, grab your ground turkey, pancetta and onion. Once you’ve got those together, put them in a cast iron skillet and let it all sauté over hot coals to get that turkey nice and brown.?#&Once that turkey starts taking to a tan, mince up that garlic clove and toss it into the skillet with the Italian seasoning, 1/4 teaspoon of basil and 1/4 teaspoon of parsley. When that’s simmering, add just a touch of salt and pepper.?#&Is that turkey nice and brown? Toss in the marina and set it all aside.?#&Now is where things get cheesy. Bust out all that ricotta and 6 ounces of mozzarella and another 6 ounces of the shredded Italian cheese. Mix all that cheese and the rest of the basil and parsley in a separate bowl.?#&Grease your favorite cupcake pan and place wonton wrappers in the center of each cupcake holder.?#&Scoop out a teaspoon of the meat mixture, toss that on the wonton wrapper and then top it with a teaspoon of the cheese mix. Room to spare? That’s by design. Add in another teaspoon of meat mix and top it again with shredded cheese. Repeat and then repeat some more.?#&Now it’s time to get hot. Bake all these savory wontons over hot coals at 350 degrees and set your timer for 20 minutes until they’re finished cooking.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2329EAD4A651AC46B5CDF67E07E073E4', X'D4FB7CE1FE1AAC43ACBE07F40261B819', X'D4FB7CE1FE1AAC43ACBE07F40261B819', 0, 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', 'Grilled Watermelon Pizza', 2, NULL, 0, 'Grilled Watermelon Pizza', 'Captain Ron', 'https://grillgirl.com/wp-content/uploads/2019/10/Watermelon-Pizza-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Whole Watermelon (seedless is preferred) ?#&4 Oz package crumbled Feta Cheese ?#&2 Tbsp chopped fresh mint leaves ?#&1 Tbsp olive oil', '', 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', '', '', 'Oct 30, 2019', '', 'Instructions?#&?#&Prepare your grill for direct grilling over medium high heat ?#&Cut your watermelon into discs and coat them lightly with olive oil ?#&Place your oiled watermelon disks directly on the hot grates until there are visible grill marks underneath?#&Flip the watermelon over and top it with your crumbled feta cheese. Cover the grill and let it cook for a few more minutes. The cheese will soften but will not melt. ?#&Remove the watermelon pizza and sprinkle it with the chopped mint Slice your pizza into wedges just as you would cut a regular pizza pie ?#&?#&At this point, we like to drizzle it with a little bit of balsamic glaze for an extra burst of flavor. Let your imagination run wild!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'F6777B03E0F3744C8487EE4A422B5F17', X'89AC7053430328499918142D99B90D28', X'89AC7053430328499918142D99B90D28', 0, 'https://grillgirl.com/2019/01/top-5-keto-friendly-apps-for-the-super-bowl/', 'Top 5 Keto Friendly Apps for the Super Bowl', 2, NULL, 0, 'Bacon Jalapeno Deviled EggsINSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'6452235D17ADC64FA0C8CBFE9D1E9502', X'515BAA976753D347A4CFEAA51E8BB7B5', X'515BAA976753D347A4CFEAA51E8BB7B5', 0, 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', 'Cedar Planked Pizza on the Grill', 2, NULL, 0, 'Cedar Planked Pizza on the Grill', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/04/cedar-planked-pizzas-on-grill-edited-2-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One store bought pizza crust from the deli, divided into 4 balls OR a tube of thin crust pizza dough from the refrigerator section (this is easier to get to roll thin than the stuff from the deli!)?#&Toppings of your choice', '', 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', '', '', 'Apr 30, 2018', '', 'Instructions?#&?#&Soak your wood planks for an hour before using. They need to be soaked so they don’t catch fire while grilling.?#&?#&Roll the pizza balls into long rectangles, trying to get as thin as possible and matching the size of the 5”X11” plank. Drizzle the plank with a bit of olive oil and spread around, then add cornmeal- this is done so your pizza is less likely to stick. Place the dough onto the plank. Try to get your dough as thin as possible.?#&?#&Have all of your toppings ready to be added by the grill before you get started.?#&?#&(adsbygoogle = window.adsbygoogle || ``).push({});?#&?#&Heat your grill up to approximately 400 degrees, creating direct and indirect zones. Place the cedar planked pizzas on the indirect side and let the dough cook for about 5-7 minutes so the top has a chance to bake before adding toppings. Next, add the toppings and let continue to cook for another 10 minutes until the cheese has melted and the crust is golden brown.?#&?#&Remove from the grill and slice into pieces- you can use the cedar planks as a serving tray for presentation if you like after they have slightly cooled.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'91A48386CD23BF40BBEB51365EB412A8', X'A6C22FC1E9747648AFCDBA579F0CEA67', X'A6C22FC1E9747648AFCDBA579F0CEA67', 0, 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 2, NULL, 0, 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/Sri-lankan-chicken-wings-up-close-EDITED-150x150.jpg', '', '', '', '', '', '', 'ingredients for marinade)', '', 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', '', '', 'Dec 8, 2017', '', 'Instructions?#&?#&The making of the spice paste!?#&?#&Combine olive oil, lemongrass/or zest, cilantro, garlic, turmeric, sea salt and pepper in the food processor and pulse until uniformly mixed- you are looking to make a seasoning “paste”. Next, reserve about 2 tbsp of the marinade to drizzle before serving. Marinate the chicken wings for 30 minutes before putting on the grill.?#&?#&Prepare your grill for medium direct heat at 350 degrees. I used my Green Mountain Grill Pellet Smoker and set it for 375 degrees with the smoker holes wide open. Grill your wings, turning halfway during the process, for approximately 15-18 minutes OR until the internal temperature has reached 170 degrees with an internal read thermometer.?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#& ?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#&Once you’ve reached 170 degrees (I recommend using an internal read thermometer such as the thermapen to check internal temp), pull the wings off and drizzle with the remaining marinade. Prepare for out of this world flavor combination! Enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'BD38DCC8D73B2145B8A5D1CA7859E6FD', X'7F1E50F55520D841BC654737510586BA', X'7F1E50F55520D841BC654737510586BA', 0, 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', 'Rosemary Lemon Shrimp Skewers', 2, NULL, 0, 'Rosemary Lemon Shrimp Skewers', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/rosemary-lemon-shrimp-skewers-EDITED-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 lb jumbo or extra jumbo size shrimp- (buy the largest you can find, they will work the best for threading onto the Rosemary skewers)  shells and tails removed?#&1–2 tbsp olive oil?#&7–8 rosemary stems plus a few extra sprigs of rosemary for the marinade (1/4 cup rosemary needles are needed for the marinade)?#&8 lemons, use for 2 tbsp zest then slice thinly?#&3 cloves garlic?#&1 tbsp course Smoked sea salt (if you can’t find smoked sea salt, regular sea salt is okay)?#&1/2 tbsp fresh ground pepper?#&?#&***If you don’t have rosemary skewers, just use 6-8 bamboo skewers and buy a large bunch of rosemary for the marinade?#&?#&The rosemary stem makes for a fun skewer for the shrimp. Shrimp grills quickly so be careful to not overcook!', '', 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', '', '', 'Dec 2, 2017', '', 'Instructions?#&?#&If you have the rosemary stems, remove the rosemary needles in the middle and set aside. If you don’t have the stems, take your bunch of bunch of rosemary, reserving a few sprigs to throw on the grill, and remove the needles- you will need approximately 1/4 cup of rosemary needles for the marinade.?#&Next, take the rosemary needles/leaves and add to a bowl that will be used for making the marinade.?#&Add the olive oil, garlic and 2 tbsp of lemon zest then combine with a stick blender to create a seasoning paste or “slather”.?#&Let the shrimp marinate in the slather for 30 minutes.?#&Meanwhile, you can go prepare your grill while they marinate.?#&While the coals are doing their thing in the chimney starter, use a very sharp knife to thinly slice the lemon into 1/8” slices. The thinner the better as they will be more malleable to go onto the skewers.?#&Next, thread the shrimp onto the rosemary skewers and alternate shrimp with lemon slices that are folded in half. If you don’t have the rosemary skewers, just use regular bamboo skewers (don’t forget to soak them in water beforehand).?#&Prepare your PitBarrel Cooker for cooking on the grill grates.?#&Once your charcoal is ready to go and you are ready to put your skewers on, throw a few rosemary sprigs on the coals for some additional smoke. This will add some nice additional flavor to the shrimp.?#&Get the grill nice and hot and grill the shrimp only a few minutes on each side, looking for them to turn pink and being careful not to overcook.?#&While grilling the shrimp, throw the remaining lemons on the grill until they get charmarks (These will be served with the shrimp and make for great presentation, plus the grill makes them extra juicy for squeezing on the shrimp right before serving)?#&Once they’ve turned pink, take them off and enjoy a flavorful dish that is a true crowd pleaser!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'18184F0E09523B44AE7D5BBED202497E', X'3BB237FC8E3B164D89B84B80B71F93F1', X'3BB237FC8E3B164D89B84B80B71F93F1', 0, 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', 'Smoked Watermelon Gazpacho', 2, NULL, 0, 'Smoked Watermelon Gazpacho', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/09/smoked-watermelon-gazpacho-green-mountain-grills-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Produce:?#&?#&1/2 large watermelon, sliced in wedges?#&3 large cucumbers, skinned and sliced lengthwise?#&1 lb mini bell peppers?#&2 medium sized shallots?#&5 cloves garlic, put them on a skewer so they don’t fall through the grill grates?#&4 medium beefsteak tomatoes, cut in half?#&1 LARGE bunch of Basil, plus more for garnishEtcetera:?#&2 tbsp balsamic vinegar?#&3 tbsp olive oil?#&4 tbsp water?#&1 heaping tbsp. smoked sea salt (I prefer smoked sea salt if you have it!)?#&1 tsp fresh ground pepper (or to taste)', '', 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', '', '', 'Sep 10, 2017', '', 'Instructions?#&?#&Preheat your Green Mountain Grill or your other grill to 350 degrees. While getting your grill heated up, finish slicing your veggies. One your grill is at temperature, put your veggies on. Check your veggies every so often to ensure they are not burning- the garlic and peppers will cook the quickest- don’t let the garlic burn, just put it on long enough to soften and get some smoke. As char marks form, turn the veggies over and let the other side cook. Ideally you are looking for good char marks and the veggies and watermelon to caramelize on the grill. NOTE, the watermelon will not get as charred as the rest of the veggies unless you sliced it extremely thin and that is okay. The inherent nature of this fruit having so much water content takes it longer to get char marks- be patient. As veggies develop the char marks, remove them and let them cool in a big bowl.?#&?#&Once all veggies have been remove, place them in the largest pot you have and get out your stick blender. Now, go ahead and add your additional ingredients: balsamic, olive oil, water, sea salt and pepper and the the basil leaves. Pulse the content with your immersion blender until it is pureed. Don’t over puree it, you want it to have the consistency of a fine salsa.?#&?#&Let chill in the refrigerator for at least an hour before serving. Garnish with additional basil. Great served with grilled bread as an appetizer, also great for a light lunch. Goat cheese or sliced avocado are also a great addition for garnish!?#&?#&Enjoy being the creative cook on your block- I promise your friends and family will rave over this recipe!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B73AB64B41E7FD41B5AABAE79D00C7ED', X'CEE87EF910BE1A458F537DB8B59C68F5', X'CEE87EF910BE1A458F537DB8B59C68F5', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B19D461D8578CB43B2331509CFEF46C2', X'CA1FE746CD9E1D4F8AFBB1C15BF02C07', X'CA1FE746CD9E1D4F8AFBB1C15BF02C07', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'6E9666AC94768F44B0C77C6E71B54CF5', X'14689C5D9360A34D8BB0E49C474CE1E5', X'14689C5D9360A34D8BB0E49C474CE1E5', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'202EF19CF9B0E54D9730CF6AB5042FF1', X'AAB05C577364A343812AC56D745ADE2D', X'AAB05C577364A343812AC56D745ADE2D', 0, 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Kathy Pullin’s Turkey Lasagna Wonton Cups', 2, NULL, 0, 'Turkey Lasagna Wonton Cups', 'Kathy Pullin', 'https://grillgirl.com/wp-content/uploads/2020/05/Kathy-Pullin-Turkey-Lasagna-Wonton-Cups-150x150.jpg', '', '', '15 min', '45 min', '1 hour', '48', 'Ingredients?#&?#&1 pound of ground turkey?#&6 ounces of diced pancetta?#&1/4 cup of diced onion?#&1/2 teaspoon Italian seasoning – pick your favorite?#&1/2 teaspoon of basil flakes?#&1/2 teaspoon parsley flakes?#&1 clove of garlic?#&8 ounces of ricotta cheese?#&8 ounces of shredded mozzarella?#&8 ounces of shredded Italian cheese?#&2 cups of marinara?#&Salt and pepper?#&Lots of wonton wrappers', '', 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Baked', 'turkey, lasagna, wonton, cheese, italian', 'May 6, 2020', 'Turkey', 'Instructions?#&?#&First, grab your ground turkey, pancetta and onion. Once you’ve got those together, put them in a cast iron skillet and let it all sauté over hot coals to get that turkey nice and brown.?#&Once that turkey starts taking to a tan, mince up that garlic clove and toss it into the skillet with the Italian seasoning, 1/4 teaspoon of basil and 1/4 teaspoon of parsley. When that’s simmering, add just a touch of salt and pepper.?#&Is that turkey nice and brown? Toss in the marina and set it all aside.?#&Now is where things get cheesy. Bust out all that ricotta and 6 ounces of mozzarella and another 6 ounces of the shredded Italian cheese. Mix all that cheese and the rest of the basil and parsley in a separate bowl.?#&Grease your favorite cupcake pan and place wonton wrappers in the center of each cupcake holder.?#&Scoop out a teaspoon of the meat mixture, toss that on the wonton wrapper and then top it with a teaspoon of the cheese mix. Room to spare? That’s by design. Add in another teaspoon of meat mix and top it again with shredded cheese. Repeat and then repeat some more.?#&Now it’s time to get hot. Bake all these savory wontons over hot coals at 350 degrees and set your timer for 20 minutes until they’re finished cooking.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2B73F8496CA3F94AA3EBA978AD15D504', X'0228356B612CEE458A921AD68EF1D2D0', X'0228356B612CEE458A921AD68EF1D2D0', 0, 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', 'Grilled Watermelon Pizza', 2, NULL, 0, 'Grilled Watermelon Pizza', 'Captain Ron', 'https://grillgirl.com/wp-content/uploads/2019/10/Watermelon-Pizza-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Whole Watermelon (seedless is preferred) ?#&4 Oz package crumbled Feta Cheese ?#&2 Tbsp chopped fresh mint leaves ?#&1 Tbsp olive oil', '', 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', '', '', 'Oct 30, 2019', '', 'Instructions?#&?#&Prepare your grill for direct grilling over medium high heat ?#&Cut your watermelon into discs and coat them lightly with olive oil ?#&Place your oiled watermelon disks directly on the hot grates until there are visible grill marks underneath?#&Flip the watermelon over and top it with your crumbled feta cheese. Cover the grill and let it cook for a few more minutes. The cheese will soften but will not melt. ?#&Remove the watermelon pizza and sprinkle it with the chopped mint Slice your pizza into wedges just as you would cut a regular pizza pie ?#&?#&At this point, we like to drizzle it with a little bit of balsamic glaze for an extra burst of flavor. Let your imagination run wild!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'35F1769EFB329646B4F858E7CECFA809', X'31464C7120D1F245AC777E5BCDDED015', X'31464C7120D1F245AC777E5BCDDED015', 0, 'https://grillgirl.com/2019/01/top-5-keto-friendly-apps-for-the-super-bowl/', 'Top 5 Keto Friendly Apps for the Super Bowl', 2, NULL, 0, 'Bacon Jalapeno Deviled EggsINSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B466066555083042AEF0F4EE2D5E90A0', X'D154896C23B5CC48BB82A44658EFB3B2', X'D154896C23B5CC48BB82A44658EFB3B2', 0, 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', 'Cedar Planked Pizza on the Grill', 2, NULL, 0, 'Cedar Planked Pizza on the Grill', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/04/cedar-planked-pizzas-on-grill-edited-2-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One store bought pizza crust from the deli, divided into 4 balls OR a tube of thin crust pizza dough from the refrigerator section (this is easier to get to roll thin than the stuff from the deli!)?#&Toppings of your choice', '', 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', '', '', 'Apr 30, 2018', '', 'Instructions?#&?#&Soak your wood planks for an hour before using. They need to be soaked so they don’t catch fire while grilling.?#&?#&Roll the pizza balls into long rectangles, trying to get as thin as possible and matching the size of the 5”X11” plank. Drizzle the plank with a bit of olive oil and spread around, then add cornmeal- this is done so your pizza is less likely to stick. Place the dough onto the plank. Try to get your dough as thin as possible.?#&?#&Have all of your toppings ready to be added by the grill before you get started.?#&?#&(adsbygoogle = window.adsbygoogle || ``).push({});?#&?#&Heat your grill up to approximately 400 degrees, creating direct and indirect zones. Place the cedar planked pizzas on the indirect side and let the dough cook for about 5-7 minutes so the top has a chance to bake before adding toppings. Next, add the toppings and let continue to cook for another 10 minutes until the cheese has melted and the crust is golden brown.?#&?#&Remove from the grill and slice into pieces- you can use the cedar planks as a serving tray for presentation if you like after they have slightly cooled.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'4A0859F6BA380A42AEEB84A5D9B60BAA', X'D4885CE0A1E23E4C8326F7A085520C49', X'D4885CE0A1E23E4C8326F7A085520C49', 0, 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 2, NULL, 0, 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/Sri-lankan-chicken-wings-up-close-EDITED-150x150.jpg', '', '', '', '', '', '', 'ingredients for marinade)', '', 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', '', '', 'Dec 8, 2017', '', 'Instructions?#&?#&The making of the spice paste!?#&?#&Combine olive oil, lemongrass/or zest, cilantro, garlic, turmeric, sea salt and pepper in the food processor and pulse until uniformly mixed- you are looking to make a seasoning “paste”. Next, reserve about 2 tbsp of the marinade to drizzle before serving. Marinate the chicken wings for 30 minutes before putting on the grill.?#&?#&Prepare your grill for medium direct heat at 350 degrees. I used my Green Mountain Grill Pellet Smoker and set it for 375 degrees with the smoker holes wide open. Grill your wings, turning halfway during the process, for approximately 15-18 minutes OR until the internal temperature has reached 170 degrees with an internal read thermometer.?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#& ?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#&Once you’ve reached 170 degrees (I recommend using an internal read thermometer such as the thermapen to check internal temp), pull the wings off and drizzle with the remaining marinade. Prepare for out of this world flavor combination! Enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'3D8F57E1E2A59A479661530155C57B27', X'6E1496DC66E90545923F68F6D199C476', X'6E1496DC66E90545923F68F6D199C476', 0, 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', 'Rosemary Lemon Shrimp Skewers', 2, NULL, 0, 'Rosemary Lemon Shrimp Skewers', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/rosemary-lemon-shrimp-skewers-EDITED-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 lb jumbo or extra jumbo size shrimp- (buy the largest you can find, they will work the best for threading onto the Rosemary skewers)  shells and tails removed?#&1–2 tbsp olive oil?#&7–8 rosemary stems plus a few extra sprigs of rosemary for the marinade (1/4 cup rosemary needles are needed for the marinade)?#&8 lemons, use for 2 tbsp zest then slice thinly?#&3 cloves garlic?#&1 tbsp course Smoked sea salt (if you can’t find smoked sea salt, regular sea salt is okay)?#&1/2 tbsp fresh ground pepper?#&?#&***If you don’t have rosemary skewers, just use 6-8 bamboo skewers and buy a large bunch of rosemary for the marinade?#&?#&The rosemary stem makes for a fun skewer for the shrimp. Shrimp grills quickly so be careful to not overcook!', '', 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', '', '', 'Dec 2, 2017', '', 'Instructions?#&?#&If you have the rosemary stems, remove the rosemary needles in the middle and set aside. If you don’t have the stems, take your bunch of bunch of rosemary, reserving a few sprigs to throw on the grill, and remove the needles- you will need approximately 1/4 cup of rosemary needles for the marinade.?#&Next, take the rosemary needles/leaves and add to a bowl that will be used for making the marinade.?#&Add the olive oil, garlic and 2 tbsp of lemon zest then combine with a stick blender to create a seasoning paste or “slather”.?#&Let the shrimp marinate in the slather for 30 minutes.?#&Meanwhile, you can go prepare your grill while they marinate.?#&While the coals are doing their thing in the chimney starter, use a very sharp knife to thinly slice the lemon into 1/8” slices. The thinner the better as they will be more malleable to go onto the skewers.?#&Next, thread the shrimp onto the rosemary skewers and alternate shrimp with lemon slices that are folded in half. If you don’t have the rosemary skewers, just use regular bamboo skewers (don’t forget to soak them in water beforehand).?#&Prepare your PitBarrel Cooker for cooking on the grill grates.?#&Once your charcoal is ready to go and you are ready to put your skewers on, throw a few rosemary sprigs on the coals for some additional smoke. This will add some nice additional flavor to the shrimp.?#&Get the grill nice and hot and grill the shrimp only a few minutes on each side, looking for them to turn pink and being careful not to overcook.?#&While grilling the shrimp, throw the remaining lemons on the grill until they get charmarks (These will be served with the shrimp and make for great presentation, plus the grill makes them extra juicy for squeezing on the shrimp right before serving)?#&Once they’ve turned pink, take them off and enjoy a flavorful dish that is a true crowd pleaser!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'1D37FF89EFD2134CACF81B3B1E6ADAC5', X'2503E79EDE705C4A9D0E27CC94650D96', X'2503E79EDE705C4A9D0E27CC94650D96', 0, 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', 'Smoked Watermelon Gazpacho', 2, NULL, 0, 'Smoked Watermelon Gazpacho', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/09/smoked-watermelon-gazpacho-green-mountain-grills-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Produce:?#&?#&1/2 large watermelon, sliced in wedges?#&3 large cucumbers, skinned and sliced lengthwise?#&1 lb mini bell peppers?#&2 medium sized shallots?#&5 cloves garlic, put them on a skewer so they don’t fall through the grill grates?#&4 medium beefsteak tomatoes, cut in half?#&1 LARGE bunch of Basil, plus more for garnishEtcetera:?#&2 tbsp balsamic vinegar?#&3 tbsp olive oil?#&4 tbsp water?#&1 heaping tbsp. smoked sea salt (I prefer smoked sea salt if you have it!)?#&1 tsp fresh ground pepper (or to taste)', '', 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', '', '', 'Sep 10, 2017', '', 'Instructions?#&?#&Preheat your Green Mountain Grill or your other grill to 350 degrees. While getting your grill heated up, finish slicing your veggies. One your grill is at temperature, put your veggies on. Check your veggies every so often to ensure they are not burning- the garlic and peppers will cook the quickest- don’t let the garlic burn, just put it on long enough to soften and get some smoke. As char marks form, turn the veggies over and let the other side cook. Ideally you are looking for good char marks and the veggies and watermelon to caramelize on the grill. NOTE, the watermelon will not get as charred as the rest of the veggies unless you sliced it extremely thin and that is okay. The inherent nature of this fruit having so much water content takes it longer to get char marks- be patient. As veggies develop the char marks, remove them and let them cool in a big bowl.?#&?#&Once all veggies have been remove, place them in the largest pot you have and get out your stick blender. Now, go ahead and add your additional ingredients: balsamic, olive oil, water, sea salt and pepper and the the basil leaves. Pulse the content with your immersion blender until it is pureed. Don’t over puree it, you want it to have the consistency of a fine salsa.?#&?#&Let chill in the refrigerator for at least an hour before serving. Garnish with additional basil. Great served with grilled bread as an appetizer, also great for a light lunch. Goat cheese or sliced avocado are also a great addition for garnish!?#&?#&Enjoy being the creative cook on your block- I promise your friends and family will rave over this recipe!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'A8BE99802A7AB8428546352C57DDAF9C', X'83B0AC8AF45DCE41A95FCED0EEB6C926', X'83B0AC8AF45DCE41A95FCED0EEB6C926', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B790691595779342918467EFD7948A10', X'ABC6CADFB1F6DF4AA2A4567F1987370D', X'ABC6CADFB1F6DF4AA2A4567F1987370D', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'BEB969526C072F418A960D9E24E58E91', X'027ADF27FD786D448BB5E776396BB01A', X'027ADF27FD786D448BB5E776396BB01A', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'8DBC49AD3C99EB4287DD5C6E6E554C8F', X'90F7C6B261C05F4EA288B12A5E6E8932', X'90F7C6B261C05F4EA288B12A5E6E8932', 0, 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Kathy Pullin’s Turkey Lasagna Wonton Cups', 2, NULL, 0, 'Turkey Lasagna Wonton Cups', 'Kathy Pullin', 'https://grillgirl.com/wp-content/uploads/2020/05/Kathy-Pullin-Turkey-Lasagna-Wonton-Cups-150x150.jpg', '', '', '15 min', '45 min', '1 hour', '48', 'Ingredients?#&?#&1 pound of ground turkey?#&6 ounces of diced pancetta?#&1/4 cup of diced onion?#&1/2 teaspoon Italian seasoning – pick your favorite?#&1/2 teaspoon of basil flakes?#&1/2 teaspoon parsley flakes?#&1 clove of garlic?#&8 ounces of ricotta cheese?#&8 ounces of shredded mozzarella?#&8 ounces of shredded Italian cheese?#&2 cups of marinara?#&Salt and pepper?#&Lots of wonton wrappers', '', 'https://grillgirl.com/2020/05/kathy-pullin-turkey-lasagna-wonton-cups-recipe/', 'Baked', 'turkey, lasagna, wonton, cheese, italian', 'May 6, 2020', 'Turkey', 'Instructions?#&?#&First, grab your ground turkey, pancetta and onion. Once you’ve got those together, put them in a cast iron skillet and let it all sauté over hot coals to get that turkey nice and brown.?#&Once that turkey starts taking to a tan, mince up that garlic clove and toss it into the skillet with the Italian seasoning, 1/4 teaspoon of basil and 1/4 teaspoon of parsley. When that’s simmering, add just a touch of salt and pepper.?#&Is that turkey nice and brown? Toss in the marina and set it all aside.?#&Now is where things get cheesy. Bust out all that ricotta and 6 ounces of mozzarella and another 6 ounces of the shredded Italian cheese. Mix all that cheese and the rest of the basil and parsley in a separate bowl.?#&Grease your favorite cupcake pan and place wonton wrappers in the center of each cupcake holder.?#&Scoop out a teaspoon of the meat mixture, toss that on the wonton wrapper and then top it with a teaspoon of the cheese mix. Room to spare? That’s by design. Add in another teaspoon of meat mix and top it again with shredded cheese. Repeat and then repeat some more.?#&Now it’s time to get hot. Bake all these savory wontons over hot coals at 350 degrees and set your timer for 20 minutes until they’re finished cooking.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'E7F03C54650C0A468A4A14AEBB7A54E1', X'1CC345BAD1EF554788D696C2C2A4E295', X'1CC345BAD1EF554788D696C2C2A4E295', 0, 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', 'Grilled Watermelon Pizza', 2, NULL, 0, 'Grilled Watermelon Pizza', 'Captain Ron', 'https://grillgirl.com/wp-content/uploads/2019/10/Watermelon-Pizza-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Whole Watermelon (seedless is preferred) ?#&4 Oz package crumbled Feta Cheese ?#&2 Tbsp chopped fresh mint leaves ?#&1 Tbsp olive oil', '', 'https://grillgirl.com/2019/10/grilled-watermelon-pizza/', '', '', 'Oct 30, 2019', '', 'Instructions?#&?#&Prepare your grill for direct grilling over medium high heat ?#&Cut your watermelon into discs and coat them lightly with olive oil ?#&Place your oiled watermelon disks directly on the hot grates until there are visible grill marks underneath?#&Flip the watermelon over and top it with your crumbled feta cheese. Cover the grill and let it cook for a few more minutes. The cheese will soften but will not melt. ?#&Remove the watermelon pizza and sprinkle it with the chopped mint Slice your pizza into wedges just as you would cut a regular pizza pie ?#&?#&At this point, we like to drizzle it with a little bit of balsamic glaze for an extra burst of flavor. Let your imagination run wild!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'619E74481B90E44AA37B08C8BE06AEFE', X'DC22755A8A8BEE4EA050CD21F93C5BEE', X'DC22755A8A8BEE4EA050CD21F93C5BEE', 0, 'https://grillgirl.com/2019/01/top-5-keto-friendly-apps-for-the-super-bowl/', 'Top 5 Keto Friendly Apps for the Super Bowl', 2, NULL, 0, 'Bacon Jalapeno Deviled EggsINSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'EFEEE843941DEE4184CA69D0FEA4A2CA', X'F35ECA88686E3B43A642E120F1055E51', X'F35ECA88686E3B43A642E120F1055E51', 0, 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', 'Cedar Planked Pizza on the Grill', 2, NULL, 0, 'Cedar Planked Pizza on the Grill', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/04/cedar-planked-pizzas-on-grill-edited-2-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&One store bought pizza crust from the deli, divided into 4 balls OR a tube of thin crust pizza dough from the refrigerator section (this is easier to get to roll thin than the stuff from the deli!)?#&Toppings of your choice', '', 'https://grillgirl.com/2018/04/cedar-planked-pizza-on-the-grill/', '', '', 'Apr 30, 2018', '', 'Instructions?#&?#&Soak your wood planks for an hour before using. They need to be soaked so they don’t catch fire while grilling.?#&?#&Roll the pizza balls into long rectangles, trying to get as thin as possible and matching the size of the 5”X11” plank. Drizzle the plank with a bit of olive oil and spread around, then add cornmeal- this is done so your pizza is less likely to stick. Place the dough onto the plank. Try to get your dough as thin as possible.?#&?#&Have all of your toppings ready to be added by the grill before you get started.?#&?#&(adsbygoogle = window.adsbygoogle || ``).push({});?#&?#&Heat your grill up to approximately 400 degrees, creating direct and indirect zones. Place the cedar planked pizzas on the indirect side and let the dough cook for about 5-7 minutes so the top has a chance to bake before adding toppings. Next, add the toppings and let continue to cook for another 10 minutes until the cheese has melted and the crust is golden brown.?#&?#&Remove from the grill and slice into pieces- you can use the cedar planks as a serving tray for presentation if you like after they have slightly cooled.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'27E2FFF611B75947ABDCEFF90E33F1D5', X'86301EA5AFD11A46A36A9C87871FA6A2', X'86301EA5AFD11A46A36A9C87871FA6A2', 0, 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 2, NULL, 0, 'Lemongrass, Cilantro & Garlic Grilled Chicken Wings', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/Sri-lankan-chicken-wings-up-close-EDITED-150x150.jpg', '', '', '', '', '', '', 'ingredients for marinade)', '', 'https://grillgirl.com/2017/12/lemongrass-cilantro-garlic-grilled-chicken-wings/', '', '', 'Dec 8, 2017', '', 'Instructions?#&?#&The making of the spice paste!?#&?#&Combine olive oil, lemongrass/or zest, cilantro, garlic, turmeric, sea salt and pepper in the food processor and pulse until uniformly mixed- you are looking to make a seasoning “paste”. Next, reserve about 2 tbsp of the marinade to drizzle before serving. Marinate the chicken wings for 30 minutes before putting on the grill.?#&?#&Prepare your grill for medium direct heat at 350 degrees. I used my Green Mountain Grill Pellet Smoker and set it for 375 degrees with the smoker holes wide open. Grill your wings, turning halfway during the process, for approximately 15-18 minutes OR until the internal temperature has reached 170 degrees with an internal read thermometer.?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#& ?#&?#&The Chicken wings on the Green Mountain Grill.?#&?#&Once you’ve reached 170 degrees (I recommend using an internal read thermometer such as the thermapen to check internal temp), pull the wings off and drizzle with the remaining marinade. Prepare for out of this world flavor combination! Enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'486A1E2CA420644796D0D52ECEC71A43', X'39E52E7458D12043BCE90596D5F315BF', X'39E52E7458D12043BCE90596D5F315BF', 0, 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', 'Rosemary Lemon Shrimp Skewers', 2, NULL, 0, 'Rosemary Lemon Shrimp Skewers', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/rosemary-lemon-shrimp-skewers-EDITED-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 lb jumbo or extra jumbo size shrimp- (buy the largest you can find, they will work the best for threading onto the Rosemary skewers)  shells and tails removed?#&1–2 tbsp olive oil?#&7–8 rosemary stems plus a few extra sprigs of rosemary for the marinade (1/4 cup rosemary needles are needed for the marinade)?#&8 lemons, use for 2 tbsp zest then slice thinly?#&3 cloves garlic?#&1 tbsp course Smoked sea salt (if you can’t find smoked sea salt, regular sea salt is okay)?#&1/2 tbsp fresh ground pepper?#&?#&***If you don’t have rosemary skewers, just use 6-8 bamboo skewers and buy a large bunch of rosemary for the marinade?#&?#&The rosemary stem makes for a fun skewer for the shrimp. Shrimp grills quickly so be careful to not overcook!', '', 'https://grillgirl.com/2017/12/rosemary-lemon-shrimp-skewers/', '', '', 'Dec 2, 2017', '', 'Instructions?#&?#&If you have the rosemary stems, remove the rosemary needles in the middle and set aside. If you don’t have the stems, take your bunch of bunch of rosemary, reserving a few sprigs to throw on the grill, and remove the needles- you will need approximately 1/4 cup of rosemary needles for the marinade.?#&Next, take the rosemary needles/leaves and add to a bowl that will be used for making the marinade.?#&Add the olive oil, garlic and 2 tbsp of lemon zest then combine with a stick blender to create a seasoning paste or “slather”.?#&Let the shrimp marinate in the slather for 30 minutes.?#&Meanwhile, you can go prepare your grill while they marinate.?#&While the coals are doing their thing in the chimney starter, use a very sharp knife to thinly slice the lemon into 1/8” slices. The thinner the better as they will be more malleable to go onto the skewers.?#&Next, thread the shrimp onto the rosemary skewers and alternate shrimp with lemon slices that are folded in half. If you don’t have the rosemary skewers, just use regular bamboo skewers (don’t forget to soak them in water beforehand).?#&Prepare your PitBarrel Cooker for cooking on the grill grates.?#&Once your charcoal is ready to go and you are ready to put your skewers on, throw a few rosemary sprigs on the coals for some additional smoke. This will add some nice additional flavor to the shrimp.?#&Get the grill nice and hot and grill the shrimp only a few minutes on each side, looking for them to turn pink and being careful not to overcook.?#&While grilling the shrimp, throw the remaining lemons on the grill until they get charmarks (These will be served with the shrimp and make for great presentation, plus the grill makes them extra juicy for squeezing on the shrimp right before serving)?#&Once they’ve turned pink, take them off and enjoy a flavorful dish that is a true crowd pleaser!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'FE2193120E127443AC43E0FA37A83388', X'E94C65B6DF70564192D1A52F5B8F9237', X'E94C65B6DF70564192D1A52F5B8F9237', 0, 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', 'Smoked Watermelon Gazpacho', 2, NULL, 0, 'Smoked Watermelon Gazpacho', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/09/smoked-watermelon-gazpacho-green-mountain-grills-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Produce:?#&?#&1/2 large watermelon, sliced in wedges?#&3 large cucumbers, skinned and sliced lengthwise?#&1 lb mini bell peppers?#&2 medium sized shallots?#&5 cloves garlic, put them on a skewer so they don’t fall through the grill grates?#&4 medium beefsteak tomatoes, cut in half?#&1 LARGE bunch of Basil, plus more for garnishEtcetera:?#&2 tbsp balsamic vinegar?#&3 tbsp olive oil?#&4 tbsp water?#&1 heaping tbsp. smoked sea salt (I prefer smoked sea salt if you have it!)?#&1 tsp fresh ground pepper (or to taste)', '', 'https://grillgirl.com/2017/09/smoked-watermelon-gazpacho/', '', '', 'Sep 10, 2017', '', 'Instructions?#&?#&Preheat your Green Mountain Grill or your other grill to 350 degrees. While getting your grill heated up, finish slicing your veggies. One your grill is at temperature, put your veggies on. Check your veggies every so often to ensure they are not burning- the garlic and peppers will cook the quickest- don’t let the garlic burn, just put it on long enough to soften and get some smoke. As char marks form, turn the veggies over and let the other side cook. Ideally you are looking for good char marks and the veggies and watermelon to caramelize on the grill. NOTE, the watermelon will not get as charred as the rest of the veggies unless you sliced it extremely thin and that is okay. The inherent nature of this fruit having so much water content takes it longer to get char marks- be patient. As veggies develop the char marks, remove them and let them cool in a big bowl.?#&?#&Once all veggies have been remove, place them in the largest pot you have and get out your stick blender. Now, go ahead and add your additional ingredients: balsamic, olive oil, water, sea salt and pepper and the the basil leaves. Pulse the content with your immersion blender until it is pureed. Don’t over puree it, you want it to have the consistency of a fine salsa.?#&?#&Let chill in the refrigerator for at least an hour before serving. Garnish with additional basil. Great served with grilled bread as an appetizer, also great for a light lunch. Goat cheese or sliced avocado are also a great addition for garnish!?#&?#&Enjoy being the creative cook on your block- I promise your friends and family will rave over this recipe!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'6569FDE37A890646A71745AE609AFED9', X'8065B4F635380E4FAEB6BA4EF54AFC3E', X'8065B4F635380E4FAEB6BA4EF54AFC3E', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'1486160ACB6CAF4195A78D503DC79B42', X'7D01473554666A4CBA0E5EA1F919719E', X'7D01473554666A4CBA0E5EA1F919719E', 0, 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 2, NULL, 0, 'Smithfield Roasted Garlic and Herb Pork Loin, Goat Cheese and Arugula Flatbread Pizzas with Balsamic Glaze, Plus A Chance to win a trip to Napa', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/2006-11-18-15.31-1-150x150.jpg', '', '', '', '', '', '2 pizzas', 'Ingredients?#&?#&1 Smithfield Roasted Garlic & Herb Pork Loin Filet?#&4 Flat Outs Wraps/flatbreads (these are usually in the tortilla or bread/bakery section at the supermarket)?#&8 oz goat cheese?#&1 red onion, chopped in 1/4” rings?#&2 tbsp balsamic glaze (or more to taste)?#&2 cups arugula', '', 'https://grillgirl.com/2017/03/smithfield-roasted-garlic-herb-pork-loin-goat-cheese-arugula-flatbread-pizzas-balsamic-glaze-plus-real-favor-real-fast-contest-chance-win-trip-napa/', '', '', 'Mar 30, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat, about 350 degrees, with two zones- direct and indirect (fire/no fire- on a charcoal grill this means the coals are on one side, on a gas grill this means that you have on side of the grill turned on to medium high, the other side turned off).?#&?#&Prepare the pork loin filet by slicing it in half, keeping each piece as similar in size as possible so it cooks more evenly (one end of the loin filet will be smaller in diameter). Oil your grill grates with a high temperature cooking oil so the pork does not stick. Next, Grill the pork loin on direct heat until it reaches 145 degrees internal temperature (I recommend using an instant read thermometer such as the thermapen to check internal temps). If the pork is cooking too quickly on the outside but is still not to temp on the inside, place the filets on indirect heat to continue to cook until 145 degrees internal temp has been reached.?#&?#&Remove the pork loin and let it rest under foil. Next, grill the onion “rings” until char marks have formed on both sides. Remove and let cool. Slice the pork thinly into bitesized pieces, and then chop the grilled onions.?#&?#&The flatbread pizzas bake on indirect heat on the grill for 8-10 minutes until they become crispy and the cheese has melted. After you pull it off the grill, top with arugula?#&On a large platter or cutting board, Layer two flatbreads on top of each other making two pizzas. Layer goat cheese crumbles, onion, and pork on the pizzas. Drizzle with balsamic glaze. Use a large spatula or pick up the pizzas and lay them on the INDIRECT side of the grill and let them bake until the cheese has begun and the flat breads begin to brown and get crispy and firm, approximately 8-10 minutes. Remove from the grill and place on a cutting board and layer arugula onto each pizza and drizzle more balsamic glaze over the pizzas. Slice into small pieces, approximately 6 each pizza. These are awesome for dinner but are equally awesome as an appetizer!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'DE9CFCD74E254F49B450AEA9A3824B80', X'800096FCD41F424BBF54FA4454009A7C', X'800096FCD41F424BBF54FA4454009A7C', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'28B3B3400E7BFD49B670BB07E927F6B2', X'3B5794751312B74CB43DC4C9D0A37CB7', X'3B5794751312B74CB43DC4C9D0A37CB7', 0, 'https://grillgirl.com/2016/11/fire-roasted-pumpkin-arugula-goat-cheese-salad/', 'Fire Roasted Pumpkin, Arugula and Goat Cheese Salad', 2, NULL, 0, 'Fire Roasted Pumpkin, Arugula and Goat Cheese Salad', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/11/Ember-Roasted-Pumpkin-Salad-150x150.jpg', '', '', '', '', '', 'serves 4', 'Ingredients?#&?#&2 small pumpkins (called Pie pumpkins at the store- the ones for cooking with, not carving for jack-o-lanterns)?#&8 oz goat cheese?#&2 cups arugula?#&2 tbsp balsamic glaze?#&1/2 cup pecans (optional)?#&1/4 cup dried cherries (optional)', '', 'https://grillgirl.com/2016/11/fire-roasted-pumpkin-arugula-goat-cheese-salad/', '', '', 'Nov 11, 2016', '', 'Instructions?#&?#&Create a fire in your grill for medium direct heat. Make sure you use enough coals and they are evenly lit- you will be roasting the pumpkins in the coals.?#&?#&Roast the pumpkins in the coals for 30-45 minutes, making sure that the pumpkins are uniformly surrounded by charcoal so they cook evenly. Pumpkins are ready to be removed once you can easily remove with a form.?#&?#&Remove the pumpkins (using tongs or heat proof gloves) and let cool. Split open and remove seeds. Add arugula and goat cheese. Drizzle with olive oil, balsamic glaze; add salt and pepper to taste. Wonderful with the addition of pecans and dried cherries. What is neat is the half pumpkin makes for a cool serving vehicle for the rest of the ingredients- like a salad bowl. Other winter squshes can be used for this technique- I’ve done this with acorn squash and it was excellent! This recipe is perfect for a Holiday gathering! Do you have a cool way to serve pumpkin? Please share!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'6F741313165B7541A89F1557FD79B3C1', X'005CA3CD0DD2834593BEB25D9BEA25C8', X'005CA3CD0DD2834593BEB25D9BEA25C8', 0, 'https://grillgirl.com/2016/02/thaibasilchickennoodlesalad/', 'Thai Basil Curried Chicken “Noodle” Salad {#Paleo and #GF)', 2, NULL, 0, 'Thai Basil Curried Chicken “Noodle” Salad', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/02/Lemongrass-Basil-Chicken-Noodle-Salad-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&4 large zucchini?#&1lb (boneless) chicken tenders?#&1 tbsp Dizzy Pig Curry Rub OR 1 tbsp curry powder + 2 tsp sea salt?#&1 can coconut milk?#&1 bunch basil?#&1 drop lemongrass essential oil (must be 100% pure, therapeutic grade)?#&Sea Salt- approx. ½ tsp or to taste?#&Fresh Ground Pepper – ½ tsp or to taste', '', 'https://grillgirl.com/2016/02/thaibasilchickennoodlesalad/', '', '', 'Feb 25, 2016', '', 'Instructions?#&?#&Skim the coconut cream off the top of the can of the coconut milk and place in a medium size bowl. This will be about ¼ of the can. Add the salt, pepper and lemongrass essential oil to it and mix.?#&?#&Now, go ahead and preheat your grill to 350 degrees, prepping for direct heat.?#&?#&Add chicken to another bowl and pour in the remaining coconut milk. Add the rub or curry/salt/pepper to the mix and let marinate for a few minutes. Then, grill the chicken for 5-7 minutes or until the chicken has reached an internal temp of 170 degrees. Pull the chicken off the grill and let rest while you finish up inside.?#&?#&While the chicken is grilling, use a vegetable peeler to create zucchini noodles. You will first need to get some of the skin off and if you “square off” the zucchini- ie, create 4 sides to it, it will be easier to get more usable “noodles” from it. Add the coconut cream with the salt/pepper and lemongrass oil and toss until all the noodles are covered in the coconut milk mixture.?#&?#&Chop the basil and add to the noodles. Chop the chicken and add to the noodles. Toss everything together one more time. Enjoy the fragrance of the coconut milk, curry, basil and lemon grass coming together in this flavorful “pasta”. This pairs beautifully with a nice Sauvignon Blanc.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'991FCABEDE28FD4D8C07CCC10E5E894F', X'F0E9915B90BC3A42B5B2E2F9D0FFAF2E', X'F0E9915B90BC3A42B5B2E2F9D0FFAF2E', 0, 'https://grillgirl.com/2015/04/orange-maple-bacon-paleo-cinnamon-buns/', 'Orange Maple Bacon Paleo Cinnamon Buns', 2, NULL, 0, 'Orange Maple Bacon Paleo Cinnamon Buns', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/04/Paleo-Cinnamon-buns_edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Dough:?#&?#&2.5 cups almond flour?#&¼ cup coconut flour?#&¼ tsp baking powder?#&½ teaspoon sea salt?#&¼ cup coconut oil?#&3 tbsp honey or maple syrup?#&2 eggs?#&1 tbsp pure vanilla?#&?#&Filling:?#&?#&½ cup of unsweetened flaked coconut?#&½ cup chopped nuts (I used almonds but I think pecans would be better)?#&½ cups raisins (optional)?#&1 tbsp honey or maple syrup?#&2 tbsp water?#&1++ tbsp orange zest?#&1 tsp cinnamon?#&?#&Frosting:?#&?#&¼ cup honey or maple syrup?#&¼ cup coconut oil?#&2 teaspoons vanilla?#&½ tsp cinnamon?#&¼ cup coconut crème (refrigerate full fat coconut milk overnight and skim the thick part off the top for the coconut crème in this recipe)?#&?#&plus+:?#&?#&4 strips of crispy bacon, chopped into small pieces', '', 'https://grillgirl.com/2015/04/orange-maple-bacon-paleo-cinnamon-buns/', '', '', 'Apr 14, 2015', '', 'Instructions?#&?#&Preheat your oven to 350 degrees. (You can also bake these on the grill in cast iron using indirect heat.)?#&?#&Combine your dry ingredients in one bowl, and then your wet ingredients in another bowl and then combine them together and mix well. Put the dough in the freezer for 15 minutes. The colder the dough the better for working with!?#&?#&Next, combine the filling ingredients.?#&?#&Remove the dough from the freezer. Roll out 2 big pieces of parchment paper. Place the dough ball in the middle of the parchment paper and then put the other piece of parchment on top and roll the dough out lengthwise into a big rectangle until it is ¼ inch thick. The top parchment paper on top is important so the dough does not stick to your rolling pin.?#&?#&Once you’ve made your rectangle, spread your filling out. Next, carefully roll the dough up, using the parchment paper to guide you. Once the dough is rolled up, slice it into 1” pieces using a serrated knife. Gently place the pieces onto a parchment lined cookie sheet.?#&?#&Bake the dough for 20 minutes.?#&?#&Once you remove the buns, let them cool for 5 minutes and mix your glaze ingredients. Generously drizzle the icing over the buns and then finish with a hefty helping of chopped bacon.?#&?#&Prepare to enjoy (almost guilt free) cinnamon bun bliss! I served mine with fruit and extra bacon on the side. These are great reheated the next day in the toaster oven or microwave.?#&?#&Do you enjoy #paleo or #glutenfree baking? Try my cherry lemon Paleo scones or my Paleo drop biscuits. You won’t be disappointed!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2E1B6B253F91D445BB7FCE253ABAB2C2', X'777803E16B8F7D4EB1AC2A07E1637385', X'777803E16B8F7D4EB1AC2A07E1637385', 0, 'https://grillgirl.com/2015/01/grilled-banana-bread-pudding/', 'Grilled Banana Bread Pudding', 2, NULL, 0, 'Grilled Banana Bread Pudding', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/01/Grilled_BananaBreadPudding_CupcakesCocktailsAndKids_MichelleLara_Florida_cck-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&3 bananas {if they’re not fully ripe no worries you will be sprinkling some sugar on them}?#&1 loaf of day old bread?#&3 eggs?#&1 12 oz. can of evaporated milk?#&1 12 oz. can of coconut milk {if you don’t like coconut this can be omitted you can substitute with almond, soy or  regular milk}?#&1 8 oz. can of cream of coconut {if you don’t like coconut this can be omitted}?#&1/4 cup of milk?#&1 tsp. sweetened condensed milk?#&1/4 cup of raisins {optional – you can even add nuts walnuts would be delish}?#&1 tsp. nutmeg?#&2 tsp. cinnamon?#&1 cup of brown sugar?#&1 pinch of salt?#&1/2 stick of unsalted butter', '', 'https://grillgirl.com/2015/01/grilled-banana-bread-pudding/', '', '', 'Jan 28, 2015', '', 'Instructions?#&?#&Preheat your cast iron on the grill and add melt 1/4 stick of butter. slice the bananas and let them coat in the butter and sprinkle some brown sugar on them – cook on a low flame you want them to caramelize not burn. Once fully cooked remove them from the cast iron and set them aside. Add all your wet ingredients in a large mixing bowl. Make sure everything is mixed well and start adding cubed pieces of the bread making sure they’re fully soaked. Lastly add your bananas but set a few aside to place on the top of the bread pudding.?#&?#&Butter the cast iron and pour your mixture , and place the remaining bananas on the top, sprinkle a bit more brown sugar so they can caramelize. Grill on the lowest temperature area of your grill for about an hour covered in aluminum foil. For the last 10 minutes uncover and cook on medium to high heat to get a nice crust. Serve warm with ice cream and/or whip cream!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'F0DE2F37296A07498460EEBF4FEEA772', X'AEC803F50468474B857CE425824F6B8D', X'AEC803F50468474B857CE425824F6B8D', 0, 'https://grillgirl.com/2014/12/smokey-grilled-shrimp-grits-pancetta-gravy/', 'Smokey Grilled Shrimp and Grits with Pancetta Gravy', 2, NULL, 0, 'Smokey Grilled Shrimp and Grits with Pancetta Gravy', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/12/IMG_2356-e1419801022449-113x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/IMG_2361-2-e1419800962353-113x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/IMG_2359-e1419800997199-113x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/IMG_2362-e1419800923292-113x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/IMG_2373-150x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/shrimp-and-grits-creative-commons-150x150.jpg', '', '', '', '', '', '', 'Ingredients:?#&?#&1 1/2 lbs shrimp, deveined and shells removed?#&1 lb Andouille sausage (I used Aidell’s Chicken sausage)?#&4 oz diced pancetta or bacon?#&1 8oz bag mini bell peppers?#&2 tbsp old bay?#&1 tbsp (or more) olive oil?#&6 bamboo skewers, presoaked?#&2 cups of War Eagle Grits?#&1 qt chicken stock, plus 2 cups water?#&1/3 cup milk?#&1 – 14.5 oz can chicken stock (for the gravy)?#&¼ cup flour (I used gluten free)?#&1 tbsp sea salt or to taste?#&1 tbsp fresh ground pepper, to taste?#&1 tbsp smoked paprika', '', 'https://grillgirl.com/2014/12/smokey-grilled-shrimp-grits-pancetta-gravy/', '', '', 'Dec 28, 2014', '', 'Instructions?#&?#&Preheat your grill to 350 degrees and prepare for direct heat. Meanwhile, toss the shrimp in olive oil and Old Bay Seasoning. Thread the shrimp on the skewers. Next, grill the shrimp for 2 minutes per side or until pink and remove from the grill- DO NOT Overcook. Add the sausage and peppers and grill until the sausage has reached an internal temp of 140 degrees (I recommend the thermapen instant read thermometer for checking internal temps quickly) and the peppers have achieved nice char marks on both sides. Remove the sausage and peppers and let cool. Remove the shrimp from the skewers and then chop the peppers and sausage.?#&?#&Next, heat the quart of chicken broth + 2 cups of water in a big pot and bring to a boil. Add 2 cups of grits gradually over 2 minutes and stir constantly. Let the grits simmer for 30-40 minutes and stir constantly. After the grits and broth have cooked down, add milk and cook a minute more. Finish with salt, pepper and smoked paprika.?#&?#&While the grits are cooking, heat pancetta in a cast iron skillet or sauté pan. Cook until crispy and remove the pancetta. Now, bring the pan to medium heat and add the 14.5 oz can of chicken broth. Gradually add the flour, a tablespoon at a time, and stir with a whisk to get remove the lumps. Stir constantly while adding the flour. Let the gravy reduce down and stir constantly to remove any lumps.?#&?#&To serve, place a heaping ladle-full of grits in a bowl, add shrimp, sausage, peppers and bits of pancetta. Finish with a generous serving of gravy! ENJOY!! Goes great with a spicy Bloody Mary for a proper Southern Brunch. And, don’t forget the Hot Sauce!!! My favorite with Shrimp and Grits is Texas Pete.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'C8B35DB513AA9646878C3E706C82C736', X'1B5FBE7C72AD2249A84B3898BAD83C87', X'1B5FBE7C72AD2249A84B3898BAD83C87', 0, 'https://grillgirl.com/2014/12/paleo-cherry-lemon-scones/', 'Paleo Cherry Lemon Scones', 2, NULL, 0, 'Paleo Cherry Lemon Scones', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/12/cherry-lemon-scones-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Original recipe inspiration is from cookeatpaleo.com (I made a few modifications).?#&?#&3 cups almond flour?#&1 tsp baking powder?#&1/8 tsp sea salt?#&zest of 1 lemon?#&2 eggs?#&2 tbsp honey?#&1 tsp vanilla?#&juice of 1 lemon (approximately 2 tbsp juice)?#&¾ cup cherries, chopped in half', '', 'https://grillgirl.com/2014/12/paleo-cherry-lemon-scones/', '', '', 'Dec 2, 2014', '', 'Instructions?#&?#&In a large bowl mix the flour, baking powder, salt and zest. Next, make a spot in the middle of the dry ingredients and add the wet ingredients. Start in the center with the wet ingredients and mix in with the dry ingredients until the dough is uniformly combined. Next, fold in the cherries.?#&?#&Preheat your oven to 325 and line a baking sheet with parchment paper. Using a large spoon or ice cream scoop drop the dough onto the baking sheet and flatten the tops with your hands.?#&?#&Bake for 20 minutes or until the edges begin to brown. After they have cooled, refrigerate these scones and reheat in the oven to serve.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2FCBEA34C9DCF84A835C533539C70151', X'B1E944565F9E4B41909A4B5B142B54F3', X'B1E944565F9E4B41909A4B5B142B54F3', 0, 'https://grillgirl.com/2014/10/grilled-asparagus-sunny-side-eggs-parmesan/', 'Grilled Asparagus with Sunny Side Up Eggs and Parmesan', 2, NULL, 0, 'Grilled Asparagus with Sunny Side Up Eggs and Parmesan', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/10/grilled-asparagus-with-sunny-side-up-eggs-150x150.jpg', '', '', '', '', '', 'serves 4 as a side dish', 'Ingredients?#&?#&1 lb asparagus, thick bottoms of the stalk removed?#&4 eggs?#&1 tblsp olive oil?#&fresh ground pepper, to taste?#&sea salt (or sel gris, fleur de sel, whatever suits your fancy)?#&2 tbsp (or more to your liking) fresh grates parmesan', '', 'https://grillgirl.com/2014/10/grilled-asparagus-sunny-side-eggs-parmesan/', '', '', 'Oct 14, 2014', '', 'Instructions?#&?#&Preheat your grill to medium direct heat, about 350 degrees. Oil your grill grates so food does not stick. Toss the asparagus in the olive oil and then grill the asparagus directly on the grates perpendicular so they do not fall through the grates. Grill approximately 4-5 minutes per side or until char marks have begun to form. Remove the asparagus from the grill. Next, get a skillet hot and coat in butter. Cook 2 eggs until the outer white begins to solidify, and then remove and place on top of the asparagus. Finish with salt, pepper and a parmesan. Prepare to be amazed at how easy and delish this is!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'162666CBAAF7AF43B2F2780C24803D41', X'A40D17EC9E9AD54284EABFFA235F8F2B', X'A40D17EC9E9AD54284EABFFA235F8F2B', 0, 'https://grillgirl.com/2014/02/paleo-friendly-grain-free-coconut-flour-waffles-mango-puree/', 'Paleo/Keto Friendly, Grain Free Coconut Flour Waffles with Mango Puree', 2, NULL, 0, 'Paleo Friendly, Grain Free Coconut Flour Waffles with Mango Puree', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/02/Paleo-Friendly-Waffles-150x150.jpg', '', '', '', '', '', 'Makes approximately 4 waffles', 'Ingredients?#&?#&Recipe from Wellness Mama (slight derivation below)?#&?#&8 eggs?#&½ cup coconut oil?#&½ cup and 1 heaping tblsp coconut flour (make sure it is liquid, not solid)?#&1 tbsp cinnamon?#&dash allspice (optional, I just love allspice)?#&1 tsp vanilla?#&½ tsp salt', '', 'https://grillgirl.com/2014/02/paleo-friendly-grain-free-coconut-flour-waffles-mango-puree/', '', '', 'Feb 9, 2014', '', 'Instructions?#&?#&Beat all the eggs in a large bowl.  Next, add the coconut oil, spices, vanilla and salt and mix well. Add the coconut flour and mix until uniform in consistency (if you have a stick blender this is a great time to use it).?#&?#&If the batter is pretty thin, add a little more coconut flour-you are looking for a very thick mixture.?#&?#&Pour small amounts of batter into a well oiled waffle iron and let cook until golden and firm to the touch.?#&?#&For the mango puree, simmer 1/2 bag frozen mangos in a saucepan with a little water and vanilla (I added a tbsp ginger as well). Puree with your stick blender.?#&?#&These are great served with thick bacon slices  or salty dishes for a sweet/savory combo, and the leftovers can be frozen for a quick weekday breakfast. (I had them for dinner the other night- breakfast for dinner is my fave!)');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B28FD873BF521A469C00FCB2DEFC9E7A', X'169C482A43807042A1E6335A4890DD58', X'169C482A43807042A1E6335A4890DD58', 0, 'https://grillgirl.com/2014/01/skillet-smashed-sweet-potatoes/', 'Skillet Smashed Sweet Potatoes', 2, NULL, 0, 'Skillet Smashed Sweet Potatoes', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/01/Charred-Sweet-Potatoes-150x150.jpg', '', '', '', '', '', 'serves 2', 'Ingredients?#&?#&2 large sweet potatoes?#&2–3 tbsp coconut oil?#&sea salt, to taste?#&fresh ground pepper, to taste', '', 'https://grillgirl.com/2014/01/skillet-smashed-sweet-potatoes/', '', '', 'Jan 28, 2014', '', 'Instructions?#&?#&Drizzle Sweet potatoes with coconut oil and  then roast them in a 350 degree oven for 45 minutes or until tender in the middle. Let the sweet potatoes cool for a few minutes, then slice into 1/2 rounds. Fry  in coconut oil in a medium/high heat cast iron skillet until edges become crispy. Take your spatula and press down on the rounds and smash them into the pan to get the edges crispy.?#&?#&Sprinkle with sea salt, pepper and a drizzle of coconut oil. Enjoy with abandon!?#&?#&Serving ideas: I have eaten these on their own as a side, they are also great tossed with arugula, toasted pecans, and orange sections for a light salad.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'F6BD9CBEA9E8974AA4A0E1588B6E62C0', X'1FC14F3FB9F09241B1844B4B8D4EF277', X'1FC14F3FB9F09241B1844B4B8D4EF277', 0, 'https://grillgirl.com/2013/06/bacon-biscuits-with-molasses/', 'Bacon Biscuits with Molasses', 2, NULL, 0, 'Bacon Biscuits with Molasses', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2013/06/DSC_0612-150x150.jpg', '', '', '', '', '', 'approx. 5 biscuits', 'Ingredients?#&?#&1 1/2 cups Bisquick Mix?#&1/2 cup low fat or skim milk?#&4 strips cooked bacon?#&1 teaspoon smoked sea salt?#&1 teaspoon cracked plack pepper?#&1 dash smoked paprika?#&?#&For Serving:?#&?#&Addition 6-8 slices of cooked bacon?#&Molasses- enough for 5 biscuits to be smothered and covered!', '', 'https://grillgirl.com/2013/06/bacon-biscuits-with-molasses/', '', '', 'Jun 25, 2013', '', 'Instructions?#&?#&Heat oven to 450. Stir biscuit ingredients until a soft dough forms. Place on a surface generously sprinkled with more Bisquick so it does not stick and knead 8-10 times. Next, roll the dough out to 1/2″ thick and cut with a biscuit cutter or glass approx. 2 1/2″ diameter ( again, make sure glass has been dipped in the bisquick mix so it will not stick.)  Place on an ungreased cookie sheet.?#&?#&Bake 7-9 minutes until golden. Please note, the size of your biscuits (and what you used to cut them) will determine baking time so the smaller the biscuits, the faster they will cook.?#&?#&To Serve: Slice the biscuits and layer 2-3 pieces of bacon on them. Add molasses for a sweet and salty treat that will satisfy your craving for some Southern Goodness!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'D973B3A734B51448B39B187613217660', X'FEB09886A143F148909B43D18F8BE9D2', X'FEB09886A143F148909B43D18F8BE9D2', 0, 'https://grillgirl.com/2016/11/fire-roasted-pumpkin-arugula-goat-cheese-salad/', 'Fire Roasted Pumpkin, Arugula and Goat Cheese Salad', 2, NULL, 0, 'Fire Roasted Pumpkin, Arugula and Goat Cheese Salad', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/11/Ember-Roasted-Pumpkin-Salad-150x150.jpg', '', '', '', '', '', 'serves 4', 'Ingredients?#&?#&2 small pumpkins (called Pie pumpkins at the store- the ones for cooking with, not carving for jack-o-lanterns)?#&8 oz goat cheese?#&2 cups arugula?#&2 tbsp balsamic glaze?#&1/2 cup pecans (optional)?#&1/4 cup dried cherries (optional)', '', 'https://grillgirl.com/2016/11/fire-roasted-pumpkin-arugula-goat-cheese-salad/', '', '', 'Nov 11, 2016', '', 'Instructions?#&?#&Create a fire in your grill for medium direct heat. Make sure you use enough coals and they are evenly lit- you will be roasting the pumpkins in the coals.?#&?#&Roast the pumpkins in the coals for 30-45 minutes, making sure that the pumpkins are uniformly surrounded by charcoal so they cook evenly. Pumpkins are ready to be removed once you can easily remove with a form.?#&?#&Remove the pumpkins (using tongs or heat proof gloves) and let cool. Split open and remove seeds. Add arugula and goat cheese. Drizzle with olive oil, balsamic glaze; add salt and pepper to taste. Wonderful with the addition of pecans and dried cherries. What is neat is the half pumpkin makes for a cool serving vehicle for the rest of the ingredients- like a salad bowl. Other winter squshes can be used for this technique- I’ve done this with acorn squash and it was excellent! This recipe is perfect for a Holiday gathering! Do you have a cool way to serve pumpkin? Please share!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'39D142BEF52C424CB7B6247BE5FD9DE0', X'CE866C4BF6BA45458E4EFB5326A142A1', X'CE866C4BF6BA45458E4EFB5326A142A1', 0, 'https://grillgirl.com/2016/02/thaibasilchickennoodlesalad/', 'Thai Basil Curried Chicken “Noodle” Salad {#Paleo and #GF)', 2, NULL, 0, 'Thai Basil Curried Chicken “Noodle” Salad', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/02/Lemongrass-Basil-Chicken-Noodle-Salad-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&4 large zucchini?#&1lb (boneless) chicken tenders?#&1 tbsp Dizzy Pig Curry Rub OR 1 tbsp curry powder + 2 tsp sea salt?#&1 can coconut milk?#&1 bunch basil?#&1 drop lemongrass essential oil (must be 100% pure, therapeutic grade)?#&Sea Salt- approx. ½ tsp or to taste?#&Fresh Ground Pepper – ½ tsp or to taste', '', 'https://grillgirl.com/2016/02/thaibasilchickennoodlesalad/', '', '', 'Feb 25, 2016', '', 'Instructions?#&?#&Skim the coconut cream off the top of the can of the coconut milk and place in a medium size bowl. This will be about ¼ of the can. Add the salt, pepper and lemongrass essential oil to it and mix.?#&?#&Now, go ahead and preheat your grill to 350 degrees, prepping for direct heat.?#&?#&Add chicken to another bowl and pour in the remaining coconut milk. Add the rub or curry/salt/pepper to the mix and let marinate for a few minutes. Then, grill the chicken for 5-7 minutes or until the chicken has reached an internal temp of 170 degrees. Pull the chicken off the grill and let rest while you finish up inside.?#&?#&While the chicken is grilling, use a vegetable peeler to create zucchini noodles. You will first need to get some of the skin off and if you “square off” the zucchini- ie, create 4 sides to it, it will be easier to get more usable “noodles” from it. Add the coconut cream with the salt/pepper and lemongrass oil and toss until all the noodles are covered in the coconut milk mixture.?#&?#&Chop the basil and add to the noodles. Chop the chicken and add to the noodles. Toss everything together one more time. Enjoy the fragrance of the coconut milk, curry, basil and lemon grass coming together in this flavorful “pasta”. This pairs beautifully with a nice Sauvignon Blanc.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'958B82477AE5A8489F7755D863B18EDC', X'4680DE8EBFFD7C4E9A90FB0F36FA4A75', X'4680DE8EBFFD7C4E9A90FB0F36FA4A75', 0, 'https://grillgirl.com/2015/04/orange-maple-bacon-paleo-cinnamon-buns/', 'Orange Maple Bacon Paleo Cinnamon Buns', 2, NULL, 0, 'Orange Maple Bacon Paleo Cinnamon Buns', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/04/Paleo-Cinnamon-buns_edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Dough:?#&?#&2.5 cups almond flour?#&¼ cup coconut flour?#&¼ tsp baking powder?#&½ teaspoon sea salt?#&¼ cup coconut oil?#&3 tbsp honey or maple syrup?#&2 eggs?#&1 tbsp pure vanilla?#&?#&Filling:?#&?#&½ cup of unsweetened flaked coconut?#&½ cup chopped nuts (I used almonds but I think pecans would be better)?#&½ cups raisins (optional)?#&1 tbsp honey or maple syrup?#&2 tbsp water?#&1++ tbsp orange zest?#&1 tsp cinnamon?#&?#&Frosting:?#&?#&¼ cup honey or maple syrup?#&¼ cup coconut oil?#&2 teaspoons vanilla?#&½ tsp cinnamon?#&¼ cup coconut crème (refrigerate full fat coconut milk overnight and skim the thick part off the top for the coconut crème in this recipe)?#&?#&plus+:?#&?#&4 strips of crispy bacon, chopped into small pieces', '', 'https://grillgirl.com/2015/04/orange-maple-bacon-paleo-cinnamon-buns/', '', '', 'Apr 14, 2015', '', 'Instructions?#&?#&Preheat your oven to 350 degrees. (You can also bake these on the grill in cast iron using indirect heat.)?#&?#&Combine your dry ingredients in one bowl, and then your wet ingredients in another bowl and then combine them together and mix well. Put the dough in the freezer for 15 minutes. The colder the dough the better for working with!?#&?#&Next, combine the filling ingredients.?#&?#&Remove the dough from the freezer. Roll out 2 big pieces of parchment paper. Place the dough ball in the middle of the parchment paper and then put the other piece of parchment on top and roll the dough out lengthwise into a big rectangle until it is ¼ inch thick. The top parchment paper on top is important so the dough does not stick to your rolling pin.?#&?#&Once you’ve made your rectangle, spread your filling out. Next, carefully roll the dough up, using the parchment paper to guide you. Once the dough is rolled up, slice it into 1” pieces using a serrated knife. Gently place the pieces onto a parchment lined cookie sheet.?#&?#&Bake the dough for 20 minutes.?#&?#&Once you remove the buns, let them cool for 5 minutes and mix your glaze ingredients. Generously drizzle the icing over the buns and then finish with a hefty helping of chopped bacon.?#&?#&Prepare to enjoy (almost guilt free) cinnamon bun bliss! I served mine with fruit and extra bacon on the side. These are great reheated the next day in the toaster oven or microwave.?#&?#&Do you enjoy #paleo or #glutenfree baking? Try my cherry lemon Paleo scones or my Paleo drop biscuits. You won’t be disappointed!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'E16F5C318D84D74E856D32D401489C23', X'2954FC6A0B06E74E9206EFF55B1F2872', X'2954FC6A0B06E74E9206EFF55B1F2872', 0, 'https://grillgirl.com/2015/01/grilled-banana-bread-pudding/', 'Grilled Banana Bread Pudding', 2, NULL, 0, 'Grilled Banana Bread Pudding', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/01/Grilled_BananaBreadPudding_CupcakesCocktailsAndKids_MichelleLara_Florida_cck-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&3 bananas {if they’re not fully ripe no worries you will be sprinkling some sugar on them}?#&1 loaf of day old bread?#&3 eggs?#&1 12 oz. can of evaporated milk?#&1 12 oz. can of coconut milk {if you don’t like coconut this can be omitted you can substitute with almond, soy or  regular milk}?#&1 8 oz. can of cream of coconut {if you don’t like coconut this can be omitted}?#&1/4 cup of milk?#&1 tsp. sweetened condensed milk?#&1/4 cup of raisins {optional – you can even add nuts walnuts would be delish}?#&1 tsp. nutmeg?#&2 tsp. cinnamon?#&1 cup of brown sugar?#&1 pinch of salt?#&1/2 stick of unsalted butter', '', 'https://grillgirl.com/2015/01/grilled-banana-bread-pudding/', '', '', 'Jan 28, 2015', '', 'Instructions?#&?#&Preheat your cast iron on the grill and add melt 1/4 stick of butter. slice the bananas and let them coat in the butter and sprinkle some brown sugar on them – cook on a low flame you want them to caramelize not burn. Once fully cooked remove them from the cast iron and set them aside. Add all your wet ingredients in a large mixing bowl. Make sure everything is mixed well and start adding cubed pieces of the bread making sure they’re fully soaked. Lastly add your bananas but set a few aside to place on the top of the bread pudding.?#&?#&Butter the cast iron and pour your mixture , and place the remaining bananas on the top, sprinkle a bit more brown sugar so they can caramelize. Grill on the lowest temperature area of your grill for about an hour covered in aluminum foil. For the last 10 minutes uncover and cook on medium to high heat to get a nice crust. Serve warm with ice cream and/or whip cream!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'629D8309273B3D43B8117560468D60F6', X'CBE48AFAE0FAC64A8A5C2C082024D9BA', X'CBE48AFAE0FAC64A8A5C2C082024D9BA', 0, 'https://grillgirl.com/2014/12/smokey-grilled-shrimp-grits-pancetta-gravy/', 'Smokey Grilled Shrimp and Grits with Pancetta Gravy', 2, NULL, 0, 'Smokey Grilled Shrimp and Grits with Pancetta Gravy', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/12/IMG_2356-e1419801022449-113x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/IMG_2361-2-e1419800962353-113x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/IMG_2359-e1419800997199-113x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/IMG_2362-e1419800923292-113x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/IMG_2373-150x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/shrimp-and-grits-creative-commons-150x150.jpg', '', '', '', '', '', '', 'Ingredients:?#&?#&1 1/2 lbs shrimp, deveined and shells removed?#&1 lb Andouille sausage (I used Aidell’s Chicken sausage)?#&4 oz diced pancetta or bacon?#&1 8oz bag mini bell peppers?#&2 tbsp old bay?#&1 tbsp (or more) olive oil?#&6 bamboo skewers, presoaked?#&2 cups of War Eagle Grits?#&1 qt chicken stock, plus 2 cups water?#&1/3 cup milk?#&1 – 14.5 oz can chicken stock (for the gravy)?#&¼ cup flour (I used gluten free)?#&1 tbsp sea salt or to taste?#&1 tbsp fresh ground pepper, to taste?#&1 tbsp smoked paprika', '', 'https://grillgirl.com/2014/12/smokey-grilled-shrimp-grits-pancetta-gravy/', '', '', 'Dec 28, 2014', '', 'Instructions?#&?#&Preheat your grill to 350 degrees and prepare for direct heat. Meanwhile, toss the shrimp in olive oil and Old Bay Seasoning. Thread the shrimp on the skewers. Next, grill the shrimp for 2 minutes per side or until pink and remove from the grill- DO NOT Overcook. Add the sausage and peppers and grill until the sausage has reached an internal temp of 140 degrees (I recommend the thermapen instant read thermometer for checking internal temps quickly) and the peppers have achieved nice char marks on both sides. Remove the sausage and peppers and let cool. Remove the shrimp from the skewers and then chop the peppers and sausage.?#&?#&Next, heat the quart of chicken broth + 2 cups of water in a big pot and bring to a boil. Add 2 cups of grits gradually over 2 minutes and stir constantly. Let the grits simmer for 30-40 minutes and stir constantly. After the grits and broth have cooked down, add milk and cook a minute more. Finish with salt, pepper and smoked paprika.?#&?#&While the grits are cooking, heat pancetta in a cast iron skillet or sauté pan. Cook until crispy and remove the pancetta. Now, bring the pan to medium heat and add the 14.5 oz can of chicken broth. Gradually add the flour, a tablespoon at a time, and stir with a whisk to get remove the lumps. Stir constantly while adding the flour. Let the gravy reduce down and stir constantly to remove any lumps.?#&?#&To serve, place a heaping ladle-full of grits in a bowl, add shrimp, sausage, peppers and bits of pancetta. Finish with a generous serving of gravy! ENJOY!! Goes great with a spicy Bloody Mary for a proper Southern Brunch. And, don’t forget the Hot Sauce!!! My favorite with Shrimp and Grits is Texas Pete.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'723F26E86A518446A1C6B9575EB1CCDE', X'AA1C8FDCCA9EF84EB122E7C2D374F9EB', X'AA1C8FDCCA9EF84EB122E7C2D374F9EB', 0, 'https://grillgirl.com/2014/12/paleo-cherry-lemon-scones/', 'Paleo Cherry Lemon Scones', 2, NULL, 0, 'Paleo Cherry Lemon Scones', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/12/cherry-lemon-scones-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Original recipe inspiration is from cookeatpaleo.com (I made a few modifications).?#&?#&3 cups almond flour?#&1 tsp baking powder?#&1/8 tsp sea salt?#&zest of 1 lemon?#&2 eggs?#&2 tbsp honey?#&1 tsp vanilla?#&juice of 1 lemon (approximately 2 tbsp juice)?#&¾ cup cherries, chopped in half', '', 'https://grillgirl.com/2014/12/paleo-cherry-lemon-scones/', '', '', 'Dec 2, 2014', '', 'Instructions?#&?#&In a large bowl mix the flour, baking powder, salt and zest. Next, make a spot in the middle of the dry ingredients and add the wet ingredients. Start in the center with the wet ingredients and mix in with the dry ingredients until the dough is uniformly combined. Next, fold in the cherries.?#&?#&Preheat your oven to 325 and line a baking sheet with parchment paper. Using a large spoon or ice cream scoop drop the dough onto the baking sheet and flatten the tops with your hands.?#&?#&Bake for 20 minutes or until the edges begin to brown. After they have cooled, refrigerate these scones and reheat in the oven to serve.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'E3F446677BBA734DAFE5B61E65C660E1', X'BBE83FDD2AD0EB4AA64BFFCA56AAC394', X'BBE83FDD2AD0EB4AA64BFFCA56AAC394', 0, 'https://grillgirl.com/2014/10/grilled-asparagus-sunny-side-eggs-parmesan/', 'Grilled Asparagus with Sunny Side Up Eggs and Parmesan', 2, NULL, 0, 'Grilled Asparagus with Sunny Side Up Eggs and Parmesan', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/10/grilled-asparagus-with-sunny-side-up-eggs-150x150.jpg', '', '', '', '', '', 'serves 4 as a side dish', 'Ingredients?#&?#&1 lb asparagus, thick bottoms of the stalk removed?#&4 eggs?#&1 tblsp olive oil?#&fresh ground pepper, to taste?#&sea salt (or sel gris, fleur de sel, whatever suits your fancy)?#&2 tbsp (or more to your liking) fresh grates parmesan', '', 'https://grillgirl.com/2014/10/grilled-asparagus-sunny-side-eggs-parmesan/', '', '', 'Oct 14, 2014', '', 'Instructions?#&?#&Preheat your grill to medium direct heat, about 350 degrees. Oil your grill grates so food does not stick. Toss the asparagus in the olive oil and then grill the asparagus directly on the grates perpendicular so they do not fall through the grates. Grill approximately 4-5 minutes per side or until char marks have begun to form. Remove the asparagus from the grill. Next, get a skillet hot and coat in butter. Cook 2 eggs until the outer white begins to solidify, and then remove and place on top of the asparagus. Finish with salt, pepper and a parmesan. Prepare to be amazed at how easy and delish this is!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'5B12269D06D2FD4F89EB98AA93F4C926', X'375746BA7B6E2747AD301378155F8791', X'375746BA7B6E2747AD301378155F8791', 0, 'https://grillgirl.com/2014/02/paleo-friendly-grain-free-coconut-flour-waffles-mango-puree/', 'Paleo/Keto Friendly, Grain Free Coconut Flour Waffles with Mango Puree', 2, NULL, 0, 'Paleo Friendly, Grain Free Coconut Flour Waffles with Mango Puree', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/02/Paleo-Friendly-Waffles-150x150.jpg', '', '', '', '', '', 'Makes approximately 4 waffles', 'Ingredients?#&?#&Recipe from Wellness Mama (slight derivation below)?#&?#&8 eggs?#&½ cup coconut oil?#&½ cup and 1 heaping tblsp coconut flour (make sure it is liquid, not solid)?#&1 tbsp cinnamon?#&dash allspice (optional, I just love allspice)?#&1 tsp vanilla?#&½ tsp salt', '', 'https://grillgirl.com/2014/02/paleo-friendly-grain-free-coconut-flour-waffles-mango-puree/', '', '', 'Feb 9, 2014', '', 'Instructions?#&?#&Beat all the eggs in a large bowl.  Next, add the coconut oil, spices, vanilla and salt and mix well. Add the coconut flour and mix until uniform in consistency (if you have a stick blender this is a great time to use it).?#&?#&If the batter is pretty thin, add a little more coconut flour-you are looking for a very thick mixture.?#&?#&Pour small amounts of batter into a well oiled waffle iron and let cook until golden and firm to the touch.?#&?#&For the mango puree, simmer 1/2 bag frozen mangos in a saucepan with a little water and vanilla (I added a tbsp ginger as well). Puree with your stick blender.?#&?#&These are great served with thick bacon slices  or salty dishes for a sweet/savory combo, and the leftovers can be frozen for a quick weekday breakfast. (I had them for dinner the other night- breakfast for dinner is my fave!)');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'923C5A782F87FE4DB29038DE2E9866FA', X'6107D49BFD66D5409A35B0A0DF3BF8FB', X'6107D49BFD66D5409A35B0A0DF3BF8FB', 0, 'https://grillgirl.com/2014/01/skillet-smashed-sweet-potatoes/', 'Skillet Smashed Sweet Potatoes', 2, NULL, 0, 'Skillet Smashed Sweet Potatoes', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/01/Charred-Sweet-Potatoes-150x150.jpg', '', '', '', '', '', 'serves 2', 'Ingredients?#&?#&2 large sweet potatoes?#&2–3 tbsp coconut oil?#&sea salt, to taste?#&fresh ground pepper, to taste', '', 'https://grillgirl.com/2014/01/skillet-smashed-sweet-potatoes/', '', '', 'Jan 28, 2014', '', 'Instructions?#&?#&Drizzle Sweet potatoes with coconut oil and  then roast them in a 350 degree oven for 45 minutes or until tender in the middle. Let the sweet potatoes cool for a few minutes, then slice into 1/2 rounds. Fry  in coconut oil in a medium/high heat cast iron skillet until edges become crispy. Take your spatula and press down on the rounds and smash them into the pan to get the edges crispy.?#&?#&Sprinkle with sea salt, pepper and a drizzle of coconut oil. Enjoy with abandon!?#&?#&Serving ideas: I have eaten these on their own as a side, they are also great tossed with arugula, toasted pecans, and orange sections for a light salad.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'5A42430A64ACCF44BBF9F3472C0E21D9', X'BD02206505060C4F9FB0736745C3AC04', X'BD02206505060C4F9FB0736745C3AC04', 0, 'https://grillgirl.com/2013/06/bacon-biscuits-with-molasses/', 'Bacon Biscuits with Molasses', 2, NULL, 0, 'Bacon Biscuits with Molasses', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2013/06/DSC_0612-150x150.jpg', '', '', '', '', '', 'approx. 5 biscuits', 'Ingredients?#&?#&1 1/2 cups Bisquick Mix?#&1/2 cup low fat or skim milk?#&4 strips cooked bacon?#&1 teaspoon smoked sea salt?#&1 teaspoon cracked plack pepper?#&1 dash smoked paprika?#&?#&For Serving:?#&?#&Addition 6-8 slices of cooked bacon?#&Molasses- enough for 5 biscuits to be smothered and covered!', '', 'https://grillgirl.com/2013/06/bacon-biscuits-with-molasses/', '', '', 'Jun 25, 2013', '', 'Instructions?#&?#&Heat oven to 450. Stir biscuit ingredients until a soft dough forms. Place on a surface generously sprinkled with more Bisquick so it does not stick and knead 8-10 times. Next, roll the dough out to 1/2″ thick and cut with a biscuit cutter or glass approx. 2 1/2″ diameter ( again, make sure glass has been dipped in the bisquick mix so it will not stick.)  Place on an ungreased cookie sheet.?#&?#&Bake 7-9 minutes until golden. Please note, the size of your biscuits (and what you used to cut them) will determine baking time so the smaller the biscuits, the faster they will cook.?#&?#&To Serve: Slice the biscuits and layer 2-3 pieces of bacon on them. Add molasses for a sweet and salty treat that will satisfy your craving for some Southern Goodness!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'406EA47DFB342843BE15241CF7DC5AB1', X'1AD0D6F811D7394ABA077CD52CD663B8', X'1AD0D6F811D7394ABA077CD52CD663B8', 0, 'https://grillgirl.com/2016/11/fire-roasted-pumpkin-arugula-goat-cheese-salad/', 'Fire Roasted Pumpkin, Arugula and Goat Cheese Salad', 2, NULL, 0, 'Fire Roasted Pumpkin, Arugula and Goat Cheese Salad', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/11/Ember-Roasted-Pumpkin-Salad-150x150.jpg', '', '', '', '', '', 'serves 4', 'Ingredients?#&?#&2 small pumpkins (called Pie pumpkins at the store- the ones for cooking with, not carving for jack-o-lanterns)?#&8 oz goat cheese?#&2 cups arugula?#&2 tbsp balsamic glaze?#&1/2 cup pecans (optional)?#&1/4 cup dried cherries (optional)', '', 'https://grillgirl.com/2016/11/fire-roasted-pumpkin-arugula-goat-cheese-salad/', '', '', 'Nov 11, 2016', '', 'Instructions?#&?#&Create a fire in your grill for medium direct heat. Make sure you use enough coals and they are evenly lit- you will be roasting the pumpkins in the coals.?#&?#&Roast the pumpkins in the coals for 30-45 minutes, making sure that the pumpkins are uniformly surrounded by charcoal so they cook evenly. Pumpkins are ready to be removed once you can easily remove with a form.?#&?#&Remove the pumpkins (using tongs or heat proof gloves) and let cool. Split open and remove seeds. Add arugula and goat cheese. Drizzle with olive oil, balsamic glaze; add salt and pepper to taste. Wonderful with the addition of pecans and dried cherries. What is neat is the half pumpkin makes for a cool serving vehicle for the rest of the ingredients- like a salad bowl. Other winter squshes can be used for this technique- I’ve done this with acorn squash and it was excellent! This recipe is perfect for a Holiday gathering! Do you have a cool way to serve pumpkin? Please share!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'C5268A97A3E08A49B481E85B8E2CCFD4', X'4B410EEA3B79234FA63C436360B8F23D', X'4B410EEA3B79234FA63C436360B8F23D', 0, 'https://grillgirl.com/2016/02/thaibasilchickennoodlesalad/', 'Thai Basil Curried Chicken “Noodle” Salad {#Paleo and #GF)', 2, NULL, 0, 'Thai Basil Curried Chicken “Noodle” Salad', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/02/Lemongrass-Basil-Chicken-Noodle-Salad-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&4 large zucchini?#&1lb (boneless) chicken tenders?#&1 tbsp Dizzy Pig Curry Rub OR 1 tbsp curry powder + 2 tsp sea salt?#&1 can coconut milk?#&1 bunch basil?#&1 drop lemongrass essential oil (must be 100% pure, therapeutic grade)?#&Sea Salt- approx. ½ tsp or to taste?#&Fresh Ground Pepper – ½ tsp or to taste', '', 'https://grillgirl.com/2016/02/thaibasilchickennoodlesalad/', '', '', 'Feb 25, 2016', '', 'Instructions?#&?#&Skim the coconut cream off the top of the can of the coconut milk and place in a medium size bowl. This will be about ¼ of the can. Add the salt, pepper and lemongrass essential oil to it and mix.?#&?#&Now, go ahead and preheat your grill to 350 degrees, prepping for direct heat.?#&?#&Add chicken to another bowl and pour in the remaining coconut milk. Add the rub or curry/salt/pepper to the mix and let marinate for a few minutes. Then, grill the chicken for 5-7 minutes or until the chicken has reached an internal temp of 170 degrees. Pull the chicken off the grill and let rest while you finish up inside.?#&?#&While the chicken is grilling, use a vegetable peeler to create zucchini noodles. You will first need to get some of the skin off and if you “square off” the zucchini- ie, create 4 sides to it, it will be easier to get more usable “noodles” from it. Add the coconut cream with the salt/pepper and lemongrass oil and toss until all the noodles are covered in the coconut milk mixture.?#&?#&Chop the basil and add to the noodles. Chop the chicken and add to the noodles. Toss everything together one more time. Enjoy the fragrance of the coconut milk, curry, basil and lemon grass coming together in this flavorful “pasta”. This pairs beautifully with a nice Sauvignon Blanc.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'A164C9BA7306F342B593E008B162B18B', X'AE6CE059F8CF454FB6ADF0DC3BC46FE1', X'AE6CE059F8CF454FB6ADF0DC3BC46FE1', 0, 'https://grillgirl.com/2015/04/orange-maple-bacon-paleo-cinnamon-buns/', 'Orange Maple Bacon Paleo Cinnamon Buns', 2, NULL, 0, 'Orange Maple Bacon Paleo Cinnamon Buns', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/04/Paleo-Cinnamon-buns_edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Dough:?#&?#&2.5 cups almond flour?#&¼ cup coconut flour?#&¼ tsp baking powder?#&½ teaspoon sea salt?#&¼ cup coconut oil?#&3 tbsp honey or maple syrup?#&2 eggs?#&1 tbsp pure vanilla?#&?#&Filling:?#&?#&½ cup of unsweetened flaked coconut?#&½ cup chopped nuts (I used almonds but I think pecans would be better)?#&½ cups raisins (optional)?#&1 tbsp honey or maple syrup?#&2 tbsp water?#&1++ tbsp orange zest?#&1 tsp cinnamon?#&?#&Frosting:?#&?#&¼ cup honey or maple syrup?#&¼ cup coconut oil?#&2 teaspoons vanilla?#&½ tsp cinnamon?#&¼ cup coconut crème (refrigerate full fat coconut milk overnight and skim the thick part off the top for the coconut crème in this recipe)?#&?#&plus+:?#&?#&4 strips of crispy bacon, chopped into small pieces', '', 'https://grillgirl.com/2015/04/orange-maple-bacon-paleo-cinnamon-buns/', '', '', 'Apr 14, 2015', '', 'Instructions?#&?#&Preheat your oven to 350 degrees. (You can also bake these on the grill in cast iron using indirect heat.)?#&?#&Combine your dry ingredients in one bowl, and then your wet ingredients in another bowl and then combine them together and mix well. Put the dough in the freezer for 15 minutes. The colder the dough the better for working with!?#&?#&Next, combine the filling ingredients.?#&?#&Remove the dough from the freezer. Roll out 2 big pieces of parchment paper. Place the dough ball in the middle of the parchment paper and then put the other piece of parchment on top and roll the dough out lengthwise into a big rectangle until it is ¼ inch thick. The top parchment paper on top is important so the dough does not stick to your rolling pin.?#&?#&Once you’ve made your rectangle, spread your filling out. Next, carefully roll the dough up, using the parchment paper to guide you. Once the dough is rolled up, slice it into 1” pieces using a serrated knife. Gently place the pieces onto a parchment lined cookie sheet.?#&?#&Bake the dough for 20 minutes.?#&?#&Once you remove the buns, let them cool for 5 minutes and mix your glaze ingredients. Generously drizzle the icing over the buns and then finish with a hefty helping of chopped bacon.?#&?#&Prepare to enjoy (almost guilt free) cinnamon bun bliss! I served mine with fruit and extra bacon on the side. These are great reheated the next day in the toaster oven or microwave.?#&?#&Do you enjoy #paleo or #glutenfree baking? Try my cherry lemon Paleo scones or my Paleo drop biscuits. You won’t be disappointed!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'691D26289C087C42B44F0A2F2D5D472F', X'B72FFF80C2A46E4B84B1F9A2386AAC2B', X'B72FFF80C2A46E4B84B1F9A2386AAC2B', 0, 'https://grillgirl.com/2015/01/grilled-banana-bread-pudding/', 'Grilled Banana Bread Pudding', 2, NULL, 0, 'Grilled Banana Bread Pudding', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/01/Grilled_BananaBreadPudding_CupcakesCocktailsAndKids_MichelleLara_Florida_cck-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&3 bananas {if they’re not fully ripe no worries you will be sprinkling some sugar on them}?#&1 loaf of day old bread?#&3 eggs?#&1 12 oz. can of evaporated milk?#&1 12 oz. can of coconut milk {if you don’t like coconut this can be omitted you can substitute with almond, soy or  regular milk}?#&1 8 oz. can of cream of coconut {if you don’t like coconut this can be omitted}?#&1/4 cup of milk?#&1 tsp. sweetened condensed milk?#&1/4 cup of raisins {optional – you can even add nuts walnuts would be delish}?#&1 tsp. nutmeg?#&2 tsp. cinnamon?#&1 cup of brown sugar?#&1 pinch of salt?#&1/2 stick of unsalted butter', '', 'https://grillgirl.com/2015/01/grilled-banana-bread-pudding/', '', '', 'Jan 28, 2015', '', 'Instructions?#&?#&Preheat your cast iron on the grill and add melt 1/4 stick of butter. slice the bananas and let them coat in the butter and sprinkle some brown sugar on them – cook on a low flame you want them to caramelize not burn. Once fully cooked remove them from the cast iron and set them aside. Add all your wet ingredients in a large mixing bowl. Make sure everything is mixed well and start adding cubed pieces of the bread making sure they’re fully soaked. Lastly add your bananas but set a few aside to place on the top of the bread pudding.?#&?#&Butter the cast iron and pour your mixture , and place the remaining bananas on the top, sprinkle a bit more brown sugar so they can caramelize. Grill on the lowest temperature area of your grill for about an hour covered in aluminum foil. For the last 10 minutes uncover and cook on medium to high heat to get a nice crust. Serve warm with ice cream and/or whip cream!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'EF649CF0D6DD004897B6C685BA128066', X'0AE0A4528C395847BCB75305ED0C5573', X'0AE0A4528C395847BCB75305ED0C5573', 0, 'https://grillgirl.com/2014/12/smokey-grilled-shrimp-grits-pancetta-gravy/', 'Smokey Grilled Shrimp and Grits with Pancetta Gravy', 2, NULL, 0, 'Smokey Grilled Shrimp and Grits with Pancetta Gravy', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/12/IMG_2356-e1419801022449-113x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/IMG_2361-2-e1419800962353-113x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/IMG_2359-e1419800997199-113x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/IMG_2362-e1419800923292-113x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/IMG_2373-150x150.jpghttps://grillgirl.com/wp-content/uploads/2014/12/shrimp-and-grits-creative-commons-150x150.jpg', '', '', '', '', '', '', 'Ingredients:?#&?#&1 1/2 lbs shrimp, deveined and shells removed?#&1 lb Andouille sausage (I used Aidell’s Chicken sausage)?#&4 oz diced pancetta or bacon?#&1 8oz bag mini bell peppers?#&2 tbsp old bay?#&1 tbsp (or more) olive oil?#&6 bamboo skewers, presoaked?#&2 cups of War Eagle Grits?#&1 qt chicken stock, plus 2 cups water?#&1/3 cup milk?#&1 – 14.5 oz can chicken stock (for the gravy)?#&¼ cup flour (I used gluten free)?#&1 tbsp sea salt or to taste?#&1 tbsp fresh ground pepper, to taste?#&1 tbsp smoked paprika', '', 'https://grillgirl.com/2014/12/smokey-grilled-shrimp-grits-pancetta-gravy/', '', '', 'Dec 28, 2014', '', 'Instructions?#&?#&Preheat your grill to 350 degrees and prepare for direct heat. Meanwhile, toss the shrimp in olive oil and Old Bay Seasoning. Thread the shrimp on the skewers. Next, grill the shrimp for 2 minutes per side or until pink and remove from the grill- DO NOT Overcook. Add the sausage and peppers and grill until the sausage has reached an internal temp of 140 degrees (I recommend the thermapen instant read thermometer for checking internal temps quickly) and the peppers have achieved nice char marks on both sides. Remove the sausage and peppers and let cool. Remove the shrimp from the skewers and then chop the peppers and sausage.?#&?#&Next, heat the quart of chicken broth + 2 cups of water in a big pot and bring to a boil. Add 2 cups of grits gradually over 2 minutes and stir constantly. Let the grits simmer for 30-40 minutes and stir constantly. After the grits and broth have cooked down, add milk and cook a minute more. Finish with salt, pepper and smoked paprika.?#&?#&While the grits are cooking, heat pancetta in a cast iron skillet or sauté pan. Cook until crispy and remove the pancetta. Now, bring the pan to medium heat and add the 14.5 oz can of chicken broth. Gradually add the flour, a tablespoon at a time, and stir with a whisk to get remove the lumps. Stir constantly while adding the flour. Let the gravy reduce down and stir constantly to remove any lumps.?#&?#&To serve, place a heaping ladle-full of grits in a bowl, add shrimp, sausage, peppers and bits of pancetta. Finish with a generous serving of gravy! ENJOY!! Goes great with a spicy Bloody Mary for a proper Southern Brunch. And, don’t forget the Hot Sauce!!! My favorite with Shrimp and Grits is Texas Pete.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'18CDF565B2130D48ABB9C70351335FF5', X'E6FC08062D1DE044B8A256DDF5F00864', X'E6FC08062D1DE044B8A256DDF5F00864', 0, 'https://grillgirl.com/2014/12/paleo-cherry-lemon-scones/', 'Paleo Cherry Lemon Scones', 2, NULL, 0, 'Paleo Cherry Lemon Scones', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/12/cherry-lemon-scones-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Original recipe inspiration is from cookeatpaleo.com (I made a few modifications).?#&?#&3 cups almond flour?#&1 tsp baking powder?#&1/8 tsp sea salt?#&zest of 1 lemon?#&2 eggs?#&2 tbsp honey?#&1 tsp vanilla?#&juice of 1 lemon (approximately 2 tbsp juice)?#&¾ cup cherries, chopped in half', '', 'https://grillgirl.com/2014/12/paleo-cherry-lemon-scones/', '', '', 'Dec 2, 2014', '', 'Instructions?#&?#&In a large bowl mix the flour, baking powder, salt and zest. Next, make a spot in the middle of the dry ingredients and add the wet ingredients. Start in the center with the wet ingredients and mix in with the dry ingredients until the dough is uniformly combined. Next, fold in the cherries.?#&?#&Preheat your oven to 325 and line a baking sheet with parchment paper. Using a large spoon or ice cream scoop drop the dough onto the baking sheet and flatten the tops with your hands.?#&?#&Bake for 20 minutes or until the edges begin to brown. After they have cooled, refrigerate these scones and reheat in the oven to serve.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'663AFB7C62E37A4186BB878B7B9E1840', X'5351EA239D6A0845AA4DCB0D77E6F02D', X'5351EA239D6A0845AA4DCB0D77E6F02D', 0, 'https://grillgirl.com/2014/10/grilled-asparagus-sunny-side-eggs-parmesan/', 'Grilled Asparagus with Sunny Side Up Eggs and Parmesan', 2, NULL, 0, 'Grilled Asparagus with Sunny Side Up Eggs and Parmesan', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/10/grilled-asparagus-with-sunny-side-up-eggs-150x150.jpg', '', '', '', '', '', 'serves 4 as a side dish', 'Ingredients?#&?#&1 lb asparagus, thick bottoms of the stalk removed?#&4 eggs?#&1 tblsp olive oil?#&fresh ground pepper, to taste?#&sea salt (or sel gris, fleur de sel, whatever suits your fancy)?#&2 tbsp (or more to your liking) fresh grates parmesan', '', 'https://grillgirl.com/2014/10/grilled-asparagus-sunny-side-eggs-parmesan/', '', '', 'Oct 14, 2014', '', 'Instructions?#&?#&Preheat your grill to medium direct heat, about 350 degrees. Oil your grill grates so food does not stick. Toss the asparagus in the olive oil and then grill the asparagus directly on the grates perpendicular so they do not fall through the grates. Grill approximately 4-5 minutes per side or until char marks have begun to form. Remove the asparagus from the grill. Next, get a skillet hot and coat in butter. Cook 2 eggs until the outer white begins to solidify, and then remove and place on top of the asparagus. Finish with salt, pepper and a parmesan. Prepare to be amazed at how easy and delish this is!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'169317372897B645B9C2D748801FAFA5', X'01F9548440066944980322CE21A47311', X'01F9548440066944980322CE21A47311', 0, 'https://grillgirl.com/2014/02/paleo-friendly-grain-free-coconut-flour-waffles-mango-puree/', 'Paleo/Keto Friendly, Grain Free Coconut Flour Waffles with Mango Puree', 2, NULL, 0, 'Paleo Friendly, Grain Free Coconut Flour Waffles with Mango Puree', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/02/Paleo-Friendly-Waffles-150x150.jpg', '', '', '', '', '', 'Makes approximately 4 waffles', 'Ingredients?#&?#&Recipe from Wellness Mama (slight derivation below)?#&?#&8 eggs?#&½ cup coconut oil?#&½ cup and 1 heaping tblsp coconut flour (make sure it is liquid, not solid)?#&1 tbsp cinnamon?#&dash allspice (optional, I just love allspice)?#&1 tsp vanilla?#&½ tsp salt', '', 'https://grillgirl.com/2014/02/paleo-friendly-grain-free-coconut-flour-waffles-mango-puree/', '', '', 'Feb 9, 2014', '', 'Instructions?#&?#&Beat all the eggs in a large bowl.  Next, add the coconut oil, spices, vanilla and salt and mix well. Add the coconut flour and mix until uniform in consistency (if you have a stick blender this is a great time to use it).?#&?#&If the batter is pretty thin, add a little more coconut flour-you are looking for a very thick mixture.?#&?#&Pour small amounts of batter into a well oiled waffle iron and let cook until golden and firm to the touch.?#&?#&For the mango puree, simmer 1/2 bag frozen mangos in a saucepan with a little water and vanilla (I added a tbsp ginger as well). Puree with your stick blender.?#&?#&These are great served with thick bacon slices  or salty dishes for a sweet/savory combo, and the leftovers can be frozen for a quick weekday breakfast. (I had them for dinner the other night- breakfast for dinner is my fave!)');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'4F47983B91F5EA47AB9093CE8FCF131C', X'3E5ADFDF03971E468DBA619CD84ED36A', X'3E5ADFDF03971E468DBA619CD84ED36A', 0, 'https://grillgirl.com/2014/01/skillet-smashed-sweet-potatoes/', 'Skillet Smashed Sweet Potatoes', 2, NULL, 0, 'Skillet Smashed Sweet Potatoes', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/01/Charred-Sweet-Potatoes-150x150.jpg', '', '', '', '', '', 'serves 2', 'Ingredients?#&?#&2 large sweet potatoes?#&2–3 tbsp coconut oil?#&sea salt, to taste?#&fresh ground pepper, to taste', '', 'https://grillgirl.com/2014/01/skillet-smashed-sweet-potatoes/', '', '', 'Jan 28, 2014', '', 'Instructions?#&?#&Drizzle Sweet potatoes with coconut oil and  then roast them in a 350 degree oven for 45 minutes or until tender in the middle. Let the sweet potatoes cool for a few minutes, then slice into 1/2 rounds. Fry  in coconut oil in a medium/high heat cast iron skillet until edges become crispy. Take your spatula and press down on the rounds and smash them into the pan to get the edges crispy.?#&?#&Sprinkle with sea salt, pepper and a drizzle of coconut oil. Enjoy with abandon!?#&?#&Serving ideas: I have eaten these on their own as a side, they are also great tossed with arugula, toasted pecans, and orange sections for a light salad.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'49A1CE24D97793458B905524AE8659DF', X'8226053AB93CBE48AE7DDE94CAB0D1D6', X'8226053AB93CBE48AE7DDE94CAB0D1D6', 0, 'https://grillgirl.com/2013/06/bacon-biscuits-with-molasses/', 'Bacon Biscuits with Molasses', 2, NULL, 0, 'Bacon Biscuits with Molasses', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2013/06/DSC_0612-150x150.jpg', '', '', '', '', '', 'approx. 5 biscuits', 'Ingredients?#&?#&1 1/2 cups Bisquick Mix?#&1/2 cup low fat or skim milk?#&4 strips cooked bacon?#&1 teaspoon smoked sea salt?#&1 teaspoon cracked plack pepper?#&1 dash smoked paprika?#&?#&For Serving:?#&?#&Addition 6-8 slices of cooked bacon?#&Molasses- enough for 5 biscuits to be smothered and covered!', '', 'https://grillgirl.com/2013/06/bacon-biscuits-with-molasses/', '', '', 'Jun 25, 2013', '', 'Instructions?#&?#&Heat oven to 450. Stir biscuit ingredients until a soft dough forms. Place on a surface generously sprinkled with more Bisquick so it does not stick and knead 8-10 times. Next, roll the dough out to 1/2″ thick and cut with a biscuit cutter or glass approx. 2 1/2″ diameter ( again, make sure glass has been dipped in the bisquick mix so it will not stick.)  Place on an ungreased cookie sheet.?#&?#&Bake 7-9 minutes until golden. Please note, the size of your biscuits (and what you used to cut them) will determine baking time so the smaller the biscuits, the faster they will cook.?#&?#&To Serve: Slice the biscuits and layer 2-3 pieces of bacon on them. Add molasses for a sweet and salty treat that will satisfy your craving for some Southern Goodness!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'A23A8524D3E7CF4C8FC870596CFB054A', X'F5AA4A7A1C23CC47A678C69917A4263B', X'F5AA4A7A1C23CC47A678C69917A4263B', 0, 'https://grillgirl.com/2020/07/grill-school-grilled-cobbler-video/', 'Grill School: Grilled Cobbler (Video)', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2020/07/grill-school-grilled-cobbler-video/', '', '', 'Jul 3, 2020', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'DD09AEC147F8174BB853CF70CA72E1F8', X'6CE8CD550CF54A4B907F77D6C347975A', X'6CE8CD550CF54A4B907F77D6C347975A', 0, 'https://grillgirl.com/2019/08/cedar-plank-peaches-pellet-smoker-dessert-recipe-grillgirl/', 'Cedar Planked Peaches with Mascarpone, Pistachios and Honey', 2, NULL, 0, 'Cedar Plank Peaches with Mascarpone, Pistachios and Honey', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/08/Cedar-planked-peaches2-150x150.jpeg', '', '', '10 min', '30 min', '40 min', '', 'Ingredients?#&?#&4 peaches, halved and pits removed?#&2 cedar planks (soaked)?#&1/2 cup (approximately 4 oz) mascarpone cheese?#&¼ cup honey?#&½ cup chopped pistachios?#&1 tbsp ground cinnamon', '', 'https://grillgirl.com/2019/08/cedar-plank-peaches-pellet-smoker-dessert-recipe-grillgirl/', 'Smoking, Plank Cooking', 'smoking wood, peach, apple, plank cooking, plank, dessert recipe, smoked dessert', 'Aug 21, 2019', 'Dessert', 'Instructions?#&?#&Soak the cedar planks for 10 minutes before using.?#&?#&Halve the peaches and remove their seeds.?#&?#&Put 4 peach halves on each cedar plank and smoke for 30 minutes.?#&?#&When I make cedar plank smoked peaches I set my pellet smoker to the temperature of 250 degrees (f).?#&?#&Remove the peaches from the smoker and place on separate dessert plates for serving.?#&?#&Add one tbsp mascarpone, a drizzle of honey, 1 tbsp chopped pistachios and a sprinkle of ground cinnamon on top.?#&?#&Prepare for dessert ecstacy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'A93D2910D47FD94393A41C74EFE59C06', X'772E29FFBA4C1F44A8C4866E9E8B90A8', X'772E29FFBA4C1F44A8C4866E9E8B90A8', 0, 'https://grillgirl.com/2018/02/smoked-bourbon-salted-caramel-apple-pie/', 'Smoked Bourbon and Salted Caramel Apple Pie', 2, NULL, 0, 'Smoked Bourbon and Salted Caramel Apple Pie', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/02/bourbon-salted-caramel-apple-pie-150x150.jpg', '4', '1', '', '', '', '', 'Ingredients?#&?#&2 refrigerated pie crusts?#&5-cups sliced apples, equivalent to 6 small-medium apples chopped?#&1/2 cup dark brown sugar?#&1/2 tsp cinnamon?#&1 dash each: nutmeg, allspice, ginger?#&2 tbsp melted butter?#&4 tbsp dulce de Leche?#&2 tbsp bourbon?#&1 tbsp maldon smoked sea salt or regular sea salt', '', 'https://grillgirl.com/2018/02/smoked-bourbon-salted-caramel-apple-pie/', '', '', 'Feb 25, 2018', '', 'Instructions?#&?#&Roughly peel the apples with a vegetable peeler to remove the majority of the peel (doesn’t have to be perfect), then chop them up and put them in a big bowl. Add the bourbon, spices, brown sugar and butter and stir to combine.?#&Next, let your refrigerated pie crusts slightly thaw and soften out of the refrigerator. I prefer to bake in 12″ cast iron skillet for pies on my smoker, but a pie plate would work as well.?#&On a pregreased skillet or pie pan, roll out the pie dough into the skillet trying to get the dough to roll up on the sides as far as possible. Next, add your apple mixture.?#&Here’s the fun part! Put the dulce de leche into a Ziploc sandwich bag and add the maldon sea salt and stir tother in the bag with a fork.?#&Next, run it under hot water in the sink so the salt will combine and the caramel will spread more easily. Snip the end of the bag to create a pastry bag.?#&Now, pipe the dulce de leche onto the top of the apple pie mixture. Now sprinkle just a little bit more of salt onto the caramel (don’t overdo it!)?#&Take the other pie crust and put it on top of the pie and seal the edges. Cut a few slits to let the steam escape.?#&Set your Green Mountain Grill or pellet smoker to 300 degrees, and let your pie bake for approximately 45 minutes OR until the crust has browned and the apple mixture will become bubbly. Watch the edges of the crust, if it is browning too quickly cover the edges with foil. It is normal for some of the pie juice to come up on the side of the crust, I believe this is due to the shape of the cast iron skillet.?#&When serving a slice, drizzle more dulce de leche onto each piece for extra yumminess! If you want icecream, I love this with vanilla gelato');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2688FD4F9EF4DE488CEA113BCE42C24C', X'5157B90277494A4EBFA5104C87501C42', X'5157B90277494A4EBFA5104C87501C42', 0, 'https://grillgirl.com/2017/11/blueberry-peach-cobbler-pitbarrel-cooker/', 'Blueberry Peach Cobbler on the PitBarrel Cooker', 2, NULL, 0, 'Blueberry Peach Cobbler on the PitBarrel Cooker', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/11/blueberry-peach-cobbler-pbc-logo-visible-EDITED-150x100.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&4 peaches, diced?#&1 1/2 cups of blueberries?#&1/4 cup honey?#&1 dash each: nutmeg, cinnamon, allspice?#&1 tbsp lemon zest (lemon really brightens the flavor of the fruit in any dish)?#&jiffy cake mix?#&1 egg?#&1/2 cup water?#&1 tbsp coconut oil or butter to oil cast iron pan', '', 'https://grillgirl.com/2017/11/blueberry-peach-cobbler-pitbarrel-cooker/', '', '', 'Nov 4, 2017', '', 'Instructions?#&?#&Combine fruit, honey, spices and lemon zest in one bowl and let macerate. In the meantime, combine the egg and water with the jiffy cake mix.?#&?#&Coat your 12” cast iron pan with butter or coconut oil so that none of your ingredients stick. Next, add your batter on top. Take a fork and spoon and lightly mix the fruit with the batter so that some of the fruit is visible (don’t mix too much- this is a rustic cobbler).?#&?#&Cook on your PitBarrel that has a hot fire going for about 30 minutes or until the fruit has begun to bubble and the batter has browned on the top. Depending on how hot your PBC is and how much charcoal is left, this may take up to 45 minutes.?#&?#&Serve with vanilla ice cream or fresh whipped cream. Delish and super easy!! If you love cobbler, I have a ton of cobbler recipes including a mango ginger cobbler and my Red, White and Blue cobbler that are always a big hit!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'1C81D0704C40624690F17100141D6036', X'E1009BAF756996448CC55D98105C0314', X'E1009BAF756996448CC55D98105C0314', 0, 'https://grillgirl.com/2017/05/grilled-bananas-foster-myers-rum-sauce/', 'Bananas Foster Grilled with Myer’s Rum Sauce', 2, NULL, 0, 'Grilled Bananas Foster with Myer’s Rum Sauce', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/Grilled-bananas-foster-1-2-150x150.jpg', '', '', '', '', '', 'serves 4', 'Ingredients?#&?#&2 bananas, sliced lengthwise with skin on?#&2 teaspoon cinnamon?#&2 tbsp butter?#&1/4 tsp allspice?#&1/4 tsp nutmeg?#&1/3 cup dark rum (I prefer Meyers Rum)?#&1/2 cup cup brown sugar?#&dash salt?#&Icecream?#&1/4 cup candied pecans or pralines (optional)', '', 'https://grillgirl.com/2017/05/grilled-bananas-foster-myers-rum-sauce/', '', '', 'May 11, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat at 350 degrees, creating direct and indirect zones.?#&In a grill safe saucepan (this can also be done inside), melt the butter, add the brown sugar, allspice, nutmeg, and salt and stir on the indirect heat side.?#&Add the rum and let the sauce reduce down.?#&While the sauce reduces on the grill, grill the bananas, exposed side down, until char marks form, repeat on “skin on” side (keeping the skin on helps keep the bananas in tact).?#&Remove the bananas from the grill and sprinkle with cinnamon.?#&Scoop ice cream into a bowl. Remove the banana from its skin and place on top of ice cream. Drizzle the rum sauce generously over the banana and ice cream mixture. Add pecans or pralines if you so desire!?#&?#&Enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'82AA9BC8F3BA914AB0DE743C6A9E7E77', X'FAD88297D05AF24D98979C5393A7614E', X'FAD88297D05AF24D98979C5393A7614E', 0, 'https://grillgirl.com/2016/11/bourbon-smoked-pecan-pie/', 'Bourbon and Smoked Pecan Pie', 2, NULL, 0, 'Bourbon and Smoked Pecan Pie', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/11/bourbon-and-smoked-pecan-pie-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Pie Crust/pastry dough?#&3/4 stick unsalted butter?#&1 1/4 cups light brown sugar?#&3/4th cup light corn syrup?#&2 tbsp bourbon?#&1 tbsp orange zest?#&3 eggs?#&dash cinnamon?#&1 tsp vanilla?#&1/4 tsp salt?#&2 cups pecan halves', '', 'https://grillgirl.com/2016/11/bourbon-smoked-pecan-pie/', '', '', 'Nov 18, 2016', '', 'Instructions?#&?#&Soak your nuts for 10 minutes and then drain the water.?#&Then go ahead and smoke your nuts. The water helps the smoke adhere to the nuts. I?#&f using the smoking gun, put the nuts in a glass bowl covered with saran wrap and then insert the hose into the bowl.?#&While using the smoking gun (you will need to fill with wood, turn on and light and it will produce smoke), insert the tube where the smoke comes out into the bowl of pecans.?#&Keep it covered so the smoke is trapped. Do this until the wood chips burn out and then repeat a few times.?#&Let the nuts stay in the smoker for about 10 minutes and then remove the saran wrap. Let the nuts dry out before baking with them.?#&Preheat the oven to 350 degrees.?#&Meanwhile, in a medium sized saucepan melt butter on medium low and add the brown sugar and corn syrup and stir to combine.?#&Next, add the orange peel, bourbon, vanilla and cinnamon and remove from the heat.?#&Beat the eggs in a bowl and add to the sugar mixture once it has significantly cooled.?#&Roll the pie crust out into a 12” cast iron skillet slightly crimping the edges. Fill with the pecans. Pour the sugar mixture over the pecans. Bake for 50 minutes or until the mixture is set and the crust has browned.?#&Remove from the oven and let cool. Excellent served with vanilla ice cream or homemade whipped cream.?#&?#&Enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'16AACCE9383A3F4B9CDD0781DDC6322A', X'3CF74BDC24E47A4C8FD0A136EE146A9B', X'3CF74BDC24E47A4C8FD0A136EE146A9B', 0, 'https://grillgirl.com/2016/07/grilled-pineapple-pound-cake/', 'Grilled Pineapple Pound Cake Topped With Honey Mascapone Cheese', 2, NULL, 0, 'Grilled Pineapple Pound Cake Topped With Honey Mascapone Cheese', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/07/Grilled-Pineapple-Cake_Michelle-Lara_Horizontal-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Frozen pound cake?#&1 Fresh Pineapple {sliced thick – either in wedges or in circles}?#&4–5 tbsp of Mascarpone Cheese?#&1 tsp of honey?#&1/4 tsp of vanilla extract?#&a few sprinkles of cinnamon + nutmeg', '', 'https://grillgirl.com/2016/07/grilled-pineapple-pound-cake/', '', '', 'Jul 20, 2016', '', 'Instructions?#&?#&While your pound cake is still frozen cut it in thick slices at least 2 inches thick. Place the cake on the grill in indirect. You’ll need about 3-4 minutes per side just so it can defrost and get some grill marks. On the other half of your grill add your pineapple slices onto direct grill. Grill for about 2 minutes per side – you definitely want some char on them as this helps caramelize the pineapple and adds tons more flavor. In a large bowl spread the mascarpone cheese and drizzle on some honey and add the vanilla extract, cinnamon and nutmeg. The honey will not only add tons of flavor but make the cheese softer and easier to spread. Once everything is grilled top the pound cake with the grilled pineapple and a dollop of the honey mascarpone spread. Drizzle with a little extra honey right and enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'3675F9E7F034984F86F813BB33EF0335', X'78A68A452B46C547B0DD8A41A676358B', X'78A68A452B46C547B0DD8A41A676358B', 0, 'https://grillgirl.com/2016/06/cast-iron-strawberry-shortcake/', 'Cast Iron Strawberry Shortcake', 2, NULL, 0, 'Cast Iron Strawberry Shortcake', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/06/Strawberry-Shortcake-squared-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&4 cups of fresh strawberries {hulled + sliced}?#&2.5 cups of biscuit mix?#&2/3 cup of milk {for biscuit mix}?#&1/2 cup of sugar?#&1/4 tsp cornstarch?#&butter?#&for fresh whipped cream: 1 cup of heavy whipping cream, 1tsp of vanilla extract, sprinkle of sugar', '', 'https://grillgirl.com/2016/06/cast-iron-strawberry-shortcake/', '', '', 'Jun 24, 2016', '', 'Instructions?#&?#&Preheat your grill to 425 degrees. Follow the biscuit mix instructions on the box {or if you are making it yourself} – set aside. In a large bowl add your strawberries a squeeze or two of your lemon and 1 tsp sugar and the cornstarch. Mix together with hands – this will be the syrupy base for your shortcake. Butter your cast iron. Use some of the biscuit batter as the bottom base just a very thin layer is needed. Add your strawberries and then drop like 6 – 8 small mounds of biscuit batter on the top about 1 finger width apart. Bake in indirect heat on the grill for 25-30 minutes or until the top has browned.?#&?#&Fresh whipped cream is always best in my opinion and a breeze to make! Start with very cold heavy whipping cream and I put my mixing bowl in the freezer for a few minutes so it’s cold too. Add your vanilla and sprinkle of sugar and whip on high till peaks are formed. Fold together and put in fridge until you are ready to serve.?#&?#&Serve the Strawberry Shortcake warm and top with fresh whip cream and a strawberry for garnish.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'CFF078091F7299458446D8B081DE5822', X'4381194DC1C9CA4AAD3FA9DA24FD06A9', X'4381194DC1C9CA4AAD3FA9DA24FD06A9', 0, 'https://grillgirl.com/2015/10/grilled-mini-apple-pies-perfect-for-game-day/', 'Grilled Mini Apple Pies Perfect For Game Day', 2, NULL, 0, 'Grilled Mini Apple Pies Perfect For Game Day', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/10/Grilled-Mini-Apple-Pies_Michelle-Lara_CupcakesCocktailsAndKids-150x150.jpg', '', '', '', '', '', '{10 mini pies, or one big pie - when done the internal temperature of these pies should be 175 degrees}', 'Ingredients?#&?#&I used 3″ foil tart pans and was able to make?#&?#&2 boxes of pre-made pie dough?#&4 granny smith – these will be grilled so cut larger than normal?#&4 red delicious apples {thickly sliced}?#&zest of one lemon, juice of 1/2 a lemon?#&1/4 tsp nutmeg?#&1/4 tsp cinnamon?#&1/4 cup of sugar?#&pinch of salt?#&1 tsp butter?#&2 tsp condensed milk?#&2 tsp of brandy {optional}', '', 'https://grillgirl.com/2015/10/grilled-mini-apple-pies-perfect-for-game-day/', '', '', 'Oct 29, 2015', '', 'Instructions?#&?#&Prepare your grill for indirect grilling. In a large saucepan melt down your butter and add all the ingredients {minus the granny smith apples, but add the other apples}. Bring to a boil and then slowly simmer until the sauce thickens into a syrupy consistency.?#&?#&Brush your grilling apples with some of the butter mixture and grill them for a few minutes per side {just enough to get grill marks}. It’s important that you thickly cut your apples so they don’t break on the grill. Remove the grilling apples from the grill and slice them smaller, since I was working with a small tart pan I cut all the apples {the Grilled Granny Smith + Red Delicious into bite sized pieces}.  Add the grilled apples to your butter/apple mixture and set aside.?#&?#&Roll out your pie dough and cut into small circles {or use the entire circle if you are making a large pie}. Pour the apple filling into each and make sure you get a good amount of the apple/butter sauce as well. I played around with the pie tops and did a lattice design on a few and a regular pie crust on the top. If doing a full pie crust just remember to cut a few slits on the time to vent and sprinkle a little sugar on the top.?#&?#&Grill the mini pies on indirect heat for about 15 minutes and make sure to quarter turn them halfway in between. Keep a close eye these grill up quickly especially if you are making mini pies like I did. A whole pie would need about 35 – 45 minutes.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'AF045933D5522849BD5055BF2FE80094', X'1AF8CF0BEB9801428D9105D5C7D09B57', X'1AF8CF0BEB9801428D9105D5C7D09B57', 0, 'https://grillgirl.com/2015/06/mango-ginger-cobbler/', 'Mango Ginger Cobbler', 2, NULL, 0, 'Mango Ginger Cobbler', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/06/IMG_3689-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&3 cups mango?#&1 tblespoon fresh grated ginger?#&2 tbsp honey (if your mango is very very ripe, you can skip the honey)?#&tbsp. butter?#&1 teaspoon allspice?#&1 box of Jiffy Golden cake mix (I love baking with Jiffy and this mix is only .99 per box!)?#&1 egg (for the cake mix)?#&½ cup water (for the water)?#&1 tsp vanilla?#&1 tbsp coconut oil', '', 'https://grillgirl.com/2015/06/mango-ginger-cobbler/', '', '', 'Jun 7, 2015', '', 'Instructions?#&?#&Heat your grill to 350 degrees creating a direct and indirect zone. For baking the cobbler it will go on the indirect side. Using an oven, preheat your oven to 350 degrees.?#&?#&Combine the cake mix with the water, egg and vanilla in one bowl and set aside. In another bowl, mix the mangos, honey, ginger, butter and allspice.?#&?#&Oil your skillet with the coconut oil. Pour the mango mixture in. Next, add the cake mix. Mix together very roughly with a fork so the mangos and cobbler combine- just a quick stir.?#&?#&Bake for 25-30 minutes or until the top has browned and the cobbler is bubbling.?#&?#&This goes great with coconut gelato!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'BCF4F599A4396B458DAE8896FB8BA6C4', X'4162AB3F0B1A054CBA3E6632B768C58C', X'4162AB3F0B1A054CBA3E6632B768C58C', 0, 'https://grillgirl.com/2020/07/grill-school-grilled-cobbler-video/', 'Grill School: Grilled Cobbler (Video)', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2020/07/grill-school-grilled-cobbler-video/', '', '', 'Jul 3, 2020', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B57BB482A43FFE40A4690C5703400906', X'35B4F238848C324DB4E371F6806A3B70', X'35B4F238848C324DB4E371F6806A3B70', 0, 'https://grillgirl.com/2019/08/cedar-plank-peaches-pellet-smoker-dessert-recipe-grillgirl/', 'Cedar Planked Peaches with Mascarpone, Pistachios and Honey', 2, NULL, 0, 'Cedar Plank Peaches with Mascarpone, Pistachios and Honey', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/08/Cedar-planked-peaches2-150x150.jpeg', '', '', '10 min', '30 min', '40 min', '', 'Ingredients?#&?#&4 peaches, halved and pits removed?#&2 cedar planks (soaked)?#&1/2 cup (approximately 4 oz) mascarpone cheese?#&¼ cup honey?#&½ cup chopped pistachios?#&1 tbsp ground cinnamon', '', 'https://grillgirl.com/2019/08/cedar-plank-peaches-pellet-smoker-dessert-recipe-grillgirl/', 'Smoking, Plank Cooking', 'smoking wood, peach, apple, plank cooking, plank, dessert recipe, smoked dessert', 'Aug 21, 2019', 'Dessert', 'Instructions?#&?#&Soak the cedar planks for 10 minutes before using.?#&?#&Halve the peaches and remove their seeds.?#&?#&Put 4 peach halves on each cedar plank and smoke for 30 minutes.?#&?#&When I make cedar plank smoked peaches I set my pellet smoker to the temperature of 250 degrees (f).?#&?#&Remove the peaches from the smoker and place on separate dessert plates for serving.?#&?#&Add one tbsp mascarpone, a drizzle of honey, 1 tbsp chopped pistachios and a sprinkle of ground cinnamon on top.?#&?#&Prepare for dessert ecstacy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'A3A0AF8693EFDA47A9F368B639A8F785', X'2C5D1AA8B5C3674BAE17543005428F83', X'2C5D1AA8B5C3674BAE17543005428F83', 0, 'https://grillgirl.com/2018/02/smoked-bourbon-salted-caramel-apple-pie/', 'Smoked Bourbon and Salted Caramel Apple Pie', 2, NULL, 0, 'Smoked Bourbon and Salted Caramel Apple Pie', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/02/bourbon-salted-caramel-apple-pie-150x150.jpg', '4', '1', '', '', '', '', 'Ingredients?#&?#&2 refrigerated pie crusts?#&5-cups sliced apples, equivalent to 6 small-medium apples chopped?#&1/2 cup dark brown sugar?#&1/2 tsp cinnamon?#&1 dash each: nutmeg, allspice, ginger?#&2 tbsp melted butter?#&4 tbsp dulce de Leche?#&2 tbsp bourbon?#&1 tbsp maldon smoked sea salt or regular sea salt', '', 'https://grillgirl.com/2018/02/smoked-bourbon-salted-caramel-apple-pie/', '', '', 'Feb 25, 2018', '', 'Instructions?#&?#&Roughly peel the apples with a vegetable peeler to remove the majority of the peel (doesn’t have to be perfect), then chop them up and put them in a big bowl. Add the bourbon, spices, brown sugar and butter and stir to combine.?#&Next, let your refrigerated pie crusts slightly thaw and soften out of the refrigerator. I prefer to bake in 12″ cast iron skillet for pies on my smoker, but a pie plate would work as well.?#&On a pregreased skillet or pie pan, roll out the pie dough into the skillet trying to get the dough to roll up on the sides as far as possible. Next, add your apple mixture.?#&Here’s the fun part! Put the dulce de leche into a Ziploc sandwich bag and add the maldon sea salt and stir tother in the bag with a fork.?#&Next, run it under hot water in the sink so the salt will combine and the caramel will spread more easily. Snip the end of the bag to create a pastry bag.?#&Now, pipe the dulce de leche onto the top of the apple pie mixture. Now sprinkle just a little bit more of salt onto the caramel (don’t overdo it!)?#&Take the other pie crust and put it on top of the pie and seal the edges. Cut a few slits to let the steam escape.?#&Set your Green Mountain Grill or pellet smoker to 300 degrees, and let your pie bake for approximately 45 minutes OR until the crust has browned and the apple mixture will become bubbly. Watch the edges of the crust, if it is browning too quickly cover the edges with foil. It is normal for some of the pie juice to come up on the side of the crust, I believe this is due to the shape of the cast iron skillet.?#&When serving a slice, drizzle more dulce de leche onto each piece for extra yumminess! If you want icecream, I love this with vanilla gelato');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'D559CFFA6A3E3E4B9913206578F7DE47', X'0CE00DB99AD0074DB89020A5A94B06B3', X'0CE00DB99AD0074DB89020A5A94B06B3', 0, 'https://grillgirl.com/2017/11/blueberry-peach-cobbler-pitbarrel-cooker/', 'Blueberry Peach Cobbler on the PitBarrel Cooker', 2, NULL, 0, 'Blueberry Peach Cobbler on the PitBarrel Cooker', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/11/blueberry-peach-cobbler-pbc-logo-visible-EDITED-150x100.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&4 peaches, diced?#&1 1/2 cups of blueberries?#&1/4 cup honey?#&1 dash each: nutmeg, cinnamon, allspice?#&1 tbsp lemon zest (lemon really brightens the flavor of the fruit in any dish)?#&jiffy cake mix?#&1 egg?#&1/2 cup water?#&1 tbsp coconut oil or butter to oil cast iron pan', '', 'https://grillgirl.com/2017/11/blueberry-peach-cobbler-pitbarrel-cooker/', '', '', 'Nov 4, 2017', '', 'Instructions?#&?#&Combine fruit, honey, spices and lemon zest in one bowl and let macerate. In the meantime, combine the egg and water with the jiffy cake mix.?#&?#&Coat your 12” cast iron pan with butter or coconut oil so that none of your ingredients stick. Next, add your batter on top. Take a fork and spoon and lightly mix the fruit with the batter so that some of the fruit is visible (don’t mix too much- this is a rustic cobbler).?#&?#&Cook on your PitBarrel that has a hot fire going for about 30 minutes or until the fruit has begun to bubble and the batter has browned on the top. Depending on how hot your PBC is and how much charcoal is left, this may take up to 45 minutes.?#&?#&Serve with vanilla ice cream or fresh whipped cream. Delish and super easy!! If you love cobbler, I have a ton of cobbler recipes including a mango ginger cobbler and my Red, White and Blue cobbler that are always a big hit!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'3E4FF6E18456B94C832CEDB0025F0CAD', X'097360DEFBED2B49A06EEEB0E8D6BBD1', X'097360DEFBED2B49A06EEEB0E8D6BBD1', 0, 'https://grillgirl.com/2017/05/grilled-bananas-foster-myers-rum-sauce/', 'Bananas Foster Grilled with Myer’s Rum Sauce', 2, NULL, 0, 'Grilled Bananas Foster with Myer’s Rum Sauce', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/Grilled-bananas-foster-1-2-150x150.jpg', '', '', '', '', '', 'serves 4', 'Ingredients?#&?#&2 bananas, sliced lengthwise with skin on?#&2 teaspoon cinnamon?#&2 tbsp butter?#&1/4 tsp allspice?#&1/4 tsp nutmeg?#&1/3 cup dark rum (I prefer Meyers Rum)?#&1/2 cup cup brown sugar?#&dash salt?#&Icecream?#&1/4 cup candied pecans or pralines (optional)', '', 'https://grillgirl.com/2017/05/grilled-bananas-foster-myers-rum-sauce/', '', '', 'May 11, 2017', '', 'Instructions?#&?#&Prepare a grill for medium heat at 350 degrees, creating direct and indirect zones.?#&In a grill safe saucepan (this can also be done inside), melt the butter, add the brown sugar, allspice, nutmeg, and salt and stir on the indirect heat side.?#&Add the rum and let the sauce reduce down.?#&While the sauce reduces on the grill, grill the bananas, exposed side down, until char marks form, repeat on “skin on” side (keeping the skin on helps keep the bananas in tact).?#&Remove the bananas from the grill and sprinkle with cinnamon.?#&Scoop ice cream into a bowl. Remove the banana from its skin and place on top of ice cream. Drizzle the rum sauce generously over the banana and ice cream mixture. Add pecans or pralines if you so desire!?#&?#&Enjoy!');
