INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'9338A1A026C5704B8775872DAF01A04D', X'F8D98FE913F3614B8A8E1A960AA8276D', X'F8D98FE913F3614B8A8E1A960AA8276D', 0, 'https://grillgirl.com/2018/11/garlic-cauli-potato-mash-recipe/', 'Garlic Cauli-Potato Mash Recipe', 2, NULL, 0, 'Garlic Cauli-Potato Mash', 'Amber Hanson', 'https://grillgirl.com/wp-content/uploads/2018/11/Garlic-Cauli-Potato-Mash-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 sm. (about 8-ounces) russet potato, peeled and large diced?#&1 lg. head cauliflower, cut into florets (about 6 cups)?#&1 clove garlic, peeled and smashed?#&½ cup unsweetened nut milk, warm?#&¼ cup ghee, melted?#&1 tsp. sea salt', '', 'https://grillgirl.com/2018/11/garlic-cauli-potato-mash-recipe/', '', '', 'Nov 20, 2018', '', 'Instructions?#&?#&Place potatoes, cauliflower, and garlic in a 4-quart saucepan with cold salted water. Bring to a boil.?#&Reduce heat slightly and cook 8-10 minutes until vegetables are tender. Drain and return to the pan or place in a mixing bowl.?#&Add milk, ghee, and salt. Use a potato masher or hand mixer to blend mixture until creamy.');
