INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'20E0A78B6BAE3641914B959A3D6D2FF7', X'223537D4B46D26498D0F515BA0B006E7', X'223537D4B46D26498D0F515BA0B006E7', 0, 'https://grillgirl.com/2015/09/grilled-prosciutto-wrapped-blue-cheese-stuffed-figs/', 'Grilled Prosciutto Wrapped, Blue Cheese Stuffed Figs', 2, NULL, 0, 'Grilled Prosciutto Wrapped, Blue Cheese Stuffed Figs', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/09/proscuitto-wrapped-figs-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 pint fresh figs (if you cant find fresh you can reconstitute dried figs but they may not hold up as well when grilling)?#&1 block blue cheese?#&4–5 oz prosciutto?#&2 tblsp balsamic glaze?#&toothpicks', '', 'https://grillgirl.com/2015/09/grilled-prosciutto-wrapped-blue-cheese-stuffed-figs/', '', '', 'Sep 30, 2015', '', 'Instructions?#&?#&Halve the figs. Next, stuff blue cheese into the cavity of each halved fig. Wrap a piece of prosciutto around the fig to contain the cheese and secure with a toothpick.?#&?#&Grill the figs on medium direct heat, about 350 degrees, until the cheese begins to melt, about 5 minutes. Finish with a drizzle of balsamic glaze. These will make the happiest of happy hours! See the great wines we tried with these yummy pockets of tasty goodness!!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'BDDE7C6B4C348A4DB282BB5979E21E72', X'938C282561FC3F47AF9BB478ADA2A72F', X'938C282561FC3F47AF9BB478ADA2A72F', 0, 'https://grillgirl.com/2015/09/grill-for-the-win-part-2-fajitas-tex-mex-skillet-beans-and-game-day-grilling-tips-kingsfordtailgate/', 'Grill For The Win, Part 2: Fajitas, Tex Mex Skillet Beans and Game Day Grilling Tips #KingsfordTailgate', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2015/09/grill-for-the-win-part-2-fajitas-tex-mex-skillet-beans-and-game-day-grilling-tips-kingsfordtailgate/', '', '', 'Sep 23, 2015', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'593FF3CA9AD8D6478849688ED034BFC7', X'37FFEEA998E137418C470C73950365E3', X'37FFEEA998E137418C470C73950365E3', 0, 'https://grillgirl.com/2015/09/chile-lemongrass-basil-wings/', 'Chile Lemongrass Basil Wings', 2, NULL, 0, 'Chile Lemongrass Basil Wings', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/Sri-lankan-chicken-wings-up-close-EDITED-150x150.jpg', '', '', '15 min', '30 min', '45 minutes', '4', 'Ingredients?#&?#&2 lbs chicken drummettes?#&2 cans coconut milk?#&2 large chile peppers?#&1 large bunch lemongrass stalks (bottom part)?#&1 large bunch of lemon basil (regular basil can be substituted)?#&sea salt, fresh ground pepper to taste.', '', 'https://grillgirl.com/2015/09/chile-lemongrass-basil-wings/', 'Grilled', 'chili peppers, lemongrass wings, lemon basil, grilled chicken wings recipe, coconut milk marinade', 'Sep 16, 2015', 'Chicken Wings', 'Instructions?#&?#&Chop up the lemongrass stalk (outer stalk removed so you are cutting the fragrant part of the stalk) before putting it into the food processor.?#&Next, roughly chop the chilis and remove the stem. Remove the stem from the basil.?#&Pour the 2 cans of coconut milk into the food processor. Add the lemongrass, basil, and chile peppers.?#&Pulse the ingredients until everything is minced together well. You will want the lemongrass to be totally shredded at this point.?#&Reserve 1.5 cups of the marinade and set aside. Marinate the chicken in the remaining marinade as long as possible until the time you plan to grill them. (I like to put these in the marinade in the morning and let them marinate until dinner time).?#&Prepare a grill for medium direct heat: about 350 degrees.?#&Grill the wings with direct heat until the internal temp is 170 degrees as read by an internal-read instant thermometer.?#&Let rest for 5 minutes or so under foil.?#&Drizzle the wings with reserved marinade before serving and season with salt and pepper.');
