INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'38CC5CD037443244A135F1C4010FD6A8', X'404541928B355647B9B2299105F16918', X'404541928B355647B9B2299105F16918', 0, 'https://grillgirl.com/2020/01/citrus-and-herb-smoked-whole-snapper/', 'Citrus and Herb Smoked Whole Snapper', 2, NULL, 0, 'Citrus and Herb Smoked Whole Snapper', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2020/01/smoked-whole-snapper-1280x640-1-150x150.jpg', '', '', '10 minutes', '30 minutes', '40 minutes', '2', 'Ingredients?#&?#&1 Whole Snapper, inside gizzards removed and de-scaled (have your butcher do this for you)?#&1 lemon, thinly sliced?#&1 lime, thinly sliced?#&2 garlic cloves, minced?#&1 small bunch thyme?#&½ tbsp olive oil?#&2 tsp sea salt?#&2 tsp fresh ground pepper', 'American, Seafood', 'https://grillgirl.com/2020/01/citrus-and-herb-smoked-whole-snapper/', 'Smoked, Grilled', 'smoked snapper, how to smoke snapper, how to smoke fish', 'Jan 21, 2020', 'American, Seafood, Fish', 'Instructions?#&?#&1) Drizzle the insides of the snapper first with olive oil, then add the salt, pepper and garlic and spread in the inside of the cavity evenly.?#&?#&2) layer lemon and lime slices in the cavity starting from front to back and then add the thyme sprigs in the cavity. Save any remaining lemon and lime slices for serving with the fish once it’s cooked.?#&?#&3) Place the snapper on a frog mat and smoke until the internal temperature reaches 145 degrees- this will take anywhere from 45 minutes to an hour depending on the size of the fish.?#&?#&4) Once the internal temp has been met, remove and serve immediately with remaining lemon and lime slices. The outer filets will remove pretty cleanly for serving but be extra vigilant about looking for bones before serving guests.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2D75988BE298464983943979C4A48064', X'F383B04AB62FE8408C45B30F29DF0CAE', X'F383B04AB62FE8408C45B30F29DF0CAE', 0, 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', 'Rum Pineapple Pork Ribs are Sweet and Tangy', 2, NULL, 0, 'Rum Pineapple Pork Ribs are Sweet and Tangy', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/08/rum-pineapple-ribs-150x150.jpeg', '', '', '20 minutes', '5 hours', '5 hours 20 minutes', '', 'Ingredients?#&?#&1 slab baby back ribs?#&Mustard?#&3 tbsp smoked all purpose rub ?#&1 pineapple, cored and sliced into 1/2” rings – should make about 9 rings total – 3 of which are used in the sauce, save juice for sauce (if using canned look for a corn syrup free, reduced sugar version)?#&?#&Texas Crutch?#&?#&1/4 cup apple juice?#&2 tbsp pineapple preserves?#&?#&Pineapple Rum BBQ Sauce?#&?#&1/3 cup BBQ sauce or your favorite store bought variety?#&3 tbsp pineapple preserves?#&1/4 cup Myers rum?#&3 tbsp pineapple juice?#&Dash allspice?#&3 slice pineapple, chopped', '', 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', '2-2-1 Method', 'pork ribs, baby back ribs, 2-2-1 ribs, smoked BBQ ribs, texas crutch, pork ribs recipe', 'Aug 29, 2019', 'Pork Ribs', 'Instructions?#&?#&Smoke Temp: 225F?#&?#&Smoking Woods: FRUIT WOODS- (apple, pecan, pear) OR mix with OAK, PECAN, MAPLE?#&?#&The ribs are going to use the 2-2-1 method: 2 hours in the smoker, 2 hours in foil, 1 hour letting the sauce glaze.?#&?#&Start by removing the membrane from the ribs. This is easier with a paper towel for gripping and a butter knife for separating from the meat.?#&Prepare your smoker for 225 degrees- this recipe can be made on any kind of smoker (and was originally created for my Healthy Electric Smoker Cookbook).?#&Next, slather with mustard so the rub can adhere. Apply the rub and smoke the meat for 2 hours.?#&After 2 hours, remove from the smoker and do the “Texas Crutch,” i.e., “wrap in foil”. Mix the apple juice and pineapple preserve and add to the foil packet. Let smoke another two hours.?#&Meanwhile, as the meat is smoking make your rum barbecue sauce. Combine the BBQ sauce, pineapple preserves, rum, chopped pineapple, pineapple juice and allspice. Let reduce down for 20 minutes on medium simmer.?#&During the last hour, remove the ribs from foil and glaze with the pineapple rum BBQ sauce.?#&Layer slices of pineapple on top of the ribs and drizzle more sauce over the pineapple.?#&Let smoke for an additional hour to let the sauce set. Serve immediately.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'9E2128422EF35D4E89E84830F3A5BA50', X'0864FA3037A51A4388013A12B4519B4B', X'0864FA3037A51A4388013A12B4519B4B', 0, 'https://grillgirl.com/2017/11/diy-oyster-shell-salt-cellars/', 'DIY Oyster Shell Salt Cellars', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2017/11/diy-oyster-shell-salt-cellars/', '', '', 'Nov 18, 2017', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'46BFB333B1F57D42B51A6535EC5099F3', X'8937B93AD4599E47BF4D3D00BDACBB4D', X'8937B93AD4599E47BF4D3D00BDACBB4D', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2223A510F53CEC4AAAEDBD1ACD0B02E2', X'555624F3626832489B26D4137E5C1328', X'555624F3626832489B26D4137E5C1328', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'3857DB253F8D7A44967666D057C11D4D', X'D4B93C378AFF3C47BB8BB03A006B4843', X'D4B93C378AFF3C47BB8BB03A006B4843', 0, 'https://grillgirl.com/2016/08/coconut-mojito/', 'Coconut Mojito', 2, NULL, 0, 'Coconut Mojito', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/08/Coconut-Mojito_Michelle-Lara_A-150x150.jpg', '', '', '', '', '', 'makes 2 cocktails', 'Ingredients?#&?#&3 oz white rum?#&12–14 mint leaves?#&1 tsp of sugar?#&2 oz of fresh lime juice?#&2 tsp of coconut cream {I used this brand}?#&club soda', '', 'https://grillgirl.com/2016/08/coconut-mojito/', '', '', 'Aug 18, 2016', '', 'Instructions?#&?#&Muddle the mint leaves, sugar and lime juice in a cocktail glass. Add ice and pour the rum, and coconut cream – give it a good shake. Pour over ice and add a splash of club soda and mint garnish – enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'CD903BA1D6E91E4DAE2BD7FC812D6DA3', X'D8B3088A80B2344B95986BD26C5E697B', X'D8B3088A80B2344B95986BD26C5E697B', 0, 'https://grillgirl.com/2016/08/grilled-ginger-pineapple-chicken/', 'Pineapple Ginger Grilled Chicken', 2, NULL, 0, 'Pineapple Ginger Grilled Chicken', 'Michelle Lara', 'https://grillgirl.com/wp-content/uploads/2016/08/Grilled-Chicken_resized-150x150.jpg', '', '', '10 minutes + marinate time', '30 minutes', '40 minutes', '4', 'Ingredients?#&?#&8 – 10 chicken thighs {with skin}?#&2 cloves of garlic minced/chopped?#&3 tsp of grated/minced ginger?#&1 cup of pineapple juice?#&1.5 cups of soy sauce {i use a low sodium one}?#&1/2 cup of chopped cilantro?#&1/2 cup of chopped mint?#&1/2 cup of chopped basil', 'American, Outdoor Cooking, Backyard BBQ', 'https://grillgirl.com/2016/08/grilled-ginger-pineapple-chicken/', 'Grilled', 'Pineapple Ginger Grilled chicken', 'Aug 18, 2016', 'American, Grilled Chicken', 'Instructions?#&?#&In a large gallon size bag add all the ingredients. Give it a good shake so the chicken thighs are completely coated. Store in the fridge to marinate for at least an hour {overnight is best}. Preheat your grill {we use a charcoal} to 350 degrees. Line the thighs up on indirect heat skin facing up. I give them a good 10-15 minutes to grill and then I cover them with the vents open. Leave them for about another 25 minutes and then turn and cover. Once you have an internal temperature of at least 165 bring them to the direct heat so they can get a nice char not them. Let them rest for at least 10 minutes and enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'EC05CBA7C7EED74BBCC259CB88D9FF06', X'0FB4B36150740847B15D02CAA7D37489', X'0FB4B36150740847B15D02CAA7D37489', 0, 'https://grillgirl.com/2016/07/caribbean-mango-slaw/', 'Caribbean Mango Slaw', 2, NULL, 0, 'Caribbean Mango Slaw', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/07/Mango-Slaw_Michelle-Lara_Horizontal-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1/2 a red cabbage?#&2–3 ripe mangoes {diced or thinly sliced}?#&1 cup of chopped cilantro?#&juice of 1 lime?#&juice of 1 grapefruit?#&juice + zest of 1 navel or blood orange?#&1 jalapeno sliced {add more seeds if you want some extra heat}?#&1/4 cup of white wine vinegar?#&salt + pepper to taste?#&a few dashes of garlic powder', '', 'https://grillgirl.com/2016/07/caribbean-mango-slaw/', '', '', 'Jul 19, 2016', '', 'Instructions?#&?#&Wash and then grate the red cabbage, place in a large bowl. Add the rest of the ingredients and make sure to taste make salt + pepper adjustments as needed. Refrigerate for at least 30 minutes so the flavors can develop. This slaw is great on tacos or as a side {or even on it’s own as a quick snack!}');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'8284408C1606C94D8B2927DCF6AA6D4D', X'59983FBFB0093E4DB75EF73D38BC6777', X'59983FBFB0093E4DB75EF73D38BC6777', 0, 'https://grillgirl.com/2015/09/chile-lemongrass-basil-wings/', 'Chile Lemongrass Basil Wings', 2, NULL, 0, 'Chile Lemongrass Basil Wings', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/12/Sri-lankan-chicken-wings-up-close-EDITED-150x150.jpg', '', '', '15 min', '30 min', '45 minutes', '4', 'Ingredients?#&?#&2 lbs chicken drummettes?#&2 cans coconut milk?#&2 large chile peppers?#&1 large bunch lemongrass stalks (bottom part)?#&1 large bunch of lemon basil (regular basil can be substituted)?#&sea salt, fresh ground pepper to taste.', '', 'https://grillgirl.com/2015/09/chile-lemongrass-basil-wings/', 'Grilled', 'chili peppers, lemongrass wings, lemon basil, grilled chicken wings recipe, coconut milk marinade', 'Sep 16, 2015', 'Chicken Wings', 'Instructions?#&?#&Chop up the lemongrass stalk (outer stalk removed so you are cutting the fragrant part of the stalk) before putting it into the food processor.?#&Next, roughly chop the chilis and remove the stem. Remove the stem from the basil.?#&Pour the 2 cans of coconut milk into the food processor. Add the lemongrass, basil, and chile peppers.?#&Pulse the ingredients until everything is minced together well. You will want the lemongrass to be totally shredded at this point.?#&Reserve 1.5 cups of the marinade and set aside. Marinate the chicken in the remaining marinade as long as possible until the time you plan to grill them. (I like to put these in the marinade in the morning and let them marinate until dinner time).?#&Prepare a grill for medium direct heat: about 350 degrees.?#&Grill the wings with direct heat until the internal temp is 170 degrees as read by an internal-read instant thermometer.?#&Let rest for 5 minutes or so under foil.?#&Drizzle the wings with reserved marinade before serving and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'BE8462486984C5469026EEB9A3A14D02', X'E7962D8B1F81A0499052EA8B11558D83', X'E7962D8B1F81A0499052EA8B11558D83', 0, 'https://grillgirl.com/2015/07/grilled-watermelon-cucumber-cosmo/', 'GRILLED WATERMELON + CUCUMBER COSMO', 2, NULL, 0, 'GRILLED WATERMELON + CUCUMBER COSMO', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/07/Watermelon_Cucumber_Cosmo_MichelleLara_GrillGirl-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Here’s what you’ll need to make 1 cocktail:?#&?#&1.5 oz Hendricks gin?#&.5 oz lime juice?#&1 oz white cranberry juice?#&1–2 mint leaves?#&3 cucumber slices?#&2–3 grilled watermelon wedges?#&.5 oz Triple Sec?#&ice', '', 'https://grillgirl.com/2015/07/grilled-watermelon-cucumber-cosmo/', '', '', 'Jul 29, 2015', '', 'Instructions?#&?#&In a blender mix the lime juice, 2 cucumber slices and cubed grilled watermelon slices.?#&In a cocktail shaker add the ingredients along with gin, triple sec, cranberry juice and mint leaves.?#&Shake for about a minute and strain into a martini glass.?#&Garnish with the leftover cucumber slice.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'BE35E7AE2522DF4EA319315920598B03', X'59FA1253D346F74AAFB1974136DCBB8A', X'59FA1253D346F74AAFB1974136DCBB8A', 0, 'https://grillgirl.com/2020/01/citrus-and-herb-smoked-whole-snapper/', 'Citrus and Herb Smoked Whole Snapper', 2, NULL, 0, 'Citrus and Herb Smoked Whole Snapper', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2020/01/smoked-whole-snapper-1280x640-1-150x150.jpg', '', '', '10 minutes', '30 minutes', '40 minutes', '2', 'Ingredients?#&?#&1 Whole Snapper, inside gizzards removed and de-scaled (have your butcher do this for you)?#&1 lemon, thinly sliced?#&1 lime, thinly sliced?#&2 garlic cloves, minced?#&1 small bunch thyme?#&½ tbsp olive oil?#&2 tsp sea salt?#&2 tsp fresh ground pepper', 'American, Seafood', 'https://grillgirl.com/2020/01/citrus-and-herb-smoked-whole-snapper/', 'Smoked, Grilled', 'smoked snapper, how to smoke snapper, how to smoke fish', 'Jan 21, 2020', 'American, Seafood, Fish', 'Instructions?#&?#&1) Drizzle the insides of the snapper first with olive oil, then add the salt, pepper and garlic and spread in the inside of the cavity evenly.?#&?#&2) layer lemon and lime slices in the cavity starting from front to back and then add the thyme sprigs in the cavity. Save any remaining lemon and lime slices for serving with the fish once it’s cooked.?#&?#&3) Place the snapper on a frog mat and smoke until the internal temperature reaches 145 degrees- this will take anywhere from 45 minutes to an hour depending on the size of the fish.?#&?#&4) Once the internal temp has been met, remove and serve immediately with remaining lemon and lime slices. The outer filets will remove pretty cleanly for serving but be extra vigilant about looking for bones before serving guests.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'7668ED7258B1FB40A9D8B0CBFD76BF06', X'0811893BEAC5704A98D65A541D49C3B5', X'0811893BEAC5704A98D65A541D49C3B5', 0, 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', 'Rum Pineapple Pork Ribs are Sweet and Tangy', 2, NULL, 0, 'Rum Pineapple Pork Ribs are Sweet and Tangy', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/08/rum-pineapple-ribs-150x150.jpeg', '', '', '20 minutes', '5 hours', '5 hours 20 minutes', '', 'Ingredients?#&?#&1 slab baby back ribs?#&Mustard?#&3 tbsp smoked all purpose rub ?#&1 pineapple, cored and sliced into 1/2” rings – should make about 9 rings total – 3 of which are used in the sauce, save juice for sauce (if using canned look for a corn syrup free, reduced sugar version)?#&?#&Texas Crutch?#&?#&1/4 cup apple juice?#&2 tbsp pineapple preserves?#&?#&Pineapple Rum BBQ Sauce?#&?#&1/3 cup BBQ sauce or your favorite store bought variety?#&3 tbsp pineapple preserves?#&1/4 cup Myers rum?#&3 tbsp pineapple juice?#&Dash allspice?#&3 slice pineapple, chopped', '', 'https://grillgirl.com/2019/08/rum-pineapple-pork-ribs-recipe-sweet-and-tangy/', '2-2-1 Method', 'pork ribs, baby back ribs, 2-2-1 ribs, smoked BBQ ribs, texas crutch, pork ribs recipe', 'Aug 29, 2019', 'Pork Ribs', 'Instructions?#&?#&Smoke Temp: 225F?#&?#&Smoking Woods: FRUIT WOODS- (apple, pecan, pear) OR mix with OAK, PECAN, MAPLE?#&?#&The ribs are going to use the 2-2-1 method: 2 hours in the smoker, 2 hours in foil, 1 hour letting the sauce glaze.?#&?#&Start by removing the membrane from the ribs. This is easier with a paper towel for gripping and a butter knife for separating from the meat.?#&Prepare your smoker for 225 degrees- this recipe can be made on any kind of smoker (and was originally created for my Healthy Electric Smoker Cookbook).?#&Next, slather with mustard so the rub can adhere. Apply the rub and smoke the meat for 2 hours.?#&After 2 hours, remove from the smoker and do the “Texas Crutch,” i.e., “wrap in foil”. Mix the apple juice and pineapple preserve and add to the foil packet. Let smoke another two hours.?#&Meanwhile, as the meat is smoking make your rum barbecue sauce. Combine the BBQ sauce, pineapple preserves, rum, chopped pineapple, pineapple juice and allspice. Let reduce down for 20 minutes on medium simmer.?#&During the last hour, remove the ribs from foil and glaze with the pineapple rum BBQ sauce.?#&Layer slices of pineapple on top of the ribs and drizzle more sauce over the pineapple.?#&Let smoke for an additional hour to let the sauce set. Serve immediately.');
