INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B5FA009AF156934DB4442430441F8053', X'EB0FB085F7208F41AC3D0A92FFF3F87A', X'EB0FB085F7208F41AC3D0A92FFF3F87A', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'74099E958B319242AF36DEA8708529F9', X'4CCE15DEEBC48B409C2F0A65100238A6', X'4CCE15DEEBC48B409C2F0A65100238A6', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'D2A71D3217A70D4387665B72CEA7C84C', X'4B62B1D1A1DC16499BE5813C3833F0DC', X'4B62B1D1A1DC16499BE5813C3833F0DC', 0, 'https://grillgirl.com/2016/08/coconut-mojito/', 'Coconut Mojito', 2, NULL, 0, 'Coconut Mojito', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/08/Coconut-Mojito_Michelle-Lara_A-150x150.jpg', '', '', '', '', '', 'makes 2 cocktails', 'Ingredients?#&?#&3 oz white rum?#&12–14 mint leaves?#&1 tsp of sugar?#&2 oz of fresh lime juice?#&2 tsp of coconut cream {I used this brand}?#&club soda', '', 'https://grillgirl.com/2016/08/coconut-mojito/', '', '', 'Aug 18, 2016', '', 'Instructions?#&?#&Muddle the mint leaves, sugar and lime juice in a cocktail glass. Add ice and pour the rum, and coconut cream – give it a good shake. Pour over ice and add a splash of club soda and mint garnish – enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'E4AB2BD4BBBDF04EBF9314AC4BC9D003', X'834290A9A7873F4085BDAB91A983EA46', X'834290A9A7873F4085BDAB91A983EA46', 0, 'https://grillgirl.com/2016/08/grilled-ginger-pineapple-chicken/', 'Pineapple Ginger Grilled Chicken', 2, NULL, 0, 'Pineapple Ginger Grilled Chicken', 'Michelle Lara', 'https://grillgirl.com/wp-content/uploads/2016/08/Grilled-Chicken_resized-150x150.jpg', '', '', '10 minutes + marinate time', '30 minutes', '40 minutes', '4', 'Ingredients?#&?#&8 – 10 chicken thighs {with skin}?#&2 cloves of garlic minced/chopped?#&3 tsp of grated/minced ginger?#&1 cup of pineapple juice?#&1.5 cups of soy sauce {i use a low sodium one}?#&1/2 cup of chopped cilantro?#&1/2 cup of chopped mint?#&1/2 cup of chopped basil', 'American, Outdoor Cooking, Backyard BBQ', 'https://grillgirl.com/2016/08/grilled-ginger-pineapple-chicken/', 'Grilled', 'Pineapple Ginger Grilled chicken', 'Aug 18, 2016', 'American, Grilled Chicken', 'Instructions?#&?#&In a large gallon size bag add all the ingredients. Give it a good shake so the chicken thighs are completely coated. Store in the fridge to marinate for at least an hour {overnight is best}. Preheat your grill {we use a charcoal} to 350 degrees. Line the thighs up on indirect heat skin facing up. I give them a good 10-15 minutes to grill and then I cover them with the vents open. Leave them for about another 25 minutes and then turn and cover. Once you have an internal temperature of at least 165 bring them to the direct heat so they can get a nice char not them. Let them rest for at least 10 minutes and enjoy!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'E1CA3C31D24CE649B33886F572081E9D', X'43F947B43FF88544AE1E43105A166E72', X'43F947B43FF88544AE1E43105A166E72', 0, 'https://grillgirl.com/2016/07/caribbean-mango-slaw/', 'Caribbean Mango Slaw', 2, NULL, 0, 'Caribbean Mango Slaw', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/07/Mango-Slaw_Michelle-Lara_Horizontal-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1/2 a red cabbage?#&2–3 ripe mangoes {diced or thinly sliced}?#&1 cup of chopped cilantro?#&juice of 1 lime?#&juice of 1 grapefruit?#&juice + zest of 1 navel or blood orange?#&1 jalapeno sliced {add more seeds if you want some extra heat}?#&1/4 cup of white wine vinegar?#&salt + pepper to taste?#&a few dashes of garlic powder', '', 'https://grillgirl.com/2016/07/caribbean-mango-slaw/', '', '', 'Jul 19, 2016', '', 'Instructions?#&?#&Wash and then grate the red cabbage, place in a large bowl. Add the rest of the ingredients and make sure to taste make salt + pepper adjustments as needed. Refrigerate for at least 30 minutes so the flavors can develop. This slaw is great on tacos or as a side {or even on it’s own as a quick snack!}');
