INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'87AA3952C07200488BAE5CCBFBE6E06A', X'3AF23D7772F36449AE0F3E39A11207C7', X'3AF23D7772F36449AE0F3E39A11207C7', 0, 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 2, NULL, 0, 'Lionfish Ceviche (“Eat ‘Em to Beat ‘Em”)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/05/lionfish-ceviche-edited-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 2 lbs lionfish filets?#&• 1 cup lime juice- approximately 7 large limes?#&• 1/4 tsp lime zest?#&• 1 tbsp white vinegar?#&• 1 red onion, thinly sliced?#&• 1-2 jalapeno or serrano pepper, diced (depending on how spicy you like!)?#&• 2 medium sized tomatoes, diced?#&• 1 large bunch cilantro, chopped?#&• 2 avocados, diced?#&• fresh ground pepper, to taste?#&• sea salt, to taste', '', 'https://grillgirl.com/2017/05/lionfish-ceviche-eat-em-beat-em/', '', '', 'May 30, 2017', '', 'Instructions?#&?#&Chop the Lionfish into small chunks, making sure that they are evenly sized so they cook evenly.?#&Chop all veggies and set aside. Juice your limes, combine with vinegar.?#&Add the fish and all veggies except the avocado to the lime juice mixture.?#&Let marinate for at least 20 minutes or until the fish is opaque.?#&Season with salt and pepper to taste. Toss with avocado.?#&Serve with plantain or yucca chips for a nice appetizer or light meal when visiting Florida!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'0846A270699D1640ACF4FDBCD22C7E32', X'86C1110E1750834BA7CF11A5A1E9E049', X'86C1110E1750834BA7CF11A5A1E9E049', 0, 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', 'Grilled Mussels with Garlic Herb Butter', 2, NULL, 0, 'Grilled Mussels with Garlic Herb Butter', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/IMG_6889-1-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&2 lb of mussels?#&2 lemons?#&1 stick butter?#&3 cloves garlic, minced?#&1 large bunch parsley or herb of your choice, chopped?#&Sea salt, fresh ground pepper to taste', '', 'https://grillgirl.com/2017/02/grilled-mussels-garlic-herb-butter/', '', '', 'Feb 24, 2017', '', 'Instructions?#&?#&Prepare a grill for medium direct heat. While grill is heating up, Melt your butter over low heat. Add the garlic and herbs and let reduce down.?#&?#&Grilling: If your grill grate skew to being far apart, you will need to put your mussels on a grill/veggies pan. If they are within a small enough apart, you can grill the mussels directly on the grates. Grill the mussels until the pop open. This will be around 5 minutes or so… The ones that open move to a less hot part of the grill while waiting on the others. Any mussels that don’t open should be thrown away.?#&?#&Place the mussels in a big bowl and add sea salt and pepper. Pour the butter over the mussels.?#&?#&These are also really great with grilled bread- grill bread pieces until char marks form while also grilling the mussels. Dip them in the drawn butter! YESSSSS! How do you like your mussels? What do you serve them with? I would pair these with a nice glass of wine of course!');
