INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'8255B24245C8BB478CE24CC7CB931051', X'806E538B33AA1B4DAD7AB1A3547CF31E', X'806E538B33AA1B4DAD7AB1A3547CF31E', 0, 'https://grillgirl.com/2019/07/cast-iron-grilled-mussels-in-a-white-wine-bacon-herb-butter-sauce/', 'Cast Iron Grilled Mussels in a White Wine, Bacon Herb Butter Sauce', 2, NULL, 0, 'Cast Iron Grilled Mussels in a White Wine, Bacon Herb Butter Sauce', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/07/DSC_016172-150x150.jpg', '', '', '5 minutes', '15 minutes', '20 minutes', '3', 'Ingredients?#&?#&1 dozen mussels?#&2/3 cup Beringer Main and Vine Pinot Grigio?#&3 tbsp butter?#&1 strip bacon, partially cooked?#&1 small bunch of fresh thyme sprigs?#&4 cloves garlic, minced?#&Salt, to taste?#&1 tsp pepper', 'American, French', 'https://grillgirl.com/2019/07/cast-iron-grilled-mussels-in-a-white-wine-bacon-herb-butter-sauce/', 'grill, cast iron', 'how to grill mussels, cast iron grilled mussels', 'Jul 26, 2019', 'american, grilled', 'Instructions?#&?#&Precook a slice of bacon so it is ¾ of the way cooked- this can easily be done in the microwave. Next, add the wine, butter, garlic, bacon (remove fatty parts of the bacon and chop before adding), thyme, salt and pepper.?#&?#&Add the mussels to the cast iron skillet and grill on direct heat over Kingsford Charcoal until the mussels open up- about 15 minutes or less. Serve with grilled or toasted crusty bread like ciabatta or French bread to sop up the white wine sauce.');
