INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'C8A197AA87DAC346A12EE3B0A035B8EB', X'E42CBBB20A11614EB6941137F08F31A1', X'E42CBBB20A11614EB6941137F08F31A1', 0, 'https://grillgirl.com/2018/05/everything-big-green-egg-series-learn-how-to-use-your-bge-like-a-pro/', 'Everything “Big Green Egg” Series: Learn How To Use Your BGE Like a Pro', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2018/05/everything-big-green-egg-series-learn-how-to-use-your-bge-like-a-pro/', '', '', 'May 23, 2018', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'5F97453CBB295743BE1A83C261DF006C', X'FC1E453B9D62DA4395454105FA1A2B5C', X'FC1E453B9D62DA4395454105FA1A2B5C', 0, 'https://grillgirl.com/2010/03/mesquite-smoked-beef-tenderloin-on-the-big-green-egg/', 'Mesquite Smoked Beef Tenderloin on the Big Green Egg', 2, NULL, 0, 'Mesquite Smoked Beef Tenderloin on the Big Green Egg', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2010/03/IMG_0294-150x150.jpg', '', '', '30 minutes', '25 minutes', '55 minutes', '', 'Ingredients', '', 'https://grillgirl.com/2010/03/mesquite-smoked-beef-tenderloin-on-the-big-green-egg/', 'Smoked', '', 'Mar 24, 2010', 'Beef', 'Instructions?#&?#&Pre-soaked mesquite wood chips for 30 minutes.?#&Rub 2 tablespoons Stubb’s BBQ rub (or your own) on both sides of the meat.?#&Let the beef tenderloin sit out for around 30 minutes to get up to room temperature.?#&Preheat the Green Egg to 275 degrees.?#&Put the mesquite wood chips on the charcoal.?#&Put the meat on the grill.?#&Cook at 250 to to 275 degrees until the internal temperature reaches 160 degrees ( Medium- Allow about 20 to 25 minutes per pound of meat.?#&Do not over cook because meat still continues to cook after you take it off the grill.)?#&?#&Let the meat rest about 10 minutes before you slice in 1/4 to 3/8″ size pieces and serve with your favorite sides.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'4EA37B9BE3813B42BAD6A1AB4C890AF6', X'0834E38E7377E34CB04EF64C012F641D', X'0834E38E7377E34CB04EF64C012F641D', 0, 'https://grillgirl.com/2020/08/smoked-au-gratin-potatoes/', 'Smoked Au Gratin Potatoes', 2, NULL, 0, 'Smoked Cast Iron Au Gratin Potatoes', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2020/04/augratinpotatoes-150x150.jpg', '', '', '20 minutes', '1:30', '1 hour 40 minutes', '1/8 skillet, serves 8', 'Ingredients?#&?#&5–6 medium potatoes, thinly sliced with a mandolin on largest setting ?#&3 tbsp butter ?#&3 tbsp all purpose flour (gluten free is okay) ?#&1 tsp sea salt (or smoked sea salt – page x) ?#&½ tsp fresh ground pepper ?#&2 cups 2% milk ?#&1 cup cheddar cheese (you can use smoked cheese to add additional smokiness)?#&2 tbsp chopped onion ?#&2 tsp fresh thyme (a few sprigs)', 'American', 'https://grillgirl.com/2020/08/smoked-au-gratin-potatoes/', 'Smoke/Bake', 'cast iron smoked au gratin potatoes, smoked cheesy potatoes, smoked au gratin potatoes, pellet smoker potatoes', 'Aug 11, 2020', 'Smoked potatoes', 'Instructions?#&?#&Slice potatoes with a mandolin so they are thinly and uniformly sliced.?#&Start your sauce by melting butter over low heat and gradually adding in the flour, then the salt and pepper. Stir constantly to create a roux. ?#&Gradually add in the milk and bring to a boil. Cook for 2 minutes or until thickened.?#&Remove from heat and add in cheese- stir until melted.   ?#&Layer the potatoes in a cast iron skillet. Once the entire bottom is covered with potatoes, layer with the sauce and onions. ?#&Repeat with the remaining potatoes and sauce until all are finished. Try to ensure all potatoes touch the sauce.?#&Smoke the potatoes for 20 minutes. While smoking, preheat your oven to 350 degrees (this can also be done on a grill on indirect heat).?#&Remove potatoes from the smoker and cover with foil. Bake in the oven for one hour.?#&Remove the foil after an hour and continue to bake for another 25 minutes or until the potatoes are fork tender. ?#&Sprinkle with thyme and let cool before serving.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'9CEFB64697D56546AA8E3B4EF5AC1896', X'96039FC040F54B42B8B34B3C46A86E71', X'96039FC040F54B42B8B34B3C46A86E71', 0, 'https://grillgirl.com/2019/07/cast-iron-grilled-mussels-in-a-white-wine-bacon-herb-butter-sauce/', 'Cast Iron Grilled Mussels in a White Wine, Bacon Herb Butter Sauce', 2, NULL, 0, 'Cast Iron Grilled Mussels in a White Wine, Bacon Herb Butter Sauce', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/07/DSC_016172-150x150.jpg', '', '', '5 minutes', '15 minutes', '20 minutes', '3', 'Ingredients?#&?#&1 dozen mussels?#&2/3 cup Beringer Main and Vine Pinot Grigio?#&3 tbsp butter?#&1 strip bacon, partially cooked?#&1 small bunch of fresh thyme sprigs?#&4 cloves garlic, minced?#&Salt, to taste?#&1 tsp pepper', 'American, French', 'https://grillgirl.com/2019/07/cast-iron-grilled-mussels-in-a-white-wine-bacon-herb-butter-sauce/', 'grill, cast iron', 'how to grill mussels, cast iron grilled mussels', 'Jul 26, 2019', 'american, grilled', 'Instructions?#&?#&Precook a slice of bacon so it is ¾ of the way cooked- this can easily be done in the microwave. Next, add the wine, butter, garlic, bacon (remove fatty parts of the bacon and chop before adding), thyme, salt and pepper.?#&?#&Add the mussels to the cast iron skillet and grill on direct heat over Kingsford Charcoal until the mussels open up- about 15 minutes or less. Serve with grilled or toasted crusty bread like ciabatta or French bread to sop up the white wine sauce.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'9327E11E4B202340919CC63B401E5AEF', X'A1210AB1D4315B4F86546F53B05BE8FE', X'A1210AB1D4315B4F86546F53B05BE8FE', 0, 'https://grillgirl.com/2019/03/smoked-poblano-peach-blue-cornbread-recipe/', 'Smoked Poblano Peach Blue Cornbread Recipe', 2, NULL, 0, 'Peach Poblano Blue Cornbread Recipe', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2019/03/Peach-pablano-150x150.jpeg', '', '', '20', '25', '45 minutes', '', 'Ingredients?#&?#&10” cast iron skillet?#&2 smoked peaches?#&1 smoked poblano pepper?#&1 tbsp butter (for coating cast iron)?#&2 cups blue cornmeal, you can find on Amazon here or try (Bob’s Red Mill, Anson Mills or War Eagle Farms)?#&1 ½ cups all-purpose flour?#&2 tsp salt?#&2 tsp baking powder?#&1 tsp baking soda?#&2 tbsp sugar?#&3 eggs, beaten?#&2 cups milk?#&¼ cup butter, melted', '', 'https://grillgirl.com/2019/03/smoked-poblano-peach-blue-cornbread-recipe/', '', 'blue corn bread, skillet corn bread, blue cornmeal, corn bread with blue corn meal, blue corn bread', 'Mar 1, 2019', '', 'Instructions?#&?#&When smoking other things, go ahead and take the opportunity to add peaches and poblanos to put into the cornbread recipe!?#&?#&Add halved peaches and poblano pepper to the smoker and let the peaches smoke for 30-35 minutes. I used peach wood to compliment the flavor of the peach. Let the pepper smoke for an hour. Remove and keep in the refrigerator until ready to bake the cornbread.?#&?#&Preheat oven to 425 degrees. Coat cast iron skillet with a tablespoon of butter on the bottom and sides of the skillet. Combine dry ingredients in one bowl: cornmeal, flour, baking powder, baking soda, and sugar. In another bowl, combine the wet ingredients: beaten eggs, milk, and butter. Finely chop one and a half of the peaches, reserving one half to top the cornbread. Slice the remaining half-peach thinly lengthwise. Finely chop the poblano, also reserving a few pieces for the top.?#&?#&Add the wet ingredients to the dry ingredients and stir until combined. Add the chopped peaches and poblanos and fold in. Pour the batter into the cast iron skillet. In the middle of the skillet, arrange the remaining peach slices in a circle in the middle. Sprinkle remaining chopped poblanos on top –  this will make for an eye-catching presentation once baked.?#&?#&Bake the cornbread for 20-25 minutes until browned and a toothpick inserted in the middle comes out clean. Serve with butter and honey. I dare you to eat just one piece!?#&?#&Tip Bubble: Blue cornmeal can be found from Anson Mills, War Eagle Farms, and Bob’s Red Mill. If the cornmeal is very coarse, pulse in the food processor before baking with it for a finer texture. Gluten-free all-purpose flour can easily be used in any baking recipe – I recommend “Cup for Cup” all-purpose gluten-free flour. It can easily be found on Amazon.com.?#&?#&Pairing Suggestions/Maximizing Leftovers: For a sweeter cornbread without adding extra calories, add 2 tablespoons stevia in the raw to the batter before baking. This cornbread is great paired with any dish that comes out of the smoker!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2A451058F7CFFD4D95863A05A4E32560', X'684DC8EFE6FACA4C802899FDEA404CF2', X'684DC8EFE6FACA4C802899FDEA404CF2', 0, 'https://grillgirl.com/2019/02/brussels-sprouts-on-the-big-green-egg/', 'Brussels Sprouts on the Big Green Egg', 2, NULL, 0, 'Brussels Sprouts on the Big Green Egg', 'Jon Solberg', 'https://grillgirl.com/wp-content/uploads/2019/02/IMG_0724-e1551334392230-150x150.jpg', '', '', '20', '20', '40 minutes', '4', 'Ingredients?#&?#&1 lb. of Brussels sprouts with the ends trimmed and any yellowing leaves removed, rinsed and drained. If using frozen sprouts, these will be ready to go right out of the bag?#&2 TBSP olive oil?#&1 TBSP kosher salt?#&1 TSP granulated garlic?#&Fresh ground black pepper to taste', '', 'https://grillgirl.com/2019/02/brussels-sprouts-on-the-big-green-egg/', '', '', 'Feb 28, 2019', '', 'Instructions?#&?#&Set your Egg up for indirect cooking with your convEGGtor and get the temp up to 400°F.?#&?#&Once your Egg is up to temperature, get your sprouts on board. I like to place mine in a cast iron skillet.  You can place them directly on the cooking grate as well.  There are so many wonderful ways to grill these tasty veggies. If your sprouts are still on the stalk, place it directly on the cooking grate.?#&?#&Your sprouts will take about 20 minutes to cook, though a bit faster if you have par-cooked them. Oddly, the frozen sprouts take about the same time as the thawed. Check them every 4 or 5 minutes and give them a stir. For on-the-stalk sprouts, give them a ¼ turn at each check, or as needed until you’ve cooked the sprouts all the way around. Cook until they reach an internal temperature of 165°F. Once finished, get them off the Egg and serve.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'64D8C392020481499BD7B2F632CA23B0', X'2D89D1227FB5134F8759046DFBE5A0A3', X'2D89D1227FB5134F8759046DFBE5A0A3', 0, 'https://grillgirl.com/2018/02/smoked-bourbon-salted-caramel-apple-pie/', 'Smoked Bourbon and Salted Caramel Apple Pie', 2, NULL, 0, 'Smoked Bourbon and Salted Caramel Apple Pie', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2018/02/bourbon-salted-caramel-apple-pie-150x150.jpg', '4', '1', '', '', '', '', 'Ingredients?#&?#&2 refrigerated pie crusts?#&5-cups sliced apples, equivalent to 6 small-medium apples chopped?#&1/2 cup dark brown sugar?#&1/2 tsp cinnamon?#&1 dash each: nutmeg, allspice, ginger?#&2 tbsp melted butter?#&4 tbsp dulce de Leche?#&2 tbsp bourbon?#&1 tbsp maldon smoked sea salt or regular sea salt', '', 'https://grillgirl.com/2018/02/smoked-bourbon-salted-caramel-apple-pie/', '', '', 'Feb 25, 2018', '', 'Instructions?#&?#&Roughly peel the apples with a vegetable peeler to remove the majority of the peel (doesn’t have to be perfect), then chop them up and put them in a big bowl. Add the bourbon, spices, brown sugar and butter and stir to combine.?#&Next, let your refrigerated pie crusts slightly thaw and soften out of the refrigerator. I prefer to bake in 12″ cast iron skillet for pies on my smoker, but a pie plate would work as well.?#&On a pregreased skillet or pie pan, roll out the pie dough into the skillet trying to get the dough to roll up on the sides as far as possible. Next, add your apple mixture.?#&Here’s the fun part! Put the dulce de leche into a Ziploc sandwich bag and add the maldon sea salt and stir tother in the bag with a fork.?#&Next, run it under hot water in the sink so the salt will combine and the caramel will spread more easily. Snip the end of the bag to create a pastry bag.?#&Now, pipe the dulce de leche onto the top of the apple pie mixture. Now sprinkle just a little bit more of salt onto the caramel (don’t overdo it!)?#&Take the other pie crust and put it on top of the pie and seal the edges. Cut a few slits to let the steam escape.?#&Set your Green Mountain Grill or pellet smoker to 300 degrees, and let your pie bake for approximately 45 minutes OR until the crust has browned and the apple mixture will become bubbly. Watch the edges of the crust, if it is browning too quickly cover the edges with foil. It is normal for some of the pie juice to come up on the side of the crust, I believe this is due to the shape of the cast iron skillet.?#&When serving a slice, drizzle more dulce de leche onto each piece for extra yumminess! If you want icecream, I love this with vanilla gelato');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B0E8CFEAB2E62543B26508C8B003F0DE', X'E2FAE8970BA01049BE280AE381BC4611', X'E2FAE8970BA01049BE280AE381BC4611', 0, 'https://grillgirl.com/2018/01/grill-review-lodge-l410-pre-seasoned-sportsmans-charcoal-grill/', 'Grill Review: Lodge L410 Pre-Seasoned Sportsman’s Charcoal Grill', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2018/01/grill-review-lodge-l410-pre-seasoned-sportsmans-charcoal-grill/', '', '', 'Jan 31, 2018', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'317E5D77EC197142A2317ECD86A07750', X'76E270EA35E72E46B4769A971ED78987', X'76E270EA35E72E46B4769A971ED78987', 0, 'https://grillgirl.com/2017/11/blueberry-peach-cobbler-pitbarrel-cooker/', 'Blueberry Peach Cobbler on the PitBarrel Cooker', 2, NULL, 0, 'Blueberry Peach Cobbler on the PitBarrel Cooker', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/11/blueberry-peach-cobbler-pbc-logo-visible-EDITED-150x100.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&4 peaches, diced?#&1 1/2 cups of blueberries?#&1/4 cup honey?#&1 dash each: nutmeg, cinnamon, allspice?#&1 tbsp lemon zest (lemon really brightens the flavor of the fruit in any dish)?#&jiffy cake mix?#&1 egg?#&1/2 cup water?#&1 tbsp coconut oil or butter to oil cast iron pan', '', 'https://grillgirl.com/2017/11/blueberry-peach-cobbler-pitbarrel-cooker/', '', '', 'Nov 4, 2017', '', 'Instructions?#&?#&Combine fruit, honey, spices and lemon zest in one bowl and let macerate. In the meantime, combine the egg and water with the jiffy cake mix.?#&?#&Coat your 12” cast iron pan with butter or coconut oil so that none of your ingredients stick. Next, add your batter on top. Take a fork and spoon and lightly mix the fruit with the batter so that some of the fruit is visible (don’t mix too much- this is a rustic cobbler).?#&?#&Cook on your PitBarrel that has a hot fire going for about 30 minutes or until the fruit has begun to bubble and the batter has browned on the top. Depending on how hot your PBC is and how much charcoal is left, this may take up to 45 minutes.?#&?#&Serve with vanilla ice cream or fresh whipped cream. Delish and super easy!! If you love cobbler, I have a ton of cobbler recipes including a mango ginger cobbler and my Red, White and Blue cobbler that are always a big hit!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'39FF9B046E23B3408025C801684AD355', X'1A824CFC1C169E44968944F657D55E4E', X'1A824CFC1C169E44968944F657D55E4E', 0, 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 2, NULL, 0, 'Bacon, Pork & Guinness Cassoulet (Dutch Oven Pork and Beans)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/10/Bacon-Pork-Guinness-Cassoulet-edited-150x100.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&Dutch Oven or large aluminum pan to put on the grill?#&Smithfield Applewood Smoked Bacon Marinated Fresh Pork Loin Filet, sliced into 1/2” thick pieces for grilling?#&1/2 lb bacon, chopped into 1/2 inch pieces?#&2 15 oz cans navy beans, drained and rinsed?#&1 15 oz can kidney beans?#&1 medium sweet onion, sliced into rings for grilling?#&4 medium sized (I used Campari) tomatoes, sliced in half?#&2 carrots, chopped?#&2 celery sticks, chopped?#&2 cloves garlic, chopped?#&1 large bunch thyme?#&1–2 Guinness Beers or another Porter Style Beer?#&2 tbsp brown sugar?#&French Bread?#&Compound Butter (herbed butter)', '', 'https://grillgirl.com/2017/10/bacon-pork-guinness-cassoulet-dutch-oven-pork-beans/', '', '', 'Oct 7, 2017', '', 'Instructions?#&?#&Heat your grill up, preparing it for medium direct cooking- at a temperature of approximately 350 degrees. This means you will be cooking directly over the fire!?#&?#&While the grill heats up go ahead and prepare your ingredients for the grill; chop the bacon, carrots and celery and garlic. Slice the tenderloin into 1/2 thick pieces, and slice onion into rings for the grill, cut the tomatoes in half. Get all your other ingredients ready to take outside- I usually put everything on a big cutting board and carry it outside and then get another plate for switching out.?#&?#&Once the grill is heated up, place the cast iron on one half of the grill and add the bacon and let it reduce. Meanwhile, with your existing grill surface area, go ahead and grill the pork tenderloin. This will only take a few minutes per side, remove once char marks have begun to form (remember these will also go into the stew and continue to cook). Next, grill the tomatoes and onions until char marks have formed, then remove from the grill.?#&?#&In the dutch oven, once the bacon fat has rendered, add the carrots and celery and let them cook in the bacon fat. Meanwhile, chop the onions and tomatoes and add to the dutch oven, stirring frequently to ensure no bacon is burning on the bottom. Now add the garlic. Next, add the first Guinness beer and deglaze the bottom of the pan. Add the sugar and stir. Now, chop the pork into small bite sized pieces and add to the dutch oven. Last but not least, add the beans and thyme and let the cassoulet simmer for 10 minutes or until the liquid has reduced. If you like a more soup like stew, add another 1/2 to full beer, depending on how much time you have to let simmer.?#&?#&While the Cassoulet simmers on the grill, slice the French bread into 1” thick pieces and slather with butter on both sides. Grill very quickly – no more than a minute- to get grill marks, ensuring you don’t let it burn.?#&?#&Serve the Cassoulet with the grilled bread and be prepared for big smiles on the faces of your family- like I said, this recipe is pure comfort food and expect to add this to your list of recipes you make again and again. Also great for freezing and giving to those in need of an easy meal- like friends and family going through an illness or just home with a new baby.?#&?#&If you give this recipe a try, please let me know your thoughts in the comments below! Also, when reheating you’ll notice that the beans soak up a lot of the liquid. You can add more beer if you want more stew like consistency before reheating.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'9A37652080430142A51FE1E784FD99E9', X'22F7D0FA3D076C4AAE6C8C6AA0B3B42D', X'22F7D0FA3D076C4AAE6C8C6AA0B3B42D', 0, 'https://grillgirl.com/2016/11/bourbon-smoked-pecan-pie/', 'Bourbon and Smoked Pecan Pie', 2, NULL, 0, 'Bourbon and Smoked Pecan Pie', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2016/11/bourbon-and-smoked-pecan-pie-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Pie Crust/pastry dough?#&3/4 stick unsalted butter?#&1 1/4 cups light brown sugar?#&3/4th cup light corn syrup?#&2 tbsp bourbon?#&1 tbsp orange zest?#&3 eggs?#&dash cinnamon?#&1 tsp vanilla?#&1/4 tsp salt?#&2 cups pecan halves', '', 'https://grillgirl.com/2016/11/bourbon-smoked-pecan-pie/', '', '', 'Nov 18, 2016', '', 'Instructions?#&?#&Soak your nuts for 10 minutes and then drain the water.?#&Then go ahead and smoke your nuts. The water helps the smoke adhere to the nuts. I?#&f using the smoking gun, put the nuts in a glass bowl covered with saran wrap and then insert the hose into the bowl.?#&While using the smoking gun (you will need to fill with wood, turn on and light and it will produce smoke), insert the tube where the smoke comes out into the bowl of pecans.?#&Keep it covered so the smoke is trapped. Do this until the wood chips burn out and then repeat a few times.?#&Let the nuts stay in the smoker for about 10 minutes and then remove the saran wrap. Let the nuts dry out before baking with them.?#&Preheat the oven to 350 degrees.?#&Meanwhile, in a medium sized saucepan melt butter on medium low and add the brown sugar and corn syrup and stir to combine.?#&Next, add the orange peel, bourbon, vanilla and cinnamon and remove from the heat.?#&Beat the eggs in a bowl and add to the sugar mixture once it has significantly cooled.?#&Roll the pie crust out into a 12” cast iron skillet slightly crimping the edges. Fill with the pecans. Pour the sugar mixture over the pecans. Bake for 50 minutes or until the mixture is set and the crust has browned.?#&Remove from the oven and let cool. Excellent served with vanilla ice cream or homemade whipped cream.?#&?#&Enjoy!');
