INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'FBC45D4EAF292843A3E64FFE563AF09F', X'06D08C1142B79E4EACD6F39A7838023E', X'06D08C1142B79E4EACD6F39A7838023E', 0, 'https://grillgirl.com/2015/01/brisket-caramelized-onion-grilled-cheese/', 'Brisket and Caramelized Onion Grilled Cheese', 2, NULL, 0, 'Brisket and Caramelized Onion Grilled Cheese', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/01/Brisket-Grilled-cheese-header-shot-150x150.jpg', '', '', '', '', '', '1 sandwich', 'Ingredients?#&?#&2 tbsp butter?#&2 slices thick white bread?#&2 middle slices of cooked brisket?#&3 tablespoons Kraft Original Barbecue Sauce?#&¼ cup diced onions, these cooked in a skillet or grilled in barbecue sauce?#&4 slices Colby jack or cheddar cheese', '', 'https://grillgirl.com/2015/01/brisket-caramelized-onion-grilled-cheese/', '', '', 'Jan 20, 2015', '', 'Instructions?#&?#&Preheat your grill to medium direct heat- 350 degrees and place a cast iron on the grates (you can also use a griddle on the grill or indoors).?#&Add 1 tbsp of butter and tbsp. of barbecue sauce and sauté the onions in the sauce and butter for about 5 minutes or until they have begun to melt and break down.?#&Remove from skillet.?#&Next, melt remaining butter and add the bread and let it begin to toast.?#&Meanwhile, warm the leftover brisket in the skillet next to the bread.?#&Add 2 slices of cheese per slice and grill until it begins to melt, about 4 minutes.?#&Turn the heat down on your grill if the bread is heating too quickly.?#&Once the cheese has begun to melt, remove the brisket and slice in half so you can add it to the bread.?#&Place the brisket on top of one of the slices of grilled cheese and then add the onions, followed by Kraft Barbecue Sauce.?#&Place the remaining piece of cheese bread on top and let the flavors melt together. Slice in half. Experience grilled cheese and brisket smothered in barbecue sauce ecstasy.?#&?#&Visit www.KraftRecipes.com/BBQ and Facebook.com/KraftDressing to learn more about Kraft Barbecue Sauce, find delicious barbecue recipes and more.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'F1BE82221A7ACB4CABC2C8A43D2259D3', X'9CFDC51D0AE92347BB7A50420CD47A21', X'9CFDC51D0AE92347BB7A50420CD47A21', 0, 'https://grillgirl.com/2014/10/authentic-cuban-sammies-you-can-make-on-your-grill/', 'Authentic Cuban Sammies you can make on your grill', 2, NULL, 0, 'Authentic Cuban Sammies you can make on your grill', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/10/brick-grilled-cuban-sandwich-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 1 loaf of Cuban Bread – go to your Latin market if you can’t find at your grocery store. The bread is important to a true Cuban.?#&• 2-3 cups roasted pork (to be really authentic, marinate the pork in Mojo first: FYI, I do not currently have an oven and didn’t have time to smoke a pork butt so I roasted a pork butt in the crock pot for 6 hours in mojo marinade and sliced oranges and lemons. It turned out great for this purpose).?#&• Ham (I used applegate farms nitrate free ham- any boiled or glazed ham should work okay).?#&• Dill pickles, thinkly sliced?#&• 2 package sliced swiss cheese- enough slices to generously fill an entire loaf?#&• Mustard- I used El Diablo Mango Mustard to add a little kick?#&?#&Tools:?#&2 bricks covered in aluminum foil OR a large cast iron skillet', '', 'https://grillgirl.com/2014/10/authentic-cuban-sammies-you-can-make-on-your-grill/', '', '', 'Oct 25, 2014', '', 'Instructions?#&?#&Preheat your grill to medium high heat (375-400), creating a direct and indirect zone. You will want to grill the sandwich on indirect heat so the sandwich is not directly over the flame, but still gets plenty hot to melt the cheese and toast the bread and form char marks.?#&Grill under the foil covered brick (or use an inverted cast iron skillet) with the lid closed for approximately 10 minutes or until the cheese has melted nicely and the bread is toasted and golden.?#&?#&Traditional Cubans are sliced diagonally into triangles. For serving a crowd or a tailgating party, slice into mini servings so people can grab and go. I finished these off with these cute Miami Dolphins toothpicks that I found on Etsy, which are perfect for a tailgating party.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'8F3DEE152D6C23488D20C398DC54D5FB', X'128007C8F628BC4B92DB6F1FBB7B470B', X'128007C8F628BC4B92DB6F1FBB7B470B', 0, 'https://grillgirl.com/2014/09/tailgating-fun-build-grilled-cheese-bar/', 'Tailgating Fun: Build Your Own Grilled Cheese Bar', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2014/09/tailgating-fun-build-grilled-cheese-bar/', '', '', 'Sep 10, 2014', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'634858AEE429F54ABAF548726D8D0D54', X'B762EA55CF87F24592255FFC29078A53', X'B762EA55CF87F24592255FFC29078A53', 0, 'https://grillgirl.com/2014/04/turkey-brie-cranberry-subs-surprising-sandwich-next-picnic/', 'Turkey Sandwich with Brie and Cranberry: Picnic Perfect!', 2, NULL, 0, 'Turkey, Brie and Cranberry Subs Perfect for a Picnic', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/04/turkey-brie-and-cranberry-sandwiches.jpg-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 loaf french bread?#&1 lb thinly sliced turkey meat (I try to look for nitrate, preservative free sandwich meat)?#&½ cup mayo (I like the olive oil mayo)?#&1 tbsp chopped rosemary (if using dried, use less)?#&1 small can of cranberry sauce?#&1 5 oz. container alouette speadable brie?#&1 bunch arugula?#&fresh ground pepper to taste', '', 'https://grillgirl.com/2014/04/turkey-brie-cranberry-subs-surprising-sandwich-next-picnic/', '', '', 'Apr 19, 2014', '', 'Instructions?#&?#&Slice ends off baquette and then slice lengthwise. I like to remove some of the bread on the inside of the baquette to form a “pocket” so the fillings stay in better, it also makes the sandwich less “bready”.?#&Combine the rosemary and mayo and spread generously on both pieces of bread.?#&Next, spread the brie on the bottom piece of bread.?#&Add the turkey, and then add generous tablespoon fulls of cranberry on the turkey.?#&Add freshly ground pepper over the toppings. Next, layer with arugula.?#&Put the top baguette on the sandwich and compress down to help combine the flavors.?#&Slice into 2” serving sizes and then put into a Tupperware container layered tightly so the sandwiches cannot come apart.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'7982F04F91CCD74EA1B3991145C5A24B', X'B3F062EF6A00784899EB3A5DB474A1F3', X'B3F062EF6A00784899EB3A5DB474A1F3', 0, 'https://grillgirl.com/2014/01/classic-comfort-food-twist/', 'Classic Comfort Food With A Twist', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2014/01/classic-comfort-food-twist/', '', '', 'Jan 10, 2014', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'B9EF872D2AC05E4FAA6EE80630D95E31', X'90B7055479078849ACA548565D02828E', X'90B7055479078849ACA548565D02828E', 0, 'https://grillgirl.com/2013/07/mexican-torta-burgers/', 'Torta Burgers with Bacon Wrapped Stuffed Peppers', 2, NULL, 0, 'Mexican Inspired Torta Burgers- (Burgers with Bacon Wrapped Stuffed Peppers)', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2013/07/SouthWestern-Torta-burgers-150x150.jpg', '', '', '', '', '', 'Serves 2', 'ingredients)?#&2–3 large tortillas (good to have an extra in case one breaks, etc)?#&large bunch cilantro, leaves removed?#&toothpicks- for securing the bacon around the peppers', '', 'https://grillgirl.com/2013/07/mexican-torta-burgers/', '', '', 'Jul 21, 2013', '', 'Instructions?#&?#&Preheat grill to medium direct heat, about 350 degrees. Meanwhile, remove the stems from the cubanelle peppers and stuff them with cream cheese. Wrap them with bacon, using the bacon to close the open end of the pepper so the cream cheese cannot run out, and secure with a toothpick.?#&?#&Grill the burgers, bacon wrapped peppers and tomatoes on direct heat until char marks form on both sides (and for the peppers- the bacon is cooked through). For the burgers, you will flip them when beads of sweat appear on the top side. Burgers are ready to be pulled off when they reach an internal temp of 135 degrees.?#&?#&Once the burgers are cooked, tent them in foil a few minutes to reabsorb their juices. Then, place them on the tortillas, and add the grilled tomatoes, sliced avocado, bacon wrapped grilled peppers and cilantro. Then, wrap the tortillas over the burger to form a pouch for the burger.?#&?#&Enjoy burger ecstacy!!');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'DC477E4988F5274B841268131116CD32', X'CDC779375D606C4795FC5A57438C87F0', X'CDC779375D606C4795FC5A57438C87F0', 0, 'https://grillgirl.com/2013/05/goat-sliders/', 'Goat Sliders With Wine Reduction and Blue Cheese Sauce', 2, NULL, 0, 'Goat Sliders With Wine Reduction and Blue Cheese Sauce', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2013/05/goat-sliders-Eric-Chester-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 Goat Leg?#&3–5 lbs of bacon?#&6 cloves of garlic minced?#&1 softened stick of unsalted butter?#&7–8 leaves of fresh mint shredded.?#&4 bottles of Guinness or stout of your choice?#&2 Tbsp of sugar?#&1 Tbsp of sea salt?#&3 cups of cold water?#&?#&Cracked Pepper Red Wine Glaze?#&?#&2–3 cups of Red Wine (Choose a red wine that you would drink, I prefer a Cabernet Sauvignon)?#&1 tsp onion powder?#&1tsp minced garlic?#&½ tsp of cayenne?#& Fresh ground black pepper to your taste.?#&?#&Blue Cheese Sauce?#&?#&½ of a white onion chopped?#&1 red bell pepper – fire roasted and chopped?#&9 oz blue cheese?#&1 tsp of minced garlic?#&1 pint of heavy cream', '', 'https://grillgirl.com/2013/05/goat-sliders/', '', '', 'May 29, 2013', '', 'Instructions?#&?#&To begin cooking extravaganza trim away any excess fat from the goat leg. Expose as much of the meat as possible the set it aside. Goat can be a rather tough meat if prepared incorrectly. The best way to tenderize the meat is a good brine. Take the first 2 bottles of Guinness and pour them into a small stock pot. Open the 3rd bottle and enjoy. Add the 2 tbsp of sugar and 1 tbsp of salt to the Guinness and simmer until dissolved. Add 3 cups of water to the mixture and allow it to cool. Place the goat leg in a container large enough that it can lay flat and pour the Guinness brine over the leg.  Leave in the brine for 3-5 hours rotating the leg every 30 mins. While we brine the leg…may as well have that other bottle of Guinness.?#&?#&This would also be a great time to start working on the next step – BACON WEAVNG. Take a large sheet of aluminum foil (Large enough to cover the goat leg) and begin weaving. Think back to elementary school and making mats out of construction paper….it is the same, but with tasty, tasty bacon. If you aren’t a master bacon weaver, don’t worries you can lay out the bacon in overlapping strips.  This would also be a good time to bring your smoker up to 225 degrees.?#&?#&After 3-5 hrs, remove the goat leg from the brine and pat dry with a paper towel. Rub the entire leg down with the softened butter and minced garlic. Once rubbed down, sprinkle the shredded mint leaves over the leg. Now wrap the bacon around the leg. Make sure you leave no gaps…add more bacon. Now that it is happily wrapped in bacon wrap the leg in aluminum foil. Place the leg in the smoker and cook 12-14 hrs (depending on the size of the leg) at 225 degrees.?#&?#&Take a nap…have a drink or two, like Robyn’s Grilled Margaritas.?#&?#&An hour or so before the meat is done start working on your Cracked Pepper Red Wine Glaze and Blue Cheese Sauce.?#&?#&Cracked Pepper Red Wine Glaze: Pour the wine into a medium sauce pan. Add the onion powder, garlic, cayenne and black pepper. Bring the mix to boil for 2-3 min then bring the heat down to a simmer for 10- 15 min.  Remove from heat and set to the side.  Pour yourself a glass or two of wine. It’s not too early is it??#&?#&Blue Cheese Sauce: Add the garlic, onions, and fire-roasted pepper to a large skillet and heat until the onions begin to sweat. Add the blue cheese and heavy cream to the mix and continue stirring until the cheese is blended smooth.?#&?#&Now that the goat has been on for the 12-14 hours remove it from the smoker and allow it to rest 15 – 20 mins in the foil. Unwrap the foil and the bacon…but don’t discard the bacon…its bacon…eat it…It’s awesome. Pull the meat from the leg like you would a pork shoulder.  Place the meat on toasted wheat rolls.  Drizzle the cracked pepper wine glaze over the pulled goat.  Spoon the blue cheese sauce over top of the meat. Add a piece of lettuce or spinach and Viola!  Pour yourself a drink and Bon Appetite!”');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'F2D8FF01045E7543AC8C8EE9398A8922', X'34ABAA759EC224439389005ED304EED3', X'34ABAA759EC224439389005ED304EED3', 0, 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 2, NULL, 0, 'Hawaiian Grilled Pineapple and Pepper Pork Tenderloin Sliders', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2017/06/Hawaiian-Pork-Tenderloin-Sliders_1-150x150.jpg', '', '', '', '', '', 'Serves 4, 2 sliders per person', 'Ingredients?#&?#&1 Smithfield Roasted Garlic and Cracked Black Pepper Marinated Fresh Pork Tenderloin1 large pack Kings Hawaiian Rolls?#&1 small bag mini peppers?#&1 pineapple, cored and sliced into rings (you can also use canned pineapple)?#&1/4 cup mayo?#&1 tbsp Sriracha', '', 'https://grillgirl.com/2017/06/hawaiian-grilled-pineapple-pepper-pork-tenderloin-sliders/', '', '', 'Jun 22, 2017', '', 'Instructions?#&?#&Preheat your grill to 350 degrees preparing a direct and indirect zone. While the grill is heating up, slice your pork tenderloin into 3/4” filets.?#&?#&Coat your grill grates with a little oil so the food does not stick (make sure to do this especially before grilling the pork filets).?#&?#&Grill the pineapple and peppers until char marks have formed and they have begun to caramelize. Remove and let cool.?#&?#&Next, grill the pork tenderloin filets- about 5 minutes max, or until the internal temp reads 145 degrees with an internal read thermometer (I recommend the thermapen).?#&On the indirect side, toast the rolls very quickly being careful to not let them burn.?#&?#&Slice the peppers and then combine the mayo with the sriracha and spread on the buns. Now, assemble your sliders with the tenderloin, pineapple and peppers.?#&?#&The sweetness of the pineapple and peppers in this recipe really compliment the spice of the Black Pepper marinated pork tenderloin, don’t be surprised that you’ll go back for seconds or thirds!?#&?#&What are your favorite recipes for getting dinner on the table in less than 30 minutes? Please share in the comments below!');
