INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'E50328CB6CE8664CBBFBAC8749D965A6', X'29ABD2869F0F2346BA956D0614059B3C', X'29ABD2869F0F2346BA956D0614059B3C', 0, 'https://grillgirl.com/2015/01/brisket-caramelized-onion-grilled-cheese/', 'Brisket and Caramelized Onion Grilled Cheese', 2, NULL, 0, 'Brisket and Caramelized Onion Grilled Cheese', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2015/01/Brisket-Grilled-cheese-header-shot-150x150.jpg', '', '', '', '', '', '1 sandwich', 'Ingredients?#&?#&2 tbsp butter?#&2 slices thick white bread?#&2 middle slices of cooked brisket?#&3 tablespoons Kraft Original Barbecue Sauce?#&¼ cup diced onions, these cooked in a skillet or grilled in barbecue sauce?#&4 slices Colby jack or cheddar cheese', '', 'https://grillgirl.com/2015/01/brisket-caramelized-onion-grilled-cheese/', '', '', 'Jan 20, 2015', '', 'Instructions?#&?#&Preheat your grill to medium direct heat- 350 degrees and place a cast iron on the grates (you can also use a griddle on the grill or indoors).?#&Add 1 tbsp of butter and tbsp. of barbecue sauce and sauté the onions in the sauce and butter for about 5 minutes or until they have begun to melt and break down.?#&Remove from skillet.?#&Next, melt remaining butter and add the bread and let it begin to toast.?#&Meanwhile, warm the leftover brisket in the skillet next to the bread.?#&Add 2 slices of cheese per slice and grill until it begins to melt, about 4 minutes.?#&Turn the heat down on your grill if the bread is heating too quickly.?#&Once the cheese has begun to melt, remove the brisket and slice in half so you can add it to the bread.?#&Place the brisket on top of one of the slices of grilled cheese and then add the onions, followed by Kraft Barbecue Sauce.?#&Place the remaining piece of cheese bread on top and let the flavors melt together. Slice in half. Experience grilled cheese and brisket smothered in barbecue sauce ecstasy.?#&?#&Visit www.KraftRecipes.com/BBQ and Facebook.com/KraftDressing to learn more about Kraft Barbecue Sauce, find delicious barbecue recipes and more.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'62A418E8B590CC488DE6B572732F59C4', X'46671F2AED75FC4C8C68DC61571A51E4', X'46671F2AED75FC4C8C68DC61571A51E4', 0, 'https://grillgirl.com/2014/10/authentic-cuban-sammies-you-can-make-on-your-grill/', 'Authentic Cuban Sammies you can make on your grill', 2, NULL, 0, 'Authentic Cuban Sammies you can make on your grill', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/10/brick-grilled-cuban-sandwich-1-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&• 1 loaf of Cuban Bread – go to your Latin market if you can’t find at your grocery store. The bread is important to a true Cuban.?#&• 2-3 cups roasted pork (to be really authentic, marinate the pork in Mojo first: FYI, I do not currently have an oven and didn’t have time to smoke a pork butt so I roasted a pork butt in the crock pot for 6 hours in mojo marinade and sliced oranges and lemons. It turned out great for this purpose).?#&• Ham (I used applegate farms nitrate free ham- any boiled or glazed ham should work okay).?#&• Dill pickles, thinkly sliced?#&• 2 package sliced swiss cheese- enough slices to generously fill an entire loaf?#&• Mustard- I used El Diablo Mango Mustard to add a little kick?#&?#&Tools:?#&2 bricks covered in aluminum foil OR a large cast iron skillet', '', 'https://grillgirl.com/2014/10/authentic-cuban-sammies-you-can-make-on-your-grill/', '', '', 'Oct 25, 2014', '', 'Instructions?#&?#&Preheat your grill to medium high heat (375-400), creating a direct and indirect zone. You will want to grill the sandwich on indirect heat so the sandwich is not directly over the flame, but still gets plenty hot to melt the cheese and toast the bread and form char marks.?#&Grill under the foil covered brick (or use an inverted cast iron skillet) with the lid closed for approximately 10 minutes or until the cheese has melted nicely and the bread is toasted and golden.?#&?#&Traditional Cubans are sliced diagonally into triangles. For serving a crowd or a tailgating party, slice into mini servings so people can grab and go. I finished these off with these cute Miami Dolphins toothpicks that I found on Etsy, which are perfect for a tailgating party.');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'678E6268EF88F543B4504A3BD1F090CE', X'0EAEA6B7D74F5E48B2F7C3578692FFE4', X'0EAEA6B7D74F5E48B2F7C3578692FFE4', 0, 'https://grillgirl.com/2014/09/tailgating-fun-build-grilled-cheese-bar/', 'Tailgating Fun: Build Your Own Grilled Cheese Bar', 2, NULL, 0, '', '', '', '', '', '', '', '', '', '', '', 'https://grillgirl.com/2014/09/tailgating-fun-build-grilled-cheese-bar/', '', '', 'Sep 10, 2014', '', '');
INSERT INTO `VWR INTERNAL Grillgirl recipeLink` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, name, author, image, aggregateRating, aggregateCount, prepTime, cookTime, totalTime, recipeYield, recipeIngredientHTML, recipeCuisine, url, recipeMethod, Keywords, dateModified, recipeCategory, recipeInstructionnsHTML) VALUES (X'2E4BA61F7BB1104D9B0B71BFD3E3927E', X'C69D699D99A4F94E90C59B5506919627', X'C69D699D99A4F94E90C59B5506919627', 0, 'https://grillgirl.com/2014/04/turkey-brie-cranberry-subs-surprising-sandwich-next-picnic/', 'Turkey Sandwich with Brie and Cranberry: Picnic Perfect!', 2, NULL, 0, 'Turkey, Brie and Cranberry Subs Perfect for a Picnic', 'Robyn', 'https://grillgirl.com/wp-content/uploads/2014/04/turkey-brie-and-cranberry-sandwiches.jpg-150x150.jpg', '', '', '', '', '', '', 'Ingredients?#&?#&1 loaf french bread?#&1 lb thinly sliced turkey meat (I try to look for nitrate, preservative free sandwich meat)?#&½ cup mayo (I like the olive oil mayo)?#&1 tbsp chopped rosemary (if using dried, use less)?#&1 small can of cranberry sauce?#&1 5 oz. container alouette speadable brie?#&1 bunch arugula?#&fresh ground pepper to taste', '', 'https://grillgirl.com/2014/04/turkey-brie-cranberry-subs-surprising-sandwich-next-picnic/', '', '', 'Apr 19, 2014', '', 'Instructions?#&?#&Slice ends off baquette and then slice lengthwise. I like to remove some of the bread on the inside of the baquette to form a “pocket” so the fillings stay in better, it also makes the sandwich less “bready”.?#&Combine the rosemary and mayo and spread generously on both pieces of bread.?#&Next, spread the brie on the bottom piece of bread.?#&Add the turkey, and then add generous tablespoon fulls of cranberry on the turkey.?#&Add freshly ground pepper over the toppings. Next, layer with arugula.?#&Put the top baguette on the sandwich and compress down to help combine the flavors.?#&Slice into 2” serving sizes and then put into a Tupperware container layered tightly so the sandwiches cannot come apart.');
