INSERT INTO `VWR INTERNAL Holycowvegan1` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment) VALUES (X'3FACDD28AE42094F923745A2DBAE60CE', NULL, NULL, 0, 'https://holycowvegan.net/vegan-thanksgiving-recipes/', NULL, 2, NULL, 0);
INSERT INTO `VWR INTERNAL Holycowvegan1` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment) VALUES (X'39409E1318D8BD41801C4AC4CD81042E', NULL, NULL, 0, 'https://holycowvegan.net/indian-vegan-restaurant-style-recipes/', NULL, 2, NULL, 0);
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1753D7CA93419A448341AC13919B9E13', X'EE347D6082714143B954DE6E10EDD30E', X'EE347D6082714143B954DE6E10EDD30E', 0, NULL, NULL, 2, NULL, 0, 'Coat the baking dish with some vegan butter or oil spray. Arrange the slices of bread in the casserole, overlapping them like shingles. Fill in any empty spaces with the smaller end slices.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'300BD6BCDF123B458398AF26F0B87928', X'EE347D6082714143B954DE6E10EDD30E', X'EE347D6082714143B954DE6E10EDD30E', 1, NULL, NULL, 2, NULL, 0, 'In a blender, place the banana, cashews, nondairy milk, cinnamon, nutmeg, sugar, flax meal, aquafaba and vanilla. Blend until smooth and creamy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB767CC17C721648923E57E447DA8298', X'EE347D6082714143B954DE6E10EDD30E', X'EE347D6082714143B954DE6E10EDD30E', 2, NULL, NULL, 2, NULL, 0, 'Pour the custard over the bread evenly and set aside for 15 minutes, while you make the walnut topping.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2F463DA1D7AE442ACE3E10FAA664229', X'EE347D6082714143B954DE6E10EDD30E', X'EE347D6082714143B954DE6E10EDD30E', 3, NULL, NULL, 2, NULL, 0, 'Set the oven to preheat to 350 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'026F2776FC48A64BB67FAC3C10F6B410', X'EE347D6082714143B954DE6E10EDD30E', X'EE347D6082714143B954DE6E10EDD30E', 4, NULL, NULL, 2, NULL, 0, 'To make the topping, place the walnuts in a bowl with the sugar, butter, cinnamon and nutmeg. Mix with your hands until the butter is dispersed through the mixture. Sprinkle it evenly on top of the casserole.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22920F1C8AE86741A7B3C62326B5CCA4', X'EE347D6082714143B954DE6E10EDD30E', X'EE347D6082714143B954DE6E10EDD30E', 5, NULL, NULL, 2, NULL, 0, 'Bake the casserole for 40 minutes. Serve the French toast with maple syrup, if desired.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8ABC5107E34494EB3060A69BEBDF3DD', X'0587CDDD039990418826AF3F10A0361D', X'0587CDDD039990418826AF3F10A0361D', 0, NULL, NULL, 2, NULL, 0, 'Place the cashews in a high-powered blender with 3 cups water and blitz until very smooth. If your blender is not high-powered, soak the cashews first for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ADCDC235B83FA45AAC53942EF8E2702', X'0587CDDD039990418826AF3F10A0361D', X'0587CDDD039990418826AF3F10A0361D', 1, NULL, NULL, 2, NULL, 0, 'In a food processor or a large bowl, whisk together the cashew milk, maple syrup, pumpkin puree, tofu, cinnamon, nutmeg, ginger, cloves and vanilla extract. You will need to blend the tofu separately first if you''re not using a food processor in order to get it smooth.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B280689593E4E488BAE0FE4D4BC477D', X'0587CDDD039990418826AF3F10A0361D', X'0587CDDD039990418826AF3F10A0361D', 2, NULL, NULL, 2, NULL, 0, 'Spray a 9 X 12 inch baking dish with cooking spray. Arrange the cut pieces of bread in a single layer and pour over the custard evenly. Let the pudding stand at least 30 minutes, during which the bread will soak up a lot of the custard.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'131DC285AA851B41A3F281CDF2EBF4E9', X'0587CDDD039990418826AF3F10A0361D', X'0587CDDD039990418826AF3F10A0361D', 3, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF7C0107710CBD488261299C94992A4F', X'0587CDDD039990418826AF3F10A0361D', X'0587CDDD039990418826AF3F10A0361D', 4, NULL, NULL, 2, NULL, 0, 'Just before baking, give the pudding a quick stir. Don''t be too heavy-handed or you''ll mush up the bread pieces.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE988B2C6935EF45A34B42C26E860E0B', X'0587CDDD039990418826AF3F10A0361D', X'0587CDDD039990418826AF3F10A0361D', 5, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 45 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'075CAE746D93464587A6B11C168A08A9', X'0587CDDD039990418826AF3F10A0361D', X'0587CDDD039990418826AF3F10A0361D', 6, NULL, NULL, 2, NULL, 0, 'Serve with a scoop of vanilla ice cream or whipped cream.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CCAEA6928B54F4DA7663F1598E9CD88', X'E5943F60C0526448AF67A4F6FE78ED7A', X'E5943F60C0526448AF67A4F6FE78ED7A', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl or in the bowl of a stand mixer, mix the yeast with the warm water. Let stand five minutes until yeast is frothy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF6870743FB81041BAF6FAE80ACB180B', X'E5943F60C0526448AF67A4F6FE78ED7A', X'E5943F60C0526448AF67A4F6FE78ED7A', 1, NULL, NULL, 2, NULL, 0, 'Add the nondairy milk, sugar or maple syrup, salt, and apple cider vinegar and mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6BBAD90843B0847B6BBF75A134641AB', X'E5943F60C0526448AF67A4F6FE78ED7A', X'E5943F60C0526448AF67A4F6FE78ED7A', 2, NULL, NULL, 2, NULL, 0, 'Add ½ cup of bread flour and all of the whole wheat flour and mix well. Gradually add the remaining bread flour, a little at a time, until you have a smooth, slightly sticky dough. Continue kneading for about 10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D830A820F23374480EEC29605C7C2DC', X'E5943F60C0526448AF67A4F6FE78ED7A', X'E5943F60C0526448AF67A4F6FE78ED7A', 3, NULL, NULL, 2, NULL, 0, 'Form the dough into a smooth ball, place in a large bowl sprayed with oil, cover, and set in a warm place to rise for an hour or until doubled.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02A3B381BCF00545B040A49F7C1059CF', X'E5943F60C0526448AF67A4F6FE78ED7A', X'E5943F60C0526448AF67A4F6FE78ED7A', 4, NULL, NULL, 2, NULL, 0, 'While the dough is rising, mix together the apple filling ingredients in a bowl and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FF6DE264C8F4A4EA947F8C03D4689A0', X'E5943F60C0526448AF67A4F6FE78ED7A', X'E5943F60C0526448AF67A4F6FE78ED7A', 5, NULL, NULL, 2, NULL, 0, 'Once the dough has risen, knead it to deflate, and divide into 12 portions.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7CFC9A8A53DC54FA7C9BA301A6A5DD8', X'E5943F60C0526448AF67A4F6FE78ED7A', X'E5943F60C0526448AF67A4F6FE78ED7A', 6, NULL, NULL, 2, NULL, 0, 'Roll each portion into a ball, then roll into an oval, about four inches across on the wider side.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8C9E495AD788C46803038ED10DA57BA', X'E5943F60C0526448AF67A4F6FE78ED7A', X'E5943F60C0526448AF67A4F6FE78ED7A', 7, NULL, NULL, 2, NULL, 0, 'Place 2 apple slices in the center of the oval, and brush on some of the juice that the apples have expressed along with any sugar and spices. Fold the oval in half. Continue doing this for the rest of the balls of dough, and then stack them side by side, open side up, inside an oiled loaf pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D533C151CE98940AD4AA87CC49F1F31', X'E5943F60C0526448AF67A4F6FE78ED7A', X'E5943F60C0526448AF67A4F6FE78ED7A', 8, NULL, NULL, 2, NULL, 0, 'Cover with a loose kitchen towel or plastic wrap and set aside to rise in a warm place, about two hours. The dough will mushroom over the top of the pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54D85C88CA1D094A8BD42EAEB8E3DB80', X'E5943F60C0526448AF67A4F6FE78ED7A', X'E5943F60C0526448AF67A4F6FE78ED7A', 9, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B73ADE591CD6EC4295FC5619E6CDED25', X'E5943F60C0526448AF67A4F6FE78ED7A', X'E5943F60C0526448AF67A4F6FE78ED7A', 10, NULL, NULL, 2, NULL, 0, 'Once the loaf is risen, brush any remaining apple juice over the top of the loaf gently, then place in the oven. Bake 10 minutes, lower heat to 350 degrees, and continue baking 45-50 minutes or until the top is golden brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59CA72A43C0F3541B27981917D286F85', X'E5943F60C0526448AF67A4F6FE78ED7A', X'E5943F60C0526448AF67A4F6FE78ED7A', 11, NULL, NULL, 2, NULL, 0, 'Cool on a rack, then unmold and dig in.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E012DDB8E60C994CB3FAA80B691B7BBC', X'669E8364FE28494BB4C4686078EC951F', X'669E8364FE28494BB4C4686078EC951F', 0, NULL, NULL, 2, NULL, 0, 'Mix the yeast with the water, almond milk and sugar in a large bowl or in the bowl of a stand mixer. Let it stand for five minutes so the yeast can flower.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEF517EBD053C14DA4E00BC53AC0E406', X'669E8364FE28494BB4C4686078EC951F', X'669E8364FE28494BB4C4686078EC951F', 1, NULL, NULL, 2, NULL, 0, 'Add the aquafaba and olive oil and mix it in. Now add 2 cups of all purpose flour and mix by hand or on medium-low speed in the mixer until everything is incorporated. Slowly, add more flour, half a cup at a time, until you get a smooth, supple dough. I needed a total of about 3 ½ cups on a humid day.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57711B8FA46AE5448552D5A52C7448BA', X'669E8364FE28494BB4C4686078EC951F', X'669E8364FE28494BB4C4686078EC951F', 2, NULL, NULL, 2, NULL, 0, 'Continue to knead for a couple of minutes, by hand or on low speed in the stand mixer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6AAB77A88322E43A525181ECD12867C', X'669E8364FE28494BB4C4686078EC951F', X'669E8364FE28494BB4C4686078EC951F', 3, NULL, NULL, 2, NULL, 0, 'Remove the dough to a bowl sprayed with oil and set aside to rise in a warm place until doubled, about 1 hour. (I leave it in the unheated oven with the light on).');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'489CE3EFC1D33247A51E51E1292AC8CB', X'669E8364FE28494BB4C4686078EC951F', X'669E8364FE28494BB4C4686078EC951F', 4, NULL, NULL, 2, NULL, 0, 'When the dough has doubled in size, punch it down and roll it into a rectangle on a floured surface, 15 inches in length and 9 inches in width.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11D2AD40C7585248BE6E9BB1943D79F3', X'669E8364FE28494BB4C4686078EC951F', X'669E8364FE28494BB4C4686078EC951F', 5, NULL, NULL, 2, NULL, 0, 'Mix the filling ingredients in a small bowl: sugar, cinnamon and walnuts. Sprinkle them evenly over the surface of the dough.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A2346821FD6A1469AC01EF6D87DBDA9', X'669E8364FE28494BB4C4686078EC951F', X'669E8364FE28494BB4C4686078EC951F', 6, NULL, NULL, 2, NULL, 0, 'Start rolling up the dough as tightly as possible along the longer seam, so you have a 15-inch cylinder.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9EDC75990C34B489142ACC6BFF7CD87', X'669E8364FE28494BB4C4686078EC951F', X'669E8364FE28494BB4C4686078EC951F', 7, NULL, NULL, 2, NULL, 0, 'Cut the roll at 1-inch intervals, making 15 individual rolls.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4608967D71219F46B95983E725FF8F85', X'669E8364FE28494BB4C4686078EC951F', X'669E8364FE28494BB4C4686078EC951F', 8, NULL, NULL, 2, NULL, 0, 'Spray a 9 X 12 inch pan with some oil. Place the rolls side by side.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92E68051809C1748B200BC09CA95B345', X'669E8364FE28494BB4C4686078EC951F', X'669E8364FE28494BB4C4686078EC951F', 9, NULL, NULL, 2, NULL, 0, 'Set the rolls aside, again in a warm place, for about 45-60 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EE261AB3EE06B449018C75635412FEF', X'669E8364FE28494BB4C4686078EC951F', X'669E8364FE28494BB4C4686078EC951F', 10, NULL, NULL, 2, NULL, 0, 'A few minutes before baking, preheat the oven to 350 degrees (if your rolls are rising in the oven, be sure you take them out before you preheat!)');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72B2FADBFF0912468B2D014278EE8696', X'669E8364FE28494BB4C4686078EC951F', X'669E8364FE28494BB4C4686078EC951F', 11, NULL, NULL, 2, NULL, 0, 'Bake the rolls for 30 minutes until golden brown. Remove and place on a rack to cool slightly before drizzling the glaze on.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCB3B47D75FBE045868C66879BD4511B', X'532094F2B3495F4CA680D688F5DEF8AC', X'532094F2B3495F4CA680D688F5DEF8AC', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients in a blender with ½ cup water and blend until very smooth. Try and make the glaze at least half an hour before you glaze the cinnamon buns to let it thicken a little.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE9EB71E2688AE4FB3643E34411F01B3', X'532094F2B3495F4CA680D688F5DEF8AC', X'532094F2B3495F4CA680D688F5DEF8AC', 1, NULL, NULL, 2, NULL, 0, 'Drizzle or brush on the glaze over still-warm rolls.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96FA8FDB2122E246899B2F1D5AB18F04', X'5D7613D357EBC44181FE7B060686B48E', X'5D7613D357EBC44181FE7B060686B48E', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FE487CAA1817F4886A90251CE4FCC64', X'5D7613D357EBC44181FE7B060686B48E', X'5D7613D357EBC44181FE7B060686B48E', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, whisk together the almond and whole wheat pastry flours with the baking soda, baking powder, and salt.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B271AB27774A044B4536AD96196AAFE', X'5D7613D357EBC44181FE7B060686B48E', X'5D7613D357EBC44181FE7B060686B48E', 2, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer or in any large bowl, mix the sugar with the oil, flax eggs, apple sauce, vanilla extract, and orange juice. Mix or whisk well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BCAA9711AC25A4DAB43631708D1FD35', X'5D7613D357EBC44181FE7B060686B48E', X'5D7613D357EBC44181FE7B060686B48E', 3, NULL, NULL, 2, NULL, 0, 'Add half the dry ingredients to the wet in two batches, mixing after every addition until the dry ingredients are thoroughly incorporated.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9624E7D28AC8FB4485F51AF4D0072685', X'5D7613D357EBC44181FE7B060686B48E', X'5D7613D357EBC44181FE7B060686B48E', 4, NULL, NULL, 2, NULL, 0, 'Prepare a standard 9 by 5 inch loaf pan by spraying or coating with cooking spray or oil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'064DE8063B0BC340A5955D5D58F7F24A', X'5D7613D357EBC44181FE7B060686B48E', X'5D7613D357EBC44181FE7B060686B48E', 5, NULL, NULL, 2, NULL, 0, 'Pour in the batter and even out the top with a spatula.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C0B8EC360AF144A90100F23CA7CE9D0', X'5D7613D357EBC44181FE7B060686B48E', X'5D7613D357EBC44181FE7B060686B48E', 6, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 50-55 minutes or until a toothpick inserted in the center comes out dry or with crumbs (not wet batter) sticking to it.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'552A3CE87746EC4CA8BB5938BFF25967', X'5D7613D357EBC44181FE7B060686B48E', X'5D7613D357EBC44181FE7B060686B48E', 7, NULL, NULL, 2, NULL, 0, 'Cool the loaf in the pan for about 30 minutes. Then slide out of the loaf pan (run a knife or spatula along the sides to help it along, if necessary) and place on a rack.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89125CBA05340044B8D73CFD8F5EFB10', X'5D7613D357EBC44181FE7B060686B48E', X'5D7613D357EBC44181FE7B060686B48E', 8, NULL, NULL, 2, NULL, 0, 'Make the marmalade glaze: In a small saucepan, heat the marmalade and the orange juice until the marmalade is warm and a little runny. Brush or spoon over the top of the loaf.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CBDFFE04AD7B349B2F6A6CD1D3202DD', X'5D7613D357EBC44181FE7B060686B48E', X'5D7613D357EBC44181FE7B060686B48E', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle on some chopped almonds for garnish.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4F6D5F8FDBBBE4894002017C49FC2B4', X'A52D9AAE114DF64EB6A8288E270351A4', X'A52D9AAE114DF64EB6A8288E270351A4', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees Fahrenheit. Oil and flour a a 9 X 5 loaf pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D45285A0E7A6DB4E8A4ACE346CDFCCE3', X'A52D9AAE114DF64EB6A8288E270351A4', X'A52D9AAE114DF64EB6A8288E270351A4', 1, NULL, NULL, 2, NULL, 0, 'Mix the streusel ingredients until the mixture resembles large breadcrumbs. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D20594A97D3544DB684BCB29D5B5394', X'A52D9AAE114DF64EB6A8288E270351A4', X'A52D9AAE114DF64EB6A8288E270351A4', 2, NULL, NULL, 2, NULL, 0, 'Mix the apple cider vinegar and almond milk and set aside for a couple of minutes to curdle.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65B795EAAF2A114C94241CDB55A0D87B', X'A52D9AAE114DF64EB6A8288E270351A4', X'A52D9AAE114DF64EB6A8288E270351A4', 3, NULL, NULL, 2, NULL, 0, 'In a bowl, mix the flour with the baking powder, baking soda, flaxmeal, salt and sugar.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D86497218268B43A7ADC8FAAE26087C', X'A52D9AAE114DF64EB6A8288E270351A4', X'A52D9AAE114DF64EB6A8288E270351A4', 4, NULL, NULL, 2, NULL, 0, 'In a larger bowl, whisk together the applesauce, nondairy milk, butter and vanilla extract.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5476747242BF34F941075ABB622F9D3', X'A52D9AAE114DF64EB6A8288E270351A4', X'A52D9AAE114DF64EB6A8288E270351A4', 5, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients to the wet in two batches, mixing thoroughly with a spatula after each addition.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06A1F36A87E7F148B610719EBE3D6CD6', X'A52D9AAE114DF64EB6A8288E270351A4', X'A52D9AAE114DF64EB6A8288E270351A4', 6, NULL, NULL, 2, NULL, 0, 'Fold in the cranberries and walnuts, if using.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E39C14A86894ED48A0C5D27254685CFB', X'A52D9AAE114DF64EB6A8288E270351A4', X'A52D9AAE114DF64EB6A8288E270351A4', 7, NULL, NULL, 2, NULL, 0, 'Scrape the batter into the loaf pan and smooth the top with the spatula. Sprinkle the streusel on evenly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFBAF54AD726924A9DD8889D7B439A32', X'A52D9AAE114DF64EB6A8288E270351A4', X'A52D9AAE114DF64EB6A8288E270351A4', 8, NULL, NULL, 2, NULL, 0, 'Place in the preheated oven and bake 1 hour and 20 minutes or until a toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB196CFBF4F0654CBCEA423D4C9CC56A', X'A52D9AAE114DF64EB6A8288E270351A4', X'A52D9AAE114DF64EB6A8288E270351A4', 9, NULL, NULL, 2, NULL, 0, 'Cool thoroughly on a rack before unmolding. Slice and serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0471BD2C428F9E4BBE609E7C92A42E53', X'9C709EA964ACAC47BD96377BF39EEE39', X'9C709EA964ACAC47BD96377BF39EEE39', 0, NULL, NULL, 2, NULL, 0, 'Make the bechamel sauce by heating the oil. Add the leeks and garlic, season with salt and pepper, and saute, stirring, until the leeks are soft, about five minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A56DB302B0D6C84CA16EA7C4B9614F3F', X'9C709EA964ACAC47BD96377BF39EEE39', X'9C709EA964ACAC47BD96377BF39EEE39', 1, NULL, NULL, 2, NULL, 0, 'Add the sausage and herbs and continue to stir-fry about two or three minutes. Add the flour, mix well, and stir-fry another two minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C67D1D75710D49458033D2DDF95CEBD3', X'9C709EA964ACAC47BD96377BF39EEE39', X'9C709EA964ACAC47BD96377BF39EEE39', 2, NULL, NULL, 2, NULL, 0, 'Add half the cashew milk and stir into the sausage mixture until it begins to thicken. Add the remaining cashew milk and mix well. Bring the sauce to a boil over medium heat and turn off.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BF8CFE5646E6645BE475E3EF5C2D1EE', X'1C85F8BAD6F95746BBC750093A9EFCFD', X'1C85F8BAD6F95746BBC750093A9EFCFD', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96085C7FC8FF674EA9977B1646642E62', X'1C85F8BAD6F95746BBC750093A9EFCFD', X'1C85F8BAD6F95746BBC750093A9EFCFD', 1, NULL, NULL, 2, NULL, 0, 'Spread a couple of ladles of the bechamel sauce on the bottom of a 12 X 8 inch baking pan, then layer five noodles over the sauce to cover the bottom of the pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8556C4029E21354FAF010A848AF0B8B3', X'1C85F8BAD6F95746BBC750093A9EFCFD', X'1C85F8BAD6F95746BBC750093A9EFCFD', 2, NULL, NULL, 2, NULL, 0, 'Spread half of the chopped spinach over the noodles, then spread a third of the remaining bechamel on top of the spinach. Sprinkle a third of the cheese and a third of the scallions on top, then layer on another five noodles.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE4DA866A23BBE47B8A4E689E8E008DF', X'1C85F8BAD6F95746BBC750093A9EFCFD', X'1C85F8BAD6F95746BBC750093A9EFCFD', 3, NULL, NULL, 2, NULL, 0, 'Repeat with the other half of the spinach, a third of the bechamel, a third of cheese and a third of scallions and layer on the last five noodles.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C4424122DF0BB4D9FE13958C41C9882', X'1C85F8BAD6F95746BBC750093A9EFCFD', X'1C85F8BAD6F95746BBC750093A9EFCFD', 4, NULL, NULL, 2, NULL, 0, 'Ladle on the remaining bechamel on top of the noodles, sprinkle on the remaining scallions and cheese, and cover with aluminum foil. If you plan on baking this the next day, you can cover tightly with cling wrap at this time and refrigerate overnight. The next day, before baking, remove the cling wrap and replace with foil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5EF1ACCD001AB4FAA99CCAFC1309DD6', X'1C85F8BAD6F95746BBC750093A9EFCFD', X'1C85F8BAD6F95746BBC750093A9EFCFD', 5, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 45 minutes, then remove the foil cover and continue baking another 15 minutes until golden on top.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34477A35BE3A1345A088413BCEA442F7', X'1C85F8BAD6F95746BBC750093A9EFCFD', X'1C85F8BAD6F95746BBC750093A9EFCFD', 6, NULL, NULL, 2, NULL, 0, 'Let stand for five minutes, then cut and serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF9F0ADC634276488081AA979923DAD4', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', 0, NULL, NULL, 2, NULL, 0, 'Make the cheesy sauce by combining all ingredients and blending into a really smooth paste. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09B829B02E6EF24C8A79F57310FB7F92', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', 1, NULL, NULL, 2, NULL, 0, 'Place the diced potatoes in a microwave-safe dish and add 1 tbsp water. Cover and nuke on high for five minutes or until potatoes are tender.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D708D4A047E2345B3AC879785EDA30A', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFF7C7A56837FC46BEC1265E1F2A7D20', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', 3, NULL, NULL, 2, NULL, 0, 'In a large skillet, heat the oil. You can do this in a 10-inch cast-iron skillet or any skillet that can go from the stove to the oven, to reduce cleanup.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6BEDD8F92A8DE459F1E5D291FE2DB05', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', 4, NULL, NULL, 2, NULL, 0, 'Add the onions, garlic and parsley to the oil. Season with salt and ground black pepper and saute, stirring, for five minutes over medium heat or until the onions are soft.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D97864F059856A4FB521181C6F37C65F', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', 5, NULL, NULL, 2, NULL, 0, 'Add the sausage and potatoes and continue to cook, about five minutes. Check seasoning and add more pepper and salt if needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58032876E44B0E4C8EF6BAAF5DD27F27', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', 6, NULL, NULL, 2, NULL, 0, 'If you are using a cast iron or a stove-to-oven skillet, arrange the potato mixture in an even layer and pour the cheesy sauce over it evenly. If using a baking dish, like I did, transfer the potato sausage mixture to the baking dish and pour the cheesy sauce on top.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13E04F0E458FDE4B973906DD9B12A29D', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', 7, NULL, NULL, 2, NULL, 0, 'Transfer the skillet or baking dish to the oven and bake 30 minutes or until the top starts to lightly brown. Switch to the broil function on your oven and broil for another five minutes on the highest setting, or until gold-brown spots appear on the potatoes and cheesy sauce.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98D787C806508549A178CEDD68479D58', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', X'F2C81F3B7CB6C744AAAEEAFB32E042A9', 8, NULL, NULL, 2, NULL, 0, 'Remove from the oven, garnish with more parsley if you wish, and serve hot or at room temperature.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0463740AFE793E419457A44FE429135F', X'F6143ED7D8A71D45A5E4292CE1BB587D', X'F6143ED7D8A71D45A5E4292CE1BB587D', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a skillet. Add the garlic and saute 30 seconds until fragrant, then add the yellow squash. Season with salt and pepper and saute for about two to three minutes until the squash starts to soften.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'700D6B3B5363724F8CD5A03BE8CAD5E8', X'F6143ED7D8A71D45A5E4292CE1BB587D', X'F6143ED7D8A71D45A5E4292CE1BB587D', 1, NULL, NULL, 2, NULL, 0, 'Add the rice and herbs to the skillet and stir to mix everything. Add the coconut milk, let it cook until the liquid begins to bubble at the corners, then place a tight lid on the skillet and cook 20 minutes without disturbing.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44574C31A1CDB44389F502855D376A7B', X'F6143ED7D8A71D45A5E4292CE1BB587D', X'F6143ED7D8A71D45A5E4292CE1BB587D', 2, NULL, NULL, 2, NULL, 0, 'While the rice is cooking, preheat the oven to 350 degrees. Also blend the cashews with 1 ½ cups water (or, for a creamier consistency, you can also use any nondairy milk)');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'586E3101B4E4A048982E251F1D735D1C', X'F6143ED7D8A71D45A5E4292CE1BB587D', X'F6143ED7D8A71D45A5E4292CE1BB587D', 3, NULL, NULL, 2, NULL, 0, 'Once the rice is done, stir in the herbs along with the cashew cream and add more salt and ground black pepper if you wish. If you are using a baking dish, at this point transfer the whole thing into the baking dish.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EA9C6F5CF140E4BA1B34180DBBCFAE9', X'F6143ED7D8A71D45A5E4292CE1BB587D', X'F6143ED7D8A71D45A5E4292CE1BB587D', 4, NULL, NULL, 2, NULL, 0, 'Cover tightly with aluminum foil (or an oven-safe lid) and bake 20 minutes. Then remove the foil or lid and continue cooking another 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F2D7026B6D6264AB64EB66BBB9B9FC4', X'F6143ED7D8A71D45A5E4292CE1BB587D', X'F6143ED7D8A71D45A5E4292CE1BB587D', 5, NULL, NULL, 2, NULL, 0, 'Serve hot or warm.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FFF4ABD062242408844311B8BBA7AA6', X'C8F11B2959C7784189A2EC6E30061945', X'C8F11B2959C7784189A2EC6E30061945', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6ABB09B104B8A4B9224225C2E12852B', X'C8F11B2959C7784189A2EC6E30061945', X'C8F11B2959C7784189A2EC6E30061945', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil and add the onions and garlic along with some salt and ground black pepper. Saute for a couple of minutes until the onion start to brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB9999C1F5B3624EBE35625F1C41FC5E', X'C8F11B2959C7784189A2EC6E30061945', X'C8F11B2959C7784189A2EC6E30061945', 2, NULL, NULL, 2, NULL, 0, 'Add the thyme and vegan sausage and continue to saute a couple more minutes. Add the hash browns and stir to mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54265B16C482A046A02B83F1C9BE78E0', X'C8F11B2959C7784189A2EC6E30061945', X'C8F11B2959C7784189A2EC6E30061945', 3, NULL, NULL, 2, NULL, 0, 'Turn off the heat and stir in the cream of mushroom soup and the mozzarella shreds. Reserve some of the mozzarella shreds to sprinkle over the top, if you wish. Before you sprinkle the mozzarella over the top, make sure you check for seasoning and add more salt and pepper to your taste. (If you aren''t using a cast-iron skillet, coat a 9 X 12 baking dish with cooking spray and turn the mixture into the dish, then sprinkle the remaining mozzarella over the top.)');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDDD82CCC41ED64380C5DA17EBE3B215', X'C8F11B2959C7784189A2EC6E30061945', X'C8F11B2959C7784189A2EC6E30061945', 4, NULL, NULL, 2, NULL, 0, 'Place the skillet or the baking dish into the oven and bake 45 minutes until the cheese is all bubbly and golden.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47632CD8C72CCC49A36B4F25B0D3E483', X'C8F11B2959C7784189A2EC6E30061945', X'C8F11B2959C7784189A2EC6E30061945', 5, NULL, NULL, 2, NULL, 0, 'Remove from the oven and serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59FBE005B8BB1C469B4878BB44EEDEFC', X'977AC7274731D4478EA2CBD74ED24608', X'977AC7274731D4478EA2CBD74ED24608', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450 degrees. Bake the croissants, cut side down, for five minutes. Remove from the oven and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58622D2FDCA2934FA18D46D56BE9D7AF', X'977AC7274731D4478EA2CBD74ED24608', X'977AC7274731D4478EA2CBD74ED24608', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil. Add the garlic and saute for a few seconds, then add the vegan sausage. Stir-fry for a couple of minutes until the sausage starts to brown. Add the zucchini and bell peppers and the oregano or thyme. Season with pepper and salt and stir-fry for 2-3 minutes until they soften a little. Turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42A42BFDB7E782498950DB4A1165C50F', X'977AC7274731D4478EA2CBD74ED24608', X'977AC7274731D4478EA2CBD74ED24608', 2, NULL, NULL, 2, NULL, 0, 'Place the cashews in a blender with 4 cups of water and process into a very smooth milk. If you are using a nondairy milk, you can add the milk directly to the blender. Add the onion flakes, garlic powder, nutritional yeast, nutmeg, and salt and ground black pepper. Mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8976425AB63D4444B9A5187062A6B05A', X'977AC7274731D4478EA2CBD74ED24608', X'977AC7274731D4478EA2CBD74ED24608', 3, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'969AB97337049E44A9C998A537386306', X'977AC7274731D4478EA2CBD74ED24608', X'977AC7274731D4478EA2CBD74ED24608', 4, NULL, NULL, 2, NULL, 0, 'Butter or oil a baking dish (I used an oval baking dish that''s roughly 12 X 9 inches and 3 inches deep and it was the perfect size for this dish).');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B35AE1DDBD23643961B47E7DC3DA610', X'977AC7274731D4478EA2CBD74ED24608', X'977AC7274731D4478EA2CBD74ED24608', 5, NULL, NULL, 2, NULL, 0, 'Place the croissants in the baking dish. I lay some down and make the others stand. It may be a little crowded but that''s okay. Scatter on the vegan cheese, reserving some to sprinkle on the top.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B4AE3B33F62FD4A8AA6D98E95E8E5C1', X'977AC7274731D4478EA2CBD74ED24608', X'977AC7274731D4478EA2CBD74ED24608', 6, NULL, NULL, 2, NULL, 0, 'Spread the cooked sausage and veggies evenly over the croissants and cheese.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F78719A3EEB5044182D0BB8F71D64CD1', X'977AC7274731D4478EA2CBD74ED24608', X'977AC7274731D4478EA2CBD74ED24608', 7, NULL, NULL, 2, NULL, 0, 'Now pour in the cheesy cashew milk evenly. Press down on the croissants with a ladle to submerge them so they soak up all that cheesy milk. You can either bake this rightaway, or, for the best results, let it stand at least 15 minutes before baking. I like to slide a baking sheet under the dish to make it easy to take it in and out of the oven.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1098BA7288C0474596ECB2429EA70CD6', X'977AC7274731D4478EA2CBD74ED24608', X'977AC7274731D4478EA2CBD74ED24608', 8, NULL, NULL, 2, NULL, 0, 'Bake 45 minutes until the casserole is golden and bubbly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62586CCCBE662E4CA12A88320D0F7C0C', X'977AC7274731D4478EA2CBD74ED24608', X'977AC7274731D4478EA2CBD74ED24608', 9, NULL, NULL, 2, NULL, 0, 'Let stand 5-10 minutes, then serve!');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B3163D8B783474480E1B65A1263C0E0', X'32C0A052BC502048B7CA4CAC2E27E7A4', X'32C0A052BC502048B7CA4CAC2E27E7A4', 0, NULL, NULL, 2, NULL, 0, 'In a skillet, heat 1 tsp olive oil. Add the garlic, sauté for a few seconds. Don''t let it brown. Add the zucchini, tomatoes and onions. Season with ground black pepper and Italian seasoning. Saute for five minutes or until zucchini is soft but not mushy. If there is liquid in the pan, cook over high heat for a few minutes until most of it has evaporated. Turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C11C84D69BA58438330D5900FEC0FD5', X'32C0A052BC502048B7CA4CAC2E27E7A4', X'32C0A052BC502048B7CA4CAC2E27E7A4', 1, NULL, NULL, 2, NULL, 0, 'While the zucchini is cooking, place the soaked, drained cashews in a blender along with 2 cups water, basil, lemon juice, salt and jalapeno. Add salt to taste and blend until very smooth.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A4936E1524D79479FB9D40D79517AE6', X'32C0A052BC502048B7CA4CAC2E27E7A4', X'32C0A052BC502048B7CA4CAC2E27E7A4', 2, NULL, NULL, 2, NULL, 0, 'Make the mashed potatoes by mashing together the boiled, peeled potatoes with the remaining 1 tbsp of extra virgin olive oil, salt, sage, and lemon zest.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'949273B822E66E489E8703127A559B32', X'32C0A052BC502048B7CA4CAC2E27E7A4', X'32C0A052BC502048B7CA4CAC2E27E7A4', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, mix the zucchini-tomato mixture, bread, and half the mashed potatoes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FD49340083D024DA9FDCBACFA9F4214', X'32C0A052BC502048B7CA4CAC2E27E7A4', X'32C0A052BC502048B7CA4CAC2E27E7A4', 4, NULL, NULL, 2, NULL, 0, 'Place them in a 9 X 12 inch baking dish and spread evenly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8042EE4D2F70EB4DAD4A75743A3FB0D6', X'32C0A052BC502048B7CA4CAC2E27E7A4', X'32C0A052BC502048B7CA4CAC2E27E7A4', 5, NULL, NULL, 2, NULL, 0, 'Pour on the basil cream evenly, and toss or press down slightly to make sure the bread cubes are immersed in the cream.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'846183856E8BD44FAFE52AEFCA76E69B', X'32C0A052BC502048B7CA4CAC2E27E7A4', X'32C0A052BC502048B7CA4CAC2E27E7A4', 6, NULL, NULL, 2, NULL, 0, 'Scatter on the remaining mashed potatoes over the casserole.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A43D11DC288C544B638376AADA22354', X'32C0A052BC502048B7CA4CAC2E27E7A4', X'32C0A052BC502048B7CA4CAC2E27E7A4', 7, NULL, NULL, 2, NULL, 0, 'Cover tightly with a cling wrap and place in the refrigerator overnight for the flavors to merge and for the bread to get thoroughly soaked.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACF4AF40AF0AA344BC62BC67B7239329', X'32C0A052BC502048B7CA4CAC2E27E7A4', X'32C0A052BC502048B7CA4CAC2E27E7A4', 8, NULL, NULL, 2, NULL, 0, 'When you''re ready to bake, preheat the oven to 350 degrees');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75C1457DEBDFE742AC7F57ED318EA406', X'32C0A052BC502048B7CA4CAC2E27E7A4', X'32C0A052BC502048B7CA4CAC2E27E7A4', 9, NULL, NULL, 2, NULL, 0, 'Place the baking dish in the oven and cook 40 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FBADAE58A55BA4DAD5FA6C73FA6D829', X'32C0A052BC502048B7CA4CAC2E27E7A4', X'32C0A052BC502048B7CA4CAC2E27E7A4', 10, NULL, NULL, 2, NULL, 0, 'After 40 minutes, turn on the custom broil to 450 degrees. Broil for 8-10 minutes or until the tops of the bread and mashed potatoes turn golden brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B08A798C0738E40AE9E369D901C637E', X'32C0A052BC502048B7CA4CAC2E27E7A4', X'32C0A052BC502048B7CA4CAC2E27E7A4', 11, NULL, NULL, 2, NULL, 0, 'Remove from the oven, garnish with basil, let stand about 10 minutes. Then scoop out and serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9067F11DFE7566428645614C794BAF03', X'8D2802BD0FE85043B5EB8EC952764CCE', X'8D2802BD0FE85043B5EB8EC952764CCE', 0, NULL, NULL, 2, NULL, 0, 'Set the Instant Pot to the saute function, add the oil and when it heats up, add the shallots or leeks, celery, carrots and garlic with some salt and ground black pepper. Saute, stirring frequently, a couple of minutes until the onions begin to soften.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DCB448D478013439B237BA8F5177FCB', X'8D2802BD0FE85043B5EB8EC952764CCE', X'8D2802BD0FE85043B5EB8EC952764CCE', 1, NULL, NULL, 2, NULL, 0, 'Add the rosemary and potatoes with the vegan sausage, making sure you crumble the sausage into little bits or run it once through the food processor before adding. Mix well, and add the beans and 3 cups of vegetable stock.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76802228E0667448A87FDB5800E81908', X'8D2802BD0FE85043B5EB8EC952764CCE', X'8D2802BD0FE85043B5EB8EC952764CCE', 2, NULL, NULL, 2, NULL, 0, 'Cover the Instant Pot and set it to the `chili` function, which is 30 minutes of pressure cooking time. At the end of cooking, you can either wait for the pressure to release, or release it manually after 10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'620FD4059B827C468F74B827F1F653D3', X'8D2802BD0FE85043B5EB8EC952764CCE', X'8D2802BD0FE85043B5EB8EC952764CCE', 3, NULL, NULL, 2, NULL, 0, 'Place the cashews in a blender with the remaining ½ cup of vegetable stock. Blitz into a smooth cream.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B629A2047C10544B02459899AF6B7F1', X'8D2802BD0FE85043B5EB8EC952764CCE', X'8D2802BD0FE85043B5EB8EC952764CCE', 4, NULL, NULL, 2, NULL, 0, 'Put the IP back at the saute function, add the cashew cream, and let it warm through. Check at this time if you need more ground black pepper.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36FED326E337104A904CB99F8C8EFDB5', X'8D2802BD0FE85043B5EB8EC952764CCE', X'8D2802BD0FE85043B5EB8EC952764CCE', 5, NULL, NULL, 2, NULL, 0, 'The filling should be pretty thick, but if you want to thin it out, add more stock.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46ABE8CD6F394B418FCE068038F9C80D', X'8D2802BD0FE85043B5EB8EC952764CCE', X'8D2802BD0FE85043B5EB8EC952764CCE', 6, NULL, NULL, 2, NULL, 0, 'Make the puff pastry topping by first preheating your oven to 375 degrees. Unroll the puff pastry sheet and cut it into pretty shapes using a cookie cutter. You can make one large disc that will sit on top of your ramekin, or smaller shapes, like the hearts I made.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8312ECB22E7DDF4ABFCF9AA26496E752', X'8D2802BD0FE85043B5EB8EC952764CCE', X'8D2802BD0FE85043B5EB8EC952764CCE', 7, NULL, NULL, 2, NULL, 0, 'Place the puff pastry shapes on a baking sheet and bake in the preheated oven 15 minutes or until golden and puffy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'155305F574F6CC41862FA07311BA8F85', X'8D2802BD0FE85043B5EB8EC952764CCE', X'8D2802BD0FE85043B5EB8EC952764CCE', 8, NULL, NULL, 2, NULL, 0, 'Ladle the pot pie filling into ramekins, top with the puff pastry, and serve piping hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAB1D4ED53D89D4D8B14D76DC13B731E', X'A0E973EDDFA50E4FA858ADEC950144E9', X'A0E973EDDFA50E4FA858ADEC950144E9', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8F5752B167ED44C987D45683902C889', X'A0E973EDDFA50E4FA858ADEC950144E9', X'A0E973EDDFA50E4FA858ADEC950144E9', 1, NULL, NULL, 2, NULL, 0, 'In a cast-iron or other skillet, heat the olive oil. Add the garlic and onions and season with salt and pepper. Saute until the onions turn soft.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E13A8BBBFFE3544D87C004C3A34261F8', X'A0E973EDDFA50E4FA858ADEC950144E9', X'A0E973EDDFA50E4FA858ADEC950144E9', 2, NULL, NULL, 2, NULL, 0, 'Add the asparagus and sage or mint, saute for 2-3 minutes, then add the chorizo and continue to cook, stirring frequently, for another five minutes. Turn off heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A54123F2B9477428DF45B61E2294696', X'A0E973EDDFA50E4FA858ADEC950144E9', X'A0E973EDDFA50E4FA858ADEC950144E9', 3, NULL, NULL, 2, NULL, 0, 'Place the cashews, tofu, garlic powder, salt and ground black pepper to taste in a blender along with ½ cup water. Blend until very smooth.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B5E6A738D47DD4FAA4B675497062E15', X'A0E973EDDFA50E4FA858ADEC950144E9', X'A0E973EDDFA50E4FA858ADEC950144E9', 4, NULL, NULL, 2, NULL, 0, 'Pour the tofu into the chorizo mixture and mix well. You can simply bake the quiche in the cast iron skillet (or any oven-safe skillet) at this point, or pour it into a pie or tart plate.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D35B7D042672D94393D7665ED96EF1BD', X'A0E973EDDFA50E4FA858ADEC950144E9', X'A0E973EDDFA50E4FA858ADEC950144E9', 5, NULL, NULL, 2, NULL, 0, 'Bake for 40 minutes. Let it stand 10 minutes, then serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7831033076780F43AAFB5E85B4324F28', X'1336AF5B48F3814AA021CB9DF4E3A2EF', X'1336AF5B48F3814AA021CB9DF4E3A2EF', 0, NULL, NULL, 2, NULL, 0, 'Mix the yeast, sugar and water in a large bowl or in the bowl of a stand mixer and let it stand five minutes or until the yeast is frothy and bubbly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21B0CFEDDE92DF4DABBCDB5BAF31D0AB', X'1336AF5B48F3814AA021CB9DF4E3A2EF', X'1336AF5B48F3814AA021CB9DF4E3A2EF', 1, NULL, NULL, 2, NULL, 0, 'Add the orange juice, aquafaba, oil, flour and salt and 2 cups of flour and mix until the dough comes together. Continue to add flour, ¼ cup at a time, until you have a smooth, pliable dough that doesn''t stick. Continue to knead a couple more minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF820927306F3541826A2BF03F025AC8', X'1336AF5B48F3814AA021CB9DF4E3A2EF', X'1336AF5B48F3814AA021CB9DF4E3A2EF', 2, NULL, NULL, 2, NULL, 0, 'Form the dough into a smooth ball and place in an oiled bowl, turning the dough over once to ensure it is coated with oil. Cover tightly with cling wrap and set aside in a refrigerator overnight. If you want to make the rolls the same day, set the dough to rise in a warm place for 1-2 hours or until doubled.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB2D8DA026E0364FBD690A43E77277F0', X'1336AF5B48F3814AA021CB9DF4E3A2EF', X'1336AF5B48F3814AA021CB9DF4E3A2EF', 3, NULL, NULL, 2, NULL, 0, 'In the morning, or when ready to shape the rolls, punch down the dough and roll into a rectangle 12 inches long and six inches wide.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57FC9AE049D7EA4EACC3E40BAA23504F', X'1336AF5B48F3814AA021CB9DF4E3A2EF', X'1336AF5B48F3814AA021CB9DF4E3A2EF', 4, NULL, NULL, 2, NULL, 0, 'Spread the orange marmalade for the filling on the surface of the dough. Starting along the long seam, roll the dough as tightly as possible until you have a cylinder 12 inches in length.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2016602609CBAC41842927FF9F25DCCD', X'1336AF5B48F3814AA021CB9DF4E3A2EF', X'1336AF5B48F3814AA021CB9DF4E3A2EF', 5, NULL, NULL, 2, NULL, 0, 'Cut the dough into 10-12 pieces, equal in size if possible.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBF3916964A39E4C86C1835724796A40', X'1336AF5B48F3814AA021CB9DF4E3A2EF', X'1336AF5B48F3814AA021CB9DF4E3A2EF', 6, NULL, NULL, 2, NULL, 0, 'Place each roll, cut side up, in a 10-inch round or square pan. I used an 8-inch cake pan and things were a bit tight in there which made the rolls rise rather high, but you can use an 9-inch pan if that''s your only option. A 10-inch cast-iron skillet would work well too.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6202B50A7BF5B54E8A2678A6CF438DBC', X'1336AF5B48F3814AA021CB9DF4E3A2EF', X'1336AF5B48F3814AA021CB9DF4E3A2EF', 7, NULL, NULL, 2, NULL, 0, 'Cover the rolls with cling wrap or a kitchen towel and set aside to rise in a warm place for an hour until they are puffy and risen.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DABFBB372B56924F8D557B1AE8B4F41A', X'1336AF5B48F3814AA021CB9DF4E3A2EF', X'1336AF5B48F3814AA021CB9DF4E3A2EF', 8, NULL, NULL, 2, NULL, 0, 'About 20 minutes before baking, preheat your oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4A3D8B18423F14C9C3E96C3182B3B68', X'1336AF5B48F3814AA021CB9DF4E3A2EF', X'1336AF5B48F3814AA021CB9DF4E3A2EF', 9, NULL, NULL, 2, NULL, 0, 'To bake, place the orange rolls in the oven and bake for 28-30 minutes or until browned on top.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDAAD6269888D34BA03646383BC5FEB5', X'38B2B470511395499B7E15B6B02CC936', X'38B2B470511395499B7E15B6B02CC936', 0, NULL, NULL, 2, NULL, 0, 'Mix all the glaze ingredients with a whisk. If using vegan butter, make sure you melt it first.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F94000B9D5BC994885891545AB8357BB', X'38B2B470511395499B7E15B6B02CC936', X'38B2B470511395499B7E15B6B02CC936', 1, NULL, NULL, 2, NULL, 0, 'Drizzle the glaze over the rolls and serve warm.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D022E3755EFBD468AA49529F860F782', X'E9FE364C6F35464FBE7EA9363AD2C1D9', X'E9FE364C6F35464FBE7EA9363AD2C1D9', 0, NULL, NULL, 2, NULL, 0, 'Place the raw cashews in a high-powered blender with ¾ cup of water and blitz until very smooth.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4F7B275259E9348A739F27E6B4218D3', X'E9FE364C6F35464FBE7EA9363AD2C1D9', X'E9FE364C6F35464FBE7EA9363AD2C1D9', 1, NULL, NULL, 2, NULL, 0, 'Place the potatoes in a microwave-safe bowl and cover with water. Cover with a microwave-safe lid or dish and zap 10-15 minutes on high or until a knife inserted in the center of a potato goes cleanly through. Since different microwaves work differently, I''d recommend starting out with 10 minutes and adding more time if needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DE3EBC408A8524C8BA1847DF9431C93', X'E9FE364C6F35464FBE7EA9363AD2C1D9', X'E9FE364C6F35464FBE7EA9363AD2C1D9', 2, NULL, NULL, 2, NULL, 0, 'When the potatoes are cooked, take the bowl out of the microwave, being very careful not to burn yourself by spilling the hot water, and drain the potatoes. When cool enough to handle, peel and mash with a potato masher.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15A9E728A4DE94468B8C1714F8AC5E05', X'E9FE364C6F35464FBE7EA9363AD2C1D9', X'E9FE364C6F35464FBE7EA9363AD2C1D9', 3, NULL, NULL, 2, NULL, 0, 'Add the melted butter, salt and ground black pepper to taste, and half the cashew cream. Mix well, then, using a handheld mixer, whip the potatoes for about two minutes, stopping frequently to mix with a spatula. If doing this in a stand mixer, use the whisk attachment. Add more of the cashew cream as you mix, until you get the consistency you like.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8918B9F62CD1445BE11310DB6B41877', X'E9FE364C6F35464FBE7EA9363AD2C1D9', X'E9FE364C6F35464FBE7EA9363AD2C1D9', 4, NULL, NULL, 2, NULL, 0, 'Check seasoning and add more salt and ground black pepper if needed. Scrape into a bowl and season with chives or parsley.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D79F7C6B1DB7E42BA1AB6F2817DE375', X'E9FE364C6F35464FBE7EA9363AD2C1D9', X'E9FE364C6F35464FBE7EA9363AD2C1D9', 5, NULL, NULL, 2, NULL, 0, 'Serve with mushroom gravy (link in recipe notes below)');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEF23D18228CEE4B8F80C37E0A628678', X'CCCDEBDBD89357459CAFD8D06648A183', X'CCCDEBDBD89357459CAFD8D06648A183', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil and add the onions or shallots. Season with salt and ground black pepper to taste. Cook, stirring frequently, over medium heat until the onions caramelize and turn golden-brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F90BA07A7A11EF43AA97159F9933E06D', X'CCCDEBDBD89357459CAFD8D06648A183', X'CCCDEBDBD89357459CAFD8D06648A183', 1, NULL, NULL, 2, NULL, 0, 'Add the mushrooms with the apple cider vinegar and cook, stirring frequently, until the moisture has evaporated.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'579CB43BD62FF247B4985878EF46E57F', X'CCCDEBDBD89357459CAFD8D06648A183', X'CCCDEBDBD89357459CAFD8D06648A183', 2, NULL, NULL, 2, NULL, 0, 'Add the flour and cook, stirring, 2-3 minutes, until the flour is completely absorbed by the veggies.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1B2B0F3B4C0DF4990089E93E464A020', X'CCCDEBDBD89357459CAFD8D06648A183', X'CCCDEBDBD89357459CAFD8D06648A183', 3, NULL, NULL, 2, NULL, 0, 'Add two cups of the stock, whisking as you add it, until you have a smooth sauce. Add the remaining stock, continuing to whisk.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68187593FF43114CA36F7CCF12098988', X'CCCDEBDBD89357459CAFD8D06648A183', X'CCCDEBDBD89357459CAFD8D06648A183', 4, NULL, NULL, 2, NULL, 0, 'Stir, add the soy sauce and sage, and continue to cook for another 15-20 minutes over high heat or until the gravy is reduced by almost half and is thick and creamy. Stir in the cashew cream if using');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69605D1E40AADE469D3F07C5BFD373D6', X'CCCDEBDBD89357459CAFD8D06648A183', X'CCCDEBDBD89357459CAFD8D06648A183', 5, NULL, NULL, 2, NULL, 0, 'Turn off the heat and, at this point, you can strain the sauce for serving. Or, if you''d rather, serve it as is. The bits of mushroom and onion in the gravy are delicious.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C49EF6E2FE827E41AD41802A434FCF62', X'55F13B02915EEA4BBDA50979F4E6867C', X'55F13B02915EEA4BBDA50979F4E6867C', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to boil while you prep the green beans. When the water boils, add the green beans, wait six minutes, then drain the beans and reserve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AB354209AB556499299CBD3650BA6D7', X'55F13B02915EEA4BBDA50979F4E6867C', X'55F13B02915EEA4BBDA50979F4E6867C', 1, NULL, NULL, 2, NULL, 0, 'While the beans cook, place the cashews, almond milk, chipotle pepper, 1 tbsp of fried onions and salt in a blender. Blitz until smooth.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8424B3EDBC602458759A66A65F796A1', X'55F13B02915EEA4BBDA50979F4E6867C', X'55F13B02915EEA4BBDA50979F4E6867C', 2, NULL, NULL, 2, NULL, 0, 'Place the green beans in a bowl and drizzle the chipotle sauce over it. Mix well and garnish with remaining fried onions. Serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BEE12104C2ABD43A2BA64536CAAA15E', X'75667276D7E45149B74999191507CF62', X'75667276D7E45149B74999191507CF62', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 degrees F.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8761D815CA7404D8923F05614DFE037', X'75667276D7E45149B74999191507CF62', X'75667276D7E45149B74999191507CF62', 1, NULL, NULL, 2, NULL, 0, 'Mix the nondairy milk and the vinegar and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16C6D6E8863DBF4CACDFC0FCEC74534E', X'75667276D7E45149B74999191507CF62', X'75667276D7E45149B74999191507CF62', 2, NULL, NULL, 2, NULL, 0, 'Place the flour, salt, sugar, baking powder and baking soda in a bowl and whisk them together to mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B249DADDFB3A5F48AF6BB74162B2E48E', X'75667276D7E45149B74999191507CF62', X'75667276D7E45149B74999191507CF62', 3, NULL, NULL, 2, NULL, 0, 'Add the cubes of butter and using a pastry cutter or a fork, cut the butter into the flour until the mixture is grainy with a few pea-size pieces of butter.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'932CB31428F65B4081DF313A1EC5AB2A', X'75667276D7E45149B74999191507CF62', X'75667276D7E45149B74999191507CF62', 4, NULL, NULL, 2, NULL, 0, 'Add the milk-vinegar mixture, a little at a time, stirring with a fork to ensure everything mixes. Don''t overmix because you don''t want to promote the formation of gluten which will make your biscuits hard rather than crumbly and flaky.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99FFAC69B8283C43AAB01BDF88A3B095', X'75667276D7E45149B74999191507CF62', X'75667276D7E45149B74999191507CF62', 5, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper. Using an ice cream scoop, or with a spoon, scoop out the batter in mounds on the baking sheet to make eight biscuits. You can also make 10 smaller biscuits. Make sure there''s at least an inch of space between the biscuits.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CD33FE54C1ABC458E2D9CE0C84DEE6F', X'75667276D7E45149B74999191507CF62', X'75667276D7E45149B74999191507CF62', 6, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes or until the tops are golden brown and a toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DACF851E1485C14480EA5B6C57FC9886', X'75667276D7E45149B74999191507CF62', X'75667276D7E45149B74999191507CF62', 7, NULL, NULL, 2, NULL, 0, 'Cool on a rack for five minutes, then serve hot or warm.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'034D1D7C7609614AB1E8787BBFE9C01E', X'6CE58CBA8E46AD49A465B33E51D68DB0', X'6CE58CBA8E46AD49A465B33E51D68DB0', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E49FDEAAA8CF9546A0E9B7EC155408FB', X'6CE58CBA8E46AD49A465B33E51D68DB0', X'6CE58CBA8E46AD49A465B33E51D68DB0', 1, NULL, NULL, 2, NULL, 0, 'Place the bread cubes on a baking sheet. Sprinkle on some salt and pepper and the ½ tsp each of sage and thyme. Spray with cooking spray and toss well to mix. Bake for 10 minutes or until the bread is quite dry.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE6466180BD074478C6DDBE5A1824F36', X'6CE58CBA8E46AD49A465B33E51D68DB0', X'6CE58CBA8E46AD49A465B33E51D68DB0', 2, NULL, NULL, 2, NULL, 0, 'On another baking sheet place the tater tots and bake them also for 10 minutes or until they are almost crisped up all the way but not quite.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FCC09571D874649A8FB8901FD3617B0', X'6CE58CBA8E46AD49A465B33E51D68DB0', X'6CE58CBA8E46AD49A465B33E51D68DB0', 3, NULL, NULL, 2, NULL, 0, 'In a skillet, heat the oil. Use a cast-iron skillet if you have one that can go from the stovetop into the oven.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96B8F22305C4AC4ABB32DB02B7D50DBC', X'6CE58CBA8E46AD49A465B33E51D68DB0', X'6CE58CBA8E46AD49A465B33E51D68DB0', 4, NULL, NULL, 2, NULL, 0, 'Add the carrots, celery, onions and garlic. Season with salt and pepper and saute until the veggies turn very soft, about 10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF4922754095474D800C7CB2CEEF4DFB', X'6CE58CBA8E46AD49A465B33E51D68DB0', X'6CE58CBA8E46AD49A465B33E51D68DB0', 5, NULL, NULL, 2, NULL, 0, 'Add the fresh herbs and mix them in. Add the vegetable stock and then the bread cubes and stir well to mix. Check seasoning and add more salt and pepper if needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E92582D81DF484885F51E80F90E9BCC', X'6CE58CBA8E46AD49A465B33E51D68DB0', X'6CE58CBA8E46AD49A465B33E51D68DB0', 6, NULL, NULL, 2, NULL, 0, 'If you''re not using an oven-safe skillet, transfer the stuffing to a casserole or baking dish. Cover with aluminum foil and bake 20 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6219DC9C424614438A5D49DBCEE95DBA', X'6CE58CBA8E46AD49A465B33E51D68DB0', X'6CE58CBA8E46AD49A465B33E51D68DB0', 7, NULL, NULL, 2, NULL, 0, 'Remove the skillet or baking dish from the oven and stir in the tater tots. Increase oven temperature to 425 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1DC1E5E4FE2CF49A0F92AB1D863E07A', X'6CE58CBA8E46AD49A465B33E51D68DB0', X'6CE58CBA8E46AD49A465B33E51D68DB0', 8, NULL, NULL, 2, NULL, 0, 'Place the skillet back in the oven for another 15 minutes, uncovered, or until the tater tots are crunchy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'916BA37127488247B7E03992AAD673D4', X'6CE58CBA8E46AD49A465B33E51D68DB0', X'6CE58CBA8E46AD49A465B33E51D68DB0', 9, NULL, NULL, 2, NULL, 0, 'Remove from the oven and let stand at least 10 minutes before serving. Handle hot skillets with care.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B102CC2A9524054F8C8816A77C3A5491', X'4038B7C1A35A3648A83DC64E7D0C77C4', X'4038B7C1A35A3648A83DC64E7D0C77C4', 0, NULL, NULL, 2, NULL, 0, 'Make the breadcrumb mixture by placing the breadcrumbs with the parsley, cheeze if using, and the salt and pepper with ½ tbsp oil in the food processor or a bowl and mixing well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7D9DA5582792D4599518D2FC73DCCA8', X'4038B7C1A35A3648A83DC64E7D0C77C4', X'4038B7C1A35A3648A83DC64E7D0C77C4', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41918B1C7E615C4A8CAEA5886BE3383E', X'4038B7C1A35A3648A83DC64E7D0C77C4', X'4038B7C1A35A3648A83DC64E7D0C77C4', 2, NULL, NULL, 2, NULL, 0, 'Heat 1 tbsp of the oil in a skillet (use an oven-proof skillet for a one-pot dish). Add the onions and garlic with some salt and pepper and saute, stirring frequently, until the onion is soft.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D023FF4F5FCE842BC4CA920C17A1AE4', X'4038B7C1A35A3648A83DC64E7D0C77C4', X'4038B7C1A35A3648A83DC64E7D0C77C4', 3, NULL, NULL, 2, NULL, 0, 'Add the cubed butternut squash along with the rosemary and the nutmeg. Cook, stirring frequently, about 7-8 minutes or until the squash is quite tender. You can cover the skillet to speed this, but check frequently to ensure nothing''s sticking to the bottom of the pan. Season with salt and pepper as needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA472F62D7D7E1469EAE74FC226241E9', X'4038B7C1A35A3648A83DC64E7D0C77C4', X'4038B7C1A35A3648A83DC64E7D0C77C4', 4, NULL, NULL, 2, NULL, 0, 'Prep a baking dish, if using one, by coating it with a thin layer of oil. Once the squash is cooked, ladle it out into the baking dish in an even layer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBC93FE92BADE14FAF11DC754FB20CB2', X'4038B7C1A35A3648A83DC64E7D0C77C4', X'4038B7C1A35A3648A83DC64E7D0C77C4', 5, NULL, NULL, 2, NULL, 0, 'Pour the coconut milk evenly over the squash cubes. Then top evenly with the breadcrumb mixture.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEBB838312176D448F9C3CB08B396982', X'4038B7C1A35A3648A83DC64E7D0C77C4', X'4038B7C1A35A3648A83DC64E7D0C77C4', 6, NULL, NULL, 2, NULL, 0, 'Bake the gratin until it is bubbling and the top is lightly golden. Serve hot or warm.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC3F8355909AC5488575CE0E8871657B', X'DB10B7CF398A984AA93F782B2DE7A8DD', X'DB10B7CF398A984AA93F782B2DE7A8DD', 0, NULL, NULL, 2, NULL, 0, 'Place the sweet potatoes on a baking sheet. Sprinkle with salt and ground black pepper and coat with a teaspoon of the olive oil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5583AB81198D8646BEA5A50424DC4821', X'DB10B7CF398A984AA93F782B2DE7A8DD', X'DB10B7CF398A984AA93F782B2DE7A8DD', 1, NULL, NULL, 2, NULL, 0, 'Bake in a preheated 450-degree Fahrenheit oven for 40 minutes. Remove and set aside to cool.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20AAD071023246469E4E99251F49CAC6', X'DB10B7CF398A984AA93F782B2DE7A8DD', X'DB10B7CF398A984AA93F782B2DE7A8DD', 2, NULL, NULL, 2, NULL, 0, 'Heat the remaining teaspoon of oil in a saucepan. Add the onions and garlic and ginger. Sprinkle some salt. Saute over medium heat until the onions turn translucent, about four to five minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62FD1F7D61809E43B6B1E1D782CA94A9', X'DB10B7CF398A984AA93F782B2DE7A8DD', X'DB10B7CF398A984AA93F782B2DE7A8DD', 3, NULL, NULL, 2, NULL, 0, 'Add the cayenne and garam masala. Stir to coat the onions, then add the chopped kale and cook, stirring, 2-3 minutes or until the kale has wilted and is tender.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A3644ECCF90A0468D5A30600EA722F5', X'DB10B7CF398A984AA93F782B2DE7A8DD', X'DB10B7CF398A984AA93F782B2DE7A8DD', 4, NULL, NULL, 2, NULL, 0, 'Add the coriander leaves and lemon juice, mix well, then turn off the heat. Check seasoning and add more salt if needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7057476A9933114DA646372C8C1753C6', X'DB10B7CF398A984AA93F782B2DE7A8DD', X'DB10B7CF398A984AA93F782B2DE7A8DD', 5, NULL, NULL, 2, NULL, 0, 'Cut each sweet potato down the middle, lengthwise. Scoop out half of the flesh with a spoon and place in a bowl. Mash the sweet potato mixture, then add the cooked kale mixture to it and mix well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E6CE898B0D47E4AAD032A55A81ED9BE', X'DB10B7CF398A984AA93F782B2DE7A8DD', X'DB10B7CF398A984AA93F782B2DE7A8DD', 6, NULL, NULL, 2, NULL, 0, 'Scoop back the mashed sweet potato and kale mixture into each of the sweet potato halves.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A71F1C3EC2FB849BAA2EB0E3D200DFD', X'DB10B7CF398A984AA93F782B2DE7A8DD', X'DB10B7CF398A984AA93F782B2DE7A8DD', 7, NULL, NULL, 2, NULL, 0, 'Place the sweet potato skins back on the baking sheet and bake in a preheated 450-degree oven for 15 minutes for the skins to crisp up.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4EBD56A3CA6D64ABC515404DDDDE9B5', X'DB10B7CF398A984AA93F782B2DE7A8DD', X'DB10B7CF398A984AA93F782B2DE7A8DD', 8, NULL, NULL, 2, NULL, 0, 'Serve hot, drizzled with some cinnamon cream. To make the cinnamon cashew cream, place all the ingredients in a blender and whiz until you have a really smooth cream.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F6E249FF14F91488EA80A47DD7CA22A', X'91CAEA0E5A910543AB77DAC0E7A824AB', X'91CAEA0E5A910543AB77DAC0E7A824AB', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees. Make the pecan topping by mixing the ingredients together until crumbly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E2497883BD27E41AD37ACEC1DD3CE11', X'91CAEA0E5A910543AB77DAC0E7A824AB', X'91CAEA0E5A910543AB77DAC0E7A824AB', 1, NULL, NULL, 2, NULL, 0, 'Make the sauce by heating the oil. Add the garlic and fresh herbs and saute for a few seconds, then add the flour and continue to cook until the flour is well mixed into the oil and forms small crumbs.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B659D3F891DB764CAA06DDACA1780C0D', X'91CAEA0E5A910543AB77DAC0E7A824AB', X'91CAEA0E5A910543AB77DAC0E7A824AB', 2, NULL, NULL, 2, NULL, 0, 'Add a cup of the nondairy milk, using a whisk to mix so lumps don''t form. Add the remaining milk and whisk it in as well. Add the nutmeg, salt, and ground black pepper to taste and bring to a boil. The sauce should thicken. Turn off the heat and add the orange juice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A47FB5D9448C04181649AD4F66C4D49', X'91CAEA0E5A910543AB77DAC0E7A824AB', X'91CAEA0E5A910543AB77DAC0E7A824AB', 3, NULL, NULL, 2, NULL, 0, 'Spray a casserole or baking dish with cooking spray. Shingle in half the sweet potato slices in a decorative pattern -- I like making concentric circles. Pour half the creamy sauce over the sweet potatoes, using a ladle.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CC5C8405C090143A443AE2011401164', X'91CAEA0E5A910543AB77DAC0E7A824AB', X'91CAEA0E5A910543AB77DAC0E7A824AB', 4, NULL, NULL, 2, NULL, 0, 'Shingle on the remaining sweet potato slices and pour the remaining sauce evenly over them.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE59F40A929C8744893B7CC798ADF52C', X'91CAEA0E5A910543AB77DAC0E7A824AB', X'91CAEA0E5A910543AB77DAC0E7A824AB', 5, NULL, NULL, 2, NULL, 0, 'Cover with aluminum foil, place on a baking sheet, and bake for 35 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33E8C3773B0F51418BABC63699FCD26E', X'91CAEA0E5A910543AB77DAC0E7A824AB', X'91CAEA0E5A910543AB77DAC0E7A824AB', 6, NULL, NULL, 2, NULL, 0, 'Remove the foil, scatter the pecan topping over the sweet potatoes, and continue baking, uncovered, for another 25 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'721D1E07E895B644B0B1E77F0A814787', X'91CAEA0E5A910543AB77DAC0E7A824AB', X'91CAEA0E5A910543AB77DAC0E7A824AB', 7, NULL, NULL, 2, NULL, 0, 'Let the scalloped potatoes stand on a rack at least 30 minutes before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'694534EEC7F4E542B58C78E9C58EB5F7', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01212BB8E2427F41B5E6F4305780E17A', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 1, NULL, NULL, 2, NULL, 0, 'In a very large bowl, mix 1 cup of the nondairy milk, rosemary, garlic, salt and ground black pepper to taste. Toss the sliced potatoes gently in the mixture to coat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D39A3A3F9F72C46A8A0110082A310E6', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 2, NULL, NULL, 2, NULL, 0, 'To make the bechamel, heat the olive oil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19F916972C115E47A0E6F6FC46AA2F5E', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 3, NULL, NULL, 2, NULL, 0, 'Add the spring onions with a pinch of salt and ground black pepper. Cook, stirring, a couple of minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0BFB350E549B0449524E3BD57619925', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 4, NULL, NULL, 2, NULL, 0, 'Add the all-purpose flour and cook the flour over medium-low heat, scraping it up if it starts to stick, about a minute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24D18CEE9647524E8F9094DDF5151B73', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 5, NULL, NULL, 2, NULL, 0, 'Add the remaining nondairy milk slowly, whisking with a wire whisk as you pour it in. Whisking will prevent any lumps from forming and you''ll have a smooth, silken bechamel.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DAFF34E31E651478EE73D13A7783B6F', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 6, NULL, NULL, 2, NULL, 0, 'Add the nutmeg and more salt and black pepper to taste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F69FE4729EE1E4CB26DCB8A7877B388', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 7, NULL, NULL, 2, NULL, 0, 'Stir frequently over medium heat until the mixture reaches a simmer and the sauce is thick enough to coat the back of a spoon.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F626918181F1A40BEC91546E8D6B974', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 8, NULL, NULL, 2, NULL, 0, 'Turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBB231C90C8087479A0AD76E54BE9C59', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 9, NULL, NULL, 2, NULL, 0, 'Brush or spray a 9-inch oval or square baking dish with extra-virgin olive oil. Pour any of the milk flavored with rosemary and garlic that hasn''t been absorbed by the potatoes into the dish.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1C603F413F49E4B834067BF6D6B4264', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 10, NULL, NULL, 2, NULL, 0, 'Arrange the potatoes in the dish, making them stand, until the potatoes are tightly packed in.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A897D710CC9E60418662CD44E6CD08E7', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 11, NULL, NULL, 2, NULL, 0, 'Now pour the bechamel over the potatoes evenly, holding back about ½ a cup.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8FA31CDDB2AB34C828CFF7347CDB68F', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 12, NULL, NULL, 2, NULL, 0, 'Cover the baking dish with aluminum foil. Place on a foil-lined baking sheet to catch any drips.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'885112E9A6924F4E8605F1D56D949334', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 13, NULL, NULL, 2, NULL, 0, 'Bake for an hour, then remove the aluminum foil from the baking dish. Pour the remaining bechamel over the potatoes and continue to bake another 30 minutes until golden and bubbly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30A73B39A82C254EA1366CE1B488D8CA', X'884EE78FD693AD4B80B2E55DE0A2A9F8', X'884EE78FD693AD4B80B2E55DE0A2A9F8', 14, NULL, NULL, 2, NULL, 0, 'Remove, let cool for 15 minutes, then serve garnished with more spring onions, if desired.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6A9EF72F522A84D901A5D068E2EC18F', X'B667B820A5765E408E459725E5E80438', X'B667B820A5765E408E459725E5E80438', 0, NULL, NULL, 2, NULL, 0, 'Place the wild rice in a saucepan with 4 cups water. Bring to a boil, lower heat to a simmer, cover and cook 45 minutes. Drain.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E3E828974C44B4FBF0A24807A9FD901', X'B667B820A5765E408E459725E5E80438', X'B667B820A5765E408E459725E5E80438', 1, NULL, NULL, 2, NULL, 0, 'If using dry mushrooms, soak them in 2 cups hot or boiling water for at least 30 minutes. Drain and reserve the stock.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD74409E6A8FB941A41A826A0913A00C', X'B667B820A5765E408E459725E5E80438', X'B667B820A5765E408E459725E5E80438', 2, NULL, NULL, 2, NULL, 0, 'Blend the cashews with ½ cup vegetable stock into a very smooth paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3ABCCAC6DDF4914D89AA12C94AE9448F', X'B667B820A5765E408E459725E5E80438', X'B667B820A5765E408E459725E5E80438', 3, NULL, NULL, 2, NULL, 0, 'Heat the oil in a saucepan. Add the onions, garlic and celery. Season with salt and pepper and saute until soft and translucent.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E75D2B44196A1444969864D3FD487D1F', X'B667B820A5765E408E459725E5E80438', X'B667B820A5765E408E459725E5E80438', 4, NULL, NULL, 2, NULL, 0, 'Add the mushrooms with the wine. Cook until the wine has evaporated, stirring frequently.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2478B489E92AC46863F8A7C2F9402BC', X'B667B820A5765E408E459725E5E80438', X'B667B820A5765E408E459725E5E80438', 5, NULL, NULL, 2, NULL, 0, 'Add the herbs and the vegetable stock and the reserved mushroom stock. Bring to a boil, cover and cook 10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BB2D73D3A640648BF16E6D76DBD6DB6', X'B667B820A5765E408E459725E5E80438', X'B667B820A5765E408E459725E5E80438', 6, NULL, NULL, 2, NULL, 0, 'Remove half the soup to a blender and blend until smooth. You can blend up to ¾th if you want less texture in the soup.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4728E2A0C5BA814AA41876E0EF888A44', X'B667B820A5765E408E459725E5E80438', X'B667B820A5765E408E459725E5E80438', 7, NULL, NULL, 2, NULL, 0, 'Return the blended soup to the pot with the rest, add the drained wild rice, and cook another five minutes. Season with salt and pepper to taste. Stir in the cashew cream, warm through, and serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCE7066907CEC244A8A8227A965C46BF', X'52D90C62721C1C479CBDEB7EE2ADE6D9', X'52D90C62721C1C479CBDEB7EE2ADE6D9', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C14138C3B2F54B4E9796D08DDA4EE02C', X'52D90C62721C1C479CBDEB7EE2ADE6D9', X'52D90C62721C1C479CBDEB7EE2ADE6D9', 1, NULL, NULL, 2, NULL, 0, 'Place the oil, maple syrup, hot sauce and salt in a small saucepan and bring to a boil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE76F4D91EBDCA41BA3FDD7542CEA9B7', X'52D90C62721C1C479CBDEB7EE2ADE6D9', X'52D90C62721C1C479CBDEB7EE2ADE6D9', 2, NULL, NULL, 2, NULL, 0, 'Place the delicata squash in a baking dish, cut side down.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A314EE24D65874DA460E471D97835A1', X'52D90C62721C1C479CBDEB7EE2ADE6D9', X'52D90C62721C1C479CBDEB7EE2ADE6D9', 3, NULL, NULL, 2, NULL, 0, 'Nestle the rosemary sprigs and the garlic within the sliced squash.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86E9196BCADE3141A446221AF6F9C781', X'52D90C62721C1C479CBDEB7EE2ADE6D9', X'52D90C62721C1C479CBDEB7EE2ADE6D9', 4, NULL, NULL, 2, NULL, 0, 'Brush on some of the maple syrup-chili glaze.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F16A17B19468945A7C40640CCE46AF5', X'52D90C62721C1C479CBDEB7EE2ADE6D9', X'52D90C62721C1C479CBDEB7EE2ADE6D9', 5, NULL, NULL, 2, NULL, 0, 'Place the baking dish in the oven and bake 15 minutes. Remove, baste with more of the glaze, and bake again for 15 more minutes. Baste again and repeat for another 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AD36546343D4C43A31DE845DC026187', X'52D90C62721C1C479CBDEB7EE2ADE6D9', X'52D90C62721C1C479CBDEB7EE2ADE6D9', 6, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB2E991AF6506042A332393F8F9B963B', X'527DAA687A11314C8A82005935B2B3FE', X'527DAA687A11314C8A82005935B2B3FE', 0, NULL, NULL, 2, NULL, 0, 'Mix 1 tsp of the oil, garlic and rosemary and set aside for 15-30 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C34AB8A50AF3A54FB0F4FA6BFE7E2B12', X'527DAA687A11314C8A82005935B2B3FE', X'527DAA687A11314C8A82005935B2B3FE', 1, NULL, NULL, 2, NULL, 0, 'In a bowl,mix the remaining oil, Brussels sprouts, salt and pepper. Spread the sprouts on a baking sheet in a single layer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AA36B905D0832429C13AB41A6709B29', X'527DAA687A11314C8A82005935B2B3FE', X'527DAA687A11314C8A82005935B2B3FE', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 degrees F. Roast the Brussels sprouts for 35 minutes. About 15 minutes into the baking, sprinkle the oil-infused garlic and rosemary over the sprouts and stir slightly to mix. Continue roasting.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D953F878E9B02479AA8677350D20439', X'527DAA687A11314C8A82005935B2B3FE', X'527DAA687A11314C8A82005935B2B3FE', 3, NULL, NULL, 2, NULL, 0, 'Serve hot or cold. These are so delicious, I pop them into my mouth like candy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CF4020713D9194D874254F76B5E574C', X'42C6104E24F2424ABF52F7DFF02CEBCD', X'42C6104E24F2424ABF52F7DFF02CEBCD', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'001C79F53CBCA442A3B5332D9D5F70BA', X'42C6104E24F2424ABF52F7DFF02CEBCD', X'42C6104E24F2424ABF52F7DFF02CEBCD', 1, NULL, NULL, 2, NULL, 0, 'Place the potatoes in a baking dish large enough to hold them in a single layer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9048E3BBECA273448FCA374DE219216D', X'42C6104E24F2424ABF52F7DFF02CEBCD', X'42C6104E24F2424ABF52F7DFF02CEBCD', 2, NULL, NULL, 2, NULL, 0, 'Add the lemon juice and zest, garlic, vegetable stock, olive oil, salt and ground black pepper and oregano and mix everything.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A1AD9547D095642B25DCA879457F5B8', X'42C6104E24F2424ABF52F7DFF02CEBCD', X'42C6104E24F2424ABF52F7DFF02CEBCD', 3, NULL, NULL, 2, NULL, 0, 'Place the baking dish in the oven and bake for 1 hour and 15 minutes. About halfway through, turn the potatoes once with a spatula to make sure they roast evenly. Continue roasting until all the potatoes are slightly crispy and fork-tender and the stock has evaporated.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'376ACB95CEF5D249B47B8B1612C19DEC', X'42C6104E24F2424ABF52F7DFF02CEBCD', X'42C6104E24F2424ABF52F7DFF02CEBCD', 4, NULL, NULL, 2, NULL, 0, 'Serve hot or at room temperature.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B24080351DFA84780F2C39E0B537701', X'4D33EBED5A3E6745878D104F55E530C9', X'4D33EBED5A3E6745878D104F55E530C9', 0, NULL, NULL, 2, NULL, 0, 'Blend the cashews with 1 cup of the vegetable stock and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62051E7CE2594A4FB01C11B9027721A5', X'4D33EBED5A3E6745878D104F55E530C9', X'4D33EBED5A3E6745878D104F55E530C9', 1, NULL, NULL, 2, NULL, 0, 'In a saucepan, heat the oil or vegetable stock. Add the onions and garlic along with a pinch of salt and ground black pepper. Saute, stirring frequently, until the onions turn translucent but do not brown. It is important that you cut the onions really fine so they just melt into the soup.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4576200B91F1D4CA4A2D5139266B5B8', X'4D33EBED5A3E6745878D104F55E530C9', X'4D33EBED5A3E6745878D104F55E530C9', 2, NULL, NULL, 2, NULL, 0, 'Add the mushrooms. At this stage you can add a splosh of white wine for more flavor, if you wish. Saute the mushrooms over medium-high heat until they are tender, about 5-8 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC47176CC112A34ABD11018EEA61C5C5', X'4D33EBED5A3E6745878D104F55E530C9', X'4D33EBED5A3E6745878D104F55E530C9', 3, NULL, NULL, 2, NULL, 0, 'Add the thyme or sage, stir to mix, and then add the all purpose flour or rice flour. Mix well and let the flour cook for a couple of minutes, stirring frequently to let it cook evenly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E73E7D15FE0024182DA6AEC21040F60', X'4D33EBED5A3E6745878D104F55E530C9', X'4D33EBED5A3E6745878D104F55E530C9', 4, NULL, NULL, 2, NULL, 0, 'Add a cup of the vegetable stock, stirring vigorously as you do so to avoid any lumps from forming. You can also use a whisk to mix at this stage. Add one more cup of stock, continuing to stir, and when it comes to a boil, stir in the blended cashew milk.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'934801FAEDCDD54C981A23186AB60E31', X'4D33EBED5A3E6745878D104F55E530C9', X'4D33EBED5A3E6745878D104F55E530C9', 5, NULL, NULL, 2, NULL, 0, 'Once the soup comes to a boil, you can thin out with more stock to get it to the consistency you like. Check for seasoning and add more salt and pepper as needed. You can also add a tablespoon of soy sauce for more flavor, unless you want to be soy-free.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE73AA0D971DF34282AE988C8BFC5FA4', X'4D33EBED5A3E6745878D104F55E530C9', X'4D33EBED5A3E6745878D104F55E530C9', 6, NULL, NULL, 2, NULL, 0, 'When the soup comes to a boil, stir, and turn off. Serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D01226C9B770B4583F2BC423AC945CB', X'175BE453AA72FA4F899A3DD32D1E3901', X'175BE453AA72FA4F899A3DD32D1E3901', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees F.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD89B1380568974B9B96C863E9742708', X'175BE453AA72FA4F899A3DD32D1E3901', X'175BE453AA72FA4F899A3DD32D1E3901', 1, NULL, NULL, 2, NULL, 0, 'Heat oil in a large skillet. Add the onions and garlic and season with salt and pepper. Cook, stirring, until onions are softened.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A3266A6BEFF8F45A04C42A4E266D980', X'175BE453AA72FA4F899A3DD32D1E3901', X'175BE453AA72FA4F899A3DD32D1E3901', 2, NULL, NULL, 2, NULL, 0, 'Add the beefless beef, stir to mix, then add the artichoke hearts and the thyme and continue to cook another 5-10 minutes until the artichokes are just tender. Check seasoning and turn off heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69EAE491A4AC7745A6391C26F87B20E4', X'175BE453AA72FA4F899A3DD32D1E3901', X'175BE453AA72FA4F899A3DD32D1E3901', 3, NULL, NULL, 2, NULL, 0, 'Place the cashews in the blender with garlic powder, salt and ground black pepper to taste and 2 cups water. Blend until smooth.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'911FE964839C1048A27596AC9A1901F8', X'175BE453AA72FA4F899A3DD32D1E3901', X'175BE453AA72FA4F899A3DD32D1E3901', 4, NULL, NULL, 2, NULL, 0, 'To assemble the gratin, layer or shingle half the potatoes in the bottom of a baking dish approx. 10 inches by 8 inches and 2 inches deep. It''s okay if some potatoes overlap.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E88AD166DBF604F863DF32C0378E842', X'175BE453AA72FA4F899A3DD32D1E3901', X'175BE453AA72FA4F899A3DD32D1E3901', 5, NULL, NULL, 2, NULL, 0, 'Spoon the artichoke mixture on top of the potatoes in an even layer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F2D33224BB622459F1B669B15379C44', X'175BE453AA72FA4F899A3DD32D1E3901', X'175BE453AA72FA4F899A3DD32D1E3901', 6, NULL, NULL, 2, NULL, 0, 'Cover the artichoke mixture with the remaining potatoes. Shingle them in a decorative fashion for a nice look to your gratin.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DB30CF37C44894AA5617C710081BFCE', X'175BE453AA72FA4F899A3DD32D1E3901', X'175BE453AA72FA4F899A3DD32D1E3901', 7, NULL, NULL, 2, NULL, 0, 'Pour the cashew mixture over the gratin evenly. Press down on the potatoes with a spatula to submerge them as far as possible.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41BC92D25144AD43AAE8812719551CC4', X'175BE453AA72FA4F899A3DD32D1E3901', X'175BE453AA72FA4F899A3DD32D1E3901', 8, NULL, NULL, 2, NULL, 0, 'Cover the baking dish tightly with a foil. Place in the oven and bake 45 minutes or until the potatoes are tender.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C69CCBC3D15DA44A085C12DDAD8091A', X'175BE453AA72FA4F899A3DD32D1E3901', X'175BE453AA72FA4F899A3DD32D1E3901', 9, NULL, NULL, 2, NULL, 0, 'Mix the breadcrumbs with the olive oil, nutritional yeast if using, thyme, and salt and black pepper. Remove the baking dish carefully from the oven, remove the foil, and sprinkle the breadcrumb topping over the gratin.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'638C7E44371E7946B16D04C5622B6217', X'175BE453AA72FA4F899A3DD32D1E3901', X'175BE453AA72FA4F899A3DD32D1E3901', 10, NULL, NULL, 2, NULL, 0, 'Return the gratin to the oven, without the foil covering this time and bake another 10-15 minutes or until the breadcrumb topping is golden and crunchy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F932837BF3EF634095F3971F41F0D35D', X'175BE453AA72FA4F899A3DD32D1E3901', X'175BE453AA72FA4F899A3DD32D1E3901', 11, NULL, NULL, 2, NULL, 0, 'Let stand 5-10 minutes before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6ED70B91C899A48A774237886312804', X'D8532EA5D303154DB4500695E7AB2257', X'D8532EA5D303154DB4500695E7AB2257', 0, NULL, NULL, 2, NULL, 0, 'Place both kinds of mushrooms in a bowl and add ground black pepper, salt to taste, and the all purpose flour. Toss well to mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'852700DC00A0834CB2C77177C95A4407', X'D8532EA5D303154DB4500695E7AB2257', X'D8532EA5D303154DB4500695E7AB2257', 1, NULL, NULL, 2, NULL, 0, 'Heat the 1 tbsp oil in a large saucepan. Add the mushrooms to the pan and saute for a couple of minutes, or until the mushrooms start to soften and brown slightly. Remove to a bowl.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E44755B3E1F7949A977DE4E18848643', X'D8532EA5D303154DB4500695E7AB2257', X'D8532EA5D303154DB4500695E7AB2257', 2, NULL, NULL, 2, NULL, 0, 'In the same pot, add the remaining 1 tsp of olive oil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB1524E5949BB74CB70C072D9D260BC2', X'D8532EA5D303154DB4500695E7AB2257', X'D8532EA5D303154DB4500695E7AB2257', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic, onions, carrots and celery. Add a pinch of salt and stir-fry, scraping the bottom of the pan for any bits of mushrooms or flour left behind. They will add wonderful flavor.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'020F354C0F5CD6408C58487DB0B426D0', X'D8532EA5D303154DB4500695E7AB2257', X'D8532EA5D303154DB4500695E7AB2257', 4, NULL, NULL, 2, NULL, 0, 'When the onions and carrots start to soften but not brown, add the mushrooms back into the pot along with the peas, 1 tbsp of herbs and all of the mushroom stock.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52EB7F8DFCDBF748B68D66B9AF56DE43', X'D8532EA5D303154DB4500695E7AB2257', X'D8532EA5D303154DB4500695E7AB2257', 5, NULL, NULL, 2, NULL, 0, 'Stir well, bring to a boil, cover and cook for 20 minutes or until the carrots are tender.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AFCB5009AD6454494B2BD6A16F49C02', X'D8532EA5D303154DB4500695E7AB2257', X'D8532EA5D303154DB4500695E7AB2257', 6, NULL, NULL, 2, NULL, 0, 'Add the remaining herbs and cashew paste. Stir well to mix. The stew should have thickened because of the flour you dredged the mushrooms in, and if it is getting too thick, add some water or stock to thin it out.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EF46629525CA342AA57BED42770E95C', X'D8532EA5D303154DB4500695E7AB2257', X'D8532EA5D303154DB4500695E7AB2257', 7, NULL, NULL, 2, NULL, 0, 'Add salt and ground black pepper to taste. Stir in the parsley.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A1DA649C3219646BA17C589634BC92F', X'D8532EA5D303154DB4500695E7AB2257', X'D8532EA5D303154DB4500695E7AB2257', 8, NULL, NULL, 2, NULL, 0, 'Turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA848290F41CED41802603E44E86302C', X'99C56C608C193541863426766B41EA49', X'99C56C608C193541863426766B41EA49', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425° Fahrenheit');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'843E17A63E5ECE4FB21A60F2ACA0BBF2', X'99C56C608C193541863426766B41EA49', X'99C56C608C193541863426766B41EA49', 1, NULL, NULL, 2, NULL, 0, 'Roll out the puff pastry and, using a glass or a cookie cutter, cut circles or squares large enough to fit over the ramekins or pie plate you will use. I cut circles just large enough to fit over the ramekins I used, but because the pastry shrinks when it cooks, the stew bubbled up through the sides of the ramekins. I don''t mind it because it looks rather rustic, but if you want a more polished effect, make sure the puff pastry covers the top of the ramekin completely.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'027BE638D40D5C41A09E982C708DC674', X'99C56C608C193541863426766B41EA49', X'99C56C608C193541863426766B41EA49', 2, NULL, NULL, 2, NULL, 0, 'Spoon the mushroom filling into the ramekins and cover the tops with the puff pastry circles or squares.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C49D062A1E58FB4AAF4845764DE1C709', X'99C56C608C193541863426766B41EA49', X'99C56C608C193541863426766B41EA49', 3, NULL, NULL, 2, NULL, 0, 'At this point, for a prettier crust, I like to brush on a mixture of 1 tsp olive oil + 1 tsp nondairy milk on top of the crusts-- this makes them really golden, but it''s completely optional.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA7FB03CDB79A74BA304350A3E5584FC', X'99C56C608C193541863426766B41EA49', X'99C56C608C193541863426766B41EA49', 4, NULL, NULL, 2, NULL, 0, 'Cut a small slit in the top of the puff pastry in each ramekin.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5935BE8574486F419E8157410CEA255C', X'99C56C608C193541863426766B41EA49', X'99C56C608C193541863426766B41EA49', 5, NULL, NULL, 2, NULL, 0, 'Place the ramekins on a baking sheet and place in the oven. Bake 15 minutes, then turn down the heat to 350 degrees and continue baking for another 10 minutes or until the tops are all golden and puffy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2905AECD2BF4AC45BA56493AF5B07484', X'99C56C608C193541863426766B41EA49', X'99C56C608C193541863426766B41EA49', 6, NULL, NULL, 2, NULL, 0, 'Remove from the oven, let stand 15 minutes before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80B43009DB307B4CA282A320884D6379', X'AB9CAFDF757CB742B11A9E2670DDF8EB', X'AB9CAFDF757CB742B11A9E2670DDF8EB', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB1CD68226F3DC40A86D3E9A7A4AE9EC', X'AB9CAFDF757CB742B11A9E2670DDF8EB', X'AB9CAFDF757CB742B11A9E2670DDF8EB', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a 12-inch cast iron skillet. Add the garlic and onions, season with salt and ground black pepper, and cook over medium heat until soft but not brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00056E70F3E3284A8F72E5836057C177', X'AB9CAFDF757CB742B11A9E2670DDF8EB', X'AB9CAFDF757CB742B11A9E2670DDF8EB', 2, NULL, NULL, 2, NULL, 0, 'Add the sausage and stir for a couple of minutes until it starts to brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4EEE8DE2DE4204E9EB4075D15FE515F', X'AB9CAFDF757CB742B11A9E2670DDF8EB', X'AB9CAFDF757CB742B11A9E2670DDF8EB', 3, NULL, NULL, 2, NULL, 0, 'Add the mushrooms and stir well. Saute for a few minutes until the mushrooms start to soften, then add the sage or thyme and the flour and mix well. Cook, stirring, for another couple of minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86FAC6DE1789BD4D8BA9B2BFAE8A2608', X'AB9CAFDF757CB742B11A9E2670DDF8EB', X'AB9CAFDF757CB742B11A9E2670DDF8EB', 4, NULL, NULL, 2, NULL, 0, 'Add the frozen veggies, mix, and then add the nondairy milk. Stir frequently until the mixture just comes to a boil. Add more salt and pepper as needed. Turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DCBEFFC2AE682459BD95E90984B2885', X'AB9CAFDF757CB742B11A9E2670DDF8EB', X'AB9CAFDF757CB742B11A9E2670DDF8EB', 5, NULL, NULL, 2, NULL, 0, 'Arrange the frozen tater tots on top of the veggies. You can make a simple but appealing design, like I did, by arranging the tots in concentric circles.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BAB18B3BA3ADE42A1C2FA0AB95C83C9', X'AB9CAFDF757CB742B11A9E2670DDF8EB', X'AB9CAFDF757CB742B11A9E2670DDF8EB', 6, NULL, NULL, 2, NULL, 0, 'Place the skillet in the oven and bake 55 minutes or until the tater tots are golden-brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72E82C6E891EA74ABA9B27F221D7CAA9', X'AB9CAFDF757CB742B11A9E2670DDF8EB', X'AB9CAFDF757CB742B11A9E2670DDF8EB', 7, NULL, NULL, 2, NULL, 0, 'Remove the skillet from the oven and let stand 5 minutes before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A56DE90BD22819459D114F927B561AE4', X'553EA9BFD6E2E04B9455133906F41F29', X'553EA9BFD6E2E04B9455133906F41F29', 0, NULL, NULL, 2, NULL, 0, 'Thaw the puff pastry sheets to room temperature.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8AE842221CADB41BB53FE97C5399C1F', X'553EA9BFD6E2E04B9455133906F41F29', X'553EA9BFD6E2E04B9455133906F41F29', 1, NULL, NULL, 2, NULL, 0, 'Place the lentils in a saucepan, cover with an inch of water, bring to a boil, cover, and continue cooking 10-15 minutes or until lentils are tender but not mushy. Drain and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC87358BC26D2146AFE43495748FAE5A', X'553EA9BFD6E2E04B9455133906F41F29', X'553EA9BFD6E2E04B9455133906F41F29', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AF68DA1A903AF4C90CD03E9D399F224', X'553EA9BFD6E2E04B9455133906F41F29', X'553EA9BFD6E2E04B9455133906F41F29', 3, NULL, NULL, 2, NULL, 0, 'Heat the oil in a skillet. Add the carrots, celery, onions and garlic along with a dash of salt and ground black pepper. Saute, stirring frequently, until the veggies start to caramelize.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A7AD040BDF9874FAC4BDBFDFA32E410', X'553EA9BFD6E2E04B9455133906F41F29', X'553EA9BFD6E2E04B9455133906F41F29', 4, NULL, NULL, 2, NULL, 0, 'Add the mushrooms, saute for a minute, then add the wine. Continue to cook, stirring often, until the wine has evaporated and the mushrooms are browned.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21123F57F988034DBE5D49C98F30E238', X'553EA9BFD6E2E04B9455133906F41F29', X'553EA9BFD6E2E04B9455133906F41F29', 5, NULL, NULL, 2, NULL, 0, 'Add the herbs, soy sauce or tamari, dijon mustard and salt and ground black pepper to taste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'795E11F8E187C444B171588ED8BB9A50', X'553EA9BFD6E2E04B9455133906F41F29', X'553EA9BFD6E2E04B9455133906F41F29', 6, NULL, NULL, 2, NULL, 0, 'Add the lentils and continue cooking until there is no visible liquid in the skillet. Add the flour and mix well until the flour is absorbed by the mixture. Turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79609CF6F47CAF438B22725295313E40', X'553EA9BFD6E2E04B9455133906F41F29', X'553EA9BFD6E2E04B9455133906F41F29', 7, NULL, NULL, 2, NULL, 0, 'Roll out one of the puff pastry sheets to 11 X 13 inches on a lightly floured surface. Divide the lentil mixture in two and place one half in the center of the puff pastry sheets, forming a loaf with your hands as you go. Press it in firmly so it holds together, then bring one side of the puff pastry sheet over the top, and then the other. Tuck in the top and bottom so you have a neat-looking, fully wrapped log. Press in the seams to seal, brush with water first to help the puff pastry stick. Repeat with the second sheet of puff pastry and the remaining lentil-veggie mixture.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB336AA5B51EC64B85B9DCBDAD3C0F7B', X'553EA9BFD6E2E04B9455133906F41F29', X'553EA9BFD6E2E04B9455133906F41F29', 8, NULL, NULL, 2, NULL, 0, 'Place the logs on a baking sheet, at least two inches apart. Using a sharp knife, score the top of the log in a decorative pattern-- I did a crosshatch that''s pretty and also pretty easy to make. Mix the two wash ingredients and brush the wash on the tops of the wellington loaves. This gives the baked loaf a lovely color.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06B5806C3CAA1F4887DF3ED40204F159', X'553EA9BFD6E2E04B9455133906F41F29', X'553EA9BFD6E2E04B9455133906F41F29', 9, NULL, NULL, 2, NULL, 0, 'Place the baking sheet in the preheated oven and bake the wellington logs for 45 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0139084F6C8C614A933CC99535D03EC5', X'553EA9BFD6E2E04B9455133906F41F29', X'553EA9BFD6E2E04B9455133906F41F29', 10, NULL, NULL, 2, NULL, 0, 'Remove, let the wellingtons stand for about 10 minutes, then slice and serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'940F1CDF46D7C346B5E21F7ABF24217D', X'916A0F3993A65643993A51239947C268', X'916A0F3993A65643993A51239947C268', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3C3CADAD7FAA14A90CB10878E49F7F0', X'916A0F3993A65643993A51239947C268', X'916A0F3993A65643993A51239947C268', 1, NULL, NULL, 2, NULL, 0, 'Heat 1 tbsp of oil in a skillet. Add half the garlic and as it begins to turn golden, add the oregano and the fresh mushrooms. Season to taste with salt and ground black pepper. Saute, stirring frequently, for five minutes or until the mushrooms start to brown. Drain the reconstituted mushrooms and add to the skillet. Reserve the mushroom stock. Add the wine.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5592D0A77D8D674D9A34B69E4BA93AB5', X'916A0F3993A65643993A51239947C268', X'916A0F3993A65643993A51239947C268', 2, NULL, NULL, 2, NULL, 0, 'Continue cooking for another five minutes until the wine has evaporated, then add the leeks. Continue cooking another five minutes or until the leeks are just tender. Turn off the heat and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC3557BDF07A56499D11A6F57401C4B9', X'916A0F3993A65643993A51239947C268', X'916A0F3993A65643993A51239947C268', 3, NULL, NULL, 2, NULL, 0, 'Make the bechamel. In a saucepan, heat the remaining oil. Add the shallot and the remaining garlic. Season with salt and pepper and cook until the shallots start to turn translucent. Add the flour and cook, stirring frequently, over medium heat, until the flour begins to brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC92D4422B33EB458EBC5E122E2FF3B6', X'916A0F3993A65643993A51239947C268', X'916A0F3993A65643993A51239947C268', 4, NULL, NULL, 2, NULL, 0, 'Slowly, stir in the cashew milk, using a whisk to beat out any lumps of flour, until you have smooth sauce. Add the reserved mushroom sauce. Add nutmeg, check seasoning and add more salt and ground black pepper if needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64C536BB60E55F46939D58E354F617E4', X'916A0F3993A65643993A51239947C268', X'916A0F3993A65643993A51239947C268', 5, NULL, NULL, 2, NULL, 0, 'Let the sauce come to a boil and thicken. It should be thick enough to coat the back of a spoon. Turn off the heat. Reserve 1 ½ cups of the sauce and add the rest of it to the mushroom and leek mixture and stir well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5ADBE5D20A44214B83D80906745B408F', X'916A0F3993A65643993A51239947C268', X'916A0F3993A65643993A51239947C268', 6, NULL, NULL, 2, NULL, 0, 'Coat the bottom of a 12 X 8 inch baking pan with the reserved bechamel sauce. Layer lasagna noodles side by side on top. In the pan I used, three noodles fitted side by side in each layer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1D58B75B9E1B64DB68F638466F40B6D', X'916A0F3993A65643993A51239947C268', X'916A0F3993A65643993A51239947C268', 7, NULL, NULL, 2, NULL, 0, 'Pour one-third of the mushroom-leek mixture on top of the lasagna noodles in an even layer. Sprinkle on one-third of the vegan mozzarella');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F747CDD9772A474B9488F56358D14B2C', X'916A0F3993A65643993A51239947C268', X'916A0F3993A65643993A51239947C268', 8, NULL, NULL, 2, NULL, 0, 'Arrange three more noodles on top and again layer on one-third of the mushrooms and leeks and the mozzarella. Repeat one more time, and finish off by layering mushrooms and leeks, mozzarella, and the vegan parmesan on the top layer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3282A3FB966BF4FA3004B3E0F1556D5', X'916A0F3993A65643993A51239947C268', X'916A0F3993A65643993A51239947C268', 9, NULL, NULL, 2, NULL, 0, 'Cover the lasagna pan with tinfoil and bake in the preheated oven for 45 minutes. After 45 minutes, take off the foil and let the lasagna cook another 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07493546A40DB248ACDA4264DE276059', X'916A0F3993A65643993A51239947C268', X'916A0F3993A65643993A51239947C268', 10, NULL, NULL, 2, NULL, 0, 'Let the lasagna stand 10 minutes. Garnish with parsley before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6870A4128CC5A548914F2A2067F0A050', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 0, NULL, NULL, 2, NULL, 0, 'If using regular lasagna noodles, cook them in salted boiling water until al dente but not mushy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCAFAC826C17C44E8489439D750D4785', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 1, NULL, NULL, 2, NULL, 0, 'Make the meat sauce by heating the oil in a large saucepan. Add the onions and garlic. Saute, until they soften, about five minutes. Add the `meat` crumbles and saute for a couple of minutes. Add the tomato paste and mix in.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFB7DEC3365E26438EA80F11C9A5B133', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 2, NULL, NULL, 2, NULL, 0, 'Put the plum tomatoes in a bowl and squish them to break with your fingers. Add to the saucepan along with 3 cups of vegetable stock if using no-boil noodles, and 2 cups of stock if using regular noodles that have been cooked.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48DCF63FA3D2CC4685A4F872CD2F0396', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 3, NULL, NULL, 2, NULL, 0, 'Stir well, bring to a boil, add the oregano, red pepper flakes, salt and ground black pepper to taste. Stir and let the sauce cook for about 15 minutes or until it turns a bright red. Turn off heat and check seasoning. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B9A418DC599B44D9240F6B5F213BB37', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 4, NULL, NULL, 2, NULL, 0, 'Make the ricotta, by placing the cashews in a food processor with all ingredients except the parsley. Process for a couple of minutes until fairly smooth. Mix in the parsley and pulse until it''s mixed into the rest of the ricotta. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75A2ED5761B8CB47A8033852F7F15BED', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 5, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B3351EB2D232846AD9EE5F357497B51', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 6, NULL, NULL, 2, NULL, 0, 'Assemble the lasagna by spraying a 12 X 9 inch lasagna pan with cooking spray or oil. Ladle out a small amount of sauce into the pan and spread.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F692C82164FE140A3760D96439E90F5', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 7, NULL, NULL, 2, NULL, 0, 'Layer on four sheets of the noodles into the bottom of the pan. If using no-boil noodles, which are smaller than regular noodles, you might need to layer on five noodles vertically and one more horizontally.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8780E7B7F3877F4CA230162018BD5D05', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 8, NULL, NULL, 2, NULL, 0, 'Layer on one-fourth of the meat sauce over the lasagna noodles.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE46B3A7E01D714E9498E08C814F8EDE', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 9, NULL, NULL, 2, NULL, 0, 'Layer on one-fourth of the cashew parmesan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3F92B9E3BFE6246A427650DC21E1404', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 10, NULL, NULL, 2, NULL, 0, 'Layer on one-fourth of the vegan mozzarella.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1E932FA6101C34D91E95D9F9F8BB989', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 11, NULL, NULL, 2, NULL, 0, 'Layer on one-fourth of the vegan ricotta.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D859A5DDFB301D49961ED4FFD61F46A7', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 12, NULL, NULL, 2, NULL, 0, 'Repeat until you have built up three layers of the meat sauce and the cheeses. Over the fourth and final layer, layer the remaining meat sauce, parm and mozzarella.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83BEAB790D93ED4EA98D6DE76D81AE26', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 13, NULL, NULL, 2, NULL, 0, 'Cover tight with aluminum foil, place the lasagna pan on a rimmed baking sheet, and bake in the preheated oven for 30 minutes. After 30 minutes, remove the aluminum foil and bake another 30 minutes until bubbly and hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C52BD95CAA3C1A41BD5D34FA058E28C0', X'76CC5F656BC1EF4BA47980C9E90A4982', X'76CC5F656BC1EF4BA47980C9E90A4982', 14, NULL, NULL, 2, NULL, 0, 'Let the lasagna stand at least 15 minutes to firm up, then cut and serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DF75D0D1AA67B42B7C20A4E0E76A975', X'5BD1B685A726804FAA9ECB737C9CC65B', X'5BD1B685A726804FAA9ECB737C9CC65B', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A58E82931453F458C19B1FB097D740E', X'5BD1B685A726804FAA9ECB737C9CC65B', X'5BD1B685A726804FAA9ECB737C9CC65B', 1, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a skillet. Add the garlic, saute for a few seconds until it just starts to turn color, then add the cabbage and leeks. Season with salt and pepper and cook for 5-8 minutes or until the vegetables are just tender but not mushy. Turn off the heat');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3CE68AE42362543ACF75DA09D8DC155', X'5BD1B685A726804FAA9ECB737C9CC65B', X'5BD1B685A726804FAA9ECB737C9CC65B', 2, NULL, NULL, 2, NULL, 0, 'In a blender, place the cashews, cornstarch, nutmeg and salt and ground black pepper to taste. Add ¾ cup water and blend into a very smooth paste. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10DA25DE607F3340A09769B10C7FE941', X'5BD1B685A726804FAA9ECB737C9CC65B', X'5BD1B685A726804FAA9ECB737C9CC65B', 3, NULL, NULL, 2, NULL, 0, 'Roll out the puff pastry sheet to fit into your tart pan. I used a rectangular tart pan that''s 12 inches by 8 inches, but a round pan would do just as well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'067060C5348CE84E924108164C217AE4', X'5BD1B685A726804FAA9ECB737C9CC65B', X'5BD1B685A726804FAA9ECB737C9CC65B', 4, NULL, NULL, 2, NULL, 0, 'Place the puff pastry into the tart pan and press into the sides, making sure it fits into all the corners and crevices. Prick the pastry sheet all over with the tines of a fork, which will keep it from puffing too much. Don''t remove any overhanging dough yet.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3D8233AE89F3A43BE8AD2F7A48E72C8', X'5BD1B685A726804FAA9ECB737C9CC65B', X'5BD1B685A726804FAA9ECB737C9CC65B', 5, NULL, NULL, 2, NULL, 0, 'Arrange the slices of potatoes in the bottom of the tart pan, then layer on the cabbage and leeks. Finally, pour the cashew sauce all over. Once you''ve filled the tart pan, pinch off any excess dough along the edges of the pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C314A40DF642E148934E3AA2F55D3332', X'5BD1B685A726804FAA9ECB737C9CC65B', X'5BD1B685A726804FAA9ECB737C9CC65B', 6, NULL, NULL, 2, NULL, 0, 'Place the tart pan in the preheated oven, preferably on a baking sheet, and bake 30-35 minutes or until the puff pastry is golden-brown and the filling is set.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'665BA2F1A5060A48BB8BEF7E3A1E12BA', X'5BD1B685A726804FAA9ECB737C9CC65B', X'5BD1B685A726804FAA9ECB737C9CC65B', 7, NULL, NULL, 2, NULL, 0, 'Let the tart stand 10 minutes before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F87F676E2C1A9449BA8FE80F93774A9E', X'D3E3D83639788E44A9C91A7EDD518296', X'D3E3D83639788E44A9C91A7EDD518296', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B2B6FE5F583B04185D18D6CB647AC5C', X'DF2B0154F722064198DD5619A72C5AA0', X'DF2B0154F722064198DD5619A72C5AA0', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients except water in the bowl of a food processor or a large bowl. Mix well, then drizzle in the water slowly until the dough just comes together. Use a fork to mix, if doing this without a food processor.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1BFE29D0B3A9A439EA7CEFF31D48B83', X'DF2B0154F722064198DD5619A72C5AA0', X'DF2B0154F722064198DD5619A72C5AA0', 1, NULL, NULL, 2, NULL, 0, 'Gather the dough into a ball, and then wrap tightly in cling wrap, shaping it with your hands into a disc. Refrigerate while you prepare the filling.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5D9EE6F21AEA743857AEA96CC2946B0', X'0686802F35664C4090BD6C4FDE5098D3', X'0686802F35664C4090BD6C4FDE5098D3', 0, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a saute pan. Add the leeks with the garlic, some salt and ground black pepper to taste. Saute for about 10 minutes over medium-low heat, stirring frequently, until the leeks soften and become tender, but do not brown. Turn off heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'452BE3E990D6744F861E0C8FCE006214', X'0686802F35664C4090BD6C4FDE5098D3', X'0686802F35664C4090BD6C4FDE5098D3', 1, NULL, NULL, 2, NULL, 0, 'Place all ingredients--the tofu, nutritional yeast, cashews, nutmeg, garlic powder and water--in a blender and blend until smooth. Season with salt and ground black pepper.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D451E2C55FFDE4FAC7978FE7DF108C8', X'0686802F35664C4090BD6C4FDE5098D3', X'0686802F35664C4090BD6C4FDE5098D3', 2, NULL, NULL, 2, NULL, 0, 'Add the tofu cream to the leeks and mix well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BC6CC785F1F304B975B298CAC76E954', X'EE6E177113F64C4DB2BE90CB2C243D6F', X'EE6E177113F64C4DB2BE90CB2C243D6F', 0, NULL, NULL, 2, NULL, 0, 'Roll the crust to fit a 9-inch tart pan. Fold into half and place in the pan. Unfold and, using your fingers, fit the crust into the pan. Cut out any overhanging dough by running your rolling pin over it.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA6F6E6D9594DA4A9E950B610AADB2D0', X'EE6E177113F64C4DB2BE90CB2C243D6F', X'EE6E177113F64C4DB2BE90CB2C243D6F', 1, NULL, NULL, 2, NULL, 0, 'Prick the bottom of the crust all over with the tines of a fork. Place the tart pan on a baking sheet.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13BE9A7549DD5C4D8D0DF395B22D108A', X'EE6E177113F64C4DB2BE90CB2C243D6F', X'EE6E177113F64C4DB2BE90CB2C243D6F', 2, NULL, NULL, 2, NULL, 0, 'Pour the filling into the crust.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'623F47661B4D8A48B4CE6D8716478528', X'EE6E177113F64C4DB2BE90CB2C243D6F', X'EE6E177113F64C4DB2BE90CB2C243D6F', 3, NULL, NULL, 2, NULL, 0, 'Place the baking sheet with the tart pan into the preheated oven and bake for 35-40 minutes until the filling has set.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD8CEE23CB444C4A91142505DBF27B87', X'EE6E177113F64C4DB2BE90CB2C243D6F', X'EE6E177113F64C4DB2BE90CB2C243D6F', 4, NULL, NULL, 2, NULL, 0, 'Remove and let the quiche cool about 20 minutes before unmolding. Serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EDBEB96EAF3EF448A6D33A242C21506', X'AF787BC7802E8C4FBDFFD702C78E5168', X'AF787BC7802E8C4FBDFFD702C78E5168', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a saucepan. Over medium heat add the flour and cook, stirring constantly, until the flour becomes very fragrant, about 3-4 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFF0DA42B97E694A93BDFBF7F78D638F', X'AF787BC7802E8C4FBDFFD702C78E5168', X'AF787BC7802E8C4FBDFFD702C78E5168', 1, NULL, NULL, 2, NULL, 0, 'Add the nondairy milk, stirring constantly with a whisk to prevent lumps from forming. Add the pumpkin puree and continue to whisk. Cook the sauce for 10 minutes. You want it to be thick but fluid, about the consistency of pancake batter. If the sauce gets too thick, add some more nondairy milk.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1214F2B7CDFFC4783F5AA30F58033DB', X'AF787BC7802E8C4FBDFFD702C78E5168', X'AF787BC7802E8C4FBDFFD702C78E5168', 2, NULL, NULL, 2, NULL, 0, 'Stir in the nutmeg and add salt and pepper to taste. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8851BAFEE88E842A7978CBCF370B088', X'BD6CF92597C49A4B84DAE5944D9CF70C', X'BD6CF92597C49A4B84DAE5944D9CF70C', 0, NULL, NULL, 2, NULL, 0, 'Blend the cashews with 1 cup water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FC32EB11EC09A47ABD97C6B1B476E44', X'BD6CF92597C49A4B84DAE5944D9CF70C', X'BD6CF92597C49A4B84DAE5944D9CF70C', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a saucepan and add the onion and garlic.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A1C30E8AB85D144B0820D536E9291BC', X'BD6CF92597C49A4B84DAE5944D9CF70C', X'BD6CF92597C49A4B84DAE5944D9CF70C', 2, NULL, NULL, 2, NULL, 0, 'Saute over medium heat until the onions turn translucent.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'523D1BB5235F43468CE8D075E9ABE425', X'BD6CF92597C49A4B84DAE5944D9CF70C', X'BD6CF92597C49A4B84DAE5944D9CF70C', 3, NULL, NULL, 2, NULL, 0, 'Add the flour, and cook, stirring, about 2-3 minutes. Add the cashew cream and stir well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7859D56F4F2E54988F5430DA29BD52E', X'BD6CF92597C49A4B84DAE5944D9CF70C', X'BD6CF92597C49A4B84DAE5944D9CF70C', 4, NULL, NULL, 2, NULL, 0, 'Add the spinach and continue cooking about two minutes. This will be thick, but if you find it is sticking to the bottom of the pan, add a tiny bit of water. Add nutmeg, salt and ground pepper, then turn off heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5730D660D6B974DBD9A90F8E721D264', X'B1105C338E6A584EAF5FE9A2B53DB013', X'B1105C338E6A584EAF5FE9A2B53DB013', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F3AFB377BF60C4D8A689880ACFC3F2C', X'B1105C338E6A584EAF5FE9A2B53DB013', X'B1105C338E6A584EAF5FE9A2B53DB013', 1, NULL, NULL, 2, NULL, 0, 'Spread a couple of ladlefuls of the pumpkin bechamel on the bottom of a 9 X 13 inch baking dish.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'181FA609AD01AB46832FE630CAB8584F', X'B1105C338E6A584EAF5FE9A2B53DB013', X'B1105C338E6A584EAF5FE9A2B53DB013', 2, NULL, NULL, 2, NULL, 0, 'Layer five of the lasagna noodles on top of the sauce, then layer on half of the creamed spinach, spreading it evenly over the noodles. On top of the spinach, layer on approximately a third of the pumpkin sauce.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D44730727B2F574B9B2F7FCF05A006D5', X'B1105C338E6A584EAF5FE9A2B53DB013', X'B1105C338E6A584EAF5FE9A2B53DB013', 3, NULL, NULL, 2, NULL, 0, 'Cover with five more noodles and continue layering with all of the remaining creamed spinach and a third of the bechamel.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13A3D1A40F989F4AA7D4D5F05394F201', X'B1105C338E6A584EAF5FE9A2B53DB013', X'B1105C338E6A584EAF5FE9A2B53DB013', 4, NULL, NULL, 2, NULL, 0, 'Place your last five noodles in the pan, and this time cover with just the remaining pumpkin bechamel.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28D0EEF9CAB4C94395D06906F513496E', X'B1105C338E6A584EAF5FE9A2B53DB013', X'B1105C338E6A584EAF5FE9A2B53DB013', 5, NULL, NULL, 2, NULL, 0, 'Top evenly with the breadcrumbs. Cover the pan with aluminum foil and bake in the preheated oven for 30 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48C8CFA595DD184784D3F5AE5904B187', X'B1105C338E6A584EAF5FE9A2B53DB013', X'B1105C338E6A584EAF5FE9A2B53DB013', 6, NULL, NULL, 2, NULL, 0, 'After 30 minutes remove the foil and continue to bake for 30 more minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D22A8497AA5F0B4298C2146D4EC224F1', X'B1105C338E6A584EAF5FE9A2B53DB013', X'B1105C338E6A584EAF5FE9A2B53DB013', 7, NULL, NULL, 2, NULL, 0, 'Remove from the oven. Let stand 10 minutes, then serve the lasagna hot or later, at room temperature. If desired, sprinkle on some chopped parsley or basil before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7473364A2406743B75AF5189654B416', X'4FF1FD305680BC478244405A92CB8D7B', X'4FF1FD305680BC478244405A92CB8D7B', 0, NULL, NULL, 2, NULL, 0, 'Whisk the flour and the salt in a bowl. Add the cold butter cubes to the bowl and cut it into the flour, using a fork or a pastry cutter, until you have a coarse, grainy mix with some pea-sized pieces of butter.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2101B07D5C9D6499C1DD7FBFACF56B8', X'4FF1FD305680BC478244405A92CB8D7B', X'4FF1FD305680BC478244405A92CB8D7B', 1, NULL, NULL, 2, NULL, 0, 'Drizzle in the ice-cold water, a little at a time, stirring frequently until the dough comes together. Try not to make the dough wet -- stop adding water as soon as you can pull the dough together in a ball.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F19CD203E3773D4CA4E7FF83D7DC63DE', X'4FF1FD305680BC478244405A92CB8D7B', X'4FF1FD305680BC478244405A92CB8D7B', 2, NULL, NULL, 2, NULL, 0, 'Wrap the dough tightly in cling wrap, shaping into a disc with your hands as you wrap it. Refrigerate for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDDDF66DB9297742A62F7C7999FCC4B2', X'73DB0A7F231D44499CA87507BA4BCE11', X'73DB0A7F231D44499CA87507BA4BCE11', 0, NULL, NULL, 2, NULL, 0, 'Place all of the ingredients in a bowl, and toss to mix. Spread the faux chicken pieces on a baking sheet and bake 20 minutes in a 350 degree oven or until chewy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'494C403D19DAB543B862FF1CDFD2CBD2', X'0D5D056635E3524EBA2034CBC566A4CF', X'0D5D056635E3524EBA2034CBC566A4CF', 0, NULL, NULL, 2, NULL, 0, 'Heat oil in a saucepan and add the onions. Saute until they turn golden-brown, then add in the ginger-garlic paste. Stir-fry for a minutes, then add in the tomatoes and tomato paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F42C8F0FA44054782773ECA2A6EF856', X'0D5D056635E3524EBA2034CBC566A4CF', X'0D5D056635E3524EBA2034CBC566A4CF', 1, NULL, NULL, 2, NULL, 0, 'Cook the tomatoes, stirring frequently, until they become very pulpy and darken slightly. Add the powdered spices -- the coriander powder, cumin powder, garam masala, paprika and turmeric. Mix well and cook for another couple of minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0B7E0E75E667342A2CB9A2394DF4625', X'0D5D056635E3524EBA2034CBC566A4CF', X'0D5D056635E3524EBA2034CBC566A4CF', 2, NULL, NULL, 2, NULL, 0, 'Add 2 cups of vegetable stock to the saucepan and mix well. Chop the `chicken` tikka pieces into smaller, bite-sized bits and add to the sauce. Bring everything to a boil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE02AF2D7E6354428876324A9E59ADCF', X'0D5D056635E3524EBA2034CBC566A4CF', X'0D5D056635E3524EBA2034CBC566A4CF', 3, NULL, NULL, 2, NULL, 0, 'Add the cashew paste and salt to taste. Add the green peppers and shallots. If the sauce seems too thick at this point, add some vegetable stock. The sauce will thicken as it cooks in the oven, so don''t make it too thick at this point. Once you have the desired consistency, turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66C2E234729ECB4FAA231BEC4CDA5554', X'0BB0D3A0D03E5C40809106AA988857DD', X'0BB0D3A0D03E5C40809106AA988857DD', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425 degrees F.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8802A572483E6E489B76786F95751C1B', X'0BB0D3A0D03E5C40809106AA988857DD', X'0BB0D3A0D03E5C40809106AA988857DD', 1, NULL, NULL, 2, NULL, 0, 'Ladle out the tikka masala sauce into four ramekins. You can also make this in one large ramekin. Place the ramekins on a baking sheet.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'597A895489B8D54B8867FDFC889F56E7', X'0BB0D3A0D03E5C40809106AA988857DD', X'0BB0D3A0D03E5C40809106AA988857DD', 2, NULL, NULL, 2, NULL, 0, 'Roll out the pastry dough on a floured surface, and, using an empty ramekin as the guide, cut four circles of dough large enough to fit the mouth of the ramekins.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1358EAE5408254458BC6850791B4BEC4', X'0BB0D3A0D03E5C40809106AA988857DD', X'0BB0D3A0D03E5C40809106AA988857DD', 3, NULL, NULL, 2, NULL, 0, 'Place each circle of dough on top of the tikka masala sauce in the ramekin. Cut a couple of vents into the top of the puff pastry using a sharp or serrated knife.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E9FD53953349A4DBC7C54298CECBB6E', X'0BB0D3A0D03E5C40809106AA988857DD', X'0BB0D3A0D03E5C40809106AA988857DD', 4, NULL, NULL, 2, NULL, 0, 'Place the baking sheet with the ramekins in the preheated oven and bake 20 minutes. Turn the heat down to 350 degrees and continue baking 15-20 minutes or until the pastry is golden-brown and the sauce is bubbling down the sides or through the vents in the pastry.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'886FA7CC380B8A43A7CE9792D97263DB', X'0BB0D3A0D03E5C40809106AA988857DD', X'0BB0D3A0D03E5C40809106AA988857DD', 5, NULL, NULL, 2, NULL, 0, 'Remove the ramekins from the oven and let stand five minutes at least before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'025C5E0E67B26843A001A9B17BAD9057', X'610407A389BB29409B4A81DE3382F520', X'610407A389BB29409B4A81DE3382F520', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E4C204504B93D458DAF0F1C6A1B31CA', X'610407A389BB29409B4A81DE3382F520', X'610407A389BB29409B4A81DE3382F520', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a saucepan. Add the garlic and, when they sizzle, add the leeks and thyme. Season with salt and ground black pepper and cook for about 15 minutes, covered, until the leeks are tender. Stir in the cornstarch and mix. Turn off heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E73738CEB63E5445A6E652CF9EFF2477', X'610407A389BB29409B4A81DE3382F520', X'610407A389BB29409B4A81DE3382F520', 2, NULL, NULL, 2, NULL, 0, 'While the leeks are cooking, season the sourdough cubes with salt and ground black pepper. Spray on some cooking spray and toast in the oven for 15 minutes or until the bread cubes are lightly golden.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE79CBC706ABD54791246E49D3B6D9A7', X'610407A389BB29409B4A81DE3382F520', X'610407A389BB29409B4A81DE3382F520', 3, NULL, NULL, 2, NULL, 0, 'Add the toasted cubes of sourdough to the leek mixture and toss together.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'602CBAC12F24B746AD2D01E668063F34', X'610407A389BB29409B4A81DE3382F520', X'610407A389BB29409B4A81DE3382F520', 4, NULL, NULL, 2, NULL, 0, 'Place the nondairy milk in a bowl and whisk in the garlic powder, onion powder, salt and ground black pepper to taste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4117E976AE7EDB47BA5410DCCD72FA17', X'610407A389BB29409B4A81DE3382F520', X'610407A389BB29409B4A81DE3382F520', 5, NULL, NULL, 2, NULL, 0, 'In a small baking dish (I used an oval baking dish about 9 inches long and 5 inches wide, but a square 8-inch dish would work as well), place the sourdough-leek mixture and spread evenly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DA916503E49124898857B01EF068099', X'610407A389BB29409B4A81DE3382F520', X'610407A389BB29409B4A81DE3382F520', 6, NULL, NULL, 2, NULL, 0, 'Pour all but ½ cup of the nondairy milk mixture over the bread-leek mixture. Press slightly to submerge the cubes of bread, but not all the way.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE75FCE83047194BB643541AA2FBA91A', X'610407A389BB29409B4A81DE3382F520', X'610407A389BB29409B4A81DE3382F520', 7, NULL, NULL, 2, NULL, 0, 'Let the pudding stand for 5 minutes, then pour in the remaining nondairy milk.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9AB05A719D3CA43B70CB8ABDF5285CA', X'610407A389BB29409B4A81DE3382F520', X'610407A389BB29409B4A81DE3382F520', 8, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 1 hour or until the pudding is set and the tops of the bread are golden-brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FA5F56F6E31B54BA18FB5391E3E86B0', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 0, NULL, NULL, 2, NULL, 0, 'In a pan, bring 1 ½ cups of water to a boil. Add the drained, soaked rice to it along with the bay leaves and enough salt to flavor the rice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA5F9A0915DCB64CA7F8A7D4077A47E0', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 1, NULL, NULL, 2, NULL, 0, 'Bring the mixture back to a boil, cover with a tight lid, and let the rice steam on the lowest heat setting for 10 minutes. Turn off the heat and let the rice stand.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F593DF6D814C14409267068D97BC1E43', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large, heavy-bottomed pan. Add the shahjeera or caraway seeds, cardamom pods, and cloves. When they begin to turn color, add the onions and bell peppers.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5CE3E7A4FFCBA459A1D2C6B19D8FA08', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 3, NULL, NULL, 2, NULL, 0, 'Saute the onions and the peppers until the onions start to brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4C410E1EB537F459B9868AC5D7EE308', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 4, NULL, NULL, 2, NULL, 0, 'Add the ginger and garlic along with the green chilies, mint and coriander leaves. Saute for 30 seconds.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E80CD095A86E449BF11522919BE5ECE', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 5, NULL, NULL, 2, NULL, 0, 'Add the pumpkin puree and mix well. Cook the pumpkin puree, stirring frequently, about 5 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AC81BF3CCFFED418FE01C815CB37260', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 6, NULL, NULL, 2, NULL, 0, 'Add the cashew paste and lemon juice and mix thoroughly. Cook for another two to three minutes, stirring frequently.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97973CEA64A4834CBC34E4B6E30C0F03', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 7, NULL, NULL, 2, NULL, 0, 'Fluff the cooked rice with a fork and add i to the pumpkin mixture in the large pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C0136A9354E30418958552A5820E9F8', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 8, NULL, NULL, 2, NULL, 0, 'Carefully, taking care not to break the grains of rice, stir them with the pumpkin mixture to coat evenly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1BC930CC493CC438DD44274D275C23F', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle on the non-dairy milk mixed with saffron threads.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D51A2AB94A73F3409325371C7DEA8124', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 10, NULL, NULL, 2, NULL, 0, 'Cover the pot with a very tight lid and let the biryani steam on a very low heat setting for 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'604BB188028B924F9D57BF9827F09615', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 11, NULL, NULL, 2, NULL, 0, 'Turn off the heat and let it stand for another 10 minutes before opening.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'570806982A02824882CB2E10E775301A', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 12, NULL, NULL, 2, NULL, 0, 'Sprinkle on some candied or dry cranberries and sauteed pumpkin seeds, as well as the spring onions.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B508B249F3124B458F715C730AAAC292', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 13, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A1C10B603C52640AA72DB1BB30F42BB', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 14, NULL, NULL, 2, NULL, 0, 'Make candied cranberries:');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0624FF2FDE2B364AA4A8A9FB472701D9', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 15, NULL, NULL, 2, NULL, 0, 'In a small baking pan, place the cranberries and sprinkle sugar over them.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F1080AB1E343143B3ACBE5A69AF2788', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 16, NULL, NULL, 2, NULL, 0, 'Cover tightly with aluminum foil and bake in a 350-degree oven for 1 hour. Stir the cranberries occasionally.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'773F7FF5CD17AB4693CE27DED9DA9F36', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 17, NULL, NULL, 2, NULL, 0, 'Let them cool and preferably chill before using.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12374683F6C3DD4A88E17FA5C61CB066', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 18, NULL, NULL, 2, NULL, 0, 'SERVING IDEAS:');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03342A038585C44AA59FA214C39D1CC5', X'8A25C0D29CDD974194A256631EE3AAEB', X'8A25C0D29CDD974194A256631EE3AAEB', 19, NULL, NULL, 2, NULL, 0, 'Serve the biryani with some of this creamy Kofta Curry or Tofu Makhani.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42A062CBAF864C449E8041B3AC617897', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 0, NULL, NULL, 2, NULL, 0, 'To make the tart shell, mix the flour, cornmeal, salt and black pepper in a bowl.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B79A4FF2A06C67438E51D0E0D4BBCA53', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 1, NULL, NULL, 2, NULL, 0, 'Cut the coconut oil into the all purpose flour with a fork or a pastry cutter until you have small pieces of the fat dispersed throughout the flour.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A314D9351626CD45A437962838A0A935', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 2, NULL, NULL, 2, NULL, 0, 'Drizzle in ice-cold water and mix with a fork until the dough comes together in a ball. Add the water slowly because you don''t want a wet dough-- it should be moist enough to just hold together.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2384787E07CEF04E86644A25E30A6611', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 3, NULL, NULL, 2, NULL, 0, 'Press the dough into a flat disc and wrap tightly in cling wrap. Refrigerate for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71DACF8419667E408FD0A870DE33B452', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 4, NULL, NULL, 2, NULL, 0, 'In the meantime, make the filling.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59B7DD3C188FE840B230B62489861051', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 5, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large saucepan and add the onions, sugar and a little salt. Cook, stirring frequently, until the onions start to caramelize and become golden brown. This took me about 20 minutes over medium heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0131855673C9C242964659EA72B8204C', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 6, NULL, NULL, 2, NULL, 0, 'When the onions are browned, stir in the thyme, then add the cognac to deglaze the pan and scrape up all the tasty brown bits stuck at the bottom. If you''d rather not use cognac, use vegetable stock or plain old water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'608F49D37E5AA54CAC3DE63F7F8E28AF', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 7, NULL, NULL, 2, NULL, 0, 'Turn off the heat and set the onions aside to cool.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B490763FC8D2C14A86087B84954AC5B2', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 8, NULL, NULL, 2, NULL, 0, 'In a blender, crumble in the tofu then add soymilk, nutritional yeast if you''re using it, mustard and salt to taste. Blend until you have a very smooth mixture.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12536291F7E2014C8EC79B374306E908', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 9, NULL, NULL, 2, NULL, 0, 'Scrape the tofu mixture into the pan with the onions and mix well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38DF360A37618549940454FA01EA425B', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 10, NULL, NULL, 2, NULL, 0, 'To assemble the tart:');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FD07CE0A108C743BFDEA74C8F9BCDF2', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 11, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62C2A2385C835F4E9952123C3E56A20B', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 12, NULL, NULL, 2, NULL, 0, 'Remove the tart dough from the refrigerator and roll it out on a floured surface to a diameter two inches larger than the base of the tart pan (mine is 9 inches wide). Fold the dough in half and then transfer to a tart pan and open it so it lies evenly across the pan. If the dough tears, don''t worry. Patch it up once you have transferred it to the pan. No one will be able to tell.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D25C0FE41E809489B053E3EA35E59F9', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 13, NULL, NULL, 2, NULL, 0, 'Run the rolling pin across the edges of the tart pan and remove any excess dough hanging over the sides.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82D269EE63DD7E4CB193B84DBC5378B4', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 14, NULL, NULL, 2, NULL, 0, 'With a fork, prick the bottom of the tart shell in several places. Then cover with aluminum foil and fill it to the brim with rice or beans or pie weights. I keep some rice in a box and reuse it every time I need to blind bake a pie or tart crust.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8ADA460EB40844994F4EEAE353CC620', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 15, NULL, NULL, 2, NULL, 0, 'Bake the filled tart crust for 10 minutes, then carefully remove the foil with the rice or beans in it, and continue to bake the crust for another 10 minutes until it is quite dry.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EAC63336556294FBB54F95C517869BC', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 16, NULL, NULL, 2, NULL, 0, 'Remove the crust from the oven and increase the heat to 425 degrees. Brush the bottom of the crust with some extra virgin olive oil. Now scrape in the filling and smooth it.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B1EAB23BA1755448A37D39E648EB663', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 17, NULL, NULL, 2, NULL, 0, 'Return the tart pan to the oven and bake for 40 minutes until the top of the tart is golden-brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD7DA7C90F06F149AF8EB38B0AAB4AF3', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 18, NULL, NULL, 2, NULL, 0, 'Remove the tart to a rack and cool 10 minutes. Then unmold it -- I do that by placing the tart on top of a mason jar and letting the rim slide off. Slide the tart off the base and onto the rack and continue cooling it for another 10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08016619AD855D49AF88B084D1D5044C', X'3EF4269661F5FF4B93254578DB87D8D1', X'3EF4269661F5FF4B93254578DB87D8D1', 19, NULL, NULL, 2, NULL, 0, 'Serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A41DBE5735511E47B26E45E08258A15C', X'297B421AEF9AA14A8D47F29D69357689', X'297B421AEF9AA14A8D47F29D69357689', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a saucepan. Add the onions, carrots and celery along with a dash of salt and ground black pepper. Sweat the vegetables until the onions are translucent. You don''t want to brown them.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59AA0FF9E903204AA84EB3E785CEB977', X'297B421AEF9AA14A8D47F29D69357689', X'297B421AEF9AA14A8D47F29D69357689', 1, NULL, NULL, 2, NULL, 0, 'Add the garlic, saute a few seconds, then add the potatoes, rosemary and thyme. Mix well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFC626D9A3909E4B9A70EB0CD420AAAC', X'297B421AEF9AA14A8D47F29D69357689', X'297B421AEF9AA14A8D47F29D69357689', 2, NULL, NULL, 2, NULL, 0, 'Add the all purpose flour and saute for a couple of minutes, stirring constantly. Add in the entire bottle of Guinness. Alcohol is highly flammable, so when adding it to anything that''s on the stove, make sure you either turn your flame off, or reduce it really low so it doesn''t catch any spatter.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2996EF50A8CA314BA0191FC326339A5E', X'297B421AEF9AA14A8D47F29D69357689', X'297B421AEF9AA14A8D47F29D69357689', 3, NULL, NULL, 2, NULL, 0, 'Stir the stew and let it bubble away for a couple of minutes. It will look really thick at this stage because of the all purpose roux, but that''s fine.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7EE7CAA4B3FE343AFA5FFF05B468654', X'297B421AEF9AA14A8D47F29D69357689', X'297B421AEF9AA14A8D47F29D69357689', 4, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and let the stew continue to cook, covered, for about 10 minutes. You want to let the potatoes get tender and you want the bitterness of the Guinness to dissipate to your liking. The more you let the stew cook, the more you''ll cook out the alcohol.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAE1DBC1BE12864AB9D90281FD0B8597', X'297B421AEF9AA14A8D47F29D69357689', X'297B421AEF9AA14A8D47F29D69357689', 5, NULL, NULL, 2, NULL, 0, 'Add the chick''n pieces to the pot and, if needed, add some of the vegetable stock to thin out your stew. You can make this as thick or thin as you want. I prefer my pot pie filling really thick, so I used less.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EB419D6D59A7D4C99A7C75157609475', X'297B421AEF9AA14A8D47F29D69357689', X'297B421AEF9AA14A8D47F29D69357689', 6, NULL, NULL, 2, NULL, 0, 'Check to see if you need more salt and ground black pepper, and turn off the heat after the stew comes to a boil. Stir in the vegan butter and the basil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0176DD5B57498F43AE41130E86C008D4', X'297B421AEF9AA14A8D47F29D69357689', X'297B421AEF9AA14A8D47F29D69357689', 7, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BAB7DFB5368E14B8DC9881278B69F9B', X'297B421AEF9AA14A8D47F29D69357689', X'297B421AEF9AA14A8D47F29D69357689', 8, NULL, NULL, 2, NULL, 0, 'To assemble your Guinness pie, pour the stew into a two quart baking dish (a deep-dish pie plate works too)');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBCCD96C1C68DA40AD43A6F939B96F02', X'297B421AEF9AA14A8D47F29D69357689', X'297B421AEF9AA14A8D47F29D69357689', 9, NULL, NULL, 2, NULL, 0, 'Cut the puff pastry sheet into strips with a pastry cutter or pizza cutter. Depending on the width or diameter of your baking dish, you may need to roll out the puff pastry a little to fit. Now layer on the strips over the stew, as nicely as you can, forming a lattice top. Trim any overhanging edges. Or, if you''d rather not do this, just place the puff pastry sheet over the stew and cut a few slits in the middle, as you would for a pie.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCDA1FA77A617C42A3158BA71613CF9F', X'297B421AEF9AA14A8D47F29D69357689', X'297B421AEF9AA14A8D47F29D69357689', 10, NULL, NULL, 2, NULL, 0, 'Place the Guinness Pie in the oven and bake 30 minutes or until the puff pastry is all golden and the stew is bubbling around the edges.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DAE78AEA0A93F4281D51F4D23A6EEB1', X'297B421AEF9AA14A8D47F29D69357689', X'297B421AEF9AA14A8D47F29D69357689', 11, NULL, NULL, 2, NULL, 0, 'Remove from the oven and serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7B2D91C15D33547A3CCD1EB6E983917', X'A5A09B2C9DEE5A4CBA5446DE6BACDE2E', X'A5A09B2C9DEE5A4CBA5446DE6BACDE2E', 0, NULL, NULL, 2, NULL, 0, 'Pour a cup of boiling water over the dry shiitake mushrooms in a bowl and let them steep for 20 minutes. Drain the mushrooms and squeeze out as much water from them as possible. Reserve the stock for use in the gravy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BB1D5A458CF154BAEF6FE191AFF334D', X'A5A09B2C9DEE5A4CBA5446DE6BACDE2E', X'A5A09B2C9DEE5A4CBA5446DE6BACDE2E', 1, NULL, NULL, 2, NULL, 0, 'Place the mushrooms in a food processor and pulse five or six times until they are broken down into small bits. Add the rest of the ingredients, except the oil, to the food processor and pulse a few times until they are thoroughly mixed and the mixture holds together when you form a ball.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7607683BF5970A45BC31785524E3A3E6', X'A5A09B2C9DEE5A4CBA5446DE6BACDE2E', X'A5A09B2C9DEE5A4CBA5446DE6BACDE2E', 2, NULL, NULL, 2, NULL, 0, 'Shape into 20 meatballs. Heat the oil in a large skillet and fry the meatballs, turning them to brown all over. Remove the meatballs to a bowl large enough to hold the sauce.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8BFB4C83778B44BBF9C4FD6CA7D6814', X'96B1BA86D1EA99409617F8262647D741', X'96B1BA86D1EA99409617F8262647D741', 0, NULL, NULL, 2, NULL, 0, 'Blend the cashews with ½ cup water into a very thick cream. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F5DCAC859C6C2409BCC693FA4661B66', X'96B1BA86D1EA99409617F8262647D741', X'96B1BA86D1EA99409617F8262647D741', 1, NULL, NULL, 2, NULL, 0, 'Heat oil in a large saucepan. Add the onions and garlic along with some salt and pepper and saute for five to six minutes over medium heat. The onions should brown nicely so your sauce has great flavor, but don''t let the garlic burn. Stir frequently.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'039CE7CF7849704A954C036DFD54C569', X'96B1BA86D1EA99409617F8262647D741', X'96B1BA86D1EA99409617F8262647D741', 2, NULL, NULL, 2, NULL, 0, 'Add the mushrooms to the pan along with the apple cider vinegar. Saute until the mushrooms have expressed most of their water and it has evaporated, another five to six minutes over medium-high heat. Now add the flour and stir until it gets absorbed into the mushrooms.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'223621E999520E48A4E610C06D7C7E79', X'96B1BA86D1EA99409617F8262647D741', X'96B1BA86D1EA99409617F8262647D741', 3, NULL, NULL, 2, NULL, 0, 'Stir in the rosemary, then pour the vegetable stock in, stirring constantly, so the flour dissolves in it. Bring the stock to a boil. It will look very soupy at this stage, but don''t worry -- you need to let the stock reduce to about four cups, which will take about 10-15 minutes over high heat. Stir occasionally to ensure nothing sticks to the bottom.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C95A7B4F9590B54D8283BE84B65D6D54', X'96B1BA86D1EA99409617F8262647D741', X'96B1BA86D1EA99409617F8262647D741', 4, NULL, NULL, 2, NULL, 0, 'Once the sauce has reduced and is quite thick, season with salt and pepper. Wait until the sauce has cooled a little or, if doing this while the sauce is still hot, very carefully strain it into a large bowl.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11735D21008F2C41964C4A23D985D028', X'96B1BA86D1EA99409617F8262647D741', X'96B1BA86D1EA99409617F8262647D741', 5, NULL, NULL, 2, NULL, 0, 'Pour the sauce back into the saucepan and bring to a boil over medium-low heat. Stir in the liquid aminos or soy sauce and cashew cream. Turn off the heat and pour over the meatballs. Garnish with parsley and serve hot or warm.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B2CFCD4D1D7944695D05C6A46928D2F', X'F6824B7D38A0444A85B2D8ECA01A75D0', X'F6824B7D38A0444A85B2D8ECA01A75D0', 0, NULL, NULL, 2, NULL, 0, 'Make the mashed potatoes by mixing together the potatoes, olive oil, almond milk and salt and ground black pepper. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1E6FCFCAC3659468D392F1670214EE1', X'F6824B7D38A0444A85B2D8ECA01A75D0', X'F6824B7D38A0444A85B2D8ECA01A75D0', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil for the filling in a saucepan. Add the onions, celery. peas and carrots and the minced garlic. Add salt and a good pinch of ground black pepper. Saute until the onions turn translucent.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'032C695AD2359C408586793509C55699', X'F6824B7D38A0444A85B2D8ECA01A75D0', X'F6824B7D38A0444A85B2D8ECA01A75D0', 2, NULL, NULL, 2, NULL, 0, 'Add the mushrooms and tempeh and the whiskey. Saute, stirring frequently, until the liquid has evaporated.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BCC59ECEA231C41868CCDFCCFA029EE', X'F6824B7D38A0444A85B2D8ECA01A75D0', X'F6824B7D38A0444A85B2D8ECA01A75D0', 3, NULL, NULL, 2, NULL, 0, 'Add the tamari, mustard, thyme, mint, paprika, cayenne and some ground black pepper. Mix well, then add the lentils.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF25B1C31599994B960F06AD2F358A54', X'F6824B7D38A0444A85B2D8ECA01A75D0', X'F6824B7D38A0444A85B2D8ECA01A75D0', 4, NULL, NULL, 2, NULL, 0, 'Cook until most of the moisture has evaporated. Add salt.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'416E7578C2097A4B9AAD7ECD578001F7', X'F6824B7D38A0444A85B2D8ECA01A75D0', X'F6824B7D38A0444A85B2D8ECA01A75D0', 5, NULL, NULL, 2, NULL, 0, 'Oil a baking dish (I used an oval one that''s 10 inches long). Spoon the lentil-tempeh filling into the dish.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B33EC441D73CE48A2189F47F4D6EFBD', X'F6824B7D38A0444A85B2D8ECA01A75D0', X'F6824B7D38A0444A85B2D8ECA01A75D0', 6, NULL, NULL, 2, NULL, 0, 'Layer on the mashed potatoes evenly. When you''re done, run the tines of the fork through the top of the potatoes to give it a pretty, traditional look.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F47FBABBBCE6C46B69A0E9C284F29C7', X'F6824B7D38A0444A85B2D8ECA01A75D0', X'F6824B7D38A0444A85B2D8ECA01A75D0', 7, NULL, NULL, 2, NULL, 0, 'Spray the top with some oil. Preheat an oven to 400 degrees Fahrenheit and bake the Shepherd''s Pie for 25 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB28267E7E8E7547A0A295F790E7A476', X'F6824B7D38A0444A85B2D8ECA01A75D0', X'F6824B7D38A0444A85B2D8ECA01A75D0', 8, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD583846F75F464CBCA2EC22A5C642CE', X'CB20DAB55B0B3A4A98A7E0627CCD243C', X'CB20DAB55B0B3A4A98A7E0627CCD243C', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5B5017FB6CBCC42B4AEB1A8D2058AC1', X'9967804D2184274AB8513B197A7EEF3C', X'9967804D2184274AB8513B197A7EEF3C', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients for the crust in a food processor and add water, a tablespoon at a time, until the mixture comes together.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0FD90559CFA2745975C8BC00F056019', X'9967804D2184274AB8513B197A7EEF3C', X'9967804D2184274AB8513B197A7EEF3C', 1, NULL, NULL, 2, NULL, 0, 'Dump the almond cookie dough into an 8-inch tart pan and press down with the heel of your hand to form an even layer. Press the dough up the sides of the pan with your fingers until the entire pan is covered. Prick the bottom of the crust all over with the tines of a fork and refrigerate until you make the filling.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C90095458957D3499E326EFEC18CDCBA', X'0ACC87670F692340908B883D31E974E7', X'0ACC87670F692340908B883D31E974E7', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients for the filling in the food processor and process until smooth. Pour into the tart shell. Use a knife or spatula to spread it evenly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD31F2716DB008419FA46301B7C9D91F', X'0B09AB3A9CE9AC449CF1CF8357B2D4DD', X'0B09AB3A9CE9AC449CF1CF8357B2D4DD', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients for the praline in the food processor and process until the nuts are chopped into fairly small pieces. You can stop earlier, if you prefer larger pieces of pecans.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9F1B38340AABD4B9BCD3EFDE5A0755C', X'0B09AB3A9CE9AC449CF1CF8357B2D4DD', X'0B09AB3A9CE9AC449CF1CF8357B2D4DD', 1, NULL, NULL, 2, NULL, 0, 'Spread the praline evenly over the top of the filling.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6043ED09D053174786F57AF6D4C48A18', X'0B09AB3A9CE9AC449CF1CF8357B2D4DD', X'0B09AB3A9CE9AC449CF1CF8357B2D4DD', 2, NULL, NULL, 2, NULL, 0, 'Place the tart pan on a cookie sheet and bake 40 minutes or until the top feels firm.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96F24AD8AD78534C9F520748A8E2F2AB', X'0B09AB3A9CE9AC449CF1CF8357B2D4DD', X'0B09AB3A9CE9AC449CF1CF8357B2D4DD', 3, NULL, NULL, 2, NULL, 0, 'Remove and let cool thoroughly. Chill for an hour before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D7A4CBD3010C94EB93E971C8F18DC02', X'A627FAA3C0D9544096CF683857AB71D8', X'A627FAA3C0D9544096CF683857AB71D8', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59800DD271891C47A04D6DC8EA7F7F11', X'A627FAA3C0D9544096CF683857AB71D8', X'A627FAA3C0D9544096CF683857AB71D8', 1, NULL, NULL, 2, NULL, 0, 'Mix the apples with the sugar in a large bowl.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8008EEA2CD33E64E945A9117C74343F1', X'A627FAA3C0D9544096CF683857AB71D8', X'A627FAA3C0D9544096CF683857AB71D8', 2, NULL, NULL, 2, NULL, 0, 'Roll out the puff pastry sheet to fit in your tart pan. I used a 12 X 8 inch rectangular pan with a removable bottom, but a round one would work too.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FED74199D4E454B83C025448A0815E4', X'A627FAA3C0D9544096CF683857AB71D8', X'A627FAA3C0D9544096CF683857AB71D8', 3, NULL, NULL, 2, NULL, 0, 'Place the puff pastry inside the pan and press in the sides to fit to the pan. You can use a rolling pin to cut any overhanging pastry dough, or tuck it under the edges to get a neat look. With a fork, prick the dough all over to keep it from rising in the pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE302322BC40BE429346B84750F69876', X'A627FAA3C0D9544096CF683857AB71D8', X'A627FAA3C0D9544096CF683857AB71D8', 4, NULL, NULL, 2, NULL, 0, 'Layer the sliced apples inside the puff pastry in a decorative pattern, tucking any small pieces into the corners to ensure the surface is evenly covered. If you''re working with a round pan, you can layer them in concentric circles.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D0B46D6B563DD4E91D3B0B964799EC8', X'A627FAA3C0D9544096CF683857AB71D8', X'A627FAA3C0D9544096CF683857AB71D8', 5, NULL, NULL, 2, NULL, 0, 'Place the tart on a rimmed baking sheet to catch any juices that spill over, and then in the oven. Bake 45 minutes, turning around once halfway through baking. Remove to a rack.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95945BFCDDF78B4882E284F845EDB8ED', X'A627FAA3C0D9544096CF683857AB71D8', X'A627FAA3C0D9544096CF683857AB71D8', 6, NULL, NULL, 2, NULL, 0, 'Heat the apricot jam with the water or liqueur in a small saucepan until it''s runny enough to work with. Brush on top of the apples once the tart is out of the oven. If your pan has a removable bottom, unmold before the tart cools down completely because any running juices could cause the pastry to stick to the pan. Continue cooling on a rack. Slice and serve at room temperature.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F52B618E0E992458EE6669009224499', X'7F5C172F709D0340ACC33ED78F1C3024', X'7F5C172F709D0340ACC33ED78F1C3024', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and sugar in a bowl. Add the cubed butter. Using a pastry cutter, cut the butter into the flour until you have large crumbs of butter dispersed through the dough.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3749112071531D47AC5DE2FF3425881B', X'7F5C172F709D0340ACC33ED78F1C3024', X'7F5C172F709D0340ACC33ED78F1C3024', 1, NULL, NULL, 2, NULL, 0, 'Using a fork to mix, drizzle ice cold water into the flour, a little at a time, until the dough comes together in a ball. Don''t add too much water, the dough shouldn''t be wet. It should just hold together without any dry flour remaining.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1188DF7A2E43CE4ABF4FF94FBAA48F7B', X'7F5C172F709D0340ACC33ED78F1C3024', X'7F5C172F709D0340ACC33ED78F1C3024', 2, NULL, NULL, 2, NULL, 0, 'Divide the dough into two and wrap tightly in cling wrap, shaping each ball into a disc. Refrigerate for at least 30 minutes or overnight. You can make the dough the earlier day.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBBC08BAAD74A5499B03B9E68DA6B121', X'0F99D6D8DC228349ACF2D7660F3EEB17', X'0F99D6D8DC228349ACF2D7660F3EEB17', 0, NULL, NULL, 2, NULL, 0, 'Place the cherries in a saucepan and stir for a couple of minutes until they begin to express liquid. Let the cherries cook another five minutes at which point they will have expressed a good amount of liquid. Using a spider strainer or a slotted spoon, remove the cherries to a bowl, leaving the juices behind. Bring the juice to a boil and let it reduce to about half, which should take around 5-10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2958423A0E86A74F89C2E1EDD51147D7', X'0F99D6D8DC228349ACF2D7660F3EEB17', X'0F99D6D8DC228349ACF2D7660F3EEB17', 1, NULL, NULL, 2, NULL, 0, 'Add the sugar to the cherry juice and stir until dissolved. Now add the cornstarch to the liquid, a tablespoon at a time, whisking in each time until thoroughly dissolved. Once all the cornstarch has been dissolved in the cherry juices, turn off the heat and pour the juice into the cherries. Mix in the vanilla and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C64F6055C1F4447BC28839A44534757', X'B3EF9222B53DA1408E0BDEFDB7FD9BC2', X'B3EF9222B53DA1408E0BDEFDB7FD9BC2', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0F057DE0A3BDC4BA9314B304E2F515C', X'B3EF9222B53DA1408E0BDEFDB7FD9BC2', X'B3EF9222B53DA1408E0BDEFDB7FD9BC2', 1, NULL, NULL, 2, NULL, 0, 'To assemble the pie, roll out one disc of dough to a width slightly larger than your pie plate. Fit it into the bottom of the pie plate, leaving a slight overhang. Refrigerate while you roll out the top crust.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DC0B524ECE87B419B34D696B8F91C95', X'B3EF9222B53DA1408E0BDEFDB7FD9BC2', X'B3EF9222B53DA1408E0BDEFDB7FD9BC2', 2, NULL, NULL, 2, NULL, 0, 'You can make a decorative lattice top, like I did (not the best looking lattice, I''ll admit, but it still looks quite nice), or just make a plain crust that''ll still look beautiful when it bakes up all golden and flaky. To make a lattice top, roll out the dough to a disc slightly wider than the pie plate and cut it into thin strips using a pizza cutter. Arrange the strips in a lattice pattern on top of the pie and then seal the edges, cutting out any overhanging dough. The filling is rather juicy which makes the lattice top harder to arrange, so if you aren''t very comfortable doing it, just make a plain top crust. To do this, place the rolled-out dough on top of the filling, press down the edges of the dough to seal with the edges of the bottom crust using the tines of a fork, and cut four vents at the top of the pie to let the steam out as the pie bakes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C9112A2434D734A8CBC1178CEDA3A86', X'B3EF9222B53DA1408E0BDEFDB7FD9BC2', X'B3EF9222B53DA1408E0BDEFDB7FD9BC2', 3, NULL, NULL, 2, NULL, 0, 'Place the pie on a cookie sheet and bake in the preheated oven for 50 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFE792D56DF5FB49B43FCB3EC209A621', X'B3EF9222B53DA1408E0BDEFDB7FD9BC2', X'B3EF9222B53DA1408E0BDEFDB7FD9BC2', 4, NULL, NULL, 2, NULL, 0, 'Remove the pie plate to a rack and cool thoroughly for 3-4 hours before cutting and serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ACFD208CC285441A76F63DD22B59476', X'053B38BC2BD04646B9BC95E240604140', X'053B38BC2BD04646B9BC95E240604140', 0, NULL, NULL, 2, NULL, 0, 'Place the flour in a bowl with sugar and salt. Whisk together, then add the cold cubes of butter and cut into the flour with a pie cutter or a fork until you have a crumbly mixture with some pea-sized pieces of butter.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56C294BCB2F20B4B81B5E098B63AE604', X'053B38BC2BD04646B9BC95E240604140', X'053B38BC2BD04646B9BC95E240604140', 1, NULL, NULL, 2, NULL, 0, 'Drizzle in the water, a little at a time, while mixing with a fork. As soon as the dough comes together and holds, turn it out into cling wrap and wrap tightly while shaping into a disc with your palms. Refrigerate for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4898791951CB954ABB5559B769F6F73F', X'F3A1CE0BE286894B8D9A81AC3AE77A59', X'F3A1CE0BE286894B8D9A81AC3AE77A59', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20E95282E2F04F4C8D832C666198087E', X'F3A1CE0BE286894B8D9A81AC3AE77A59', X'F3A1CE0BE286894B8D9A81AC3AE77A59', 1, NULL, NULL, 2, NULL, 0, 'Blend the cashews with ¼ cup water until very smooth.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'428AE20009202146983972C0059D7C03', X'F3A1CE0BE286894B8D9A81AC3AE77A59', X'F3A1CE0BE286894B8D9A81AC3AE77A59', 2, NULL, NULL, 2, NULL, 0, 'Prick the sweet potatoes all over with a knife or fork. Place on a baking sheet and bake for 35-45 minutes or until a knife inserted in the center slides easily all the way through without pressure. Remove the sweet potatoes from the oven and set aside until cool enough to handle.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0141043CAE0B3440BB2AFBE010BA7E42', X'F3A1CE0BE286894B8D9A81AC3AE77A59', X'F3A1CE0BE286894B8D9A81AC3AE77A59', 3, NULL, NULL, 2, NULL, 0, 'Peel the sweet potatoes and place them in a food processor (or you can do this in a bowl), along with the cashew cream, sugar, tapioca starch, coconut oil, cinnamon, nutmeg and pure vanilla extract. Process until you have a very smooth mixture.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FCBC6758F82284EA67BEFA49DAC7CB9', X'61923E748A5F594382AB5ACC0AEEA114', X'61923E748A5F594382AB5ACC0AEEA114', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E6DDDCBFD46634BB5B6422CF22CAFC3', X'61923E748A5F594382AB5ACC0AEEA114', X'61923E748A5F594382AB5ACC0AEEA114', 1, NULL, NULL, 2, NULL, 0, 'On a floured surface, roll out the pie dough into a circle big enough to fit your pie plate. Place the circle of dough into the pie plate and crimp the edges with a fork or your fingers.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'629F3F3473E16543897E45EC44095B2B', X'61923E748A5F594382AB5ACC0AEEA114', X'61923E748A5F594382AB5ACC0AEEA114', 2, NULL, NULL, 2, NULL, 0, 'To make a decorative design the way I did, you''ll need extra pie crust dough. I doubled my pie crust recipe and then cut out stars using a cookie cutter. You can make all sorts of designs -- leaves, flowers, hearts, circles, they all work.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'168F5E41B0608149895F5D853A3F3E92', X'61923E748A5F594382AB5ACC0AEEA114', X'61923E748A5F594382AB5ACC0AEEA114', 3, NULL, NULL, 2, NULL, 0, 'Pour the filling into the pie crust. The filling is quite thick, so smooth it down with a spatula.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAC5DFD68DCDD54D853C4FD8E946990E', X'61923E748A5F594382AB5ACC0AEEA114', X'61923E748A5F594382AB5ACC0AEEA114', 4, NULL, NULL, 2, NULL, 0, 'Bake the pie for 15 minutes, then turn down the heat to 350 degrees and bake 40 minutes. At this point, if you have a top crust, tent the pie loosely with aluminum foil and continue baking another 15 minutes or until the top is set and a knife near the center comes out almost clean. Your filling won''t set completely in the oven the way it would if you were using eggs, but it will set as it cools down.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5CC7879021062429656C87318A988A0', X'61923E748A5F594382AB5ACC0AEEA114', X'61923E748A5F594382AB5ACC0AEEA114', 5, NULL, NULL, 2, NULL, 0, 'Remove the pie from the oven and let it stand on a rack until completely cool. You can also chill the pie for about 30 minutes in the refrigerator.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'183D909425C30B4F9B4BF875FCD092E7', X'61923E748A5F594382AB5ACC0AEEA114', X'61923E748A5F594382AB5ACC0AEEA114', 6, NULL, NULL, 2, NULL, 0, 'Slice and serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2F5D2557B813946AE033960E5B98C18', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 0, NULL, NULL, 2, NULL, 0, 'Make the crust: You can do this in a food processor or in a bowl. If using a food processor, place all ingredients except water in the food processor and pulse a few times, drizzling water in a tablespoon at a time, until the dough comes together. You don''t want a wet dough because that will encourage gluten formation, making your crust chewy. Err, if you will, on the side of using less water than more.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A82C21AD0903E4C8866434CD31E4427', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 1, NULL, NULL, 2, NULL, 0, 'If you''re doing this by hand, place all the ingredients except water in a bowl and, using a fork, cut the pieces of butter into the flour until they break down into smaller bits but don''t melt.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2218F1FCC97B3B4BBB03D33DA69D96B2', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 2, NULL, NULL, 2, NULL, 0, 'Drizzle in the water, a tablespoon at a time, mixing with your fork, until the dough just comes together. Again, try not to add too much water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97495FEF2E4A2444AC926FBA8E46C596', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 3, NULL, NULL, 2, NULL, 0, 'Wrap the dough in cling wrap, patting with your fingers to shape it into a disc. Refrigerate while you make the filling.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1973514581E4C948AC0E5ABAB68CE540', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 4, NULL, NULL, 2, NULL, 0, 'Make the filling: Place the sliced apples in a bowl with the sugar, lemon, and ground ginger. Mix well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9B64D6A094A02459F7B964B807FEEEC', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 5, NULL, NULL, 2, NULL, 0, 'Set aside for 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FF90188F875E1499132EDFD9753A466', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 6, NULL, NULL, 2, NULL, 0, 'Assemble the crostata:Preheat the oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCBB9AD1C1C5164A8BCFF10A4590CEB1', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 7, NULL, NULL, 2, NULL, 0, 'Prepare a baking sheet by lining it with some parchment paper or foil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B1358B9DF97C7458D3EDAC3226B450E', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 8, NULL, NULL, 2, NULL, 0, 'Flour your rolling pin and the kitchen counter. Roll the dough into a disc about 12 inches across, trying to keep it as round as possible. If the edges start to fray, knock them back into a cleaner line by tapping with your fingers.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F547ABF47C43A468B831392531E9A36', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 9, NULL, NULL, 2, NULL, 0, 'Be sure to keep turning the dough as you roll to ensure it doesn''t stick to the surface, and dust with more flour if needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A5698756C6C494780ABAC56A06D3C6B', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 10, NULL, NULL, 2, NULL, 0, 'Fold the crust once and transfer to the baking sheet. Unfold.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33CFC7160C17274C9A1ABF61007D3F15', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 11, NULL, NULL, 2, NULL, 0, 'Arrange the apple slices inside the crostata, leaving a 1-½ inch border. You can either overlap them in a circle, as I did, or you can just pile them in and spread them so they are in an even layer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AD3E754CB5B0245B1A0842B1856BFCA', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 12, NULL, NULL, 2, NULL, 0, 'Fold the edges of the crostata over. You can be as precise or casual about this as you like: it''s going to look great either way. If there is some juice left at the bottom of the bowl with apples, reserve it.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AB12C00098185499F19EEB6BC50992A', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 13, NULL, NULL, 2, NULL, 0, 'Place the crostata in the preheated oven and bake for 40 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5206521CB841414FA7FB942F19F36B4D', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 14, NULL, NULL, 2, NULL, 0, 'Remove the crostata, brush the crust and the tops of the apples with the reserved juice from the apples.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97F7A4806065E540B986FF49078F58F0', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 15, NULL, NULL, 2, NULL, 0, 'Put the crostata back in the oven and bake another 15 minutes. The crust should be golden.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53BAB47934ACBF4FB48F91AB231CCD4D', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 16, NULL, NULL, 2, NULL, 0, 'Remove the crostata from the oven and, using a wide spatula, transfer immediately from the parchment lined sheet to a rack. Be careful because the dough still feels pliable at this time-- it will firm up as it cools.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33F0D30A1D7C164A85D0137D95537CFB', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 17, NULL, NULL, 2, NULL, 0, 'Scatter cranberries and almonds on top.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20A9DFABEC1BFA45B36DD4791C15C9A9', X'2D763F8DF7FAE2428B4DB4C26487082C', X'2D763F8DF7FAE2428B4DB4C26487082C', 18, NULL, NULL, 2, NULL, 0, 'Serve after the crostata has cooled thoroughly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7016B2E274D8FA4F889EBC9AB2C706A4', X'12BD0CE002EB7248A70441389DA8D062', X'12BD0CE002EB7248A70441389DA8D062', 0, NULL, NULL, 2, NULL, 0, 'Cut the butter into 1-cm cubes and place in the food processor along with the flour, sugar and salt. Pulse a few times, for a few seconds each time, until the dough forms clumps.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3794364AD8C814B8B7A2647BFA7B01C', X'12BD0CE002EB7248A70441389DA8D062', X'12BD0CE002EB7248A70441389DA8D062', 1, NULL, NULL, 2, NULL, 0, 'Take an eight-inch tart pan with a removable bottom. Empty all of the filling into the the tart pan and then, working from the center, press the dough evenly around the bottom and the sides of the tart pan. When you have a fairly even layer, use a spoon or spatula to smooth it out.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BF43C78E68D06499F9A40E980F7E065', X'12BD0CE002EB7248A70441389DA8D062', X'12BD0CE002EB7248A70441389DA8D062', 2, NULL, NULL, 2, NULL, 0, 'Pierce the bottom of the tart shell several times with the tines of a fork. This will keep it from puffing up when you bake.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AE4A90718E88F4DAE575F9A3A85E6C7', X'12BD0CE002EB7248A70441389DA8D062', X'12BD0CE002EB7248A70441389DA8D062', 3, NULL, NULL, 2, NULL, 0, 'Cover the tart pan with cling wrap and freeze it for 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E481407AB2D0D49B541900C4F7991EE', X'12BD0CE002EB7248A70441389DA8D062', X'12BD0CE002EB7248A70441389DA8D062', 4, NULL, NULL, 2, NULL, 0, 'Remove from the freezer and bake in a preheated 425 degree oven for 18 minutes. Meanwhile, make the filling.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AC873E28E98574C9A3E1C89074A64ED', X'96BCEBDE9288974AACC0A76272609BCC', X'96BCEBDE9288974AACC0A76272609BCC', 0, NULL, NULL, 2, NULL, 0, 'Drain the hazelnuts from the soaking water, then place it in a high-powered blender with 1 ½ cups of water. Blend until very smooth.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10411FEDFE8D25488E13C5631BA57198', X'96BCEBDE9288974AACC0A76272609BCC', X'96BCEBDE9288974AACC0A76272609BCC', 1, NULL, NULL, 2, NULL, 0, 'Strain the hazelnut paste through a medium sieve. I really only do this to get any big bits of skin out. The strained paste will be quite thick, like the consistency of pancake batter.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EF2DE0033F5854599D2AC56A846F46F', X'96BCEBDE9288974AACC0A76272609BCC', X'96BCEBDE9288974AACC0A76272609BCC', 2, NULL, NULL, 2, NULL, 0, 'Heat the hazelnut paste in a small saucepan until it starts to bubble, which it will quite fast. Don''t leave it unsupervised.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23DF6EE43F087E4C87051B8BA194E8EF', X'96BCEBDE9288974AACC0A76272609BCC', X'96BCEBDE9288974AACC0A76272609BCC', 3, NULL, NULL, 2, NULL, 0, 'Once the hazelnut paste has heated, stir in the chocolate chips, maple syrup, vanilla and salt. Turn off the heat and mix until the filling is very smooth.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CDE953E0FCF0742AAD437B9AFCAC74F', X'96BCEBDE9288974AACC0A76272609BCC', X'96BCEBDE9288974AACC0A76272609BCC', 4, NULL, NULL, 2, NULL, 0, 'Pour the filling into the tart shell and after about an hour carefully transfer to the refrigerator where it should remain for at least six hours until it''s completely chilled and the filling is set. Dust with some cocoa powder, if you wish.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD244EE0B2265A49B7009B3700877E15', X'96BCEBDE9288974AACC0A76272609BCC', X'96BCEBDE9288974AACC0A76272609BCC', 5, NULL, NULL, 2, NULL, 0, 'Remove the tart from the pan and slice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E6F33C949DC024484A25F48D94BE03D', X'9708CBBB90526C44996B4EB6C2924039', X'9708CBBB90526C44996B4EB6C2924039', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE83C82DD662A647B8AE0A275EAA141A', X'9708CBBB90526C44996B4EB6C2924039', X'9708CBBB90526C44996B4EB6C2924039', 1, NULL, NULL, 2, NULL, 0, 'Place all the crust ingredients in the food processor or a bowl and mix until they come together in a ball.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E4CAF6A3D986F4E916E6DAEFC18F0F1', X'9708CBBB90526C44996B4EB6C2924039', X'9708CBBB90526C44996B4EB6C2924039', 2, NULL, NULL, 2, NULL, 0, 'Remove the dough to a pie plate and pat the crust into it in an even layer, making sure you come up all the way up the sides.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEFCE1DC9D398B4FB34162B64E4923BD', X'9708CBBB90526C44996B4EB6C2924039', X'9708CBBB90526C44996B4EB6C2924039', 3, NULL, NULL, 2, NULL, 0, 'Place the pie plate in the oven and bake 11 minutes or until the edges are light gold. Remove and set aside to cool.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EF5D8C1625BBF448202844FA5B50F1E', X'119A5B7329673C49A31CBE27A19758A1', X'119A5B7329673C49A31CBE27A19758A1', 0, NULL, NULL, 2, NULL, 0, 'Place the cocoa butter in a bowl along with the coconut oil and place the bowl on a saucepan with at least an inch of water in it. Turn on the heat. The cocoa butter will melt into a clear liquid. Keep stirring until there are no pieces of cocoa buttter remaining. Add the sugar to the bowl and turn off the heat after two minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2C01FE97C914346A7C19DDC54C8AC6B', X'119A5B7329673C49A31CBE27A19758A1', X'119A5B7329673C49A31CBE27A19758A1', 1, NULL, NULL, 2, NULL, 0, 'Blend the cashews with ½ cup water into a very smooth, silky paste. If your blender isn''t powerful, you may need to soak the cashews first for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A2E45BFF807724CB4285436AC9F53E1', X'119A5B7329673C49A31CBE27A19758A1', X'119A5B7329673C49A31CBE27A19758A1', 2, NULL, NULL, 2, NULL, 0, 'Add the cashew paste to the food processor along with the cocoa butter and sugar mixture, vanilla extract and cream cheese. Process for a few seconds until everything is thoroughly incorporated into a very silky paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EE26BB9E1FEEB4CBD3635A4D4151DB0', X'119A5B7329673C49A31CBE27A19758A1', X'119A5B7329673C49A31CBE27A19758A1', 3, NULL, NULL, 2, NULL, 0, 'Pour the white chocolate filling into the prepared and baked pie crust. Freeze for at least two hours.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48CBC495F74CE2449FE7816FA6FCA3D7', X'119A5B7329673C49A31CBE27A19758A1', X'119A5B7329673C49A31CBE27A19758A1', 4, NULL, NULL, 2, NULL, 0, 'If making the raspberry drizzle, heat the raspberry jam in a saucepan with the water until smooth and fairly runny. Drizzle over the cheesecake before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB16519AA93F0343A9840B985DB688A0', X'2C066CDD3EBD764989597F5B3115DBD1', X'2C066CDD3EBD764989597F5B3115DBD1', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour and sugar in a bowl.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63A745D89C0E3942A169F8C8AB750487', X'2C066CDD3EBD764989597F5B3115DBD1', X'2C066CDD3EBD764989597F5B3115DBD1', 1, NULL, NULL, 2, NULL, 0, 'Cut the butter into ¼-inch cubes and add to the flour. Using a pastry cutter or a fork, `cut` the butter into the flour until you have small pieces of butter evenly dispersed through the flour.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA3429F18E0570418505423C27C384B8', X'2C066CDD3EBD764989597F5B3115DBD1', X'2C066CDD3EBD764989597F5B3115DBD1', 2, NULL, NULL, 2, NULL, 0, 'Drizzle ice-cold water into the dough, a little at a time, mixing with a fork, until the dough is moist enough to hold together.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3363D9251E0FA54C8F28F3D63751401C', X'2C066CDD3EBD764989597F5B3115DBD1', X'2C066CDD3EBD764989597F5B3115DBD1', 3, NULL, NULL, 2, NULL, 0, 'Divide the dough into two, and shape each half into a disc. Wrap tightly in cling wrap and refrigerate for at least 30 minutes or as much as 2 days. If making well in advance, freeze and thaw completely before using.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EBF69426895F84D8B90F5A558B62AED', X'93FE5C5D7AB6DF47B13B889C71F2D37F', X'93FE5C5D7AB6DF47B13B889C71F2D37F', 0, NULL, NULL, 2, NULL, 0, 'Place all the ingredients for the filling in a large bowl. Mix thoroughly and let the filling stand at least 30 minutes before baking. You can also make the filling the day before and refrigerate.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C15A5BCD90CB204EB435C7F10ADFD40C', X'BE795FE5FAA244469DB39E6080409F22', X'BE795FE5FAA244469DB39E6080409F22', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B644B44D7EA72B44AC7F83331C464C47', X'BE795FE5FAA244469DB39E6080409F22', X'BE795FE5FAA244469DB39E6080409F22', 1, NULL, NULL, 2, NULL, 0, 'Roll out one of the crusts so it''s big enough to fit into a 9-inch cast iron skillet. Place the crust into the skillet and fit it to the sides. Refrigerate until you make the top crust.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75172A8F2B8EEF45AA340DF562329835', X'BE795FE5FAA244469DB39E6080409F22', X'BE795FE5FAA244469DB39E6080409F22', 2, NULL, NULL, 2, NULL, 0, 'Roll out the other crust to a diameter about 2 inches wider than the top of the cast iron skillet. You need to make vents in the upper crust for steam to escape while baking. You can either do this with a knife after you place the top crust on the filling or, if you want to cut a decorative shape with a cookie cutter (I cut a star), do it before placing the crust on the filling.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6182AF04ADA13E4DACC3A9DEF01B93B8', X'BE795FE5FAA244469DB39E6080409F22', X'BE795FE5FAA244469DB39E6080409F22', 3, NULL, NULL, 2, NULL, 0, 'Pour the apple filling and any juices into the bottom crust in the skillet. Then fold the top crust in half and carefully transfer to the skillet, covering the filling completely. Fold in any overhanging dough and crimp with your fingers or with a fork for a decorative edge, and to seal in the apples.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D07F8F2A417B440BC951BC15561C642', X'BE795FE5FAA244469DB39E6080409F22', X'BE795FE5FAA244469DB39E6080409F22', 4, NULL, NULL, 2, NULL, 0, 'For a golden crust, brush on a mixture of 1 tbsp oil and 1 tbsp nondairy milk on the top crust.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB0A5A88CF7EA441BC30949176122397', X'BE795FE5FAA244469DB39E6080409F22', X'BE795FE5FAA244469DB39E6080409F22', 5, NULL, NULL, 2, NULL, 0, 'Bake in the oven for 45 minutes or until the top is golden brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54DD355C531DAB4D80400EF4D28F573F', X'BE795FE5FAA244469DB39E6080409F22', X'BE795FE5FAA244469DB39E6080409F22', 6, NULL, NULL, 2, NULL, 0, 'Let the pie cool thoroughly on a rack before cutting into it. Exercise care when you handle the hot iron skillet because it is heavy and extremely hot. Make sure you wear oven mitts at all times when handling it before it cools down thoroughly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11415F25A739C4459EEA77A3855EC4BB', X'0F8D484EEBEF4448B7B79E23566ED153', X'0F8D484EEBEF4448B7B79E23566ED153', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADACBC0D7905734BBA7E2E36B4CE62BC', X'0F8D484EEBEF4448B7B79E23566ED153', X'0F8D484EEBEF4448B7B79E23566ED153', 1, NULL, NULL, 2, NULL, 0, 'Oil and flour a 9-inch springform pan or a bundt pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38A0FB6715CBCB429BF627334A3F9BD6', X'0F8D484EEBEF4448B7B79E23566ED153', X'0F8D484EEBEF4448B7B79E23566ED153', 2, NULL, NULL, 2, NULL, 0, 'Mix the flour, baking powder, baking soda and salt in a bowl.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3B54FB33FA9FD4D8B2C5E12EBF08A4B', X'0F8D484EEBEF4448B7B79E23566ED153', X'0F8D484EEBEF4448B7B79E23566ED153', 3, NULL, NULL, 2, NULL, 0, 'Put the oil, sugar and applesauce in a large bowl or the bowl of a stand mixer and beat on medium speed for two minutes. Scrape down the bottom and sides of bowl, then add the lemon juice, zest, vanilla, apple cider vinegar and milk.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6F27C8897EA9C4781DF85AA8CC866AA', X'0F8D484EEBEF4448B7B79E23566ED153', X'0F8D484EEBEF4448B7B79E23566ED153', 4, NULL, NULL, 2, NULL, 0, 'Add the flour to the milk-oil mixture in two batches, mixing for no more than 20 seconds after each addition. Scrape down the sides of the bowl with a spatula to make sure everything''s mixed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14F53C8AE41E034D96593416775194E3', X'0F8D484EEBEF4448B7B79E23566ED153', X'0F8D484EEBEF4448B7B79E23566ED153', 5, NULL, NULL, 2, NULL, 0, 'Remove the bowl from the mixer and stir in the blueberries. You can toss the blueberries with a tablespoon of flour first if you want to make sure they don''t sink to the bottom of the cake. I didn''t coat them and they didn''t sink.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'474D058E8CE1DC47AA103F4E156426CD', X'0F8D484EEBEF4448B7B79E23566ED153', X'0F8D484EEBEF4448B7B79E23566ED153', 6, NULL, NULL, 2, NULL, 0, 'Pour the batter into the prepared cake pan, place the pan on a baking sheet, and bake for 45-55 minutes or until a toothpick in the center comes out clean.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66F9CB184C3D1745A4ED8B132B8EE849', X'0F8D484EEBEF4448B7B79E23566ED153', X'0F8D484EEBEF4448B7B79E23566ED153', 7, NULL, NULL, 2, NULL, 0, 'Remove the cake pan to a rack and let it cool in the pan for 10 minutes before unmolding. If using a bundt pan, unmold by placing a plate over the mouth of the pan and flipping in a quick motion.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F67FD8E3F8F1244682CBD44B2155D9AB', X'91F0F80C68FB3644A13D07AD3F8A9817', X'91F0F80C68FB3644A13D07AD3F8A9817', 0, NULL, NULL, 2, NULL, 0, 'Place the blueberries in a saucepan with the sugar, ½ cup water and lemon juice and zest. Bring to boil over medium heat and let the blueberries cook, covered, until they begin to burst. Continue cooking for another 10 minutes over medium-low heat. Stir in the vanilla.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A72DE04998A51443B1A29735428930FD', X'91F0F80C68FB3644A13D07AD3F8A9817', X'91F0F80C68FB3644A13D07AD3F8A9817', 1, NULL, NULL, 2, NULL, 0, 'Whisk the tapioca with the water and add it to the blueberry mixture, whisking as you add. The topping should thicken. Turn off the heat. This is a fairly runny topping, which we like in our home -- if you want a thicker topping, double the amount of tapioca.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF9B925413741943991F0FCDDC6CDD2F', X'91F0F80C68FB3644A13D07AD3F8A9817', X'91F0F80C68FB3644A13D07AD3F8A9817', 2, NULL, NULL, 2, NULL, 0, 'Pour the topping over the cooled cake, slice and serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28B92596337AAA4AB9CD4BACD8142908', X'58EB9E5FDBF92F46A9DDE64328F98A97', X'58EB9E5FDBF92F46A9DDE64328F98A97', 0, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk the flours, spices, baking powder, baking soda, and salt. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63B6AC374517B3469F4F052E47098D65', X'58EB9E5FDBF92F46A9DDE64328F98A97', X'58EB9E5FDBF92F46A9DDE64328F98A97', 1, NULL, NULL, 2, NULL, 0, 'Mix the almond milk and the apple cider vinegar and set aside for a couple of minutes to curdle.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4541C2AC13D9B440B1C6D3DA8E5B7AA4', X'58EB9E5FDBF92F46A9DDE64328F98A97', X'58EB9E5FDBF92F46A9DDE64328F98A97', 2, NULL, NULL, 2, NULL, 0, 'In the bowl of a stand mixer, or with a hand mixer, beat the sugar and the oil for a minute. Then add ½ cup of the almond milk, vanilla extract, and flax meal and continue to beat for another two minutes until the mixture is quite fluffy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5AF67EB191CE84594F7342681B59A8C', X'58EB9E5FDBF92F46A9DDE64328F98A97', X'58EB9E5FDBF92F46A9DDE64328F98A97', 3, NULL, NULL, 2, NULL, 0, 'Add the flour and the almond milk to the sugar mixture in three batches, alternating and beating for 20 seconds after each addition. Scrape down the sides and the bottom of the bowl with a spatula frequently to ensure everything is well-mixed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26BBFCB446F2244AA07A5DC139F5C5B5', X'58EB9E5FDBF92F46A9DDE64328F98A97', X'58EB9E5FDBF92F46A9DDE64328F98A97', 4, NULL, NULL, 2, NULL, 0, 'Finally add the pumpkin puree and mix for 20 seconds.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'308D52E0EFDD0C4CA612C03E37ED5DF4', X'58EB9E5FDBF92F46A9DDE64328F98A97', X'58EB9E5FDBF92F46A9DDE64328F98A97', 5, NULL, NULL, 2, NULL, 0, 'Scrape the batter into an oiled and floured bundt pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDA037C79C92CD4C8758303D5BB53B04', X'58EB9E5FDBF92F46A9DDE64328F98A97', X'58EB9E5FDBF92F46A9DDE64328F98A97', 6, NULL, NULL, 2, NULL, 0, 'Bake in a preheated oven set at 350 degrees Fahrenheit for 1 hour and 5 minutes or until a toothpick inserted in the center of the bundt cake comes out clean or with a few crumbs sticking to it.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9DD2D3668F8A24E8A61C20952A2C7FD', X'58EB9E5FDBF92F46A9DDE64328F98A97', X'58EB9E5FDBF92F46A9DDE64328F98A97', 7, NULL, NULL, 2, NULL, 0, 'Set on a rack to cool for 30 minutes, then unmold and continue cooling the cake on a rack.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45275C7C89828E4C8F2EEF224D846CCB', X'58EB9E5FDBF92F46A9DDE64328F98A97', X'58EB9E5FDBF92F46A9DDE64328F98A97', 8, NULL, NULL, 2, NULL, 0, 'I just serve this with a dusting of powdered sugar, but you could serve with whipped vegan cream or some vanilla ice cream.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'009ABBFE92FD2D45BC9E9B971B4209F3', X'F117EFAEF074F64AB324B89275B8A631', X'F117EFAEF074F64AB324B89275B8A631', 0, NULL, NULL, 2, NULL, 0, 'Whisk the flour, salt and sugar in a bowl and add the butter. Cut it into the flour using a pastry cutter or a fork until you have small lumps of butter dispersed througout the dough. The butter should not melt into the flour if you want a flaky pastry, so work fast.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'815FFF77AC821D44B3C21D97012407E4', X'F117EFAEF074F64AB324B89275B8A631', X'F117EFAEF074F64AB324B89275B8A631', 1, NULL, NULL, 2, NULL, 0, 'Drizzle the cold water into the bowl, a tablespoon at a time, and mix with a fork until the dough is just moistened and comes together in a ball. Do not overmix or overmoisten. You don''t want to develop too much gluten in the flour because that will make your crust hard.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC673E1E9577974BA19110F162A33809', X'F117EFAEF074F64AB324B89275B8A631', X'F117EFAEF074F64AB324B89275B8A631', 2, NULL, NULL, 2, NULL, 0, 'Divide the dough into two balls and wrap tightly in cling wrap, flattening the ball into a disc as you wrap. Refrigerate the two discs of dough while you make the filling.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'376A2489A5DCAC4DA52534AC3E3BFFFA', X'E2D10C5D81CE0747A054264EA8EE95DD', X'E2D10C5D81CE0747A054264EA8EE95DD', 0, NULL, NULL, 2, NULL, 0, 'Place the fruit in a bowl and add the cinnamon, sugar, flour, lemon and salt. Stir well to mix and set aside for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9818A54FDBFF5A45A722D3982F829E5E', X'3647D22D0523814B9E4D1767368E94AE', X'3647D22D0523814B9E4D1767368E94AE', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EC785343C56E54EA293961A08AD5C9C', X'3647D22D0523814B9E4D1767368E94AE', X'3647D22D0523814B9E4D1767368E94AE', 1, NULL, NULL, 2, NULL, 0, 'Take one of the discs of dough from the refrigerator and roll into a circumference about an inch larger than your pie plate (use a 9- to 10-inch pie plate for this). Place the dough into the pie plate -- it''s best to fold your rolled dough into half and then lift it into the plate to keep it from tearing.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33F42DB9C5D53B40B14616AA9B29EEF9', X'3647D22D0523814B9E4D1767368E94AE', X'3647D22D0523814B9E4D1767368E94AE', 2, NULL, NULL, 2, NULL, 0, 'Fit the dough into the pie pan and fold over any overhang. I prefer not to cut it out-- more crust! You can crimp the edges of your crust with your fingers or a fork for a decorative look.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A114AEC9E3411147B0518D2570714D3E', X'3647D22D0523814B9E4D1767368E94AE', X'3647D22D0523814B9E4D1767368E94AE', 3, NULL, NULL, 2, NULL, 0, 'Place the pie plate in the refrigerator while you make your lattice top. To make the lattice top, roll the second disc of dough until it''s slightly larger than the circumference of the top of your pie plate. Cut it into 10 equal strips using a pizza cutter or a knife or a fluted pastry wheel, if you have it.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57627FF42BA8F248878D63C6BCDCF5C6', X'3647D22D0523814B9E4D1767368E94AE', X'3647D22D0523814B9E4D1767368E94AE', 4, NULL, NULL, 2, NULL, 0, 'Place the filling inside the prepared pie plate, including any juices, and then form the lattice over the top of the filling by laying out five strips over the pie and then weaving the rest of the strips crosswise through them. It doesn''t have to be perfect-- do your best and it will look great anyway. If you can''t be bothered, skip the lattice and cover the pie with the rolled dough, then cut decorative vents in it to let it steam.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57617CB515481B4FA18FC583E44B9745', X'3647D22D0523814B9E4D1767368E94AE', X'3647D22D0523814B9E4D1767368E94AE', 5, NULL, NULL, 2, NULL, 0, 'Place the pie plate on a cookie sheet to catch any juices that may drip during baking, then place in the hot oven. Bake 60 minutes or until the top is golden-brown- to ensure a better color, brush on a mix of 1 tsp nondairy milk and 1 tsp oil on the top crust before placing into the oven.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0AE0E760AD9A54B8B34EA4162149081', X'3647D22D0523814B9E4D1767368E94AE', X'3647D22D0523814B9E4D1767368E94AE', 6, NULL, NULL, 2, NULL, 0, 'Once the pie comes out of the oven, place it on a rack and let it cool thoroughly before cutting. This can take 4-5 hours. You can slice your pie while it''s still a little warm, but that could mean some of the juices may not have thickened yet and could run.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70FA6493C45F624D9345D90AF1F0FDB1', X'CAE7C9861ACBEB46A6844275C584EC31', X'CAE7C9861ACBEB46A6844275C584EC31', 0, NULL, NULL, 2, NULL, 0, 'Sift together the flour, salt, baking soda, baking powder and cinnamon.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7F2C93C778F6A4CA0DACA9BC495B0D8', X'CAE7C9861ACBEB46A6844275C584EC31', X'CAE7C9861ACBEB46A6844275C584EC31', 1, NULL, NULL, 2, NULL, 0, 'Beat together the oil, sugar and vanilla for about a minute to aerate.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3209211E1F31BB43940A05543E79A4EF', X'CAE7C9861ACBEB46A6844275C584EC31', X'CAE7C9861ACBEB46A6844275C584EC31', 2, NULL, NULL, 2, NULL, 0, 'Add the flaxmeal mixture, beat for a few more seconds to mix, and then add the flour mixture.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80DB3E0392494E45B3628EB54D8E94FE', X'CAE7C9861ACBEB46A6844275C584EC31', X'CAE7C9861ACBEB46A6844275C584EC31', 3, NULL, NULL, 2, NULL, 0, 'Stir with a spatula to get the ingredients well-mixed. The dough will be very stiff, but don''t worry -- the apples will express juices as they bake. Add the apples and the walnuts and mix well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3039775D5D80AA4CA5A094E236534810', X'CAE7C9861ACBEB46A6844275C584EC31', X'CAE7C9861ACBEB46A6844275C584EC31', 4, NULL, NULL, 2, NULL, 0, 'Pour the batter into an 8-inch oiled and floured square pan (or use a round cake pan if that''s what you have). To make the cake easier to unmold, line the pan with parchment paper or aluminum foil and then oil and flour. (The first time I baked this cake years ago, I didn''t take that extra step and unmolding this cake from the pan was a small battle.)');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDB7A425F657614AB5851FE92FE71B46', X'CAE7C9861ACBEB46A6844275C584EC31', X'CAE7C9861ACBEB46A6844275C584EC31', 5, NULL, NULL, 2, NULL, 0, 'Bake in a preheated 350-degree oven for 40 minutes or until a toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'639F36EA38D13942B91A3DF64B04D51A', X'CAE7C9861ACBEB46A6844275C584EC31', X'CAE7C9861ACBEB46A6844275C584EC31', 6, NULL, NULL, 2, NULL, 0, 'Cool on a rack for at least 10 minutes. Unmold and continue cooling.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D7E71E953B19A4D89689C2DDCF79C26', X'DF89724731959E48A3EB9EBA90D94888', X'DF89724731959E48A3EB9EBA90D94888', 0, NULL, NULL, 2, NULL, 0, 'Whisk together all of the glaze ingredients in a bowl. Drizzle over the cake just before serving. (Adding the drizzle in advance will cause it to seep into the cake, making it more moist -- it will be totally delicious, but will get too tender.)');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAA323516F9E3B4BB9706FE9CE0F99FD', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 0, NULL, NULL, 2, NULL, 0, 'Make the crust:');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'986C50FE9FB0674D9553B12B2276A85C', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 1, NULL, NULL, 2, NULL, 0, 'Place all the ingredients except the water in a glass or steel bowl. Using a fork, mix the ingredients.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED2E91C149650049A9192D93656B5809', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 2, NULL, NULL, 2, NULL, 0, 'Then slowly drizzle the cold water, a tablespoon at a time, over the flour, mixing with the fork quickly until the dough starts to clear the sides of the bowl and comes together.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A118D2EFAB2FD04991B4B89D41941D2B', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 3, NULL, NULL, 2, NULL, 0, 'Divide the dough into half and place each half in cling wrap, shaping into a disc as you wrap.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A7D7279E6F46842993B762A89B5AFA7', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 4, NULL, NULL, 2, NULL, 0, 'Place both dough discs in the refrigerator for an hour at least or longer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F52ADE066F453A439EC3FA2CAB135CF2', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 5, NULL, NULL, 2, NULL, 0, 'Make the strawberry filling:');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'402216AD6A514D45AF57113C28F2DC9C', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 6, NULL, NULL, 2, NULL, 0, 'Let the strawberries thaw to the point where they are no longer icy but can be cut into halves.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CA593F12E02E741BE495A6A337E188D', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 7, NULL, NULL, 2, NULL, 0, 'Cut into halves and place in a bowl. Mix in all the other ingredients and stir thoroughly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F96D37BC29D93469777388FEE5EE01B', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 8, NULL, NULL, 2, NULL, 0, 'Set aside for 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D3BFF3E4919064F97374370221BE624', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 9, NULL, NULL, 2, NULL, 0, 'Put your pie together:');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48D71B7E12C1654C8AE898A71A007765', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 10, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D418370FA295774FBFD2F5E6893A33E9', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 11, NULL, NULL, 2, NULL, 0, 'Take one of the discs of dough and roll it out to about 12 inches, using as much flour as you need to keep it from sticking to the surface. Keep moving the dough as you roll it to ensure it does not stick.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CCC48DDFF89C14CA7A968B70EBC5978', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 12, NULL, NULL, 2, NULL, 0, 'Fold into half and lift into a 9-inch pie pan (regular, not deep-dish).');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B73ABBC2AF66340BC15454CB7EBEC7C', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 13, NULL, NULL, 2, NULL, 0, 'Unfold the dough and fit it into the pie pan. Fold any overhanging dough under itself so you have a neat-looking crust.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17810E0E1C72604784F1BF5AB8B635A3', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 14, NULL, NULL, 2, NULL, 0, 'Pour the filling into the crust. Place the pie pan into the refrigerator while you prepare the top crust using the second disc of dough.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABB6B4D3B4526D4DBF13490E76D174AD', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 15, NULL, NULL, 2, NULL, 0, 'If you are making just a regular, covered pie, roll the disc out so it is slightly bigger than the diameter of the dish. Place on top of the pie dish and with your fingers or with a fork crimp the edges to seal them. Cut four slits at the top of the pie, preferably in a cross pattern.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DA3D08589341F4B9880DED6301318C2', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 16, NULL, NULL, 2, NULL, 0, 'If you are making a lattice crust, cut the dough into strips and then weave them into a lattice top following');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'505F71040DB4B14D9AAA41D52C4BDCFD', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 17, NULL, NULL, 2, NULL, 0, 'these instructions');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9185D124C49BCD4EBDE0A6D007A51080', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 18, NULL, NULL, 2, NULL, 0, 'If you want to make a crust like the one I did, use a small cookie cutter in any shape (you can even use fun animal shapes if you have kids) to cut out little pieces of the dough. Keep them on a cold baking sheet while you cut out the others. Gather the scraps, roll out the dough, and cut more shapes until you have exhausted the dough.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33778E19C6BEC841A701721E6D609CC9', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 19, NULL, NULL, 2, NULL, 0, 'Arrange the shapes on top of the strawberries in a decorative pattern-- I arranged them in concentric circles.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77F7397418B6FB49BFFBEBAA0E4DC996', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 20, NULL, NULL, 2, NULL, 0, 'Sprinkle 1 tbsp of sugar on the crust. This gives some additional sweetness and helps the crust brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C72C561F285C7646A1918F44BEF0C624', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 21, NULL, NULL, 2, NULL, 0, 'Place the pie pan on a baking sheet and bake for 20 minutes. Then lower the heat to 400 degrees and bake another 30 minutes. Once more lower the heat to 350 degrees and cook 20 more minutes or until the juices have thickened and are bubbling.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6237D0D9C86D7A4D9E35F0BA7A4E490C', X'73424C45D490E3449D86F8EC4F60A77A', X'73424C45D490E3449D86F8EC4F60A77A', 22, NULL, NULL, 2, NULL, 0, 'Remove the pie pan to a rack and cool thoroughly before cutting and serving. Because of the tartness of the strawberries, this pie would be great with some vegan whipped cream.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2E2B921A0B9BC4384DF3FCED0DDC770', X'11C3E40CBEFA6D44940E008684DEBA6C', X'11C3E40CBEFA6D44940E008684DEBA6C', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32BBE28EE2881C41AC77A61283CF97A4', X'11C3E40CBEFA6D44940E008684DEBA6C', X'11C3E40CBEFA6D44940E008684DEBA6C', 1, NULL, NULL, 2, NULL, 0, 'Prepare an 8 X 8 baking pan by lining with parchment paper.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B5DCF173BAC804F81299DECEBFB7157', X'11C3E40CBEFA6D44940E008684DEBA6C', X'11C3E40CBEFA6D44940E008684DEBA6C', 2, NULL, NULL, 2, NULL, 0, 'Place all of the ingredients for the cookie base or crust in a food processor and pulse a few times until the dough comes together. Press the dough into the prepared baking pan using your fingers, until you have an even layer. It can be a little annoying to work with the parchment paper sliding under your fingers, but you''ll get there. Don''t worry if the top isn''t perfectly smooth-- you''ll be pouring the topping over it.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8184A330196684ABFD866F6E34D6E09', X'11C3E40CBEFA6D44940E008684DEBA6C', X'11C3E40CBEFA6D44940E008684DEBA6C', 3, NULL, NULL, 2, NULL, 0, 'Place the baking pan in the oven and bake the crust for 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35513D868E9E864DAC6256A6CA8C54CD', X'11C3E40CBEFA6D44940E008684DEBA6C', X'11C3E40CBEFA6D44940E008684DEBA6C', 4, NULL, NULL, 2, NULL, 0, 'While the crust is baking, make the topping by placing all ingredients except the pecans in a saucepan. Stir to help the sugar melt. Let the mixture reach a boil, continue to let it boil for a minute, then turn off the heat and add the pecans.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF839E298EBF8744B446CF63ACF9CD59', X'11C3E40CBEFA6D44940E008684DEBA6C', X'11C3E40CBEFA6D44940E008684DEBA6C', 5, NULL, NULL, 2, NULL, 0, 'Pour the pecan topping evenly over the baked crust and return to the oven for 15 more minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0EA9B12697F5F48A8AF8A3C2AF135F3', X'11C3E40CBEFA6D44940E008684DEBA6C', X'11C3E40CBEFA6D44940E008684DEBA6C', 6, NULL, NULL, 2, NULL, 0, 'Remove the baking pan and cool it thoroughly on a rack. You can cut the bars at this point, or transfer the baking pan to a refrigerator and let it cool for at least a couple of hours before cutting.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'109193879E2524458A18AEEBF1C010AB', X'AB6BA3919625F74987D58D831498CD8F', X'AB6BA3919625F74987D58D831498CD8F', 0, NULL, NULL, 2, NULL, 0, 'Place 22 Oreos in the food processor with the melted butter or oil. Process or pulse until the cookies are broken down into very fine crumbs.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B6A0540BC916B4C95F7E1D2D0C7231D', X'AB6BA3919625F74987D58D831498CD8F', X'AB6BA3919625F74987D58D831498CD8F', 1, NULL, NULL, 2, NULL, 0, 'Press the Oreo crust into a pie pan and freeze for at least an hour.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1283690E8602D14DB7711C546BEF2585', X'AB6BA3919625F74987D58D831498CD8F', X'AB6BA3919625F74987D58D831498CD8F', 2, NULL, NULL, 2, NULL, 0, 'Make the filling by placing the cream cheese, peanut butter, vanilla and maple syrup in a food processor. Once the ingredients are mixed, open the food processor tube and whip for another two minutes to make the filling fluffy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63C92E865E61CE498D23A9F8E7A43BD3', X'AB6BA3919625F74987D58D831498CD8F', X'AB6BA3919625F74987D58D831498CD8F', 3, NULL, NULL, 2, NULL, 0, 'Scrape the filling into the prepared pie crust, smooth the top, then crumble on the remaining Oreos. You can skip the Oreos and use another cookie or candy to make this your own.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7367942F5A122F40837DF87F64DAE315', X'AB6BA3919625F74987D58D831498CD8F', X'AB6BA3919625F74987D58D831498CD8F', 4, NULL, NULL, 2, NULL, 0, 'Freeze another hour, then slice and serve');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5543F3F5F325C1459F63D69A2876D5D3', X'06DF5B8C4692E3438C961894B9CD4718', X'06DF5B8C4692E3438C961894B9CD4718', 0, NULL, NULL, 2, NULL, 0, 'Mix the nondairy milk with the apple cider vinegar and set aside for a minute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2CEA151BFA762458A184F9F8AEE6FC8', X'06DF5B8C4692E3438C961894B9CD4718', X'06DF5B8C4692E3438C961894B9CD4718', 1, NULL, NULL, 2, NULL, 0, 'Combine the flour, baking soda, baking powder and salt in a bowl.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82DA9F22F9316C4081DBB5B6F2E29FB0', X'06DF5B8C4692E3438C961894B9CD4718', X'06DF5B8C4692E3438C961894B9CD4718', 2, NULL, NULL, 2, NULL, 0, 'Beat together the oil and sugar with a handheld mixer or in a stand mixer or with a whisk for about two minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'067E0661783F10489F4D08A387C4F760', X'06DF5B8C4692E3438C961894B9CD4718', X'06DF5B8C4692E3438C961894B9CD4718', 3, NULL, NULL, 2, NULL, 0, 'Add the nondairy milk and beat until just mixed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7B0499B87A4C7409308FE9EB8106B0E', X'06DF5B8C4692E3438C961894B9CD4718', X'06DF5B8C4692E3438C961894B9CD4718', 4, NULL, NULL, 2, NULL, 0, 'Add the mashed bananas and applesauce and beat in. Make sure there are no big lumps.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74C61291DA49A5448D9D01FD0F7624B0', X'06DF5B8C4692E3438C961894B9CD4718', X'06DF5B8C4692E3438C961894B9CD4718', 5, NULL, NULL, 2, NULL, 0, 'Add the flour to the sugar-banana mixture in two batches, mixing well after each addition until the mixture is smooth. Don''t overdo it.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF07DC1409BD0142AFB9F3A37635054C', X'06DF5B8C4692E3438C961894B9CD4718', X'06DF5B8C4692E3438C961894B9CD4718', 6, NULL, NULL, 2, NULL, 0, 'Line the bottom of two 9-inch cake pans with parchment or wax paper. Oil and flour the pans.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'822B9E44CD5495438C858113A1238A41', X'06DF5B8C4692E3438C961894B9CD4718', X'06DF5B8C4692E3438C961894B9CD4718', 7, NULL, NULL, 2, NULL, 0, 'Divide the batter equally between the two pans and bake 25-30 minutes in a 350-degree oven until a toothpick inserted in the center of each cake comes out clean.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10E475DC60986F498D84EDAB44E086E9', X'06DF5B8C4692E3438C961894B9CD4718', X'06DF5B8C4692E3438C961894B9CD4718', 8, NULL, NULL, 2, NULL, 0, 'Place on a rack to cool, about 10 minutes. Unmold, peel off the parchment paper, and leave on the rack to cool thoroughly before frosting.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3716C8A2D25BA408E92D1BAAAC79D00', X'056F040DC8F098408D617CFC61DAD68E', X'056F040DC8F098408D617CFC61DAD68E', 0, NULL, NULL, 2, NULL, 0, 'Have the peanut butter and vegan butter at room temperature. Place them in a bowl and mix with a handheld or stand mixer or whisk until everything''s combined and smooth.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB3574D012231F40BF94B070C9E3108F', X'056F040DC8F098408D617CFC61DAD68E', X'056F040DC8F098408D617CFC61DAD68E', 1, NULL, NULL, 2, NULL, 0, 'Add the sugar ½ cup at a time, beating in well after each addition. Confectioner''s sugar can lump up sometimes, in that case pass the sugar through a sieve. If the frosting feels stiff, add in some nondairy milk, a tablespoon at a time, beating after each addition, until you have a smooth, frosting-like consistency');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88D8900C339F6747B72B1F6C9F90BA67', X'6FDB67AE90CA274E80FF07DD3CBE1A22', X'6FDB67AE90CA274E80FF07DD3CBE1A22', 0, NULL, NULL, 2, NULL, 0, 'Place one cake on a cake stand or flat dish. Put about &frac13;rd of the frosting (recipe follows) in the center of the cake and using a spatula spread it evenly across the surface.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F3E8EC8DEBCE34D965D1E28F96415A1', X'6FDB67AE90CA274E80FF07DD3CBE1A22', X'6FDB67AE90CA274E80FF07DD3CBE1A22', 1, NULL, NULL, 2, NULL, 0, 'Carefully place the second cake on top of the first one. Put the remaining icing in the center of the cake and evenly frost the top and sides.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'257352B432EB2D4785D1F2757F9CA98B', X'6FDB67AE90CA274E80FF07DD3CBE1A22', X'6FDB67AE90CA274E80FF07DD3CBE1A22', 2, NULL, NULL, 2, NULL, 0, 'Garnish with chopped, roasted peanuts.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DB0EA344AD09F42A6293E82E4CF7E45', X'6876160DEBAC87418A20096F5483E8F8', X'6876160DEBAC87418A20096F5483E8F8', 0, NULL, NULL, 2, NULL, 0, 'Mix the cinnamon and sugar in a bowl and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'082B4CCBA6819E4F9EB76F824418872C', X'9AE54589D5444D409D13ACEECA874461', X'9AE54589D5444D409D13ACEECA874461', 0, NULL, NULL, 2, NULL, 0, 'In another bowl, mix the flour, cinnamon, butter and sugar until everything comes together. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66AE0DDED502504ABE35B27B8BBCDEF3', X'E76E5106149FB64E974BBB4215AC01A6', X'E76E5106149FB64E974BBB4215AC01A6', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'869229A4BAAD37448A26644066BB63CB', X'E76E5106149FB64E974BBB4215AC01A6', X'E76E5106149FB64E974BBB4215AC01A6', 1, NULL, NULL, 2, NULL, 0, 'Prepare a large baking pan (mine is a round one, about 11 inches in diameter, but a rectangular 9 X 13 inch pan would be fine).');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07304E7A5D7C624C876C1AF9766F6C28', X'E76E5106149FB64E974BBB4215AC01A6', X'E76E5106149FB64E974BBB4215AC01A6', 2, NULL, NULL, 2, NULL, 0, 'Mix the flour with the baking soda, baking powder and salt in a bowl and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05653C70E5EE2E4890C1B31FDC89B4A0', X'E76E5106149FB64E974BBB4215AC01A6', X'E76E5106149FB64E974BBB4215AC01A6', 3, NULL, NULL, 2, NULL, 0, 'Mix the nondairy milk and apple cider vinegar and set aside to curdle.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAEE138DAF7E2F48AE8F3C0761CFBA36', X'E76E5106149FB64E974BBB4215AC01A6', X'E76E5106149FB64E974BBB4215AC01A6', 4, NULL, NULL, 2, NULL, 0, 'In a large bowl or the bowl of a stand mixer, beat the sugar with the oil and applesauce using the whisk attachment until everything is well mixed, about a minute. Add the aquafaba and beat on medium-high speed for another two minutes. The mixture will look curdled but don''t worry.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'322A75565767EF4A9545C3B4C7540143', X'E76E5106149FB64E974BBB4215AC01A6', X'E76E5106149FB64E974BBB4215AC01A6', 5, NULL, NULL, 2, NULL, 0, 'Add the vanilla and nondairy milk to the bowl, mix, and then add the flour in three batches. Make sure everything is mixed well and scrape down the sides and bottom of the bowl after each addition.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E13DFD643B2DDD42BA43438127CBB757', X'E76E5106149FB64E974BBB4215AC01A6', X'E76E5106149FB64E974BBB4215AC01A6', 6, NULL, NULL, 2, NULL, 0, 'Pour half the batter into the prepared pan. Sprinkle the cinnamon filling over the batter and use a knife to gently swirl it into the batter. Now pour the remaining batter over the top of the filling. Scatter the prepared streusel over the top evenly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'457FAF6D0D3A0943B410E4ED08F1C6E6', X'E76E5106149FB64E974BBB4215AC01A6', X'E76E5106149FB64E974BBB4215AC01A6', 7, NULL, NULL, 2, NULL, 0, 'Bake the cake in the preheated oven for 50-55 minutes or until a toothpick inserted in the center comes out clean or with crumbs sticking to it.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87DDCB4AB2F083479A0ECF3B0DCB0C1C', X'E76E5106149FB64E974BBB4215AC01A6', X'E76E5106149FB64E974BBB4215AC01A6', 8, NULL, NULL, 2, NULL, 0, 'Remove the cake from the rack and allow it to cool.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1935B823036B8D438C1AA81764F272F1', X'568C2BFFA090314CA961ABD364164B80', X'568C2BFFA090314CA961ABD364164B80', 0, NULL, NULL, 2, NULL, 0, 'While the cake is cooling, place all ingredients for the glaze-- the cashews, the milk, vanilla, cinnamon and sugar -- in a blender and blend into a very, very smooth paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FABBB5FF9CDBA344AFC266EA4C1745BF', X'568C2BFFA090314CA961ABD364164B80', X'568C2BFFA090314CA961ABD364164B80', 1, NULL, NULL, 2, NULL, 0, 'Drizzle the glaze over the cooled cake. Cut and serve the cake right out of the pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3947278CCFB44D4195C3194B8953C76D', X'48646EFCBE1809428B9E7F4562F0F8B7', X'48646EFCBE1809428B9E7F4562F0F8B7', 0, NULL, NULL, 2, NULL, 0, 'Place 1 ¼ cups of unbleached all purpose flour in a bowl. Mix in 1 tablespoon of sugar and ¼ teaspoon of salt. Then cut in 8 tablespoons of very cold vegan butter into the flour.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF89A1210937DA44B20ED205841DC04D', X'48646EFCBE1809428B9E7F4562F0F8B7', X'48646EFCBE1809428B9E7F4562F0F8B7', 1, NULL, NULL, 2, NULL, 0, 'Once the butter’s been incorporated into the flour, drizzle in very cold, iced water into the bowl slowly, mixing with your fork at the same time, until the dough comes together into a ball.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8592210364949499D2C17D2A5940DD3', X'48646EFCBE1809428B9E7F4562F0F8B7', X'48646EFCBE1809428B9E7F4562F0F8B7', 2, NULL, NULL, 2, NULL, 0, 'Lift it out of the bowl and into a sheet of cling wrap. Wrap it tight, and shape the dough into a disc by patting it with your fingers. Try not to handle the dough as much as possible because your body heat is not kind to a pie crust. You want those buttery pockets to stay intact, remember, so your crust gets flaky, not dense. Refrigerate the crust for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B688DA51DC74D4BAC92990CE20FDCFE', X'48646EFCBE1809428B9E7F4562F0F8B7', X'48646EFCBE1809428B9E7F4562F0F8B7', 3, NULL, NULL, 2, NULL, 0, 'Form the dough into a disc and put it in the refrigerator while you get the rest of the ingredients ready.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C998E971002FBD4E9BF05964FABE8C92', X'43EC8410A23EB145A7CB2AAD72A70561', X'43EC8410A23EB145A7CB2AAD72A70561', 0, NULL, NULL, 2, NULL, 0, 'Melt the vegan butter in a skillet (if you plan to bake the entire tart in a skillet, a 10-inch cast-iron skillet works best).');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DE46DA8AC96174A87B0E2B5E26C6B7A', X'43EC8410A23EB145A7CB2AAD72A70561', X'43EC8410A23EB145A7CB2AAD72A70561', 1, NULL, NULL, 2, NULL, 0, 'Once the butter’s melted, sprinkle one cup of sugar into the pan. Then add in five sliced apples. It may seem like a lot to begin with, but the apples will soften and settle down as they release their juices. At this stage I add in a small pinch of salt to help them do that.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E493D5A47EF0354AB102DCD8EA42030F', X'43EC8410A23EB145A7CB2AAD72A70561', X'43EC8410A23EB145A7CB2AAD72A70561', 2, NULL, NULL, 2, NULL, 0, 'Stir the apples gently into the butter, trying not to break them. Continue to cook the apples over medium-high heat for about 10 minutes, turning them over gently every now and then, until the apples are softened and have released their juices. Turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FA8F75977900D49AA5CEFD44E9DAA44', X'43EC8410A23EB145A7CB2AAD72A70561', X'43EC8410A23EB145A7CB2AAD72A70561', 3, NULL, NULL, 2, NULL, 0, 'When I’m using a cake tin, I butter the tin and line the bottom with parchment paper for extra insurance. You don’t have to use the parchment because the tarte tatin does slide out quite easily when it’s still quite warm, but do butter the pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B28DD5CAC8C0D4CACD186E12E389ABD', X'43EC8410A23EB145A7CB2AAD72A70561', X'43EC8410A23EB145A7CB2AAD72A70561', 4, NULL, NULL, 2, NULL, 0, 'Using tongs, lift out the apple slices and arrange them in circles around the pan, overlapping them slightly. You should have enough apples to make two layers. Pour the juice remaining in the saucepan evenly over the apples. If you’re going to continue baking in the skillet, you can just leave your apples free-form because arranging them into a pattern in a hot skillet will be tougher — you’ll have a more rustic-looking but still very pretty tart.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28FD911BB0ED634880F003B5E9696911', X'3AAA721D30AC844E9684070599CE7B93', X'3AAA721D30AC844E9684070599CE7B93', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3A439CECE6FC34FA9592B9B16B58C4E', X'3AAA721D30AC844E9684070599CE7B93', X'3AAA721D30AC844E9684070599CE7B93', 1, NULL, NULL, 2, NULL, 0, 'Flour the countertop and roll out the pastry dough into a circle slightly larger than the diameter of your cake pan or skillet. Make sure you keep moving your dough as you roll, flouring the surface more if needed, so the dough doesn’t stick to the countertop. Once your dough is rolled out, if you want a more finished look, try and cut any rough edges away with a knife. I have two cake tins of the same size, so I use the other as a guide.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C66D2C2EBAC19E44988879E70E4E3334', X'3AAA721D30AC844E9684070599CE7B93', X'3AAA721D30AC844E9684070599CE7B93', 2, NULL, NULL, 2, NULL, 0, 'Gently lift your pastry dough (easiest way to do this is to fold it once, and then once more before lifting), and carefully place it on top of the apples. Unfold it and if it is slightly larger than the cake tin, just gently fold in the edges. If you’re doing this in your skillet be careful — the skillet’s hot and you don’t want to burn yourself!');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F76824C053F7A41B87C977C1A34E963', X'3AAA721D30AC844E9684070599CE7B93', X'3AAA721D30AC844E9684070599CE7B93', 3, NULL, NULL, 2, NULL, 0, 'Place the cake pan or the skillet in the oven and bake 35 minutes. The puff pastry should be a light gold when you remove the tart from the oven.Let the pan or skillet cool on a rack for 30 minutes. Then run a knife along the sides of the pan to loosen the tart and place a dish on top of the pan. In a quick motion, invert the pan — the tart should drop quite easily into the plate.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75CB38349187CA49B1A498E9CAD17F6D', X'3AAA721D30AC844E9684070599CE7B93', X'3AAA721D30AC844E9684070599CE7B93', 4, NULL, NULL, 2, NULL, 0, 'Remove the pan and the parchment, if it comes off with the tart. If any of the apple slices have gone askew, just rearrange them. Et voila.If you baked your tart in a skillet, be careful when you invert the tart because the skillet is still quite warm. Use oven mitts when doing this.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3332C82BBEC4F48920F748982007E69', X'3AAA721D30AC844E9684070599CE7B93', X'3AAA721D30AC844E9684070599CE7B93', 5, NULL, NULL, 2, NULL, 0, 'Let the tart cool thoroughly before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DED79B0CDED68242874A4DE0D3331937', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'101837E55610CE4C9D660396DA5CDBF5', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 1, NULL, NULL, 2, NULL, 0, 'Pulse 1 cup of graham crackers with 1 cup of walnuts in a food processor with 2 tbsp of softened vegan butter until the nuts are broken down but not powdery.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D77F327123B7CC4EA510B2C1E3710695', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 2, NULL, NULL, 2, NULL, 0, 'Press the crust into an even layer in the bottom of a 10-inch springform pan, place the pan on a baking sheet, and bake the crust for 10 minutes. Remove and set aside while you make the filling.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'016040BFD5FC0B45A84509023FF29290', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 3, NULL, NULL, 2, NULL, 0, 'Make the mango cheesecake filling:');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CEE95D4101C454D9AF73C345EF5C255', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 4, NULL, NULL, 2, NULL, 0, 'Blend ½ cup of cashews with ½ cup of any nondairy milk until very creamy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB6832552E3C294A9F8732F75EEA10B9', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 5, NULL, NULL, 2, NULL, 0, 'In a bowl or in the bowl of a stand mixer, beat 20 oz of room temperature vegan cream cheese with 1 cup sugar until well-mixed. Add the cashew cream, 1 tsp green cardamom powder, 3 tbsp of cornstarch and the mango puree. Mix until you have a very smooth, creamy texture.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBAF2F3605654B46909AF1B57935CA4E', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 6, NULL, NULL, 2, NULL, 0, 'Before you start baking the cake, place a small, ovenproof pan filled with water on a lower shelf of the oven. Pour the mango filling into the springform pan which should still be standing on a baking sheet (to make it easier to get the pan in and out of the oven).');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7E88184CEA1474885997C555CD623E3', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 7, NULL, NULL, 2, NULL, 0, 'Place the pan and baking sheet into the oven and bake 30 minutes, then lower the heat to 325 degrees and continue baking another 30 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEABB8071D3A384B9CEC4A3963B94CCA', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 8, NULL, NULL, 2, NULL, 0, 'When the cheesecake is done, the sides will look firm and the center might still look slightly jiggly, which is okay.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FCC6D1BB9387E4BB1FD2593442FA973', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 9, NULL, NULL, 2, NULL, 0, 'Make the sour cream topping:');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CB02C17AC170A48962FBDCA2BB4C9B9', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 10, NULL, NULL, 2, NULL, 0, 'About 15 minutes before the cake comes out of the oven, blend 1 cup of vegan sour cream with ¼ cup sugar and ½ tsp of cardamom.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F531BCE64EA4FD41B3103261C5D8B9B3', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 11, NULL, NULL, 2, NULL, 0, 'As soon as the cake comes out of the pan, pour the filling on the cake and, using oven mitts, gently tilt the pan around so the topping is evenly distributed over the cake. Return the cake to the oven and continue baking another 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3279E5CC887C7E43A5355E9B34B5482F', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 12, NULL, NULL, 2, NULL, 0, 'Remove the cake from the oven and let it cook thoroughly on a rack. Once cool, run a knife gently along the edges to loosen the cake from the pan. Then, with the cheesecake still inside the pan, refrigerate it for several hours or overnight until thoroughly chilled.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA078F0467150D41A276813669D382D7', X'0712B600E7F19949AB0467FDD8530E34', X'0712B600E7F19949AB0467FDD8530E34', 13, NULL, NULL, 2, NULL, 0, 'Unmold, slice and eat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDC774F10526B046BB310E9D052CF796', X'31A94FEA324584429D7C574509F34486', X'31A94FEA324584429D7C574509F34486', 0, NULL, NULL, 2, NULL, 0, 'Place the orange juice, sugar, oil, vinegar and vanilla in a large bowl or in the bowl of a stand mixer. Use a whisk or the whisk attachment of the stand mixer to mix for one minute or until everything appears to be well-mixed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E903F59496FC947A1F4E85936F14722', X'31A94FEA324584429D7C574509F34486', X'31A94FEA324584429D7C574509F34486', 1, NULL, NULL, 2, NULL, 0, 'Add the baking soda, salt and flour to the bowl and mix. Scrape down the sides of the pan after 20 seconds of mixing and mix for 20 seconds more, but no longer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'903B71EA9E03D14C881DD21FEF70600E', X'31A94FEA324584429D7C574509F34486', X'31A94FEA324584429D7C574509F34486', 2, NULL, NULL, 2, NULL, 0, 'Spray two 8- or 9-inch cake pans with cooking spray or oil. Preferably line the bottom with parchment paper, or, if you don''t have this, flour the pans. Divide the batter equally between the two pans.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67FF7BED756FED48B896CFFF87F9A489', X'31A94FEA324584429D7C574509F34486', X'31A94FEA324584429D7C574509F34486', 3, NULL, NULL, 2, NULL, 0, 'Bake the cakes in a 350-degree oven for 30-35 minutes until a toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEF6A91EB79BB742866C9D57BB123BE3', X'31A94FEA324584429D7C574509F34486', X'31A94FEA324584429D7C574509F34486', 4, NULL, NULL, 2, NULL, 0, 'Cool on a rack for 10 minutes, then slide a thin knife around the edges of each cake');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA05A25148C5854EA5C7AD5C4AE505B6', X'31A94FEA324584429D7C574509F34486', X'31A94FEA324584429D7C574509F34486', 5, NULL, NULL, 2, NULL, 0, 'Gently flip each cake onto a plate, slide onto a rack, and continue cooling before you frost.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECB09B1FF8704041BFE7050C0AF2B3E0', X'420002BB9F2E9B419B0447807EA28C69', X'420002BB9F2E9B419B0447807EA28C69', 0, NULL, NULL, 2, NULL, 0, 'Place the orange marmalade, vanilla and butter in a bowl or the bowl of a stand mixer. Beat until combined, around 30 seconds.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A68DAE6F3FA38247A025681AC5EBC0D9', X'420002BB9F2E9B419B0447807EA28C69', X'420002BB9F2E9B419B0447807EA28C69', 1, NULL, NULL, 2, NULL, 0, 'Add the powdered sugar, half a cup at a time, incorporating well into the butter mixture after each addition. Your frosting should be smooth, creamy and fluffy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'749F37FB2D20334B8DCB2BC151C648CE', X'E55B08B6C0B20345AD1DF8C973C2F398', X'E55B08B6C0B20345AD1DF8C973C2F398', 0, NULL, NULL, 2, NULL, 0, 'Place one of the cakes, on a plate or cake stand. Dump half of the frosting on the cake, use an offset spatula or knife to spread it evenly, then place the second cake on top, taking care to ensure they line up evenly. Spread the remaining frosting evenly over the top of the cake, using anything that remains for the sides.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4257F38D813044D8284B45743A7B089', X'E55B08B6C0B20345AD1DF8C973C2F398', X'E55B08B6C0B20345AD1DF8C973C2F398', 1, NULL, NULL, 2, NULL, 0, 'Slice and serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D7B4F2FDA97BC409F1257B1FB3486BD', X'F2B115503389B648BFA1C115D66EFABF', X'F2B115503389B648BFA1C115D66EFABF', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8523C70F09D3A4EAA4856343F704B7E', X'F2B115503389B648BFA1C115D66EFABF', X'F2B115503389B648BFA1C115D66EFABF', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, mix the flour, baking soda, baking powder, cornstarch, and salt.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8356768FE9AB324E901680A2B7851F44', X'F2B115503389B648BFA1C115D66EFABF', X'F2B115503389B648BFA1C115D66EFABF', 2, NULL, NULL, 2, NULL, 0, 'In a larger bowl, mix the oil, sugar, yogurt, vanilla extract, and lemon juice and zest until combined.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70E6D2FCC0989643B2A36603D2CF9608', X'F2B115503389B648BFA1C115D66EFABF', X'F2B115503389B648BFA1C115D66EFABF', 3, NULL, NULL, 2, NULL, 0, 'Add the flour to the wet mixture in three batches, mixing thoroughly with a spatula after each addition. Make sure you scrape the bottom and sides of the bowl after each addition.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A7B2CDC7842044AA80192133C242D1E', X'F2B115503389B648BFA1C115D66EFABF', X'F2B115503389B648BFA1C115D66EFABF', 4, NULL, NULL, 2, NULL, 0, 'Prepare a bundt pan by spraying with cooking spray. Pour the batter evenly into the bundt pan. Place it in the oven and bake 50-60 minutes or until a skewer inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'930FD399B23CC545882190AE4F0DDC89', X'F2B115503389B648BFA1C115D66EFABF', X'F2B115503389B648BFA1C115D66EFABF', 5, NULL, NULL, 2, NULL, 0, 'Place the bundt pan on a rack and unmold the cake after 10 minutes. It should come out very easily, but if you need help, run a knife gently along the edges.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E03D6BC772EF04EA90B726C78087ABE', X'F2B115503389B648BFA1C115D66EFABF', X'F2B115503389B648BFA1C115D66EFABF', 6, NULL, NULL, 2, NULL, 0, 'Prepare the syrup by melting the sugar with the lemon juice. Poke some holes into the bundt cake with a toothpick, and drizzle on the syrup. Let the cake cool thoroughly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D081C828A401C4DB101FC4348F68F49', X'F2B115503389B648BFA1C115D66EFABF', X'F2B115503389B648BFA1C115D66EFABF', 7, NULL, NULL, 2, NULL, 0, 'Once the cake has cooled, prepare the glaze by mixing the confectioners'' sugar with the lemon juice. Drizzle it over the bundt cake, and reserve some glaze to drizzle over individual slices.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04C67DC5E85185429E52B7B3E39D7EDF', X'E61022F057053E41A7977C5ABB83DD5E', X'E61022F057053E41A7977C5ABB83DD5E', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, carom seeds, oil and salt in a large bowl or in a stand mixer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CA13E69AA71C8409A03CF1B4C892246', X'E61022F057053E41A7977C5ABB83DD5E', X'E61022F057053E41A7977C5ABB83DD5E', 1, NULL, NULL, 2, NULL, 0, 'Add water, half a cup at first and then a tablespoon at a time, and knead until you have a very stiff dough. You want to be very sure your dough is very stiff and not soft. Cover the dough and set aside while you make the filling');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC88AF8225385547BB1D606B9804E9D9', X'D0A084399E720842803BFE2F6B1EAE03', X'D0A084399E720842803BFE2F6B1EAE03', 0, NULL, NULL, 2, NULL, 0, 'Crumble the potatoes with your fingers so you have a few lumps -- you don''t want to mash these like you would if you were making mashed potatoes. You want some texture in here.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'160B75B5CF3E414EA4758BC6268A896B', X'D0A084399E720842803BFE2F6B1EAE03', X'D0A084399E720842803BFE2F6B1EAE03', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F665FDB9C890BD4991E8DDDB4BB116D7', X'D0A084399E720842803BFE2F6B1EAE03', X'D0A084399E720842803BFE2F6B1EAE03', 2, NULL, NULL, 2, NULL, 0, 'Add the cumin seeds and, as they start to brown, add the ginger.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98E35B9414F0FD4EBF06B955D59451A4', X'D0A084399E720842803BFE2F6B1EAE03', X'D0A084399E720842803BFE2F6B1EAE03', 3, NULL, NULL, 2, NULL, 0, 'Saute the ginger for 30 seconds, the add the coriander leaves and the green chili pepper. Saute for a few seconds.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D87694D9A4F3A4A988D39440429D992', X'D0A084399E720842803BFE2F6B1EAE03', X'D0A084399E720842803BFE2F6B1EAE03', 4, NULL, NULL, 2, NULL, 0, 'Add the coriander powder, turmeric, pomegranate seed powder and chaat masala (or lemon juice if you''re using that instead). Stir to mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A5471555D1D6B4BA6BB18BBE77B6BC5', X'D0A084399E720842803BFE2F6B1EAE03', X'D0A084399E720842803BFE2F6B1EAE03', 5, NULL, NULL, 2, NULL, 0, 'Add the potatoes and mix well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'947575F96B4E1941822B4002D3120B9B', X'D0A084399E720842803BFE2F6B1EAE03', X'D0A084399E720842803BFE2F6B1EAE03', 6, NULL, NULL, 2, NULL, 0, 'Add the peas and stir-fry until the peas are tender and the mixture is very dry, about two to three more minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68AB9950ECBFD041856C387B3A766465', X'D0A084399E720842803BFE2F6B1EAE03', X'D0A084399E720842803BFE2F6B1EAE03', 7, NULL, NULL, 2, NULL, 0, 'Add salt to taste. Turn off the heat and let the filling cool slightly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE6ED91DBFB54E48877C5FF5C37EAA60', X'68C55DBF6D04F94A9151FA5BFD8CDD32', X'68C55DBF6D04F94A9151FA5BFD8CDD32', 0, NULL, NULL, 2, NULL, 0, 'Divide the dough into 7 equal balls.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4094D8890FBA904FA06ACC843F9D7371', X'68C55DBF6D04F94A9151FA5BFD8CDD32', X'68C55DBF6D04F94A9151FA5BFD8CDD32', 1, NULL, NULL, 2, NULL, 0, 'Roll out each ball into an oval, about six inches long and four inches wide. Use some oil to coat the countertop if needed to keep the dough from sticking.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70E63A27AE890149946E9C20EEDB296D', X'68C55DBF6D04F94A9151FA5BFD8CDD32', X'68C55DBF6D04F94A9151FA5BFD8CDD32', 2, NULL, NULL, 2, NULL, 0, 'Using a pizza cutter, cut across the long side of the oval to create two semicircular pieces.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0689AF726A78FB40BB3BCAC0BB2DB7FA', X'68C55DBF6D04F94A9151FA5BFD8CDD32', X'68C55DBF6D04F94A9151FA5BFD8CDD32', 3, NULL, NULL, 2, NULL, 0, 'Coat the edges of each semicircle with water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C73EEFD7F406E4894ED7CEBAD01DF54', X'68C55DBF6D04F94A9151FA5BFD8CDD32', X'68C55DBF6D04F94A9151FA5BFD8CDD32', 4, NULL, NULL, 2, NULL, 0, 'Pick the two corners of the semicircle and bring them together, edges overlapping slightly, to form a cone. Press down on the seam to stick it. It''s important to do this thoroughly because you don''t want your samosas to open while you''re frying them.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8B12CE821EE564B9C39ACA5EBA70267', X'68C55DBF6D04F94A9151FA5BFD8CDD32', X'68C55DBF6D04F94A9151FA5BFD8CDD32', 5, NULL, NULL, 2, NULL, 0, 'Inside the cone, place a few teaspoons of the filling.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B4E223C5067254B80FA2E05D158DD1E', X'68C55DBF6D04F94A9151FA5BFD8CDD32', X'68C55DBF6D04F94A9151FA5BFD8CDD32', 6, NULL, NULL, 2, NULL, 0, 'Now seal the top of the samosa by pressing the top seam together and back.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'708A74E46AC5C34F87288044CC59ADB8', X'68C55DBF6D04F94A9151FA5BFD8CDD32', X'68C55DBF6D04F94A9151FA5BFD8CDD32', 7, NULL, NULL, 2, NULL, 0, 'Repeat for remaining 13 samosas.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA54A76E94C4D1439D80C6BA86FD7987', X'68C55DBF6D04F94A9151FA5BFD8CDD32', X'68C55DBF6D04F94A9151FA5BFD8CDD32', 8, NULL, NULL, 2, NULL, 0, 'In a wok or a frying pan, heat three inches of oil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BD2B54B394AB544BD26C329A12DEE8A', X'68C55DBF6D04F94A9151FA5BFD8CDD32', X'68C55DBF6D04F94A9151FA5BFD8CDD32', 9, NULL, NULL, 2, NULL, 0, 'When the oil reaches 300 degrees Fahrenheit, drop the samosas, one at a time, taking care not to crowd the pan or you''ll end up bringing the oil temperature down very fast. I have an eight-inch wok and I fry three samosas at a time.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39CB34F43910B341A36DF75B077B2FF7', X'68C55DBF6D04F94A9151FA5BFD8CDD32', X'68C55DBF6D04F94A9151FA5BFD8CDD32', 10, NULL, NULL, 2, NULL, 0, 'Fry the samosas, turning over occasionally, until they are golden-brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D81DA8B5B043C43A8BF2803F4D297A3', X'68C55DBF6D04F94A9151FA5BFD8CDD32', X'68C55DBF6D04F94A9151FA5BFD8CDD32', 11, NULL, NULL, 2, NULL, 0, 'Drain on paper towels before serving with a date-tamarind chutney.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99D04DC32EAE384B8DDE575960A0A7F6', X'BEF3EE741715094EA8B173A8040FE98B', X'BEF3EE741715094EA8B173A8040FE98B', 0, NULL, NULL, 2, NULL, 0, 'Place the potatoes in a bowl with all of the ingredients and mix well, kneading with your fingers or a fork.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B95C934164574478AD577D45ACEBEEC', X'BEF3EE741715094EA8B173A8040FE98B', X'BEF3EE741715094EA8B173A8040FE98B', 1, NULL, NULL, 2, NULL, 0, 'Shape the tikkis into discs and pat the sides to make them even. I like to make my tikkis about an inch and a half in diameter.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C7FA7E587E3544FA67CBE2A60498CF6', X'BEF3EE741715094EA8B173A8040FE98B', X'BEF3EE741715094EA8B173A8040FE98B', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a skillet. You don''t need a lot but you should have enough to coat the bottom of the skillet evenly. If you make sure that your oil is hot enough that the tikki sizzles and sputters as soon as you place it in the skillet, your tikkis won''t absorb much oil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F11B718FFE0BDB4E9B274F9E53F27293', X'BEF3EE741715094EA8B173A8040FE98B', X'BEF3EE741715094EA8B173A8040FE98B', 3, NULL, NULL, 2, NULL, 0, 'Working in batches, and making sure not to crowd them, fry the tikkis until they are golden brown, first on one side then the other.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77398AA99A143F45A80972AD3A3C7FF6', X'BEF3EE741715094EA8B173A8040FE98B', X'BEF3EE741715094EA8B173A8040FE98B', 4, NULL, NULL, 2, NULL, 0, 'Serve hot with any green chutney or tamarind chutney.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C2F957E427CB84E996F43F4B2B16B3D', X'DD5D4A2923C7F249A5C45E8690E59FD8', X'DD5D4A2923C7F249A5C45E8690E59FD8', 0, NULL, NULL, 2, NULL, 0, 'Mix together all the ingredients and add just enough water so they hold together.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D633FBA0BDD56B4CAA68F0992F217056', X'DD5D4A2923C7F249A5C45E8690E59FD8', X'DD5D4A2923C7F249A5C45E8690E59FD8', 1, NULL, NULL, 2, NULL, 0, 'Heat oil, about 1 ½ inches deep, in a pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4488AF96F341149851636438DBDEB3C', X'DD5D4A2923C7F249A5C45E8690E59FD8', X'DD5D4A2923C7F249A5C45E8690E59FD8', 2, NULL, NULL, 2, NULL, 0, 'When the oil reaches about 350-375 degrees, lower the heat to about medium and drop about a tablespoon of the mixture, one at a time, into the oil. Don''t worry about shaping the pakoras which should be rather shapeless. Also, don''t crowd the pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E3E2064EB0D084CBE78A7D00DD533B4', X'DD5D4A2923C7F249A5C45E8690E59FD8', X'DD5D4A2923C7F249A5C45E8690E59FD8', 3, NULL, NULL, 2, NULL, 0, 'Deep-fry each pakora on both sides until golden-brown. This should take 4-5 minutes. If they brown too fast, lower the heat because the outside will get cooked and the inside will remain raw.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E1209ABB0262B4BB3AC42D220DBCFB9', X'307F24C2AC09534FBE8ED38671FCFA32', X'307F24C2AC09534FBE8ED38671FCFA32', 0, NULL, NULL, 2, NULL, 0, 'Cook the potatoes in the microwave in a bowl of water, or on the stovetop by covering with water, until they are fork-tender. When cool enough to handle, drain the potatoes and coarsely mash them with a fork.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6A91B89A7EB2B4C9A5BE1EA559777F3', X'2D336C2446B3214FBDB302DE2927E3CE', X'2D336C2446B3214FBDB302DE2927E3CE', 0, NULL, NULL, 2, NULL, 0, 'Place all the ingredients in a blender with ¼ cup water and blend into a smooth paste. If the paste is too thick, add a little more water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E05033DBF12204B9CD208F0C25D927A', X'D1FBA829037351418CBB4A42B334670D', X'D1FBA829037351418CBB4A42B334670D', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients in the blender with 2 tbsp water and blend into a smooth paste. Again, add more water if too thick, a teaspoon at a time.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79F82A0CB21D3A4592208F7D4D1051CE', X'1A72475C9DF68E4BABD862E7DF80D0B2', X'1A72475C9DF68E4BABD862E7DF80D0B2', 0, NULL, NULL, 2, NULL, 0, 'Place the chickpea flour in a bowl along with the cayenne, turmeric and salt. Use a whisk to mix as you drizzle in enough water to make a runny batter that should coat the back of a spoon.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F43A2ADDBE5A2946897C24B4530339C8', X'1A72475C9DF68E4BABD862E7DF80D0B2', X'1A72475C9DF68E4BABD862E7DF80D0B2', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil for frying in a flat-bottomed skillet. I use a skillet just large enough to hold the sandwich, so I can use less oil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43481554125DD84E94E63075D9C1DF5C', X'1A72475C9DF68E4BABD862E7DF80D0B2', X'1A72475C9DF68E4BABD862E7DF80D0B2', 2, NULL, NULL, 2, NULL, 0, 'To the mashed potatoes, add a couple of tablespoons of the tamarind chutney, the chaat masala, and salt to taste. Mix well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'123D94D24211EA47BA1D294654C5D42B', X'1A72475C9DF68E4BABD862E7DF80D0B2', X'1A72475C9DF68E4BABD862E7DF80D0B2', 3, NULL, NULL, 2, NULL, 0, 'Smear some green chutney on a slice of bread, and some tamarind chutney on the other slice. Place a thick layer of potato stuffing on one of the slices and cover with the other. The potato will help glue them together.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B5FD281D1D7964E8ED293BEBF97161E', X'1A72475C9DF68E4BABD862E7DF80D0B2', X'1A72475C9DF68E4BABD862E7DF80D0B2', 4, NULL, NULL, 2, NULL, 0, 'Dip the prepared sandwich into the chickpea batter, making sure you coat it thoroughly. Shake off the excess batter and place in the hot oil. Fry on each side until golden-brown. Repeat for the remaining three sandwiches.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A4668A45154F14BBE4D3EB8ECEDBDD9', X'1A72475C9DF68E4BABD862E7DF80D0B2', X'1A72475C9DF68E4BABD862E7DF80D0B2', 5, NULL, NULL, 2, NULL, 0, 'Remove to a cutting board and cut the pakora diagonally into two triangles. You can also cut it into rectangles or squares.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'903796F7674AA744AC1A8B0C8124846F', X'1A72475C9DF68E4BABD862E7DF80D0B2', X'1A72475C9DF68E4BABD862E7DF80D0B2', 6, NULL, NULL, 2, NULL, 0, 'Serve with any remaining green chutney drizzled on top.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFC773EC7017D54CA4696869FF93D2DA', X'8F03F5F6B3ED6E469762FB34ED375F7A', X'8F03F5F6B3ED6E469762FB34ED375F7A', 0, NULL, NULL, 2, NULL, 0, 'Mix all the ingredients except the water in a large bowl. Trickle in the water and mix into a stiff but smooth dough. Set aside in an air-tight tin for at least half an hour.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28FE16274D18A744A7C21EA3F39A1E28', X'8F03F5F6B3ED6E469762FB34ED375F7A', X'8F03F5F6B3ED6E469762FB34ED375F7A', 1, NULL, NULL, 2, NULL, 0, 'Make the samosa filling:');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0273565F10EB654CA8BDF2BE05CEDE1B', X'8F03F5F6B3ED6E469762FB34ED375F7A', X'8F03F5F6B3ED6E469762FB34ED375F7A', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil. Add the onions and saute until brown spots appear.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'821011330CFCED4C80229D673CC4C9A7', X'8F03F5F6B3ED6E469762FB34ED375F7A', X'8F03F5F6B3ED6E469762FB34ED375F7A', 3, NULL, NULL, 2, NULL, 0, 'Add the ginger garlic paste and stir-fry for 30 seconds.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F22ADA4437AD940BC1AFF2723D09966', X'8F03F5F6B3ED6E469762FB34ED375F7A', X'8F03F5F6B3ED6E469762FB34ED375F7A', 4, NULL, NULL, 2, NULL, 0, 'Add the tomato ketchup, mix, add the garam masala, coriander and cumin powders, soy sauce, turmeric, paprika, and cayenne, if using. Stir well to mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40D7C04FD926A44BB8DDDA7D148CCD8F', X'8F03F5F6B3ED6E469762FB34ED375F7A', X'8F03F5F6B3ED6E469762FB34ED375F7A', 5, NULL, NULL, 2, NULL, 0, 'Add the carrots and potatoes and stir to mix. Saute for a couple of minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFC8C2FD57938F40B86832BCE8317F07', X'8F03F5F6B3ED6E469762FB34ED375F7A', X'8F03F5F6B3ED6E469762FB34ED375F7A', 6, NULL, NULL, 2, NULL, 0, 'Add the TVP. Mix well, then cover and let the filling cook until all the water has dried up. Once the TVP starts sticking slightly to the bottom, you will be done.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B788AC5D9E9304C9A6D62E677CACEBF', X'8F03F5F6B3ED6E469762FB34ED375F7A', X'8F03F5F6B3ED6E469762FB34ED375F7A', 7, NULL, NULL, 2, NULL, 0, 'Add the green peas and let them warm through until tender.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5181A2F03E323F4AA4C60414B92FA47F', X'8F03F5F6B3ED6E469762FB34ED375F7A', X'8F03F5F6B3ED6E469762FB34ED375F7A', 8, NULL, NULL, 2, NULL, 0, 'Add salt if needed, coriander leaves, and lemon juice. Mix well and turn off the heat. There should be no visible moisture in the filling.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E81DF6AB0441CB45BB5560DEFE4BE00F', X'40476503C209DC4A9A1EB69962C82F50', X'40476503C209DC4A9A1EB69962C82F50', 0, NULL, NULL, 2, NULL, 0, 'Divide the dough into 24 pieces. If you want bigger samosas, you can just make fewer pieces. It won''t change the baking time. Or do as I did and make a few big ones and a few small ones.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D033F7DFAC3884EBAF899CE6E81B061', X'40476503C209DC4A9A1EB69962C82F50', X'40476503C209DC4A9A1EB69962C82F50', 1, NULL, NULL, 2, NULL, 0, 'Roll each piece of dough into a smooth ball. Then roll it out, using enough flour, into a very thin circle, about three to four inches in diameter.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01B374122798004CBD6450FDDF8DBD5D', X'40476503C209DC4A9A1EB69962C82F50', X'40476503C209DC4A9A1EB69962C82F50', 2, NULL, NULL, 2, NULL, 0, 'Moisten the edges of each circle. Place a heaping tablespoon of filling in the center of each of the circles, then fold over to form a half-moon shape. Press down on the edges to seal. The dough will stretch a bit and help you along, but don''t tear it. You can also shape these in the traditional samosa shape. .');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91F96D098311D848BBE5D7E2B8EF35A8', X'40476503C209DC4A9A1EB69962C82F50', X'40476503C209DC4A9A1EB69962C82F50', 3, NULL, NULL, 2, NULL, 0, 'Press down on the edges with a fork to seal it tight.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91C9494A268F314091B3E3DDC5D98723', X'40476503C209DC4A9A1EB69962C82F50', X'40476503C209DC4A9A1EB69962C82F50', 4, NULL, NULL, 2, NULL, 0, 'Place the samosas on baking sheets sprayed with oil. Spray or brush some oil on the top of the samosas which will help them crisp up and also give them a nice color.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2DCA80045A21B4091C38B84E1C63908', X'40476503C209DC4A9A1EB69962C82F50', X'40476503C209DC4A9A1EB69962C82F50', 5, NULL, NULL, 2, NULL, 0, 'Bake in a 350-degree preheated oven for 45 minutes or until the edges are lightly brown and crispy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'643F56CF11C9234CBCC20AC375B28E5C', X'40476503C209DC4A9A1EB69962C82F50', X'40476503C209DC4A9A1EB69962C82F50', 6, NULL, NULL, 2, NULL, 0, 'Let stand on a rack for five minutes, then serve hot with chutney.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC0FBAC0B26A26498393B10A5274FDA4', X'B0A63569BE822241B464A26C128B0F28', X'B0A63569BE822241B464A26C128B0F28', 0, NULL, NULL, 2, NULL, 0, 'Place the tofu in a baking dish or a dish large enough to hold all six slices, then slather on the first marinade, spreading it evenly. Let the tofu stand for an hour in the marinade.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8521ADCB17CB38499D332D4C26E93C27', X'B0A63569BE822241B464A26C128B0F28', X'B0A63569BE822241B464A26C128B0F28', 1, NULL, NULL, 2, NULL, 0, 'Make the second marinade by mixing all the ingredients together. Slather the second marinade on to the tofu slices, again making sure you spread it evenly. Let the tofu stand for at least 15 more minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B3BC7A40C35EB4E89E41AA92470289C', X'B0A63569BE822241B464A26C128B0F28', X'B0A63569BE822241B464A26C128B0F28', 2, NULL, NULL, 2, NULL, 0, 'While the tofu is marinating for the second time, preheat the oven to 350 degrees F. Place the tofu on a baking sheet covered with parchment paper or tinfoil and bake 40 minutes, turning over once halfway through baking.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4981B1D49A411D4FA9F9EF3C09BAA1F8', X'B0A63569BE822241B464A26C128B0F28', X'B0A63569BE822241B464A26C128B0F28', 3, NULL, NULL, 2, NULL, 0, 'Once the tofu has baked 40 minutes, turn the broiler on to 500 degrees. Broil the tofu for another 3-5 minutes or until some black spots appear on it.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC6B2D9A105BB146AFD3AD518909DEAE', X'B0A63569BE822241B464A26C128B0F28', X'B0A63569BE822241B464A26C128B0F28', 4, NULL, NULL, 2, NULL, 0, 'Remove from the oven, let the tofu stand for five minutes, then serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'000CD331216DE54C8BABE72B8F1C39CD', X'8C7B8DCCD14D53489DE21DF8306E479E', X'8C7B8DCCD14D53489DE21DF8306E479E', 0, NULL, NULL, 2, NULL, 0, 'Place the flaxmeal, cornstarch, paprika, ginger and garlic along with the water and salt to taste in a blender and blend into a very smooth paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9321E0339540145825F40AFABF532B0', X'8C7B8DCCD14D53489DE21DF8306E479E', X'8C7B8DCCD14D53489DE21DF8306E479E', 1, NULL, NULL, 2, NULL, 0, 'Place the cauliflower florets in a very large bowl and pour the cornstarch mixture over the florets. Using your hands or a spoon, toss the cauliflower in the mixture until thoroughly coated. Let it stand 20 minutes for the flavors to penetrate the cauliflower.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'531C4D69BFEE1246A582F60F5D990E09', X'8C7B8DCCD14D53489DE21DF8306E479E', X'8C7B8DCCD14D53489DE21DF8306E479E', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450 degrees. Line a baking sheet with parchment paper and spray with oil. Place the cauliflower florets on the baking sheet in a single layer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA84FF9C07CA2644A85C49FC3A8ADACF', X'8C7B8DCCD14D53489DE21DF8306E479E', X'8C7B8DCCD14D53489DE21DF8306E479E', 3, NULL, NULL, 2, NULL, 0, 'Bake the cauliflower for 20 to 25 minutes or until the florets are tender enough but have a slight bite. You don''t want the florets to be too soft.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'286B391EC5CCC7409366CA2186E05AF1', X'8C7B8DCCD14D53489DE21DF8306E479E', X'8C7B8DCCD14D53489DE21DF8306E479E', 4, NULL, NULL, 2, NULL, 0, 'In a large wok, heat the coconut oil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'093056E23EA32C498CD1CB8885E7493A', X'8C7B8DCCD14D53489DE21DF8306E479E', X'8C7B8DCCD14D53489DE21DF8306E479E', 5, NULL, NULL, 2, NULL, 0, 'Add the mustard seeds and when they sputter, add the curry leaves and green chili pepper, if using. Saute for a minute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F857FF751140A4B85379EA64C7E8C72', X'8C7B8DCCD14D53489DE21DF8306E479E', X'8C7B8DCCD14D53489DE21DF8306E479E', 6, NULL, NULL, 2, NULL, 0, 'Add the roasted cauliflower florets and toss in the mustard oil. Squeeze on the lemon juice and mix. Turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FAC13BFBDEF184E848E8EE4BC281EE0', X'8C7B8DCCD14D53489DE21DF8306E479E', X'8C7B8DCCD14D53489DE21DF8306E479E', 7, NULL, NULL, 2, NULL, 0, 'I love to serve this with some sliced raw onion on the side.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC45D176D0CCFF4C9C619FFC43071EB4', X'25F1BE8B5A62C84389342537363414C1', X'25F1BE8B5A62C84389342537363414C1', 0, NULL, NULL, 2, NULL, 0, 'To make the filling, place each boiled potato on a chopping board and use a heavy fork to mash the potato (the way you would if you were making smashed potatoes), then cut into small bits. You want your potato filling to be a crumbly, semi-mashed mix for an authentic filling, not cut in a neat dice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'407704D39A8438419D87AFD6A24FF6CB', X'25F1BE8B5A62C84389342537363414C1', X'25F1BE8B5A62C84389342537363414C1', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425 degrees F.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDD933C750ECB24D804A79581B9C6EA8', X'25F1BE8B5A62C84389342537363414C1', X'25F1BE8B5A62C84389342537363414C1', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a skillet. Add the cumin seeds and as they darken and become fragrant, add the onions, ginger, garlic and green cilantro, chili peppers or red pepper flakes. Stir to mix and saute until the onions turn translucent and start to change color.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'761736D294B75442ADE3842FAC4BB797', X'25F1BE8B5A62C84389342537363414C1', X'25F1BE8B5A62C84389342537363414C1', 3, NULL, NULL, 2, NULL, 0, 'Add the turmeric, stir it in, then add the crumbly potatoes and salt and lemon juice. Mix well and saute for a couple of minutes, then turn off the heat. Your potato mixture should be very dry.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D688F7119C25D54C9CFEC4F8B15C8667', X'25F1BE8B5A62C84389342537363414C1', X'25F1BE8B5A62C84389342537363414C1', 4, NULL, NULL, 2, NULL, 0, 'To make the pastry squares, place one puff pastry sheet on the countertop. Use the folds in the puff pastry as a guide to cut into three strips. Cut each strip further into three equal squares. You should have eighteen squares in all -- nine from each sheet.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CA1E4BF6681C74D902F85769AAE5FA0', X'25F1BE8B5A62C84389342537363414C1', X'25F1BE8B5A62C84389342537363414C1', 5, NULL, NULL, 2, NULL, 0, 'Use water to moisten one of the pastry squares along the edges. Place a couple of tablespoons of filling in the middle. The more crumbly your filling is, the more you''ll be able to accommodate on the square. Take another pastry square and place it over the filling and press the edges of the top and bottom squares together with your fingers to seal them, making a pocket. Use the tines of a fork to press down on the edges -- this also creates a decorative pattern. Use a sharp knife to cut a cross on the top of the square.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37F9AC13C5897C4384A9523BBEC67975', X'25F1BE8B5A62C84389342537363414C1', X'25F1BE8B5A62C84389342537363414C1', 6, NULL, NULL, 2, NULL, 0, 'Continue doing this for the remaining pastry and filling. Then place the squares on a baking sheet, at least an inch apart. Bake in the preheated oven 15 minutes until gold and crispy. You can brush on some vegetable oil on the tops of the squares before baking for a deeper gold color to your puff pastry squares.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87A54E8093849C489218EC4FF9BC046F', X'25F1BE8B5A62C84389342537363414C1', X'25F1BE8B5A62C84389342537363414C1', 7, NULL, NULL, 2, NULL, 0, 'Serve hot or warm or at room temperature. To reheat, heat the oven to 350 degrees and pop in for 2-3 minutes or until hot. These will keep in the refrigerator for about a week.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E927340CCEE51F40BB3553123A3653A2', X'066AD055A0F5FE4C93DE910A9CCFD165', X'066AD055A0F5FE4C93DE910A9CCFD165', 0, NULL, NULL, 2, NULL, 0, 'Mix all the ingredients well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10D6BA21E34EDE48B20CA2E7BE930A17', X'066AD055A0F5FE4C93DE910A9CCFD165', X'066AD055A0F5FE4C93DE910A9CCFD165', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a pan wide enough to deep-fry in. If possible, use a thermometer to ensure it reaches between 350 and 375 degrees before you begin to fry.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A32B5408B4B214448478FF8F376510BF', X'066AD055A0F5FE4C93DE910A9CCFD165', X'066AD055A0F5FE4C93DE910A9CCFD165', 2, NULL, NULL, 2, NULL, 0, 'Make a ball of the potato dough, about ¾ of an inch in diameter, and place it on your fingers.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C91A08A4C836B47AB4A044176D066DB', X'066AD055A0F5FE4C93DE910A9CCFD165', X'066AD055A0F5FE4C93DE910A9CCFD165', 3, NULL, NULL, 2, NULL, 0, 'Using the thumb of the same hand, flatten it out into a disc, about 1 ½ inches wide.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2E31CCB7B0577459FF19F544438930B', X'066AD055A0F5FE4C93DE910A9CCFD165', X'066AD055A0F5FE4C93DE910A9CCFD165', 4, NULL, NULL, 2, NULL, 0, 'Slip the wadas into the oil, as many as the pan will take at a time without becoming overcrowded.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'356916F8BA797649B527ADF58030C2DC', X'066AD055A0F5FE4C93DE910A9CCFD165', X'066AD055A0F5FE4C93DE910A9CCFD165', 5, NULL, NULL, 2, NULL, 0, 'Fry each side until golden-brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF4F179CBAA9254398B805E879FB317C', X'066AD055A0F5FE4C93DE910A9CCFD165', X'066AD055A0F5FE4C93DE910A9CCFD165', 6, NULL, NULL, 2, NULL, 0, 'Serve hot with chutney or just enjoy by itself.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BF7F2D07B4DA74180AB511D704970CB', X'DA2A2E10F2F923449F5F1EB3055DA8A6', X'DA2A2E10F2F923449F5F1EB3055DA8A6', 0, NULL, NULL, 2, NULL, 0, 'In a dry saucepan or cast iron skillet, roast the garlic, skin on, for a couple of minutes, turning over once, until slightly soft. Peel and place in a blender with the soaked chilies and ½ cup of water or the soaking liquid. Blend until smooth and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BFFDF2737B9B44CA66CFD73FF042398', X'DA2A2E10F2F923449F5F1EB3055DA8A6', X'DA2A2E10F2F923449F5F1EB3055DA8A6', 1, NULL, NULL, 2, NULL, 0, 'In a large wok or saucepan, heat the oil. Add the mustard seeds and when they sputter, add the fenugreek and fennel seeds and turmeric. Stir for half a minute, then add the potatoes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BF56B157FBE154188295FBB40CF3085', X'DA2A2E10F2F923449F5F1EB3055DA8A6', X'DA2A2E10F2F923449F5F1EB3055DA8A6', 2, NULL, NULL, 2, NULL, 0, 'Stir well to coat the potatoes with the spices. Turn the heat to medium and place a tight-fitting lid on the pan. Pour some water over the lid (a rimmed lid works best for this-- the water on the lid helps the potatoes steam without sticking or burning). Cook the potatoes for 15 minutes or until they are fork-tender but not mushy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59F27580B4A2554685C6F9846BFE7FC8', X'DA2A2E10F2F923449F5F1EB3055DA8A6', X'DA2A2E10F2F923449F5F1EB3055DA8A6', 3, NULL, NULL, 2, NULL, 0, 'Add the red chili-garlic paste and stir well to mix. Let the potatoes cook in the past for 2-3 minutes until the water dries out. Add salt to taste and lime juice and garnish, if desired, with coriander.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C96E3157708F624A8C9ADBDCDA9F1271', X'DA2A2E10F2F923449F5F1EB3055DA8A6', X'DA2A2E10F2F923449F5F1EB3055DA8A6', 4, NULL, NULL, 2, NULL, 0, 'Serve hot with rice and dal.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1DBBF4F27BB744FBD52860A3BDD00A7', X'78F8BAF2A36EF24CB7C4FD246C343D2A', X'78F8BAF2A36EF24CB7C4FD246C343D2A', 0, NULL, NULL, 2, NULL, 0, 'Boil the green beans and carrots until tender but not mushy. I do this by putting them in a bowl, adding about ¼ cup of water, and zapping them, covered, in a microwave, about five minutes on high. Drain all water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48BDFFE9B56B714EBB0F277BB2FD6CA7', X'78F8BAF2A36EF24CB7C4FD246C343D2A', X'78F8BAF2A36EF24CB7C4FD246C343D2A', 1, NULL, NULL, 2, NULL, 0, 'Heat oil in a skillet. Add the cumin seeds, and, once they sputter, add the onions and ginger.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E45AD9C3976D8F4DB9A7BE3F3DDA8ED8', X'78F8BAF2A36EF24CB7C4FD246C343D2A', X'78F8BAF2A36EF24CB7C4FD246C343D2A', 2, NULL, NULL, 2, NULL, 0, 'Stir-fry the onions for about a couple of minutes until they start to soften.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43FC4134A2EB374DAE2C782C837A3FC2', X'78F8BAF2A36EF24CB7C4FD246C343D2A', X'78F8BAF2A36EF24CB7C4FD246C343D2A', 3, NULL, NULL, 2, NULL, 0, 'Add the chilli and turmeric powders, stir into the oil, then add the peas, carrots, and green beans. Cook for another two or three minutes until they are quite tender. Stir in the coriander leaves. Place the cooked veggie mixture in a food processor and pulse three to four times for five seconds each. You don''t want a paste. Some bits of the veggies should still be visible.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6090E187FA291D41B0339560010AE1CB', X'78F8BAF2A36EF24CB7C4FD246C343D2A', X'78F8BAF2A36EF24CB7C4FD246C343D2A', 4, NULL, NULL, 2, NULL, 0, 'Add the veggies to the mashed potatoes and stir well together. Add salt to taste, and lemon juice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CD5C4C090F97D4D8E204B8C769B603F', X'78F8BAF2A36EF24CB7C4FD246C343D2A', X'78F8BAF2A36EF24CB7C4FD246C343D2A', 5, NULL, NULL, 2, NULL, 0, 'Turn off the heat and stir in the flour.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B62FDE111EFB545AF56AC18BA3531F7', X'78F8BAF2A36EF24CB7C4FD246C343D2A', X'78F8BAF2A36EF24CB7C4FD246C343D2A', 6, NULL, NULL, 2, NULL, 0, 'Heat a cast-iron or non-stick skillet.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F5F4F64FFD3AD40AF27ADE02C4724DE', X'78F8BAF2A36EF24CB7C4FD246C343D2A', X'78F8BAF2A36EF24CB7C4FD246C343D2A', 7, NULL, NULL, 2, NULL, 0, 'Spray or spread on a film of oil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'714095C4DC59E74C9CC730DCDA1C59A1', X'78F8BAF2A36EF24CB7C4FD246C343D2A', X'78F8BAF2A36EF24CB7C4FD246C343D2A', 8, NULL, NULL, 2, NULL, 0, 'Take a 1-inch ball of the potato-veggie mixture and flatten it into a disc.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F90F47146A3D945B10B6174751F47BE', X'78F8BAF2A36EF24CB7C4FD246C343D2A', X'78F8BAF2A36EF24CB7C4FD246C343D2A', 9, NULL, NULL, 2, NULL, 0, 'Dip the cutlets in the chickpea-water mixture to coat on both sides. Then dredge them in the bread crumbs crumbs so you have a fairly even coating on both sides.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BD642DA18811044B780E8907B2100C5', X'78F8BAF2A36EF24CB7C4FD246C343D2A', X'78F8BAF2A36EF24CB7C4FD246C343D2A', 10, NULL, NULL, 2, NULL, 0, 'Place the cutlets one by one in the hot skillet, without overcrowding. Cook on each side until golden-brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63409C6941F1C34881C44C5F1FC0FB2C', X'78F8BAF2A36EF24CB7C4FD246C343D2A', X'78F8BAF2A36EF24CB7C4FD246C343D2A', 11, NULL, NULL, 2, NULL, 0, 'Serve hot with ketchup or chutney.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6A9841CA79C7C479393B41B9C90D0B7', X'7649B881B10CE34881CE033BB412BB1C', X'7649B881B10CE34881CE033BB412BB1C', 0, NULL, NULL, 2, NULL, 0, 'Place the yeast in a large bowl or the bowl of a stand mixer. Add the sugar, nondairy milk, and ½ cup water. Let the yeast stand at least five minutes until it starts to look frothy and `blooms`');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53C0B2585C709A4CB69AF59A6DFB4E7E', X'7649B881B10CE34881CE033BB412BB1C', X'7649B881B10CE34881CE033BB412BB1C', 1, NULL, NULL, 2, NULL, 0, 'Add the flour and salt to the bowl and begin to knead, slowly drizzling in additional water until your dough comes together in a ball that''s just slightly sticky (traces of the dough will stick to your fingers when you touch it). Keep in mind that you may not need all of the water, so don''t dump it all in at one go.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7B47CB62A49354E9AF3B0ADA46C9115', X'7649B881B10CE34881CE033BB412BB1C', X'7649B881B10CE34881CE033BB412BB1C', 2, NULL, NULL, 2, NULL, 0, 'Drizzle the oil on top of the dough, turn it over once or twice to make sure the oil coats the dough, cover the bowl tightly with cling wrap or a lid or a kitchen towel, and set it aside to rise in a warm place for an hour to 90 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69252A3CC8D8444499781820BC13106B', X'7649B881B10CE34881CE033BB412BB1C', X'7649B881B10CE34881CE033BB412BB1C', 3, NULL, NULL, 2, NULL, 0, 'After the dough has risen, turn it out on your work surface. Knead it lightly, form into a disc and cut into eight even pieces, the way you would a pizza. Roll each piece into a ball (it won''t be firm), dust each ball lightly with flour, and set them aside, covered, for 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63D675ABDDCD8949ACC19FB4C3CBA26D', X'7649B881B10CE34881CE033BB412BB1C', X'7649B881B10CE34881CE033BB412BB1C', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, make your garlic butter mixture by heating the butter in a small saucepan along with the garlic. Let the butter and garlic cook together for 3-4 minutes over medium-low heat until the garlic flavor infuses the butter and the garlic starts to turn a light golden. You don''t want the garlic to burn. At this point, turn off the heat and stir in the herbs. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'505DCCBB12C1614FB89704CC9A89B8AC', X'7649B881B10CE34881CE033BB412BB1C', X'7649B881B10CE34881CE033BB412BB1C', 5, NULL, NULL, 2, NULL, 0, 'To shape the naan, either roll each ball into a teardrop shape or a round with a rolling pin. You can dust your work surface lightly with flour if needed. Or use your fingers to shape each ball into a small disc, then lift the disc by its edge and let the dough stretch and fall with the aid of gravity. Continue shaping by moving your fingers along the edges. If you''re not used to doing this, it''s possible your dough could tear, so be careful. If you do get a tear, patch it up by pressing the dough together and continue shaping with a rolling pin.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'941639BA93303043AE340ED4EC10A0B2', X'7649B881B10CE34881CE033BB412BB1C', X'7649B881B10CE34881CE033BB412BB1C', 6, NULL, NULL, 2, NULL, 0, 'Heat a cast-iron or any griddle over high heat until it''s screaming hot. Place the naan on the griddle. You will immediately see it start to bubble up and turn opaque. Once the underside gets golden-brown spots, turn over and continue cooking the other side. This process should take you no more than two minutes or less on either side.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EC85AAF3EF3AB4387DA53685F039D17', X'7649B881B10CE34881CE033BB412BB1C', X'7649B881B10CE34881CE033BB412BB1C', 7, NULL, NULL, 2, NULL, 0, 'As soon as you take the naan off the griddle, brush it with the garlic-herb-butter mixture on one side. Keep your naans wrapped in a kitchen towel until ready to serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DF46930874EFE479765CAEFAA252DD0', X'7649B881B10CE34881CE033BB412BB1C', X'7649B881B10CE34881CE033BB412BB1C', 8, NULL, NULL, 2, NULL, 0, 'You can reheat any leftover naans on the griddle before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB3F15ED4DDED645B6072920253745EB', X'5989F60CED4A19499497A9A0553F079C', X'5989F60CED4A19499497A9A0553F079C', 0, NULL, NULL, 2, NULL, 0, 'Place all the ingredients except water in a bowl or in a stand mixer. Trickle in the water slowly, kneading as you go, until you have a stiff but pliable dough. Set aside for at least 30 minutes for the dough to relax.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1981D78A29E6B94BB84D33F9649303CB', X'42ECC8375500C348B665B372B863A9D9', X'42ECC8375500C348B665B372B863A9D9', 0, NULL, NULL, 2, NULL, 0, 'Place all the ingredients in a bowl and mix well. Divide into 16 equal-sized portions and form into balls.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5772C0F601B3646B163946CB059A4DF', X'DEBE66E46C7A9E4F914A996142CC4CD1', X'DEBE66E46C7A9E4F914A996142CC4CD1', 0, NULL, NULL, 2, NULL, 0, 'Divide the dough into 16 equal-sized portions and shape into balls.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9E9E3F4F2E4454D99D3097694D08DD7', X'DEBE66E46C7A9E4F914A996142CC4CD1', X'DEBE66E46C7A9E4F914A996142CC4CD1', 1, NULL, NULL, 2, NULL, 0, 'Dust the surface and roll out one of the balls of dough into a disc, about four inches in diameter. Keep the center thicker and make the edges thin.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8ADBBF5AC153D4AA9BCFDB7CAC3F23C', X'DEBE66E46C7A9E4F914A996142CC4CD1', X'DEBE66E46C7A9E4F914A996142CC4CD1', 2, NULL, NULL, 2, NULL, 0, 'Place the stuffing in the center and pull the edges over the top of the stuffing. Pinch to make a tight seal and squeeze off any excess dough.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B92FD257CDEE7F4FBCA6206DFCA90024', X'DEBE66E46C7A9E4F914A996142CC4CD1', X'DEBE66E46C7A9E4F914A996142CC4CD1', 3, NULL, NULL, 2, NULL, 0, 'Flatten into a disc, flour both sides, and roll into a circle about six to seven inches in diameter.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0203DE3F4F45614B93845896AC2F8B5C', X'DEBE66E46C7A9E4F914A996142CC4CD1', X'DEBE66E46C7A9E4F914A996142CC4CD1', 4, NULL, NULL, 2, NULL, 0, 'Heat a griddle and spray with oil. Place the flatbread on the griddle and let it cook until golden-brown spots appear. Flip over and cook the other side. Flip over a couple more times if needed until the surface is evenly cooked and covered with golden-brown spots.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39DB6ED76B9A3E41B30180CF902B001E', X'DEBE66E46C7A9E4F914A996142CC4CD1', X'DEBE66E46C7A9E4F914A996142CC4CD1', 5, NULL, NULL, 2, NULL, 0, 'Serve hot with chutney or a cucumber raita or Indian pickles.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8FFCC34E974B44CB6DE3F2DBD03A2F2', X'66D7A695646E1B4793C95907EDE2A15D', X'66D7A695646E1B4793C95907EDE2A15D', 0, NULL, NULL, 2, NULL, 0, 'Slice the tofu into six pieces. Cover a baking sheet with tin foil and arrange the slices of tofu about an inch apart. Bake in a preheated 400 degree oven for 30 minutes, flipping over once halfway. Set aside to cool.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAA26729E63FBF498120CFC2633BD5FA', X'66D7A695646E1B4793C95907EDE2A15D', X'66D7A695646E1B4793C95907EDE2A15D', 1, NULL, NULL, 2, NULL, 0, 'In a wok or a skillet, lightly roast the chickpea flour until it turns a couple of shades darker and smells fragrant. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE0F3738E5A45D40B21E6208E61981D1', X'66D7A695646E1B4793C95907EDE2A15D', X'66D7A695646E1B4793C95907EDE2A15D', 2, NULL, NULL, 2, NULL, 0, 'Add the tofu and chickpea flour to the food processor with the remaining stuffing ingredients. Pulse until crumbly, with small bits of tofu. The mixture will look dry but should come together in a ball when shaped.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D566BFE6122C28478D55C68E951407D5', X'66D7A695646E1B4793C95907EDE2A15D', X'66D7A695646E1B4793C95907EDE2A15D', 3, NULL, NULL, 2, NULL, 0, 'Knead the paratha ingredients into a smooth dough using enough water to yield a firm but pliable dough. Set aside for at least 15 minutes before making the parathas.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF3E238843119B4EB7FDB611F2A304D5', X'66D7A695646E1B4793C95907EDE2A15D', X'66D7A695646E1B4793C95907EDE2A15D', 4, NULL, NULL, 2, NULL, 0, 'Divide the dough into approximately 15 balls.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A53078676C2CC3429DF0C3885E5636CA', X'66D7A695646E1B4793C95907EDE2A15D', X'66D7A695646E1B4793C95907EDE2A15D', 5, NULL, NULL, 2, NULL, 0, 'On a lightly floured surface, roll each ball into a 3-inch disc. Place 1 heaping tablespoon of the stuffing in the center and gather up the sides, pinching off the top if you have too much excess dough.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'168E2A289EF3CD45A502CDBFD700E7CC', X'66D7A695646E1B4793C95907EDE2A15D', X'66D7A695646E1B4793C95907EDE2A15D', 6, NULL, NULL, 2, NULL, 0, 'Roll out into a paratha about 5 inches in diameter. Flour the rolling pin and the paratha as needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1A75B86A997EE4EBF2F92E8E907F83A', X'66D7A695646E1B4793C95907EDE2A15D', X'66D7A695646E1B4793C95907EDE2A15D', 7, NULL, NULL, 2, NULL, 0, 'Heat a griddle and when hot but not smoking, place the paratha on it. Turn when small bubbles start to form. Cook on both sides until golden brown. You can make this without any added oil by using a nonstick pan or a well-seasoned cast iron pan, or spray lightly with cooking spray.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A6001E92098F04A800F0A41C4D49893', X'66D7A695646E1B4793C95907EDE2A15D', X'66D7A695646E1B4793C95907EDE2A15D', 8, NULL, NULL, 2, NULL, 0, 'Serve piping hot with mint chutney or spicy Indian pickle.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C204A73911E68040855EBF90100181C4', X'923D631E5FE8E148816C2A63F08D6579', X'923D631E5FE8E148816C2A63F08D6579', 0, NULL, NULL, 2, NULL, 0, 'Place the flour in a bowl along with the salt and mix. Add the sourdough discard, if using, or the baking powder. Drizzle in water, a little at a time, kneading until you have a shaggy dough that sticks to your fingers. I needed just under ¾ths cup of water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED03DD0A319E134D9686B3CC8F739F12', X'923D631E5FE8E148816C2A63F08D6579', X'923D631E5FE8E148816C2A63F08D6579', 1, NULL, NULL, 2, NULL, 0, 'Add 2 tsp of oil and continue kneading until incorporated.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD9E92B26182C740AD921EAF676F7139', X'923D631E5FE8E148816C2A63F08D6579', X'923D631E5FE8E148816C2A63F08D6579', 2, NULL, NULL, 2, NULL, 0, 'Cover the bowl and set it aside in a warm place for 2-3 hours. If using baking powder, you can go to the next step after 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE78858190AE1840ABF02CBC7FC486DC', X'923D631E5FE8E148816C2A63F08D6579', X'923D631E5FE8E148816C2A63F08D6579', 3, NULL, NULL, 2, NULL, 0, 'Flour your work surface. Using your fingers or a rolling pin, stretch the dough out to a rectangle about six inches wide and eight inches long. Brush on some oil, sprinkle on some flour. Pick the two long sides and fold them over so they overlap, creating three layers. Roll out the dough again and once again oil, flour, fold. Repeat one more time for a total of three times.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'059537A55B8DD44BBC52EE903F39EEC1', X'923D631E5FE8E148816C2A63F08D6579', X'923D631E5FE8E148816C2A63F08D6579', 4, NULL, NULL, 2, NULL, 0, 'After you''ve done this three times, roll the dough into a cylinder and cut lengthwise into eight pieces. Roll each piece into a ball and set aside, covered with a kitchen towel.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4F36A905213BE4397E93B25B592BFBA', X'69FEB3A5AB173B4CAD8E0E8FB04F1994', X'69FEB3A5AB173B4CAD8E0E8FB04F1994', 0, NULL, NULL, 2, NULL, 0, 'Place the mashed potatoes in a bowl with all the spices. Add salt to taste and mix well with your hands or a fork until everything is incorporated.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE49D9B3FDF6454BAF384CF65818F253', X'81240F74CC5DEB41A34D4BF97B29CA26', X'81240F74CC5DEB41A34D4BF97B29CA26', 0, NULL, NULL, 2, NULL, 0, 'Heat a griddle over medium-high heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'946C5288D155E54686ED4E9129412276', X'81240F74CC5DEB41A34D4BF97B29CA26', X'81240F74CC5DEB41A34D4BF97B29CA26', 1, NULL, NULL, 2, NULL, 0, 'Take a ball of dough and with your fingers or a rolling pin, roll it into a diameter of about four inches. Form the stuffing into a ball almost as big as the ball of dough and place it into the center. Pull up the sides of the dough over the stuffing and form a seal at the top.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'269ED0F9C9D3954EB9B0D75F10F5F293', X'81240F74CC5DEB41A34D4BF97B29CA26', X'81240F74CC5DEB41A34D4BF97B29CA26', 2, NULL, NULL, 2, NULL, 0, 'Place a bowl of water next to you. Wet your fingertips and press into the dough with them to shape the dough into a diameter of about six inches. You can also just roll the dough with a rolling pin and press into it with wet fingertips at the end, or just brush on some water with a pastry brush.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2065B6F33010FC479E0D526A8850F56B', X'81240F74CC5DEB41A34D4BF97B29CA26', X'81240F74CC5DEB41A34D4BF97B29CA26', 3, NULL, NULL, 2, NULL, 0, 'Place the kulcha, wet side down, on the griddle. If adding the cilantro and onion seeds at this point, sprinkle them on, then press into them very carefully with your fingertips so they are embedded within the dough. You can skip this step if not comfortable working with heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'282DF54B385CE146B8CCE32F385C6FBF', X'81240F74CC5DEB41A34D4BF97B29CA26', X'81240F74CC5DEB41A34D4BF97B29CA26', 4, NULL, NULL, 2, NULL, 0, 'Cover with a lid and let the kulcha steam-cook for about a minute or two or until the underside has golden-brown spots. Flip over and cook until golden spots appear on the other side.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDEFA731DF9020478DB7271F2954F82E', X'81240F74CC5DEB41A34D4BF97B29CA26', X'81240F74CC5DEB41A34D4BF97B29CA26', 5, NULL, NULL, 2, NULL, 0, 'Remove and brush on some vegan butter. If you didn''t add the cilantro earlier, you can mix it into the vegan butter and brush it on at this time.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'677102DFCC6EDB4386C02210B6CCF95C', X'81240F74CC5DEB41A34D4BF97B29CA26', X'81240F74CC5DEB41A34D4BF97B29CA26', 6, NULL, NULL, 2, NULL, 0, 'Pick up the kulcha with your palms as soon as you can handle it and crush it between your palms to make it separate slightly into flaky layers.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F4E087DEF7AEC42AD78FC329D762CE3', X'81240F74CC5DEB41A34D4BF97B29CA26', X'81240F74CC5DEB41A34D4BF97B29CA26', 7, NULL, NULL, 2, NULL, 0, 'Serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD7C9DFD797A6F4EA44B95915997C26D', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 0, NULL, NULL, 2, NULL, 0, 'Place the flour, salt and oil in a bowl and mix until the oil has dispersed through the flour.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7219C2B0964A9C4E88C1D246542F2170', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 1, NULL, NULL, 2, NULL, 0, 'Add enough water to make a stiff but pliable dough. Set the dough aside for at least 30 minutes so it can relax and roll out easily.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BC1D7C1BBAE2241832B281DC002935A', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 2, NULL, NULL, 2, NULL, 0, 'Roll the dough on a floured surface into a rectangle, about 10 X 7 inches.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A371C00DAA8A214A81B38CD3F1C72A41', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 3, NULL, NULL, 2, NULL, 0, 'Brush the entire top surface lightly with some oil, then dust lightly with a little flour.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EEAD97D8D0B2D49BA63CC305845262E', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 4, NULL, NULL, 2, NULL, 0, 'Lift the sides of the rectangle and fold them one over the other horizontally, so you have three layers.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB2DC255D8A2D448A664DD0966053E9C', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 5, NULL, NULL, 2, NULL, 0, 'Place the rectangle of dough in the refrigerator on a flat surface, like a cookie sheet.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A463F1C6AE38C4CBE7FFE3673C3E316', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 6, NULL, NULL, 2, NULL, 0, 'After half an hour, pull out the dough, roll it again into a rectangle about 10 X 7 inches, brush with oil, dust with flour, and fold again to get three more layers. Put it back in the refrigerator.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B536AD3EC969734CB47B9D13BB376550', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 7, NULL, NULL, 2, NULL, 0, 'Repeat the entire process two more times.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A9390CF9C0A9140B4654DFCDC650AC5', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 8, NULL, NULL, 2, NULL, 0, 'After you''ve folded and refrigerated the dough four times, roll it again into a rectangle and brush it with oil. This time, roll the dough horizontally the way you would a jelly roll.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06C8F30F58403541B0AD9E60E775EDF5', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 9, NULL, NULL, 2, NULL, 0, 'When you have a cylinder, tuck the seam under and, using a knife, cut it into 10 equal portions.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4AA0A70E73D3C4790381542940760BA', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 10, NULL, NULL, 2, NULL, 0, 'Take one roll, flatten it, and then roll it into a circle, about 6 inches in diameter. Don''t make the paratha too think, or you will end up flattening the layers. Use some flour to help you roll, if the dough sticks. Repeat for all the remaining 9 portions.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E55B431AD57F034F8D026E99CBD9FF74', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 11, NULL, NULL, 2, NULL, 0, 'Heat a griddle until it''s piping hot. Lower the heat to medium-high, place a paratha on the griddle, and wait until bubbles appear.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95341EC1A19B714CB4121662559BDFFA', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 12, NULL, NULL, 2, NULL, 0, 'Flip over the paratha and lightly brush some oil on the surface. When the underside is cooked and golden-brown spots appear, flip over once more and cook for a few more seconds or until golden spots appear.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'283CA38CB20CD5469FF348D38FDFC1CB', X'D2413BF5C2229E4F8049D3229AF557D0', X'D2413BF5C2229E4F8049D3229AF557D0', 13, NULL, NULL, 2, NULL, 0, 'Serve rightaway.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E1E2515E8DB4343A054661725B63945', X'56354C090A78094CBAF0AD918F7B759D', X'56354C090A78094CBAF0AD918F7B759D', 0, NULL, NULL, 2, NULL, 0, 'Place the black gram lentils in a bowl with the flattened rice and fenugreek seeds and cover by at least two inches of water. Set aside to soak at least six hours or overnight.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8645AA85A6790D48A5BC4C9A3C5E3C90', X'56354C090A78094CBAF0AD918F7B759D', X'56354C090A78094CBAF0AD918F7B759D', 1, NULL, NULL, 2, NULL, 0, 'Mix the parboiled rice and brown rice in another bowl and cover them with at least two inches of water. Set aside, again for at least six hours or overnight.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0651F5A331E2F24085FDF96140219E31', X'56354C090A78094CBAF0AD918F7B759D', X'56354C090A78094CBAF0AD918F7B759D', 2, NULL, NULL, 2, NULL, 0, 'Drain the lentils and grind them in a high powered blender, adding just enough water to get a batter that''s the consistency of a thick pancake batter. The batter should be very smooth. Remove the batter to a large bowl.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE7A4DA7716A124CBC6B34F4BC35922C', X'56354C090A78094CBAF0AD918F7B759D', X'56354C090A78094CBAF0AD918F7B759D', 3, NULL, NULL, 2, NULL, 0, 'Add the rice to the blender, again with enough water to create the consistency of a thick pancake batter. This time, grind until the batter still has a slight coarseness -- you don''t want to see broken grains of rice, but the batter should have a slight grittiness, like that of cornmeal, when you rub it between your fingers.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F73145917FD88D40939CCF3D36E9A73E', X'1891D5920805BA46A9C0AE6440438F23', X'1891D5920805BA46A9C0AE6440438F23', 0, NULL, NULL, 2, NULL, 0, 'Add the rice batter to the lentil batter and mix them both, preferably with your very clean hands. The reason for this is that the warmth of your hand helps start and hastens the fermentation process.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1795C01AC483F469908D6E560A3C84C', X'1891D5920805BA46A9C0AE6440438F23', X'1891D5920805BA46A9C0AE6440438F23', 1, NULL, NULL, 2, NULL, 0, 'Cover the bowl very tightly with cling wrap. Your bowl should be large enough that the batter reaches only half way up because the batter will rise. If you''re not sure, slide a plate or a baking sheet under your bowl to catch any overflow.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1008214060EB0F4CB75200AC16D317CB', X'1891D5920805BA46A9C0AE6440438F23', X'1891D5920805BA46A9C0AE6440438F23', 2, NULL, NULL, 2, NULL, 0, 'Place the bowl in a warm place or in a cold oven with the pilot light turned on and let it stand overnight or at least eight hours. By that time it should have become puff and risen.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD87AFB59FC321459B5C03F415EA56CB', X'D6AFCFA0F222B246B1DB61B897F90A25', X'D6AFCFA0F222B246B1DB61B897F90A25', 0, NULL, NULL, 2, NULL, 0, 'In a large stockpot with a vented lid that lets steam escape, or in a pressure cooker with a removable pressure regulator, place a trivet and an inch of water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A80F511FF7EFE40856DD200B69DB9FE', X'D6AFCFA0F222B246B1DB61B897F90A25', X'D6AFCFA0F222B246B1DB61B897F90A25', 1, NULL, NULL, 2, NULL, 0, 'Spray the idli molds lightly with cooking spray and fill them up, stopping just short of filling them up all the way because the idlis will puff up a bit as they steam. I first fill in the bottom-most plate of the mold, then slide on the second one, fill it up, slide on the third, fill it and so on.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5159D37516447142AFD670C3F60F3203', X'D6AFCFA0F222B246B1DB61B897F90A25', X'D6AFCFA0F222B246B1DB61B897F90A25', 2, NULL, NULL, 2, NULL, 0, 'Place the idli mold in the stockpot or pressure cooker and cover with the lid. Do not put the pressure regulator on the pressure cooker vent.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFEB0B7D47C20C4BBF69CFF27BA7D9F8', X'D6AFCFA0F222B246B1DB61B897F90A25', X'D6AFCFA0F222B246B1DB61B897F90A25', 3, NULL, NULL, 2, NULL, 0, 'Place your stockpot or cooker on medium-high heat. When you see wisps of steam rise from the vent in the lid or the pressure cooker, set the timer to 10 minutes and let the idlis steam away. At the end of 10 minutes, turn off the heat and let the idlis stand for a couple of minutes before opening and removing the mold.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'450C27C87B53964CB1910E324AF7FC47', X'D6AFCFA0F222B246B1DB61B897F90A25', X'D6AFCFA0F222B246B1DB61B897F90A25', 4, NULL, NULL, 2, NULL, 0, 'Disassemble the mold and let the idlis stand in the plates for another five minutes or so. Then slide them off, either with your fingers or with a spoon.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B237F0FBD4FFE498B1EACFD9D1F307D', X'21592875A2EE8A488DB5E8BBAB916B71', X'21592875A2EE8A488DB5E8BBAB916B71', 0, NULL, NULL, 2, NULL, 0, 'Cover the brown rice with water in a microwave-safe bowl and nuke it for five minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FBA3E4ACDB36E42A155EC8699FE0908', X'21592875A2EE8A488DB5E8BBAB916B71', X'21592875A2EE8A488DB5E8BBAB916B71', 1, NULL, NULL, 2, NULL, 0, 'Allow the rice to sit in the hot water for another half an hour.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F61694031CFDA24B813E55C93ACC91AD', X'21592875A2EE8A488DB5E8BBAB916B71', X'21592875A2EE8A488DB5E8BBAB916B71', 2, NULL, NULL, 2, NULL, 0, 'Now add the rice to the remaining ingredients, along with the water. Add more water to cover the lentils and rice. Allow them to soak for at least 5 hours and more if you have the time. Drain.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9846980F56D1E49B1A8596AA259DFD7', X'21592875A2EE8A488DB5E8BBAB916B71', X'21592875A2EE8A488DB5E8BBAB916B71', 3, NULL, NULL, 2, NULL, 0, 'Blend the rice-dal mixture, in several batches, adding enough water to make a smooth batter that''s runny enough to spread into a crepe, but thick enough to coat the back of a spoon. At this point, you can leave the batter to ferment overnight, which helps beneficial probiotic bacteria in the batter grow. You can also make the dosas rightaway.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A94367A7FE2864D83FD83948FF8BC2E', X'21592875A2EE8A488DB5E8BBAB916B71', X'21592875A2EE8A488DB5E8BBAB916B71', 4, NULL, NULL, 2, NULL, 0, 'To make dosas, heat a cast-iron or non-stick griddle. The griddle should be hot enough that when you sprinkle a few drops of water on it, they sizzle and evaporate.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'408EA500E2D7914F8D58C94AE334830D', X'21592875A2EE8A488DB5E8BBAB916B71', X'21592875A2EE8A488DB5E8BBAB916B71', 5, NULL, NULL, 2, NULL, 0, 'Using a ladle with a rounded bottom, pour some batter into the center of the griddle and, in a quick but smooth motion, spread outward in circles. Don''t be afraid if you make holes: just add a small drop of batter to patch it. If your dosa does not spread smoothly, it''s possible your ladle is hot. Turn off or lower the heat, and try again.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F76A2D1713347D42BAE91B71F79DD42C', X'21592875A2EE8A488DB5E8BBAB916B71', X'21592875A2EE8A488DB5E8BBAB916B71', 6, NULL, NULL, 2, NULL, 0, 'Pour a few drops of oil around the dosa''s edges to help it crisp up. Once the underside is golden brown, loosen the dosa gently from the skillet and flip over. If your griddle was hot enough to begin with, this step will be very, very easy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1643BF3EF4BD7744AFD7A0BDFE4CE792', X'21592875A2EE8A488DB5E8BBAB916B71', X'21592875A2EE8A488DB5E8BBAB916B71', 7, NULL, NULL, 2, NULL, 0, 'Cook the other side for a few seconds, giving more time if your dosa is thicker. Serve hot with some sambar or chutney or both.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9EBFFB65962C44EB357242E69D3836F', X'21592875A2EE8A488DB5E8BBAB916B71', X'21592875A2EE8A488DB5E8BBAB916B71', 8, NULL, NULL, 2, NULL, 0, 'To make the chutney, just give all the ingredients a whir in the blender until the cilantro is completely broken down. Check salt and serve with the dosas.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5A2C90133784C41ADCB9369B05C2037', X'85EEE49C923DAE4096A2861FB04EB3F2', X'85EEE49C923DAE4096A2861FB04EB3F2', 0, NULL, NULL, 2, NULL, 0, 'Make the potato subzi:');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2CA498290314A46A775870EC6FDA74B', X'85EEE49C923DAE4096A2861FB04EB3F2', X'85EEE49C923DAE4096A2861FB04EB3F2', 1, NULL, NULL, 2, NULL, 0, 'Boil the potatoes in their jackets until a fork inserted in the thickest portion goes cleanly through. Dice the potatoes into a fairly small dice. I leave the skins on because I love them, but you can peel your potatoes if you''d rather.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'862BFACCDD32A74E93D5CBD1D5F97B82', X'85EEE49C923DAE4096A2861FB04EB3F2', X'85EEE49C923DAE4096A2861FB04EB3F2', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a wok or a large saucepan');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17763277C7DA29489C9130903019CA8A', X'85EEE49C923DAE4096A2861FB04EB3F2', X'85EEE49C923DAE4096A2861FB04EB3F2', 3, NULL, NULL, 2, NULL, 0, 'Add the mustard seeds and when they crackle, add the red chili peppers and onions.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58631C86F2042C4292CA4970B5C674C1', X'85EEE49C923DAE4096A2861FB04EB3F2', X'85EEE49C923DAE4096A2861FB04EB3F2', 4, NULL, NULL, 2, NULL, 0, 'Saute, stirring frequently, until the onion just starts to turn color');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66E7EDE9EADAD744924980461FDE4C2F', X'85EEE49C923DAE4096A2861FB04EB3F2', X'85EEE49C923DAE4096A2861FB04EB3F2', 5, NULL, NULL, 2, NULL, 0, 'Add the ginger and saute a few more seconds. Add the diced bell peppers and ground black pepper.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FB308CD937335418FED51F523C934A3', X'85EEE49C923DAE4096A2861FB04EB3F2', X'85EEE49C923DAE4096A2861FB04EB3F2', 6, NULL, NULL, 2, NULL, 0, 'Cook, stirring occasionally, for about five minutes or until the peppers start to soften. Add the potatoes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1667561435EFC848B3AF9BF2BA4B5431', X'85EEE49C923DAE4096A2861FB04EB3F2', X'85EEE49C923DAE4096A2861FB04EB3F2', 7, NULL, NULL, 2, NULL, 0, 'Let the potatoes cook over medium-high heat until they start to form a crust at the bottom. Stir a few times so they don''t stick. With a potato masher or a heavy ladle, mash the potatoes slightly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E8739DF84C9954BB33DB6B1641BEC7E', X'85EEE49C923DAE4096A2861FB04EB3F2', X'85EEE49C923DAE4096A2861FB04EB3F2', 8, NULL, NULL, 2, NULL, 0, 'Season with salt and stir in the lemon juice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'974579651C91E443857CF44E5A7C20AF', X'85EEE49C923DAE4096A2861FB04EB3F2', X'85EEE49C923DAE4096A2861FB04EB3F2', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle the cilantro and mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B629A1C172D314FA1A913268576AB05', X'F91764163344A34292365F5A5F54F875', X'F91764163344A34292365F5A5F54F875', 0, NULL, NULL, 2, NULL, 0, 'Mix the flours, baking soda and salt in a bowl with enough water to make a runny batter. You want something far runnier than a pancake batter because you want to spread it fairly thin.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C444212EBE13445BFE7925ADC0B0AE4', X'F91764163344A34292365F5A5F54F875', X'F91764163344A34292365F5A5F54F875', 1, NULL, NULL, 2, NULL, 0, 'Heat a griddle over medium-high heat. Using a round-bottomed ladle, pour about half a cup of batter into the center of the griddle. Quickly, spread outward in a circular motion with the bottom of the ladle. You want your dosa to be fairly thin if you like it crispy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'307B80AA4326D645871C40D9A5CE1DBE', X'F91764163344A34292365F5A5F54F875', X'F91764163344A34292365F5A5F54F875', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle or spray some oil around the edges. Cook until the top is dry and the edges are browned. Remove to a plate.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E97F03A418AEF428A4F3D47B977EE15', X'F91764163344A34292365F5A5F54F875', X'F91764163344A34292365F5A5F54F875', 3, NULL, NULL, 2, NULL, 0, 'Place the stuffing inside the dosa, fold over, and serve with some chutney.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2599C58F57325948AC071A93D3BA32CE', X'F91764163344A34292365F5A5F54F875', X'F91764163344A34292365F5A5F54F875', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF50F1C04B25284399C289BCEF5FBD89', X'DE5B9582817A0E46AF2A2B7E77E56E1E', X'DE5B9582817A0E46AF2A2B7E77E56E1E', 0, NULL, NULL, 2, NULL, 0, 'Mix the parboiled brown rice and the two dals, cover with at least 2 inches of water, and let them soak for 5-6 hours.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'200F9CE0AAD32E4589ABBE14ABA017EE', X'DE5B9582817A0E46AF2A2B7E77E56E1E', X'DE5B9582817A0E46AF2A2B7E77E56E1E', 1, NULL, NULL, 2, NULL, 0, 'Drain the soaked dals and rice and place in a blender along with the remaining ingredients. Add water and blend into a smooth batter that should be about the consistency of a pancake batter.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E8CA161A254B54C8ECF95AB4BC5D1AA', X'DE5B9582817A0E46AF2A2B7E77E56E1E', X'DE5B9582817A0E46AF2A2B7E77E56E1E', 2, NULL, NULL, 2, NULL, 0, 'Heat a cast-iron or nonstick griddle on a medium flame until a drop of water flicked on the surface skitters and evaporates.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'157870FEF820E7498467DDEF8410EFF4', X'DE5B9582817A0E46AF2A2B7E77E56E1E', X'DE5B9582817A0E46AF2A2B7E77E56E1E', 3, NULL, NULL, 2, NULL, 0, 'Pour about ½ cup of the uthappam batter in the center and, if needed, coax a little with the ladle to form a circle about 5-6 inches in diameter. You can make your uthappam thicker if you like. Either use more batter or shape it into a smaller circle.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB86AB95086C134CBED98E0DB35666E0', X'DE5B9582817A0E46AF2A2B7E77E56E1E', X'DE5B9582817A0E46AF2A2B7E77E56E1E', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle on some of the chopped onions and any other veggies or herbs you’re using.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52B291E4509BAC40836E7A9443CB5BF9', X'DE5B9582817A0E46AF2A2B7E77E56E1E', X'DE5B9582817A0E46AF2A2B7E77E56E1E', 5, NULL, NULL, 2, NULL, 0, 'Drizzle a few drops of oil along the edges of the uthappam. This makes for a crisper edge and also makes it easier to flip the uthappam.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A2344E784B9154FA8DED2269251EB99', X'DE5B9582817A0E46AF2A2B7E77E56E1E', X'DE5B9582817A0E46AF2A2B7E77E56E1E', 6, NULL, NULL, 2, NULL, 0, 'Place a lid on top of the pan and let the uthappam cook about two minutes. It is important to cover the pan because if you don’t you might end up with semi-cooked batter on the inside and that wouldn’t taste any good. You want the uthappam to steam thoroughly on the inside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D42677F904C614CAFD3777932BB287C', X'DE5B9582817A0E46AF2A2B7E77E56E1E', X'DE5B9582817A0E46AF2A2B7E77E56E1E', 7, NULL, NULL, 2, NULL, 0, 'Flip the uthappam once it is golden brown on the bottom and cook for about a minute more.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B6737FD1BB591479621C022875956A3', X'DE5B9582817A0E46AF2A2B7E77E56E1E', X'DE5B9582817A0E46AF2A2B7E77E56E1E', 8, NULL, NULL, 2, NULL, 0, 'Serve hot with some Sundried Tomato Chutney (recipe follows) or with a traditional coriander-coconut chutney.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE33DF1CFD38624B950D8D793C903B9E', X'D58F3E2F9844B241A3712BDC00250477', X'D58F3E2F9844B241A3712BDC00250477', 0, NULL, NULL, 2, NULL, 0, 'Place all the ingredients in a blender and blend into a smooth paste. Serve with the Brown Rice Uthappam.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95C7E76265171B42A4D44C2DB39ED39B', X'3B51D0D873EC65498345E0CF1599A2AE', X'3B51D0D873EC65498345E0CF1599A2AE', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large saucepan. Add the cumin seeds and saute for a few seconds. Once they start to change color, add the other spices (if using all or some of them) at this point, one by one, and stir to mix in the end. Otherwise, skip to the next step.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4FCD1D8B53F2204BA6662091E91954CE', X'3B51D0D873EC65498345E0CF1599A2AE', X'3B51D0D873EC65498345E0CF1599A2AE', 1, NULL, NULL, 2, NULL, 0, 'Add the rice to the saucepan and stir-fry for a couple of minutes until the rice starts to turn opaque.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42222DB2E09FC5488538014128C17691', X'3B51D0D873EC65498345E0CF1599A2AE', X'3B51D0D873EC65498345E0CF1599A2AE', 2, NULL, NULL, 2, NULL, 0, 'Add four cups of water and the salt, stir, and bring to a boil over high heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70B0FDFAB365804A866FCB3FECF79B2F', X'3B51D0D873EC65498345E0CF1599A2AE', X'3B51D0D873EC65498345E0CF1599A2AE', 3, NULL, NULL, 2, NULL, 0, 'Cover the saucepan with a tight-fitting lid and turn the heat down to low. Cook for 15 minutes, never removing the lid from the pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBC5813B3615D14A8055D97EBF398782', X'3B51D0D873EC65498345E0CF1599A2AE', X'3B51D0D873EC65498345E0CF1599A2AE', 4, NULL, NULL, 2, NULL, 0, 'Once you turn off the heat, don''t yet take the lid off. Wait at least 10 minutes, then open and fluff the grains of rice with a fork to separate.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EA451C1F2844C4EA8FF79E21B1E6E8B', X'3B51D0D873EC65498345E0CF1599A2AE', X'3B51D0D873EC65498345E0CF1599A2AE', 5, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA5D6A4E8418E642A304A5A6D1F2EF7A', X'F6921C539ED5D541B6D2BD33C415E5C8', X'F6921C539ED5D541B6D2BD33C415E5C8', 0, NULL, NULL, 2, NULL, 0, 'Place 2 cups of water in a microwave safe bowl along with three green cardamom pods, three cloves, cinnamon, bay leaves and half the caraway seeds. Add salt to your taste. Add the rice to the bowl and microwave for eight minutes. Let the rice stand in the microwave until ready to use.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5649C4751E3D034AB9011FF71C49CB85', X'F6921C539ED5D541B6D2BD33C415E5C8', X'F6921C539ED5D541B6D2BD33C415E5C8', 1, NULL, NULL, 2, NULL, 0, 'After putting the rice in the microwave, chop your veggies, and simultaneously heat the oil in a large saucepan with an airtight lid. When the oil is hot, add the remaining green cardamom pods and cloves, brown cardamom pods and the remaining caraway seeds.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'762284085A96C546AF18BDD71142B97D', X'F6921C539ED5D541B6D2BD33C415E5C8', X'F6921C539ED5D541B6D2BD33C415E5C8', 2, NULL, NULL, 2, NULL, 0, 'Let the spices cook in the oil for a minute, then add the chopped carrots and potatoes. Season with salt and pepper and then cover and cook over medium-low heat for 5 minutes or until the veggies are almost tender. If the veggies are sticking to the pan at this time, add ¼ cup of water or vegetable stock and scrape up any bits.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE0F331C07C61A45AEA0D1879D45988D', X'F6921C539ED5D541B6D2BD33C415E5C8', X'F6921C539ED5D541B6D2BD33C415E5C8', 3, NULL, NULL, 2, NULL, 0, 'Add the ginger-garlic paste, cilantro, mint and dill and stir to mix. Add the biryani masala, cashews if using, half the fried onions, tofu cubes and yogurt and stir well. Check if there''s enough salt and add more if needed. Bring the masala to a boil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B99BCD94E3FFB844A73E473D8AE5BBC2', X'F6921C539ED5D541B6D2BD33C415E5C8', X'F6921C539ED5D541B6D2BD33C415E5C8', 4, NULL, NULL, 2, NULL, 0, 'Lower the heat and bring the rice out of the microwave. Run a fork through it to loosen the grains. The rice should be almost done when you take it out, but not quite. Spread it evenly on top of the veggies and masala in the pot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6B678831703624AA5C1298DA347ED1C', X'F6921C539ED5D541B6D2BD33C415E5C8', X'F6921C539ED5D541B6D2BD33C415E5C8', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle the remaining fried onions on top of the rice. Cover with the airtight lid and let the biryani cook over low heat for 10 minutes, undisturbed. Let the biryani stand 10 minutes after cooking is done before opening to serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBF458C5196FD3438618E3FA7DC8D16B', X'F6921C539ED5D541B6D2BD33C415E5C8', X'F6921C539ED5D541B6D2BD33C415E5C8', 6, NULL, NULL, 2, NULL, 0, 'Serve hot with Cucumber Raita (recipe link below)');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'376417187167B54491DCEB7A93543D65', X'BFF7CCD38A1F9F4989A1652507529E56', X'BFF7CCD38A1F9F4989A1652507529E56', 0, NULL, NULL, 2, NULL, 0, 'Blend the raw cashews with 1 cup of water and the lemon juice. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A34A04673BA504EB1944DBA4C021541', X'BFF7CCD38A1F9F4989A1652507529E56', X'BFF7CCD38A1F9F4989A1652507529E56', 1, NULL, NULL, 2, NULL, 0, 'In a saucepan, place 2 cups of water, cardamom, bay leaves, shahjeera. Add salt to taste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A7CFD0FDDDD9B43B6F551A4344FB559', X'BFF7CCD38A1F9F4989A1652507529E56', X'BFF7CCD38A1F9F4989A1652507529E56', 2, NULL, NULL, 2, NULL, 0, 'Pour in the drained rice and place over a medium fire. When the water starts to boil, cover the saucepan with a tight-fitting lid and then turn down the heat to the lowest setting.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'982639E30E9BD242BC7BD5CB30E8B138', X'BFF7CCD38A1F9F4989A1652507529E56', X'BFF7CCD38A1F9F4989A1652507529E56', 3, NULL, NULL, 2, NULL, 0, 'Let the rice steam for 8 minutes. Turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B867304692CF240AE22A8A16CE07536', X'BFF7CCD38A1F9F4989A1652507529E56', X'BFF7CCD38A1F9F4989A1652507529E56', 4, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large saucepan with a tight-fitting lid. Add the shahjeera seeds ir cumin seeds and, when they start to change color, add the mint and coriander leaves. Stir to mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67AC95C47F8AD549A88EF7F82A7D73EF', X'BFF7CCD38A1F9F4989A1652507529E56', X'BFF7CCD38A1F9F4989A1652507529E56', 5, NULL, NULL, 2, NULL, 0, 'Add the ginger and garlic pastes and saute another couple of minutes, stirring frequently to ensure they do not stick to the bottom of the pan. Sprinkle on some salt and pepper to season.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6CD3360B4C8D34F9C783DDA766B31F7', X'BFF7CCD38A1F9F4989A1652507529E56', X'BFF7CCD38A1F9F4989A1652507529E56', 6, NULL, NULL, 2, NULL, 0, 'Add the turmeric, cayenne or paprika, biryani masala, half of the fried onions, and the blended cashew-lemon paste. Stir well to mix and let it cook for a minute. Add salt.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA7A3DDD1082E64E8F67D56089CE4EB0', X'BFF7CCD38A1F9F4989A1652507529E56', X'BFF7CCD38A1F9F4989A1652507529E56', 7, NULL, NULL, 2, NULL, 0, 'Add the mushrooms to the saucepan and stir to coat with the spices. If the mixture looks too dry, add ½ cup of water or vegetable stock. Let the sauce come to a boil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F23095A50EE8AC48917CAD1B2959DB52', X'BFF7CCD38A1F9F4989A1652507529E56', X'BFF7CCD38A1F9F4989A1652507529E56', 8, NULL, NULL, 2, NULL, 0, 'Turn off the heat. Spread the cooked rice on top of the sauce in an even layer. Sprinkle on the remaining fried onions and the saffron with the milk.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF006022BEAEB047B7ED082F174B94B3', X'BFF7CCD38A1F9F4989A1652507529E56', X'BFF7CCD38A1F9F4989A1652507529E56', 9, NULL, NULL, 2, NULL, 0, 'Cover with a tight lid. If the lid you have doesn''t fit snugly, you can cover with a sheet of aluminum foil and then place the lid on top to make it as airtight as possible.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6256820FF9A75A46B14F8B3351A7893F', X'BFF7CCD38A1F9F4989A1652507529E56', X'BFF7CCD38A1F9F4989A1652507529E56', 10, NULL, NULL, 2, NULL, 0, 'Turn the heat to high for about three minutes, then lower it to the lowest setting, and let the biryani cook, undisturbed, for 20 minutes. Turn off the heat and let it stand for at least 10 minutes, uncovered, before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C722692B07CDC048B39AC7B5F40A2FDB', X'8476AD45A3796F4295C981C47AE01C9A', X'8476AD45A3796F4295C981C47AE01C9A', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to boil, lightly salt it, then add the rice and cook until the rice grains are almost tender but not fully cooked. Drain and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'648C233C17C5504D97B3F6C90FC3EE2D', X'8476AD45A3796F4295C981C47AE01C9A', X'8476AD45A3796F4295C981C47AE01C9A', 1, NULL, NULL, 2, NULL, 0, 'Mix the saffron with the nondairy milk and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E889AB6311CA7B47B018F3FE0CF97039', X'8476AD45A3796F4295C981C47AE01C9A', X'8476AD45A3796F4295C981C47AE01C9A', 2, NULL, NULL, 2, NULL, 0, 'Heat 1 ½ tbsp oil. Add the cardamoms, cloves, cinnamon, bay leaf and cumin seeds. Stir-fry until the spices release their fragrance and begin to turn color.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1EB0AD50CF6F4429DAA93C2E5B3E22C', X'8476AD45A3796F4295C981C47AE01C9A', X'8476AD45A3796F4295C981C47AE01C9A', 3, NULL, NULL, 2, NULL, 0, 'Add the potatoes, saute for 2-3 minutes, then add the carrots and saute for another 2-3 minutes. Add the cauliflower and green beans and saute for a couple of minutes. Add green peas, then add some salt, cover the pot and let the veggies cook over a medium-low flame for about five minutes. Stir a couple of times to ensure they do not stick. You don''t want the veggies to brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70A6895B8CA1EF43ADE2A1851274901C', X'8476AD45A3796F4295C981C47AE01C9A', X'8476AD45A3796F4295C981C47AE01C9A', 4, NULL, NULL, 2, NULL, 0, 'When the potatoes are almost cooked and the cauliflower is al dente, mix in the rice, tofu cubes and the saffron in milk. Cover and let the rice continue cooking over low heat for 10 more minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF2974FCE418814E9A9A752B591E9699', X'8476AD45A3796F4295C981C47AE01C9A', X'8476AD45A3796F4295C981C47AE01C9A', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the remaining ½ tbsp oil in a small saucepan. Add the cashews and raisins to it, and finally the blanched, sliced almonds. Cook until the nuts are a light gold, then pour over the rice and mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2BA481C4EFFA24E919EB1DF144748C1', X'8476AD45A3796F4295C981C47AE01C9A', X'8476AD45A3796F4295C981C47AE01C9A', 6, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCE9967A5420D841A9E8426CA31D55FC', X'9363943CBA090446B99F7EFE908C13E1', X'9363943CBA090446B99F7EFE908C13E1', 0, NULL, NULL, 2, NULL, 0, 'Place the chopped cucumbers in a colander with a bowl underneath to collect the juices. Sprinkle a little salt on the cucumbers and set aside for 10 minutes. After 10 minutes, press down on the cucumbers to release their juices. Set the juices aside. This step helps you control the amount of liquid you add to the yogurt rice while not wasting all of the delicious flavor of the cucumbers.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1D83863EF5CCF459F30218CAB366938', X'9363943CBA090446B99F7EFE908C13E1', X'9363943CBA090446B99F7EFE908C13E1', 1, NULL, NULL, 2, NULL, 0, 'Cook the rice. You can use the microwave or, if cooking on the stovetop, bring 2 cups of water to a boil, add the rice, bring back to a boil, cover and cook for 15 minutes. Let the rice stand a few minutes before opening.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFECF3788EE81A4CAE4BF4041293A1BF', X'9363943CBA090446B99F7EFE908C13E1', X'9363943CBA090446B99F7EFE908C13E1', 2, NULL, NULL, 2, NULL, 0, 'Measure out the reserved cucumber juices in a half-cup measure and add enough nondairy milk to make a half cup. Place this in a blender along with the tofu and the lemon juice and blend into a very smooth paste. If using cashew yogurt, you can skip this step.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98BE21437FDB9F4B854E7CD403343905', X'9363943CBA090446B99F7EFE908C13E1', X'9363943CBA090446B99F7EFE908C13E1', 3, NULL, NULL, 2, NULL, 0, 'Add the blended tofu to a bowl along with the cooked rice and cucumbers and mix well. If using cashew yogurt, add it now to the bowl with the rice and cucumbers and mix in the cucumber juices. You can add some nondairy milk if the rice is too dry. Your curd rice should have the consistency of a risotto-- a little runny and very creamy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E18F75B8E1296A4AA05E07F95C07BA65', X'9363943CBA090446B99F7EFE908C13E1', X'9363943CBA090446B99F7EFE908C13E1', 4, NULL, NULL, 2, NULL, 0, 'Heat the oil in a small skillet. Add the mustard seeds and, when they sputter, add the black gram lentils. Once they start to turn a light golden color, add the jalapenos and curry leaves. Saute for a few seconds, then add the peanuts and cashew nuts if using. Saute until the nuts are lightly golden.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3E9768BCB51F34A8299FB2CDBBF7AFF', X'9363943CBA090446B99F7EFE908C13E1', X'9363943CBA090446B99F7EFE908C13E1', 5, NULL, NULL, 2, NULL, 0, 'Add the mustard seed-curry-leaf-nuts mixture to the bowl with the yogurt and rice and mix well. Add salt to taste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C75AD34B9713C4BB431741E90E64675', X'9363943CBA090446B99F7EFE908C13E1', X'9363943CBA090446B99F7EFE908C13E1', 6, NULL, NULL, 2, NULL, 0, 'Serve at room temperature or cold.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07BB7B74927C764FA44A8E7ABFE90271', X'6677B9F558D28D4DAB63821049A30B6B', X'6677B9F558D28D4DAB63821049A30B6B', 0, NULL, NULL, 2, NULL, 0, 'In a large saucepan, add the drained rice and the coconut milk along with 1 cup water and salt to taste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D1EA502C0CD3E41BF263ED2D15DB0AB', X'6677B9F558D28D4DAB63821049A30B6B', X'6677B9F558D28D4DAB63821049A30B6B', 1, NULL, NULL, 2, NULL, 0, 'When the liquid begins to boil, cover the saucepan with a tight-fitting lid, lower heat to low, and let the rice cook 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DE22528ABFF384094288526E2E47B5F', X'6677B9F558D28D4DAB63821049A30B6B', X'6677B9F558D28D4DAB63821049A30B6B', 2, NULL, NULL, 2, NULL, 0, 'In a small skillet, heat half the oil and add the chana dal, half of the urad dal, and the red chili peppers. Roast until golden and then powder in a spice grinder or with a mortar and pestle.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B35DD0FBEB46341B8A5C1EF15DF810E', X'6677B9F558D28D4DAB63821049A30B6B', X'6677B9F558D28D4DAB63821049A30B6B', 3, NULL, NULL, 2, NULL, 0, 'In the same small skillet, heat the remaining oil and add the mustard seeds. When they sputter, add the remaining urad dal, cashews and the curry leaves. Saute until the dal and cashews are lightly golden. Add the shredded coconut and continue to saute until the coconut starts to just turn a very light gold.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'412C7A7637A05C4B8C9E22A2993D6C1A', X'6677B9F558D28D4DAB63821049A30B6B', X'6677B9F558D28D4DAB63821049A30B6B', 4, NULL, NULL, 2, NULL, 0, 'Add this and the powdered dals to the rice and mix gently with a fork, taking care not to mash the rice. Add salt if needed and serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B33059F70505A4FA6CCAE047F10D863', X'A192B3D881A7CC4B8973EE37CAE2BBF9', X'A192B3D881A7CC4B8973EE37CAE2BBF9', 0, NULL, NULL, 2, NULL, 0, 'If you have a pressure cooker, heat oil in the pressure cooker. Otherwise use a large saucepan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D4535E894DA0241A9DDA55538555D65', X'A192B3D881A7CC4B8973EE37CAE2BBF9', X'A192B3D881A7CC4B8973EE37CAE2BBF9', 1, NULL, NULL, 2, NULL, 0, 'Add the cumin seeds and when they sputter, add the onions and curry leaves.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDD3BC7A6093A3479C445175CB1237D1', X'A192B3D881A7CC4B8973EE37CAE2BBF9', X'A192B3D881A7CC4B8973EE37CAE2BBF9', 2, NULL, NULL, 2, NULL, 0, 'Saute, stirring, until brown spots appear on the onions.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A5930BFDAF13E42BAA48C75B773A443', X'A192B3D881A7CC4B8973EE37CAE2BBF9', X'A192B3D881A7CC4B8973EE37CAE2BBF9', 3, NULL, NULL, 2, NULL, 0, 'Add the ginger and saute another minute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97AACD7CDEEF1949B106AB5AE2F77B1B', X'A192B3D881A7CC4B8973EE37CAE2BBF9', X'A192B3D881A7CC4B8973EE37CAE2BBF9', 4, NULL, NULL, 2, NULL, 0, 'Add the tomato puree, turmeric, cayenne and coriander powder. Mix well and cook, stirring frequently, until the tomato puree darkens a couple of shades and bubbles at the corners.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8B611E5D301464297A4DE0B362657E6', X'A192B3D881A7CC4B8973EE37CAE2BBF9', X'A192B3D881A7CC4B8973EE37CAE2BBF9', 5, NULL, NULL, 2, NULL, 0, 'Add the vegetables. If you''re using frozen peas, don''t add them now.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DAAEDBB75942447937ED0E6C96E00E1', X'A192B3D881A7CC4B8973EE37CAE2BBF9', X'A192B3D881A7CC4B8973EE37CAE2BBF9', 6, NULL, NULL, 2, NULL, 0, 'Add the rice and lentils.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F32B0E5EDA94D748B5AD398BAE6504B8', X'A192B3D881A7CC4B8973EE37CAE2BBF9', X'A192B3D881A7CC4B8973EE37CAE2BBF9', 7, NULL, NULL, 2, NULL, 0, 'Mix everything well together, then add six cups of water or vegetable stock.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6255B6862D17844381DF6A0E06CD562E', X'A192B3D881A7CC4B8973EE37CAE2BBF9', X'A192B3D881A7CC4B8973EE37CAE2BBF9', 8, NULL, NULL, 2, NULL, 0, 'If you''re cooking in a pressure cooker, cover now and let the khichdi cook for 20 minutes after the cooker comes to pressure.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67D4E33DAD8F0B46BBE371B9068C33E0', X'A192B3D881A7CC4B8973EE37CAE2BBF9', X'A192B3D881A7CC4B8973EE37CAE2BBF9', 9, NULL, NULL, 2, NULL, 0, 'On the stovetop, if not using a pressure cooker, bring the water to a boil, cover the pot with a tight-fitting lid and cook 45 minutes or until the rice and lentils are really soft. Add more water if needed at any time. You want the khichdi to have a slightly runny consistency, like a risotto. It shouldn''t be too dry.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5255463BAE70734AAF7BF52C59845661', X'A192B3D881A7CC4B8973EE37CAE2BBF9', X'A192B3D881A7CC4B8973EE37CAE2BBF9', 10, NULL, NULL, 2, NULL, 0, 'Open the pressure cooker or pot. If you''re using frozen peas, add them at this point and stir the khichdi well. Add the garam masala and salt and return the khichdi to heat to let it warm through.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C89B44D9A6E4647A2E741FD385C99D8', X'A192B3D881A7CC4B8973EE37CAE2BBF9', X'A192B3D881A7CC4B8973EE37CAE2BBF9', 11, NULL, NULL, 2, NULL, 0, 'Garnish with the coriander or mint leaves.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37421EDAF04B744F9B617CCCF9FF6256', X'A192B3D881A7CC4B8973EE37CAE2BBF9', X'A192B3D881A7CC4B8973EE37CAE2BBF9', 12, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFF59249F1EBCD4DA44AFF9326D58350', X'E52E7DCFC696E44393A79D75C55E64A4', X'E52E7DCFC696E44393A79D75C55E64A4', 0, NULL, NULL, 2, NULL, 0, 'Crush the peppercorns and cumin seeds coarsely with a mortar and pestle. It''s important that you don''t grind them too fine -- you want big pieces.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11F670A639951D459C137CB2C91EA852', X'E52E7DCFC696E44393A79D75C55E64A4', X'E52E7DCFC696E44393A79D75C55E64A4', 1, NULL, NULL, 2, NULL, 0, 'Place the rice and beans with six cups of water in a pressure cooker or Instant Pot. If using a pressure cooker, turn off the heat after six whistles. In an Instant Pot, let the rice and dal cook for 10 minutes on high pressure, then either wait for it to release, or force-release after 10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A68CEB898D467749ACE049B556127FC2', X'E52E7DCFC696E44393A79D75C55E64A4', X'E52E7DCFC696E44393A79D75C55E64A4', 2, NULL, NULL, 2, NULL, 0, 'Once the rice and lentils are cooked, they should have a slightly runny, creamy consistency. Stir with a ladle, mashing the rice and lentils a bit, and add a little more water if needed to get to this consistency. If you add more water, do it over medium heat so the rice warms through.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D360F63FD7BA842836BA74D81B839C0', X'E52E7DCFC696E44393A79D75C55E64A4', X'E52E7DCFC696E44393A79D75C55E64A4', 3, NULL, NULL, 2, NULL, 0, 'Make the tempering by heating the oil in a skillet. Add the cumin seeds, pepper and ginger and stir for a minute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8BBFFDFEBF0344D82F6065B7A84C3D7', X'E52E7DCFC696E44393A79D75C55E64A4', X'E52E7DCFC696E44393A79D75C55E64A4', 4, NULL, NULL, 2, NULL, 0, 'Add the nuts and stir until lightly golden.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34627C6FE8B2054883E35F5FF078C83E', X'E52E7DCFC696E44393A79D75C55E64A4', X'E52E7DCFC696E44393A79D75C55E64A4', 5, NULL, NULL, 2, NULL, 0, 'Add the tempering to the cooked rice and lentils along with the coconut milk and salt to taste. Mix well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A36EAF7630099947AF25D4310257A060', X'E52E7DCFC696E44393A79D75C55E64A4', X'E52E7DCFC696E44393A79D75C55E64A4', 6, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D8DD189FDF29D4CBA0B8A61BB6A6B36', X'6B380B26DFC35F4D847CBC20476D4A92', X'6B380B26DFC35F4D847CBC20476D4A92', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large saucepan. Add the bay leaves cardamom and when they start to brown lightly, in about a minute, add the onion. Saute for a minute or two until the onion starts to turn translucent.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'638FED9CB67DEB48878CCEE6485D0587', X'6B380B26DFC35F4D847CBC20476D4A92', X'6B380B26DFC35F4D847CBC20476D4A92', 1, NULL, NULL, 2, NULL, 0, 'Add the dill leaves, pistachios, and the turmeric. Stir to mix. Add the rice and stir-fry for a minute or until the rice begins to turn slightly opaque.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD7B3B23A043A74A985A3D360A80BE18', X'6B380B26DFC35F4D847CBC20476D4A92', X'6B380B26DFC35F4D847CBC20476D4A92', 2, NULL, NULL, 2, NULL, 0, 'Add the vegetable stock and salt and ground black pepper to taste. Stir to mix and let the water come to a boil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'199E978C95293C4698F6D30C1AF0623C', X'6B380B26DFC35F4D847CBC20476D4A92', X'6B380B26DFC35F4D847CBC20476D4A92', 3, NULL, NULL, 2, NULL, 0, 'Cover the saucepan with a tight-fitting lid, reduce heat to medium-low, and cook for 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E216227FF262A94BB67247483F06BBA7', X'6B380B26DFC35F4D847CBC20476D4A92', X'6B380B26DFC35F4D847CBC20476D4A92', 4, NULL, NULL, 2, NULL, 0, 'Turn off the heat and let the rice stand, undisturbed, for another 10 minutes. Uncover, fluff the rice with a fork, garnish with more pistachios if desired, and serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0508B491E396D43BE968AFE7298A84C', X'4BF24FF8218AFA4BADB812814A0FBE23', X'4BF24FF8218AFA4BADB812814A0FBE23', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large saucepan with a tight-fitting lid. Add the cumin seeds, bay leaves, cloves, cinnamon and cardamom and saute a few seconds until the bay leaves start to turn color. Add the onions, a pinch of salt, and stir-fry until the onions are golden-brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1465C0A45091F4586D453251D04D4CE', X'4BF24FF8218AFA4BADB812814A0FBE23', X'4BF24FF8218AFA4BADB812814A0FBE23', 1, NULL, NULL, 2, NULL, 0, 'Add the green chili pepper, garlic and ginger and continue to saute another minute. Add the tomatoes along with the turmeric, cayenne and coriander powder. Mix well. Let the tomatoes cook, stirring frequently, until they are mostly broken down and pulpy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD71A8EF6E6D2E45A4AB23233684C4F8', X'4BF24FF8218AFA4BADB812814A0FBE23', X'4BF24FF8218AFA4BADB812814A0FBE23', 2, NULL, NULL, 2, NULL, 0, 'Add the garam masala and the Maggi masala magic if using. Add the soy chunks to the pan, mix well, cover and cook another seven or eight minutes until the soy chunks have absorbed some of these yummy flavors.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D361C2BFD0E9774E90255A25A190FCF2', X'4BF24FF8218AFA4BADB812814A0FBE23', X'4BF24FF8218AFA4BADB812814A0FBE23', 3, NULL, NULL, 2, NULL, 0, 'Add the drained rice to the pan, mix well, then add four cups of water. Add enough salt that the water tastes a little saltier than you''d like your rice to be. Bring to a boil, cover with a lid, and continue cooking on medium heat for five minutes. Turn down the heat to the lowest point and continue cooking another 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38F5D696D567A647AAE3D3722232ED67', X'4BF24FF8218AFA4BADB812814A0FBE23', X'4BF24FF8218AFA4BADB812814A0FBE23', 4, NULL, NULL, 2, NULL, 0, 'Turn off the heat and let the rice stand, undisturbed, for another 10 minutes. Serve hot with a raita.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DEF3BDE870C9C40B8D31BF2280C4E3D', X'61FB4B3F880E4548932657321F326186', X'61FB4B3F880E4548932657321F326186', 0, NULL, NULL, 2, NULL, 0, 'Spray a skillet with cooking spray. Place the slices of tofu on the skillet in a single layer and cook both sides until lightly golden. When cool, cut into a ½-inch dice. Set aside');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B4E4760ADEC8B4EA45DDAF9182FCD3B', X'61FB4B3F880E4548932657321F326186', X'61FB4B3F880E4548932657321F326186', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil. Add the cloves, cardamom and cinnamon and, when they start to color, add the cumin seeds. As they start to darken, add the onions and the garlic. Saute for a few minutes until the onions become golden-brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2C75322342D5A4582DA13AA2A51476B', X'61FB4B3F880E4548932657321F326186', X'61FB4B3F880E4548932657321F326186', 2, NULL, NULL, 2, NULL, 0, 'Add the ginger-garlic paste, saute for a couple of minutes, then add the tomatoes. Add the turmeric, cayenne, cumin and coriander powders and mix well. Cover and let the tomatoes cook until they are very soft and pulpy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAE915E6C4772F4786011AFFCD78BC37', X'61FB4B3F880E4548932657321F326186', X'61FB4B3F880E4548932657321F326186', 3, NULL, NULL, 2, NULL, 0, 'Add the kasoori methi, if using, and mix well. Add the spinach puree, mix, and let the sauce come to a boil. Lower the heat and continue cooking the spinach for 10 minutes. Add some vegetable stock or water if it looks too thick, but don''t add too much liquid at this point-- you want the spinach to cook thoroughly and lose any raw flavor.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5C0DADDFE6E1F479FF7FD9CE8C18AD7', X'61FB4B3F880E4548932657321F326186', X'61FB4B3F880E4548932657321F326186', 4, NULL, NULL, 2, NULL, 0, 'Add the garam masala, cubes of tofu and salt. Mix well and let it all simmer another two minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD4EF8F179B5AF4C951295CB81564653', X'61FB4B3F880E4548932657321F326186', X'61FB4B3F880E4548932657321F326186', 5, NULL, NULL, 2, NULL, 0, 'Turn off the heat and add the cashew paste, if using. The cashew paste adds a rich, luxurious finish, but if you want a nut-free recipe, it''s completely optional.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9B445513CD5E749AA2E4E31F6D6266E', X'61FB4B3F880E4548932657321F326186', X'61FB4B3F880E4548932657321F326186', 6, NULL, NULL, 2, NULL, 0, 'Serve hot or warm with naan or roti.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13FFEA12DF7F004DABFAF73AF6282735', X'BF3A957CFF690E42ABA1C45C6C3ED7B4', X'BF3A957CFF690E42ABA1C45C6C3ED7B4', 0, NULL, NULL, 2, NULL, 0, 'In a large saucepan, place the spinach and mustard greens with ½ cup of water. Cover and let the greens cook five minutes or until they are completely wilted and tender. Remove with the liquid to a food processor and blend into a smooth puree. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9363921556949468CEF8FDFE062D27E', X'BF3A957CFF690E42ABA1C45C6C3ED7B4', X'BF3A957CFF690E42ABA1C45C6C3ED7B4', 1, NULL, NULL, 2, NULL, 0, 'Make sure the saucepan is dry and heat the oil. Add the cornmeal and cook it, stirring to make sure it doesn''t stick, for a minute or so.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'736B5A6BD3170646AC4404ED5CF01B2C', X'BF3A957CFF690E42ABA1C45C6C3ED7B4', X'BF3A957CFF690E42ABA1C45C6C3ED7B4', 2, NULL, NULL, 2, NULL, 0, 'Add the tomato onion sauce and mix well. Add the pureed greens and bring everything to a boil. Lower the heat so the sauce continues to simmer with gentle bubbling in the pot. If your curry looks too dry, add a little vegetable stock or water. Let the curry cook for 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8464D6C2B3FE824BB82FB15152D06958', X'BF3A957CFF690E42ABA1C45C6C3ED7B4', X'BF3A957CFF690E42ABA1C45C6C3ED7B4', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle on the garam masala and add salt to taste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C838AC7B88F7448B2514DE08D8D1CFC', X'BF3A957CFF690E42ABA1C45C6C3ED7B4', X'BF3A957CFF690E42ABA1C45C6C3ED7B4', 4, NULL, NULL, 2, NULL, 0, 'Serve hot, preferably with makki ki roti or any Indian flatbread like naan or roti. Because this is a rustic dish, it tastes great served with an Indian pickle, slices of raw onion and wedges of lime or lemon.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00918A6BC71BA04BBDA9DC10355DC94A', X'6D9E5C0215ADBA458CF8A50735E266CC', X'6D9E5C0215ADBA458CF8A50735E266CC', 0, NULL, NULL, 2, NULL, 0, 'Heat a saucepan and add the onion and garlic to it. Add 2 tbsp water and saute the onion and garlic over medium heat until the onions soften and start to change color, about five minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F1D8511CC402D4F96BF3085F76B3ECA', X'6D9E5C0215ADBA458CF8A50735E266CC', X'6D9E5C0215ADBA458CF8A50735E266CC', 1, NULL, NULL, 2, NULL, 0, 'Add the tomatoes along with the coriander powder, cumin powder, cayenne, turmeric and kasoori methi. Stir well, cover, and cook for 5 minutes or until the tomatoes are very soft and mushy. Stir in the cashew nuts and turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37B629EC96C9954BAE40F1229F208450', X'6D9E5C0215ADBA458CF8A50735E266CC', X'6D9E5C0215ADBA458CF8A50735E266CC', 2, NULL, NULL, 2, NULL, 0, 'Let the onions and tomatoes cool slightly, then add a cup of cold water and use an immersion blender to blend to a smooth consistency or transfer to a blender and blend into a very smooth paste. Use caution. When blending hot liquids, I fill the blender only halfway, remove the blender''s center insert and hold a kitchen towel over the top, and then blend. This keeps steam from building up inside the blender which could blow the lid off.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F92280863AD28B48BF18A221ABCE8482', X'6D9E5C0215ADBA458CF8A50735E266CC', X'6D9E5C0215ADBA458CF8A50735E266CC', 3, NULL, NULL, 2, NULL, 0, 'Rinse the saucepan and place over the stove over medium heat. Add the mushrooms to the pan along with a little salt and pepper. Stir-fry the mushrooms in the dry pan until they begin to caramelize, about three to four minutes. Add the peas and cover until peas are cooked, which should take no more than two to three minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B083257B85C4B41B3A4D04F48A59C00', X'6D9E5C0215ADBA458CF8A50735E266CC', X'6D9E5C0215ADBA458CF8A50735E266CC', 4, NULL, NULL, 2, NULL, 0, 'Add the blended sauce to the pan and give it a good stir. Add the garam masala and check if more salt is needed. Add water if the sauce looks too dry. Stir in the sugar.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F53B2C4C3378E742978D4CC37BF5FD4A', X'6D9E5C0215ADBA458CF8A50735E266CC', X'6D9E5C0215ADBA458CF8A50735E266CC', 5, NULL, NULL, 2, NULL, 0, 'Heat through and once the mixture comes to a boil, turn it off. Garnish with coriander, if you wish, or serve as is.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF855CC15570E7488927DDF6F4C648F3', X'1E0C63759BB74C479182A4EEE49591C9', X'1E0C63759BB74C479182A4EEE49591C9', 0, NULL, NULL, 2, NULL, 0, 'Place the cauliflower, carrots, green beans and potatoes in a large saucepan. Add half a cup of water, bring to a boil, cover and cook for five minutes. After five minutes, add the green beans and bell peppers and continue cooking another five minutes or until potatoes are tender. Drain and remove to a bowl. You can reserve the cooking water and use it as a stock instead of water later in the recipe.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57B0F85FA8AF5E45B02A9C862E032210', X'1E0C63759BB74C479182A4EEE49591C9', X'1E0C63759BB74C479182A4EEE49591C9', 1, NULL, NULL, 2, NULL, 0, 'Place the onion in a saucepan, cover with water, bring to a boil, cover and cook for 10 minutes. Drain the onion, put it in a blender, and make a smooth paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'131D785CB41F704BB168B0E7E313DCBB', X'1E0C63759BB74C479182A4EEE49591C9', X'1E0C63759BB74C479182A4EEE49591C9', 2, NULL, NULL, 2, NULL, 0, 'Crush the garlic, ginger and green chili peppers, preferably with a mortar and pestle (or food processor is fine). Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'304E452A4024644682CA9E28FD425CDF', X'1E0C63759BB74C479182A4EEE49591C9', X'1E0C63759BB74C479182A4EEE49591C9', 3, NULL, NULL, 2, NULL, 0, 'Blend half the cashews (¼ cup) into a very smooth paste in the blender with ½ cup water)');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4941A4E8052074D806963D4D118F4DE', X'1E0C63759BB74C479182A4EEE49591C9', X'1E0C63759BB74C479182A4EEE49591C9', 4, NULL, NULL, 2, NULL, 0, 'In a large saucepan (you can use the one you cooked the veggies in), add 1 tbsp of oil and add the cinnamon stick, cloves, cardamom and peppercorns. Saute for a minute until they start to change color and become fragrant.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC5F6630A62D3046A8998005E345A36A', X'1E0C63759BB74C479182A4EEE49591C9', X'1E0C63759BB74C479182A4EEE49591C9', 5, NULL, NULL, 2, NULL, 0, 'Lower the heat and add the onion paste. Continue cooking for another 7-8 minutes, stirring frequently to keep the paste from sticking to the bottom of the pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BCD5609B5131C4EB84547596B884324', X'1E0C63759BB74C479182A4EEE49591C9', X'1E0C63759BB74C479182A4EEE49591C9', 6, NULL, NULL, 2, NULL, 0, 'Add the crushed ginger-garlic-green-chili-pepper paste and saute another minute. Now add the cashew paste and the yogurt, mix, and continue cooking for another five minutes. Stir frequently to avoid sticking.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39DB8E67D1277C40A7891885B5F71309', X'1E0C63759BB74C479182A4EEE49591C9', X'1E0C63759BB74C479182A4EEE49591C9', 7, NULL, NULL, 2, NULL, 0, 'Add all of the cooked vegetables and tofu. Mix well and add a little more water if needed (you can use the reserved water from cooking the vegetables as needed). You want the gravy to be thick enough to coat the veggies, and not too runny. Add salt to taste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30BFCBDC9C77224389BE3145905A85AF', X'1E0C63759BB74C479182A4EEE49591C9', X'1E0C63759BB74C479182A4EEE49591C9', 8, NULL, NULL, 2, NULL, 0, 'Let the korma come to a boil and cook another five minutes for all of the flavors to blend. Turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F52F534824282645BD74666AE7413C0C', X'1E0C63759BB74C479182A4EEE49591C9', X'1E0C63759BB74C479182A4EEE49591C9', 9, NULL, NULL, 2, NULL, 0, 'In a small saucepan, heat the remaining ½ tbsp oil and add the remaining ¼ cup of cashews and raisins. Stir-fry until the raisins become puffy and the cashews start to become golden.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12978FC23B323B45A5AD68950BB07009', X'1E0C63759BB74C479182A4EEE49591C9', X'1E0C63759BB74C479182A4EEE49591C9', 10, NULL, NULL, 2, NULL, 0, 'Pour the cashews and raisins into the korma, sprinkle on the garam masala powder, stir once to mix, and serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3698AD2343B6064F83587FCDFF88C03A', X'D0FFAD16FA4DD04DAC74AD250F4D6500', X'D0FFAD16FA4DD04DAC74AD250F4D6500', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a skillet or wok.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6260003C220E904699107F3545DF1D48', X'D0FFAD16FA4DD04DAC74AD250F4D6500', X'D0FFAD16FA4DD04DAC74AD250F4D6500', 1, NULL, NULL, 2, NULL, 0, 'Add the cumin seeds. As they begin to turn a little darker and fragrant, add the onions with the green chili pepper and half the cilantro. Add some salt. Stir-fry over medium heat until the onions start to caramelize, about five minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89B7E13AF7FB5B408E924FF65ED17BC5', X'D0FFAD16FA4DD04DAC74AD250F4D6500', X'D0FFAD16FA4DD04DAC74AD250F4D6500', 2, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, coriander powder and the ginger garlic paste. Continue to stir-fry until the tomatoes have broken down completely in a pulp.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'527F067B60C0064EAA7727088B5E79B7', X'D0FFAD16FA4DD04DAC74AD250F4D6500', X'D0FFAD16FA4DD04DAC74AD250F4D6500', 3, NULL, NULL, 2, NULL, 0, 'Add the turmeric, cayenne or paprika, if using, mango powder or lemon juice, and stir to mix. Add the potatoes and mix well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59948B65F90E9B4EA6C0BFC234556AE1', X'D0FFAD16FA4DD04DAC74AD250F4D6500', X'D0FFAD16FA4DD04DAC74AD250F4D6500', 4, NULL, NULL, 2, NULL, 0, 'Add the cauliflower, mix, and and sprinkle on a couple of tablespoons of water. Turn the heat to medium-low. Cover the wok or skillet with a tight lid, preferably one that can hold a couple of tablespoons of water poured over the lid. This helps the veggies to steam-cook without burning them. If you don''t have a lid that can hold water, just monitor your wok more closely and sprinkle on more water if the veggies start to stick to the bottom before they are cooked.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59F36AC0EA17C94A82F6D645D62B4AA8', X'D0FFAD16FA4DD04DAC74AD250F4D6500', X'D0FFAD16FA4DD04DAC74AD250F4D6500', 5, NULL, NULL, 2, NULL, 0, 'Once the veggies are done, sprinkle on the garam masala and mix. You might want to add a little at a time to suit your taste. Check for salt and add more if needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC0C3DF7CEE10742B348A87D7D2E53C0', X'D0FFAD16FA4DD04DAC74AD250F4D6500', X'D0FFAD16FA4DD04DAC74AD250F4D6500', 6, NULL, NULL, 2, NULL, 0, 'Garnish with the remaining cilantro and serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C5FB0AEF21C9947B774A0D41E534D4F', X'568F538F9D49494991707BF8B9AE74DB', X'568F538F9D49494991707BF8B9AE74DB', 0, NULL, NULL, 2, NULL, 0, 'Place all the cut veggies into a large pot with 1 cup of water and turmeric.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFC84BE4AA0A4745973FA03116F20521', X'568F538F9D49494991707BF8B9AE74DB', X'568F538F9D49494991707BF8B9AE74DB', 1, NULL, NULL, 2, NULL, 0, 'Place the coconut milk in the blender with the cumin, green chili peppers, and bengal gram dal or rice. Blend into a paste. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34CC2FAA9ED9394493852E8A6517DABF', X'568F538F9D49494991707BF8B9AE74DB', X'568F538F9D49494991707BF8B9AE74DB', 2, NULL, NULL, 2, NULL, 0, 'When the veggies are cooked -- it should take no more than 15-20 minutes -- add the coconut paste to the veggies and stir well to mix. Let the curry come to a gentle boil over medium-low heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'529D373C276E954B9F7138FED526C23E', X'568F538F9D49494991707BF8B9AE74DB', X'568F538F9D49494991707BF8B9AE74DB', 3, NULL, NULL, 2, NULL, 0, 'Turn off the heat and add the yogurt or lime juice. Stir to mix but be careful not to mash up the veggies because they''ll be very tender at this stage.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D78963459221A41BCF9B4AEB1FDCF19', X'568F538F9D49494991707BF8B9AE74DB', X'568F538F9D49494991707BF8B9AE74DB', 4, NULL, NULL, 2, NULL, 0, 'Heat the coconut oil. Add mustard seeds and when they sputter, add the curry leaves and the ginger. Saute for a minute or two until the fragrance permeates the house.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'431E62469CC46A4CB2842734E6A086FC', X'568F538F9D49494991707BF8B9AE74DB', X'568F538F9D49494991707BF8B9AE74DB', 5, NULL, NULL, 2, NULL, 0, 'Pour the tempering over the avial and stir to mix. Garnish, if you wish, with coriander and some fresh coconut.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CDA2591A2A37A4DBC7A491FD8F554E9', X'568F538F9D49494991707BF8B9AE74DB', X'568F538F9D49494991707BF8B9AE74DB', 6, NULL, NULL, 2, NULL, 0, 'Serve hot with rice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30591198B3B57D49B750E09EBB99D0B3', X'2B8C45F134E49C48A68DF53A3F408417', X'2B8C45F134E49C48A68DF53A3F408417', 0, NULL, NULL, 2, NULL, 0, 'Toss the okra with ½ tsp of oil and place in an air fryer basket. Air fry at 375 degrees for 8 minutes, tossing halfway through, or until the okra is almost tender. The okra should still look green, and should have a slight bite when your teeth dig into it. Set aside. (If you don''t have an air-fryer, spread the okra on a baking sheet in a single layer and bake in a preheated 375 degree oven for 20-30 minutes or until almost cooked. A third option, the more traditional one, is to stir-fry the okra in 2 tbsp of oil until almost done.)');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E3957F94BFFBC40A17F42CCA7D1B4DF', X'2B8C45F134E49C48A68DF53A3F408417', X'2B8C45F134E49C48A68DF53A3F408417', 1, NULL, NULL, 2, NULL, 0, 'Heat the remaining oil in a kadhai or wok or skillet. Add the cumin seeds and when they darken slightly, add the onions with a pinch of salt. Saute until the onions are translucent and begin to brown. Add the ginger and garlic and green chili peppers if using and continue to saute for a couple more minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D0FE7009CF03F4896583A4980D1D589', X'2B8C45F134E49C48A68DF53A3F408417', X'2B8C45F134E49C48A68DF53A3F408417', 2, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, mix, then add the powdered spices -- coriander powder, turmeric, paprika or cayenne, amchoor and garam masala. You want to saute the tomatoes only until they are very mushy and have let go of all their liquid, making a nice sauce. If you overcook your tomatoes to the point where they have lost most of their moisture, they will also lose their sweetness, so don''t overdo it. You want a hint of sweetness in this recipe to balance out the spicy, salty and tangy flavors. If your tomatoes aren''t too sweet, add a teaspoon of sugar -- it''ll do the trick.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DE18CDEB637D24E928C1917BBAA8032', X'2B8C45F134E49C48A68DF53A3F408417', X'2B8C45F134E49C48A68DF53A3F408417', 3, NULL, NULL, 2, NULL, 0, 'Add a cup of water to the tomatoes and onions and bring to a boil. Season with salt and add the prepped okra to the sauce. Stir to mix and continue cooking 3-4 minutes until the flavors meld together and the okra is cooked to your liking. I like my okra with just a little bite to it. Keep in mind that the longer you cook the okra, the more discolored it will get, losing that bright green color. It will still taste awesome, though.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6216DD042AB0F04CB6A0CD2D599F5766', X'2B8C45F134E49C48A68DF53A3F408417', X'2B8C45F134E49C48A68DF53A3F408417', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle on the cilantro and serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F095F35598BDC3449A8D6629DF1C6B43', X'9800D3D228BF9543A5E897E31682F7F9', X'9800D3D228BF9543A5E897E31682F7F9', 0, NULL, NULL, 2, NULL, 0, 'Prep the okra in the air fryer or oven or stove top. Heat two cups of the tomato onion sauce in the wok or skillet, then add the coriander powder, amchur and garam masala. Add a teaspoon of sugar. Add a cup of water, and when everything comes to a boil, add the okra and cook for 3-4 minutes until the flavors meld. Garnish with coriander and serve.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F0453C6AF4F824BAA23B373F4870324', X'C45E150EB9FD6A469F5F247B9FCB7400', X'C45E150EB9FD6A469F5F247B9FCB7400', 0, NULL, NULL, 2, NULL, 0, 'Blend the cashews and coconut milk into a very smooth paste. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA59B34773A2024DA1DC7048CB875897', X'C45E150EB9FD6A469F5F247B9FCB7400', X'C45E150EB9FD6A469F5F247B9FCB7400', 1, NULL, NULL, 2, NULL, 0, 'Heat 1 tsp of coconut oil in a saucepan. Add the onions and ginger-garlic paste and saute until the onions turn transparent but not brown. Add a couple of tablespoons of water if the ginger-garlic paste starts to stick.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4F6A28F8F43DE4381F14343A590DFED', X'C45E150EB9FD6A469F5F247B9FCB7400', X'C45E150EB9FD6A469F5F247B9FCB7400', 2, NULL, NULL, 2, NULL, 0, 'Transfer the onions to a blender, add ¼ cup of water and the chili peppers and blend into a smooth paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02F520B1697D8547AB1BCF9A2B5222CA', X'C45E150EB9FD6A469F5F247B9FCB7400', X'C45E150EB9FD6A469F5F247B9FCB7400', 3, NULL, NULL, 2, NULL, 0, 'In the same saucepan, heat the remaining teaspoon of coconut oil. Add the onion-ginger-garlic paste and bring it to a simmer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7099347EC8BFBE448BC5832960E78133', X'C45E150EB9FD6A469F5F247B9FCB7400', X'C45E150EB9FD6A469F5F247B9FCB7400', 4, NULL, NULL, 2, NULL, 0, 'Add the cauliflower florets and ½ cup of water along with the cardamom powder, pepper, chat masala, and kasoori methi.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8A5BE57B3E97F4CA3225F6F75749461', X'C45E150EB9FD6A469F5F247B9FCB7400', X'C45E150EB9FD6A469F5F247B9FCB7400', 5, NULL, NULL, 2, NULL, 0, 'Stir well to mix, bring to a boil, and cover with a lid. Cook on a medium-low flame, stirring occasionally, for 15 minutes or until the cauliflower is tender but still has a bite. If you like your cauliflower softer, continue to cook a little longer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'288FD29C578D8A44AAB8684ED15901D6', X'C45E150EB9FD6A469F5F247B9FCB7400', X'C45E150EB9FD6A469F5F247B9FCB7400', 6, NULL, NULL, 2, NULL, 0, 'Add the cashew-coconut cream and stir well to mix. Add water if the mixture is too thick, and thin it out to your liking. I like this curry rather thick and creamy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1A096980018B54F9FC502C8D11C6D10', X'C45E150EB9FD6A469F5F247B9FCB7400', X'C45E150EB9FD6A469F5F247B9FCB7400', 7, NULL, NULL, 2, NULL, 0, 'Once the curry comes to a boil, turn off the heat and add the lemon juice. Add salt to taste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9819C82B9407B84B9F92734D0B0221D6', X'C45E150EB9FD6A469F5F247B9FCB7400', X'C45E150EB9FD6A469F5F247B9FCB7400', 8, NULL, NULL, 2, NULL, 0, 'Serve hot with rotis or naan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BD6556BB279BD48B8BC9D0E0A93A7A1', X'CF25E0F213B8E34B86CE8CE2C6D9A18A', X'CF25E0F213B8E34B86CE8CE2C6D9A18A', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a wok or large fry pan. Add the cumin seeds and as they start to darken slightly, add the curry leaves, green chili peppers, and turmeric.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93399062C62E2B42928D38C206B3851B', X'CF25E0F213B8E34B86CE8CE2C6D9A18A', X'CF25E0F213B8E34B86CE8CE2C6D9A18A', 1, NULL, NULL, 2, NULL, 0, 'Stir quickly to mix and add the boiled potatoes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA6587EC45E4874680A447B349FA6B5A', X'CF25E0F213B8E34B86CE8CE2C6D9A18A', X'CF25E0F213B8E34B86CE8CE2C6D9A18A', 2, NULL, NULL, 2, NULL, 0, 'Stir-fry the potatoes for a couple of minutes to warm them through. Add salt and turn off heat. You can squeeze some lemon juice into this sabzi, but I prefer it without any.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'850E2ECFDFFB6C41B97AF125342093C1', X'0E005A245F5330458032FDC331632A65', X'0E005A245F5330458032FDC331632A65', 0, NULL, NULL, 2, NULL, 0, 'Place potatoes in a large pot with enough water to cover by an inch. Bring the water to a boil, cover with the lid, lower the heat, and cook another 10 minutes or until a knife pierced in the center of the potato goes in cleanly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB14F76E5AAD444CB93A07A99D13FBFF', X'0E005A245F5330458032FDC331632A65', X'0E005A245F5330458032FDC331632A65', 1, NULL, NULL, 2, NULL, 0, 'Drain the potatoes immediately and set aside. Place the frozen peas in the warm pot you cooked the potatoes in and cover. This should be enough to warm them without overcooking them.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACD656EF0FB2894CAC6CEC8F58BA5B61', X'0E005A245F5330458032FDC331632A65', X'0E005A245F5330458032FDC331632A65', 2, NULL, NULL, 2, NULL, 0, 'To make the dressing, heat the oil in a small saucepan. Add the mustard seeds. When they crackle, add the turmeric, red pepper flakes, and ginger. Stir-fry for 30 seconds, then turn off heat. Let it cool for a few minutes, then stir in the coconut milk.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77E30E8E1434D94FAC8755DCE3E357B0', X'0E005A245F5330458032FDC331632A65', X'0E005A245F5330458032FDC331632A65', 3, NULL, NULL, 2, NULL, 0, 'Halve the potatoes and place in a large bowl. Add in the green peas, the turmeric-coconut dressing, and the spring onions. Squeeze in the lemon juice. Add salt to taste and toss everything together.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB0F3D1D0DF7EC47B77DF80F7F341928', X'0E005A245F5330458032FDC331632A65', X'0E005A245F5330458032FDC331632A65', 4, NULL, NULL, 2, NULL, 0, 'Serve warm or cold.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA568EB001C0764BA5F6BF6881B6A9E1', X'06A57299A76A3A4990262BD12570776D', X'06A57299A76A3A4990262BD12570776D', 0, NULL, NULL, 2, NULL, 0, 'Grind together the masala ingredients with enough water to make a thick paste. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E1667AECA70374D9A00D20949CC59BE', X'06A57299A76A3A4990262BD12570776D', X'06A57299A76A3A4990262BD12570776D', 1, NULL, NULL, 2, NULL, 0, 'Mix the oil, chili powder, turmeric, and salt to taste and toss the potatoes in the mixture. Place in a 400-degree oven and roast 30 minutes or until the potatoes are cooked through. (Pierce with a fork to test).');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18A6E3D0BC58124DB937450F57DC1F97', X'06A57299A76A3A4990262BD12570776D', X'06A57299A76A3A4990262BD12570776D', 2, NULL, NULL, 2, NULL, 0, 'In a saucepan, bring the ground paste to a boil, adding more water or vegetable stock if it''s too thick. Then add half the coconut milk and the baked potatoes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03440FEED3D6EC4F92588490A53246DA', X'06A57299A76A3A4990262BD12570776D', X'06A57299A76A3A4990262BD12570776D', 3, NULL, NULL, 2, NULL, 0, 'Once the sauce starts to bubble, turn the heat to the lowest setting and place a tight-fitting lid over the saucepan. Let cook 20 minutes. Add some water or vegetable stock if the sauce dries up.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B4AB07DDBE9134DA88388432E7B9221', X'06A57299A76A3A4990262BD12570776D', X'06A57299A76A3A4990262BD12570776D', 4, NULL, NULL, 2, NULL, 0, 'Add the remaining coconut milk, more salt if needed, and the mint/coriander leaves and lemon juice. Stir well and turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E53118E0D8C5D44B4262FD6803EF998', X'06A57299A76A3A4990262BD12570776D', X'06A57299A76A3A4990262BD12570776D', 5, NULL, NULL, 2, NULL, 0, 'Serve hot with pooris or rotis or rice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48255541B93B434194D3134107218FC6', X'62928B5215ACFE49B610C6050F38F1E6', X'62928B5215ACFE49B610C6050F38F1E6', 0, NULL, NULL, 2, NULL, 0, 'Blend the cashews with ¼ cup water or vegetable stock into a smooth paste and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDC9EF975332E148A6E061D93D4EFDE2', X'62928B5215ACFE49B610C6050F38F1E6', X'62928B5215ACFE49B610C6050F38F1E6', 1, NULL, NULL, 2, NULL, 0, 'Blend the onions, garlic, ginger and the paprika with ¾ cup water or vegetable stock. Place the tofu cubes in a bowl and add half a cup of the onion-ginger-garlic-paprika puree to the bowl. Toss the tofu cubes in the puree.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3695F36CA23DE544BA07B816582CFD07', X'62928B5215ACFE49B610C6050F38F1E6', X'62928B5215ACFE49B610C6050F38F1E6', 2, NULL, NULL, 2, NULL, 0, 'Set the Instant Pot to the saute function. Add the oil and when it''s hot, add the tofu cubes. Saute, stirring frequently, about 2-3 minutes. Then add the remaining onion-ginger-garlic-paprika puree, the tomato puree, coriander powder, cayenne and kasoori methi. Add salt to taste, give it a stir, and put the lid on. Set the Instant Pot to cook at high pressure for 10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35C3A61324AE114C86C51A6925271204', X'62928B5215ACFE49B610C6050F38F1E6', X'62928B5215ACFE49B610C6050F38F1E6', 3, NULL, NULL, 2, NULL, 0, 'After cooking is complete, release pressure manually after 10 minutes. Open the lid.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDA41BB6A1114A49A802957C50E4582F', X'62928B5215ACFE49B610C6050F38F1E6', X'62928B5215ACFE49B610C6050F38F1E6', 4, NULL, NULL, 2, NULL, 0, 'If the butter tofu is too thick, you can thin it out with more vegetable stock or water. Stir in the garam masala, cashew cream or vegan butter, maple syrup or sugar and lemon or lime juice. Add more salt if needed. Heat the butter chicken tofu, especially if you added more stock or water, by turning the saute function back on. Once it''s warmed through, remove to a bowl, garnish with coriander leaves, and serve hot with naan or roti or rice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17E7B8C5CD6F164ABE0C03DA63785B66', X'C8204EBE694AAA4AA83202BD06E0A803', X'C8204EBE694AAA4AA83202BD06E0A803', 0, NULL, NULL, 2, NULL, 0, 'Place the tofu, cashews, potatoes, cornstarch, minced chili peppers, ginger and salt in a bowl and knead until the dough holds together. The dough will be soft but it will hold together. Form 16 smooth balls with the dough. If you''re stuffing raisins into the kofta dumplings, flatten the dough first in your palm a little, place two raisins in the middle, then shape into a ball.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91917C3CBF05E64596E8BD7C6108147A', X'C8204EBE694AAA4AA83202BD06E0A803', X'C8204EBE694AAA4AA83202BD06E0A803', 1, NULL, NULL, 2, NULL, 0, 'Heat oil in a small wok or saucepan, until a small piece of the kofta dough added to the oil sizzles immediately and rises to the top. Deep fry the kofta balls in batches on a medium flame until they are deep golden on the outside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A86EA76CAE6504F8B887754C187D74D', X'C8204EBE694AAA4AA83202BD06E0A803', X'C8204EBE694AAA4AA83202BD06E0A803', 2, NULL, NULL, 2, NULL, 0, 'Place the kofta balls in a large serving bowl.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'394B96842F057B4E92D0BD1BD3DE3A0D', X'5E343375BE0C3544B89E451414E73485', X'5E343375BE0C3544B89E451414E73485', 0, NULL, NULL, 2, NULL, 0, 'Heat oil in a saucepan. Add the cardamom, cloves and cinnamon');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3E2E6D58BCF3F42995203444DD787EC', X'5E343375BE0C3544B89E451414E73485', X'5E343375BE0C3544B89E451414E73485', 1, NULL, NULL, 2, NULL, 0, 'Add the onions and saute until the onions are soft and just beginning to turn color.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F083B1EA98D517428BA1B45EB30D5014', X'5E343375BE0C3544B89E451414E73485', X'5E343375BE0C3544B89E451414E73485', 2, NULL, NULL, 2, NULL, 0, 'Add the ginger and garlic, saute for no more than 30 seconds, then add in the turmeric, cayenne and coriander powders. Stir to mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DC5065CF87B1443B26B8E9FC94EE787', X'5E343375BE0C3544B89E451414E73485', X'5E343375BE0C3544B89E451414E73485', 3, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and cook until the tomatoes are pulpy and broken down, then add in the cashew nuts, pumpkin seeds and kasoori methi and mix well. Turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0973AEA5C246164DA0299F59BC82153E', X'5E343375BE0C3544B89E451414E73485', X'5E343375BE0C3544B89E451414E73485', 4, NULL, NULL, 2, NULL, 0, 'Remove the entire mixture to the blender, add two cups of cold water and process to a really smooth paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50B16325A50C6D4AB56E53A7D611BCC3', X'5E343375BE0C3544B89E451414E73485', X'5E343375BE0C3544B89E451414E73485', 5, NULL, NULL, 2, NULL, 0, 'This step is optional, but you can strain the sauce at this point for a really smooth texture.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'636C110C6E14254BACCEE29FB419C109', X'5E343375BE0C3544B89E451414E73485', X'5E343375BE0C3544B89E451414E73485', 6, NULL, NULL, 2, NULL, 0, 'Add the sauce back to the pan and bring it back to a boil over low heat. If it''s too thick to your liking, dilute with more water. Mix in the garam masala and add salt. Stir in the sugar if using, and turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2DB30C5F43BE044AAA9F43C1E3036C8', X'5E343375BE0C3544B89E451414E73485', X'5E343375BE0C3544B89E451414E73485', 7, NULL, NULL, 2, NULL, 0, 'Pour the hot sauce over the kofta balls, and serve immediately.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8355C667E5FEEF4BA973AD7126869012', X'0EB59F0F9FC32D44ADFC7166B0883692', X'0EB59F0F9FC32D44ADFC7166B0883692', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil and add the curry leaves and onions to it. Saute for a few minutes until the onions start to brown, then add in the ginger and garlic and black pepper and continue to saute another minute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A093D64875DDE41917A67C1F0C02DE2', X'0EB59F0F9FC32D44ADFC7166B0883692', X'0EB59F0F9FC32D44ADFC7166B0883692', 1, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and stir in the turmeric and paprika, if using. Mix well and cover the pot and let the tomatoes cook five minutes or until they are completely pulpy and mushy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5961A9A453693240921CAE7912F57258', X'0EB59F0F9FC32D44ADFC7166B0883692', X'0EB59F0F9FC32D44ADFC7166B0883692', 2, NULL, NULL, 2, NULL, 0, 'Add the potatoes and 2 cups of the vegetable stock along with half of the coconut milk. Mix well, cover and let the curry cook until the potatoes are very tender, about 10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F420CBB772CF643893C30F42D63932A', X'0EB59F0F9FC32D44ADFC7166B0883692', X'0EB59F0F9FC32D44ADFC7166B0883692', 3, NULL, NULL, 2, NULL, 0, 'When the potatoes are tender, stir in the garam masala and the chick''n pieces or chickpeas. Add the remaining vegetable stock at this point if needed to thin the curry out. You don''t want to cook the curry for too long at this point or the chick''n will get too soft. Once the curry comes to a boil, add the remaining coconut milk and turn off the heat. Stir in the cilantro.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3039B0782DA27A4BBF67C2CB3962CC95', X'0EB59F0F9FC32D44ADFC7166B0883692', X'0EB59F0F9FC32D44ADFC7166B0883692', 4, NULL, NULL, 2, NULL, 0, 'Serve the curry with rice, rotis, bread and wedges of lime to squeeze over the curry.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86E91C3BE622DB408CF3802A8307A217', X'3BC83D71880CA348BC785D7164556438', X'3BC83D71880CA348BC785D7164556438', 0, NULL, NULL, 2, NULL, 0, 'Heat oil, add the curry leaves and stir for a minute in the oil, then add 1 ½ cups of the tomato onion sauce. Once the sauce boils, proceed with step 3 where you add the potatoes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DE3CD979D1328448DB2E5A91233F2C5', X'59C2C65A33B9A74C9DB51C6C539FFBAB', X'59C2C65A33B9A74C9DB51C6C539FFBAB', 0, NULL, NULL, 2, NULL, 0, 'Cut the tofu into six pieces and set aside. Preheat the oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4225219BB4DBB4881B227CEB28542E2', X'59C2C65A33B9A74C9DB51C6C539FFBAB', X'59C2C65A33B9A74C9DB51C6C539FFBAB', 1, NULL, NULL, 2, NULL, 0, 'In an oven-safe dish, make the tofu marinade by mixing together the coconut milk, ginger-garlic paste, coriander powder, cumin powder, garam masala, lemon juice, paprika, half the kasoori methi, and turmeric. Pour out half the marinade into another oven-safe dish.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFF0365F00304F44A90B7802F301011C', X'59C2C65A33B9A74C9DB51C6C539FFBAB', X'59C2C65A33B9A74C9DB51C6C539FFBAB', 2, NULL, NULL, 2, NULL, 0, 'Place the bell pepper chunks and half the onion chopped into 1-inch chunks in one of the oven-safe dishes, and the tofu in the other. Turn the tofu around to coat with the marinade. Toss the vegetables in the other dish with the marinade to coat the veggies evenly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B9211731E628F469F138854107A64B1', X'59C2C65A33B9A74C9DB51C6C539FFBAB', X'59C2C65A33B9A74C9DB51C6C539FFBAB', 3, NULL, NULL, 2, NULL, 0, 'Now place both dishes into a preheated oven and bake for 30 minutes or until the tops of the veggies start to brown. Turn the tofu around once halfway through baking.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07351E03C869E94FB84E991D92E21FD1', X'59C2C65A33B9A74C9DB51C6C539FFBAB', X'59C2C65A33B9A74C9DB51C6C539FFBAB', 4, NULL, NULL, 2, NULL, 0, 'While the tofu and veggies are baking, blend the tomatoes and the raw cashews with ½ cup of water into a smooth paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B456F41ACE5CB24CB79067DED1DD9D51', X'59C2C65A33B9A74C9DB51C6C539FFBAB', X'59C2C65A33B9A74C9DB51C6C539FFBAB', 5, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large saucepan. Add the finely diced onions and saute for about 8-10 minutes over medium high heat until the onions turn golden-brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73C547888B947D40BEB58A6C85385DAA', X'59C2C65A33B9A74C9DB51C6C539FFBAB', X'59C2C65A33B9A74C9DB51C6C539FFBAB', 6, NULL, NULL, 2, NULL, 0, 'Add the tomato-cashew puree and cover and cook for 20 minutes. If the mixture gets too dry, add ½ cup water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D4275BF27A33B49934E76B2AAA2FD2B', X'59C2C65A33B9A74C9DB51C6C539FFBAB', X'59C2C65A33B9A74C9DB51C6C539FFBAB', 7, NULL, NULL, 2, NULL, 0, 'Add the baked veggies and remaining kasoori methi to the tomato sauce and stir well to mix. If there''s any marinade sticking to the dish, pour it into the saucepan, adding some water to the dish if needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6597D8F3A1A1694F84A7778694E1CB03', X'59C2C65A33B9A74C9DB51C6C539FFBAB', X'59C2C65A33B9A74C9DB51C6C539FFBAB', 8, NULL, NULL, 2, NULL, 0, 'Add the tofu to the sauce and again, pour in any marinade, using a little water to release any that is stuck to the dish. Add the sugar or maple syrup, if using. I like this because it rounds out the various flavors in the dish without making it sweet.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49D34699F797C24E976E8E1B19326ECB', X'59C2C65A33B9A74C9DB51C6C539FFBAB', X'59C2C65A33B9A74C9DB51C6C539FFBAB', 9, NULL, NULL, 2, NULL, 0, 'You want the sauce to be creamy but not watery. If your sauce appears too dry, add some water or vegetable stock. Bring to a boil, add salt, and turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'403C27B02CA48A4F989EF16958C9BBA9', X'59C2C65A33B9A74C9DB51C6C539FFBAB', X'59C2C65A33B9A74C9DB51C6C539FFBAB', 10, NULL, NULL, 2, NULL, 0, 'Garnish with cilantro. Serve hot with rice or naan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7DF3A9299832746BCC3CC5A03949152', X'BA8B723808E5F74683CECA6AB6557F12', X'BA8B723808E5F74683CECA6AB6557F12', 0, NULL, NULL, 2, NULL, 0, 'To prepare the tofu:');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1217148DB108742B398A2EE4A5A5C6A', X'BA8B723808E5F74683CECA6AB6557F12', X'BA8B723808E5F74683CECA6AB6557F12', 1, NULL, NULL, 2, NULL, 0, 'Mix all the ingredients except the tofu. Now slather the marinade on the slices of tofu you''ve prepared and set aside for about half an hour.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E08FCD2B9133E34C9832CC851B8016C1', X'BA8B723808E5F74683CECA6AB6557F12', X'BA8B723808E5F74683CECA6AB6557F12', 2, NULL, NULL, 2, NULL, 0, 'Smear the oil evenly in a nonstick or cast-iron griddle. When it''s hot, place the slices of tofu in the pan without crowding them. Cook until golden-brown on each side, about four minutes per side. Remove to a plate and, when cool, cut into ¾-inch cubes. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81C554677723114EB85132805A6AB3DD', X'BA8B723808E5F74683CECA6AB6557F12', X'BA8B723808E5F74683CECA6AB6557F12', 3, NULL, NULL, 2, NULL, 0, 'To make the Makhani curry:');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54EFB05C26E9A04C81AE87DF53439A40', X'BA8B723808E5F74683CECA6AB6557F12', X'BA8B723808E5F74683CECA6AB6557F12', 4, NULL, NULL, 2, NULL, 0, 'Heat the oil in a saucepan. Add the cumin seeds, and when they sputter add cardamom, cloves, peppercorns and cinnamon. Saute for a minute over medium-high heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18737AE20408A94A9B44A80BCC97EDC7', X'BA8B723808E5F74683CECA6AB6557F12', X'BA8B723808E5F74683CECA6AB6557F12', 5, NULL, NULL, 2, NULL, 0, 'Add the onions and a little salt and saute until the onions start to brown, about five minutes. Add ginger and garlic pastes, kasoori methi, and cashew nuts and saute for another minute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'807AC6907912D048A9EC3415371C6533', X'BA8B723808E5F74683CECA6AB6557F12', X'BA8B723808E5F74683CECA6AB6557F12', 6, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and tomato paste, powdered coriander, turmeric, and chilli powder.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FC1839D935F8449A1C2D7AB7BFFFC97', X'BA8B723808E5F74683CECA6AB6557F12', X'BA8B723808E5F74683CECA6AB6557F12', 7, NULL, NULL, 2, NULL, 0, 'Saute the mixture until the tomatoes turn really soft and melt into the saucepan. If the mixture starts to get too dry before the tomatoes are done, add some water or vegetable stock and continue cooking.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D51FAC706411DE48AB15D357060C0F0C', X'BA8B723808E5F74683CECA6AB6557F12', X'BA8B723808E5F74683CECA6AB6557F12', 8, NULL, NULL, 2, NULL, 0, 'Once the tomatoes are really soft, turn off the heat and let the mixture cool. Pour into a blender and add a cup of vegetable stock. Blend to a smooth paste. (Don''t blend the mixture while it''s still hot because it can be dangerous. If you have a hand blender, this is the time to use it.)');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D96A9621288994A9E7E4268375238E6', X'BA8B723808E5F74683CECA6AB6557F12', X'BA8B723808E5F74683CECA6AB6557F12', 9, NULL, NULL, 2, NULL, 0, 'Pour the blended paste back into the saucepan, turn on the heat, add the remaining vegetable stock if the mixture is thick, and bring to a simmer. Now add the tofu cubes and stir them in. Add salt to taste. Let the mixture simmer for about 10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E37B351993BF34AB48EB2D395434796', X'BA8B723808E5F74683CECA6AB6557F12', X'BA8B723808E5F74683CECA6AB6557F12', 10, NULL, NULL, 2, NULL, 0, 'Add the vegan butter and turn off the heat. Stir to melt the butter into the sauce.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21D266C4B567E0428465A1C852B58AE6', X'BA8B723808E5F74683CECA6AB6557F12', X'BA8B723808E5F74683CECA6AB6557F12', 11, NULL, NULL, 2, NULL, 0, 'Add the lemon juice and maple syrup, mix well, garnish with coriander leaves, and serve hot with some boiled rice or naan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11881AAE89B3594088EC6E933B2A3FD9', X'12E42A027441944F8F4DE165531AD7F9', X'12E42A027441944F8F4DE165531AD7F9', 0, NULL, NULL, 2, NULL, 0, 'Heat half a tablespoon of oil. Add the cloves, cinnamon, black cumin, coriander seeds, fennel seeds, black peppercorns and red chilies and saute until the coriander is a few shades darker and the spices are fragrant.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'235083714E14CB46B39F1F0C41CB82E7', X'12E42A027441944F8F4DE165531AD7F9', X'12E42A027441944F8F4DE165531AD7F9', 1, NULL, NULL, 2, NULL, 0, 'Add the onions and garlic with a pinch of salt. Saute, stirring frequently, until the onions start to brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B822895BC2251048933054149F4DFC65', X'12E42A027441944F8F4DE165531AD7F9', X'12E42A027441944F8F4DE165531AD7F9', 2, NULL, NULL, 2, NULL, 0, 'Add the coconut (thaw first if frozen). After adding the coconut, don''t wander away because coconut can burn fast. Stir frequently until the coconut''s turning pale blonde and toasty.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CB1701C0843DC43819448EB681A5937', X'12E42A027441944F8F4DE165531AD7F9', X'12E42A027441944F8F4DE165531AD7F9', 3, NULL, NULL, 2, NULL, 0, 'Transfer the mixture to a blender, add a cup of water, and blend into a smooth paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB0DDB2216D1CF4D823E90383ED7F705', X'4E84EB901A8BC541AC518DC03DF81B18', X'4E84EB901A8BC541AC518DC03DF81B18', 0, NULL, NULL, 2, NULL, 0, 'Heat the remaining oil. Add the onions and some salt and saute until they start to brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08719F4655225A40A47941A7FCF87EB9', X'4E84EB901A8BC541AC518DC03DF81B18', X'4E84EB901A8BC541AC518DC03DF81B18', 1, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, turmeric, and paprika, if using. Stir to mix, cover with a lid, and let it all cook until the tomatoes are quite mushy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0342D5A29BABF34A934071D8899CB4D5', X'4E84EB901A8BC541AC518DC03DF81B18', X'4E84EB901A8BC541AC518DC03DF81B18', 2, NULL, NULL, 2, NULL, 0, 'Add the ground masala and stir well to mix. If the curry is really thick, add some water to thin it out.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFF813192C2F3146A21AB69521E3EE36', X'4E84EB901A8BC541AC518DC03DF81B18', X'4E84EB901A8BC541AC518DC03DF81B18', 3, NULL, NULL, 2, NULL, 0, 'Bring the curry to a boil, add salt, then add the grated nutmeg and tofu cubes. Stir well to mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'119925E533570F4C89C2028BB896FAC3', X'4E84EB901A8BC541AC518DC03DF81B18', X'4E84EB901A8BC541AC518DC03DF81B18', 4, NULL, NULL, 2, NULL, 0, 'Let the tofu poach in the curry over medium-low heat, about 8 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E3DA650248AE340A4852811797F886A', X'4E84EB901A8BC541AC518DC03DF81B18', X'4E84EB901A8BC541AC518DC03DF81B18', 5, NULL, NULL, 2, NULL, 0, 'Squeeze in the lemon juice and add the coriander leaves.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77D39F6065A9604A904B2E45E4CCFAF1', X'4E84EB901A8BC541AC518DC03DF81B18', X'4E84EB901A8BC541AC518DC03DF81B18', 6, NULL, NULL, 2, NULL, 0, 'Serve hot with some rice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9D2993542F8F94BA5B6E8DA36414151', X'735BECEA800BF0448B84496D59C4A034', X'735BECEA800BF0448B84496D59C4A034', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a saucepan. Add the cumin, cardamom, cloves, cinnamon and peppercorns. As the spices start to change color, add the tomato onion sauce and kasoori methi and mix well. You don''t need to let the sauce cook through at this stage.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDE3D92247008C40AF59803C1886A8DF', X'735BECEA800BF0448B84496D59C4A034', X'735BECEA800BF0448B84496D59C4A034', 1, NULL, NULL, 2, NULL, 0, 'Turn off the heat and pour the sauce into a blender. Add the cashews and a cup of water and blend until smooth, then pour it all back into the saucepan and turn on the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D431D1DFD78AB242B39B0CA632891A22', X'735BECEA800BF0448B84496D59C4A034', X'735BECEA800BF0448B84496D59C4A034', 2, NULL, NULL, 2, NULL, 0, 'Add the tofu cubes and let the sauce come to a boil. Stir in the garam masala. If your sauce feels too thick at any time, thin it out with water. You want a sauce that''s thick enough to coat the tofu. Let the curry cook another five minutes on medium-low heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04F002E8A686AE4C891DCBDEC628CF40', X'735BECEA800BF0448B84496D59C4A034', X'735BECEA800BF0448B84496D59C4A034', 3, NULL, NULL, 2, NULL, 0, 'Stir in the lemon juice. Add salt and ground black pepper, if needed, and turn off the heat. Stir in the lemon juice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56577EB3A16D9D4F828073D0A2C858FC', X'735BECEA800BF0448B84496D59C4A034', X'735BECEA800BF0448B84496D59C4A034', 4, NULL, NULL, 2, NULL, 0, 'Serve hot with naan, roti, paratha or rice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB27B10161DF2F4B910C031FA2CB3E6E', X'AD47DAED3889B244A9554DA7B30FDBF2', X'AD47DAED3889B244A9554DA7B30FDBF2', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees (you can do this in the toaster oven or the air fryer).');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2718A5BCB6E2445B61EE8A8E58E13BB', X'AD47DAED3889B244A9554DA7B30FDBF2', X'AD47DAED3889B244A9554DA7B30FDBF2', 1, NULL, NULL, 2, NULL, 0, 'Place the meat substitute in an oven-proof baking dish with the turmeric, 1 tsp paprika, 1 tsp oil and half the ginger-garlic paste. Add salt and, if you like, ground black pepper. Toss to mix well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1FFBA17CAE93F46943AEA2A72AC2CEC', X'AD47DAED3889B244A9554DA7B30FDBF2', X'AD47DAED3889B244A9554DA7B30FDBF2', 2, NULL, NULL, 2, NULL, 0, 'Place the dish in the oven and bake 20 minutes. You might need only about 15 minutes in the air fryer, and if you''re using one, check after 10 minutes and toss if needed to make sure they cook evenly. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0276F23A76D34A4BA9F123AE57AD6AFB', X'AD47DAED3889B244A9554DA7B30FDBF2', X'AD47DAED3889B244A9554DA7B30FDBF2', 3, NULL, NULL, 2, NULL, 0, 'Heat the remaining teaspoon of oil in a large saucepan. Add the onions along with salt and saute until they start to brown. Add the remaining ginger garlic paste, stir to mix, and add in the tomatoes. Mix well and let the tomatoes cook over medium high heat, stirring frequently, until they turn a darker shade and most of the moisture is gone, about five minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2239358D172F2044AD69A471A028C95A', X'AD47DAED3889B244A9554DA7B30FDBF2', X'AD47DAED3889B244A9554DA7B30FDBF2', 4, NULL, NULL, 2, NULL, 0, 'Add the remaining paprika, coriander powder and garam masala. Saute for another minute so the spices coat the veggies and are lightly toasted, then 2 cups of the vegetable stock. Mix well, and add more stock if you want it thinner. Add in the reserved meat substitute cubes and salt to taste. Bring to a boil and turn off immediately.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CBCB83691588142B11D95A21475E28D', X'AD47DAED3889B244A9554DA7B30FDBF2', X'AD47DAED3889B244A9554DA7B30FDBF2', 5, NULL, NULL, 2, NULL, 0, 'You can, if you wish, add a touch of cashew cream or coconut milk -- no more than ¼ cup -- just before finishing for more creaminess, but this curry is delicious as is.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF3F516DBA99DB4EAC21493109613B2A', X'AD47DAED3889B244A9554DA7B30FDBF2', X'AD47DAED3889B244A9554DA7B30FDBF2', 6, NULL, NULL, 2, NULL, 0, 'Garnish with cilantro or mint and serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E3FB42370293848ABD8DF67B17BBC5C', X'98EE05287101DD4384C1639688C24AA8', X'98EE05287101DD4384C1639688C24AA8', 0, NULL, NULL, 2, NULL, 0, 'Blend the cashews with ¼ cup water and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4341B1AC9F624F4497F710418967AF65', X'98EE05287101DD4384C1639688C24AA8', X'98EE05287101DD4384C1639688C24AA8', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil and add the cumin seeds. As they begin to darken slightly and become fragrant, add the bell peppers. Saute the peppers three to four minutes or until they soften a bit, then add the kasoori methi, stir it in, then add tomato sauce and salt to taste. Add the tofu and mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A9892254F42C9418FDD1CC054F52058', X'98EE05287101DD4384C1639688C24AA8', X'98EE05287101DD4384C1639688C24AA8', 2, NULL, NULL, 2, NULL, 0, 'When the sauce comes to a boil, add the cashew paste and stir it in. Turn off the heat and serve. Garnish with cilantro, if you wish.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FE0F2C5C32A674899C8CB725E37B1C4', X'1E6665B1C3C6174AAD23A756E05A08CF', X'1E6665B1C3C6174AAD23A756E05A08CF', 0, NULL, NULL, 2, NULL, 0, 'Heat 1 tbsp butter in a saucepan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'853A6F325A62164F9D0AE727DA48A70C', X'1E6665B1C3C6174AAD23A756E05A08CF', X'1E6665B1C3C6174AAD23A756E05A08CF', 1, NULL, NULL, 2, NULL, 0, 'Add the onions, garlic and ginger and saute, stirring frequently, until the onions turn translucent.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF34C43E97FE384EA6A02B29361AABC8', X'1E6665B1C3C6174AAD23A756E05A08CF', X'1E6665B1C3C6174AAD23A756E05A08CF', 2, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and the cashews. Cook, covered but stirring frequently, until the tomatoes have reduced to a pulp. Add the coriander powder, turmeric and paprika and continue cooking for another 5 minutes. Turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3189E3B59C37641990B70ED9B8EB2A3', X'1E6665B1C3C6174AAD23A756E05A08CF', X'1E6665B1C3C6174AAD23A756E05A08CF', 3, NULL, NULL, 2, NULL, 0, 'Carefully, taking care not to burn yourself, ladle out the tomato-onion-spice mixture into a blender. Or, if you have an immersion blender, use that. Add a cup of water or preferably vegetable stock and blend into a very, very smooth paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0ABFC3392A8E446889D84C0CB51F386', X'1E6665B1C3C6174AAD23A756E05A08CF', X'1E6665B1C3C6174AAD23A756E05A08CF', 4, NULL, NULL, 2, NULL, 0, 'Pour the sauce back into the saucepan. For an even smoother texture to your sauce, you can strain it. Add the garam masala and stir in.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B06713602B46DA46B7743D3E78B1BD04', X'1E6665B1C3C6174AAD23A756E05A08CF', X'1E6665B1C3C6174AAD23A756E05A08CF', 5, NULL, NULL, 2, NULL, 0, 'Add the kasoori methi and sugar and stir well. If the sauce looks too thick, you can thin it out with more vegetable stock. Bring the sauce to a boil over medium heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51A8955D170DD84893DF294DDBA2F327', X'1E6665B1C3C6174AAD23A756E05A08CF', X'1E6665B1C3C6174AAD23A756E05A08CF', 6, NULL, NULL, 2, NULL, 0, 'Add the cubes of tofu, season with salt, bring back to a boil, and continue to cook, covered for another five minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B268F2B142EE094495BE8807C02B827E', X'1E6665B1C3C6174AAD23A756E05A08CF', X'1E6665B1C3C6174AAD23A756E05A08CF', 7, NULL, NULL, 2, NULL, 0, 'Turn off the heat, garnish with cilantro, and serve hot with naan or roti or rice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BADE0122A01D6458D6D2CA4A34B1EA4', X'C63DFFC120C6134299AE013128414F62', X'C63DFFC120C6134299AE013128414F62', 0, NULL, NULL, 2, NULL, 0, 'In a dry, hot skillet, roast the coriander and cumin seeds. Place in a blender.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B77B2FB877DDB478C0C76C8A2CAA41B', X'C63DFFC120C6134299AE013128414F62', X'C63DFFC120C6134299AE013128414F62', 1, NULL, NULL, 2, NULL, 0, 'Heat ½ tsp of oil in the skillet. Add the curry leaves, garlic, chili peppers and shallots and saute until brown spots appear on the onions. Add to the blender, add half a cup of vegetable stock, and blend into a very smooth paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4ECC5F410EDE445A9E7D25FA4FFEA33', X'C63DFFC120C6134299AE013128414F62', X'C63DFFC120C6134299AE013128414F62', 2, NULL, NULL, 2, NULL, 0, 'Heat the remaining 1 tbsp oil in the skillet. Add the potatoes and stir-fry for five minutes until golden spots appear.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BC02EC17B632C4BB2DA449B45B18CB9', X'C63DFFC120C6134299AE013128414F62', X'C63DFFC120C6134299AE013128414F62', 3, NULL, NULL, 2, NULL, 0, 'Add the mushrooms and turmeric and saute until the mushrooms begin to soften, about 2-3 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0B1781900FB7E44BCA17CC342DC28BE', X'C63DFFC120C6134299AE013128414F62', X'C63DFFC120C6134299AE013128414F62', 4, NULL, NULL, 2, NULL, 0, 'Now add the blended masala and the tomato puree. Cook for about five minutes, stirring frequently, then add 1 ½ cups of vegetable stock. Check seasoning and add more salt if needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21D4901712C0B048B8F985A3E5AD42AC', X'C63DFFC120C6134299AE013128414F62', X'C63DFFC120C6134299AE013128414F62', 5, NULL, NULL, 2, NULL, 0, 'Cover and cook about 20 minutes or until the potatoes are tender. If your curry gets too thick, you can thin it out with more vegetable stock.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3539D3C57E4F649B5052057F2C1E621', X'C63DFFC120C6134299AE013128414F62', X'C63DFFC120C6134299AE013128414F62', 6, NULL, NULL, 2, NULL, 0, 'Garnish with coriander leaves and serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1FC677D2715034D99EA2B36E1079762', X'07E82D02638EE443AD224F587FA3791D', X'07E82D02638EE443AD224F587FA3791D', 0, NULL, NULL, 2, NULL, 0, 'Place the sausages in a bowl with turmeric, 1 tsp paprika, cinnamon, ground black pepper, 1 tbsp ginger garlic paste and the vinegar. Mix well to coat the sausage pieces with the spices. Set aside for at least 30 minutes or overnight in the refrigerator.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C633E4E40E0AA4A8D61037C93EF4198', X'07E82D02638EE443AD224F587FA3791D', X'07E82D02638EE443AD224F587FA3791D', 1, NULL, NULL, 2, NULL, 0, 'Heat oil in a skillet or wok and add the potatoes to it. Stir-fry the potatoes over medium-high heat until they are a light golden-brown. Add the onions, remaining ginger-garlic paste and cilantro along with the remaining paprika and a little salt and stir-fry until the onions begin to turn golden-brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50FCE860828E0746BCCC998DF658B9BE', X'07E82D02638EE443AD224F587FA3791D', X'07E82D02638EE443AD224F587FA3791D', 2, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and cook until they turn pulpy. Now add the marinated sausages, stir to mix, and then add in a cup of vegetable stock or water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9551F01E02113C4BA40FA5AD9F853DCA', X'07E82D02638EE443AD224F587FA3791D', X'07E82D02638EE443AD224F587FA3791D', 3, NULL, NULL, 2, NULL, 0, 'Add more salt if needed, and bring the curry to a boil. At this point, you can add more stock or water if you want a saucier curry. Cover and cook the curry for another five minutes or until the potatoes are tender.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A71824E4D3FF74184E5E15FB3AFE1FD', X'07E82D02638EE443AD224F587FA3791D', X'07E82D02638EE443AD224F587FA3791D', 4, NULL, NULL, 2, NULL, 0, 'Turn off the heat and serve hot with bread rolls or rice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A22E18B5069E4543BC9A2F8DCCA117B8', X'E4E627F5AC94FD4CBAA5B72B93496CA7', X'E4E627F5AC94FD4CBAA5B72B93496CA7', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil and add the mustard seeds. When they sputter, add the onions.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8647A308FA11A9469E95C66EAB301581', X'E4E627F5AC94FD4CBAA5B72B93496CA7', X'E4E627F5AC94FD4CBAA5B72B93496CA7', 1, NULL, NULL, 2, NULL, 0, 'Add some salt and saute the onions over medium heat until they start to get brown spots. Add ginger and garlic and saute for a few seconds.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BAC03672CA09A42A8EF0A17D6424A0B', X'E4E627F5AC94FD4CBAA5B72B93496CA7', X'E4E627F5AC94FD4CBAA5B72B93496CA7', 2, NULL, NULL, 2, NULL, 0, 'Add the tomato puree and the powdered spices, including the turmeric, cayenne, paprika, chana masala powder and aamchoor. Let the mixture cook until the tomato puree is a few shades darker and starts to stick to the bottom of the pan. Scrape so it doesn''t burn. This should take about 5 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B50354C1543DCA4495F31A0A7180A99E', X'E4E627F5AC94FD4CBAA5B72B93496CA7', X'E4E627F5AC94FD4CBAA5B72B93496CA7', 3, NULL, NULL, 2, NULL, 0, 'Add 2-3 cups of the vegetable stock or water and chickpeas and let the curry come to a boil. Lower the heat and let it simmer for about 5 minutes. Add salt and sugar. If the mixture is too dry, add more stock or water');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0B133D2C758B94DBE22D9BFD8CE2A2F', X'E4E627F5AC94FD4CBAA5B72B93496CA7', X'E4E627F5AC94FD4CBAA5B72B93496CA7', 4, NULL, NULL, 2, NULL, 0, 'Garnish with the coriander and serve hot with rotis, bhatura or rice. I love it with some simple pilaf rice');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9650F45E20F2ED4DB842103C6A06965B', X'B6511337ACC1804FB5DACF28C90DE962', X'B6511337ACC1804FB5DACF28C90DE962', 0, NULL, NULL, 2, NULL, 0, 'Soak the beans for a couple of hours at least or overnight, then cooked in a pressure cooker until really tender. If you are cooking on the stovetop, place enough water in the pan to cover the beans by an inch, bring to a boil, lower the heat to a simmer, cover, and let the beans cook until tender. You might need to add more water as they cook, so check every once in a way. The beans should take about an hour to become tender. Reserve the beans and the cooking liquid.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13AF5FEA38D7DD4D914E695078DB7A05', X'B6511337ACC1804FB5DACF28C90DE962', X'B6511337ACC1804FB5DACF28C90DE962', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large saucepan. Add the cumin and carom seeds. Stir-fry for 30 seconds, then add the onions.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEB6043B46BB1C438920467BCC221193', X'B6511337ACC1804FB5DACF28C90DE962', X'B6511337ACC1804FB5DACF28C90DE962', 2, NULL, NULL, 2, NULL, 0, 'Saute the onions until brown spots start to appear. Add the ginger-garlic paste, saute for a few seconds, then add the tomatoes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB6144DD81C8E74CB6F0501EE347FBF1', X'B6511337ACC1804FB5DACF28C90DE962', X'B6511337ACC1804FB5DACF28C90DE962', 3, NULL, NULL, 2, NULL, 0, 'Add the garam masala, coriander powder, paprika and cayenne. Stir to mix well. Cover the saucepan with a lid and let the tomatoes cook on medium heat until they are completely broken down. You can help the process along by adding ½ cup of the bean stock.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F002EB10DD66C94299E284FF89C3F6C2', X'B6511337ACC1804FB5DACF28C90DE962', X'B6511337ACC1804FB5DACF28C90DE962', 4, NULL, NULL, 2, NULL, 0, 'Add the beans to the saucepan, stir well to mix, then add the kasoori methi, crushing the dry leaves as you add to powder them.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0A5E147B1F49E4BABFC51CC677741EE', X'B6511337ACC1804FB5DACF28C90DE962', X'B6511337ACC1804FB5DACF28C90DE962', 5, NULL, NULL, 2, NULL, 0, 'Add some water or the bean cooking stock to thin out the gravy, if it''s too thick. I like to take a potato masher at this point and mash some of the kidney beans to make the gravy thick and creamy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B83AA0E0949DB4286D6CB17E774E208', X'B6511337ACC1804FB5DACF28C90DE962', X'B6511337ACC1804FB5DACF28C90DE962', 6, NULL, NULL, 2, NULL, 0, 'Cover the saucepan with a lid and let the beans cook for another 10 minutes. Add salt to taste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F84A5728E7AF04286F28416CE408E97', X'B6511337ACC1804FB5DACF28C90DE962', X'B6511337ACC1804FB5DACF28C90DE962', 7, NULL, NULL, 2, NULL, 0, 'Stir in coriander leaves and take off the heat. Stir in the optional vegan butter at this point.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B5A5F96DFED31468E23378C6E7FF825', X'B6511337ACC1804FB5DACF28C90DE962', X'B6511337ACC1804FB5DACF28C90DE962', 8, NULL, NULL, 2, NULL, 0, 'Serve hot over some brown or white rice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3672F0CD37BC2345B4871793D3B245D5', X'7AFFD51662494945BC0509A25276A36F', X'7AFFD51662494945BC0509A25276A36F', 0, NULL, NULL, 2, NULL, 0, 'Place the urad dal and rajma together in a large bowl and wash in three to four changes of water, rubbing the beans to remove all the dirt that may be sticking to them. Finally, top the beans with water by at least three inches. Let the beans soak at least eight hours or overnight.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF86C339F8BF2F43A420D460FE403B1E', X'7AFFD51662494945BC0509A25276A36F', X'7AFFD51662494945BC0509A25276A36F', 1, NULL, NULL, 2, NULL, 0, 'Place the drained beans and lentils in a large stockpot along with 1 tsp of garlic and a bay leaf, if using. Cover by at least an inch and a half of water and bring them to a boil. If you see a white, frothy layer accumulate on top, skim it away. Cover and cook the lentils for an hour and a half or until the black lentils are really tender and the kidney beans are cooked but not falling apart.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'503D67250E3F484FB6C229370BD72EFE', X'7AFFD51662494945BC0509A25276A36F', X'7AFFD51662494945BC0509A25276A36F', 2, NULL, NULL, 2, NULL, 0, 'Place 1 tbsp of garlic and the ginger in a mortar and pestle and crush into a coarse paste. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A56D0521C183A34C9F7BCF892E3415D9', X'7AFFD51662494945BC0509A25276A36F', X'7AFFD51662494945BC0509A25276A36F', 3, NULL, NULL, 2, NULL, 0, 'Blend the cashews with ¼ cup of water into a very smooth cream. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2CC9F866C54BB45BDD93C8B9FA85C66', X'7AFFD51662494945BC0509A25276A36F', X'7AFFD51662494945BC0509A25276A36F', 4, NULL, NULL, 2, NULL, 0, 'Heat the oil in a saucepan. Add the ginger-garlic paste, saute until the garlic begins to turn blonde, then add in the paprika and the kasoori methi. Stir quickly and immediately add the tomato puree.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18D144A2EB662344A1B9A66CD4DC8997', X'7AFFD51662494945BC0509A25276A36F', X'7AFFD51662494945BC0509A25276A36F', 5, NULL, NULL, 2, NULL, 0, 'Cook the tomato puree for five minutes, stirring frequently. Stir in the sugar and add the cooked dal along with the liquid it was cooked in. If it looks like you have too much liquid, hold some back and add according to the consistency you want. If there''s too little liquid, add water to thin the dal out.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EAD63FDC15909488360B4538A145CD4', X'7AFFD51662494945BC0509A25276A36F', X'7AFFD51662494945BC0509A25276A36F', 6, NULL, NULL, 2, NULL, 0, 'Bring the dal to a boil, cover, turn down the heat, and let it cook for five minutes. Use a potato masher or a heavy ladle to mash some of the lentils so the consistency gets creamier. Stir in the vegan butter and salt to taste. Add the cashew cream.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B25E5D6D8BF96E4E9D47DCDE36B72130', X'7AFFD51662494945BC0509A25276A36F', X'7AFFD51662494945BC0509A25276A36F', 7, NULL, NULL, 2, NULL, 0, 'Check seasoning and add more salt if needed. Turn off the heat and serve hot or warm before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EB95AB10B0BC74D9835B84B5276B620', X'70F58543A698734CACC33441C9CA9CBF', X'70F58543A698734CACC33441C9CA9CBF', 0, NULL, NULL, 2, NULL, 0, 'Cook the legumes. I will share with you here my `shortcut` way of doing this-- because rajma takes much longer to cook than the two lentils, and because I don''t always remember to presoak my beans, I faux-soak the rajma by covering it with 2 inches of water in a microwave-safe dish, zap it for about 10 minutes, add more water if needed, and then zap again for another 10 minutes. Then I place the legumes and the rajma in a pressure cooker and cook them together. If you don''t have a pressure cooker, you can cover the lentils and the soaked rajma with water in a large saucepan, slap on a lid, and cook about an hour.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'372FFD96FC886D4197880E963CE80F65', X'70F58543A698734CACC33441C9CA9CBF', X'70F58543A698734CACC33441C9CA9CBF', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large saucepan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1CB7B6A41387741B63D44C63A70890E', X'70F58543A698734CACC33441C9CA9CBF', X'70F58543A698734CACC33441C9CA9CBF', 2, NULL, NULL, 2, NULL, 0, 'Add the ginger and garlic, saute for a minute on a medium-low flame, and then add the onions.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'807DD33F97D25A44B50D2C9AAF4D0007', X'70F58543A698734CACC33441C9CA9CBF', X'70F58543A698734CACC33441C9CA9CBF', 3, NULL, NULL, 2, NULL, 0, 'Saute the onions until brown spots appear, about 8-10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18E3C69E442F9540B10A6F61762DF934', X'70F58543A698734CACC33441C9CA9CBF', X'70F58543A698734CACC33441C9CA9CBF', 4, NULL, NULL, 2, NULL, 0, 'Add the cumin, coriander, turmeric powders and salt to taste. Add the tomatoes and saute until they are cooked down, about five minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C6E60F29897C24EA63CE3E350327624', X'70F58543A698734CACC33441C9CA9CBF', X'70F58543A698734CACC33441C9CA9CBF', 5, NULL, NULL, 2, NULL, 0, 'Now add the cooked lentils and stir well to mix. Add some water if the dal is too thick.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBDCEDAAEEA0234C979DD31024858035', X'70F58543A698734CACC33441C9CA9CBF', X'70F58543A698734CACC33441C9CA9CBF', 6, NULL, NULL, 2, NULL, 0, 'Cover with a lid and cook about 8-10 minutes for all the flavors to meld together. Crush the kasoori methi with your fingers and sprinkle over the dal. Mix in the butter and stir until it''s melted into the dal.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E693C40B3E552644A31961F156A75A4C', X'70F58543A698734CACC33441C9CA9CBF', X'70F58543A698734CACC33441C9CA9CBF', 7, NULL, NULL, 2, NULL, 0, 'Stir in the cilantro, turn off the heat, and serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'859D7CEE433A4B4D8E13DCCB6B7F84F1', X'B15C47373554024B83276408545F6DB6', X'B15C47373554024B83276408545F6DB6', 0, NULL, NULL, 2, NULL, 0, 'In a pressure cooker or in a saucepan, combine the lentils with bay leaves and turmeric. If you''re cooking in a saucepan, cover the lentils with an inch of water, bring to a boil, and let the lentils cook at a simmer, covered. Check frequently to ensure they don''t dry up and add more water if needed. Cook the lentils until they are really soft and tender.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDC8349B93A33443AC72A565BF893DB3', X'B15C47373554024B83276408545F6DB6', X'B15C47373554024B83276408545F6DB6', 1, NULL, NULL, 2, NULL, 0, 'In a saucepan, heat the oil. Add the mustard seeds and, when they sputter, add the cloves, cardamom and cinnamon. Saute a minute until the cardamom looks puffy, then add the slivers of coconut, if using, and saute until they get lightly golden brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93297B736DA7984EA6E3EC603FEE12DA', X'B15C47373554024B83276408545F6DB6', X'B15C47373554024B83276408545F6DB6', 2, NULL, NULL, 2, NULL, 0, 'Add the raisins and ginger, saute for 30 seconds, then add the cooked dal. Stir well to mix. Add some water if the dal is too dry. You want it to have a thicker consistency than most dals do, but not so thick that it''s dry.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E745E67695FC44E8D74065089E17296', X'B15C47373554024B83276408545F6DB6', X'B15C47373554024B83276408545F6DB6', 3, NULL, NULL, 2, NULL, 0, 'Add the jaggery and coconut milk and heat the dal through.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C552BC449D8C274B95B310ECAC0D3E5E', X'B15C47373554024B83276408545F6DB6', X'B15C47373554024B83276408545F6DB6', 4, NULL, NULL, 2, NULL, 0, 'Garnish with coriander leaves and serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DFB0E9AC922B7428342FF79D0DFB732', X'848823C692E8294FB787803067E43598', X'848823C692E8294FB787803067E43598', 0, NULL, NULL, 2, NULL, 0, 'Mix the lentils and turmeric, add water and cook until tender in a pressure cooker. Or cover the peas with an inch of water in a pot and bring to a boil, then slap on a lid, lower to simmer, and boil for 30-45 minutes or until the peas are tender enough to mash. Add more water if needed as they cook.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1CD372A8FBE8749AB1A2FA78597A67F', X'848823C692E8294FB787803067E43598', X'848823C692E8294FB787803067E43598', 1, NULL, NULL, 2, NULL, 0, 'Prepare the ground masala by heating 1 tsp of oil. Add to it the coriander seeds, chana dal, fenugreek seeds and chillies and saute until the coriander seeds and dal turn a few shades darker and are lightly golden-brown. Remove to a blender, add enough water to keep the blades moving, and process to a smooth paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B9CA6A2F0ED2F4C9BC011332DDB3FBC', X'848823C692E8294FB787803067E43598', X'848823C692E8294FB787803067E43598', 2, NULL, NULL, 2, NULL, 0, 'Heat 1 tsp of the oil, add the pearl onions and stir-fry until the start to turn golden. Add the tamarind extract and let the mixture cook about 8 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA3F8D334569EB479D520B1D85AA3A52', X'848823C692E8294FB787803067E43598', X'848823C692E8294FB787803067E43598', 3, NULL, NULL, 2, NULL, 0, 'Add the ground masala and stir well to mix. Add the cooked tuvar dal and bring everything to a boil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED699408F86DF740A607CAA2A34C051A', X'848823C692E8294FB787803067E43598', X'848823C692E8294FB787803067E43598', 4, NULL, NULL, 2, NULL, 0, 'Lower the heat to a simmer, add salt to taste, and let the sambar cook for 10 minutes so all the flavors have a chance to meld together.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A60203A49402F4DBF12DA071CF47508', X'848823C692E8294FB787803067E43598', X'848823C692E8294FB787803067E43598', 5, NULL, NULL, 2, NULL, 0, 'To temper the sambar-- an important flavor-building step-- heat the last teaspoon of oil in a small saucepan and add the mustard seeds. When they crackle, add the asafetida and curry leaves. Fry for a few seconds, turn off the heat, and pour over the sambar. Mix well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'794A4274B1B61547859EF3B2A1C79624', X'2D18DE446FCAA54EAAA75ED6330E24A0', X'2D18DE446FCAA54EAAA75ED6330E24A0', 0, NULL, NULL, 2, NULL, 0, 'Cook the dal with turmeric in a pressure cooker or on the stovetop with 2 cups of water until it''s really soft. Use a whisk to mash the dal up. Do not discard the stock.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A372D39FF01D7448A96CEE38CDD7F253', X'2D18DE446FCAA54EAAA75ED6330E24A0', X'2D18DE446FCAA54EAAA75ED6330E24A0', 1, NULL, NULL, 2, NULL, 0, 'Using a mortar and pestle, or in a food processor, crush 2 cloves of garlic, the green chili pepper, and cumin seeds to a coarse paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D6C9D0DEB391D4D9664E1E4D4CA6FA6', X'2D18DE446FCAA54EAAA75ED6330E24A0', X'2D18DE446FCAA54EAAA75ED6330E24A0', 2, NULL, NULL, 2, NULL, 0, 'Crush the remaining garlic cloves slightly but let them hold their shape.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EA3B09F2E792B4495093A91A7B08B62', X'2D18DE446FCAA54EAAA75ED6330E24A0', X'2D18DE446FCAA54EAAA75ED6330E24A0', 3, NULL, NULL, 2, NULL, 0, 'Heat the oil in a saucepan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF6E737023F65442A6958BD3AE451E7E', X'2D18DE446FCAA54EAAA75ED6330E24A0', X'2D18DE446FCAA54EAAA75ED6330E24A0', 4, NULL, NULL, 2, NULL, 0, 'Add the mustard seeds and, when it sputters, add the asafetida.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDB2F636DFB3A045AEB90AB5ABA679CA', X'2D18DE446FCAA54EAAA75ED6330E24A0', X'2D18DE446FCAA54EAAA75ED6330E24A0', 5, NULL, NULL, 2, NULL, 0, 'Add the cumin-garlic-chili paste, stir-fry for a few seconds, then add the ginger, curry leaves, coriander leaves, and crushed, whole garlic. Stir-fry for a few seconds until the garlic starts to very slightly change color.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68AAED8E6767B7419360BECABAD41ABD', X'2D18DE446FCAA54EAAA75ED6330E24A0', X'2D18DE446FCAA54EAAA75ED6330E24A0', 6, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and stir-fry until they just start to turn mushy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B9B917ADFF6F346BCACF14733A6B18D', X'2D18DE446FCAA54EAAA75ED6330E24A0', X'2D18DE446FCAA54EAAA75ED6330E24A0', 7, NULL, NULL, 2, NULL, 0, 'Add the tamarind extract with 1 cup of water and bring to a boil. Add the rasam powder and continue to boil for another five minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64937E102C508B42BF6FE571128687E6', X'2D18DE446FCAA54EAAA75ED6330E24A0', X'2D18DE446FCAA54EAAA75ED6330E24A0', 8, NULL, NULL, 2, NULL, 0, 'Add the cooked dal/lentils to the saucepan with salt to taste. Bring the rasam to a rolling boil, reduce heat to medium, and let it boil for another five minutes. If it starts to get thick, add water. Rasam should have a very watery consistency, although if you prefer it thick, you can add less water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AE83ECE294FAA4BB192FC00609B43B4', X'2D18DE446FCAA54EAAA75ED6330E24A0', X'2D18DE446FCAA54EAAA75ED6330E24A0', 9, NULL, NULL, 2, NULL, 0, 'Check salt and add more if needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2CEB11045772D4EACEDC1FC07F5AA13', X'2D18DE446FCAA54EAAA75ED6330E24A0', X'2D18DE446FCAA54EAAA75ED6330E24A0', 10, NULL, NULL, 2, NULL, 0, 'Serve very hot over rice or in a glass or bowl.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D18DE81B89A2D438B9B0C002D4526BA', X'63084D35C4F68140B9A0C93459B81273', X'63084D35C4F68140B9A0C93459B81273', 0, NULL, NULL, 2, NULL, 0, 'Combine, wash and cook the lentils with the turmeric, preferably in a pressure cooker. If cooking on a stovetop, cover with an inch of water, bring to a boil, slap on a lid, keep on the lowest setting for the lentils to just simmer, and cook for 30-45 minutes or until the lentils are really soft and easily mushed up. Add more water during the cooking if necessary.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E63B009BD100E44196CCDB57660816B8', X'63084D35C4F68140B9A0C93459B81273', X'63084D35C4F68140B9A0C93459B81273', 1, NULL, NULL, 2, NULL, 0, 'In a saucepan, heat the oil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0290B8DAACDDE41995936088C570E8E', X'63084D35C4F68140B9A0C93459B81273', X'63084D35C4F68140B9A0C93459B81273', 2, NULL, NULL, 2, NULL, 0, 'Add the mustard seeds, red chillies and asafetida.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D827397932AB845AF206298BBD73BBF', X'63084D35C4F68140B9A0C93459B81273', X'63084D35C4F68140B9A0C93459B81273', 3, NULL, NULL, 2, NULL, 0, 'When the mustard seeds crackle and sputter, add the garlic and stir-fry until the garlic browns. Don''t let it burn-- stir constantly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D32EA0A248326488DA60D61A7E0ED9B', X'63084D35C4F68140B9A0C93459B81273', X'63084D35C4F68140B9A0C93459B81273', 4, NULL, NULL, 2, NULL, 0, 'Add the tomato and cook for a few minutes or until the tomato breaks down.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDBF6B471763C447BDEA8364A34830DA', X'63084D35C4F68140B9A0C93459B81273', X'63084D35C4F68140B9A0C93459B81273', 5, NULL, NULL, 2, NULL, 0, 'Add the lentils and water, if needed. You want the dal to be runny but not too watery. This is a matter of personal preference, though, so if you like your dals thick, feel free to use less water. Keep in mind that dals do thicken on standing.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91AF39A2530DDA4D81D70B1D9D4EE927', X'63084D35C4F68140B9A0C93459B81273', X'63084D35C4F68140B9A0C93459B81273', 6, NULL, NULL, 2, NULL, 0, 'Bring the lentils to a boil and let them cook for a few minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'569235964C49874DBEB030B79AA71EA7', X'63084D35C4F68140B9A0C93459B81273', X'63084D35C4F68140B9A0C93459B81273', 7, NULL, NULL, 2, NULL, 0, 'Add the sugar and salt and aamchur and stir to mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B3C82421896C148BB450D3D3D393786', X'63084D35C4F68140B9A0C93459B81273', X'63084D35C4F68140B9A0C93459B81273', 8, NULL, NULL, 2, NULL, 0, 'Add the coriander leaves, mix well, and turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'478A4BFF31CB0A46BD4597093C3495D5', X'63084D35C4F68140B9A0C93459B81273', X'63084D35C4F68140B9A0C93459B81273', 9, NULL, NULL, 2, NULL, 0, 'Serve hot with rice or parathas or roti.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80614423F2077846A54672CD5489DF2F', X'D61D05EB87B1544198509939733E9F04', X'D61D05EB87B1544198509939733E9F04', 0, NULL, NULL, 2, NULL, 0, 'Cook or pressure-cook the dal along with the ginger, cayenne or paprika and turmeric until tender and mushy. If you want a creamy dal, blend half the cooked dal into a smooth puree and add back to the remaining dal.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D12F15E95631444B922409D8C69E82D', X'D61D05EB87B1544198509939733E9F04', X'D61D05EB87B1544198509939733E9F04', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large saucepan');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24C3CDB30137D24188C54D78CD72705D', X'D61D05EB87B1544198509939733E9F04', X'D61D05EB87B1544198509939733E9F04', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic and stir quickly for about a minute. Add the tomatoes and cook until the puree darkens, about five minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A37BBFEBB0FF934D8CBB1AEF0EE94C45', X'D61D05EB87B1544198509939733E9F04', X'D61D05EB87B1544198509939733E9F04', 3, NULL, NULL, 2, NULL, 0, 'Add the cooked urad dal, garam masala powder (use more or less based on your preference and the brand you have) and the kasoori methi (crush it between your fingers as you add it to the pot). Stir to mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10D13C111B0B8144B63728BEDD2E11A3', X'D61D05EB87B1544198509939733E9F04', X'D61D05EB87B1544198509939733E9F04', 4, NULL, NULL, 2, NULL, 0, 'Add water or vegetable stock if the dal is too thick. Bring the dal to a boil and simmer over medium-low heat for 10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85D137F050090644A587252BACC4DD51', X'D61D05EB87B1544198509939733E9F04', X'D61D05EB87B1544198509939733E9F04', 5, NULL, NULL, 2, NULL, 0, 'Add salt to taste. Stir in the vegan butter and add chopped coriander leaves. Turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D63A8B96A840840B80DBDB2F89239E7', X'D61D05EB87B1544198509939733E9F04', X'D61D05EB87B1544198509939733E9F04', 6, NULL, NULL, 2, NULL, 0, 'Serve hot with rice or rotis.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44F564EF9904974FB2E2DC43921EB318', X'A4C347C80B81774AACB56EB9FC46C5A9', X'A4C347C80B81774AACB56EB9FC46C5A9', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil. Add the cumin seeds. When they darken slightly, add the ginger and garlic and saute for a few seconds, then add the coriander and cumin powders, tomato puree, kasoori methi, cayenne and turmeric.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'895B03D1C588854885CE3CA7FC74281D', X'A4C347C80B81774AACB56EB9FC46C5A9', X'A4C347C80B81774AACB56EB9FC46C5A9', 1, NULL, NULL, 2, NULL, 0, 'Stir and cook until the tomato puree darkens and most of the moisture has evaporated.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB2A8FBBE362764E8F774CBCC701E164', X'A4C347C80B81774AACB56EB9FC46C5A9', X'A4C347C80B81774AACB56EB9FC46C5A9', 2, NULL, NULL, 2, NULL, 0, 'Add the cooked dal and stir well to mix. If the dal is too thick, add water to make it more fluid-- you want it to be runny but still fairly thick.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1873E5F3035C4D4F8D1732D0EC098C62', X'A4C347C80B81774AACB56EB9FC46C5A9', X'A4C347C80B81774AACB56EB9FC46C5A9', 3, NULL, NULL, 2, NULL, 0, 'Bring the dal to a boil, lower the heat, and let it simmer for another 10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51BD19A34ABC004C9B840E78A966EC33', X'A4C347C80B81774AACB56EB9FC46C5A9', X'A4C347C80B81774AACB56EB9FC46C5A9', 4, NULL, NULL, 2, NULL, 0, 'Add the garam masala, stir in, and then add the vegan butter and salt to taste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3638A3DA55C2644BDA3B06ED5B50666', X'A4C347C80B81774AACB56EB9FC46C5A9', X'A4C347C80B81774AACB56EB9FC46C5A9', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle on the coriander or spring onions, turn off the heat, and serve hot with some rice or naan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A0DBA8F7D9EBE4B9715A94109B9D64A', X'28A76DA3D3A46848AE3D8408BD5F4327', X'28A76DA3D3A46848AE3D8408BD5F4327', 0, NULL, NULL, 2, NULL, 0, 'Mix the marinade ingredients with enough water to make a paste. Toss the tofu cubes in it to coat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9540653198E6D140A20CE780E83C2CAE', X'28A76DA3D3A46848AE3D8408BD5F4327', X'28A76DA3D3A46848AE3D8408BD5F4327', 1, NULL, NULL, 2, NULL, 0, 'Heat 2 tsp of the oil in a nonstick or cast-iron pan. Saute the tofu cubes on all sides until they are golden-brown. Remove to a plate.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3806A2EDD097604CA08FFE8958204AC6', X'28A76DA3D3A46848AE3D8408BD5F4327', X'28A76DA3D3A46848AE3D8408BD5F4327', 2, NULL, NULL, 2, NULL, 0, 'Heat the remaining 1 tsp of oil and add the cumin seeds. When they sputter, add the ginger and garlic and onion.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2192A119E4F3EC429E014E0D38DECD80', X'28A76DA3D3A46848AE3D8408BD5F4327', X'28A76DA3D3A46848AE3D8408BD5F4327', 3, NULL, NULL, 2, NULL, 0, 'Saute until the onion becomes transparent. Now add the coriander powder, black pepper, and minced green chilli. Toss to coat with the spices and then add the green pepper.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'064FDCF9F4718944A0AAD783AA26E720', X'28A76DA3D3A46848AE3D8408BD5F4327', X'28A76DA3D3A46848AE3D8408BD5F4327', 4, NULL, NULL, 2, NULL, 0, 'Stir-fry until the bell pepper starts to soften but still has a good bite to it. Now add the tofu cubes and toss them with the spices. Add the tamari or soy sauce and the cornflour mixed with water (this will give the tofu a nice glaze). Stir until well mixed and remove from the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C460A2889F5DD44B0021695E2F33C65', X'28A76DA3D3A46848AE3D8408BD5F4327', X'28A76DA3D3A46848AE3D8408BD5F4327', 5, NULL, NULL, 2, NULL, 0, 'Toss in the coriander and serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C3D1591613784409836B01BA3523667', X'F38381A49A85C645AB9D71B93974B817', X'F38381A49A85C645AB9D71B93974B817', 0, NULL, NULL, 2, NULL, 0, 'Heat oil for frying in a wok or fryer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C449E7EBD58B2548B975276C55B1B2D4', X'F38381A49A85C645AB9D71B93974B817', X'F38381A49A85C645AB9D71B93974B817', 1, NULL, NULL, 2, NULL, 0, 'Grate all of the veggies, including the onion, with a grater or better, in a food processor.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44EC12CA1B9478428E1E4854C1B1231B', X'F38381A49A85C645AB9D71B93974B817', X'F38381A49A85C645AB9D71B93974B817', 2, NULL, NULL, 2, NULL, 0, 'Place the veggies and the rest of the ingredients in the bowl and mix until a ball formed with the veggies holds together. You don''t want the balls to be doughy, but if absolutely necessary, add a little more flour if needed to bind.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E36DE7F15CC73B479BCBC98118745397', X'F38381A49A85C645AB9D71B93974B817', X'F38381A49A85C645AB9D71B93974B817', 3, NULL, NULL, 2, NULL, 0, 'Shape the mixture into ¾-inch balls (you should get about 32) and flatten each slightly before adding to the hot oil. You can fry several at a time but don''t overcrowd the pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D8F4EB5A332484FAA9B08C700A2A1BD', X'F38381A49A85C645AB9D71B93974B817', X'F38381A49A85C645AB9D71B93974B817', 4, NULL, NULL, 2, NULL, 0, 'Turn the manchurian balls over as needed so they are golden-brown on both sides. Remove them to a dish lined with paper towels and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A5ABBB872999C45BCC1174426F9D60C', X'E7DEC610C5C48A48ADCCABEE335E9482', X'E7DEC610C5C48A48ADCCABEE335E9482', 0, NULL, NULL, 2, NULL, 0, 'To make the sauce, place the wok on high heat and when it is very hot, pour the oil in along the sides of the pan.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7AD3B015FC604418111179A550A730E', X'E7DEC610C5C48A48ADCCABEE335E9482', X'E7DEC610C5C48A48ADCCABEE335E9482', 1, NULL, NULL, 2, NULL, 0, 'Add the garlic and stir-fry for just 10 seconds or so, then immediately add in the bell peppers and onion. Saute just a minute, then add the sauces one by one in the center of the wok--the ketchup, tamari, chili sauce, vinegar, maple syrup and powdered ginger. Mix together quickly and then add four cups of water to the wok. Bring to a boil and taste the sauce. If needed, add salt.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5097621CF6041942AD339C5EC7684CB4', X'E7DEC610C5C48A48ADCCABEE335E9482', X'E7DEC610C5C48A48ADCCABEE335E9482', 2, NULL, NULL, 2, NULL, 0, 'Mix the cornstarch with 4 tbsp water and add a little at a time until the sauce thickens. You don''t have to use all of it--stop when the sauce is thick enough for your liking. Cook the sauce until it comes to a boil and becomes glossy, 2-4 minutes. Add the manchurian balls. Turn off heat and season with scallions before serving.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D6348C9E9CFB149934068C6E66BA112', X'E7DEC610C5C48A48ADCCABEE335E9482', X'E7DEC610C5C48A48ADCCABEE335E9482', 3, NULL, NULL, 2, NULL, 0, 'Serve with fried rice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F7F75260DE9C74594ABD1CD5A4E016E', X'1B826F30BCF8794AA15574AE558822A2', X'1B826F30BCF8794AA15574AE558822A2', 0, NULL, NULL, 2, NULL, 0, 'You can either make the rice in the microwave or the stovetop. To make it in the microwave, place the drained rice in a microwave-safe bowl and add 3 cups water to it. Add salt and mix. Cook on high power for 9 minutes or until the rice is cooked about 90 percent--enough to mash it with some pressure, but not soft. Drain the rice thoroughly if there''s any water remaining. To cook this on the stovetop, bring a large pot of salted water to boil, the way you would for pasta. Add the drained rice and cook about 8-9 minutes, again until the rice is about 90 percent done. Drain thoroughly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEBE311B3BC69D47BC996AD06A433CB7', X'1B826F30BCF8794AA15574AE558822A2', X'1B826F30BCF8794AA15574AE558822A2', 1, NULL, NULL, 2, NULL, 0, 'Spread the rice on a baking sheet in as thin a layer as possible and set aside for at least 15-20 minutes or even more if possible. The rice should cool down thoroughly.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02477F20B4C6F9469D266DE602C5CAE7', X'1B826F30BCF8794AA15574AE558822A2', X'1B826F30BCF8794AA15574AE558822A2', 2, NULL, NULL, 2, NULL, 0, 'Heat the wok over very high heat--as high as your stove will go.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47C9896F07A569469B1A16925FB45BBF', X'1B826F30BCF8794AA15574AE558822A2', X'1B826F30BCF8794AA15574AE558822A2', 3, NULL, NULL, 2, NULL, 0, 'Pour in the vegetable oil along the sides of the pan so by the time it pools at the bottom it''s already hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1C63FB0F35DD74596784C5A2DF35CF7', X'1B826F30BCF8794AA15574AE558822A2', X'1B826F30BCF8794AA15574AE558822A2', 4, NULL, NULL, 2, NULL, 0, 'Immediately add the garlic, green chili peppers, the white part of the scallions, carrots and green bell peppers. Stir-fry rapidly for just about a minute, then add in the sauces--hot chili sauce, tamari, rice vinegar and liquid aminos. Mix them quickly and then add the rice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22031275CCCBB64A834C41E22F2740B2', X'1B826F30BCF8794AA15574AE558822A2', X'1B826F30BCF8794AA15574AE558822A2', 5, NULL, NULL, 2, NULL, 0, 'Stir-fry the rice, with the wok still on high heat, for a couple of minutes, moving it constantly so every grain of rice has an opportunity to make contact with the hot surface of the wok several times. After two to three minutes, taste the rice and add ground black pepper and more salt if needed. Mix thoroughly, turn off the heat, garnish with the green parts of the scallions and serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'908B4C8DBFE6AC41883A343BDED7D2A1', X'379C201B5116FD45AC02F8841FA33687', X'379C201B5116FD45AC02F8841FA33687', 0, NULL, NULL, 2, NULL, 0, 'In a bowl, whisk together the chickpea flour with the water, turmeric, cayenne and salt until there are no lumps.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'059567E14D90FF42BACA71B6749DF041', X'379C201B5116FD45AC02F8841FA33687', X'379C201B5116FD45AC02F8841FA33687', 1, NULL, NULL, 2, NULL, 0, 'Heat a non-stick or cast-iron wok or wide skillet. Coat the bottom with oil or cooking spray.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72A341E544809941A5E9EB27BADEF19A', X'379C201B5116FD45AC02F8841FA33687', X'379C201B5116FD45AC02F8841FA33687', 2, NULL, NULL, 2, NULL, 0, 'When the pan is hot, pour in the chickpea mixture. Cook until brown spots appear, then flip with a wide spatula and cook the other side for 2-3 minutes more until set.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6BE959DABA7A64283980B012F6B6962', X'379C201B5116FD45AC02F8841FA33687', X'379C201B5116FD45AC02F8841FA33687', 3, NULL, NULL, 2, NULL, 0, 'Remove to a plate and set aside while you make the rice. When cool enough to handle, roll the omelet and cut into very thin strips.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC97EEFD770F0A41B2FFAD9C23200B99', X'CBB73F63C1B46D419F4C77DF5E508B9B', X'CBB73F63C1B46D419F4C77DF5E508B9B', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in the same wok or skillet you made the omelet in. Add the garlic and green chilies and saute for a few seconds until the garlic gets fragrant.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A13D2C8D4C57F48B5C9FD9D2D419ABF', X'CBB73F63C1B46D419F4C77DF5E508B9B', X'CBB73F63C1B46D419F4C77DF5E508B9B', 1, NULL, NULL, 2, NULL, 0, 'Add the spring onions and saute for a minute, then add the rice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9360BD51B59F74A8FEB2B1259444BFD', X'CBB73F63C1B46D419F4C77DF5E508B9B', X'CBB73F63C1B46D419F4C77DF5E508B9B', 2, NULL, NULL, 2, NULL, 0, 'Stir-fry for 3-4 minutes over high heat. Add salt, stir, and cover with a lid and let the rice cook for 2 minutes, no more.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1144A0970A593E48BDAC158CC42606E1', X'CBB73F63C1B46D419F4C77DF5E508B9B', X'CBB73F63C1B46D419F4C77DF5E508B9B', 3, NULL, NULL, 2, NULL, 0, 'Remove the lid and add the shredded vegan `egg.` Mix well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D082A3A9B8A9A54DB30D191A60047FC7', X'CBB73F63C1B46D419F4C77DF5E508B9B', X'CBB73F63C1B46D419F4C77DF5E508B9B', 4, NULL, NULL, 2, NULL, 0, 'Serve hot with soy sauce and chilli vinegar on the side.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F303AD533CD08B409B094DDCF2312260', X'9C0B1CD5B23FFF49AD19D91C9DDBF427', X'9C0B1CD5B23FFF49AD19D91C9DDBF427', 0, NULL, NULL, 2, NULL, 0, 'Cook the rice, like pasta, in a large pot of salted boiling water, until about 80 percent done. The grains should still be al dente. This will help keep your rice from turning into mush when you stir-fry it. Spread the rice on a baking sheet in a thin layer to help any additional moisture evaporate.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CABFF25034DE9D45AF7F7AFEC044045D', X'9C0B1CD5B23FFF49AD19D91C9DDBF427', X'9C0B1CD5B23FFF49AD19D91C9DDBF427', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large wok and add the chopped garlic to it while it''s still cold. Cook the garlic, sauteing frequently, until it is golden brown and almost burnt. Remove three-fourths of the garlic from the oil using a slotted spoon and set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'947D91E215023841AFC63F6C6A176256', X'9C0B1CD5B23FFF49AD19D91C9DDBF427', X'9C0B1CD5B23FFF49AD19D91C9DDBF427', 2, NULL, NULL, 2, NULL, 0, 'Add the green bell pepper to the hot oil along with the baked tofu cubes, if using, and saute for a couple of minutes. Add the red pepper flakes, then add the cooked, cooled rice along with the soy sauce and rice vinegar. Use more or less of the red pepper and vinegar, according to your taste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B00A4EB9F3DCCD4A930DFFC3D4C56F67', X'9C0B1CD5B23FFF49AD19D91C9DDBF427', X'9C0B1CD5B23FFF49AD19D91C9DDBF427', 3, NULL, NULL, 2, NULL, 0, 'Stir-fry the rice in the wok over high heat, about five minutes. In the last couple of minutes of cooking, add the chopped spring onions and add salt if needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE0A76AEC83ED449BE08615F46FD0442', X'9C0B1CD5B23FFF49AD19D91C9DDBF427', X'9C0B1CD5B23FFF49AD19D91C9DDBF427', 4, NULL, NULL, 2, NULL, 0, 'Serve hot after sprinkling on the reserved garlic.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CF112AB4BE1EA48B63BD2A366FBF3F7', X'7707E225C3F47D479855E8763935E9A8', X'7707E225C3F47D479855E8763935E9A8', 0, NULL, NULL, 2, NULL, 0, 'Crush the ginger, garlic, cumin and green chile peppers into a paste using a mortar and pestle or a food processor.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'728066B2DB1B47469C70BE21730CA6DD', X'7707E225C3F47D479855E8763935E9A8', X'7707E225C3F47D479855E8763935E9A8', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil in a wide skillet or preferably a wok');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BC0337D080435489B778A38A92740E5', X'7707E225C3F47D479855E8763935E9A8', X'7707E225C3F47D479855E8763935E9A8', 2, NULL, NULL, 2, NULL, 0, 'Add the onions and saute, until golden spots appear. Add the ginger-garlic paste and continue to saute another minute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'385FD21EA4B1E040837D6E6F78D1BE39', X'7707E225C3F47D479855E8763935E9A8', X'7707E225C3F47D479855E8763935E9A8', 3, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and cook over high heat until they begin to darken. Add in the grated cauliflower, peas and bell peppers along with ½ cup water and salt. Cover and cook for five minutes or until the veggies are quite soft.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF8473DFC2BE8F418A1F95FF6072EC41', X'7707E225C3F47D479855E8763935E9A8', X'7707E225C3F47D479855E8763935E9A8', 4, NULL, NULL, 2, NULL, 0, 'Add in the boiled potatoes along with the pav bhaji masala, paprika if using, and about 1 ½ cups water or enough to make a thick, gravy-like consistency. You don''t want the bhaji to be runny, but it should not be too dry either.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4092202990C342469A313B15D4D13221', X'7707E225C3F47D479855E8763935E9A8', X'7707E225C3F47D479855E8763935E9A8', 5, NULL, NULL, 2, NULL, 0, 'Using a masher, mash down the veggies until most of them are broken down. After the bhaji comes to a boil, turn up the heat, and cook the veggies over high heat for five minutes, stirring constantly to ensure they don''t stick. This helps roast some of the veggies and gives your pav bhaji an authentic flavor, much as something from the streets would taste. If the veggies get too dry at any point, add more water. Remember, you want a scoopable, sludgy consistency.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2DE9C30D514284388BD6A763FE91F88', X'7707E225C3F47D479855E8763935E9A8', X'7707E225C3F47D479855E8763935E9A8', 6, NULL, NULL, 2, NULL, 0, 'You can add more pav bhaji masala if you like--go with your tastebuds, but remember it shouldn''t be too strongly flavored. Add more salt if needed before turning off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D72AD7FFC71B74F8819AC9E33D4E936', X'A365C87BF96D6C4584D84B19E09D550C', X'A365C87BF96D6C4584D84B19E09D550C', 0, NULL, NULL, 2, NULL, 0, 'Mix the sugar, ½ cup warm water and the yeast in a mixing bowl and set aside for about 5minutes until the mixture starts to froth, indicating the yeast is alive and well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3A9B1262E6F8542A9865763404762ED', X'A365C87BF96D6C4584D84B19E09D550C', X'A365C87BF96D6C4584D84B19E09D550C', 1, NULL, NULL, 2, NULL, 0, 'Sift the flour and baking soda into the bowl. Knead on low speed in a stand mixer or by hand for about 3 minutes, trickling in enough milk until you have a dough that’s smooth but slightly sticky (I needed ¾ to 1 cup of milk).');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64A67884B78C50469AD905133328BCA7', X'A365C87BF96D6C4584D84B19E09D550C', X'A365C87BF96D6C4584D84B19E09D550C', 2, NULL, NULL, 2, NULL, 0, 'Add the oil and continue to knead until the oil has been absorbed by the dough, about 1 more minute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDF135AE3B8972408FDFB7BC941F6862', X'A365C87BF96D6C4584D84B19E09D550C', X'A365C87BF96D6C4584D84B19E09D550C', 3, NULL, NULL, 2, NULL, 0, 'Now place in an oiled bowl, turning over once to coat all over with oil, cover with a kitchen towel, and set aside for 2 hours until the dough has risen.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F46A1288ADA8984CA104A7D68B623FD8', X'A365C87BF96D6C4584D84B19E09D550C', X'A365C87BF96D6C4584D84B19E09D550C', 4, NULL, NULL, 2, NULL, 0, 'Punch down the dough and divide into 12 balls');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A4C95371EEC534A98D956461D548BBE', X'A365C87BF96D6C4584D84B19E09D550C', X'A365C87BF96D6C4584D84B19E09D550C', 5, NULL, NULL, 2, NULL, 0, 'Shape them into a slightly rectangular shape by pulling at the sides of the dough and tucking under on all four sides.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E6C49244CCDA84AB84D30A7E124EC9C', X'A365C87BF96D6C4584D84B19E09D550C', X'A365C87BF96D6C4584D84B19E09D550C', 6, NULL, NULL, 2, NULL, 0, 'Place the tolls in a rectangular 9 X 13 inch baking dish smeared with oil and lightly floured, or on a cookie sheet, close enough but not touching each other. Let the rolls rise for 30 minutes. They will join at the ends when they have risen.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82BC0CC4FE8F95448419CCFBFB60D96D', X'A365C87BF96D6C4584D84B19E09D550C', X'A365C87BF96D6C4584D84B19E09D550C', 7, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 370 degrees. Brush the tops of the pavs with the a mixture of 1 tsp oil and 1 tsp nondairy milk mixture which gives them a nice color on top.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8EADB9B0B6F804E82FA740A75361E14', X'A365C87BF96D6C4584D84B19E09D550C', X'A365C87BF96D6C4584D84B19E09D550C', 8, NULL, NULL, 2, NULL, 0, 'Bake 22 minutes. Then turn off the oven and let the pav stand inside for another 4 miuntes before removing it from the oven and allowing it to cool for 10 minutes on a rack.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E345C243D7E58841BC03FD8F205D2E4B', X'E920F56D8248A24E9AA46581626088AB', X'E920F56D8248A24E9AA46581626088AB', 0, NULL, NULL, 2, NULL, 0, 'The pav needs to be toasted before serving. To do this, heat a griddle. Slit the pav into two, then smear on some butter and, if you like, sprinkle on some paprika or pav bhaji masala directly on the pav. Place, crumb-side down, on the hot skillet and let the pav toast a minute or so until golden-brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B6424C862CA394095804F030192E562', X'E920F56D8248A24E9AA46581626088AB', X'E920F56D8248A24E9AA46581626088AB', 1, NULL, NULL, 2, NULL, 0, 'Next ladle out the piping-hot bhaji on a plate or in a bowl. Add a square of vegan butter and then sprinkle the top with onions and cilantro and squeeze on some lemon juice. Serve with the toasted pav.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97CE055AC41AAA418CDA11874C4C5016', X'066D7BA6FE57F54BA7946F8868C3213F', X'066D7BA6FE57F54BA7946F8868C3213F', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a skillet and add the ginger-garlic paste. Saute over medium-low heat for a few seconds until fragrant but not burnt, then add the green chili peppers, turmeric, chaat masala, if using, white peas and salt. Add about two cups of the cooking liquid from the white peas, or water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E5D70F8CCFEA045994AC76C3D56703A', X'066D7BA6FE57F54BA7946F8868C3213F', X'066D7BA6FE57F54BA7946F8868C3213F', 1, NULL, NULL, 2, NULL, 0, 'Stir together, then bring the sauce to a boil, turn down the heat, and simmer for 10 minutes until the gravy is still a little runny but quite thick. You can add more water if needed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93BA9964D406FE4A861467D2BFCA4E2A', X'066D7BA6FE57F54BA7946F8868C3213F', X'066D7BA6FE57F54BA7946F8868C3213F', 2, NULL, NULL, 2, NULL, 0, 'Check salt, add more if needed, then turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F693F6CCA304EB4795ECFCABBD067A0E', X'0F15DEC4F73A9F4A8AA7BE62A53368D2', X'0F15DEC4F73A9F4A8AA7BE62A53368D2', 0, NULL, NULL, 2, NULL, 0, 'Place the potatoes and other ingredients and form into 12 flat patties, about 2 ½ inches in diameter and a third of an inch thick.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D331E41CFC50E046852B1B503597D39C', X'0F15DEC4F73A9F4A8AA7BE62A53368D2', X'0F15DEC4F73A9F4A8AA7BE62A53368D2', 1, NULL, NULL, 2, NULL, 0, 'Heat the skillet, pour enough oil to coat the bottom evenly. When hot, fry the patties in batches. Let each patty cook about 2-3 minutes on medium-high heat or until the surface is a rich golden-brown. Flip over and cook the other side.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'496EB98728B3F441899585E03194EA91', X'E2E462ACE2F51F4B82B4935FC1F72171', X'E2E462ACE2F51F4B82B4935FC1F72171', 0, NULL, NULL, 2, NULL, 0, 'Place all ingredients for the chutney in a blender, add ½ cup water, salt if needed (you may not want it) and blend into a smooth and fairly runny paste, about the consistency of ketchup.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C26CFC8DF3C0649B048A45A157EA336', X'43B5C1796989FD4D8DCE20F80B74F368', X'43B5C1796989FD4D8DCE20F80B74F368', 0, NULL, NULL, 2, NULL, 0, 'Place all the ingredients into a blender with ½ cup water and salt and blend into a runny paste. This chutney will be more liquid than the tamarind-date chutney.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'385D383DABE6AF4797AF9E1BA389555F', X'62EA8D30F8ED6842BC492768F0718162', X'62EA8D30F8ED6842BC492768F0718162', 0, NULL, NULL, 2, NULL, 0, 'To build you plate of Ragda Pattice, ladle the ragda into a plate. Place two patties on it. Sprinkle on some more chaat masala, if you wish. Drizzle on the tamarind-date and mint chutneys followed by onions, cilantro, and finally the sev.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04B3141C4ABB9749BDEFEA355F602D96', X'A836D97147B58141B94BEA61D28E715F', X'A836D97147B58141B94BEA61D28E715F', 0, NULL, NULL, 2, NULL, 0, 'Puree the spinach with ½ cup water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E02CF7CF50712F478AFFFCFA2F298892', X'A836D97147B58141B94BEA61D28E715F', X'A836D97147B58141B94BEA61D28E715F', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil. Add the onions and garlic and saute until the onion softens. Add the tomatoes, cayenne and garam masala and cook until the tomatoes are very soft and pulpy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57DFEE723FDB8141B89160B18AB63736', X'A836D97147B58141B94BEA61D28E715F', X'A836D97147B58141B94BEA61D28E715F', 2, NULL, NULL, 2, NULL, 0, 'Add the spinach puree and the marinated and baked tofu cubes. Add salt and cook over medium high heat until the sauce is very thick and coats the tofu. If there''s too much liquid in your sauce it''ll be difficult to put it in a wrap.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B44A1EF8E9BF3344B71C17F0F8A2C2CA', X'A836D97147B58141B94BEA61D28E715F', X'A836D97147B58141B94BEA61D28E715F', 3, NULL, NULL, 2, NULL, 0, 'Turn off the heat and let stand while you prep the wraps.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7727314D2FC354A8B5640A5AFF220A9', X'970D2A1519947F4FB24F5BAAE5873EB4', X'970D2A1519947F4FB24F5BAAE5873EB4', 0, NULL, NULL, 2, NULL, 0, 'Make the tofu `egg` wash by placing the tofu in the blender with the nondairy milk and tapioca starch. Blend until very smooth, then pour into a large plate.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5A0E841D63DC440981A451B509B08E1', X'970D2A1519947F4FB24F5BAAE5873EB4', X'970D2A1519947F4FB24F5BAAE5873EB4', 1, NULL, NULL, 2, NULL, 0, 'Add enough oil to coat the bottom of a skillet that''s large enough to hold the tortilla.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DC1B4308D23424CBB8E36886323BDAF', X'970D2A1519947F4FB24F5BAAE5873EB4', X'970D2A1519947F4FB24F5BAAE5873EB4', 2, NULL, NULL, 2, NULL, 0, 'Dip a tortilla into the `egg` wash and turn over to coat both sides with the wash.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3521322E1890884C8F332DE2C59AC166', X'970D2A1519947F4FB24F5BAAE5873EB4', X'970D2A1519947F4FB24F5BAAE5873EB4', 3, NULL, NULL, 2, NULL, 0, 'Place the tortilla into the hot skillet and let it cook until it is golden brown. Carefully turn over and cook the other side until golden brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46DCA99F36EC9E4F94314807AB086B3A', X'970D2A1519947F4FB24F5BAAE5873EB4', X'970D2A1519947F4FB24F5BAAE5873EB4', 4, NULL, NULL, 2, NULL, 0, 'Remove the tortilla to a plate and place a fourth of the tofu filling inside it along with any of the other optional fillings. Bring in both ends of the tortilla, then roll into a tight wrap.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4CCBBE60FA46041AB1C35732B58782F', X'970D2A1519947F4FB24F5BAAE5873EB4', X'970D2A1519947F4FB24F5BAAE5873EB4', 5, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90D9D202897570429D9970DCCFD98774', X'D2F5270FDB1EA244B1F3E6D956A35B81', X'D2F5270FDB1EA244B1F3E6D956A35B81', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil. Add the coriander seeds, cumin seeds, cloves, cinnamon and peppercorns. Saute for a minute or two until the coriander is slightly darker and fragrant. Then the sliced onions and a pinch of salt and saute, stirring, about 2-3 minutes. Add the garlic and ginger and continue to saute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'825204E9786CF341926D672014AAEDBD', X'D2F5270FDB1EA244B1F3E6D956A35B81', X'D2F5270FDB1EA244B1F3E6D956A35B81', 1, NULL, NULL, 2, NULL, 0, 'When the onions are beginning to get brown spots, remove the onions and spices to a blender.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACA022631B5CB44296A4F21564255664', X'D2F5270FDB1EA244B1F3E6D956A35B81', X'D2F5270FDB1EA244B1F3E6D956A35B81', 2, NULL, NULL, 2, NULL, 0, 'Add the coconut to the dry skillet and roast over a low heat until it starts to turn lightly golden. Add to the blender along with the tomatoes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C2FAC9CB9BAD74FAE98666454185F66', X'D2F5270FDB1EA244B1F3E6D956A35B81', X'D2F5270FDB1EA244B1F3E6D956A35B81', 3, NULL, NULL, 2, NULL, 0, 'Add enough water to make a smooth paste. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94007139D87A50478ADA482B1DB96AD6', X'D2F5270FDB1EA244B1F3E6D956A35B81', X'D2F5270FDB1EA244B1F3E6D956A35B81', 4, NULL, NULL, 2, NULL, 0, 'In a large saucepan, heat the other teaspoon of oil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CAC021ACEBACF40A550C96F76C86BA7', X'D2F5270FDB1EA244B1F3E6D956A35B81', X'D2F5270FDB1EA244B1F3E6D956A35B81', 5, NULL, NULL, 2, NULL, 0, 'Add the finely chopped onion and saute until it starts to brown. Add the turmeric and cayenne or paprika. Give everything a good stir.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA4CD0F10C20274994800C32BF0B952E', X'D2F5270FDB1EA244B1F3E6D956A35B81', X'D2F5270FDB1EA244B1F3E6D956A35B81', 6, NULL, NULL, 2, NULL, 0, 'Add the ground masala and bring it to a boil. Let the masala cook five minutes on a simmer.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5653FBAF70C67E4DA8F7929F743E2D94', X'D2F5270FDB1EA244B1F3E6D956A35B81', X'D2F5270FDB1EA244B1F3E6D956A35B81', 7, NULL, NULL, 2, NULL, 0, 'Add the sprouts and stir them in.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4F004137434034E86CC55CE30C3717D', X'D2F5270FDB1EA244B1F3E6D956A35B81', X'D2F5270FDB1EA244B1F3E6D956A35B81', 8, NULL, NULL, 2, NULL, 0, 'When the curry comes to a boil, lower the heat, cover, and let it simmer for about 20-30 minutes or until the beans are fully cooked but not mushy. Add salt to taste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D36C78CDCED75448916D567964057AC', X'D2F5270FDB1EA244B1F3E6D956A35B81', X'D2F5270FDB1EA244B1F3E6D956A35B81', 9, NULL, NULL, 2, NULL, 0, 'Turn off the heat and serve hot with the toppings, sliced or quartered lemons on the side, and bread.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDD4B3859F34FF488946EE677B5AF893', X'D2F5270FDB1EA244B1F3E6D956A35B81', X'D2F5270FDB1EA244B1F3E6D956A35B81', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF5CD705CD1CD1489658A9EBECD2A8DA', X'B84537D4336A5244A2F4183AA6289844', X'B84537D4336A5244A2F4183AA6289844', 0, NULL, NULL, 2, NULL, 0, 'Roast the chickpea flour lightly, stirring, until it smells fragrant, about 5 minutes on medium-low heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7FAE71B8594EB46BB4F07DEE610E537', X'B84537D4336A5244A2F4183AA6289844', X'B84537D4336A5244A2F4183AA6289844', 1, NULL, NULL, 2, NULL, 0, 'Spray oil in a skillet. Add asafetida and sustard seeds. When the seeds sputter, add the onions. Saute until the onions turn translucent but before they start to color.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E10534DD940C4C43886118E19F55B187', X'B84537D4336A5244A2F4183AA6289844', X'B84537D4336A5244A2F4183AA6289844', 2, NULL, NULL, 2, NULL, 0, 'Add the chilli powder, turmeric, spring onions and chopped green peppers. Saute for a few minutes until the vegetables start to soften.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5193391050115D4F9B1C47980384499F', X'B84537D4336A5244A2F4183AA6289844', X'B84537D4336A5244A2F4183AA6289844', 3, NULL, NULL, 2, NULL, 0, 'Add the chickpea flour and salt and stir in. Add 1 cup of water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54F9F3E9AB9A6D43B0AE7CE0BC56A2AD', X'B84537D4336A5244A2F4183AA6289844', X'B84537D4336A5244A2F4183AA6289844', 4, NULL, NULL, 2, NULL, 0, 'Stir well, cover, and cook on medium-low heat about 7-8 minutes, stirring occasionally, until all the water has been absorbed.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C77C45A8B02F74CBC3BAE571D5053CA', X'B84537D4336A5244A2F4183AA6289844', X'B84537D4336A5244A2F4183AA6289844', 5, NULL, NULL, 2, NULL, 0, 'Turn off heat. Garnish with the garlic greens or coriander leaves. Serve hot with bhakri (recipe follows).');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AA01DD1D949AA4CA64B1428AF89B0F6', X'D71A6B612E02BA4AA25762D997083013', X'D71A6B612E02BA4AA25762D997083013', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour and salt in a bowl. Add water, a little at a time, kneading the flour until the dough comes together in a ball.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23A7B24A17545A44BF258AB09346D4CA', X'D71A6B612E02BA4AA25762D997083013', X'D71A6B612E02BA4AA25762D997083013', 1, NULL, NULL, 2, NULL, 0, 'Pull off a lemon-size piece of the dough. Sprinkle the rolling surface liberally with all-purpose or wheat flour and roll out the bhakri carefully into a circle about 4-5 inches in diameter.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D7395582147D34290EFA745DD62A2A8', X'D71A6B612E02BA4AA25762D997083013', X'D71A6B612E02BA4AA25762D997083013', 2, NULL, NULL, 2, NULL, 0, 'These bhakris are extremely difficult to roll, so it might take some time to get it right. I sometimes substitute half the millet flour with regular whole-wheat flour which makes them easier to roll.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3EB119556CFF9458A7417941FB1A878', X'D71A6B612E02BA4AA25762D997083013', X'D71A6B612E02BA4AA25762D997083013', 3, NULL, NULL, 2, NULL, 0, 'Heat a cast-iron or other griddle. Place the bhakri on it and leave alone until the surface starts to turn opaque. Flip over. Smear a little oil over the bhakri. Cook both sides until golden-brown spots appear.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEC5F029CC41184D8655BC1AF0B7CE03', X'D71A6B612E02BA4AA25762D997083013', X'D71A6B612E02BA4AA25762D997083013', 4, NULL, NULL, 2, NULL, 0, 'Serve hot with the zunka, and some raw, chopped onions.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D181FD0FF0A8B408BFE2A73E890EBA6', X'ABFF089E96486F42A86D5412FAA8FBC3', X'ABFF089E96486F42A86D5412FAA8FBC3', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil and add the turmeric and the asafetida.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A8E4C4286EE404BBE33E6EAC6D0D124', X'ABFF089E96486F42A86D5412FAA8FBC3', X'ABFF089E96486F42A86D5412FAA8FBC3', 1, NULL, NULL, 2, NULL, 0, 'Now add the ginger, garlic and chilli pastes and saute just a few seconds. Add the potatoes and salt, mix well, and take off the heat. Mix in the lemon juice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D3A05AA6E7B2145A86621DC72791E15', X'ABFF089E96486F42A86D5412FAA8FBC3', X'ABFF089E96486F42A86D5412FAA8FBC3', 2, NULL, NULL, 2, NULL, 0, 'Allow the mixture to cool before you handle it.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC23E14628EB684B9558DA1960E47632', X'ABFF089E96486F42A86D5412FAA8FBC3', X'ABFF089E96486F42A86D5412FAA8FBC3', 3, NULL, NULL, 2, NULL, 0, 'Take the chickpea-chilli mixture and add enough water to make a fairly thick batter, about the consistency of pancake batter.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E069CD24409F7040AD044FC17263FC3F', X'ABFF089E96486F42A86D5412FAA8FBC3', X'ABFF089E96486F42A86D5412FAA8FBC3', 4, NULL, NULL, 2, NULL, 0, 'Make balls with the potato mixture, about 1 inch in diameter. Dunk one at a time into the chickpea batter. Turn to coat and then drop into the oil which should be at between 350 and 375 degrees.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'533456106AF2084E85A574F960965550', X'ABFF089E96486F42A86D5412FAA8FBC3', X'ABFF089E96486F42A86D5412FAA8FBC3', 5, NULL, NULL, 2, NULL, 0, 'Fry the vadas on all sides until they turn reddish-brown. Don''t overcrowd the pan. Remove to paper towels and drain.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10B60EB5F7ED4A44A94B51EE8D0861C8', X'B3599E58B0447148B48019A8F3738E93', X'B3599E58B0447148B48019A8F3738E93', 0, NULL, NULL, 2, NULL, 0, 'One by one roast half the garlic cloves and the rest of the ingredients except the tamarind and salt, allowing everything to turn lightly golden brown and putting each into a plate before moving on to the next ingredient. Be very careful roasting the coconut because it will brown very fast.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1BDE1E9624EF943888E52EBC639867E', X'B3599E58B0447148B48019A8F3738E93', X'B3599E58B0447148B48019A8F3738E93', 1, NULL, NULL, 2, NULL, 0, 'Place all the ingredients including the unroasted garlic cloves, the tamarind, salt and 1 tbsp oil in a food processor.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1A97E02C4BC7B4DA254EAA4E47120BC', X'B3599E58B0447148B48019A8F3738E93', X'B3599E58B0447148B48019A8F3738E93', 2, NULL, NULL, 2, NULL, 0, 'Process until everything breaks down into a coarse powder.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'513B86C192327846AE1450F24C3D1D67', X'F371DDCE0B0AE34BBF6D08391B70974A', X'F371DDCE0B0AE34BBF6D08391B70974A', 0, NULL, NULL, 2, NULL, 0, 'Mix the sugar, ½ cup warm water and the yeast in a mixing bowl and set aside for about 10 minutes until the mixture starts to froth, indicating the yeast is alive and well.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'278DE3C9A769B847993B272F729E75E7', X'F371DDCE0B0AE34BBF6D08391B70974A', X'F371DDCE0B0AE34BBF6D08391B70974A', 1, NULL, NULL, 2, NULL, 0, 'Sift all the flours and baking soda into the bowl. Knead on low speed in a stand mixer or by hand for about 3 minutes, trickling in 1 cup of water until you have a dough that''s smooth but slightly sticky');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC31024277F7CA418957B3EF030C8448', X'F371DDCE0B0AE34BBF6D08391B70974A', X'F371DDCE0B0AE34BBF6D08391B70974A', 2, NULL, NULL, 2, NULL, 0, 'Add the oil and continue to knead until the oil has been absorbed by the dough, about 1 more minute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BFB10A77DCFF8468B5146792DA60137', X'F371DDCE0B0AE34BBF6D08391B70974A', X'F371DDCE0B0AE34BBF6D08391B70974A', 3, NULL, NULL, 2, NULL, 0, 'Now place in an oiled bowl, turning over once to coat all over with oil, cover with a kitchen towel, and set aside for 2 hours until the dough has risen.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A152F93DDFF815469FBACC82764950A8', X'F371DDCE0B0AE34BBF6D08391B70974A', X'F371DDCE0B0AE34BBF6D08391B70974A', 4, NULL, NULL, 2, NULL, 0, 'Punch down the dough and divide into 9 or 12 balls, depending on how large or small you want your pav.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCB0795D43DA19429EE654E0A99C33F5', X'F371DDCE0B0AE34BBF6D08391B70974A', X'F371DDCE0B0AE34BBF6D08391B70974A', 5, NULL, NULL, 2, NULL, 0, 'Shape them into a slightly rectangular shape by pulling at the sides of the dough and tucking under on all four sides.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'313BC2F007CA4C428F338137771A5BD5', X'F371DDCE0B0AE34BBF6D08391B70974A', X'F371DDCE0B0AE34BBF6D08391B70974A', 6, NULL, NULL, 2, NULL, 0, 'Place the tolls in a rectangular 9 X 13 inch baking dish smeared with oil and lightly floured, or on a cookie sheet, close enough but not touching each other. Let the rolls rise for an hour. They will join at the ends when they have risen, forming a slab, or laadi in Marathi');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DA2E494E0B24D4BA3F3ED2586533DCE', X'F371DDCE0B0AE34BBF6D08391B70974A', X'F371DDCE0B0AE34BBF6D08391B70974A', 7, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 370 degrees. Place the pav in the oven and bake 23 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2AD59EEA6CA074B97C291C1759E675E', X'F371DDCE0B0AE34BBF6D08391B70974A', X'F371DDCE0B0AE34BBF6D08391B70974A', 8, NULL, NULL, 2, NULL, 0, 'Brush the tops with a little oil, if desired, for a pretty, glossy look.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA9A1DA367CEFE4C80333550BF6BDD2A', X'F371DDCE0B0AE34BBF6D08391B70974A', X'F371DDCE0B0AE34BBF6D08391B70974A', 9, NULL, NULL, 2, NULL, 0, 'Remove to a rack and allow the rolls to cool before breaking them off.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6289BD642B03AB40AF85876ABCF8F1F3', X'01EA09ADFC3B5C489B102948EDE21311', X'01EA09ADFC3B5C489B102948EDE21311', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in the wok.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2172802A760EF545AD1645B7E4184A42', X'01EA09ADFC3B5C489B102948EDE21311', X'01EA09ADFC3B5C489B102948EDE21311', 1, NULL, NULL, 2, NULL, 0, 'Add the onions and, working constantly over the highest heat setting on your burner, stir-fry them until they start to just change color.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'552D6D70BC9E2D4FBDC5D8400A23592C', X'01EA09ADFC3B5C489B102948EDE21311', X'01EA09ADFC3B5C489B102948EDE21311', 2, NULL, NULL, 2, NULL, 0, 'Add the ginger and green peppers and stir-fry for another couple of minutes or until the bell peppers begin to soften.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C07097A8DBBDE438E183CED382A4557', X'01EA09ADFC3B5C489B102948EDE21311', X'01EA09ADFC3B5C489B102948EDE21311', 3, NULL, NULL, 2, NULL, 0, 'Add the cayenne, turmeric, and pav bhaji masala and stir well. Add the chopped tomatoes or tomato puree and cook, stirring frequently, about two minutes. Add the green peas and potatoes and stir-fry for a minute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9307518FAB109B45B07CC480A325A4C9', X'01EA09ADFC3B5C489B102948EDE21311', X'01EA09ADFC3B5C489B102948EDE21311', 4, NULL, NULL, 2, NULL, 0, 'Add the rice and stir-fry, mixing everything thoroughly, another two to three minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFE8EFB8E48B3F44B0BE0A3FFED094A4', X'01EA09ADFC3B5C489B102948EDE21311', X'01EA09ADFC3B5C489B102948EDE21311', 5, NULL, NULL, 2, NULL, 0, 'Turn off heat, add the lemon juice and garnish with coriander leaves or spring onions, or both.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7410959113981E43BE5632AE4BF02AE4', X'01EA09ADFC3B5C489B102948EDE21311', X'01EA09ADFC3B5C489B102948EDE21311', 6, NULL, NULL, 2, NULL, 0, 'Serve piping hot.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D7F73E47436504BBDF2133003637F6B', X'F3A86FB4577B9A48B48A6A1168796D82', X'F3A86FB4577B9A48B48A6A1168796D82', 0, NULL, NULL, 2, NULL, 0, 'After putting the pasta water to boil, place the cashews in a blender with ½ cup water and blend into a very smooth paste. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31F9BB15DC663C49AB519AC7F3D4FD10', X'F3A86FB4577B9A48B48A6A1168796D82', X'F3A86FB4577B9A48B48A6A1168796D82', 1, NULL, NULL, 2, NULL, 0, 'Place the tomatoes or puree in a blender along with the tomato paste, paprika, peppers and roasted cumin. Blend into a coarse paste.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CD5AF7D70886B4F900FFCB524174303', X'F3A86FB4577B9A48B48A6A1168796D82', X'F3A86FB4577B9A48B48A6A1168796D82', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large wok or skillet. Working on a medium-high flame, add the garlic, stir-fry for a few seconds, then add the blended tomato masala and saute fora couple of minutes. Add the pasta, diced green bell peppers, oregano, vegetable oil, and the powdered spices--the masala magic and the curry powder. Season with salt and, if you like even more spice, some ground black pepper.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4B6E0400D7D5F4AA84B61F2541EFF8F', X'F3A86FB4577B9A48B48A6A1168796D82', X'F3A86FB4577B9A48B48A6A1168796D82', 3, NULL, NULL, 2, NULL, 0, 'Stir-fry the pasta, moving it constantly, to ensure the sauce cooks evenly and the pasta doesn''t stick to the pan. You can do this for about five to 10 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D69F0DCC6A19C54B93D00CE27F3B0B3D', X'F3A86FB4577B9A48B48A6A1168796D82', X'F3A86FB4577B9A48B48A6A1168796D82', 4, NULL, NULL, 2, NULL, 0, 'Finish off by adding half the cashew cream, and mixing the pasta one last time. Serve with more cashew cream drizzled on top. You can also garnish with scallions or with vegan cheese, like cashew parmesan (I''ll link that recipe in the notes)');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D55AE21101A5742B11A69788DC777B9', X'7FAEBC7907BC1244B7562E5E3E69082F', X'7FAEBC7907BC1244B7562E5E3E69082F', 0, NULL, NULL, 2, NULL, 0, 'Place all the ingredients except the water in a food processor, bowl of a stand mixer, or, if kneading by hand, in a large bowl. Drizzle in the water and knead until you have an elastic but firm dough. Place in an airtight container and set it aside while you make the chana masala.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE5C67D9325FBF4F8329262FA01F9540', X'AD018F3A6CBC074AADD6532ECAD58176', X'AD018F3A6CBC074AADD6532ECAD58176', 0, NULL, NULL, 2, NULL, 0, 'Heat the oil in a saucepan and add the whole spices -- the bay leaf, cardamom, cinnamon and cloves. Finally add the cumin and stir to mix. When the cumin sizzles, add the onions with some salt and cook until the onions turn golden-brown.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'381D40D9C8D85247A7F8925C909DE3DD', X'AD018F3A6CBC074AADD6532ECAD58176', X'AD018F3A6CBC074AADD6532ECAD58176', 1, NULL, NULL, 2, NULL, 0, 'Add the ginger-garlic paste and saute for a minute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B184D51DF48EF4490D118A06D2EA088', X'AD018F3A6CBC074AADD6532ECAD58176', X'AD018F3A6CBC074AADD6532ECAD58176', 2, NULL, NULL, 2, NULL, 0, 'Add the cayenne and turmeric, saute for another minute, then add the diced tomatoes. Mix well and let the tomatoes cook, stirring them frequently, until they are completely broken down and very pulpy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28F063AD384DF745A0E2D4F40EBFC467', X'AD018F3A6CBC074AADD6532ECAD58176', X'AD018F3A6CBC074AADD6532ECAD58176', 3, NULL, NULL, 2, NULL, 0, 'Add the chickpeas along with 2 cups of water and stir well. Add the chana masala spice mix. and stir it in.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D684D098DFFAEA49B5E92BE0CFCF8EFC', X'AD018F3A6CBC074AADD6532ECAD58176', X'AD018F3A6CBC074AADD6532ECAD58176', 4, NULL, NULL, 2, NULL, 0, 'Bring to a boil, turn heat to low, cover and simmer about 15-20 minutes so all the flavors merge. You can use a potato masher to mash some of the chickpeas and thicken the sauce. If the chana masala looks too dry, add more water.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63DD2C341AB49140B51C560C7E92478F', X'AD018F3A6CBC074AADD6532ECAD58176', X'AD018F3A6CBC074AADD6532ECAD58176', 5, NULL, NULL, 2, NULL, 0, 'Squeeze in the lemon juice and garnish with coriander leaves, if using.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E19D6E61B9E744B82FFF088A2395A0B', X'C038D72ED9367A4A8EC3E69F22C8712C', X'C038D72ED9367A4A8EC3E69F22C8712C', 0, NULL, NULL, 2, NULL, 0, 'Heat oil for frying in a pan large enough to hold the bhatura. Divide the dough into 12 pieces and roll each into a ball. Roll out into a circle about six to eight inches in diameter. Make sure you roll the dough evenly, without any spots that are too thick or too thin.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EAABFC70DD8584CA616E5B1CACA07E2', X'C038D72ED9367A4A8EC3E69F22C8712C', X'C038D72ED9367A4A8EC3E69F22C8712C', 1, NULL, NULL, 2, NULL, 0, 'When the oil is hot, about 360 degrees, place a bhatura into the pan, taking care not to splash the oil on yourself. Use a spatula to press the bhatura down as it puffs up -- this will ensure it cooks evenly and puffs into a ball.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E16EAD9DC7D3A448A187FA44D42863F5', X'C038D72ED9367A4A8EC3E69F22C8712C', X'C038D72ED9367A4A8EC3E69F22C8712C', 2, NULL, NULL, 2, NULL, 0, 'Remove the bhatura to a colander lined with paper towels. Serve hot with the chana masala.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8293806314C9ED47AAB3A12BCD502AB0', X'7A35DD4DA81F024CBCC9B342F129B4AB', X'7A35DD4DA81F024CBCC9B342F129B4AB', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3AF145ECF97584BA8093E2ED2092244', X'7A35DD4DA81F024CBCC9B342F129B4AB', X'7A35DD4DA81F024CBCC9B342F129B4AB', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, toss the chickpeas with the oil, chaat masala, garam masala, cayenne or paprika, turmeric, and salt.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65D0D62D4462254A8B7727CD19E6BEA7', X'7A35DD4DA81F024CBCC9B342F129B4AB', X'7A35DD4DA81F024CBCC9B342F129B4AB', 2, NULL, NULL, 2, NULL, 0, 'Spread the chickpeas in a single layer on a baking sheet.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE6030A02DB2394EB6BE6F612BF7DDA0', X'7A35DD4DA81F024CBCC9B342F129B4AB', X'7A35DD4DA81F024CBCC9B342F129B4AB', 3, NULL, NULL, 2, NULL, 0, 'Place the baking sheet in the oven and let the chickpeas roast 30-45 minutes, until they are crisp. Oven temperatures vary, so if they are not crispy at the end of 45 minutes, give them some more time.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25581CB5DCA5C44C93C939F0C1E5F2C1', X'7A35DD4DA81F024CBCC9B342F129B4AB', X'7A35DD4DA81F024CBCC9B342F129B4AB', 4, NULL, NULL, 2, NULL, 0, 'Remove the baking sheet from the oven, place it on a rack and let the chickpeas cool about 10-15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CC5D2A6E0ACA741A0603B89F354DD94', X'7A35DD4DA81F024CBCC9B342F129B4AB', X'7A35DD4DA81F024CBCC9B342F129B4AB', 5, NULL, NULL, 2, NULL, 0, 'Toss the chickpeas with the spring onions, coriander leaves and lemon and sprinkle with some more chaat masala, if you wish. Some people add diced tomatoes, but I prefer not to because they tend to make the chickpeas soggy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2947CC26F7B81A489665265F1C758D00', X'7A35DD4DA81F024CBCC9B342F129B4AB', X'7A35DD4DA81F024CBCC9B342F129B4AB', 6, NULL, NULL, 2, NULL, 0, 'Eat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B74B919EA16F440AF90BF57945E5647', X'042C22F55FCABF4294A17E4FFFE3CBDF', X'042C22F55FCABF4294A17E4FFFE3CBDF', 0, NULL, NULL, 2, NULL, 0, 'Blend the cashews with the soaking water, cumin, green chili pepper, ginger, salt, and lime juice until you have a very smooth paste. This by itself makes a great dressing for any salad.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DF9930B1725474098BF81645B7A717C', X'042C22F55FCABF4294A17E4FFFE3CBDF', X'042C22F55FCABF4294A17E4FFFE3CBDF', 1, NULL, NULL, 2, NULL, 0, 'Remove the cashew paste to a bowl and mix with the grated cucumber and onion, if using. Sprinkle on some cayenne, if you wish.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BE5E247C27EE34D9474CF4767D6DC3F', X'042C22F55FCABF4294A17E4FFFE3CBDF', X'042C22F55FCABF4294A17E4FFFE3CBDF', 2, NULL, NULL, 2, NULL, 0, 'Serve on the side with any spicy Indian dish like a biryani or a curry or a paratha.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EF013434B96ED45AC53A653D75B324B', X'4BCDEB851E110A41A88513C171B16185', X'4BCDEB851E110A41A88513C171B16185', 0, NULL, NULL, 2, NULL, 0, 'In a skillet, heat the oil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3486E0721731348903E2963E6C05FF9', X'4BCDEB851E110A41A88513C171B16185', X'4BCDEB851E110A41A88513C171B16185', 1, NULL, NULL, 2, NULL, 0, 'Add the mustard seeds and blackgram dal. When the seeds sputter and the dal turns');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0F41FFAD574EE40A7FA2F7634148E6A', X'4BCDEB851E110A41A88513C171B16185', X'4BCDEB851E110A41A88513C171B16185', 2, NULL, NULL, 2, NULL, 0, 'lightly golden, add the onions and green chilies and stir for a minute.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC65AB37C445CE4196F14ABE51FFA88D', X'4BCDEB851E110A41A88513C171B16185', X'4BCDEB851E110A41A88513C171B16185', 3, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, the sambar powder, the turmeric, and the curry leaves.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B9004977E29C442AF21FF4C6F46FD54', X'4BCDEB851E110A41A88513C171B16185', X'4BCDEB851E110A41A88513C171B16185', 4, NULL, NULL, 2, NULL, 0, 'Stir well. The tomatoes will almost immediately start expressing their juices. Allow them to cook on medium-low heat for as long as it takes for most--but not all-- of the juices to evaporate. The tomatoes should have broken down completely.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4360CE16255ACA4081B51F74173FD0FB', X'4BCDEB851E110A41A88513C171B16185', X'4BCDEB851E110A41A88513C171B16185', 5, NULL, NULL, 2, NULL, 0, 'Add the jaggery and salt to taste. Cook for another minute, stirring.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7528F3FC1228514B9F959F904C2EB320', X'4BCDEB851E110A41A88513C171B16185', X'4BCDEB851E110A41A88513C171B16185', 6, NULL, NULL, 2, NULL, 0, 'Add the coriander and turn off the heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90413DC68F041842BE2BE0149F353B93', X'4BCDEB851E110A41A88513C171B16185', X'4BCDEB851E110A41A88513C171B16185', 7, NULL, NULL, 2, NULL, 0, 'Enjoy hot with some chapatis or rotis or with sambar and rice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'806D987B24A6424DAF0747575FFF9E2C', X'31E4FA31E50EE44E90B67843A7B8B216', X'31E4FA31E50EE44E90B67843A7B8B216', 0, NULL, NULL, 2, NULL, 0, 'Heat 1 tsp of oil in a skillet. Add the shallots and the red chili peppers and a pinch of salt. Saute over medium-high heat, stirring frequently, until the onions are caramelized.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E5E4E7DDE4D81468FD4CF62642D8378', X'31E4FA31E50EE44E90B67843A7B8B216', X'31E4FA31E50EE44E90B67843A7B8B216', 1, NULL, NULL, 2, NULL, 0, 'Add the tamarind and continue to saute for another couple of minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6656543F354A5C479AD265A6657429B8', X'31E4FA31E50EE44E90B67843A7B8B216', X'31E4FA31E50EE44E90B67843A7B8B216', 2, NULL, NULL, 2, NULL, 0, 'Remove the onions to a blender and add ¼ cup of water and season with salt as needed. Blend into a smooth paste. If you need to add more water to blend, add no more than a teaspoon at a time. Remove the chutney to a bowl.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74A2E46EAA66AA4E8E8431D11F75286E', X'31E4FA31E50EE44E90B67843A7B8B216', X'31E4FA31E50EE44E90B67843A7B8B216', 3, NULL, NULL, 2, NULL, 0, 'In the same skillet used to caramelize the onions, add the remaining ½ tsp of oil. Add the mustard seeds and, when they sputter, add the udal dal and curry leaves. Stir-fry for a few more seconds until the dal turns lightly golden.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'803E9A4A3BAFF44E928E5FC3271EEED2', X'31E4FA31E50EE44E90B67843A7B8B216', X'31E4FA31E50EE44E90B67843A7B8B216', 4, NULL, NULL, 2, NULL, 0, 'Pour the mustard-udad dal mixture over the chutney and mix.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'214352FECB484648BB12980D0792E147', X'E7A9A8E4FF7800438CBEA9CF88310A9E', X'E7A9A8E4FF7800438CBEA9CF88310A9E', 0, NULL, NULL, 2, NULL, 0, 'Roast the peanuts in a small saucepan for 2-3 minutes or until the skins are slightly darker. Set aside.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25188851DA1E9D4A9A847E508226094E', X'E7A9A8E4FF7800438CBEA9CF88310A9E', X'E7A9A8E4FF7800438CBEA9CF88310A9E', 1, NULL, NULL, 2, NULL, 0, 'In the saucepan, add the oil, heat, and then add the onions with a pinch of salt.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57A8C44DF23C1443A626ABB951407557', X'E7A9A8E4FF7800438CBEA9CF88310A9E', X'E7A9A8E4FF7800438CBEA9CF88310A9E', 2, NULL, NULL, 2, NULL, 0, 'Cook until the onions are softened, about five minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B607903AAEB4243BFD4A28E7903982C', X'E7A9A8E4FF7800438CBEA9CF88310A9E', X'E7A9A8E4FF7800438CBEA9CF88310A9E', 3, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, sugar, curry powder, paprika and cayenne. Cook, stirring frequently, until the tomatoes have reduced and are darker and most of the liquid is gone.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C14DF6CF0B82FC49B32D5F78441E4B34', X'E7A9A8E4FF7800438CBEA9CF88310A9E', X'E7A9A8E4FF7800438CBEA9CF88310A9E', 4, NULL, NULL, 2, NULL, 0, 'Place the peanuts and red peppers with the tomatoes and onions in a food processor. Blend. I like my chutney quite smooth, but leave it a little coarse if you''d like texture.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C03905180E1A5439AD07D50EB234EEC', X'E7A9A8E4FF7800438CBEA9CF88310A9E', X'E7A9A8E4FF7800438CBEA9CF88310A9E', 5, NULL, NULL, 2, NULL, 0, 'Serve with dosa or use as a dip. Perfect either way.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5728901BFE0F4E4DB01EA3814AC3A8D0', X'74CBA422CFE9AE43983616F6BB15D7D1', X'74CBA422CFE9AE43983616F6BB15D7D1', 0, NULL, NULL, 2, NULL, 0, 'Place all the ingredients in a blender and blend until smooth and creamy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D196C091AE2824B976FE899EE2121F8', X'74CBA422CFE9AE43983616F6BB15D7D1', X'74CBA422CFE9AE43983616F6BB15D7D1', 1, NULL, NULL, 2, NULL, 0, 'Pour into tall glasses over ice.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFE43027A312BB4DA23BEE9693BB0F6E', X'74CBA422CFE9AE43983616F6BB15D7D1', X'74CBA422CFE9AE43983616F6BB15D7D1', 2, NULL, NULL, 2, NULL, 0, 'Garnish with chopped pistachios or mint.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFA2F43318112044AC340F694DA3EB8D', X'74CBA422CFE9AE43983616F6BB15D7D1', X'74CBA422CFE9AE43983616F6BB15D7D1', 3, NULL, NULL, 2, NULL, 0, 'Yum.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9696E8BF60CF044EAE766E115790752D', X'712DDFCE76862B4298EC763EDC1AAE23', X'712DDFCE76862B4298EC763EDC1AAE23', 0, NULL, NULL, 2, NULL, 0, 'In a small saucepan, bring the water, cardamom, ginger and sugar to a boil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'219DF179B9E9B34D8B7F9D94DE5CE4D5', X'712DDFCE76862B4298EC763EDC1AAE23', X'712DDFCE76862B4298EC763EDC1AAE23', 1, NULL, NULL, 2, NULL, 0, 'Add the tea leaves and continue at a rolling boil for at least two more minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59BE1DFB0977AA46B1AC23FF7FFC7E6C', X'712DDFCE76862B4298EC763EDC1AAE23', X'712DDFCE76862B4298EC763EDC1AAE23', 2, NULL, NULL, 2, NULL, 0, 'Turn off the heat and immediately add the soymilk or creamer at room temperature');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E514C57BDC0BD43869BB5FAF0AB4C01', X'712DDFCE76862B4298EC763EDC1AAE23', X'712DDFCE76862B4298EC763EDC1AAE23', 3, NULL, NULL, 2, NULL, 0, 'Cover the saucepan, let everything steep together for a minute or two, then strain into little glass tumblers.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A12ADE0F2B50047829D0A1B5773E948', X'712DDFCE76862B4298EC763EDC1AAE23', X'712DDFCE76862B4298EC763EDC1AAE23', 4, NULL, NULL, 2, NULL, 0, 'Serve hot with samosas, bhujias, or cookies.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E6982BCC147104AA1BA685083A322F2', X'3C9922BFFB9F914F8E7CF6DF8BFF1974', X'3C9922BFFB9F914F8E7CF6DF8BFF1974', 0, NULL, NULL, 2, NULL, 0, 'Pressure cook the mangoes with two cups of water for up to five `whistles` or for 15 minutes in the Instant Pot. If you don''t have either a pressure cooker or an Instant Pot, place the mangoes in a saucepan, cover with an inch of water, and bring to a boil. Cover the pot and let simmer over medium-low heat until a knife pierced near the center goes cleanly through.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC283251976BE340BBD266F9636AAD87', X'3C9922BFFB9F914F8E7CF6DF8BFF1974', X'3C9922BFFB9F914F8E7CF6DF8BFF1974', 1, NULL, NULL, 2, NULL, 0, 'When the mangoes are cool enough to handle, peel them and remove all the pulp to a blender. Discard the seeds and peels.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2076DCEBB9A2B409FD06E8A1627EDED', X'3C9922BFFB9F914F8E7CF6DF8BFF1974', X'3C9922BFFB9F914F8E7CF6DF8BFF1974', 2, NULL, NULL, 2, NULL, 0, 'Add the sugar to the blender along with the cardamom powder and a dash of salt. A rule of thumb for the perfect sweetness is to use as much sugar as mango pulp, so be sure to measure your pulp. Blend into a very smooth paste. This is the concentrate. You can refrigerate this for 3-4 days or freeze for 2-3 months.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8D66A51F1701A43B79F8E7DD2A464E8', X'3C9922BFFB9F914F8E7CF6DF8BFF1974', X'3C9922BFFB9F914F8E7CF6DF8BFF1974', 3, NULL, NULL, 2, NULL, 0, 'To serve the panna, measure out ¾ cup of the concentrate and stir in 2 ½ cups of ice-cold water. This makes enough for 3-4 servings.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A68573E4E0B1B64294FB1A6B055AD3BB', X'3C9922BFFB9F914F8E7CF6DF8BFF1974', X'3C9922BFFB9F914F8E7CF6DF8BFF1974', 4, NULL, NULL, 2, NULL, 0, 'You can crush some mint into the glass before serving, or add a dash of cayenne or roasted cumin. You can also serve with lime wedges. Or just serve plain. It''s delicious.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'576D22723840074DA910FC806DA49AE9', X'A4FDFCD878EF154B8A92552A2861237F', X'A4FDFCD878EF154B8A92552A2861237F', 0, NULL, NULL, 2, NULL, 0, 'Place the sugar and water in a saucepan and bring to a boil. Turn the heat to a simmer, add the cardamom and lemon juice, and let it cook for another five minutes. Turn off heat.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFC9A357842CD64A8F2D0DAC94E0A357', X'74113B17C945D742BD721FCC125180DA', X'74113B17C945D742BD721FCC125180DA', 0, NULL, NULL, 2, NULL, 0, 'Place the breadcrumbs in a bowl and mix in the cashew cream, a little at a time until you have a smooth, pliable dough that''s not too stiff or dry.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE5C417629C908418FF54375DF2DADF9', X'74113B17C945D742BD721FCC125180DA', X'74113B17C945D742BD721FCC125180DA', 1, NULL, NULL, 2, NULL, 0, 'Divide into 14 equal sized pieces and roll each into a ball. You want a very smooth ball with no visible cracks on the surface. You can use some oil to grease your palms, which will help you shape the jamuns more easily.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84C2060C636A9947AEBD8A3BB48162BF', X'74113B17C945D742BD721FCC125180DA', X'74113B17C945D742BD721FCC125180DA', 2, NULL, NULL, 2, NULL, 0, 'Heat oil in a wok or a fryer. You don''t want to oil to be too hot, around 300 degrees is ideal. If the oil is too hot, the jamuns will brown very fast on the outside and not cook all the way through.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9066B6CC63B484B854DBABD4032B164', X'74113B17C945D742BD721FCC125180DA', X'74113B17C945D742BD721FCC125180DA', 3, NULL, NULL, 2, NULL, 0, 'Now place the jamuns, a few at a time so as to not clutter, into the fryer or wok. The oil should bubble only slightly. Don''t let the jamuns settle at the bottom. Use your spider or a slotted ladle to keep moving them around until they become a deep, reddish color.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'500055B34AE19B41A15730C376730991', X'74113B17C945D742BD721FCC125180DA', X'74113B17C945D742BD721FCC125180DA', 4, NULL, NULL, 2, NULL, 0, 'Remove them to a plate lined with a paper towel. When the jamun is still warm but can be handled, use a very thin pin or needle to poke holes all around. This will help it better absorb the syrup.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3855509EF690C240A05F0AFBDAB225AE', X'74113B17C945D742BD721FCC125180DA', X'74113B17C945D742BD721FCC125180DA', 5, NULL, NULL, 2, NULL, 0, 'Place the jamuns inside the syrup while the syrup is still warm but not very hot. They should be completely immersed. Let them stand for 3 hours before serving, so they have enough time to soak up the syrup.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C02F4E60154BB647A0AB9924B857835D', X'74113B17C945D742BD721FCC125180DA', X'74113B17C945D742BD721FCC125180DA', 6, NULL, NULL, 2, NULL, 0, 'Serve jamuns with a drizzle of the syrup and a sprinkling of nuts (optional).');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82C92C1A1C610242A88849C5D7895A46', X'CA87B37BD65E894CAD3A9F4A907C900F', X'CA87B37BD65E894CAD3A9F4A907C900F', 0, NULL, NULL, 2, NULL, 0, 'Blend the cashews with 3 cups water into a very smooth milk/cream. If your blender isn''t very powerful, soak the cashews in water for at least an hour first.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D105D64CB7B4E4F8CEF4B50EE9FD1B5', X'CA87B37BD65E894CAD3A9F4A907C900F', X'CA87B37BD65E894CAD3A9F4A907C900F', 1, NULL, NULL, 2, NULL, 0, 'Heat 1 tbsp oil in a large pan, preferably nonstick. A wider pan will allow the liquid to thicken and evaporate more easily. Add the sliced almonds or any other nuts you are using. Saute them for a couple of minutes until they just begin to change color. Add the carrots and cardamom, saute a couple of minutes, then add the cashew milk. * (If using dates to sweeten, see notes below before adding all the milk).');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C99024E5239CA43A61A81F128AA708D', X'CA87B37BD65E894CAD3A9F4A907C900F', X'CA87B37BD65E894CAD3A9F4A907C900F', 2, NULL, NULL, 2, NULL, 0, 'Cook, stirring occasionally, over medium-low heat until most of the milk has been absorbed into the cashews. This will take at least 15-20 minutes. If it''s going too slow, increase the heat a bit but keep an eye on the carrots and stir them often because you don''t want them to stick to the bottom.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F4C6CDF8CD46442ABAB43171C181E85', X'CA87B37BD65E894CAD3A9F4A907C900F', X'CA87B37BD65E894CAD3A9F4A907C900F', 3, NULL, NULL, 2, NULL, 0, 'Add the sugar and the remaining 1 tbsp oil and stir until it''s well mixed and the sugar has dissolved.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95F4863F37AA994CB47A4ED1EF612D5C', X'CA87B37BD65E894CAD3A9F4A907C900F', X'CA87B37BD65E894CAD3A9F4A907C900F', 4, NULL, NULL, 2, NULL, 0, 'At this stage, you can pretty much decide how much longer you want your halwa to cook -- taking it off the heat at this point will give you a more pudding-like texture, but you can continue to cook longer until the halwa pulls off the bottom and sides of the pan and there''s absolutely no visible liquid. I like to cook the halwa to this stage, but it means some exercise for your arm, so be warned.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9055B509F9BA114C95FD564E405DC71B', X'CA87B37BD65E894CAD3A9F4A907C900F', X'CA87B37BD65E894CAD3A9F4A907C900F', 5, NULL, NULL, 2, NULL, 0, 'Once the halwa reaches the desired state of doneness, take if off the heat. You can eat the halwa warm or at room temperature or cold, it''s delicious every way. Garnish with sliced almonds before serving, if you wish.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3674F04946108E46BD8835B7CCE09588', X'DFB287CFD2F7264BA426B2414AC00D2D', X'DFB287CFD2F7264BA426B2414AC00D2D', 0, NULL, NULL, 2, NULL, 0, 'Heat 1 tsp of oil in a large non-stick wok or wide saucepan. Add the grated carrots and a very small pinch of salt. Stir well and then cover with a tight lid that can hold some water on the top. This helps the carrots steam without burning and it also helps them cook faster. Let the carrots cook 15 minutes, stirring a couple of times to ensure they are not sticking to the bottom.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'904AA0E863D3EA46AF1552654C61EEBB', X'DFB287CFD2F7264BA426B2414AC00D2D', X'DFB287CFD2F7264BA426B2414AC00D2D', 1, NULL, NULL, 2, NULL, 0, 'Blend the dates with 1 cup of almond milk and add to the carrots along with the almond flour. Add the cardamom and mix well. Cover again with a lid, again add some drops of water to the lid, and let the carrots cook for another 15 minutes, stirring a few times in between.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DFA3A34820D0C4098A6774E2C93BAAF', X'DFB287CFD2F7264BA426B2414AC00D2D', X'DFB287CFD2F7264BA426B2414AC00D2D', 2, NULL, NULL, 2, NULL, 0, 'By this time the carrots should have absorbed all the almond milk and become fairly dry. If there''s any visible liquid, cook uncovered for a few minutes until it reduces. Add the remaining 1 tsp of oil and stir the carrots well to mix. Cook, stirring frequently, uncovered, for another 10 minutes. The carrots should have a much darker hue by this time and should be very tender and without the earthy flavor.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01B779B2265F58488666DAB0E6E819E1', X'DFB287CFD2F7264BA426B2414AC00D2D', X'DFB287CFD2F7264BA426B2414AC00D2D', 3, NULL, NULL, 2, NULL, 0, 'Add the vanilla and the remaining 1 ½ cups of almond milk and cook until warmed through. If you like the pudding a little runnier, add more almond milk.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA3601D440BCBE4CADD43AFF74693284', X'DFB287CFD2F7264BA426B2414AC00D2D', X'DFB287CFD2F7264BA426B2414AC00D2D', 4, NULL, NULL, 2, NULL, 0, 'If using the garnish, coat the bottom of a small skillet with cooking spray and cook the cashews and raisins, until the raisins plump up and the cashews are slightly golden. Sprinkle over the pudding before serving');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E508D08D6754D47A2D4B5E458C1148F', X'AAEDC0E0D1E9BE41A7A81AF8855AAD8B', X'AAEDC0E0D1E9BE41A7A81AF8855AAD8B', 0, NULL, NULL, 2, NULL, 0, 'Soak the cashews in water for about 30 minutes, then drain');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3F76593B9EF644D965401B2F5DBD8DC', X'AAEDC0E0D1E9BE41A7A81AF8855AAD8B', X'AAEDC0E0D1E9BE41A7A81AF8855AAD8B', 1, NULL, NULL, 2, NULL, 0, 'Add all of the ingredients to the blender and blend until very, very smooth.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0610A9C0A7E1CA45A45CD330983456C2', X'AAEDC0E0D1E9BE41A7A81AF8855AAD8B', X'AAEDC0E0D1E9BE41A7A81AF8855AAD8B', 2, NULL, NULL, 2, NULL, 0, 'Pour into popsicle molds, pierce each popsicle with a popsicle stick, and freeze.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02C1B6ABF4040545A7176104AA73A499', X'AAEDC0E0D1E9BE41A7A81AF8855AAD8B', X'AAEDC0E0D1E9BE41A7A81AF8855AAD8B', 3, NULL, NULL, 2, NULL, 0, 'To unmold, dip the popsicle mold in slightly warm water, pop out the popsicle, and enjoy.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCBAAA1CA1B253449EEBA8806AB2AE23', X'3CA7A4E5AF32784280834B8E1D67F057', X'3CA7A4E5AF32784280834B8E1D67F057', 0, NULL, NULL, 2, NULL, 0, 'The day before you want to make the Phirni, place the almonds in a bowl and pour over enough boiling water to cover the almonds. Set aside overnight.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'897F0BFABC7F344DA899A87591560D13', X'3CA7A4E5AF32784280834B8E1D67F057', X'3CA7A4E5AF32784280834B8E1D67F057', 1, NULL, NULL, 2, NULL, 0, 'Next day, peel the almonds and discard the skins.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D34192375EDC34BBD9A52B337A4617B', X'3CA7A4E5AF32784280834B8E1D67F057', X'3CA7A4E5AF32784280834B8E1D67F057', 2, NULL, NULL, 2, NULL, 0, 'Place the almonds in a blender with 4 cups of water and blend into a very smooth milk. Pour through a sieve to catch any large bits that might have remained, or any pieces of skin.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1EEF64B11278C48A49123B1D38985D2', X'3CA7A4E5AF32784280834B8E1D67F057', X'3CA7A4E5AF32784280834B8E1D67F057', 3, NULL, NULL, 2, NULL, 0, 'In a heavy-bottomed saucepan, bring all but ¼th of a cup of the almond milk to a boil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B3448F74D512842A9F434AD5AF22ED0', X'3CA7A4E5AF32784280834B8E1D67F057', X'3CA7A4E5AF32784280834B8E1D67F057', 4, NULL, NULL, 2, NULL, 0, 'Add the sugar and cook, stirring frequently, over medium-low heat until the milk starts to thicken, about 15 minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B9C681E84E5C541B02BE628067CE0D8', X'3CA7A4E5AF32784280834B8E1D67F057', X'3CA7A4E5AF32784280834B8E1D67F057', 5, NULL, NULL, 2, NULL, 0, 'Drain the rice that''s soaking and grind with with the reserved ¼th cup of almond milk.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'741900259AEEB244A22238BE5D736548', X'3CA7A4E5AF32784280834B8E1D67F057', X'3CA7A4E5AF32784280834B8E1D67F057', 6, NULL, NULL, 2, NULL, 0, 'Add the rice paste to the almond milk and continue to cook, stirring, for another 15-20 minutes. You want to feel the pudding thickening as time goes by.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'973DE3B2A7FF1443BA24827E596BB637', X'3CA7A4E5AF32784280834B8E1D67F057', X'3CA7A4E5AF32784280834B8E1D67F057', 7, NULL, NULL, 2, NULL, 0, 'Cover the phirni with a tight-fitting lid and let the mixture cook another five minutes.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42B56B1E0B328549A27916EDCE835A36', X'3CA7A4E5AF32784280834B8E1D67F057', X'3CA7A4E5AF32784280834B8E1D67F057', 8, NULL, NULL, 2, NULL, 0, 'Remove the lid, add the cardamom, rose water and cashew nuts,and cook for another five minutes. Turn off the heat and pour it into individual serving dishes or bowls.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EF3CCDFAFE66D499E69673472564E4D', X'3CA7A4E5AF32784280834B8E1D67F057', X'3CA7A4E5AF32784280834B8E1D67F057', 9, NULL, NULL, 2, NULL, 0, 'Garnish with chopped strawberries or nuts. The slight tartness of the strawberries is a perfect flavor pairing with the sweet Phirni. You can also add saffron-- soak a generous pinch in 1 tbsp almond milk and add along with the cardamom and cashew nuts.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEDFF5CF7F21854B85B793D3BFFF6D88', X'3CA7A4E5AF32784280834B8E1D67F057', X'3CA7A4E5AF32784280834B8E1D67F057', 10, NULL, NULL, 2, NULL, 0, 'Refrigerate the Phirni until thoroughly chilled.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E5C22C32DB4E54B85B619FE4A2EF7F9', X'DEB54A97638D5B4A8E86D78D96F8FBA5', X'DEB54A97638D5B4A8E86D78D96F8FBA5', 0, NULL, NULL, 2, NULL, 0, 'Place the water and sugar in a heavy-bottomed wok or skillet (non-stick works best for this). Add the cardamom and let it come to a boil.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8533D2A3261874B8A847A27FF6856B1', X'DEB54A97638D5B4A8E86D78D96F8FBA5', X'DEB54A97638D5B4A8E86D78D96F8FBA5', 1, NULL, NULL, 2, NULL, 0, 'Turn the heat to low or medium-low. Add the cashew flour and almond flour and mix. Continue stirring at very frequent intervals, making sure your flour is not sticking to the bottom. If it is, scrape it up rightaway.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B4C1DB573CE0A4F907C6FE5514EC436', X'DEB54A97638D5B4A8E86D78D96F8FBA5', X'DEB54A97638D5B4A8E86D78D96F8FBA5', 2, NULL, NULL, 2, NULL, 0, 'Within a few minutes, the mixture will begin to pull off the sides and bottom of the pan. Add the coconut oil at this point. The mixture will be quite translucent and sticky at this stage, which will make stirring it a bit of a pain, but persist. Since stoves vary, you might want to turn up your heat just a teeny bit if this is taking too long, but around 12-15 minutes after you began cooking, the mixture should turn opaque and dry (meaning it won''t be sticky anymore). If you make a ball, it should hold together. Turn off the heat at this point and remove the mixture to a bowl.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B45B3018D56C4349B5242A68A2B7DA07', X'DEB54A97638D5B4A8E86D78D96F8FBA5', X'DEB54A97638D5B4A8E86D78D96F8FBA5', 3, NULL, NULL, 2, NULL, 0, 'As soon as you can handle the dough, knead it into a smooth ball. I like kneading in a little cashew cream into the mixture at this time because it just makes the barfi softer, which I love. You can skip this step.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D84F53EA62C62D4BB39AFDDDEEE7596D', X'DEB54A97638D5B4A8E86D78D96F8FBA5', X'DEB54A97638D5B4A8E86D78D96F8FBA5', 4, NULL, NULL, 2, NULL, 0, 'Lay a piece of parchment paper on a baking sheet or a flat baking pan. Pat out the barfi mixture on it in an even layer, about six inches square (or thinner, if you want thinner barfis). Press the sides to make them as even as possible.');
INSERT INTO `VWR INTERNAL Holycowvegan1 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8D06D401A05B34F943D98542C984932', X'DEB54A97638D5B4A8E86D78D96F8FBA5', X'DEB54A97638D5B4A8E86D78D96F8FBA5', 5, NULL, NULL, 2, NULL, 0, 'Press some nuts for garnish on the top and let the barfi cool completely. When cool, cut the barfi into pieces. The barfi will store in the refrigerator for at least a week.');
