INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'949174D2D12F1243AC093F674D6D42D9', X'64EEFD0EBA499B4F90DB73AD5A897368', X'64EEFD0EBA499B4F90DB73AD5A897368', 4, NULL, NULL, 2, NULL, 0, 'Top with berries, syrup, and powdered sugar, then serve.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5409DAE3B66E0C4C8EF4C394555C2452', X'29B52EE4CA45BD4FB6F6E07CFD1CC975', X'29B52EE4CA45BD4FB6F6E07CFD1CC975', 0, NULL, NULL, 2, NULL, 0, 'Trim the top, bottom and sides of the pound cake just enough to remove the golden brown pieces. Slice the trimmed pound cake into1-inch cubes.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F04BB647A4C1D46A95C58BE0D279A6F', X'29B52EE4CA45BD4FB6F6E07CFD1CC975', X'29B52EE4CA45BD4FB6F6E07CFD1CC975', 1, NULL, NULL, 2, NULL, 0, 'Place cubes in a single layer on a cookie sheet. Spear each cube with a lollipop stick or a skewer to make a lollipop. Freeze 1-2 hours.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD3A6A59E9AEE54BA67E99C591F2FC0A', X'29B52EE4CA45BD4FB6F6E07CFD1CC975', X'29B52EE4CA45BD4FB6F6E07CFD1CC975', 2, NULL, NULL, 2, NULL, 0, 'When ready to dip and decorate the pops, place each type of sprinkles, crushed candies and crushed cookies into separate bowls.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3065B788B88844CB30E4D2B0B68169C', X'29B52EE4CA45BD4FB6F6E07CFD1CC975', X'29B52EE4CA45BD4FB6F6E07CFD1CC975', 3, NULL, NULL, 2, NULL, 0, 'Place chocolate chips in a large glass bowl or measuring cup. Microwave on high for 1 minute or until chocolate starts to melt. Stir until chips are completely melted. If necessary, microwave at additional 10-15 second intervals, stirring just until chocolate is melted.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6A8264D45E44943A13DED0218B4A83E', X'29B52EE4CA45BD4FB6F6E07CFD1CC975', X'29B52EE4CA45BD4FB6F6E07CFD1CC975', 4, NULL, NULL, 2, NULL, 0, 'Working quickly, dip each pound cake pop into the chocolate, roll in desired sprinkles, crushed candy or cookies. Place back onto cookie sheet(s). Chill until firm, about 1 hour.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFD24203796F5848A006BC8C3D015B82', X'C3D94EE140EB624581A7296EF8972589', X'C3D94EE140EB624581A7296EF8972589', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400°F.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEFC40002E77364AB8DB69B681C3D9CE', X'C3D94EE140EB624581A7296EF8972589', X'C3D94EE140EB624581A7296EF8972589', 1, NULL, NULL, 2, NULL, 0, 'Spray a 13×9-inch baking pan with no-stick cooking spray. Cut pound cake into 15 slices. Place in the bottom of a 13×9-inch baking pan cutting to fit, if needed.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2953D5363A56654BA402301170B797A6', X'C3D94EE140EB624581A7296EF8972589', X'C3D94EE140EB624581A7296EF8972589', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle marshmallows, chocolate chips and peanuts evenly over pound cake.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C03F3B4C83A3D24CBC53FE840C4F25DB', X'C3D94EE140EB624581A7296EF8972589', X'C3D94EE140EB624581A7296EF8972589', 3, NULL, NULL, 2, NULL, 0, 'Bake for 8-10 minutes or until marshmallows are melted and lightly toasted.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30FF0E07E10EB24DAD8A7F825FBD0CDB', X'C3D94EE140EB624581A7296EF8972589', X'C3D94EE140EB624581A7296EF8972589', 4, NULL, NULL, 2, NULL, 0, 'Cool; cut into squares.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1077975E78B5EF4A9202E316841F7DEC', X'4874C69623E46642B6A2B3DC76251780', X'4874C69623E46642B6A2B3DC76251780', 0, NULL, NULL, 2, NULL, 0, 'Remove lid; leave pound cake in aluminum pan. Using a long skewer, poke holes into cake every 1/2-inch, wiping skewer occasionally, if needed.');
