INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F9601E079D6EE4EAD316255F65956E3', X'4874C69623E46642B6A2B3DC76251780', X'4874C69623E46642B6A2B3DC76251780', 2, NULL, NULL, 2, NULL, 0, 'Pour 1/2 cup of milk mixture over cake; allow to absorb into cake.');
