INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2E58E00F2DF2A4EA43EEF8C4C0FD667', X'4874C69623E46642B6A2B3DC76251780', X'4874C69623E46642B6A2B3DC76251780', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate 2 hours or overnight to allow milk mixture to absorb into cake.');
