INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B008E6A769ADF4DB2044FB1E2FDBFD1', X'0D84687B26E908428D2A88449B4E48D4', X'0D84687B26E908428D2A88449B4E48D4', 0, NULL, NULL, 2, NULL, 0, 'Remove cheesecake from pan according to package directions and set on a plate.');
