INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'859712600E0CBB42936350B4D5864035', X'2D816FF9A47B5F499232092CBB85DD0A', X'2D816FF9A47B5F499232092CBB85DD0A', 3, NULL, NULL, 2, NULL, 0, 'Place mascarpone cheese, cream, lemon curd, marshmallow cream and zest in a large bowl. Beat until smooth.');
