INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D11288E33C264848822AE48DCCFD08C3', X'71BF25C25BDB1C478438D684BD47FC97', X'71BF25C25BDB1C478438D684BD47FC97', 0, NULL, NULL, 2, NULL, 0, 'Cut pound cake into 1-inch cubes. Set aside.');
