INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DDAC96F9ECAE448A3A33E4686371627', X'832012888A7CBF46802FBDD59AB76135', X'832012888A7CBF46802FBDD59AB76135', 0, NULL, NULL, 2, NULL, 0, 'Lightly sprinkle both sides of pound cake with cinnamon sugar if desired, shaking off any excess sugar. Toast in oven or toaster, or place on a grill for a few seconds.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8514599BB432A54DAEEA18C2ED96B1DC', X'832012888A7CBF46802FBDD59AB76135', X'832012888A7CBF46802FBDD59AB76135', 1, NULL, NULL, 2, NULL, 0, 'Spread with favorite toppings such as peanut butter, jam, jelly or cream cheese spread and top with sliced fruit and /or chocolate chips, coconut and nuts.');
