INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ABA4F3CB4EB1E46B4FFCA4C418E9B1D', X'975B99A630C93D42BBB99C8B499E2B4B', X'975B99A630C93D42BBB99C8B499E2B4B', 2, NULL, NULL, 2, NULL, 0, 'Layer pound cake, berries, pudding and whipped topping alternately in 8 dessert or wine glasses. Serve immediately.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D43DC56213EDB0489DAC47A32FB7CFDF', X'052E5042B6CFFF45B2E36B198D6F9D9A', X'052E5042B6CFFF45B2E36B198D6F9D9A', 0, NULL, NULL, 2, NULL, 0, 'Combine raspberries and sugar in blender container. Cover and blend on high until smooth. Press through sieve to remove seeds. Place in small saucepan.');
