INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54A70B09E4F29E4E80EFF8A9F50874D8', X'052E5042B6CFFF45B2E36B198D6F9D9A', X'052E5042B6CFFF45B2E36B198D6F9D9A', 3, NULL, NULL, 2, NULL, 0, 'Stir in berries; serve over pound cake slices (1 pound cake slice with about 2 tablespoons raspberry sauce).');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A422D7088FF4FA4F8C90CB3A7530258D', X'C502CD774A850545A3767A6D6BC176CB', X'C502CD774A850545A3767A6D6BC176CB', 0, NULL, NULL, 2, NULL, 0, 'Thaw 2 pound cake slices according to package directions. Cut thawed pound cake into 1-inch cubes.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D9FF2CA3D162B448B5931380626ED9C', X'C502CD774A850545A3767A6D6BC176CB', X'C502CD774A850545A3767A6D6BC176CB', 1, NULL, NULL, 2, NULL, 0, 'Mix yogurt with 1/2 cup light whipped topping, reserving remaining whipped topping for later.');
