INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01F1759BF2B1CD4CAB637D1D0B229FC3', X'C502CD774A850545A3767A6D6BC176CB', X'C502CD774A850545A3767A6D6BC176CB', 3, NULL, NULL, 2, NULL, 0, 'Top with remaining light whipped topping and toasted almonds.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE7C3BA92220CF43BBA04A1AE4778056', X'C502CD774A850545A3767A6D6BC176CB', X'C502CD774A850545A3767A6D6BC176CB', 4, NULL, NULL, 2, NULL, 0, 'Tip: You can make this recipe for more people, by scaling up the ingredients and presenting in a pretty glass bowl.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64238C9CB7D4BC4988707BF05D08D139', X'80ED8F7817E66443AE72214FCB4D062B', X'80ED8F7817E66443AE72214FCB4D062B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325°F. Spray a round 2 quart baking dish with cooking spray.');
