INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2995926C8EE1B847B482531D7D107F92', X'80ED8F7817E66443AE72214FCB4D062B', X'80ED8F7817E66443AE72214FCB4D062B', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle 1/3 of the brown-sugar mixture on bottom of the prepared pan. Top with 1/2 of the toasted pound cake and banana; repeat layers.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E22460A38A946144B5088A19A1A88B42', X'80ED8F7817E66443AE72214FCB4D062B', X'80ED8F7817E66443AE72214FCB4D062B', 4, NULL, NULL, 2, NULL, 0, 'Pour egg mixture evenly over the cake. Press down the cake cubes with the back of a spoon to cover; let stand 10 minutes. Sprinkle with remaining brown sugar mixture.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0940703EA870649B630EC295DD21F13', X'80ED8F7817E66443AE72214FCB4D062B', X'80ED8F7817E66443AE72214FCB4D062B', 5, NULL, NULL, 2, NULL, 0, 'Cover the baking dish with foil. Bake 30 minutes. Remove cover and continue baking 30 minutes or until no liquid egg remains. Let cool for 10 minutes before serving. Garnish with whipped cream and a sprinkle of nutmeg, if desired.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'287447A0FFD09446A989956F69A60531', X'E7B1210D14A80E45BDED270F6726A205', X'E7B1210D14A80E45BDED270F6726A205', 0, NULL, NULL, 2, NULL, 0, 'Slice frozen pound cake into ½-inch slices.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FB6FB4BA62EFD4F848FA454A2EB85B6', X'E7B1210D14A80E45BDED270F6726A205', X'E7B1210D14A80E45BDED270F6726A205', 1, NULL, NULL, 2, NULL, 0, 'Fill with ice cream and drizzle caramel or chocolate sauce, if desired.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03B7E0A8421BB54D90DC98326D1F86D4', X'E7B1210D14A80E45BDED270F6726A205', X'E7B1210D14A80E45BDED270F6726A205', 2, NULL, NULL, 2, NULL, 0, 'Top with second pound cake slice.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A37818CCC57588448AB179FC969C539B', X'F8F5B59EF072B8479A5EA73F9AFED618', X'F8F5B59EF072B8479A5EA73F9AFED618', 0, NULL, NULL, 2, NULL, 0, 'Crumble cheesecake in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F144657F6CBE042A9FE52DD5D5FE352', X'F8F5B59EF072B8479A5EA73F9AFED618', X'F8F5B59EF072B8479A5EA73F9AFED618', 1, NULL, NULL, 2, NULL, 0, 'Mix in graham crackers and apple cider.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DA26C09A7908C4BBBBE00CAD91466EF', X'F8F5B59EF072B8479A5EA73F9AFED618', X'F8F5B59EF072B8479A5EA73F9AFED618', 2, NULL, NULL, 2, NULL, 0, 'In a smaller bowl, mix together cinnamon and sugar.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A91BC423E39E5244A5EF554651A9BED4', X'F8F5B59EF072B8479A5EA73F9AFED618', X'F8F5B59EF072B8479A5EA73F9AFED618', 3, NULL, NULL, 2, NULL, 0, 'Grease hands and form the cheesecake mixture into balls.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73465C80DF7BFF4E949F47AF044DC98A', X'F8F5B59EF072B8479A5EA73F9AFED618', X'F8F5B59EF072B8479A5EA73F9AFED618', 4, NULL, NULL, 2, NULL, 0, 'Roll in cinnamon sugar mixture.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'759191B97E8E004E9F5231E55364F66E', X'F8F5B59EF072B8479A5EA73F9AFED618', X'F8F5B59EF072B8479A5EA73F9AFED618', 5, NULL, NULL, 2, NULL, 0, 'Plate and drizzle with caramel sauce, then serve.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F0B80290A18824FA1F9739A1DB60417', X'0F586B0F89791F4292C363D9852CBB0C', X'0F586B0F89791F4292C363D9852CBB0C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven at 375°F.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31B6583919D3A84EAD3309A69F187553', X'0F586B0F89791F4292C363D9852CBB0C', X'0F586B0F89791F4292C363D9852CBB0C', 1, NULL, NULL, 2, NULL, 0, 'Slice pound cake into 3 slices lengthwise. Place on a cutting board and cut using a 3 inch round cookie cutter; cut 3 circles from each slice; cut each circle horizontally in half making 18 circles.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'599EBDCDD853B74C8F49E557386D5129', X'0F586B0F89791F4292C363D9852CBB0C', X'0F586B0F89791F4292C363D9852CBB0C', 2, NULL, NULL, 2, NULL, 0, 'Place circles in a single layer on an ungreased, large-sized baking sheet(s). Brush circles with melted butter.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4359F5700253D940A96DE61534641B13', X'0F586B0F89791F4292C363D9852CBB0C', X'0F586B0F89791F4292C363D9852CBB0C', 3, NULL, NULL, 2, NULL, 0, 'Bake 10 minutes or until toasted. Cool completely.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8506DA84079474290EDE3C19D3CEFC3', X'0F586B0F89791F4292C363D9852CBB0C', X'0F586B0F89791F4292C363D9852CBB0C', 4, NULL, NULL, 2, NULL, 0, 'Place cream cheese, peanut butter and vanilla in a large mixing bowl. Beat with an electric mixer on medium speed until smooth. Slowly add powdered sugar and mix until combined.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EB68267D29E5A49BD2F4CB399F2CF6C', X'0F586B0F89791F4292C363D9852CBB0C', X'0F586B0F89791F4292C363D9852CBB0C', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate 5-10 minutes or until slightly thickened.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E18F934F51CC7B4BAA74BBE50AEBAF1C', X'0F586B0F89791F4292C363D9852CBB0C', X'0F586B0F89791F4292C363D9852CBB0C', 6, NULL, NULL, 2, NULL, 0, 'Spread peanut butter mixture on the bottom of 9 toasted pound cake circles. Top with remaining circles forming whoopie pies.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DA6D60B4108484EBF622E2F4DA9CD77', X'0F586B0F89791F4292C363D9852CBB0C', X'0F586B0F89791F4292C363D9852CBB0C', 7, NULL, NULL, 2, NULL, 0, 'Melt chocolate chips according to package directions. Dip one end of each whoopie pie into melted chocolate; then dip in peanuts. Place on a parchment paper lined baking sheet and place in refrigerator for 10 minutes or until chocolate is set.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A22A086CADE8641963E70211FFF390C', X'76AE6C88C97CC546905825B7EDC3A394', X'76AE6C88C97CC546905825B7EDC3A394', 0, NULL, NULL, 2, NULL, 0, 'Cut pound cake into 1-inch cubes. Layer half of the cake into a 3-quart serving bowl. Sprinkle pound cake with 2 tablespoons orange juice.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'405BBD2641BD04438518A9C8E101A458', X'76AE6C88C97CC546905825B7EDC3A394', X'76AE6C88C97CC546905825B7EDC3A394', 1, NULL, NULL, 2, NULL, 0, 'Prepare pudding according to package directions using milk. Let stand 5 minutes. Spread half the pudding over cake. Top with one third of the whipped topping and half of the pie filling. Repeat layers ending with pie filling on top.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7901AF97922AC4A827A673E0865A671', X'76AE6C88C97CC546905825B7EDC3A394', X'76AE6C88C97CC546905825B7EDC3A394', 2, NULL, NULL, 2, NULL, 0, 'Cover; refrigerate at least 1 hour before serving.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'922AF539B89E844DA821093BD61583D4', X'76AE6C88C97CC546905825B7EDC3A394', X'76AE6C88C97CC546905825B7EDC3A394', 3, NULL, NULL, 2, NULL, 0, 'Spread with remaining whipped topping and sprinkle with nuts, if desired.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C8E9D940488E043A9208FDFCD220ED4', X'76AE6C88C97CC546905825B7EDC3A394', X'76AE6C88C97CC546905825B7EDC3A394', 4, NULL, NULL, 2, NULL, 0, 'Serve immediately or refrigerate until ready to serve.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10B839DB3A72564EB923D1094F759E7B', X'9BBB0E8E41573E44AA100AF945424B11', X'9BBB0E8E41573E44AA100AF945424B11', 0, NULL, NULL, 2, NULL, 0, 'Using a melon baller or 1-inch scoop, scoop small balls out of the cheesecake.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C3062D96BE44241B3BBD758D97FF60F', X'9BBB0E8E41573E44AA100AF945424B11', X'9BBB0E8E41573E44AA100AF945424B11', 1, NULL, NULL, 2, NULL, 0, 'Place cheesecake balls in a single layer on a cookie sheet. Spear each ball with a lollipop stick or a skewer to make a lollipop. Freeze 1-2 hours.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC7FEF2E48FF5A4A9DF714D9A15413DB', X'9BBB0E8E41573E44AA100AF945424B11', X'9BBB0E8E41573E44AA100AF945424B11', 2, NULL, NULL, 2, NULL, 0, 'When ready to dip and decorate the pops, place each type of sprinkles, crushed candies and crushed cookies into separate bowls.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F45B946AEDF444B9620A8462E6FE0BF', X'9BBB0E8E41573E44AA100AF945424B11', X'9BBB0E8E41573E44AA100AF945424B11', 3, NULL, NULL, 2, NULL, 0, 'Place ¾ of chocolate chips into a large glass bowl. Microwave on low power for 5-7 minutes, stirring frequently, until chips are completely melted. Remove chocolate from microwave and stir in remaining chips, continuing to stir until chips are completely melted.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8FD07AE31CA134DAAFD64167B03001C', X'9BBB0E8E41573E44AA100AF945424B11', X'9BBB0E8E41573E44AA100AF945424B11', 4, NULL, NULL, 2, NULL, 0, 'Dip each cheesecake pop into the chocolate, then roll in desired sprinkle, candy or cookie bowl. Place back onto the cookie sheet. When you have decorated all the pops, chill until firm, about 1 hour.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C719933123B1D43811410F93D806FF8', X'63CDE5A2AEF37F4C8F50F8F0DD01C917', X'63CDE5A2AEF37F4C8F50F8F0DD01C917', 0, NULL, NULL, 2, NULL, 0, 'Using an ice cream scoop, scoop out cheesecake, placing scoop into a parfait glass.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F04DE9AC8AE37B42AC1E6A3CCA54A78E', X'63CDE5A2AEF37F4C8F50F8F0DD01C917', X'63CDE5A2AEF37F4C8F50F8F0DD01C917', 1, NULL, NULL, 2, NULL, 0, 'Top cheesecake with first topping, place another scoop of cheesecake on top, then drizzle with second topping (or more of the first) and whipped cream.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7503E2487108AB4D8DCBC43D60D431FB', X'9C55DE6A217CD74E8F69202089FC1124', X'9C55DE6A217CD74E8F69202089FC1124', 0, NULL, NULL, 2, NULL, 0, 'Remove lid; leave pound cake in aluminum pan. Using a long skewer, poke holes into cake every 1/2-inch, wiping skewer occasionally, if needed.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'071993B99769094AAF75B3E044F9FC32', X'9C55DE6A217CD74E8F69202089FC1124', X'9C55DE6A217CD74E8F69202089FC1124', 1, NULL, NULL, 2, NULL, 0, 'Place margarita mix and tequila or water in a small saucepan; bring to a boil. Remove from heat.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF2C4BAF93CF2542A43D8CF8BD9A345C', X'9C55DE6A217CD74E8F69202089FC1124', X'9C55DE6A217CD74E8F69202089FC1124', 2, NULL, NULL, 2, NULL, 0, 'Add gelatin; stir until completely dissolved (about 2 minutes).');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E1DBD8E5A56E94A8370190F0F01BAD8', X'9C55DE6A217CD74E8F69202089FC1124', X'9C55DE6A217CD74E8F69202089FC1124', 3, NULL, NULL, 2, NULL, 0, 'Pour over cake. Cover loosely. Refrigerate 3 hours or until gelatin is set.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26A375DCCE532D41853ADDAEA06B66A3', X'9C55DE6A217CD74E8F69202089FC1124', X'9C55DE6A217CD74E8F69202089FC1124', 4, NULL, NULL, 2, NULL, 0, 'Remove cake from pan; discard any gelatin mixture. Place on a serving plate. Frost top and sides with whipped topping. Cut into slices and garnish with green sugar and lime slices, if desired.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF890C3F5C9B5E488456F57ACD8F5AF8', X'C9C17BCD5C01EE4985A283A8F203483D', X'C9C17BCD5C01EE4985A283A8F203483D', 0, NULL, NULL, 2, NULL, 0, 'Combine ice cream, milk and vanilla extract in your blender. Puree until smooth, about 1 minute.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F72F4CFC5087754E8E47657BF13EDBD8', X'C9C17BCD5C01EE4985A283A8F203483D', X'C9C17BCD5C01EE4985A283A8F203483D', 1, NULL, NULL, 2, NULL, 0, 'Coarsely chop cheesecake into chunks. Add to blender and pulse 10-15 times, to incorporate cheesecake into milkshake.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B14DA4CC952A1488571321E2CE107C8', X'1C2C19B61E22384EAD90C3423FC87DF4', X'1C2C19B61E22384EAD90C3423FC87DF4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F. Line 6 muffin cups with cupcake liners.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA63305B6BD6FB4F813C450B5A64CFA1', X'1C2C19B61E22384EAD90C3423FC87DF4', X'1C2C19B61E22384EAD90C3423FC87DF4', 1, NULL, NULL, 2, NULL, 0, 'Cut pound cake into 1-inch cubes.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5934F17ACA18E843AD8E8CAD431C5D47', X'1C2C19B61E22384EAD90C3423FC87DF4', X'1C2C19B61E22384EAD90C3423FC87DF4', 2, NULL, NULL, 2, NULL, 0, 'Whisk cream, sugar and eggs until well combined in a large mixing bowl. Add cake; mix lightly until well combined. Divide mixture evenly into muffin cups.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DFE36AE22EC864FA4617EA1D75EBA05', X'1C2C19B61E22384EAD90C3423FC87DF4', X'1C2C19B61E22384EAD90C3423FC87DF4', 3, NULL, NULL, 2, NULL, 0, 'Bake 20-25 minutes or until golden brown and toothpick inserted in center comes out clean.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7848C6FD1E6304DB29EE45A0D813082', X'1C2C19B61E22384EAD90C3423FC87DF4', X'1C2C19B61E22384EAD90C3423FC87DF4', 4, NULL, NULL, 2, NULL, 0, 'Combine caramel sauce and sea salt in a small bowl. Drizzle over cakes. Cool at least 10 minutes before serving.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03A78A09CEC5FB46BDED77324954583E', X'3D06D75F1E95D84BA236CF21FDF396AF', X'3D06D75F1E95D84BA236CF21FDF396AF', 0, NULL, NULL, 2, NULL, 0, 'Using an ice cream scoop, scoop out cheesecake, placing each scoop into a parfait glass.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'781399D677CF754793FC69E373D090E6', X'3D06D75F1E95D84BA236CF21FDF396AF', X'3D06D75F1E95D84BA236CF21FDF396AF', 1, NULL, NULL, 2, NULL, 0, 'Top cheesecake with fudge, caramel, pecans, and whipped cream. Place a cherry on top!');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A008C41A93A16A41A062BB733272804B', X'3C474169657EDA478CE5BA91BD0536ED', X'3C474169657EDA478CE5BA91BD0536ED', 0, NULL, NULL, 2, NULL, 0, 'Cut frozen coffee cake into two layers.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B4C9D37DFF9DE49BC3461E61ACCD572', X'3C474169657EDA478CE5BA91BD0536ED', X'3C474169657EDA478CE5BA91BD0536ED', 1, NULL, NULL, 2, NULL, 0, 'Place bottom layer into foil pan. Cover with apple pie filling. Replace top.');
