INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11920C133428F44BBB58C8CA55046FAB', X'B910ABF8B1CA4349A75011A35C4891E6', X'B910ABF8B1CA4349A75011A35C4891E6', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F.');
