INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E142EA6E3B3D3D4E919D5FE562F355F7', X'FF43A3AB9917D04790C6A03C6CEF476F', X'FF43A3AB9917D04790C6A03C6CEF476F', 0, NULL, NULL, 2, NULL, 0, 'Slice pound cake into six slices and place on serving plates.');
