INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EEB303C5E59DC4EA510277FFA3FEF21', X'FF43A3AB9917D04790C6A03C6CEF476F', X'FF43A3AB9917D04790C6A03C6CEF476F', 2, NULL, NULL, 2, NULL, 0, 'Spread each pound cake with yogurt mixture. Top with fruit and granola.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F1A67B22E45284B95A150E78C7821D9', X'FF43A3AB9917D04790C6A03C6CEF476F', X'FF43A3AB9917D04790C6A03C6CEF476F', 3, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DA45299FE34064E8C95D10F60C6DCF8', X'CBA3B26CB13B6941A84A915D90932B8D', X'CBA3B26CB13B6941A84A915D90932B8D', 0, NULL, NULL, 2, NULL, 0, 'Cut pound cake lengthwise into 3 layers. Set aside.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88A4CA76AFCF1E44B98FBF7C7901AC49', X'CBA3B26CB13B6941A84A915D90932B8D', X'CBA3B26CB13B6941A84A915D90932B8D', 1, NULL, NULL, 2, NULL, 0, 'Place chocolate chips in a medium bowl. Bring 1/2 cup of the whipping cream just to a boil in a small saucepan. Remove from heat and pour over chocolate chips; let stand 3 minutes. Add 2 teaspoons of the espresso powder; whisk until chocolate is melted and mixture is smooth. Cool 15 minutes.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C676FC031831043BDC2754E5A4BAD93', X'CBA3B26CB13B6941A84A915D90932B8D', X'CBA3B26CB13B6941A84A915D90932B8D', 2, NULL, NULL, 2, NULL, 0, 'Place remaining 1 cup whipping cream, sugar, remaining 3 teaspoons espresso powder and vanilla into a chilled mixing bowl; beat until stiff peaks form. Cover bowl and refrigerate until needed.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0643CEFA66F3564C8F33424D44266AF7', X'CBA3B26CB13B6941A84A915D90932B8D', X'CBA3B26CB13B6941A84A915D90932B8D', 3, NULL, NULL, 2, NULL, 0, 'To assemble cake, place bottom layer of cake onto a serving plate. Spread with 1/3 of the cream filling and sprinkle with 1 tablespoon of the toffee bits. Repeat 2 times.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85E6374DDD67AF448E1D3F9F829A7423', X'CBA3B26CB13B6941A84A915D90932B8D', X'CBA3B26CB13B6941A84A915D90932B8D', 4, NULL, NULL, 2, NULL, 0, 'Drizzle ganache mixture over cake. Sprinkle with remaining 1 tablespoon toffee bits, if desired. Freeze for 30 minutes or until chocolate sets. Cut into slices to serve. Store wrapped in the freezer.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66EAE1FC26F5DF42B7806AFA163D87F6', X'C6D86FA6DA453E4CB08E3E76F456C028', X'C6D86FA6DA453E4CB08E3E76F456C028', 0, NULL, NULL, 2, NULL, 0, 'Trim the top, bottom and sides of the pound cake just enough to remove the golden brown pieces. Slice the trimmed pound cake into1/2-inch thick slices. Cut each slice into ½-inch strips. These are the “fries.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5681EE71AE369243A7B112FEB9A0050F', X'32B8563AD222824BA93CD6DA17D234B0', X'32B8563AD222824BA93CD6DA17D234B0', 0, NULL, NULL, 2, NULL, 0, 'Toast two slices of thawed pound cake in toaster on lightest setting until lightly browned.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67AA1B7B498ED74A9DCF33BEB9F423B6', X'32B8563AD222824BA93CD6DA17D234B0', X'32B8563AD222824BA93CD6DA17D234B0', 1, NULL, NULL, 2, NULL, 0, 'Add a scoop of warm apple pie filling.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6832B97DC3165240B931CA41147E4BE2', X'32B8563AD222824BA93CD6DA17D234B0', X'32B8563AD222824BA93CD6DA17D234B0', 2, NULL, NULL, 2, NULL, 0, 'Top with a scoop of vanilla ice cream.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4410F4094F4A54F8C9D0E70D5FCA017', X'32B8563AD222824BA93CD6DA17D234B0', X'32B8563AD222824BA93CD6DA17D234B0', 3, NULL, NULL, 2, NULL, 0, 'Add dollop of whipped cream and a sprinkling of toasted pecans (optional).');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'322012CA88519240BDEAD25B31904D63', X'0E759049A38B264DAE5598C57ADAA927', X'0E759049A38B264DAE5598C57ADAA927', 0, NULL, NULL, 2, NULL, 0, 'Cut pound cake into 12 slices.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE418C0289F6E3488471BFC8FDF18430', X'0E759049A38B264DAE5598C57ADAA927', X'0E759049A38B264DAE5598C57ADAA927', 1, NULL, NULL, 2, NULL, 0, 'Grill pineapple slices over medium heat for 2-3 minutes per side or until lightly browned. Remove from heat. Brush both sides of pound cake with melted butter. Grill over medium heat 1-2 minutes per side or until lightly browned.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE0620D0CB2FF941A35C54EBD196B2D8', X'0E759049A38B264DAE5598C57ADAA927', X'0E759049A38B264DAE5598C57ADAA927', 2, NULL, NULL, 2, NULL, 0, 'Beat cream, sugar and vanilla in large mixing bowl with an electric mixer until stiff peaks form.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FDEDA704BA6C04BB42F5081BCC8E922', X'0E759049A38B264DAE5598C57ADAA927', X'0E759049A38B264DAE5598C57ADAA927', 3, NULL, NULL, 2, NULL, 0, 'Place 1 slice of pound cake on 6 serving plates. Top each with pineapple ring, dollop of whipped cream, another slice of pound cake and additional whipped cream. Drizzle each serving with caramel sauce; sprinkle with salt and pecans. Serve immediately.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25FDA4646A37694EA37AEA637A92ED5B', X'D294619BFC724247A0A2BCE7DAA044B1', X'D294619BFC724247A0A2BCE7DAA044B1', 0, NULL, NULL, 2, NULL, 0, 'Cut pound cake into 8 slices. Spray a Panini press with cooking spray. Toast pound cake in Panini press or in oven at 350°F for 10 minutes or until lightly browned. Set aside.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A195906FA1CB84D862B7D32FB667780', X'D294619BFC724247A0A2BCE7DAA044B1', X'D294619BFC724247A0A2BCE7DAA044B1', 1, NULL, NULL, 2, NULL, 0, 'Combine balsamic vinegar and 2 tablespoons of the sugar in a small saucepan. Cook and stir over medium heat for 5 minutes. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81E1E7F6740DBF4DB78DB404C7F2CAB4', X'D294619BFC724247A0A2BCE7DAA044B1', X'D294619BFC724247A0A2BCE7DAA044B1', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle strawberries with remaining tablespoon of sugar.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B99DFF1D329E4F42BB7C9A7A54CCE382', X'D294619BFC724247A0A2BCE7DAA044B1', X'D294619BFC724247A0A2BCE7DAA044B1', 3, NULL, NULL, 2, NULL, 0, 'Place toasted pound cake on serving plates. Top each slice with strawberries and goat cheese. Drizzle with vinegar mixture and sprinkle with cilantro, if desired. Serve immediately.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2848C1114033054C935949CCF4663358', X'5B6FA670BF3C4749B41CDF24B9E0BAC1', X'5B6FA670BF3C4749B41CDF24B9E0BAC1', 0, NULL, NULL, 2, NULL, 0, 'Cut pound cake horizontally into 2 layers.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37EE181DF8E39B43B3F494054945A224', X'5B6FA670BF3C4749B41CDF24B9E0BAC1', X'5B6FA670BF3C4749B41CDF24B9E0BAC1', 1, NULL, NULL, 2, NULL, 0, 'Using a fluted or round cookie cutter, cut pound cake into circles.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'078CD0026EA3AA46B5EB9EDB0711801F', X'5B6FA670BF3C4749B41CDF24B9E0BAC1', X'5B6FA670BF3C4749B41CDF24B9E0BAC1', 2, NULL, NULL, 2, NULL, 0, 'Using the tip of a spoon or your thumb, make indent in the center of each round shape.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27F6F02E2ED51E4A98802EDEEDEB64A2', X'5B6FA670BF3C4749B41CDF24B9E0BAC1', X'5B6FA670BF3C4749B41CDF24B9E0BAC1', 3, NULL, NULL, 2, NULL, 0, 'Fill a sandwich bag with caramel sauce and then snip a very small corner off of the bag. Using this bag, fill each circle with caramel. Make sure to leave some of the cake showing on the edge as the crust.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4F5B42EE253B84190E7632C4C102AAA', X'5B6FA670BF3C4749B41CDF24B9E0BAC1', X'5B6FA670BF3C4749B41CDF24B9E0BAC1', 4, NULL, NULL, 2, NULL, 0, 'Using a new sandwich bag, repeat the above process with melted chocolate chips or chocolate sauce. Drizzle across the caramel sauce.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'581A2D116F83F346BFE1674C182B4A47', X'5B6FA670BF3C4749B41CDF24B9E0BAC1', X'5B6FA670BF3C4749B41CDF24B9E0BAC1', 5, NULL, NULL, 2, NULL, 0, 'Top the pound cake pie with chopped pecans and mini chocolate chips.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DBB4F003425CE4382987A337C765252', X'B895B0908AA06B408EB45A5806547758', X'B895B0908AA06B408EB45A5806547758', 0, NULL, NULL, 2, NULL, 0, 'Cut pound cake horizontally into 3 layers.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4D51B402C5EAC47953AC09E85C0201D', X'B895B0908AA06B408EB45A5806547758', X'B895B0908AA06B408EB45A5806547758', 1, NULL, NULL, 2, NULL, 0, 'Combine milk and pudding in a large bowl; mixing until well blended. Let stand 2-5 minutes or until pudding thickens. Stir in pumpkin and pie spice; mixing until well blended. Fold in whipped topping and chopped pecans.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E756F032E949134B843A23C783A04939', X'B895B0908AA06B408EB45A5806547758', X'B895B0908AA06B408EB45A5806547758', 2, NULL, NULL, 2, NULL, 0, 'Frost bottom layer with pumpkin mixture. Place second layer on top and frost with pumpkin mixture. Top with third layer; frost cake with remaining pumpkin mixture.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A804DB79C45E0347B255C5ECCC4BC816', X'B895B0908AA06B408EB45A5806547758', X'B895B0908AA06B408EB45A5806547758', 3, NULL, NULL, 2, NULL, 0, 'Top with pecan halves, if desired. Refrigerate at least 1-4 hours, before serving.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE3A5EE48DCB4747BA6FA8B16F6E820D', X'B895B0908AA06B408EB45A5806547758', X'B895B0908AA06B408EB45A5806547758', 4, NULL, NULL, 2, NULL, 0, 'Cut into 8 slices to serve.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2BC673DB0AD874B943C28ECB08321BF', X'602769D933B52D4A9C42796040FCF442', X'602769D933B52D4A9C42796040FCF442', 0, NULL, NULL, 2, NULL, 0, 'Slice pound cake horizontally into 3 layers. Using a cookie cutter of a preferred shape, cut pound cake into shapes. Cut remaining pound cake into 1-inch pieces or into smaller shapes.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'511A0FB5104C484D975483C251D2FDF5', X'602769D933B52D4A9C42796040FCF442', X'602769D933B52D4A9C42796040FCF442', 1, NULL, NULL, 2, NULL, 0, 'Place half of the cut-up pound cake pieces on the bottom of a trifle or serving bowl. Place cut-out shapes on side of bowl.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A7730CC88A6494B972A9C41B992CEB6', X'602769D933B52D4A9C42796040FCF442', X'602769D933B52D4A9C42796040FCF442', 2, NULL, NULL, 2, NULL, 0, 'Top with half of the fruit and whipped topping; repeat layers. Garnish with small shapes, if desired. Serve immediately or store in refrigerator until ready to serve.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63FD6ABFE1BB3845B5718E942E031172', X'7948D6AA7754F343961D863C42184D25', X'7948D6AA7754F343961D863C42184D25', 0, NULL, NULL, 2, NULL, 0, 'Slice pound cake slice crosswise in half forming 2 slices. Spread one slice of frozen pound cake with peanut butter and marshmallow cream; sprinkle with chocolate chips; top with second pound cake slice forming a sandwich.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F81E6FB84805B4CAE3BC427DBBCA148', X'7948D6AA7754F343961D863C42184D25', X'7948D6AA7754F343961D863C42184D25', 1, NULL, NULL, 2, NULL, 0, 'Spray both sides of pound cake slices with cooking spray. Grill sandwich in a skillet over medium heat about 1-2 minutes per side or until crisp and toasted. OR, Place on a microwave-safe plate and heat sandwich 20-30 seconds or until chocolate chips are melted.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'899B7F55C6E3534B8D00D806B4DBC9CA', X'D47059BCAF8EA14A844F421F630F81B3', X'D47059BCAF8EA14A844F421F630F81B3', 0, NULL, NULL, 2, NULL, 0, 'Trim the top, bottom and sides of the pound cake just enough to remove the golden brown pieces. Slice the trimmed pound cake into 1-inch cubes.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F9E653FCE178E489DDC6E7504B9D812', X'D47059BCAF8EA14A844F421F630F81B3', X'D47059BCAF8EA14A844F421F630F81B3', 1, NULL, NULL, 2, NULL, 0, 'Place cubes in a single layer on a cookie sheet. Freeze until firm, about 1-2 hours.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC8FB8306D086D4F95CB05ADA8B1E5C7', X'D47059BCAF8EA14A844F421F630F81B3', X'D47059BCAF8EA14A844F421F630F81B3', 2, NULL, NULL, 2, NULL, 0, 'When ready to dip and decorate the bites, place each type of sprinkles, crushed cookies and colored sugars into separate bowls.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'396378497A718846900DE8967CCC677E', X'D47059BCAF8EA14A844F421F630F81B3', X'D47059BCAF8EA14A844F421F630F81B3', 3, NULL, NULL, 2, NULL, 0, 'Place almond bark in a large glass bowl or measuring cup. Microwave on High for 90 seconds; stir. Continue microwaving in 15 second intervals, stirring after each, until melted and smooth.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1602D3234FD4D4998AE4013AC699447', X'D47059BCAF8EA14A844F421F630F81B3', X'D47059BCAF8EA14A844F421F630F81B3', 4, NULL, NULL, 2, NULL, 0, 'Working quickly, dip each pound cake bite into the melted almond bark; sprinkle each with desired sprinkles, crushed cookies or sugar. Place back onto cookie sheet(s). Chill until firm, about 1 hour.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A344FF193AFF443A1426979455F17EC', X'4C962EF8D533794FA65B9F9D5118352F', X'4C962EF8D533794FA65B9F9D5118352F', 0, NULL, NULL, 2, NULL, 0, 'Slice pound cake horizontally into 3 layers.');
