INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0A11D7814B1EB4A8396E7AD48488B24', X'4C962EF8D533794FA65B9F9D5118352F', X'4C962EF8D533794FA65B9F9D5118352F', 2, NULL, NULL, 2, NULL, 0, 'Frost entire cake with thawed chocolate whipped topping frosting. Sprinkle top with shaved chocolate, if desired. Refrigerate until ready to serve.');
INSERT INTO `VWR INTERNAL Saraleedesserts recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98FE7EC3C3BA354397AF134BEC567D11', X'4C962EF8D533794FA65B9F9D5118352F', X'4C962EF8D533794FA65B9F9D5118352F', 3, NULL, NULL, 2, NULL, 0, 'Cut into 8 slices, serve.');
