INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EBEB035DD7DB747A1509364E92871FD', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 0, NULL, NULL, 2, NULL, 0, 'Unwrap peanut butter cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B63A4FA8071508479D3BA3E73B37E4BB', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 1, NULL, NULL, 2, NULL, 0, 'Unwrap caramels and soften them by heating in the microwave on defrost for 5 second increments, kneading after each, until soft.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0450ECC9FAFCF4284D0A0026C9384B9', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 2, NULL, NULL, 2, NULL, 0, 'Flatten 6 of the caramels, and cut using a 1 - 1 1/2 inch heart cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1183892BF46A804BAA52924274DCE3F3', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 3, NULL, NULL, 2, NULL, 0, 'Cut 12 small circles using a small round plunger cutter or pinch off pieces and roll into small balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57CDF8F21FA8904197BDFA6090E51671', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 4, NULL, NULL, 2, NULL, 0, 'Press one red heart sprinkle onto each caramel heart forming the lion''s nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A9D5BED4D53CD4A83532B8553F494F2', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 5, NULL, NULL, 2, NULL, 0, 'Break the remaining 3 heart sprinkles in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5B3F549A9EE774B9AE99DC2C49745D4', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 6, NULL, NULL, 2, NULL, 0, 'Attach one piece with the rounded side facing down, under the curved part of the caramel heart, creating the lion''s tongue.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC6AADBED725D64FA43805ABEA1C22AF', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 7, NULL, NULL, 2, NULL, 0, 'Draw eyes, eyebrows, and freckles onto each caramel heart using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3BA2F183733FB844BC96B4EF39BDF5AF', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 8, NULL, NULL, 2, NULL, 0, 'Attach one caramel heart lion''s face and two caramel ears to each peanut butter cup using melted confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DB9A2F39DDB2F4E80A075A8E234F002', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 9, NULL, NULL, 2, NULL, 0, 'Cut the remaining caramel squares in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02C8BCF67654B446A33ECBD2D089F7EB', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 10, NULL, NULL, 2, NULL, 0, 'Roll each into a ball then into a teardrop shaped paw.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78CD4BBE9B01CC40A4211B1352236AFE', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 11, NULL, NULL, 2, NULL, 0, 'Draw 5 claws onto each paw using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB0959ADBAA0314A86700DA709EB76FC', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 12, NULL, NULL, 2, NULL, 0, 'Wrap each cupcake in a printable `You Make My Heart Roar` cupcake wrapper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC6C74CA25D4DD4C918D8BBF1D311340', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 13, NULL, NULL, 2, NULL, 0, 'Spoon frosting into a large pastry bag fitted with a large 1M star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CEAADA673135A4E93DDFB500AC3A08E', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 14, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of frosting on top of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D36AB49A68487B4A8A65FF8D5E310D51', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 15, NULL, NULL, 2, NULL, 0, 'Press a Reese''s Cup lion onto the front at the top of each frosting swirl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A703CFA44856D418A5D447B4BA0155B', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 16, NULL, NULL, 2, NULL, 0, 'Press a conversation heart onto the frosting just below the lion''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A903711634ED0A48A3D135F408E44FEF', X'4069FEABDAB3494280564EC510FC07F6', X'4069FEABDAB3494280564EC510FC07F6', 17, NULL, NULL, 2, NULL, 0, 'Attach one paw on either side of the candy heart.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80371438312BE8459C4A7FE1CCE756B1', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3976D8C36F3FD64CB1E7550EC926D958', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 1, NULL, NULL, 2, NULL, 0, 'Either line a 9 inch by 13 inch baking pan with non-stick tin foil or spray it with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2761D2FF69E8F24DADE7503CD4D6EAC0', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 2, NULL, NULL, 2, NULL, 0, 'Combine cake mix, pudding mix, sour cream, milk, oil, and eggs in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7E6ED21787C6C4AB17036974C993CEF', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 3, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on low speed for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'820A87896CFECA439A2C96EDF0933007', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 4, NULL, NULL, 2, NULL, 0, 'Increase speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6304C572EE9EC64DA1B688A3F04DDB5B', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 5, NULL, NULL, 2, NULL, 0, 'Pour into pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A32D2F025F441247A520CB1060E824BA', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 6, NULL, NULL, 2, NULL, 0, 'Bake for 28-34 minutes until a toothpick inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7C324B692BB834AB1AA1FAAEE12D59F', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 7, NULL, NULL, 2, NULL, 0, 'Allow cake to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'636BDAB23F70714298C4F92E51119451', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 8, NULL, NULL, 2, NULL, 0, 'Crumble cake into a large bowl, preferable the bowl of a stand mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'928A2D1C19D48D479905DD3F25C2F7DC', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 9, NULL, NULL, 2, NULL, 0, 'Add frosting and stir or beat until well blended and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D89ACA502E7A2F4A8508C9DAE1622665', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 10, NULL, NULL, 2, NULL, 0, 'Scoop out 48 tablespoon size portions of the cake ball dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB1636048D4F744D86294CE0C3D898E9', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 11, NULL, NULL, 2, NULL, 0, 'Create an indentation in one cake ball and press a maraschino cherry into it.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6ED8744EE8305F4F80A3B3AB96E2F24E', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 12, NULL, NULL, 2, NULL, 0, 'Press the cake ball dough around the cherry.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'215013FD449D134AB3411528D9F0A0C9', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 13, NULL, NULL, 2, NULL, 0, 'Roll into a ball with the stem sticking out.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D93928C4AAF3F7438495C0A85923D300', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 14, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42F968AEE03B674A846EFE5C71A7DA8A', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 15, NULL, NULL, 2, NULL, 0, 'Let cake balls sit at room temperature for 1 hour to firm up a bit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75E3DF9B9F8C08438DCEDD802D0E754F', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 16, NULL, NULL, 2, NULL, 0, 'Dip each cake ball into dark chocolate, allowing the excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D8663254C24A04BBCB414D33262A6B8', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 17, NULL, NULL, 2, NULL, 0, 'Set on a parchment lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9ABAF566F05304CAA871EC246824E24', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 18, NULL, NULL, 2, NULL, 0, 'If using pure chocolate, chill the Chocolate Cherry Bombs in the refrigerator until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'733EE0CB3EC19047B5D64622F27A9F6C', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 19, NULL, NULL, 2, NULL, 0, 'If using candy melts pop them in the freezer, instead.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DC38EA99566E646806E98E4055A1B6B', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', X'2E0FFE2AC7E01847B5F075CC8BBD7EC2', 20, NULL, NULL, 2, NULL, 0, 'Let the cherry bombs sit at room temperature for at least 30 minutes before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC72B64513D8284DA5749F54B9FF2374', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'641D446CE61A7245BCF8A5A5B25A164F', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 1, NULL, NULL, 2, NULL, 0, 'Either line a 9 inch by 13-inch baking pan with non-stick tin foil or spray it with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65E7D96D6D897646B32DBB59EC37D569', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 2, NULL, NULL, 2, NULL, 0, 'Combine cake mix, pudding mix, sour cream, milk, oil, and eggs in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1692998F8E7D8C4D95C4BD438848B33F', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 3, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on low speed for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CC5E1C477C26240BE272A8D4C44BF19', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 4, NULL, NULL, 2, NULL, 0, 'Increase speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77F6BB96CB9F8F4985865C251436412A', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 5, NULL, NULL, 2, NULL, 0, 'Pour into pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9243562E2ED70947AAE2DCC9C89D21A8', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 6, NULL, NULL, 2, NULL, 0, 'Bake for 28-34 minutes until a toothpick inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27FFABE6908A464FBC45E375E8EAEACB', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 7, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEE880B0BCDA6E45B27329F0F2DF9843', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 8, NULL, NULL, 2, NULL, 0, 'Crumble the cake into a large bowl, preferably the bowl of a stand mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D702DCE38D125545AF6E46B18CADB5D0', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 9, NULL, NULL, 2, NULL, 0, 'Add frosting and stir or beat until well blended and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F6605AD2E3DFC4BB0FE3790F6630736', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 10, NULL, NULL, 2, NULL, 0, 'Scoop out 48 tablespoon-size portions of the cake ball dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D13E6861AFD4F4E8C34C5A4B21A4D9B', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 11, NULL, NULL, 2, NULL, 0, 'Roll them into balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F09F7F2A72673449FE2849029142D1E', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 12, NULL, NULL, 2, NULL, 0, 'Set aside to dry for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4C8F21DA8D1A747A594F7D0A83199E3', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 13, NULL, NULL, 2, NULL, 0, 'Meanwhile, pour the melted candy melts into a squeeze bottle, disposable pastry bag, or zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B537105854B724294C203CBAD397ADD', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 14, NULL, NULL, 2, NULL, 0, 'Cut the tip off if using a disposable pastry bag or zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35A9E070F43C0F43B6F84B5EAD8444B7', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 15, NULL, NULL, 2, NULL, 0, 'Set warm fuzzy feet template on a baking sheet and cover with a piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABFAC378ECA3EB458E003D2BE6959D03', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 16, NULL, NULL, 2, NULL, 0, 'Pipe the melted candy melts over the template creating two feet for each warm fuzzy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'596FECC9D0F6A04A8334A347559BB6AB', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 17, NULL, NULL, 2, NULL, 0, 'Pop them in the freezer for about 3 minutes just until they harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50CC8C62437B874BA488B7E51E07E198', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 18, NULL, NULL, 2, NULL, 0, 'Pipe antenna onto a piece of parchment paper, adding sprinkles to the tips if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BCB0351D664C649BDB9F0C8F84F37DE', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 19, NULL, NULL, 2, NULL, 0, 'Let them harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6983EE853EBF1A49A73F2B23BCD20F7D', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 20, NULL, NULL, 2, NULL, 0, 'Dip the cake balls in melted white candy melts and immediately roll each ball in colored coconut and insert two antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A176AEA2F89569449B64E53D08DDB49F', X'E51383BF0E28C9439281D346CEE85CE2', X'E51383BF0E28C9439281D346CEE85CE2', 21, NULL, NULL, 2, NULL, 0, 'Use melted white candy melts to attach two candy eyes and two feet to each warm fuzzy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FF7DC617584AB4DB363FA36BE19D6CF', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 0, NULL, NULL, 2, NULL, 0, 'Set your cream cheese, sour cream, and egg out on the counter to come to room temperature at least one hour, but preferably 2 hours, before you plan to bake your cheesecakes. You need to use room temperature ingredients!');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3759D985C2A815418825070B063746BA', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 1, NULL, NULL, 2, NULL, 0, 'Place a rimmed cookie sheet or baking pan filled with 1/2-inch to 1-inch of water on the lowest rack in your oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09F8DD60FA106C45976264BCD736344B', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 2, NULL, NULL, 2, NULL, 0, 'Arrange another rack in the middle of the oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4526F12A19E2043A91BD1806621FB1C', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 3, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 300 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E561086AD0A86D43954CE24203BB6333', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 4, NULL, NULL, 2, NULL, 0, 'Crush 12 OREO Cookies into fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1BDD30E5E91A348991714F879015B6D', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 5, NULL, NULL, 2, NULL, 0, 'Stir 2 tablespoons of melted butter into the cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'102C8B68DEF21A4DA8CE78A5AF5E8726', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 6, NULL, NULL, 2, NULL, 0, 'Scoop out about one tablespoon of the cookie crumbs and drop into a paper-lined cupcake pan. Repeat, filling 12 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAD6E1BF33C54943BA769BA2B7C6042D', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 7, NULL, NULL, 2, NULL, 0, 'Press the crumbs down into an even layer in the bottom of the cupcake wrapper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'569859E02700714380C0F6A5E5EFE825', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 8, NULL, NULL, 2, NULL, 0, 'Place 8 ounces of softened cream cheese and 1/3 cup granulated sugar in a large mixing bowl and beat using an electric mixer on low speed just until it is creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D997147D7B539B4AB40964766CD7F0EC', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 9, NULL, NULL, 2, NULL, 0, 'Add the room temperature sour cream and vanilla and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1C212F46E0DE747A22FFB078BEF8ED4', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 10, NULL, NULL, 2, NULL, 0, 'Stir in 4 ounces of melted semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72A8A0BE11431947BF08D2D1809FF3F7', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 11, NULL, NULL, 2, NULL, 0, 'Then pour a lightly beaten, room temperature, egg over the chocolate mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA58594E3B3E9446BDC887E7BF0D39FB', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 12, NULL, NULL, 2, NULL, 0, 'Stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'247042B13CAFF74281F59617C69D18D4', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 13, NULL, NULL, 2, NULL, 0, 'Scoop a little more than 2 tablespoons of the chocolate cheesecake filling over each cookie crust and spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'799B9EE266CEC24A8AB344A58FDFFD67', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 14, NULL, NULL, 2, NULL, 0, 'Tap the pan on the counter about 10 times to allow any air bubbles to rise to the surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BE5459566A875449A29B7340FF9B82D', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 15, NULL, NULL, 2, NULL, 0, 'Place on the middle rack in the oven and bake for 24-28 minutes until the tops look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C39DE6B0012644D911C1233A1F2EFDC', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 16, NULL, NULL, 2, NULL, 0, 'The cheesecakes should still jiggle when the pan is jostled and will be glossy but should not look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08BD5ED558D8EC4DA52C36846FB18ED0', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 17, NULL, NULL, 2, NULL, 0, 'Turn the oven off, leave the cheesecakes inside, but crack the door open.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F51F6657EEE494CB5FE8E1B2DD121A1', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 18, NULL, NULL, 2, NULL, 0, 'Allow the cheesecakes to rest in the warm oven for 45 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABF859349BA18147921094333426A610', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 19, NULL, NULL, 2, NULL, 0, 'Remove and allow the cheesecakes to cool on the counter for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C57159BCE88154083F41D639E8BAC81', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 20, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'914846FA92C7B54492247E46226F6202', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 21, NULL, NULL, 2, NULL, 0, 'Remove from the pan and peel off the paper before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C54C438CBE98B4D8A0E6A2B9A744338', X'3B51CE7E84E47547926032753BC1787E', X'3B51CE7E84E47547926032753BC1787E', 22, NULL, NULL, 2, NULL, 0, 'Add toppings, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE5033A3A3A7E04BAC5FAB3C68A703B8', X'7EFE64ECC9D1B844BD2C9227E4D833A2', X'7EFE64ECC9D1B844BD2C9227E4D833A2', 0, NULL, NULL, 2, NULL, 0, 'Finely chop or shave your chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50722C14166B5D4A86EE60276FB36A9B', X'7EFE64ECC9D1B844BD2C9227E4D833A2', X'7EFE64ECC9D1B844BD2C9227E4D833A2', 1, NULL, NULL, 2, NULL, 0, 'Heat the heavy whipping cream in a saucepan over medium heat just until bubbles begin to form around the edges of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D73B1471334DA349AEF3E4A4CE1E4788', X'074FDB32C4A92840872B1BD945F81B36', X'074FDB32C4A92840872B1BD945F81B36', 0, NULL, NULL, 2, NULL, 0, 'Pour chopped chocolate into a shallow bowl, creating a small well in the center of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15CCE677FDD7D34F9905100FE8F2BE51', X'074FDB32C4A92840872B1BD945F81B36', X'074FDB32C4A92840872B1BD945F81B36', 1, NULL, NULL, 2, NULL, 0, 'Pour the hot liquid over the chocolate allowing the cream to flow into the center of the bowl and underneath the chocolate. Make sure all the chocolate is submerged under the cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FFA7527FE02F1345A0C730AB751B288F', X'074FDB32C4A92840872B1BD945F81B36', X'074FDB32C4A92840872B1BD945F81B36', 2, NULL, NULL, 2, NULL, 0, 'Let sit for 3 minutes allowing the chocolate to begin to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7CAAA7B42686564DA905242DE448F752', X'074FDB32C4A92840872B1BD945F81B36', X'074FDB32C4A92840872B1BD945F81B36', 3, NULL, NULL, 2, NULL, 0, 'Begin stirring in a small circle in the center of the bowl to create an emulsion of the chocolate and cream then widen the circle slowly until all of the chocolate and cream blend together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B3EDFE5B741464C8B434D486305738B', X'074FDB32C4A92840872B1BD945F81B36', X'074FDB32C4A92840872B1BD945F81B36', 4, NULL, NULL, 2, NULL, 0, 'As soon as the chocolate and cream come together, and your chocolate ganache looks shiny and smooth, stop stirring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'28F5E4B1D59F8849B11705B7B8A315F1', X'074FDB32C4A92840872B1BD945F81B36', X'074FDB32C4A92840872B1BD945F81B36', 5, NULL, NULL, 2, NULL, 0, 'Cover the ganache directly with plastic wrap or with a tight-fitting lid and set aside until it firms up, at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC6BD79C975EA146B487E80CFE4154E3', X'0B2388E79C40C247A3D395FF0D7171F1', X'0B2388E79C40C247A3D395FF0D7171F1', 0, NULL, NULL, 2, NULL, 0, 'Pour the hot cream over the chopped chocolate and close the lid.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'994E4C22AEC9FB4BB4F6BFBA71BC55DB', X'0B2388E79C40C247A3D395FF0D7171F1', X'0B2388E79C40C247A3D395FF0D7171F1', 1, NULL, NULL, 2, NULL, 0, 'Pulse twice then remove the lid and scrape the sides of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE86BA787525F640BFD3ECE5764351A8', X'0B2388E79C40C247A3D395FF0D7171F1', X'0B2388E79C40C247A3D395FF0D7171F1', 2, NULL, NULL, 2, NULL, 0, 'Replace the lid and let the chocolate melt for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66A8E938256B1A4E898EF40D1EEF3320', X'0B2388E79C40C247A3D395FF0D7171F1', X'0B2388E79C40C247A3D395FF0D7171F1', 3, NULL, NULL, 2, NULL, 0, 'Pulse 3-6 times just until the chocolate and cream come together and looks glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B1AF880DFB32C48A35143E0A6E345BE', X'0B2388E79C40C247A3D395FF0D7171F1', X'0B2388E79C40C247A3D395FF0D7171F1', 4, NULL, NULL, 2, NULL, 0, 'Pour into a bowl, cover, and let rest at room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'768924A6AB01464EAFA56912BCD5A00B', X'BB231EC908E1C54C904933841E8BBEC4', X'BB231EC908E1C54C904933841E8BBEC4', 0, NULL, NULL, 2, NULL, 0, 'Pour 1/4 of the hot cream into a tall immersion blender cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2314D0BC09C8CC43A2954DF8C7B57BF7', X'BB231EC908E1C54C904933841E8BBEC4', X'BB231EC908E1C54C904933841E8BBEC4', 1, NULL, NULL, 2, NULL, 0, 'Add the finely chopped chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D367558B6020ED45A38A5FC7A038B5AC', X'BB231EC908E1C54C904933841E8BBEC4', X'BB231EC908E1C54C904933841E8BBEC4', 2, NULL, NULL, 2, NULL, 0, 'Pour the remaining hot cream over top of the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D4590E122DA844582A7C6DBE0D660AE', X'BB231EC908E1C54C904933841E8BBEC4', X'BB231EC908E1C54C904933841E8BBEC4', 3, NULL, NULL, 2, NULL, 0, 'Set it aside for about 2 minutes to allow the chocolate to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38A1CC4560AF0748A96275F6C107DCBF', X'BB231EC908E1C54C904933841E8BBEC4', X'BB231EC908E1C54C904933841E8BBEC4', 4, NULL, NULL, 2, NULL, 0, 'Use the immersion blender to combine the chocolate and cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0A958037DBFEE448C80B28E0CBB1102', X'BB231EC908E1C54C904933841E8BBEC4', X'BB231EC908E1C54C904933841E8BBEC4', 5, NULL, NULL, 2, NULL, 0, 'Scrape the sides of the cup and pulse again just until the ganache is smooth, glossy, and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'483190DD1D5CAA4E87DDF829874B9F10', X'BB231EC908E1C54C904933841E8BBEC4', X'BB231EC908E1C54C904933841E8BBEC4', 6, NULL, NULL, 2, NULL, 0, 'Pour into a shallow bowl, cover, and set aside for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B84AEBA0911FF43855D3BCDE3BF4F26', X'0B193F096EF1C846B04DF7CE2BB4AA16', X'0B193F096EF1C846B04DF7CE2BB4AA16', 0, NULL, NULL, 2, NULL, 0, 'Using a small ice cream scoop or spoon, scoop the ganache onto a parchment paper-lined baking sheet and let rest for about 30 minutes until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DD6964142C5244DB14192B3DDFE82CA', X'0B193F096EF1C846B04DF7CE2BB4AA16', X'0B193F096EF1C846B04DF7CE2BB4AA16', 1, NULL, NULL, 2, NULL, 0, 'Roll a scoop of ganache into a ball then immediately roll into the toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0439548D42B27248B0C185DA68FBEE9D', X'0B193F096EF1C846B04DF7CE2BB4AA16', X'0B193F096EF1C846B04DF7CE2BB4AA16', 2, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5FB14CCF0905C40B9F5268F3115D922', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B1994054D64AC4A97AC693C5A71E8D7', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 1, NULL, NULL, 2, NULL, 0, 'Spread almonds out on a baking sheet, and toast for 5-6 minutes, until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A7677F2FBB42D439C4265B5FE0B5B13', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 2, NULL, NULL, 2, NULL, 0, 'Remove and pour nuts into the bowl of a food processor and let cool for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'101FE609CD091749A6BC2E890EDC4593', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 3, NULL, NULL, 2, NULL, 0, 'Add 1/4 cup of the sugar and pulse until the nuts become finely ground.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'864E81EE413F2F4F8634E9765AC5E8BD', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 4, NULL, NULL, 2, NULL, 0, 'Whisk the flour, salt, lemon zest, and cinnamon, if using, together then set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D9BA0DB030F4D49898087FCE73EE220', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 5, NULL, NULL, 2, NULL, 0, 'Cream the butter and the remaining 1/2 cup sugar together in a large mixing bowl just until it becomes pale yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8F9090F01883B468FA2C037D0C34E33', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 6, NULL, NULL, 2, NULL, 0, 'Beat in vanilla and the egg yolks until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16CB50C038DF9F48B602717486BD71A7', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 7, NULL, NULL, 2, NULL, 0, 'Mix in the ground almonds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3E34DC67087CD4DBB1838ECC2E2610E', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 8, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients and mix just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CE23CEEC93B2F41BD179D3F170746B7', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 9, NULL, NULL, 2, NULL, 0, 'Remove the dough from the bowl, divide in half, then flatten into two discs and place each half into a zip top bag or wrap in plastic wrap.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A4714545DD2754AAB4FCAE6FB489A1D', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 10, NULL, NULL, 2, NULL, 0, 'Refrigerate the dough for about 15-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F956124B4B26941BED93EF11FA1A967', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 11, NULL, NULL, 2, NULL, 0, 'Dust a cutting board with flour or use a silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D6D40855A791C489AB98A99937637DA', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 12, NULL, NULL, 2, NULL, 0, 'Roll 1 disc of chilled cookie dough out to 1/8 inch thickness onto the cutting board or silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EECB718ADF25A645AFF5CD42D191939F', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 13, NULL, NULL, 2, NULL, 0, 'Cut out as many shapes as you can using Linzer cutters or cookie cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B78D824979856C449FF7E6A3BE851A3B', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 14, NULL, NULL, 2, NULL, 0, 'Remove excess dough, re-roll and continue to cut out shapes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA25632DC3E69244ADCDD114558DF426', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 15, NULL, NULL, 2, NULL, 0, 'Chill cut-outs in the freezer for 5-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A0DAB36EF2BCF49A100E60DA7384BE1', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 16, NULL, NULL, 2, NULL, 0, 'Move cookies to a parchment lined baking sheet. (see tutorial for tips)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1DCC6A73529CE4E9985E66F171D31A6', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 17, NULL, NULL, 2, NULL, 0, 'Bake for 8-12 minutes until lightly golden brown. The time will depend on the size of the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7B7EAEF94F99043BBD984F36A4E1527', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 18, NULL, NULL, 2, NULL, 0, 'Roll the remaining disc of dough and cut out the same number and same shapes as before re-rolling as needed using the Linzer cutter fitted with the small cutter (which will cut the hole at the same time) or using traditional cookie cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48E143107DD71F42B686E6A034993AED', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 19, NULL, NULL, 2, NULL, 0, 'If needed, cut a smaller hole in the center of each cookie using 1/2 inch to 1 inch cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56886E3A2B82004496EFAD27143A427E', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 20, NULL, NULL, 2, NULL, 0, 'Remove excess dough re-roll and cut out more cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13FD51A7F93C4841895F2CADE529A3CC', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 21, NULL, NULL, 2, NULL, 0, 'Freeze the cut-out dough for 5-10 minutes then move to parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E92592A71DB2F146AAC62F7B5C421EC2', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 22, NULL, NULL, 2, NULL, 0, 'Bake for about 8-12 minutes until lightly golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC4F6EF2D10CE44BAEF88FC181C0190F', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 23, NULL, NULL, 2, NULL, 0, 'Allow all the cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E85F931168856479EC11FDD3031C005', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 24, NULL, NULL, 2, NULL, 0, 'Dust the cookies with the holes in the middle with powdered sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8331887D27F9CF48953A25E16F47A803', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 25, NULL, NULL, 2, NULL, 0, 'Spread preserves, jam, curd, or chocolate spread on the whole cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7651A2BD4D0B984C90123BD6A1EA545E', X'BD4EA3E490F01C4C9250C4731989987B', X'BD4EA3E490F01C4C9250C4731989987B', 26, NULL, NULL, 2, NULL, 0, 'Place one powdered sugar dusted cut-out cookie on top of the jam and gently sandwich the cookies together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D32C195595C3448A58D488D3AFF9CC7', X'958A430FB0031B40B85A0F397BADD2C6', X'958A430FB0031B40B85A0F397BADD2C6', 0, NULL, NULL, 2, NULL, 0, 'Either allow the frozen raspberries to thaw then press them through a fine-mesh sieve to remove the seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BD9DED1BCE77445A0067F03CFD8F249', X'958A430FB0031B40B85A0F397BADD2C6', X'958A430FB0031B40B85A0F397BADD2C6', 1, NULL, NULL, 2, NULL, 0, 'Or heat the raspberries in a medium saucepan set over medium heat until the berries break down then press them through the sieve to remove the seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6082CDC8A6B3704BB33D6428EE4F88E3', X'958A430FB0031B40B85A0F397BADD2C6', X'958A430FB0031B40B85A0F397BADD2C6', 2, NULL, NULL, 2, NULL, 0, 'You should have about 3/4 cup of puree.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9D71ABC45390D46BD8D671393ADEA5C', X'E0DF706D3D20D94CABC1200B5F07688B', X'E0DF706D3D20D94CABC1200B5F07688B', 0, NULL, NULL, 2, NULL, 0, 'Heat the raspberry puree in the saucepan set over medium heat just until bubbles begin to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'778B01C21FEC894083917A36E72FF8CE', X'E0DF706D3D20D94CABC1200B5F07688B', X'E0DF706D3D20D94CABC1200B5F07688B', 1, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat and let it rest for one minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68D15EAC03838E44819A12CCB8FA1C7C', X'E0DF706D3D20D94CABC1200B5F07688B', X'E0DF706D3D20D94CABC1200B5F07688B', 2, NULL, NULL, 2, NULL, 0, 'Then pour the chocolate chips into the pan and stir just to coat the chocolate in the puree.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D214CA875088BE40AAEA2FC88D5C4FB4', X'E0DF706D3D20D94CABC1200B5F07688B', X'E0DF706D3D20D94CABC1200B5F07688B', 3, NULL, NULL, 2, NULL, 0, 'Set the pan aside for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F429657FCC492409E721E27113E6DB4', X'E0DF706D3D20D94CABC1200B5F07688B', X'E0DF706D3D20D94CABC1200B5F07688B', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, whisk or beat the cream until it comes to stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A15CBF71C918A6428FE1459E539E96DD', X'E0DF706D3D20D94CABC1200B5F07688B', X'E0DF706D3D20D94CABC1200B5F07688B', 5, NULL, NULL, 2, NULL, 0, 'Stir the chocolate and raspberry puree until smooth then pour it into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7E3EBAC844AEF469E92270F17DCC774', X'E0DF706D3D20D94CABC1200B5F07688B', X'E0DF706D3D20D94CABC1200B5F07688B', 6, NULL, NULL, 2, NULL, 0, 'Stir 1/3 of the whipped cream into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8462DF57E6DEB3449202778FD2C4E7E7', X'E0DF706D3D20D94CABC1200B5F07688B', X'E0DF706D3D20D94CABC1200B5F07688B', 7, NULL, NULL, 2, NULL, 0, 'Then fold in the remaining cream until all the white streaks are gone.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D666799DDFE6DD4D80F3F99EA5631399', X'E0DF706D3D20D94CABC1200B5F07688B', X'E0DF706D3D20D94CABC1200B5F07688B', 8, NULL, NULL, 2, NULL, 0, 'Chill until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57B6AE37D38D434AB1BCE1058C23C696', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'037C5FC969121B44843F3C107C81395F', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 1, NULL, NULL, 2, NULL, 0, 'Make cake batter using the package instructions and ingredients (oil, water and egg whites) adding 3 tablespoons of instant pudding mix and 3 tablespoons sour cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B4BB897CD62F74AA0A24CC08FB7A26F', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 2, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with non-stick tin foil or parchment paper OR grease and flour 3 (6-inch) heart pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D27B9F5F6BA3054C9DA9EA5315528FBB', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 3, NULL, NULL, 2, NULL, 0, 'Spread cake batter in an even layer in pan/s.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'713434FD68DB2945BC1FE8FEDA496730', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 4, NULL, NULL, 2, NULL, 0, 'Bake sheet cake for 18-22 minutes OR bake heart cakes for 24-30 minutes until lightly golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59E66515A6606949859711EF84AF1F15', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 5, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool completely then remove cake/s from pan/s.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'070EE6B094330141A5653CF9C9F6F3DF', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 6, NULL, NULL, 2, NULL, 0, 'If using a sheet cake cut 6 hearts out using a 5 or 6-inch heart-shaped cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A14630AF995D04BA53EEAB258FB134A', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 7, NULL, NULL, 2, NULL, 0, 'If you baked heart-shaped cakes, cut off the domed tops of the cakes, then cut the cake into two equally thick layers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4ADD893AA7C97C498D47E2AE79B9A58A', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 8, NULL, NULL, 2, NULL, 0, 'Use the smaller cookie cutter to cut the centers out of 4 of the hearts, leaving 2 heart cakes intact.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C89C4196769AA42A40EF8E0ABDFDCB9', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 9, NULL, NULL, 2, NULL, 0, 'Cut a small cake board into a heart shape, that is at least 1/4-inch larger than your heart cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD8CE86AC52CF143B54FFEB333D241F6', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 10, NULL, NULL, 2, NULL, 0, 'Set one full cake on the cake board.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11BA30C77E988445B75959B0ABB174E6', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 11, NULL, NULL, 2, NULL, 0, 'Pipe some icing around the edge of the cake, then stack a cake with a heart-shaped hole in the center on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6B27975104EB44CAE38A4E98F61B63D', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 12, NULL, NULL, 2, NULL, 0, 'Repeat, frosting and stacking the remaining cut-out cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24F98275A78F9E44A77FCB6131F13DFE', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 13, NULL, NULL, 2, NULL, 0, 'Pour conversation heart candies into the center of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B91CEBE79BC2FA428AB472A38BC567A3', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 14, NULL, NULL, 2, NULL, 0, 'Pipe frosting around the edge of the top cut-out cake and set the remaining whole cake on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'232B24425950804A9373E01446D54BE8', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 15, NULL, NULL, 2, NULL, 0, 'Color the remaining white frosting using pink coloring, or whatever color you choose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DEBF0EB0381B694C85BC52CB79971019', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 16, NULL, NULL, 2, NULL, 0, 'If desired, crumb coat the cake then frost the cake with a thick layer of frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EBD90FF3DC233468B07F852AC017328', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 17, NULL, NULL, 2, NULL, 0, 'To add a phrase to the top of your cake, you can cut letters out of red modeling chocolate or fondant, and place them on top of the cake or use red frosting to pipe them on the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'923256DFCD60C44CBAE452363838B7E5', X'2367D8F17AF7584AB8283EF9A6EE2F0C', X'2367D8F17AF7584AB8283EF9A6EE2F0C', 18, NULL, NULL, 2, NULL, 0, 'Just before serving, cut into the cake and allow the candy to fall out.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4741A20A7B1012448561CA990C89BDC0', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 0, NULL, NULL, 2, NULL, 0, 'Pat chicken breasts dry with paper towels.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F332CCB38C21C47A19A3EB91E3487D6', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 1, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26F9F9DF2F55074786C24706D558A384', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 2, NULL, NULL, 2, NULL, 0, 'Cover and set aside for 10 minutes to allow the salt to tenderize the chicken.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FFCCE62C8DA3846B496FC0AEF015444', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 3, NULL, NULL, 2, NULL, 0, 'Pour Mazola Corn Oil into skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAFD010A12D94042834C642F68984EB9', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 4, NULL, NULL, 2, NULL, 0, 'Set over medium-high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E8D739BEC667C4DABAB8C91834518F1', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 5, NULL, NULL, 2, NULL, 0, 'Heat until the oil begins to shimmer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0FFDAC093282A42968B19A372761EF9', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 6, NULL, NULL, 2, NULL, 0, 'Pat the chicken dry again then place into the hot oil in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97C3077D2091414B838BCEA34C47D6FC', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 7, NULL, NULL, 2, NULL, 0, 'Cook 2-3 minutes on each side until the chicken reaches an internal temperature of 165 degrees Fahrenheit (74 degrees C).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1EF240D345EB644879A66884486461F', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 8, NULL, NULL, 2, NULL, 0, 'Remove from the heat, tent with tin foil, and allow to rest for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81C6C8416FF15546A2251033DA03D072', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 9, NULL, NULL, 2, NULL, 0, 'Cut each chicken breast crosswise into slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40E72F5AF057C340855FC37B33D27436', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 10, NULL, NULL, 2, NULL, 0, 'Set one chicken breast onto each of your dinner plates.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DCD658A2F56704BA2D4D04BE0B95F57', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 11, NULL, NULL, 2, NULL, 0, 'Arrange the diced tomatoes next to the chicken breast forming a heart shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'356EFB775634D5488EB2F4F28F8301F2', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 12, NULL, NULL, 2, NULL, 0, 'Sprinkle 1/2 of the feta cheese and 1/2 of the basil over top of the tomatoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44DA3DC0613FAA4B98821A1F6FCC4468', X'40FD3B486B612B4997F4E41DCF93D8B4', X'40FD3B486B612B4997F4E41DCF93D8B4', 13, NULL, NULL, 2, NULL, 0, 'Drizzle balsamic glaze over top of the chicken and tomatoes and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70036C6C8950EE42BCAF0142EEF2AB2C', X'6DC897C1EE3F25479CB1A83246C0000B', X'6DC897C1EE3F25479CB1A83246C0000B', 0, NULL, NULL, 2, NULL, 0, 'Combine graham cracker crumbs, cinnamon, and butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'705D551233483F48BEB34DEB0395AFB7', X'6DC897C1EE3F25479CB1A83246C0000B', X'6DC897C1EE3F25479CB1A83246C0000B', 1, NULL, NULL, 2, NULL, 0, 'Spoon about a tablespoonful into each heart cavity. Press down into an even crust. Refrigerate until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0058C9FC352D56469B6231998A5C9E2A', X'6DC897C1EE3F25479CB1A83246C0000B', X'6DC897C1EE3F25479CB1A83246C0000B', 2, NULL, NULL, 2, NULL, 0, 'Beat cream cheese. Add sugar and beat until light and fluffy. Beat in eggs and sour cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A6F392DC5A7A944B889EA161EF8BDA8', X'6DC897C1EE3F25479CB1A83246C0000B', X'6DC897C1EE3F25479CB1A83246C0000B', 3, NULL, NULL, 2, NULL, 0, 'Equally, divide cheesecake batter into 6 bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE7AB479A206A542960B954C3F5BF32B', X'6DC897C1EE3F25479CB1A83246C0000B', X'6DC897C1EE3F25479CB1A83246C0000B', 4, NULL, NULL, 2, NULL, 0, 'Add fruit purees into bowl to create a bowl of blue, pink, yellow, orange, green and white batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FBD8BDD955CD44E9AB1CAEBB6787B3C', X'6DC897C1EE3F25479CB1A83246C0000B', X'6DC897C1EE3F25479CB1A83246C0000B', 5, NULL, NULL, 2, NULL, 0, 'Pour colored batter into heart molds, making three of each color.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62C54CAE3BE21C42BB35D9FF2E66840D', X'6DC897C1EE3F25479CB1A83246C0000B', X'6DC897C1EE3F25479CB1A83246C0000B', 6, NULL, NULL, 2, NULL, 0, 'Bake in a water bath at 325 degrees Fahrenheit for 22-25 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0D2D2245C04E84F94117B1A94BBF5EE', X'6DC897C1EE3F25479CB1A83246C0000B', X'6DC897C1EE3F25479CB1A83246C0000B', 7, NULL, NULL, 2, NULL, 0, 'Cool at room temperature for an hour then freeze for at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A96F25384D7BD42993BEDA80C74C185', X'6DC897C1EE3F25479CB1A83246C0000B', X'6DC897C1EE3F25479CB1A83246C0000B', 8, NULL, NULL, 2, NULL, 0, 'Un-mold and use red puree to paint sentiments onto the hearts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7031DE0A96847548BDFAC4B6BAAB2267', X'6DC897C1EE3F25479CB1A83246C0000B', X'6DC897C1EE3F25479CB1A83246C0000B', 9, NULL, NULL, 2, NULL, 0, 'Keep refrigerated until serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82E13494279322419D57D2C76503B19D', X'42E856B6543C1741884511AE95C9CFC2', X'42E856B6543C1741884511AE95C9CFC2', 0, NULL, NULL, 2, NULL, 0, 'Unroll pie crust onto a large cutting board. Cut 24 hearts using the larger heart cutter, re-rolling dough as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30ECABEAB7BF2149AD5310B85A0F62B0', X'42E856B6543C1741884511AE95C9CFC2', X'42E856B6543C1741884511AE95C9CFC2', 1, NULL, NULL, 2, NULL, 0, 'Cut 12 hearts using the smaller heart cutter out of the mozzarella cheese slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65DBBF608EC57E46B217F55153E122EB', X'42E856B6543C1741884511AE95C9CFC2', X'42E856B6543C1741884511AE95C9CFC2', 2, NULL, NULL, 2, NULL, 0, 'Brush egg white over one dough heart, place a mozzarella cheese heart in the center, top it with a second dough heart, press the edges of the dough together. Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E93F3DEB75F0FE4E90791C9A5AD9ACAC', X'42E856B6543C1741884511AE95C9CFC2', X'42E856B6543C1741884511AE95C9CFC2', 3, NULL, NULL, 2, NULL, 0, 'Crimp edges of hearts with a fork.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56C2E9880EE41C4EB9A71FCDD81BE43E', X'42E856B6543C1741884511AE95C9CFC2', X'42E856B6543C1741884511AE95C9CFC2', 4, NULL, NULL, 2, NULL, 0, 'Brush tops of ravioli with egg white. Place on a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10E107E6451182428EB08925627C55FD', X'42E856B6543C1741884511AE95C9CFC2', X'42E856B6543C1741884511AE95C9CFC2', 5, NULL, NULL, 2, NULL, 0, 'Optional: Sprinkle Parmesan cheese over the tops of each heart. If you prefer you can brush the egg white in a heart pattern on the dough and sprinkle the cheese over top or just leave them plain. I personally like how they look and taste completely covered in the grated Parmesan cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD85D5D9B4421849837B40B65C1A9E9D', X'42E856B6543C1741884511AE95C9CFC2', X'42E856B6543C1741884511AE95C9CFC2', 6, NULL, NULL, 2, NULL, 0, 'Freeze hearts for 15-20 minutes. Meanwhile, preheat oven to 400 degrees and make pesto.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EBFD76A9D543F4EBE31947B55AB04CB', X'42E856B6543C1741884511AE95C9CFC2', X'42E856B6543C1741884511AE95C9CFC2', 7, NULL, NULL, 2, NULL, 0, 'Remove the hearts from the freezer and bake for 8-12 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EE1107D87790242BA010CF47E4716D7', X'42E856B6543C1741884511AE95C9CFC2', X'42E856B6543C1741884511AE95C9CFC2', 8, NULL, NULL, 2, NULL, 0, 'Serve hot with Roasted Red Pepper Pesto or marinara sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6997F64319A5A2478D810673DB94F364', X'611E87A8C5032E4787D812120CF02D86', X'611E87A8C5032E4787D812120CF02D86', 0, NULL, NULL, 2, NULL, 0, 'Puree roasted red pepper, roasted garlic, roasted almonds, parsley, lemon juice, and 3 tablespoons of Parmesan cheese in a food processor, keeping puree somewhat chunky. Add oil and puree just to incorporate the oil. Season with salt and pepper, to taste. Pour into a small serving bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3ECD076502FE9147A665DEBA48B863CE', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 0, NULL, NULL, 2, NULL, 0, 'Dip a clean, food-use-only, fine-tipped paintbrush into the pink candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1A4854C7455C24785C20EA26E73F231', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 1, NULL, NULL, 2, NULL, 0, 'Dab the candy over the rose cavity in the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FF03768E0A6B342BFEC91137557ED73', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 2, NULL, NULL, 2, NULL, 0, 'Then dip another clean paintbrush into the light pink candy melts and paint on the inner triangle in both of the unicorns ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DA91E23BC0DBC40BC5E57AA9273C6A7', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 3, NULL, NULL, 2, NULL, 0, 'Now begin to paint the mane by dabbing in dark blue or bright purple into the tuft of hair that is closest to the rose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E659FEFC6CA3754E842C381FBDA1034B', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 4, NULL, NULL, 2, NULL, 0, 'Continue to paint the tufts of hair in this order: light blue, green, yellow, orange, and red.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'344BA7FA59B8324DB8BF482E35F13C99', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 5, NULL, NULL, 2, NULL, 0, 'Use a very fine paintbrush to paint the eyelashes using black or dark cocoa candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F733FBFAF8E3A42AE0BA2528D5AB038', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 6, NULL, NULL, 2, NULL, 0, 'Pop the mold into the freezer for about 3 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BDD907435FE21A42B27DF3952EE73F14', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 7, NULL, NULL, 2, NULL, 0, 'Remove the mold and let it sit at room temperature for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B65016B69D7D964F8655202B007B007A', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 8, NULL, NULL, 2, NULL, 0, 'Your mold must not be cold when you add in the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF2B67E6D06C1B4AA121A013BDC7FB4D', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 9, NULL, NULL, 2, NULL, 0, 'Make sure your white chocolate is not hot to the touch, then fill the entire mold with white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4100FDE850EE56439ABB984CE2D7F168', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 10, NULL, NULL, 2, NULL, 0, 'Carefully invert the mold over your bowl of white chocolate and allow the wet candy to drip out of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9592B972E8525429E8C4541DF27D777', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 11, NULL, NULL, 2, NULL, 0, 'Shake the mold and allow more to drip out until you just have a nice thin coating around the interior of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96DA651D3090E44E9107B7B3ACBB5E18', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 12, NULL, NULL, 2, NULL, 0, 'Use a metal scraper to scrape off the excess so that the edge is nice and clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14425C41E47B604FBBA7DC9DB6812B45', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 13, NULL, NULL, 2, NULL, 0, 'Insert a wooden popsicle stick into the wet white chocolate in the silicone unicorn mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8621BCE0E1E2304CB1CCA2C37F64F5EA', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 14, NULL, NULL, 2, NULL, 0, 'Immediately insert a wood popsicle stick into the mold while the white chocolate is still wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BC3864A9E0FEC4CA6064040C1A5FF6E', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 15, NULL, NULL, 2, NULL, 0, 'Pop the mold in the freezer for about 5 minutes just until the white candy melts harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A8D9153FDEC804C9A574C913A4627BB', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 16, NULL, NULL, 2, NULL, 0, 'Step-by-step images showing how to fill a white chocolate unicorn with cake ball filling before covering it in more white chocolate. Then brush edible gold luster dust over the horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'503DA7AA858C454CA7C4A1AF90B9CE7C', X'6CB5D1E8219F62459DFE426F48FC8009', X'6CB5D1E8219F62459DFE426F48FC8009', 17, NULL, NULL, 2, NULL, 0, 'Let the mold sit for about 10 minutes to come to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE631ACEDA981E49BC9645ADD869DA0E', X'8BA44DE1F747734483744EC9C41BC5B1', X'8BA44DE1F747734483744EC9C41BC5B1', 0, NULL, NULL, 2, NULL, 0, 'Cut off the browned edges of the cake then crumble into really small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF35924F1A482049904DB07C29F90434', X'8BA44DE1F747734483744EC9C41BC5B1', X'8BA44DE1F747734483744EC9C41BC5B1', 1, NULL, NULL, 2, NULL, 0, 'Stir in at least 1/4 cup of frosting until the cake ball filling is smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72544977CD76584E99B1B58B572464CE', X'8BA44DE1F747734483744EC9C41BC5B1', X'8BA44DE1F747734483744EC9C41BC5B1', 2, NULL, NULL, 2, NULL, 0, 'Press cake ball filling into the mold and, if necessary, under the popsicle stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DE0155A879D614685331A5C56218028', X'8BA44DE1F747734483744EC9C41BC5B1', X'8BA44DE1F747734483744EC9C41BC5B1', 3, NULL, NULL, 2, NULL, 0, 'Fill the mold to about 1/8th inch from the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'468400CBF285A846AE1DB7A772220AC6', X'8BA44DE1F747734483744EC9C41BC5B1', X'8BA44DE1F747734483744EC9C41BC5B1', 4, NULL, NULL, 2, NULL, 0, 'Spoon white chocolate over top of the cake ball filling and spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'893CCC8B3317E949958E30E1900AD3E9', X'8BA44DE1F747734483744EC9C41BC5B1', X'8BA44DE1F747734483744EC9C41BC5B1', 5, NULL, NULL, 2, NULL, 0, 'Scrape over top using a metal scraper to remove all of the excess chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95A5E3EACD89EE419F90510B94467FFE', X'8BA44DE1F747734483744EC9C41BC5B1', X'8BA44DE1F747734483744EC9C41BC5B1', 6, NULL, NULL, 2, NULL, 0, 'Freeze for about 5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97D476398F33FE449C7F221BEB57A780', X'8BA44DE1F747734483744EC9C41BC5B1', X'8BA44DE1F747734483744EC9C41BC5B1', 7, NULL, NULL, 2, NULL, 0, 'Then unmold the unicorn and allow it to warm to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F65D6B5C3610594C8A0E0F22694D8810', X'8BA44DE1F747734483744EC9C41BC5B1', X'8BA44DE1F747734483744EC9C41BC5B1', 8, NULL, NULL, 2, NULL, 0, 'Brush a light dusting of edible gold luster dust over the unicorns horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5FA430A6FDA81448C8AC224287D9E2D', X'D4B5A59233FEC84F98F3BBA96BC32F13', X'D4B5A59233FEC84F98F3BBA96BC32F13', 0, NULL, NULL, 2, NULL, 0, 'Grease a large mixing bowl, rubber spatula, and 9-inch x 13-inch pan with butter or non-stick spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D53685C4242E647BC0E34972591B86E', X'D4B5A59233FEC84F98F3BBA96BC32F13', X'D4B5A59233FEC84F98F3BBA96BC32F13', 1, NULL, NULL, 2, NULL, 0, 'Pour the cereal into the bowl and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31DA26F4142E354D97B8D84EC36221A1', X'D4B5A59233FEC84F98F3BBA96BC32F13', X'D4B5A59233FEC84F98F3BBA96BC32F13', 2, NULL, NULL, 2, NULL, 0, 'Begin melting the butter in a medium-large saucepan set over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47B22214A0941442A5857759DC9ECB56', X'D4B5A59233FEC84F98F3BBA96BC32F13', X'D4B5A59233FEC84F98F3BBA96BC32F13', 3, NULL, NULL, 2, NULL, 0, 'Add marshmallows and continue to heat, stirring often, until about 75% melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2BA59B91B8E034D8599428E7B7DE566', X'D4B5A59233FEC84F98F3BBA96BC32F13', X'D4B5A59233FEC84F98F3BBA96BC32F13', 4, NULL, NULL, 2, NULL, 0, 'Remove from the heat and continue to stir vigorously until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23905D496B229A40A3818BA56841B682', X'D4B5A59233FEC84F98F3BBA96BC32F13', X'D4B5A59233FEC84F98F3BBA96BC32F13', 5, NULL, NULL, 2, NULL, 0, 'Pour the melted marshmallows over the cereal and stir to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E30E301DB1FB441AC37C7429D1A0895', X'D4B5A59233FEC84F98F3BBA96BC32F13', X'D4B5A59233FEC84F98F3BBA96BC32F13', 6, NULL, NULL, 2, NULL, 0, 'Spread into an even layer in the pan and set aside to cool for at least one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04BA4431E4651343B1BE53F14D8408E2', X'D4B5A59233FEC84F98F3BBA96BC32F13', X'D4B5A59233FEC84F98F3BBA96BC32F13', 7, NULL, NULL, 2, NULL, 0, 'Cut into twelve 2-inch by 3-inch rectangles, saving the scraps to fill in any craters on the rectangle treats before enjoying as a snack.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1167664CFBC3054AB384CD17FF956FDD', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 0, NULL, NULL, 2, NULL, 0, 'Insert the tip of a lollipop stick into the melted and tempered white chocolate or melted confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1B9257E50EAD64B8F3FEE9F30D62B18', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 1, NULL, NULL, 2, NULL, 0, 'Then insert the stick into a cereal treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BEB6E8DBD59DE4EB6FD395677B59F6B', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 2, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6C3FE3F679E3F44A392EBEC06D684EE', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 3, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to harden for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92A28641BC089F4389A0A1BAD2C23EF3', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 4, NULL, NULL, 2, NULL, 0, 'Fill a tall 3 inch diameter glass with white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5975A2E54DBD14584FDEA2A4163347E', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 5, NULL, NULL, 2, NULL, 0, 'Dip a cereal treat into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26712E5075C1434DA54AB02A794B0106', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 6, NULL, NULL, 2, NULL, 0, 'Lift it out and allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAEEA2788D37B048A1E476CB89064562', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 7, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A89F526944E2E48B2CB5839C9F96689', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 8, NULL, NULL, 2, NULL, 0, 'If using pure white chocolate allow the chocolate to dry at room temperature or pop in the refrigerator for about 5 minutes. If using confectionery coating, pop in the freezer for 3-5 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CE7A3E9ACDB2E4DA704A969FB44FE36', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 9, NULL, NULL, 2, NULL, 0, 'Repeat, dipping all 12 rice krispie treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13551AEB5AE8AB4B843D292A39F14E97', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 10, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of the gold modeling chocolate and roll into pointed logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3667D1E5753714B82D2EF217CD68798', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 11, NULL, NULL, 2, NULL, 0, 'Twist two logs together to create a unicorn horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C36F92158C9D6441BC2BA611FAFCAF6D', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 12, NULL, NULL, 2, NULL, 0, 'If desired, brush the horns with edible gold luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'268901A2278B634B8DD27A375A8BDAB7', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 13, NULL, NULL, 2, NULL, 0, 'Roll out the white modeling chocolate to about 1/4 inch thickness and cut out 24 small triangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6960F13F1FDF844F9295B8EE3C1E86FA', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 14, NULL, NULL, 2, NULL, 0, 'Roll out the pink modeling chocolate really thin and cut out 24 small triangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01E112836715304A895B2BB2F1C57B8F', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 15, NULL, NULL, 2, NULL, 0, 'Use water to attach the inner pink ears (triangles) to the outer white ears (triangles).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'748D6DFBD7CAEE4487FADC22C4E4671B', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 16, NULL, NULL, 2, NULL, 0, 'Dust a silicone rose mold with corn starch.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4307B92A9EDEF946AC55FC3ABE6083B0', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 17, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of your colored modeling chocolate and press them into the mold creating various sized roses.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBF0F8F43066234E9269527474A568B1', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 18, NULL, NULL, 2, NULL, 0, 'Attach one horn to the top of each of the rice krispie treats and attach two ears on either side using white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B383416C8B9F3047890D7C02EE557690', X'0B6F83C61A977A48886B17F1FE7F9E9C', X'0B6F83C61A977A48886B17F1FE7F9E9C', 19, NULL, NULL, 2, NULL, 0, 'Then attach 5 flowers on the front, top edge, of each treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16D6488FDA5CF24CB716DD2C0A0F3C3E', X'D20DADC1E1275D4397FF9A74AB67AA4F', X'D20DADC1E1275D4397FF9A74AB67AA4F', 0, NULL, NULL, 2, NULL, 0, 'Pour water into the bottom of a saucepan set over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39256D487950B04F9B16767EF78A1742', X'D20DADC1E1275D4397FF9A74AB67AA4F', X'D20DADC1E1275D4397FF9A74AB67AA4F', 1, NULL, NULL, 2, NULL, 0, 'Add sugar and allow to come to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BB871667ABDDC42B9EB833A61180C99', X'D20DADC1E1275D4397FF9A74AB67AA4F', X'D20DADC1E1275D4397FF9A74AB67AA4F', 2, NULL, NULL, 2, NULL, 0, 'Boil until all the sugar is dissolved, stirring if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CF5083922C09E41B760F1CE3F747479', X'D20DADC1E1275D4397FF9A74AB67AA4F', X'D20DADC1E1275D4397FF9A74AB67AA4F', 3, NULL, NULL, 2, NULL, 0, 'Pour egg yolks into a mixing bowl and whisk to break up.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FBF64D3EF0CD0429950CF54F73020FA', X'D20DADC1E1275D4397FF9A74AB67AA4F', X'D20DADC1E1275D4397FF9A74AB67AA4F', 4, NULL, NULL, 2, NULL, 0, 'Continue whisking while slowly drizzling in the sugar syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94ED0CE7DF8FBF4E9B459759DD11DF98', X'D20DADC1E1275D4397FF9A74AB67AA4F', X'D20DADC1E1275D4397FF9A74AB67AA4F', 5, NULL, NULL, 2, NULL, 0, 'Pour 1/2 cup of heavy whipping cream into the saucepan and set it back on the stove on low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4B0191ED9AC3141B2C3C49531D63532', X'D20DADC1E1275D4397FF9A74AB67AA4F', X'D20DADC1E1275D4397FF9A74AB67AA4F', 6, NULL, NULL, 2, NULL, 0, 'Stir in the egg mixture and continue to stir for at least 3 minutes until heated through (and registers at least 140 degrees Fahrenheit).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F54D71D3D68FE94588EEC195F8925150', X'D20DADC1E1275D4397FF9A74AB67AA4F', X'D20DADC1E1275D4397FF9A74AB67AA4F', 7, NULL, NULL, 2, NULL, 0, 'Pour through a fine mesh sieve that is set over your bowl of melted semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E8B059038198084B90948499B18C26B4', X'D20DADC1E1275D4397FF9A74AB67AA4F', X'D20DADC1E1275D4397FF9A74AB67AA4F', 8, NULL, NULL, 2, NULL, 0, 'Discard anything that remains in the sieve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01C1A0ABCD5FC54592FC1D478057D1CE', X'D20DADC1E1275D4397FF9A74AB67AA4F', X'D20DADC1E1275D4397FF9A74AB67AA4F', 9, NULL, NULL, 2, NULL, 0, 'Stir the chocolate until glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01C98FA9E3D4A34E840DB70362FBD0EA', X'D20DADC1E1275D4397FF9A74AB67AA4F', X'D20DADC1E1275D4397FF9A74AB67AA4F', 10, NULL, NULL, 2, NULL, 0, 'Fold the whipped cream into the chocolate in two stages.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDE5522FDC69E24FB98463756A7EB101', X'D20DADC1E1275D4397FF9A74AB67AA4F', X'D20DADC1E1275D4397FF9A74AB67AA4F', 11, NULL, NULL, 2, NULL, 0, 'Cover and chill until the mousse thickens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A329ABD093059B4EB932E2A49211D4BB', X'0CC1F4E1D11CC44FAC02DE48E7C46FEF', X'0CC1F4E1D11CC44FAC02DE48E7C46FEF', 0, NULL, NULL, 2, NULL, 0, 'Combine cream cheese and sugar in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDC9BF0AD6114D4ABF23FFA20A3DF189', X'0CC1F4E1D11CC44FAC02DE48E7C46FEF', X'0CC1F4E1D11CC44FAC02DE48E7C46FEF', 1, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on medium-high speed until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D08684659BB889458A5A492838FD42DE', X'0CC1F4E1D11CC44FAC02DE48E7C46FEF', X'0CC1F4E1D11CC44FAC02DE48E7C46FEF', 2, NULL, NULL, 2, NULL, 0, 'Add marshmallow fluff and vanilla bean paste or extract and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'563CF54D90C72746B230AA9923C44D68', X'0CC1F4E1D11CC44FAC02DE48E7C46FEF', X'0CC1F4E1D11CC44FAC02DE48E7C46FEF', 3, NULL, NULL, 2, NULL, 0, 'Pour in whipped topping and beat until creamy and smooth. If using real whipped cream, gently fold it into the chocolate mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68DF7487C15CDF4887BCFF04090EEA34', X'AE53EE57C334CB41AF57F82208D7C1CD', X'AE53EE57C334CB41AF57F82208D7C1CD', 0, NULL, NULL, 2, NULL, 0, 'Fill one disposable pastry bag with about 1 1/2 cups of dark chocolate mousse and fill another back with about the same amount of the cheesecake mousse.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE371C7CC3A2E64DBB46566AB263A549', X'AE53EE57C334CB41AF57F82208D7C1CD', X'AE53EE57C334CB41AF57F82208D7C1CD', 1, NULL, NULL, 2, NULL, 0, 'Fit a large pastry bag with a large star pastry tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'385B50F89321664293C376F3962AA1CD', X'AE53EE57C334CB41AF57F82208D7C1CD', X'AE53EE57C334CB41AF57F82208D7C1CD', 2, NULL, NULL, 2, NULL, 0, 'Cut off the tips of the filled bags then snuggle them down into the third bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED95C36E8D3DA842B75E53D87FCA24F2', X'AE53EE57C334CB41AF57F82208D7C1CD', X'AE53EE57C334CB41AF57F82208D7C1CD', 3, NULL, NULL, 2, NULL, 0, 'Squeeze until the mousse comes of of the bag and you can see both colors of mousse.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2561708CB6DC0340883AA8223C2B48A0', X'AE53EE57C334CB41AF57F82208D7C1CD', X'AE53EE57C334CB41AF57F82208D7C1CD', 4, NULL, NULL, 2, NULL, 0, 'Pipe between 2/3 and 3/4 cup of the swirled chocolate cheesecake mousse into small dessert cups or bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'705A0286C478A1449EE2CA1A8B95FD0B', X'6FEA17D04A4703478F353B3C54C968C9', X'6FEA17D04A4703478F353B3C54C968C9', 0, NULL, NULL, 2, NULL, 0, 'Dip strawberries in melted and tempered pure white chocolate or in melted white confectionery coating (candy melts).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95E81CF91A3C414E8D203347EB63EF73', X'6FEA17D04A4703478F353B3C54C968C9', X'6FEA17D04A4703478F353B3C54C968C9', 1, NULL, NULL, 2, NULL, 0, 'Allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E2379602BD32146B8A9277B8917D44E', X'6FEA17D04A4703478F353B3C54C968C9', X'6FEA17D04A4703478F353B3C54C968C9', 2, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AE269EC291C5A46BAC1C2E60986F211', X'6FEA17D04A4703478F353B3C54C968C9', X'6FEA17D04A4703478F353B3C54C968C9', 3, NULL, NULL, 2, NULL, 0, 'If desired, allow the white chocolate to puddle at the bottom of the strawberry for a few seconds, then lift up the berry and place it on a clean spot on the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB620D0F63C343409A380AF1751D1972', X'6FEA17D04A4703478F353B3C54C968C9', X'6FEA17D04A4703478F353B3C54C968C9', 4, NULL, NULL, 2, NULL, 0, 'Chill in the fridge (for 3-5 minutes) if using candy melts or let air dry (for about 10 minutes) if using pure white chocolate just until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'061077971B7EFC4882D27B4B38F2BEFB', X'5370279C9B95334B824782268D8BB619', X'5370279C9B95334B824782268D8BB619', 0, NULL, NULL, 2, NULL, 0, 'EYES and NOSE: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two small circles and one 1/2 inch heart for each polar bear using a small plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CEADDCA4C9A4474F858F3A14A8CD4F80', X'5370279C9B95334B824782268D8BB619', X'5370279C9B95334B824782268D8BB619', 1, NULL, NULL, 2, NULL, 0, 'EARS: Cut a curved sliver off of two white chocolate wafers for each polar bear or create ears using 1/4 inch thick white modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'252D577A6F71414E870FACA2D348ABCD', X'5370279C9B95334B824782268D8BB619', X'5370279C9B95334B824782268D8BB619', 2, NULL, NULL, 2, NULL, 0, 'BOW: Cut two small hearts and one small circle out of pink or red modeling chocolate. Set the hearts together with the points touching then attach the circle over top using water or clear piping gel to `glue` the bow together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BFC9879D9948942B9AA659112DF8952', X'5370279C9B95334B824782268D8BB619', X'5370279C9B95334B824782268D8BB619', 3, NULL, NULL, 2, NULL, 0, 'Dip the white chocolate coated strawberries into white chocolate again and press a fork up against the chocolate then pull away quickly. Do this all over the berry, over and over again, until spikes of fur start to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01D6AD2B66E35D478EB89783B48AC289', X'5370279C9B95334B824782268D8BB619', X'5370279C9B95334B824782268D8BB619', 4, NULL, NULL, 2, NULL, 0, 'Attach two candy ears on the top edge of the strawberry on opposite sides of the green stem using melted white chocolate. Then add more white chocolate over the ears and create spikes of fur on them too.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9775E2E6D714F844A506A025B9BF9969', X'5370279C9B95334B824782268D8BB619', X'5370279C9B95334B824782268D8BB619', 5, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add a nose to the tip of the strawberry and two eyes above it. Add a bow if making female polar bears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C641AA82CC23BA4586BA75C11C56D1E0', X'2971A592C248A14EAC83C09044016A9B', X'2971A592C248A14EAC83C09044016A9B', 0, NULL, NULL, 2, NULL, 0, 'NOSE and FUR PATCHES: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two ovals using a plunger cutter and cut out a small 1/2 inch heart using a heart plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFE184C35F938F44AE1189FEAFEE2659', X'2971A592C248A14EAC83C09044016A9B', X'2971A592C248A14EAC83C09044016A9B', 1, NULL, NULL, 2, NULL, 0, 'EARS: You can cut a small sliver off two dark chocolate wafers to create the ears or you can cut out black modeling chocolate circles (with a curved notch removed) to create the ears.');
