INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8CBF108D73C2E44863CAFB24A6B9CCB', X'2971A592C248A14EAC83C09044016A9B', X'2971A592C248A14EAC83C09044016A9B', 3, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add two ears on the top edge of the strawberry, a nose to the tip of the strawberry and two fur patches above it off to the side. Add a bow in front of an ear to make panda bear girls.');
