INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'961F87D27142A14CAFF959B559BF15B3', X'BB7B72D2F14DA24AA22F12C01B6779CF', X'BB7B72D2F14DA24AA22F12C01B6779CF', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03BD4155CF0FF74FB068EB0D7BE5F79F', X'BB7B72D2F14DA24AA22F12C01B6779CF', X'BB7B72D2F14DA24AA22F12C01B6779CF', 2, NULL, NULL, 2, NULL, 0, 'Brush two 6-inch cake pans with butter then dust with flour and line with a round of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EE566E344907D4C8527C5C99E71B723', X'BB7B72D2F14DA24AA22F12C01B6779CF', X'BB7B72D2F14DA24AA22F12C01B6779CF', 3, NULL, NULL, 2, NULL, 0, 'Brush 4 cupcake cavities in a muffin tin with butter then dust with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D1989D39FD1D844A38B46A88C0EA0EC', X'BB7B72D2F14DA24AA22F12C01B6779CF', X'BB7B72D2F14DA24AA22F12C01B6779CF', 4, NULL, NULL, 2, NULL, 0, 'Combine the cake mix, water, oil, egg whites, sour cream, and pudding mix together in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0B61B570E5D2A4CB32CE99D500C87BB', X'BB7B72D2F14DA24AA22F12C01B6779CF', X'BB7B72D2F14DA24AA22F12C01B6779CF', 5, NULL, NULL, 2, NULL, 0, 'Use an electric mixer to beat on low for 30 seconds then increase speed to medium and beat for 1 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3811C7BD7BC63A4599B3B60B231F7D0B', X'BB7B72D2F14DA24AA22F12C01B6779CF', X'BB7B72D2F14DA24AA22F12C01B6779CF', 6, NULL, NULL, 2, NULL, 0, 'Fill 4 cupcake cavities about 2/3''s full with batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF9537CF5C408C48958108646C874836', X'BB7B72D2F14DA24AA22F12C01B6779CF', X'BB7B72D2F14DA24AA22F12C01B6779CF', 7, NULL, NULL, 2, NULL, 0, 'Divide the remaining batter among the two 6-inch cake pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'376B7C2CD323AB4D947ADC47C9DABBD7', X'BB7B72D2F14DA24AA22F12C01B6779CF', X'BB7B72D2F14DA24AA22F12C01B6779CF', 8, NULL, NULL, 2, NULL, 0, 'Bake the cupcakes and cakes for 18-26 minutes removing the cupcakes as soon as they are done at around 18-20 minutes. Check the cakes at around the 22 minute mark.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1FC1E727AD6D84F94DA3CE428CBB671', X'BB7B72D2F14DA24AA22F12C01B6779CF', X'BB7B72D2F14DA24AA22F12C01B6779CF', 9, NULL, NULL, 2, NULL, 0, 'A toothpick inserted into the cakes should come out with just a few moist crumbs. Or the cakes should spring back when you press a finger on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE2E379569DCDA47BB555771725C486C', X'BB7B72D2F14DA24AA22F12C01B6779CF', X'BB7B72D2F14DA24AA22F12C01B6779CF', 10, NULL, NULL, 2, NULL, 0, 'Allow the cakes and cupcakes to cool for 10 minutes in their pans then remove them and set them on a cooling rack. Peel off the parchment paper once the cakes are cooled completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AAC8AEE4513E14FA29888EB9D0FA6FC', X'BB7B72D2F14DA24AA22F12C01B6779CF', X'BB7B72D2F14DA24AA22F12C01B6779CF', 11, NULL, NULL, 2, NULL, 0, 'Place cakes forming two bears on a large cake board and cut each cupcake at a curve so that they fit up against the cake to form the panda bear''s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F67F6F5E40A62048983EDAE6B4AB09F0', X'13E9765861BC6D45BA8E725F4A17F1D9', X'13E9765861BC6D45BA8E725F4A17F1D9', 0, NULL, NULL, 2, NULL, 0, 'Beat the shortening and butter together until well combined using an electric mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6523A84AB690442A0ECB131F5F55C34', X'13E9765861BC6D45BA8E725F4A17F1D9', X'13E9765861BC6D45BA8E725F4A17F1D9', 1, NULL, NULL, 2, NULL, 0, 'Add the powdered sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89B2736C4E15BE44BEDC7109844CCDD1', X'13E9765861BC6D45BA8E725F4A17F1D9', X'13E9765861BC6D45BA8E725F4A17F1D9', 2, NULL, NULL, 2, NULL, 0, 'Add the vanilla and 3 tablespoons of the milk and beat until fully incorporated and the frosting is smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A071BA9D49E7847BA83843E0961CC3A', X'13E9765861BC6D45BA8E725F4A17F1D9', X'13E9765861BC6D45BA8E725F4A17F1D9', 3, NULL, NULL, 2, NULL, 0, 'Add more milk as needed to make the frosting soft enough to pipe through a star tip yet hold it''s shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3310E697945666409E374622D9749915', X'13E9765861BC6D45BA8E725F4A17F1D9', X'13E9765861BC6D45BA8E725F4A17F1D9', 4, NULL, NULL, 2, NULL, 0, 'Spoon 2/3 cup of the frosting into another bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DABD263433EE6D4DB6B8244143B5F4DB', X'13E9765861BC6D45BA8E725F4A17F1D9', X'13E9765861BC6D45BA8E725F4A17F1D9', 5, NULL, NULL, 2, NULL, 0, 'Stir the cocoa powder into that small bowl of frosting then color using black food coloring and add more milk, if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6651CD6DC5B51F42AB5B88C0A08AB3C7', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', 0, NULL, NULL, 2, NULL, 0, 'Roll out the black, white, and pink modeling chocolate to about 1/8th inch thickness on a silicone mat using a silicone rolling pin.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C109BD12B094C4FB9C51AE1DC964612', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', 1, NULL, NULL, 2, NULL, 0, 'Cut four 2 1/4 inch by 1 3/4 inch ovals from the black modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A50854F336E9347A32D3BDB2236F994', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', 2, NULL, NULL, 2, NULL, 0, 'Cut four 1 1/2 inch circles from the white, four small 1/4 inch hearts from the white and four 1 inch circles from the black.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72FD87AAAB7B4644BC2F7B379ECF4266', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', 3, NULL, NULL, 2, NULL, 0, 'Attach one white circle to one black oval using a thin layer of water or clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7202E5CFE2E73B48A5A489C4D5AB9DC0', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', 4, NULL, NULL, 2, NULL, 0, 'Attach one black circle on top of the white circle then attach one small white heart along one edge of the black circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5ADE6A20D7265D4C846072B9AA7DD399', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', 5, NULL, NULL, 2, NULL, 0, 'Repeat creating 4 eyes with all the hearts pointing up or down in one direction.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AADE9CCF000C1418DB564AC6D6444A0', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', 6, NULL, NULL, 2, NULL, 0, 'Cut out two 1 1/2 inch long heart shaped noses from the black modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B3A62FEBA3AD34AAEB0734A50FEDF29', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', 7, NULL, NULL, 2, NULL, 0, 'Cut two 1 inch hearts and one 1/2 inch circle from the pink modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B81DC3E149FFB4082553CCD1DBA4EF8', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', X'1E72EE97AF9E1648ABB6ABE672CFCBC7', 8, NULL, NULL, 2, NULL, 0, 'Place the two points of the heart together then attach the circle over the top of the tips using water or piping gel to form a cute bow.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56071FE3154972449CA73F6E0BFA6A9B', X'1176704594FC8C4DB8F41CC26EAE6D19', X'1176704594FC8C4DB8F41CC26EAE6D19', 0, NULL, NULL, 2, NULL, 0, 'Attach two eyes and a nose using frosting to each round cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01323D958B7BB7458CC457F62CB425CD', X'1176704594FC8C4DB8F41CC26EAE6D19', X'1176704594FC8C4DB8F41CC26EAE6D19', 1, NULL, NULL, 2, NULL, 0, 'Pipe white frosting using a star tip over the entire surface of the round cakes around the eyes and nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'076743969612A445A748A4BE1AB976A0', X'1176704594FC8C4DB8F41CC26EAE6D19', X'1176704594FC8C4DB8F41CC26EAE6D19', 2, NULL, NULL, 2, NULL, 0, 'Pipe black frosting stars over the cupcake ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DF3EFB733BC7F4B973856C52F4427D7', X'1176704594FC8C4DB8F41CC26EAE6D19', X'1176704594FC8C4DB8F41CC26EAE6D19', 3, NULL, NULL, 2, NULL, 0, 'Press the pink modeling chocolate bow just below one ear of one of the panda cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'286056364680C14887F2838AFB78766D', X'1176704594FC8C4DB8F41CC26EAE6D19', X'1176704594FC8C4DB8F41CC26EAE6D19', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2214F3D7F0B8645869DF276E1576D44', X'8BD1CE5F93A3774A9C351D63B04B786D', X'8BD1CE5F93A3774A9C351D63B04B786D', 0, NULL, NULL, 2, NULL, 0, 'Pour the chocolate into a small bowl or glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C364A1BADEF5784FAA982606AFBF9289', X'8BD1CE5F93A3774A9C351D63B04B786D', X'8BD1CE5F93A3774A9C351D63B04B786D', 1, NULL, NULL, 2, NULL, 0, 'Dip one strawberry into the chocolate then set it on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83B8D8215FBAF6418156C1E2C3E25FD6', X'8BD1CE5F93A3774A9C351D63B04B786D', X'8BD1CE5F93A3774A9C351D63B04B786D', 2, NULL, NULL, 2, NULL, 0, 'Take a fork and press it into the chocolate, then pull up and away quickly creating spikes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB0442024720684A816B6875865895FA', X'8BD1CE5F93A3774A9C351D63B04B786D', X'8BD1CE5F93A3774A9C351D63B04B786D', 3, NULL, NULL, 2, NULL, 0, 'Repeat this process until the entire chocolate strawberry looks furry.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'652DD6695B3D05469852E2E030B8D126', X'8BD1CE5F93A3774A9C351D63B04B786D', X'8BD1CE5F93A3774A9C351D63B04B786D', 4, NULL, NULL, 2, NULL, 0, 'Immediate press two candy eyes and an M&M nose onto the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'062F260D139BD145B26DC6C16552E377', X'8BD1CE5F93A3774A9C351D63B04B786D', X'8BD1CE5F93A3774A9C351D63B04B786D', 5, NULL, NULL, 2, NULL, 0, 'Dip the cut edge of two chocolate wafers in chocolate and attach them to either side of the top of the strawberry creating the bear''s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B08A9D6F4205B478C1C76CF11819D99', X'8BD1CE5F93A3774A9C351D63B04B786D', X'8BD1CE5F93A3774A9C351D63B04B786D', 6, NULL, NULL, 2, NULL, 0, 'Let the strawberry air dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B0CB9990B578A40A6E523F9B0B92CB0', X'8BD1CE5F93A3774A9C351D63B04B786D', X'8BD1CE5F93A3774A9C351D63B04B786D', 7, NULL, NULL, 2, NULL, 0, 'Repeat dipping all 16 bears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E9631FB074F774DB30FFD60535B8E0B', X'8BD1CE5F93A3774A9C351D63B04B786D', X'8BD1CE5F93A3774A9C351D63B04B786D', 8, NULL, NULL, 2, NULL, 0, 'If desired, brush a second layer of chocolate over the bottom of each strawberry to seal in the berry.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'022AF17E3364CB4B82BA256854D603F3', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 0, NULL, NULL, 2, NULL, 0, 'Pour water into the bottom of a saucepan set over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7181BC3541FBFA4BB56E371BB1856C8C', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 1, NULL, NULL, 2, NULL, 0, 'Add sugar and allow to come to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F82862ADD951414A80CE0557AA05AF43', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 2, NULL, NULL, 2, NULL, 0, 'Boil until all the sugar is dissolved, stirring if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46C484618424A3459BFC1D0E719FE09D', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 3, NULL, NULL, 2, NULL, 0, 'Pour egg yolks into a mixing bowl and whisk to break up.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A9CD0AC8BB26C4FAD920F9B6B8C41C9', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 4, NULL, NULL, 2, NULL, 0, 'Continue whisking while slowly drizzling in the sugar syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'656E1CB052AD194283EEF8C9DD2C6618', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 5, NULL, NULL, 2, NULL, 0, 'Pour 1/2 cup of heavy whipping cream into the saucepan and set it back on the stove on low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37E62A4655F90C4698E554D5963F7E26', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 6, NULL, NULL, 2, NULL, 0, 'Stir in the egg mixture and continue to stir for at least 3 minutes until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45911784DD975F4DB85EA62D0A7A3E3E', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 7, NULL, NULL, 2, NULL, 0, 'You may heat until thickened, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E12456E61A76C0489531AC84D4ADF479', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 8, NULL, NULL, 2, NULL, 0, 'Pour through a fine mesh sieve that is set over your bowl of melted semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70A6F81558D1D74080B7913A0FFFF13B', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 9, NULL, NULL, 2, NULL, 0, 'Discard anything that remains in the sieve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'374265BACFF83E4FB6E649C70F36B7F6', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 10, NULL, NULL, 2, NULL, 0, 'Stir the chocolate until glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'901534320A2A894BB4A583139EE91D62', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 11, NULL, NULL, 2, NULL, 0, 'Fold the whipped cream into the chocolate in two stages.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC1E2C580B695043965C98B37C70F255', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 12, NULL, NULL, 2, NULL, 0, 'Cover and chill until the mousse thickens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F8FA01D3DF0154F94677866761A7322', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 13, NULL, NULL, 2, NULL, 0, 'Spoon or pipe into bowls or chocolate cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCD598871929A54B8B7DB4B3909F2773', X'389DC2B5DE5465418F48EC64E593F1B1', X'389DC2B5DE5465418F48EC64E593F1B1', 14, NULL, NULL, 2, NULL, 0, 'Let sit at room temperature for at least 15 minutes before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1EAAC4308AEFC488DDCAACE9457DA4C', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 0, NULL, NULL, 2, NULL, 0, 'Divide cake batter into 6 bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50D6A639E6A0D34EA11BBC1FEBD3154C', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 1, NULL, NULL, 2, NULL, 0, 'Color each bowl a different conversation heart color using food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DE7A300B631F64F9BD5F52592C0C45E', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 2, NULL, NULL, 2, NULL, 0, 'Divide each batter into 3 heart shaped cavities in a silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5088688C5930194F989EA19C44DA23A2', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 3, NULL, NULL, 2, NULL, 0, 'Bake for 12-16 minutes, in a 350 degree F oven, until you press down on the center of a cupcake and the indentation springs back.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48BE01958C0405419EB64D4F6C0C52DA', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 4, NULL, NULL, 2, NULL, 0, 'Remove and let cool for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E5235310EB3FF4DA40236D7490C5F93', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 5, NULL, NULL, 2, NULL, 0, 'Pop the molds in the freezer for at least 30 minutes until the cakes are chilled and firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2032D3D06109446B42E6AB0C3B4CD6B', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 6, NULL, NULL, 2, NULL, 0, 'Push cakes out of mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61194E023BADD540A0386D3D5B189119', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 7, NULL, NULL, 2, NULL, 0, 'Cut off the domed part of each cake. Save both halves.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42CAB1FA88ACBE4B96EB318E8A610F01', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 8, NULL, NULL, 2, NULL, 0, 'To make each color of white chocolate ganache, combine 4 ounces white chocolate with 3 tablespoons of heavy whipping cream and 1 teaspoon corn syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30C80D175594DF45A6052AFDD6E0B6DA', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 9, NULL, NULL, 2, NULL, 0, 'Heat in microwave for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'363A9FFCEF48AE4A859CEFFF0FF4FD26', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 10, NULL, NULL, 2, NULL, 0, 'Let sit for 3 minutes then stir until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9E8676AB3D1994CA0E54AAFA81CAC45', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 11, NULL, NULL, 2, NULL, 0, 'Or melt in a double boiler set over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3423A9BAADE0D44EBA842BE2956B7EA7', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 12, NULL, NULL, 2, NULL, 0, 'Set cakes on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3B5F878C8C80D4CAFF6231C44D6B21D', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 13, NULL, NULL, 2, NULL, 0, 'Pour glaze over 3 of the flat heart shaped cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E8D4A292DBB46468EB305428C39AF76', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 14, NULL, NULL, 2, NULL, 0, 'Scoop up any glaze that drips off the cakes and use to glaze the smaller domed heart shaped cakes (which you can enjoy as a snack).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A36A8D1A87CEDB40982B4F965FB846F0', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 15, NULL, NULL, 2, NULL, 0, 'Pick up cakes and set on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCEC0A67D39CB9478B9F95D780A8B667', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 16, NULL, NULL, 2, NULL, 0, 'Set aside for 2-4 hours (or freeze for 1 hour then set at room temperature for 1 hour) until the glaze feels dry to the touch.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1779C56F3EDE6248B558292521634A1C', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 17, NULL, NULL, 2, NULL, 0, 'Set a stencil over top of a cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'158DDA905AC16042A440CCDC0F50EEAF', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 18, NULL, NULL, 2, NULL, 0, 'Brush red luster dust over the stencil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA1846A87CBFAA43B2CB909A4B219F37', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 19, NULL, NULL, 2, NULL, 0, 'Remove stencil and repeat with the remaining cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43785917D9F92243B4DAF7AB9D90CDF9', X'62758C5A91ABD24B901CE6E23F71F8B2', X'62758C5A91ABD24B901CE6E23F71F8B2', 20, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4DA7758FA8AC3D44B54134DE1CC95260', X'4E74A6CE2C47E14B9CFA1E2D0327923A', X'4E74A6CE2C47E14B9CFA1E2D0327923A', 0, NULL, NULL, 2, NULL, 0, 'Pour white Cannellini Beans, roasted red peppers, basil pesto, and goat cheese in the bowl of a food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7779866F1CAACC43B03DF2E59D145D85', X'4E74A6CE2C47E14B9CFA1E2D0327923A', X'4E74A6CE2C47E14B9CFA1E2D0327923A', 1, NULL, NULL, 2, NULL, 0, 'Pulse until fairly smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DC861FBB03B2D4E87C9AECA23AAC149', X'4E74A6CE2C47E14B9CFA1E2D0327923A', X'4E74A6CE2C47E14B9CFA1E2D0327923A', 2, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FFAACE345F3B24383D17F09E89BC9EB', X'4E74A6CE2C47E14B9CFA1E2D0327923A', X'4E74A6CE2C47E14B9CFA1E2D0327923A', 3, NULL, NULL, 2, NULL, 0, 'Pour into a bowl and serve with chips, crackers, or vegetables.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C4799B0E7FD2247BCF713E3B8C8D9DC', X'09CD67B56777D240906F86C9709629A2', X'09CD67B56777D240906F86C9709629A2', 0, NULL, NULL, 2, NULL, 0, 'Spoon about 1/2 teaspoon of pink candy melts onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64F1C59DAAF0BC42947BBA90DA40EF6E', X'09CD67B56777D240906F86C9709629A2', X'09CD67B56777D240906F86C9709629A2', 1, NULL, NULL, 2, NULL, 0, 'Dip one mini pretzel twist in pink candy melts then remove and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'343626C454567641B1D6E31642DCED36', X'09CD67B56777D240906F86C9709629A2', X'09CD67B56777D240906F86C9709629A2', 2, NULL, NULL, 2, NULL, 0, 'Set over top of the spoonful of pink candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'188C69D69A73EE4C9C92C811E38AA1F2', X'09CD67B56777D240906F86C9709629A2', X'09CD67B56777D240906F86C9709629A2', 3, NULL, NULL, 2, NULL, 0, 'Immediately attach two candy eyes, two jumbo pink heart sprinkle wings, and two heart sprinkle ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E01B9EE36F2B4942AB2718A92BE17D38', X'09CD67B56777D240906F86C9709629A2', X'09CD67B56777D240906F86C9709629A2', 4, NULL, NULL, 2, NULL, 0, 'Chill in the freezer until hardened, about 3-4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF5D321D025537458EEB93FF6C651608', X'09CD67B56777D240906F86C9709629A2', X'09CD67B56777D240906F86C9709629A2', 5, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 24 pretzels owls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AE891610BD8BD4C9B1291121A13B61B', X'09CD67B56777D240906F86C9709629A2', X'09CD67B56777D240906F86C9709629A2', 6, NULL, NULL, 2, NULL, 0, 'Dip a pretzel dipping stick into milk chocolate then set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56EC958E3073E7479913071F9A58639F', X'09CD67B56777D240906F86C9709629A2', X'09CD67B56777D240906F86C9709629A2', 7, NULL, NULL, 2, NULL, 0, 'Immediately press one pretzel owl along the top edge of the chocolate dipped pretzel making it look like the owl is perched on a branch.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'869603358F84794CBEB75F1BB3C25E88', X'09CD67B56777D240906F86C9709629A2', X'09CD67B56777D240906F86C9709629A2', 8, NULL, NULL, 2, NULL, 0, 'Add two red heart sprinkle feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C05A719DB705543A2DE1CCBEFC266A9', X'09CD67B56777D240906F86C9709629A2', X'09CD67B56777D240906F86C9709629A2', 9, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'866B6F2A4342A94F80EF7AA48A44A716', X'09CD67B56777D240906F86C9709629A2', X'09CD67B56777D240906F86C9709629A2', 10, NULL, NULL, 2, NULL, 0, 'Let the pretzels dry completely before packaging.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A372B85F050A242BAF875A70D083CDB', X'40C3ED46B3237048A819D249A0EB6C4D', X'40C3ED46B3237048A819D249A0EB6C4D', 0, NULL, NULL, 2, NULL, 0, 'Break both curved parts off of 12 of the heart sprinkles creating 24 little red tongues.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57438598189C8E4998D1FC7F2FEC222D', X'40C3ED46B3237048A819D249A0EB6C4D', X'40C3ED46B3237048A819D249A0EB6C4D', 1, NULL, NULL, 2, NULL, 0, 'Spoon about 1/2 teaspoon of white chocolate onto a piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01B3736BA4A1824FAC4B3A26E14FDAF4', X'40C3ED46B3237048A819D249A0EB6C4D', X'40C3ED46B3237048A819D249A0EB6C4D', 2, NULL, NULL, 2, NULL, 0, 'Dip one mini pretzel twist in white chocolate then remove and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'478832CF7B42AE47B0BE6F8E4E0DC884', X'40C3ED46B3237048A819D249A0EB6C4D', X'40C3ED46B3237048A819D249A0EB6C4D', 3, NULL, NULL, 2, NULL, 0, 'Set over top of the spoonful of white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'619655BB2DF61740AC9B921D214D93D3', X'40C3ED46B3237048A819D249A0EB6C4D', X'40C3ED46B3237048A819D249A0EB6C4D', 4, NULL, NULL, 2, NULL, 0, 'Immediately press one red tongue and 1 brown candy nose onto the bottom edge of the pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'222F3D9FABD17E47B79D5D2AE087DF37', X'40C3ED46B3237048A819D249A0EB6C4D', X'40C3ED46B3237048A819D249A0EB6C4D', 5, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator if using pure chocolate and in the freezer if using Candy Melts until hardened, about 3-4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3414EA7ACE935347963F64FA6CE4729E', X'40C3ED46B3237048A819D249A0EB6C4D', X'40C3ED46B3237048A819D249A0EB6C4D', 6, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 24 white chocolate dipped pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C1144EB64F3224BAC38D71B0210975A', X'40C3ED46B3237048A819D249A0EB6C4D', X'40C3ED46B3237048A819D249A0EB6C4D', 7, NULL, NULL, 2, NULL, 0, 'Paint on a spot of milk chocolate above and to one side of the candy nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B541BFD714DA140A121682386DEBB24', X'40C3ED46B3237048A819D249A0EB6C4D', X'40C3ED46B3237048A819D249A0EB6C4D', 8, NULL, NULL, 2, NULL, 0, 'Immediately press one candy eye over the chocolate spot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BFC5564F22522C4A88BFCAA776146644', X'40C3ED46B3237048A819D249A0EB6C4D', X'40C3ED46B3237048A819D249A0EB6C4D', 9, NULL, NULL, 2, NULL, 0, 'Attach the other candy eye using a small amount of milk chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58ADBE3C720E7647B715D98C3EA07B3E', X'40C3ED46B3237048A819D249A0EB6C4D', X'40C3ED46B3237048A819D249A0EB6C4D', 10, NULL, NULL, 2, NULL, 0, 'Paint on some tiny freckles next to the candy nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03116510BE1F784AA71C1E9C48D0414B', X'40C3ED46B3237048A819D249A0EB6C4D', X'40C3ED46B3237048A819D249A0EB6C4D', 11, NULL, NULL, 2, NULL, 0, 'Let the chocolate harden completely before packaging them.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3E38E889D8FBF4EA184A0DF478BC51C', X'7FD965E3319DE0428477E9E02A16D428', X'7FD965E3319DE0428477E9E02A16D428', 0, NULL, NULL, 2, NULL, 0, 'Dip one mini pretzel twist in pink candy melts using a dipping tool or fork.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6CCE3250A9350449E6EB907CFBA22CD', X'7FD965E3319DE0428477E9E02A16D428', X'7FD965E3319DE0428477E9E02A16D428', 1, NULL, NULL, 2, NULL, 0, 'Remove and allow excess candy to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C111D4D00EA04459DF495318520FF59', X'7FD965E3319DE0428477E9E02A16D428', X'7FD965E3319DE0428477E9E02A16D428', 2, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'519814AF416D574BA41739993A52AAC6', X'7FD965E3319DE0428477E9E02A16D428', X'7FD965E3319DE0428477E9E02A16D428', 3, NULL, NULL, 2, NULL, 0, 'Attach 4 jumbo pink heart sprinkles to create a tail and 3 fins.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65851DCA890B0F44AD958D4CC3A0201C', X'7FD965E3319DE0428477E9E02A16D428', X'7FD965E3319DE0428477E9E02A16D428', 4, NULL, NULL, 2, NULL, 0, 'Attach 1 candy eye and 1 mini red heart sprinkle creating lips.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE644B9A4A06F144B3C73DE6515559C5', X'7FD965E3319DE0428477E9E02A16D428', X'7FD965E3319DE0428477E9E02A16D428', 5, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator for 3-4 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3576C8C600D864D93C0CAFB4ABFB455', X'7FD965E3319DE0428477E9E02A16D428', X'7FD965E3319DE0428477E9E02A16D428', 6, NULL, NULL, 2, NULL, 0, 'Repeat dipping and decorating a total of 36 pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79D916FD34B501459620DE621596166B', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 0, NULL, NULL, 2, NULL, 0, 'Pinch off pieces of the white modeling chocolate and roll into a thin rope.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7C6FA757DC36C4B82EA0F632379EA06', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 1, NULL, NULL, 2, NULL, 0, 'Cut off 1/2 inch pieces and roll into a cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC3366DBEC9E84479A2CEB8FEFB1B7B0', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 2, NULL, NULL, 2, NULL, 0, 'Twist two of those small pieces together to form a unicorn horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'998E68B1CD64994999C694D2CDCB8528', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 3, NULL, NULL, 2, NULL, 0, 'Brush with edible gold luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6732C7CB734484E84588856F04E2E78', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 4, NULL, NULL, 2, NULL, 0, 'Set aside to dry for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE3657304773BF418558B2A79C8678D2', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 5, NULL, NULL, 2, NULL, 0, 'Attach one small white heart to one jumbo pink heart using white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'062608951169444A8957DABF07801D43', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 6, NULL, NULL, 2, NULL, 0, 'Spoon about 1/2 teaspoon of white chocolate onto a piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0B008442D3E7941B5A650A2FF829B72', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 7, NULL, NULL, 2, NULL, 0, 'Dip one mini pretzel twist in white chocolate then remove and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C01BE893BA7274B812F40F06A353463', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 8, NULL, NULL, 2, NULL, 0, 'Set over top of the spoonful of white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'275A00294DBA4E4986994F22A8A3CA36', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 9, NULL, NULL, 2, NULL, 0, 'Immediately press one unicorn horn in between the two rounded parts of the pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'015DCC3A79BA9744BF2FBD9C70B7A462', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 10, NULL, NULL, 2, NULL, 0, 'Attach two pink heart ears on either side of the horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DE0D1FE255DA44D874F18BE453953E0', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 11, NULL, NULL, 2, NULL, 0, 'Sprinkle 5 rainbow heart sprinkles on the pretzel in between the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'982573D68B921F4BA1EF6D6AF53C2324', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 12, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator if using pure white chocolate and in the freezer is using Candy Melts for 3-4 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6B5F49E80663A41A21FC61E869C9E6A', X'F93B4B7CFD21A34495C4BD3DDC8711FE', X'F93B4B7CFD21A34495C4BD3DDC8711FE', 13, NULL, NULL, 2, NULL, 0, 'Repeat dipping and decorating a total of 24 pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BF838BED7F12B40A1D01BBDF9A49011', X'04D6A2BCDEBED742A15E1583146DDE12', X'04D6A2BCDEBED742A15E1583146DDE12', 0, NULL, NULL, 2, NULL, 0, 'Spoon about an ounce of chocolate into one heart shaped cavity in your silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AF7B1860A37AF479A70025EE34F8CBC', X'04D6A2BCDEBED742A15E1583146DDE12', X'04D6A2BCDEBED742A15E1583146DDE12', 1, NULL, NULL, 2, NULL, 0, 'Brush the chocolate up the sides of the mold adding more chocolate as needed to create a nice shell. Let the chocolate set slightly then add a second layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'212119D5F9C7824280F177BFD14741A2', X'04D6A2BCDEBED742A15E1583146DDE12', X'04D6A2BCDEBED742A15E1583146DDE12', 2, NULL, NULL, 2, NULL, 0, 'You will use approximately 2 ounces of chocolate per heart shaped bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6EDC108E4F25C94E8A46E229F56A1762', X'04D6A2BCDEBED742A15E1583146DDE12', X'04D6A2BCDEBED742A15E1583146DDE12', 3, NULL, NULL, 2, NULL, 0, 'Repeat creating 12 heart bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CE9204B361E8D4884FBDACF9109E9BF', X'04D6A2BCDEBED742A15E1583146DDE12', X'04D6A2BCDEBED742A15E1583146DDE12', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate until completely set then remove and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDC64498AB1192449597908400D8085E', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 0, NULL, NULL, 2, NULL, 0, 'Pour water into the bottom of a saucepan set over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'836AA1CF9B2B7C4A833726AA3B814EBB', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 1, NULL, NULL, 2, NULL, 0, 'Add sugar and allow to come to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'799BE5FF4C906644AE70111EB4ECAAD6', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 2, NULL, NULL, 2, NULL, 0, 'Boil until all the sugar is dissolved, stirring if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05CCE758223DDC4882CD58A0A75467B9', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 3, NULL, NULL, 2, NULL, 0, 'Pour egg yolks into a mixing bowl and whisk to break up.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D90959B96341343AAC32736A08A325A', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 4, NULL, NULL, 2, NULL, 0, 'Continue whisking while slowly drizzling in the sugar syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B46A43D126E28408D243D275888E224', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 5, NULL, NULL, 2, NULL, 0, 'Pour 1/2 cup of heavy whipping cream into the saucepan and set it back on the stove on low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06665738F4857D438C44E50CA1FDD973', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 6, NULL, NULL, 2, NULL, 0, 'Stir in the egg mixture and continue to stir for at least 3 minutes until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6A91D4BE187A342A4738C8246AB9847', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 7, NULL, NULL, 2, NULL, 0, 'You may heat until thickened, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F41386AFDDD3D8479EB3DB9BB4935E3E', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 8, NULL, NULL, 2, NULL, 0, 'Pour through a fine mesh sieve that is set over your bowl of melted semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AF0BC186B3B2540A909AFB185961857', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 9, NULL, NULL, 2, NULL, 0, 'Discard anything that remains in the sieve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'209E84F07659AF4DB9DB14D466A05D40', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 10, NULL, NULL, 2, NULL, 0, 'Stir the chocolate until glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D138CE197D7CB47980CD20843F26BFD', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 11, NULL, NULL, 2, NULL, 0, 'Fold the whipped cream into the chocolate in two stages.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98B81EB2E2449247B2D1CD4E3906D4A6', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 12, NULL, NULL, 2, NULL, 0, 'Cover and chill until the mousse thickens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38A9C8EE22A8F9488BF956B48CCB8DEB', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 13, NULL, NULL, 2, NULL, 0, 'Spoon mousse into a pastry bag fitted with a large star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BF4CAA31D842C4FAB29A094E2576558', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 14, NULL, NULL, 2, NULL, 0, 'Pipe into chocolate heart bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85C25E9A681B3C48A7CE2A0E33B0DCB6', X'094E194DBC1044479A8DADB968438AE4', X'094E194DBC1044479A8DADB968438AE4', 15, NULL, NULL, 2, NULL, 0, 'Garnish with fresh raspberries and heart sprinkles if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD64A00F14C36F4E8843B0F676FC2C0B', X'69DBE2F7D18BB647B36B666E04CF2BFC', X'69DBE2F7D18BB647B36B666E04CF2BFC', 0, NULL, NULL, 2, NULL, 0, 'Stir the toffee bits into the chocolate and pour into a 5 inch heart cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49FD60475EAD5546939B4B54212A3693', X'69DBE2F7D18BB647B36B666E04CF2BFC', X'69DBE2F7D18BB647B36B666E04CF2BFC', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle on M&M''s.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'523B2790081DF54E9F335A2D376ADA2B', X'69DBE2F7D18BB647B36B666E04CF2BFC', X'69DBE2F7D18BB647B36B666E04CF2BFC', 2, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator if using pure chocolate and in the freezer if using Candy Melts until the chocolate hardens, about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3700D7135030294DAA0827CABAD812CA', X'69DBE2F7D18BB647B36B666E04CF2BFC', X'69DBE2F7D18BB647B36B666E04CF2BFC', 3, NULL, NULL, 2, NULL, 0, 'Remove and pop the chocolate out of the cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E48A93CB5372D8499FEDBEE2F5EEC6AA', X'69DBE2F7D18BB647B36B666E04CF2BFC', X'69DBE2F7D18BB647B36B666E04CF2BFC', 4, NULL, NULL, 2, NULL, 0, 'Set aside at room temperature for 20-30 minutes before packaging in clear cellophane bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA8B3BC5CE5EDC49B80FEFE1A7680378', X'2F5E480A9D0C5140AC3FA55513B446B7', X'2F5E480A9D0C5140AC3FA55513B446B7', 0, NULL, NULL, 2, NULL, 0, 'Stir the crisp rice cereal into the chocolate and pour into a 5 inch heart cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A98FF6B51F6EF40A63F104FDE382651', X'2F5E480A9D0C5140AC3FA55513B446B7', X'2F5E480A9D0C5140AC3FA55513B446B7', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle on M&M''s and sprinkle mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38435EF058FFD749911D876E2D96B5A7', X'2F5E480A9D0C5140AC3FA55513B446B7', X'2F5E480A9D0C5140AC3FA55513B446B7', 2, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator if using pure chocolate and in the freezer if using Candy Melts until the chocolate hardens, about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF71F7B06E4E8140BCE9E58D505AFD83', X'2F5E480A9D0C5140AC3FA55513B446B7', X'2F5E480A9D0C5140AC3FA55513B446B7', 3, NULL, NULL, 2, NULL, 0, 'Remove and pop the chocolate out of the cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54E0F4DBBFE8B940A84D833326E694A4', X'2F5E480A9D0C5140AC3FA55513B446B7', X'2F5E480A9D0C5140AC3FA55513B446B7', 4, NULL, NULL, 2, NULL, 0, 'Set aside at room temperature for 20-30 minutes before packaging in clear cellophane bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCDDB12DBE5A054EAEC8A25883E70C6E', X'B383066752C09645A39164023D763D0C', X'B383066752C09645A39164023D763D0C', 0, NULL, NULL, 2, NULL, 0, 'Spoon Dulce de Leche into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E93C6219552F4D4099098DA1DB947637', X'B383066752C09645A39164023D763D0C', X'B383066752C09645A39164023D763D0C', 1, NULL, NULL, 2, NULL, 0, 'Pour half of the chocolate into the heart shaped cookie cutter and spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FFB69ADBC3FC0542836B604E49CDC21E', X'B383066752C09645A39164023D763D0C', X'B383066752C09645A39164023D763D0C', 2, NULL, NULL, 2, NULL, 0, 'Pipe a heart of the Dulce de Leche into the center of the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9F37EE4755D1541A25F3DB22DEE40BA', X'B383066752C09645A39164023D763D0C', X'B383066752C09645A39164023D763D0C', 3, NULL, NULL, 2, NULL, 0, 'Chill for 3-5 minutes until the chocolate begins to set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1D6A6902166F84A966C5CAD21A71A8E', X'B383066752C09645A39164023D763D0C', X'B383066752C09645A39164023D763D0C', 4, NULL, NULL, 2, NULL, 0, 'Pour the remaining chocolate over top and spread into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'170E8DF9663D5746A92F549A16DD2A55', X'B383066752C09645A39164023D763D0C', X'B383066752C09645A39164023D763D0C', 5, NULL, NULL, 2, NULL, 0, 'Arrange the Mega M&M''s around the perimeter of the heart.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'541B25E9CD60054E9C8E5AB8F8E2FA6A', X'B383066752C09645A39164023D763D0C', X'B383066752C09645A39164023D763D0C', 6, NULL, NULL, 2, NULL, 0, 'Chill until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99B1DCF5A55568498A068B7D486BA5F2', X'B383066752C09645A39164023D763D0C', X'B383066752C09645A39164023D763D0C', 7, NULL, NULL, 2, NULL, 0, 'Set aside for 20-30 minutes before packaging.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39E9A1B181E9964680D3FE7D4DCD60DE', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 0, NULL, NULL, 2, NULL, 0, 'Combine the yellow Candy Melts and the sweetened condensed milk in a microwave safe mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9FAE1C52B5D3C246A1AA1A7BB6C35658', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 1 minute then stir well until the candy begins to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E6DC4139B42AE48ACF98A8CE99BFBBE', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 2, NULL, NULL, 2, NULL, 0, 'Heat for 30 more seconds on high power then let the bowl sit in the microwave for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E5E7FBA0CB5A544870F437887EAF661', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 3, NULL, NULL, 2, NULL, 0, 'Remove and stir thoroughly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0968692435DCC54D91CC6156E571BAFA', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 4, NULL, NULL, 2, NULL, 0, 'Let rest for 2 more minutes then stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'877080C36EA9F7459B540A54D141F128', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 5, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments on high power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D38FC06A439B934B8668B479C43E5BD8', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 6, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap and set the bowl aside for several hours until the fudge firms up or refrigerate for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F82D7EFC85EDC408C5E6625279CC5CD', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 7, NULL, NULL, 2, NULL, 0, 'Meanwhile rub the black Candy Melts wafers in the palm of your hand. This will remove any white marks and soften the wafer slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C12C71A226E6A428921B196B3113470', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 8, NULL, NULL, 2, NULL, 0, 'Cut out one or two smiles from each wafer using a 1 1/4 inch round cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B309CDAA823E1841B23700142BEA2134', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 9, NULL, NULL, 2, NULL, 0, 'Scoop out 1 1/2 tablespoons of fudge using an ice cream scoop or spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EB7EE2ED7E91D4B9BCC5FC4DB965603', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 10, NULL, NULL, 2, NULL, 0, 'Roll into a ball then flatten into a 2 1/4 inch round disc onto a non-stick silicone mat or piece of wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45070B367CA2824A94BB848C92AC71CB', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 11, NULL, NULL, 2, NULL, 0, 'Immediately press two heart sprinkles or two Sixlets into the fudge creating the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE8CA2A2052F21409BCDFA7EE4559C3D', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 12, NULL, NULL, 2, NULL, 0, 'Press a black Candy Melts smile below the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E04B6B33619104F9344AF9005A51153', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 13, NULL, NULL, 2, NULL, 0, 'Repeat the process, creating a total of 30 emoji.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66F8FFE87D7F8047A4FEE87FCBFBA12D', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 14, NULL, NULL, 2, NULL, 0, 'Pop the candy into the freezer for 10 minutes until they firm up and are easy to remove from the silicone mat or wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23AB5C9CACED86429683029F306B2FA5', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 15, NULL, NULL, 2, NULL, 0, 'Let warm up for 15 minutes before packaging them in clear 3 inch x 5 inch cellophane bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D33D5FF7FD191E43B4FD2AF6D663A8E9', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 16, NULL, NULL, 2, NULL, 0, 'Fold over the top of the bag and tape it closed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB401445B0B84A40ABABC95D5B2EAE47', X'D15094C0ADF467489B511ADDF69DB76C', X'D15094C0ADF467489B511ADDF69DB76C', 17, NULL, NULL, 2, NULL, 0, 'Print and cut the Valentine''s Day tags, fold them over, add your child''s name and the recipient''s name, then use double stick tape to attach them to the top of the bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD296EDE1CED47438F057FC8B10CE7C6', X'C44A257586440140BF9226F62DC98CCD', X'C44A257586440140BF9226F62DC98CCD', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'178C77663391FA4DB0A60978B296FD27', X'C44A257586440140BF9226F62DC98CCD', X'C44A257586440140BF9226F62DC98CCD', 1, NULL, NULL, 2, NULL, 0, 'Spray the Wilton Tasty-Fill Heart Pan with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0A7ECBD7B0C5B4091D31BC29791B349', X'C44A257586440140BF9226F62DC98CCD', X'C44A257586440140BF9226F62DC98CCD', 2, NULL, NULL, 2, NULL, 0, 'Combine the cake mix, pudding, whipped topping, sour cream, milk, eggs, and oil in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B645E355EEA4648870688723E3D65BF', X'C44A257586440140BF9226F62DC98CCD', X'C44A257586440140BF9226F62DC98CCD', 3, NULL, NULL, 2, NULL, 0, 'Beat, using an electric mixer, on low speed for 30 seconds then medium speed for 1 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CC20F9E75617249B6B9459E51CD6ACB', X'C44A257586440140BF9226F62DC98CCD', X'C44A257586440140BF9226F62DC98CCD', 4, NULL, NULL, 2, NULL, 0, 'Equally divide batter among the two cake pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5336C4B17A90DE428CB56A2B3D9DFAE8', X'C44A257586440140BF9226F62DC98CCD', X'C44A257586440140BF9226F62DC98CCD', 5, NULL, NULL, 2, NULL, 0, 'Bake for 25-30 minutes until a toothpick inserted in the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05D9667607E420479F68C398559D0085', X'C44A257586440140BF9226F62DC98CCD', X'C44A257586440140BF9226F62DC98CCD', 6, NULL, NULL, 2, NULL, 0, 'Allow cakes to cool in pans for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37F1B05878A39D428BB97C79633FC86C', X'C44A257586440140BF9226F62DC98CCD', X'C44A257586440140BF9226F62DC98CCD', 7, NULL, NULL, 2, NULL, 0, 'Then turn cakes out onto a cooling rack and cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0676D27C52DCC04D9632EC71840628CD', X'C44A257586440140BF9226F62DC98CCD', X'C44A257586440140BF9226F62DC98CCD', 8, NULL, NULL, 2, NULL, 0, 'Pop cakes in freezer for 30-60 minutes to firm up before filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D555461A53EDE4AA2CB80E0E8FEFFAB', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', 0, NULL, NULL, 2, NULL, 0, 'Pour the strawberry pie filling into a fine mesh sieve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE62226C4C299E4CB641D3EEB158046F', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', 1, NULL, NULL, 2, NULL, 0, 'Allow the gel to drain away from the strawberries.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC5BF39A264F5A4DA21DEAE1CE515A96', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', 2, NULL, NULL, 2, NULL, 0, 'Place the softened cream cheese in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B6DC519B54FEF40A48F91719E96609D', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', 3, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat the cream cheese on medium high speed until fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B69426CD56D9E468323B62C8CBE0C8F', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', 4, NULL, NULL, 2, NULL, 0, 'Sift the powdered sugar into the bowl and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'294750577EC5354D8D131117DBC43898', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', 5, NULL, NULL, 2, NULL, 0, 'Add the strawberries and beat until the strawberries become small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC5A440CFEF32A49BE035BE30492D563', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', 6, NULL, NULL, 2, NULL, 0, 'Then add the whipped topping and beat just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE7D9F5EAFD14148A55998A3986CD957', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', 7, NULL, NULL, 2, NULL, 0, 'If desired, stir in some pink food coloring to intensify the color.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAC5CAAD6EC0F2428926F116DBEECD4A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', 8, NULL, NULL, 2, NULL, 0, 'Pour some of the filling into a disposable pastry bag or a large zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'134D9E8BEED88A46BF6B5BC9220A91EF', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', 9, NULL, NULL, 2, NULL, 0, 'Remove cakes from freezer, place the cake with the single groove on an 8 inch cake board.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44DDD3E375B0CD43B9B2B7BE341BD37A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', 10, NULL, NULL, 2, NULL, 0, 'Pipe some filling into the groove in the cake then spread it into an even layer making sure you don''t cover the circle of cake in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D7B7F79CBB2124689C68F3706532253', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', 11, NULL, NULL, 2, NULL, 0, 'Fill the other cake then flip that cake upside down onto the bottom layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0AA8B24778DC57429AD6FD287079F082', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', X'0FCDBFE6CE4BAA4EAA352F5232FF0C3A', 12, NULL, NULL, 2, NULL, 0, 'Frost the exterior of the cake using the remaining strawberry cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'473A7AD50FE74E45A0092B437513E771', X'86FAB27C7AE2364FACAE236DF9636370', X'86FAB27C7AE2364FACAE236DF9636370', 0, NULL, NULL, 2, NULL, 0, 'Combine the chocolate, 1/4 cup heavy whipping cream, and strawberry pie filling in a small glass bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60838DA7492A3543B8AFBDCEDCE5AC43', X'86FAB27C7AE2364FACAE236DF9636370', X'86FAB27C7AE2364FACAE236DF9636370', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power in the microwave for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A706A0511A66034EB0A7B48A8831DAD2', X'86FAB27C7AE2364FACAE236DF9636370', X'86FAB27C7AE2364FACAE236DF9636370', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9085C44BFF65DA4EAB83337A5A51D002', X'86FAB27C7AE2364FACAE236DF9636370', X'86FAB27C7AE2364FACAE236DF9636370', 3, NULL, NULL, 2, NULL, 0, 'Heat for another 20 seconds, then let sit in microwave for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC16295E463F4544AD085D41ED9CB494', X'86FAB27C7AE2364FACAE236DF9636370', X'86FAB27C7AE2364FACAE236DF9636370', 4, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F74A3BD09352545B4DF41075C3C07B4', X'86FAB27C7AE2364FACAE236DF9636370', X'86FAB27C7AE2364FACAE236DF9636370', 5, NULL, NULL, 2, NULL, 0, 'Stir in the remaining tablespoon of cream until smooth and glossy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D9377A88B69F14D8B293C73E198606F', X'86FAB27C7AE2364FACAE236DF9636370', X'86FAB27C7AE2364FACAE236DF9636370', 6, NULL, NULL, 2, NULL, 0, 'Pour over the cake allowing drips to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B4E9F4D6993584CBAF0CD76F5A77E12', X'51CD27BAA9F84946A253966786799CBF', X'51CD27BAA9F84946A253966786799CBF', 0, NULL, NULL, 2, NULL, 0, 'Pour candy melts into silicone shot glass mold reserving about a tablespoon of candy to use as `glue` to attach the sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9FAB59A91BFFEF48B30382315831EBAB', X'51CD27BAA9F84946A253966786799CBF', X'51CD27BAA9F84946A253966786799CBF', 1, NULL, NULL, 2, NULL, 0, 'Tap the mold to allow the air bubbles to rise to the surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9656FF63B843D94AB534C02A7FD1AC62', X'51CD27BAA9F84946A253966786799CBF', X'51CD27BAA9F84946A253966786799CBF', 2, NULL, NULL, 2, NULL, 0, 'If needed, pop the air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D5E8E513D30674EA227D005B1EA197B', X'51CD27BAA9F84946A253966786799CBF', X'51CD27BAA9F84946A253966786799CBF', 3, NULL, NULL, 2, NULL, 0, 'Freeze the mold for about 15 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3424D717350174283D02A712E914FD3', X'51CD27BAA9F84946A253966786799CBF', X'51CD27BAA9F84946A253966786799CBF', 4, NULL, NULL, 2, NULL, 0, 'Remove and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36762AA44836954BA4C166865BAC5130', X'51CD27BAA9F84946A253966786799CBF', X'51CD27BAA9F84946A253966786799CBF', 5, NULL, NULL, 2, NULL, 0, 'Use dabs of melted chocolate or candy melts to attach pink or red heart sprinkles around the outside of the chocolate heart cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF1BF76C4D83BA478B973B332EE4D11A', X'51CD27BAA9F84946A253966786799CBF', X'51CD27BAA9F84946A253966786799CBF', 6, NULL, NULL, 2, NULL, 0, 'Fill with candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2904CE116DC5140B96D19BE64F496AD', X'ED14E97EB350B44AB6D4E191C3C6FCF3', X'ED14E97EB350B44AB6D4E191C3C6FCF3', 0, NULL, NULL, 2, NULL, 0, 'Line a pan with parchment paper, wax paper, or a silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E749D010F2699240955851F32B44F0A5', X'ED14E97EB350B44AB6D4E191C3C6FCF3', X'ED14E97EB350B44AB6D4E191C3C6FCF3', 1, NULL, NULL, 2, NULL, 0, 'Combine the chocolate chips and Dulce de Leche in a medium saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1549B509328EE0499414471850BC03EA', X'ED14E97EB350B44AB6D4E191C3C6FCF3', X'ED14E97EB350B44AB6D4E191C3C6FCF3', 2, NULL, NULL, 2, NULL, 0, 'Heat over low heat, stirring frequently until just small pieces of solid chocolate remain.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCE7F0235B3A9F41BE1E9D8C62F28C2F', X'ED14E97EB350B44AB6D4E191C3C6FCF3', X'ED14E97EB350B44AB6D4E191C3C6FCF3', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and let sit for 3-4 minutes, then stir until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED2AD98104A9D74A88AAE5410E35556D', X'ED14E97EB350B44AB6D4E191C3C6FCF3', X'ED14E97EB350B44AB6D4E191C3C6FCF3', 4, NULL, NULL, 2, NULL, 0, 'Pour into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B7AEA694FCD3D489E2ABEE0B7749868', X'ED14E97EB350B44AB6D4E191C3C6FCF3', X'ED14E97EB350B44AB6D4E191C3C6FCF3', 5, NULL, NULL, 2, NULL, 0, 'Cut off the tip and pipe out 10 big swirls of the fudge.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46F289EE5BBF0049B7F34509B0D445F1', X'ED14E97EB350B44AB6D4E191C3C6FCF3', X'ED14E97EB350B44AB6D4E191C3C6FCF3', 6, NULL, NULL, 2, NULL, 0, 'Immediately press two candy eyes onto each swirl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A878B6E848EC84881892EDC549DBB7C', X'ED14E97EB350B44AB6D4E191C3C6FCF3', X'ED14E97EB350B44AB6D4E191C3C6FCF3', 7, NULL, NULL, 2, NULL, 0, 'Then cut the white candy wafers into smiles and press onto the fudge just below the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DA5F82CDA02E54398813A4C8543AB3C', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', 0, NULL, NULL, 2, NULL, 0, 'Heat white chocolate/confectionery coating and sweetened condensed milk medium saucepan over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5BA73C29659FD4F9973A9C88B46570F', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', 1, NULL, NULL, 2, NULL, 0, 'Stir frequently until about 3/4''s of the chocolate has melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE7085B299409548898528FD41942861', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', 2, NULL, NULL, 2, NULL, 0, 'Remove from the heat and let sit for 2-4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'050B18AD4A5AE845BAE18B489F4379F7', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', 3, NULL, NULL, 2, NULL, 0, 'Then stir until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E61F5342FF6C664EB2AEA7AF9A643886', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', 4, NULL, NULL, 2, NULL, 0, 'Stir in the cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BB2BCB60BCCF746B853D17AA756C263', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', 5, NULL, NULL, 2, NULL, 0, 'Pour the fudge into a glass pie plate or 8x8 square dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6817C115A0A71947A34FAC554BF1912B', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', 6, NULL, NULL, 2, NULL, 0, 'Press a piece of plastic wrap directly on top of the fudge.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29CD613B12D377478B90D3218C43B5AE', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', 7, NULL, NULL, 2, NULL, 0, 'Set aside to cool for several hours or pop it in the refrigerator for about an hour until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21C47841D30B0B4F9B0329D3BEE833C0', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', 8, NULL, NULL, 2, NULL, 0, 'Scoop out about 2 tablespoons of fudge, roll then shape into rocks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80B15C77FB1F434DBE6B8D226EC94B36', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', X'844A4BB473BE6D4A9BCDF01ED1C41DBA', 9, NULL, NULL, 2, NULL, 0, 'While the fudge rocks are still really soft press two candy eyes into them then add one chocolate sprinkle for the smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5910590B7B2C6949A6C5DC48CCFA2B97', X'35BAD737F42D6643910284DBE31F642F', X'35BAD737F42D6643910284DBE31F642F', 0, NULL, NULL, 2, NULL, 0, 'Combine heavy whipping cream and corn syrup in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'161B3565B7036646B984BEF496093AC1', X'35BAD737F42D6643910284DBE31F642F', X'35BAD737F42D6643910284DBE31F642F', 1, NULL, NULL, 2, NULL, 0, 'Heat for 1 minute then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'200B579080356D42A060DF697684731B', X'35BAD737F42D6643910284DBE31F642F', X'35BAD737F42D6643910284DBE31F642F', 2, NULL, NULL, 2, NULL, 0, 'Pour chopped chocolate into the bowl of a food processor or in a mixing bowl and pour hot cream over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2816AF3A00C25469854C824355D4B18', X'35BAD737F42D6643910284DBE31F642F', X'35BAD737F42D6643910284DBE31F642F', 3, NULL, NULL, 2, NULL, 0, 'Allow to sit for 3 minutes, then pulse or stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9BAE74470A9B04BA9F7B4BDBF63C607', X'35BAD737F42D6643910284DBE31F642F', X'35BAD737F42D6643910284DBE31F642F', 4, NULL, NULL, 2, NULL, 0, 'Let cool for one hour until thickened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D769D81425A093419D7D17DBFC565BAC', X'C4097CA2134318449F544D9C616C345E', X'C4097CA2134318449F544D9C616C345E', 0, NULL, NULL, 2, NULL, 0, 'Color 2 ounces of white chocolate or confectionery coating using red candy coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A575671FB005514DB52CFAC041ED1DF0', X'C4097CA2134318449F544D9C616C345E', X'C4097CA2134318449F544D9C616C345E', 1, NULL, NULL, 2, NULL, 0, 'Use a food use only paint brush to paint polka dots on the inside of 24 paper candy cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9426F6F254A8F84D9324E6C7F5458D3C', X'C4097CA2134318449F544D9C616C345E', X'C4097CA2134318449F544D9C616C345E', 2, NULL, NULL, 2, NULL, 0, 'Refrigerate for 5 minutes until the dots are hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'138366299509E94FBD7B974F05200F53', X'C4097CA2134318449F544D9C616C345E', X'C4097CA2134318449F544D9C616C345E', 3, NULL, NULL, 2, NULL, 0, 'Spoon about 1/2 teaspoon of white chocolate into a cup and brush it up around the sides of the cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0280E983E62E9B4AA59E1CDEDA6B6B9E', X'C4097CA2134318449F544D9C616C345E', X'C4097CA2134318449F544D9C616C345E', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate for 5-8 minutes until hardened. Freeze if using confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F630709AA2882341A00098992046D1F3', X'C4097CA2134318449F544D9C616C345E', X'C4097CA2134318449F544D9C616C345E', 5, NULL, NULL, 2, NULL, 0, 'Peel the paper off the white chocolate cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C68B5F79A16B342AD6079C459CFE698', X'D8E2B8594334A7498F0638CCE4AB621B', X'D8E2B8594334A7498F0638CCE4AB621B', 0, NULL, NULL, 2, NULL, 0, 'Pour thickened chocolate ganache into a pastry bag fitted with a large star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5352BB0F3984A47B9F4B3B86E69E1AA', X'D8E2B8594334A7498F0638CCE4AB621B', X'D8E2B8594334A7498F0638CCE4AB621B', 1, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of ganache into each candy cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B65D876A6A92047BC5B657E50D144C3', X'D8E2B8594334A7498F0638CCE4AB621B', X'D8E2B8594334A7498F0638CCE4AB621B', 2, NULL, NULL, 2, NULL, 0, 'Immediately top with a chocolate coated candy ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A613A34FFA7AF48AF1C4CE4D364D18B', X'CC322A31753A3240BB42643A79D9DD88', X'CC322A31753A3240BB42643A79D9DD88', 0, NULL, NULL, 2, NULL, 0, 'Spoon a heaping tablespoonful of melted white chocolate into one cavity in a semi-sphere silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'942CDC63BB8D584490DC605D961558B4', X'CC322A31753A3240BB42643A79D9DD88', X'CC322A31753A3240BB42643A79D9DD88', 1, NULL, NULL, 2, NULL, 0, 'Brush the white chocolate up the sides of the cavity.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF7D190F35FA8D4EA180EC6BF34D08F5', X'CC322A31753A3240BB42643A79D9DD88', X'CC322A31753A3240BB42643A79D9DD88', 2, NULL, NULL, 2, NULL, 0, 'Refrigerate is using pure white chocolate or freeze if using confectionery coating for 8-14 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70292F27DC34024285994DE23FA68E18', X'CC322A31753A3240BB42643A79D9DD88', X'CC322A31753A3240BB42643A79D9DD88', 3, NULL, NULL, 2, NULL, 0, 'Un-mold and allow them to warm to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9ED0F43F077EB845A33A761596E07B8C', X'CC322A31753A3240BB42643A79D9DD88', X'CC322A31753A3240BB42643A79D9DD88', 4, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of chocolate over the entire backside of the bowl or just around the outer and inner rim of the bowl then immediately roll in a bowl of sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85F2FA07590B654AA844C1F403D16B3B', X'CC322A31753A3240BB42643A79D9DD88', X'CC322A31753A3240BB42643A79D9DD88', 5, NULL, NULL, 2, NULL, 0, 'Once dry you can fill the bowls with your favorite treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6295ECCF7846A4586E5194BD81C472B', X'0B270304665AD0479513E8803E2F2BF3', X'0B270304665AD0479513E8803E2F2BF3', 0, NULL, NULL, 2, NULL, 0, 'Melt ice cream in microwave on high for 20 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78FC2B5A8DFA3048BC96045FF60734B8', X'0B270304665AD0479513E8803E2F2BF3', X'0B270304665AD0479513E8803E2F2BF3', 1, NULL, NULL, 2, NULL, 0, 'Remove and stir, heat for 20-30 more seconds until completely melted and it just comes to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA03A8B62E2455489AE4AD11D289145A', X'0B270304665AD0479513E8803E2F2BF3', X'0B270304665AD0479513E8803E2F2BF3', 2, NULL, NULL, 2, NULL, 0, 'Measure out 1/2 cup of melted ice cream and stir into the finely chopped chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64C7C48A2AFC0840BDED93EB888D5B7E', X'0B270304665AD0479513E8803E2F2BF3', X'0B270304665AD0479513E8803E2F2BF3', 3, NULL, NULL, 2, NULL, 0, 'If all the chocolate doesnt melt, heat at high power for 10 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B66C78DC44B874B97E093EDDBE19CB5', X'0B270304665AD0479513E8803E2F2BF3', X'0B270304665AD0479513E8803E2F2BF3', 4, NULL, NULL, 2, NULL, 0, 'Let it sit in the microwave for a minute, then remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F7389DE69147F49981A9E3ED4D09FEC', X'0B270304665AD0479513E8803E2F2BF3', X'0B270304665AD0479513E8803E2F2BF3', 5, NULL, NULL, 2, NULL, 0, 'Repeat if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62BF6AEB459F2241A9F0DAABCD1589C3', X'0B270304665AD0479513E8803E2F2BF3', X'0B270304665AD0479513E8803E2F2BF3', 6, NULL, NULL, 2, NULL, 0, 'Press a piece of plastic wrap directly on the surface of the chocolate ganache and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAFF548D3C0DA3469215BA400F19023D', X'0B270304665AD0479513E8803E2F2BF3', X'0B270304665AD0479513E8803E2F2BF3', 7, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to cool and thicken for several hours or overnight.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E904FF740AC1D143B7BDCF564C6972D9', X'0B270304665AD0479513E8803E2F2BF3', X'0B270304665AD0479513E8803E2F2BF3', 8, NULL, NULL, 2, NULL, 0, 'When firm, scoop and roll into your favorite toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C9B6F9CFEE55943874EDA391F66FFF3', X'877D78E1606DA34D9356721DD7CA6D4D', X'877D78E1606DA34D9356721DD7CA6D4D', 0, NULL, NULL, 2, NULL, 0, 'Line an 8 inch square pan with non-stick tin foil or parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1C3F1773E1FCD4CBB8219865F8C47CB', X'877D78E1606DA34D9356721DD7CA6D4D', X'877D78E1606DA34D9356721DD7CA6D4D', 1, NULL, NULL, 2, NULL, 0, 'Cut Sugar Stamp sheet to fit in bottom of pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'420D7D96EFC74E42AC3AF08E3692D278', X'877D78E1606DA34D9356721DD7CA6D4D', X'877D78E1606DA34D9356721DD7CA6D4D', 2, NULL, NULL, 2, NULL, 0, 'Stir together finely chopped white chocolate and sweetened condensed milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF27346B663E7642844FDCC4E93C716F', X'877D78E1606DA34D9356721DD7CA6D4D', X'877D78E1606DA34D9356721DD7CA6D4D', 3, NULL, NULL, 2, NULL, 0, 'Heat in microwave on high power for 45 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE3262827316424AA8DB163B0E6A0E34', X'877D78E1606DA34D9356721DD7CA6D4D', X'877D78E1606DA34D9356721DD7CA6D4D', 4, NULL, NULL, 2, NULL, 0, 'Remove and stir, then let sit for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CA2B6E54DF3B44E921A65A262EDEBB8', X'877D78E1606DA34D9356721DD7CA6D4D', X'877D78E1606DA34D9356721DD7CA6D4D', 5, NULL, NULL, 2, NULL, 0, 'Stir again then place back in the microwave and heat for 20 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBE48C1CA5E1544480B1CD8E0DA4D0AE', X'877D78E1606DA34D9356721DD7CA6D4D', X'877D78E1606DA34D9356721DD7CA6D4D', 6, NULL, NULL, 2, NULL, 0, 'Allow the bowl to sit in the microwave for 3 minutes then remove and stir slowly until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01C3CDC35668344EA332CCCA7C059B7B', X'877D78E1606DA34D9356721DD7CA6D4D', X'877D78E1606DA34D9356721DD7CA6D4D', 7, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BCEC72EBADB7A4CBA94C19704420418', X'877D78E1606DA34D9356721DD7CA6D4D', X'877D78E1606DA34D9356721DD7CA6D4D', 8, NULL, NULL, 2, NULL, 0, 'Pour into prepared pan and smooth into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F96BA7331B19D24593519E125185642D', X'877D78E1606DA34D9356721DD7CA6D4D', X'877D78E1606DA34D9356721DD7CA6D4D', 9, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap and let cool for 2 hours or refrigerate or freeze until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CA749E0D98EAA43B21A4288D2E8BD53', X'877D78E1606DA34D9356721DD7CA6D4D', X'877D78E1606DA34D9356721DD7CA6D4D', 10, NULL, NULL, 2, NULL, 0, 'Remove fudge from pan and peel off tin foil then peel off acetate from the sugar stamp.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'831B39657D84F14FB027D247FA9491CD', X'877D78E1606DA34D9356721DD7CA6D4D', X'877D78E1606DA34D9356721DD7CA6D4D', 11, NULL, NULL, 2, NULL, 0, 'Cut into squares and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A473FA1F4B2668468B924F94EF685390', X'7A96B8115E2CAC43B1B94AFD7FB7158E', X'7A96B8115E2CAC43B1B94AFD7FB7158E', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA745DF73F037F46AEAD8F347BC4E88B', X'7A96B8115E2CAC43B1B94AFD7FB7158E', X'7A96B8115E2CAC43B1B94AFD7FB7158E', 1, NULL, NULL, 2, NULL, 0, 'Scoop a heaping tablespoonful of sugar cookie dough into 12 heart shaped cavities in 2 silicone molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C771DE641887834EAF45E08A46ECB867', X'7A96B8115E2CAC43B1B94AFD7FB7158E', X'7A96B8115E2CAC43B1B94AFD7FB7158E', 2, NULL, NULL, 2, NULL, 0, 'Flatten dough into an even layer then press down in the center creating a lip around the edge of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9FCA7CCEC9F675419CE9AFBE150F199C', X'7A96B8115E2CAC43B1B94AFD7FB7158E', X'7A96B8115E2CAC43B1B94AFD7FB7158E', 3, NULL, NULL, 2, NULL, 0, 'Equally divide the cherry pie filling covering the cookie dough hearts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0485280BA142884686DC8DBB67BF9B52', X'7A96B8115E2CAC43B1B94AFD7FB7158E', X'7A96B8115E2CAC43B1B94AFD7FB7158E', 4, NULL, NULL, 2, NULL, 0, 'Break the remaining cookie dough into small chunks and scatter them over the pie filling in each of the hearts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'883493E9BF26E942BE7E087A5518829C', X'7A96B8115E2CAC43B1B94AFD7FB7158E', X'7A96B8115E2CAC43B1B94AFD7FB7158E', 5, NULL, NULL, 2, NULL, 0, 'Bake for 25-30 minutes until the cookie is golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C9F2004BBF4034FB65555EA7B076B95', X'7A96B8115E2CAC43B1B94AFD7FB7158E', X'7A96B8115E2CAC43B1B94AFD7FB7158E', 6, NULL, NULL, 2, NULL, 0, 'Set molds on a cooling rack and cool for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1644AA33A5182B46A732DE350CCB20C1', X'7A96B8115E2CAC43B1B94AFD7FB7158E', X'7A96B8115E2CAC43B1B94AFD7FB7158E', 7, NULL, NULL, 2, NULL, 0, 'Place in the freezer for at least one hour until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAD26B2E4BEF6C4393418EAC9484A5D7', X'7A96B8115E2CAC43B1B94AFD7FB7158E', X'7A96B8115E2CAC43B1B94AFD7FB7158E', 8, NULL, NULL, 2, NULL, 0, 'Un-mold by pressing on the backside of each heart cavity allowing the bars to fall out into your hand.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D04DBE58992024E85C810728872D720', X'7A96B8115E2CAC43B1B94AFD7FB7158E', X'7A96B8115E2CAC43B1B94AFD7FB7158E', 9, NULL, NULL, 2, NULL, 0, 'Turn right side up and allow to warm up to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2F38E07B7BB81419656B58FF1E111E1', X'7A96B8115E2CAC43B1B94AFD7FB7158E', X'7A96B8115E2CAC43B1B94AFD7FB7158E', 10, NULL, NULL, 2, NULL, 0, 'Serve at room temperature or heat until warmed through.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24BBC7A8B0EF214BB15A2545675BF56A', X'D45C512CADC97B4EB56E385D3703AC10', X'D45C512CADC97B4EB56E385D3703AC10', 0, NULL, NULL, 2, NULL, 0, 'Melt pink confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8488EC50202F724BB14E8A407C25FDE6', X'D45C512CADC97B4EB56E385D3703AC10', X'D45C512CADC97B4EB56E385D3703AC10', 1, NULL, NULL, 2, NULL, 0, 'Pour it into a squeeze bottle or pastry bag fitted with a small round pastry tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0648DB6A08010F489AF0F2F97E227109', X'D45C512CADC97B4EB56E385D3703AC10', X'D45C512CADC97B4EB56E385D3703AC10', 2, NULL, NULL, 2, NULL, 0, 'Pipe inch size X''s and O''s onto a parchment lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'422FD12D012B0944BB7DFAC8A3028953', X'D45C512CADC97B4EB56E385D3703AC10', X'D45C512CADC97B4EB56E385D3703AC10', 3, NULL, NULL, 2, NULL, 0, 'Refrigerate for about 3 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E7F80E868F111438B02E76D7A945643', X'D45C512CADC97B4EB56E385D3703AC10', X'D45C512CADC97B4EB56E385D3703AC10', 4, NULL, NULL, 2, NULL, 0, 'Remove from parchment paper and let sit to warm to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'174E4843D5AA30468B7FDB22492DE8C0', X'D45C512CADC97B4EB56E385D3703AC10', X'D45C512CADC97B4EB56E385D3703AC10', 5, NULL, NULL, 2, NULL, 0, 'Melt and temper pure milk chocolate or melt light cocoa candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E498498DA3F0B4EA473A217DE117F26', X'D45C512CADC97B4EB56E385D3703AC10', X'D45C512CADC97B4EB56E385D3703AC10', 6, NULL, NULL, 2, NULL, 0, 'Spread onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F57B494FD1292D4ABB7456CA0E33968E', X'D45C512CADC97B4EB56E385D3703AC10', X'D45C512CADC97B4EB56E385D3703AC10', 7, NULL, NULL, 2, NULL, 0, 'Immediately, drizzle with pink candy coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24CBAFC51D55CC4A86C3F05CFA60695E', X'D45C512CADC97B4EB56E385D3703AC10', X'D45C512CADC97B4EB56E385D3703AC10', 8, NULL, NULL, 2, NULL, 0, 'Press X''s and O''s on top, leaving room to make cuts around each set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98B5A1C8D72C084DB8D844F1DB77D926', X'D45C512CADC97B4EB56E385D3703AC10', X'D45C512CADC97B4EB56E385D3703AC10', 9, NULL, NULL, 2, NULL, 0, 'Toss on heart sprinkles and nonpareils.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA05D33FCCC32A4F8BAAEE4D89BF1BF3', X'D45C512CADC97B4EB56E385D3703AC10', X'D45C512CADC97B4EB56E385D3703AC10', 10, NULL, NULL, 2, NULL, 0, 'If using pure chocolate, let sit until it sets but is still soft.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4B9AE6CEECA7B45AE11DBC8378FCC8C', X'D45C512CADC97B4EB56E385D3703AC10', X'D45C512CADC97B4EB56E385D3703AC10', 11, NULL, NULL, 2, NULL, 0, 'If using confectionery coating, refrigerate until set but still soft, 1-3 minutes.');
