INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D88BAB116893E247A7423BDB2689EAE5', X'854A4C8FD5C8FB43853CC30525FF141D', X'854A4C8FD5C8FB43853CC30525FF141D', 13, NULL, NULL, 2, NULL, 0, 'Place in a baking pan and cover with tin foil. Bake for 25 minutes then uncover and bake for an additional 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B489F2F8A3ED64892E972E665C48685', X'854A4C8FD5C8FB43853CC30525FF141D', X'854A4C8FD5C8FB43853CC30525FF141D', 14, NULL, NULL, 2, NULL, 0, 'Let cool for 5 minutes, then run a plastic spreader/spatula around the edge of the pan. Release the clamp and remove the sides of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'907E9D0B49E43242901DF3D606614CE2', X'854A4C8FD5C8FB43853CC30525FF141D', X'854A4C8FD5C8FB43853CC30525FF141D', 15, NULL, NULL, 2, NULL, 0, 'Set each enchilada heart on a dinner plate and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D76E85D4AB82B04E8A5F7E52F2E9AB63', X'854A4C8FD5C8FB43853CC30525FF141D', X'854A4C8FD5C8FB43853CC30525FF141D', 16, NULL, NULL, 2, NULL, 0, 'Decorate with enchilada sauce, if youd like. I strained some of the sauce through a fine-mesh sieve, then poured it into a squeeze bottle so I could drizzle it over the heart and add dots of sauce to the plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'138B29360C23434FA6E990B67426D531', X'B92350D09EEA854690BB737BAD3A37D2', X'B92350D09EEA854690BB737BAD3A37D2', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6BFBDE95309244F8D3E389E3B038F51', X'B92350D09EEA854690BB737BAD3A37D2', X'B92350D09EEA854690BB737BAD3A37D2', 1, NULL, NULL, 2, NULL, 0, 'Heat the frozen raspberries in a saucepan set over medium heat. As the raspberries get hot, begin smashing them. Once they are broken down, remove them from the heat and pour into a fine mesh sieve set over a glass measuring cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26AB5D2C73500F4DB825F6853173BD2B', X'B92350D09EEA854690BB737BAD3A37D2', X'B92350D09EEA854690BB737BAD3A37D2', 2, NULL, NULL, 2, NULL, 0, 'Press the raspberries to extract the juice. Discard the pulp and seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED219A78B65EE348BBFD6281035DD2A8', X'B92350D09EEA854690BB737BAD3A37D2', X'B92350D09EEA854690BB737BAD3A37D2', 3, NULL, NULL, 2, NULL, 0, 'You need 1 1/3 cup of the raspberry puree for the pound cake recipe. If you dont have enough, add some water. If you have too much, remove some.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3ED2E9E13B9C7040B9671FB2A76B3E56', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 0, NULL, NULL, 2, NULL, 0, 'Combine 2 pound cake mixes with whipped topping mix and butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0122EEA5FD59B45BC1C6B873E0F7462', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 1, NULL, NULL, 2, NULL, 0, 'Mix on low speed in a stand mixer until the butter is broken up into pea size pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5E6D753E870054EB77F3B2CBDABD7E8', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 2, NULL, NULL, 2, NULL, 0, 'Add the raspberry puree, eggs, egg whites, and raspberry flavored candy oil, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64DB28214983EF41BB6A5CFDFF29C414', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 3, NULL, NULL, 2, NULL, 0, 'Mix on low speed for 1 minute, then increase speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58A55ECF6308C34890D3DDD6C1A79DF5', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 4, NULL, NULL, 2, NULL, 0, 'Divide the raspberry pound cake batter into 3 bowls and color one red, one bright pink, and one light pink.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4728F621FE59FF48863E5AF48B2274FF', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 5, NULL, NULL, 2, NULL, 0, 'Spray a Wilton Long Loaf pan with Wilton Bake Easy Spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05F17416E701414389EB516566008671', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 6, NULL, NULL, 2, NULL, 0, 'Pour each color batter into a Wilton Disposable Pastry Bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11D3C64370985E40A1DEA89A7CD12276', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 7, NULL, NULL, 2, NULL, 0, 'Pipe squiggles of batter over the bottom of the pan. Start with red, then light pink, then red, then bright pink. Repeat until you fill the entire pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3A922F7918CA1448EB5EB1AFD30A560', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 8, NULL, NULL, 2, NULL, 0, 'Tap the pan a few times on the counter to remove any big air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1E30FAF349A634E8235D92426649956', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 9, NULL, NULL, 2, NULL, 0, 'Bake for 70-80 minutes, until a toothpick inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'241BD2A2650DEE4E95948CE9543BD46F', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 10, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool for 10 minutes, then remove it from the pan and allow it to cool for 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'996CA6B234CCFA4EBA61D9FBC1FA0041', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 11, NULL, NULL, 2, NULL, 0, 'Trim the cracked top and sides off the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54F9E0C72E13D548897EE31F7596776F', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 12, NULL, NULL, 2, NULL, 0, 'Use a Wilton Cake Level set at the lowest setting to cut 4 even slices out of the length of cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7DAF03134D74A42ADFB8475351601CE', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 13, NULL, NULL, 2, NULL, 0, 'The slices need to be no thicker than the cookie cutter you will use.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12521D740E8C854C9C78DD40B42F089D', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 14, NULL, NULL, 2, NULL, 0, 'Cut I Y O U and a heart out of each slice.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3EFB279C754024DBEEC002529A78395', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 15, NULL, NULL, 2, NULL, 0, 'Lay the cake cutouts in a single layer on a cutting board or baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0DE0FA9085A36409B94BF42721A0D19', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 16, NULL, NULL, 2, NULL, 0, 'Freeze for at least 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D41940388AB5574E9ADD058BAED6CC59', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', X'C5EEAFCE5B22C341B8F7DEEB9F906C69', 17, NULL, NULL, 2, NULL, 0, 'Once the cut-outs are frozen, you can make the lemon pound cake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08D705CA8993F04FB89045FC6F31BB67', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 0, NULL, NULL, 2, NULL, 0, 'Combine all the lemon pound cake ingredients in the bowl of a stand mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79775524ECB46F4D8CB34C127CF0FE9C', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 1, NULL, NULL, 2, NULL, 0, 'Beat on low speed for one minute, then on medium speed for 2 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E06064258867A44A508B009E8CDD475', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 2, NULL, NULL, 2, NULL, 0, 'Spray a Wilton Long Loaf pan with Bake Easy Spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4EA81914A36CD408FABCE1B56B77516', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 3, NULL, NULL, 2, NULL, 0, 'Pour a small amount of cake batter into the pan, filling it about an inch deep.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'950B5E52B48BF74899676E61952CEE3F', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 4, NULL, NULL, 2, NULL, 0, 'Stack the I cakes together and set them near one end of the pan, pressing them down into the cake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF1D96E0502B734DA66DA98C5C08AA2E', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 5, NULL, NULL, 2, NULL, 0, 'Stack the U cakes together and set at the opposite end of the pan, pressing them down into the cake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AE62695FE77EC47893981D9E5948C52', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 6, NULL, NULL, 2, NULL, 0, 'Stack the O cakes together and set next to the U cakes, pressing them down into the cake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91F0F14783E4734BB18029866333D58B', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 7, NULL, NULL, 2, NULL, 0, 'Pour some of the cake batter into a 16 inch disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF475F5330C5B44E9747B67D12625D8D', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 8, NULL, NULL, 2, NULL, 0, 'Pipe some batter around the U cakes and in the middle of the U cakes and in the middle of the O cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C814202CEBA6BE4BAC4537D6FDE5693F', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 9, NULL, NULL, 2, NULL, 0, 'Pipe some batter in between the I cakes and the O cakes. You want enough to hold the hearts and the Y cakes in place.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'186B8B5FF63ABF43B3E82DB20F563D1D', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 10, NULL, NULL, 2, NULL, 0, 'Stack the heart cakes together and press into the batter next to the I cakes. Do the same for the Y cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6408AFC0F921F458E272FB6941C977D', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 11, NULL, NULL, 2, NULL, 0, 'Cover the letters with the remaining cake batter. Spread it out in an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04AD136A799B2E4BB5DC9A3CE9C64C5E', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 12, NULL, NULL, 2, NULL, 0, 'Mark the pan so you know which way the letters are facing.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3F658A1803EA4438F6569084D34A51C', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 13, NULL, NULL, 2, NULL, 0, 'Bake the cake at 325 degrees Fahrenheit for 75-85 minutes until a cake tester (or skewer) inserted into the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A1D004344B1E34E873F4E171AFDD9B8', X'A9088DC201C5B64BB44F046D53F5DC2D', X'A9088DC201C5B64BB44F046D53F5DC2D', 14, NULL, NULL, 2, NULL, 0, 'Allow to cool in the pan for 10 minutes, then turn it out onto a cooling rack to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC27D90388DFC045A972EF1B5BDE7C26', X'5694383B42C35046AADCCB94A505443E', X'5694383B42C35046AADCCB94A505443E', 0, NULL, NULL, 2, NULL, 0, 'Use an offset spatula to frost the cake so that you have a very thick layer of frosting all over the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5578D2BFAFCAF48A3E5B306FE8A964C', X'5694383B42C35046AADCCB94A505443E', X'5694383B42C35046AADCCB94A505443E', 1, NULL, NULL, 2, NULL, 0, 'Cover the cake in fondant.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B1E6E967827C543BCE4AFADB7F7146B', X'5694383B42C35046AADCCB94A505443E', X'5694383B42C35046AADCCB94A505443E', 2, NULL, NULL, 2, NULL, 0, 'Roll out the red fondant.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D881068443EA8F4297FA75EF73B9CF83', X'5694383B42C35046AADCCB94A505443E', X'5694383B42C35046AADCCB94A505443E', 3, NULL, NULL, 2, NULL, 0, 'Cut four different size hearts using cookie cutters and fondant plunger cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9C7566A6C20314F9905686BBE386819', X'5694383B42C35046AADCCB94A505443E', X'5694383B42C35046AADCCB94A505443E', 4, NULL, NULL, 2, NULL, 0, 'Brush the back sides of the fondant hearts with water using Wiltons Water Brush (which is another amazing tool,) and press them onto the fondant covered cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84E921F5AD138C448B52372320F13C03', X'5694383B42C35046AADCCB94A505443E', X'5694383B42C35046AADCCB94A505443E', 5, NULL, NULL, 2, NULL, 0, 'Place a few tiny hearts on the cake board, to mark the front of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D01B0B713430547867368339956B029', X'0B04B97D4D4DC543AC649213415643C9', X'0B04B97D4D4DC543AC649213415643C9', 0, NULL, NULL, 2, NULL, 0, 'Paint the ears and paws using white, the eyes using dark cocoa and white, the nose light cocoa, and the vest red.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D19BFE5A17CB624982C89FA5A4EDF131', X'0B04B97D4D4DC543AC649213415643C9', X'0B04B97D4D4DC543AC649213415643C9', 1, NULL, NULL, 2, NULL, 0, 'Once you have all the details painted, pop the mold in the freezer for 3-4 minutes, until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AA0B9FACF8C01458D699187C8CA0AFF', X'0B04B97D4D4DC543AC649213415643C9', X'0B04B97D4D4DC543AC649213415643C9', 2, NULL, NULL, 2, NULL, 0, 'Remove it from the freezer and allow the mold to come back to room temperature while you make the super easy fudge.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE5883318BFD25418FEF749FF4371F86', X'0B04B97D4D4DC543AC649213415643C9', X'0B04B97D4D4DC543AC649213415643C9', 3, NULL, NULL, 2, NULL, 0, 'Heat the finely chopped milk chocolate in the microwave on high power for 30 seconds. Stir. Heat for 25 seconds, stir. Heat for 20 seconds. Let bowl sit in microwave for 2 minutes then stir. If needed, heat for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9202843F5332774CAAED09FB8160B282', X'0B04B97D4D4DC543AC649213415643C9', X'0B04B97D4D4DC543AC649213415643C9', 4, NULL, NULL, 2, NULL, 0, 'Stir in peanut butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E44C7AF3887E243A34ECD4790397716', X'0B04B97D4D4DC543AC649213415643C9', X'0B04B97D4D4DC543AC649213415643C9', 5, NULL, NULL, 2, NULL, 0, 'Pour the chocolate peanut butter fudge into the silicone molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2211092D112C44EBC30B294B6F234F8', X'0B04B97D4D4DC543AC649213415643C9', X'0B04B97D4D4DC543AC649213415643C9', 6, NULL, NULL, 2, NULL, 0, 'Pop the molds in the refrigerator for one hour, then pop in the freezer for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45CBEC9C9BF00B4A9736329F7CA8DEEF', X'0B04B97D4D4DC543AC649213415643C9', X'0B04B97D4D4DC543AC649213415643C9', 7, NULL, NULL, 2, NULL, 0, 'Unmold the fudge bears, by turning the mold upside down, and pressing on the backside of the mold. If the fudge doesnt come out easily, it may need to chill in the refrigerator a bit longer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D85EFDC66BEE594C918052B0F71025D4', X'C18F43AADD92D8419401564932F59E00', X'C18F43AADD92D8419401564932F59E00', 0, NULL, NULL, 2, NULL, 0, 'Draw two nostrils and one mouth on 10 of the peanut butter candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA910959E9B30442AF9C9CA06E49B2A2', X'C18F43AADD92D8419401564932F59E00', X'C18F43AADD92D8419401564932F59E00', 1, NULL, NULL, 2, NULL, 0, 'Cut a small curved section off 20 of the peanut butter candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8350F22283A4D14EB91F66E09EE110EB', X'C18F43AADD92D8419401564932F59E00', X'C18F43AADD92D8419401564932F59E00', 2, NULL, NULL, 2, NULL, 0, 'Melt remaining 10 wafers in the microwave for 10-15 second burst on high power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B7E78C1C41AF54498EBFDC3E72CA4066', X'C18F43AADD92D8419401564932F59E00', X'C18F43AADD92D8419401564932F59E00', 3, NULL, NULL, 2, NULL, 0, 'Use the melted candy as glue to attach two peanut butter candy melt ears, the nose, and two candy eyes to each peanut butter cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6C6DDE5F00F534DAC8C6DC55F0B6356', X'C18F43AADD92D8419401564932F59E00', X'C18F43AADD92D8419401564932F59E00', 4, NULL, NULL, 2, NULL, 0, 'Allow them to dry for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3CEDEB90AAA7B4F9B96D29935A9EABC', X'C18F43AADD92D8419401564932F59E00', X'C18F43AADD92D8419401564932F59E00', 5, NULL, NULL, 2, NULL, 0, 'Set one monkey on each frosted cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4446BEA097A9904C9F261861BB7F8D3E', X'C18F43AADD92D8419401564932F59E00', X'C18F43AADD92D8419401564932F59E00', 6, NULL, NULL, 2, NULL, 0, 'Add two Whopper hands holding a banana candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8145D52442DDF243B386BF6CC197F3CE', X'7B45611FD233EE4B9C166177626ADBF8', X'7B45611FD233EE4B9C166177626ADBF8', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A74DF75BDB8C0149AC5CB675EA6D1AD6', X'7B45611FD233EE4B9C166177626ADBF8', X'7B45611FD233EE4B9C166177626ADBF8', 1, NULL, NULL, 2, NULL, 0, 'Generously grease the sides and base of a heart shaped springform pan with butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8D759CB66BA7240A8A38BE89CFF987E', X'7B45611FD233EE4B9C166177626ADBF8', X'7B45611FD233EE4B9C166177626ADBF8', 2, NULL, NULL, 2, NULL, 0, 'Melt chocolate and butter in a double boiler set over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C31D083AC14EA4881651A47F85E8667', X'7B45611FD233EE4B9C166177626ADBF8', X'7B45611FD233EE4B9C166177626ADBF8', 3, NULL, NULL, 2, NULL, 0, 'Allow to cool for 5 minutes, then stir in vanilla and egg yolks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F62E9179AA4AB45A16D88596F97A301', X'7B45611FD233EE4B9C166177626ADBF8', X'7B45611FD233EE4B9C166177626ADBF8', 4, NULL, NULL, 2, NULL, 0, 'Whisk egg whites and salt until foam forms.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07B555C090EEE6468785ACC8B5D0C582', X'7B45611FD233EE4B9C166177626ADBF8', X'7B45611FD233EE4B9C166177626ADBF8', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle in about 1/2 of the brown sugar and whisk until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C5D1A27F06E6241A45117546EFC70E4', X'7B45611FD233EE4B9C166177626ADBF8', X'7B45611FD233EE4B9C166177626ADBF8', 6, NULL, NULL, 2, NULL, 0, 'Add the rest of the brown sugar and whisk to soft peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC5088DD09347C49A281BDEB536D9595', X'7B45611FD233EE4B9C166177626ADBF8', X'7B45611FD233EE4B9C166177626ADBF8', 7, NULL, NULL, 2, NULL, 0, 'Fold whipped egg whites into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2AE38487788564F8293BF1B77A259B3', X'7B45611FD233EE4B9C166177626ADBF8', X'7B45611FD233EE4B9C166177626ADBF8', 8, NULL, NULL, 2, NULL, 0, 'Pour batter into springform pan and bake for 15-18 minutes until cake has risen, and springs back when touched in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'292355EA1817B34FB1D2A8A62C5B3280', X'7B45611FD233EE4B9C166177626ADBF8', X'7B45611FD233EE4B9C166177626ADBF8', 9, NULL, NULL, 2, NULL, 0, 'Cool completely for one hour. The cake will fall as it cools.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04E16E53BCEB2F4C96153FA5632CA4CA', X'3340C4999971CE499C2F6A9C6FC7B2D0', X'3340C4999971CE499C2F6A9C6FC7B2D0', 0, NULL, NULL, 2, NULL, 0, 'Whisk whipping cream to soft peaks. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB06A8C60B5B624F8A67C3867FE6B27B', X'3340C4999971CE499C2F6A9C6FC7B2D0', X'3340C4999971CE499C2F6A9C6FC7B2D0', 1, NULL, NULL, 2, NULL, 0, 'Pour water and sugar in a small saucepan and bring it to a boil over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57ADDD4E4BB24E40B9400BCF44E99839', X'3340C4999971CE499C2F6A9C6FC7B2D0', X'3340C4999971CE499C2F6A9C6FC7B2D0', 2, NULL, NULL, 2, NULL, 0, 'Whisk egg yolks in a medium bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E6E9CFD5155584CA36EC2304B5C8DD1', X'3340C4999971CE499C2F6A9C6FC7B2D0', X'3340C4999971CE499C2F6A9C6FC7B2D0', 3, NULL, NULL, 2, NULL, 0, 'Slowly drizzle the hot sugar syrup (water and sugar) into the eggs, whisking continuously.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41B60DA1886F9E438EEDA22018E2E440', X'3340C4999971CE499C2F6A9C6FC7B2D0', X'3340C4999971CE499C2F6A9C6FC7B2D0', 4, NULL, NULL, 2, NULL, 0, 'Stir into melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF06975D80CB364BA1614E4097989F3C', X'3340C4999971CE499C2F6A9C6FC7B2D0', X'3340C4999971CE499C2F6A9C6FC7B2D0', 5, NULL, NULL, 2, NULL, 0, 'Fold whipped cream into chocolate until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3174027B44A5F43A4FBB5FA869579A2', X'3340C4999971CE499C2F6A9C6FC7B2D0', X'3340C4999971CE499C2F6A9C6FC7B2D0', 6, NULL, NULL, 2, NULL, 0, 'Spread evenly over flourless chocolate cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11156A242EEC4A4AA9CB6AE617364567', X'3340C4999971CE499C2F6A9C6FC7B2D0', X'3340C4999971CE499C2F6A9C6FC7B2D0', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6E09C949A85B44C9E8A8458029BC746', X'7C57560D1376934489156572F3BB4466', X'7C57560D1376934489156572F3BB4466', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle gelatin over water. Set aside for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EFABD357CAF1E4C8575A90AB3E2312E', X'7C57560D1376934489156572F3BB4466', X'7C57560D1376934489156572F3BB4466', 1, NULL, NULL, 2, NULL, 0, 'Bring 1/2 cup heavy whipping cream just to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C9FC354A3E9CA4AABEFE130AE984318', X'7C57560D1376934489156572F3BB4466', X'7C57560D1376934489156572F3BB4466', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in gelatin until dissolved.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6959A01F51C54544925290B4B3232669', X'7C57560D1376934489156572F3BB4466', X'7C57560D1376934489156572F3BB4466', 3, NULL, NULL, 2, NULL, 0, 'Pour over white chocolate. Let sit one minute, then stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F071AE9E5549B4BB19916326004DF5A', X'7C57560D1376934489156572F3BB4466', X'7C57560D1376934489156572F3BB4466', 4, NULL, NULL, 2, NULL, 0, 'Whisk remaining 1 cups heavy whipping cream to stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0AC9CDB51F29A40B550935A1CC1AEC0', X'7C57560D1376934489156572F3BB4466', X'7C57560D1376934489156572F3BB4466', 5, NULL, NULL, 2, NULL, 0, 'Fold whipped cream into white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9567CB017EBFC94FB9E02A701264904A', X'7C57560D1376934489156572F3BB4466', X'7C57560D1376934489156572F3BB4466', 6, NULL, NULL, 2, NULL, 0, 'Fold maraschino cherries into white chocolate mousse, leaving a few to sprinkle on top of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F09F98F69C17B74DAFF72F08E19FB105', X'7C57560D1376934489156572F3BB4466', X'7C57560D1376934489156572F3BB4466', 7, NULL, NULL, 2, NULL, 0, 'Spread evenly over chocolate mousse layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CB8154122DC984787E7CE120D2C451C', X'7C57560D1376934489156572F3BB4466', X'7C57560D1376934489156572F3BB4466', 8, NULL, NULL, 2, NULL, 0, 'Refrigerate cake for at least 2 hours before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2BC55B1FB585F4CBB7831B41D2C6548', X'2D5FA7ECBD8A1846A65D32EC28F8F901', X'2D5FA7ECBD8A1846A65D32EC28F8F901', 0, NULL, NULL, 2, NULL, 0, 'Stir cream of coconut, then add white chocolate chips to the top bowl of a double boiler.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13A58F4714C69A489055FBFCBB1190E8', X'2D5FA7ECBD8A1846A65D32EC28F8F901', X'2D5FA7ECBD8A1846A65D32EC28F8F901', 1, NULL, NULL, 2, NULL, 0, 'Fill bottom double boiler pot with 1 inch of water. Put bowl on top and heat over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE9DCFDBACCDDC43A65831D95698487B', X'2D5FA7ECBD8A1846A65D32EC28F8F901', X'2D5FA7ECBD8A1846A65D32EC28F8F901', 2, NULL, NULL, 2, NULL, 0, 'Stir occasionally until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36A5AA774636B6499101E5063864ACDA', X'2D5FA7ECBD8A1846A65D32EC28F8F901', X'2D5FA7ECBD8A1846A65D32EC28F8F901', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in coconut.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05BBC73A022FC54CBC05D0A57C48C231', X'2D5FA7ECBD8A1846A65D32EC28F8F901', X'2D5FA7ECBD8A1846A65D32EC28F8F901', 4, NULL, NULL, 2, NULL, 0, 'Divide into 3 bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7A229E0EE01B64494871410426C1FAB', X'2D5FA7ECBD8A1846A65D32EC28F8F901', X'2D5FA7ECBD8A1846A65D32EC28F8F901', 5, NULL, NULL, 2, NULL, 0, 'Color 1 batch bright pink, 1 batch medium pink, 1 batch light pink.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D33CDC59460FD4683DF32D526F17B49', X'2D5FA7ECBD8A1846A65D32EC28F8F901', X'2D5FA7ECBD8A1846A65D32EC28F8F901', 6, NULL, NULL, 2, NULL, 0, 'Spoon a teaspoonful of the light pink into 24 heart cavities. Press down into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F59EEDC998ABF4896D7F6B18469B725', X'2D5FA7ECBD8A1846A65D32EC28F8F901', X'2D5FA7ECBD8A1846A65D32EC28F8F901', 7, NULL, NULL, 2, NULL, 0, 'Top with a layer of medium pink then a layer of bright pink. Press to compress the hearts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCC8EF696022AB4B96BE6D078DF9EC32', X'2D5FA7ECBD8A1846A65D32EC28F8F901', X'2D5FA7ECBD8A1846A65D32EC28F8F901', 8, NULL, NULL, 2, NULL, 0, 'Freeze for 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE91A7E4B616864B875B1CAA45C6E9F5', X'2D5FA7ECBD8A1846A65D32EC28F8F901', X'2D5FA7ECBD8A1846A65D32EC28F8F901', 9, NULL, NULL, 2, NULL, 0, 'Un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'410F3070085EED438AC5DC57CD9E628D', X'2D5FA7ECBD8A1846A65D32EC28F8F901', X'2D5FA7ECBD8A1846A65D32EC28F8F901', 10, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for up to 10 days.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5476A35844C2F45AF4718ED4EBD8E28', X'D8E51F2438AE6741BFF23ACCB4D3AC15', X'D8E51F2438AE6741BFF23ACCB4D3AC15', 0, NULL, NULL, 2, NULL, 0, 'Stir together ricotta, mozzarella, Parmesan, one egg, and pinch of salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0B0A431F46DFF4D9A18EE687DD6AF53', X'D8E51F2438AE6741BFF23ACCB4D3AC15', X'D8E51F2438AE6741BFF23ACCB4D3AC15', 1, NULL, NULL, 2, NULL, 0, 'Spray the inside of each heart with non-stick cooking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1A490054924BC46AF08BA36E481B7F5', X'D8E51F2438AE6741BFF23ACCB4D3AC15', X'D8E51F2438AE6741BFF23ACCB4D3AC15', 2, NULL, NULL, 2, NULL, 0, 'Open and unroll one tube of dough. Cut dough in half. Drape one piece of dough over heart cavity.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAB1BAFB9BF58E43BD548377F81CEB13', X'D8E51F2438AE6741BFF23ACCB4D3AC15', X'D8E51F2438AE6741BFF23ACCB4D3AC15', 3, NULL, NULL, 2, NULL, 0, 'Fill with 1 tablespoon pizza sauce and a little over 1/4 cup of the cheese mixture. Add toppings of your choice.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5522F4F6EE40BC4F9F72B5CBC621986E', X'D8E51F2438AE6741BFF23ACCB4D3AC15', X'D8E51F2438AE6741BFF23ACCB4D3AC15', 4, NULL, NULL, 2, NULL, 0, 'Fold dough over, pinching pieces together, cutting off excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'137FDD81152CE7419CA14D22114D0AF0', X'D8E51F2438AE6741BFF23ACCB4D3AC15', X'D8E51F2438AE6741BFF23ACCB4D3AC15', 5, NULL, NULL, 2, NULL, 0, 'Brush egg wash over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B88600C0D42D249ABF4FEB6CF7A2CBD', X'D8E51F2438AE6741BFF23ACCB4D3AC15', X'D8E51F2438AE6741BFF23ACCB4D3AC15', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11042803FD1B904AB572CB183D6B179C', X'D8E51F2438AE6741BFF23ACCB4D3AC15', X'D8E51F2438AE6741BFF23ACCB4D3AC15', 7, NULL, NULL, 2, NULL, 0, 'Heat oven to 375 degrees Fahrenheit. Bake for 18-22 minutes, until golden brown');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4159BD28B821046B3213F9274CD1F7F', X'D8E51F2438AE6741BFF23ACCB4D3AC15', X'D8E51F2438AE6741BFF23ACCB4D3AC15', 8, NULL, NULL, 2, NULL, 0, 'Heat remaining pizza sauce, then spoon into the indentations on the top of each heart. Serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F382D41CAF6A9948A8432042396D860E', X'912CFFED5B2BE34AA47CAC2F72273190', X'912CFFED5B2BE34AA47CAC2F72273190', 0, NULL, NULL, 2, NULL, 0, 'Melt Cherry Cordial Candy Melts, semi-sweet chocolate (if using) and sweetened condensed milk in a double boiler over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'453E5E445ADA7B42B953C67A64655365', X'912CFFED5B2BE34AA47CAC2F72273190', X'912CFFED5B2BE34AA47CAC2F72273190', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat after 3/4 melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47271F21FA8B6840A23FF6676A8E2B8E', X'912CFFED5B2BE34AA47CAC2F72273190', X'912CFFED5B2BE34AA47CAC2F72273190', 2, NULL, NULL, 2, NULL, 0, 'Stir then allow to sit for a minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C84E0E439A20094F8603407CD6E26040', X'912CFFED5B2BE34AA47CAC2F72273190', X'912CFFED5B2BE34AA47CAC2F72273190', 3, NULL, NULL, 2, NULL, 0, 'Stir until melted, returning to heat if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8D241EE4A1F2044982683708AC9916B', X'912CFFED5B2BE34AA47CAC2F72273190', X'912CFFED5B2BE34AA47CAC2F72273190', 4, NULL, NULL, 2, NULL, 0, 'Pour into mini heart silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9450D34DBB55FB4197D6A388CAF375B4', X'912CFFED5B2BE34AA47CAC2F72273190', X'912CFFED5B2BE34AA47CAC2F72273190', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate for 1 hour, then freeze for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1343FEF3FD1B8A488DCA678ECD63EDDE', X'912CFFED5B2BE34AA47CAC2F72273190', X'912CFFED5B2BE34AA47CAC2F72273190', 6, NULL, NULL, 2, NULL, 0, 'Pop fudge hearts out of mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6685B24F7AD9804FA908C5977C1FE40A', X'912CFFED5B2BE34AA47CAC2F72273190', X'912CFFED5B2BE34AA47CAC2F72273190', 7, NULL, NULL, 2, NULL, 0, 'Melt pink candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0A0942909EDF74C92D8F280C3589EFE', X'912CFFED5B2BE34AA47CAC2F72273190', X'912CFFED5B2BE34AA47CAC2F72273190', 8, NULL, NULL, 2, NULL, 0, 'Drizzle over hearts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09358B15D41BC040BC9088579ACBD315', X'695A8B115A56304AB85C9E1EE5A93A0F', X'695A8B115A56304AB85C9E1EE5A93A0F', 0, NULL, NULL, 2, NULL, 0, 'Crumble cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A7B6D6BA963954799F2BB45E940E973', X'695A8B115A56304AB85C9E1EE5A93A0F', X'695A8B115A56304AB85C9E1EE5A93A0F', 1, NULL, NULL, 2, NULL, 0, 'Mix in frosting, adding just enough so that the cake balls hold together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D74BD6450CE2544E838E86CA06794475', X'695A8B115A56304AB85C9E1EE5A93A0F', X'695A8B115A56304AB85C9E1EE5A93A0F', 2, NULL, NULL, 2, NULL, 0, 'Scoop out tablespoonfuls of cake ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A649C965F405384DAA0AD037314B3CC6', X'695A8B115A56304AB85C9E1EE5A93A0F', X'695A8B115A56304AB85C9E1EE5A93A0F', 3, NULL, NULL, 2, NULL, 0, 'Wrap each spoonful around maraschino cherries.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DBC98650A3C0F4991AB71A3F09D3646', X'695A8B115A56304AB85C9E1EE5A93A0F', X'695A8B115A56304AB85C9E1EE5A93A0F', 4, NULL, NULL, 2, NULL, 0, 'Roll into balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6605181F93F19E49A34A623761BF4721', X'695A8B115A56304AB85C9E1EE5A93A0F', X'695A8B115A56304AB85C9E1EE5A93A0F', 5, NULL, NULL, 2, NULL, 0, 'Melt Cordial Cherry Candy Melts');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EAAC10071A5CF409CC6AFA3A9920421', X'695A8B115A56304AB85C9E1EE5A93A0F', X'695A8B115A56304AB85C9E1EE5A93A0F', 6, NULL, NULL, 2, NULL, 0, 'Dip cake balls into candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82C6A95EE832724381BDFD4ABF5AA5B9', X'695A8B115A56304AB85C9E1EE5A93A0F', X'695A8B115A56304AB85C9E1EE5A93A0F', 7, NULL, NULL, 2, NULL, 0, 'Freeze for about 5 minutes, until the candy coating is set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'292E67CBC67213439ECDD8800B7B6A02', X'695A8B115A56304AB85C9E1EE5A93A0F', X'695A8B115A56304AB85C9E1EE5A93A0F', 8, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for up to 5 days.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F9623DD74773E43ACAA0CA74D73837A', X'83795A92AD32B6468EE4E89F90465AB8', X'83795A92AD32B6468EE4E89F90465AB8', 0, NULL, NULL, 2, NULL, 0, 'Melt ice cream in microwave on high for 20 seconds. Remove and stir, heat for 20-30 more seconds until completely melted and it just comes to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A53C0BF1A6627B49A93E94CE86FEDC32', X'83795A92AD32B6468EE4E89F90465AB8', X'83795A92AD32B6468EE4E89F90465AB8', 1, NULL, NULL, 2, NULL, 0, 'Measure out 1/2 cup of melted ice cream and stir into the finely chopped chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3239A79312999C4686E45BB2C22E4FBF', X'83795A92AD32B6468EE4E89F90465AB8', X'83795A92AD32B6468EE4E89F90465AB8', 2, NULL, NULL, 2, NULL, 0, 'If all the chocolate doesn''t melt, heat at high power for 15 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED0FF2B11D36414FBD36F4CE41C8BF02', X'83795A92AD32B6468EE4E89F90465AB8', X'83795A92AD32B6468EE4E89F90465AB8', 3, NULL, NULL, 2, NULL, 0, 'Let it sit in the microwave for a minute, then remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98E2A1E75BF6554CBA84789E290E5F15', X'83795A92AD32B6468EE4E89F90465AB8', X'83795A92AD32B6468EE4E89F90465AB8', 4, NULL, NULL, 2, NULL, 0, 'Heat again if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5C912AA0CBBE2488D8CFB48EC39D888', X'83795A92AD32B6468EE4E89F90465AB8', X'83795A92AD32B6468EE4E89F90465AB8', 5, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to cool and thicken at room temperature for about 6 hours or in the refrigerator for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8609535798FD2142ACDA67BDCE511AA7', X'83795A92AD32B6468EE4E89F90465AB8', X'83795A92AD32B6468EE4E89F90465AB8', 6, NULL, NULL, 2, NULL, 0, 'Scoop out using a small ice cream scoop (2 teaspoon size) or a spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'866687BEC33F3343AC7857519875F9C3', X'83795A92AD32B6468EE4E89F90465AB8', X'83795A92AD32B6468EE4E89F90465AB8', 7, NULL, NULL, 2, NULL, 0, 'Roll each scoop into a ball and immediately roll in the chocolate shavings.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8888A473C9E5FD43B05105E8D15CF8D3', X'83795A92AD32B6468EE4E89F90465AB8', X'83795A92AD32B6468EE4E89F90465AB8', 8, NULL, NULL, 2, NULL, 0, 'Store in an airtight container at room temp for up to 2 weeks. Refrigerate up to 3 weeks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E16B8A73668204A9518BD9083F4D67C', X'A76D4F923438874A85AA0C28F591BE03', X'A76D4F923438874A85AA0C28F591BE03', 0, NULL, NULL, 2, NULL, 0, 'Place white confectionery coating wafers and peanut butter chips in a microwave safe mixing bowl. Heat on high power for 45 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F3F04F658E1F041B2F71CF0EF4FDCEB', X'A76D4F923438874A85AA0C28F591BE03', X'A76D4F923438874A85AA0C28F591BE03', 1, NULL, NULL, 2, NULL, 0, 'Remove and stir the chips around or they will burn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EBAC2416B28B94CA1BDD85EAE4545C6', X'A76D4F923438874A85AA0C28F591BE03', X'A76D4F923438874A85AA0C28F591BE03', 2, NULL, NULL, 2, NULL, 0, 'Heat for 30 more seconds, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0B8FC0BC431B449AF24DED90232906C', X'A76D4F923438874A85AA0C28F591BE03', X'A76D4F923438874A85AA0C28F591BE03', 3, NULL, NULL, 2, NULL, 0, 'Heat for 25 seconds, then let it sit in the microwave for 2-3 minutes, then stir vigorously.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E61E37EA8244749AB286AA814A82793', X'A76D4F923438874A85AA0C28F591BE03', X'A76D4F923438874A85AA0C28F591BE03', 4, NULL, NULL, 2, NULL, 0, 'If all of the chips are not melted, heat at ten second intervals, stirring in between each, until completely melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82666AFA0822DF4392EEA17FCC00EE7E', X'A76D4F923438874A85AA0C28F591BE03', X'A76D4F923438874A85AA0C28F591BE03', 5, NULL, NULL, 2, NULL, 0, 'Stir in peanut butter and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B714546B0BDCD499AE2EC269E9219D8', X'A76D4F923438874A85AA0C28F591BE03', X'A76D4F923438874A85AA0C28F591BE03', 6, NULL, NULL, 2, NULL, 0, 'NOTES: Make sure to mix really well, scraping the bottom of the bowl. If you don''t stir the peanut butter into the candy, you''ll end up with clumps of hardened candy coating in your filling.Allow it to cool a bit before piping it into your chocolate shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'707898EC34FB0C44A4791F97A49BF9FB', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 0, NULL, NULL, 2, NULL, 0, 'Fill a roasting pan with enough water to come half way up the sides of the silicone heart molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A22F71461B35AD4893CE3C84D2BFC817', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 1, NULL, NULL, 2, NULL, 0, 'Set pan in oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18C8871F46C1B24F9D6930730D52C21A', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 2, NULL, NULL, 2, NULL, 0, 'Heat oven to 325 degree Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0714557137E0344285DA5FBEAE0FB940', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 3, NULL, NULL, 2, NULL, 0, 'Mix together the graham cracker crumbs, cinnamon, and melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A91A7FAEDC1E4479F0921C3815E70CE', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 4, NULL, NULL, 2, NULL, 0, 'Equally divide the crumbs among 18 heart shaped silicone mold cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D949A39CD73CDB439815BD9F0E6E64B8', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 5, NULL, NULL, 2, NULL, 0, 'Press the crumbs into an even layer and refrigerate until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE2F6DCDB86DE44C84C882A9A51E2CE3', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 6, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8B7826F19C8594690F135A616E12A7F', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 7, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E17BF22E0FB989499BB89394BFEF5515', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 8, NULL, NULL, 2, NULL, 0, 'Beat in eggs then add sour cream and vanilla and mix just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2198A089D3C0C94BAA217B3C873C4119', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 9, NULL, NULL, 2, NULL, 0, 'Equally divide batter into 6 bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDC790FDCAF10F40AAC140487EAB884D', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 10, NULL, NULL, 2, NULL, 0, 'Color one bowl of filling pink,another lavender, yellow, orange, green, and blue using food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC4DFB749D86894A962DA947F82B5F9B', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 11, NULL, NULL, 2, NULL, 0, 'Equally divide each color of filling among 3 heart cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F379B1D8152CEA4E98356C957A8D9F00', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 12, NULL, NULL, 2, NULL, 0, 'Bake cheesecakes in a water bath for 18-24 minutes until the edges look set but the center is jiggly but not wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57DF7483ECA13A4A9C95D9CECBA9E959', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 13, NULL, NULL, 2, NULL, 0, 'Remove from oven and water bath and allow to cool at room temperature for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20B85DBF6AE04A44B43242934A5A3918', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 14, NULL, NULL, 2, NULL, 0, 'Freeze for at least two hours then remove and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF49B5EA0C662645BC1F7C0F462FCC28', X'BA2401A136621943A3DA90F1FE0F8D1F', X'BA2401A136621943A3DA90F1FE0F8D1F', 15, NULL, NULL, 2, NULL, 0, 'Paint conversation heart phrases onto the tops of the cheesecakes using red food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C58637729BB8024984C64CA14F618776', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat oven to 450 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'776B62A772F78148B983BDDA27364083', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 1, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper or non-stick aluminum foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58893D8F76B21D4FB86BD6F26DBAE0C6', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 2, NULL, NULL, 2, NULL, 0, 'Thaw one Pillsbury pie crust according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'984C4A346436FC4AA91AE08F1D41C543', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 3, NULL, NULL, 2, NULL, 0, 'Unroll onto a work surface (I used a pie dough mat.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75CDEA1C4EEBF744BFB2A507F4BC7671', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 4, NULL, NULL, 2, NULL, 0, 'Use a rolling pin to roll the dough from its current 11 1/2` to 12 1/2`, just to thin it out a bit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8865417D779B874B90E77527FA86AE89', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 5, NULL, NULL, 2, NULL, 0, 'Cut 15 large hearts using the 2 1/2` cookie cutter and 15 small hearts using the 2` cutter, re-rolling the dough as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07C0218021CAC44FACA57B766A812193', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 6, NULL, NULL, 2, NULL, 0, 'Set the 15 small hearts on the prepared baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B28729F0E0CB943B4EED1872A4BA2E9', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 7, NULL, NULL, 2, NULL, 0, 'Whisk egg white with water in a small bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BC921CC40C797458B00A6B3A494D5BD', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 8, NULL, NULL, 2, NULL, 0, 'Set one chocolate heart on each of the small dough hearts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7C4DC452BFB1847AF975A019E8B97E2', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 9, NULL, NULL, 2, NULL, 0, 'Brush egg wash on the dough around the chocolate heart (or just brush egg wash over the entire surface of the dough and place a chocolate heart on top  just do a few at a time or the egg wash will dry.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38CA575592EC22469174DABEADFC0E10', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 10, NULL, NULL, 2, NULL, 0, 'Top each with one large dough heart.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03EDCFB6503F144E93627A47F85EEE9A', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 11, NULL, NULL, 2, NULL, 0, 'Pinch the edges of the two dough hearts together to seal well.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C70DAE8B319D942970120C62CE9C120', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 12, NULL, NULL, 2, NULL, 0, 'The top dough will overlap the bottom dough by a little bit and that is alright.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B1D0F8FC0CF334C80706F97815734AE', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 13, NULL, NULL, 2, NULL, 0, 'Use a fork to crimp all around the edges of each heart ravioli.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'957D37BD116B834CBAECB931647F268E', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 14, NULL, NULL, 2, NULL, 0, 'Brush the top of the heart with egg wash, for a more shiny finish. If you want, you can even sprinkle of bit of plain or colored sugar over each ravioli.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'187585FF8D2ED5488C6F2384C290BBE0', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 15, NULL, NULL, 2, NULL, 0, 'Bake for 8-9 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF636B6B6C267443A3A7D19C2CEA980D', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 16, NULL, NULL, 2, NULL, 0, 'Heat raspberry preserves in microwave for 10-second increments, stirring after each, until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'972FC8453ED8D3469426948BC694D98C', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 17, NULL, NULL, 2, NULL, 0, 'Arrange chocolate ravioli hearts on a dessert plate and drizzle with raspberry preserves.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35D1539B10B46F4CBF481F64F7269AF0', X'10390064F066394FA436D9729A2C3EA2', X'10390064F066394FA436D9729A2C3EA2', 18, NULL, NULL, 2, NULL, 0, 'Add some fresh raspberries and serve while the ravioli are warm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96CE9AEE5986D04B9B1076BBE8C5A420', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 0, NULL, NULL, 2, NULL, 0, 'Lay the chicken breast on a cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C286E0D7A33A104488ECA9E473DCBE88', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 1, NULL, NULL, 2, NULL, 0, 'Press a large heart-shaped cookie cutter into the breast.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D82EB361216C34190B9BB2BCDC29DBD', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 2, NULL, NULL, 2, NULL, 0, 'Use kitchen shears or a knife cut though the chicken breast, cutting around the heart shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F92DA33585A26444AE15C09F90BF56B4', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 3, NULL, NULL, 2, NULL, 0, 'Reserve excess chicken.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DFEB79FBE8230428663034C748D23F6', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 4, NULL, NULL, 2, NULL, 0, 'Season chicken breasts with salt and pepper and set in a shallow dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D00640C9358A9044A985816D5B7741ED', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 5, NULL, NULL, 2, NULL, 0, 'Pour buttermilk over chicken.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90553365B139BD4A96A02AB186C9769B', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 6, NULL, NULL, 2, NULL, 0, 'Cover the dish in plastic wrap and refrigerate for 30-45 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6F2751A65B57A48A02B14AC9C5B03E3', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 7, NULL, NULL, 2, NULL, 0, 'Remove and flip chicken breasts over.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70556780EED81449929B2705CECCB8A5', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 8, NULL, NULL, 2, NULL, 0, 'Return to refrigerator for 30 more minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'679F231EA866CE42BEC34562FF9A2533', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 9, NULL, NULL, 2, NULL, 0, 'Pour panko bread crumbs into a cold skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D2371F7CED1E54FA3435BC79A9F6CEC', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 10, NULL, NULL, 2, NULL, 0, 'Add olive oil and toss to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52BDF0A5AFEE994EAA9422E5EC606489', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 11, NULL, NULL, 2, NULL, 0, 'Place over medium-low heat. Stir often until the bread crumbs become golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84F2444958417A46812C087F921098A7', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 12, NULL, NULL, 2, NULL, 0, 'Remove toasted crumbs from the heat and pour into a shallow bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15C809359E0107499C2B2A9D088BD0F5', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 13, NULL, NULL, 2, NULL, 0, 'Allow to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EC4A66DC86F454395C990A103953503', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 14, NULL, NULL, 2, NULL, 0, 'Stir in grated Parmesan cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71AE6EE47E7F3748B97D6D03938D15E1', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 15, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6EBD7D088D59C48B79B7599E7023628', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 16, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 475 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE93B604C545734AA17156B9777E7AB3', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 17, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with aluminum foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'463110DC40404A43A4079ED7A436AC96', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 18, NULL, NULL, 2, NULL, 0, 'Lay a metal cooling rack over pan and spray with olive oil cooking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16EADE13BFB7EF4D9658094E32EC4AC9', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 19, NULL, NULL, 2, NULL, 0, 'Remove chicken breast hearts from the refrigerator.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56F8FAA86077B145A0FA4E2E7E0B26EF', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 20, NULL, NULL, 2, NULL, 0, 'Pick up a chicken breast and allow the excess buttermilk to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A47BE14AD386B346854D9DE2DAF2050E', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 21, NULL, NULL, 2, NULL, 0, 'Lay chicken in the crunchy Parmesan coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'117BE60C301C1541B00D603AE10996C1', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 22, NULL, NULL, 2, NULL, 0, 'Press the coating onto the chicken.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'591B0E58D2829F4F80CA062AA024474F', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 23, NULL, NULL, 2, NULL, 0, 'Pick up the chicken and allow excess coating to fall off before setting it on the prepared baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7121E317E5D2B48A48666B55FC530BC', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 24, NULL, NULL, 2, NULL, 0, 'Spray chicken breasts with olive oil cooking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0B82D9A7D683F41A477A5EEC1D51E43', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 25, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2022237ECFD5694DBA3D7EC9305EFF32', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 26, NULL, NULL, 2, NULL, 0, 'Meanwhile, prepare your cheese shaped hearts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B71B4E5F9DF9BA4F9196752C2D14BC25', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 27, NULL, NULL, 2, NULL, 0, 'Cut cheese using a heart-shaped cookie cutter that is smaller than the cutter used for the chicken. You can also just sprinkle on shredded cheese if you prefer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB65B1702AC03C49B834343B7D1C2D7D', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 28, NULL, NULL, 2, NULL, 0, 'Once the chicken has baked for 20 minutes, remove it from the oven and place a slice of cheese on top of each chicken breast.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C0B6F4261746B4FB0718BFF69C7ECD1', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 29, NULL, NULL, 2, NULL, 0, 'Return to oven and heat for about 5 minutes until the cheese is melted, and the chicken registers 165 degrees on an instant-read thermometer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07C3D302DF4BF342B88F46A3843B18A6', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 30, NULL, NULL, 2, NULL, 0, 'Heat your marinara sauce on the stove or in the microwave until it reaches the desired temperature for serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'265BE2D8E8A6AA4A85016F7746ED4F65', X'BF1C6D5377B3E04297BABF130310CB3D', X'BF1C6D5377B3E04297BABF130310CB3D', 31, NULL, NULL, 2, NULL, 0, 'Pour some hot marinara sauce on a dinner plate and top with the heart-shaped chicken Parmesan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'654A28FC46B02A4FBE0A3621725D329A', X'F1E6E1F903B8BA4FBD8833EAF02E1F56', X'F1E6E1F903B8BA4FBD8833EAF02E1F56', 0, NULL, NULL, 2, NULL, 0, 'Wash, dry, and tear your lettuce into bite-size pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6C78AB18BD11D41803DC957D3D2FC3A', X'F1E6E1F903B8BA4FBD8833EAF02E1F56', X'F1E6E1F903B8BA4FBD8833EAF02E1F56', 1, NULL, NULL, 2, NULL, 0, 'Cut Kalamata olives into thin slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96FED8AF7C46F446B6646237C9939407', X'F1E6E1F903B8BA4FBD8833EAF02E1F56', X'F1E6E1F903B8BA4FBD8833EAF02E1F56', 2, NULL, NULL, 2, NULL, 0, 'Put a serving of lettuce on a salad plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EAE1EFCF08BEE4B9F6387C9ABE6177E', X'F1E6E1F903B8BA4FBD8833EAF02E1F56', X'F1E6E1F903B8BA4FBD8833EAF02E1F56', 3, NULL, NULL, 2, NULL, 0, 'Top with cucumbers, kalamata olives, and feta cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B19410F5DA30294C9806C19B03472FD1', X'17077B26CFC331448630C3735C966A15', X'17077B26CFC331448630C3735C966A15', 0, NULL, NULL, 2, NULL, 0, 'Place grape tomato on the cutting board. Cut at a diagonal across the short side of the tomato.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAD413083ED78B46820692DAD22BC2C5', X'17077B26CFC331448630C3735C966A15', X'17077B26CFC331448630C3735C966A15', 1, NULL, NULL, 2, NULL, 0, 'Separate the two halves of the tomato.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2D76B98A5C78A40A9EB1CFFE2D87479', X'17077B26CFC331448630C3735C966A15', X'17077B26CFC331448630C3735C966A15', 2, NULL, NULL, 2, NULL, 0, 'Pick up one half of the cut tomato.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'891457E8B356BD408BADBF8764BE6747', X'17077B26CFC331448630C3735C966A15', X'17077B26CFC331448630C3735C966A15', 3, NULL, NULL, 2, NULL, 0, 'Rotate one-half of the tomato 180 degrees (turn it upside down.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'667A88F62594714B8541A2A630C0E834', X'17077B26CFC331448630C3735C966A15', X'17077B26CFC331448630C3735C966A15', 4, NULL, NULL, 2, NULL, 0, 'Press the two tomato halves together forming a heart.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'458CF8539F39BF47AB6C357289A7875B', X'17077B26CFC331448630C3735C966A15', X'17077B26CFC331448630C3735C966A15', 5, NULL, NULL, 2, NULL, 0, 'Carefully pick up your heart-shaped tomatoes and artfully arrange them over the top of the salad.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DFF2541E7E903E4F8069D97CBE4C16AF', X'17077B26CFC331448630C3735C966A15', X'17077B26CFC331448630C3735C966A15', 6, NULL, NULL, 2, NULL, 0, 'Serve your festive salads with homemade Tomato Vinaigrette or your favorite salad dressing.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44D3000E856D3445B9EF60D97FFE0FC5', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 0, NULL, NULL, 2, NULL, 0, 'Cut vine-ripened tomatoes into quarters.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62C48A372FFB2F42BD880CBFBE36EAA6', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle them with a pinch of salt and put into the bowl of a food processor (the food processor pictured at left is a mini one.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE11B87C1D98CD439147B673819FC06A', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 2, NULL, NULL, 2, NULL, 0, 'Puree the tomatoes until just slightly chunky.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AAD6AF03D8BFEE43A260A2AE8CB8C17D', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 3, NULL, NULL, 2, NULL, 0, 'Pour tomato puree into a fine-mesh sieve set over a measuring cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B42E0FAF770E85419D048700BF83EFF9', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 4, NULL, NULL, 2, NULL, 0, 'Allow the juice to drip down into the cup. Use a rubber spatula to press on the tomato solids to extract as much juice as possible.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFC6CC3F412C7D4B821FD80FF4F97D26', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 5, NULL, NULL, 2, NULL, 0, 'Discard the seeds and tomato solids.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9776568B08BC524D8D877AEDD4295A04', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 6, NULL, NULL, 2, NULL, 0, 'You will need 1/2 cup of tomato juice for one recipe. Discard any extra juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'205B548C36DAC345AF79663E424A6B82', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 7, NULL, NULL, 2, NULL, 0, 'Pour tomato juice into a small saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64048DD8F9F2FF4888E4C70E2BF6F037', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 8, NULL, NULL, 2, NULL, 0, 'Add red wine vinegar, oregano, and garlic.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'552E1EE8C038524FA9F8C21F13D24185', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 9, NULL, NULL, 2, NULL, 0, 'Heat over medium heat, simmering until liquid reduces to 3 tablespoons.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FA44707BD048A46B84C2A5D7709512B', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 10, NULL, NULL, 2, NULL, 0, 'Remove from heat and allow to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7751F7ACD32CE446859A20E83E32F18E', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 11, NULL, NULL, 2, NULL, 0, 'Once cooled, slowly drizzle in the olive oil while continually whisking to create an emulsion (a good blending of a watery liquid with an oil.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5D521EAED413643AEC367348E39544A', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 12, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'141CCA134422994D823E2CA7124CC4C1', X'A588F66AA1324940853801F602644B30', X'A588F66AA1324940853801F602644B30', 13, NULL, NULL, 2, NULL, 0, 'Drizzle over salad.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64908A43D8AB25499FD957D290B5C8C1', X'2D7BB125B811DE428436976ACC8175FC', X'2D7BB125B811DE428436976ACC8175FC', 0, NULL, NULL, 2, NULL, 0, 'Allow the cake ingredients to sit at room temperature for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85E4E6158E16A3448D375B01C91376A0', X'2D7BB125B811DE428436976ACC8175FC', X'2D7BB125B811DE428436976ACC8175FC', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB68E1B1A61BF043948645421978B2BE', X'2D7BB125B811DE428436976ACC8175FC', X'2D7BB125B811DE428436976ACC8175FC', 2, NULL, NULL, 2, NULL, 0, 'Line 24 cupcake cavities with cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F95BD4ACDB79CF4E9564D83506DEE8E7', X'2D7BB125B811DE428436976ACC8175FC', X'2D7BB125B811DE428436976ACC8175FC', 3, NULL, NULL, 2, NULL, 0, 'Combine the cake mix, water, oil, eggs, sour cream, pudding mix, and peppermint extract together in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1611A07A21A3D4291C4D88CA0CCBFFC', X'2D7BB125B811DE428436976ACC8175FC', X'2D7BB125B811DE428436976ACC8175FC', 4, NULL, NULL, 2, NULL, 0, 'Use an electric mixer to beat on low for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9CA6DB73B2F5B4A8409555E2143F921', X'2D7BB125B811DE428436976ACC8175FC', X'2D7BB125B811DE428436976ACC8175FC', 5, NULL, NULL, 2, NULL, 0, 'Add drops of green food coloring until you get the desired shade of green coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'905524C97086E9419EA76310C5FD40EE', X'2D7BB125B811DE428436976ACC8175FC', X'2D7BB125B811DE428436976ACC8175FC', 6, NULL, NULL, 2, NULL, 0, 'Increase the mixer speed to medium and beat for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBD6D18A89CCE6448583B2A7FEE15760', X'2D7BB125B811DE428436976ACC8175FC', X'2D7BB125B811DE428436976ACC8175FC', 7, NULL, NULL, 2, NULL, 0, 'Toss 1 tablespoon of flour with 1 cup of mini chocolate chips then stir them into the cake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0ED9E9075AC6644CB27E50DD98CD1CD7', X'2D7BB125B811DE428436976ACC8175FC', X'2D7BB125B811DE428436976ACC8175FC', 8, NULL, NULL, 2, NULL, 0, 'Fill 24 cupcake cavities about 2/3''s full with batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5707F68CE837BD4BBCCDC95A674C4678', X'2D7BB125B811DE428436976ACC8175FC', X'2D7BB125B811DE428436976ACC8175FC', 9, NULL, NULL, 2, NULL, 0, 'Bake the cupcakes for 16-20 minutes. A toothpick inserted into the cupcakes should come out with just a few moist crumbs or the cakes should spring back when you press a finger on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A24CC35BD8F1CF479BD2C726DCAA2B7E', X'2D7BB125B811DE428436976ACC8175FC', X'2D7BB125B811DE428436976ACC8175FC', 10, NULL, NULL, 2, NULL, 0, 'Allow the cupcakes to cool for 10 minutes in their pans then remove them and set them on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3186DCDA827F1B49A89105BF60DD7D9A', X'3A1BA11D80537A46A3141670E7391883', X'3A1BA11D80537A46A3141670E7391883', 0, NULL, NULL, 2, NULL, 0, 'Beat the shortening and butter together until well combined using an electric mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D5FD04EF60B2C438D26AFA824555627', X'3A1BA11D80537A46A3141670E7391883', X'3A1BA11D80537A46A3141670E7391883', 1, NULL, NULL, 2, NULL, 0, 'Add the powdered sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79C8D8E21572DF498A16549CC14B39F9', X'3A1BA11D80537A46A3141670E7391883', X'3A1BA11D80537A46A3141670E7391883', 2, NULL, NULL, 2, NULL, 0, 'Add the peppermint extract, 3 tablespoons of the milk, and drops of green food coloring and beat until fully incorporated and the frosting is smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF506E7D01212C46AF99201E5982C196', X'3A1BA11D80537A46A3141670E7391883', X'3A1BA11D80537A46A3141670E7391883', 3, NULL, NULL, 2, NULL, 0, 'Add more milk, as needed to make the frosting soft enough to pipe through a star tip, yet firm enough to hold it''s shape, and add more green food coloring until you get a vibrant Kelly green (the color will intensify as the frosting sits).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1D82E8EA9260A4289DEF7209ECA5ADB', X'6EE86DAD32E155459D79B810647CA8ED', X'6EE86DAD32E155459D79B810647CA8ED', 0, NULL, NULL, 2, NULL, 0, 'Roll out the black modeling chocolate to about 1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCF92A40C2D5E849BEE9DF8BDCF6BEA2', X'6EE86DAD32E155459D79B810647CA8ED', X'6EE86DAD32E155459D79B810647CA8ED', 1, NULL, NULL, 2, NULL, 0, 'Cut one 8 inch by 1 1/2 inch strip for the hat band.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EEA62B713F3134C9B86C84B72B60EB7', X'6EE86DAD32E155459D79B810647CA8ED', X'6EE86DAD32E155459D79B810647CA8ED', 2, NULL, NULL, 2, NULL, 0, 'Cut one 2 inch square then cut out a 1 1/4 inch square from the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E40EB0AAAB9E6F4A98B16FFBBD8A7696', X'6EE86DAD32E155459D79B810647CA8ED', X'6EE86DAD32E155459D79B810647CA8ED', 3, NULL, NULL, 2, NULL, 0, 'Cut two 1 3/4 inch long by 1/8th inch wide strips to attach to the backside of the square along the top and bottom edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA7EED6459ACA64E8111B0F45A7C7B9F', X'6EE86DAD32E155459D79B810647CA8ED', X'6EE86DAD32E155459D79B810647CA8ED', 4, NULL, NULL, 2, NULL, 0, 'Brush gold luster dust over the flat side of the square.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E459AE0C055C4E4E8D389DA7839002CA', X'E0C3A443B03C334C80FAFC617E1E0D7A', X'E0C3A443B03C334C80FAFC617E1E0D7A', 0, NULL, NULL, 2, NULL, 0, 'Arrange one row of 5 cupcakes along the bottom edge of a rectangle cake board or platter to form the hat''s brim.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6301DFC9FDABC4C91656C961576CC03', X'E0C3A443B03C334C80FAFC617E1E0D7A', X'E0C3A443B03C334C80FAFC617E1E0D7A', 1, NULL, NULL, 2, NULL, 0, 'Stack three rows of three cupcakes above the hat''s brim.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A3D296EB58FC742B33283C9A1CA35F8', X'E0C3A443B03C334C80FAFC617E1E0D7A', X'E0C3A443B03C334C80FAFC617E1E0D7A', 2, NULL, NULL, 2, NULL, 0, 'Cut rice krispie treats in half then shape them to fit in between the cupcakes, filling in the gaps.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A27A250128E1E4A88377BDB87B76713', X'E0C3A443B03C334C80FAFC617E1E0D7A', X'E0C3A443B03C334C80FAFC617E1E0D7A', 3, NULL, NULL, 2, NULL, 0, 'Fill a pastry bag fitted with an open star tip with green frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D58B9128805C2C4781D24DB9B7818DF7', X'E0C3A443B03C334C80FAFC617E1E0D7A', X'E0C3A443B03C334C80FAFC617E1E0D7A', 4, NULL, NULL, 2, NULL, 0, 'Pipe frosting stars over the cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7291ACB01A96404FB7C94DBAE6E5262E', X'E0C3A443B03C334C80FAFC617E1E0D7A', X'E0C3A443B03C334C80FAFC617E1E0D7A', 5, NULL, NULL, 2, NULL, 0, 'Frost the entire surface of the Leprechaun Hat Cupcake Cake with green frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1393EDBE21F5B544A33FF0668BF3E97B', X'E0C3A443B03C334C80FAFC617E1E0D7A', X'E0C3A443B03C334C80FAFC617E1E0D7A', 6, NULL, NULL, 2, NULL, 0, 'Set the black modeling chocolate band over the first row of 3 cupcakes just above the hat''s brim.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5868F7FCE323404AA46BADDAAA266916', X'E0C3A443B03C334C80FAFC617E1E0D7A', X'E0C3A443B03C334C80FAFC617E1E0D7A', 7, NULL, NULL, 2, NULL, 0, 'Place the buckle in the center of the hat brim.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'28683CC92CFE6A45977388084E11195B', X'E0C3A443B03C334C80FAFC617E1E0D7A', X'E0C3A443B03C334C80FAFC617E1E0D7A', 8, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D82E84F2609C154DA2645D3C3A234C5B', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 0, NULL, NULL, 2, NULL, 0, 'Run a line of butter around the inside edges of a 12 x 18 inch baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'678CC1AEF061EE4AAC5EA11537AD78E9', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 1, NULL, NULL, 2, NULL, 0, 'Set a piece of parchment or wax paper down on the pan then rub more butter over it.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63AFD5D1D1EC0D41A47DA6B4C0823B3C', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 2, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9D47E1D42194444B9C1F8B89CAFFB1F', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 3, NULL, NULL, 2, NULL, 0, 'Equally divide the marshmallows (a heaping cup of each) into 6 microwave safe bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'936B344EDECB974BB4A3C72C11750E14', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 4, NULL, NULL, 2, NULL, 0, 'Cut the butter into 1/2 tablespoon pieces and set one piece in each bowl of marshmallows.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'375ECF1149494848B9EB441381D8D524', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 5, NULL, NULL, 2, NULL, 0, 'Heat each bowl on high power for 45-75 seconds until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD6E7714B1EF384FBF075D03130CC676', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 6, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA3F322892FEC84E8572CF455A44D326', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 7, NULL, NULL, 2, NULL, 0, 'Repeat, melting all the marshmallows.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49EBA5481DA613449D76731C35CE96CE', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 8, NULL, NULL, 2, NULL, 0, 'Add purple coloring to one bowl and stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3974FF3AB5C7545954B6C2CECBB21C4', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 9, NULL, NULL, 2, NULL, 0, 'Add one of each of the remaining colors to the remaining bowls of marshmallow.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBFF6BD16E0A824AA6BAEF881820B84A', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 10, NULL, NULL, 2, NULL, 0, 'Stir 1 cup of Rice Krispies Cereal into each bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2952509B58C4EA40B75E558A1085EF9C', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 11, NULL, NULL, 2, NULL, 0, 'Scoop the purple cereal treat mixture out of the bowl and stretch it out over the long edge of the prepared pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6E9C4BF6F3780499D2F4EF1C72524A6', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 12, NULL, NULL, 2, NULL, 0, 'Flatten it into a 2 inch wide strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'593A41A4A89960459C255987958BADDF', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 13, NULL, NULL, 2, NULL, 0, 'Add a blue strip next to the purple one then add a green, then yellow, orange, and pink strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B8CFB27DFABB644B4BFC0ECFDDF4606', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 14, NULL, NULL, 2, NULL, 0, 'Press the seams together so the colored strips are well sealed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A0A6FDC053D49478019CEC339638FA9', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 15, NULL, NULL, 2, NULL, 0, 'Run a knife or plastic scraper around the edge of the pan loosening the cereal treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FFB4A66E9924741876EF8162F7A553A', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 16, NULL, NULL, 2, NULL, 0, 'Carefully flip the pan over allowing the rainbow rice krispie treat to fall out onto your work surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19A70E9FCA0BFC4492A28C27345B2266', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 17, NULL, NULL, 2, NULL, 0, 'Peel the paper off the treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCC42A88C4A8184B96BD591EB71E8E86', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 18, NULL, NULL, 2, NULL, 0, 'Begin rolling the treat along the long edge (either the purple or pink strip).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65E80ECF18B5634EB15017A1B8003C8E', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 19, NULL, NULL, 2, NULL, 0, 'Roll it, pinching together any cracks, until you have an 18 inch long log.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E4D36BBE2F9144C99BE9C007B7CEC29', X'BA24CDBD030F384096F046AC9B676A24', X'BA24CDBD030F384096F046AC9B676A24', 20, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, cut the roll into 12-18 slices and they are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0786532168ACD7468D1A7802B25ACD35', X'2FEBF633DAC5D249AE48CAC7D96338B4', X'2FEBF633DAC5D249AE48CAC7D96338B4', 0, NULL, NULL, 2, NULL, 0, 'Heat red candy melts in microwave on high power for 30 seconds then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'031207C00EE19E4497649AE96B8CAE03', X'2FEBF633DAC5D249AE48CAC7D96338B4', X'2FEBF633DAC5D249AE48CAC7D96338B4', 1, NULL, NULL, 2, NULL, 0, 'Heat another 30 seconds then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC97F5CED442C44C9394273D2A77C40B', X'2FEBF633DAC5D249AE48CAC7D96338B4', X'2FEBF633DAC5D249AE48CAC7D96338B4', 2, NULL, NULL, 2, NULL, 0, 'Heat for 20 seconds then let sit in microwave for 2 minutes before stirring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47189238ED79B947AE237D3BA7CE0755', X'2FEBF633DAC5D249AE48CAC7D96338B4', X'2FEBF633DAC5D249AE48CAC7D96338B4', 3, NULL, NULL, 2, NULL, 0, 'If not all melted, let sit for another 2 minutes then stir again.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D0DFC5BDCCD704D973002479C52BA03', X'2FEBF633DAC5D249AE48CAC7D96338B4', X'2FEBF633DAC5D249AE48CAC7D96338B4', 4, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5128079BA35A34CB069078F6B938781', X'2FEBF633DAC5D249AE48CAC7D96338B4', X'2FEBF633DAC5D249AE48CAC7D96338B4', 5, NULL, NULL, 2, NULL, 0, 'It''s best to keep the candy from getting over 95 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5E05207418D1948B67DA1E2C3978768', X'2FEBF633DAC5D249AE48CAC7D96338B4', X'2FEBF633DAC5D249AE48CAC7D96338B4', 6, NULL, NULL, 2, NULL, 0, 'If the candy got really hot, let it cool, stirring often, until just warm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F29E25DBA6692045B128F34FA8E2C308', X'2FEBF633DAC5D249AE48CAC7D96338B4', X'2FEBF633DAC5D249AE48CAC7D96338B4', 7, NULL, NULL, 2, NULL, 0, 'Add the marshmallow creme and stir just until half of it is mixed in then add the food coloring, if using, and continue to stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4E74F3108BE074791355D495D61B08C', X'2FEBF633DAC5D249AE48CAC7D96338B4', X'2FEBF633DAC5D249AE48CAC7D96338B4', 8, NULL, NULL, 2, NULL, 0, 'The mixture will become thick quickly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A6939154560CD488C21D4B5A5130AF0', X'2FEBF633DAC5D249AE48CAC7D96338B4', X'2FEBF633DAC5D249AE48CAC7D96338B4', 9, NULL, NULL, 2, NULL, 0, 'Spread the red nougat into the bottom of a silicone loaf pan or into a metal loaf pan lined with strips of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35C30A72F807A14598D4C90812591A65', X'4D52FDBF767C0B41AB1CA502B2104C2D', X'4D52FDBF767C0B41AB1CA502B2104C2D', 0, NULL, NULL, 2, NULL, 0, 'Repeat the process above layering in the orange, yellow, green, blue, indigo, and purple nougat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C6D2F67B517874C92963401C38B292C', X'4D52FDBF767C0B41AB1CA502B2104C2D', X'4D52FDBF767C0B41AB1CA502B2104C2D', 1, NULL, NULL, 2, NULL, 0, 'Set aside for 30 minutes so that the candy firms up.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77B0E578FB6BEB439AEA3C26E8675459', X'E86191030B1DE742ACC687130681B973', X'E86191030B1DE742ACC687130681B973', 0, NULL, NULL, 2, NULL, 0, 'Pour just enough chocolate to cover the top of the purple layer and spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E67DBB4A0300354B996AB615E40967C8', X'E86191030B1DE742ACC687130681B973', X'E86191030B1DE742ACC687130681B973', 1, NULL, NULL, 2, NULL, 0, 'Tap the mold on the counter a few times to remove any air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'847E585E2C8287428D3DF212147AED34', X'E86191030B1DE742ACC687130681B973', X'E86191030B1DE742ACC687130681B973', 2, NULL, NULL, 2, NULL, 0, 'If using pure chocolate refrigerate for 10-15 minutes until the chocolate hardens.If using Candy Melts freeze for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE241BB81F8BE14095382DB08248C7C0', X'E86191030B1DE742ACC687130681B973', X'E86191030B1DE742ACC687130681B973', 3, NULL, NULL, 2, NULL, 0, 'Remove and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79D1B84D80DBC84B81E0250A7CB1DF13', X'E86191030B1DE742ACC687130681B973', X'E86191030B1DE742ACC687130681B973', 4, NULL, NULL, 2, NULL, 0, 'Let the candy bar sit at room temperature for 15 minutes');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B5E74FC5C745249B13B5793C273C0A1', X'E86191030B1DE742ACC687130681B973', X'E86191030B1DE742ACC687130681B973', 5, NULL, NULL, 2, NULL, 0, 'Set the candy bar up on two glasses that fit underneath.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7132B0F21FC63419CAFB167C8C65922', X'E86191030B1DE742ACC687130681B973', X'E86191030B1DE742ACC687130681B973', 6, NULL, NULL, 2, NULL, 0, 'Pour the remaining chocolate over top of the bar allowing it to run down over the sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFFF3F3F20B9CB4D99C26E52827F442D', X'E86191030B1DE742ACC687130681B973', X'E86191030B1DE742ACC687130681B973', 7, NULL, NULL, 2, NULL, 0, 'Clean up the drips of chocolate from the bottom edge of the bar using a spatula or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0FDE9AA04AF743479DF80BA6423730C3', X'E86191030B1DE742ACC687130681B973', X'E86191030B1DE742ACC687130681B973', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle on rainbow chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BDF82C78F66B5448BBACC68033063A28', X'E86191030B1DE742ACC687130681B973', X'E86191030B1DE742ACC687130681B973', 9, NULL, NULL, 2, NULL, 0, 'Chill for 10-15 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2B3F25D78DF8E49BE314B7511838925', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 0, NULL, NULL, 2, NULL, 0, 'Pour white and orange candy melts in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39F9F2235DD04045A60570B37150A05C', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 15-30 second increments, stirring after each until melted creating a peach color.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43DD4D0FB60268409A673A1C004CAFB8', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 2, NULL, NULL, 2, NULL, 0, 'Dip Oreo in peach colored candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37453A56B519DE4388F14E014EF06494', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 3, NULL, NULL, 2, NULL, 0, 'Remove using a dipping fork and allow excess candy coating to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED67285FFF4D1C4C89129AE92BFA6ECB', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 4, NULL, NULL, 2, NULL, 0, 'Set on parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF6D3A7F40288A468D9D56AF743747C5', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 5, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF7EB52FEF337743BAF0EB63B5BFCBF9', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 6, NULL, NULL, 2, NULL, 0, 'Freeze for 5-8 minutes until candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2357CE50F8857A429BB97CC9739081F9', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 7, NULL, NULL, 2, NULL, 0, 'Pipe 32 small tear drops onto parchment paper for the hands.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A56328CBF3045488BABF0B1BE908ED5', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 8, NULL, NULL, 2, NULL, 0, 'Melt green candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'205293FCA7466544B9BF9D423270F901', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 9, NULL, NULL, 2, NULL, 0, 'Pipe or paint the candy melts onto parchment in the shape of a leprechaun hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36E2B2D14949144DBBAA1F41FCD1E756', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 10, NULL, NULL, 2, NULL, 0, 'Make 16 hats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A2883698B9CB943B8FDA4975F30DEB2', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 11, NULL, NULL, 2, NULL, 0, 'Freeze for 3-5 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88750B1C3D8C5E429A0C33670A103F6E', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 12, NULL, NULL, 2, NULL, 0, 'Use the green candy decorating marker to draw a band onto each hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FF6466B44849D47A0CDE23ADC59F360', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 13, NULL, NULL, 2, NULL, 0, 'Mix gold petal dust with just enough lemon extract to make a thin paste (a few drops.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90BFF3E89F3B5F4F83CC1CED28E72F1D', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 14, NULL, NULL, 2, NULL, 0, 'Brush it onto each hat creating a buckle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84010EEABF8E6F47918BD7F5E93C87FD', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 15, NULL, NULL, 2, NULL, 0, 'Melt orange candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEE7B3951A9560408155C626A4314CB0', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 16, NULL, NULL, 2, NULL, 0, 'Paint a 1/4 inch wide border all around each cookie creating the hair and beard.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E224BFBF226C844810B2793E10201C8', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 17, NULL, NULL, 2, NULL, 0, 'Draw eyes and a smile onto each cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B69AC9AE86E524480C5FAC43E860551', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 18, NULL, NULL, 2, NULL, 0, 'Add dots of peach candy coating at each end of the smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E50C222C87A1B649AB399B99C72417B3', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 19, NULL, NULL, 2, NULL, 0, 'Attach one hat to each cookie using candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8589DB681BE15547BACB3196ECFDFECF', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 20, NULL, NULL, 2, NULL, 0, 'Attach one Leprechaun cookies to the top of the swirl on each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6F61A21D9FB6B49B658FE35EBE461BA', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 21, NULL, NULL, 2, NULL, 0, 'Insert a pot of gold pick or attach a gold coin or shamrock candy to the front of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D253DB23F92F8542B42CDE44A67EAF8E', X'41A00E2F1594F2458046EB9BA37C2515', X'41A00E2F1594F2458046EB9BA37C2515', 22, NULL, NULL, 2, NULL, 0, 'Insert two hands into the frosting so they look like they are holding the pot of gold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4E7818F389297418810983B406FB0DB', X'9ED0BED432AADE47A33F70367B7C240B', X'9ED0BED432AADE47A33F70367B7C240B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D73A191B456424A9E063BA131A520B5', X'9ED0BED432AADE47A33F70367B7C240B', X'9ED0BED432AADE47A33F70367B7C240B', 1, NULL, NULL, 2, NULL, 0, 'Dust cutting board with flour. Unroll one tube pizza dough onto cutting board. Cut 4 large, 5  inch hearts and one or two stems out of the dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18E3051EBEA43049BFF92D6EDD3C6DA8', X'9ED0BED432AADE47A33F70367B7C240B', X'9ED0BED432AADE47A33F70367B7C240B', 2, NULL, NULL, 2, NULL, 0, 'Arrange the four hearts into a 4 leaf clover, so that the points of the hearts touch on a parchment paper lined baking sheet. Add a dough stem in between two of the hearts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA67BF4FC6047C4386D014F884220B79', X'9ED0BED432AADE47A33F70367B7C240B', X'9ED0BED432AADE47A33F70367B7C240B', 3, NULL, NULL, 2, NULL, 0, 'Whisk together the egg white and water. Brush the egg wash over the 4 leaf clover pizza dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4650B451065C074F9FE816B2EF99DCC6', X'9ED0BED432AADE47A33F70367B7C240B', X'9ED0BED432AADE47A33F70367B7C240B', 4, NULL, NULL, 2, NULL, 0, 'Spoon  cup sausage,  of the scrambled eggs(1 egg,)  cup shredded sharp cheddar cheese in the center of each heart, leaving about an inch boarder of uncovered dough. Top the stem with another dough stem.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4DF564BA64C61499129DDA6806BD3E8', X'9ED0BED432AADE47A33F70367B7C240B', X'9ED0BED432AADE47A33F70367B7C240B', 5, NULL, NULL, 2, NULL, 0, 'Dust cutting board with flour. Unroll one tube pizza dough onto cutting board. Cut 4 large, 5  inch hearts out of the dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D9BDB089E60D1498D27EE151728D7A7', X'9ED0BED432AADE47A33F70367B7C240B', X'9ED0BED432AADE47A33F70367B7C240B', 6, NULL, NULL, 2, NULL, 0, 'Cover each sausage, egg, cheese topped heart with a dough heart. Press the edges of the dough together to seal.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D4C655997903D42812CD0EEC1DBBD33', X'9ED0BED432AADE47A33F70367B7C240B', X'9ED0BED432AADE47A33F70367B7C240B', 7, NULL, NULL, 2, NULL, 0, 'Brush egg wash over the entire surface of the 4 leaf clover.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AB4063E936D3242BCB24297D28ECDB2', X'9ED0BED432AADE47A33F70367B7C240B', X'9ED0BED432AADE47A33F70367B7C240B', 8, NULL, NULL, 2, NULL, 0, 'Bake for 18-22 minutes until deep golden brown, and baked through.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F950481E1343FC4098551ED0E46518BE', X'9ED0BED432AADE47A33F70367B7C240B', X'9ED0BED432AADE47A33F70367B7C240B', 9, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC9C8B42DBC59244A3DF619565F5DC33', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 0, NULL, NULL, 2, NULL, 0, 'Mix 1 cup of the sweetened condensed milk with the semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF1CC314C948EC4BB00C78B737D2E4D3', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 1, NULL, NULL, 2, NULL, 0, 'Mix remaining sweetened condensed milk with the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDA25CF69B2895419ABE90125E691FDB', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 2, NULL, NULL, 2, NULL, 0, 'Heat white chocolate in microwave on high for 25 seconds. Let rest 1-2 minutes then stir. Heat for 15 more seconds, rest, stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10E67EE9265C054682933EAA64448ADD', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 3, NULL, NULL, 2, NULL, 0, 'If chocolate isn''t all melted heat on 50% power for 10 second increments, resting and stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CB474B02964774C9D81FB3C72CD4D9A', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 4, NULL, NULL, 2, NULL, 0, 'Stir in 4 teaspoons of the creme de menthe.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'431C59A384BF844981207270C2BCADA9', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 5, NULL, NULL, 2, NULL, 0, 'Spoon a heaping tablespoonful of the green fudge into all the cavities in two Wilton Silicone Mini Shamrock molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57724F7942472044A0B783C3984D0B00', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 6, NULL, NULL, 2, NULL, 0, 'Tap mold so that the fudge spreads into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2E309F6F488FD44B5E1911A7BAF46EC', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 7, NULL, NULL, 2, NULL, 0, 'Heat semi-sweet chocolate on high in microwave for 45 seconds. Rest 1-2 minutes then stir. Heat for 15 more seconds, rest, stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49E381A148004C44B32121150FB0B9AA', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 8, NULL, NULL, 2, NULL, 0, 'If chocolate isn''t all melted heat on 50% power for 10 second increments, resting and stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F99034F34602554D8B90B68BDEC2FC72', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 9, NULL, NULL, 2, NULL, 0, 'Stir in remaining creme de menthe.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACC6D435A7127D4D8AEED1CE509D104E', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 10, NULL, NULL, 2, NULL, 0, 'Spoon 2 tablespoons of chocolate fudge into each shamrock cavity.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E406F9ABDE02BB41B00D4474977F2343', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 11, NULL, NULL, 2, NULL, 0, 'Tap mold so that the fudge spreads into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E50FA69ACBC4D042BC8A49F63A6F4B33', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 12, NULL, NULL, 2, NULL, 0, 'Let fudge shamrocks sit at room temperature for 1 hour, then refrigerate for 1 hour, then freeze for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A22EBDC1C41FC24199B8EE943A538556', X'41EC1071170CAC4FB181190DBE3B516B', X'41EC1071170CAC4FB181190DBE3B516B', 13, NULL, NULL, 2, NULL, 0, 'Un-mold. If shamrocks don''t come out easily, freeze for a bit longer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADED35475A535E4DB3D4CC9D8F2F219A', X'16F74BA848DA514B9B4D15B75FB7DE9F', X'16F74BA848DA514B9B4D15B75FB7DE9F', 0, NULL, NULL, 2, NULL, 0, 'Melt the chocolate chips in the microwave at high power for 30-second burst of power, stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8355306D0F40534299459C9494483C39', X'16F74BA848DA514B9B4D15B75FB7DE9F', X'16F74BA848DA514B9B4D15B75FB7DE9F', 1, NULL, NULL, 2, NULL, 0, 'Stir in the almond butter until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D068406064939D4D8CFF35202C36B4D3', X'16F74BA848DA514B9B4D15B75FB7DE9F', X'16F74BA848DA514B9B4D15B75FB7DE9F', 2, NULL, NULL, 2, NULL, 0, 'Pour into the lined 8-inch baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56870528856A8544A1A5C04D659A2ABE', X'16F74BA848DA514B9B4D15B75FB7DE9F', X'16F74BA848DA514B9B4D15B75FB7DE9F', 3, NULL, NULL, 2, NULL, 0, 'Cover and refrigerate for about 2 hours until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11A8834564EE394FACA82ED86F06279F', X'16F74BA848DA514B9B4D15B75FB7DE9F', X'16F74BA848DA514B9B4D15B75FB7DE9F', 4, NULL, NULL, 2, NULL, 0, 'Remove and let the fudge sit at room temperature for about 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63EF3B5F1D6E844C8FDA3ED74FA97B0B', X'16F74BA848DA514B9B4D15B75FB7DE9F', X'16F74BA848DA514B9B4D15B75FB7DE9F', 5, NULL, NULL, 2, NULL, 0, 'Use a bunny-shaped cookie cutter to cut out fudge bunnies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7035A43BA1A47443BB6502E15D6991A8', X'16F74BA848DA514B9B4D15B75FB7DE9F', X'16F74BA848DA514B9B4D15B75FB7DE9F', 6, NULL, NULL, 2, NULL, 0, 'Remelt the chocolate fudge using 10-second bursts of power, stirring after each, then pour the remaining fudge into a smaller pan and repeat the process or pour the fudge directly into some cookie cutters and chill until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF7579CB49F17A46B587EF02B2DF3D83', X'29935396299D8140892DBFC3257856CB', X'29935396299D8140892DBFC3257856CB', 0, NULL, NULL, 2, NULL, 0, 'Either allow the frozen raspberries to thaw then press them through a fine-mesh sieve to remove the seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5AEBF226C76CDA48A1B6125CD37941A8', X'29935396299D8140892DBFC3257856CB', X'29935396299D8140892DBFC3257856CB', 1, NULL, NULL, 2, NULL, 0, 'Or heat the raspberries in a medium saucepan set over medium heat until the berries break down then press them through the sieve to remove the seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16FB64A5EC0B99468F8C62C1512F4FC6', X'29935396299D8140892DBFC3257856CB', X'29935396299D8140892DBFC3257856CB', 2, NULL, NULL, 2, NULL, 0, 'You should have about 3/4 cup of puree.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F1EF62376E97D449708D546DCB1D410', X'7E665CEA9AC9814296D968350DC29EFC', X'7E665CEA9AC9814296D968350DC29EFC', 0, NULL, NULL, 2, NULL, 0, 'Heat the raspberry puree in the saucepan set over medium heat just until bubbles begin to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3886A562ED2E3B43A191191ACC651B27', X'7E665CEA9AC9814296D968350DC29EFC', X'7E665CEA9AC9814296D968350DC29EFC', 1, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat and let it rest for one minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF7517ECDAF19E4D949F89D06485D4FD', X'7E665CEA9AC9814296D968350DC29EFC', X'7E665CEA9AC9814296D968350DC29EFC', 2, NULL, NULL, 2, NULL, 0, 'Then pour the chocolate chips into the pan and stir just to coat the chocolate in the puree.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D070F45C76A68C4F8C98A57A9949F9BB', X'7E665CEA9AC9814296D968350DC29EFC', X'7E665CEA9AC9814296D968350DC29EFC', 3, NULL, NULL, 2, NULL, 0, 'Set the pan aside for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF3B6DC1BE795A43B1E158F31A5323D0', X'7E665CEA9AC9814296D968350DC29EFC', X'7E665CEA9AC9814296D968350DC29EFC', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, whisk or beat the cream until it comes to stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09E893000BDBEC439DFE5B80EA73D40D', X'7E665CEA9AC9814296D968350DC29EFC', X'7E665CEA9AC9814296D968350DC29EFC', 5, NULL, NULL, 2, NULL, 0, 'Stir the chocolate and raspberry puree until smooth then pour it into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FB46550548D254E8FC8424A8D02CF3E', X'7E665CEA9AC9814296D968350DC29EFC', X'7E665CEA9AC9814296D968350DC29EFC', 6, NULL, NULL, 2, NULL, 0, 'Stir 1/3 of the whipped cream into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2D9B38F6674C44B9F59D5E5A07E014C', X'7E665CEA9AC9814296D968350DC29EFC', X'7E665CEA9AC9814296D968350DC29EFC', 7, NULL, NULL, 2, NULL, 0, 'Then fold in the remaining cream until all the white streaks are gone.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AA797D3510AFA499A617AF8CC658FF9', X'7E665CEA9AC9814296D968350DC29EFC', X'7E665CEA9AC9814296D968350DC29EFC', 8, NULL, NULL, 2, NULL, 0, 'Chill until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10D046A183430648B4F8FD0AB97B9141', X'7072B3C7A995BA4F8166C2DA7522B712', X'7072B3C7A995BA4F8166C2DA7522B712', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCA5AE240B802B4C8DB7E6CD635D3F8C', X'DF5144F3FE8A4F41893E022F83FC0881', X'DF5144F3FE8A4F41893E022F83FC0881', 0, NULL, NULL, 2, NULL, 0, 'Cut tube of bread into 12 equal slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E1C1031B7D3E745A400741BF828C64F', X'DF5144F3FE8A4F41893E022F83FC0881', X'DF5144F3FE8A4F41893E022F83FC0881', 1, NULL, NULL, 2, NULL, 0, 'Arrange the slices around the inside perimeter of the pie dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF13AF0F0CCD394B82D3CFE4C298ED62', X'B3233CEF3CB4574488CDFF6E36FC3637', X'B3233CEF3CB4574488CDFF6E36FC3637', 0, NULL, NULL, 2, NULL, 0, 'Combine chicken dip ingredients in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5945A5A3D766E4B8D123319D4EE91AF', X'B3233CEF3CB4574488CDFF6E36FC3637', X'B3233CEF3CB4574488CDFF6E36FC3637', 1, NULL, NULL, 2, NULL, 0, 'Using an electric mixer beat on medium speed until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92C714125CA49842BD267B4F65A5013A', X'B3233CEF3CB4574488CDFF6E36FC3637', X'B3233CEF3CB4574488CDFF6E36FC3637', 2, NULL, NULL, 2, NULL, 0, 'Spoon the chicken dip in the center of the pie dish and spread it into an even layer so that the dip just touches the inner part of the bread ring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3369CA5B0D140C43A170DD5ACB217B71', X'B3233CEF3CB4574488CDFF6E36FC3637', X'B3233CEF3CB4574488CDFF6E36FC3637', 3, NULL, NULL, 2, NULL, 0, 'Bake for 25-30 minutes until the bread is golden brown and baked through.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'136D3BFCC614E244A5058A165C684F13', X'94FD8F5E87BABD4389D1B72A567257A1', X'94FD8F5E87BABD4389D1B72A567257A1', 0, NULL, NULL, 2, NULL, 0, 'While the dip is in the oven, color the 2 teaspoons of cream cheese using pink food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AE6815E04EEA74BAB9E25A06BD894D4', X'94FD8F5E87BABD4389D1B72A567257A1', X'94FD8F5E87BABD4389D1B72A567257A1', 1, NULL, NULL, 2, NULL, 0, 'Spread about 1/2 teaspoon of the pink cream cheese in the center of each Naan Dipper or bread ear to create the look of the furry inner ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9950429C40B89F4F91B62B5D573DD3BD', X'94FD8F5E87BABD4389D1B72A567257A1', X'94FD8F5E87BABD4389D1B72A567257A1', 2, NULL, NULL, 2, NULL, 0, 'Shape the remaining teaspoon of pink cream cheese into a nose. Pop it in the freezer until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C63CDCFBC2DBA249BCEABA7B087F5A34', X'94FD8F5E87BABD4389D1B72A567257A1', X'94FD8F5E87BABD4389D1B72A567257A1', 3, NULL, NULL, 2, NULL, 0, 'Cut thin crescent moon shaped slices from the black olive to create the eyes and mouth of the lamb.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EBA05F6B78F5B489F263EB372DF5A95', X'94FD8F5E87BABD4389D1B72A567257A1', X'94FD8F5E87BABD4389D1B72A567257A1', 4, NULL, NULL, 2, NULL, 0, 'Pull the hot chicken dip from the oven and add two ears, two eyes, the pink nose, and two olive slices for the mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'425D5F1F100A0E489DCB51AD54F06D4B', X'94FD8F5E87BABD4389D1B72A567257A1', X'94FD8F5E87BABD4389D1B72A567257A1', 5, NULL, NULL, 2, NULL, 0, 'Serve the dip hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37F08C14935DE94EB69A2365F2661137', X'B43278B7DC50F847959CA365C8003B4A', X'B43278B7DC50F847959CA365C8003B4A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB886329F8AB1D4EB9AEF30224821972', X'B43278B7DC50F847959CA365C8003B4A', X'B43278B7DC50F847959CA365C8003B4A', 1, NULL, NULL, 2, NULL, 0, 'Grease and flour a 9x13-inch baking pan then line the bottom with parchment paper for ease of removal.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8E6473940B29A46861A96C0E17F5BB7', X'B43278B7DC50F847959CA365C8003B4A', X'B43278B7DC50F847959CA365C8003B4A', 2, NULL, NULL, 2, NULL, 0, 'Combine all the cake ingredients in a mixing bowl and beat, using an electric mixer, on low speed for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3C7E01AB69B984AAE2A80183CA3C11B', X'B43278B7DC50F847959CA365C8003B4A', X'B43278B7DC50F847959CA365C8003B4A', 3, NULL, NULL, 2, NULL, 0, 'Increase the speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCB2CFB002E8944AAF220816940BA622', X'B43278B7DC50F847959CA365C8003B4A', X'B43278B7DC50F847959CA365C8003B4A', 4, NULL, NULL, 2, NULL, 0, 'Pour batter into the prepared pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56C4AF036CB80345841117A25E8D79BB', X'B43278B7DC50F847959CA365C8003B4A', X'B43278B7DC50F847959CA365C8003B4A', 5, NULL, NULL, 2, NULL, 0, 'Bake for 26-34 minutes until a toothpick inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F18152766E6BEC44AEB44BF213E3B6C2', X'B43278B7DC50F847959CA365C8003B4A', X'B43278B7DC50F847959CA365C8003B4A', 6, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A1DFA1988B78049B4DE5F4A850EC080', X'B43278B7DC50F847959CA365C8003B4A', X'B43278B7DC50F847959CA365C8003B4A', 7, NULL, NULL, 2, NULL, 0, 'Carefully flip the cake upside down and unmold it onto a serving platter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DBE09DCD0419E4D9ED23F0DEC659F6D', X'B43278B7DC50F847959CA365C8003B4A', X'B43278B7DC50F847959CA365C8003B4A', 8, NULL, NULL, 2, NULL, 0, 'Cut out the printable Easter Bunny template.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2F627DCDCE942459BE2105900CD60B9', X'B43278B7DC50F847959CA365C8003B4A', X'B43278B7DC50F847959CA365C8003B4A', 9, NULL, NULL, 2, NULL, 0, 'Place it on top of the cake and use a sharp knife to gently cut around the template cutting just enough to see the lines but not enough to cut through the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDB44735F5E97045835C52AD12C4C0E7', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 0, NULL, NULL, 2, NULL, 0, 'Beat the butter until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'385660EB482DF146A5FCFC00095664B4', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 1, NULL, NULL, 2, NULL, 0, 'Add the shortening and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'935DE9D6F6833A44ABBADE641C9FCFFF', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 2, NULL, NULL, 2, NULL, 0, 'Sift the powdered sugar then add about a cup at a time into the butter mixture and blend until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D39802B8AE8D8409D997521806DA039', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 3, NULL, NULL, 2, NULL, 0, 'Beat in the milk as needed to create a medium consistency frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D378F136484AA4AA225D01B2752289E', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 4, NULL, NULL, 2, NULL, 0, 'Add the vanilla and beat the frosting until fluffy for 3-5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C23C659007CBE840801263EFD88067EC', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 5, NULL, NULL, 2, NULL, 0, 'Color 1 cup of the frosting using the pink food coloring and spoon it into a pastry bag fitted with a #4 round pastry tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'515A1B9074510F49882CAB0B76B46177', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 6, NULL, NULL, 2, NULL, 0, 'Using the cut lines on the cake as a char(36)e, pipe the pink frosting around the outer edge of the bunny then pipe on the paws, inner ears, paws, nose, and mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B95723831C5CB142B1A09CE5934763D9', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 7, NULL, NULL, 2, NULL, 0, 'Fill a pastry bag fitted with a star tip with white frosting and pipe all over the bunny inside the pink border.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67506125FA11AE47AFA9E161027F9413', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 8, NULL, NULL, 2, NULL, 0, 'Fill a pastry bag fitted with a star tip with light blue icing and pipe stars all around the bunny just down to the bottom edge of the bunny design.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6EFFFA6F5F40614CBC97618BCF307F94', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 9, NULL, NULL, 2, NULL, 0, 'Pipe blue stars around the sides of the cake to that bottom edge too.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'472FA0B6BC94F8479BEDC873186039CB', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 10, NULL, NULL, 2, NULL, 0, 'Blend the remaining white frosting with the cocoa powder to create chocolate frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1938B56F40FED43A76ED9C2353BA892', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 11, NULL, NULL, 2, NULL, 0, 'If needed, add a little water or milk (up to a teaspoon) to thin down the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E6F2BE4D661454E9BD3CFC0440BE3B7', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 12, NULL, NULL, 2, NULL, 0, 'Fill a pastry bag fitted with a star tip with the chocolate frosting then pipe stars all over the bottom edge of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'528C80E54712CA4E83C7CDEB31D23D93', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 13, NULL, NULL, 2, NULL, 0, 'Add the two black Sixlet or chocolate chip eyes or use the chocolate frosting to pipe on the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D93C2584D8C749448D62E4B415D595C0', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 14, NULL, NULL, 2, NULL, 0, 'Use the pink frosting to pipe on whiskers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97531BA262E6FA428CD9DE596893EC63', X'E882A81647DDC7459C81238D8A4F6E1D', X'E882A81647DDC7459C81238D8A4F6E1D', 15, NULL, NULL, 2, NULL, 0, 'Your Easter cake is now ready to serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E0F698026542041BBFFC40A99E21C4B', X'461E4E7BF0BF824FB3B5AE37F5815574', X'461E4E7BF0BF824FB3B5AE37F5815574', 0, NULL, NULL, 2, NULL, 0, 'Unwrap Reese''s Cups and Tootsie Rolls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93FD07487842E04DAEA08831AB274DB2', X'461E4E7BF0BF824FB3B5AE37F5815574', X'461E4E7BF0BF824FB3B5AE37F5815574', 1, NULL, NULL, 2, NULL, 0, 'Use melted confectionery coating to attach two candy eyes and one pink Sixlet on each Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04B05DF142233544B0C3C8841EEAFE84', X'461E4E7BF0BF824FB3B5AE37F5815574', X'461E4E7BF0BF824FB3B5AE37F5815574', 2, NULL, NULL, 2, NULL, 0, 'Draw whiskers and a mouth on each Reese''s Cup using a food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA2E2FA58875AF40809D14EC443EB4D2', X'461E4E7BF0BF824FB3B5AE37F5815574', X'461E4E7BF0BF824FB3B5AE37F5815574', 3, NULL, NULL, 2, NULL, 0, 'Flatten the Tootsie Roll Midgees.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB58440F156B5040A7387B4ABABFF84A', X'461E4E7BF0BF824FB3B5AE37F5815574', X'461E4E7BF0BF824FB3B5AE37F5815574', 4, NULL, NULL, 2, NULL, 0, 'Cut each chocolate Tootsie Roll into two bunny ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2116961BFD68A44A9BAC46389A523AC', X'461E4E7BF0BF824FB3B5AE37F5815574', X'461E4E7BF0BF824FB3B5AE37F5815574', 5, NULL, NULL, 2, NULL, 0, 'Cut each Vanilla Tootsie Roll into the smaller inside fluff of the bunny ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E26B196EC59969499A4472A069F1857D', X'461E4E7BF0BF824FB3B5AE37F5815574', X'461E4E7BF0BF824FB3B5AE37F5815574', 6, NULL, NULL, 2, NULL, 0, 'Brush the backside of each Vanilla Tootsie Roll with some water and press them onto a chocolate ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F8ED0385D3B89489ACAE279348E9AEC', X'461E4E7BF0BF824FB3B5AE37F5815574', X'461E4E7BF0BF824FB3B5AE37F5815574', 7, NULL, NULL, 2, NULL, 0, 'Cut the bottom tips off the Tootsie Roll ears so that they sit flat on top of the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BD3327CA7CDC247AE196A45E49242D9', X'461E4E7BF0BF824FB3B5AE37F5815574', X'461E4E7BF0BF824FB3B5AE37F5815574', 8, NULL, NULL, 2, NULL, 0, 'Use the melted candy coating to attach the ears to the Reese''s Cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A040DEC53E5A7548BA8D4A8DFAF13DC0', X'461E4E7BF0BF824FB3B5AE37F5815574', X'461E4E7BF0BF824FB3B5AE37F5815574', 9, NULL, NULL, 2, NULL, 0, 'Set one bunny head on each of the frosted cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CFCFA91A31E1F4E83D48D2CC631FEF1', X'461E4E7BF0BF824FB3B5AE37F5815574', X'461E4E7BF0BF824FB3B5AE37F5815574', 10, NULL, NULL, 2, NULL, 0, 'Add two Whoppers and one gummy carrot so each bunny looks like it has paws holding the carrot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22D794D52D550D4685F70CE10AFE6D37', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'918DFD4B3A288B4E8BA6455C58E7D305', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 1, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy. Stir in eggs, vanilla bean paste, and almond extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A4B9EF56714AC47B2CB05A9E16EA83F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 2, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients and stir just until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04E3E33997239D4EA5746338F2B0C8EB', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 3, NULL, NULL, 2, NULL, 0, 'Divide the dough into 4 equal parts and color pink, violet, blue, and yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF1970BDB1ADC84D96BB1D8F440C4D4A', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 4, NULL, NULL, 2, NULL, 0, 'Lightly dust your work surface, roll out the dough to between 1/8th of an inch and 1/16th of an inch thickness. Cut out 4 hat brims using a round scalloped cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF5B89B1010DD242A4C602817E9CD49F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 5, NULL, NULL, 2, NULL, 0, 'Place on a parchment paper lined baking sheet and refrigerate for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3548E549D26324EAD2D069A79FF1138', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E5B68B52A42BA4BB3EDE1D7BE11ABCC', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 7, NULL, NULL, 2, NULL, 0, 'Bake these cookies for 12-14 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'944F92A7F73A2B46899033FE5222A156', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 8, NULL, NULL, 2, NULL, 0, 'Re-roll each colored cookie dough out to a 1/16 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CA5AB64816581479DB8F55D214A76D5', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 9, NULL, NULL, 2, NULL, 0, 'Cut four circles of each color using 2 1/2 inch (or smaller) round cutters, so that when you drape the dough over your cake ball pan, the dough drapes over but doesnt touch the base of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6C32A80EF62CA408DFB1442852E6E0E', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 10, NULL, NULL, 2, NULL, 0, 'Drape circles of dough over each mound on your pan and smooth with your fingers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'378410E4BA94AA439EE34068DCAFB094', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 11, NULL, NULL, 2, NULL, 0, 'Place the pan in the freezer for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C3BFA136C8A8B4A93E41FF752943FDB', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 12, NULL, NULL, 2, NULL, 0, 'Bake the cookies on the bottom rack of your oven for 8-11 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BBD79B3C3489949BAD4D11B86AB20DA', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', X'55D043AF4D8B7C4BB6D32A3DB8193C5F', 13, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6896FCDC01E6542BBA4C47674383B37', X'A1898CB806602E4B8C21D4ABDDD8435C', X'A1898CB806602E4B8C21D4ABDDD8435C', 0, NULL, NULL, 2, NULL, 0, 'To assemble your hat cookies, fill the rounded cookies with candies, then pipe a thin line of royal icing around the top edge of the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E4ABAFDBC236440A662FC5A6E713348', X'A1898CB806602E4B8C21D4ABDDD8435C', X'A1898CB806602E4B8C21D4ABDDD8435C', 1, NULL, NULL, 2, NULL, 0, 'Turn one of the scalloped cookies upside down, set it on top of the iced cookie, and press them together to seal. Turn the cookie right side up and allow to dry for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91D7BE492207224AA325C583C898CF2F', X'A1898CB806602E4B8C21D4ABDDD8435C', X'A1898CB806602E4B8C21D4ABDDD8435C', 2, NULL, NULL, 2, NULL, 0, 'To decorate your hat cookies, roll out some modeling chocolate or fondant and cut into 1/8th inch wide by 8 inch long strips and attach to the hats using royal icing, cutting off any excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F61FFA254FB8494C8CB40E62CC25B7CA', X'A1898CB806602E4B8C21D4ABDDD8435C', X'A1898CB806602E4B8C21D4ABDDD8435C', 3, NULL, NULL, 2, NULL, 0, 'Color small pieces of white modeling chocolate or fondant to match your hats. Color some green as well.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55EC1CB710F43B4084544A6BDBADD8F7', X'A1898CB806602E4B8C21D4ABDDD8435C', X'A1898CB806602E4B8C21D4ABDDD8435C', 4, NULL, NULL, 2, NULL, 0, 'Roll out the modeling chocolate and cut out flowers using a small daisy plunger cutter. Cut the center of the flowers out using a small round plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'124817CA2D87BA4FB012A4DDCAC240D8', X'A1898CB806602E4B8C21D4ABDDD8435C', X'A1898CB806602E4B8C21D4ABDDD8435C', 5, NULL, NULL, 2, NULL, 0, 'Attach the centers to the daisies using a small dab of water.Note: I used Wiltons new Water Brush, which I love. I dont have to mess around with bowls of water, which Ive been known to spill all over my modeling chocolate. The water is contained in the brush. It was perfect for attaching these tiny pieces together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D90CBAF9EF2834FAC9FA3FDB35F6285', X'A1898CB806602E4B8C21D4ABDDD8435C', X'A1898CB806602E4B8C21D4ABDDD8435C', 6, NULL, NULL, 2, NULL, 0, 'Once all of your cookies are assembled and decorated they are ready to dress up your dessert table.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8340E828249E44AA65CF2D4E31D0CE2', X'D23CD99E14350244A2C962F11A4FD4F3', X'D23CD99E14350244A2C962F11A4FD4F3', 0, NULL, NULL, 2, NULL, 0, 'Melt and temper pure milk chocolate or melt the candy melts. (See tutorial for instructions).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EA00948858FC54BA83F08EF9E1EC9CA', X'D23CD99E14350244A2C962F11A4FD4F3', X'D23CD99E14350244A2C962F11A4FD4F3', 1, NULL, NULL, 2, NULL, 0, 'Dip one vanilla wafer in the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47D3B7C40449DF42B0B934A98BCB538D', X'D23CD99E14350244A2C962F11A4FD4F3', X'D23CD99E14350244A2C962F11A4FD4F3', 2, NULL, NULL, 2, NULL, 0, 'Remove using a fork and allow excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0277B153DE5EE648AD2E42B92FDA420C', X'D23CD99E14350244A2C962F11A4FD4F3', X'D23CD99E14350244A2C962F11A4FD4F3', 3, NULL, NULL, 2, NULL, 0, 'Set chocolate dipped cookie on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A6C11BD7CBAEB4ABB91756FA9530633', X'D23CD99E14350244A2C962F11A4FD4F3', X'D23CD99E14350244A2C962F11A4FD4F3', 4, NULL, NULL, 2, NULL, 0, 'Immediately pipe a round head and two pointed ears next to the chocolate dipped cookie then pipe a long triangle shaped pick opposite the head.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E86A5FAC0BDC5A40872232FB4F3C18AC', X'D23CD99E14350244A2C962F11A4FD4F3', X'D23CD99E14350244A2C962F11A4FD4F3', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate, if using pure chocolate, and freeze, if using candy melts, for about 5 minutes just until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7512210195B71845B0203BC97A06C6F3', X'D23CD99E14350244A2C962F11A4FD4F3', X'D23CD99E14350244A2C962F11A4FD4F3', 6, NULL, NULL, 2, NULL, 0, 'Repeat creating 21 bunnies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'820ACA190CB8D44FB755F262CE73DE38', X'D23CD99E14350244A2C962F11A4FD4F3', X'D23CD99E14350244A2C962F11A4FD4F3', 7, NULL, NULL, 2, NULL, 0, 'Pipe a small dab of chocolate on the bottom edge of the bunny, just above the chocolate pick, and attach one white Sixlet candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E0F27DCCE238349911A214CA54A2273', X'D23CD99E14350244A2C962F11A4FD4F3', X'D23CD99E14350244A2C962F11A4FD4F3', 8, NULL, NULL, 2, NULL, 0, 'Optionally, roll a mini marshmallow in the palms of your hand until it looks like a fluffy bunny tail. Attach that to the bunny instead of the white Sixlet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA11059A7F7BC344942BFE41ACDA0CDE', X'84C7A61276E942408E960579064701D1', X'84C7A61276E942408E960579064701D1', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EFE23371EED514C9928CF427B4C013D', X'84C7A61276E942408E960579064701D1', X'84C7A61276E942408E960579064701D1', 1, NULL, NULL, 2, NULL, 0, 'Line 21 muffin tin cavities with paper cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA8F7C3E84142D49B6E0559EB40DE1A1', X'84C7A61276E942408E960579064701D1', X'84C7A61276E942408E960579064701D1', 2, NULL, NULL, 2, NULL, 0, 'Combine the white cake mix, vanilla pudding mix, sour cream, water, oil, and egg whites in a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D327AAC5E9C1F34384F48A5FDED22A42', X'84C7A61276E942408E960579064701D1', X'84C7A61276E942408E960579064701D1', 3, NULL, NULL, 2, NULL, 0, 'Beat on low speed for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'613300799AC2C94A8602A9674CE185BB', X'84C7A61276E942408E960579064701D1', X'84C7A61276E942408E960579064701D1', 4, NULL, NULL, 2, NULL, 0, 'Beat on medium-high speed for another two minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3BE617DDF54EAE44B4D79660F1404F3A', X'84C7A61276E942408E960579064701D1', X'84C7A61276E942408E960579064701D1', 5, NULL, NULL, 2, NULL, 0, 'Equally divide the cake batter among 21 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B652DA67E16ABB45A47FA60B0DE86E8F', X'84C7A61276E942408E960579064701D1', X'84C7A61276E942408E960579064701D1', 6, NULL, NULL, 2, NULL, 0, 'Bake for 18-24 minutes then remove and cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'588904C06FE0C14F897932E0E86803C4', X'C0B7EAB23B4DA143BB4261DA1CF13C41', X'C0B7EAB23B4DA143BB4261DA1CF13C41', 0, NULL, NULL, 2, NULL, 0, 'Beat the shortening and butter until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04EA5D42BE1C374F883D6472057BA923', X'C0B7EAB23B4DA143BB4261DA1CF13C41', X'C0B7EAB23B4DA143BB4261DA1CF13C41', 1, NULL, NULL, 2, NULL, 0, 'Add the powdered sugar and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C6BDB7766B6FF48972C1C45D6F09501', X'C0B7EAB23B4DA143BB4261DA1CF13C41', X'C0B7EAB23B4DA143BB4261DA1CF13C41', 2, NULL, NULL, 2, NULL, 0, 'Pour in 3 tablespoons of the milk and green food coloring and beat until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B888839EEE53D7448A04379C936C2C92', X'C0B7EAB23B4DA143BB4261DA1CF13C41', X'C0B7EAB23B4DA143BB4261DA1CF13C41', 3, NULL, NULL, 2, NULL, 0, 'If needed, add more milk, a teaspoon at a time, until the frosting is of piping consistency.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31ED6163F78A1743A9191D0C4C282FDE', X'C0B7EAB23B4DA143BB4261DA1CF13C41', X'C0B7EAB23B4DA143BB4261DA1CF13C41', 4, NULL, NULL, 2, NULL, 0, 'Spoon into a pastry bag that has a multi-hole grass tip attached.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'518A34850349CC48A72EB8C3FB134DA0', X'6957D53D6BCCE641AC712C6577511AA4', X'6957D53D6BCCE641AC712C6577511AA4', 0, NULL, NULL, 2, NULL, 0, 'Pipe green grass over top of each of the cooled cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92189355BB5F45469E21A0821B7C393F', X'6957D53D6BCCE641AC712C6577511AA4', X'6957D53D6BCCE641AC712C6577511AA4', 1, NULL, NULL, 2, NULL, 0, 'Insert a chocolate bunny in the center of each frosted cupcake before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'085D6D474B99624C9D93317F6D0FC752', X'2732CAC8DEF6BF449BBA72AF2B66E491', X'2732CAC8DEF6BF449BBA72AF2B66E491', 0, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese using an electric mixer or food processor until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41BF23827C5A734FBBB3982C335A471D', X'2732CAC8DEF6BF449BBA72AF2B66E491', X'2732CAC8DEF6BF449BBA72AF2B66E491', 1, NULL, NULL, 2, NULL, 0, 'Scoop out about 2 1/2 tablespoons of the cream cheese and place it in a disposable pastry bag or a sandwich size zip top bag and set aside until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AF17D18409CF64285225A0BCE1B807D', X'2732CAC8DEF6BF449BBA72AF2B66E491', X'2732CAC8DEF6BF449BBA72AF2B66E491', 2, NULL, NULL, 2, NULL, 0, 'To the remaining cream cheese, add the Worcestershire sauce, seasoned salt, garlic powder, and 1 cup of the shredded white cheddar cheese and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BC71DACDDB04A4DA69904D318CDC41E', X'2732CAC8DEF6BF449BBA72AF2B66E491', X'2732CAC8DEF6BF449BBA72AF2B66E491', 3, NULL, NULL, 2, NULL, 0, 'Toss in the finely chopped bacon and stir well.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B27BA5238F5924295C2F6E3249E9A5F', X'2732CAC8DEF6BF449BBA72AF2B66E491', X'2732CAC8DEF6BF449BBA72AF2B66E491', 4, NULL, NULL, 2, NULL, 0, 'Scoop out 18 level tablespoons of the cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3D64FFC6C68A4499496A0027B1A3428', X'2732CAC8DEF6BF449BBA72AF2B66E491', X'2732CAC8DEF6BF449BBA72AF2B66E491', 5, NULL, NULL, 2, NULL, 0, 'Drop each of the scoops onto a pile of the remaining shredded cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB0C980C4407434DBE2E1EF25D9DE85F', X'2732CAC8DEF6BF449BBA72AF2B66E491', X'2732CAC8DEF6BF449BBA72AF2B66E491', 6, NULL, NULL, 2, NULL, 0, 'Roll into a ball coated in the cheese then set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A2DA6909290E742B9A5A15E8BDBB5E1', X'2732CAC8DEF6BF449BBA72AF2B66E491', X'2732CAC8DEF6BF449BBA72AF2B66E491', 7, NULL, NULL, 2, NULL, 0, 'Use the reserved cream cheese to pipe a small round tail onto the top of each mini cheese ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3837920376F7EC4691601C965A8521EE', X'2732CAC8DEF6BF449BBA72AF2B66E491', X'2732CAC8DEF6BF449BBA72AF2B66E491', 8, NULL, NULL, 2, NULL, 0, 'Cut 36 small (about 3/4 inch long) ovals out of the white cheese slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'440F0544C968A34FA9838F9BA295733F', X'2732CAC8DEF6BF449BBA72AF2B66E491', X'2732CAC8DEF6BF449BBA72AF2B66E491', 9, NULL, NULL, 2, NULL, 0, 'Use a pink food coloring marker to draw four toes and one pad onto each of the ovals creating 36 bunny feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5410CFCD4766B4A9D6FABF2D3F9532B', X'2732CAC8DEF6BF449BBA72AF2B66E491', X'2732CAC8DEF6BF449BBA72AF2B66E491', 10, NULL, NULL, 2, NULL, 0, 'Use a dab of cream cheese to attach two bunny feet to each of the mini cheese balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'096795D81640724D91DFA43FA2434027', X'2732CAC8DEF6BF449BBA72AF2B66E491', X'2732CAC8DEF6BF449BBA72AF2B66E491', 11, NULL, NULL, 2, NULL, 0, 'If desired, use some cream cheese to attach the Mini Cheese Ball Bunnies to round crackers just before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22B77D389BE7F9498DA6C813D26A3827', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D21B9FF6F7B3104A93672A1E5F37A7CD', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 1, NULL, NULL, 2, NULL, 0, 'Bring a pot of water to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F10B6CAE86258947B27B82C6F38204DF', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 2, NULL, NULL, 2, NULL, 0, 'Set 2 half sphere silicone molds on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E396366CAE014A42AA5199FDAC82A49D', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 3, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until light and fluffy then add the sugar and blend to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A418BB367FE8E44F8EDCFC8C24B57DC8', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 4, NULL, NULL, 2, NULL, 0, 'Add eggs and vanilla then stir in sour cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A33F85D8A1E18748A618171FBADDD65C', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 5, NULL, NULL, 2, NULL, 0, 'Spoon into 9 half sphere silicone mold cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F06EDAAD3F854F41BBF27D834E90639B', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 6, NULL, NULL, 2, NULL, 0, 'Set the pan holding the molds into the oven and pour enough boiling water into the pan so that it goes halfway up the sides of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A96ADA1ABDFA84B8FF51FC419680EAD', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 7, NULL, NULL, 2, NULL, 0, 'Bake for 12-14 minutes until the top looks set but the center still jiggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9F966D66195594399F02A825BEC1B11', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 8, NULL, NULL, 2, NULL, 0, 'Remove from the oven and lift the molds out of the water bath.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B51BE3E953E1548AAC6766B7412E3E7', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 9, NULL, NULL, 2, NULL, 0, 'Let the cheesecakes cool for about 45 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB0C7D26AADCF04896D0F5BCEA291573', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 10, NULL, NULL, 2, NULL, 0, 'Freeze the cheesecakes for at least 3 hours until frozen firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B6EBE4969F678489A5C3C641C5BC035', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 11, NULL, NULL, 2, NULL, 0, 'Stir together the OREO Cookie crumbs and butter then equally divide the crumbs among 9 small dessert plates.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF7D23A8D1E92D43A96A0C70CDFD9041', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 12, NULL, NULL, 2, NULL, 0, 'Before serving, pop the frozen cheesecakes out of the mold and set on in the center of each plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E8204ED4A5778C4880A425E05974B495', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 13, NULL, NULL, 2, NULL, 0, 'Let the cheesecakes thaw for about 20-30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83B774CE0F3B6948AC3DAAF37A8378B4', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 14, NULL, NULL, 2, NULL, 0, 'Meanwhile cut the marshmallows into 3 equal size slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'612F6A3CABD93A4B9DC29C703F837F9F', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 15, NULL, NULL, 2, NULL, 0, 'Roll the middle marshmallow piece into a ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'695489F5C710C6438AEE25B548889C12', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 16, NULL, NULL, 2, NULL, 0, 'Draw three toes and one pad onto each of the other 18 marshmallow slices using a pink food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A25F2F27C34C554AAD3EB5E2081C9C3F', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 17, NULL, NULL, 2, NULL, 0, 'Set two marshmallow feet along the bottom edge of each cheesecake then add one marshmallow tail on top of the cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6840594D4EA9DE489865CA9AA9653593', X'19472C8A5D2C424F837BDD6866C10582', X'19472C8A5D2C424F837BDD6866C10582', 18, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F8272C1D4E14449917EDF6F391FEEC3', X'B192179836E1BF43B6388784B998E0FB', X'B192179836E1BF43B6388784B998E0FB', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87DAE30DE45A324CB4C574211AA87389', X'B192179836E1BF43B6388784B998E0FB', X'B192179836E1BF43B6388784B998E0FB', 1, NULL, NULL, 2, NULL, 0, 'Pour enough water to come halfway up the sides of an egg silicone mold into a large roasting pan and set it in the oven and allow it to heat up as the oven preheats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'326F06C6753C8643B3B3B21CB529C6CD', X'FDF504011F4B3646BF79C2157056CF41', X'FDF504011F4B3646BF79C2157056CF41', 0, NULL, NULL, 2, NULL, 0, 'Beat cream cheese using an electric mixer until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65B4688C2E95BB449F93DDD98B9D03BA', X'FDF504011F4B3646BF79C2157056CF41', X'FDF504011F4B3646BF79C2157056CF41', 1, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5050C32D6713B54C8B421FA77412CA47', X'FDF504011F4B3646BF79C2157056CF41', X'FDF504011F4B3646BF79C2157056CF41', 2, NULL, NULL, 2, NULL, 0, 'Pour in the whole egg and egg yolk along with the vanilla and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C24A659BFBA8A94FA14D79FC256F94AF', X'FDF504011F4B3646BF79C2157056CF41', X'FDF504011F4B3646BF79C2157056CF41', 3, NULL, NULL, 2, NULL, 0, 'Add the sour cream and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD4ECD7EBF90EB41BBEF76ECBE4AA683', X'FDF504011F4B3646BF79C2157056CF41', X'FDF504011F4B3646BF79C2157056CF41', 4, NULL, NULL, 2, NULL, 0, 'Pour into 5 egg shaped cavities in a silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E3E2DADB9B5D14AA35A86B4666F979A', X'FDF504011F4B3646BF79C2157056CF41', X'FDF504011F4B3646BF79C2157056CF41', 5, NULL, NULL, 2, NULL, 0, 'Set into the water bath in the roasting pan and bake for 18-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0002DE7076E1E0438DB226FD1A006E06', X'FDF504011F4B3646BF79C2157056CF41', X'FDF504011F4B3646BF79C2157056CF41', 6, NULL, NULL, 2, NULL, 0, 'When set the cheesecakes should still jiggle but not look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19C0341E6E915A4E891F3D84C42F3240', X'FDF504011F4B3646BF79C2157056CF41', X'FDF504011F4B3646BF79C2157056CF41', 7, NULL, NULL, 2, NULL, 0, 'Remove the cheesecakes from the water bath and set on a cooling rack to cool for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B63947B8D3035842A690E72B77CCC22C', X'FDF504011F4B3646BF79C2157056CF41', X'FDF504011F4B3646BF79C2157056CF41', 8, NULL, NULL, 2, NULL, 0, 'Freeze the cheesecakes for at least 3 hours until firmly set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24F19C58DB507A4BADB4E1E6D70CA41C', X'5151C3664C46F14E90D583A31A83962D', X'5151C3664C46F14E90D583A31A83962D', 0, NULL, NULL, 2, NULL, 0, 'Place the graham crackers and butter in a food processor and pulse until you have fine crumbs. Alternatively, crush the graham crackers and stir in the butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9853F814470F84BBC8188CFC94DC1EB', X'5151C3664C46F14E90D583A31A83962D', X'5151C3664C46F14E90D583A31A83962D', 1, NULL, NULL, 2, NULL, 0, 'Equally divide the crumbs among 5 small serving plates.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0D026E29234BC4884D316F8340F23DC', X'5151C3664C46F14E90D583A31A83962D', X'5151C3664C46F14E90D583A31A83962D', 2, NULL, NULL, 2, NULL, 0, 'Remove the cheesecake eggs and push them out of the mold, placing one on each of the plates.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADB83BD35388C8449B8E738178F93A9C', X'5151C3664C46F14E90D583A31A83962D', X'5151C3664C46F14E90D583A31A83962D', 3, NULL, NULL, 2, NULL, 0, 'Allow the cheesecake to thaw for about 45 minutes to an hour before adding the legs/feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87DD674670862142842C4C3303F26E7C', X'E2C6ED273DF2B446A19E079BA8879CFB', X'E2C6ED273DF2B446A19E079BA8879CFB', 0, NULL, NULL, 2, NULL, 0, 'Pour melted orange candy melts into a squeeze bottle or zip top bag (snip off the tip.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'157EDE32DD7A2E4B9310A07F423BD509', X'E2C6ED273DF2B446A19E079BA8879CFB', X'E2C6ED273DF2B446A19E079BA8879CFB', 1, NULL, NULL, 2, NULL, 0, 'Pipe 10 chicken feet and 10 legs onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5E522F316494844AF06E81280EB6210', X'E2C6ED273DF2B446A19E079BA8879CFB', X'E2C6ED273DF2B446A19E079BA8879CFB', 2, NULL, NULL, 2, NULL, 0, 'Freeze for 3-5 minutes just until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DD252C4039D8143999590AD02C85099', X'E2C6ED273DF2B446A19E079BA8879CFB', X'E2C6ED273DF2B446A19E079BA8879CFB', 3, NULL, NULL, 2, NULL, 0, 'Remove and pipe a dot of candy melts on the back end of each foot then attach a leg coming out of the topside of the foot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87A32FD8C8A3D04D9464BB87DDE00C94', X'E2C6ED273DF2B446A19E079BA8879CFB', X'E2C6ED273DF2B446A19E079BA8879CFB', 4, NULL, NULL, 2, NULL, 0, 'Let the chicken legs dry for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F678B69EDC0D924DB364AC72F3DEE904', X'E2C6ED273DF2B446A19E079BA8879CFB', X'E2C6ED273DF2B446A19E079BA8879CFB', 5, NULL, NULL, 2, NULL, 0, 'Just before serving, insert two candy legs/feet into a thawed cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEB2BCBDCE19F041B802B3B1DAC0E8D4', X'E2C6ED273DF2B446A19E079BA8879CFB', X'E2C6ED273DF2B446A19E079BA8879CFB', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B38F57155F722C47BE624173E42350EA', X'A84FB760759F1049827A30CF43253F06', X'A84FB760759F1049827A30CF43253F06', 0, NULL, NULL, 2, NULL, 0, 'Combine lemon cake mix with lemon pudding mix, sour cream, eggs, water, and vegetable oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'959F655FC911C240983FAECC3C1E09C2', X'A84FB760759F1049827A30CF43253F06', X'A84FB760759F1049827A30CF43253F06', 1, NULL, NULL, 2, NULL, 0, 'Beat, using an electric mixer, on low speed for 30 seconds then increase the speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80909954C44FF748B404F51D54F49C8C', X'A84FB760759F1049827A30CF43253F06', X'A84FB760759F1049827A30CF43253F06', 2, NULL, NULL, 2, NULL, 0, 'Spoon into 24 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A39D16D8403D17479E02BA3CB26DE31B', X'A84FB760759F1049827A30CF43253F06', X'A84FB760759F1049827A30CF43253F06', 3, NULL, NULL, 2, NULL, 0, 'Bake for 18-20 minutes until the cupcake springs back when your press the center with your finger.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAA299E4645FAE4BA086A9C091EE560B', X'A84FB760759F1049827A30CF43253F06', X'A84FB760759F1049827A30CF43253F06', 4, NULL, NULL, 2, NULL, 0, 'Cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A260CDAE2AC4A7499ED5E655A11B9F4E', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 0, NULL, NULL, 2, NULL, 0, 'Beat the softened butter until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'876520046C6FA34BB4D77BAE92094048', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 1, NULL, NULL, 2, NULL, 0, 'Add the freshly squeezed lemon juice, lemon zest, and 2 cups of powdered sugar and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D81688D511734140B435B06991FCD9DF', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 2, NULL, NULL, 2, NULL, 0, 'Add the remaining 2 1/2 cups of powdered sugar and beat until light and fluffy, about a minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CAAA0F43AAA58E4B80B1B5B3512953BC', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 3, NULL, NULL, 2, NULL, 0, 'Remove 1 cup of the frosting then color the remaining frosting using yellow food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F004F3F18713746B57AF4CC0A8D2C97', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 4, NULL, NULL, 2, NULL, 0, 'Leave about 1/3 cup of the frosting white.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F06996E5176E464D96B5B191DE47D547', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 5, NULL, NULL, 2, NULL, 0, 'Color 1/2 cup of the frosting orange.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A3AE6FF98EE074CB600AA478617C6CE', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 6, NULL, NULL, 2, NULL, 0, 'Color the remaining tablespoons of frosting black.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A483F042864B7C4985429D229A898009', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 7, NULL, NULL, 2, NULL, 0, 'Arrange 21 of the cupcakes in an oval on a cake board or serving platter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D4DF286F0B89D4E98228A3B2096FD3D', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 8, NULL, NULL, 2, NULL, 0, 'Fill the gaps in your cupcake cake using pieces of the rice krispie treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D90079D1B9BED44BED864FD4B13F77E', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 9, NULL, NULL, 2, NULL, 0, 'Fit 4 pastry bags with star tips. Fill one bag with yellow frosting, one with white, one with orange, and one with black.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7952090943DDC418DB2343CD23E7165', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 10, NULL, NULL, 2, NULL, 0, 'Pipe two circles of black frosting for the chick''s pupils then surround those with white frosting. Add a white highlight in each pupil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23E3FFD5109D404A826CCF349209BC45', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 11, NULL, NULL, 2, NULL, 0, 'Add a beak under the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87E10800DFA4C141ACBFFFC5D6C8CE44', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 12, NULL, NULL, 2, NULL, 0, 'Then pipe yellow frosting over the rest of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B6680E06CDF73448B972B19BEC195FC', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 13, NULL, NULL, 2, NULL, 0, 'Pipe on two feet using orange frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E81D33DCBA34D34C8A343123FFC8490F', X'0B8092B950807D43A18CB6900FFBE2D1', X'0B8092B950807D43A18CB6900FFBE2D1', 14, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BCADF8400E0AA43A46E843BEBF1CF40', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A9137B70F4BF64EBB5B0ABDBFE83677', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 1, NULL, NULL, 2, NULL, 0, 'Unroll the Pillsbury Crescent Recipe Creations Seamless Dough Sheet onto a cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'153BB9087F54BE4AA279981148E835E4', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 2, NULL, NULL, 2, NULL, 0, 'Cut the dough lengthwise into 6 equal strips.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F271DAACE075B41A526F65716A1DFBE', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 3, NULL, NULL, 2, NULL, 0, 'Brush one strip with egg wash.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40E6FA36BCF4D04482AC8DC8F9414A1C', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 4, NULL, NULL, 2, NULL, 0, 'Roll the strips into a 17`-18` long log.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31CDE058CC6AF2418B023F1D245E2A4C', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 5, NULL, NULL, 2, NULL, 0, 'Wrap one dough log around each cream horn mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E898BECBD3E5141AB4879B6BF9517DE', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 6, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A91EABE282E1924EBBD03B95A6D230AE', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 7, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with non-stick aluminum foil or parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'389E9DF3DB0501428BFD4F953C9A995D', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 8, NULL, NULL, 2, NULL, 0, 'Brush the orange food coloring over each carrot shaped dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3ACD61ADB49E1D4B9E50FE6E8A1B8D20', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 9, NULL, NULL, 2, NULL, 0, 'Set carrots on lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12B8329115F35F4B91A4458472C05065', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 10, NULL, NULL, 2, NULL, 0, 'Bake for 6-8 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF030A3D0A4E4F46A438C5D3B50629C0', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 11, NULL, NULL, 2, NULL, 0, 'Allow carrot crescents to cool for about 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CA8DF0A5F9A16469A8F62B0FFCB8DF8', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 12, NULL, NULL, 2, NULL, 0, 'Remove cream horn form and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'884196E0EF0A3C418CAF6508B54BC9D6', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 13, NULL, NULL, 2, NULL, 0, 'Fill each Crescent Roll Carrot with egg salad or ham salad.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1C9552DD99A9044ACD045AF1F8718A9', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 14, NULL, NULL, 2, NULL, 0, 'Add a few sprigs of dill coming out of the carrot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84851DE06D037445947CEBD06C0CFF80', X'CD99D2A65932F3479E05EFC333B4763E', X'CD99D2A65932F3479E05EFC333B4763E', 15, NULL, NULL, 2, NULL, 0, 'Serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'137B0E302403074F989D8FA0DE21E84D', X'FD36A31F665ABC438669B84E99FB1623', X'FD36A31F665ABC438669B84E99FB1623', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2633CF87D99FF40BE49D40E1F404AA0', X'FD36A31F665ABC438669B84E99FB1623', X'FD36A31F665ABC438669B84E99FB1623', 1, NULL, NULL, 2, NULL, 0, 'Beat butter and sugar until fully combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25C44AE81FFCF441814EAF110E1D4ED6', X'FD36A31F665ABC438669B84E99FB1623', X'FD36A31F665ABC438669B84E99FB1623', 2, NULL, NULL, 2, NULL, 0, 'Add melted chocolate and beat until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01B20B91500E9643ACB7C1CBCFDD3998', X'FD36A31F665ABC438669B84E99FB1623', X'FD36A31F665ABC438669B84E99FB1623', 3, NULL, NULL, 2, NULL, 0, 'Mix in eggs and egg yolks one at a time until just combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88D08ADB8A362E419E1B8A7BACA97105', X'FD36A31F665ABC438669B84E99FB1623', X'FD36A31F665ABC438669B84E99FB1623', 4, NULL, NULL, 2, NULL, 0, 'Mix in oil, vanilla, and sour cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'635626C06B2CFC41B6311B16CDBCD76F', X'FD36A31F665ABC438669B84E99FB1623', X'FD36A31F665ABC438669B84E99FB1623', 5, NULL, NULL, 2, NULL, 0, 'Sift together flour, salt, baking soda, and cocoa powder together then mix into wet ingredients in three increments.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01041FAE43E34F4E85A817AC5D675E1F', X'FD36A31F665ABC438669B84E99FB1623', X'FD36A31F665ABC438669B84E99FB1623', 6, NULL, NULL, 2, NULL, 0, 'Stir in water until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CAB0E7ACCEEDB54A980FD0A614CB0F6F', X'FD36A31F665ABC438669B84E99FB1623', X'FD36A31F665ABC438669B84E99FB1623', 7, NULL, NULL, 2, NULL, 0, 'Fill 12 silicone terra cotta pot molds with 5 tablespoons of batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2513ED9A84B4824EB8240C18C5296076', X'FD36A31F665ABC438669B84E99FB1623', X'FD36A31F665ABC438669B84E99FB1623', 8, NULL, NULL, 2, NULL, 0, 'Equally divide remaining batter in 4 standard cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'583BE399400C0B4AB82201AEBE21C41F', X'FD36A31F665ABC438669B84E99FB1623', X'FD36A31F665ABC438669B84E99FB1623', 9, NULL, NULL, 2, NULL, 0, 'Bake for 18-24 minutes until a toothpick inserted into the center of a cupcake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A4FCCB5FF655342A800975328EBCF1D', X'FD36A31F665ABC438669B84E99FB1623', X'FD36A31F665ABC438669B84E99FB1623', 10, NULL, NULL, 2, NULL, 0, 'Remove and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'694916E5B7DB2A499C015BFB61993B9D', X'FD36A31F665ABC438669B84E99FB1623', X'FD36A31F665ABC438669B84E99FB1623', 11, NULL, NULL, 2, NULL, 0, 'Remove about an inch or so of cake from the center of each terra cotta cupcake by using a cupcake corer or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'002533A8B01FE84CA265C3BD2BFB2AF6', X'FD36A31F665ABC438669B84E99FB1623', X'FD36A31F665ABC438669B84E99FB1623', 12, NULL, NULL, 2, NULL, 0, 'Crumble the removed cake pieces along with the 1 of the cupcakes. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'217CB20A0404914B88279AB8087B55F1', X'E727684A9557FA4094989EA641160E66', X'E727684A9557FA4094989EA641160E66', 0, NULL, NULL, 2, NULL, 0, 'Combine chocolate with 3/4 cup heavy whipping cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F9D27DBE0B91E4885B301D37199F02B', X'E727684A9557FA4094989EA641160E66', X'E727684A9557FA4094989EA641160E66', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power in the microwave for 1 minute 15 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBBA406E3F3381479FC5878C5B2712D0', X'E727684A9557FA4094989EA641160E66', X'E727684A9557FA4094989EA641160E66', 2, NULL, NULL, 2, NULL, 0, 'Let chocolate rest in microwave for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60087DC18F361C43B7E76686D97492C5', X'E727684A9557FA4094989EA641160E66', X'E727684A9557FA4094989EA641160E66', 3, NULL, NULL, 2, NULL, 0, 'Remove and stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD648A5AF8D464499188A99F5B7F68C7', X'E727684A9557FA4094989EA641160E66', X'E727684A9557FA4094989EA641160E66', 4, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10-15 more seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3932C6BBE9C564892164FEE284DF0ED', X'E727684A9557FA4094989EA641160E66', X'E727684A9557FA4094989EA641160E66', 5, NULL, NULL, 2, NULL, 0, 'Pour in remaining whipping cream and stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86492DC648CFB644B8F1BFD349104C42', X'E727684A9557FA4094989EA641160E66', X'E727684A9557FA4094989EA641160E66', 6, NULL, NULL, 2, NULL, 0, 'Pour warm ganache over top of one of the terra cotta cakes then sprinkle cake crumbs over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C585D545E35B14DB7A11F62009A1A22', X'E727684A9557FA4094989EA641160E66', X'E727684A9557FA4094989EA641160E66', 7, NULL, NULL, 2, NULL, 0, 'Repeat covering all the terra cotta cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC89B3A9B518D44FA77820E0A244E3A3', X'E727684A9557FA4094989EA641160E66', X'E727684A9557FA4094989EA641160E66', 8, NULL, NULL, 2, NULL, 0, 'Use the remaining ganache to frost the plain cupcakes and enjoy a snack.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF7B49806BFFEA44950FF594BA239CB3', X'82BFB671AE50FD48B6800100532E375C', X'82BFB671AE50FD48B6800100532E375C', 0, NULL, NULL, 2, NULL, 0, 'Scoop about 2 tablespoons of cold white frosting using an ice cream scoop and set it in the center of one of the terra cotta pot cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E619052FEA75D44B92AEDAD34DA516CE', X'82BFB671AE50FD48B6800100532E375C', X'82BFB671AE50FD48B6800100532E375C', 1, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF976DBD454F6D418268BD7496F2FFAB', X'82BFB671AE50FD48B6800100532E375C', X'82BFB671AE50FD48B6800100532E375C', 2, NULL, NULL, 2, NULL, 0, 'Attach two candy feet to each scoop of frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D58383337F7914CAADE220A2CA11E3B', X'82BFB671AE50FD48B6800100532E375C', X'82BFB671AE50FD48B6800100532E375C', 3, NULL, NULL, 2, NULL, 0, 'Scoop out about 1/2 teaspoon of cold frosting and roll it into a small ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD9282225323DE46832DF0AA35ABD676', X'82BFB671AE50FD48B6800100532E375C', X'82BFB671AE50FD48B6800100532E375C', 4, NULL, NULL, 2, NULL, 0, 'Set it on a scoop of frosting to create the bunny''s tail.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'069EDBEEE6265340856004EC1E432A6A', X'82BFB671AE50FD48B6800100532E375C', X'82BFB671AE50FD48B6800100532E375C', 5, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE45012809464D45877D586094291E4C', X'82BFB671AE50FD48B6800100532E375C', X'82BFB671AE50FD48B6800100532E375C', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1885AC0A8C9B5F4A998D2D1964AE0C37', X'4F2BEBEF29083D49AE8EB9A2515A66ED', X'4F2BEBEF29083D49AE8EB9A2515A66ED', 0, NULL, NULL, 2, NULL, 0, 'Spoon about 2 tablespoons of orange candy melts into a cone shaped plastic dessert cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D463B8AE0EEE346897CC151782AFEA3', X'4F2BEBEF29083D49AE8EB9A2515A66ED', X'4F2BEBEF29083D49AE8EB9A2515A66ED', 1, NULL, NULL, 2, NULL, 0, 'Use a food use only paint brush to brush the candy melts all over the interior of the cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65269B2D8869624D89F23C5484AF0C8A', X'4F2BEBEF29083D49AE8EB9A2515A66ED', X'4F2BEBEF29083D49AE8EB9A2515A66ED', 2, NULL, NULL, 2, NULL, 0, 'Repeat coating all 10 cone shaped cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B01631A95CAC04CBEBCDCE9D9B86A23', X'4F2BEBEF29083D49AE8EB9A2515A66ED', X'4F2BEBEF29083D49AE8EB9A2515A66ED', 3, NULL, NULL, 2, NULL, 0, 'Once the candy melts have hardened, brush on a second layer of orange candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F361372B5E4CC743A6AF9AD09D810472', X'4F2BEBEF29083D49AE8EB9A2515A66ED', X'4F2BEBEF29083D49AE8EB9A2515A66ED', 4, NULL, NULL, 2, NULL, 0, 'Pop the cups into the freezer for 3-5 minutes then remove and let sit at room temperature for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20C5FA1DD0CB3943B3E21258E76B30B9', X'4F2BEBEF29083D49AE8EB9A2515A66ED', X'4F2BEBEF29083D49AE8EB9A2515A66ED', 5, NULL, NULL, 2, NULL, 0, 'Tug on the candy and slide the candy cones out of the plastic cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'398C411874E1664BAE30D44925B1A693', X'4F2BEBEF29083D49AE8EB9A2515A66ED', X'4F2BEBEF29083D49AE8EB9A2515A66ED', 6, NULL, NULL, 2, NULL, 0, 'Gently drag a sharp knife around the candy cups creating grooves to look like the grooves in a real carrot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CEB4BB4D39E344EB04400CB1BE28315', X'4F2BEBEF29083D49AE8EB9A2515A66ED', X'4F2BEBEF29083D49AE8EB9A2515A66ED', 7, NULL, NULL, 2, NULL, 0, 'Set the candy cups upright in the display stand.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'896EFFAD019B4543B5A55DDB64F31B93', X'4F2BEBEF29083D49AE8EB9A2515A66ED', X'4F2BEBEF29083D49AE8EB9A2515A66ED', 8, NULL, NULL, 2, NULL, 0, 'Fill the candy cups with Easter candies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0F077E750D5044D93E360701DDF9D22', X'4F2BEBEF29083D49AE8EB9A2515A66ED', X'4F2BEBEF29083D49AE8EB9A2515A66ED', 9, NULL, NULL, 2, NULL, 0, 'Cut the edible Easter grass into 3 inch long pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAF162728784BE4991F58ADBED06BBC7', X'4F2BEBEF29083D49AE8EB9A2515A66ED', X'4F2BEBEF29083D49AE8EB9A2515A66ED', 10, NULL, NULL, 2, NULL, 0, 'Snuggle a small bunch of the edible grass in the middle of the candy in each candy cup so it looks like the greens on a real carrot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B622FD856B0C2B43ACB6F8EFCF759A64', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 0, NULL, NULL, 2, NULL, 0, 'Run a line of butter around the inside edges of a 12 x 18 inch baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'849482E118893642A429340E61DE56C7', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 1, NULL, NULL, 2, NULL, 0, 'Set a piece of parchment or wax paper down on the pan then rub more butter over it.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D4C3B7F6D7B044CBA3A17FDD359372F', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 2, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57CF87C84B5CC44AA41BD0164FF09009', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 3, NULL, NULL, 2, NULL, 0, 'Equally divide the marshmallows (a heaping cup of each) into 6 microwave safe bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6E75111AB52EB4C8D42BF49C9CD6831', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 4, NULL, NULL, 2, NULL, 0, 'Cut the butter into 1/2 tablespoon pieces and set one piece in each bowl of marshmallows.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18BFAB76ADE0F34A84149AE3621BBDD5', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 5, NULL, NULL, 2, NULL, 0, 'Heat each bowl on high power for 45-75 seconds until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BA4E2490C00B748BD4BE144039F0A18', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 6, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5548B889B51604C98BE71D78F580822', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 7, NULL, NULL, 2, NULL, 0, 'Repeat, melting all the marshmallows.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D1DEAEC090EDB48A43B473C9452661E', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 8, NULL, NULL, 2, NULL, 0, 'Add purple coloring to one bowl and stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CF6C6E472072D479B3F6D45D6F2B897', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 9, NULL, NULL, 2, NULL, 0, 'Add one of each of the remaining colors to the remaining bowls of marshmallow.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21A88C87FCC07F4C8BF08E4D9EC20BCB', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 10, NULL, NULL, 2, NULL, 0, 'Stir 1 cup of Rice Krispies Cereal into each bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BE0F8ED3FB19C4CBC786247B69699C8', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 11, NULL, NULL, 2, NULL, 0, 'Scoop the purple cereal treat mixture out of the bowl and stretch it out over the long edge of the prepared pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E773A39FDB359A46B5840A90FC98A1CC', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 12, NULL, NULL, 2, NULL, 0, 'Flatten it into a 2 inch wide strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46890286C23BE940BFAABEE2880027F1', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 13, NULL, NULL, 2, NULL, 0, 'Add a blue strip next to the purple one then add a green, then yellow, orange, and pink strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C41F6BC6B25C49469D635E7BB3029DD6', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 14, NULL, NULL, 2, NULL, 0, 'Press the seams together so the colored strips are well sealed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'934E9B3FB15AED4784A37C53F30A1D17', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 15, NULL, NULL, 2, NULL, 0, 'Run a knife or plastic scraper around the edge of the pan loosening the cereal treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FB783D025A5604FA1F3B44AF4903B97', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 16, NULL, NULL, 2, NULL, 0, 'Carefully flip the pan over allowing the rainbow rice krispie treat to fall out onto your work surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C154B81701E184D8DD219EDAE12E60B', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 17, NULL, NULL, 2, NULL, 0, 'Peel the paper off the treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87F4C220763D614FB067A45B53B2EFCD', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 18, NULL, NULL, 2, NULL, 0, 'Begin rolling the treat along the long edge (either the purple or pink strip).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FD9BEFCB15F6346BF3B40870EB8C50B', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 19, NULL, NULL, 2, NULL, 0, 'Roll it, pinching together any cracks, until you have an 18 inch long log.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D044C0A91EE8F741934A9BEE0A5079B0', X'4C833E5A9F7B9A49A821784A022759D8', X'4C833E5A9F7B9A49A821784A022759D8', 20, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, cut the roll into 12-18 slices and they are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'078414E2A0206844A625AEE0D6B4F24E', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 0, NULL, NULL, 2, NULL, 0, 'Pour marshmallows and butter into large microwave safe mixing bowl');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4A178CD991ED949B654AC2AB09850A1', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 1, NULL, NULL, 2, NULL, 0, 'Heat on high for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EE1C3589C10914FBF789FE8B2980307', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2C2CF96029F6D4B9FC47BB78D205A41', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 3, NULL, NULL, 2, NULL, 0, 'Heat for another 30-60 seconds until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B32DD714C6B5A64D9AB02DC9B21BB75E', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 4, NULL, NULL, 2, NULL, 0, 'Stir then add Fruity Pebbles and stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D90D5DA2C574A4590A17ABFE7A267AA', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 5, NULL, NULL, 2, NULL, 0, 'Equally divide the cereal mixture into 6 sections.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'589A69AFA4DCC3469247F659569B1F7E', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 6, NULL, NULL, 2, NULL, 0, 'Scoop out one section and shape it into a Peeps bunny.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AA41B1F15262B4ABE4DEF105EC443C3', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 7, NULL, NULL, 2, NULL, 0, 'Set the bunny on wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D5CE450BF21B84B97E0DB44B9B4E366', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 8, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'931DD21DD9388C4F884E4729721C27AE', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 9, NULL, NULL, 2, NULL, 0, 'Freeze the bunnies for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67966B42A0763D489DFCEED3DA973538', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 10, NULL, NULL, 2, NULL, 0, 'Remove and peel the bunnies off the wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4AAC1B69D16CB469B37F48C33ECDF56', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 11, NULL, NULL, 2, NULL, 0, 'Set the bunnies on a metal cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4DBC3BB65B8CE642B8547F8F50AC2F46', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 12, NULL, NULL, 2, NULL, 0, 'Drizzle white candy melts over one bunny.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'699E1689B5EC4549B453B3643D8FC270', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 13, NULL, NULL, 2, NULL, 0, 'Drizzle a few stripes of pink candy melts over the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCE7404B6808E24EA34B9FFCFE005E33', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 14, NULL, NULL, 2, NULL, 0, 'Then add a few stripes of orange candy melts overlapping the pink stripes and repeat adding yellow, green, blue, and purple candy melts over the bunny creating a striped rainbow effect.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AD5BC729C19FD4EB5969C2B312DFF58', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 15, NULL, NULL, 2, NULL, 0, 'Tap the bunny so that the stripes blend a bit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12C7BFA1BBBEEB4DA9AF371375258EB4', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 16, NULL, NULL, 2, NULL, 0, 'Carefully remove the bunny from the cooling rack and set on a wax or parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'139F00DDD474B848B54BF72AA59AE3AE', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 17, NULL, NULL, 2, NULL, 0, 'Place in the freezer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B18FFAA5B5E8074F8A968310F3C672C7', X'70E8575E3E04F9438FAD1B460615403F', X'70E8575E3E04F9438FAD1B460615403F', 18, NULL, NULL, 2, NULL, 0, 'Repeat adding stripes to all your cereal treat bunnies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9C04655F1C585408711867D03DEA373', X'A06DA4F4E1C9C744B89E90B593FA81B5', X'A06DA4F4E1C9C744B89E90B593FA81B5', 0, NULL, NULL, 2, NULL, 0, 'Remove eyes from the Peeps.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'421C19E8C66BF649A22D67F8E35D8234', X'A06DA4F4E1C9C744B89E90B593FA81B5', X'A06DA4F4E1C9C744B89E90B593FA81B5', 1, NULL, NULL, 2, NULL, 0, 'Combine 3 ounces of one color Peeps in a large mixing bowl with 1 tablespoon butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1061DCE647E3644291FD7481338EFCF4', X'A06DA4F4E1C9C744B89E90B593FA81B5', X'A06DA4F4E1C9C744B89E90B593FA81B5', 2, NULL, NULL, 2, NULL, 0, 'Heat on high in the microwave for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F427855E66110D4C851D45C26578DC2C', X'A06DA4F4E1C9C744B89E90B593FA81B5', X'A06DA4F4E1C9C744B89E90B593FA81B5', 3, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F124D2B0E8950E498DE647DAB0D31EE4', X'A06DA4F4E1C9C744B89E90B593FA81B5', X'A06DA4F4E1C9C744B89E90B593FA81B5', 4, NULL, NULL, 2, NULL, 0, 'Heat for another 30-60 seconds, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8405A6A960EEA4781736FE8B6686E4E', X'A06DA4F4E1C9C744B89E90B593FA81B5', X'A06DA4F4E1C9C744B89E90B593FA81B5', 5, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFA2335AFAFC534A8377A419D863BD4C', X'A06DA4F4E1C9C744B89E90B593FA81B5', X'A06DA4F4E1C9C744B89E90B593FA81B5', 6, NULL, NULL, 2, NULL, 0, 'Pour in 3 cups of popcorn and stir until evenly coated.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1ECA05910194EC4B9B847F5ED96BC20E', X'A06DA4F4E1C9C744B89E90B593FA81B5', X'A06DA4F4E1C9C744B89E90B593FA81B5', 7, NULL, NULL, 2, NULL, 0, 'Repeat, creating several bowls with different colored popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02ADFC577CB39A4393F7055E02245399', X'A06DA4F4E1C9C744B89E90B593FA81B5', X'A06DA4F4E1C9C744B89E90B593FA81B5', 8, NULL, NULL, 2, NULL, 0, 'Allow the popcorn to cool for a minute or two then pinch off pieces of each color and press together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B03B84BADFD6542BA7DEE564F247171', X'A06DA4F4E1C9C744B89E90B593FA81B5', X'A06DA4F4E1C9C744B89E90B593FA81B5', 9, NULL, NULL, 2, NULL, 0, 'Roll into a ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B68189FD7867BD4C93A746F495E8823C', X'A06DA4F4E1C9C744B89E90B593FA81B5', X'A06DA4F4E1C9C744B89E90B593FA81B5', 10, NULL, NULL, 2, NULL, 0, 'Repeat, creating a total of 12 baseball size popcorn balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81022DB4AC0AB14C9429E69A4311A00F', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 0, NULL, NULL, 2, NULL, 0, 'Combine marshmallows, 6 ounces yellow candy melts, and butter in a large microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D88BBBB4C8480743B019FACC9AB796BD', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 1, NULL, NULL, 2, NULL, 0, 'Heat on high for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C299BDF7B055B64481C0C6FC4C416881', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40039435F33F2D46AFEA97702D4F930B', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 3, NULL, NULL, 2, NULL, 0, 'Heat for another 45-60 seconds until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4473881D70D1A54AA8CF7EA767F148FC', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 4, NULL, NULL, 2, NULL, 0, 'Add a few drops of yellow food coloring and stir until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86BD400354BE244186B27A1697542FCD', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 5, NULL, NULL, 2, NULL, 0, 'Stir in the Rice Krispies Cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1461FEC3C5BDFE4F81BBD3269C9FFE6A', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 6, NULL, NULL, 2, NULL, 0, 'Allow to cool slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E482202E61E3944EB5388C58DA107EDB', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 7, NULL, NULL, 2, NULL, 0, 'Grease hands with butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BFED017203A7D49B9CF4315BB01146B', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 8, NULL, NULL, 2, NULL, 0, 'Scoop and roll 9 balls using about 1/2 cup cereal treat in each ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A3B3250312ABA43A29D93AA2DB4C03B', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 9, NULL, NULL, 2, NULL, 0, 'Reserve about 3/4 cup for the wings and feathers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CF66E54B0CA864F92CAE8EC0C2BFD12', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 10, NULL, NULL, 2, NULL, 0, 'Pinch off teaspoon size pieces of the cereal treat and shape into tear drop shaped wings and immediately attach two, one on either side of a ball. If they don''t stick well, then use melted candy melts to attach them.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A3004E0E1C70F4CB3B8C607C910E489', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 11, NULL, NULL, 2, NULL, 0, 'Roll the remaining cereal treat mixture into thin logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4191DA04ABC0D546BDA68AF722C0942F', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 12, NULL, NULL, 2, NULL, 0, 'Pinch off three short pieces and stick them to the tops of each rice krispie treat chick forming 3 feathers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3BEF1771CC1D44C9C57F654DF4A9D4C', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 13, NULL, NULL, 2, NULL, 0, 'Cut the circus peanuts into 8 equal size slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB20CC4A1A800D46A5FF73504F7D35C5', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 14, NULL, NULL, 2, NULL, 0, 'Cut 18 slices into triangles to form the beaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79EE82B9A6FBCC40A6C2D84412058568', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 15, NULL, NULL, 2, NULL, 0, 'Cut 18 slices into chicken feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4651C5B1D472B4AA0F8F6FDDC18A43B', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 16, NULL, NULL, 2, NULL, 0, 'Melt the remaining 2 ounces of yellow candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F064948EA418A648ADD45E285DC584D6', X'65FAB48047630B4BA8305BAF99669793', X'65FAB48047630B4BA8305BAF99669793', 17, NULL, NULL, 2, NULL, 0, 'Use the candy melts to attach two candy eyes, two circus peanut feet and beak to each chick.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5F2B6EDF1B6844EB990D1AEF73D437E', X'AE09E8F3AA340244B97EE6E32E7B93FF', X'AE09E8F3AA340244B97EE6E32E7B93FF', 0, NULL, NULL, 2, NULL, 0, 'Stir together the yogurt and Cool Whip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'109E1D954FE4B149998EE1219601486A', X'AE09E8F3AA340244B97EE6E32E7B93FF', X'AE09E8F3AA340244B97EE6E32E7B93FF', 1, NULL, NULL, 2, NULL, 0, 'Pour into the 3 bunny shaped cavities in the silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5AC0038B018D943A74259481B0EA916', X'AE09E8F3AA340244B97EE6E32E7B93FF', X'AE09E8F3AA340244B97EE6E32E7B93FF', 2, NULL, NULL, 2, NULL, 0, 'Insert popsicle sticks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'281E2D1D0D5BDF4AAEB9548B82A318E4', X'AE09E8F3AA340244B97EE6E32E7B93FF', X'AE09E8F3AA340244B97EE6E32E7B93FF', 3, NULL, NULL, 2, NULL, 0, 'Freeze for about 4 hours until the pops are firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CA5787C9C9AFF46B9A7655B4B8C6F9B', X'AE09E8F3AA340244B97EE6E32E7B93FF', X'AE09E8F3AA340244B97EE6E32E7B93FF', 4, NULL, NULL, 2, NULL, 0, 'Remove from mold and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D3EA819740C2E49A57C80F39A86589E', X'AE09E8F3AA340244B97EE6E32E7B93FF', X'AE09E8F3AA340244B97EE6E32E7B93FF', 5, NULL, NULL, 2, NULL, 0, 'Store in the freezer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14BAC9A88062D741AACEF3372D5F9229', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 0, NULL, NULL, 2, NULL, 0, 'Unwrap the Yellow Reese''s Eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80B346A437320E48AFB67FC4B40E5D35', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 1, NULL, NULL, 2, NULL, 0, 'Pipe little feathers using yellow candy melts onto a parchment paper lined baking sheet and immediately attach a Reese''s egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F87652A85944F742893029D8DC108E00', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 2, NULL, NULL, 2, NULL, 0, 'Add 3 dots of yellow candy melts to the egg and attach two candy eyes and one beak.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A32506FE2FA394429C8CE7067209CE23', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 3, NULL, NULL, 2, NULL, 0, 'Freeze for a minute or so just until the candy feathers harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'180CBFE4A5D4434AB1D707512357AAF2', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 4, NULL, NULL, 2, NULL, 0, 'Carefully peel the chicks off the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3D62DA680819D428801F6FB26D0040C', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 5, NULL, NULL, 2, NULL, 0, 'Pour a spoonful of white candy melts into the smaller half of a plastic egg. Brush the candy coating up the sides of the egg painting the top edge so it''s jagged.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C691F675473F8499D4B0FFEA1E47038', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 6, NULL, NULL, 2, NULL, 0, 'Freeze upright for 1 1/2 minutes then remove and let sit at room temperature for a few minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BCC317B389C6545B277152BD2A965D5', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 7, NULL, NULL, 2, NULL, 0, 'Pour another small spoonful of candy melts into the egg and brush it up the sides adding a second coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F02E872C2C42624BA60338C04480818D', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 8, NULL, NULL, 2, NULL, 0, 'Freeze for another 2 minutes then remove and gently tug on the candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73D2A93EA3CC064DA34B558CA0BA4925', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 9, NULL, NULL, 2, NULL, 0, 'If the candy shell releases easily then pull it out.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4BB3B090F12474B9E190CE8F793759D', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 10, NULL, NULL, 2, NULL, 0, 'If not, freeze for another minute then try again.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84D88792FB3A1341BBE6486F2B26394D', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 11, NULL, NULL, 2, NULL, 0, 'Repeat making a total of 12 egg shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E729DA142E01C46AA91EF79ADDA504C', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 12, NULL, NULL, 2, NULL, 0, 'Set the shells upright and spoon in about a teaspoon of white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64FC875089BF5E46A4801F8DDDB5AE26', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 13, NULL, NULL, 2, NULL, 0, 'Let that candy thicken slightly then set a chick upright in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8583842DB8B1A04698A1173ED29C1141', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 14, NULL, NULL, 2, NULL, 0, 'Hold the chick in place until it stands upright on it''s own.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'519EC9BCCEA098478DF160EDA66C1EBA', X'BE92F07C7003E648B1D9A167FA14B0DC', X'BE92F07C7003E648B1D9A167FA14B0DC', 15, NULL, NULL, 2, NULL, 0, 'Arrange on a serving platter and sprinkle toasted coconut around the chicks, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BDF4147765A7045AAB3ECA3A02E1D83', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2DC336B0422D148966147FADD2373A5', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', 1, NULL, NULL, 2, NULL, 0, 'In a medium-sized mixing bowl or the bowl of a stand mixer, mix butter and sugar until fully combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'249489F15466C941B22D8DAF968A9116', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', 2, NULL, NULL, 2, NULL, 0, 'Add melted chocolate and beat until well mixed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'197F4802AF66564FB029E1AA19F4CB2A', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', 3, NULL, NULL, 2, NULL, 0, 'Mix in eggs and egg yolks one at a time until just combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9A4644457D7374EB5E9CBA234E7812B', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', 4, NULL, NULL, 2, NULL, 0, 'Mix in oil, vanilla, and sour cream until just combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B274DD6E3A595240A90F7377324AC3BD', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', 5, NULL, NULL, 2, NULL, 0, 'Sift together flour, salt, baking soda, and cocoa powder together then mix into wet ingredients in three increments.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29198345050DAE4A8E126B2FF3134090', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', 6, NULL, NULL, 2, NULL, 0, 'Mix in water until just combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8B72B5C9FAE3D49AB425C6B8230E889', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', 7, NULL, NULL, 2, NULL, 0, 'Fill silicone mold with 5 tablespoons of batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E10CB9F025F814D82EFEBF639094039', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', 8, NULL, NULL, 2, NULL, 0, 'Equally divide remaining batter in 4 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C497C1F2EC765418ED01B017F98FB7B', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', 9, NULL, NULL, 2, NULL, 0, 'Bake for 18-24 minutes until a toothpick inserted into the center of a cupcake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80C75AC66351F64D885D3B7E2DA2A0DA', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', 10, NULL, NULL, 2, NULL, 0, 'Remove and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C50A94AA50C7284A88DC1F09BDE19433', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', 11, NULL, NULL, 2, NULL, 0, 'Remove about an inch or so of cake from the center of each terra cotta cupcake by using a cupcake corer or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D6FE4124F71C844AA0D7829D6541BD6', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', X'71E5D09CB49A9A4BAB44863FE3F0BB9E', 12, NULL, NULL, 2, NULL, 0, 'Crumble the removed cake pieces along with one of the cupcakes. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3507576613E65849B0FD62A1A38D53ED', X'D8C5007A3E03EB429D576D67C1E4DC5D', X'D8C5007A3E03EB429D576D67C1E4DC5D', 0, NULL, NULL, 2, NULL, 0, 'Combine chocolate with 3/4 cup heavy whipping cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'695ED85F2C24C645B79624EF6AFFC4C3', X'D8C5007A3E03EB429D576D67C1E4DC5D', X'D8C5007A3E03EB429D576D67C1E4DC5D', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power in the microwave for 1 minute 15 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EC2E3FA8727964FBF9DA54BF7E41852', X'D8C5007A3E03EB429D576D67C1E4DC5D', X'D8C5007A3E03EB429D576D67C1E4DC5D', 2, NULL, NULL, 2, NULL, 0, 'Let chocolate rest in microwave for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F24C50F48390C54D8D3107BD549D683C', X'D8C5007A3E03EB429D576D67C1E4DC5D', X'D8C5007A3E03EB429D576D67C1E4DC5D', 3, NULL, NULL, 2, NULL, 0, 'Remove and stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD50A6B04F994141A05ECBAA6B0E8982', X'D8C5007A3E03EB429D576D67C1E4DC5D', X'D8C5007A3E03EB429D576D67C1E4DC5D', 4, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10-15 more seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A685D61B7AE174BA8BC61464035B0AB', X'D8C5007A3E03EB429D576D67C1E4DC5D', X'D8C5007A3E03EB429D576D67C1E4DC5D', 5, NULL, NULL, 2, NULL, 0, 'Pour in remaining whipping cream and stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93C381A76622D1438EDF8363FC2DF792', X'D8C5007A3E03EB429D576D67C1E4DC5D', X'D8C5007A3E03EB429D576D67C1E4DC5D', 6, NULL, NULL, 2, NULL, 0, 'Pour warm ganache over top of one of the terra cotta cakes then sprinkle cake crumbs over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'724BBB999BFBB94CA09AB637CC103C2E', X'D8C5007A3E03EB429D576D67C1E4DC5D', X'D8C5007A3E03EB429D576D67C1E4DC5D', 7, NULL, NULL, 2, NULL, 0, 'Repeat covering all the terra cotta cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'128DB1D30F3D064B9177A5FD0C5CCE51', X'D8C5007A3E03EB429D576D67C1E4DC5D', X'D8C5007A3E03EB429D576D67C1E4DC5D', 8, NULL, NULL, 2, NULL, 0, 'Use the remaining ganache to frost the plain cupcakes and enjoy a snack.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49F478CC73D13E49A17CDEF8263E6BB0', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 0, NULL, NULL, 2, NULL, 0, 'Unwrap White Reese''s Cups and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31AFB7E2B6643F409AA31A651A51D180', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 1, NULL, NULL, 2, NULL, 0, 'Roll out creamy white, pink, bright white and black candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5AB98BB171E7E146949E0893F0B10FD9', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 2, NULL, NULL, 2, NULL, 0, 'Cut out twenty four 2 1/2 inch long tear drop shaped creamy white ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51159A8BACC49D45AE3E1FBD08249FA8', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 3, NULL, NULL, 2, NULL, 0, 'Cut out twenty four 1 3/4 inch long tear drop shaped pink ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'290EDD1F697C324D93E933C2EA832335', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 4, NULL, NULL, 2, NULL, 0, 'Brush a dab of water on a pink ear and set the wet side on the white ear, centering the pink part.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'064D691B9DCB4D48BECBD50AC91502BE', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 5, NULL, NULL, 2, NULL, 0, 'Repeat creating 24 ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A20D91807A96CC40B12CCB17F16BD83D', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 6, NULL, NULL, 2, NULL, 0, 'Cut out twenty four 1/2 inch bright white circles and 3/8 inch black circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C59A47B2A3DE0F40B9DF9C158E6461EA', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 7, NULL, NULL, 2, NULL, 0, 'Brush one side of a white circle with water and set the wet side down on the Reese''s Cup situated where you''d want your Bunny''s eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A47FFE65B9D0441B508A093C87A092C', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 8, NULL, NULL, 2, NULL, 0, 'Press down on the white candy clay smoothing it out and making it a bit larger.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FF4062FD497E34F974862409D728960', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 9, NULL, NULL, 2, NULL, 0, 'Repeat, adding the second white circle right next to the first.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'088E0FC3EFBB594DA54FC4F094EACE37', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 10, NULL, NULL, 2, NULL, 0, 'Attach two black pupils to the whites using a very small amount of water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BA41B14A7A6F5408585DA86BD4EEBAD', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 11, NULL, NULL, 2, NULL, 0, 'Continue adding eyes to all your Reese''s Cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57F769E9E1E30D4BBF0BA4D503119EA8', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 12, NULL, NULL, 2, NULL, 0, 'Pinch off a small piece of pink candy clay and shape it into a nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC7349ACCB703043BB62BB21E2073FFD', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 13, NULL, NULL, 2, NULL, 0, 'Attach under the eyes using a dab of water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D953515FD5DC2D4D83CD7A2AF46813E8', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 14, NULL, NULL, 2, NULL, 0, 'Set the points of the bunny ears together on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CDEE80A5C9F274AB04DDC6694FE54CA', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 15, NULL, NULL, 2, NULL, 0, 'Pour a small amount of white candy melts over the tips of the ears then continue adding a 2 1/2 inch line of candy melts straight down from the ear forming a pick that will go into the cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D74FF09464701649B21099508223CAF4', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 16, NULL, NULL, 2, NULL, 0, 'Set the Reese''s cup on the wet candy so that the bunny faces the correct direction.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'839409736D03974A8273C8761FDB5465', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 17, NULL, NULL, 2, NULL, 0, 'Freeze for 5 minutes until the candy melts harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61E8AE325FAFFB4980F745175BD54659', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 18, NULL, NULL, 2, NULL, 0, 'Remove from freezer and let sit at room temperature for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06DA88C61305DE4E8EC708A46ECD89E3', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 19, NULL, NULL, 2, NULL, 0, 'Bend one ear a little bit then flip the bunny over.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5D6C486EA5F6F4EA87776CFF3CB73A6', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 20, NULL, NULL, 2, NULL, 0, 'Brush candy melts over the back of the bunny''s ears, head, and pick to give it more stability.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EDF35A54BB73D45929CFB83F6EE3CDD', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 21, NULL, NULL, 2, NULL, 0, 'Let the candy melts dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6A90B39C1A0E3478DF98BEBA9EB777D', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 22, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of orange candy clay and shape them into carrots.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA709FCBBD86204FA903D7E3B9D56CA4', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 23, NULL, NULL, 2, NULL, 0, 'Poke a hole in the larger end of the carrot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F160FC58172E5C46BF96272FBC7BE118', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 24, NULL, NULL, 2, NULL, 0, 'Roll tiny pieces of green candy clay into carrot shapes as well.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBA4AEE36A8E7C4C86B16094DFF9C55B', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 25, NULL, NULL, 2, NULL, 0, 'Brush some water on the pointed end and insert it into the hole in the orange carrot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27FBFC7639A83745A9107236D1AD6C0D', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 26, NULL, NULL, 2, NULL, 0, 'Flatten out the green candy melts, then make indentations using a sharp knife so that the green candy looks like carrot greens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F38DF2E703BD64B8DCD00775CBF1E12', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 27, NULL, NULL, 2, NULL, 0, 'Use the back of the knife to create grooves around the carrot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5851AF4C562B341B216202E4A0270BA', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 28, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of creamy white candy clay and roll them into 24 balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9E99442B831FB4C87BCCA55A6C4CD9D', X'134FECC2B08842458ACFBCE39D7FE7EE', X'134FECC2B08842458ACFBCE39D7FE7EE', 29, NULL, NULL, 2, NULL, 0, 'Cut a slit halfway through each ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AB135CE4688464A999BD0F7D3D0CED1', X'CE225B9807016940A3BCCAD5AC5DF6F0', X'CE225B9807016940A3BCCAD5AC5DF6F0', 0, NULL, NULL, 2, NULL, 0, 'Insert a Reese''s Cup Bunny into a terra cotta pot cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C963DAF57672846973527105A39ACAF', X'CE225B9807016940A3BCCAD5AC5DF6F0', X'CE225B9807016940A3BCCAD5AC5DF6F0', 1, NULL, NULL, 2, NULL, 0, 'Place a white ball of candy clay so that the slit goes over the edge of the silicone terra cotta pot forming one bunny paw. Add a second paw.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9A730B65DDEC14A98C37D5EE7EE4FD4', X'CE225B9807016940A3BCCAD5AC5DF6F0', X'CE225B9807016940A3BCCAD5AC5DF6F0', 2, NULL, NULL, 2, NULL, 0, 'Poke a hole in the cupcake and insert the tip of a carrot in front of the bunny.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BD9B583405CAC4F84CC86B83A1048A5', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0061D0EBAF7F3A47B77FDC74C3CB3406', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 1, NULL, NULL, 2, NULL, 0, 'Line a 9x13 inch baking pan with non-stick tin foil, parchment paper, or baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C74B967E312A854DA633A788E301A3B6', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 2, NULL, NULL, 2, NULL, 0, 'Combine the cake mix, graham cracker crumbs, water, sour cream, oil, and eggs in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D30ACEF867724A4980208F5833BFD2BF', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 3, NULL, NULL, 2, NULL, 0, 'Beat on low speed using an electric mixer for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'754EA5C840BE894085F7D06786EEF8EE', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 4, NULL, NULL, 2, NULL, 0, 'Increase the speed to medium and beat for 1 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE84490BD4F28E4B9F16867F1A589B8F', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 5, NULL, NULL, 2, NULL, 0, 'Fold in the chocolate chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'203029834686634E8BCACEE4DBCF34E2', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 6, NULL, NULL, 2, NULL, 0, 'Pour batter into prepared pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3567F7FFA3906347B08B76E6B5A4AD78', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 7, NULL, NULL, 2, NULL, 0, 'Bake for 28-32 minutes until a toothpick inserted into the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49BE7E02D59821429DB3D5020C236482', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 8, NULL, NULL, 2, NULL, 0, 'Remove from oven, and set pan on a cooling rack and allow cake to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85B2B07E61EB9947AE053FB478404197', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 9, NULL, NULL, 2, NULL, 0, 'Remove cake from pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC9F015261FA9340BBCAA6E69A4A77B7', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 10, NULL, NULL, 2, NULL, 0, 'Cut cake in half so you have two 9 inch by 6 1/2 inch pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3F042A1BF28164AB5E0E085B7DD4AFF', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 11, NULL, NULL, 2, NULL, 0, 'One piece will be the unicorn''s body.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E802BFE12EAFA4E97EAA9EA7E7430C4', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 12, NULL, NULL, 2, NULL, 0, 'Cut the other piece in half so you have two 6 1/2 by 4 1/2 inch pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C18823285CBD2948BD92D7256A8574E1', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 13, NULL, NULL, 2, NULL, 0, 'One piece will be the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C79CBD411D69B4784D2B8B2771A34BE', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 14, NULL, NULL, 2, NULL, 0, 'Cut two 2 inch by 3 inch pieces for the unicorn''s legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'111AF592A0014A40B31ED67B094F844B', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 15, NULL, NULL, 2, NULL, 0, 'Discard or eat the remaining pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EE1DA32C29D82439E3A9DEFA324686A', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 16, NULL, NULL, 2, NULL, 0, 'Assemble the cake by placing the 9 inch by 6 1/2 inch cake in the lower half of your cake board forming the unicorn''s body.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC318760B7A0164088C34FA1D2DFF8C4', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 17, NULL, NULL, 2, NULL, 0, 'Set the 6 1/2 inch by 4 1/2 inch piece about 2 1/2 inches from the top edge of the larger cake forming the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C7570343BF48C448D80C9E8903C6FE2', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 18, NULL, NULL, 2, NULL, 0, 'Place the two leg pieces under the body piece about 3/4 of an inch from either edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E1D1E5B1B6A424ABDBC9A1D226B7AA0', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 19, NULL, NULL, 2, NULL, 0, 'Mix together the marshmallow frosting ingredients.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA9FEC1A729DA34FA643C70C033AB7DA', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 20, NULL, NULL, 2, NULL, 0, 'Spread the white frosting over the body cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A6A104954794E4F9CC0BA5ED68897CF', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 21, NULL, NULL, 2, NULL, 0, 'Mix together the black marshmallow frosting ingredients, adding a tiny bit of water, as needed to thin out the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C66F97B7080AD349932E82C2B267A2DA', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 22, NULL, NULL, 2, NULL, 0, 'Frost the legs using the black frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A693FE4EB4DC84B81136DFF77ABF7AB', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 23, NULL, NULL, 2, NULL, 0, 'For the head, brush black frosting onto about 3/4 of the head piece, curving the back edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF532198C68AA443898EBEEC5B059E1A', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 24, NULL, NULL, 2, NULL, 0, 'Add white frosting on the back part of the head.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'178A391DE1E0704281B9DB68C3535356', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 25, NULL, NULL, 2, NULL, 0, 'Pour the remaining white and black frosting into individual pastry or zip top bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E29D91E79CE88347A9566F91D4C67800', X'727255C90BE8AA45B719FEFEB03AA1F8', X'727255C90BE8AA45B719FEFEB03AA1F8', 26, NULL, NULL, 2, NULL, 0, 'Pipe on a black ear, a white tail, a white eyelid with eye lashes, and a nose and mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97DE92D652B60145B076399DCE3BDA36', X'2F828A2BD08FA04DBD9FA54FA6C4851D', X'2F828A2BD08FA04DBD9FA54FA6C4851D', 0, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper or wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'389D88CB523BCE4AB7B7D412FA623FBC', X'2F828A2BD08FA04DBD9FA54FA6C4851D', X'2F828A2BD08FA04DBD9FA54FA6C4851D', 1, NULL, NULL, 2, NULL, 0, 'Pour a small spoonful of white candy melts into the center of each pretzel creating a heart shape bunny head.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38D94CEFD6F9A04CBA63715CD7054F8D', X'2F828A2BD08FA04DBD9FA54FA6C4851D', X'2F828A2BD08FA04DBD9FA54FA6C4851D', 2, NULL, NULL, 2, NULL, 0, 'Immediately add one pink heart sprinkle nose near the bottom of the pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8145F5B49BAFF74F8CA845498C43AA9C', X'2F828A2BD08FA04DBD9FA54FA6C4851D', X'2F828A2BD08FA04DBD9FA54FA6C4851D', 3, NULL, NULL, 2, NULL, 0, 'Add 3 chocolate jimmies on either side of the nose for the whiskers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37EC72A9D9B78246AB8AD8383A239B24', X'2F828A2BD08FA04DBD9FA54FA6C4851D', X'2F828A2BD08FA04DBD9FA54FA6C4851D', 4, NULL, NULL, 2, NULL, 0, 'Place two black pearl eyes above the nose then add the two broken halves of the jumbo heart sprinkles for the centers of the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E298A31ACF67946AE2BD95513693F28', X'2F828A2BD08FA04DBD9FA54FA6C4851D', X'2F828A2BD08FA04DBD9FA54FA6C4851D', 5, NULL, NULL, 2, NULL, 0, 'Repeat creating a few more then pop them in the freezer for 3-4 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FF4FDA865F6C643A417691852528ADC', X'2F828A2BD08FA04DBD9FA54FA6C4851D', X'2F828A2BD08FA04DBD9FA54FA6C4851D', 6, NULL, NULL, 2, NULL, 0, 'Continue making bunnies until you have 72 adorable Easter treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8ED2FD79EAFF694FB1775DA6444C59D2', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 0, NULL, NULL, 2, NULL, 0, 'Cut black olives in half, discarding the two halves with the big hole in the middle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F70FFB314E58342A0CE04B3429066A5', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 1, NULL, NULL, 2, NULL, 0, 'Cut the orange pepper into one beak and two feet for the chick pizza.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F20ABCB7248C2645B21309A17846FE88', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 2, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'835377F5341A6647B82F36466E5B84AE', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 3, NULL, NULL, 2, NULL, 0, 'Line a baking pan with non-stick tin foil or parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9235F67F06CFFA46AEF83FB11D91C770', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 4, NULL, NULL, 2, NULL, 0, 'Unroll pizza dough onto the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05544F78B2F7114D8596CEC6B8B6D7EF', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 5, NULL, NULL, 2, NULL, 0, 'Cut out two 1/2 inch strips from one long edge of the dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1A998802899354699EBCDC97F635F27', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 6, NULL, NULL, 2, NULL, 0, 'Then cut a large egg shape from the dough, using a parchment paper template if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73F1265B87FAB242AF04952CD8A1FB9D', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 7, NULL, NULL, 2, NULL, 0, 'Separate one egg and brush some of the egg white over the egg shaped pizza dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30134B2A83E6D842AC7A3AE4CFB1F7CA', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 8, NULL, NULL, 2, NULL, 0, 'Discard remaining egg white.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E75EEE262C2A4644A6DAAB1F20D2C66C', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 9, NULL, NULL, 2, NULL, 0, 'Line the edge of the egg shaped dough with the two strips of dough, cutting off the excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EB0CBA55FB5A44E9386641347F946B7', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 10, NULL, NULL, 2, NULL, 0, 'Bake for 8-10 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'295E8ED1ED4D994C8AF08364E94E5A48', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 11, NULL, NULL, 2, NULL, 0, 'Meanwhile, combine the remaining 7 eggs and 1 egg yolk with the half-and-half, salt, and pepper, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D53C4B69AB7334782C93127EA47100B', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 12, NULL, NULL, 2, NULL, 0, 'Put butter in a 10 or 12 inch non-stick skillet and set over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7639E4D486BF8488E263F4E37D6270F', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 13, NULL, NULL, 2, NULL, 0, 'As the butter begins to melt, swirl it around the bottom of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19A2E27249D49C4A9852F1BCAA2A3B23', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 14, NULL, NULL, 2, NULL, 0, 'Once the butter has completely melted, add the eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A06ED46F8D4D344C8EF69F7B374ABB9C', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 15, NULL, NULL, 2, NULL, 0, 'Use a rubber spatula to continuously scrape the bottom and sides of the pan until the eggs begin to clump together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13BAAA91F525764EA17425150F7E2E4C', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 16, NULL, NULL, 2, NULL, 0, 'Once the eggs are cooked about half way, turn the heat down to low and continue to fold the eggs until clumped and slightly wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEAF1E4326CC8844B44F5AB48FA93364', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 17, NULL, NULL, 2, NULL, 0, 'Remove from heat. Do not overcook the eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCF00A3245808B45945DDF7BF70FCA05', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 18, NULL, NULL, 2, NULL, 0, 'Take pizza crust from oven and top with pizza sauce, ham, and shredded cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'846041EC19B9C0489B7A120253D0E703', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 19, NULL, NULL, 2, NULL, 0, 'Pop back in the oven for 3-4 minutes until the cheese melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CC6F513020FF34F9756559B03C50569', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 20, NULL, NULL, 2, NULL, 0, 'Break up the scrambled eggs into small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1B3D566CA9C0149A14A4F1D2A69843D', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 21, NULL, NULL, 2, NULL, 0, 'Spread the eggs over the pizza and pop in the oven for 2-3 minutes just until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1604D1CB319BA14E892B35B2D7972388', X'19BB6193D4C2E34A804AB1BF5728DE26', X'19BB6193D4C2E34A804AB1BF5728DE26', 22, NULL, NULL, 2, NULL, 0, 'Move the pizza to a cutting board or serving platter, add the olive eyes, the orange pepper beak and feet and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51D854E72C22B540B437C1FEB2DEDB3F', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', 0, NULL, NULL, 2, NULL, 0, 'Spoon 1/4-1/2 teaspoon of white candy melts onto a parchment paper lined baking sheet and spread it out into an oval shaped bunny foot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'564185D32567DF4D9061BDCA423A08A7', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', 1, NULL, NULL, 2, NULL, 0, 'Immediately add one pink heart sprinkle for the pad and three pink sugar pearls for the toes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2CDDDC635E9BE4A84204CAFBBE048D2', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', 2, NULL, NULL, 2, NULL, 0, 'Repeat creating more feet then freeze until hard, about 2 minutes.*');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95248D811FB18148B20551BF04B77921', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', 3, NULL, NULL, 2, NULL, 0, 'Roll mini marshmallows into small balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50360CF20A19394DB649695BB922C670', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', 4, NULL, NULL, 2, NULL, 0, 'Arrange pretzels about 2 inches apart on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36A92DEF06DC40478FE9CC6933A33AD2', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', 5, NULL, NULL, 2, NULL, 0, 'Fill the center of one pretzel with white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BFE94161A3150D458EBD59032F134F6F', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', 6, NULL, NULL, 2, NULL, 0, 'Immediately add a marshmallow tail to the rounded top edge of the pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F8511A30785274EB0F6ABC7E7821445', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', 7, NULL, NULL, 2, NULL, 0, 'Add two candy feet, one set over top of each of the smaller rounded edges of the pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'230AFF04DDE5BD40988827C27D8CAB49', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', 8, NULL, NULL, 2, NULL, 0, 'Repeat making more pretzels then pop them in the freezer for about 3-5 minutes until the candy coating hardens.*');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59C612280D2A6243B412169BAA1FBBCC', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', X'0A9CAEA4B3E0EE41B461DEEE9EBFBE41', 9, NULL, NULL, 2, NULL, 0, '*If using pure white chocolate, refrigerate instead of freezing.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA9419B103763C49B8DE663122ACCCF1', X'645F6225E1B5FC43B7894B771815D997', X'645F6225E1B5FC43B7894B771815D997', 0, NULL, NULL, 2, NULL, 0, 'Pour chopped chocolate into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5AF125F48BB84499A087988C48C669C', X'645F6225E1B5FC43B7894B771815D997', X'645F6225E1B5FC43B7894B771815D997', 1, NULL, NULL, 2, NULL, 0, 'Heat it for two 30 second bursts on high power, stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'291F6980EA04104AB17F59B54389D45A', X'645F6225E1B5FC43B7894B771815D997', X'645F6225E1B5FC43B7894B771815D997', 2, NULL, NULL, 2, NULL, 0, 'Pour in the caramel flavored sweetened condensed milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E10CC3220A54664E8A6618CF2B919799', X'645F6225E1B5FC43B7894B771815D997', X'645F6225E1B5FC43B7894B771815D997', 3, NULL, NULL, 2, NULL, 0, 'Stir, then heat on high power for 20 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFC067262DE4524CB85166844CA851D7', X'645F6225E1B5FC43B7894B771815D997', X'645F6225E1B5FC43B7894B771815D997', 4, NULL, NULL, 2, NULL, 0, 'Let the bowl sit in the microwave for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B038783E98104B4999B832F328636CEB', X'645F6225E1B5FC43B7894B771815D997', X'645F6225E1B5FC43B7894B771815D997', 5, NULL, NULL, 2, NULL, 0, 'Then remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDF03DC4F8CFE14693E360E247C4A766', X'645F6225E1B5FC43B7894B771815D997', X'645F6225E1B5FC43B7894B771815D997', 6, NULL, NULL, 2, NULL, 0, 'If the chocolate is not all melted, heat for 10 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26A977CBEFBE5D4F8D9395E861BEF623', X'645F6225E1B5FC43B7894B771815D997', X'645F6225E1B5FC43B7894B771815D997', 7, NULL, NULL, 2, NULL, 0, 'Spoon into silicone bunny mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E302185C6B0AE41BF8F008BC1C11E47', X'645F6225E1B5FC43B7894B771815D997', X'645F6225E1B5FC43B7894B771815D997', 8, NULL, NULL, 2, NULL, 0, 'Press on the fudge to compact it.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74C829523710424A93452A5C7E770E8B', X'645F6225E1B5FC43B7894B771815D997', X'645F6225E1B5FC43B7894B771815D997', 9, NULL, NULL, 2, NULL, 0, 'Press one white Sixlet tail on each bunny.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0737113EE2ADCD4CA010C4EAB09874BD', X'645F6225E1B5FC43B7894B771815D997', X'645F6225E1B5FC43B7894B771815D997', 10, NULL, NULL, 2, NULL, 0, 'Freeze mold for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C1ECF1BE0E7BC43A62AB72BC1B4E53A', X'645F6225E1B5FC43B7894B771815D997', X'645F6225E1B5FC43B7894B771815D997', 11, NULL, NULL, 2, NULL, 0, 'Remove and press on backside of mold, to remove the bunnies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F67B545771492408901F2DCBC52163B', X'645F6225E1B5FC43B7894B771815D997', X'645F6225E1B5FC43B7894B771815D997', 12, NULL, NULL, 2, NULL, 0, 'Repeat, if needed, to make more bunnies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C709290952E7D4E8E693604A8CD55E9', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 0, NULL, NULL, 2, NULL, 0, 'Unwrap Peppermint Patties and set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D22D62ACC47DF3469BC1B233567120E8', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 1, NULL, NULL, 2, NULL, 0, 'Using a Peppermint Patty as a char(36)e, pipe two tear drop shaped ears out of the black candy coating then set the Patty on top, over the tips.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51934187499BAE4CBE88A00345B2D8B6', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 2, NULL, NULL, 2, NULL, 0, 'Pipe 4, 1 inch long, tear drop shaped legs for each lamb');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFC551CA2EB2CE4986E313A73B6F1611', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 3, NULL, NULL, 2, NULL, 0, 'Place pan in freezer for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10774CF2649323458268FC749B3D2C7F', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 4, NULL, NULL, 2, NULL, 0, 'Remove and peel candies off the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D5EB74D5E0D6340A3B08EB3AEFEE60B', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 5, NULL, NULL, 2, NULL, 0, 'Use black candy coating to attach two candy eyes and one pink sugar pearl nose to each Peppermint Patty.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'398BF17C30433B4EA9C4C73B8C849A88', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 6, NULL, NULL, 2, NULL, 0, 'Cut the domed tops off each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'880B9493DC1EA646808B5C5C7512FA86', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 7, NULL, NULL, 2, NULL, 0, 'Pipe frosting on the cut top of half of the cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4881FCC05353FA49AF5F9008BDC5EE9A', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 8, NULL, NULL, 2, NULL, 0, 'Turn another cupcake upside down on top of the frosted cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF82D32ECDBC1C41A66D3D1941002E87', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 9, NULL, NULL, 2, NULL, 0, 'Carve each stack of cupcakes into a ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89A572FE71FA6B4E819B7887CEF5B8A9', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 10, NULL, NULL, 2, NULL, 0, 'Pipe or spread frosting all over the cupcake, making it look woolly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'246B3E4B50A0A245B3219D741C60C5B1', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 11, NULL, NULL, 2, NULL, 0, 'Pipe an inch tall mound of frosting on top of the frosted cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AA35BC7916A3D4193BA64349833AB09', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 12, NULL, NULL, 2, NULL, 0, 'Set a Peppermint Patty sheep in front of the mound of frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B663594AEABC54FB04FCB7E8E2800A4', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 13, NULL, NULL, 2, NULL, 0, 'Pipe some dots of frosting on top of the Peppermint Patty sheep heads.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'399D0F4D4B98CE41A9D1D66ACE6D6101', X'B92F4BA935CC874A9C357DD6511844AD', X'B92F4BA935CC874A9C357DD6511844AD', 14, NULL, NULL, 2, NULL, 0, 'Insert 4 tear drop shaped legs into each lamb cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47AB2DB0676C4841895FE1FDBA428125', X'59306032559FEA488B44A6D3C4DEEE65', X'59306032559FEA488B44A6D3C4DEEE65', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52481B0CAAA5A64B809BADE45E9FD350', X'59306032559FEA488B44A6D3C4DEEE65', X'59306032559FEA488B44A6D3C4DEEE65', 1, NULL, NULL, 2, NULL, 0, 'Stir together graham cracker crumbs and melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5147D2498E8DE34297A1135053A00C6C', X'59306032559FEA488B44A6D3C4DEEE65', X'59306032559FEA488B44A6D3C4DEEE65', 2, NULL, NULL, 2, NULL, 0, 'Spoon about a tablespoon of crumbs into each of 16 oval shaped cavities in 2 silicone molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'840B2EAEAA48F948921CA7B2B9D91009', X'59306032559FEA488B44A6D3C4DEEE65', X'59306032559FEA488B44A6D3C4DEEE65', 3, NULL, NULL, 2, NULL, 0, 'Press crumbs into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CA961424276384983C22D5C6A0456DA', X'59306032559FEA488B44A6D3C4DEEE65', X'59306032559FEA488B44A6D3C4DEEE65', 4, NULL, NULL, 2, NULL, 0, 'Bake for 8-12 minutes until fragrant and golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CB563DE85EF58449B6AFDCF6D0421E3', X'59306032559FEA488B44A6D3C4DEEE65', X'59306032559FEA488B44A6D3C4DEEE65', 5, NULL, NULL, 2, NULL, 0, 'Remove and cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'226ABEC745B7D541A018870293D916EF', X'59306032559FEA488B44A6D3C4DEEE65', X'59306032559FEA488B44A6D3C4DEEE65', 6, NULL, NULL, 2, NULL, 0, 'Set molds on baking pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46622B1A66BB2547B80464128EE8DA54', X'59306032559FEA488B44A6D3C4DEEE65', X'59306032559FEA488B44A6D3C4DEEE65', 7, NULL, NULL, 2, NULL, 0, 'Bring a pot of water to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3B90FF75C379A4090774C049210D2E1', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 0, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF1D8CD96F8CB4478F03B957CD9FE23D', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 1, NULL, NULL, 2, NULL, 0, 'Add sugar, eggs, heavy whipping cream, and vanilla and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD0EA335B1CF6545867726F34D2513E7', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 2, NULL, NULL, 2, NULL, 0, 'Equally diving filling into 4 bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EFDF517DF98DEA46B805BE9288A63A14', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 3, NULL, NULL, 2, NULL, 0, 'Tap the bowls on the counter several times to remove air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35F258240B39D443AB1063646A724F26', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 4, NULL, NULL, 2, NULL, 0, 'Color one bowl pink, one blue, one yellow, and one purple using food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52197EC2D94D084A966A665D80CC9D1D', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 5, NULL, NULL, 2, NULL, 0, 'Pour about 1/4 cup of one colored filing into a small bowl. Pour another color next to it, then another and another.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C47DE7809F0EB1439F3A97241216E347', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 6, NULL, NULL, 2, NULL, 0, 'Scoop out some filling from the bowl and pour it over one of the graham cracker crusts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9BE1D34F1D2024293380C87DD592BDC', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 7, NULL, NULL, 2, NULL, 0, 'Repeat filling the mold up to about 1/8 of an inch from the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF06F0033C141148887496C6E75BA814', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 8, NULL, NULL, 2, NULL, 0, 'Refill bowl as needed and repeat filling all 16 cheesecake Easter eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA7FDFEB94FEFC42AC5D0E705272C7E5', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 9, NULL, NULL, 2, NULL, 0, 'Set pans in oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5AAC8140B833E499781FCCD662698AA', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 10, NULL, NULL, 2, NULL, 0, 'Pour water into the pan, being careful not to pour any onto the cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12A9856C2B380A459B06D21AE196F5C9', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 11, NULL, NULL, 2, NULL, 0, 'Bake for 18-22 minutes until the edges of the cheesecake look set. The center will still be jiggly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A78C4D3B9CB064D9A86651C20DFFDE1', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 12, NULL, NULL, 2, NULL, 0, 'Remove molds from water bath, set on a cooling rack, and cool for 30 minutes .');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'835C694FB98AF9479BD936F833BDC083', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 13, NULL, NULL, 2, NULL, 0, 'Freeze for at least 3 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA302D8D7275D44480CD977F230B1BC7', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 14, NULL, NULL, 2, NULL, 0, 'Remove and push cheesecakes out of silicone molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5626DA1F62F99B4D8FE307170D63AB26', X'D749F3013A2DE142B86C5DB42DD3665A', X'D749F3013A2DE142B86C5DB42DD3665A', 15, NULL, NULL, 2, NULL, 0, 'Cover with paper towels and refrigerate until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C43AD453AF8214498909BD7E1806321', X'317FCD42866B9D40AAC0360C43DC9187', X'317FCD42866B9D40AAC0360C43DC9187', 0, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes and one pink heart sprinkle nose to each of 12 black candy melt wafers using the melted white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'755C7CD94C624941B1AAE800517DE599', X'317FCD42866B9D40AAC0360C43DC9187', X'317FCD42866B9D40AAC0360C43DC9187', 1, NULL, NULL, 2, NULL, 0, 'Cut two small tear drop shapes out of each of the remaining 12 black candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96A0B09D3825514E87F1D5D2D06C2107', X'317FCD42866B9D40AAC0360C43DC9187', X'317FCD42866B9D40AAC0360C43DC9187', 2, NULL, NULL, 2, NULL, 0, 'Reserve one tablespoon of the melted white chocolate for later use then spread the remaining white chocolate in an even layer on a parchment lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEB1813AD1E4DB41B906A1CC838A4D15', X'317FCD42866B9D40AAC0360C43DC9187', X'317FCD42866B9D40AAC0360C43DC9187', 3, NULL, NULL, 2, NULL, 0, 'Press 12 chocolate lamb heads onto the white chocolate, leaving plenty of room around each.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D3DE4DCB0DEB041A82D98168B7CC59E', X'317FCD42866B9D40AAC0360C43DC9187', X'317FCD42866B9D40AAC0360C43DC9187', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle tiny marshmallows all over the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C65E3ACAE70494CA011D86A9E6FA267', X'317FCD42866B9D40AAC0360C43DC9187', X'317FCD42866B9D40AAC0360C43DC9187', 5, NULL, NULL, 2, NULL, 0, 'Let sit for about 5 minutes until the chocolate sets soft, then cut the chocolate into bark pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B26AB70974E2114FAAA916EB76D9B06E', X'317FCD42866B9D40AAC0360C43DC9187', X'317FCD42866B9D40AAC0360C43DC9187', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for about 5 more minutes until the chocolate sets hard.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'968D5F651F7C1247BE234B91A6D5F7B2', X'317FCD42866B9D40AAC0360C43DC9187', X'317FCD42866B9D40AAC0360C43DC9187', 7, NULL, NULL, 2, NULL, 0, 'Use the reserved white chocolate to attach two candy ears on either side of each lamb head.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81B083CF4DED304B9E7C925DFAD37D66', X'317FCD42866B9D40AAC0360C43DC9187', X'317FCD42866B9D40AAC0360C43DC9187', 8, NULL, NULL, 2, NULL, 0, 'Let the chocolate dry, and the bark is ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CFB9C4C3E0DA34AB030F4D6C83A30A0', X'51755CDBFA5FB34682D84F84CFD0DCA7', X'51755CDBFA5FB34682D84F84CFD0DCA7', 0, NULL, NULL, 2, NULL, 0, 'Split Oreo Cookies in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CEB25A0DA2C0E04BABB7FB3F38CBBEB1', X'51755CDBFA5FB34682D84F84CFD0DCA7', X'51755CDBFA5FB34682D84F84CFD0DCA7', 1, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of white chocolate on the inside of both halves of the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35C8CF6A6AB9E8408F23D1810F5EEE0C', X'51755CDBFA5FB34682D84F84CFD0DCA7', X'51755CDBFA5FB34682D84F84CFD0DCA7', 2, NULL, NULL, 2, NULL, 0, 'Immediate press a lollipop stick on one half and press the halves back together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CB30A8575B2B646890C05764E262F1B', X'51755CDBFA5FB34682D84F84CFD0DCA7', X'51755CDBFA5FB34682D84F84CFD0DCA7', 3, NULL, NULL, 2, NULL, 0, 'Allow the white chocolate to harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29A9F912AAEAF7428D91845BF91160DB', X'51755CDBFA5FB34682D84F84CFD0DCA7', X'51755CDBFA5FB34682D84F84CFD0DCA7', 4, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes and one pink heart sprinkle nose to each of 6 black candy melt wafers using the melted white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6A7B429965CDE48B83A980D4D26ADAB', X'51755CDBFA5FB34682D84F84CFD0DCA7', X'51755CDBFA5FB34682D84F84CFD0DCA7', 5, NULL, NULL, 2, NULL, 0, 'Cut two small tear drop shapes out of each of the remaining 12 black candy melts creating 12 lamb ears and 12 lamb legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DEDF15C2E10B144AF492AD3088CFC71', X'51755CDBFA5FB34682D84F84CFD0DCA7', X'51755CDBFA5FB34682D84F84CFD0DCA7', 6, NULL, NULL, 2, NULL, 0, 'Dip an Oreo lollipop in white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96047F5BFA5D2F4DA88AC9BE5DA96B4E', X'51755CDBFA5FB34682D84F84CFD0DCA7', X'51755CDBFA5FB34682D84F84CFD0DCA7', 7, NULL, NULL, 2, NULL, 0, 'Press one of the chocolate wafer lamb faces in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B13087E6768FB4BB4EABCADC206B171', X'51755CDBFA5FB34682D84F84CFD0DCA7', X'51755CDBFA5FB34682D84F84CFD0DCA7', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle tiny marshmallows all over the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C3E42B15CF15E4BA73E384B670633A6', X'51755CDBFA5FB34682D84F84CFD0DCA7', X'51755CDBFA5FB34682D84F84CFD0DCA7', 9, NULL, NULL, 2, NULL, 0, 'Let dry completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B82188F7844393478972759A39EF31CB', X'51755CDBFA5FB34682D84F84CFD0DCA7', X'51755CDBFA5FB34682D84F84CFD0DCA7', 10, NULL, NULL, 2, NULL, 0, 'Repeat, creating 6 Oreo Lamb Pops.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E7F1AB4EA24D941BF11CAA1952A4FF9', X'51755CDBFA5FB34682D84F84CFD0DCA7', X'51755CDBFA5FB34682D84F84CFD0DCA7', 11, NULL, NULL, 2, NULL, 0, 'Use melted white chocolate to attach two chocolate ears and two chocolate legs to each lamb pop.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A9917705B6C944A8AB791AF8ACCE804', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 0, NULL, NULL, 2, NULL, 0, 'Break 1/2 inch off 20 of the pretzels and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A53FC90F2F3B94987251AC187211555', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 1, NULL, NULL, 2, NULL, 0, 'Arrange the remaining pretzels in groups of 5.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B56A615F618BD246B08554AF2B1FC727', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 2, NULL, NULL, 2, NULL, 0, 'Dip one pretzel in yellow candy melts, leaving top of pretzel un-coated.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83851596B2958F4EA22E057776693BDE', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 3, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D59F395062ADCC40839E3B5CD4AC2F00', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 4, NULL, NULL, 2, NULL, 0, 'Dip and set 4 more pretzels next to the first one, creating the chick''s body.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69006629AC9DC548BFB79EAF0B26C1E5', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 5, NULL, NULL, 2, NULL, 0, 'Immediately attach 2 candy eyes, 1 orange sunflower beak, and 2 sprinkle feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE5DFAFCB2AC9A40BB8C036C2C4EC197', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 6, NULL, NULL, 2, NULL, 0, 'Freeze for about 5 minutes just until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BEF5F304102BC47A0A8824E814BAA08', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 7, NULL, NULL, 2, NULL, 0, 'Repeat, creating 20 pretzel chicks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C8DC11F5842534AA41F5951B5DA3314', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 8, NULL, NULL, 2, NULL, 0, 'Remove and allow to rest for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8634C7B9FEED01429F9C08EBF30B8AF4', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 9, NULL, NULL, 2, NULL, 0, 'Dip broken pretzels in blue candy melts, coating them completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84B8ECF1A0ADBE4690300E31113751E3', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 10, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet, freeze until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29C6B199FE0C034C800E51A2A6662CD6', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 11, NULL, NULL, 2, NULL, 0, 'Draw a green dot onto the center of each daisy sprinkle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EE9D6E25BE9C04DA12E0C0DAB824860', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 12, NULL, NULL, 2, NULL, 0, 'Dip the un-coated ends of the pretzel chicks into blue candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5591F708EA19A24AB5306C886B9EC908', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 13, NULL, NULL, 2, NULL, 0, 'Set one blue pretzel over the bottom edge of the wet blue candy melts, creating a hat brim.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A2010F7B85AFF4A8F454C25AA8044F6', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 14, NULL, NULL, 2, NULL, 0, 'Attach one daisy sprinkle to the hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77C9CA979DD514489F1A3A97CDDC4D64', X'26265BB45F7E4B44B9ACCFDD05E13C87', X'26265BB45F7E4B44B9ACCFDD05E13C87', 15, NULL, NULL, 2, NULL, 0, 'Freeze for 2-3 minutes until the blue candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C096509514E9C4F9ECCDB777A9D4C8D', X'D08EC70760671E4FB1E6374DC9F2233E', X'D08EC70760671E4FB1E6374DC9F2233E', 0, NULL, NULL, 2, NULL, 0, 'Combine white confectionery coating and peanut butter chips in a microwave safe mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E14B36B807C0864EBA51F99193CF9462', X'D08EC70760671E4FB1E6374DC9F2233E', X'D08EC70760671E4FB1E6374DC9F2233E', 1, NULL, NULL, 2, NULL, 0, 'Heat for 15-30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BCA80B394981845917C55FD31817B5F', X'D08EC70760671E4FB1E6374DC9F2233E', X'D08EC70760671E4FB1E6374DC9F2233E', 2, NULL, NULL, 2, NULL, 0, 'Stir in the peanut butter and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB611ADD75881942A196FAC875FB989D', X'D08EC70760671E4FB1E6374DC9F2233E', X'D08EC70760671E4FB1E6374DC9F2233E', 3, NULL, NULL, 2, NULL, 0, 'Pour it into a shallow bowl and let sit for several hours until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'713DDF819EAEC5459E287D026D82254B', X'D08EC70760671E4FB1E6374DC9F2233E', X'D08EC70760671E4FB1E6374DC9F2233E', 4, NULL, NULL, 2, NULL, 0, 'Scoop out 18-20 heaping tablespoonfuls and roll into balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3B46037DA5628438AA3DAB769608002', X'CED8201DF2399649B8D3BBFFC6D12FE7', X'CED8201DF2399649B8D3BBFFC6D12FE7', 0, NULL, NULL, 2, NULL, 0, 'To make the bunny ears, pinch off 40 small pieces of your milk modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5A97B110E7FCF42A61483048F13E84F', X'CED8201DF2399649B8D3BBFFC6D12FE7', X'CED8201DF2399649B8D3BBFFC6D12FE7', 1, NULL, NULL, 2, NULL, 0, 'Roll into balls then into a teardrop shape then flatten and curve up the edges and bend 20 to the right and 20 to the left.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B3B0B8A1F7BFA4CBBB755A4334B98F5', X'CED8201DF2399649B8D3BBFFC6D12FE7', X'CED8201DF2399649B8D3BBFFC6D12FE7', 2, NULL, NULL, 2, NULL, 0, 'Pinch off smaller pieces of pink modeling chocolate and do the same making the pink fur for inside the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C83963309DC3C40B6507A7774E939C4', X'CED8201DF2399649B8D3BBFFC6D12FE7', X'CED8201DF2399649B8D3BBFFC6D12FE7', 3, NULL, NULL, 2, NULL, 0, 'Brush the back of the pink piece lightly with water and press it onto the brown ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A526294862A3AE46BA7D40525AFF82E2', X'CED8201DF2399649B8D3BBFFC6D12FE7', X'CED8201DF2399649B8D3BBFFC6D12FE7', 4, NULL, NULL, 2, NULL, 0, 'Lightly dust your work surface with powdered sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80045B2752D4474E84E6303838F268E6', X'CED8201DF2399649B8D3BBFFC6D12FE7', X'CED8201DF2399649B8D3BBFFC6D12FE7', 5, NULL, NULL, 2, NULL, 0, 'Roll the milk modeling chocolate to about 1/8` thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'811DBB7FB509CD40836E9C057BA7155C', X'CED8201DF2399649B8D3BBFFC6D12FE7', X'CED8201DF2399649B8D3BBFFC6D12FE7', 6, NULL, NULL, 2, NULL, 0, 'Use a small oval metal cutter to cut out 40 feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B5BF1C364B816418237E5845540566C', X'ECAD97F4347FF4458D0285923275A980', X'ECAD97F4347FF4458D0285923275A980', 0, NULL, NULL, 2, NULL, 0, 'Set a double boiler filled with 1 inch of water over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'824F6FB864516B41BCD436F9EF2E9A96', X'ECAD97F4347FF4458D0285923275A980', X'ECAD97F4347FF4458D0285923275A980', 1, NULL, NULL, 2, NULL, 0, 'Fill the top bowl with your chopped milk chocolate, heavy whipping cream and corn syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53C09ACED4D91044A316E2B02DBD8CF7', X'ECAD97F4347FF4458D0285923275A980', X'ECAD97F4347FF4458D0285923275A980', 2, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to melt, stirring often.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89E0D18FE2917E48BFF2AFD2F7AD5F8E', X'ECAD97F4347FF4458D0285923275A980', X'ECAD97F4347FF4458D0285923275A980', 3, NULL, NULL, 2, NULL, 0, 'Pour some of your milk chocolate ganache into a drinking glass for ease of dipping.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F667114FE4807429DC252DBE442668B', X'ECAD97F4347FF4458D0285923275A980', X'ECAD97F4347FF4458D0285923275A980', 4, NULL, NULL, 2, NULL, 0, 'Place two bunny feet next to each other on a parchment paper or Silpat lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0FB5C8F6A76454F95A8DDBC080CBF2D', X'E7978695D97245468CC28F408E5F1022', X'E7978695D97245468CC28F408E5F1022', 0, NULL, NULL, 2, NULL, 0, 'Dip one peanut butter ball into chocolate ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26D3A7872DCFF148B5365277AD9D1D65', X'E7978695D97245468CC28F408E5F1022', X'E7978695D97245468CC28F408E5F1022', 1, NULL, NULL, 2, NULL, 0, 'Remove and allow excess ganache to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FFDD9C075A7DF4E8FE2A9D2CA761C9D', X'E7978695D97245468CC28F408E5F1022', X'E7978695D97245468CC28F408E5F1022', 2, NULL, NULL, 2, NULL, 0, 'Set the peanut butter fudge ball on top of two of the modeling chocolate bunny feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6436BC6DE385214699C8554DC9A1CBA1', X'E7978695D97245468CC28F408E5F1022', X'E7978695D97245468CC28F408E5F1022', 3, NULL, NULL, 2, NULL, 0, 'Create spikes all over the ganache (see tutorial for instructions.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC92AF07E3518146ABF09403B2B2BF31', X'E7978695D97245468CC28F408E5F1022', X'E7978695D97245468CC28F408E5F1022', 4, NULL, NULL, 2, NULL, 0, 'Press the ears onto the top of the ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8FC1D6C9EC6854A82B7E184E3022770', X'E7978695D97245468CC28F408E5F1022', X'E7978695D97245468CC28F408E5F1022', 5, NULL, NULL, 2, NULL, 0, 'Press on two eyes and a heart shaped nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FB784E7712CA844BC3F5B2B8D301C87', X'E7978695D97245468CC28F408E5F1022', X'E7978695D97245468CC28F408E5F1022', 6, NULL, NULL, 2, NULL, 0, 'Repeat creating more bunnies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'368E3CE6EFCAC0429B6421F971493660', X'E7978695D97245468CC28F408E5F1022', X'E7978695D97245468CC28F408E5F1022', 7, NULL, NULL, 2, NULL, 0, 'Allow your chocolate bunnies to sit at room temperature to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A833D08F5FDED428B86619ECC466271', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 0, NULL, NULL, 2, NULL, 0, 'Pour candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A32E7712AC80841B46E02E6A18EE1AC', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 15-30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D64DDF90BD3E34EB3C20F3801925905', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 2, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B7E2217F3FE53428E424A6178B8EB57', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 3, NULL, NULL, 2, NULL, 0, 'Paint a 3/8-1/2 inch oval onto the paper and immediately press one pink heart into the center and three pink sugar pearls around the heart for the toes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F2981E383C747469151E11126C92A4B', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 4, NULL, NULL, 2, NULL, 0, 'Repeat creating 100 bunny paws.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35C1D4346A4417458BA8BD11A621C070', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 5, NULL, NULL, 2, NULL, 0, 'Pop the candy into the freezer for a few minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58E80CB79AA1D444BB6F167A809EDA09', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 6, NULL, NULL, 2, NULL, 0, 'Roll the marshmallows in the palm of your hand until they are round.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3A8F30BF0478E4AB14586DCB0996CC9', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 7, NULL, NULL, 2, NULL, 0, 'Set the pretzel rings on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17DD72E17A59B64C808FDB4F14EE0808', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 8, NULL, NULL, 2, NULL, 0, 'Re-heat the peanut butter candy melts for 15 seconds, then pour it into a squeeze bottle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B167D1BBE13B847BE29702631AF0E97', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 9, NULL, NULL, 2, NULL, 0, 'Pipe peanut butter candy into the center of a pretzel ring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'018F4D0ECD9F544C950BB9CD17BB2B86', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 10, NULL, NULL, 2, NULL, 0, 'Immediately attach a marshmallow tail and two candy bunny feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D56C451411F3D428F549E5686334E75', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 11, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67B77F7DDD15B14F8EB56C0038FE9A6F', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 12, NULL, NULL, 2, NULL, 0, 'Freeze for 5-10 minutes until the candy is hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF6EDA7B0BD3494AAB00EB9CD760E745', X'08F22C5BCC8A924AB6327619E359396B', X'08F22C5BCC8A924AB6327619E359396B', 13, NULL, NULL, 2, NULL, 0, 'Peel off the parchment paper and they are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C88A6F80198FA7408E938C32C478A9F0', X'CBCEF93557D9A244B44F4686A31316D2', X'CBCEF93557D9A244B44F4686A31316D2', 0, NULL, NULL, 2, NULL, 0, 'Pour marshmallows onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FE5E0C0ED4B91478204DD6D50BAF338', X'CBCEF93557D9A244B44F4686A31316D2', X'CBCEF93557D9A244B44F4686A31316D2', 1, NULL, NULL, 2, NULL, 0, 'Break pretzel rods in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'929EC2D19B0E6C4187B9F27713380893', X'CBCEF93557D9A244B44F4686A31316D2', X'CBCEF93557D9A244B44F4686A31316D2', 2, NULL, NULL, 2, NULL, 0, 'Heat candy melts on high power in microwave for 15-30 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0209EA6AC27E2748B634A18D2F7C4806', X'CBCEF93557D9A244B44F4686A31316D2', X'CBCEF93557D9A244B44F4686A31316D2', 3, NULL, NULL, 2, NULL, 0, 'Dip one pretzel into the white candy coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85EEB383E91D8E41B78B91578ED424A8', X'CBCEF93557D9A244B44F4686A31316D2', X'CBCEF93557D9A244B44F4686A31316D2', 4, NULL, NULL, 2, NULL, 0, 'Remove and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A3A0611EE56F7438BAF109A83EB9E61', X'CBCEF93557D9A244B44F4686A31316D2', X'CBCEF93557D9A244B44F4686A31316D2', 5, NULL, NULL, 2, NULL, 0, 'Set onto the marshmallows.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA0F28B736764B4180387A0485810B6E', X'CBCEF93557D9A244B44F4686A31316D2', X'CBCEF93557D9A244B44F4686A31316D2', 6, NULL, NULL, 2, NULL, 0, 'Press on candy lamb head onto the top of the pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'501AB05986D96F4088D5B247D6C0115F', X'CBCEF93557D9A244B44F4686A31316D2', X'CBCEF93557D9A244B44F4686A31316D2', 7, NULL, NULL, 2, NULL, 0, 'Press marshmallows all around the pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EF0D4AB8CF91A4F9F385E121BB46C35', X'CBCEF93557D9A244B44F4686A31316D2', X'CBCEF93557D9A244B44F4686A31316D2', 8, NULL, NULL, 2, NULL, 0, 'Let the pretzel sit in the marshmallows to dry before moving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9F6EA4591304343B3FD0BDFCBEC4B7A', X'CBCEF93557D9A244B44F4686A31316D2', X'CBCEF93557D9A244B44F4686A31316D2', 9, NULL, NULL, 2, NULL, 0, 'Repeat creating 12 Lamb Pretzel Pops.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3247FC955E590147A0A471A6F2BDB518', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 0, NULL, NULL, 2, NULL, 0, 'Combine white confectionery coating wafers and peanut butter chips in a microwave safe bowl. Heat on high power for 15-30 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'955E9FD85A9783458243343FD7FB604A', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 1, NULL, NULL, 2, NULL, 0, 'Stir in peanut butter and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D69FDAC35D8074092ADD25D32FB3BD7', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 2, NULL, NULL, 2, NULL, 0, 'Allow it to cool enough so that it no longer feels warm to the touch.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50F863691B3D5046AECC0694D4703F64', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 3, NULL, NULL, 2, NULL, 0, 'Paint a thin shell of melted chocolate into plastic egg molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFAD34BEBD151E4E81DF024C48AF1E8B', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B66DA4715ACC924C93B9F7D5E625F329', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 5, NULL, NULL, 2, NULL, 0, 'Pour peanut butter fudge into chocolate shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BFE76321DD9CB44A770BFE1DC0AF876', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate until firm. Then allow to warm up to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0FE2D34321B0E841B685A5B4D12B19EA', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 7, NULL, NULL, 2, NULL, 0, 'Top with a thin layer of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D808EB7BA311D54BB830800242A0D4FD', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 8, NULL, NULL, 2, NULL, 0, 'Refrigerate until firm then un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DFDB0FF615C9614ABA3E5195B88E6593', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 9, NULL, NULL, 2, NULL, 0, 'Pipe chocolate ears and paws onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A074155C1B6824EB228480FF8F3A0B2', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 10, NULL, NULL, 2, NULL, 0, 'Add pink hearts and confetti sprinkles to paws.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1844F4BC3F8E0E448A053559E4ABBEE7', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 11, NULL, NULL, 2, NULL, 0, 'Refrigerate until hard.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23EEC2C524B1CA4FB9FB0C98675ABAD7', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 12, NULL, NULL, 2, NULL, 0, 'Remove and brush pink luster dust over ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CA4F0F9B01C1F42943CD977A0A9B9A9', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 13, NULL, NULL, 2, NULL, 0, 'Spoon some chocolate onto a parchment paper lined baking sheet and set one egg on top, then sprinkle on green sprinkles and hold egg in place until it sticks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CAA86E92951816438EE28379DE0C4883', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 14, NULL, NULL, 2, NULL, 0, 'Decorate each egg by adding 2 candy eyes, a pink candy nose, 2 candy ears, and 2 paws using melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B1BF81E478D5A47B70B7C9D952C2984', X'8F2D670151F0A34D82A94E65EFF4E17F', X'8F2D670151F0A34D82A94E65EFF4E17F', 15, NULL, NULL, 2, NULL, 0, 'Draw on smile and whiskers using black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D4B12B4D2D5494EA6A4D3033276DE05', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 0, NULL, NULL, 2, NULL, 0, 'Fill a 9x13 baking dish 1/2 way with water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BF9C864B93A5D4892ABFB8213E9B850', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 1, NULL, NULL, 2, NULL, 0, 'Set dish on bottom rack in oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'274F7911FB84944FA7C2BCAFD114033C', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 2, NULL, NULL, 2, NULL, 0, 'Arrange other rack in the middle of the oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F2D0B534DC5494E914E060B6DA7C07E', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 3, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'054FD9832797EF4F89851D1633A3E8FF', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 4, NULL, NULL, 2, NULL, 0, 'Beat cream cheese on medium speed using an electric mixer until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C33D4DA056BA2A488355A9A230290DB4', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 5, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32556159DD67F142937AF9246F87EB90', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 6, NULL, NULL, 2, NULL, 0, 'Add egg and vanilla and beat until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C74500CFCF01147933D400E6BC6CFB7', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 7, NULL, NULL, 2, NULL, 0, 'Divide the cheesecake batter equally among three bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6687C87A3A26940B01C3B63205B132A', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 8, NULL, NULL, 2, NULL, 0, 'Stir raspberry puree into one bowl of batter and blueberry puree into another.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'528269723C71ED498AE193340ABCECFE', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 9, NULL, NULL, 2, NULL, 0, 'In the final bowl add the lemon juice, lemon zest, and egg yolk.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9A2CE220F2BF24392AA7CA33FDBD022', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 10, NULL, NULL, 2, NULL, 0, 'Fill four Peeps cavities in the silicone mold with each color of cheesecake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1FC2A969A60B0428315350C974B212F', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 11, NULL, NULL, 2, NULL, 0, 'Bake the cheesecake Peeps for 14 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'965140E325475540976E7ABFCC811DFC', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 12, NULL, NULL, 2, NULL, 0, 'Remove and allow to cool at room temperature for 30 minutes, then place in the freezer for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87AE37A1D8EA804C8DB45201E750C51F', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 13, NULL, NULL, 2, NULL, 0, 'Remove and un-mold cheesecakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E02DE8442A4964409387A56F2F2CA92C', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 14, NULL, NULL, 2, NULL, 0, 'Refrigerate until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17D05CEC695720408EBAD86825B6E338', X'9E5AC45600EFBA429BCBAB9C689A5570', X'9E5AC45600EFBA429BCBAB9C689A5570', 15, NULL, NULL, 2, NULL, 0, 'Add chocolate chip eyes and nose to each Peep.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E98D01BA535E24086404DC4CCC94896', X'891872D83070A4419ED8BE16102F6EF2', X'891872D83070A4419ED8BE16102F6EF2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'278EF65DED9FA044A592A41CC8982EDE', X'891872D83070A4419ED8BE16102F6EF2', X'891872D83070A4419ED8BE16102F6EF2', 1, NULL, NULL, 2, NULL, 0, 'Unroll the pizza dough and lay it over the Peeps pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF8CE02158273B45B675433654259DD1', X'891872D83070A4419ED8BE16102F6EF2', X'891872D83070A4419ED8BE16102F6EF2', 2, NULL, NULL, 2, NULL, 0, 'Cut the dough around the edge of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'313C7C5FA33B2840AD1149F41D68E89D', X'891872D83070A4419ED8BE16102F6EF2', X'891872D83070A4419ED8BE16102F6EF2', 3, NULL, NULL, 2, NULL, 0, 'Bake for 8 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7D8BDA39058404182A9D7EE6D1F7EA5', X'891872D83070A4419ED8BE16102F6EF2', X'891872D83070A4419ED8BE16102F6EF2', 4, NULL, NULL, 2, NULL, 0, 'Remove from oven and top with ham and eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDE97592742EF64B970DAA52011C9A17', X'891872D83070A4419ED8BE16102F6EF2', X'891872D83070A4419ED8BE16102F6EF2', 5, NULL, NULL, 2, NULL, 0, 'Bake for 6-8 minutes until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4FBA36E234A364D8C2CFF1E549D802A', X'891872D83070A4419ED8BE16102F6EF2', X'891872D83070A4419ED8BE16102F6EF2', 6, NULL, NULL, 2, NULL, 0, 'Remove from oven and sprinkle grated cheese over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40D6AF8F4FE8134CA16DAC8E415498D4', X'891872D83070A4419ED8BE16102F6EF2', X'891872D83070A4419ED8BE16102F6EF2', 7, NULL, NULL, 2, NULL, 0, 'Slice jumbo olives and cut into 3 small circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE94E6FBD2521545856BF3CE7EE5EFC1', X'891872D83070A4419ED8BE16102F6EF2', X'891872D83070A4419ED8BE16102F6EF2', 8, NULL, NULL, 2, NULL, 0, 'Place the olive circles on your pizza to create two eyes and one nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F48A069CF1025F49B0312E17E3810511', X'891872D83070A4419ED8BE16102F6EF2', X'891872D83070A4419ED8BE16102F6EF2', 9, NULL, NULL, 2, NULL, 0, 'Serve while hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14560B6C35D33444BE32E3C18611A2E8', X'D1E747F062A5EA43894957E3110615B6', X'D1E747F062A5EA43894957E3110615B6', 0, NULL, NULL, 2, NULL, 0, 'Heat the Light Cocoa Candy Melts for 15-30 second increments stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68B636989957904AA7D7A5B81A3EB4A3', X'D1E747F062A5EA43894957E3110615B6', X'D1E747F062A5EA43894957E3110615B6', 1, NULL, NULL, 2, NULL, 0, 'Stir in the Reese''s Chocolate Peanut Butter Spread until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EFFF0166E012347BCF45A0C7F0C46E3', X'D1E747F062A5EA43894957E3110615B6', X'D1E747F062A5EA43894957E3110615B6', 2, NULL, NULL, 2, NULL, 0, 'Reserve 1 tablespoon of the fudge.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'544510F43EA44848BEF6B39BCD8AAD9F', X'D1E747F062A5EA43894957E3110615B6', X'D1E747F062A5EA43894957E3110615B6', 3, NULL, NULL, 2, NULL, 0, 'Pour the remaining fudge into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7FCE39799DD4340888E4E5E7D81F40A', X'D1E747F062A5EA43894957E3110615B6', X'D1E747F062A5EA43894957E3110615B6', 4, NULL, NULL, 2, NULL, 0, 'Snip off the tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88F94726987991488E420EBD3AF6F22F', X'D1E747F062A5EA43894957E3110615B6', X'D1E747F062A5EA43894957E3110615B6', 5, NULL, NULL, 2, NULL, 0, 'Pipe fudge into the 24 bunny cavities in the silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D9268D48294354AB90F9809654CD093', X'D1E747F062A5EA43894957E3110615B6', X'D1E747F062A5EA43894957E3110615B6', 6, NULL, NULL, 2, NULL, 0, 'Freeze for 15-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44AE57798504E449857CC8E473B0459B', X'D1E747F062A5EA43894957E3110615B6', X'D1E747F062A5EA43894957E3110615B6', 7, NULL, NULL, 2, NULL, 0, 'Un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF26B13BD4A4BD4A90062997304C9D36', X'D1E747F062A5EA43894957E3110615B6', X'D1E747F062A5EA43894957E3110615B6', 8, NULL, NULL, 2, NULL, 0, 'Thaw for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D78AB3FF0A58A4F9E51EBD8CFD694A0', X'D1E747F062A5EA43894957E3110615B6', X'D1E747F062A5EA43894957E3110615B6', 9, NULL, NULL, 2, NULL, 0, 'Heat the reserved fudge in the microwave for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C12C7363C99E5849875F8893AD6A99D3', X'D1E747F062A5EA43894957E3110615B6', X'D1E747F062A5EA43894957E3110615B6', 10, NULL, NULL, 2, NULL, 0, 'Attach two white confetti sprinkles and one pink confetti sprinkle to each bunny.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'308BCC81C44C1F428B06E4659C23A14D', X'D1E747F062A5EA43894957E3110615B6', X'D1E747F062A5EA43894957E3110615B6', 11, NULL, NULL, 2, NULL, 0, 'Use a candy decorating pen to draw pupils onto each white sprinkle, creating eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D5D54C7614ACD49903CE93AA29137F4', X'D1E747F062A5EA43894957E3110615B6', X'D1E747F062A5EA43894957E3110615B6', 12, NULL, NULL, 2, NULL, 0, 'Draw on a smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D85A96E8252EF4EBEB8DAB3529AFE54', X'E5877ED087B4E04F90B16A0E4E651A78', X'E5877ED087B4E04F90B16A0E4E651A78', 0, NULL, NULL, 2, NULL, 0, 'Bring one inch of water in a large pot to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62F024887391A24ABAF8FE7116A95034', X'E5877ED087B4E04F90B16A0E4E651A78', X'E5877ED087B4E04F90B16A0E4E651A78', 1, NULL, NULL, 2, NULL, 0, 'Set eggs in a steamer basket and lower into pot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50A8364C7611F44DA1E324FC2DAAD83F', X'E5877ED087B4E04F90B16A0E4E651A78', X'E5877ED087B4E04F90B16A0E4E651A78', 2, NULL, NULL, 2, NULL, 0, 'Cover pot with lid and set timer for 13 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C09191EE6F7C63478C4A4CD22260A44A', X'E5877ED087B4E04F90B16A0E4E651A78', X'E5877ED087B4E04F90B16A0E4E651A78', 3, NULL, NULL, 2, NULL, 0, 'Remove lid and take steamer basket out of the pot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1956BAC5A11E2D448646729FF44C553E', X'E5877ED087B4E04F90B16A0E4E651A78', X'E5877ED087B4E04F90B16A0E4E651A78', 4, NULL, NULL, 2, NULL, 0, 'Immediately place eggs in a bowl of ice water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BFC9946D788E3489316AB9B6E401E77', X'E5877ED087B4E04F90B16A0E4E651A78', X'E5877ED087B4E04F90B16A0E4E651A78', 5, NULL, NULL, 2, NULL, 0, 'Allow to cool for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50A7CA8AF9DA8D459CBBF6C3A3973E3C', X'E5877ED087B4E04F90B16A0E4E651A78', X'E5877ED087B4E04F90B16A0E4E651A78', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile cut carrots into slices and sticks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F28839EC6F132F4DB90DA80E1F4D8570', X'E5877ED087B4E04F90B16A0E4E651A78', X'E5877ED087B4E04F90B16A0E4E651A78', 7, NULL, NULL, 2, NULL, 0, 'Cut the slices using a small `E` cookie cutter (2 per egg).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'520ACDEE4D165C428ECBA3D62FE0ECA9', X'E5877ED087B4E04F90B16A0E4E651A78', X'E5877ED087B4E04F90B16A0E4E651A78', 8, NULL, NULL, 2, NULL, 0, 'Cut the sticks so that each end is pointed ( 2 per egg).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B82716B3DC4EC479A8D2AA78D87B843', X'E5877ED087B4E04F90B16A0E4E651A78', X'E5877ED087B4E04F90B16A0E4E651A78', 9, NULL, NULL, 2, NULL, 0, 'Push a skewer into the center of each `E` creating a hole.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EAC90CCDC6637438C5BB5EA3D9B06BC', X'E5877ED087B4E04F90B16A0E4E651A78', X'E5877ED087B4E04F90B16A0E4E651A78', 10, NULL, NULL, 2, NULL, 0, 'Poke a carrot stick into each `E` creating a chicken leg and foot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D208EB67830044C823FDF3DAEBAC832', X'E5877ED087B4E04F90B16A0E4E651A78', X'E5877ED087B4E04F90B16A0E4E651A78', 11, NULL, NULL, 2, NULL, 0, 'Remove eggs from ice water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D389ACBFC44D2A4FB17077BBDDFE8667', X'E5877ED087B4E04F90B16A0E4E651A78', X'E5877ED087B4E04F90B16A0E4E651A78', 12, NULL, NULL, 2, NULL, 0, 'Poke two carrot chicken legs into each egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0C5A37D90237244B42B1B0DEC830CB6', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 0, NULL, NULL, 2, NULL, 0, 'Unwrap Rice Krispie Treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C26FDC6F7F324F42A60CAC7832459A33', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 1, NULL, NULL, 2, NULL, 0, 'Place coconut in a zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'376C7B6E4AABBD4889B5D30AE5AC66A9', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 2, NULL, NULL, 2, NULL, 0, 'Add a few drops of yellow food coloring and shake to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5058E5BDF60B4647B72517D636835319', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 3, NULL, NULL, 2, NULL, 0, 'Melt yellow candy melts and pour into a tall glass.*');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47A04EEE5198B04791B9781F8709F7A1', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 4, NULL, NULL, 2, NULL, 0, 'Dip tip of a lollipop stick into the yellow candy melts then insert into one end of a rice krispie treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAC1FBE43DAA734C8BD09C2D506280AD', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 5, NULL, NULL, 2, NULL, 0, 'Slide a paper straw over the stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D195001DD9AA64490C51C330343E74F', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 6, NULL, NULL, 2, NULL, 0, 'Repeat with all 6 treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99E41905FA73CC489E05F28CB669CC41', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 7, NULL, NULL, 2, NULL, 0, 'Allow the candy coating to dry then one at a time dip the rice krispies treats into the yellow candy coating and immediately sprinkle yellow colored coconut all over the treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63D9D3A6F9D97447AF9F60E5ECA6EF83', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 8, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6205293D23C4C418CD15EB92DFF05BE', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 9, NULL, NULL, 2, NULL, 0, 'Freeze for about 5 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DA32FB908B12141B12CCBA421762D2B', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 10, NULL, NULL, 2, NULL, 0, 'Melt orange candy melts then pour into a squeeze bottle or zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'364EF50DAAB4F447AABCD7E15528EE93', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 11, NULL, NULL, 2, NULL, 0, 'Pipe an orange beak and two feet onto each chick.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1D661379E06F2469FDEDC0159836065', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 12, NULL, NULL, 2, NULL, 0, 'Dab some orange candy coating onto the back of the candy eyes and attach two above the beak on each chick.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B90A8C585D27184CA634353FD93BA13D', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 13, NULL, NULL, 2, NULL, 0, 'Freeze for a few minutes until the orange candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A5DC62F08A0A04D8B81E232E23E3478', X'BC6667EEBE215E4290C8B7DBC3CDE589', X'BC6667EEBE215E4290C8B7DBC3CDE589', 14, NULL, NULL, 2, NULL, 0, '*See Hungry Happenings'' Chocolate Making Tips page for detailed instructions on melting Confectionery Coating (Candy Melts)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A298EEB2A49AC248865E1C5D3A2EE0EB', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D65870FCAFB93143831FDA89196251CE', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 1, NULL, NULL, 2, NULL, 0, 'Spray a non-stick whoopie pie pan with 12 round cavities with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64CDCF0382EB5C4B9774ECE99B4C8BB0', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 2, NULL, NULL, 2, NULL, 0, 'Mix brownie mix, egg, oil, and water (and a fudge packet if included) according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E23BF82307DB474FA06AF827C2E47588', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 3, NULL, NULL, 2, NULL, 0, 'Equally divide batter among the 12 round whoopie pie cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E94A75060722DE4FAB1EF85C2DCB2B00', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 4, NULL, NULL, 2, NULL, 0, 'Bake for 22-28 minutes until the edges look set and the center is not longer wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8C61FB9C02C8E419CE18242B32F2523', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 5, NULL, NULL, 2, NULL, 0, 'Remove and allow to cool completely before removing from pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C35B5FB6279285408DA053C64DBA6FE1', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 6, NULL, NULL, 2, NULL, 0, 'Roll out bright pink modeling chocolate to about 1/16th of an inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AC2CE905E745B4FBFA75AE62DAF510D', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 7, NULL, NULL, 2, NULL, 0, 'Cut 24 circles using a 1/2 inch round plunger or metal cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2632596ADB62B45B6F9443F5866FEF7', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 8, NULL, NULL, 2, NULL, 0, 'Cut 72 circles using a 1/4 inch round plunger cutter or straw.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA675E532A9A3042BA24A9ED23593E73', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 9, NULL, NULL, 2, NULL, 0, 'Brush water on one side of a 1/2 pink circle then press the wet side on top in the center of a Reese''s Egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA8CC180257A234EA4231D622C1AAEB4', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 10, NULL, NULL, 2, NULL, 0, 'Brush water on three of the smaller pink circles and press the wet sides onto the egg to create three bunny toes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'624A3250C68DB94CAD1E0BF402EC6264', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 11, NULL, NULL, 2, NULL, 0, 'Repeat, creating all 24 bunny feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D98992EC67C8BA448DFCA656AC2C73D4', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 12, NULL, NULL, 2, NULL, 0, 'To assemble the Brownie Bunny Butts:');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'945C2C078C6BBF429F5DBF38FCDA6BB2', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 13, NULL, NULL, 2, NULL, 0, 'Sprinkle some green coconut around the edge of the plate if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F0B0AEC6F177040AB742C3E940E06E8', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 14, NULL, NULL, 2, NULL, 0, 'Set the brownie in the middle of the plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86121B44B7ED2F4589A06FBEC8D33F6F', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 15, NULL, NULL, 2, NULL, 0, 'Spoon the frosting into a small zip top or pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE6DDC4EA659474AAF9136B83BEB7078', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 16, NULL, NULL, 2, NULL, 0, 'Pipe a frosting tail near one edge of the brownie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E9CD41D5D033B458C9A62864D12B051', X'6E6DD5347D34694B995C222D52637A27', X'6E6DD5347D34694B995C222D52637A27', 17, NULL, NULL, 2, NULL, 0, 'Use frosting to attach two feet opposite the tail.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A68D14C620F6F544A97A8A5A08A13959', X'0E35D891F4B7CC4D8C3E730E44ED554D', X'0E35D891F4B7CC4D8C3E730E44ED554D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0077FFF1E7AE2340BE708F09332BD949', X'0E35D891F4B7CC4D8C3E730E44ED554D', X'0E35D891F4B7CC4D8C3E730E44ED554D', 1, NULL, NULL, 2, NULL, 0, 'Wrap two layers of heavy duty tin foil around bottom and sides of a 9 inch springform pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43A8AC51330A354EB0FE4A8CFC8946C9', X'0E35D891F4B7CC4D8C3E730E44ED554D', X'0E35D891F4B7CC4D8C3E730E44ED554D', 2, NULL, NULL, 2, NULL, 0, 'Combine vanilla wafer crumbs and butter. Press into bottom of springform pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA435DAB32BB804B8B26CA59FAF017A6', X'0E35D891F4B7CC4D8C3E730E44ED554D', X'0E35D891F4B7CC4D8C3E730E44ED554D', 3, NULL, NULL, 2, NULL, 0, 'Beat cream cheese on low for about a minute until smooth and free of lumps.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD2EAE2E1B40E249A459EF5A0DBF44E9', X'0E35D891F4B7CC4D8C3E730E44ED554D', X'0E35D891F4B7CC4D8C3E730E44ED554D', 4, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy, add eggs one at a time and beat until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5348908F7342034C9A505B12A2827C2D', X'0E35D891F4B7CC4D8C3E730E44ED554D', X'0E35D891F4B7CC4D8C3E730E44ED554D', 5, NULL, NULL, 2, NULL, 0, 'Add sour cream, orange juice, carrot juice, and orange zest. Mix until just combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F07F4C96D19771469A33841CB920A32F', X'0E35D891F4B7CC4D8C3E730E44ED554D', X'0E35D891F4B7CC4D8C3E730E44ED554D', 6, NULL, NULL, 2, NULL, 0, 'Pour over cookie crust and bake in a water bath in a roasting pan for 70-75 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1FDBC634B44744A9210C0FB851E7569', X'0E35D891F4B7CC4D8C3E730E44ED554D', X'0E35D891F4B7CC4D8C3E730E44ED554D', 7, NULL, NULL, 2, NULL, 0, 'Turn oven heat off and leave cheesecake in oven for 10-25 minutes until cheesecake looks set but not wet and still jiggles just slightly in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'596FADD04462C84F88A5462449F0A0E9', X'0E35D891F4B7CC4D8C3E730E44ED554D', X'0E35D891F4B7CC4D8C3E730E44ED554D', 8, NULL, NULL, 2, NULL, 0, 'Remove from oven and allow to cool for 30 minutes in the roasting pan, then remove and allow to cool on a rack for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A86E59A51C51440B18EAF60E05267F6', X'0E35D891F4B7CC4D8C3E730E44ED554D', X'0E35D891F4B7CC4D8C3E730E44ED554D', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 8 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40AB7E73DDB60540BFC0C853BD8C0085', X'0E35D891F4B7CC4D8C3E730E44ED554D', X'0E35D891F4B7CC4D8C3E730E44ED554D', 10, NULL, NULL, 2, NULL, 0, 'Pipe green candy coating carrot tops onto parchment paper lined baking sheets. Freeze for 3-4 minute until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18AC24B63C4D2644A0CAB0D3F6755B6A', X'0E35D891F4B7CC4D8C3E730E44ED554D', X'0E35D891F4B7CC4D8C3E730E44ED554D', 11, NULL, NULL, 2, NULL, 0, 'Cut cheesecake into 12 slices. Add three carrot tops to each slice and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5905D8B1750CF45AA7539AB8A79529D', X'6E84E8D9D2D54440B26FABBC219DDE58', X'6E84E8D9D2D54440B26FABBC219DDE58', 0, NULL, NULL, 2, NULL, 0, 'Stir together the cream cheese and cheddar cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8382CED94CFAC04A88A5CCBCBD956299', X'6E84E8D9D2D54440B26FABBC219DDE58', X'6E84E8D9D2D54440B26FABBC219DDE58', 1, NULL, NULL, 2, NULL, 0, 'Reserve 2 tablespoons of this mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47CBFD487E7C894F822A797E58928C3F', X'6E84E8D9D2D54440B26FABBC219DDE58', X'6E84E8D9D2D54440B26FABBC219DDE58', 2, NULL, NULL, 2, NULL, 0, 'Stir the bacon into the remaining cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FBA41F24D939E4BA9E52132B9C91AAB', X'0F6ADC0D4E74084C99638A09C3B5AE0D', X'0F6ADC0D4E74084C99638A09C3B5AE0D', 0, NULL, NULL, 2, NULL, 0, 'Scoop out 1/2 tablespoon size scoops of the cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'607FFABA7781054FA35D8EC53B21C093', X'0F6ADC0D4E74084C99638A09C3B5AE0D', X'0F6ADC0D4E74084C99638A09C3B5AE0D', 1, NULL, NULL, 2, NULL, 0, 'Roll into the small pieces of finely shredded cheddar cheese shaping into balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A4368BAB0D7FC4890B699D46630AA82', X'0F6ADC0D4E74084C99638A09C3B5AE0D', X'0F6ADC0D4E74084C99638A09C3B5AE0D', 2, NULL, NULL, 2, NULL, 0, 'Cut 2 tear drop shapes, 2 flower shapes, and one tiny triangle from the cheddar cheese for each cheese ball chick you have made.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E9F27D5D3AEEC49A2D6B86CAE4DD657', X'0F6ADC0D4E74084C99638A09C3B5AE0D', X'0F6ADC0D4E74084C99638A09C3B5AE0D', 3, NULL, NULL, 2, NULL, 0, 'Brush orange food coloring over the triangles and flowers (or brush the coloring over the slice of cheese, allow to dry, then cut out the shapes).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E1E5CF2DAA69748AAAB4996D388DC10', X'0F6ADC0D4E74084C99638A09C3B5AE0D', X'0F6ADC0D4E74084C99638A09C3B5AE0D', 4, NULL, NULL, 2, NULL, 0, 'Cut thin slivers off the edges of the black olives and cut into tiny circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'659410F8E56ACB429BA7A8B791508DAC', X'0F6ADC0D4E74084C99638A09C3B5AE0D', X'0F6ADC0D4E74084C99638A09C3B5AE0D', 5, NULL, NULL, 2, NULL, 0, 'Attach two tear drop shaped wings to the sides of each cheese ball using the reserved cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DBDCD5AEE919647BB497498A64BEC58', X'0F6ADC0D4E74084C99638A09C3B5AE0D', X'0F6ADC0D4E74084C99638A09C3B5AE0D', 6, NULL, NULL, 2, NULL, 0, 'If needed, warm the reserved cheese ball mixture in the microwave on the defrost setting for about 10 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'741E537F3E4B5E46B99CC614E8CFD00A', X'0F6ADC0D4E74084C99638A09C3B5AE0D', X'0F6ADC0D4E74084C99638A09C3B5AE0D', 7, NULL, NULL, 2, NULL, 0, 'Attach a triangle beak and two flower shaped feet to each cheese ball then attach two black olive eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE55846317302B4596816A6DE8425A60', X'0F6ADC0D4E74084C99638A09C3B5AE0D', X'0F6ADC0D4E74084C99638A09C3B5AE0D', 8, NULL, NULL, 2, NULL, 0, 'Store in the refrigerator in an airtight container until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D8B6DB4FF341A43A0EDF798FF113ED8', X'0F6ADC0D4E74084C99638A09C3B5AE0D', X'0F6ADC0D4E74084C99638A09C3B5AE0D', 9, NULL, NULL, 2, NULL, 0, 'Set cheese balls on crackers just before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1E75F99B1D30841A470A2B5551566ED', X'A4C25B68794BEB4F9F467BC304D7742E', X'A4C25B68794BEB4F9F467BC304D7742E', 0, NULL, NULL, 2, NULL, 0, 'Combine cream cheese, sugar, heavy whipping cream, eggs, and vanilla in the bowl of a food processor, pulse very smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D432FCB7C5DF3C4182F0973BFD47AC2D', X'A4C25B68794BEB4F9F467BC304D7742E', X'A4C25B68794BEB4F9F467BC304D7742E', 1, NULL, NULL, 2, NULL, 0, 'Color the filling using a variety of food coloring. See notes in tutorial for details.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'023432BDD33C8347AE54E3CB15A117DF', X'A4C25B68794BEB4F9F467BC304D7742E', X'A4C25B68794BEB4F9F467BC304D7742E', 2, NULL, NULL, 2, NULL, 0, 'Dip a paint brush into your colored cheesecake filling and use it to paint the indentations in the silicone Easter egg mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D40A56FF997C44CB7A2A8B4056271B9', X'A4C25B68794BEB4F9F467BC304D7742E', X'A4C25B68794BEB4F9F467BC304D7742E', 3, NULL, NULL, 2, NULL, 0, 'Once you have painted all of the indentations in 8 Easter eggs, freeze the molds for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'225E64CA2BF62344AC107EC3081E08B1', X'A4C25B68794BEB4F9F467BC304D7742E', X'A4C25B68794BEB4F9F467BC304D7742E', 4, NULL, NULL, 2, NULL, 0, 'If using a water bath and you have a flat rack for inside your roasting pan, set your mold on the rack. If not using a water bath, set your Easter egg mold on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'28C0A3B4D947F64CAB4E2B908128E490', X'A4C25B68794BEB4F9F467BC304D7742E', X'A4C25B68794BEB4F9F467BC304D7742E', 5, NULL, NULL, 2, NULL, 0, 'Pour colored cheesecake filling into the Easter egg mold, filling each egg shaped cavity to about 1/8th of an inch from the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'810A4955B5B28B44917EDA83B7A11CA5', X'A4C25B68794BEB4F9F467BC304D7742E', X'A4C25B68794BEB4F9F467BC304D7742E', 6, NULL, NULL, 2, NULL, 0, 'Fill all 8 of the painted eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'737F4740CED7EC4F82321E52560347DB', X'A4C25B68794BEB4F9F467BC304D7742E', X'A4C25B68794BEB4F9F467BC304D7742E', 7, NULL, NULL, 2, NULL, 0, 'Pour any remaining cheesecake filling into an other egg mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5962012D07ADEB499D014AECF99811D4', X'A4C25B68794BEB4F9F467BC304D7742E', X'A4C25B68794BEB4F9F467BC304D7742E', 8, NULL, NULL, 2, NULL, 0, 'Bake your cheesecakes in the water bath, if using, for 18-22 minutes until the top forms a smooth film and the center is still giggly but not wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'658BB7244A66A94EB70A731A1CDEC3CF', X'A4C25B68794BEB4F9F467BC304D7742E', X'A4C25B68794BEB4F9F467BC304D7742E', 9, NULL, NULL, 2, NULL, 0, 'Allow your cheesecakes to cool at room temperature for an hour then refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1662ECD5DEA9F4790A84AF22D4D3819', X'A4C25B68794BEB4F9F467BC304D7742E', X'A4C25B68794BEB4F9F467BC304D7742E', 10, NULL, NULL, 2, NULL, 0, 'Freeze for 1-2 hours before un-molding.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EE99E86F39D4D468E133D81E12ACA57', X'A4C25B68794BEB4F9F467BC304D7742E', X'A4C25B68794BEB4F9F467BC304D7742E', 11, NULL, NULL, 2, NULL, 0, 'Remove from the mold and immediately set each cheesecake on a small dessert plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C05B9E65F66E8C4F86C2D83B43882C66', X'A4C25B68794BEB4F9F467BC304D7742E', X'A4C25B68794BEB4F9F467BC304D7742E', 12, NULL, NULL, 2, NULL, 0, 'Allow to thaw for about 30 minutes before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F3C761635D4CA4593CAD9A2545E2710', X'724A29B93A58954B8B10E0C4CF74996B', X'724A29B93A58954B8B10E0C4CF74996B', 0, NULL, NULL, 2, NULL, 0, 'Roll mini marshmallows in the palm of your hands until they shape into fluffy bunny tails.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C10AE90DD7404646BBE1CA9620E49398', X'724A29B93A58954B8B10E0C4CF74996B', X'724A29B93A58954B8B10E0C4CF74996B', 1, NULL, NULL, 2, NULL, 0, 'Pipe one small head (a little smaller than a vanilla wafer) and two ears onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEAB4DCF7228CE4EA288898698F798AD', X'724A29B93A58954B8B10E0C4CF74996B', X'724A29B93A58954B8B10E0C4CF74996B', 2, NULL, NULL, 2, NULL, 0, 'Dip a vanilla wafer cookie into the melted chocolate or candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6627FD292EA55441B2DE0697FB7710B6', X'724A29B93A58954B8B10E0C4CF74996B', X'724A29B93A58954B8B10E0C4CF74996B', 3, NULL, NULL, 2, NULL, 0, 'Remove from the chocolate and set right next to the chocolate head/ears creating the body for the bunny.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C89AA86DBE8B904B8113D30387F23976', X'724A29B93A58954B8B10E0C4CF74996B', X'724A29B93A58954B8B10E0C4CF74996B', 4, NULL, NULL, 2, NULL, 0, 'Immediately attach one marshmallow bunny tail along the bottom edge of the chocolate dipped cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE0E1E9CEA7405469548DFFF3C62BC1C', X'724A29B93A58954B8B10E0C4CF74996B', X'724A29B93A58954B8B10E0C4CF74996B', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate if using pure milk chocolate or freeze if using candy melts for about 3-5 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDDA97C3B17B3C4896CFAD5BA7ACC729', X'724A29B93A58954B8B10E0C4CF74996B', X'724A29B93A58954B8B10E0C4CF74996B', 6, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 54 chocolate bunnies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2857B9EC0587244D8DDF0C38C4EAA854', X'5C9AFFC6B617A54B99C87446E3140FD6', X'5C9AFFC6B617A54B99C87446E3140FD6', 0, NULL, NULL, 2, NULL, 0, 'Carefully split each cookie and scrape the peanut butter filling out into a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC852DBD7BE17E42A033E2A866D98C3C', X'5C9AFFC6B617A54B99C87446E3140FD6', X'5C9AFFC6B617A54B99C87446E3140FD6', 1, NULL, NULL, 2, NULL, 0, 'Combine the creamy peanut butter, butter, cream and salt in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'365FD8E63A4E6842B80649A3B1FA97D6', X'5C9AFFC6B617A54B99C87446E3140FD6', X'5C9AFFC6B617A54B99C87446E3140FD6', 2, NULL, NULL, 2, NULL, 0, 'Heat in microwave on high for 30 seconds, stir, heat 20 more seconds, then stir and heat 10-second increments afterward, if needed until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E4D05E6C9C28847BA75550DDBA56031', X'5C9AFFC6B617A54B99C87446E3140FD6', X'5C9AFFC6B617A54B99C87446E3140FD6', 3, NULL, NULL, 2, NULL, 0, 'Stir in the powdered sugar then add the reserved peanut butter filling from the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04C8A7B6A033004CB87A13DCD247E995', X'5C9AFFC6B617A54B99C87446E3140FD6', X'5C9AFFC6B617A54B99C87446E3140FD6', 4, NULL, NULL, 2, NULL, 0, 'Scoop out 30 balls of fudge using a #70 ice cream scoop or spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'018C8A185C47A34CB1BF21257E872CA8', X'5C9AFFC6B617A54B99C87446E3140FD6', X'5C9AFFC6B617A54B99C87446E3140FD6', 5, NULL, NULL, 2, NULL, 0, 'Roll into balls and press into a slipper shape on one end of a Nutter Butter Cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49C7BBE7599CBA4CACEDA9A2C17E3A6C', X'5C9AFFC6B617A54B99C87446E3140FD6', X'5C9AFFC6B617A54B99C87446E3140FD6', 6, NULL, NULL, 2, NULL, 0, 'Create an indentation in each peanut butter fudge ball making it look like a slipper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30C3856CF39FB348AA2EBDF2E4DB0139', X'5C9AFFC6B617A54B99C87446E3140FD6', X'5C9AFFC6B617A54B99C87446E3140FD6', 7, NULL, NULL, 2, NULL, 0, 'Dip the top party of a cookie in melted pink candy melts and immediately sprinkle with grated pink candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA069438450DB14AA8A59CCA16EDB0AD', X'5C9AFFC6B617A54B99C87446E3140FD6', X'5C9AFFC6B617A54B99C87446E3140FD6', 8, NULL, NULL, 2, NULL, 0, 'Set aside to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47E5F25866D54343A01533B74C657CCF', X'5C9AFFC6B617A54B99C87446E3140FD6', X'5C9AFFC6B617A54B99C87446E3140FD6', 9, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C41209717FC36540827D111C8E89A417', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 0, NULL, NULL, 2, NULL, 0, 'Whisk the flour, salt, and baking powder together in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6FE9486DC2AB9499D7464118F77F0F0', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 1, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'369CD28929F8A5488A9D1CC4C8B0D57F', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 2, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugar together until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E1507CE535F934B9FE5C523CCAE5C3E', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 3, NULL, NULL, 2, NULL, 0, 'Stir in the egg, vanilla, and almond or lemon extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B784990DFC7764B9AAAD31D2AA55DC8', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 4, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients and stir just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A27CC9119B97E54ABD69500E31A74A69', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 5, NULL, NULL, 2, NULL, 0, 'Flatten dough into a disc and wrap in plastic wrap or a large zip-top bag and refrigerate for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC29CD6E13081A4D883F3F38955BBA74', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 6, NULL, NULL, 2, NULL, 0, 'When the dough has chilled, dust a cutting board or lightly dust a silicone baking mat and place 1/2 of the dough on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C44EF166434F064F9CB673F727FA036B', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 7, NULL, NULL, 2, NULL, 0, 'Roll out to 1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B9F77C8DF1AD141BDF6304B14448CF0', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 8, NULL, NULL, 2, NULL, 0, 'Cut out 5 petals using the 3 1/2 long tear drop cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D9FD23D684734458D6B76417AD8CEEA', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 9, NULL, NULL, 2, NULL, 0, 'Cut out one 1 1/2 inch circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E35FBFA6A5D3A5488720F2E7D1805026', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 10, NULL, NULL, 2, NULL, 0, 'Then arrange the petals in a flower shape, so that their tips almost touch, and use the round cookie cutter to cut a circle in the middle of the tips to create room for the center to fit inside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8893F8110255F44B26419D7FB1EE1AE', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 11, NULL, NULL, 2, NULL, 0, 'Cut out two leaves and one 6 1/2 inch long stem.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'039FE330DA1F54489DB5E3987E70E260', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 12, NULL, NULL, 2, NULL, 0, 'Move all the pieces onto a parchment paper or silicone mat lined baking sheet, leaving an inch in between, and place in the refrigerator for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D37234A40FF4049AC6A14CFF70A7E9E', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 13, NULL, NULL, 2, NULL, 0, 'Roll out the other chilled piece of dough and cut out the same shapes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5715D87D3FE06A49BAB77ACA48ED4F3A', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 14, NULL, NULL, 2, NULL, 0, 'Blend the remaining dough together and roll it out. Cut out a hillside on which to place your flowers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1794D56730CF74D8F17793D76D08DE7', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 15, NULL, NULL, 2, NULL, 0, 'Use the excess dough to cut out another daisy or save for another use.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34D6C289158CF54297F267FBF85D6A0B', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 16, NULL, NULL, 2, NULL, 0, 'While the cut-outs are chilling, preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DD6FACDBE615A42B7B2D5993D06D37E', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 17, NULL, NULL, 2, NULL, 0, 'Bake 1 tray of cookies at a time for 8 minutes then rotate the pan and continue to bake for 3-4 minutes just until the tops of the cookies look dry and the edges look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC0390653E0DF543BB48817513351783', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 18, NULL, NULL, 2, NULL, 0, 'Remove and set the pan on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63A86B2EB9320247AC68D6DBA1361049', X'B805C133F0FBB148B1E12A9FEB96678F', X'B805C133F0FBB148B1E12A9FEB96678F', 19, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool for 5 minutes then remove them using a metal spatula and place them directly on the cooling rack to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF399E09EE1B434DBF3195F6E474D6DF', X'F1C8FD23295CFF4D9486425EF3576A0C', X'F1C8FD23295CFF4D9486425EF3576A0C', 0, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese and 1/4 cup of the powdered sugar together until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE673E21CDDE6445B489A86B157EDD17', X'F1C8FD23295CFF4D9486425EF3576A0C', X'F1C8FD23295CFF4D9486425EF3576A0C', 1, NULL, NULL, 2, NULL, 0, 'In a clean bowl with clean beaters, beat the heavy whipping cream, the remaining 1/2 cup of powdered sugar, and the vanilla extract until you get stiff peaks. Do not over whip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'164015888101AC48BB44D73676B99F91', X'F1C8FD23295CFF4D9486425EF3576A0C', X'F1C8FD23295CFF4D9486425EF3576A0C', 2, NULL, NULL, 2, NULL, 0, 'Stir 1/3 of the whipped cream into the cream cheese until the cream cheese is lighter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE03C5A6CF797642BA216AC9A5105C10', X'F1C8FD23295CFF4D9486425EF3576A0C', X'F1C8FD23295CFF4D9486425EF3576A0C', 3, NULL, NULL, 2, NULL, 0, 'Fold in another 1/3 of the whipped cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB17B89F159B7D4BB88A468F04711F7E', X'F1C8FD23295CFF4D9486425EF3576A0C', X'F1C8FD23295CFF4D9486425EF3576A0C', 4, NULL, NULL, 2, NULL, 0, 'Then fold in the remaining 1/3 of the whipped cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F3B922528162C428A417B85CE626170', X'F1C8FD23295CFF4D9486425EF3576A0C', X'F1C8FD23295CFF4D9486425EF3576A0C', 5, NULL, NULL, 2, NULL, 0, 'Chill the cheesecake frosting for at least 30 minutes');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CD28EAE49B3854ABB7347E05C6854D9', X'88C47B7225525F4D884BA4D148F21344', X'88C47B7225525F4D884BA4D148F21344', 0, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FBA1D509E7CC34BA3677FBDF0D94ED2', X'88C47B7225525F4D884BA4D148F21344', X'88C47B7225525F4D884BA4D148F21344', 1, NULL, NULL, 2, NULL, 0, 'Add the marshmallow fluff and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A477B330B065E64C95E8A3892F00E545', X'88C47B7225525F4D884BA4D148F21344', X'88C47B7225525F4D884BA4D148F21344', 2, NULL, NULL, 2, NULL, 0, 'Then add the whipped topping and beat until nice and light.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B26277DF25E882448D42499D7DD6D625', X'7AEBC61CFCFFA44FBE47B2965BF2864E', X'7AEBC61CFCFFA44FBE47B2965BF2864E', 0, NULL, NULL, 2, NULL, 0, 'Spread or pipe a 1/4 inch layer of cheesecake filling onto each individual cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'076B43D24839914283EAD791CA9B7580', X'7AEBC61CFCFFA44FBE47B2965BF2864E', X'7AEBC61CFCFFA44FBE47B2965BF2864E', 1, NULL, NULL, 2, NULL, 0, 'Arrange the cookies on a platter or cake board.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3D625A41A493D40A50C4C1AE53FA85C', X'7AEBC61CFCFFA44FBE47B2965BF2864E', X'7AEBC61CFCFFA44FBE47B2965BF2864E', 2, NULL, NULL, 2, NULL, 0, 'Then arrange raspberries, blueberries, or strawberry pieces over the petals of your daisies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'303BA8925D608E4FA00298162696B888', X'7AEBC61CFCFFA44FBE47B2965BF2864E', X'7AEBC61CFCFFA44FBE47B2965BF2864E', 3, NULL, NULL, 2, NULL, 0, 'Top the small round cookie with pineapple, mango, or peach pieces');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83A11166B1C4414EA29A5891EBDA3AC0', X'7AEBC61CFCFFA44FBE47B2965BF2864E', X'7AEBC61CFCFFA44FBE47B2965BF2864E', 4, NULL, NULL, 2, NULL, 0, 'Top the stem and leaves with green grapes and/or kiwi slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'023DA184840B92478725E3A3413130C0', X'7AEBC61CFCFFA44FBE47B2965BF2864E', X'7AEBC61CFCFFA44FBE47B2965BF2864E', 5, NULL, NULL, 2, NULL, 0, 'Lay the hillside cookie over the bottom of the flower stems and top with kiwi slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9811DD853F4D842BE01BF4A739873D2', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B359F1756A65E74E98B4260583E7A453', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 1, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy. Stir in eggs, vanilla bean paste, and almond extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4019F67A485E7144B55F984975DF7471', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 2, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients and stir just until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BDA576CC37D5EC418FFB7070C8F4F8C6', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 3, NULL, NULL, 2, NULL, 0, 'Divide the dough into 4 equal parts and color pink, violet, blue, and yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA367597F2AB094887C86FB23F4724F9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 4, NULL, NULL, 2, NULL, 0, 'Lightly dust your work surface, roll out the dough to between 1/8th of an inch and 1/16th of an inch thickness. Cut out 4 hat brims using a round scalloped cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4027E0370E631E4391F76098B70EB242', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 5, NULL, NULL, 2, NULL, 0, 'Place on a parchment paper lined baking sheet and refrigerate for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B2E34DF5E55DF45B40B3202DE99FFDB', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D788838BEF2C6746993019FF1836D945', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 7, NULL, NULL, 2, NULL, 0, 'Bake these cookies for 12-14 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AC47062ED80F9469300E09414A2D359', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 8, NULL, NULL, 2, NULL, 0, 'Re-roll each colored cookie dough out to a 1/16 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9BC1A656E6E324FB17F7A1DC1141BBC', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 9, NULL, NULL, 2, NULL, 0, 'Cut four circles of each color using 2 1/2 inch (or smaller) round cutters, so that when you drape the dough over your cake ball pan, the dough drapes over but doesnt touch the base of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3118FA71B8585248AEE2688FCDEC62F5', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 10, NULL, NULL, 2, NULL, 0, 'Drape circles of dough over each mound on your pan and smooth with your fingers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF746393346A55498395219B4B5F660D', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 11, NULL, NULL, 2, NULL, 0, 'Place the pan in the freezer for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'654F6395BA910E44A25B007F65A2E554', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 12, NULL, NULL, 2, NULL, 0, 'Bake the cookies on the bottom rack of your oven for 8-11 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85AA79A54578024CA9A99B714FEBB707', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', X'E09C8B3A751DBC42BFC95DACBEE2F3E9', 13, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17375A4DA69A1347A54E2FDA1F941362', X'CBFBA35543BD61409FAF30E2D791320B', X'CBFBA35543BD61409FAF30E2D791320B', 0, NULL, NULL, 2, NULL, 0, 'To assemble your hat cookies, fill the rounded cookies with candies, then pipe a thin line of royal icing around the top edge of the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'701DB38A85D9FB43A69FF7589D4FF65B', X'CBFBA35543BD61409FAF30E2D791320B', X'CBFBA35543BD61409FAF30E2D791320B', 1, NULL, NULL, 2, NULL, 0, 'Turn one of the scalloped cookies upside down, set it on top of the iced cookie, and press them together to seal. Turn the cookie right side up and allow to dry for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3D60581C4291A4D9B013D688CD348C9', X'CBFBA35543BD61409FAF30E2D791320B', X'CBFBA35543BD61409FAF30E2D791320B', 2, NULL, NULL, 2, NULL, 0, 'To decorate your hat cookies, roll out some modeling chocolate or fondant and cut into 1/8th inch wide by 8 inch long strips and attach to the hats using royal icing, cutting off any excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'975E435265B1104DA67EE785BAE16303', X'CBFBA35543BD61409FAF30E2D791320B', X'CBFBA35543BD61409FAF30E2D791320B', 3, NULL, NULL, 2, NULL, 0, 'Color small pieces of white modeling chocolate or fondant to match your hats. Color some green as well.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9B2CD7BB440494A96190BB7FBB0440B', X'CBFBA35543BD61409FAF30E2D791320B', X'CBFBA35543BD61409FAF30E2D791320B', 4, NULL, NULL, 2, NULL, 0, 'Roll out the modeling chocolate and cut out flowers using a small daisy plunger cutter. Cut the center of the flowers out using a small round plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EE5DD3C9C56B44F85A9487B9DCB4302', X'CBFBA35543BD61409FAF30E2D791320B', X'CBFBA35543BD61409FAF30E2D791320B', 5, NULL, NULL, 2, NULL, 0, 'Attach the centers to the daisies using a small dab of water.Note: I used Wiltons new Water Brush, which I love. I dont have to mess around with bowls of water, which Ive been known to spill all over my modeling chocolate. The water is contained in the brush. It was perfect for attaching these tiny pieces together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24707EE0366340478BD2A238F3955F4B', X'CBFBA35543BD61409FAF30E2D791320B', X'CBFBA35543BD61409FAF30E2D791320B', 6, NULL, NULL, 2, NULL, 0, 'Once all of your cookies are assembled and decorated they are ready to dress up your dessert table.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1600DC18275A6D4F81CACADC47BAC625', X'390A344F562FB042BD2D581CE659AADB', X'390A344F562FB042BD2D581CE659AADB', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEA13AB30F20D247BD4DA0189419A718', X'390A344F562FB042BD2D581CE659AADB', X'390A344F562FB042BD2D581CE659AADB', 1, NULL, NULL, 2, NULL, 0, 'Line two cupcake pans with 21 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5803F9400D5CA14D8CDCD4386758EDCE', X'390A344F562FB042BD2D581CE659AADB', X'390A344F562FB042BD2D581CE659AADB', 2, NULL, NULL, 2, NULL, 0, 'Combine the white cake mix, instant pudding mix, sour cream, egg whites, vegetable oil, and water in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BFCBEBA88EE0241A92BC026210185E5', X'390A344F562FB042BD2D581CE659AADB', X'390A344F562FB042BD2D581CE659AADB', 3, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on low speed for 30 seconds to incorporate the ingredients then increase the speed to medium and beat for 1 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCA6EB669D182D45A1B9F91EDC69ABDC', X'390A344F562FB042BD2D581CE659AADB', X'390A344F562FB042BD2D581CE659AADB', 4, NULL, NULL, 2, NULL, 0, 'Equally divide the cake batter among the 21 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'014C27E94D792643A365D74D8E32FC1A', X'390A344F562FB042BD2D581CE659AADB', X'390A344F562FB042BD2D581CE659AADB', 5, NULL, NULL, 2, NULL, 0, 'Bake for 18-24 minutes until the cupcake springs back when your press a finger on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CDEC044CC6B224CACCA6499FE0E5482', X'390A344F562FB042BD2D581CE659AADB', X'390A344F562FB042BD2D581CE659AADB', 6, NULL, NULL, 2, NULL, 0, 'Remove the cupcakes from the oven and allow them to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E55FB08C72DE094B998B3E8CDBFBCC21', X'968491F766D7334AB365982D17482054', X'968491F766D7334AB365982D17482054', 0, NULL, NULL, 2, NULL, 0, 'Pour the strawberry pie filing into a strainer set over a large bowl and press down on the strawberries to allow the red gel to fall into the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97066D70CAF23D4E94A05307EC04231A', X'968491F766D7334AB365982D17482054', X'968491F766D7334AB365982D17482054', 1, NULL, NULL, 2, NULL, 0, 'You will use the gel to fill the cupcakes and the strawberries to make the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77ED4CC3907BA54B8AF7CDC0814E812F', X'968491F766D7334AB365982D17482054', X'968491F766D7334AB365982D17482054', 2, NULL, NULL, 2, NULL, 0, 'Spoon the gel into a pastry bag to pipe into the cupcake or just use a small spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29B3B47B688D3545ABAA68B5A6E68639', X'968491F766D7334AB365982D17482054', X'968491F766D7334AB365982D17482054', 3, NULL, NULL, 2, NULL, 0, 'Use a cupcake corer or knife to cut a well out of the center of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D27068612F89143ACD855F1750A34C6', X'968491F766D7334AB365982D17482054', X'968491F766D7334AB365982D17482054', 4, NULL, NULL, 2, NULL, 0, 'Fill each well in the cupcake with the strawberry pie filling. Reserve any remaining pie filling for another use.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CED5964BBF0C6F47B9D32AA1192E93E4', X'968491F766D7334AB365982D17482054', X'968491F766D7334AB365982D17482054', 5, NULL, NULL, 2, NULL, 0, 'Place the softened cream cheese in a large mixing bowl and beat, using an electric mixer. until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47996DC71DE6FC4797DA426DF9D9C602', X'968491F766D7334AB365982D17482054', X'968491F766D7334AB365982D17482054', 6, NULL, NULL, 2, NULL, 0, 'Sift the powdered sugar into the bowl and add the reserved strawberries Beat well until the ingredients are fully combined and the strawberries are broken into small bits.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEB8ED88DE9F984EB2BC2A8ABF96F75A', X'968491F766D7334AB365982D17482054', X'968491F766D7334AB365982D17482054', 7, NULL, NULL, 2, NULL, 0, 'Add a teaspoon of lemon juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B9C2A3317F72B4E8D9E6A47A503A11A', X'968491F766D7334AB365982D17482054', X'968491F766D7334AB365982D17482054', 8, NULL, NULL, 2, NULL, 0, 'Either add the whipped topping and beat until combined or fold in the freshly whipped whip cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EAA9AEF30C8FA42BA659A0E17E2A724', X'968491F766D7334AB365982D17482054', X'968491F766D7334AB365982D17482054', 9, NULL, NULL, 2, NULL, 0, 'Spoon the frosting into a large pastry bag fitted with a large star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F544B320695F164DB0955608B4C2FC42', X'968491F766D7334AB365982D17482054', X'968491F766D7334AB365982D17482054', 10, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of frosting on top of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42AF490A21B0AD408D23BE0D8AAB79CA', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 0, NULL, NULL, 2, NULL, 0, 'Press the thawed raspberries through a fine mesh sieve to remove seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'692D1D4B10C3FA419145A27E0C527265', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 1, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE915A8BE07E044B9EAFF82B71A9C10D', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 2, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EF702DBEA2BC248ABA2AC7CFFA0780F', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 3, NULL, NULL, 2, NULL, 0, 'Beat in eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C872B63BF77F064FB9FE84F74332328F', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 4, NULL, NULL, 2, NULL, 0, 'Pour in raspberry puree and lemon juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70052001EC47034D8A8CB32D52F1A131', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 5, NULL, NULL, 2, NULL, 0, 'Beat just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BADB3C285849D4C9C0B93DFF08EF928', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 6, NULL, NULL, 2, NULL, 0, 'Fill one silicone rose mold half way with the raspberry cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8EFFE2D9B76F240A4DCDA5E98EA13F4', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 7, NULL, NULL, 2, NULL, 0, 'Use a food-use-only paint brush to push the filling into all the small grooves in the rose mold and to pop any air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'28DD6D8BE6EFE44C99699E7C4C9FE1F5', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 8, NULL, NULL, 2, NULL, 0, 'Fill the mold with more cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A59D964630BF4E47B59B625ED30FF23A', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 9, NULL, NULL, 2, NULL, 0, 'Repeat filling a total of 10 rose molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0295EF1EC02A0541B854149841353776', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 10, NULL, NULL, 2, NULL, 0, 'Set molds on a rimmed baking sheet or in a roasting pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEA49110FE55F44884BEBA7D05BFAF24', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 11, NULL, NULL, 2, NULL, 0, 'Set pan in the oven and fill the pan with very hot water until the water comes half way up the sides of the rose mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08456DC23D99A441982C6186D48392AA', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 12, NULL, NULL, 2, NULL, 0, 'Bake for 18-24 minutes until the tops of the cheesecakes no longer look wet but the center still jiggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC1E6A49E37007449350E57789566415', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 13, NULL, NULL, 2, NULL, 0, 'Remove from the oven and from the water bath.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E63E78F8DF08B446AD4119390E5486BD', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 14, NULL, NULL, 2, NULL, 0, 'Let cool at room temperature for 20 minutes then refrigerate for 30 minutes. Cover with plastic wrap and pop in the freezer for at least 3 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3CB673A1141D346A2E8D2CD92E31F4D', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 15, NULL, NULL, 2, NULL, 0, 'Remove from freezer and push the rose shaped cheesecakes out of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB5BEE36D10D2E488C6D5640E9954859', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 16, NULL, NULL, 2, NULL, 0, 'Immediately set on a small dessert plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D321F3048149D46B7784DD08FB8E49E', X'6BF28219B7467D45BD19EF3A52553483', X'6BF28219B7467D45BD19EF3A52553483', 17, NULL, NULL, 2, NULL, 0, 'Allow to thaw at room temperature for about 30 minutes before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2034CEFBED1B774190BEA780C27F5850', X'AC1F9B431A262D4C87A0D32858E5804E', X'AC1F9B431A262D4C87A0D32858E5804E', 0, NULL, NULL, 2, NULL, 0, 'Pour chocolate and heavy whipping cream into a microwave safe bowl and heat on high power for 30 seconds. Let rest for about 3 minutes then stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'578A13A43108AA4A88EFA877ADBBC94E', X'AC1F9B431A262D4C87A0D32858E5804E', X'AC1F9B431A262D4C87A0D32858E5804E', 1, NULL, NULL, 2, NULL, 0, 'Pour into a squeeze bottle and let cool until slightly thickened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E3D83B220DDE047A58FB62DF7A459AF', X'AC1F9B431A262D4C87A0D32858E5804E', X'AC1F9B431A262D4C87A0D32858E5804E', 2, NULL, NULL, 2, NULL, 0, 'Pipe stems and leaves, dots, or squiggles onto the plate before adding the cheesecake rose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD397B248201DF488C1CFB1F67817117', X'3AFE2134DE1184459AAED9762295E917', X'3AFE2134DE1184459AAED9762295E917', 0, NULL, NULL, 2, NULL, 0, 'Pour 1 1/3 cups heavy whipping cream into a mixing bowl and whisk, or beat using an electric mixer, until you get stiff peaked whipped cream. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF783914894C2E4184576FBC053D6814', X'3AFE2134DE1184459AAED9762295E917', X'3AFE2134DE1184459AAED9762295E917', 1, NULL, NULL, 2, NULL, 0, 'Combine water and sugar in a saucepan and heat over medium high heat until it comes to a boil, stirring until the sugar completely dissolves. Remove from heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C798D278934CF745BDFB50314E05B89E', X'3AFE2134DE1184459AAED9762295E917', X'3AFE2134DE1184459AAED9762295E917', 2, NULL, NULL, 2, NULL, 0, 'Whisk the egg yolks in a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'515A889C9BC89B49AF31B20F8A809D6C', X'3AFE2134DE1184459AAED9762295E917', X'3AFE2134DE1184459AAED9762295E917', 3, NULL, NULL, 2, NULL, 0, 'Continue whisking while slowly drizzling in the hot sugar water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74549DF961994A4FB591A98A3C6082AF', X'3AFE2134DE1184459AAED9762295E917', X'3AFE2134DE1184459AAED9762295E917', 4, NULL, NULL, 2, NULL, 0, 'Return this mixture, along with the remaining 2/3 cup heavy whipping cream, to your saucepan and continue to stir constantly while heating over medium low heat until the mixture reaches 160 degrees F and starts to thicken slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4D532465BFD874882B38D9A12EC91A0', X'3AFE2134DE1184459AAED9762295E917', X'3AFE2134DE1184459AAED9762295E917', 5, NULL, NULL, 2, NULL, 0, 'Pour through a fine mesh strainer set over a bowl of melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4288B164B7CF3E4CAE97A9CE3A9CF4DE', X'3AFE2134DE1184459AAED9762295E917', X'3AFE2134DE1184459AAED9762295E917', 6, NULL, NULL, 2, NULL, 0, 'Stir until smooth and glossy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F82AB7021D44624A87F5AFC8B8DBEB71', X'3AFE2134DE1184459AAED9762295E917', X'3AFE2134DE1184459AAED9762295E917', 7, NULL, NULL, 2, NULL, 0, 'Fold in half of the whipped cream until well blended then fold in the remaining whipped cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1A4AEE9FDB0DB4CB64AC07B0E36DA97', X'3AFE2134DE1184459AAED9762295E917', X'3AFE2134DE1184459AAED9762295E917', 8, NULL, NULL, 2, NULL, 0, 'Pour into 12 silicone flower pot baking cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54DD3F7B8B4BF644877CE355A5414B4C', X'3AFE2134DE1184459AAED9762295E917', X'3AFE2134DE1184459AAED9762295E917', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle cookie crumbs over top of each pot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD573C1DD7D06B4B9EB5B303313073E0', X'3AFE2134DE1184459AAED9762295E917', X'3AFE2134DE1184459AAED9762295E917', 10, NULL, NULL, 2, NULL, 0, 'Chill for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF42EA3B1EF8074C8FCDBE8DC9AA1445', X'3AFE2134DE1184459AAED9762295E917', X'3AFE2134DE1184459AAED9762295E917', 11, NULL, NULL, 2, NULL, 0, 'Remove from refrigerator and let rest at room temperature for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5B7523F96EAA3439A94032665C59BC5', X'3AFE2134DE1184459AAED9762295E917', X'3AFE2134DE1184459AAED9762295E917', 12, NULL, NULL, 2, NULL, 0, 'Insert mint sprigs into the center of each chocolate mousse pot or top with tiny strawberries and strawberry leaves.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B896B800D92EA5438CDE3710776B346D', X'07CC07573CBD8F41B9716436C4CA96D1', X'07CC07573CBD8F41B9716436C4CA96D1', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 degrees Fahrenheit. Spread almonds out on a baking sheet, and toast until golden brown, about 5 minutes. Allow nuts to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1060C7DC8BAD32489AEAD807045B33AA', X'07CC07573CBD8F41B9716436C4CA96D1', X'07CC07573CBD8F41B9716436C4CA96D1', 1, NULL, NULL, 2, NULL, 0, 'Combine almonds and 1/4 cup sugar in a food processor and pulse until the nuts are finely ground.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37FCCDADDBCEF644AD599D27BC62CF32', X'07CC07573CBD8F41B9716436C4CA96D1', X'07CC07573CBD8F41B9716436C4CA96D1', 2, NULL, NULL, 2, NULL, 0, 'Whisk flour, cinnamon, salt, and lemon zest together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAF0C3B39C664A4BAE259D052BE0A5CB', X'07CC07573CBD8F41B9716436C4CA96D1', X'07CC07573CBD8F41B9716436C4CA96D1', 3, NULL, NULL, 2, NULL, 0, 'Cream butter and 1/2 cup sugar then add the vanilla and egg yolks and mix until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E5FB68033A7084FA94820BE5F2A815C', X'07CC07573CBD8F41B9716436C4CA96D1', X'07CC07573CBD8F41B9716436C4CA96D1', 4, NULL, NULL, 2, NULL, 0, 'Stir in the almonds and mix thoroughly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8731D600AEE6C244A7E51CA16B11EDD9', X'07CC07573CBD8F41B9716436C4CA96D1', X'07CC07573CBD8F41B9716436C4CA96D1', 5, NULL, NULL, 2, NULL, 0, 'Add flour and mix just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEC9A4B8EBDD424885D0A10AF0973C9A', X'07CC07573CBD8F41B9716436C4CA96D1', X'07CC07573CBD8F41B9716436C4CA96D1', 6, NULL, NULL, 2, NULL, 0, 'Remove half the dough from the bowl, flatten into a disc, wrap in plastic wrap and refrigerate. Repeat with remaining dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDF2D90D78AE38449C292F7B28DD57EA', X'07CC07573CBD8F41B9716436C4CA96D1', X'07CC07573CBD8F41B9716436C4CA96D1', 7, NULL, NULL, 2, NULL, 0, 'Dust work surface with some flour. Roll cookie dough out to 1/4 inch thickness. Cut using a 5 or 6 inch heart shaped cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5608502A09C5442B5EED80625B422F4', X'07CC07573CBD8F41B9716436C4CA96D1', X'07CC07573CBD8F41B9716436C4CA96D1', 8, NULL, NULL, 2, NULL, 0, 'Move cookies to a parchment lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7EC20C163FA2146851E0FF4D51B7A11', X'07CC07573CBD8F41B9716436C4CA96D1', X'07CC07573CBD8F41B9716436C4CA96D1', 9, NULL, NULL, 2, NULL, 0, 'Use small cookie cutters to cut out I (heart) Mom into half of the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AAEB95DCDB91764691D1104D5DE0BA67', X'07CC07573CBD8F41B9716436C4CA96D1', X'07CC07573CBD8F41B9716436C4CA96D1', 10, NULL, NULL, 2, NULL, 0, 'Bake for about 15 minutes. The edges of the cookies should just turn lightly golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7A63D2D47FAA34CBE0572BDD87EAE78', X'07CC07573CBD8F41B9716436C4CA96D1', X'07CC07573CBD8F41B9716436C4CA96D1', 11, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool completely, then dust the I (heart) MOM cookies with powdered sugar. Spread raspberry jam on the remaining cookies. Place one cut-out cookie on top of the jam and gently sandwich the cookies together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'985FE9D73AF4F8478F59DAC8ABAA7857', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B104D89673694C40A19F484E540A4052', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 1, NULL, NULL, 2, NULL, 0, 'Spread almonds out on a baking sheet, and toast for about 5 minutes, until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4DB69CD1BAADA4CACD2A6132B79FB3F', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 2, NULL, NULL, 2, NULL, 0, 'Remove and pour nuts into the bowl of a food processor and let cool for at least 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A745E93BC4E69142801024B1241C7D13', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 3, NULL, NULL, 2, NULL, 0, 'Add 1/4 cup of the sugar and pulse until the nuts become finely ground.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'546CD78E97FDCD40B974F357AE0ECC80', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 4, NULL, NULL, 2, NULL, 0, 'Whisk flour, salt, and lemon zest together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BD61F96CA9A87459BBCF39A7A796264', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 5, NULL, NULL, 2, NULL, 0, 'Cream butter and 1/2 cup sugar together just until it becomes pale yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6F1B5421A41F64883412FFA9639E3CD', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 6, NULL, NULL, 2, NULL, 0, 'Stir in vanilla then egg yolks and mix until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A5F2CFDCCF080478AC2265730859505', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 7, NULL, NULL, 2, NULL, 0, 'Mix in the ground almonds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14E490A53860914E8967D522BFD47A3A', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 8, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and mix just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'238ECF6A4CD659448CA96AB118CBDD99', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 9, NULL, NULL, 2, NULL, 0, 'Remove dough from bowl, divide in half, then flatten into two discs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DD4404FDA78D64186271D56320417F0', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 10, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10-15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07BEA5AB08E1DD48ACF79FC968B76BBF', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 11, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCBAAC66E9573A49A0C19F71A02522C6', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 12, NULL, NULL, 2, NULL, 0, 'Dust a silicone mat or cutting board with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69CA01128816E546817B599C0A82DFD7', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 13, NULL, NULL, 2, NULL, 0, 'Roll 1 disc of cookie dough out to 1/8 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E07160CF7A0F344D901596B29653C10E', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 14, NULL, NULL, 2, NULL, 0, 'Cut 12 daisies using a 4 inch daisy shaped cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B491205716A92E4C84DD41EAF940990E', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 15, NULL, NULL, 2, NULL, 0, 'Remove excess dough and freeze daisies for 5 minutes until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3FA729F15CD8E4E99C6E93E6182FEDB', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 16, NULL, NULL, 2, NULL, 0, 'Move cookies to a parchment lined baking sheet and bake for 12-16 minutes until lightly golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAD7B8716B57704398DB5A140617A104', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 17, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76EAF87C1AC4F548BC9B1BF0D25CDD4F', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 18, NULL, NULL, 2, NULL, 0, 'Roll the remaining dough and cut 9 or 10 daisies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A12CEB93B28FCE46B4AA442B2E54A968', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 19, NULL, NULL, 2, NULL, 0, 'Use 1 1/4 inch tear drop cookie cutter to cut out holes in each petal, leaving a small border around the edge of the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'037729FBAF8E0E45B581F3E44B200BE1', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 20, NULL, NULL, 2, NULL, 0, 'Remove excess dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'657D294DD09FC04681A93C6F714C4D0D', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 21, NULL, NULL, 2, NULL, 0, 'Re-roll dough until you get 3 more daisies with holes in the petals.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'187C22B843B3C5479F3C19067B1C2314', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 22, NULL, NULL, 2, NULL, 0, 'Freeze the 12 cut-out daisies for 10 minutes then move to parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87B136EA946FA94793AAFDF96F13D2F7', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 23, NULL, NULL, 2, NULL, 0, 'Bake for about 12-14 minutes until lightly golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'273B42254F784E46BE4EF014BB159482', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 24, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F76FF1A26C38184A80843042E5E5C6AA', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 25, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool completely, then dust with powdered sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A868B4317C562043AC786002F46AF073', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 26, NULL, NULL, 2, NULL, 0, 'Spread raspberry jam or lemon curd on the whole daisy cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'596E96424DC12245A67B98326F2109BB', X'05DBD5CE108C0846907F4A8ED918B6CD', X'05DBD5CE108C0846907F4A8ED918B6CD', 27, NULL, NULL, 2, NULL, 0, 'Place one cut-out cookie on top of the jam and gently sandwich the cookies together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AB389BD5CE6C94888F1C17792765C0F', X'1B235929C20618478192431BD46E994F', X'1B235929C20618478192431BD46E994F', 0, NULL, NULL, 2, NULL, 0, 'Spoon about an ounce of chocolate into one heart shaped cavity in your silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D3CA3169B376F42A806CCC7CED95F55', X'1B235929C20618478192431BD46E994F', X'1B235929C20618478192431BD46E994F', 1, NULL, NULL, 2, NULL, 0, 'Brush the chocolate up the sides of the mold adding more chocolate as needed to create a nice shell. Let the chocolate set slightly then add a second layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE9A390DB3CEE24DA619C4EEDBFA37A8', X'1B235929C20618478192431BD46E994F', X'1B235929C20618478192431BD46E994F', 2, NULL, NULL, 2, NULL, 0, 'You will use approximately 2 ounces of chocolate per heart shaped bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22EDBC4CE809CF4EA43AC9323F95A784', X'1B235929C20618478192431BD46E994F', X'1B235929C20618478192431BD46E994F', 3, NULL, NULL, 2, NULL, 0, 'Repeat creating 12 heart bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46A13B52F57A404CBFF668AEE720AD8D', X'1B235929C20618478192431BD46E994F', X'1B235929C20618478192431BD46E994F', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate until completely set then remove and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED715DC6C10EA2499EEFB5BE09E16932', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 0, NULL, NULL, 2, NULL, 0, 'Pour water into the bottom of a saucepan set over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B598FF218C86441B3E42EA8202C2C8D', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 1, NULL, NULL, 2, NULL, 0, 'Add sugar and allow to come to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1554A1DDA78B14E94FDFF0750EEEE34', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 2, NULL, NULL, 2, NULL, 0, 'Boil until all the sugar is dissolved, stirring if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC36583B26AA4E448089EE2DD9D20207', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 3, NULL, NULL, 2, NULL, 0, 'Pour egg yolks into a mixing bowl and whisk to break up.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE368313C4BB47489C2378E00ACB08D3', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 4, NULL, NULL, 2, NULL, 0, 'Continue whisking while slowly drizzling in the sugar syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70E0858F534ADA4DAADACF4731E42FE5', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 5, NULL, NULL, 2, NULL, 0, 'Pour 1/2 cup of heavy whipping cream into the saucepan and set it back on the stove on low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F787501BC01CEE429D75EE345B51F46A', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 6, NULL, NULL, 2, NULL, 0, 'Stir in the egg mixture and continue to stir for at least 3 minutes until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFC23281C94D224DB2DAF360EADCE979', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 7, NULL, NULL, 2, NULL, 0, 'You may heat until thickened, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8DEABDBFC957F4D8DD5FC5514C71373', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 8, NULL, NULL, 2, NULL, 0, 'Pour through a fine mesh sieve that is set over your bowl of melted semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E63B954588DFC49B6364EF1636ADE78', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 9, NULL, NULL, 2, NULL, 0, 'Discard anything that remains in the sieve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7508886CB0C2D4F91B42C5019E27FC3', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 10, NULL, NULL, 2, NULL, 0, 'Stir the chocolate until glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EB4AA2D6347F747B2D175815136DCE9', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 11, NULL, NULL, 2, NULL, 0, 'Fold the whipped cream into the chocolate in two stages.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D6481BC0657EE42B7E15E687D09B05F', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 12, NULL, NULL, 2, NULL, 0, 'Cover and chill until the mousse thickens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F37E331E08909542A02B4EA2E9A70F44', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 13, NULL, NULL, 2, NULL, 0, 'Spoon mousse into a pastry bag fitted with a large star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'012C2A56E6BEC244948DE07243B92450', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 14, NULL, NULL, 2, NULL, 0, 'Pipe into chocolate heart bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF33607C768E434EB93A3CF768E833AE', X'32409E978D686C46862F0A13CB7143B9', X'32409E978D686C46862F0A13CB7143B9', 15, NULL, NULL, 2, NULL, 0, 'Garnish with fresh raspberries and heart sprinkles if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8342A6662CC3854C8A500929170A5561', X'5ECF0B51FDC69E44BFFC7F7C234D2D18', X'5ECF0B51FDC69E44BFFC7F7C234D2D18', 0, NULL, NULL, 2, NULL, 0, 'Combine white chocolate with sweetened condensed milk in a saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04EEFA1A5DDF7943854EFFCBE2497E77', X'5ECF0B51FDC69E44BFFC7F7C234D2D18', X'5ECF0B51FDC69E44BFFC7F7C234D2D18', 1, NULL, NULL, 2, NULL, 0, 'Heat over low heat until half of the chocolate is melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20E3A1533C4D0648AB92AECB8152C219', X'5ECF0B51FDC69E44BFFC7F7C234D2D18', X'5ECF0B51FDC69E44BFFC7F7C234D2D18', 2, NULL, NULL, 2, NULL, 0, 'Remove from the heat and let sit for 3 minutes then stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'120442B8FD793146A211371ED1E004F9', X'5ECF0B51FDC69E44BFFC7F7C234D2D18', X'5ECF0B51FDC69E44BFFC7F7C234D2D18', 3, NULL, NULL, 2, NULL, 0, 'Add the Oreo Cookie crumbs and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA1308DF37C7BE4CA35E7A2F23B1F3D2', X'5ECF0B51FDC69E44BFFC7F7C234D2D18', X'5ECF0B51FDC69E44BFFC7F7C234D2D18', 4, NULL, NULL, 2, NULL, 0, 'Pour into a tin foil pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6EA231EB5969A47A0192C1BAC87FC18', X'5ECF0B51FDC69E44BFFC7F7C234D2D18', X'5ECF0B51FDC69E44BFFC7F7C234D2D18', 5, NULL, NULL, 2, NULL, 0, 'Follow the tutorial to decorate your fudge garden stone.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1070CE3CDB198F46ADE630FAB32A3DFB', X'E1D82DF8D643A041A21F0C31BAB9C9EA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E4C63DE8584C74B95E21E65C69433CA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F75BBD510411BD4FA12A95A0F6F7810C', X'E1D82DF8D643A041A21F0C31BAB9C9EA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', 2, NULL, NULL, 2, NULL, 0, 'Stir flour, sugar, baking powder, and salt together in a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF2A0501A853834B8EEC4750539B487B', X'E1D82DF8D643A041A21F0C31BAB9C9EA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', 3, NULL, NULL, 2, NULL, 0, 'Pour in whipping cream and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B8A814F8C228B548B20F83C25B471BC0', X'E1D82DF8D643A041A21F0C31BAB9C9EA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', 4, NULL, NULL, 2, NULL, 0, 'Pour out onto a flour dusted cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'372D2BDD66089A4B874EDC155097E889', X'E1D82DF8D643A041A21F0C31BAB9C9EA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', 5, NULL, NULL, 2, NULL, 0, 'Knead for 30 seconds until the dough is smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DCD57BC9EE0A447817937FA42F16B5B', X'E1D82DF8D643A041A21F0C31BAB9C9EA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', 6, NULL, NULL, 2, NULL, 0, 'Roll out to 3/4 inch thickness and cut out daisies using a 4 or 5 inch cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DC1EB871B0C6546BF887F878199CA75', X'E1D82DF8D643A041A21F0C31BAB9C9EA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', 7, NULL, NULL, 2, NULL, 0, 'Re-roll once and cut out more daisies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11AC7699D39CA44C83808C50F695C561', X'E1D82DF8D643A041A21F0C31BAB9C9EA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', 8, NULL, NULL, 2, NULL, 0, 'Set daisies on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7784545EA179EC4082F3C15C405C5F36', X'E1D82DF8D643A041A21F0C31BAB9C9EA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', 9, NULL, NULL, 2, NULL, 0, 'Brush a green circle in the middle of each daisy then brush on yellow petals.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'081648721AA815488428945673D6B8D0', X'E1D82DF8D643A041A21F0C31BAB9C9EA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', 10, NULL, NULL, 2, NULL, 0, 'Or, press a small round cookie cutter in the center of each daisy then press a small tear drop cutter in the center of each petal.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADB54668D9359748A49F9077A04A2BF2', X'E1D82DF8D643A041A21F0C31BAB9C9EA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', 11, NULL, NULL, 2, NULL, 0, 'Bake for 14-18 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3C8911F38FCD64386FDC7F1DC0D8969', X'E1D82DF8D643A041A21F0C31BAB9C9EA', X'E1D82DF8D643A041A21F0C31BAB9C9EA', 12, NULL, NULL, 2, NULL, 0, 'Cut open and fill with Smokehouse BBQ.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6C6BD1D9332B34589B44DBD53D6BB99', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 0, NULL, NULL, 2, NULL, 0, 'Pour bright pink candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73A604E7641AB0429E6C0E68B43FA9C6', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 1, NULL, NULL, 2, NULL, 0, 'Heat for 20 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93665DD6B637214F8A7813E20A9CB212', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 2, NULL, NULL, 2, NULL, 0, 'Use a food use only paint brush to paint pink polka dots on the inside of 6 cups in a muffin tin.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A8357C4FB2C7545841CA0566291A9CA', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 3, NULL, NULL, 2, NULL, 0, 'Pour bright white candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4F6AFDC00803E48A61FF3BF9D7AF925', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 4, NULL, NULL, 2, NULL, 0, 'Heat for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AA1B4621F349A4CBEC173D439873041', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 5, NULL, NULL, 2, NULL, 0, 'Stir in the remaining pink candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19975A55D3803249972870FEED98E8A5', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 6, NULL, NULL, 2, NULL, 0, 'Spoon about 2 tablespoons of the light pink candy into one muffin cup. Use a spoon to push the candy coating up and around the sides covering the pink dots completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67B35299C028A44387AEF9DC363A8950', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 7, NULL, NULL, 2, NULL, 0, 'Repeat, covering all 6 polka dotted cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEE1DBB4E3A48A49A45DC3CCB2D4DCFB', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 8, NULL, NULL, 2, NULL, 0, 'Pop in the freezer for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44688FEE5869354B8206091899FFB7E1', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 9, NULL, NULL, 2, NULL, 0, 'Remove and gently tug on the candy cups, removing them from the muffin tin.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC0464F44BB1C24D9A6D8218D1FE65C7', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 10, NULL, NULL, 2, NULL, 0, 'Allow the candy bowls to sit at room temperature for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EF3200D5B6010428BE743C80BD3FB20', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 11, NULL, NULL, 2, NULL, 0, 'Pour the pink rock candy into a shallow bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7886F50C09E5143BE724DEB3E600170', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 12, NULL, NULL, 2, NULL, 0, 'Reheat the remaining pink candy coating for 10-20 seconds until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EEF44F504484F4086492B8B8EB76F85', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 13, NULL, NULL, 2, NULL, 0, 'Dip the top edge of each candy bowl in the melted candy then immediately dip it in the pink rock candy, pressing the rocks along the edge of the candy bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D6D8E25914FCC458D6BBED35DC5E8CB', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 14, NULL, NULL, 2, NULL, 0, 'Remove and set aside to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBEC66817FA26545990D1884F43067E6', X'AC7FB50BE0AC10489891463227F5648B', X'AC7FB50BE0AC10489891463227F5648B', 15, NULL, NULL, 2, NULL, 0, 'Fill with ice cream when ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD2001889936264CB4C0DF6668ECAE07', X'F37029733B43C84FBA65A404BBEED42D', X'F37029733B43C84FBA65A404BBEED42D', 0, NULL, NULL, 2, NULL, 0, 'Unwrap 3 Rice Krispies Treat Bars. Use a rolling pin or your fingers to flatten each bar out to a 3 1/4 inch by 2 inch rectangle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'175E3D5C0D53CE49A7535041D04F4662', X'F37029733B43C84FBA65A404BBEED42D', X'F37029733B43C84FBA65A404BBEED42D', 1, NULL, NULL, 2, NULL, 0, 'Press and pinch the short ends of two of the treats together creating a 6 1/2 inch by 2 inch rectangle, then curve the treats into a 2 inch tall cylinder and press the other ends together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BDF9D1D28085E40A10B61D42220D0D3', X'F37029733B43C84FBA65A404BBEED42D', X'F37029733B43C84FBA65A404BBEED42D', 2, NULL, NULL, 2, NULL, 0, 'Set the Rice Krispies Treat cylinder on top of a third flattened treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0842CB3F59703F4580976AD8C8C14ABB', X'F37029733B43C84FBA65A404BBEED42D', X'F37029733B43C84FBA65A404BBEED42D', 3, NULL, NULL, 2, NULL, 0, 'Use a knife to cut the third treat out in a circle around the base of the cylinder.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC4907A504B31142A311DC4CD435BD02', X'F37029733B43C84FBA65A404BBEED42D', X'F37029733B43C84FBA65A404BBEED42D', 4, NULL, NULL, 2, NULL, 0, 'Reserve the scraps to create your cup handles. Press the scraps together, roll into a log, then shape into a curved handle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48038F712A20314BB481F718B33843B1', X'F37029733B43C84FBA65A404BBEED42D', X'F37029733B43C84FBA65A404BBEED42D', 5, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 8 cups and 8 handles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1958ED3BD68AC942A6FD3C182E3BBC8D', X'F37029733B43C84FBA65A404BBEED42D', X'F37029733B43C84FBA65A404BBEED42D', 6, NULL, NULL, 2, NULL, 0, 'Place the white confectionery (candy) coating in a microwave safe bowl. Heat on high power for 20-30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A317404076657241BFFB7C2F26763EDB', X'F37029733B43C84FBA65A404BBEED42D', X'F37029733B43C84FBA65A404BBEED42D', 7, NULL, NULL, 2, NULL, 0, 'Use a pastry brush or food using only paint brush to brush a layer of confectionery coating all around the inside of each Rice Krispies Treat cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0AA4D36478B1C46AD3C07E0B4A342B3', X'F37029733B43C84FBA65A404BBEED42D', X'F37029733B43C84FBA65A404BBEED42D', 8, NULL, NULL, 2, NULL, 0, 'Allow the candy coating to dry for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A623B330923FBA41A221E0B8E6E8ABE6', X'F37029733B43C84FBA65A404BBEED42D', X'F37029733B43C84FBA65A404BBEED42D', 9, NULL, NULL, 2, NULL, 0, 'Use a small amount of the candy coating to attach a handle to each cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB326D2E524A7346B1C31EC4C1633D91', X'EF24AC8A6F5CD74E84856B6DE32C4700', X'EF24AC8A6F5CD74E84856B6DE32C4700', 0, NULL, NULL, 2, NULL, 0, 'Spoon a heaping tablespoonful of white candy coating onto a parchment paper lined baking sheet. Spread it into a 3 1/4 inch circle (for a nice uniform looking circle, spoon it into a round metal cookie cutter.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD8B71A114352F46A470E59A4EFACB9F', X'EF24AC8A6F5CD74E84856B6DE32C4700', X'EF24AC8A6F5CD74E84856B6DE32C4700', 1, NULL, NULL, 2, NULL, 0, 'Immediately while the candy coating is wet, arrange two colors of M&M''s in a single row around the edge of the circle. Create four circles using Plain M&M''s and four using Peanut Butter M&M''s, or combine them together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF66BAA333ADEE4E976F59DCC567E886', X'EF24AC8A6F5CD74E84856B6DE32C4700', X'EF24AC8A6F5CD74E84856B6DE32C4700', 2, NULL, NULL, 2, NULL, 0, 'Freeze the candy 8 minutes until candy coating hardens. If using the metal cutters, simply pop the candy out once it''s hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B67277ACEBAC340A791D3B3FF5AD2BF', X'EF24AC8A6F5CD74E84856B6DE32C4700', X'EF24AC8A6F5CD74E84856B6DE32C4700', 3, NULL, NULL, 2, NULL, 0, 'Set one cup on a candy saucer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE51D602A5D0184A9202B1EDE3789F29', X'3FF1C73F527E0148A3DA4377D17429AC', X'3FF1C73F527E0148A3DA4377D17429AC', 0, NULL, NULL, 2, NULL, 0, 'Heat the heavy whipping cream in a small saucepan over medium heat just until bubbles begin to form around the edge of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EFD236850F93484E99FBE09A71AC4B59', X'3FF1C73F527E0148A3DA4377D17429AC', X'3FF1C73F527E0148A3DA4377D17429AC', 1, NULL, NULL, 2, NULL, 0, 'Stir in the hot brewed coffee.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D40CE2C93180444186BB6D61DDFA2451', X'3FF1C73F527E0148A3DA4377D17429AC', X'3FF1C73F527E0148A3DA4377D17429AC', 2, NULL, NULL, 2, NULL, 0, 'Remove from the heat and pour over the finely chopped semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC88B227633E754EA5B2A388420F2961', X'3FF1C73F527E0148A3DA4377D17429AC', X'3FF1C73F527E0148A3DA4377D17429AC', 3, NULL, NULL, 2, NULL, 0, 'Let sit for 2 minutes, then stir until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F804A4076BE89849B20200EA3CEAB772', X'3FF1C73F527E0148A3DA4377D17429AC', X'3FF1C73F527E0148A3DA4377D17429AC', 4, NULL, NULL, 2, NULL, 0, 'Allow ganache to cool for 15-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5631232496928E4C8A2A2704386E5FD6', X'3FF1C73F527E0148A3DA4377D17429AC', X'3FF1C73F527E0148A3DA4377D17429AC', 5, NULL, NULL, 2, NULL, 0, 'Assemble Cafe Mocha Rice Krispies Treat Cups and add Topping-');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7978399EFAB8F47B0C44E6A011E301D', X'3FF1C73F527E0148A3DA4377D17429AC', X'3FF1C73F527E0148A3DA4377D17429AC', 6, NULL, NULL, 2, NULL, 0, 'Fill each Rice Krispies Treat Cup with Cafe Mocha Ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9E216DFBA440148A6FD265C544C60B9', X'3FF1C73F527E0148A3DA4377D17429AC', X'3FF1C73F527E0148A3DA4377D17429AC', 7, NULL, NULL, 2, NULL, 0, 'Add three miniature marshmallows and serve with a spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40A348823FCD9A4D9BCCA11589C54413', X'F703D6E37214FC469BD7A186601345C8', X'F703D6E37214FC469BD7A186601345C8', 0, NULL, NULL, 2, NULL, 0, 'Press cookie dough into an 8-10 inch round pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29DBC890E2ECEC4EAE5EB216C84C8539', X'F703D6E37214FC469BD7A186601345C8', X'F703D6E37214FC469BD7A186601345C8', 1, NULL, NULL, 2, NULL, 0, 'Bake at 350 degrees F for 25-30 minutes until golden brown and the center is cooked through.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49DB01D14AED3B46BE5DD68AB076ECF9', X'F703D6E37214FC469BD7A186601345C8', X'F703D6E37214FC469BD7A186601345C8', 2, NULL, NULL, 2, NULL, 0, 'Cool completely, then remove from pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA41B2F2F3015E4C931A3A0FBD54E08B', X'F703D6E37214FC469BD7A186601345C8', X'F703D6E37214FC469BD7A186601345C8', 3, NULL, NULL, 2, NULL, 0, 'Spread half of the frosting over the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0868CCA3F42D14B99040269DB40E350', X'F703D6E37214FC469BD7A186601345C8', X'F703D6E37214FC469BD7A186601345C8', 4, NULL, NULL, 2, NULL, 0, 'Heat the remaining frosting in the microwave for 10-15 seconds, until fluid.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'726D26DA492CB84F899A20FC0AE6682D', X'F703D6E37214FC469BD7A186601345C8', X'F703D6E37214FC469BD7A186601345C8', 5, NULL, NULL, 2, NULL, 0, 'Pour over frosted cookie and smooth out.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0491313AA249C46A135470B336E60C0', X'F703D6E37214FC469BD7A186601345C8', X'F703D6E37214FC469BD7A186601345C8', 6, NULL, NULL, 2, NULL, 0, 'Decorate with candies to look like a garden stone.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A78E45991E2B44A8A4D49B7D1AE58F2', X'F548093CFC81EE48BF52652B1AD45619', X'F548093CFC81EE48BF52652B1AD45619', 0, NULL, NULL, 2, NULL, 0, 'Heat blueberries and water in a small saucepan over medium low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02254152365E2A449A73DF025A0B6C76', X'F548093CFC81EE48BF52652B1AD45619', X'F548093CFC81EE48BF52652B1AD45619', 1, NULL, NULL, 2, NULL, 0, 'Cook blueberries until they can easily be smashed, about 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79DA2537FFB24947BD2EE80179AA601B', X'F548093CFC81EE48BF52652B1AD45619', X'F548093CFC81EE48BF52652B1AD45619', 2, NULL, NULL, 2, NULL, 0, 'Set a fine mesh strainer over a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11C6B05F2C4B1940BB92A815EB755B61', X'F548093CFC81EE48BF52652B1AD45619', X'F548093CFC81EE48BF52652B1AD45619', 3, NULL, NULL, 2, NULL, 0, 'Pour blueberries into strainer, and press until all the liquid flows into the bowl. Discard the solids.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5730BABD0D0B94489B7384E442C583BB', X'F548093CFC81EE48BF52652B1AD45619', X'F548093CFC81EE48BF52652B1AD45619', 4, NULL, NULL, 2, NULL, 0, 'Pour eggs and egg yolks into a 3 or 4 quart saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'365800BD933E8240B235D99B00ABE369', X'F548093CFC81EE48BF52652B1AD45619', X'F548093CFC81EE48BF52652B1AD45619', 5, NULL, NULL, 2, NULL, 0, 'Whisk lightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B31F2BE2C31A314EA1623CDCA9409582', X'F548093CFC81EE48BF52652B1AD45619', X'F548093CFC81EE48BF52652B1AD45619', 6, NULL, NULL, 2, NULL, 0, 'Continue to whisk and slowly drizzle in the hot blueberry puree.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DB86F8FACA5FC4DB9D58DFCA424775B', X'F548093CFC81EE48BF52652B1AD45619', X'F548093CFC81EE48BF52652B1AD45619', 7, NULL, NULL, 2, NULL, 0, 'Add the sugar, butter, and a pinch of salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'851E19645436E44D98EEAFFE175FA12A', X'F548093CFC81EE48BF52652B1AD45619', X'F548093CFC81EE48BF52652B1AD45619', 8, NULL, NULL, 2, NULL, 0, 'Return to medium low heat and stirring constantly with a heat proof rubber spatula or a wooden spoon, cook until thickened, between 10-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'162B2EA3D136AD4D872F450597234BB7', X'F548093CFC81EE48BF52652B1AD45619', X'F548093CFC81EE48BF52652B1AD45619', 9, NULL, NULL, 2, NULL, 0, 'The blueberry curd should coat the back of your spatula. The curd will continue to thicken as it cools.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C26904CD56133A45BB6E8AFE334366DD', X'F548093CFC81EE48BF52652B1AD45619', X'F548093CFC81EE48BF52652B1AD45619', 10, NULL, NULL, 2, NULL, 0, 'Remove curd from heat and pour into a glass bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60401BEFB7CE0B4D97859C8A1F9E768B', X'F548093CFC81EE48BF52652B1AD45619', X'F548093CFC81EE48BF52652B1AD45619', 11, NULL, NULL, 2, NULL, 0, 'Stir in lemon juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF081B8B741D8A48A6686E3A9BE0B766', X'F548093CFC81EE48BF52652B1AD45619', X'F548093CFC81EE48BF52652B1AD45619', 12, NULL, NULL, 2, NULL, 0, 'Cool at room temperature for 20 minutes, then refrigerate for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB30A316B3426243BC1BB775BEEF26C8', X'90C7AD2CF6432F41B7345307826875CA', X'90C7AD2CF6432F41B7345307826875CA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C0E840B37BFA74C9305B74F0F97E325', X'90C7AD2CF6432F41B7345307826875CA', X'90C7AD2CF6432F41B7345307826875CA', 1, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, whisk the egg whites until foamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D279D66FB462F47A487ACF67087A48F', X'90C7AD2CF6432F41B7345307826875CA', X'90C7AD2CF6432F41B7345307826875CA', 2, NULL, NULL, 2, NULL, 0, 'Add the coconut and almonds and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F44FAC1FEF82F0428C4C3CB5D64146B7', X'90C7AD2CF6432F41B7345307826875CA', X'90C7AD2CF6432F41B7345307826875CA', 3, NULL, NULL, 2, NULL, 0, 'Mix in melted butter, vanilla and almond extracts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7606982793B2E543914BC4F64D75FC6F', X'90C7AD2CF6432F41B7345307826875CA', X'90C7AD2CF6432F41B7345307826875CA', 4, NULL, NULL, 2, NULL, 0, 'SPRAY your non-stick muffin tins with cooking spray. Don''t forget this, or your coconut cups wont come out of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E75193F385B884495D170FD7C7B98A0', X'90C7AD2CF6432F41B7345307826875CA', X'90C7AD2CF6432F41B7345307826875CA', 5, NULL, NULL, 2, NULL, 0, 'Equally divide the coconut mixture among the 12 muffin cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAC194BDE450B947BCCCB0ED21C75386', X'90C7AD2CF6432F41B7345307826875CA', X'90C7AD2CF6432F41B7345307826875CA', 6, NULL, NULL, 2, NULL, 0, 'Wet your fingers slightly and press the coconut mixture into the bottom and up the sides of each muffin cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6DE407634068F43A125D0DD8E32ADA2', X'90C7AD2CF6432F41B7345307826875CA', X'90C7AD2CF6432F41B7345307826875CA', 7, NULL, NULL, 2, NULL, 0, 'Bake for 20-24 minutes until the edges of your cups are really golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11BB987D8C9DE54D818E42573F4D4F80', X'90C7AD2CF6432F41B7345307826875CA', X'90C7AD2CF6432F41B7345307826875CA', 8, NULL, NULL, 2, NULL, 0, 'Remove and cool for 10 minutes in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA03320EDDA1B147802BFFAFA4A6ED51', X'90C7AD2CF6432F41B7345307826875CA', X'90C7AD2CF6432F41B7345307826875CA', 9, NULL, NULL, 2, NULL, 0, 'Carefully run a thin knife around the top edge of each coconut cup. Allow to cool for 10 more minutes, then remove the cups from the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60C61CD1E7668543BD257D0102B51982', X'90C7AD2CF6432F41B7345307826875CA', X'90C7AD2CF6432F41B7345307826875CA', 10, NULL, NULL, 2, NULL, 0, 'Allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BEC5B26A374934A9E547EB8B922598A', X'E8C5B1022DF0A9468924C9FEDAD8B0B4', X'E8C5B1022DF0A9468924C9FEDAD8B0B4', 0, NULL, NULL, 2, NULL, 0, 'Equally divide the blueberry curd among the coconut macaroon cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E2881CDF70DFF4E9D88D935191F2B63', X'E8C5B1022DF0A9468924C9FEDAD8B0B4', X'E8C5B1022DF0A9468924C9FEDAD8B0B4', 1, NULL, NULL, 2, NULL, 0, 'Chill the cups for 15-60 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F65A96F610E8C4A80BDEF303B4C2D3D', X'E8C5B1022DF0A9468924C9FEDAD8B0B4', X'E8C5B1022DF0A9468924C9FEDAD8B0B4', 2, NULL, NULL, 2, NULL, 0, 'Just before serving top with whipped cream and a few fresh blueberries, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4E434C0930C214D9587CF8AF72F86B2', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 0, NULL, NULL, 2, NULL, 0, 'If baking, preheat oven to 400 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6312B4864252A4DAC877B3966C0412F', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 1, NULL, NULL, 2, NULL, 0, 'Shred the zucchini, carrot, and potato using a cheese grater or food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DAD76B8190E0B4491382D2E525B6791', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 2, NULL, NULL, 2, NULL, 0, 'Finely slice and chop the onion.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F09C4EBF2C57D04C9F52FB00B2592CDE', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 3, NULL, NULL, 2, NULL, 0, 'Place the veggies in a salad spinner or strainer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81A2E161D0487C48AAA1E038511DDCB2', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle with salt and toss to evenly coat the veggies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29BF4DDB6737E247BB9D015F53F65CCA', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 5, NULL, NULL, 2, NULL, 0, 'Set aside for 10 minutes to allow the moister to drain from the vegetables.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'036D86A767B3FD499D84278DDF003ECF', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 6, NULL, NULL, 2, NULL, 0, 'Spin, if using a salad spinner, or press down using paper towels if using a strainer to remove as much liquid as possible.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC1B992D79524145ABBAB8ABE80C6176', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 7, NULL, NULL, 2, NULL, 0, 'Stir then let sit another 5 minutes and repeat until the veggies are quite dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8C1FE9191170C42B9F2EA64131CCD82', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 8, NULL, NULL, 2, NULL, 0, 'Pour into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A95A39949DB85D459EB76B0541C6AE65', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle on the flour and toss to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5EC2CEAB248FB47AEC8CFFBA114B4D0', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 10, NULL, NULL, 2, NULL, 0, 'Stir in the eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA8F34F042B64E45BA52C238CEF90183', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 11, NULL, NULL, 2, NULL, 0, 'If baking, spray a non-stick baking pan with olive oil cooking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E588D45D1D1E4641BDFE5AA8DC85A78F', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 12, NULL, NULL, 2, NULL, 0, 'Spoon the veggie mixture into a football shaped cookie cutter, creating a fritter that is about 1/4 inch thick.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA958909B0627E4F92A4767BBD482BEC', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 13, NULL, NULL, 2, NULL, 0, 'Remove cookie cutter and repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FD857328006C343A69C07F9A5CFB9D1', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 14, NULL, NULL, 2, NULL, 0, 'Spray olive oil spray over top of the fritters.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4BC4CAD68F3A644B8D8E88B64DC898D', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 15, NULL, NULL, 2, NULL, 0, 'Bake for 12 minutes, then remove and flip the fritters over.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B4CBB97EC19E04AA9201755D87EF232', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 16, NULL, NULL, 2, NULL, 0, 'Bake for another 12-15 minutes until evenly browned on both sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'301B4D32F954634FAA4EAEAD5841E22C', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 17, NULL, NULL, 2, NULL, 0, 'If frying, follow instructions in the tutorial.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D25C2781EC5A7E48A6213DEDC80A0917', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 18, NULL, NULL, 2, NULL, 0, 'Spoon 2 tablespoons of the yogurt or sour cream into a pastry bag or zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74E3D29116497B419B35594BECB7706B', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 19, NULL, NULL, 2, NULL, 0, 'Pipe laces and stripes on each of the footballs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'840C51068CA6DA428D36E7461B420AE8', X'37BF38AD00C08E4396F01BA60EF76516', X'37BF38AD00C08E4396F01BA60EF76516', 20, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FE071FDEF69414EA7C708BF6ABABCD9', X'363067BB46490D44B2C9CDAF13F215D8', X'363067BB46490D44B2C9CDAF13F215D8', 0, NULL, NULL, 2, NULL, 0, 'Measure three tablespoons of refried beans into a fine mesh strainer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C052B9A9A415234E9F92CC45208A777C', X'363067BB46490D44B2C9CDAF13F215D8', X'363067BB46490D44B2C9CDAF13F215D8', 1, NULL, NULL, 2, NULL, 0, 'Use a rubber spatula or spoon to press the beans through the strainer. Scrape mashed beans from the back of the strainer into a small bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB8FBBA6C034E1439F0136A7A6FE4DE6', X'363067BB46490D44B2C9CDAF13F215D8', X'363067BB46490D44B2C9CDAF13F215D8', 2, NULL, NULL, 2, NULL, 0, 'Add 4 drops of black food coloring and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B174BA87579DA42AE3A4E355F38C505', X'363067BB46490D44B2C9CDAF13F215D8', X'363067BB46490D44B2C9CDAF13F215D8', 3, NULL, NULL, 2, NULL, 0, 'Add 1-2 more drops until you reach the desired shade of black.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14D1FCA11097334B9E4D3D86879E1634', X'363067BB46490D44B2C9CDAF13F215D8', X'363067BB46490D44B2C9CDAF13F215D8', 4, NULL, NULL, 2, NULL, 0, 'Spoon into a disposable pastry bag or zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8004B69A00E41F48907BB79CFF3DE8ED', X'363067BB46490D44B2C9CDAF13F215D8', X'363067BB46490D44B2C9CDAF13F215D8', 5, NULL, NULL, 2, NULL, 0, 'Pour remaining refried beans into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8EAD08C70B44445922D6493DD4781FD', X'363067BB46490D44B2C9CDAF13F215D8', X'363067BB46490D44B2C9CDAF13F215D8', 6, NULL, NULL, 2, NULL, 0, 'Add cheese cubes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99D771374EC21D41AC98FEF6CCB6A4F0', X'363067BB46490D44B2C9CDAF13F215D8', X'363067BB46490D44B2C9CDAF13F215D8', 7, NULL, NULL, 2, NULL, 0, 'Heat in the microwave for 3-4 minutes, stirring every 45-60 seconds, until the cheese melts. Spread evenly in a 9` pie plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D77502C2B2A72548ACC60DD15E1833E9', X'363067BB46490D44B2C9CDAF13F215D8', X'363067BB46490D44B2C9CDAF13F215D8', 8, NULL, NULL, 2, NULL, 0, 'Cut the tip off the pastry bag or zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C41A85ABFB049743A41F310C8E19A806', X'363067BB46490D44B2C9CDAF13F215D8', X'363067BB46490D44B2C9CDAF13F215D8', 9, NULL, NULL, 2, NULL, 0, 'Pipe the black colored beans onto the dip creating the seams of a basketball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21D8BAEAF6314A49A28484239E57340F', X'D7FE83897F121245968EE8BE81E27E61', X'D7FE83897F121245968EE8BE81E27E61', 0, NULL, NULL, 2, NULL, 0, 'Set flour tortilla on a cutting board. Use a 2 1/2` round cookie cutter to cut circles from the tortilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FA85EE8DF336A4EA0A5231660E4CB37', X'D7FE83897F121245968EE8BE81E27E61', X'D7FE83897F121245968EE8BE81E27E61', 1, NULL, NULL, 2, NULL, 0, 'Cut as many tortilla rounds as you would like. (I made 72 chips for the picture.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF6594B2AE2F994E958F179095126F02', X'D7FE83897F121245968EE8BE81E27E61', X'D7FE83897F121245968EE8BE81E27E61', 2, NULL, NULL, 2, NULL, 0, 'Set tortilla rounds on a sheet of aluminum foil, parchment paper, or wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3BCE98C826A684ABED5452CDE43D39B', X'D7FE83897F121245968EE8BE81E27E61', X'D7FE83897F121245968EE8BE81E27E61', 3, NULL, NULL, 2, NULL, 0, 'Spray each round with Wilton''s Orange Color Mist. Use the black food marker to draw the basketball''s seam lines onto each tortilla round.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B205FCD3120D2444A22E503B54B28D11', X'D7FE83897F121245968EE8BE81E27E61', X'D7FE83897F121245968EE8BE81E27E61', 4, NULL, NULL, 2, NULL, 0, 'Heat oil in fryer or stockpot to 350 degrees. Fry about 10 tortilla rounds at a time, flipping them over midway through, until golden brown, about 30 seconds per side.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01C28B8E750D4546B6F1B70AABC7036B', X'D7FE83897F121245968EE8BE81E27E61', X'D7FE83897F121245968EE8BE81E27E61', 5, NULL, NULL, 2, NULL, 0, 'Set hot chips on paper towels and immediately sprinkle with salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE636F774E4B764781DB01B43033385B', X'D7FE83897F121245968EE8BE81E27E61', X'D7FE83897F121245968EE8BE81E27E61', 6, NULL, NULL, 2, NULL, 0, 'Allow the oil to come back up to temperature before continuing.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E102B90FB771BE4D864F12B67405C12D', X'D7FE83897F121245968EE8BE81E27E61', X'D7FE83897F121245968EE8BE81E27E61', 7, NULL, NULL, 2, NULL, 0, 'You can also choose to bake these chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E7E09A7017463499EB6E10AEC9B5B55', X'D7FE83897F121245968EE8BE81E27E61', X'D7FE83897F121245968EE8BE81E27E61', 8, NULL, NULL, 2, NULL, 0, 'Arrange them in a single layer on a baking sheet, spray them with cooking spray or brush them with oil, and bake at 350 degrees until golden brown, about 10-15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2C4ABDCFD69A341BB7C277CA437A9C8', X'D7FE83897F121245968EE8BE81E27E61', X'D7FE83897F121245968EE8BE81E27E61', 9, NULL, NULL, 2, NULL, 0, 'Serve basketball tortilla chips with basketball bean dip or your favorite dip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11BAE2EA7493314C99915D842AAF80D9', X'0732BBD7F3C0F8479A82E129A8D080EE', X'0732BBD7F3C0F8479A82E129A8D080EE', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0CABBE1BAC57FC4384A3803B510C4E7A', X'0732BBD7F3C0F8479A82E129A8D080EE', X'0732BBD7F3C0F8479A82E129A8D080EE', 1, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA93D26A119FE04A975A26E46DA88F73', X'0732BBD7F3C0F8479A82E129A8D080EE', X'0732BBD7F3C0F8479A82E129A8D080EE', 2, NULL, NULL, 2, NULL, 0, 'Stir in the egg, vanilla, almond extract, and enough orange food coloring to make the mixture really bright.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48ECC168EFCD90429039BF50A885892D', X'0732BBD7F3C0F8479A82E129A8D080EE', X'0732BBD7F3C0F8479A82E129A8D080EE', 3, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients and stir just until combined. If the dough is sticky, add a small amount of flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E80784C4795E5B43987FB5841AFFEBC1', X'1040B357006B2948B8666297C8358DB8', X'1040B357006B2948B8666297C8358DB8', 0, NULL, NULL, 2, NULL, 0, 'Roll some dough out to 3/8 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0B41E8E5CC378499F115E4900CD05CD', X'1040B357006B2948B8666297C8358DB8', X'1040B357006B2948B8666297C8358DB8', 1, NULL, NULL, 2, NULL, 0, 'Cut 6 rounds using the 3 1/4 inch cutter. Remove excess dough, set on a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1715D3E633BB2E48A0D982AE57CB65C1', X'1040B357006B2948B8666297C8358DB8', X'1040B357006B2948B8666297C8358DB8', 2, NULL, NULL, 2, NULL, 0, 'Put the large cutter around one cookie, then cut a smaller hole using the 2 3/4 inch cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B4CAEA743C89840BFF1A81EBD298FE6', X'1040B357006B2948B8666297C8358DB8', X'1040B357006B2948B8666297C8358DB8', 3, NULL, NULL, 2, NULL, 0, 'Remove the center dough round, then remove the small cookie cutter, then the large cookie cutter. This will help keep your cookies in a nice round shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4677821AA5565B4C8E5C954AD7CC92D2', X'1040B357006B2948B8666297C8358DB8', X'1040B357006B2948B8666297C8358DB8', 4, NULL, NULL, 2, NULL, 0, 'Place the baking sheet in the refrigerator for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83A0B1A4E0525D40988BF3572E652B51', X'1040B357006B2948B8666297C8358DB8', X'1040B357006B2948B8666297C8358DB8', 5, NULL, NULL, 2, NULL, 0, 'Roll the remaining dough out to 3/16th inch thickness, and cut 12 circles using the 3 1/4 inch cutter, re-rolling as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47C2D68E4C8F634998E89C85EF32D0B0', X'1040B357006B2948B8666297C8358DB8', X'1040B357006B2948B8666297C8358DB8', 6, NULL, NULL, 2, NULL, 0, 'Set cookies on parchment paper-lined cookie sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE74D509AD221F4BA436AB1AD3BF8674', X'1040B357006B2948B8666297C8358DB8', X'1040B357006B2948B8666297C8358DB8', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEC98ED2D794D840A7FC0C996CB8F142', X'34E20FE6EE60374C8746FB82B219D7DF', X'34E20FE6EE60374C8746FB82B219D7DF', 0, NULL, NULL, 2, NULL, 0, 'Bake cookies for 10-12 minutes until the edges look set and the center of the cookie no longer looks wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1F0FBEAB3F83A42873215CC345D29E5', X'34E20FE6EE60374C8746FB82B219D7DF', X'34E20FE6EE60374C8746FB82B219D7DF', 1, NULL, NULL, 2, NULL, 0, 'Allow cookies to cool completely on the baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'413BDC5BB44B634B8C889F0A0FB35BD6', X'34E20FE6EE60374C8746FB82B219D7DF', X'34E20FE6EE60374C8746FB82B219D7DF', 2, NULL, NULL, 2, NULL, 0, 'Decorate and Assemble Cookies');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFFA67E2468F4D429B45D15C81F690DD', X'34E20FE6EE60374C8746FB82B219D7DF', X'34E20FE6EE60374C8746FB82B219D7DF', 3, NULL, NULL, 2, NULL, 0, 'Heat orange candy melts on high in the microwave for 20-second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F10BA022C195348958E20A93702E3EB', X'34E20FE6EE60374C8746FB82B219D7DF', X'34E20FE6EE60374C8746FB82B219D7DF', 4, NULL, NULL, 2, NULL, 0, 'Spread a thin layer over 6 of the whole round cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E91F05B71736F541BE897DCB3C401F3F', X'34E20FE6EE60374C8746FB82B219D7DF', X'34E20FE6EE60374C8746FB82B219D7DF', 5, NULL, NULL, 2, NULL, 0, 'Place those 6 cookies in the freezer for about 4 minutes, just until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9FDD56B2E14D724BAB516316A50BA776', X'34E20FE6EE60374C8746FB82B219D7DF', X'34E20FE6EE60374C8746FB82B219D7DF', 6, NULL, NULL, 2, NULL, 0, 'Pour the remaining orange candy melts into a small zip-top bag and snip off the tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'904F89BC777CF24FBADB196B5CAA7851', X'34E20FE6EE60374C8746FB82B219D7DF', X'34E20FE6EE60374C8746FB82B219D7DF', 7, NULL, NULL, 2, NULL, 0, 'Pipe candy melts around the bottom edge of a cookie with a hole in it.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E574CAF65FC8F549AD770ED1476D60D0', X'34E20FE6EE60374C8746FB82B219D7DF', X'34E20FE6EE60374C8746FB82B219D7DF', 8, NULL, NULL, 2, NULL, 0, 'Set that cookie on top of a solid round cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E8EB3CFBD5FCEF40B64F34B245D16475', X'34E20FE6EE60374C8746FB82B219D7DF', X'34E20FE6EE60374C8746FB82B219D7DF', 9, NULL, NULL, 2, NULL, 0, 'Fill the holes with Skittles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D77849BE6E5A9F449A0DCF9632EBA966', X'34E20FE6EE60374C8746FB82B219D7DF', X'34E20FE6EE60374C8746FB82B219D7DF', 10, NULL, NULL, 2, NULL, 0, 'Pipe a thin band of orange coating around the top edge of the cookies with holes in them then set a candy-coated cookie on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C3EE2CF44332E47A02CF50FC6177564', X'34E20FE6EE60374C8746FB82B219D7DF', X'34E20FE6EE60374C8746FB82B219D7DF', 11, NULL, NULL, 2, NULL, 0, 'Press down gently to seal the two cookies together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C9ED150E60F22419B45686D5F5988BD', X'34E20FE6EE60374C8746FB82B219D7DF', X'34E20FE6EE60374C8746FB82B219D7DF', 12, NULL, NULL, 2, NULL, 0, 'Use a black food coloring marker to draw on the seam lines.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCFD8B6F43B0814099AF6C7314CE2859', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 0, NULL, NULL, 2, NULL, 0, 'Place the bacon pieces in a large skillet and set over medium-high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'373531CD395F6F43A219732ABD4DD6EB', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 1, NULL, NULL, 2, NULL, 0, 'Cook until crispy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23846D7C3B876C40ABBB52A701ED4FDD', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 2, NULL, NULL, 2, NULL, 0, 'Remove the bacon and all but 1 tablespoon of the fat from the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B4CF1C59DA2004BAE0B722C77742A3F', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 3, NULL, NULL, 2, NULL, 0, 'Season the ground sirloin with salt, garlic salt, and pepper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5BF92E97D097545AE466A1D9720BD22', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 4, NULL, NULL, 2, NULL, 0, 'Brown half of the ground sirloin in the hot skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7ABD9E89230D04498E03AF119D9F7F69', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 5, NULL, NULL, 2, NULL, 0, 'Remove the browned hamburger from the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC4C4B43DFBB9743B3DF29E5C6262061', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 6, NULL, NULL, 2, NULL, 0, 'If needed, add more bacon grease to the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2326480DA59CCF46AF19184BF3ED4EBF', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 7, NULL, NULL, 2, NULL, 0, 'Brown the remaining ground sirloin in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1ACEEC8BDDE7E345838ED6E2D8E17674', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 8, NULL, NULL, 2, NULL, 0, 'Add the other hamburger back into the pan and reduce the heat to low.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEDA776D7C01F9459672B3AF4AE7E3C5', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 9, NULL, NULL, 2, NULL, 0, 'Stir in the ketchup, Worcestershire sauce, and yellow mustard.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC4E4F5FE619054286B23C93974DB0DD', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 10, NULL, NULL, 2, NULL, 0, 'Then stir in 1/3 cup sour cream, the cream cheese, and cheddar cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1668EF40BA093A43B6C121BBFE640516', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 11, NULL, NULL, 2, NULL, 0, 'Once all the cheese has melted toss in the diced tomatoes and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21A03BE47CF60C4DA7FE25C985312988', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 12, NULL, NULL, 2, NULL, 0, 'Spoon the hot cheeseburger dip into a ceramic football bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A491E7C2FC23384BB879452B99342630', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 13, NULL, NULL, 2, NULL, 0, 'Spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DA7F04F121C234DA0A240C4091AD72A', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 14, NULL, NULL, 2, NULL, 0, 'Top it with the bacon.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44FA68281C0E994FB210E601D66FA971', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 15, NULL, NULL, 2, NULL, 0, 'Pour the sour cream into a zip top bag and snip off a tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B633596A58FEBE4A964E6E0D5A95BE3D', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 16, NULL, NULL, 2, NULL, 0, 'Pipe a line going across the football then add some laces going across that line.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D284FABF1922C34F90FA01150092FB01', X'E5D9E509A0E14C48BE487F8BA5008902', X'E5D9E509A0E14C48BE487F8BA5008902', 17, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D231BA315CC334DB4B2986A1F7F19B1', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 0, NULL, NULL, 2, NULL, 0, 'Heat taco meat, black beans, and peppers together in a skillet over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E1C23CB2C2D1448BBF2B3B6D8B16DAD', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 1, NULL, NULL, 2, NULL, 0, 'Stir in cheese just until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7C3F9D5B0C7A048839122FFD4FCCD57', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 2, NULL, NULL, 2, NULL, 0, 'Set aside to cool slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'280E8E210104F74487DA7772900DFD7F', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 3, NULL, NULL, 2, NULL, 0, 'Cut 3 football shapes out of each egg roll wrapper using a cookie cutter or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7800005CC64AF04393FDA347F3246F40', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 4, NULL, NULL, 2, NULL, 0, 'Whisk together 1 egg and water to create an egg wash.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84CA9307E1BE2543A13977B0BC709FD3', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 5, NULL, NULL, 2, NULL, 0, 'Brush egg wash over one football shaped egg roll wrapper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'220A301DA6A5EE4CBFFE457547BF79D7', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 6, NULL, NULL, 2, NULL, 0, 'Spoon one tablespoon of the taco mixture onto the football.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37E5F3CEAD53584691FF04CF7F28C247', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 7, NULL, NULL, 2, NULL, 0, 'Set another football shaped egg roll wrapper over top and press and seal the edges.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81D79D766223434B90147F465AE71CFA', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 8, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 30 footballs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'631730D12ADDFB40B994AFA424E4AE50', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 9, NULL, NULL, 2, NULL, 0, 'Heat oil to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88F01362582454448EF7637FE08F7B37', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 10, NULL, NULL, 2, NULL, 0, 'Fry taco footballs until deep golden brown, about 1 minutes, flipping them over as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB22F987A4FEA64291AEDC880534C348', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 11, NULL, NULL, 2, NULL, 0, 'Remove from oil and set on paper towels to drain excess oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9CDF1E55B201046902734CFF08AD046', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 12, NULL, NULL, 2, NULL, 0, 'Spoon sour cream into a pastry or zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0595D946008684A8474630E8FD495D8', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 13, NULL, NULL, 2, NULL, 0, 'Pipe sour cream laces over each football.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E97D1C1864967E42AB1E48BC45FC7B55', X'23981C27C2147841B9D3506074FAF718', X'23981C27C2147841B9D3506074FAF718', 14, NULL, NULL, 2, NULL, 0, 'Serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'048D230540F4934C943A0C554A758D43', X'61104C42DAD32745B31CDF4951CA4D08', X'61104C42DAD32745B31CDF4951CA4D08', 0, NULL, NULL, 2, NULL, 0, 'Coat one bag of popped popcorn with 18 ounces of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'364C814207FEA24297ACD719CFB62C92', X'61104C42DAD32745B31CDF4951CA4D08', X'61104C42DAD32745B31CDF4951CA4D08', 1, NULL, NULL, 2, NULL, 0, 'Press into the bottom and up the sides of a large, plastic, football shaped bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE6520444CDE0846B07D823D551FF4D0', X'61104C42DAD32745B31CDF4951CA4D08', X'61104C42DAD32745B31CDF4951CA4D08', 2, NULL, NULL, 2, NULL, 0, 'Coat one more bag of popped popcorn with 18 ounces of chocolate and continue to press the popcorn up the sides of the football bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81B17DE8346A4C45BFD6C71FEF2DAED5', X'61104C42DAD32745B31CDF4951CA4D08', X'61104C42DAD32745B31CDF4951CA4D08', 3, NULL, NULL, 2, NULL, 0, 'Brush the inside of the football shaped popcorn bowl with 4 ounces of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'473A19A14D8F534DB9C2340F9606B86B', X'61104C42DAD32745B31CDF4951CA4D08', X'61104C42DAD32745B31CDF4951CA4D08', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes then freeze for 10.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F509F5D9E467F343B9887C5A7CD2E0E4', X'61104C42DAD32745B31CDF4951CA4D08', X'61104C42DAD32745B31CDF4951CA4D08', 5, NULL, NULL, 2, NULL, 0, 'Remove popcorn bowl from plastic bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B625CF3044A7D74F88B05FE0DC464904', X'61104C42DAD32745B31CDF4951CA4D08', X'61104C42DAD32745B31CDF4951CA4D08', 6, NULL, NULL, 2, NULL, 0, 'Coat the remaining bag of popcorn, peanuts, and toffee bits with 18 ounces of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C35CD0CA60E84845B6D3CBDC640CFA74', X'61104C42DAD32745B31CDF4951CA4D08', X'61104C42DAD32745B31CDF4951CA4D08', 7, NULL, NULL, 2, NULL, 0, 'Spread onto a parchment paper lined baking pan and refrigerate for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F798D7FF73329438AA38CEAA65A3CAD', X'61104C42DAD32745B31CDF4951CA4D08', X'61104C42DAD32745B31CDF4951CA4D08', 8, NULL, NULL, 2, NULL, 0, 'Break the popcorn into pieces and place in the football shaped popcorn bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC013B7882680C41912E817DBB94D392', X'61104C42DAD32745B31CDF4951CA4D08', X'61104C42DAD32745B31CDF4951CA4D08', 9, NULL, NULL, 2, NULL, 0, 'Decorate the football with white modeling chocolate laces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD0304A812A85544A632F39A8B515C42', X'61104C42DAD32745B31CDF4951CA4D08', X'61104C42DAD32745B31CDF4951CA4D08', 10, NULL, NULL, 2, NULL, 0, 'Roll out the white modeling chocolate to about 1/8th inch thickness. Cut one 5 inch by 1/4 inch strip and attach it to the popcorn bowl using clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88760DFA87E8594A91A9DF79D5E7CCD9', X'61104C42DAD32745B31CDF4951CA4D08', X'61104C42DAD32745B31CDF4951CA4D08', 11, NULL, NULL, 2, NULL, 0, 'Cut eight 3/4 inch by 1/8th inch strips and attach them using piping gel going vertically across the 5 inch strip creating the laces of the football.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3406B3B689254F4BA7D64E62D20F3E3F', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', 0, NULL, NULL, 2, NULL, 0, 'Cut hard boiled eggs in half lengthwise.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5474EE8D2DF9047AB0E95E334DDB9D0', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', 1, NULL, NULL, 2, NULL, 0, 'Remove the yolks and crumble them in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'121CDB4C3BF7424B8D83AC914FB8F3E0', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', 2, NULL, NULL, 2, NULL, 0, 'Add 1/3 cup Miracle Whip or mayonnaise, mustard, pickle juice, and salt and stir until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5258DA0BC63EE04B9A34E099CFE2048A', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', 3, NULL, NULL, 2, NULL, 0, 'Equally divide the filling among the 12 egg halves, spreading the filing in an even layer over top of the egg, leaving about an 1/8th of an inch border around the outer edge of the egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44913DE79912A547B8549009A7AFF6C4', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle bacon over top and use your fingers to press the bacon into a football shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0E90987E8867342A319AED81925354F', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', 5, NULL, NULL, 2, NULL, 0, 'Spoon the remaining 1 tablespoon of Miracle Whip or mayo into a small zip top bag. Snip off the tip leaving a small opening.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'113DEF0A5DD69546BB2145D6C3AB4DB6', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', 6, NULL, NULL, 2, NULL, 0, 'Pipe a straight line of mayo down the center, going lengthwise, of each egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D88BAAACECF8F84AA4350B0DA8D4C565', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', 7, NULL, NULL, 2, NULL, 0, 'Add 3-4 short lines of mayo going across the long line to create the football laces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A41D08146FB444AB323B4826A9E64BE', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', X'3B9CBA9F3D72114A99FCAE21EAAE8AAC', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B69447051527E4DB0DE09E636317A80', X'1DA5A8D738F9924E825D076C8B8140FC', X'1DA5A8D738F9924E825D076C8B8140FC', 0, NULL, NULL, 2, NULL, 0, 'Line two 8 inch square pans with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6F1E9175F4224418C57F897DD3E44DC', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 0, NULL, NULL, 2, NULL, 0, 'Pour the white candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'313C0E8A0C9097459EBEF96FC30735F0', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 1, NULL, NULL, 2, NULL, 0, 'Pour the sweetened condensed milk over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'187AE23F80D0054D874A6499725BE7FF', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power in the microwave for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D81DB3E6B0CC45499FD294B30C34D488', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 3, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'638D9E9BF7F7BB44A0866D7924F96683', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 4, NULL, NULL, 2, NULL, 0, 'Heat on high power for another 30 seconds then let the bowl sit for 2-3 minutes in the microwave.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25274EC85CB09B45834CE954200496F7', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 5, NULL, NULL, 2, NULL, 0, 'Remove and stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'614FF11C31F055488E1F1FC25DCCE72E', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 6, NULL, NULL, 2, NULL, 0, 'If needed let bowl sit another few minutes and stir again.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C644834D91C7341B955EDA6C6FFA441', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 7, NULL, NULL, 2, NULL, 0, 'If lumps of candy remain, heat for 10 seconds and stir again.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3EBEE869DA4444B8EC6DAB41242696C', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 8, NULL, NULL, 2, NULL, 0, 'Pour fudge into the prepared pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F090187912AFA94180AABAE32E8D2087', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CD7F857A6B68247BADB627448BF0727', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 10, NULL, NULL, 2, NULL, 0, 'Remove and flip upside down onto a clean piece of parchment paper or a silicone mat set over a cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'803F42DE94910A40A8107218F409036F', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 11, NULL, NULL, 2, NULL, 0, 'Peel the parchment paper off the fudge and place the fudge in the freezer for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'066EEEAE83D2A642B535E0E63A483BF3', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 12, NULL, NULL, 2, NULL, 0, 'Remove and cut using t-shirt shaped cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E44F05C8DECBE84AAFE09AF56D96CF15', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 13, NULL, NULL, 2, NULL, 0, 'If desired, you can re-roll any scraps of fudge and cut out more shirts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'029109E30752404CAE5E0058BE2C3B6D', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 14, NULL, NULL, 2, NULL, 0, 'Pour melted blue Candy Melts into a zip top bag and snip off one tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7EA4C65B541224DA926533E63A91732', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 15, NULL, NULL, 2, NULL, 0, 'Pipe numbers and stripes onto each of the fudge shirts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'175A8D48CBC4694593BC6EFC8DEF6F5E', X'9A6928E81624F244BB9A03083321938E', X'9A6928E81624F244BB9A03083321938E', 16, NULL, NULL, 2, NULL, 0, 'Let candy melts dry before packaging or serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2F60EA8E0075243BE9785096B10EBEB', X'B5AD5AFE8953D14A99EDEF2324CBDB18', X'B5AD5AFE8953D14A99EDEF2324CBDB18', 0, NULL, NULL, 2, NULL, 0, 'Pour blue Candy Melts into microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'593A5C12C0897345A5EBA9BBA8C207D0', X'B5AD5AFE8953D14A99EDEF2324CBDB18', X'B5AD5AFE8953D14A99EDEF2324CBDB18', 1, NULL, NULL, 2, NULL, 0, 'Pour remaining 7 ounces of sweetened condensed milk over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38A5E4659B1DAB4EBDC763833204363F', X'B5AD5AFE8953D14A99EDEF2324CBDB18', X'B5AD5AFE8953D14A99EDEF2324CBDB18', 2, NULL, NULL, 2, NULL, 0, 'Repeat process above.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64D42B92C30C8C499F051683517D04F4', X'6D469A87FD85794781AE5652638E6809', X'6D469A87FD85794781AE5652638E6809', 0, NULL, NULL, 2, NULL, 0, 'Combine white confectionery coating and peanut butter chips in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEF8172859A4A64A9C5391F63C56E683', X'6D469A87FD85794781AE5652638E6809', X'6D469A87FD85794781AE5652638E6809', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1154E4C8A69D6A4983CDF70F7F17D63A', X'6D469A87FD85794781AE5652638E6809', X'6D469A87FD85794781AE5652638E6809', 2, NULL, NULL, 2, NULL, 0, 'Stir in peanut butter and pinch of salt until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B7369E991BC09B418FD0A01C581DB8E6', X'6D469A87FD85794781AE5652638E6809', X'6D469A87FD85794781AE5652638E6809', 3, NULL, NULL, 2, NULL, 0, 'Allow to cool slightly before pouring into your white chocolate shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30442C479D7CFC45ABFB3666AFC0B9A2', X'0F1986C47BA5ED4E8B74EC9B178F3869', X'0F1986C47BA5ED4E8B74EC9B178F3869', 0, NULL, NULL, 2, NULL, 0, 'Spoon about a tablespoon of white chocolate into each baseball mold and brush it up the sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA7D90D460EA6742ADD4A6D7066E5BD2', X'0F1986C47BA5ED4E8B74EC9B178F3869', X'0F1986C47BA5ED4E8B74EC9B178F3869', 1, NULL, NULL, 2, NULL, 0, 'Refrigerate (or freeze if using candy melts) for about 5 minutes until it sets.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6914D06176D2FD4A90D12C02B6A8E32A', X'0F1986C47BA5ED4E8B74EC9B178F3869', X'0F1986C47BA5ED4E8B74EC9B178F3869', 2, NULL, NULL, 2, NULL, 0, 'Fill each mold with fudge and chill until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9CE63843AB3854AAC9894EC28F727F2', X'0F1986C47BA5ED4E8B74EC9B178F3869', X'0F1986C47BA5ED4E8B74EC9B178F3869', 3, NULL, NULL, 2, NULL, 0, 'Top with a thin coating of white chocolate and chill until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'325583251C85DA4CAA2BD1E561923CBA', X'0F1986C47BA5ED4E8B74EC9B178F3869', X'0F1986C47BA5ED4E8B74EC9B178F3869', 4, NULL, NULL, 2, NULL, 0, 'Draw on details using a red food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78A9520552E0FD4DAAF0F6A5B923223E', X'962D28F275039448902493E581EFB37B', X'962D28F275039448902493E581EFB37B', 0, NULL, NULL, 2, NULL, 0, 'Combine Biscoff Spread and Butterscotch Chips together in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C74D53AC5EDF14CB3797604EE863C32', X'962D28F275039448902493E581EFB37B', X'962D28F275039448902493E581EFB37B', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power, for 15-30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04ACC3D120F6524F8FC7D086ADB7B664', X'962D28F275039448902493E581EFB37B', X'962D28F275039448902493E581EFB37B', 2, NULL, NULL, 2, NULL, 0, 'Stir in orange candy coloring to get a nice basketball color.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6210BE85AB382448BB7C54A0DB17680', X'962D28F275039448902493E581EFB37B', X'962D28F275039448902493E581EFB37B', 3, NULL, NULL, 2, NULL, 0, 'Pour into basketball molds equally dividing it among 12 basketball cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16F98D14FE3CBD449096FE5B6C79349C', X'962D28F275039448902493E581EFB37B', X'962D28F275039448902493E581EFB37B', 4, NULL, NULL, 2, NULL, 0, 'Freeze for 30 minutes until solid.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4264477028A90D4E94F1BC4B6CFA4B7C', X'962D28F275039448902493E581EFB37B', X'962D28F275039448902493E581EFB37B', 5, NULL, NULL, 2, NULL, 0, 'Pop candies out of mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'093C6BFE925C5E41B646BA819B0E43E1', X'962D28F275039448902493E581EFB37B', X'962D28F275039448902493E581EFB37B', 6, NULL, NULL, 2, NULL, 0, 'Allow basketballs to thaw and warm to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6C91DBFE3F81542BFA2C0BBC2EB3E23', X'962D28F275039448902493E581EFB37B', X'962D28F275039448902493E581EFB37B', 7, NULL, NULL, 2, NULL, 0, 'Draw on lines using a black food coloring marker, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB9EEFBF2A80A64CAD897C095C86A903', X'44F18688A8539744A25B768C9841E9ED', X'44F18688A8539744A25B768C9841E9ED', 0, NULL, NULL, 2, NULL, 0, 'Draw basketball seam lines on the flat side of each orange candy melt wafer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90C9AF4D271E40479A017477D4F7E740', X'44F18688A8539744A25B768C9841E9ED', X'44F18688A8539744A25B768C9841E9ED', 1, NULL, NULL, 2, NULL, 0, 'Stir the peanuts, pretzels, and Pringles into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1301AA22FD5F5F40AA8B7F17045DBA91', X'44F18688A8539744A25B768C9841E9ED', X'44F18688A8539744A25B768C9841E9ED', 2, NULL, NULL, 2, NULL, 0, 'Spread out into an even layer on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66B39AC05322924093F9644F28125CBF', X'44F18688A8539744A25B768C9841E9ED', X'44F18688A8539744A25B768C9841E9ED', 3, NULL, NULL, 2, NULL, 0, 'Starting around the edge and moving into the center, press the orange candy melt basketballs into the chocolate, leaving enough space to cut around them.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A06CF948FF54C3409E63D82059D1AAA1', X'44F18688A8539744A25B768C9841E9ED', X'44F18688A8539744A25B768C9841E9ED', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate for 3-5 minutes just until the chocolate dries but is not set hard.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5367E5D0BBFC94BAFC3C8738EF64AAA', X'44F18688A8539744A25B768C9841E9ED', X'44F18688A8539744A25B768C9841E9ED', 5, NULL, NULL, 2, NULL, 0, 'Remove and cut the chocolate into pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED4328BFCA94C941B86E75B51ADA6BB2', X'44F18688A8539744A25B768C9841E9ED', X'44F18688A8539744A25B768C9841E9ED', 6, NULL, NULL, 2, NULL, 0, 'Return to the refrigerator for another 5-10 minutes, until the chocolate hardens completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9322C6520962AA4187BD250B3A9EAE13', X'44F18688A8539744A25B768C9841E9ED', X'44F18688A8539744A25B768C9841E9ED', 7, NULL, NULL, 2, NULL, 0, 'Break apart and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AF0D8224544B64A8918B4CA6456F9B8', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', 0, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03F77C166C9B034FAB6D7F4AF9D641A1', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', 1, NULL, NULL, 2, NULL, 0, 'Pour popcorn into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98D2861F0BA80449B49EE73C2C2F4347', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', 2, NULL, NULL, 2, NULL, 0, 'Pour white chocolate over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7112AD9DAEB2614DA02C5B49F48F2084', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', 3, NULL, NULL, 2, NULL, 0, 'Quickly stir it and toss it to coat the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06049A7CCAEBA94F8E17BF21AB78F1AE', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', 4, NULL, NULL, 2, NULL, 0, 'Pour out onto the baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11CD6C24C2191F49BE1463D733D290A9', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', 5, NULL, NULL, 2, NULL, 0, 'Drizzle with colored confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E16B12B5919E0428FCD34D157E6F407', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', 6, NULL, NULL, 2, NULL, 0, 'Place chocolate almond footballs all around popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E278D67B5DF7B4D9010DA2BC7D51D8C', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F0AF39E6BBD0642AE403B4464DB14DA', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', X'2BFEB0B9C289CF4FA26D3B167BC73FDB', 8, NULL, NULL, 2, NULL, 0, 'Remove and break up into bite size chunks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3BD6B639C342BB42A5F10BA533756CD1', X'2DB6A110461A5D46B0214B26ACB690AF', X'2DB6A110461A5D46B0214B26ACB690AF', 0, NULL, NULL, 2, NULL, 0, 'Melt butter in a large microwave safe bowl on high power for 45-75 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E91A0AE9CBFB9646BB8813B380AABC5B', X'2DB6A110461A5D46B0214B26ACB690AF', X'2DB6A110461A5D46B0214B26ACB690AF', 1, NULL, NULL, 2, NULL, 0, 'Stir in dark brown sugar, corn syrup and sweetened condensed milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4A9D32B3F74E04F8995E4B451E9D210', X'2DB6A110461A5D46B0214B26ACB690AF', X'2DB6A110461A5D46B0214B26ACB690AF', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'755662CFE62AAB43B65F595B1B4423A2', X'2DB6A110461A5D46B0214B26ACB690AF', X'2DB6A110461A5D46B0214B26ACB690AF', 3, NULL, NULL, 2, NULL, 0, 'Remove and whisk until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'357AB66ED6F5494A8AE2AEF084DFAB65', X'2DB6A110461A5D46B0214B26ACB690AF', X'2DB6A110461A5D46B0214B26ACB690AF', 4, NULL, NULL, 2, NULL, 0, 'Heat for 5 more minutes, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E2F9567EFB67348A5FC6B963B016DD0', X'2DB6A110461A5D46B0214B26ACB690AF', X'2DB6A110461A5D46B0214B26ACB690AF', 5, NULL, NULL, 2, NULL, 0, 'Heat for an additional 5 minutes, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2D74A089A0C1E4ABAB124BB7F8BB900', X'2DB6A110461A5D46B0214B26ACB690AF', X'2DB6A110461A5D46B0214B26ACB690AF', 6, NULL, NULL, 2, NULL, 0, 'Check temperature, if it''s between 245 - 250 degrees F, pour it into the oval shaped molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B1A9056E9C9D346B9BEC6E26B2A4BE9', X'2DB6A110461A5D46B0214B26ACB690AF', X'2DB6A110461A5D46B0214B26ACB690AF', 7, NULL, NULL, 2, NULL, 0, 'If needed, heat for 1 minute increments, stirring after each until desired temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F8E65FE79EDF942BD51D5B59F3ED5F0', X'2DB6A110461A5D46B0214B26ACB690AF', X'2DB6A110461A5D46B0214B26ACB690AF', 8, NULL, NULL, 2, NULL, 0, 'Let caramels harden for 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0E47447D3114846ADE050582A057006', X'2DB6A110461A5D46B0214B26ACB690AF', X'2DB6A110461A5D46B0214B26ACB690AF', 9, NULL, NULL, 2, NULL, 0, 'Un-mold and set on a silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01F4C33C20C8014B9EBB44D436EDD020', X'2DB6A110461A5D46B0214B26ACB690AF', X'2DB6A110461A5D46B0214B26ACB690AF', 10, NULL, NULL, 2, NULL, 0, 'Pipe white chocolate laces over each football.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACCE0254C5397F4AB70C0670A78929A1', X'C793E1A441A227468722C55A6AEE3472', X'C793E1A441A227468722C55A6AEE3472', 0, NULL, NULL, 2, NULL, 0, 'Pour finely chopped milk chocolate into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8662EE55778D0F4A9509DCED693B2734', X'C793E1A441A227468722C55A6AEE3472', X'C793E1A441A227468722C55A6AEE3472', 1, NULL, NULL, 2, NULL, 0, 'Pour caramel flavored sweetened condensed milk over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7791855B691774193A184F6FA3AC0A3', X'C793E1A441A227468722C55A6AEE3472', X'C793E1A441A227468722C55A6AEE3472', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power for 45 seconds then remove and fold about 10 times. Let the bowl sit for 3 minutes, then fold a few more times.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC2D20A52B78AD4DA1019C21D6D756A5', X'C793E1A441A227468722C55A6AEE3472', X'C793E1A441A227468722C55A6AEE3472', 3, NULL, NULL, 2, NULL, 0, 'Heat for 30 more seconds, then let the bowl sit in the microwave for 3 minutes. Remove and fold 10 times. Let sit for 2 more minutes, then stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11E5671736EC264C8E72CC0A2B7654CD', X'C793E1A441A227468722C55A6AEE3472', X'C793E1A441A227468722C55A6AEE3472', 4, NULL, NULL, 2, NULL, 0, 'If needed, you can heat for 10 second increments if not all melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CFB73B5EB5A214DBFE63CFA3E0AC437', X'C793E1A441A227468722C55A6AEE3472', X'C793E1A441A227468722C55A6AEE3472', 5, NULL, NULL, 2, NULL, 0, 'Cover the fudge and let it rest for 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA7A7F4D144C214FBAE2E7BC0EE38BAB', X'C793E1A441A227468722C55A6AEE3472', X'C793E1A441A227468722C55A6AEE3472', 6, NULL, NULL, 2, NULL, 0, 'Scoop out tablespoon sized balls of fudge. Roll into smooth balls then shape into footballs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93F48837DDFFB54C81F20F5DE43226B0', X'C793E1A441A227468722C55A6AEE3472', X'C793E1A441A227468722C55A6AEE3472', 7, NULL, NULL, 2, NULL, 0, 'Add white chocolate laces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'495DBE72CB8C224B957D85F7474599B4', X'EE4B99C067FBCB43B4A7150A5FAF5560', X'EE4B99C067FBCB43B4A7150A5FAF5560', 0, NULL, NULL, 2, NULL, 0, 'Peel and slice potato into 1/4 inch thick slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'403795C1C95B1E4894E0278AFC26B39D', X'EE4B99C067FBCB43B4A7150A5FAF5560', X'EE4B99C067FBCB43B4A7150A5FAF5560', 1, NULL, NULL, 2, NULL, 0, 'Cut into small 1 1/2 - 2 inch long footballs (ovals.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E40CAF442476B43BE50D2C9337923CF', X'EE4B99C067FBCB43B4A7150A5FAF5560', X'EE4B99C067FBCB43B4A7150A5FAF5560', 2, NULL, NULL, 2, NULL, 0, 'Use a toothpick to carve the football laces in each potato.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'434257568125BC4F9A0B8717E24038B4', X'EE4B99C067FBCB43B4A7150A5FAF5560', X'EE4B99C067FBCB43B4A7150A5FAF5560', 3, NULL, NULL, 2, NULL, 0, 'Pour Swanson Chicken Broth into 3 quart pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98A9A6EA9D97554494C6203DEF45829B', X'EE4B99C067FBCB43B4A7150A5FAF5560', X'EE4B99C067FBCB43B4A7150A5FAF5560', 4, NULL, NULL, 2, NULL, 0, 'Add potatoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F88263044E501A4F9FEE589DA62DDFBC', X'EE4B99C067FBCB43B4A7150A5FAF5560', X'EE4B99C067FBCB43B4A7150A5FAF5560', 5, NULL, NULL, 2, NULL, 0, 'Heat on high until the broth comes to a boil then lower heat to low and simmer for 10-15 minutes until the potatoes are tender.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E95460E418C27A4382ADBC6108C3B7BF', X'EE4B99C067FBCB43B4A7150A5FAF5560', X'EE4B99C067FBCB43B4A7150A5FAF5560', 6, NULL, NULL, 2, NULL, 0, 'Remove and pat potatoes dry with a paper towel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6418E22A74550545909E5C337D7D936D', X'EE4B99C067FBCB43B4A7150A5FAF5560', X'EE4B99C067FBCB43B4A7150A5FAF5560', 7, NULL, NULL, 2, NULL, 0, 'Melt 1 tablespoon butter and 1 1/2 tablespoons oil in a 12 inch skillet set over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4704EC2CC0438E4F8EBAE327EC430B18', X'EE4B99C067FBCB43B4A7150A5FAF5560', X'EE4B99C067FBCB43B4A7150A5FAF5560', 8, NULL, NULL, 2, NULL, 0, 'Add a single layer of potatoes to the pan and cook for about 4 minutes on each side until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF1F61591891D84E8D4BAA6FBE195C49', X'EE4B99C067FBCB43B4A7150A5FAF5560', X'EE4B99C067FBCB43B4A7150A5FAF5560', 9, NULL, NULL, 2, NULL, 0, 'Season with salt immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'628CA575329AF345A1A953CB3F64110A', X'EE4B99C067FBCB43B4A7150A5FAF5560', X'EE4B99C067FBCB43B4A7150A5FAF5560', 10, NULL, NULL, 2, NULL, 0, 'Repeat, heating the remaining butter and oil and frying the remaining potatoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E36AE6E1C744EC4FA81BDD1D61F42F40', X'EE4B99C067FBCB43B4A7150A5FAF5560', X'EE4B99C067FBCB43B4A7150A5FAF5560', 11, NULL, NULL, 2, NULL, 0, 'If needed, reheat in a 400 degree oven for 6-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35A53940A63F234CB51F73F88C326CD0', X'C379B3F6C88F854DBB01DF9D7FFDC71C', X'C379B3F6C88F854DBB01DF9D7FFDC71C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75742D9480173640A9D8E955E9BA2C59', X'C379B3F6C88F854DBB01DF9D7FFDC71C', X'C379B3F6C88F854DBB01DF9D7FFDC71C', 1, NULL, NULL, 2, NULL, 0, 'Cook bacon in a skillet set over medium heat until crisp.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE3FEE969967804C878BB59CACDD0923', X'C379B3F6C88F854DBB01DF9D7FFDC71C', X'C379B3F6C88F854DBB01DF9D7FFDC71C', 2, NULL, NULL, 2, NULL, 0, 'Remove bacon using a slotted spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'722B3F32967E83479F76F9ABED0445F4', X'C379B3F6C88F854DBB01DF9D7FFDC71C', X'C379B3F6C88F854DBB01DF9D7FFDC71C', 3, NULL, NULL, 2, NULL, 0, 'Add the mushrooms to the pan and saute until nicely browned.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0298726B6FBFA74FB2D66CEC27B65991', X'C379B3F6C88F854DBB01DF9D7FFDC71C', X'C379B3F6C88F854DBB01DF9D7FFDC71C', 4, NULL, NULL, 2, NULL, 0, 'Remove from pan and add the ground beef, salt, pepper, and Worcestershire sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F8E33F878EEB14F9435A5A25BB5D2B3', X'C379B3F6C88F854DBB01DF9D7FFDC71C', X'C379B3F6C88F854DBB01DF9D7FFDC71C', 5, NULL, NULL, 2, NULL, 0, 'Cook until browned.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59FC1F65C11DA04F9D0EEC7BDCB0F060', X'C379B3F6C88F854DBB01DF9D7FFDC71C', X'C379B3F6C88F854DBB01DF9D7FFDC71C', 6, NULL, NULL, 2, NULL, 0, 'Remove pan from heat and stir in the mushrooms, cream cheese and sour cream. Stir in half of the cheddar cheese, Monterey Jack cheese, and bacon. Pour into a baking dish and sprinkle on the remaining cheese and bacon. Bake for 20-30 minutes until bubbly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D4F46817FC62546A5FF69F3F8639322', X'C379B3F6C88F854DBB01DF9D7FFDC71C', X'C379B3F6C88F854DBB01DF9D7FFDC71C', 7, NULL, NULL, 2, NULL, 0, 'Top with a goal post made out of the Cheddar Cheese Cheez-It Crunch''d.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F8297E9C50FAD428DCD24BC78F3A7A3', X'C379B3F6C88F854DBB01DF9D7FFDC71C', X'C379B3F6C88F854DBB01DF9D7FFDC71C', 8, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B94C50292BD61D498A87497ADE901FC7', X'62217A852D47AC40950CAE7B0F715B92', X'62217A852D47AC40950CAE7B0F715B92', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7B8F4C1AC6D3D43A99DDB9FBDD19A9A', X'62217A852D47AC40950CAE7B0F715B92', X'62217A852D47AC40950CAE7B0F715B92', 1, NULL, NULL, 2, NULL, 0, 'Stir together mashed potatoes, egg, cheddar cheese, and bacon.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45F224AE171FEA43B9F615E59C09DDAE', X'62217A852D47AC40950CAE7B0F715B92', X'62217A852D47AC40950CAE7B0F715B92', 2, NULL, NULL, 2, NULL, 0, 'Grease the football cavities with butter. Sprinkle about a tablespoon of crushed Doritos into each football cavity.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'789B54E8429D2C4BB24F947F7068BC0F', X'62217A852D47AC40950CAE7B0F715B92', X'62217A852D47AC40950CAE7B0F715B92', 3, NULL, NULL, 2, NULL, 0, 'Spoon an even layer of mashed potatoes into each football (about 2 tablespoons.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDB627864C60274D9FB0969D0A65BEA2', X'62217A852D47AC40950CAE7B0F715B92', X'62217A852D47AC40950CAE7B0F715B92', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle more Doritos crumbs over the mashed potatoes. Press the crumbs into the potatoes using your fingers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'833FF9D0DC76F7438C5462745E904AD3', X'62217A852D47AC40950CAE7B0F715B92', X'62217A852D47AC40950CAE7B0F715B92', 5, NULL, NULL, 2, NULL, 0, 'Bake for 15 minutes. Remove footballs from mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DFF2648EF2F76E4C81D69864257B568B', X'62217A852D47AC40950CAE7B0F715B92', X'62217A852D47AC40950CAE7B0F715B92', 6, NULL, NULL, 2, NULL, 0, 'Heat a thin layer of oil over medium heat. Fry footballs until golden brown, about 30 seconds on each side. Drain on paper towels.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0CF76CBDBFBB624593D075389FA1306C', X'62217A852D47AC40950CAE7B0F715B92', X'62217A852D47AC40950CAE7B0F715B92', 7, NULL, NULL, 2, NULL, 0, 'Pipe sour cream laces and stripes onto footballs. Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'267A0D63AADE7F449BC30065DDA32E10', X'33221FAE6DC3254FB07FB08C94A551C0', X'33221FAE6DC3254FB07FB08C94A551C0', 0, NULL, NULL, 2, NULL, 0, 'Heat white chocolate or white confectionery coating, sweetened condensed milk and salt in a medium saucepan set over low heat until 50% melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36F53B9F8653BA4389F9C401443A6371', X'33221FAE6DC3254FB07FB08C94A551C0', X'33221FAE6DC3254FB07FB08C94A551C0', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat and let sit for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63C4686AB2A6FE429D1D97E11BC05E5E', X'33221FAE6DC3254FB07FB08C94A551C0', X'33221FAE6DC3254FB07FB08C94A551C0', 2, NULL, NULL, 2, NULL, 0, 'Add 1/4 cup of the OREO cookie crumbs and stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3200AE02304CC54983B4FE3B5CE7E990', X'33221FAE6DC3254FB07FB08C94A551C0', X'33221FAE6DC3254FB07FB08C94A551C0', 3, NULL, NULL, 2, NULL, 0, 'Divide the fudge into 3 or 4 bowls and add varying amounts of the remaining cookie crumbs, cocoa powder, and red food coloring to achieve several different colors of fudge for your stones.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A9FD7EA8128E848AEAE9213A146DE36', X'33221FAE6DC3254FB07FB08C94A551C0', X'33221FAE6DC3254FB07FB08C94A551C0', 4, NULL, NULL, 2, NULL, 0, 'Press a piece of plastic wrap down onto the fudge in each bowl and allow it to sit at room temperature for about 30 minutes, until it is thick and no longer sticky.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED34F2E1319BE14F98B498755A82B31E', X'33221FAE6DC3254FB07FB08C94A551C0', X'33221FAE6DC3254FB07FB08C94A551C0', 5, NULL, NULL, 2, NULL, 0, 'Pinch off varying sizes of fudge, roll into odd shaped balls, and set aside to firm up.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BCE0F4A20172E44B7CBA7551EDBC139', X'33221FAE6DC3254FB07FB08C94A551C0', X'33221FAE6DC3254FB07FB08C94A551C0', 6, NULL, NULL, 2, NULL, 0, 'Display in a dump truck, on a cake, or in a pile.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED612E45C1B26F498C7C48AF6E351B7A', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 0, NULL, NULL, 2, NULL, 0, 'Combine graham cracker crumbs, cinnamon, and melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0E6EEF7E482574BA7CD4D24693781CC', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 1, NULL, NULL, 2, NULL, 0, 'Press the mixture into the bottom and up the sides of the PushPan or spring-form pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E37C223A5E1D5459E02D29F6438F95D', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 2, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until the edges begin to brown. Allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'612A83E13663874F916F72C27BE94139', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 3, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese on medium speed until smooth, scraping down the sides of the bowl and the beater as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5785613B6E1F9A47A29ABB2E80DBD611', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 4, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F850FB58BFC1148A0F42FB56C75AF79', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 5, NULL, NULL, 2, NULL, 0, 'Add eggs one at a time and beat until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D00B8A3D972E114499E1524752A7E9BD', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 6, NULL, NULL, 2, NULL, 0, 'Add sour cream and vanilla and beat until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'943654154E9BB34A915C6888C5FF6699', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 7, NULL, NULL, 2, NULL, 0, 'Pour 1/3 cup of cheesecake batter into a small bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'874D561D57DD0244AF0311D99B38D9A0', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 8, NULL, NULL, 2, NULL, 0, 'Color it red.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EA83007DC08444C8B8B3D188572D5ED', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 9, NULL, NULL, 2, NULL, 0, 'Pour 2 tablespoons of batter into another bowl and color it blue.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'913F50C3934F6F4B9061B8516D56CE77', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 10, NULL, NULL, 2, NULL, 0, 'Pour the colored batter into a squeeze bottle (or use a pastry bag or zip-top bag.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D788B6E8D5B39D41809788AF9C81766C', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 11, NULL, NULL, 2, NULL, 0, 'Create the corner of the flag using blue. You can add small little stars or one big star.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'837C8D989ED81048924BAA4013D211FF', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 12, NULL, NULL, 2, NULL, 0, 'Pipe red stripes across the cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D688319B904AA488D6D92E0919B6E42', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 13, NULL, NULL, 2, NULL, 0, 'Bake in a water bath for 45-50 minutes until the top looks set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20B4A6FF51A2864BAB0CCC0AB2155B9C', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 14, NULL, NULL, 2, NULL, 0, 'The center of the cake will jiggle but not look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'204028409AF2604889EDE542A9C5474A', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 15, NULL, NULL, 2, NULL, 0, 'If you think it might not be quite ready but are worried you will over bake it, you can open the oven door, turn off the oven and leave the cheesecake in the water bath for a while.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3B90BE724F58845806095838AF2AFF5', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 16, NULL, NULL, 2, NULL, 0, 'I''ve done this a few times and it works great. I''ve never had an over baked cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D6A9A6091E793468786E70AD16C8EC8', X'36F43C8A56BD7E47A59A440FC360BD52', X'36F43C8A56BD7E47A59A440FC360BD52', 17, NULL, NULL, 2, NULL, 0, 'Allow the cheesecake to cool for one hour at room temperature then refrigerate for at least 4 hours, preferably overnight.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2B638CA441AD04D8172FA13D40861C4', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BACF0E40810E624F815C6467FD5DB944', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 1, NULL, NULL, 2, NULL, 0, 'Make one cake mix according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A1B71CC45EA3B46B901F8DD57186A01', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 2, NULL, NULL, 2, NULL, 0, 'Divide batter in half. Color one-half red, the other blue.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9FC03984160374E80E358BF7CA31EBC', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 3, NULL, NULL, 2, NULL, 0, 'Line an 18x13 inch baking pan or two 9x13 inch baking pans with non-stick foil or parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'104EC1744537F9468F258C06C34A64A6', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 4, NULL, NULL, 2, NULL, 0, 'Pour batter into disposable pastry bags or zip-top bags. Snip off the tip of the bag. Pipe squiggles of blue batter into the bottom of the pan. Pipe on some squiggles of red batter, then repeat, layering the two colors until you use up all the batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'493A329405193F4C9409CFB948650C98', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 5, NULL, NULL, 2, NULL, 0, 'Bake cake/s for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B37FB0A405AE9E48BE8B75D76468DCF5', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 6, NULL, NULL, 2, NULL, 0, 'Allow cake/s to cool for 20 minutes, then freeze for at least one hour until firm to the touch.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC2610FA49330343AE32817F6BE7457E', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 7, NULL, NULL, 2, NULL, 0, 'Cut out about 45 red and blue swirled stars, using a 1-inch star-shaped cookie cutter. Place cut-outs on a lined baking');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E7927084124CF488FA660721D82294C', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 8, NULL, NULL, 2, NULL, 0, 'sheet and put back in the freezer for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DFC56F155FB0D40A8090C69DC8D0060', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 9, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0FDFD7EEB112A54F948F593B9B88764C', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 10, NULL, NULL, 2, NULL, 0, 'Make two more cake mixes, according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4DC94339546A1345AC56C5477E62CCC3', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 11, NULL, NULL, 2, NULL, 0, 'Prep two 6-inch round by 3-inch tall pans and one 8-inch round by 3-inch tall pans by greasing them with shortening and');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'317EDD38BAF23448892D8F5A6CBD493C', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 12, NULL, NULL, 2, NULL, 0, 'dusting with flour or by spraying with flour non-stick baking spray. Line the bottom of the pans with a round of parchment or non-stick foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1D550DD397FDD4B96E9D4692208D733', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 13, NULL, NULL, 2, NULL, 0, 'Pour one cup batter into the bottom of each pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D576E2C8D520DE4DA54AA6B1B5E964AA', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 14, NULL, NULL, 2, NULL, 0, 'Arrange 10-15 stars in a circle around the center of each pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE71D08144C5614C9E956DC1AC15C4EA', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 15, NULL, NULL, 2, NULL, 0, 'Carefully pour some batter in the center of each pan, around the edge of the pans, and over the tops of the stars.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B98E5D8EAF9504FAE757E67E109FB34', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 16, NULL, NULL, 2, NULL, 0, 'Set the 8-inch pan to the back of your oven on the middle rack then set the two smaller pans on either side in the front of the oven. Bake cakes for 58-64 minutes until a toothpick poked in the center of each cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7FDB3EEE9586A438C75492E4CF11150', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 17, NULL, NULL, 2, NULL, 0, 'Allow cakes to cool completely, about two hours. Remove from pans and peel off parchment rounds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6DE8672F1DDF441B545C6B6A3072109', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 18, NULL, NULL, 2, NULL, 0, 'Trim off domed tops on each cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9915C41C5AF13B4795C21C4499810064', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 19, NULL, NULL, 2, NULL, 0, 'Set one 6 inch round cake on a cake board, top with frosting, add another 6-inch round, and more frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06D1F43237C43A47A770E3D2FF6C7E65', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 20, NULL, NULL, 2, NULL, 0, 'Carve the 8 inch round cake around the circumference at an angle from about 6 inches in the center of the cake to the outer 8-inch edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C12D1AE3277F8A49965ECC04F463FA80', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 21, NULL, NULL, 2, NULL, 0, 'Set on top of stacked cakes and carve to shape into Uncle Sam''s hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'190D7B8A27043E41BB69AA24F62B7909', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 22, NULL, NULL, 2, NULL, 0, 'Frost entire cake, smoothing the frosting well.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB9541B10CCA084C85583FA105394428', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 23, NULL, NULL, 2, NULL, 0, 'Measure the height of the cake. Roll out red fondant. Cut 7 strips of fondant that are the height of the cake by 1 3/8 inch wide.');
