INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5ABCF2B41A3B65408507BEA7E8748ACD', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 27, NULL, NULL, 2, NULL, 0, 'Roll out more red fondant, cut out an 8 inch round, set on top of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7C1E53F062E0A4388773113603CF433', X'71CB14CC8E5389408E838009C8E65E9D', X'71CB14CC8E5389408E838009C8E65E9D', 28, NULL, NULL, 2, NULL, 0, 'Roll more red fondant out and cut a 10 inch round. Set it on a cake board or serving platter. Add a dab of frosting to the center of the red fondant circle. Pick up cake and place it on the fondant circle.');
