INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCB8CA6F6027DC41B95301E43274CC9E', X'BEA8656DE4FE514C88A0A4B57477AEEE', X'BEA8656DE4FE514C88A0A4B57477AEEE', 0, NULL, NULL, 2, NULL, 0, 'Mix cake mix according to package instructions.');
