INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6A5D6A051DF20479ACA8056242A5AF9', X'BEA8656DE4FE514C88A0A4B57477AEEE', X'BEA8656DE4FE514C88A0A4B57477AEEE', 2, NULL, NULL, 2, NULL, 0, 'Bake according to package instructions.');
