INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D808256820EDB40BE12517E0639A9DC', X'83C6905C6CD1FF4FBAAAE98F4552056F', X'83C6905C6CD1FF4FBAAAE98F4552056F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit. Line a 9 inch by 13 inch baking pan with non-stick aluminum foil.');
