INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'913AB679C3071042828FF6FF5A3792DC', X'83C6905C6CD1FF4FBAAAE98F4552056F', X'83C6905C6CD1FF4FBAAAE98F4552056F', 5, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DD7B27A7706F344B7688C19D0749AB2', X'83C6905C6CD1FF4FBAAAE98F4552056F', X'83C6905C6CD1FF4FBAAAE98F4552056F', 6, NULL, NULL, 2, NULL, 0, 'Remove from oven and immediately sprinkle red, white and blue candies over top. Place back in the oven for 5 more minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5222AAF0237D8E46B058066070C555DD', X'83C6905C6CD1FF4FBAAAE98F4552056F', X'83C6905C6CD1FF4FBAAAE98F4552056F', 7, NULL, NULL, 2, NULL, 0, 'Remove from oven and allow to cool for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57CC29FB05204642B4380DDCD96C66E9', X'83C6905C6CD1FF4FBAAAE98F4552056F', X'83C6905C6CD1FF4FBAAAE98F4552056F', 8, NULL, NULL, 2, NULL, 0, 'Drizzle candy melts over top.');
