INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5148026FAE4B84B90DC4B0F74CE2990', X'83C6905C6CD1FF4FBAAAE98F4552056F', X'83C6905C6CD1FF4FBAAAE98F4552056F', 10, NULL, NULL, 2, NULL, 0, 'Remove and allow it to sit at room temperature for at least one hour before cutting into squares.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'544B79518368794986245C85D62AFE5A', X'5193C1E4BC6DBF4EA580B75C4CE1C9E1', X'5193C1E4BC6DBF4EA580B75C4CE1C9E1', 0, NULL, NULL, 2, NULL, 0, 'Pour finely chopped chocolate in a glass bowl.');
