INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DDB72A2BDA8EB42B218D8DBBA42F7EA', X'5193C1E4BC6DBF4EA580B75C4CE1C9E1', X'5193C1E4BC6DBF4EA580B75C4CE1C9E1', 2, NULL, NULL, 2, NULL, 0, 'Bring the heavy whipping cream and corn syrup just to a boil in a saucepan set over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD6E1D42B6AB3B439CB07CBCD0484B97', X'5193C1E4BC6DBF4EA580B75C4CE1C9E1', X'5193C1E4BC6DBF4EA580B75C4CE1C9E1', 3, NULL, NULL, 2, NULL, 0, 'Pour your hot cream over the chopped chocolate and let sit for 3 minutes to allow the cream to begin melting the chocolate.');
