INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5DB4342AF1E2241A8A95CC6F2E92AF5', X'5193C1E4BC6DBF4EA580B75C4CE1C9E1', X'5193C1E4BC6DBF4EA580B75C4CE1C9E1', 5, NULL, NULL, 2, NULL, 0, 'Allow it to cool for 5-10 minutes until it thickens just slightly.');
