INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3884E6F06819F4AA231689F395C55D2', X'51336C89E7080447870EC5AE4266431B', X'51336C89E7080447870EC5AE4266431B', 3, NULL, NULL, 2, NULL, 0, 'Beat on low speed using an electric mixer for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90C2C8E9510BDA4C9B4A1B97CDAE1487', X'51336C89E7080447870EC5AE4266431B', X'51336C89E7080447870EC5AE4266431B', 4, NULL, NULL, 2, NULL, 0, 'increase speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'188EA077F4FDC04C88ECC825F56B0DF0', X'51336C89E7080447870EC5AE4266431B', X'51336C89E7080447870EC5AE4266431B', 5, NULL, NULL, 2, NULL, 0, 'Stir int eh finely chopped coconut.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'795B78BF3400E64F861020AC1FC92089', X'51336C89E7080447870EC5AE4266431B', X'51336C89E7080447870EC5AE4266431B', 6, NULL, NULL, 2, NULL, 0, 'Divide batter in half. One half will remain white.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52C1DFD850883D46AFF341F46D9A51CC', X'51336C89E7080447870EC5AE4266431B', X'51336C89E7080447870EC5AE4266431B', 7, NULL, NULL, 2, NULL, 0, 'Divide the other half into half again and color one part red an the other blue.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'052F29BDB55A8F428236CF8B6FAEC6D9', X'51336C89E7080447870EC5AE4266431B', X'51336C89E7080447870EC5AE4266431B', 8, NULL, NULL, 2, NULL, 0, 'Pour batter into individual pastry bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E60DD2FEC0C9B34690379A4BA446848C', X'51336C89E7080447870EC5AE4266431B', X'51336C89E7080447870EC5AE4266431B', 9, NULL, NULL, 2, NULL, 0, 'Pipe squiggles of batter, layering the colors red, white, blue, white, red, white, etc.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44033379F8D3EA438E250FABC0F3A0D4', X'51336C89E7080447870EC5AE4266431B', X'51336C89E7080447870EC5AE4266431B', 10, NULL, NULL, 2, NULL, 0, 'Tap the pan on the counter a few times to remove air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5D26F268233FF45BC15E54245E1A661', X'51336C89E7080447870EC5AE4266431B', X'51336C89E7080447870EC5AE4266431B', 11, NULL, NULL, 2, NULL, 0, 'Bake cake for 45-55 minutes until a toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97244F63C807AA41A04257048E0EC2D8', X'51336C89E7080447870EC5AE4266431B', X'51336C89E7080447870EC5AE4266431B', 12, NULL, NULL, 2, NULL, 0, 'Remove from oven and allow to cool in the pan for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC8CF54527DD9346A89FEC962EA9892C', X'51336C89E7080447870EC5AE4266431B', X'51336C89E7080447870EC5AE4266431B', 13, NULL, NULL, 2, NULL, 0, 'Remove cake from pan and set on a cooling rack to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A95E882D7935941A5C704E1ECC5EA96', X'51336C89E7080447870EC5AE4266431B', X'51336C89E7080447870EC5AE4266431B', 14, NULL, NULL, 2, NULL, 0, 'Carve off the domed top and sides of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8051FF764015804F9C606540951DF0ED', X'3D9E99ACC917D84284D9AD2DDD0647D1', X'3D9E99ACC917D84284D9AD2DDD0647D1', 0, NULL, NULL, 2, NULL, 0, 'Melt candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0258A286A7AA141B3CB692DC450A753', X'3D9E99ACC917D84284D9AD2DDD0647D1', X'3D9E99ACC917D84284D9AD2DDD0647D1', 1, NULL, NULL, 2, NULL, 0, 'Beat powdered sugar and butter together until fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA28E004D8E8774EB07762C1764AEE04', X'3D9E99ACC917D84284D9AD2DDD0647D1', X'3D9E99ACC917D84284D9AD2DDD0647D1', 2, NULL, NULL, 2, NULL, 0, 'Add coconut milk, melted candy melts, and extract if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC14BF7D1616874DB6C575E4DAC75E44', X'3D9E99ACC917D84284D9AD2DDD0647D1', X'3D9E99ACC917D84284D9AD2DDD0647D1', 3, NULL, NULL, 2, NULL, 0, 'Beat until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA549426F91D9948A04442CEFC25D6F0', X'3D9E99ACC917D84284D9AD2DDD0647D1', X'3D9E99ACC917D84284D9AD2DDD0647D1', 4, NULL, NULL, 2, NULL, 0, 'Spread frosting over cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16FBBA59B80CA94C94FB371188310E45', X'38C89DB032576841838BEF54A3FD3DFE', X'38C89DB032576841838BEF54A3FD3DFE', 0, NULL, NULL, 2, NULL, 0, 'Heat the red candy melts, whipping cream, and corn syrup in a microwave safe bowl on high power for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5D03ABF28148244BFCAAF8E098A5478', X'38C89DB032576841838BEF54A3FD3DFE', X'38C89DB032576841838BEF54A3FD3DFE', 1, NULL, NULL, 2, NULL, 0, 'Stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'448804FB00691948B66820DBDE3F10E3', X'E38FB825A35FFF4B98EF4284A92D5E32', X'E38FB825A35FFF4B98EF4284A92D5E32', 0, NULL, NULL, 2, NULL, 0, 'Repeat steps above to make blue ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8B7C4B2486AF94FBFD64C88820D373A', X'E38FB825A35FFF4B98EF4284A92D5E32', X'E38FB825A35FFF4B98EF4284A92D5E32', 1, NULL, NULL, 2, NULL, 0, 'Allow to cool slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DDCD583CFB6D6438AE2CD8A349ABEFE', X'E38FB825A35FFF4B98EF4284A92D5E32', X'E38FB825A35FFF4B98EF4284A92D5E32', 2, NULL, NULL, 2, NULL, 0, 'Drip ganache over the edges and top of the cake alternating red and blue as you go around the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDE9E1F8F115AD4685CDA8EA5301DF3B', X'BC27210E14605347A6F688B3BDD9FF1C', X'BC27210E14605347A6F688B3BDD9FF1C', 0, NULL, NULL, 2, NULL, 0, 'Either pipe on white frosting stars or roll out and cut stars from white modeling chocolate (candy clay).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC2DCAB35C7D7A438D367F6D6DA9C5C1', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'127D54A513ED3D4C9C3A79973549C512', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 1, NULL, NULL, 2, NULL, 0, 'Spray an 8 inch square pan and 4 small bowls with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86D22603F198DC4AAF4EF7EF5CBE4B89', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 2, NULL, NULL, 2, NULL, 0, 'Separate eggs and pour 4 egg yolks into each of the small bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4002643830C8E40BA13C1F4A8AC3858', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 3, NULL, NULL, 2, NULL, 0, 'Whisk egg whites to break up the thicker parts then pour into the 8 inch pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40F7C7C504A69D47866932266DB39D92', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 4, NULL, NULL, 2, NULL, 0, 'Set pan in a 9x13 inch baking dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71F44E49E9154948BA64F612D96BFAA1', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 5, NULL, NULL, 2, NULL, 0, 'Pour about 2 cups of boiling water into the 9x13 baking dish, being careful no water spills into the egg whites.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3ABDC4EB4E78E5458B015DD4EB38EC64', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 6, NULL, NULL, 2, NULL, 0, 'Cover the pan tightly with tin foil and bake for about 25 minutes until the egg whites are firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F86DB70CCEF034593CAF0E6EAA70872', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 7, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat about 1/2 of an inch of water in a 12 inch skillet set over high heat until boiling.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DB5F13CE13BD746A7A65E28C8D3AF24', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 8, NULL, NULL, 2, NULL, 0, 'Carefully place the 4 small bowls of egg yolks into the boiling water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B40497A3D064F45BB71949608FF7481', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 9, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low and cover the skillet with a tight fitting lid.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFAB546A7E35554F8CEEE74D04CE6F4F', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 10, NULL, NULL, 2, NULL, 0, 'Cook for about 25 minutes until the egg yolks are firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F1E7BD3585F0342870F0E2A8EC2D7C4', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 11, NULL, NULL, 2, NULL, 0, 'Remove the bowls from the water bath and let cool for about 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03EEC003E60CFF4889C57EAEF92296BE', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 12, NULL, NULL, 2, NULL, 0, 'Remove the egg whites from the oven and it''s water bath and allow to cool for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA884799E58D5F42AD10DB2FA655960F', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 13, NULL, NULL, 2, NULL, 0, 'Use a small star shaped cookie cutter to cut out stars from the pan of egg whites.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E582C8FA3C96274E944CD5BD92817EAC', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 14, NULL, NULL, 2, NULL, 0, 'Insert lollipop sticks into the star shaped egg whites.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A0B29641DF26E499EF2214605EE4384', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 15, NULL, NULL, 2, NULL, 0, 'Place the egg yolks in a food process.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'123BFE480CC6354690622E5883E228DB', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 16, NULL, NULL, 2, NULL, 0, 'Pulse until the eggs are finely chopped.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F15E80435296A747832DB927BC86154C', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 17, NULL, NULL, 2, NULL, 0, 'Add Miracle Whip or Mayo, mustard, and relish and pulse until the mixture is really smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57CB82A2701A0D4CAC64062D05DAED00', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 18, NULL, NULL, 2, NULL, 0, 'Taste, then season with salt, and pepper or paprika if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6809E955AB499F4A9450AB0583E994E7', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 19, NULL, NULL, 2, NULL, 0, 'Spoon into a pastry bag fitted with a star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC61C6702F8D0D44A18675A08486CB66', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 20, NULL, NULL, 2, NULL, 0, 'Pipe five small stars on top of the egg white star, on each of the 5 tips, and one in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91916AAEA3FE384393C2833ECC817B31', X'CA65DAC38D02D24E846BDD2DA69C16C3', X'CA65DAC38D02D24E846BDD2DA69C16C3', 21, NULL, NULL, 2, NULL, 0, 'Arrange on a serving platter and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7269DE7056137E4C9B37C4815F548325', X'0BB1C2219F3EEC448C057BFB1B11197C', X'0BB1C2219F3EEC448C057BFB1B11197C', 0, NULL, NULL, 2, NULL, 0, 'Divide the marshmallows among three large mixing bowls (3 ounces in each).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02BB92076781DC438395B6AA28C77DD9', X'0BB1C2219F3EEC448C057BFB1B11197C', X'0BB1C2219F3EEC448C057BFB1B11197C', 1, NULL, NULL, 2, NULL, 0, 'Place 1 tablespoon of butter into each bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5978BAEB30FCC428D946C19FE498E30', X'0BB1C2219F3EEC448C057BFB1B11197C', X'0BB1C2219F3EEC448C057BFB1B11197C', 2, NULL, NULL, 2, NULL, 0, 'Add red candy melts to one bowl, white to another, and blue to the other.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAE533FE0BBC4A419906E7B3FE7DB8E7', X'0BB1C2219F3EEC448C057BFB1B11197C', X'0BB1C2219F3EEC448C057BFB1B11197C', 3, NULL, NULL, 2, NULL, 0, 'Heat one bowl at a time in the microwave for 45-60 seconds until the marshmallows puff up and begin to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A2485ED9A927D4AAF40B7E5494ACF5F', X'0BB1C2219F3EEC448C057BFB1B11197C', X'0BB1C2219F3EEC448C057BFB1B11197C', 4, NULL, NULL, 2, NULL, 0, 'Remove and stir until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6AD96C70EC17D443B09D39E0E2746154', X'0BB1C2219F3EEC448C057BFB1B11197C', X'0BB1C2219F3EEC448C057BFB1B11197C', 5, NULL, NULL, 2, NULL, 0, 'Repeat for the two remaining bowls of marshmallows.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A58898FDB8F5046ACC54330381F5E3D', X'0BB1C2219F3EEC448C057BFB1B11197C', X'0BB1C2219F3EEC448C057BFB1B11197C', 6, NULL, NULL, 2, NULL, 0, 'Stir in red or blue food coloring, if desired, to make the red marshmallows and the blue marshmallows more vibrantly colored.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'498AACFA7132E24B9E0E49182307ADE8', X'0BB1C2219F3EEC448C057BFB1B11197C', X'0BB1C2219F3EEC448C057BFB1B11197C', 7, NULL, NULL, 2, NULL, 0, 'Stir 4 cups of popcorn into each of the bowls until the popcorn is evenly coated.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0213B67365858468DAF2DA3202329C6', X'0BB1C2219F3EEC448C057BFB1B11197C', X'0BB1C2219F3EEC448C057BFB1B11197C', 8, NULL, NULL, 2, NULL, 0, 'Immediately pinch off piece of each color and form 8 multi-colored red, white, and blue popcorn balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BFBB545650C4294788C6A2CDE39F8D80', X'0BB1C2219F3EEC448C057BFB1B11197C', X'0BB1C2219F3EEC448C057BFB1B11197C', 9, NULL, NULL, 2, NULL, 0, 'Store in an airtight container, preferably a metal popcorn tin.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A45FF2B8D3A2E4AB560611E012A53A4', X'194BF7BFFC4A894B8B3DED61175309FB', X'194BF7BFFC4A894B8B3DED61175309FB', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A50847D351E517459D5092BF3A524554', X'194BF7BFFC4A894B8B3DED61175309FB', X'194BF7BFFC4A894B8B3DED61175309FB', 1, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E95E3D0B1941E4E9DFF327F6550231F', X'194BF7BFFC4A894B8B3DED61175309FB', X'194BF7BFFC4A894B8B3DED61175309FB', 2, NULL, NULL, 2, NULL, 0, 'Beat in Miracle Whip, lemon juice, Worcestershire sauce and chicken until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A6C1D36ACD4FD41996F8EB935C7368D', X'194BF7BFFC4A894B8B3DED61175309FB', X'194BF7BFFC4A894B8B3DED61175309FB', 3, NULL, NULL, 2, NULL, 0, 'Spread into a shallow 8 or 9 inch round baking dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C87F2F725741843A0907B09B6A21DE1', X'194BF7BFFC4A894B8B3DED61175309FB', X'194BF7BFFC4A894B8B3DED61175309FB', 4, NULL, NULL, 2, NULL, 0, 'Crush crackers into crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21B916397B8D6D428DEBF212043B40AC', X'194BF7BFFC4A894B8B3DED61175309FB', X'194BF7BFFC4A894B8B3DED61175309FB', 5, NULL, NULL, 2, NULL, 0, 'Divide into two small zip top bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDE506E3414FC342BDF4207A8E5643A9', X'194BF7BFFC4A894B8B3DED61175309FB', X'194BF7BFFC4A894B8B3DED61175309FB', 6, NULL, NULL, 2, NULL, 0, 'Add 5 drops of red coloring into one bag and shake the bake and knead the color into the crumbs, adding more color until you get a nice vibrant red.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9D6C53DAFEF6A46BA591774B235AEE0', X'194BF7BFFC4A894B8B3DED61175309FB', X'194BF7BFFC4A894B8B3DED61175309FB', 7, NULL, NULL, 2, NULL, 0, 'Repeat with the blue.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECD03A4D5B2D044F84F443AEDDDCA93E', X'194BF7BFFC4A894B8B3DED61175309FB', X'194BF7BFFC4A894B8B3DED61175309FB', 8, NULL, NULL, 2, NULL, 0, 'Arrange a variety of star shaped cookies cutters on top of the chicken dip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'747D9173038AC945AC67B4D909F2D069', X'194BF7BFFC4A894B8B3DED61175309FB', X'194BF7BFFC4A894B8B3DED61175309FB', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle the colored cookie crumbs inside the walls of each cookie cutter, making some blue and some red.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F385287AD1DAAE488E32A06B6D2315AD', X'194BF7BFFC4A894B8B3DED61175309FB', X'194BF7BFFC4A894B8B3DED61175309FB', 10, NULL, NULL, 2, NULL, 0, 'Bake for about 15 minutes, just until the dip is hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63D0FD1D8A033247854FCD08718C6F01', X'194BF7BFFC4A894B8B3DED61175309FB', X'194BF7BFFC4A894B8B3DED61175309FB', 11, NULL, NULL, 2, NULL, 0, 'Remove and serve immediately with crackers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A4D6BD55811324292B57B0A1D17D9C5', X'7152508C9F37F14C8A3B20EE8D66104C', X'7152508C9F37F14C8A3B20EE8D66104C', 0, NULL, NULL, 2, NULL, 0, 'Dip an Oreo Cookie into white Candy Melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6809FCBCD1564E4C8373E5F72AA2837F', X'7152508C9F37F14C8A3B20EE8D66104C', X'7152508C9F37F14C8A3B20EE8D66104C', 1, NULL, NULL, 2, NULL, 0, 'Remove and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EEE78CFAEBAEC4FA6C1E7E4CECF5E78', X'7152508C9F37F14C8A3B20EE8D66104C', X'7152508C9F37F14C8A3B20EE8D66104C', 2, NULL, NULL, 2, NULL, 0, 'Set cookie on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'079DA524DA8EFD47B842E8728B9C121E', X'7152508C9F37F14C8A3B20EE8D66104C', X'7152508C9F37F14C8A3B20EE8D66104C', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle on some Pop Rocks and star sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45820C6F6629A44CAA4F84B12E13A047', X'7152508C9F37F14C8A3B20EE8D66104C', X'7152508C9F37F14C8A3B20EE8D66104C', 4, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FADE39108B00214D995C197A9A96C762', X'7152508C9F37F14C8A3B20EE8D66104C', X'7152508C9F37F14C8A3B20EE8D66104C', 5, NULL, NULL, 2, NULL, 0, 'Let candy coating dry on cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6C673E3026ABB48A1781B7F4A92EDD2', X'7152508C9F37F14C8A3B20EE8D66104C', X'7152508C9F37F14C8A3B20EE8D66104C', 6, NULL, NULL, 2, NULL, 0, 'Serve');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'341755B5026A5D449ECC5C78853E0FE7', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 0, NULL, NULL, 2, NULL, 0, 'Line a 9x13 baking pan with a well-buttered piece of parchment or wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E43F3CF72FA7AB4BB80D7B59827D2B6F', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 1, NULL, NULL, 2, NULL, 0, 'Pour marshmallows, butter and red candy melts into a large microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56770B3E932DAE4ABA779A06B1F4EE99', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power for 30 second increments, stirring after each, until melted and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'974EB5DD2C0A0A4DA1613056635DF56F', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 3, NULL, NULL, 2, NULL, 0, 'Stir in red food coloring until the marshmallows are vibrant red.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'327DB7625F96EF4FAFC6BC9CF3674FBD', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 4, NULL, NULL, 2, NULL, 0, 'Pour in cereal and mix until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CD1A81F5411104CB809EBF81E8CEDD1', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 5, NULL, NULL, 2, NULL, 0, 'Spread into an even layer in pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95989CCDDF24EB4AB980830B26F16068', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 6, NULL, NULL, 2, NULL, 0, 'Repeat the process above using white candy melts and white food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6554E709002CC347B05407990FC545CE', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 7, NULL, NULL, 2, NULL, 0, 'Spread the white cereal layer over the red layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0D7C090C78B304DABD3295B61FF91B2', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 8, NULL, NULL, 2, NULL, 0, 'Repeat using blue candy melts and blue coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BA95CFF2CF0D946A999CA54616550A9', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 9, NULL, NULL, 2, NULL, 0, 'Spread the blue layer over the white layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7369650041811844B3A29C8D8A5FCBD2', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 10, NULL, NULL, 2, NULL, 0, 'Press down on the blue layer to compact all three layers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B1E084CF261A7419030FFC22892E288', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 11, NULL, NULL, 2, NULL, 0, 'Remove from pan and set on a silicone mat or buttered parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED2D9208F39DCB43B9BA9F960140C29E', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 12, NULL, NULL, 2, NULL, 0, 'Use a rolling pin to compress the cereal, spreading it out about an inch all around the rectangle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5735F3360D45BC468C5936C4FBC8931A', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 13, NULL, NULL, 2, NULL, 0, 'Roll up into a pinwheel from the short side.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77115E048AC4A5438146542BADD3B1D7', X'87470BA3B76E3B45AD96F11D831D00C0', X'87470BA3B76E3B45AD96F11D831D00C0', 14, NULL, NULL, 2, NULL, 0, 'Cut into 12 slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32ADC167F216B44C899E4DA42874E495', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', 0, NULL, NULL, 2, NULL, 0, 'Dip one pretzel in red candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3B9A79379B52C49B86D5B2D81C922D8', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', 1, NULL, NULL, 2, NULL, 0, 'Set on a parchment lined baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F827C5D6AEDAB48B1C601107125D369', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', 2, NULL, NULL, 2, NULL, 0, 'Dip one pretzel in white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26E03DB6D7D70840B91AE2822824D221', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', 3, NULL, NULL, 2, NULL, 0, 'Set it next to the red pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3D176CD0645204DB1602D4B2E5FB79C', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', 4, NULL, NULL, 2, NULL, 0, 'Dip another red pretzel, set it next to the white one.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41EE59DB40D8714DB78D9757360BE22E', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', 5, NULL, NULL, 2, NULL, 0, 'Then add another red dipped pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B9A34C9B1B0B94B82E73A976D527506', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', 6, NULL, NULL, 2, NULL, 0, 'Place the candy coated pretzel in the freezer for 3-5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F82710148CAF348973C72B6EB1E8149', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', 7, NULL, NULL, 2, NULL, 0, 'Pipe a rectangle of blue candy melts in the upper left corner of the red and white striped pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04A4D7F2BCE86345A4E9FFFC52E7CB59', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', 8, NULL, NULL, 2, NULL, 0, 'Freeze it for about 2 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'483534469D1B234B80660A4C845BEBBD', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', 9, NULL, NULL, 2, NULL, 0, 'Attach 11 white star sprinkles to the blue rectangle using more blue candy melts as `glue.`');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDAF0E0D89E3AA47B1396B019133D002', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', X'20AFF4C0F08FAD4DA78E5DA7961AD6FD', 10, NULL, NULL, 2, NULL, 0, 'Repeat, creating 12 American Flag Pretzels');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4EBE1C3097D6E44A4BBD7B184D75C0E', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 0, NULL, NULL, 2, NULL, 0, 'Heat white chocolate or white confectionery coating and sweetened condensed milk in a medium saucepan over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A38F33618CCD84DBA9161142A9944EB', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 1, NULL, NULL, 2, NULL, 0, 'Stir frequently until about half of the chocolate or candy coating is melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70F7DA4685C565408C18E1542531A9F6', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 2, NULL, NULL, 2, NULL, 0, 'Turn off the heat or remove from the stovetop and let sit for two minutes then stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'729F2E2D87261746B13A041DA595C00C', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 3, NULL, NULL, 2, NULL, 0, 'The heat from the melted chocolate will help to melt the remaining pieces of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'598F84D8FEE8004AB771470E9F7550AD', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 4, NULL, NULL, 2, NULL, 0, 'If you still have un-melted pieces, set the pan back over low heat for a minute, then repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBA68C2FB9A3F9479578E6B0F0185DEF', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 5, NULL, NULL, 2, NULL, 0, 'Equally divide the white chocolate fudge into 3 bowls. Color one bowl red, one bowl blue, and leave one bowl white. Allow the fudge to sit at room temperature for 30-60 minutes to cool and thicken.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2A4B3A783C6654CAE2270B6EBCF6AE6', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 6, NULL, NULL, 2, NULL, 0, 'Divide the red fudge into 12 equal pieces. Roll into balls and press into the bottom of your silicone star molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DEB6EE867F6F140A5824D2E96891D10', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 7, NULL, NULL, 2, NULL, 0, 'Divide the white fudge into 12 equal pieces and press them on top of the red fudge in the molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46712FF3C360D5449BF7B5095F9A10EC', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 8, NULL, NULL, 2, NULL, 0, 'Repeat with the blue fudge.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E504C35A6E494E4DB5BF586F4C914E78', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 9, NULL, NULL, 2, NULL, 0, 'Allow the fudge to sit at room temperature for 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B0586E665E34843B6972193D947253F', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 10, NULL, NULL, 2, NULL, 0, 'Pop the fudge stars into the freezer for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0DED67268BBB443981072FD9349487D', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 11, NULL, NULL, 2, NULL, 0, 'Then remove and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F764B31876A9D64D81D2CB798AE3DD1D', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 12, NULL, NULL, 2, NULL, 0, 'Press on the backside of each star mold, pushing the fudge out onto your work surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD4A2797B7915840A5AD7818C566FA7A', X'7E1E7F31D64651458744FE6FBBC1A390', X'7E1E7F31D64651458744FE6FBBC1A390', 13, NULL, NULL, 2, NULL, 0, 'They are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AD1F6E7B643954F82DD7C112440B6A2', X'F7F1F35A41629A45A994D6FE19EA81E0', X'F7F1F35A41629A45A994D6FE19EA81E0', 0, NULL, NULL, 2, NULL, 0, 'Line rings with acetate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1124E933CE6AA64A96FBD8455AFC5986', X'F7F1F35A41629A45A994D6FE19EA81E0', X'F7F1F35A41629A45A994D6FE19EA81E0', 1, NULL, NULL, 2, NULL, 0, 'Set rings on a baking sheet that is lined with parchment paper. Otherwise, turn the bottom of your springform pan upside down and clamp it in (this makes for easier cutting of the cake.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0FEC838705D214C9E16623E21994EF0', X'1E969C2F5EF8224D97A192111EF889F9', X'1E969C2F5EF8224D97A192111EF889F9', 0, NULL, NULL, 2, NULL, 0, 'Pulse vanilla wafers in food processor until you have fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'793733246C0D524294419D55AE07D404', X'1E969C2F5EF8224D97A192111EF889F9', X'1E969C2F5EF8224D97A192111EF889F9', 1, NULL, NULL, 2, NULL, 0, 'Add blue sanding sugar (OR granulated sugar and some drops of blue food coloring).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9ACFCDDA3E61734496FB485C91A32B8E', X'1E969C2F5EF8224D97A192111EF889F9', X'1E969C2F5EF8224D97A192111EF889F9', 2, NULL, NULL, 2, NULL, 0, 'Pulse to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74FFE5F406F3F74EBD4B1C06AF83D8A9', X'1E969C2F5EF8224D97A192111EF889F9', X'1E969C2F5EF8224D97A192111EF889F9', 3, NULL, NULL, 2, NULL, 0, 'Add more blue coloring, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D6E7922F62EBB4F8EBE49D9F478805A', X'1E969C2F5EF8224D97A192111EF889F9', X'1E969C2F5EF8224D97A192111EF889F9', 4, NULL, NULL, 2, NULL, 0, 'Add the butter and pulse to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D8E40894A06174F9F9907F1824677CC', X'1E969C2F5EF8224D97A192111EF889F9', X'1E969C2F5EF8224D97A192111EF889F9', 5, NULL, NULL, 2, NULL, 0, 'Divide crumbs evenly and press into the bottom of your cake rings or springform pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3C7628987BB0447984A3FE030AE35A4', X'1E969C2F5EF8224D97A192111EF889F9', X'1E969C2F5EF8224D97A192111EF889F9', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate crusts until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C435D0F8EBA6BF4280EAFB8FFA3DCAD5', X'14265896E8201A49BCA3978B3C5EAA72', X'14265896E8201A49BCA3978B3C5EAA72', 0, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat cream cheese and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'997DB7117A97454E9DA3708E8ABC0C24', X'14265896E8201A49BCA3978B3C5EAA72', X'14265896E8201A49BCA3978B3C5EAA72', 1, NULL, NULL, 2, NULL, 0, 'Add vanilla and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5123AD14F8A6F44ABFB4C4AE1CB17436', X'14265896E8201A49BCA3978B3C5EAA72', X'14265896E8201A49BCA3978B3C5EAA72', 2, NULL, NULL, 2, NULL, 0, 'In another mixing bowl beat or whisk heavy whipping cream to stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B519DDAE8BB5874EAA891F967B084B47', X'14265896E8201A49BCA3978B3C5EAA72', X'14265896E8201A49BCA3978B3C5EAA72', 3, NULL, NULL, 2, NULL, 0, 'Fold the whipped cream into the cream cheese mixture. If using frozen whipped topping, like Cool Whip, you can beat it in using an electric mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E62CF43A8C2B64688021A5DD88404CF', X'14265896E8201A49BCA3978B3C5EAA72', X'14265896E8201A49BCA3978B3C5EAA72', 4, NULL, NULL, 2, NULL, 0, 'Pour on top of blue crusts, evenly dividing among cake rings.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0151B5130AE82C48B223F31B7D3F3888', X'14265896E8201A49BCA3978B3C5EAA72', X'14265896E8201A49BCA3978B3C5EAA72', 5, NULL, NULL, 2, NULL, 0, 'Spread filling out in an even layer, smoothing out surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AA7BDD46EB8DE449C25AB3012E107EB', X'14265896E8201A49BCA3978B3C5EAA72', X'14265896E8201A49BCA3978B3C5EAA72', 6, NULL, NULL, 2, NULL, 0, 'Clean any filling that stuck to the sides of your cake rings.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23D10426B2B7AE4E9ECA027C80C4671C', X'14265896E8201A49BCA3978B3C5EAA72', X'14265896E8201A49BCA3978B3C5EAA72', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate until your gele is ready.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1336A19401EFA54F962F52403A8781C7', X'C63B2A031AAC4F4A8669640E035F5953', X'C63B2A031AAC4F4A8669640E035F5953', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle gelatin over 1/3 cup water and allow to bloom for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA1069ED36C3224797482364424143F0', X'C63B2A031AAC4F4A8669640E035F5953', X'C63B2A031AAC4F4A8669640E035F5953', 1, NULL, NULL, 2, NULL, 0, 'Place frozen raspberries and sugar in a saucepan and bring to a boil over medium heat, stirring often.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BC6A739511E89498FD206F94ED83852', X'C63B2A031AAC4F4A8669640E035F5953', X'C63B2A031AAC4F4A8669640E035F5953', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat and pour into a fine mesh sieve set over a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FFEBBA07BE18D64AAAEC030F9F8BB426', X'C63B2A031AAC4F4A8669640E035F5953', X'C63B2A031AAC4F4A8669640E035F5953', 3, NULL, NULL, 2, NULL, 0, 'Press down on the raspberries allowing the juice to drain into the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'818EEEE598BD0942909E545C8088478B', X'C63B2A031AAC4F4A8669640E035F5953', X'C63B2A031AAC4F4A8669640E035F5953', 4, NULL, NULL, 2, NULL, 0, 'Discard seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00FB776ECD5D9544A41AAFA568871E9D', X'C63B2A031AAC4F4A8669640E035F5953', X'C63B2A031AAC4F4A8669640E035F5953', 5, NULL, NULL, 2, NULL, 0, 'Add the bloomed gelatin to the bowl, stirring to dissolve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31E1944EA97B4849A2B047942E3DA002', X'C63B2A031AAC4F4A8669640E035F5953', X'C63B2A031AAC4F4A8669640E035F5953', 6, NULL, NULL, 2, NULL, 0, 'Stir in lemon juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DE540B0EE22EE46B3013C18F187E356', X'C63B2A031AAC4F4A8669640E035F5953', X'C63B2A031AAC4F4A8669640E035F5953', 7, NULL, NULL, 2, NULL, 0, 'Allow to cool until it is slightly thickened but still pourable.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81DE60BEB646524E9DCCB15B38FD44E9', X'C63B2A031AAC4F4A8669640E035F5953', X'C63B2A031AAC4F4A8669640E035F5953', 8, NULL, NULL, 2, NULL, 0, 'Pour raspberry gele on top of your cheesecakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEC0CA30963F8B408450A7A470D0E8EE', X'C63B2A031AAC4F4A8669640E035F5953', X'C63B2A031AAC4F4A8669640E035F5953', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate until set, at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'006D83CFC61F0F45B2A51B01ADD3600E', X'C6F6449960B09E43ADE05824B3376781', X'C6F6449960B09E43ADE05824B3376781', 0, NULL, NULL, 2, NULL, 0, 'Pour the popcorn into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'267B1EE16193AD46B3C1B07CECA419B3', X'C6F6449960B09E43ADE05824B3376781', X'C6F6449960B09E43ADE05824B3376781', 1, NULL, NULL, 2, NULL, 0, 'Then, pour the melted orange candy coating over the popped popcorn and toss to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A94DA5E3B83EB64BA0583E6E04DB2A0A', X'C6F6449960B09E43ADE05824B3376781', X'C6F6449960B09E43ADE05824B3376781', 2, NULL, NULL, 2, NULL, 0, 'Stir in the OREO cookie pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8E1BC436386FC479733FAE8A910961E', X'C6F6449960B09E43ADE05824B3376781', X'C6F6449960B09E43ADE05824B3376781', 3, NULL, NULL, 2, NULL, 0, 'Divide in half and spread onto two baking sheets lined with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DE7A4666D681848ABC2A71E94AC97CB', X'C6F6449960B09E43ADE05824B3376781', X'C6F6449960B09E43ADE05824B3376781', 4, NULL, NULL, 2, NULL, 0, 'Drizzle the black candy melts over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4A395D688E50049819BA27ADCA6AB5E', X'C6F6449960B09E43ADE05824B3376781', X'C6F6449960B09E43ADE05824B3376781', 5, NULL, NULL, 2, NULL, 0, 'Freeze or refrigerate for 5-15 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99F5D5BF8FC60F4DB4C8BD3BA486F4B1', X'C6F6449960B09E43ADE05824B3376781', X'C6F6449960B09E43ADE05824B3376781', 6, NULL, NULL, 2, NULL, 0, 'Remove and break the popcorn into bite-sized chunks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73F3BA25BE3E624D9BFFEE2478EE2724', X'C6F6449960B09E43ADE05824B3376781', X'C6F6449960B09E43ADE05824B3376781', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D84331DA800C2F499B52A370AF6B569D', X'32425DD15BED544B86A7264468AED275', X'32425DD15BED544B86A7264468AED275', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B34AABA9080641498EE536024460961B', X'32425DD15BED544B86A7264468AED275', X'32425DD15BED544B86A7264468AED275', 1, NULL, NULL, 2, NULL, 0, 'Butter the cavities in your molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE07899F42E4D9428D70C7CD1863ADFD', X'32425DD15BED544B86A7264468AED275', X'32425DD15BED544B86A7264468AED275', 2, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1220B68CDCD3824C99301F575CCE8570', X'32425DD15BED544B86A7264468AED275', X'32425DD15BED544B86A7264468AED275', 3, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1A1AF4B4603064DAB4D5CB18670AA6F', X'32425DD15BED544B86A7264468AED275', X'32425DD15BED544B86A7264468AED275', 4, NULL, NULL, 2, NULL, 0, 'Add heavy whipping cream, eggs, and vanilla and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74235BE6D2ABA543BBF657DF11943485', X'32425DD15BED544B86A7264468AED275', X'32425DD15BED544B86A7264468AED275', 5, NULL, NULL, 2, NULL, 0, 'Set brownie pop molds on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97827E52F684034C9D33E6F35BB49649', X'32425DD15BED544B86A7264468AED275', X'32425DD15BED544B86A7264468AED275', 6, NULL, NULL, 2, NULL, 0, 'Fill 18-22 brownie pop cavities with cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31D11401492AEC468A9F979A0837D763', X'32425DD15BED544B86A7264468AED275', X'32425DD15BED544B86A7264468AED275', 7, NULL, NULL, 2, NULL, 0, 'Bake for 22-25 minutes until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F094A430FD54364DBE6FF62DDBD4FC2F', X'32425DD15BED544B86A7264468AED275', X'32425DD15BED544B86A7264468AED275', 8, NULL, NULL, 2, NULL, 0, 'Allow them to cool at room temperature for one hour, then freeze them for at least 3 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B291099ADC0DB94EBF10BFFF1F08C94D', X'32425DD15BED544B86A7264468AED275', X'32425DD15BED544B86A7264468AED275', 9, NULL, NULL, 2, NULL, 0, 'Un-mold them onto small dessert plates.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED6C3490EFC23741B922A52E2BD525DD', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 0, NULL, NULL, 2, NULL, 0, 'Pour the melted candy coating or melted and tempered semi-sweet chocolate into a small shallow bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23C807AA45E5424182BA92EB823A8087', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 1, NULL, NULL, 2, NULL, 0, 'Dip an Oreo into the chocolate, lift it out using a dipping fork, allow the excess candy to drip off, then set the cookie on a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'817F7C17C7E29C44B3A375581D84B263', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 2, NULL, NULL, 2, NULL, 0, 'Allow the candy coating to dry for a moment, then using the fork, press down on the candy, and lift the fork away quickly, pulling the candy up into furry looking peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC331E95A1C56F49A4AE0C1041519B5B', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 3, NULL, NULL, 2, NULL, 0, 'Do this all over the cookie, rotating the fork, so you don''t just end up with lines of fur.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F682F8EC3D7CB946AFEE01AF780213E6', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 4, NULL, NULL, 2, NULL, 0, 'Repeat the process with a vanilla wafer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF4A2BC480539D4380A76BBB9D2785B3', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 5, NULL, NULL, 2, NULL, 0, 'Set the chocolate-coated vanilla wafer next to the chocolate-coated Oreo.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'994A19F9FA4C0B45BEB7C969BEF1AC48', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 6, NULL, NULL, 2, NULL, 0, 'Use the dipping fork to create spikes of fur on this cookie too.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00E821943C503A41BF4DB1016169C8E0', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 7, NULL, NULL, 2, NULL, 0, 'Chill the cookies after you get each one made. If you are using pure chocolate chill the chocolate-dipped cookies in the refrigerator for about 5 minutes until the chocolate sets. If you use candy melts place the cookies in the freezer instead.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13D96FCA26FFEE4391142650A9D53956', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 8, NULL, NULL, 2, NULL, 0, 'After all 8 cats are created, pour the remaining chocolate in a Squeezeit Mold Painter or a zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D0DA7947FD8B1498E84592D329594AB', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 9, NULL, NULL, 2, NULL, 0, 'Pipe two thick ears onto the cat''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'951D7781DB46324B864999E06A8C48B5', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 10, NULL, NULL, 2, NULL, 0, 'Then pipe a thick curled tail onto the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06B80F2BD1994F4592BBE632975BE343', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 11, NULL, NULL, 2, NULL, 0, 'Chill the tails until they are hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9376350278A9D044BC54B35964C25F2F', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 12, NULL, NULL, 2, NULL, 0, 'Remove the tails from the refrigerator (pure chocolate) or freezer (candy melts) and peel them off the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'803F41E4283D424F8D358084B3256576', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 13, NULL, NULL, 2, NULL, 0, 'Attach the tails using a dab of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A43D2A7E433314BA948F76094ED8847', X'22ACFFCDD9478949B47DCE41362BDAA1', X'22ACFFCDD9478949B47DCE41362BDAA1', 14, NULL, NULL, 2, NULL, 0, 'Chill just until the ears harden and the tail stays in place');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEE7EB82A114A84683FC47EB626E12E3', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 0, NULL, NULL, 2, NULL, 0, 'Cut the edges off the chocolate cake and discard.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACD5915814690A4A841693502A8FF7BC', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 1, NULL, NULL, 2, NULL, 0, 'Break up the center of the cake into a mixing bowl or the bowl of a stand mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6DA6374D0196E942A472F81E6E49C0E8', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 2, NULL, NULL, 2, NULL, 0, 'Add 1/4 cup white frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B29339E6A928FD469055EB2259EC3F74', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 3, NULL, NULL, 2, NULL, 0, 'Mix until it holds together in a soft ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D10EDA322811E743A6BA89C4ED9799DD', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 4, NULL, NULL, 2, NULL, 0, 'Add more frosting if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9CC252FF0F5D44DB74B81D71E7B3AC5', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 5, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7844BC7C449E0644865A78D2D0F0F8E8', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 6, NULL, NULL, 2, NULL, 0, 'If desired, stir in drops of oil-based candy coloring to add color to your white chocolate brains.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D74067F1415D540B25D77118EBF7D03', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 7, NULL, NULL, 2, NULL, 0, 'Spoon some melted white chocolate into one of the brain mold cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6987AD785FD2404BB6EEEC3B78250BB9', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 8, NULL, NULL, 2, NULL, 0, 'Spread the chocolate around to create a nice, evenly coated, thin shell.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D412E1B8ABCA8849B9AA0B2DD1AA7914', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 9, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0CA97F8752737F4E98DF7BB2D01C363B', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 10, NULL, NULL, 2, NULL, 0, 'Wipe off any candy coating around the edges of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6356DB13924BA44E9DCBEDAE118070EA', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 11, NULL, NULL, 2, NULL, 0, 'Freeze for 5 minutes, if you are using candy melts, or refrigerate for 15 minutes, if using pure white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D65CCD1EC9E544191398E1CA6679634', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 12, NULL, NULL, 2, NULL, 0, 'Remove the molds from the freezer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9284654050CB1F4582223158F6793A15', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 13, NULL, NULL, 2, NULL, 0, 'Stir a few drops of liquid food coloring into the fruit preserves.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52FAB654A0D5104DA687A69DA8A454DD', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 14, NULL, NULL, 2, NULL, 0, 'Strain out any pieces of fruit or seeds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'222181A33DD4DD4EA10BD25D4DFE4AD8', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 15, NULL, NULL, 2, NULL, 0, 'Spoon 3/4 teaspoons of the fruit filling into each brain cavity.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'105760ED9358B749ADBE1E1E872A726B', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 16, NULL, NULL, 2, NULL, 0, 'Scoop out about 2 tablespoons of the cake ball mixture and press it into a brain cavity leaving enough room to add a layer of candy coating on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BD47ECD08D2614080EE6B509A7994B9', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 17, NULL, NULL, 2, NULL, 0, 'Spoon some more white candy coating on top of each cake ball and smooth it out.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'012EAFBDA20CA44199305B9EDF59E64B', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 18, NULL, NULL, 2, NULL, 0, 'Be sure to wipe off any excess coating around the edge of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F57F25E028A7C4DB3D96EA640F9F0FF', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 19, NULL, NULL, 2, NULL, 0, 'Freeze for 5 minutes if you used candy melts or refrigerate for about 10 minutes, if you used pure white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B7870892B3B9C47B68B87E03DFC5FFA', X'D01430AAEC496146BB4214C89FF90D77', X'D01430AAEC496146BB4214C89FF90D77', 20, NULL, NULL, 2, NULL, 0, 'Unmold the candies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'230CCE067AF4DF4BB6FD43B3AFD316E1', X'76780BC0D8AFF74F91FC8A6FCC599164', X'76780BC0D8AFF74F91FC8A6FCC599164', 0, NULL, NULL, 2, NULL, 0, 'Stir all of the cheese ball ingredients together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1D4AE43E2DE1341872E54068BDEEF0F', X'76780BC0D8AFF74F91FC8A6FCC599164', X'76780BC0D8AFF74F91FC8A6FCC599164', 1, NULL, NULL, 2, NULL, 0, 'Place 8 ounces of shredded cheddar cheese in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE21BC6ECBC60942AAE0CB78831B7E22', X'76780BC0D8AFF74F91FC8A6FCC599164', X'76780BC0D8AFF74F91FC8A6FCC599164', 2, NULL, NULL, 2, NULL, 0, 'Scoop out a tablespoonful of the cheese ball mixture and drop it into the bowl of cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79CCD64FF3719344B4D630DB2E5B2830', X'76780BC0D8AFF74F91FC8A6FCC599164', X'76780BC0D8AFF74F91FC8A6FCC599164', 3, NULL, NULL, 2, NULL, 0, 'Toss it around in the cheese, pressing the cheese onto it until the entire cheese ball is coated in shreds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA910A1F16452647A418FA5376B5D00B', X'76780BC0D8AFF74F91FC8A6FCC599164', X'76780BC0D8AFF74F91FC8A6FCC599164', 4, NULL, NULL, 2, NULL, 0, 'Repeat until you just have a tablespoon of filling remaining. You will need this to be the glue to hold the eyes and mouths onto your monsters.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'451BD3F5431D2642AD116EAA652D769D', X'76780BC0D8AFF74F91FC8A6FCC599164', X'76780BC0D8AFF74F91FC8A6FCC599164', 5, NULL, NULL, 2, NULL, 0, 'Use a small round fondant cutter to cut eyes out of white cheese slices. Draw pupils onto the eyes using a black food coloring marker. For tips on using your markers go here.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'516B76653AA49145BC936BA2630DBCC6', X'76780BC0D8AFF74F91FC8A6FCC599164', X'76780BC0D8AFF74F91FC8A6FCC599164', 6, NULL, NULL, 2, NULL, 0, 'Break pretzel sticks in half. Stick one, two or three sticks coming out of the top of each mini cheese ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C207349EC91DE448665928301F734E7', X'76780BC0D8AFF74F91FC8A6FCC599164', X'76780BC0D8AFF74F91FC8A6FCC599164', 7, NULL, NULL, 2, NULL, 0, 'Use some of the remaining cheese ball filling to attach the cheese slice eyes to the pretzel sticks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B82C5CB5F800C46A1C84FB051E40560', X'76780BC0D8AFF74F91FC8A6FCC599164', X'76780BC0D8AFF74F91FC8A6FCC599164', 8, NULL, NULL, 2, NULL, 0, 'Use the round fondant cutter to cut small little mouths out of the black olive slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E931107F1C40A4BAE2D57120B45ED00', X'76780BC0D8AFF74F91FC8A6FCC599164', X'76780BC0D8AFF74F91FC8A6FCC599164', 9, NULL, NULL, 2, NULL, 0, 'Use some of the cheese ball filling to attach them to each monster.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49FF45D9E7C9C44A936D2B712D6A910D', X'76780BC0D8AFF74F91FC8A6FCC599164', X'76780BC0D8AFF74F91FC8A6FCC599164', 10, NULL, NULL, 2, NULL, 0, 'Arrange on a serving platter and share with your guests.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45962CB67D3E2348B1A9043E4B3B58A1', X'701A41A33556284F9CF322CD8FB1527B', X'701A41A33556284F9CF322CD8FB1527B', 0, NULL, NULL, 2, NULL, 0, 'Draw some bat wings on a piece of paper allowing enough room for the snack cake in between the wings.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58805424EC26F24DBB5AC7FA4F0C24CB', X'701A41A33556284F9CF322CD8FB1527B', X'701A41A33556284F9CF322CD8FB1527B', 1, NULL, NULL, 2, NULL, 0, 'Draw a mouth and fangs on each snack cake using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'117F2E2427BB054190FB583F7BD9D9BB', X'701A41A33556284F9CF322CD8FB1527B', X'701A41A33556284F9CF322CD8FB1527B', 2, NULL, NULL, 2, NULL, 0, 'Fill your squeeze bottle, disposable pastry bag, or zip-top bag with melted dark cocoa candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61E7CC7431644044BD1E56312C42A823', X'701A41A33556284F9CF322CD8FB1527B', X'701A41A33556284F9CF322CD8FB1527B', 3, NULL, NULL, 2, NULL, 0, 'Squeeze a small drop on the back of each candy eye and attach them to the snack cake just above the mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'076A98CDED68DB48B48737D266A1E61B', X'701A41A33556284F9CF322CD8FB1527B', X'701A41A33556284F9CF322CD8FB1527B', 4, NULL, NULL, 2, NULL, 0, 'Slide the drawing of your wings under your parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF8BEFC5C95B754ABF8F1DD65B8F3BB0', X'701A41A33556284F9CF322CD8FB1527B', X'701A41A33556284F9CF322CD8FB1527B', 5, NULL, NULL, 2, NULL, 0, 'Pipe the melted confectionery coating/candy melts over the drawing creating your wings.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C16D442FAFC6514281C58DB5285E10D6', X'701A41A33556284F9CF322CD8FB1527B', X'701A41A33556284F9CF322CD8FB1527B', 6, NULL, NULL, 2, NULL, 0, 'Pipe a strip of candy coating in the middle of the wings then set the snack cake on top of that.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7E82C7FEF701F40AC4B4414F85AD9BE', X'701A41A33556284F9CF322CD8FB1527B', X'701A41A33556284F9CF322CD8FB1527B', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate until the wings are set, about 3-5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'403EC6C238097443A00EB533A8546E81', X'701A41A33556284F9CF322CD8FB1527B', X'701A41A33556284F9CF322CD8FB1527B', 8, NULL, NULL, 2, NULL, 0, 'Remove the snack cake bat from the refrigerator and carefully peel off the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'318C75BD38AA12498D6C8D8BA10F8127', X'701A41A33556284F9CF322CD8FB1527B', X'701A41A33556284F9CF322CD8FB1527B', 9, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE733561CAA8154BABC73B8D101FD0E9', X'59934F028B8B044A8B4BA36E53D75090', X'59934F028B8B044A8B4BA36E53D75090', 0, NULL, NULL, 2, NULL, 0, 'Mix the sugar, meringue powder, and water together until the sugar holds together when squeezed. If the sugar is too loose add a bit more water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03969880CA46FC4AA5A126150265A940', X'59934F028B8B044A8B4BA36E53D75090', X'59934F028B8B044A8B4BA36E53D75090', 1, NULL, NULL, 2, NULL, 0, 'Fill both the front and back of the skull mold with the wet sugar compacting it tightly so that there are no air holes. Scrape off the excess so that the tops are flat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C75F4D0CCEFFB4DAB4F053305044E88', X'59934F028B8B044A8B4BA36E53D75090', X'59934F028B8B044A8B4BA36E53D75090', 2, NULL, NULL, 2, NULL, 0, 'Cut some parchment paper and cardboard so that they are a bit bigger than the skull mold. Set the parchment paper over the top of the mold then set the cardboard on top of the parchment paper. Grab onto the mold and cardboard, and carefully flip them upside down and set on the counter. Carefully lift the mold up off the sugar skulls. The mold should come right off. If the sugar sticks, it''s too wet. Scrape it out of the mold, clean the mold, and add some more dry sugar to the mixture and try molding it again. If your sugar skulls do not hold together you will need to add a bit more water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DC723A208EF7B419E5A9ED4D84E9747', X'59934F028B8B044A8B4BA36E53D75090', X'59934F028B8B044A8B4BA36E53D75090', 3, NULL, NULL, 2, NULL, 0, 'Allow your sugar skulls to dry for at least 6 hours before flipping them over so that the other side can dry as well. Let them dry a minimum of 12 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C5B4AED6AD030478E0B360CAA531015', X'59934F028B8B044A8B4BA36E53D75090', X'59934F028B8B044A8B4BA36E53D75090', 4, NULL, NULL, 2, NULL, 0, 'Make royal icing by whisking the water and meringue powder then adding the powdered sugar and almond extract and beating until it begins to lose its shine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D6328E84FA63A47B8F3BCB748230422', X'59934F028B8B044A8B4BA36E53D75090', X'59934F028B8B044A8B4BA36E53D75090', 5, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of royal icing on the flat part of the backside of each skull. Press the front and back sides together. Use your finger to wipe off the icing that oozes out from between the two pieces. Allow the icing to dry for at least an hour before decorating your skulls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'292CEC1C7A4EF34A980C445228EE4219', X'59934F028B8B044A8B4BA36E53D75090', X'59934F028B8B044A8B4BA36E53D75090', 6, NULL, NULL, 2, NULL, 0, 'Color the royal icing using food coloring then spoon into pastry bags fitted with small round tips.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'28149C5DC6F18F43BFBDD6B610EC8DCB', X'59934F028B8B044A8B4BA36E53D75090', X'59934F028B8B044A8B4BA36E53D75090', 7, NULL, NULL, 2, NULL, 0, 'Pipe royal icing onto the skulls creating any designs you like.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'064C58C27B74534D82EEBEB6706F7469', X'59934F028B8B044A8B4BA36E53D75090', X'59934F028B8B044A8B4BA36E53D75090', 8, NULL, NULL, 2, NULL, 0, 'Allow the icing to dry for several hours before using them to decorate your Day of the Dead altar.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD1B88D02FBB11428327F5AAD9E54ADC', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 0, NULL, NULL, 2, NULL, 0, 'Lay a large piece of tin foil on the bottom rack in your oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13568216EAB74B41BF1A3EBFD81E5D9D', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 1, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D33220C0B07F1147B50178B50D78109A', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 2, NULL, NULL, 2, NULL, 0, 'Stir a few drops of red food coloring into the marinara sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F638E5B3AFBEDA43A991000856A740BC', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 3, NULL, NULL, 2, NULL, 0, 'Scoop out 2 tablespoons of the sauce and press it through a fine-mesh strainer until you squeeze out about 1 tablespoon of really thin sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E827498E6E61943B5A06219CCED476B', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 4, NULL, NULL, 2, NULL, 0, 'Splatter some of the thin sauce onto a white serving platter and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44CB45C22F4D7B4DB3658E3F24879F72', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 5, NULL, NULL, 2, NULL, 0, 'Reserve the remaining thin sauce to put over the pizza brain.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AB32B580EC8F446BCF8714E261CBF66', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 6, NULL, NULL, 2, NULL, 0, 'Stir the remaining thick sauce back into the rest of the marinara sauce and refrigerate until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6567B96D0106B45BF3E8903D83518CB', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 7, NULL, NULL, 2, NULL, 0, 'Melt the butter then stir in the olive oil, Italian seasoning, garlic powder, and Parmesan cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F000A42D01920249A8053472C77A19D1', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 8, NULL, NULL, 2, NULL, 0, 'Roll the pizza dough out to about 10-inch by 14-inch rectangle. Cut the pizza crust into about 10-inch by 3/4-inch strips (18 strips).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3959CBF7652E5043A1B2773838E4726A', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 9, NULL, NULL, 2, NULL, 0, 'Dip one strip into the melted butter mixture and shake off any excess butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04FA48C85E746E43978193056D13B3DC', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 10, NULL, NULL, 2, NULL, 0, 'Lay the strip of dough in the brain mold, using the lines of the brain as a char(36)e.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A18DCAEB63F26A47995354457AE81100', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 11, NULL, NULL, 2, NULL, 0, 'Continue to dip strips of dough into the butter mixture and arranging them in the mold in squiggly patterns using the mold as a char(36)e.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A002DE4CB1BF6A48A101E4363C653B1A', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 12, NULL, NULL, 2, NULL, 0, 'Cut some really thin pieces and arrange them in the mold to create the Cerebellum.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'342823FEEDA1BB428B9538FDBD7B728D', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 13, NULL, NULL, 2, NULL, 0, 'Once you have covered the inside of the brain mold with pizza dough, start adding the pizza toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CF50F3777EA43408D135A6F8553901C', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 14, NULL, NULL, 2, NULL, 0, 'Sprinkle half of the mini pepperoni over the crust then top with half of the mozzarella cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73D29E03ED37624EA50D5379D593D6EE', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 15, NULL, NULL, 2, NULL, 0, 'Spread two tablespoons of the pizza sauce over the cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA6979ACA264CB47BEAC68D20779B53E', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 16, NULL, NULL, 2, NULL, 0, 'Add one or two strips of butter coated pizza dough over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8BFEA8F59339C48AA03C66D9D3A9963', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 17, NULL, NULL, 2, NULL, 0, 'Then layer on the remaining pepperoni, cheese, and pizza sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74E4CF387C32E34AB5B66AF1B93C0E2B', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 18, NULL, NULL, 2, NULL, 0, 'Dip the remaining strips of pizza dough in the butter and seasoning mixture then layer them in squiggles across the top of the fillings.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D333B656CBC1B4691819D80ED047330', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 19, NULL, NULL, 2, NULL, 0, 'The entire brain should be covered with dough. There will be some holes and that is fine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'138C947A4E9ED64BA9BE043DEDCD3BB6', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 20, NULL, NULL, 2, NULL, 0, 'Set the filled silicone mold on the tin foil over the lower rack in the hot oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'779754B791B78845AB20F484F8C65CE5', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 21, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes then cover the top crust with tin foil. If the center of the dough is not baked through then cut a hole in your tin foil then set the tin foil over the brain so that the hole is open over the middle of the brain.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0ECEBBFAB0B4F4469863D3513C5ABD56', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 22, NULL, NULL, 2, NULL, 0, 'Continue to bake for add addition 12-18 minutes until the crust on top becomes golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD0B12A2FEB8ED4A9B21C1CAF61DF0F0', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 23, NULL, NULL, 2, NULL, 0, 'Remove the mold from the oven. I pulled it out and set it on a cutting board to carry it from the oven to my table.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C4A8EF6F5721848B3FCB3D4B8A992E8', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 24, NULL, NULL, 2, NULL, 0, 'Then set either a large cake spatula or another cutting board over the pizza.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B0B58D9A40B64429C23DF160746DAB9', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 25, NULL, NULL, 2, NULL, 0, 'Hold onto the spatula and the mold and carefully flip the pizza over.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53B084479C854E4681D9FEFC1EF9915A', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 26, NULL, NULL, 2, NULL, 0, 'Slide it onto your blood-splattered platter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62C3E7B9F4192548887FFD5369A063A0', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 27, NULL, NULL, 2, NULL, 0, 'Dribble the remaining thin marinara sauce over the brain.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBD1C44CCBB92E41809563A0854B15DD', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 28, NULL, NULL, 2, NULL, 0, 'I spooned the thin sauce into a squeeze bottle and piped it over the brain in some of the deeper crevices. You can see how I do that when you watch the video.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F26924245FDFAD439D8BF82D90AAF60A', X'9233656698A6634FBCD173620B94DF21', X'9233656698A6634FBCD173620B94DF21', 29, NULL, NULL, 2, NULL, 0, 'Heat the remaining thick marinara sauce and serve it as a dipping sauce with the pizza brain.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E57EFF7D0E433488FD9FB66992C2A8F', X'7BC1E581D329864BBBB570B2554DB3AB', X'7BC1E581D329864BBBB570B2554DB3AB', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83325854EB1DE74C8E65944E30EC537F', X'7BC1E581D329864BBBB570B2554DB3AB', X'7BC1E581D329864BBBB570B2554DB3AB', 1, NULL, NULL, 2, NULL, 0, 'Line a 9 inch by 13 inch baking pan with non-stick aluminum foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7939C4D304C6D45A51B8D10AD1DD118', X'7BC1E581D329864BBBB570B2554DB3AB', X'7BC1E581D329864BBBB570B2554DB3AB', 2, NULL, NULL, 2, NULL, 0, 'Mix the Oreo crumbs with melted butter and press it into the bottom of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12B6B2BCA96BF042930D591470F0E203', X'7BC1E581D329864BBBB570B2554DB3AB', X'7BC1E581D329864BBBB570B2554DB3AB', 3, NULL, NULL, 2, NULL, 0, 'Pour the sweetened condensed milk over the crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92103E0AFA5403408985EA8C25D05DF8', X'7BC1E581D329864BBBB570B2554DB3AB', X'7BC1E581D329864BBBB570B2554DB3AB', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle on pieces of Halloween Oreos and half of the Halloween morsels.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1485DA74C96D2C479A1F311B84EC510D', X'7BC1E581D329864BBBB570B2554DB3AB', X'7BC1E581D329864BBBB570B2554DB3AB', 5, NULL, NULL, 2, NULL, 0, 'Bake for 25-28 minutes. Remove from the oven and sprinkle on the reserved half a bag of Halloween morsels.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE8F8127C54EBF49B60A10FC59B7637D', X'7BC1E581D329864BBBB570B2554DB3AB', X'7BC1E581D329864BBBB570B2554DB3AB', 6, NULL, NULL, 2, NULL, 0, 'Return to the oven for about 5 more minutes until the chips melt slightly and until the sweetened condensed milk turns golden brown and the bars appear set around the edges..');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E947F52452628140B13CD3372387C174', X'7BC1E581D329864BBBB570B2554DB3AB', X'7BC1E581D329864BBBB570B2554DB3AB', 7, NULL, NULL, 2, NULL, 0, 'The center will still be slightly jiggly but should not look really wet. You bars will harden as they cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FBDA120057EF442AD54C4E8C2D80C6E', X'7BC1E581D329864BBBB570B2554DB3AB', X'7BC1E581D329864BBBB570B2554DB3AB', 8, NULL, NULL, 2, NULL, 0, 'Cool completely for at least one hour before cutting into 24 to 30 bars.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F707A71B924A0E4887620BCF13604F32', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 0, NULL, NULL, 2, NULL, 0, 'In a large pot, heat the butter, marshmallows and milk chocolate over low heat, stirring often until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F57CE81E5275B448E479048D40AA293', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 1, NULL, NULL, 2, NULL, 0, 'Or, put those ingredients in a large microwave-safe bowl and heat for 20-30 second increments until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C6F4D41496FB64D8E687065BDF300EB', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 2, NULL, NULL, 2, NULL, 0, 'Do not overheat or you''ll burn the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0340E2139FC384F9D8C4433BDED28E4', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 3, NULL, NULL, 2, NULL, 0, 'Stir the Kellogg''s Cocoa Krispies into the melted marshmallow mixture, coating it well.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2EE38B314B3DC04AB5654954BB1C4B4D', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 4, NULL, NULL, 2, NULL, 0, 'Butter the inside of your bear shaped cookie cutter then set it on a piece of well-buttered tin foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C7CECF14267274EA9AEF051653AA6A3', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 5, NULL, NULL, 2, NULL, 0, 'Fill the cutter completely with the Cocoa Krispies treat mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FE80D8AFEE4404DBA5595B5EF8AFD4D', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 6, NULL, NULL, 2, NULL, 0, 'Press down on the cereal to compact it in the cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'489A884159684346AF9EDC7095E2894D', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 7, NULL, NULL, 2, NULL, 0, 'Remove the cutter and you''ll have your first bear.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A781B37A62D9940832E16CA5123776F', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 8, NULL, NULL, 2, NULL, 0, 'Repeat until you''ve used all the cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF46601C6525C5448F540F382DFC849E', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 9, NULL, NULL, 2, NULL, 0, 'Color some modeling chocolate orange, black, green, purple, and light brown, being sure to leave a small amount white. See detailed directions here.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0379625D0845A4F8BF4349FA4207457', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 10, NULL, NULL, 2, NULL, 0, 'Dust work surface lightly with powdered sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01C22E498720ED4FAE6D1A2507975CB2', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 11, NULL, NULL, 2, NULL, 0, 'Use a rolling pin to roll out pieces of each color modeling chocolate to about 1/16th of an inch or so.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EB9F488435C0341A7939C20DFCF5C91', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 12, NULL, NULL, 2, NULL, 0, 'Use cutters to cut out the following shapes for each bears:');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA23246C4B67994BA7741FE8CED9E1E8', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 13, NULL, NULL, 2, NULL, 0, '1 orange pumpkin, large enough to be used as a bear''s costume');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96FFE59C8C37654FB6A79F968AD123BB', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 14, NULL, NULL, 2, NULL, 0, '(I had to roll my pumpkin a bit more to make it large enough to fit my bear)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECC3B477AAE27749AF9D0D603B6B4F24', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 15, NULL, NULL, 2, NULL, 0, 'cut off the stem end of the pumpkin to create the neck opening of the costume');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C579925251C7F5428B45160D96308B80', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 16, NULL, NULL, 2, NULL, 0, '2 black jack-o-lantern eyes');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'444245D21C3B4746AD1DAC5936D54BDD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 17, NULL, NULL, 2, NULL, 0, '1 black jack-o-lantern nose');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E80CE17857DC449A616AD9BA413414C', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 18, NULL, NULL, 2, NULL, 0, '1 black jack-o-lantern mouth');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26BBFEB770DF194C81549C73E27BE882', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 19, NULL, NULL, 2, NULL, 0, '1 green leaf');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7159794443A69140B30AE9FAB9E40642', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 20, NULL, NULL, 2, NULL, 0, '1 brown stem (cut an oval, then cut off part of the oval, leaving a stem shaped piece)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA99AA218F9F9B469B3F4BF139AFD6B5', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 21, NULL, NULL, 2, NULL, 0, '1 orange pumpkin hat (cut an oval then cut off a smaller oval from the first leaving a crescent moon shape)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4A1EE7D9D73FD41BF46DE0A03A753D7', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 22, NULL, NULL, 2, NULL, 0, '1 purple rectangle for the bag');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECC4FF4328AE454CAAFA65B530776B0D', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 23, NULL, NULL, 2, NULL, 0, '1 black handle (cut a circle then cut a smaller circle from the center, cut that to make the handle)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AE4460CDFB2F74B8EE7E635BA44BF01', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 24, NULL, NULL, 2, NULL, 0, '1 light brown snout (cut a circle then shape it into an oval snout)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'048F805554FCAB49889FF53A9CBC1851', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 25, NULL, NULL, 2, NULL, 0, '1 black nose (cut a small circle)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7888B3D81872DA45BCB9FE1BEDBD41B8', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 26, NULL, NULL, 2, NULL, 0, '2 white eyes (use the end of a #18 pastry tip to cut out small circles)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C90C718E49667A4EA326209AD221897B', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 27, NULL, NULL, 2, NULL, 0, '2 black pupils (use the small end of a #7 pastry tip to cut out small circles)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE607FA2FB313A418BF7E17C4871834E', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 28, NULL, NULL, 2, NULL, 0, 'Attach pieces of modeling chocolate together with a small amount of water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA2ACE4EA395634E8AFF5A376B11F9CD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 29, NULL, NULL, 2, NULL, 0, 'Example: brush the backside of the jack-o-lantern features with water and press them onto the pumpkin.Use a black food coloring marker to write `Trick or Treat` on the purple rectangle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A60A9DA1FBB71349A2986112679D0743', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 30, NULL, NULL, 2, NULL, 0, 'Cut in between the bear''s legs with a knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4E775BEE8974F46A202729194C5F525', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 31, NULL, NULL, 2, NULL, 0, 'Use frosting or Nutella to attach the modeling chocolate pieces to the bears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCFF1C63509B374BA61A89305A07E778', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', X'48A49F191A65EC4B8CBCC6DAA0AF9BBD', 32, NULL, NULL, 2, NULL, 0, 'Wrap in clear cellophane bags and tie with a ribbon if using as gifts, otherwise just keep stored in an airtight container for up to a week, maybe more.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6ADA5D72813A87458AFBA513A0282E87', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 0, NULL, NULL, 2, NULL, 0, 'Stir together 8 ounces cream cheese, cheddar cheese and bacon until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFF71205DEB8014DA3B6BC80B6ABF711', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 1, NULL, NULL, 2, NULL, 0, 'Roll into a ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'633D84F40FA35B48AACBB44A38EE9A9D', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 2, NULL, NULL, 2, NULL, 0, 'Pour poppy seeds into a shallow bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C44B7E5475D6D34E8C0FB95FB0C74F21', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 3, NULL, NULL, 2, NULL, 0, 'Roll the cheese ball in the poppy seeds leaving about 1/3 of the ball on one end uncovered.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3DDF229A03D764F8BD8FEF8245A853C', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 4, NULL, NULL, 2, NULL, 0, 'Stir the remaining 3 ounces cream cheese until soft and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BCBC509183D654989923E2751EA9DCF', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 5, NULL, NULL, 2, NULL, 0, 'Spoon into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE4863D6BB2E82448E7FAED21257BEF7', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 6, NULL, NULL, 2, NULL, 0, 'Snip off the tip of the bag and pipe a thick tube of cream cheese around edge of the uncovered area creating the rim of the cauldron.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE0A6DF4C77333468E9490DC3B8C495A', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle poppy seeds over the rim and top of the cauldron.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46A32A9A9E748F4483FD3812621B3049', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 8, NULL, NULL, 2, NULL, 0, 'Pipe a 1 1/2 inch round of cream cheese in the center of a plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FECBCDBC13C5C34896327E083C7BA5EC', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 9, NULL, NULL, 2, NULL, 0, 'Break off 2 1/2 - 3 inch pieces from the ends of the pretzel rods.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86517EA698939D48812F83E737348658', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 10, NULL, NULL, 2, NULL, 0, 'Arrange 10 pretzel rod pieces around the cream cheese on the plate so they look like logs on a fire.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E04F27FC47F574181B09477BC25565B', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 11, NULL, NULL, 2, NULL, 0, 'Enjoy the inner pretzel pieces as a snack.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'083EC674508ED34DB05EA95A8E573B86', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 12, NULL, NULL, 2, NULL, 0, 'Set the cheese ball cauldron on top of the logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85A9467977DADF46AF79DABA5254C240', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 13, NULL, NULL, 2, NULL, 0, 'Cut thin slices from the peppers then cut them into tear drop shaped flames.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'222BFCF7180E0046B390BDE27DA90A04', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 14, NULL, NULL, 2, NULL, 0, 'Attach them to the base of the cauldron using the remaining cream cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9093D40B1E40B4CAF07CFAF985DB8D7', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 15, NULL, NULL, 2, NULL, 0, 'Spoon guacamole into a pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC112658CC12674984116E8EAB1CEB8C', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 16, NULL, NULL, 2, NULL, 0, 'Cut off the tip and pipe guacamole into the top of the cauldron.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2B414E4A793274D860BC296E034DBC9', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 17, NULL, NULL, 2, NULL, 0, 'Allow a bit to drip over the rim.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76716F4E20FEC44FAC907F8912AE7EEB', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 18, NULL, NULL, 2, NULL, 0, 'Serve with your favorite crackers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAF0E9C1AC6F904F9477BBE1DD480F9C', X'7012D722C7EC054E91E8071D1A3BAD7D', X'7012D722C7EC054E91E8071D1A3BAD7D', 19, NULL, NULL, 2, NULL, 0, 'Store in the refrigerator for up to 4 days.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DFBE5109F897542958B1A3BFB437B8B', X'A0F439839D61CF4A8B3D949F694EC2B5', X'A0F439839D61CF4A8B3D949F694EC2B5', 0, NULL, NULL, 2, NULL, 0, 'Melt white and peanut butter chips in microwave for 30 second increments stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26BF715AB20DA44B975ABFF0C8CA03AE', X'A0F439839D61CF4A8B3D949F694EC2B5', X'A0F439839D61CF4A8B3D949F694EC2B5', 1, NULL, NULL, 2, NULL, 0, 'Pour into cake pops mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E38DB9026AD644BBC66AFC1A8E9323A', X'A0F439839D61CF4A8B3D949F694EC2B5', X'A0F439839D61CF4A8B3D949F694EC2B5', 2, NULL, NULL, 2, NULL, 0, 'Insert lollipop stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'28C64FB5A27D3F4B9CE5799EAE732D29', X'A0F439839D61CF4A8B3D949F694EC2B5', X'A0F439839D61CF4A8B3D949F694EC2B5', 3, NULL, NULL, 2, NULL, 0, 'Freeze for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4DCBD3503A36E34A8EABB1CEA7F04B6D', X'A0F439839D61CF4A8B3D949F694EC2B5', X'A0F439839D61CF4A8B3D949F694EC2B5', 4, NULL, NULL, 2, NULL, 0, 'Un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4792D9A30E74DF40B7358E5E82830FEC', X'A0F439839D61CF4A8B3D949F694EC2B5', X'A0F439839D61CF4A8B3D949F694EC2B5', 5, NULL, NULL, 2, NULL, 0, 'Set aside for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1B223D48931FD4F91CD25F77E5263EC', X'A0F439839D61CF4A8B3D949F694EC2B5', X'A0F439839D61CF4A8B3D949F694EC2B5', 6, NULL, NULL, 2, NULL, 0, 'Dip in melted and tempered semi-sweet chocolate. (If you use candy melts instead, you must allow them to cool to about 91 degrees so that they don''t melt the fudge.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B61D04E501D6F74B8248E910D22A556E', X'A0F439839D61CF4A8B3D949F694EC2B5', X'A0F439839D61CF4A8B3D949F694EC2B5', 7, NULL, NULL, 2, NULL, 0, 'Add a thin rope of modeling chocolate around the top of each pop.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'128F29471048C84DBE21E5270D2CAFB6', X'A0F439839D61CF4A8B3D949F694EC2B5', X'A0F439839D61CF4A8B3D949F694EC2B5', 8, NULL, NULL, 2, NULL, 0, 'Decorate with green slime sparkle gel and candy sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'734758B1B618874ABEA6C5F642F27CCF', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 0, NULL, NULL, 2, NULL, 0, 'Clean the mushrooms with a damp paper towel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0542040C94D4214FA28460859AC6A93A', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 1, NULL, NULL, 2, NULL, 0, 'Remove the stems from Shiitaki mushrooms.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6E579111B476242924D59526A05DB99', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 2, NULL, NULL, 2, NULL, 0, 'Slice and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'862D22375C2C604F8763DA426177343E', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 3, NULL, NULL, 2, NULL, 0, 'Heat 1 teaspoon olive oil in a 12` skillet and set over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'593FBEBE9C4494419B17A025C27A086C', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 4, NULL, NULL, 2, NULL, 0, 'Chop the Pancetta or bacon into small pieces and sprinkle in hot pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E4DDCB0EA0C0248A69AD18D83AFB53D', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 5, NULL, NULL, 2, NULL, 0, 'Cook, stirring often, until crisp.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE6CB7D38683A240AADCCF7AF7E04424', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 6, NULL, NULL, 2, NULL, 0, 'Remove the crispy Pancetta from the skillet, leaving the oil in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D355CC89C1CCC54488F3DF2AE305F56F', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 7, NULL, NULL, 2, NULL, 0, 'Set the Pancetta on a piece of paper towel to drain off any excess grease.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3B3181641BBF84886F544065C6D2B96', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 8, NULL, NULL, 2, NULL, 0, 'Add the shallots to the hot pan and saute until translucent.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08EC19B3FD5B834DB58A8BD56B78FEB6', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 9, NULL, NULL, 2, NULL, 0, 'Add garlic and saute for about 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE38678E83A95B488962CA167EDE05A7', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 10, NULL, NULL, 2, NULL, 0, 'Add the finely chopped roasted red peppers and cook just until all the liquid evaporates.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2C5EB9D5D969840BBB53F002D4BFA35', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 11, NULL, NULL, 2, NULL, 0, 'Pour mixture into a bowl and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'110C28645861274F858D252C10E1BE8F', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 12, NULL, NULL, 2, NULL, 0, 'In the same hot skillet add a little over a teaspoon of oil and a pat of butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F17D8A60D75044C988FF9F74BB9E149', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 13, NULL, NULL, 2, NULL, 0, 'As soon as the butter melts add a single layer of mushrooms to the pan. If you crowd the pan, the mushrooms will steam instead of caramelize.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8266F4A8539E6449821E91881F629776', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 14, NULL, NULL, 2, NULL, 0, 'Once these mushrooms are brown, remove from pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B699182D0869D848831A30F6C062FC9B', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 15, NULL, NULL, 2, NULL, 0, 'Add more oil and butter to the pan and repeat the process until all the mushroom are browned.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AD7FD33DAD20D47AA1D76493D2BA50D', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 16, NULL, NULL, 2, NULL, 0, 'Return mushrooms, Pancetta, and roasted red pepper mixture to the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5230A0671C22084EB015A057241ED343', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 17, NULL, NULL, 2, NULL, 0, 'Pour in wine, beef broth, and balsamic vinegar.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D4A22E317764B47B4373254B0495076', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 18, NULL, NULL, 2, NULL, 0, 'Scrape the bottom of the pan to remove all the brown bits and bring everything to a boil over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BE7F8F127EDCF4B8A1A6FB85EA0397E', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 19, NULL, NULL, 2, NULL, 0, 'Reduce the heat to medium low and cook, stirring occasionally, until almost all of the moisture evaporates.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1971D3A26EC8CA45BF888ACD13ACB28E', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 20, NULL, NULL, 2, NULL, 0, 'Remove from the heat and stir in the Parmigiano-Reggiano cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C8FDD76A608A7429831EF782C303EE8', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 21, NULL, NULL, 2, NULL, 0, 'Pre-heat oven to 400 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F49D6351E812E74B800C2E2EF6EC36AF', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 22, NULL, NULL, 2, NULL, 0, 'Brush a light coating of black food coloring onto the clean rubber stamp, using a foam brush or a pastry brush.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0201F7F0AF2B464C85BF7AF7428B322F', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 23, NULL, NULL, 2, NULL, 0, 'Press the stamp onto a won ton. Remove and set your won ton wrapper aside. Repeat until you have 24 stamped won tons.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50631A09B6EA314AB18A3AFE35E17589', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 24, NULL, NULL, 2, NULL, 0, 'Brush the remaining won tons with egg wash.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90BE1A746491A9489567410F243B6428', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 25, NULL, NULL, 2, NULL, 0, 'Equally divide the mushroom mixture among them, placing a scoop in the center of each. Top with a printed won ton wrapper and press around the edges to seal tightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD36FD7D9A050249B39D54AE0B5C1AA4', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 26, NULL, NULL, 2, NULL, 0, 'Bake until heated through and brown around the edges, about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1877301C8FD9C40BF00E3658C0426D3', X'22AD03B6015ECC46AB698887E9D6328F', X'22AD03B6015ECC46AB698887E9D6328F', 27, NULL, NULL, 2, NULL, 0, 'They can also be fried at 375 degrees until golden brown, if you prefer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'132402996F4D054EAEE47601519937FC', X'8F524D7FF2D7CD45858322C432413D53', X'8F524D7FF2D7CD45858322C432413D53', 0, NULL, NULL, 2, NULL, 0, 'Scrape the filling out of 1 Mini Oreo Cookie and discard filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'182B8CFDE2D2DD45A734BFEFBC8986D9', X'8F524D7FF2D7CD45858322C432413D53', X'8F524D7FF2D7CD45858322C432413D53', 1, NULL, NULL, 2, NULL, 0, 'Insert both mini sandwich cookie halves into the larger Oreo creating Mickey Mouse ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FF342AF177EF34AB1D52119DB5A46FC', X'8F524D7FF2D7CD45858322C432413D53', X'8F524D7FF2D7CD45858322C432413D53', 2, NULL, NULL, 2, NULL, 0, 'Roll out candy clay and cut out decorations.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01640CF3642FD746BE3279C231805A4B', X'8F524D7FF2D7CD45858322C432413D53', X'8F524D7FF2D7CD45858322C432413D53', 3, NULL, NULL, 2, NULL, 0, 'Attach candy clay decorations together using piping gel or water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AC710E85A51CF4CAC08A15433FFB867', X'8F524D7FF2D7CD45858322C432413D53', X'8F524D7FF2D7CD45858322C432413D53', 4, NULL, NULL, 2, NULL, 0, 'Attach the candy clay decorations to the Oreo using melted chocolate or candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97D66AB02A3D024484CE9044E4D3118B', X'8F524D7FF2D7CD45858322C432413D53', X'8F524D7FF2D7CD45858322C432413D53', 5, NULL, NULL, 2, NULL, 0, 'See Hungry Happenings for step-by-step tutorial for each decorations.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8B26C986A37AF429021ABE9841EEA61', X'DC6068A2D4DB91489DCD19992E508920', X'DC6068A2D4DB91489DCD19992E508920', 0, NULL, NULL, 2, NULL, 0, 'Bake mini corn dogs on baking sheet according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AAE1FA3F8269D45AB3651FC23EEAFD6', X'01DF3903BD34D648BDE5960B4FAFA329', X'01DF3903BD34D648BDE5960B4FAFA329', 0, NULL, NULL, 2, NULL, 0, 'Line up 9 corn dogs widthwise into a squiggly line for a centipede.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B011953E12CD845AF25528F0557F9FE', X'01DF3903BD34D648BDE5960B4FAFA329', X'01DF3903BD34D648BDE5960B4FAFA329', 1, NULL, NULL, 2, NULL, 0, 'Press two long chow mein noodles (or potato sticks) into the first corn dog to create antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8A70C5C81FAE243920A82ECA91B7287', X'01DF3903BD34D648BDE5960B4FAFA329', X'01DF3903BD34D648BDE5960B4FAFA329', 2, NULL, NULL, 2, NULL, 0, 'Press two long, but shorter chow mein noodles (or potato sticks) into the last corn dog to create the back legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'370A26F97E581142B68CBF835163B185', X'01DF3903BD34D648BDE5960B4FAFA329', X'01DF3903BD34D648BDE5960B4FAFA329', 3, NULL, NULL, 2, NULL, 0, 'Then on all remaining corn dogs, press one chow mein noodle (or potato stick) on either side, creating legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E981CB0D5AA2AC4992554F785810569B', X'01DF3903BD34D648BDE5960B4FAFA329', X'01DF3903BD34D648BDE5960B4FAFA329', 4, NULL, NULL, 2, NULL, 0, 'If needed, place in a 200-degree oven to keep warm until you are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABDF0DF97D691E49B95C443DE59F9642', X'01DF3903BD34D648BDE5960B4FAFA329', X'01DF3903BD34D648BDE5960B4FAFA329', 5, NULL, NULL, 2, NULL, 0, 'Before serving, add two ketchup eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B09A17A5FCFA3F48A8C2C478F4A44675', X'5AE53E0E21E6B847B7F7696B78DC668F', X'5AE53E0E21E6B847B7F7696B78DC668F', 0, NULL, NULL, 2, NULL, 0, 'Line up 4 or 5 corn dogs lengthwise so that each section is longer rather than wider.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'181062520C0AAF44937E948B6EBA724F', X'5AE53E0E21E6B847B7F7696B78DC668F', X'5AE53E0E21E6B847B7F7696B78DC668F', 1, NULL, NULL, 2, NULL, 0, 'Press two long chow mein noodles (or potato sticks) into the first corn dog to create antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6DB2876D9FA6A46A5228C3A97450EDF', X'5AE53E0E21E6B847B7F7696B78DC668F', X'5AE53E0E21E6B847B7F7696B78DC668F', 2, NULL, NULL, 2, NULL, 0, 'Attach three chow mein noodle legs for each body segment and have them curved toward the ground.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E13DA65BC403E542BCF87965D0A4E05F', X'5AE53E0E21E6B847B7F7696B78DC668F', X'5AE53E0E21E6B847B7F7696B78DC668F', 3, NULL, NULL, 2, NULL, 0, 'If needed, place in a 200-degree oven to keep warm until you are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A50834034F24C2439D4FF4338CE28234', X'5AE53E0E21E6B847B7F7696B78DC668F', X'5AE53E0E21E6B847B7F7696B78DC668F', 4, NULL, NULL, 2, NULL, 0, 'Just before serving add ketchup eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0ACED739E9731F4BBA5045E3C8C7367F', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 0, NULL, NULL, 2, NULL, 0, 'Dip your caramel apples into the melted and tempered chocolate (or melted candy melts/confectionery coating) to fully cover your apple.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B74AA6360D4BB46AB7B5413E6BCED35', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 1, NULL, NULL, 2, NULL, 0, 'Pull the apple up out of the chocolate allowing the excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'471D1C0F59E2824BBF437A9A6871AF7B', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 2, NULL, NULL, 2, NULL, 0, 'Set the apple on a baking pan lined with parchment paper or a Silpat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'286869CB0B0DC947BEAAA9C7D780F902', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 3, NULL, NULL, 2, NULL, 0, 'If using tempered chocolate, refrigerate until set, about 8-10 minutes. If using candy melts, freeze until set, 5-8 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C69813A7ED953F4BB6EC86CCAB729DA9', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 4, NULL, NULL, 2, NULL, 0, 'Allow the caramel apple cauldrons to sit at room temperature for at least 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05E723F093F1BE46996D3ACD21B71535', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 5, NULL, NULL, 2, NULL, 0, 'Then use a pastry brush or paper towel to rough them up a bit so they look like old cauldrons.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'521F27491FAB7C449D45120AE53B4947', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 6, NULL, NULL, 2, NULL, 0, 'Knead some dark modeling chocolate then roll it into a long log about 1/2` thick.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFD65EF4DED1BD4098ECBC462818A514', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 7, NULL, NULL, 2, NULL, 0, 'Wrap the log around the very top of the cauldron.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F597A55D708FD41A83487304E2E960F', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 8, NULL, NULL, 2, NULL, 0, 'Cut off excess and pinch two ends together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8194F36D70C874ABF2E5E4D0274279B', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 9, NULL, NULL, 2, NULL, 0, 'Color some white modeling chocolate neon green.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4635693B8E79D4D934DB9C98CEDF3EB', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 10, NULL, NULL, 2, NULL, 0, 'Pinch off very small bits of it and roll into balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC4C19772ABB9F4DB488A8A6B93DEA30', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 11, NULL, NULL, 2, NULL, 0, 'Brush the top of the apple inside the cauldron brim very lightly with water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E41BECCBD06DC42B8E73C1F1DA8D460', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 12, NULL, NULL, 2, NULL, 0, 'Gently press the small green balls of modeling chocolate inside the top of the cauldron.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'125D42820B83514DBA89BBB72299F9DB', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 13, NULL, NULL, 2, NULL, 0, 'Roll out or flatten a ball of neon green modeling chocolate and cut into a circle to fit inside the brim.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C81B5D6BC0BE743832A4A72186320BA', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 14, NULL, NULL, 2, NULL, 0, 'Use a knife or a #10 round pastry tip to cut a hole in the circle of modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78C638A78B2CFC49859CEEB960F77FA4', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 15, NULL, NULL, 2, NULL, 0, 'Pick up the circle, place the hole over the end of the caramel apple stick, and work the modeling chocolate down onto the apple.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'347B96C750E67E4FB531034CF52E6150', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 16, NULL, NULL, 2, NULL, 0, 'Press the green modeling chocolate over the green balls tucking it in around the edge of the brim, creating the look of bubbling brew.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D2B5EF7C04C5447B3B86BEAE8E678CE', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 17, NULL, NULL, 2, NULL, 0, 'Color some white modeling chocolate red, orange, and yellow then pinch off pieces and press them together, flatten, then roll it out to 1/16` thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2F4EE1FC394F04097B5509AE77D7DB6', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 18, NULL, NULL, 2, NULL, 0, 'Use a mini teardrop cutter, a leaf cutter, or a knife to cut out your flames.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'440F2C9833365748980B54D32D9B4048', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 19, NULL, NULL, 2, NULL, 0, 'Brush a very small amount of water on the backside of each flame and press them onto the side of the cauldron at the base.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BDD2AA3EA32284E836653F53188664C', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 20, NULL, NULL, 2, NULL, 0, 'Pinch off a small amount of dark modeling chocolate, roll into a skinny log, cut, and create a circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90A8945EC3432B4E9E6681E9080E77BA', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 21, NULL, NULL, 2, NULL, 0, 'Also cut a piece that''s about 1/8` long.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95E07DD2ABF92F4A8243B55927731ABF', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 22, NULL, NULL, 2, NULL, 0, 'Pinch together the ends of the circle and press the small piece on top of the seam.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FDB93A3AE190F4C8C8ED43B5165B1CC', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 23, NULL, NULL, 2, NULL, 0, 'Use a dab of water to glue the handles to the sides of the cauldron.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92EA595CFCA7EA4CA4AB97D0F6FB4319', X'56FE9158328A5A4E83EE50368BF984BB', X'56FE9158328A5A4E83EE50368BF984BB', 24, NULL, NULL, 2, NULL, 0, 'To create the dripping bubbling brew, pinch off very small amounts of neon green modeling chocolate, roll into balls, flatten into teardrop shapes, and attach to the brim of the cauldron.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0B5736B5665E04FAE7723F216102CF8', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 0, NULL, NULL, 2, NULL, 0, 'Cakesicle Filling');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2917DD3D06D4614FBAA343DAADF94A49', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 1, NULL, NULL, 2, NULL, 0, 'Crumble the cake and blend it together with the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04E686F482831342B1E506FD8F1AE761', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 2, NULL, NULL, 2, NULL, 0, 'Add more frosting if desired until the cake ball filling has a nice chocolate truffle style texture.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'598F60DAE889AA4481BFB69E4A9AFDB6', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 3, NULL, NULL, 2, NULL, 0, 'White Chocolate Shell');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'105480AF87913A4D92EDE2C5CAFBD94B', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 4, NULL, NULL, 2, NULL, 0, 'Paint black candy melts or dark chocolate over the eyes, nose, and teeth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C53D506FF10F644878D860670B84725', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 5, NULL, NULL, 2, NULL, 0, 'Freeze, if using candy melts, or refrigerate, if using pure chocolate, for about 2 minutes just until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBD34E2538522A498ACC12AA8E32F184', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 6, NULL, NULL, 2, NULL, 0, 'Remove the mold and allow it to come to room temperature before adding the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06D8C7B0F8836A418C1C21D7D83D9A3F', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 7, NULL, NULL, 2, NULL, 0, 'Spoon about 1 1/2 tablespoons of white chocolate into one popsicle cavity.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53D07AAAFF59D14AA8F17B9D653206F8', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 8, NULL, NULL, 2, NULL, 0, 'Paint it into a thin layer on the bottom and up the sides of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7880A5E17706346B7918CEECFA2C883', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 9, NULL, NULL, 2, NULL, 0, 'Immediately insert the popsicle stick which the chocolate is still wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6C3F8B105C8974BAA19A315442A0F90', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 10, NULL, NULL, 2, NULL, 0, 'Pop the mold into the refrigerator, if using pure white chocolate, and into the freezer if using confectionery coating (Candy Melts).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB0F667B627F4740B7F6052C18525280', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 11, NULL, NULL, 2, NULL, 0, 'Chill for 3-5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B11103183BF1949A4C57201F5EFC9F5', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 12, NULL, NULL, 2, NULL, 0, 'Scoop out about 1 1/2 ounces of the cake ball filling and press it into the candy shell, leaving at least 1/16 of an inch of space at the top for the additional layer of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2034DB754965C4D869215191F7D343E', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 13, NULL, NULL, 2, NULL, 0, 'Spoon white chocolate over top and spread it out thin.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6078BDB76EC01D449CE0F71EE21F6736', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 14, NULL, NULL, 2, NULL, 0, 'Use an offset spatula to scrape off any excess white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0442D701E798F43964C4F5F8728F56B', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 15, NULL, NULL, 2, NULL, 0, 'Chill for about 2 minutes until the candy layer hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1029A07BF4D88740BFB039220ACFE5A9', X'0B14386714246841A5F495603E65373E', X'0B14386714246841A5F495603E65373E', 16, NULL, NULL, 2, NULL, 0, 'Unmold the cakesicles and they are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A096FCD0CA2951468792A2ADF77F2BFF', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 0, NULL, NULL, 2, NULL, 0, 'Pour finely chopped chocolate and cocoa powder into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B7B2006DEBF271478880D8E6C471C240', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 1, NULL, NULL, 2, NULL, 0, 'Add hot coffee and stir until chocolate melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7CFA2444BF2164580A2158287D20282', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 2, NULL, NULL, 2, NULL, 0, 'Place in the refrigerator to cool for about 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A7948F959E51345BF066A9DA76B6F54', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 3, NULL, NULL, 2, NULL, 0, 'Once cooled, whisk in oil, eggs, white vinegar, and vanilla extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECDD4AD0609B984CBE77189F897B59C6', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 4, NULL, NULL, 2, NULL, 0, 'In a separate bowl whisk together the sugar, flour, baking soda, and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4C82827302EA64D870B1E29D8E60E74', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 5, NULL, NULL, 2, NULL, 0, 'Add it to the chocolate bowl and whisk until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05D549B7BD25A846A3068C7605F13BAA', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 6, NULL, NULL, 2, NULL, 0, 'Place cupcake liners in cupcake pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E2296A153069E4C89243270FC686D86', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 7, NULL, NULL, 2, NULL, 0, 'Pour a thin layer of batter into each of the cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9A5AF1A17401546BFD45CE7779BF998', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 8, NULL, NULL, 2, NULL, 0, 'Remove the Mini Cheesecake Pumpkins from the freezer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5AEBFD90A1ADEF47A06174CD63F84967', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 9, NULL, NULL, 2, NULL, 0, 'Un-mold them, and stick 2 cheesecakes together (put their backsides together then place them in the center of each cupcake liner making sure all the pumpkins are facing the same direction. (Mark your pan so you know which way they are facing.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A16E519163F044FA5A6F85C8DCCA846', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 10, NULL, NULL, 2, NULL, 0, 'Fill the cupcake liners up over the cheesecake pumpkins.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7880B9A37CCBF447949A7B4872A995BF', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 11, NULL, NULL, 2, NULL, 0, 'Once all the cups are filled allow them to sit at room temperature for about 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3A8FA5AB66D3E4FB8DB7028114D8F88', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 12, NULL, NULL, 2, NULL, 0, 'While you are waiting, preheat your oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEF13D8CCAFF844DA0B75BE6DD73F122', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 13, NULL, NULL, 2, NULL, 0, 'Bake the cupcakes for 20-22 minutes until they are set around the edges and puffed in the middle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C45A56A68B59484F8656740BCA191DC2', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 14, NULL, NULL, 2, NULL, 0, 'As the cupcakes cool, they will sink a bit in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BDD0189C2D63A9419480DC08AD7491E7', X'73BB502D7C4D484AB4F07E7D2E60258F', X'73BB502D7C4D484AB4F07E7D2E60258F', 15, NULL, NULL, 2, NULL, 0, 'Allow them to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DA419617F783E4C9ED7326E376133FA', X'5860E3F92B5F6749BD0FA1ADFA4446ED', X'5860E3F92B5F6749BD0FA1ADFA4446ED', 0, NULL, NULL, 2, NULL, 0, 'Place chopped chocolate in the bowl of a food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'686F0CA4EC210C4C804E825B916215C6', X'5860E3F92B5F6749BD0FA1ADFA4446ED', X'5860E3F92B5F6749BD0FA1ADFA4446ED', 1, NULL, NULL, 2, NULL, 0, 'Pulse until chocolate is fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C1A628C7FC3D14F9DEA835D54CC4EE9', X'5860E3F92B5F6749BD0FA1ADFA4446ED', X'5860E3F92B5F6749BD0FA1ADFA4446ED', 2, NULL, NULL, 2, NULL, 0, 'Heat heavy whipping cream in a small saucepan over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D56151D7099294EA2605BAC28905D6D', X'5860E3F92B5F6749BD0FA1ADFA4446ED', X'5860E3F92B5F6749BD0FA1ADFA4446ED', 3, NULL, NULL, 2, NULL, 0, 'Stir often until it just comes to a boil. Pour the hot cream over the chocolate, put the lid on the food processor bowl, and let sit for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27906116A0796443B2E2D6AF2F6AF054', X'5860E3F92B5F6749BD0FA1ADFA4446ED', X'5860E3F92B5F6749BD0FA1ADFA4446ED', 4, NULL, NULL, 2, NULL, 0, 'Pulse for 3 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31E2BDE219F7A24B95F0CB388E1CEBAD', X'5860E3F92B5F6749BD0FA1ADFA4446ED', X'5860E3F92B5F6749BD0FA1ADFA4446ED', 5, NULL, NULL, 2, NULL, 0, 'Remove lid and scrape down the sides and bottom of the food processor bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'961FB094C062A645A8B08040905759F9', X'5860E3F92B5F6749BD0FA1ADFA4446ED', X'5860E3F92B5F6749BD0FA1ADFA4446ED', 6, NULL, NULL, 2, NULL, 0, 'Return lid and pulse for 5 more seconds or until the mixture, which is called ganache, is smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D33423A8724C443A962EC02F102403E', X'5860E3F92B5F6749BD0FA1ADFA4446ED', X'5860E3F92B5F6749BD0FA1ADFA4446ED', 7, NULL, NULL, 2, NULL, 0, 'Pour ganache into a mixing bowl and chill in the refrigerator for 15 minutes, then remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0AA3F5409796CE4BB0B81F7DCEAFAC6B', X'5860E3F92B5F6749BD0FA1ADFA4446ED', X'5860E3F92B5F6749BD0FA1ADFA4446ED', 8, NULL, NULL, 2, NULL, 0, 'Refrigerate for 15 more minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96D28E33A9D449479845A8CCB7582832', X'5860E3F92B5F6749BD0FA1ADFA4446ED', X'5860E3F92B5F6749BD0FA1ADFA4446ED', 9, NULL, NULL, 2, NULL, 0, 'Then use an electric mixer to beat the ganache until it''s lighter in color.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FF6D469601A5A47ABE5E18D6F4F9ED1', X'5860E3F92B5F6749BD0FA1ADFA4446ED', X'5860E3F92B5F6749BD0FA1ADFA4446ED', 10, NULL, NULL, 2, NULL, 0, 'Immediately spoon the frosting into a piping bag fitted with a large star tip and pipe big swirls over cooled cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2078599C62402F4082DCE3838B16B1F1', X'5860E3F92B5F6749BD0FA1ADFA4446ED', X'5860E3F92B5F6749BD0FA1ADFA4446ED', 11, NULL, NULL, 2, NULL, 0, 'Add sprinkles on top if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33E6AA6CB5D474449EFFC0AFC40AA9DB', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees. Butter the cavities in your pumpkin molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E6EE55DC6A58A4B8C93C4C476D7EE40', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 1, NULL, NULL, 2, NULL, 0, 'Combine cream cheese, sugar, heavy whipping cream, eggs, and vanilla in the bowl of a food processor, pulse until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34229AFBB7AB404D8014E579CF8638EF', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 2, NULL, NULL, 2, NULL, 0, 'Alternatively, mix cream cheese and sugar together until well blended. Add whipping cream, eggs, and vanilla and mix until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EDE7134A654E848A76E9901AD77B326', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 3, NULL, NULL, 2, NULL, 0, 'Remove about 2 tablespoons of filling and color it green.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F8D80DA1EB95348A296E1C842897F4B', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 4, NULL, NULL, 2, NULL, 0, 'Color the remaining filling using orange food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99E404CB42C008498344FCEB99FAFCB0', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 5, NULL, NULL, 2, NULL, 0, 'Paint the green filling into the stems of 40 cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A4762E9CB01134993F708C9BD24D112', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 6, NULL, NULL, 2, NULL, 0, 'The filling is pretty thick and should stay in place nicely, but just to be safe, pop these filled molds into the freezer for 5 minutes before filling with the orange cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1303430A1072924E8456F6AD89BA8DE3', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 7, NULL, NULL, 2, NULL, 0, 'Set silicone pumpkin mold on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BFFC1A7E7596D46A953C2EC7A750D8B', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 8, NULL, NULL, 2, NULL, 0, 'Fill the pumpkins with orange cheesecake filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC06151312E43047847527C485ACAECB', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 9, NULL, NULL, 2, NULL, 0, 'Leave a small amount of room in each mold for the filling to expand while baking (about a 16th of an inch.)');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0E409C05EB6EA43A70A419CB34F65B3', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 10, NULL, NULL, 2, NULL, 0, 'Bake 12-14 mins until the tops look set but the cheesecake pumpkins are still jiggly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA69DBCE5C78D44BB1DF00176A146EAA', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 11, NULL, NULL, 2, NULL, 0, 'Allow to cool for 30 minutes at room temperature, then place in the freezer for at least 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE52F5214BB7F544BB51F47E7FF31BAA', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 12, NULL, NULL, 2, NULL, 0, 'Remove the molds from the freezer, turn the mold upside down and press the pumpkin cheesecakes out.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9ECC73C71C64F4D9959A7B535F929AE', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 13, NULL, NULL, 2, NULL, 0, 'Store the mini cheesecake pumpkins in the refrigerator for up to 5 days or keep frozen for a few weeks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E1C15DCE5EDBC48A2171F4C64BD35FC', X'6E32C73C4BF33D45AA8FA0633B3871B0', X'6E32C73C4BF33D45AA8FA0633B3871B0', 14, NULL, NULL, 2, NULL, 0, 'Before serving, decorate with mini chocolate chips, if desired. Simply press the chocolate chips into the cheesecakes creating jack-o-lantern faces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCE34A94B603D549B1F5DAF4BEF31F6E', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2422873323CAAD488F973FF35E7A9FAD', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 1, NULL, NULL, 2, NULL, 0, 'Melt butter in a mixing bowl. Allow it to cool slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1681A50D1D2FF040889652310B3F3651', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 2, NULL, NULL, 2, NULL, 0, 'Add brown sugar and granulated sugar and beat on medium speed for about a minute until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F78EC87DC29D0A49B001EB8E0CCD52A9', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 3, NULL, NULL, 2, NULL, 0, 'Add egg, egg yolk, and vanilla and beat until fully incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A42C6331C9FF44CBF8B9D70C0135001', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 4, NULL, NULL, 2, NULL, 0, 'Mix flour, salt, and baking soda together then incorporate it into the wet ingredients.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2315ACA884CC09458F4B785C761AD473', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 5, NULL, NULL, 2, NULL, 0, 'Fold about 9 ounces (1 1/2 cups) of chips into the batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F33492D338FF34DAB476CA2CF966312', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 6, NULL, NULL, 2, NULL, 0, 'Scoop out 18 cookies, about 1/4 cup of dough for each.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3932A184982A7F42BBF6BF54DC8A5AFC', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 7, NULL, NULL, 2, NULL, 0, 'Place some of the reserved chocolate chips on top of each cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F0989556A9B42468884348B1C212451', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 8, NULL, NULL, 2, NULL, 0, 'Remember to keep about 2 ounces (1/3 cup) of the chips for the spiders.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7C134CB4A735C40ADD980E83FD9BC09', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 9, NULL, NULL, 2, NULL, 0, 'Bake cookies one tray at a time, for 14-18 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E781054B88C80747BF85685D87ED0FDE', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 10, NULL, NULL, 2, NULL, 0, 'Allow cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5AD2307B05240F4B86C38F25A061AA63', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 11, NULL, NULL, 2, NULL, 0, 'Place the remaining 2 ounces of chocolate chips in a small microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AC9A3B722EB39409AA41FE1EAFA04C2', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 12, NULL, NULL, 2, NULL, 0, 'Heat for 20 seconds, stir, then for 15 seconds, stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'703F4A2A2790214EB753C5B57BF98004', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 13, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to sit for 1 minute, then stir again.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98A708773333D8499292768D8191E741', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 14, NULL, NULL, 2, NULL, 0, 'If you need to, heat it for 10 more seconds, then repeat until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8E738352B69BC4B90E30FB4B544E5FA', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 15, NULL, NULL, 2, NULL, 0, 'Pour into a squeeze-it mold painter bottle fitted with a #2 round pastry tip, or a disposable pastry bag or zip top bag with the tip snipped off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78CD9AFF55AEC94BA5BC03DF1CCDDC29', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 16, NULL, NULL, 2, NULL, 0, 'Pipe on spider heads and legs around some of the larger chocolate chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7A8F222CC297F4D84F695A940ADBD19', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 17, NULL, NULL, 2, NULL, 0, 'Allow to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60FF3CB0EC22FD41ABE5165B9EF9D971', X'412F69286BAC8D4DA51E560DDC797CB7', X'412F69286BAC8D4DA51E560DDC797CB7', 18, NULL, NULL, 2, NULL, 0, 'Serve with a glass of milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD35AC7E2B7E2B49905D3FDE5143E63E', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B089B5EC01B9DC4A978F61EE2BAD9C61', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 1, NULL, NULL, 2, NULL, 0, 'Place a baking sheet or baking stone in the oven to preheat, as well.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3396222DB106234D858CB07A137843D0', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 2, NULL, NULL, 2, NULL, 0, 'Unroll the crescent sheet on a lightly floured work surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45B63C6189CBB24781CA6D6EC36BAFC3', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 3, NULL, NULL, 2, NULL, 0, 'Use a rolling pin to roll the dough out to an 8x13 rectangle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48DDEDBF80594341AA485DDC7C2AACDD', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 4, NULL, NULL, 2, NULL, 0, 'Cut into two 4x13 strips.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF5824F35B94FC4EA4FD585DEB7774CF', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 5, NULL, NULL, 2, NULL, 0, 'Cut 3 hot dogs into 3/4 slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0746D0EB8423A42BED397665FF7BCED', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 6, NULL, NULL, 2, NULL, 0, 'Remove the end pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB17286395853042BF91BAFD1ABCA569', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 7, NULL, NULL, 2, NULL, 0, 'Lay the hot dog slices together down the middle of one dough strip leaving an inch of uncovered dough at one end (for the head).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5058D5AC8BEE14D9029FEB847371862', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 8, NULL, NULL, 2, NULL, 0, 'Bring the two sides of the dough up and over the hot dogs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A26280EDE6DA8340AE311F1D8A3D0DD8', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 9, NULL, NULL, 2, NULL, 0, 'Pinch the dough together to seal shut.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'192A5F4C094D25459E87C11BF2BB19C1', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 10, NULL, NULL, 2, NULL, 0, 'Roll over so the seam is on the bottom.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9288F38E50763F4997EE523B8EF9B427', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 11, NULL, NULL, 2, NULL, 0, 'Form a snakehead out of the hot dog-less ends.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'733BF9171A4AD74997D41F48D2A82FAB', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 12, NULL, NULL, 2, NULL, 0, 'Cut one more hot dog into 3/4 slices and remove the end pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0EC7B72A64FFB439F94A7C23C6EDBA1', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 13, NULL, NULL, 2, NULL, 0, 'Lay down the middle at the very end of the other dough strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21B8D53140646D4E851369B13C86AD8C', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 14, NULL, NULL, 2, NULL, 0, 'Cut the tips off the remaining two hot dogs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A94408A15ED21443A2325884BE182A81', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 15, NULL, NULL, 2, NULL, 0, 'Lay them together on a cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77DD6B78D614774987255E107842CE9F', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 16, NULL, NULL, 2, NULL, 0, 'Cut along the edges of the hot dogs, gradually tapering the ends to a point.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B804EBCDDF636C48BC76E8F6B1C47D59', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 17, NULL, NULL, 2, NULL, 0, 'Cut the tapered hot dogs into 1/2-3/4 slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'755C201F7170354487067BD9FE279679', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 18, NULL, NULL, 2, NULL, 0, 'Set in the middle of the dough strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'887A780D3D13604F9A07DE824E055A94', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 19, NULL, NULL, 2, NULL, 0, 'Bring the two sides of the dough up and over the hot dogs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4CDEA8FC0840746899C534057E9E129', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 20, NULL, NULL, 2, NULL, 0, 'Pinch the dough together to seal shut.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9B46AE0F083324B99C35EABF362E198', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 21, NULL, NULL, 2, NULL, 0, 'Roll over so the seam is on the bottom.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B6437325467FF41AABF8977F534B8E4', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 22, NULL, NULL, 2, NULL, 0, 'Lay a large piece of parchment paper on your work surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DAD85E2D9906541935F588A2EB5F73E', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 23, NULL, NULL, 2, NULL, 0, 'Arrange the two halves of the snake in a slithering pattern on the paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DE72FD52E853942996C06307CD8943B', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 24, NULL, NULL, 2, NULL, 0, 'Pinch the dough together in the middle of the snake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A90E75A20C51CB429F95AD803B2B6B7B', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 25, NULL, NULL, 2, NULL, 0, 'Spray (or brush) stripes of orange and black all along the snake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BDE8B31C6394CB4F8104DD163D1B68A1', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 26, NULL, NULL, 2, NULL, 0, 'Cut out two circles and one tongue from the strip of roasted red pepper. Cut them larger than you want them, as they will shrink in the oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0E425C88F8B8E4B8A4BADC7C762BE2F', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 27, NULL, NULL, 2, NULL, 0, 'Attach to the snake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A644CEDBDF82784C9F11922585E41941', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 28, NULL, NULL, 2, NULL, 0, 'Slide the snake and the foil onto the hot baking sheet or baking stone in the oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7C3419C021CB14A89652E0BA1D58095', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 29, NULL, NULL, 2, NULL, 0, 'Bake for 10-14 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A48C29F3E32A7241980D76D9021BE60C', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 30, NULL, NULL, 2, NULL, 0, 'Allow the snack to cool for 5 minutes before transferring the snake to a serving platter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE5C01274B471D4B953929F72F8DD555', X'B403C805B38FA2498A58574FEBE93484', X'B403C805B38FA2498A58574FEBE93484', 31, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25B64295D62A714E8971EE95A120C571', X'6F33652448D47E4B80C674ECD2C989E3', X'6F33652448D47E4B80C674ECD2C989E3', 0, NULL, NULL, 2, NULL, 0, 'Pour water into the bottom of a saucepan set over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3BA75578392BE649B98DC19DFA83C486', X'6F33652448D47E4B80C674ECD2C989E3', X'6F33652448D47E4B80C674ECD2C989E3', 1, NULL, NULL, 2, NULL, 0, 'Add sugar and allow to come to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E1EE95FC49545438E7F2EEB031DA2CE', X'6F33652448D47E4B80C674ECD2C989E3', X'6F33652448D47E4B80C674ECD2C989E3', 2, NULL, NULL, 2, NULL, 0, 'Boil until all the sugar is dissolved, stirring if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48C6DC995DA0AF49B6757FF7AFB9BD31', X'6F33652448D47E4B80C674ECD2C989E3', X'6F33652448D47E4B80C674ECD2C989E3', 3, NULL, NULL, 2, NULL, 0, 'Pour egg yolks into a mixing bowl and whisk to break up.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B0DE8EB96AB25418EB6FADF9AA1F334', X'6F33652448D47E4B80C674ECD2C989E3', X'6F33652448D47E4B80C674ECD2C989E3', 4, NULL, NULL, 2, NULL, 0, 'Continue whisking while slowly drizzling in the sugar syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EA5C91461088B41A7A7FE0FABC829D6', X'6F33652448D47E4B80C674ECD2C989E3', X'6F33652448D47E4B80C674ECD2C989E3', 5, NULL, NULL, 2, NULL, 0, 'Pour 1/2 cup of heavy whipping cream into the saucepan and set it back on the stove on low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3016023957B4442B0C2795EA9BEE44A', X'6F33652448D47E4B80C674ECD2C989E3', X'6F33652448D47E4B80C674ECD2C989E3', 6, NULL, NULL, 2, NULL, 0, 'Stir in the egg mixture and continue to stir for at least 3 minutes until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38DD6B33488185429A198465262ADEAB', X'6F33652448D47E4B80C674ECD2C989E3', X'6F33652448D47E4B80C674ECD2C989E3', 7, NULL, NULL, 2, NULL, 0, 'You may heat until thickened, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B195CCB8D89C5E4FB0F57EF5768AEED5', X'6F33652448D47E4B80C674ECD2C989E3', X'6F33652448D47E4B80C674ECD2C989E3', 8, NULL, NULL, 2, NULL, 0, 'Pour through a fine mesh sieve that is set over your bowl of melted semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AAA6EAB7EED7D42938B664B2544EAD7', X'6F33652448D47E4B80C674ECD2C989E3', X'6F33652448D47E4B80C674ECD2C989E3', 9, NULL, NULL, 2, NULL, 0, 'Discard anything that remains in the sieve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF122595D965634EAED691006CC7AB77', X'6F33652448D47E4B80C674ECD2C989E3', X'6F33652448D47E4B80C674ECD2C989E3', 10, NULL, NULL, 2, NULL, 0, 'Stir the chocolate until glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2EBD1D2E6DBC8F4FA34E45C68699F508', X'6F33652448D47E4B80C674ECD2C989E3', X'6F33652448D47E4B80C674ECD2C989E3', 11, NULL, NULL, 2, NULL, 0, 'Fold in whipped cream then chill until thickened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27A421FE4E583E4A914BB22BA8474D8E', X'6F33652448D47E4B80C674ECD2C989E3', X'6F33652448D47E4B80C674ECD2C989E3', 12, NULL, NULL, 2, NULL, 0, 'Spoon or pipe into small plastic dessert cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B7194B7469A4043BCDCEBB7926F554F', X'4A4B0EB62704DA44B89F9C453ED433E4', X'4A4B0EB62704DA44B89F9C453ED433E4', 0, NULL, NULL, 2, NULL, 0, 'Stir cookie crumbs into melted white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'595EDD1C4F6A7F42AB2E9324AEABE0BB', X'4A4B0EB62704DA44B89F9C453ED433E4', X'4A4B0EB62704DA44B89F9C453ED433E4', 1, NULL, NULL, 2, NULL, 0, 'Spoon into tombstone candy mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65C93D65F36B9A438D7DAEE1778608AB', X'4A4B0EB62704DA44B89F9C453ED433E4', X'4A4B0EB62704DA44B89F9C453ED433E4', 2, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator if using pure white chocolate or in the freezer if using confectionery coating (candy melts) for 5-10 minutes just until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED58C88D4506E14BB1148DB1648BB258', X'4A4B0EB62704DA44B89F9C453ED433E4', X'4A4B0EB62704DA44B89F9C453ED433E4', 3, NULL, NULL, 2, NULL, 0, 'Remove and un-mold candy tombstones.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11B7BBAD99AD534281A046BA1A6791B2', X'AD21AEDBA1A59E4D93BF94C36AFFB7A8', X'AD21AEDBA1A59E4D93BF94C36AFFB7A8', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle some chocolate jimmies over top of each chocolate mousse cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F73528FEE4654745B638211AD146B364', X'AD21AEDBA1A59E4D93BF94C36AFFB7A8', X'AD21AEDBA1A59E4D93BF94C36AFFB7A8', 1, NULL, NULL, 2, NULL, 0, 'Insert one zombie hand and one candy tombstone before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91F94E928E8B2541BC5978344EF1E328', X'1A27BA08CAD4AC438B89B703AB0E22E2', X'1A27BA08CAD4AC438B89B703AB0E22E2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees. Line a 12 x 18 baking pan with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD70FB67EB1D14459D8B5FACB644FE58', X'1A27BA08CAD4AC438B89B703AB0E22E2', X'1A27BA08CAD4AC438B89B703AB0E22E2', 1, NULL, NULL, 2, NULL, 0, 'Unroll pizza dough onto the parchment-lined pan. Cut a 10 circle in the center of the dough. Remove the excess dough and set on a cutting board. Cut three 2 1/2 circles from the excess dough. Place two of the small circles of dough on either side of the large dough circle to form the vampire''s ears. Roll the remaining dough circle into a ball and place on the large dough circle for the vampire''s nose. Bake for 6-7 minutes until the dough is lightly golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56DD5DE0FDE48141B0EB4940DF53E0EF', X'1A27BA08CAD4AC438B89B703AB0E22E2', X'1A27BA08CAD4AC438B89B703AB0E22E2', 2, NULL, NULL, 2, NULL, 0, 'Remove from oven and add toppings. Spread pizza sauce over entire dough except for the nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E180D048CDF31D44B286279BFD24CEF2', X'1A27BA08CAD4AC438B89B703AB0E22E2', X'1A27BA08CAD4AC438B89B703AB0E22E2', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle cheese over sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABF9C3F4E041C645AF8301E68F0E2203', X'1A27BA08CAD4AC438B89B703AB0E22E2', X'1A27BA08CAD4AC438B89B703AB0E22E2', 4, NULL, NULL, 2, NULL, 0, 'Cut one jumbo black olive in half lengthwise. Place above and to either side of his nose. Finely chop remaining olives. Sprinkle the chopped olives all along the top of the vampire''s head forming his pointed brow. Add the strip of roasted red pepper for his mouth and attach two garlic clove fangs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4349674DB6002545869C8F79AC4BA262', X'1A27BA08CAD4AC438B89B703AB0E22E2', X'1A27BA08CAD4AC438B89B703AB0E22E2', 5, NULL, NULL, 2, NULL, 0, 'Return to oven and bake for 6-10 minutes until the cheese is melted and bubbly. Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F184B1D09158B45B562AE1A797C786D', X'B74A2B7984434E43A6B6D0952DD5DBAE', X'B74A2B7984434E43A6B6D0952DD5DBAE', 0, NULL, NULL, 2, NULL, 0, 'Combine flour, baking powder, and salt in a mixing bowl and blend with a fork or whisk.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1E07411D4C5604C99CF634C3EE724F0', X'B74A2B7984434E43A6B6D0952DD5DBAE', X'B74A2B7984434E43A6B6D0952DD5DBAE', 1, NULL, NULL, 2, NULL, 0, 'Add half of the lard and use a pastry cutter to cut the lard into the flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'623EE44B88C00B43B24DCA5C7973D4D3', X'B74A2B7984434E43A6B6D0952DD5DBAE', X'B74A2B7984434E43A6B6D0952DD5DBAE', 2, NULL, NULL, 2, NULL, 0, 'Add the remaining lard and continue to cut it into the flour until it resembles coarse crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A5B32590A64C742A2FC0A3D148D66DE', X'B74A2B7984434E43A6B6D0952DD5DBAE', X'B74A2B7984434E43A6B6D0952DD5DBAE', 3, NULL, NULL, 2, NULL, 0, 'Slowly pour in the water stirring as you go until the dough comes together. Add at least  of a cup of the water then only add any remaining water as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FA0F5FF1D2DA84283248987BBD64084', X'B74A2B7984434E43A6B6D0952DD5DBAE', X'B74A2B7984434E43A6B6D0952DD5DBAE', 4, NULL, NULL, 2, NULL, 0, 'Knead the dough on a flour-dusted cutting board until the dough is no longer sticky and comes together in a cohesive ball then cover the dough and set aside for 10-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BBBBB8DE29A304C83E6859088188778', X'958B2D066A01DD41834A12495576DBE0', X'958B2D066A01DD41834A12495576DBE0', 0, NULL, NULL, 2, NULL, 0, 'Heat half of the oil in a large skillet set over high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F4DA18A7F9E7D4E9A4AE4D050D799D4', X'958B2D066A01DD41834A12495576DBE0', X'958B2D066A01DD41834A12495576DBE0', 1, NULL, NULL, 2, NULL, 0, 'Add half of the ground sirloin and cook until browned.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75C7488F86CD3D4A8A148E1A23F6397D', X'958B2D066A01DD41834A12495576DBE0', X'958B2D066A01DD41834A12495576DBE0', 2, NULL, NULL, 2, NULL, 0, 'Remove from skillet and add the remaining oil then brown the remaining meat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94BEE81AB83A244CB8EF5D1BD2304142', X'958B2D066A01DD41834A12495576DBE0', X'958B2D066A01DD41834A12495576DBE0', 3, NULL, NULL, 2, NULL, 0, 'Add the other meat back into the pan and stir in the taco seasoning and water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDF51B73D4945E4FA19A0D4D0E66293E', X'958B2D066A01DD41834A12495576DBE0', X'958B2D066A01DD41834A12495576DBE0', 4, NULL, NULL, 2, NULL, 0, 'Bring to a boil then reduce heat to medium-low and simmer until most of the water has evaporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AAD0E474961774BA8856E8D078C6CE5', X'088DFA92A73AC84DB988FA1A4AB21F28', X'088DFA92A73AC84DB988FA1A4AB21F28', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA1564C6266ED749AFCDBE88B0D80983', X'088DFA92A73AC84DB988FA1A4AB21F28', X'088DFA92A73AC84DB988FA1A4AB21F28', 1, NULL, NULL, 2, NULL, 0, 'Cut the dough into 6 equal portions.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98D5AB245148DC41908B90BBE3A6B6D7', X'088DFA92A73AC84DB988FA1A4AB21F28', X'088DFA92A73AC84DB988FA1A4AB21F28', 2, NULL, NULL, 2, NULL, 0, 'Dust your work surface with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73724242C53AF04FA72426D1DD964674', X'088DFA92A73AC84DB988FA1A4AB21F28', X'088DFA92A73AC84DB988FA1A4AB21F28', 3, NULL, NULL, 2, NULL, 0, 'Roll out a portion of dough thinning out the edges.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE6B52D725D3DB448ED7094C8BE9796F', X'088DFA92A73AC84DB988FA1A4AB21F28', X'088DFA92A73AC84DB988FA1A4AB21F28', 4, NULL, NULL, 2, NULL, 0, 'Drape the dough over one skull in a 6 cavity non-stick pan allowing the dough to form the skull shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4FCBE6D0B553746961A5445DB1D7D8C', X'088DFA92A73AC84DB988FA1A4AB21F28', X'088DFA92A73AC84DB988FA1A4AB21F28', 5, NULL, NULL, 2, NULL, 0, 'Fill with 1/6th of the taco meat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C95B3C45E6459647B2948C8C38F14502', X'088DFA92A73AC84DB988FA1A4AB21F28', X'088DFA92A73AC84DB988FA1A4AB21F28', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle on 1/6th of the cheddar cheese, black beans, corn, and tomatoes or your favorite burrito filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14250609B7A6FB478465ACDE1D47B753', X'088DFA92A73AC84DB988FA1A4AB21F28', X'088DFA92A73AC84DB988FA1A4AB21F28', 7, NULL, NULL, 2, NULL, 0, 'Fold the dough over the filling, cutting off any excess dough and pinching the seams together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4614A6ED2FBB504081D2DE1ABE95C541', X'088DFA92A73AC84DB988FA1A4AB21F28', X'088DFA92A73AC84DB988FA1A4AB21F28', 8, NULL, NULL, 2, NULL, 0, 'Whisk together the egg and water then brush the top of the dough with egg wash.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D701F098E2F5D4FA33E2E4DBE7C4167', X'088DFA92A73AC84DB988FA1A4AB21F28', X'088DFA92A73AC84DB988FA1A4AB21F28', 9, NULL, NULL, 2, NULL, 0, 'Repeat filling all 6 skulls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0CA76743D6F92148A6CD401E930B8623', X'088DFA92A73AC84DB988FA1A4AB21F28', X'088DFA92A73AC84DB988FA1A4AB21F28', 10, NULL, NULL, 2, NULL, 0, 'Bake for 24-26 minutes until the tops of the dough turn lightly golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E770EAACF7A964D9FCC6743C598F3B2', X'088DFA92A73AC84DB988FA1A4AB21F28', X'088DFA92A73AC84DB988FA1A4AB21F28', 11, NULL, NULL, 2, NULL, 0, 'Remove from oven and set pan on a cooling rack for a few minutes then turn out the burrito skulls onto the cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DF210A9B9638A4CB2240A57ECEC707C', X'088DFA92A73AC84DB988FA1A4AB21F28', X'088DFA92A73AC84DB988FA1A4AB21F28', 12, NULL, NULL, 2, NULL, 0, 'Serve with a side of sour cream, guacamole, or salsa.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18B2C6A784D69648A5CABC78D9DF37D3', X'20D60FD60E781647B742561F90848C2B', X'20D60FD60E781647B742561F90848C2B', 0, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D838B7AFC263949BA5D05CD8B76BE95', X'20D60FD60E781647B742561F90848C2B', X'20D60FD60E781647B742561F90848C2B', 1, NULL, NULL, 2, NULL, 0, 'Add powdered sugar and beat until creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE23939013B74C4F8212ECBD1D29BCBD', X'20D60FD60E781647B742561F90848C2B', X'20D60FD60E781647B742561F90848C2B', 2, NULL, NULL, 2, NULL, 0, 'Add vanilla and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC6C47F07E3ADD4B8701ABA2B7DB826A', X'20D60FD60E781647B742561F90848C2B', X'20D60FD60E781647B742561F90848C2B', 3, NULL, NULL, 2, NULL, 0, 'Spoon in whipped topping and beat until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72A9C8EB5828CA4F9EE965E4511C76F7', X'20D60FD60E781647B742561F90848C2B', X'20D60FD60E781647B742561F90848C2B', 4, NULL, NULL, 2, NULL, 0, 'To thin out the mousse stir in the heavy whipping cream until really smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'404E34F962E0E148A97504746357EFCE', X'20D60FD60E781647B742561F90848C2B', X'20D60FD60E781647B742561F90848C2B', 5, NULL, NULL, 2, NULL, 0, 'Fill a pastry bag with the cheesecake mousse and pipe it into 6 skull shaped glasses.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D17C5187C9A1674FA64B089F9D88C1C6', X'20D60FD60E781647B742561F90848C2B', X'20D60FD60E781647B742561F90848C2B', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for up to 5 days until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A2A226505CF4D439027B7E703489308', X'1F46586638512B478094938AEB20E70F', X'1F46586638512B478094938AEB20E70F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AABD140F342084AA19BB779E10A61A6', X'1F46586638512B478094938AEB20E70F', X'1F46586638512B478094938AEB20E70F', 1, NULL, NULL, 2, NULL, 0, 'Pour brownie mix into large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E82AFB1027BBCF498BD661718E58CFB6', X'1F46586638512B478094938AEB20E70F', X'1F46586638512B478094938AEB20E70F', 2, NULL, NULL, 2, NULL, 0, 'Add oil, water, and eggs and stir until well combined (about 40 strokes).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'370EEF5BFF36344CB02BD607DD23307B', X'1F46586638512B478094938AEB20E70F', X'1F46586638512B478094938AEB20E70F', 3, NULL, NULL, 2, NULL, 0, 'Line a 12 x 18 (or a 13 x 18) inch baking sheet with non-stick tin foil. Or spray the pan with non-stick baking spray and line with two sheets of overlapping parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E844DA5A51B2994BB24CE917BF1F82BA', X'1F46586638512B478094938AEB20E70F', X'1F46586638512B478094938AEB20E70F', 4, NULL, NULL, 2, NULL, 0, 'Pour brownie batter into the pan and spread into a thin layer using a spatula.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1FCA0F80C479FF4EA8E6429D19BFCE81', X'1F46586638512B478094938AEB20E70F', X'1F46586638512B478094938AEB20E70F', 5, NULL, NULL, 2, NULL, 0, 'Bake for 16-20 minutes until the edges of the brownie feel firm to the touch and the surface no longer looks wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE69B5FD7CF1B4419C5077F9CFD16C30', X'1F46586638512B478094938AEB20E70F', X'1F46586638512B478094938AEB20E70F', 6, NULL, NULL, 2, NULL, 0, 'Remove from the oven and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5FAA52666441F4D98F39D3714CF600D', X'1F46586638512B478094938AEB20E70F', X'1F46586638512B478094938AEB20E70F', 7, NULL, NULL, 2, NULL, 0, 'Flip out onto a cutting board and peel off the tin foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9ECC6A29674194782D66AA910552A1A', X'1F46586638512B478094938AEB20E70F', X'1F46586638512B478094938AEB20E70F', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle with powdered sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60ACD4B1D2BE9B44A91CCAA7774CD103', X'1F46586638512B478094938AEB20E70F', X'1F46586638512B478094938AEB20E70F', 9, NULL, NULL, 2, NULL, 0, 'Cut out using skull shaped cutter then press the stamp into the brownie and peel it off carefully.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B08A5F7C03DD848A7B3A50826733398', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E43857D55E3134B9BB25168CB412EA3', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 1, NULL, NULL, 2, NULL, 0, 'Stir together the chocolate graham cracker crumbs, brown sugar, and melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCEFDBFF2512DB40AF50D5E0A8DB923F', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 2, NULL, NULL, 2, NULL, 0, 'Press about 2 tablespoons of the crumbs into 18 paper-lined muffin cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA4EEB2B0E0CE54CA5656886AC2ED4C4', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 3, NULL, NULL, 2, NULL, 0, 'Beat cream cheese on medium speed, scraping down the bowl, as needed, until creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34A14F6FAC79324F8644CA545448D30B', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 4, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15EAE0E57EF82F41A08EA4D6B0F8F982', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 5, NULL, NULL, 2, NULL, 0, 'Add egg and vanilla and beat until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE25A3A58DC8DE4AA200D2DD51EB656D', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 6, NULL, NULL, 2, NULL, 0, 'Stir in the sour cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B758CC923784BA41BD70F93967A5EDF8', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 7, NULL, NULL, 2, NULL, 0, 'Equally divide cheesecake filling among the 18 muffin cups, spooning about 2 tablespoons of filling into each.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8DCEED56243514881E4B2113F1A8386', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 8, NULL, NULL, 2, NULL, 0, 'Bake on the middle rack in the oven for 15-18 minutes until the cheesecakes no longer look wet, but the center still jiggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45FA9E960DF2414EBB40A74199166949', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 9, NULL, NULL, 2, NULL, 0, 'Remove and set on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91CB57DE9B714B44890383CAD11818FD', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 10, NULL, NULL, 2, NULL, 0, 'Allow to cool for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FBA98915E09A64F8243238331EA3233', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 11, NULL, NULL, 2, NULL, 0, 'Combine chocolate and heavy whipping cream in a small glass measuring cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5795FA3F69E5BA40B8E349E0048E3252', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 12, NULL, NULL, 2, NULL, 0, 'Heat in the microwave on high power for 40 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAFFBB6ACAB72740BDFAA74F9912BC65', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 13, NULL, NULL, 2, NULL, 0, 'Let the cup sit in the microwave for 3 minutes then remove and stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'298695FB298AAE458438ABF374CA4907', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 14, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 more second bursts of power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E716DA57A214434BBADB4C7855ECA1CE', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 15, NULL, NULL, 2, NULL, 0, 'Allow this chocolate ganache to cool until slightly thickened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAC42A53C87F2E4F94379AB7FF3801F2', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 16, NULL, NULL, 2, NULL, 0, 'Pour into a pastry bag or squeeze bottle fitted with a #3 round tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A2CFB613F417448990E28E3E121089B', X'B332B82E9D6ED04C83D6FF060F764A5F', X'B332B82E9D6ED04C83D6FF060F764A5F', 17, NULL, NULL, 2, NULL, 0, 'Pipe two eyes, two nostrils, and a mouth onto each cheesecake to make them look like Jack Skellington.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'464A792B52C82347AD63B0DDE84C7B72', X'EFD8CD89E4FDC74094A9F39E2A6781C5', X'EFD8CD89E4FDC74094A9F39E2A6781C5', 0, NULL, NULL, 2, NULL, 0, 'Pour the candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2394E150244884E8CBFBF2AA3ADD36B', X'EFD8CD89E4FDC74094A9F39E2A6781C5', X'EFD8CD89E4FDC74094A9F39E2A6781C5', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for three 30 second increments, stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E28D39C179DB840BE1CF6DB1C7231AC', X'EFD8CD89E4FDC74094A9F39E2A6781C5', X'EFD8CD89E4FDC74094A9F39E2A6781C5', 2, NULL, NULL, 2, NULL, 0, 'Allow the candy melts to sit for 2 minutes, then stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19D6947487E1824F8939F862A6268774', X'EFD8CD89E4FDC74094A9F39E2A6781C5', X'EFD8CD89E4FDC74094A9F39E2A6781C5', 3, NULL, NULL, 2, NULL, 0, 'If not completely melted, heat on high for 15 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6FACA60903E214CAB834AED12BB09E1', X'EFD8CD89E4FDC74094A9F39E2A6781C5', X'EFD8CD89E4FDC74094A9F39E2A6781C5', 4, NULL, NULL, 2, NULL, 0, 'Now, the MOST IMPORTANT THING IS TO allow the candy coating to cool to between 91 and 92 degrees Fahrenheit, stirring frequently until cooled properly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE2B63881A45494F9AE7C8765DE24BDD', X'EFD8CD89E4FDC74094A9F39E2A6781C5', X'EFD8CD89E4FDC74094A9F39E2A6781C5', 5, NULL, NULL, 2, NULL, 0, 'Pour in corn syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60E61FA5E0F35F4FAFD9D0B5A005802F', X'EFD8CD89E4FDC74094A9F39E2A6781C5', X'EFD8CD89E4FDC74094A9F39E2A6781C5', 6, NULL, NULL, 2, NULL, 0, 'Stir slowly until well incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE75856B7FC5534597B99CE98856A68B', X'EFD8CD89E4FDC74094A9F39E2A6781C5', X'EFD8CD89E4FDC74094A9F39E2A6781C5', 7, NULL, NULL, 2, NULL, 0, 'Pour mixture out onto a counter top, preferably marble or granite.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'270BD71A0FC95C4383CC57864E53DF02', X'EFD8CD89E4FDC74094A9F39E2A6781C5', X'EFD8CD89E4FDC74094A9F39E2A6781C5', 8, NULL, NULL, 2, NULL, 0, 'Knead until glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A08EE606825C24698BE0BB484AB3B05', X'EFD8CD89E4FDC74094A9F39E2A6781C5', X'EFD8CD89E4FDC74094A9F39E2A6781C5', 9, NULL, NULL, 2, NULL, 0, 'Wrap the modeling chocolate in plastic wrap and place in a zip top bag or airtight container.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4C29AC1E36BD44782C9BEADA19FE97B', X'EFD8CD89E4FDC74094A9F39E2A6781C5', X'EFD8CD89E4FDC74094A9F39E2A6781C5', 10, NULL, NULL, 2, NULL, 0, 'Allow to dry for at least 30 minutes before using.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3586E2DB88B0AE4A897226E2975DE0D7', X'EFD8CD89E4FDC74094A9F39E2A6781C5', X'EFD8CD89E4FDC74094A9F39E2A6781C5', 11, NULL, NULL, 2, NULL, 0, 'Modeling chocolate will keep for several months, if stored properly.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D67431128027B48932911868B412A11', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 0, NULL, NULL, 2, NULL, 0, 'Dip one pretzel into the orange candy melts and coat it completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2A464AF0CDA0D428CF25F5FB2399DEF', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 1, NULL, NULL, 2, NULL, 0, 'Remove from the candy using a dipping fork and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'257E1492D0CB644CA4C74A89A2D76563', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 2, NULL, NULL, 2, NULL, 0, 'Set pretzel on a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'308F7019D927BE4583D4581BC630129E', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 3, NULL, NULL, 2, NULL, 0, 'Dip a second pretzel and set it right next to the first pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FCCD636FB4A9D4EB0CBC312B9988C3A', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 4, NULL, NULL, 2, NULL, 0, 'Repeat until you have a row of six candy-coated pretzels lined up forming a rectangle pumpkin.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A8260AF6EADC844AA7B021CB6194D85', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 5, NULL, NULL, 2, NULL, 0, 'Freeze for about 3 minutes, just until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C76BC8A134BC644E9DCAE149CC94CB3D', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 6, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 10 pretzel pumpkins. You should have 5 extra pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC7791B752C6844EBE651AB457961D8D', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 7, NULL, NULL, 2, NULL, 0, 'Break about 1/2 inch off each end of the remaining pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30F2806FEC43B94EBD1BEE2705264E0C', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 8, NULL, NULL, 2, NULL, 0, 'Attach one small pretzel piece to each of the pumpkins to create the stem.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA102E2E91D0DA4AA4DFF4FE91CC26E0', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 9, NULL, NULL, 2, NULL, 0, 'Pour the melted green candy melts into a squeeze bottle or zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6D33917CD63664A84D261592C487802', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 10, NULL, NULL, 2, NULL, 0, 'Pipe on some green leaves and a vine going down one side of each pumpkin.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'917FB511D1117E4FBD96AD804573AAFC', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 11, NULL, NULL, 2, NULL, 0, 'Pour the black candy melts into another bottle or bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6B1FFB6EE07054E946BB88F689DADAB', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 12, NULL, NULL, 2, NULL, 0, 'Pipe two triangle eyes, one triangle nose, and one toothy grin onto each pretzel pumpkin to make them look like jack-o-lanterns.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5958EC10917F0642B2BF7C0B6C09A2C4', X'12E69ECB8712C2418936DD21EC43716C', X'12E69ECB8712C2418936DD21EC43716C', 13, NULL, NULL, 2, NULL, 0, 'Chill for about one minute just until the decorations harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CAE8D91432FB1043BB6AF364DA26138C', X'3048533A026E0643A57327D2B7984D7B', X'3048533A026E0643A57327D2B7984D7B', 0, NULL, NULL, 2, NULL, 0, 'Chop candy bars into small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA68878CCDEF734E85B3A3E48586DF8C', X'3048533A026E0643A57327D2B7984D7B', X'3048533A026E0643A57327D2B7984D7B', 1, NULL, NULL, 2, NULL, 0, 'Pour popcorn into an extra large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97DB6802B41EA34EB8F08F2558373553', X'3048533A026E0643A57327D2B7984D7B', X'3048533A026E0643A57327D2B7984D7B', 2, NULL, NULL, 2, NULL, 0, 'Pour white chocolate over popcorn and toss to evenly coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'489C37CF71B5DB4790194BD706C5CA5D', X'3048533A026E0643A57327D2B7984D7B', X'3048533A026E0643A57327D2B7984D7B', 3, NULL, NULL, 2, NULL, 0, 'Stir in candy bar pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F5D193CAB04384AA5F76CD36A2F1538', X'3048533A026E0643A57327D2B7984D7B', X'3048533A026E0643A57327D2B7984D7B', 4, NULL, NULL, 2, NULL, 0, 'Pour popcorn onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20300E57BB40974B84B30DDD1145991F', X'3048533A026E0643A57327D2B7984D7B', X'3048533A026E0643A57327D2B7984D7B', 5, NULL, NULL, 2, NULL, 0, 'Drizzle the milk chocolate over top of the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7CA2941F66DAC4BAA2C9015ADB0B71D', X'3048533A026E0643A57327D2B7984D7B', X'3048533A026E0643A57327D2B7984D7B', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10-15 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06C274503134FD4896D8A028BBAA239B', X'3048533A026E0643A57327D2B7984D7B', X'3048533A026E0643A57327D2B7984D7B', 7, NULL, NULL, 2, NULL, 0, 'Break popcorn into chunks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4D5BCDFB0760940B13A88EA179CE350', X'3048533A026E0643A57327D2B7984D7B', X'3048533A026E0643A57327D2B7984D7B', 8, NULL, NULL, 2, NULL, 0, 'Enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A611FBFF358474685E39D4C8626A554', X'C8D0C35371FC254BB1D6D34488197D9A', X'C8D0C35371FC254BB1D6D34488197D9A', 0, NULL, NULL, 2, NULL, 0, 'Unwrap the Rice Krispie Treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33F1F99A98E9C14F97A4A6CB77E90A10', X'C8D0C35371FC254BB1D6D34488197D9A', X'C8D0C35371FC254BB1D6D34488197D9A', 1, NULL, NULL, 2, NULL, 0, 'Cut a triangle out of one end of the cereal treat creating two ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'830DF35EAEF9A04B9939DF399EAF70ED', X'C8D0C35371FC254BB1D6D34488197D9A', X'C8D0C35371FC254BB1D6D34488197D9A', 2, NULL, NULL, 2, NULL, 0, 'Pour the chocolate into a tall drinking glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45CAB224249B554AAF196234D26DBE67', X'C8D0C35371FC254BB1D6D34488197D9A', X'C8D0C35371FC254BB1D6D34488197D9A', 3, NULL, NULL, 2, NULL, 0, 'Dip the tip of a lollipop stick or paper straw into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F89D385F67E5154B94C52C9D0D4530C2', X'C8D0C35371FC254BB1D6D34488197D9A', X'C8D0C35371FC254BB1D6D34488197D9A', 4, NULL, NULL, 2, NULL, 0, 'Insert the stick into the cereal treat. Repeat with all the treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'791C4176600F5C46AD8DD8480244A3E3', X'C8D0C35371FC254BB1D6D34488197D9A', X'C8D0C35371FC254BB1D6D34488197D9A', 5, NULL, NULL, 2, NULL, 0, 'Let the chocolate dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD70DEE0556C63468B965F263B75D1AE', X'C8D0C35371FC254BB1D6D34488197D9A', X'C8D0C35371FC254BB1D6D34488197D9A', 6, NULL, NULL, 2, NULL, 0, 'Holding the end of the stick, dip the Rice Krispie Treat into the chocolate, then lift it out and shake it to remove any excess chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61CDDE2487576F4F94EB9E7C9A2C42BD', X'C8D0C35371FC254BB1D6D34488197D9A', X'C8D0C35371FC254BB1D6D34488197D9A', 7, NULL, NULL, 2, NULL, 0, 'Set the chocolate dipped treat on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEAFFE7B10ACF64A8DE1DD750E37359D', X'C8D0C35371FC254BB1D6D34488197D9A', X'C8D0C35371FC254BB1D6D34488197D9A', 8, NULL, NULL, 2, NULL, 0, 'Immediately add two candy eyes and 1 pink heart sprinkle nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D37AB22E3129A64080103C5118520E33', X'C8D0C35371FC254BB1D6D34488197D9A', X'C8D0C35371FC254BB1D6D34488197D9A', 9, NULL, NULL, 2, NULL, 0, 'Add three white jimmies on either side of the nose to form whiskers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60B6EEF7088F8146A657AD89D6ECE46A', X'C8D0C35371FC254BB1D6D34488197D9A', X'C8D0C35371FC254BB1D6D34488197D9A', 10, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator (for pure chocolate) or the freezer (for candy melts) until hardened, about 5-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E71C98D9F671E245A4D309A82B91EE03', X'C8D0C35371FC254BB1D6D34488197D9A', X'C8D0C35371FC254BB1D6D34488197D9A', 11, NULL, NULL, 2, NULL, 0, 'Repeat dipping and decorating all 8 treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E45F17B6E53E8479D969D48B478C783', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 0, NULL, NULL, 2, NULL, 0, 'Fill all the cavities in one Day of the Dead sugar skull silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F393BB69DC8EAC4F9BF2EB593C438814', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 1, NULL, NULL, 2, NULL, 0, 'Turn the mold upside down over a piece of parchment paper allowing the excess chocolate to drip out.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EBD4A11B4F27F498325AE3EE219893C', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 2, NULL, NULL, 2, NULL, 0, 'Shake the mold until you just have a thin coating of chocolate around the side and bottom of the skulls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A6FD13C14187E4F863C4B3D747C5DDC', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 3, NULL, NULL, 2, NULL, 0, 'Turn the mold over and use an offset spatula to scrape the chocolate off the top of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A665ECE47AA9844DB8148322040CC7D3', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate until the chocolate hardens then remove and let sit at room temperature for at least 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE2D45B96B729748A84E31E15C8A0137', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 5, NULL, NULL, 2, NULL, 0, 'Repeat using a second mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0B0641E9D0C58459044A750053CAF8A', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 6, NULL, NULL, 2, NULL, 0, 'In a small saucepan or in the microwave, heat the heavy whipping cream just until it begins to bubble around the edges. (Scald the cream).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D10552BD45609C4E97FEA9D55AA5BBA0', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle the cinnamon and cayenne pepper into the cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED39204DCDD464469B096F78B8813570', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 8, NULL, NULL, 2, NULL, 0, 'Stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7E6E01723CF13468F8A4C746645B947', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 9, NULL, NULL, 2, NULL, 0, 'Set aside for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FB0DE2D958A7940B184882DA05B9443', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 10, NULL, NULL, 2, NULL, 0, 'Reheat just until hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'845033B7C8E16E4DAEE2EFBA5134955E', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 11, NULL, NULL, 2, NULL, 0, 'Strain through a fine mesh sieve that is set over the bowl of 6 ounces of melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C267682A6EBD0C468BF5861A196F90CB', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 12, NULL, NULL, 2, NULL, 0, 'Begin stirring in the center of the bowl until the chocolate and cream start to blend together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC06198925DCE04A92F936EABC39038A', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 13, NULL, NULL, 2, NULL, 0, 'Continue to stir through the entire bowl until the chocolate ganache completely comes together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC0E03FAE79A9E4F89DACE277897DF25', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 14, NULL, NULL, 2, NULL, 0, 'Pour ganache into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C30615A0C3CB1D499C711F7C5DB9E3E0', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 15, NULL, NULL, 2, NULL, 0, 'Pipe the ganache into all 18 skull shaped chocolate shells filling each so that you have between 1/8th and 1/16th of an inch of space between the ganache and the top edge of the chocolate shell.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9FE446968B8D3468832EC63E8455A95', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 16, NULL, NULL, 2, NULL, 0, 'Refrigerate until the ganache firms up.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17DB4444D03E2243AD673ADE0A7E5550', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 17, NULL, NULL, 2, NULL, 0, 'Remove and let the filled mold sit at room temperature for at least 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92EAB0AC735AA049B3C6BA301ED169BB', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 18, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of chocolate over top of each candy, scraping off any excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DB8A427EA262046B3565FD65D91C9FF', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 19, NULL, NULL, 2, NULL, 0, 'Refrigerate for a few minutes until that chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1113C06BE73AD84B9532BD04A64EBBA1', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 20, NULL, NULL, 2, NULL, 0, 'Remove the chocolates from the molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2E9B0BAFE07A84B9B46D2DECAED6FA2', X'8E90B833BF82DE4FB4858BE969E673D3', X'8E90B833BF82DE4FB4858BE969E673D3', 21, NULL, NULL, 2, NULL, 0, 'If desired, brush some silver luster dust over top of each candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B87CC2116DA87489CBCF0D8CDA6F080', X'0DEA33097990F44AA2D86B2145E1E9DF', X'0DEA33097990F44AA2D86B2145E1E9DF', 0, NULL, NULL, 2, NULL, 0, 'Pour the melted green candy into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94A5583625AB474986BB36A1DF4D2624', X'0DEA33097990F44AA2D86B2145E1E9DF', X'0DEA33097990F44AA2D86B2145E1E9DF', 1, NULL, NULL, 2, NULL, 0, 'Pipe the candy into 8 cavities of a silicone shot glass mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DFF9C2785D46C47B9403FD055734F6F', X'0DEA33097990F44AA2D86B2145E1E9DF', X'0DEA33097990F44AA2D86B2145E1E9DF', 2, NULL, NULL, 2, NULL, 0, 'Tap the mold to remove air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5179449E9FE5740B8770B8CA0485B77', X'0DEA33097990F44AA2D86B2145E1E9DF', X'0DEA33097990F44AA2D86B2145E1E9DF', 3, NULL, NULL, 2, NULL, 0, 'Freeze for 10-12 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9C7C4387F5A5148A4B187386050E0F9', X'0DEA33097990F44AA2D86B2145E1E9DF', X'0DEA33097990F44AA2D86B2145E1E9DF', 4, NULL, NULL, 2, NULL, 0, 'Remove and push snack cups out of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6611131659590D4BB7A1FA273413536D', X'0DEA33097990F44AA2D86B2145E1E9DF', X'0DEA33097990F44AA2D86B2145E1E9DF', 5, NULL, NULL, 2, NULL, 0, 'Set aside for 10 minutes so the candy comes to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D51920CA0F4C643885DC5E5D47A5DB4', X'0DEA33097990F44AA2D86B2145E1E9DF', X'0DEA33097990F44AA2D86B2145E1E9DF', 6, NULL, NULL, 2, NULL, 0, 'Pour black candy melts into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'558F7F3ACB647644B666F235383E6F18', X'0DEA33097990F44AA2D86B2145E1E9DF', X'0DEA33097990F44AA2D86B2145E1E9DF', 7, NULL, NULL, 2, NULL, 0, 'Pipe dribbles of candy over the top edge of the snack cups forming the monster''s black hair.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'472A6AFD572C8144AE7527461A295A57', X'0DEA33097990F44AA2D86B2145E1E9DF', X'0DEA33097990F44AA2D86B2145E1E9DF', 8, NULL, NULL, 2, NULL, 0, 'Use the black candy to attach two candy eyes to each snack cup just under the black hair.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15494C43BC7C9548A1E9D3B27CBDE2CD', X'0DEA33097990F44AA2D86B2145E1E9DF', X'0DEA33097990F44AA2D86B2145E1E9DF', 9, NULL, NULL, 2, NULL, 0, 'Attach two chocolate fudge rolls on either side of the snack cups, placing them near the bottom edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F18642713980A6418F5FFCCCEEE27F82', X'0DEA33097990F44AA2D86B2145E1E9DF', X'0DEA33097990F44AA2D86B2145E1E9DF', 10, NULL, NULL, 2, NULL, 0, 'Pipe on a scar using black candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74AA036E5730E841AC65B9DA91C494C4', X'0DEA33097990F44AA2D86B2145E1E9DF', X'0DEA33097990F44AA2D86B2145E1E9DF', 11, NULL, NULL, 2, NULL, 0, 'Freeze for 3 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98457EFF302D1643ACB31D2780C4858B', X'0DEA33097990F44AA2D86B2145E1E9DF', X'0DEA33097990F44AA2D86B2145E1E9DF', 12, NULL, NULL, 2, NULL, 0, 'Fill with Welch''s Fruit Snacks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'887515490346834ABB6286F254640E22', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 0, NULL, NULL, 2, NULL, 0, 'Pour the vibrant green candy melts into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEED70BE1542C94C9AA24CA1116A7871', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 1, NULL, NULL, 2, NULL, 0, 'Pipe the green candy melts into 8 shot glass cavities in your silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A2E45399029644AB838C04C21EDED77', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 2, NULL, NULL, 2, NULL, 0, 'Tap the mold a few times to remove any air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5D00ED1DC1F264084042E547A099B7A', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 3, NULL, NULL, 2, NULL, 0, 'Freeze for 12-15 minutes until the candy is hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'897EE8A807DE014DBB43AAD608CE0FBC', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 4, NULL, NULL, 2, NULL, 0, 'Let sit at room temperature for 5 minutes then un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A931C874816DC549BA1BD7B9379F59FF', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 5, NULL, NULL, 2, NULL, 0, 'Push the candy out of the silicone mold to remove.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C53B737D9F4E5F49B0213BA18A482813', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 6, NULL, NULL, 2, NULL, 0, 'Set aside for 10 minutes before decorating.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B58630E4B06722419CAF9730511E3A71', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 7, NULL, NULL, 2, NULL, 0, 'Pour red candy melts into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1422E03D04D2ED4AA311E042564D8F76', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 8, NULL, NULL, 2, NULL, 0, 'Pipe some red candy melts onto the back of a candy eye and press the eye onto the candy cup being sure to smear some of the red candy around the edge of the candy eye.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F35F3EAC9FE4A41863632EF101A6534', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 9, NULL, NULL, 2, NULL, 0, 'Add another eye, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46CE771C22B1114BB335BC59F61D6AE7', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 10, NULL, NULL, 2, NULL, 0, 'Pipe drips of blood red candy melts over the top edge of the candy cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD8D0A53DE284D41AEE481C30C3E67B2', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 11, NULL, NULL, 2, NULL, 0, 'Pop the candy cup in the freezer for 2-3 minutes until the red candy melts harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CAD7A6A0AA4E4D418DF890711C9DFDFB', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 12, NULL, NULL, 2, NULL, 0, 'Remove and allow the candy cup to sit at room temperature for a few minutes before piping on a black mouth, nose, and scars, as desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DFA450F9880D3418871037A7F2F8483', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 13, NULL, NULL, 2, NULL, 0, 'Let the black candy melts dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A25D382667BD8E4AB0CA0870B5037B4B', X'59473B56B572CA43954A0B3602E2DFAC', X'59473B56B572CA43954A0B3602E2DFAC', 14, NULL, NULL, 2, NULL, 0, 'Fill the candy cups with candy, pudding, or chocolate mousse.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E17520454099C439F89F5D612EF6FBE', X'073377BDD2492F40960CF4C026A60681', X'073377BDD2492F40960CF4C026A60681', 0, NULL, NULL, 2, NULL, 0, 'Fill a small plastic bowl with orange chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA436875ECBB794695F955916EBD608D', X'073377BDD2492F40960CF4C026A60681', X'073377BDD2492F40960CF4C026A60681', 1, NULL, NULL, 2, NULL, 0, 'Turn the bowl upside down and allow some of the chocolate to drip out.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D935C9AC40C363438F4A8353E585C256', X'073377BDD2492F40960CF4C026A60681', X'073377BDD2492F40960CF4C026A60681', 2, NULL, NULL, 2, NULL, 0, 'You want just enough candy coating the sides of the bowl to form a nice shell (about 1/8th inch thick).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC538A1644CC2C429BB8A47E4DEFBB0B', X'073377BDD2492F40960CF4C026A60681', X'073377BDD2492F40960CF4C026A60681', 3, NULL, NULL, 2, NULL, 0, 'Scrape the dripping chocolate off the edge of the bowl then invert the bowl and place it in the freezer for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB2D5C9F5F08AA47A98DD6B2F50A8F72', X'073377BDD2492F40960CF4C026A60681', X'073377BDD2492F40960CF4C026A60681', 4, NULL, NULL, 2, NULL, 0, 'Repeat, making a total of 6 bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63128532FBB90D46B6D09B940ACFF9A6', X'073377BDD2492F40960CF4C026A60681', X'073377BDD2492F40960CF4C026A60681', 5, NULL, NULL, 2, NULL, 0, 'Let sit at room temperature for at least 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89B3F2FD3CBAC34B8E0FD17193302915', X'073377BDD2492F40960CF4C026A60681', X'073377BDD2492F40960CF4C026A60681', 6, NULL, NULL, 2, NULL, 0, 'Draw jack-o-lantern faces on the bowls using a food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BC76C53D3327C40869757D7235A2A41', X'0DE4E5FCADB43C4D974D42A8F14CCEBB', X'0DE4E5FCADB43C4D974D42A8F14CCEBB', 0, NULL, NULL, 2, NULL, 0, 'Beat heaving whipping cream until it holds stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6584CE7D6E59E34B816ECF899141073A', X'0DE4E5FCADB43C4D974D42A8F14CCEBB', X'0DE4E5FCADB43C4D974D42A8F14CCEBB', 1, NULL, NULL, 2, NULL, 0, 'Add sweetened condensed milk, orange oil, and orange food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'649F2E4F6AFE5046AB49B61D873127B0', X'0DE4E5FCADB43C4D974D42A8F14CCEBB', X'0DE4E5FCADB43C4D974D42A8F14CCEBB', 2, NULL, NULL, 2, NULL, 0, 'Beat just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C73F38C04077014990731C502D5F2D0C', X'0DE4E5FCADB43C4D974D42A8F14CCEBB', X'0DE4E5FCADB43C4D974D42A8F14CCEBB', 3, NULL, NULL, 2, NULL, 0, 'Pour into a plastic tub and freeze for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0ABCAF5EEF6E1B4EA5E739FB3DBAD29D', X'0DE4E5FCADB43C4D974D42A8F14CCEBB', X'0DE4E5FCADB43C4D974D42A8F14CCEBB', 4, NULL, NULL, 2, NULL, 0, 'Scoop ice cream into chocolate jack-o-lantern bowls just before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E77E1F6F4F56D3429D9745411950CFE8', X'0DE4E5FCADB43C4D974D42A8F14CCEBB', X'0DE4E5FCADB43C4D974D42A8F14CCEBB', 5, NULL, NULL, 2, NULL, 0, 'Optional: add a chocolate fudge roll stem if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E8C49C1B05B2AA42B15ED99EFBFA4309', X'7C74219C00282D41BC1388699E44CDEF', X'7C74219C00282D41BC1388699E44CDEF', 0, NULL, NULL, 2, NULL, 0, 'Place Oreo Cookies in the bowl of a food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01C4A1AD39E68C45A9276FB5ABCD8DA0', X'7C74219C00282D41BC1388699E44CDEF', X'7C74219C00282D41BC1388699E44CDEF', 1, NULL, NULL, 2, NULL, 0, 'Pulse on high until you have fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DDA3E09DA6B6646BDC7519DC0F3611B', X'7C74219C00282D41BC1388699E44CDEF', X'7C74219C00282D41BC1388699E44CDEF', 2, NULL, NULL, 2, NULL, 0, 'Scoop out 2/3 cup crumbs for later.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'861581684AB071468D3A6C0F691AF9E8', X'7C74219C00282D41BC1388699E44CDEF', X'7C74219C00282D41BC1388699E44CDEF', 3, NULL, NULL, 2, NULL, 0, 'Add cream cheese to the food processor bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'924FCE8819B3624098FDEAF11D1A436A', X'7C74219C00282D41BC1388699E44CDEF', X'7C74219C00282D41BC1388699E44CDEF', 4, NULL, NULL, 2, NULL, 0, 'Pulse until the mixture is well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06B1A804F4692541BB4DBC3D928B8CFE', X'7C74219C00282D41BC1388699E44CDEF', X'7C74219C00282D41BC1388699E44CDEF', 5, NULL, NULL, 2, NULL, 0, 'Scoop out 28 tablespoon size balls of Oreo dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC11702AB4A4A341B3C3535E7E570845', X'7C74219C00282D41BC1388699E44CDEF', X'7C74219C00282D41BC1388699E44CDEF', 6, NULL, NULL, 2, NULL, 0, 'Roll each scoop into a ball then roll in Oreo Cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C788697B84BAB44284CE4B3CC60C902C', X'7C74219C00282D41BC1388699E44CDEF', X'7C74219C00282D41BC1388699E44CDEF', 7, NULL, NULL, 2, NULL, 0, 'Pipe `L` shaped spider legs onto parchment paper lined baking sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C50606BC7E952543A0DFE85E123A2FE3', X'7C74219C00282D41BC1388699E44CDEF', X'7C74219C00282D41BC1388699E44CDEF', 8, NULL, NULL, 2, NULL, 0, 'You''ll need 8 legs per spider but you''ll want extras in case of breakage.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A33C082CBF74024E96655F79925A472F', X'7C74219C00282D41BC1388699E44CDEF', X'7C74219C00282D41BC1388699E44CDEF', 9, NULL, NULL, 2, NULL, 0, 'Let the candy harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3791BD62AF11584683D8CFBF00E9CE68', X'7C74219C00282D41BC1388699E44CDEF', X'7C74219C00282D41BC1388699E44CDEF', 10, NULL, NULL, 2, NULL, 0, 'Insert 8 legs into each Oreo ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4C5BC352A750D4B878A5B80F5479776', X'7C74219C00282D41BC1388699E44CDEF', X'7C74219C00282D41BC1388699E44CDEF', 11, NULL, NULL, 2, NULL, 0, 'Attach 2 candy eyes using candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E05B1D5315FA3C408D9F26303CB9F42A', X'62F639D3C64C744D974AC23585A332ED', X'62F639D3C64C744D974AC23585A332ED', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 275 degrees. Set a wire cooling rack on a baking sheet and spray with cooking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB0EA0BEC4C1B144A53BBDD23586DBEC', X'62F639D3C64C744D974AC23585A332ED', X'62F639D3C64C744D974AC23585A332ED', 1, NULL, NULL, 2, NULL, 0, 'Break bread into small pieces. Place in a mixing bowl. Pour milk over bread and mash with a fork until it forms a paste. Stir in salt, pepper, garlic, and ketchup. Add ground sirloin and stir, just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA5823C45B9A0648B54FC8A530DD964A', X'62F639D3C64C744D974AC23585A332ED', X'62F639D3C64C744D974AC23585A332ED', 2, NULL, NULL, 2, NULL, 0, 'Spray a hand shaped cookie cutter with non-stick cooking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8F777C2A619084F91C3D1F6AEC77662', X'62F639D3C64C744D974AC23585A332ED', X'62F639D3C64C744D974AC23585A332ED', 3, NULL, NULL, 2, NULL, 0, 'Press 1/6th of the mixture into the cookie cutter, being careful to press the meat into the finger area.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59C99329B3C7F643A31D89AC5E5C7047', X'62F639D3C64C744D974AC23585A332ED', X'62F639D3C64C744D974AC23585A332ED', 4, NULL, NULL, 2, NULL, 0, 'Remove the cutter and use a spatula to transfer the Hand-Burger to the prepared rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F28090111896547BA0C395B488EC478', X'62F639D3C64C744D974AC23585A332ED', X'62F639D3C64C744D974AC23585A332ED', 5, NULL, NULL, 2, NULL, 0, 'Repeat for remaining burgers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36E736F380EB0A42BA5A2BDC0F11A462', X'62F639D3C64C744D974AC23585A332ED', X'62F639D3C64C744D974AC23585A332ED', 6, NULL, NULL, 2, NULL, 0, 'Bake for 12 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B78B3B43F774AD48B733F75CB6DEAD1B', X'62F639D3C64C744D974AC23585A332ED', X'62F639D3C64C744D974AC23585A332ED', 7, NULL, NULL, 2, NULL, 0, 'Spray an indoor grill pan (or skillet) with non-stick cooking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'859557EFEDACA14BAE366C5F8C83EF72', X'62F639D3C64C744D974AC23585A332ED', X'62F639D3C64C744D974AC23585A332ED', 8, NULL, NULL, 2, NULL, 0, 'Heat over high heat. Remove Hand-Burgers from oven and place them onto grill pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BFD154EB083C74AB499878030AAFE8F', X'62F639D3C64C744D974AC23585A332ED', X'62F639D3C64C744D974AC23585A332ED', 9, NULL, NULL, 2, NULL, 0, 'Brown burgers on both sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D37981DE424884B82F5718631C762AF', X'62F639D3C64C744D974AC23585A332ED', X'62F639D3C64C744D974AC23585A332ED', 10, NULL, NULL, 2, NULL, 0, 'Cook to medium (140-145 degrees) or medium well (150-155 degrees).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'325866C94D808148A17161DAFF4201C1', X'62F639D3C64C744D974AC23585A332ED', X'62F639D3C64C744D974AC23585A332ED', 11, NULL, NULL, 2, NULL, 0, 'Serve on dinner rolls or slider buns with ketchup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9F365376E919747848FE27216982EEF', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 0, NULL, NULL, 2, NULL, 0, 'Unwrap Reese''s Cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A01FC1B6E674064FAE82423317134089', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 1, NULL, NULL, 2, NULL, 0, 'Using a food use only paint brush, paint a black candy melts widow''s peak (hair) onto each Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C077FBB0A53B364F9F538DF1331F2D27', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 2, NULL, NULL, 2, NULL, 0, 'Add two eyes, a mouth and two eyebrows.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E286B0D47A22E4DB5763C57E2D10A69', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 3, NULL, NULL, 2, NULL, 0, 'Pop the candy into the freezer for a minute or two until the black candy melts harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'130BCEC1377E564E93A3BBBD8F85F76A', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 4, NULL, NULL, 2, NULL, 0, 'Add a tiny white candy melts highlight in each eye and add two fangs to the mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B34FFF6A480B646B26B4329DAB6E348', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 5, NULL, NULL, 2, NULL, 0, 'Allow the candy to harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E586BF4795824429407E421AB1FE385', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 6, NULL, NULL, 2, NULL, 0, 'Dip a Pocky Stick in black candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F1B6B2C22C29045A3CE007E9251CE8F', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 7, NULL, NULL, 2, NULL, 0, 'Set it on a piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D50EF53BA6A6B4799A4F4DCCCBA0824', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 8, NULL, NULL, 2, NULL, 0, 'Set one Reese''s Cup vampire on top so the Pocky Stick acts like a lollipop stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAB780B08F9E9C4A88581FADDFDDD807', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 9, NULL, NULL, 2, NULL, 0, 'Allow that candy to harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60729D42A4635549AEDC07FCFC652527', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 10, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of black frosting on a chocolate cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCDC0C55B0273544B8D88A05FFD02D32', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 11, NULL, NULL, 2, NULL, 0, 'Insert the Pocky Stick with the vampire into the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF685BC1D31813498ABE9FFC65A027D6', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 12, NULL, NULL, 2, NULL, 0, 'Pipe white frosting onto the black frosting swirl to make a ruffled shirt for the vampire.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A35E915876B0E84FB2ED0C75FC6D7182', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 13, NULL, NULL, 2, NULL, 0, 'Cut collars and bow ties out of fruit rolls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D90BDD899E93174F8E26B70A733D9457', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 14, NULL, NULL, 2, NULL, 0, 'Wrap one collar around the frosting swirl just under the vampire''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'125F7C0505CE30499154D8B644887226', X'4D0D57E68CF1B84D90C8779809FFED6D', X'4D0D57E68CF1B84D90C8779809FFED6D', 15, NULL, NULL, 2, NULL, 0, 'Add a bow tie to the ruffled white frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23C11C1C6F7FC241BACFD6323476FA24', X'08CECE62B54D1E46BC468AC2499B05EE', X'08CECE62B54D1E46BC468AC2499B05EE', 0, NULL, NULL, 2, NULL, 0, 'Combine flour, baking powder, and salt in a mixing bowl and blend with a fork or whisk.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26356AE3C887C740ACA4EF9D770EF722', X'08CECE62B54D1E46BC468AC2499B05EE', X'08CECE62B54D1E46BC468AC2499B05EE', 1, NULL, NULL, 2, NULL, 0, 'Add half of the lard and use a pastry cutter to cut the lard into the flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C4250F8B7A8364A85D5E25C87F26A07', X'08CECE62B54D1E46BC468AC2499B05EE', X'08CECE62B54D1E46BC468AC2499B05EE', 2, NULL, NULL, 2, NULL, 0, 'Add the remaining lard and continue to cut it into the flour until it resembles coarse crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5C661495443E64C90D0D7C32C37F80C', X'08CECE62B54D1E46BC468AC2499B05EE', X'08CECE62B54D1E46BC468AC2499B05EE', 3, NULL, NULL, 2, NULL, 0, 'Slowly pour in the water stirring as you go until the dough comes together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD86BA3D6D45B24F99990CCE97E4EA8A', X'08CECE62B54D1E46BC468AC2499B05EE', X'08CECE62B54D1E46BC468AC2499B05EE', 4, NULL, NULL, 2, NULL, 0, 'Add at least  of a cup of the water then only add any remaining water as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03FAF5E922E3384284855DFAD218DD5A', X'08CECE62B54D1E46BC468AC2499B05EE', X'08CECE62B54D1E46BC468AC2499B05EE', 5, NULL, NULL, 2, NULL, 0, 'You want the dough to come together in a cohesive ball and not be sticky.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D8DCAA8B9E7A14192EB9FC08246510F', X'08CECE62B54D1E46BC468AC2499B05EE', X'08CECE62B54D1E46BC468AC2499B05EE', 6, NULL, NULL, 2, NULL, 0, 'Cover the dough and set aside for 10-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8132BB2C10D89F48B138C32FA2937434', X'024AAE680F27484D9ECF23103A177F8A', X'024AAE680F27484D9ECF23103A177F8A', 0, NULL, NULL, 2, NULL, 0, 'Stir together the chicken, enchilada sauce and cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5685EA1781C6484D8B6D8276B71EF93A', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65C7DCE9DB9AC64B8D5487AEEA9A7692', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 1, NULL, NULL, 2, NULL, 0, 'Cut the dough into 6 equal portions.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95DC9BFB2CA2934E8FCF90EAFA7C49A8', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 2, NULL, NULL, 2, NULL, 0, 'Dust your work surface with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'617F5B2BF7791B419CEA6C5C741CC2FB', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 3, NULL, NULL, 2, NULL, 0, 'Roll out a portion of dough thinning out the edges.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0B6E40529A64543BCE320683AC7F29C', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 4, NULL, NULL, 2, NULL, 0, 'Drape the dough over one skull in a 6 cavity non-stick pan allowing the dough to form the skull shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69B0341DE8D2D0449F564D7D1CDE7396', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 5, NULL, NULL, 2, NULL, 0, 'Spoon in 1/6th of the chicken mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46F5C925192A9445AAAB394668A715E9', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 6, NULL, NULL, 2, NULL, 0, 'Whisk together the egg and water.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'335FC2CC905F75478B0EB2BDD837F7C2', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 7, NULL, NULL, 2, NULL, 0, 'Brush the top edge of the dough that hangs over the edge of the skull.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E315C54BEDA1C408521D56158D64BCC', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 8, NULL, NULL, 2, NULL, 0, 'Fold the dough over the chicken, cutting off any excess dough and pinching the seams together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83959F58756E914DA51BADAA51310321', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 9, NULL, NULL, 2, NULL, 0, 'Brush the top with egg wash.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AACE5047F283EF40848F4C6DCC94CE02', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 10, NULL, NULL, 2, NULL, 0, 'Repeat filling all 6 skull cavities in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'493DF33A9A09C241A9551AD2C40D7662', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 11, NULL, NULL, 2, NULL, 0, 'Bake for 24-26 minutes until the top crust starts to turn light golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54722885CC0F24468EBCAE715A7F0C98', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 12, NULL, NULL, 2, NULL, 0, 'Remove and let cool in pan for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDE8C70AA032FB499978E3A875FDBDC5', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 13, NULL, NULL, 2, NULL, 0, 'Place a cutting board over the skulls and carefully flip the pan and board upside down so the skulls gently fall out onto the cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5CC44538748F0408E510CFF665A9FAE', X'2226C380B4342548B8B6BBD86DD1308F', X'2226C380B4342548B8B6BBD86DD1308F', 14, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'007DF5132B4DB745A81788EC5C3ABBBE', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 0, NULL, NULL, 2, NULL, 0, 'Create a simply syrup by combining the water and sugar in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'910D47FAB73D4D41A052857FB622BA72', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for one minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E4982628059994585E61F5CA6A6A2A6', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BA79A02CD573E43B41C3C76BA48D53A', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 3, NULL, NULL, 2, NULL, 0, 'Heat on high for 30-45 seconds until it comes to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51FFC89BEF0AB249AC84AD21B52299BF', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 4, NULL, NULL, 2, NULL, 0, 'Carefully remove from microwave.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A5A6462CAFA564DA1623F6690211B59', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 5, NULL, NULL, 2, NULL, 0, 'Place block of cream cheese in large mixing bowl (with a spout, preferably).');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B5C71B5A0750C4AB146FBE61F1F4805', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 6, NULL, NULL, 2, NULL, 0, 'Pour all but 1 tablespoon of the simple syrup over the cream cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'941B5C326B386E4E9A9EEAC65AD1146E', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 7, NULL, NULL, 2, NULL, 0, 'Beat with electric mixer until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E69F84395FF7E24AA4D8561B3D0BA90E', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 8, NULL, NULL, 2, NULL, 0, 'Add heavy whipping cream and vanilla and blend together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DAFD0E24D584E48B25E91B55AAE6A3C', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 9, NULL, NULL, 2, NULL, 0, 'Stir the remaining tablespoon of simple syrup into the raspberry puree.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2C755236261754BBB044D0F35F5E338', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 10, NULL, NULL, 2, NULL, 0, 'Drizzle raspberry puree into 8 zombie popsicle molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'887FD0A567298B428FAB03687B860DE3', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 11, NULL, NULL, 2, NULL, 0, 'Fill the molds with the cheesecake mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9E764296EE8CA4191C6A91C06C25136', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 12, NULL, NULL, 2, NULL, 0, 'Insert the legs, making sure they are facing forward, and pop into the freezer for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'208F32F49ACDC14797584BEBE0A7498A', X'A0081EB1476E2540B5FC387A99CA713F', X'A0081EB1476E2540B5FC387A99CA713F', 13, NULL, NULL, 2, NULL, 0, 'Peel off the silicone mold before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74DA96836BA3FF48AA74169D8ED0AF7E', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 0, NULL, NULL, 2, NULL, 0, 'Unwrap peppermint patties.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A37F2E8A26C524EA6639A20E3D065D9', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 1, NULL, NULL, 2, NULL, 0, 'Brush a small amount of melted black candy melts on the back of each mini peppermint patty and attach one to each larger peppermint patty.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AC1141185D28343A3D0DD819A02DB5E', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 2, NULL, NULL, 2, NULL, 0, 'Dab some black candy coating in the center of the mini peppermint patty and arrange 3 white jimmies on either size to create the cat''s whiskers and add a jumbo pink heart in the center on top for the nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD5EB9E6B9F915479C85D838077FDDDE', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 3, NULL, NULL, 2, NULL, 0, 'Use black candy coating to attach two candy eyes to each peppermint patty.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83917BA973DB45419B1A513783B3853B', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 4, NULL, NULL, 2, NULL, 0, 'Brush two triangle shaped ears onto a parchment lined baking sheet and set the peppermint patty cat over the bottom edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'972EAD7DCAE55C48A4DD15E5558F9750', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 5, NULL, NULL, 2, NULL, 0, 'Immediately set 1 small pink heart sprinkle up against the peppermint patty on top of each candy ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CF9BA3565DB3D4EB23E3EF176A9FD9F', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 6, NULL, NULL, 2, NULL, 0, 'Let the candy ears harden before moving your peppermint patty cats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'518CE13322DF62428FCA9DD64DCF2ED9', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 7, NULL, NULL, 2, NULL, 0, 'Pour white candy melts into a zip top bag, snip off one tip, and pipe 12 fish carcasses onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'746AD593CE0AAB4DA76EEACA13748414', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 8, NULL, NULL, 2, NULL, 0, 'Freeze for 3-5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F6D2C90E5AE41438CF0CA98D05B3469', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 9, NULL, NULL, 2, NULL, 0, 'Remove and peel the fish carcasses off the paper and flip over so the smooth side faces out.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'895710E3D184BF419B14AA2FD6DF1700', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 10, NULL, NULL, 2, NULL, 0, 'Assemble the cupcakes by wrapping each cupcake in a picket fence cupcake wrapper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1A142348DFF734B841F7B9DD03DF3CA', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 11, NULL, NULL, 2, NULL, 0, 'Then press one peppermint patty cat on the front near the top of the frosting swirl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'788056F2E391F747BC7807E98AE34981', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 12, NULL, NULL, 2, NULL, 0, 'Press one black M&M on either side of the frosting swirl below the cat head to create the paws.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8426F5699F55654FB14722C9763FAEF0', X'9FE18D26796F04419C0A2998FA9E481A', X'9FE18D26796F04419C0A2998FA9E481A', 13, NULL, NULL, 2, NULL, 0, 'Attach one fish carcass to the frosting swirl in between the two candy paws.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B7BD1581A9B5F47810F6E224DD60061', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 0, NULL, NULL, 2, NULL, 0, 'Arrange sets of 5 Pretzel Dipping Sticks on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B021C5500CE4F746AEFD597837BBC84A', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 1, NULL, NULL, 2, NULL, 0, 'Pour melted green candy melts into a tall glass or mug.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DC2F563B642F34699ECBAC823759F19', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 2, NULL, NULL, 2, NULL, 0, 'Dip one pretzel in the green candy melts so that all but about 1/2 inch at one end is covered.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE263A6FC0612040A6DD2BECCE28D334', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 3, NULL, NULL, 2, NULL, 0, 'Lift out of candy coating and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59140D75B3A67E448BCC69352D2D3193', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 4, NULL, NULL, 2, NULL, 0, 'Set on the parchment lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7B717811986054BA362E965563DBA7E', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 5, NULL, NULL, 2, NULL, 0, 'Dip a second pretzel then set it so that it sticks to the first pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27A3163BD438E744A4F05E8910FE0C75', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 6, NULL, NULL, 2, NULL, 0, 'Repeat until you have all five dipped pretzels stuck to each other forming a rectangle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87348B0D7F92FC4EA845F8AF1A9C848D', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 7, NULL, NULL, 2, NULL, 0, 'Freeze for 3-4 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BF9D97FCB81214E8F6BD425B2B1FD5F', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 8, NULL, NULL, 2, NULL, 0, 'Let sit at room temperature for 5 minutes before decorating.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F18ECF5D027C5A4CAAF88D286A7C6C1D', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 9, NULL, NULL, 2, NULL, 0, 'Pour black candy melts into a zip top bag and snip the tip off of one end.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1020AD54EB45024888720B3C287C1DF8', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 10, NULL, NULL, 2, NULL, 0, 'Pipe black candy melts over the exposed edge of the pretzels making the candy look like hair.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF0EBB4721A3F44B98A57C7E9284E5B3', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 11, NULL, NULL, 2, NULL, 0, 'Pipe a scar and a mouth onto each pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F347246E4F757744975BAD7D0AC62081', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 12, NULL, NULL, 2, NULL, 0, 'Pipe a dot of black candy melts on the back side of two candy eyes and attach them to each of the pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B8C6F993FAF474AA375F9F1CAA190B1', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 13, NULL, NULL, 2, NULL, 0, 'Pop in the freezer for 1-2 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2091A6C8EFF0C4F80F88761825F207A', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 14, NULL, NULL, 2, NULL, 0, 'Break or cut enough dipping pretzels to create 36 small pieces, approximately 1/2 inch in length.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D6BCAEBA0A90D46A707326BAB2E70B8', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', X'D6BBDC8F08C5CB4FAEAF84EE99B43C04', 15, NULL, NULL, 2, NULL, 0, 'Dip one end of two pretzel pieces in melted green candy melts and attach on either side of the pretzel monster to create it''s bolts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50F114CA0EBD244388CD2975654EBFA0', X'0611B974B338A54D9D11BF5E096C6D0E', X'0611B974B338A54D9D11BF5E096C6D0E', 0, NULL, NULL, 2, NULL, 0, 'Divide the frosting into 3 bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC5254FDBED7CA429892E7B928ADA936', X'0611B974B338A54D9D11BF5E096C6D0E', X'0611B974B338A54D9D11BF5E096C6D0E', 1, NULL, NULL, 2, NULL, 0, 'Color one bowl of frosting orange.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'528C396DA291364992413B9FCAE9EDD3', X'0611B974B338A54D9D11BF5E096C6D0E', X'0611B974B338A54D9D11BF5E096C6D0E', 2, NULL, NULL, 2, NULL, 0, 'Color another bowl yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65DED48D2E191240BFD8B11B57C5FA48', X'0611B974B338A54D9D11BF5E096C6D0E', X'0611B974B338A54D9D11BF5E096C6D0E', 3, NULL, NULL, 2, NULL, 0, 'Leave the third bowl of frosting white.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D83A4E689CB64240B3239D1C7DFB7D97', X'0611B974B338A54D9D11BF5E096C6D0E', X'0611B974B338A54D9D11BF5E096C6D0E', 4, NULL, NULL, 2, NULL, 0, 'Spoon the orange into a disposable pastry bag fitted with one of the three Color Swirl 3-Color Coupler pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0D0506CBB0E5747AF51BAD188F020A2', X'0611B974B338A54D9D11BF5E096C6D0E', X'0611B974B338A54D9D11BF5E096C6D0E', 5, NULL, NULL, 2, NULL, 0, 'Repeat with the orange and white frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'230597575F8B3D4092DAA93A9CC50C46', X'0611B974B338A54D9D11BF5E096C6D0E', X'0611B974B338A54D9D11BF5E096C6D0E', 6, NULL, NULL, 2, NULL, 0, 'Fit the three interlocking coupler pieces together, add a large star pastry tip, screw on the ring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1607F78D7A0B945A138CE31EF5CFE89', X'0611B974B338A54D9D11BF5E096C6D0E', X'0611B974B338A54D9D11BF5E096C6D0E', 7, NULL, NULL, 2, NULL, 0, 'Tie the three bags together.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD33D79655B4904387D17B60C72AD606', X'0611B974B338A54D9D11BF5E096C6D0E', X'0611B974B338A54D9D11BF5E096C6D0E', 8, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of frosting on top of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDF6F0765FB8D940ADAAC6AD225DE8A1', X'0611B974B338A54D9D11BF5E096C6D0E', X'0611B974B338A54D9D11BF5E096C6D0E', 9, NULL, NULL, 2, NULL, 0, 'Fill a plastic wine glass half-way with candy corn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0F7221066F5AF4983171D5FED1B2180', X'0611B974B338A54D9D11BF5E096C6D0E', X'0611B974B338A54D9D11BF5E096C6D0E', 10, NULL, NULL, 2, NULL, 0, 'Just before serving, set a cupcake on top of the candy corn in each wine glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CBC91F2A6E4724C976CB109B39A5BBA', X'2358CAE06828C94AA460D63876526203', X'2358CAE06828C94AA460D63876526203', 0, NULL, NULL, 2, NULL, 0, 'Dip a small balloon in white chocolate so that you cover about 3/4 of the balloon.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84398C0C5CC2044987BD7029368AD576', X'2358CAE06828C94AA460D63876526203', X'2358CAE06828C94AA460D63876526203', 1, NULL, NULL, 2, NULL, 0, 'Remove balloon from the white chocolate and allow the excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9972469D8EE154880C04F360FFC2B30', X'2358CAE06828C94AA460D63876526203', X'2358CAE06828C94AA460D63876526203', 2, NULL, NULL, 2, NULL, 0, 'Set dipped balloon on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62CEB5D8E7398243842B67210177EFF6', X'2358CAE06828C94AA460D63876526203', X'2358CAE06828C94AA460D63876526203', 3, NULL, NULL, 2, NULL, 0, 'Repeat dipping a total of 6 balloons.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C842D95AEAC2AC43B844062CDEE21A78', X'2358CAE06828C94AA460D63876526203', X'2358CAE06828C94AA460D63876526203', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate for about 5 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B8CBCAF52471BA4AA5B84115A1558CFA', X'2358CAE06828C94AA460D63876526203', X'2358CAE06828C94AA460D63876526203', 5, NULL, NULL, 2, NULL, 0, 'Repeat, dipping 6 more balloons.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B1CB1E17B77904F958221AEBC495EE4', X'2358CAE06828C94AA460D63876526203', X'2358CAE06828C94AA460D63876526203', 6, NULL, NULL, 2, NULL, 0, 'Remove balloons from fridge and immediately cut a small hole in the top of the balloon and allow the balloon to gently deflate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61F20D63EFEC3B45B178D6E034A5B713', X'2358CAE06828C94AA460D63876526203', X'2358CAE06828C94AA460D63876526203', 7, NULL, NULL, 2, NULL, 0, 'Pull the balloon out of the chocolate bowl and discard.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3227A680A7DB94B9A152439E5B54D39', X'2358CAE06828C94AA460D63876526203', X'2358CAE06828C94AA460D63876526203', 8, NULL, NULL, 2, NULL, 0, 'Pour the black candy melts or dark chocolate into a small zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6DE6D6451E89E489DACB2104FAF6C07', X'2358CAE06828C94AA460D63876526203', X'2358CAE06828C94AA460D63876526203', 9, NULL, NULL, 2, NULL, 0, 'Snip off one tip from the bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9703499923CC754FAB30F7B90DC8CF51', X'2358CAE06828C94AA460D63876526203', X'2358CAE06828C94AA460D63876526203', 10, NULL, NULL, 2, NULL, 0, 'Pipe eyes, nostrils, and a mouth onto each bowl creating Jack''s face.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9FA757BD521F4844BDC787A215D0CE88', X'2358CAE06828C94AA460D63876526203', X'2358CAE06828C94AA460D63876526203', 11, NULL, NULL, 2, NULL, 0, 'Freeze for 2-3 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BA93BCADD16364FA5F74D5E773A990C', X'923BCE707E6A9E43966ADAC13D014BFE', X'923BCE707E6A9E43966ADAC13D014BFE', 0, NULL, NULL, 2, NULL, 0, 'Combine cream cheese and sugar in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69DA60A2DCF7CD47818B3BE6E0946C89', X'923BCE707E6A9E43966ADAC13D014BFE', X'923BCE707E6A9E43966ADAC13D014BFE', 1, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on medium-high speed until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BB04C77F900B44E980E2BAEC14AB3E7', X'923BCE707E6A9E43966ADAC13D014BFE', X'923BCE707E6A9E43966ADAC13D014BFE', 2, NULL, NULL, 2, NULL, 0, 'Add marshmallow fluff and vanilla bean paste or extract and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9CA7F055BEA2043B2FEBCEEF5F195FC', X'923BCE707E6A9E43966ADAC13D014BFE', X'923BCE707E6A9E43966ADAC13D014BFE', 3, NULL, NULL, 2, NULL, 0, 'Pour in whipped topping and beat until creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D9050D87A9F07449A5F0DF82736ACE0', X'923BCE707E6A9E43966ADAC13D014BFE', X'923BCE707E6A9E43966ADAC13D014BFE', 4, NULL, NULL, 2, NULL, 0, 'Spoon into a pastry bag fitted with a large star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A049C3313735746A1E5B4F01BE4E33B', X'923BCE707E6A9E43966ADAC13D014BFE', X'923BCE707E6A9E43966ADAC13D014BFE', 5, NULL, NULL, 2, NULL, 0, 'Pipe the cheesecake mousse into each chocolate bowl filling them just half way.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18950E91B20FF64D95CB0B1A181C5B17', X'923BCE707E6A9E43966ADAC13D014BFE', X'923BCE707E6A9E43966ADAC13D014BFE', 6, NULL, NULL, 2, NULL, 0, 'Break up an Oreo and crumble it in each chocolate bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AD1920039BC0C42B200A76C31FB9402', X'923BCE707E6A9E43966ADAC13D014BFE', X'923BCE707E6A9E43966ADAC13D014BFE', 7, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of cheesecake mousse over the cookies in each chocolate bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CF180DC2314B34285A4254FC6348B33', X'923BCE707E6A9E43966ADAC13D014BFE', X'923BCE707E6A9E43966ADAC13D014BFE', 8, NULL, NULL, 2, NULL, 0, 'Crush the remaining Oreo Cookies into fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F86A1E288A125F4C85D27A323A5D497F', X'923BCE707E6A9E43966ADAC13D014BFE', X'923BCE707E6A9E43966ADAC13D014BFE', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle the crumbs over top of the mousse filled bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9331ADC3714C04DBC4EB95378251E0F', X'923BCE707E6A9E43966ADAC13D014BFE', X'923BCE707E6A9E43966ADAC13D014BFE', 10, NULL, NULL, 2, NULL, 0, 'Serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C7B306277235E4EAD54B00DF350BBE5', X'1EDA3A572EBF9847B22450051BAAE105', X'1EDA3A572EBF9847B22450051BAAE105', 0, NULL, NULL, 2, NULL, 0, 'In a medium saucepan bring chicken broth, salt, and butter to a boil over high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0B5A769A92E874EA2ED655C6C475B5E', X'1EDA3A572EBF9847B22450051BAAE105', X'1EDA3A572EBF9847B22450051BAAE105', 1, NULL, NULL, 2, NULL, 0, 'When it comes to a boil, stir until the butter melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E692500B1D4954A96C73CCDF980DF55', X'1EDA3A572EBF9847B22450051BAAE105', X'1EDA3A572EBF9847B22450051BAAE105', 2, NULL, NULL, 2, NULL, 0, 'Remove from the heat and stir in the heavy whipping cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E8196AE2EAA8C43B9944C4AA668EE02', X'1EDA3A572EBF9847B22450051BAAE105', X'1EDA3A572EBF9847B22450051BAAE105', 3, NULL, NULL, 2, NULL, 0, 'Stir in the potato flakes until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B8E69395E190D4DA48159A9FF105975', X'1EDA3A572EBF9847B22450051BAAE105', X'1EDA3A572EBF9847B22450051BAAE105', 4, NULL, NULL, 2, NULL, 0, 'Place a slice of bread on a plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06FB52692F3C914EBCBA680D8236DFD7', X'1EDA3A572EBF9847B22450051BAAE105', X'1EDA3A572EBF9847B22450051BAAE105', 5, NULL, NULL, 2, NULL, 0, 'Set some slices of turkey on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C13E133515FEB41BAE609EB4C432D15', X'1EDA3A572EBF9847B22450051BAAE105', X'1EDA3A572EBF9847B22450051BAAE105', 6, NULL, NULL, 2, NULL, 0, 'Pour gravy over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F46A8D66AECE549BE1401CF1FB6527A', X'1EDA3A572EBF9847B22450051BAAE105', X'1EDA3A572EBF9847B22450051BAAE105', 7, NULL, NULL, 2, NULL, 0, 'Spray the face mask candy mold with cooking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'581DA2C94CDD964DBACCF0F8B74BA9B2', X'1EDA3A572EBF9847B22450051BAAE105', X'1EDA3A572EBF9847B22450051BAAE105', 8, NULL, NULL, 2, NULL, 0, 'Spoon hot mashed potatoes into the face mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB572A852F6DA644AFF095CE0399F60C', X'1EDA3A572EBF9847B22450051BAAE105', X'1EDA3A572EBF9847B22450051BAAE105', 9, NULL, NULL, 2, NULL, 0, 'Make sure the potatoes fill the mold completely, being sure to fill the nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C571391F07E0B040B8C418CBFF0C70C3', X'1EDA3A572EBF9847B22450051BAAE105', X'1EDA3A572EBF9847B22450051BAAE105', 10, NULL, NULL, 2, NULL, 0, 'Carefully turn the mold upside down right over the sandwich and the potato face will slide right out onto the turkey.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FBFF260917CDD74EA5F77FB295686A3A', X'1EDA3A572EBF9847B22450051BAAE105', X'1EDA3A572EBF9847B22450051BAAE105', 11, NULL, NULL, 2, NULL, 0, 'Spray the mold with more spray and make another sandwich.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55C31E6D46749A4C9DE086166E1355BF', X'71BD11FBABB1EF41884B766EE003D46C', X'71BD11FBABB1EF41884B766EE003D46C', 0, NULL, NULL, 2, NULL, 0, 'Pour white and pink candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4E5BC3FEE77494EAE996F42928CBDD3', X'71BD11FBABB1EF41884B766EE003D46C', X'71BD11FBABB1EF41884B766EE003D46C', 1, NULL, NULL, 2, NULL, 0, 'Heat for three 30 second increments on high power, stirring well after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E99B4A15B86C96488E75C950FC1A0CAB', X'71BD11FBABB1EF41884B766EE003D46C', X'71BD11FBABB1EF41884B766EE003D46C', 2, NULL, NULL, 2, NULL, 0, 'If needed heat for 10 second increments on high power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D55356318569284A83CA9599586BCA87', X'71BD11FBABB1EF41884B766EE003D46C', X'71BD11FBABB1EF41884B766EE003D46C', 3, NULL, NULL, 2, NULL, 0, 'Spoon 1 tablespoon of candy coating into each brain cavity reserving the remaining candy to top off the brains once filled.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C840A301260E334D894B2363D7997A19', X'71BD11FBABB1EF41884B766EE003D46C', X'71BD11FBABB1EF41884B766EE003D46C', 4, NULL, NULL, 2, NULL, 0, 'Brush the candy around the entire interior of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'950F16FA63BA7E4F994492736AF7F15F', X'71BD11FBABB1EF41884B766EE003D46C', X'71BD11FBABB1EF41884B766EE003D46C', 5, NULL, NULL, 2, NULL, 0, 'Freeze for 3-5 minutes just until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F7C57ED8D2500449D36876C5CB1FD08', X'D5D97DC00C9B874E93DE6D5DE6C1AA18', X'D5D97DC00C9B874E93DE6D5DE6C1AA18', 0, NULL, NULL, 2, NULL, 0, 'Stir the preserves until very runny, adding a drop or so of water if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEC3FBDB9512EF48B65CD3B73CBA80DD', X'D5D97DC00C9B874E93DE6D5DE6C1AA18', X'D5D97DC00C9B874E93DE6D5DE6C1AA18', 1, NULL, NULL, 2, NULL, 0, 'If needed, you can warm your jelly, just be sure it''s only slightly warm when you pour it into the brain.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32F64A4420056643A5897AE08929A4CC', X'D5D97DC00C9B874E93DE6D5DE6C1AA18', X'D5D97DC00C9B874E93DE6D5DE6C1AA18', 2, NULL, NULL, 2, NULL, 0, 'Spoon 1 tablespoon into each brain.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B02D8624655CE4DB88C94DC1A3F6C88', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 0, NULL, NULL, 2, NULL, 0, 'Combine the 4 ounces of white candy melts with the peanut butter chips in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9458B7CB27453D40A9E9C89632BBB75E', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for two 30 second increments, stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C6EBD930B04D0469F6F67029C7E74DE', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 2, NULL, NULL, 2, NULL, 0, 'Heat for 15 second increments on high power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B1C54FCA41F794D9A758633893B5109', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 3, NULL, NULL, 2, NULL, 0, 'Stir in the peanut butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1FE2A82A7DA7041B081281C067B5D59', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 4, NULL, NULL, 2, NULL, 0, 'Allow the fudge to cool for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80E61C147151484DB4686EBE635BD0C4', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 5, NULL, NULL, 2, NULL, 0, 'Pour a heaping tablespoonful into each brain on top of the jelly, filling to 1/8th of an inch from the edge of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCA0D9ACE1232749A44C5C18B58ECA50', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 6, NULL, NULL, 2, NULL, 0, 'Freeze the brains for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'875AC38317ED5A478E1FEE342B516D14', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 7, NULL, NULL, 2, NULL, 0, 'Remove and let sit for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'749E4FE241A2D649BDFCC95D6A024521', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 8, NULL, NULL, 2, NULL, 0, 'Re-heat the pinkish candy coating in the microwave for 10 second bursts at high power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5252274E2A0174AA42E4494902B84DE', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 9, NULL, NULL, 2, NULL, 0, 'Spoon about a teaspoon of the candy coating on top of each brain, over the peanut butter fudge layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4AD2A8E09CC644D9DE7748B36B519D8', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 10, NULL, NULL, 2, NULL, 0, 'Smooth it out by gently tapping the mold on the table.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF5DA96B08A608478B50D46B89C16688', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 11, NULL, NULL, 2, NULL, 0, 'Wipe off any candy coating on the top edge of the brain mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04CC94A44DD91A44A291E9ECAEAB85E0', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 12, NULL, NULL, 2, NULL, 0, 'Freeze for about 5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5095186C535A549B078435D42020848', X'72D41DC77E9C6945B3ECEEAB864CF954', X'72D41DC77E9C6945B3ECEEAB864CF954', 13, NULL, NULL, 2, NULL, 0, 'Remove from freezer and un-mold by peeling away the silicone mold from the brains.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5AA736FC0E89A04884C1A6F6EDCC321C', X'55C4E38A7371264C9266F67CC973251C', X'55C4E38A7371264C9266F67CC973251C', 0, NULL, NULL, 2, NULL, 0, 'Toss melted orange candy coating with popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D17A7244470C3409B90D627AEFE09D4', X'55C4E38A7371264C9266F67CC973251C', X'55C4E38A7371264C9266F67CC973251C', 1, NULL, NULL, 2, NULL, 0, 'Spread on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E020BF494A990D4FA226EABCA4B24771', X'55C4E38A7371264C9266F67CC973251C', X'55C4E38A7371264C9266F67CC973251C', 2, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A40A1960A151114D9BB3C82DD30F3459', X'55C4E38A7371264C9266F67CC973251C', X'55C4E38A7371264C9266F67CC973251C', 3, NULL, NULL, 2, NULL, 0, 'Break apart into small popcorn sized pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E289BD1A5953246A207B680189146CA', X'0E0B60564D17404EA3A8109F351AC574', X'0E0B60564D17404EA3A8109F351AC574', 0, NULL, NULL, 2, NULL, 0, 'Repeat above tossing popcorn in yellow candy coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D5CA225ADAA1E4ABCDC1A68A73B2795', X'85D7312F3868324E8776B161E6713858', X'85D7312F3868324E8776B161E6713858', 0, NULL, NULL, 2, NULL, 0, 'Do it again using white.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'574DDEC7667C974F8D4E51AE186020AB', X'06DD04C586246349864902526E1E2C49', X'06DD04C586246349864902526E1E2C49', 0, NULL, NULL, 2, NULL, 0, 'Equally divide the popcorn into two 18 inch pastry bags, layering the white then yellow then orange.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A5FF02BC7C0934BA17C9F48EA28F365', X'06DD04C586246349864902526E1E2C49', X'06DD04C586246349864902526E1E2C49', 1, NULL, NULL, 2, NULL, 0, 'Tie with ribbon so each bag looks like a candy corn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAFED1DF93A85546A81E6B5F36A00D04', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', 0, NULL, NULL, 2, NULL, 0, 'Spread Light Cocoa Candy Melts over the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E62538D7FF9E2A4283571687DDC0BE22', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', 1, NULL, NULL, 2, NULL, 0, 'Using a food use only paint brush press the brush into the wet candy melts and pull up quickly creating furry spikes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6889B9239240B4D9D80584922858D07', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', 2, NULL, NULL, 2, NULL, 0, 'Attach a mini Reese''s Cup snout and brush candy melts all around it making it furry too.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05F19304CEFFBC4AB75DA41CA90AEAF7', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', 3, NULL, NULL, 2, NULL, 0, 'Attach a black candy nose and yellow candy eyes using more candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD7A823C15C00F4A926136F9C7FA5D02', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', 4, NULL, NULL, 2, NULL, 0, 'Pop the Reese''s Cup into the freezer for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59DB49AF5F28A146A258DE20F16E2017', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', 5, NULL, NULL, 2, NULL, 0, 'Cut ears from two candy melt wafers and attach them to the Reese''s Cup and make them furry using more candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B12945474C3FC44197647E7AD17258FB', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', 6, NULL, NULL, 2, NULL, 0, 'Either pipe on white candy melt fangs or attach 4 small white heart sprinkles for fangs using more light cocoa candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A874B5A38F2D2F4C9E505363D2F91E92', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', 7, NULL, NULL, 2, NULL, 0, 'Pipe a candy moon with a pick attached onto a piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A3A1000716C9E4EB728A5FB315DA9DB', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', 8, NULL, NULL, 2, NULL, 0, 'Freeze candy moon for 3-5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62BA7E408DD3FA4491E63E0A9FF0D41B', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', X'8EFA8DFF19AF9A4B80E5159C66D3C33A', 9, NULL, NULL, 2, NULL, 0, 'Insert the pick into the frosting so the moon sits behind the werewolf.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BD699C5FA8652478BFEA8AD7BFF61D6', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 0, NULL, NULL, 2, NULL, 0, 'Pour caramel bits into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B8F4A8B29EE3E488F585AB5748178DB', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 1, NULL, NULL, 2, NULL, 0, 'Heat on medium power for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FA2E413AA3BD3419902093709DD83E8', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27182CDBB92BA0488D51F2D63BDDC302', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 3, NULL, NULL, 2, NULL, 0, 'Heat on medium power for 20 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BC0E248D4CBB242940129A406786A99', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 4, NULL, NULL, 2, NULL, 0, 'Let sit in microwave for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C04ECDD638C17545A9FA0010F7ABC96C', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 5, NULL, NULL, 2, NULL, 0, 'Remove and stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62EFEFE6D4F4754D8CB7FAFAAF6638EF', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 6, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77109ADBE4AF5042AA3014FA9958E7C4', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 7, NULL, NULL, 2, NULL, 0, 'Note: Don''t allow the caramel to get too hot because it will harden like hard candy. You just want to warm it until it''s spreadable.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71EBC136867AE14BB54F7D80A367FD22', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 8, NULL, NULL, 2, NULL, 0, 'Spread caramel over three quarters of a pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'533293100BED7A4AA5AE4D25965453C0', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 9, NULL, NULL, 2, NULL, 0, 'Immediately roll caramel coated pretzel in chopped nuts or toffee bits.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50DAD0C8B1CE9A46A1B66529EA7C29BF', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 10, NULL, NULL, 2, NULL, 0, 'Repeat, with 5 more pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6A7AE0F6A19A34A898FDD65FA69F669', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 11, NULL, NULL, 2, NULL, 0, 'Freeze pretzels for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6AF59163503F374E84A66BE8FA983ECB', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 12, NULL, NULL, 2, NULL, 0, 'Spoon candy melts over caramel coated pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0AED19C8B2842543AF0DCFCDEF22DB47', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 13, NULL, NULL, 2, NULL, 0, 'Immediately decorate using candy eyes, candies and sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA34775286B49B4D85729332E3FA3BDC', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 14, NULL, NULL, 2, NULL, 0, 'Freeze for 3 minutes until candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFF59C93A5160C4792F25755880BC3FA', X'E5D15B609208564A8C5DC0FB7719B8BB', X'E5D15B609208564A8C5DC0FB7719B8BB', 15, NULL, NULL, 2, NULL, 0, 'Allow pretzels to sit at room temperature for a while before adding details with food coloring markers and then packaging.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'408CAC874E3FA243869EDA4CCAC01812', X'BD82F92BDB69454DB55F78520A696A1C', X'BD82F92BDB69454DB55F78520A696A1C', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'533974C62A21354D9772A79E83E79767', X'BD82F92BDB69454DB55F78520A696A1C', X'BD82F92BDB69454DB55F78520A696A1C', 1, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3E6F471C7DE6145BB0BF4DC2C9DA6AB', X'BD82F92BDB69454DB55F78520A696A1C', X'BD82F92BDB69454DB55F78520A696A1C', 2, NULL, NULL, 2, NULL, 0, 'Stir in egg, vanilla, almond extract, and some green food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61973DF8C675E24D971EC808E20A5D8C', X'BD82F92BDB69454DB55F78520A696A1C', X'BD82F92BDB69454DB55F78520A696A1C', 3, NULL, NULL, 2, NULL, 0, 'Add more coloring until you get a really bright shade of green and mix until well combined. The color will get lighter when you add the dry ingredients.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A84B96141FD1AA47B7C1ABDEC17C36BC', X'BD82F92BDB69454DB55F78520A696A1C', X'BD82F92BDB69454DB55F78520A696A1C', 4, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until combined. If the dough is sticky, add a small amount of flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDF7198DC516004D964D7B9F461DAD52', X'BD82F92BDB69454DB55F78520A696A1C', X'BD82F92BDB69454DB55F78520A696A1C', 5, NULL, NULL, 2, NULL, 0, 'Flatten into a disc and refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CB01FD74BFA4B43826F545C7856E919', X'494C333E0D614648A512B96D5C8A82C4', X'494C333E0D614648A512B96D5C8A82C4', 0, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine butter and sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BCD022BFC01E74D92F9695470966246', X'494C333E0D614648A512B96D5C8A82C4', X'494C333E0D614648A512B96D5C8A82C4', 1, NULL, NULL, 2, NULL, 0, 'Cream until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35A1B30FDEB7A047BEE37FAAEC297438', X'494C333E0D614648A512B96D5C8A82C4', X'494C333E0D614648A512B96D5C8A82C4', 2, NULL, NULL, 2, NULL, 0, 'Mix in eggs and vanilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5EF65283A76EE47893E8D318FA533CB', X'494C333E0D614648A512B96D5C8A82C4', X'494C333E0D614648A512B96D5C8A82C4', 3, NULL, NULL, 2, NULL, 0, 'Combine cocoa powder, baking powder, salt, and flour in a small mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F818AA4BC59FAA4DB367E7240A5AB654', X'494C333E0D614648A512B96D5C8A82C4', X'494C333E0D614648A512B96D5C8A82C4', 4, NULL, NULL, 2, NULL, 0, 'Stir in to wet ingredients and mix just until incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F57A5A16F51A043AA7E68FFDFB728E3', X'494C333E0D614648A512B96D5C8A82C4', X'494C333E0D614648A512B96D5C8A82C4', 5, NULL, NULL, 2, NULL, 0, 'Flatten dough into a disc and place in a zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'086F8FF4391EFC4B98D258AB85C57143', X'494C333E0D614648A512B96D5C8A82C4', X'494C333E0D614648A512B96D5C8A82C4', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A888A90D52FC842986944068B4745EB', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B660C803814D6D43BF3C5140DDBAC799', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 1, NULL, NULL, 2, NULL, 0, 'Roll out green dough to about 1/8th inch thickness and cut out twenty two 2 1/4 inch rounds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF629B8AB059A2499064F20C73CD821C', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 2, NULL, NULL, 2, NULL, 0, 'Set those rounds on a baking sheet and refrigerate for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BDB7E6172DEEF548AEF4484A7ABFA8B9', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 3, NULL, NULL, 2, NULL, 0, 'Roll the remaining dough out to about 1/16th inch thickness and cut out twenty two 2 1/2 inch rounds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF378AE98D00094187041787C89CF12B', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 4, NULL, NULL, 2, NULL, 0, 'Drape them over a cake pops pan then smooth them out into a half sphere.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'799D14EE446D8543B5C406EE5326AEC5', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 5, NULL, NULL, 2, NULL, 0, 'Roll out chocolate cookie dough to 1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F6298C351EF714B841D0B9805C16416', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 6, NULL, NULL, 2, NULL, 0, 'Cut out twenty two witch hats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95072E7000573C468EE6E6BEFBC9D4CE', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 7, NULL, NULL, 2, NULL, 0, 'Cut a curved section out of the bottom of each witch hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C51511244BF8A488334A84E49CCDBC6', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 8, NULL, NULL, 2, NULL, 0, 'Brush the edge of that cut with a bit of water and set it up against a green round cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9E74F0471F1BE4B891A024C03AF4FF4', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate the cut out cookies for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'972F1FECE5E1CB4185C495280998051E', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 10, NULL, NULL, 2, NULL, 0, 'Bake the witch cookies for 10-12 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6EAA735048EEA2419719E6F6C47E6BBC', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 11, NULL, NULL, 2, NULL, 0, 'Bake the half sphere cookies for about 8 minutes until the edges look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3CBCA7C0D4E9743B5C49AF94A2D301D', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 12, NULL, NULL, 2, NULL, 0, 'Allow cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3762B30683F22441BAE4594BB58D2D00', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 13, NULL, NULL, 2, NULL, 0, 'Fill half sphere cookies with candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4B4F98817D9B34C851F3921C73C414E', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 14, NULL, NULL, 2, NULL, 0, 'Pipe melted candy melts or royal icing on edge of candy filled cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'078E5EC42870ED4BA32749F2862ADB35', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 15, NULL, NULL, 2, NULL, 0, 'Press a witch cookie on top, lining up the circle with the half sphere.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'582DCA8177DA7947B541CB644C7955D9', X'17B765E1C1B5BE44B70725953E31D62E', X'17B765E1C1B5BE44B70725953E31D62E', 16, NULL, NULL, 2, NULL, 0, 'Decorate using candy clay, candy eyes, candies, and food coloring marker according to step-by-step tutorial instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8663626F57BF584B93C533F5B395B0A9', X'3B3F73AC87F54141BFEE564EEAFA4ACC', X'3B3F73AC87F54141BFEE564EEAFA4ACC', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DD0600D0E966C48B6449AA1FE46A079', X'3B3F73AC87F54141BFEE564EEAFA4ACC', X'3B3F73AC87F54141BFEE564EEAFA4ACC', 1, NULL, NULL, 2, NULL, 0, 'Brush 8 skull cavities in 2 skull pans with butter or spray with non-stick spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'363496B172D18A4B910EB38BFBB470E5', X'3B3F73AC87F54141BFEE564EEAFA4ACC', X'3B3F73AC87F54141BFEE564EEAFA4ACC', 2, NULL, NULL, 2, NULL, 0, 'Combine Cheesy Broccoli Chicken Oven Sauce with broccoli, chicken, and cheese. Stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'703E5EE00FDA8F47AADFAD88261876D0', X'3B3F73AC87F54141BFEE564EEAFA4ACC', X'3B3F73AC87F54141BFEE564EEAFA4ACC', 3, NULL, NULL, 2, NULL, 0, 'Unroll tubes of pizza dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'111C7EC255F48B459E90B35CF0E80A10', X'3B3F73AC87F54141BFEE564EEAFA4ACC', X'3B3F73AC87F54141BFEE564EEAFA4ACC', 4, NULL, NULL, 2, NULL, 0, 'Cut dough in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3F87DCC32D4B440B5BFE1F4BE2B9282', X'3B3F73AC87F54141BFEE564EEAFA4ACC', X'3B3F73AC87F54141BFEE564EEAFA4ACC', 5, NULL, NULL, 2, NULL, 0, 'Lay 1 piece of dough over a skull cavity in a pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'681FB762F3A2CA46AA97D04CA9465566', X'3B3F73AC87F54141BFEE564EEAFA4ACC', X'3B3F73AC87F54141BFEE564EEAFA4ACC', 6, NULL, NULL, 2, NULL, 0, 'Continue filling 8 skulls with dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A229C3DCA111FA40BE019B1D0FE72063', X'3B3F73AC87F54141BFEE564EEAFA4ACC', X'3B3F73AC87F54141BFEE564EEAFA4ACC', 7, NULL, NULL, 2, NULL, 0, 'Equally divide the filling among the 8 skulls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E471268F2F6514459737B4FFBC0A5564', X'3B3F73AC87F54141BFEE564EEAFA4ACC', X'3B3F73AC87F54141BFEE564EEAFA4ACC', 8, NULL, NULL, 2, NULL, 0, 'Fold dough over top of filling, cutting off excess, pinching together the seams.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91C0F6B47174EE44814F4DDC73F9CEE0', X'3B3F73AC87F54141BFEE564EEAFA4ACC', X'3B3F73AC87F54141BFEE564EEAFA4ACC', 9, NULL, NULL, 2, NULL, 0, 'Brush egg wash over tops of dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2D51F01699040438995CA678FF7EB79', X'3B3F73AC87F54141BFEE564EEAFA4ACC', X'3B3F73AC87F54141BFEE564EEAFA4ACC', 10, NULL, NULL, 2, NULL, 0, 'Bake for 24-28 minutes until the top dough is baked through.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49F292CD57CEAD49B89C559204CD1C3C', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 0, NULL, NULL, 2, NULL, 0, 'Combine the white chips with the peanut butter chips and melt them in the microwave on high for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E77BE9C17F8E4F4DAA3397803A52DFD8', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 1, NULL, NULL, 2, NULL, 0, 'Then stir in a pinch of salt and the creamy peanut butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'730A8986A9CA4046835DD09F1FEED52F', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 2, NULL, NULL, 2, NULL, 0, 'Pour the liquid fudge into a disposable pastry bag or a squeeze bottle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'442142DE3AD28843BFB442494BBD78AF', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 3, NULL, NULL, 2, NULL, 0, 'Squeeze the fudge into the cavities of a cake pops pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'184B4D9C6D581F4CA3E143503AF7E93D', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 4, NULL, NULL, 2, NULL, 0, 'Insert a lollipop stick into each.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD54FF57E8E16A4A875BD4C9EA04EB9B', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 5, NULL, NULL, 2, NULL, 0, 'Freeze for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E15898EB899CC429C421924AA6FE114', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 6, NULL, NULL, 2, NULL, 0, 'Remove and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'893927118C30CE49987E03C98071D4C1', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 7, NULL, NULL, 2, NULL, 0, 'Allow the pops to sit at room temperature for at least 45 minutes to come to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A698C5956A5A2B4A8927F63E160B560B', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 8, NULL, NULL, 2, NULL, 0, 'Dip one pop into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'325970E15BF99347A07DABC3C11D384A', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 9, NULL, NULL, 2, NULL, 0, 'Allow to set for 30-90 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F66F9F72B2906141BD95F554C6FE37FB', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 10, NULL, NULL, 2, NULL, 0, 'Press a fork over the surface of the pop, pulling away quickly, creating spikes of chocolate fur.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62D69158544F5541BC05DB5851C9A2A0', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 11, NULL, NULL, 2, NULL, 0, 'Pipe small triangles of chocolate onto a parchment lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB8F09BBA596ED45B4B691007CE86195', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 12, NULL, NULL, 2, NULL, 0, 'Remove once set, and attach two to each pop using a dab of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EFDCD4B54A57E459B0EAB0A650A01F5', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 13, NULL, NULL, 2, NULL, 0, 'Add more chocolate over each triangle, then use a fork to create furry spikes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0FF2DBFD6D50904CAA786AE13FCD2DB9', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 14, NULL, NULL, 2, NULL, 0, 'Allow pops to harden then decorate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'001F03F48FF0DD4AB6DEF22350C321C3', X'004DC04185002A46AEC91E1D9B0C3578', X'004DC04185002A46AEC91E1D9B0C3578', 15, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes, whiskers, and a pink candy heart nose to each pop using chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D94B7E18F91F244A418498F343EFD08', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E16DE201CF60EE4581288B45CABA8807', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 1, NULL, NULL, 2, NULL, 0, 'Combine flour, baking powder, sugar and salt in the bowl of a food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97402E90F05ABA42A49096824D2BCCF9', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 2, NULL, NULL, 2, NULL, 0, 'Pulse a few times to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AAC0D578CF34F4A9AA5BC5D5B0C87C5', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 3, NULL, NULL, 2, NULL, 0, 'Add butter and pulse until the butter breaks up into pea size pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'993F96F32565B8478612BF89FDAADCC7', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 4, NULL, NULL, 2, NULL, 0, 'Add the oil and pulse until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'405F0D60B5A75F418FD81F2E7FA3CD41', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 5, NULL, NULL, 2, NULL, 0, 'Turn on the food processor and slowly pour the water in through the feed tube.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52DF10FC6102494E8BB551C75AAD4BEC', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 6, NULL, NULL, 2, NULL, 0, 'Add just enough water for the dough to form a ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1809F0E31AF13D4BB0103AF3098C67C4', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 7, NULL, NULL, 2, NULL, 0, 'Remove dough and cut it in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03A074238677C94DBBDC18C46FF21E1D', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 8, NULL, NULL, 2, NULL, 0, 'Roll out on a silicone mat until the dough is as thin as you can get it.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2AF5C4F09E5D243B740B520C594CAE7', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 9, NULL, NULL, 2, NULL, 0, 'Cut the dough using Halloween plunger cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D9DEB24B391C14DB8FFF9A4133B305C', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 10, NULL, NULL, 2, NULL, 0, 'Remove the excess dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEDC1531A6F70E45BE517C8429A1FEC6', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 11, NULL, NULL, 2, NULL, 0, 'Set the silicone mat on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8EAF63157C5E6489C9F90E4729A188D', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 12, NULL, NULL, 2, NULL, 0, 'Poke holes in crackers using a toothpick.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E8F14A6A8652D479B54DFEA40A5CE8B', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 13, NULL, NULL, 2, NULL, 0, 'Bake crackers for 10-12 minutes until golden brown and crispy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D02F7BB8C738C46AEBAB88468C85CD1', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 14, NULL, NULL, 2, NULL, 0, 'Remove pan from oven and immediately brush crackers with melted butter and sprinkle with some salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D83D3CE119DBCC4499DD5078CA55D6E3', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 15, NULL, NULL, 2, NULL, 0, 'Repeat using the other half of the dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FD4A30441E2AD46A02116ECEE46877A', X'77E9441B48B5604497E5DC9D35EC4E5D', X'77E9441B48B5604497E5DC9D35EC4E5D', 16, NULL, NULL, 2, NULL, 0, 'If you want you can re-roll the excess dough and cut more crackers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61C40BCE51DD7A47ADA23C8139AFAF52', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2F9312DAE75E746934072F4F2417B79', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 1, NULL, NULL, 2, NULL, 0, 'Line two baking sheets with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42A13EFB7D57384FB6F67D519FED7A50', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 2, NULL, NULL, 2, NULL, 0, 'Unroll one tube of pizza dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB1C7BD45473C647B13791E3F1C4E7A5', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 3, NULL, NULL, 2, NULL, 0, 'Cut out 3 triangles, 1 toothy grin, and 1 stem.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2B5A527765C6843B44D1EEA030A40E7', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 4, NULL, NULL, 2, NULL, 0, 'Set on parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67431964E707F74D877A71BF282731F9', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 5, NULL, NULL, 2, NULL, 0, 'Brush black food coloring over the triangles and grin.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5BDFC4001DE5A4D871332DC77605731', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 6, NULL, NULL, 2, NULL, 0, 'Whisk together the egg and water to create egg wash.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A41D2BF9B7D1644285CC32D56BCCC02D', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 7, NULL, NULL, 2, NULL, 0, 'Brush egg wash over the stem.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66E15AB025777D4090D9E423ED06A4CC', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 8, NULL, NULL, 2, NULL, 0, 'Bake for 12-16 minutes until the stem is golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B5C2AEF54D2594386D1BAA2ECAAEFFD', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 9, NULL, NULL, 2, NULL, 0, 'Cut block of cheese into 15 pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FBF349BA33FF664BA96571AA7B4A31C1', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 10, NULL, NULL, 2, NULL, 0, 'Cut 3 rectangles from the remaining dough and unroll the other tube of dough and cut it into 12 rectangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19077C7677FA5044B91694D79F194303', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 11, NULL, NULL, 2, NULL, 0, 'Brush egg wash over the pieces of pizza dough. Set one piece of the cheese in the center of each dough rectangle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BE681454AA9304C9E1DB5B31E71F8FF', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 12, NULL, NULL, 2, NULL, 0, 'Attach opposing corners of the dough, then pinch around the edges to seal the dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE65D4946DB02F48A46E774A5E502820', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 13, NULL, NULL, 2, NULL, 0, 'Pinch and roll into a ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F3F1C6F5B0B2241B98224F8F579E6BD', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 14, NULL, NULL, 2, NULL, 0, 'Set balls of dough around the edge of the buttered skillet, creating one row of 15.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'581820A10BAE3E4CBD6BF0F4666CA87F', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 15, NULL, NULL, 2, NULL, 0, 'Mix together the cream cheese, buffalo chicken sauce, ranch dip, sour cream, 2/3 cup cheddar cheese, Monterrey Jack cheese, and shredded chicken.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'941BFC54F7A0FD4784B02C02BAF1B1F4', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 16, NULL, NULL, 2, NULL, 0, 'Spoon into the center of the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06B514E74EAA7443AF92814849E6082D', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 17, NULL, NULL, 2, NULL, 0, 'Bake for 32 minutes then remove from oven and sprinkle the remaining 1/2 cup cheddar cheese over top. Add the Jack-O-Lantern eyes, nose, grin, and stem.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3131714A0FEEC843BF7A5DDED8A5F5F3', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 18, NULL, NULL, 2, NULL, 0, 'Heat for 3-5 minutes until the cheese melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEFC973695A2104CB599EF90720E4E9A', X'6B0EF71DAD22334F837E31CCE77429BF', X'6B0EF71DAD22334F837E31CCE77429BF', 19, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EC393E271A92143A1DFA01050516584', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 0, NULL, NULL, 2, NULL, 0, 'Open the box of caramel dip and remove the plastic covering.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16FC5DB24A69414EA3EEBDEEC065371C', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 1, NULL, NULL, 2, NULL, 0, 'Heat in the microwave on high power for 45 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7365A774303159409748BFB42DBD8C8F', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 2, NULL, NULL, 2, NULL, 0, 'Carefully stir in the heavy whipping cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B044C10449D6946BA1931E4A71D83C4', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 3, NULL, NULL, 2, NULL, 0, 'Allow to cool until it''s cool to the touch, stirring often.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BCD4FED2863F14F99625C9147795136', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 4, NULL, NULL, 2, NULL, 0, 'Pour the candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDE97F11248B614AA3FC2210AAA916E1', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 5, NULL, NULL, 2, NULL, 0, 'Heat for 45 seconds, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0E19EB68305B74D805A3BAF589B6236', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 6, NULL, NULL, 2, NULL, 0, 'Heat for 30 seconds, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56015A5B6E39A641A143F38941C9A9CB', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 7, NULL, NULL, 2, NULL, 0, 'Heat for 15 seconds, then let sit in microwave for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1769A78F8B218E46B8E203348C43608D', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 8, NULL, NULL, 2, NULL, 0, 'Remove and stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'604F636C35D5204F9EA59D343105BD48', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 9, NULL, NULL, 2, NULL, 0, 'If you need to, heat for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB12D5F5D5316246ADACE21F134BBD82', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 10, NULL, NULL, 2, NULL, 0, 'Allow the candy melts to cool to 91 degrees Fahrenheit, stirring the bowl often to make sure the candy around the edge of the bowl does not harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B41B6A2319F70A4093325DBB86ECAEA0', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 11, NULL, NULL, 2, NULL, 0, 'You need to keep the candy fluid.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3988B74EE74E744E8B82C97BBBB5033F', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 12, NULL, NULL, 2, NULL, 0, 'Once it reaches 91 degrees, pour in the caramel, and stir slowly until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'404B681BAC988E4F963F276A958831E2', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 13, NULL, NULL, 2, NULL, 0, 'Let it sit in the bowl for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C4C9B4B6DF6CB4BB1064FABC188212B', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 14, NULL, NULL, 2, NULL, 0, 'Then, pour it out onto a cool surface, like marble, granite, or the back of a cookie sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8CFB2B42A0F1342A3F39A163BEC638D', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 15, NULL, NULL, 2, NULL, 0, 'Knead until smooth and pliable.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4415B4CE84DD2143A08E6F14359443D5', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 16, NULL, NULL, 2, NULL, 0, 'Press into silicone spider molds or candy molds to create fun Halloween treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'913C7414D5E5EC4CB9693CA5B84736CF', X'94CC90A62E339C45BD4A1CD00623E348', X'94CC90A62E339C45BD4A1CD00623E348', 17, NULL, NULL, 2, NULL, 0, 'Freeze for 10-15 minutes, until very firm, then remove and press candy out of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91B808BA2AB6284DA0A5A3781AAF2FAE', X'AA9B5655A6C9C347A188D37A31290022', X'AA9B5655A6C9C347A188D37A31290022', 0, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine butter and sugar.');
