INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B261B9218988E4DA47BE9598CF5329C', X'97196E1E7358B4479F865996913F7572', X'97196E1E7358B4479F865996913F7572', 14, NULL, NULL, 2, NULL, 0, 'If using, attach two candy eyes to each candy corn truffle using a dab of candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE564C07DE447A498B2196BF2624C942', X'97196E1E7358B4479F865996913F7572', X'97196E1E7358B4479F865996913F7572', 15, NULL, NULL, 2, NULL, 0, 'Draw on a nose and mouth using a black candy melts writer.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00C37D271E205346BD192C025A91A258', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEA94571A1ADC346B879DCB694D8628C', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 1, NULL, NULL, 2, NULL, 0, 'Spray 2 silicone brownie pops mold with non-stick spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B266A72B090AE84ABB1319D6A21E378A', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 2, NULL, NULL, 2, NULL, 0, 'Make brownie mix according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D86F90AF85CC7478F831B7DECCA2BA0', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 3, NULL, NULL, 2, NULL, 0, 'Pour batter into mold, filling just to the fill line.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B117B5151316C74793407CC89486700A', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 4, NULL, NULL, 2, NULL, 0, 'If you have extra batter, fill a few cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5869291D7466C846B16F24336411BC6C', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 5, NULL, NULL, 2, NULL, 0, 'Bake brownies for 26-30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B8B763165460BB4BADC74A43F552FDA1', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 6, NULL, NULL, 2, NULL, 0, 'Remove and allow to cool for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D949AACFA171AF498B70850672A59773', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 7, NULL, NULL, 2, NULL, 0, 'Cut the domed tops off the brownies then allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED8D0F00402AED4DBAF47028511F6876', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 8, NULL, NULL, 2, NULL, 0, 'Remove brownies from mold and set on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35AFDBF040FD534495876DFCBD4B1A0A', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 9, NULL, NULL, 2, NULL, 0, 'Make chocolate ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0D5CE98CCCA8E4A947A03C0DBBED17B', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 10, NULL, NULL, 2, NULL, 0, 'Heat heavy whipping cream in a saucepan set over medium heat, just until bubbles form around the edge of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C047E7E3C3B1B41B2ADFFD8148312F1', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 11, NULL, NULL, 2, NULL, 0, 'Pour over chopped chocolate and allow to sit for about 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CAE387678927D4C99CB5B5A393E5B89', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 12, NULL, NULL, 2, NULL, 0, 'Stir until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'951A0EAC941004498D6FD358E222C7B9', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 13, NULL, NULL, 2, NULL, 0, 'Pour over each of the brownies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D24A6F7BBCB30440A1F71ED6E5C99B4B', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 14, NULL, NULL, 2, NULL, 0, 'Roll out modeling chocolate and cut into 4 inch circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60908B3CC2175A47972CB14F087314FB', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 15, NULL, NULL, 2, NULL, 0, 'Cut eyes, mouth and lots of other holes to create ghost costumes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'302A3D623C2D9C439BFF3B0688DE5B27', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 16, NULL, NULL, 2, NULL, 0, 'Drape over brownies, pleating as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6445451275AFE04FBA7FCE3EFB8A9A07', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 17, NULL, NULL, 2, NULL, 0, 'If using, insert two marshmallow hands in holes in the modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E67B5E5FD364344B1C2539FFB3654C3', X'2A34F357FC82B240A17D01A6182FCAB8', X'2A34F357FC82B240A17D01A6182FCAB8', 18, NULL, NULL, 2, NULL, 0, 'Attach a tiny chocolate rock using some melted white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE92E01E59A8B04CB0CFB338C604A85A', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 0, NULL, NULL, 2, NULL, 0, 'Pour 1.8 ounces (9 cups) popped popcorn into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABF21CA11E48C14FA8F557C99BF72963', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 1, NULL, NULL, 2, NULL, 0, 'Pour 20 ounces orange-colored white chocolate or candy melts over popcorn');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CC57EDF2C01D942A6D33EFAB7490857', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 2, NULL, NULL, 2, NULL, 0, 'Toss to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D30CDD07E31544D8365AC4365CEE76E', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 3, NULL, NULL, 2, NULL, 0, 'Immediately pour the popcorn into the plastic bowl (mold) and start pressing it into the bottom and up the sides of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26F5B2275920FD4BBC410635A1C0C388', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 4, NULL, NULL, 2, NULL, 0, 'Repeat, making a 2nd batch of coated popcorn, continuing up the sides of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F24D51C0017C44287D18B23DC2A442B', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 5, NULL, NULL, 2, NULL, 0, 'You want the sides of the popcorn bowl to be about 1 1/2 inches thick.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DE5C8EA86486049A28C076B6876DBC9', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 6, NULL, NULL, 2, NULL, 0, 'Pack it in tightly, filling in any big holes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59029D8E9CA34B44ABE657EBC1E721B6', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 7, NULL, NULL, 2, NULL, 0, 'Pop the bowl in the freezer for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F70865EDFC5E141951BB020AD224CFF', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 8, NULL, NULL, 2, NULL, 0, 'Remove from the freezer, carefully turn the bowl upside down, and pull the popcorn bowl out.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9633693E1C7A74AACC6C75929A17F19', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 9, NULL, NULL, 2, NULL, 0, 'It should pop out easily so be careful to hold onto the popcorn so it doesn''t just fall out.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B9A9701A4503C46AA4B9F96D99E78C8', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 10, NULL, NULL, 2, NULL, 0, 'Roll out your dark modeling chocolate or black fondant and cut out a jack-o-lantern face using metal cutters or a knife. Attach them to the pumpkin popcorn bowl using some melted chocolate or candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FFCD6766F1AC5469A70454F01C65F59', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 11, NULL, NULL, 2, NULL, 0, 'To make your Halloween Popcorn, break up the Halloween Oreo Cookies into small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BB1761EDDB9BF48B09F6744D8435F2A', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 12, NULL, NULL, 2, NULL, 0, 'Pour 1.8 ounces of popped popcorn into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DA981483500244EB1507BDEDB78363A', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 13, NULL, NULL, 2, NULL, 0, 'Pour on 16 ounces melted and tempered white chocolate or melted white candy melts and add the Oreos, Reese''s Pieces, Toffee Bits, and Caramel Bits.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B8220A1212E9142A7B50D802309F599', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 14, NULL, NULL, 2, NULL, 0, 'Toss together to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A02EC45B8228C74E94A0A8464015B783', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 15, NULL, NULL, 2, NULL, 0, 'Spread 1/2 of the popcorn onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91F2E901C89A384AADE5F47CBD9C8535', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 16, NULL, NULL, 2, NULL, 0, 'Immediately press some pumpkin shaped pretzels on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A5EE08A090BA94ABD40CCC9687BBDF6', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 17, NULL, NULL, 2, NULL, 0, 'Spread the remaining popcorn onto another baking sheet and add more pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7A45EB8D233F74AA7820254E9460D1E', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 18, NULL, NULL, 2, NULL, 0, 'Drizzle orange and dark chocolate over top of the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'965D45DA2EE39C4DBFCF076910AC1062', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 19, NULL, NULL, 2, NULL, 0, 'Refrigerate or freeze for 10-15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CB502A777A9A640B4C380C5CB5154AF', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 20, NULL, NULL, 2, NULL, 0, 'Remove and allow it to come to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27C33F4B31C8994CAD31F66C49F3CDB3', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 21, NULL, NULL, 2, NULL, 0, 'Break apart into bite size pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B05B131A2E0BB640B1BD92167023AF73', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 22, NULL, NULL, 2, NULL, 0, 'Fill the Pumpkin Popcorn Bowl with the Halloween Popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2307B4F13D3CF4080D0E0BF55C39E2C', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 23, NULL, NULL, 2, NULL, 0, 'I stored my popcorn bowl in a 5 gallon popcorn tin. I set the bowl on the inside of the lid, and put the base over top. This will keep my white chocolate popcorn fresh for over a month.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'362BAC32A65C8A49843C2147EEB5B1A7', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 24, NULL, NULL, 2, NULL, 0, 'If you don''t have an airtight container that is large enough for the popcorn bowl to fit inside, then wrap it in plastic wrap and keep it on your counter for a few days.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CF9595DF2CADA40AD5A798DC1DA1B2F', X'65DB87630A15F74EB2D0F7204F2295D3', X'65DB87630A15F74EB2D0F7204F2295D3', 25, NULL, NULL, 2, NULL, 0, 'It will last about a week, but will be freshest if eaten within three days.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D69CD14FCCD1E40BA9F7E077E49299D', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 0, NULL, NULL, 2, NULL, 0, 'Select 16 large plump and juicy raisins for the spider bodies and 16 very small raisins for the spider heads.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58F1EFDECD652740B7DFA0663BC2986C', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 1, NULL, NULL, 2, NULL, 0, 'Flatten the remaining raisins as thin as you can.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D7EDD9CEF79DA4AACBB437C178A0FA0', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 2, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, cut thin long strips out of the raisins for the spider legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73013AD9B07AED4AAF792CA768A976BF', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 3, NULL, NULL, 2, NULL, 0, 'Create enough legs for all 16 of your spiders.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'506C6BD92E42E44F9727FDE2E7679202', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350F.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD7192E5F47BF04AA12256E92C7251C2', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 5, NULL, NULL, 2, NULL, 0, 'Beat butter, brown sugar, and granulated sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1FD91E311E1FC41954844D03C17F85C', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 6, NULL, NULL, 2, NULL, 0, 'Add one egg at a time mixing to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA3599F10D0F904BB44EFE82BA995488', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 7, NULL, NULL, 2, NULL, 0, 'Add vanilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8ED28CF7EBCF6449FDD792EFB54BD5C', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 8, NULL, NULL, 2, NULL, 0, 'Whisk together the flour, nutmeg, cinnamon, salt, and baking powder.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1B786EA24E1D348A135BE4D53F8CAD0', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 9, NULL, NULL, 2, NULL, 0, 'Add to the wet ingredients and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18865947026BEC4594086E204D513E86', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 10, NULL, NULL, 2, NULL, 0, 'Stir in the oats and 1 1/2 cups of raisins.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E46F5C1D2F2A5459C9ACCCB50BA410E', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 11, NULL, NULL, 2, NULL, 0, 'Scoop out two thirds of a cup of cookie dough, roll into a ball, and place on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'369E580BF116424DA7DE584DC88A12F5', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 12, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 16 cookies. Flatten the cookies slightly. These cookies are really big, which helps keep them moist and chewy on the inside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D22AEFA76BC564A857987AFA12D5B9B', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 13, NULL, NULL, 2, NULL, 0, 'Arrange your raisin spiders on top. Start by placing the eight legs on the cookie then put the large raisin body over top and add a head.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F0B777C4C79034382190583144C20CC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 14, NULL, NULL, 2, NULL, 0, 'Bake for 18 to 22 min. until the edges turn golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E6F5D4C05DFBD42B1D3906FC2703B39', X'62AB9A3C71D49B45B179EB6E0BFDADDC', X'62AB9A3C71D49B45B179EB6E0BFDADDC', 15, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool and then go have fun creeping out your friends and family.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4ADFD653899E004B894C9CEA99CF1328', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BD12F89FD5C4E4788C94F506FC6DC25', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 1, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2DD7A99A9EDF6469FACDCB35413BA8D', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 2, NULL, NULL, 2, NULL, 0, 'Stir in eggs, vanilla, and almond extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53B34EC09B75BB44A4554164D56CFF86', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 3, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until it''s mixed in.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63792A09E7BCFF4585734A4B90FBA59B', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 4, NULL, NULL, 2, NULL, 0, 'Roll cookie dough out to 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE027A9402C5464D8D59334D91AEDC37', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 5, NULL, NULL, 2, NULL, 0, 'Cut out 24 circles using a 3 inch cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DBFE15E5A7B8343A41F401CD61A73C9', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 6, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet and refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DDC1F19C8CA774CB2C6A1706A1CA010', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 7, NULL, NULL, 2, NULL, 0, 'Cut out 24 more round cookies, then using a 2 1/4 inch cutter, cut a circle from the center of each of those cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A3D631DEEFCBA48896318A5C47796E6', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 8, NULL, NULL, 2, NULL, 0, 'Re-rolling dough as needed, do this again creating 24 more cookies with holes in them, making a total of 48 holey cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7A08837CF82214FAF2E3E537506DC75', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EFDA4B179A9F74692344B53F15B8D48', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 10, NULL, NULL, 2, NULL, 0, 'Bake cookies in a 375 degree oven for 7-11 minutes until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0066B62DD969604EB89798D5599D7DF5', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 11, NULL, NULL, 2, NULL, 0, 'Cut open one tube of the Betty Crocker Cookie Icing and pour it into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'112DA44F99ED144F8B5D0EB7732F85E4', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 12, NULL, NULL, 2, NULL, 0, 'Heat it for about 20 seconds until it is thin and pourable.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E3CB70057D23F4B8B7C9FF387925BB3', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 13, NULL, NULL, 2, NULL, 0, 'Dip the tops of the whole round cookies in the icing so you get a nice smooth surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDF1784022CFEE4981D17DDD369CCBE7', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 14, NULL, NULL, 2, NULL, 0, 'Allow the cookies to dry for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95178995A4C6E94AAC28794448651895', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 15, NULL, NULL, 2, NULL, 0, 'Attach one holey cookie on top of each of the frosted cookies using a thin band of white icing.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF89BDFBF152AA4EB88BB4AB8935BB3F', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 16, NULL, NULL, 2, NULL, 0, 'Allow these cookies to dry overnight! The icing needs to be very hard so the googly eyes can slide around on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77666ABC540C6942AFC76918A2B84252', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 17, NULL, NULL, 2, NULL, 0, 'Place three baking pans in a 350 degree oven and allow them to get good and hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2B39119C2CE3D479C9D0EF71AF181C0', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 18, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the clear tops of the googly eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE6A3462F3F8974AB0756C38E37C95E4', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 19, NULL, NULL, 2, NULL, 0, 'Start by placing the Isomalt nibs or broken sticks into a glass microwave safe bowl with a pour spout.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9493A38A8C18364D9A70EA62280506DA', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 20, NULL, NULL, 2, NULL, 0, 'Heat in the microwave until melted and thin.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01D81161FB71C54592D34D77E40A1166', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 21, NULL, NULL, 2, NULL, 0, 'Remove one hot pan from the oven. Set it on a towel or trivets.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA49F31709276140AF0332429B49AA9D', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 22, NULL, NULL, 2, NULL, 0, 'Place a piece of parchment paper in the pan. Set 8 cookies on the paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3281063C3A22D34EADF56365C8FCDDD5', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 23, NULL, NULL, 2, NULL, 0, 'Pour some isomalt in the center of each cookie, allowing it to completely cover the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF4A0C7ECB723C4EABF7091ABC9A79ED', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 24, NULL, NULL, 2, NULL, 0, 'Allow the isomalt to harden overnight.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97D22486D46C65459468AE2226281CE1', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 25, NULL, NULL, 2, NULL, 0, 'Good morning. Now you are ready to assemble your Cookie Googly Eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD0BB5B36383E14E98619844F16A4F73', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 26, NULL, NULL, 2, NULL, 0, 'The Ghirardelli Candy Making Wafers and most Candy Melt type wafers have a tip on one side.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F310B6B5AAE3C54F959E51EDEBFC0B5B', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 27, NULL, NULL, 2, NULL, 0, 'Use a sharp knife to cut wafers into thin flat wafers, cutting off the tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2C4ADDD6484E048B56BF9201CF95106', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 28, NULL, NULL, 2, NULL, 0, 'Set one candy wafer inside the stacked cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B7A08C00E2BC54A96EB84425E59DE60', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 29, NULL, NULL, 2, NULL, 0, 'Pipe some icing on the back side of the candy filled cookies and set one on each cookie stack.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2E8CBA6CF34094C95D925A73EA35CF8', X'424B31573A6692478D2B6C6419DE3291', X'424B31573A6692478D2B6C6419DE3291', 30, NULL, NULL, 2, NULL, 0, 'Now go have fun wiggling your cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70ED7CB06953FC48952F5984A6ECD0A8', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 0, NULL, NULL, 2, NULL, 0, 'Cut Velveeta Con Queso into small chunks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9663CB88EC339D41A282CBC7FE3035EB', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 1, NULL, NULL, 2, NULL, 0, 'Melt in the microwave, stirring often.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A7EE0449B239D4FB3C2A9383685023A', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 2, NULL, NULL, 2, NULL, 0, 'Pour melted cheese into sugar skull mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E61B0DC6050E8C43B39757A1B1CC07BC', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 3, NULL, NULL, 2, NULL, 0, 'Tap the mold down several times to remove air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'417470D4F1C9294DBB5F10B4CFAB28E6', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 4, NULL, NULL, 2, NULL, 0, 'Place mold in freezer for about 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23E77C2F3AF72143AE7FBC230CAC5805', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 5, NULL, NULL, 2, NULL, 0, 'Use your left over melted cheese or melt more to make the colorful decorations.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'414E3EF93E13594AB0163CD9EE7C58AD', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 6, NULL, NULL, 2, NULL, 0, 'Divide the cheese into small bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB8EE7534CCF384F95586B340C1D0888', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 7, NULL, NULL, 2, NULL, 0, 'Color with various food colorings.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B30622AE0FE4F428D5915762E196756', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 8, NULL, NULL, 2, NULL, 0, 'Set the cheese in between two non-stick mats and rolled it out using a rolling pin.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'357A99AE01AA8544B7E8DB7FF674147D', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 9, NULL, NULL, 2, NULL, 0, 'Freeze your colored cheese for about 10 minutes until it''s still flexible but not sticky.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27CA15A3F866EF4B8A67A22652806C3F', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 10, NULL, NULL, 2, NULL, 0, 'Remove your sugar skull mold from freezer, turn it upside down over your work surface, press down on the plastic mold, releasing the cheese and allowing it to gently fall onto the counter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C12885498D709649960A38E1ACE1DBD9', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 11, NULL, NULL, 2, NULL, 0, 'Paint black food coloring into the eye and nose sockets.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E3331DF5CC57E41AF262B43EA793911', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 12, NULL, NULL, 2, NULL, 0, 'Use small metal cutters and plunger cutters to cut decorations out of the colored cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61CB339A3A0CF04EB9BA63B6DA3D39ED', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 13, NULL, NULL, 2, NULL, 0, 'Press the decorations onto the cheese skull.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D98A52BC32431842937406F28C026D94', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 14, NULL, NULL, 2, NULL, 0, 'Cut blue stars, pink hearts, green dots, and blue and purple petals out of cheese to decorate your cheese skull.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0FD86BF61DF5D24AB5C9D755399041B6', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 15, NULL, NULL, 2, NULL, 0, 'Use black food coloring to put some finishing touches on your cheese sugar skull.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FCA31627FEDED4DBC5FDF201792624B', X'D7BDABC2C9042144AB4EB83345FE1BE5', X'D7BDABC2C9042144AB4EB83345FE1BE5', 16, NULL, NULL, 2, NULL, 0, 'Serve with crackers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61315FC13D278B47978A145ADB7F8351', X'7EA81928AAF48F408D927F250677AF2A', X'7EA81928AAF48F408D927F250677AF2A', 0, NULL, NULL, 2, NULL, 0, 'Cut each dough sheet in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04D3029C2FDDBF4197C303E52AEBAB73', X'7EA81928AAF48F408D927F250677AF2A', X'7EA81928AAF48F408D927F250677AF2A', 1, NULL, NULL, 2, NULL, 0, 'Drape dough into skull cavity in pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBFB8B03892CDA42B660553C2C7B8C04', X'7EA81928AAF48F408D927F250677AF2A', X'7EA81928AAF48F408D927F250677AF2A', 2, NULL, NULL, 2, NULL, 0, 'Fill with 1 1/2 tablespoons of sauce, pizza toppings, and 1/4 cup of shredded mozzarella cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABB78FC1954A854C9F26B89192C403BA', X'7EA81928AAF48F408D927F250677AF2A', X'7EA81928AAF48F408D927F250677AF2A', 3, NULL, NULL, 2, NULL, 0, 'Cut off the excess dough, pinch together the seams.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB802845CA27F14D94BD0C6D42A43739', X'7EA81928AAF48F408D927F250677AF2A', X'7EA81928AAF48F408D927F250677AF2A', 4, NULL, NULL, 2, NULL, 0, 'Brush egg wash over top of dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F340C27EFF7AE04DA5DD6ADACBA43B5D', X'7EA81928AAF48F408D927F250677AF2A', X'7EA81928AAF48F408D927F250677AF2A', 5, NULL, NULL, 2, NULL, 0, 'Repeat creating 4 skulls if using the Wilton skull pan and 6 skulls if using the Nordic Ware skull pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B5039CF4D14DB4BAA79930B1EEC9BC9', X'7EA81928AAF48F408D927F250677AF2A', X'7EA81928AAF48F408D927F250677AF2A', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4CB7D1F75D7E247B2636786C621016B', X'7EA81928AAF48F408D927F250677AF2A', X'7EA81928AAF48F408D927F250677AF2A', 7, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 375 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E08DE13B0E6D34F93AA38ED82C7E77C', X'7EA81928AAF48F408D927F250677AF2A', X'7EA81928AAF48F408D927F250677AF2A', 8, NULL, NULL, 2, NULL, 0, 'Bake for 18-20 minutes until the crust looks cooked through and golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C568BF4CB05FF44487524DA3D28FE3C8', X'7EA81928AAF48F408D927F250677AF2A', X'7EA81928AAF48F408D927F250677AF2A', 9, NULL, NULL, 2, NULL, 0, 'Remove skulls from pan and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C946417743F9E645B0B8E454EE40FA3C', X'EACE3F3984A427469BC0E8AC445A8E21', X'EACE3F3984A427469BC0E8AC445A8E21', 0, NULL, NULL, 2, NULL, 0, 'Pop 1/3 cup of popcorn kernels in an air popper. If you don''t have an air popper, then place your kernels in a plain brown lunch bag, fold the top of the bag over a few times, heat in microwave on high power for 2-3 minutes until you hear the popping of the kernels slow to one to two pops per second.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44BA9914B56B104986EA9FA901202A34', X'EACE3F3984A427469BC0E8AC445A8E21', X'EACE3F3984A427469BC0E8AC445A8E21', 1, NULL, NULL, 2, NULL, 0, 'Place the popped kernels in a very large mixing bowl and discard any unpopped kernels.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DDD5405AE3F154B9B3F5E0DAB05C6CA', X'EACE3F3984A427469BC0E8AC445A8E21', X'EACE3F3984A427469BC0E8AC445A8E21', 2, NULL, NULL, 2, NULL, 0, 'Pour melted and tempered white chocolate or melted white confectionery coating over popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6FC950D2C1DD44896062ECD30939920', X'EACE3F3984A427469BC0E8AC445A8E21', X'EACE3F3984A427469BC0E8AC445A8E21', 3, NULL, NULL, 2, NULL, 0, 'Use a large spatula or spoon to gently fold the popcorn and chocolate just until the popcorn is covered.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C7299B5F9FD3744926CC9D325AE045C', X'EACE3F3984A427469BC0E8AC445A8E21', X'EACE3F3984A427469BC0E8AC445A8E21', 4, NULL, NULL, 2, NULL, 0, 'Quickly spoon the white chocolate popcorn into the skull mold, pressing down, making sure the top is level.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE52C7AAA5566F4CA21B883106AABD6E', X'EACE3F3984A427469BC0E8AC445A8E21', X'EACE3F3984A427469BC0E8AC445A8E21', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate if using white chocolate, freeze if using confectionery coating, until set, about 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5ED614D56BAB064398149AD60FB73CF0', X'EACE3F3984A427469BC0E8AC445A8E21', X'EACE3F3984A427469BC0E8AC445A8E21', 6, NULL, NULL, 2, NULL, 0, 'To un-mold, hold your hand over the popcorn, turn mold upside down and set just above your work surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50FA56D5BAD88542943E9331D7C15426', X'EACE3F3984A427469BC0E8AC445A8E21', X'EACE3F3984A427469BC0E8AC445A8E21', 7, NULL, NULL, 2, NULL, 0, 'Press gently on mold, allow popcorn skull to fall out. If it wont release from mold, it may need a few more minutes of chilling time.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4418C9FE2CE824E92C7C200CAF55EF4', X'EACE3F3984A427469BC0E8AC445A8E21', X'EACE3F3984A427469BC0E8AC445A8E21', 8, NULL, NULL, 2, NULL, 0, 'Pour melted and tempered dark chocolate or melted dark cocoa candy melts into a disposable pastry bag or zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CD88C4B50C47F409B09E0001BBA4244', X'EACE3F3984A427469BC0E8AC445A8E21', X'EACE3F3984A427469BC0E8AC445A8E21', 9, NULL, NULL, 2, NULL, 0, 'Snip off one tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'289036B8E27DFB40AEBE2C1FCDE87F66', X'EACE3F3984A427469BC0E8AC445A8E21', X'EACE3F3984A427469BC0E8AC445A8E21', 10, NULL, NULL, 2, NULL, 0, 'Pipe on eyes, nose and mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35597B21D37A1249917BB831E11DAA24', X'EACE3F3984A427469BC0E8AC445A8E21', X'EACE3F3984A427469BC0E8AC445A8E21', 11, NULL, NULL, 2, NULL, 0, 'Refrigerate for 7-10 minutes until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7E8B60BEA6F7547BB56912F5A608204', X'EACE3F3984A427469BC0E8AC445A8E21', X'EACE3F3984A427469BC0E8AC445A8E21', 12, NULL, NULL, 2, NULL, 0, 'Serve skull within two days or store it in a large metal popcorn tin for up to 3 weeks.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE44A50CCA19C64585DE8E016CEC2C79', X'177EAC2511CE8045B58849BEB50BB667', X'177EAC2511CE8045B58849BEB50BB667', 0, NULL, NULL, 2, NULL, 0, 'Butter your brain molds before you make your batch of gumdrops.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05EAB62B2BC6F944A9C04CA57107DE7F', X'177EAC2511CE8045B58849BEB50BB667', X'177EAC2511CE8045B58849BEB50BB667', 1, NULL, NULL, 2, NULL, 0, 'Click here, to see the recipe for homemade gumdrops.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56A6D38EDDCDA94E9F5F5716A0B88C61', X'177EAC2511CE8045B58849BEB50BB667', X'177EAC2511CE8045B58849BEB50BB667', 2, NULL, NULL, 2, NULL, 0, 'As soon as your gumdrop mixture is ready, pour it into a glass bowl, preferably with a spout.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD2A29331FEE214BB2818EE015C39B05', X'177EAC2511CE8045B58849BEB50BB667', X'177EAC2511CE8045B58849BEB50BB667', 3, NULL, NULL, 2, NULL, 0, 'Fill up each brain cavity about 3/4 full.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CCA7F67208C8044BAAB41D3B2F319D1', X'177EAC2511CE8045B58849BEB50BB667', X'177EAC2511CE8045B58849BEB50BB667', 4, NULL, NULL, 2, NULL, 0, 'Immediately, poke a hole in each brain using a skewer or toothpick that has been dipped in red food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44A084AB4D48124789267CB095B507EC', X'177EAC2511CE8045B58849BEB50BB667', X'177EAC2511CE8045B58849BEB50BB667', 5, NULL, NULL, 2, NULL, 0, 'Swirl the skewer around a bit, spreading the red coloring around inside the brains.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D69F7CC23D23D44A1B1C8A7593524CB', X'177EAC2511CE8045B58849BEB50BB667', X'177EAC2511CE8045B58849BEB50BB667', 6, NULL, NULL, 2, NULL, 0, 'Allow the gumdrops to cool for 1 hour then pop the molds into the freezer for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DCD3D8F14E4FE40AA7DFEB6405224B8', X'177EAC2511CE8045B58849BEB50BB667', X'177EAC2511CE8045B58849BEB50BB667', 7, NULL, NULL, 2, NULL, 0, 'Remove, and un-mold the brains onto some sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BADF418DA0F9848A477D56D9CD32953', X'177EAC2511CE8045B58849BEB50BB667', X'177EAC2511CE8045B58849BEB50BB667', 8, NULL, NULL, 2, NULL, 0, 'Coat all the gumdrop brains in sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68D951F56B643443A08604BFEC68B722', X'177EAC2511CE8045B58849BEB50BB667', X'177EAC2511CE8045B58849BEB50BB667', 9, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for up to a month.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9A34DD2557CCB4FB38CB0094D65DD79', X'58E9F78B925DAC438AF87018B28CD686', X'58E9F78B925DAC438AF87018B28CD686', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8A7C7CE22A6F440AADA26FF26C3D9CD', X'58E9F78B925DAC438AF87018B28CD686', X'58E9F78B925DAC438AF87018B28CD686', 1, NULL, NULL, 2, NULL, 0, 'Butter leaf molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AAFD463EE4976145932B7C27AB4719C8', X'58E9F78B925DAC438AF87018B28CD686', X'58E9F78B925DAC438AF87018B28CD686', 2, NULL, NULL, 2, NULL, 0, 'Combine cream cheese, sugar, heavy whipping cream, eggs, and vanilla in the bowl of a food processor, pulse until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C88E95317320A04394B287926C06B3BD', X'58E9F78B925DAC438AF87018B28CD686', X'58E9F78B925DAC438AF87018B28CD686', 3, NULL, NULL, 2, NULL, 0, 'Divide cheesecake mixture into three or more bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7989A60E4432145993806BD235D6C66', X'58E9F78B925DAC438AF87018B28CD686', X'58E9F78B925DAC438AF87018B28CD686', 4, NULL, NULL, 2, NULL, 0, 'Color one bowl orange, red, and yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58E210E2CDE93A4FA9FB192BDF092583', X'58E9F78B925DAC438AF87018B28CD686', X'58E9F78B925DAC438AF87018B28CD686', 5, NULL, NULL, 2, NULL, 0, 'Fill each leaf mold with a swirl of orange, red, and yellow filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3D7FBA61720624D9129A85E447E7FF3', X'58E9F78B925DAC438AF87018B28CD686', X'58E9F78B925DAC438AF87018B28CD686', 6, NULL, NULL, 2, NULL, 0, 'Tap the mold several times to remove excess air bubbles after each scoop of cheesecake filling you add.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A7BF078E467F047875F45D8B9650F3F', X'58E9F78B925DAC438AF87018B28CD686', X'58E9F78B925DAC438AF87018B28CD686', 7, NULL, NULL, 2, NULL, 0, 'Bake your cheesecakes for 20-24 minutes until the top forms a smooth film and the center is still giggly but not wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA0FF2672DED5D4DBF42A4BB7468EE0E', X'58E9F78B925DAC438AF87018B28CD686', X'58E9F78B925DAC438AF87018B28CD686', 8, NULL, NULL, 2, NULL, 0, 'Allow your cheesecakes to cool for an hour then freeze for at least 3 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2AC7A39043F97429856E808863812FA', X'58E9F78B925DAC438AF87018B28CD686', X'58E9F78B925DAC438AF87018B28CD686', 9, NULL, NULL, 2, NULL, 0, 'Un-mold cheesecakes directly onto a serving dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4850A8F06F4F144FA1939781D20416F1', X'58E9F78B925DAC438AF87018B28CD686', X'58E9F78B925DAC438AF87018B28CD686', 10, NULL, NULL, 2, NULL, 0, 'Allow to defrost in the refrigerator for a few hours before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4373AC37C534C74F97B541C4A507AF9C', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 0, NULL, NULL, 2, NULL, 0, 'In food processor, combine 7 oz. cream cheese, Havarti cheese, Gruyere cheese, roasted red peppers, and garlic.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C68F1B22A491104785D02515F61BB5C0', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 1, NULL, NULL, 2, NULL, 0, 'Pulse until creamy and smooth. You will need 1 heaping teaspoon of this cheese for the cat''s nose. Wrap the 1 teaspoon in plastic wrap and refrigerate until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0931FA5311B6C94D9355216847411FC7', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 2, NULL, NULL, 2, NULL, 0, 'Place the remaining cheese mixture on a large piece of plastic wrap.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEDAF81A2D0E2B47A5B38B5E3F5DCEF6', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 3, NULL, NULL, 2, NULL, 0, 'Use the plastic wrap to mold the cheese mixture into a flattened ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1905A17169FE34E94F0DE3CFBC98346', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 4, NULL, NULL, 2, NULL, 0, 'Reserve two whole blue corn tortilla chips for the cat''s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E799775C81B1F842A11DA7E72223573C', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 5, NULL, NULL, 2, NULL, 0, 'Place remaining chips in a clean food processor bowl and pulse to fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC2AC90FEA1C5E47B1E0EBF2DEBF25F5', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 6, NULL, NULL, 2, NULL, 0, 'Pour onto a large piece of plastic wrap.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D20750BB7563542AC79CEDEFAB4AD34', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 7, NULL, NULL, 2, NULL, 0, 'Set cheese ball round size down on crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D7C3E1E7EE31448810885B6802C8107', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 8, NULL, NULL, 2, NULL, 0, 'Use the plastic wrap to press the crumbs all over the surface of the cheese ball. Set cheese ball on a serving platter.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F853F3970C8AC94E9862817C84EDBA3F', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 9, NULL, NULL, 2, NULL, 0, 'Put the remaining 1 oz. cream cheese into pastry bag or zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55D590BFDE855C4F91B394F9E9528671', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 10, NULL, NULL, 2, NULL, 0, 'Snip off the tip of the bag. Pipe three whiskers onto the cat''s face.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAD42EB06FDB1C47ACB76D3771BF2784', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 11, NULL, NULL, 2, NULL, 0, 'Remove the reserved cheese mixture from the refrigerator and mold it into a triangular shape for the nose; place in the middle of the cat''s face, on top of the middle of the whiskers.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1916570361156F43B02F993B4AD57210', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 12, NULL, NULL, 2, NULL, 0, 'To create the eyes I suggest you scoop out the crushed tortilla chips and a bit of the cheese ball in two circles above the nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A554D46EDA91CA40ACF4D428F1131C1F', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 13, NULL, NULL, 2, NULL, 0, 'Pipe cream cheese into these holes. Smooth it out into a nice circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A735253A178BD46BC745DBFE27A23E1', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 14, NULL, NULL, 2, NULL, 0, 'Cut an olive in half and lay on top of the eyes for pupils.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3781493DCF5727408993EB4552DCEE5A', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 15, NULL, NULL, 2, NULL, 0, 'Press the two reserved blue corn tortilla chips into top of head for ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1FC288EA53A60744A848D110ACDF3DB0', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', X'FEAF204B0AF02F42BEC3A7CA79C6DF57', 16, NULL, NULL, 2, NULL, 0, 'Refrigerate until needed. Let sit at room temperature for at least 30 minutes before serving with crackers.');
