INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54F4492396688745B859901A599B734B', X'363E5A480168F148BEAFC8AC138EB830', X'363E5A480168F148BEAFC8AC138EB830', 0, NULL, NULL, 2, NULL, 0, 'Cut 4 leaves out of each slice of cheese and set one slice on each cracker.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80E809E5E6F8444A87E069CDC526E612', X'363E5A480168F148BEAFC8AC138EB830', X'363E5A480168F148BEAFC8AC138EB830', 1, NULL, NULL, 2, NULL, 0, 'Arrange in four rows in a half-moon shape on a large serving platter, overlapping the crackers as you go, leaving an opening on the interior of the half-moon for the cheese ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6D710D2613E844F8126178579728E83', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', 0, NULL, NULL, 2, NULL, 0, 'Stir together the softened cream cheese, shredded cheese (or scraps from above), the ranch dressing, and Worcestershire sauce until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC019ACD5AE94E48A9B264DBEEB267E0', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', 1, NULL, NULL, 2, NULL, 0, 'Scoop about 1/3rd of the cheese spread into a shallow bowl filled with the bacon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1064F09A0C29D41998B39EDA18DFC89', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', 2, NULL, NULL, 2, NULL, 0, 'Coat the cheese spread in the bacon and roll it into a ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'761276CD9AF4254F876A62F73A02A52D', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', 3, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85C745B157AE254D8FB4FC35AA1F2807', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', 4, NULL, NULL, 2, NULL, 0, 'Reserve 1 tablespoon of the cheese spread for later.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9DD5A37128FF0419E7A8F7345CB3C17', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', 5, NULL, NULL, 2, NULL, 0, 'Roll the rest in bacon shaping it into an oval-shaped turkey body.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88FE6549B754864086640C40628E1F3A', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', 6, NULL, NULL, 2, NULL, 0, 'Set the turkey body in the opening in the crackers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEB531E988A86C4EBE8F0E8878278A63', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', X'F80D4C2CA2C1A54D965B723E4F7CCDAE', 7, NULL, NULL, 2, NULL, 0, 'Attach the cheese ball head using some of the reserved cheese spread.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80690A143E22D841BC8285BC7ED80572', X'13ADF18252768A47B749267306531A72', X'13ADF18252768A47B749267306531A72', 0, NULL, NULL, 2, NULL, 0, 'Cut two circle-shaped eyes out of the white cheese and draw on pupils using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1391D505A0617343BA5355837E6E1933', X'13ADF18252768A47B749267306531A72', X'13ADF18252768A47B749267306531A72', 1, NULL, NULL, 2, NULL, 0, 'Cut out a triangle-shaped beak and a teardrop-shaped wattle from the orange cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92E12E8F61D99C409CCA8BB375FCC33E', X'13ADF18252768A47B749267306531A72', X'13ADF18252768A47B749267306531A72', 2, NULL, NULL, 2, NULL, 0, 'Brush red food coloring over the teardrop-shaped wattle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F72F5FADB57236468CF64163209F2731', X'13ADF18252768A47B749267306531A72', X'13ADF18252768A47B749267306531A72', 3, NULL, NULL, 2, NULL, 0, 'Cut two triangle-shaped feet each with 3 toes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1835A0080889A4A929BBB7A79113DD7', X'13ADF18252768A47B749267306531A72', X'13ADF18252768A47B749267306531A72', 4, NULL, NULL, 2, NULL, 0, 'Attach the Turkey Eyes, Wattle, Beak, and Feet to the cheese ball using the remaining cheese spread.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4667B54824177469D6F1452C5D3B140', X'13ADF18252768A47B749267306531A72', X'13ADF18252768A47B749267306531A72', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C288C115407FA49BD63C35D5D018606', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 0, NULL, NULL, 2, NULL, 0, 'Combine the peanut butter candy melts or chips and the white candy melts in a microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED9670985789EF4DB79E214F50C69728', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 1, NULL, NULL, 2, NULL, 0, 'Heat the wafers on high for 20-30 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4449A59253875C4CAE080A0B198080D0', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 2, NULL, NULL, 2, NULL, 0, 'Pour the melted peanut butter candy coating into a tall glass or jar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0BA4EB2DA6E104B90204D7151EB21AC', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 3, NULL, NULL, 2, NULL, 0, 'Hold onto one end of a pretzel stick and dip it into the candy coating, covering about 80% of the pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9F1896730FF6847B206BDF7B3BFEC2E', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 4, NULL, NULL, 2, NULL, 0, 'Lift the pretzel out of the peanut butter candy coating and shake it allowing the excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D12711E3EBBC8B49ABF7E692B15107F5', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 5, NULL, NULL, 2, NULL, 0, 'Set on a parchment-paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82C2B28476F33545B9342C8E0DEE2811', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 6, NULL, NULL, 2, NULL, 0, 'Continue dipping 5 more pretzels, setting them next to each other.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C24784D65A88014FACFE338E4F1C8AEA', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 7, NULL, NULL, 2, NULL, 0, 'Pop in the refrigerator for 3-5 minutes just until the candy coating sets.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02EDABD04EC06F46A6B93FDEE7C99992', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 8, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 20 groupings of 6 peanut butter candy-coated pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3CAC8BB8B26A5418224760715843983', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 9, NULL, NULL, 2, NULL, 0, 'Drop a pretzel into a bowl of melted and tempered pure milk chocolate or melted light cocoa candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D007614B2D41A54DB0213850B0376014', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 10, NULL, NULL, 2, NULL, 0, 'Use a dipping fork or kitchen fork to remove the pretzel, then set it on a parchment-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E125940815ECD041AF430FF9A03BC58C', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 11, NULL, NULL, 2, NULL, 0, 'Refrigerate for 3 minutes until the chocolate sets.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A31C320A42A0A54393EF8D7248630224', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 12, NULL, NULL, 2, NULL, 0, 'Color some white modeling chocolate orange, yellow, yellow-orange, and dark brown or black.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BE27BD99CBE1F4082DF543C7BA3BF96', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 13, NULL, NULL, 2, NULL, 0, 'Roll out some of the yellow-orange modeling chocolate and cut thin strips for the hair.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E0BAB184F216D4CABFDC3BD92A13A63', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 14, NULL, NULL, 2, NULL, 0, 'Roll out the orange and cut small triangles for the noses.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C82821A692AB814B821D097FE2DC75B3', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 15, NULL, NULL, 2, NULL, 0, 'Use a daisy plunger cutter to cut two yellow daisies, set one on top of another, press into the center using a wood dowel, then brush a dab of water on a small ball of dark brown modeling chocolate and insert it into the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25B8BC6EED02704B9AB8C55E13D091B0', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 16, NULL, NULL, 2, NULL, 0, 'Attach some modeling chocolate hair to the un-coated part of the pretzels so that the hair falls down over the peanut butter coated portion of the pretzels (the scarecrow''s face).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FCA8A8F95DB094792349304CE1219D1', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 17, NULL, NULL, 2, NULL, 0, 'Use small dabs of chocolate to make them stick if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F48872B73FA5E479584AD2061A73253', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 18, NULL, NULL, 2, NULL, 0, 'Dip the top of pretzel scarecrow milk chocolate to create his hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'56F3FADC27AD2D4E9BCA715E7C85D676', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 19, NULL, NULL, 2, NULL, 0, 'Set on a non-stick mat or a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DFFB1230BC76304884D8588237B8B708', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 20, NULL, NULL, 2, NULL, 0, 'Set one chocolate dipped pretzel across the bottom edge of the wet chocolate so that it covers the tips of the modeling chocolate hair and it creates the hat''s brim.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6C67DB25EC7B744975F0D486BE9B790', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 21, NULL, NULL, 2, NULL, 0, 'Set one sunflower in one corner of the chocolate hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32B6846206A22A47A2D90FA50F845D56', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 22, NULL, NULL, 2, NULL, 0, 'Use chocolate to attach two candy eyes to the peanut butter pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'228A28F8BEBC094494273DDEB2548D1A', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 23, NULL, NULL, 2, NULL, 0, 'Attach one orange triangle nose using a dab of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BD603DD95404842930A6093F60D9D26', X'9E871B7D859754478AF810713A6EDEC7', X'9E871B7D859754478AF810713A6EDEC7', 24, NULL, NULL, 2, NULL, 0, 'Use a black food coloring marker to draw on a smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84DE9B72E8CB814B8840764358D7C127', X'3CF38C1D0713A648AC98D35FB8B11D94', X'3CF38C1D0713A648AC98D35FB8B11D94', 0, NULL, NULL, 2, NULL, 0, 'Dip the tip of your straw, lollipop stick, or craft stick in melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'048D7225EF43BD40BF80C0489569CA91', X'3CF38C1D0713A648AC98D35FB8B11D94', X'3CF38C1D0713A648AC98D35FB8B11D94', 1, NULL, NULL, 2, NULL, 0, 'Push it into one end of the Rice Krispie Treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB1B0441AD38D54E8F1E4BC75BE43028', X'3CF38C1D0713A648AC98D35FB8B11D94', X'3CF38C1D0713A648AC98D35FB8B11D94', 2, NULL, NULL, 2, NULL, 0, 'Allow that chocolate to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD68A7A8A2F41E48AB8EE8875F1ADA50', X'3CF38C1D0713A648AC98D35FB8B11D94', X'3CF38C1D0713A648AC98D35FB8B11D94', 3, NULL, NULL, 2, NULL, 0, 'Dip the cereal treat into the melted chocolate then remove and allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12BBF8BC7EC5AC488BEA30619AB5EB62', X'3CF38C1D0713A648AC98D35FB8B11D94', X'3CF38C1D0713A648AC98D35FB8B11D94', 4, NULL, NULL, 2, NULL, 0, 'Set the chocolate-dipped treat on a baking sheet lined with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'604B948D2A5BF34E99AE18969922D10A', X'3CF38C1D0713A648AC98D35FB8B11D94', X'3CF38C1D0713A648AC98D35FB8B11D94', 5, NULL, NULL, 2, NULL, 0, 'If using pure milk chocolate, refrigerate for 5-10 minutes until set. If using light cocoa candy melts, freeze for 5-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31504538D19EA44F8FB6314912CD46EA', X'3CF38C1D0713A648AC98D35FB8B11D94', X'3CF38C1D0713A648AC98D35FB8B11D94', 6, NULL, NULL, 2, NULL, 0, 'Allow the chocolate-dipped cereal treats to come to room temperature before decorating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D76BBF40099ADD4598D7F70F0BA170A9', X'3CF38C1D0713A648AC98D35FB8B11D94', X'3CF38C1D0713A648AC98D35FB8B11D94', 7, NULL, NULL, 2, NULL, 0, 'Using a fondant roller, roll all the colors of modeling chocolate out to about 1/16th of an inch.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1E3E93422B5EF47B8379C7E4E01CFE3', X'3CF38C1D0713A648AC98D35FB8B11D94', X'3CF38C1D0713A648AC98D35FB8B11D94', 8, NULL, NULL, 2, NULL, 0, 'Pinch off 6 tiny pieces of red and roll them into teardrops, then shape each into the Turkeys'' wattles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F4E86239A7DFB42BEF476E8E858561F', X'3CF38C1D0713A648AC98D35FB8B11D94', X'3CF38C1D0713A648AC98D35FB8B11D94', 9, NULL, NULL, 2, NULL, 0, 'Use a small round plunger cutter or the tip of a #4 pastry tip to make the black pupils.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E06C80FEACCD6546BD387C8614E678A2', X'3CF38C1D0713A648AC98D35FB8B11D94', X'3CF38C1D0713A648AC98D35FB8B11D94', 10, NULL, NULL, 2, NULL, 0, 'Cut two different sized circles out of the white for each turkey.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE0A4BAD9BC38B4798C8679667E82987', X'3CF38C1D0713A648AC98D35FB8B11D94', X'3CF38C1D0713A648AC98D35FB8B11D94', 11, NULL, NULL, 2, NULL, 0, 'Very lightly brush the backsides of the black pupils with water and attach them to the white eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13779F8A1409C04081CA1C838DC1B14B', X'3CF38C1D0713A648AC98D35FB8B11D94', X'3CF38C1D0713A648AC98D35FB8B11D94', 12, NULL, NULL, 2, NULL, 0, 'Also, use water to attach the facial features to the chocolate dipped rice cereal treat bars.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86DC567E8A94784A8046FA0498F93F5D', X'46C45A488D249E49ABC63B06296F07B7', X'46C45A488D249E49ABC63B06296F07B7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'335EE7374A5E4942B5CE800AC5887084', X'46C45A488D249E49ABC63B06296F07B7', X'46C45A488D249E49ABC63B06296F07B7', 1, NULL, NULL, 2, NULL, 0, 'Unroll dough onto a flour-dusted cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E04CAA30BF18604A8E8A7639A9A66021', X'46C45A488D249E49ABC63B06296F07B7', X'46C45A488D249E49ABC63B06296F07B7', 2, NULL, NULL, 2, NULL, 0, 'Cut leaves using cookie cutters and set on a parchment paper-lined cookie sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1E5BE59051A5A48AA5377B089CD301E', X'46C45A488D249E49ABC63B06296F07B7', X'46C45A488D249E49ABC63B06296F07B7', 3, NULL, NULL, 2, NULL, 0, 'Brush leaves with melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA3D5B048C4AE147953FDE4FAEC679FA', X'46C45A488D249E49ABC63B06296F07B7', X'46C45A488D249E49ABC63B06296F07B7', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle cheese over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD3ADF4FE012844E9B85B9925E728533', X'46C45A488D249E49ABC63B06296F07B7', X'46C45A488D249E49ABC63B06296F07B7', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle on paprika and green toppings if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C757363FCC61DA4694086F97E8050E21', X'46C45A488D249E49ABC63B06296F07B7', X'46C45A488D249E49ABC63B06296F07B7', 6, NULL, NULL, 2, NULL, 0, 'Bake for 6-8 minutes until the cheese is bubbly and the crescents are golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'926D12D7CD0F1840977E3F1443AE5519', X'46C45A488D249E49ABC63B06296F07B7', X'46C45A488D249E49ABC63B06296F07B7', 7, NULL, NULL, 2, NULL, 0, 'If desired, cut the excess cheese off the leaves using the same cookie cutter or using kitchen shears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE3C716F25A9E547BB3EBF02BE51D748', X'46C45A488D249E49ABC63B06296F07B7', X'46C45A488D249E49ABC63B06296F07B7', 8, NULL, NULL, 2, NULL, 0, 'Serve warm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CD20957EEDE4B4997215C12E027B767', X'F52D5692F7B05844947694EB1FE165A7', X'F52D5692F7B05844947694EB1FE165A7', 0, NULL, NULL, 2, NULL, 0, 'Unroll dough then cut into 8 long strips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74207A39FD01F44083F785AE77283D86', X'F52D5692F7B05844947694EB1FE165A7', X'F52D5692F7B05844947694EB1FE165A7', 1, NULL, NULL, 2, NULL, 0, 'Use 6 of those strips to wrap around either cream horn forms or sugar cones that have been wrapped in non-stick tin foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A66C7E349F2B684294EE80E25659D70D', X'F52D5692F7B05844947694EB1FE165A7', X'F52D5692F7B05844947694EB1FE165A7', 2, NULL, NULL, 2, NULL, 0, 'Cut the remaining two strips in half lengthwise.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CEDD31BA9268D449E0576D242EC2DFB', X'F52D5692F7B05844947694EB1FE165A7', X'F52D5692F7B05844947694EB1FE165A7', 3, NULL, NULL, 2, NULL, 0, 'Twist two of those strips together then cut into three equal lengths then repeat with the other two strips of dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1715798AB55D504180203AA8B2C06F0A', X'F52D5692F7B05844947694EB1FE165A7', X'F52D5692F7B05844947694EB1FE165A7', 4, NULL, NULL, 2, NULL, 0, 'Whisk together the water and egg then brush the egg wash over the opening of each crescent roll cone and wrap one of the twisted strips around that edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76C01E6551FC8F48B9B0CFB5A3E95DF1', X'F52D5692F7B05844947694EB1FE165A7', X'F52D5692F7B05844947694EB1FE165A7', 5, NULL, NULL, 2, NULL, 0, 'Set the cone on a tin foil (preferably non-stick) lined baking sheet. If desired set the tip of the cones under a roll of tin foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81EC6436232C624ABB9F3F3D0F1D63E6', X'F52D5692F7B05844947694EB1FE165A7', X'F52D5692F7B05844947694EB1FE165A7', 6, NULL, NULL, 2, NULL, 0, 'Repeat making a total of 6 crescent roll cornucopia.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1246B7DD1D30C47BC66C2526A12BB22', X'F52D5692F7B05844947694EB1FE165A7', X'F52D5692F7B05844947694EB1FE165A7', 7, NULL, NULL, 2, NULL, 0, 'Brush egg wash over the tops and sides of each crescent roll cornucopia.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91DB834E90E3B345A454BD134ACF2899', X'F52D5692F7B05844947694EB1FE165A7', X'F52D5692F7B05844947694EB1FE165A7', 8, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48343DA215F23647B70F28482E27B4B9', X'F52D5692F7B05844947694EB1FE165A7', X'F52D5692F7B05844947694EB1FE165A7', 9, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat the oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9D5CE9506CB6B49AFC990690DD2A7D7', X'F52D5692F7B05844947694EB1FE165A7', X'F52D5692F7B05844947694EB1FE165A7', 10, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F64E6E7AEE41B349AC7F7342B341C34C', X'F52D5692F7B05844947694EB1FE165A7', X'F52D5692F7B05844947694EB1FE165A7', 11, NULL, NULL, 2, NULL, 0, 'Let cool for 10 minutes then gently twist them off the foil lined cones.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FB044F7CD071F46887796628C3431BC', X'5535AF1836E4CB4CBD67D5633C82D0DE', X'5535AF1836E4CB4CBD67D5633C82D0DE', 0, NULL, NULL, 2, NULL, 0, 'Toss together the diced turkey, dried cranberries, celery, toasted almond slivers, and mayonnaise.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDE4B511D1E2C049A2DAE6BB976AD82D', X'5535AF1836E4CB4CBD67D5633C82D0DE', X'5535AF1836E4CB4CBD67D5633C82D0DE', 1, NULL, NULL, 2, NULL, 0, 'Spoon some of the turkey salad into a crescent roll cornucopia then set it on a plate and add more turkey salad coming out of the cornucopia.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B62EF47D205A794F8EE5F90FDA90AE8D', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9CF754C557D2748A96BDFF585CCDCEF', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 1, NULL, NULL, 2, NULL, 0, 'Spray the inside of both halves of the turkey cake pan with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8727BBA0A2E484A950CD9B24115D7FF', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle about 1/3rd of the shredded Parmigiano Reggiano cheese into the pan (except over the turkey''s head).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B32207DB466D1C4E9150871A03824C2A', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 3, NULL, NULL, 2, NULL, 0, 'Pour flour into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'167262726B82D444A9B9C118A28CDA81', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 4, NULL, NULL, 2, NULL, 0, 'Add baking powder and salt and stir to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F7ACB5265817A4D99FA118DDCBABEF3', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 5, NULL, NULL, 2, NULL, 0, 'Add the cheddar cheese cubes and the remaining Parmigiano Reggiano cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FC84E2DF2362542BECC2C5CD61CBC41', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 6, NULL, NULL, 2, NULL, 0, 'Toss to coat the cheese in the flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C18553652F78B498854D35B99DEE705', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 7, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk together the milk, sour cream, butter, and egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3622C18BFFD7364AB3891F1BD64F88CD', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 8, NULL, NULL, 2, NULL, 0, 'Pour it over the flour mixture and fold just until combined. (Do not over mix or the bread will be dense).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'894F5FF5EF79014CBEFC1E4E4C913524', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 9, NULL, NULL, 2, NULL, 0, 'Bake for 50-60 minutes until deep golden brown on the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CC0AE0A47DDF74E90A9B97F1971EB10', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 10, NULL, NULL, 2, NULL, 0, 'If you plan to serve the two halves separately, allow the bread to cool in the pan for 20 minutes then remove and set on a cooling rack until completely cooled.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FFCAF75C6F02B41B92E3424BB3BA4CE', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 11, NULL, NULL, 2, NULL, 0, 'If you plan to put the two halves together then allow the bread to cool in the pan for 1 hour, then use a serrated knife to cut the domed tops off the bread loaves.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71B30AC752ECF54DB2EE7E860928EF80', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 12, NULL, NULL, 2, NULL, 0, 'Remove from pan and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'949E11515FBF7E439D05378966A1F913', X'2E75EAAE07CF134CB895C8556658DDB7', X'2E75EAAE07CF134CB895C8556658DDB7', 13, NULL, NULL, 2, NULL, 0, 'Slather a layer of cheddar cheese spread over one loaf then press the two halves together forming a 3-D turkey.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF6391C42E2E2F41B20962C0D9C36155', X'BDACA3BDD283394CB23FD9323B25523C', X'BDACA3BDD283394CB23FD9323B25523C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350-degree Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3110DC266306749BB1F0FCE59C36A57', X'BDACA3BDD283394CB23FD9323B25523C', X'BDACA3BDD283394CB23FD9323B25523C', 1, NULL, NULL, 2, NULL, 0, 'Blend flour, baking soda, baking powder, and salt together. Then set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB0CFB5E42C8084895DB7AE508DE503E', X'BDACA3BDD283394CB23FD9323B25523C', X'BDACA3BDD283394CB23FD9323B25523C', 2, NULL, NULL, 2, NULL, 0, 'Beat butter, peanut butter, brown sugar, and granulated sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0186DEB6C7DC04AAE1340D7D8B120AF', X'BDACA3BDD283394CB23FD9323B25523C', X'BDACA3BDD283394CB23FD9323B25523C', 3, NULL, NULL, 2, NULL, 0, 'Add egg and mix to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB59003840A93443B97F338E564518B0', X'BDACA3BDD283394CB23FD9323B25523C', X'BDACA3BDD283394CB23FD9323B25523C', 4, NULL, NULL, 2, NULL, 0, 'Stir in the vanilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1917875668B6724AAF8BDAAC9841CB31', X'BDACA3BDD283394CB23FD9323B25523C', X'BDACA3BDD283394CB23FD9323B25523C', 5, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients and mix just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77016464E99D1747AFDA797F32A6E986', X'BDACA3BDD283394CB23FD9323B25523C', X'BDACA3BDD283394CB23FD9323B25523C', 6, NULL, NULL, 2, NULL, 0, 'Scoop tablespoon-size balls of dough into 22 of the brownie pops molds cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6C8403B6F2A8F4BACA87080509AE939', X'BDACA3BDD283394CB23FD9323B25523C', X'BDACA3BDD283394CB23FD9323B25523C', 7, NULL, NULL, 2, NULL, 0, 'Bake for 16-20 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C63A2A6FF03CBA45A6ED7A59FB7BDFFE', X'BDACA3BDD283394CB23FD9323B25523C', X'BDACA3BDD283394CB23FD9323B25523C', 8, NULL, NULL, 2, NULL, 0, 'Remove and allow the cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'732A3900538A7141A2C84A01FB494ED4', X'BDACA3BDD283394CB23FD9323B25523C', X'BDACA3BDD283394CB23FD9323B25523C', 9, NULL, NULL, 2, NULL, 0, 'Pop the cookies out of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE5465AF884D41468E4C07F54174BFBC', X'409C766E5CCA924383F8DA4FBEA3E0E7', X'409C766E5CCA924383F8DA4FBEA3E0E7', 0, NULL, NULL, 2, NULL, 0, 'Fill disposable pastry bags with the Candy Melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A786A8BB264C2F4EAEBFF83B431F0395', X'409C766E5CCA924383F8DA4FBEA3E0E7', X'409C766E5CCA924383F8DA4FBEA3E0E7', 1, NULL, NULL, 2, NULL, 0, 'Pipe the outer edge of the turkey''s tail feathers using the Light Cocoa Candy Melts onto a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7B293A77E39F94AA611C6ADC4245799', X'409C766E5CCA924383F8DA4FBEA3E0E7', X'409C766E5CCA924383F8DA4FBEA3E0E7', 2, NULL, NULL, 2, NULL, 0, 'Pipe the interior of the feathers using the Peanut Butter Candy Melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DA432C8BDB4424C86DE025D364ED8ED', X'409C766E5CCA924383F8DA4FBEA3E0E7', X'409C766E5CCA924383F8DA4FBEA3E0E7', 3, NULL, NULL, 2, NULL, 0, 'Freeze for about 5 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A3B070FDA6C194C953782D58A471674', X'409C766E5CCA924383F8DA4FBEA3E0E7', X'409C766E5CCA924383F8DA4FBEA3E0E7', 4, NULL, NULL, 2, NULL, 0, 'Repeat until you have 22 tail feather candies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C6AF591D679644898575839529DFD3A', X'409C766E5CCA924383F8DA4FBEA3E0E7', X'409C766E5CCA924383F8DA4FBEA3E0E7', 5, NULL, NULL, 2, NULL, 0, 'Use Light Cocoa Candy Melts to attach two candy eyes, an orange sunflower beak, a red sunflower wattle, two orange leaf sprinkle feet, and two or three orange Jimmie head feathers onto the one side of the peanut butter cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73E56150AC60294E8B84190D01EDC3ED', X'409C766E5CCA924383F8DA4FBEA3E0E7', X'409C766E5CCA924383F8DA4FBEA3E0E7', 6, NULL, NULL, 2, NULL, 0, 'Attach the tail feathers to the opposite side so that the tops of the feathers rise above the top of the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC66ABDB5974AA47BCEA37BC3C711A25', X'409C766E5CCA924383F8DA4FBEA3E0E7', X'409C766E5CCA924383F8DA4FBEA3E0E7', 7, NULL, NULL, 2, NULL, 0, 'Set aside until all the decorations have attached firmly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2949D4F87371FE4E82920F561063405D', X'409C766E5CCA924383F8DA4FBEA3E0E7', X'409C766E5CCA924383F8DA4FBEA3E0E7', 8, NULL, NULL, 2, NULL, 0, 'Store in an airtight container until Thanksgiving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E659717DB96480418FC778EDAF6BCF65', X'D389E7251186904E84E371F0E66B33E2', X'D389E7251186904E84E371F0E66B33E2', 0, NULL, NULL, 2, NULL, 0, 'Line a 9 x 13 inch baking pan with non-stick tin foil or spread butter inside the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7371C76056C1F94EB5C0F4933D3DE973', X'D389E7251186904E84E371F0E66B33E2', X'D389E7251186904E84E371F0E66B33E2', 1, NULL, NULL, 2, NULL, 0, 'Melt butter and marshmallows in a saucepan over medium-low heat (or in the microwave).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6EFA246D5902F43B18D4F82D58463D2', X'D389E7251186904E84E371F0E66B33E2', X'D389E7251186904E84E371F0E66B33E2', 2, NULL, NULL, 2, NULL, 0, 'Stir in the freeze-dried apples, chopped dried apple rings, and Cinnamon Pebbles Cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16708DE13D76B8418104338415660B76', X'D389E7251186904E84E371F0E66B33E2', X'D389E7251186904E84E371F0E66B33E2', 3, NULL, NULL, 2, NULL, 0, 'Pour into the prepared pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C67B0E023F727442B937D4A3ECAAAE85', X'D389E7251186904E84E371F0E66B33E2', X'D389E7251186904E84E371F0E66B33E2', 4, NULL, NULL, 2, NULL, 0, 'Flatten into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A9A89FC1FE14843B59A0E5673235CBA', X'D389E7251186904E84E371F0E66B33E2', X'D389E7251186904E84E371F0E66B33E2', 5, NULL, NULL, 2, NULL, 0, 'Set aside for about an hour until the cereal treats firm up.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A039A7741F96A844B62CD3969E449ACB', X'D389E7251186904E84E371F0E66B33E2', X'D389E7251186904E84E371F0E66B33E2', 6, NULL, NULL, 2, NULL, 0, 'Remove from pan and cut into 24 squares.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32C83E651A93EF428A994616AA37575D', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 0, NULL, NULL, 2, NULL, 0, 'Fill a plastic shot glass with chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67DE896D0E00394B87E4E2F418C26CE0', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 1, NULL, NULL, 2, NULL, 0, 'Set aside for 3-5 minutes (if your chocolate is thin it will take 5 minutes to create a thick enough coating on the glass).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7D19859341CDE4F82D91565D8DC60CD', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 2, NULL, NULL, 2, NULL, 0, 'Turn shot glass upside down and allow the wet chocolate to drip out (tapping the cup as needed to help the chocolate fall out more quickly).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE7BB32DEE334541B9EFD43D7DCB12FB', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 3, NULL, NULL, 2, NULL, 0, 'Scrape the chocolate off the opening of the glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B8007E9FAC37804D95CD008AAA0C8686', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 4, NULL, NULL, 2, NULL, 0, 'Set the glass open side down on a parchment-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B936AC8F2B1C242BF4943EF074F67A3', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 5, NULL, NULL, 2, NULL, 0, 'If using pure tempered chocolate you can either leave the chocolate cup at room temperature or refrigerate it for 5-10 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77A46E6BDA964246A5FC4187B5D21C38', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 6, NULL, NULL, 2, NULL, 0, 'If using candy melts, freeze the chocolate cup for about 5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41635D5FC7AEFD42BB2767C466CC86AD', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 7, NULL, NULL, 2, NULL, 0, 'Once the chocolate hardens, pull the chocolate out of the glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7098426B662E442B2D3D61A44761454', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 8, NULL, NULL, 2, NULL, 0, 'Use melted chocolate as `glue` to attach two candy eyes and one orange sunflower beak and one red sunflower wattle to your cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEEE912DCD1D7A41AB6C5FC89934A91D', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 9, NULL, NULL, 2, NULL, 0, 'Pipe two 1 1/2 inch long chocolate legs and two feet onto a parchment paper lined baking sheet for each turkey, making several extra sets in case of breakage.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0DD159E91EA0B458F540ED052AA987B', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 10, NULL, NULL, 2, NULL, 0, 'Let the chocolate set until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F388E8F00D65D499C3D059CDA8300F1', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 11, NULL, NULL, 2, NULL, 0, 'Attach two legs to the base of each chocolate cup using melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'618DF5DB50BC254CA52CF04460319679', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 12, NULL, NULL, 2, NULL, 0, 'Attach two feet to the ends of the legs, holding them in place until the stay upright.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4385BA474999C84CB362C945160011CB', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 13, NULL, NULL, 2, NULL, 0, 'Roll out candy clay to 1/16th-inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'574BB72D4A5BA74F9660EA2331A4D863', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 14, NULL, NULL, 2, NULL, 0, 'Cut out 5 long teardrop-shaped feathers for each turkey.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'251B6C1B43A8E04FA96A8F8D32F194DD', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 15, NULL, NULL, 2, NULL, 0, 'Press the pointed ends of 5 candy clay feathers together forming a fan of feathers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D27D324747196B4BB5AA64292F95D853', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 16, NULL, NULL, 2, NULL, 0, 'If needed, you can brush a tiny bit of water on the edges of the candy clay feathers to get them to stick to each other.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E155F5DEB4591428A5183566C38C592', X'5F8C2174E0936E4EA417A23EE7B1FE95', X'5F8C2174E0936E4EA417A23EE7B1FE95', 17, NULL, NULL, 2, NULL, 0, 'Let the feathers dry for 30 minutes then use melted chocolate to attach the feathers to the upper edge of the back of each chocolate cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C90AC9ABCAFFD04B8A74B0D7C9386417', X'FC8963E32A8E2D4F8A9A2DCD2839C98A', X'FC8963E32A8E2D4F8A9A2DCD2839C98A', 0, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese using an electric mixer until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73229B29397E354787643BEAE18EA0DA', X'FC8963E32A8E2D4F8A9A2DCD2839C98A', X'FC8963E32A8E2D4F8A9A2DCD2839C98A', 1, NULL, NULL, 2, NULL, 0, 'Add powdered sugar and beat until combined. Add the pumpkin, vanilla, and pie spice and beat just until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C13C602159E5D44BA5E07DB2C413A4C0', X'FC8963E32A8E2D4F8A9A2DCD2839C98A', X'FC8963E32A8E2D4F8A9A2DCD2839C98A', 2, NULL, NULL, 2, NULL, 0, 'Spoon in the whipped cream and beat until fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7896F79DACB3D34CA411B9EF79FE2B20', X'FC8963E32A8E2D4F8A9A2DCD2839C98A', X'FC8963E32A8E2D4F8A9A2DCD2839C98A', 3, NULL, NULL, 2, NULL, 0, 'Spoon or pipe into the chocolate turkey cups just before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBDDC2E68C42FB4390F791600ACBB410', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 0, NULL, NULL, 2, NULL, 0, 'Dust a cutting board and rolling pin with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F2255E5048FA04FA5C3AD1DABF1DE64', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 1, NULL, NULL, 2, NULL, 0, 'Un-fold the puff pastry sheet on the cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDEE62AB05E66343AE5B1143222F8A0B', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 2, NULL, NULL, 2, NULL, 0, 'Roll out the dough to about 12 inches by 14 inches.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA1C48607763B3488830F214BDBDB4AC', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 3, NULL, NULL, 2, NULL, 0, 'Cut the dough sheet into six squares.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DEBC09E5D617748B341CB4A2FC0E61C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 4, NULL, NULL, 2, NULL, 0, 'Drape one square over the pumpkin cavity in the baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BB4BB91BCCD3D428F324D1625BBBA68', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 5, NULL, NULL, 2, NULL, 0, 'Wiggle the dough into the cavity leaving the excess to hang over the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E3AD9F4C2BC53478A06F83B92F919B6', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 6, NULL, NULL, 2, NULL, 0, 'Spoon in about 1/4 cup of pie filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9835F54897FC0A41A9FC5570236201E4', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 7, NULL, NULL, 2, NULL, 0, 'Whisk together egg white and water to make an egg wash.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'466E1D0533DBA64E8948629351FB4234', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 8, NULL, NULL, 2, NULL, 0, 'Fold the excess dough over the pie filling brushing it with egg wash as needed for the pieces to stick together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E496EE3BC8F474DB8229F2C25816A96', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 9, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A956070E8C3D5D47861F07194C04A036', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 10, NULL, NULL, 2, NULL, 0, 'Brush egg wash over the tops of the dough and sprinkle sugar over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'361F91BC34D57A44ABC28E5186E09EEA', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 11, NULL, NULL, 2, NULL, 0, 'Freeze for 15-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEC2CA4AED99F948A578081AFE3DBCAA', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 12, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat oven to 400 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D2631AC633E634DBD61E25D2D802F07', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 13, NULL, NULL, 2, NULL, 0, 'Bake pies for 20-26 minutes until the tops are deep golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA09F2FFA46C2740BF49F374A2238535', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 14, NULL, NULL, 2, NULL, 0, 'Remove pies from pan immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'796321435F92144A83B862322F1BB0F5', X'49A1D4A2342CBC4A9926D2FEA65EE47C', X'49A1D4A2342CBC4A9926D2FEA65EE47C', 15, NULL, NULL, 2, NULL, 0, 'Let cool slightly before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49E831A3DA8C77498C398BBAF7159D65', X'0F417F86674ABC4DA37A2BDC100852BD', X'0F417F86674ABC4DA37A2BDC100852BD', 0, NULL, NULL, 2, NULL, 0, 'Unwrap the cereal treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3C837F9AE6040428AED2AAC1F65D57A', X'0F417F86674ABC4DA37A2BDC100852BD', X'0F417F86674ABC4DA37A2BDC100852BD', 1, NULL, NULL, 2, NULL, 0, 'Insert 6 candy corn in a fan shape across the top of each cereal treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0035D3504AB8CD4B84DA32EF56EE56A3', X'0F417F86674ABC4DA37A2BDC100852BD', X'0F417F86674ABC4DA37A2BDC100852BD', 2, NULL, NULL, 2, NULL, 0, 'Press two jumbo candy eyes onto each treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7482D470EBF0C408F9CCDE18F8419E5', X'0F417F86674ABC4DA37A2BDC100852BD', X'0F417F86674ABC4DA37A2BDC100852BD', 3, NULL, NULL, 2, NULL, 0, 'Cut orange slices in half so you have two half-moon shaped pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6DF10B8E10D4AA4EB80D2887FC176999', X'0F417F86674ABC4DA37A2BDC100852BD', X'0F417F86674ABC4DA37A2BDC100852BD', 4, NULL, NULL, 2, NULL, 0, 'Cut each piece into 3 triangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA3668C6FD165D4F8E4F1B307AE04C28', X'0F417F86674ABC4DA37A2BDC100852BD', X'0F417F86674ABC4DA37A2BDC100852BD', 5, NULL, NULL, 2, NULL, 0, 'Press one triangle onto each cereal treat just below the two eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C1DCB48CF48E0478718C895D49E6571', X'0F417F86674ABC4DA37A2BDC100852BD', X'0F417F86674ABC4DA37A2BDC100852BD', 6, NULL, NULL, 2, NULL, 0, 'Cut the red jelly beans in half lengthwise so you have two bean-shaped halves.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'344EC7EDB5167C4EBC6706E147CD1413', X'0F417F86674ABC4DA37A2BDC100852BD', X'0F417F86674ABC4DA37A2BDC100852BD', 7, NULL, NULL, 2, NULL, 0, 'Press one half onto each cereal treat next to the orange slice beak to create the turkey''s waddle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D22CD005529624EA80D9C445F5DC8C9', X'5F20CDA8FC99D749B6813CC812642D35', X'5F20CDA8FC99D749B6813CC812642D35', 0, NULL, NULL, 2, NULL, 0, 'Toss melted orange candy coating with popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BA4DAA566DC704ABE33287631034BC4', X'5F20CDA8FC99D749B6813CC812642D35', X'5F20CDA8FC99D749B6813CC812642D35', 1, NULL, NULL, 2, NULL, 0, 'Spread on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D45DE5891AE454B81C0A2E9B3C9F4B5', X'5F20CDA8FC99D749B6813CC812642D35', X'5F20CDA8FC99D749B6813CC812642D35', 2, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24C4D60E7CA78B4598659F1B9BF2D6CD', X'5F20CDA8FC99D749B6813CC812642D35', X'5F20CDA8FC99D749B6813CC812642D35', 3, NULL, NULL, 2, NULL, 0, 'Break apart into small popcorn sized pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74E63E1258E62242A6F7B00FA8DD338B', X'BD596E59768F07459773FD1E5ABA40CE', X'BD596E59768F07459773FD1E5ABA40CE', 0, NULL, NULL, 2, NULL, 0, 'Repeat above tossing popcorn in yellow candy coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'603172575D00B242AD78F6DD999916AB', X'4710E53D9441FD4A8B41A52BCDBE677F', X'4710E53D9441FD4A8B41A52BCDBE677F', 0, NULL, NULL, 2, NULL, 0, 'Do it again using white.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0A1491817011349B378FB87FDB33A4D', X'7ED05A3A297AA74392208CADAED57EA7', X'7ED05A3A297AA74392208CADAED57EA7', 0, NULL, NULL, 2, NULL, 0, 'Equally divide the popcorn into two 18 inch pastry bags, layering the white then yellow then orange.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82688F471DF10948B65E501C17B560AD', X'7ED05A3A297AA74392208CADAED57EA7', X'7ED05A3A297AA74392208CADAED57EA7', 1, NULL, NULL, 2, NULL, 0, 'Tie with ribbon so each bag looks like a candy corn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2A169CB985A414C8F9D6383C530D129', X'C58E12D2B6441243A034C9B74A2E19FC', X'C58E12D2B6441243A034C9B74A2E19FC', 0, NULL, NULL, 2, NULL, 0, 'Roll out your cookie dough (I chill the dough first) and cut out cookies with the cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0493F12C0266A4C8F04FBEA2498DAA9', X'C58E12D2B6441243A034C9B74A2E19FC', X'C58E12D2B6441243A034C9B74A2E19FC', 1, NULL, NULL, 2, NULL, 0, 'With a spatula, place dough pieces on the parchment lined cookie sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29AEAD26080460438B1EBB217FCA55A5', X'C58E12D2B6441243A034C9B74A2E19FC', X'C58E12D2B6441243A034C9B74A2E19FC', 2, NULL, NULL, 2, NULL, 0, 'I usually place the cookie trays in the freezer to help the cookies hold their shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'150140E0CC786E4488A738882DF7334A', X'C58E12D2B6441243A034C9B74A2E19FC', X'C58E12D2B6441243A034C9B74A2E19FC', 3, NULL, NULL, 2, NULL, 0, 'Bake according to recipe.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08DB51C2BAAE0D47A603BC3E51F88740', X'C58E12D2B6441243A034C9B74A2E19FC', X'C58E12D2B6441243A034C9B74A2E19FC', 4, NULL, NULL, 2, NULL, 0, 'Let cookies cool for about 10 minutes and then place on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFD1940F0EC90F4EB13879C33C71BE07', X'C58E12D2B6441243A034C9B74A2E19FC', X'C58E12D2B6441243A034C9B74A2E19FC', 5, NULL, NULL, 2, NULL, 0, 'Fill a decorating bag with the ivory tinted royal icing, thin enough to fill the `stick` section of the cookie, and fitted with a #2 tip. Let it dry for about 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E8B4AA3AB0798A4390BAC9A0D3AE6B9E', X'C58E12D2B6441243A034C9B74A2E19FC', X'C58E12D2B6441243A034C9B74A2E19FC', 6, NULL, NULL, 2, NULL, 0, 'Fill a decorating bag with the green (leaf green with a few drops of gold) royal icing and a #2 tip, and apply it the the top section of the apple. Let it dry for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0881B398BA19B04B880C1196DC999BFE', X'C58E12D2B6441243A034C9B74A2E19FC', X'C58E12D2B6441243A034C9B74A2E19FC', 7, NULL, NULL, 2, NULL, 0, 'Fill a decorating bag with the caramel colored (warm brown with a bit of gold) royal icing and a #3 tip, filling in the remainder of the apple. Allow to dry overnight, about 8 hours. (If you''re using the candy melts, decorate the cookie with it at this point, instead of the royal icing.)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3CAB13CBE09F0478ACA640111A7EB2E', X'C58E12D2B6441243A034C9B74A2E19FC', X'C58E12D2B6441243A034C9B74A2E19FC', 8, NULL, NULL, 2, NULL, 0, 'With the green icing and a #1 tip, outline the apple details.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91770513EA5C6D4C845F0CEA309A3C16', X'C58E12D2B6441243A034C9B74A2E19FC', X'C58E12D2B6441243A034C9B74A2E19FC', 9, NULL, NULL, 2, NULL, 0, 'With the caramel icing and a #1 tip, add the caramel details.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03496D1AC1411244B8B8473EFEBCB91A', X'C58E12D2B6441243A034C9B74A2E19FC', X'C58E12D2B6441243A034C9B74A2E19FC', 10, NULL, NULL, 2, NULL, 0, 'Use the food marker and add facial details.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'700F020DB9B2A243A7E64B9A3E8ACF81', X'C58E12D2B6441243A034C9B74A2E19FC', X'C58E12D2B6441243A034C9B74A2E19FC', 11, NULL, NULL, 2, NULL, 0, 'Finally, add the cheeks using the red royal icing and a #2 tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B006393E5104F342A82B594D20B8AF27', X'C58E12D2B6441243A034C9B74A2E19FC', X'C58E12D2B6441243A034C9B74A2E19FC', 12, NULL, NULL, 2, NULL, 0, 'Have fun and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'498279A6345DFD4BAD859FF721DE0EA5', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 0, NULL, NULL, 2, NULL, 0, 'CAKE CHIPS:');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E13D15C4D947344BB1F3C1A1E342C0A9', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61972E0AD97F194EB51058183B9BAB86', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 2, NULL, NULL, 2, NULL, 0, 'Spray two 9 inch round baking pans with cooking spray and line the bottoms with a round of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC94C6BBB531594FBF501F05F1A845F5', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 3, NULL, NULL, 2, NULL, 0, 'Combine cake mix, egg, water and oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7531471F17BAB47B788887104693375', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 4, NULL, NULL, 2, NULL, 0, 'Beat it on low speed using an electric mixer for 30 seconds, then beat on medium speed for 15 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA114B77E2F32C42A62852E6493AAEE5', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 5, NULL, NULL, 2, NULL, 0, 'Evenly divide the batter and spread it into the bottom of the cake pans and bake for about 10 minutes until the tops just begin to brown. When you press a finger in the center of the cake, the indentation should spring back.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E45AB8565960584394996D9562A8B71E', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 6, NULL, NULL, 2, NULL, 0, 'Allow the cakes to cool for 5 minutes, then remove them from the pans and peel off the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6523E97B8589234F9295160AF28B943C', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 7, NULL, NULL, 2, NULL, 0, 'Cut each cake into 8 wedges.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4DEB3A1C8B94594B878AC8E21AF6A9E5', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 8, NULL, NULL, 2, NULL, 0, 'Spray a baking sheet with coconut oil spray (or vegetable oil spray) and set the wedges on the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6514A26FD6DE8549B77A6432B2BB159C', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 9, NULL, NULL, 2, NULL, 0, 'Spray the coconut oil over top of the wedges and return them to the oven for 10 minutes. Remove and flip the wedges over and bake for another 10-12 minutes until golden brown on both sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D79B62D6FCCC8F4E810A73A4E1989A37', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 10, NULL, NULL, 2, NULL, 0, 'Remove and set on a cooling rack. Cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CC34174D75C95428E8F0D4D597BE026', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 11, NULL, NULL, 2, NULL, 0, 'CARAMEL GLAZED APPLES & CARAMEL SAUCE:');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BF08EC8F08AF74EA38E83C0F4D36BCF', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 12, NULL, NULL, 2, NULL, 0, 'Peel apples and cut into 1/4 - 1/2 inch cubes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E6DE1745CEC4A49A2A1A9796DEE8F04', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 13, NULL, NULL, 2, NULL, 0, 'Place in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5D7C8B140CB5D488699DC4172258A05', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 14, NULL, NULL, 2, NULL, 0, 'Toss them with cinnamon apple juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'476DBE3AB9C0B44687FA8F33E9886884', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 15, NULL, NULL, 2, NULL, 0, 'In a skillet, melt butter over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B708C8C1C7C4F148AEF479D8D34F4243', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 16, NULL, NULL, 2, NULL, 0, 'Add brown sugar and stir constantly until the sugar begins to dissolve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DDFE74A7C396349911834B5572B081B', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 17, NULL, NULL, 2, NULL, 0, 'Add the apples and toss to coat. Continue to stir until the sugar completely dissolves.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBFCF7A348EA5448AC782C5204837543', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 18, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium low and let apples simmer for 4-6 minutes, stirring occasionally, until the apples are tender.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'211F4221DABC3B4A942405D3E4C5C429', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 19, NULL, NULL, 2, NULL, 0, 'Pour the apples into a strainer set over a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86F7842844783349B54C2F306FD25F83', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 20, NULL, NULL, 2, NULL, 0, 'Let the syrup drain then pour it back into the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48E50487CE5C044C92271AE2F05714A8', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 21, NULL, NULL, 2, NULL, 0, 'Increase heat to medium, and stir constantly until thickened into a nice caramel sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A611F8E8CCA4A44A9A65B42EF9E6082', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 22, NULL, NULL, 2, NULL, 0, 'CHEESECAKE FLUFF:');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3748A1EF78755F48A8A2A158EAC0B97C', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 23, NULL, NULL, 2, NULL, 0, 'Beat cream cheese with sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D33BB4A99512841818AB58E93D98FAD', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 24, NULL, NULL, 2, NULL, 0, 'Add whipped topping and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B7C6B27A43930542B9A889179573A06F', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 25, NULL, NULL, 2, NULL, 0, 'ASSEMBLE NACHOS:');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2692257FD84924D94C929E78BA2BA0A', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 26, NULL, NULL, 2, NULL, 0, 'Spread or pipe the cheesecake fluff over top of the cake chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D737CA2CA41AA4449B68B5F164FA6D58', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 27, NULL, NULL, 2, NULL, 0, 'Sprinkle on the caramel glazed apples.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FF8BF39889C1943BD8B3B6049B02579', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 28, NULL, NULL, 2, NULL, 0, 'Drizzle with some chocolate sauce and the cooled caramel sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B79C70C05F0C094D82D4D56171B3D7BB', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 29, NULL, NULL, 2, NULL, 0, 'Sprinkle on toasted pecan pieces and M&M''s® Pecan Pie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D56E08A346AD0346B01861A943BD6D6C', X'BEA880A45A52B543BBA6060BFFC2D271', X'BEA880A45A52B543BBA6060BFFC2D271', 30, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0D8CDAC6DBA524A8C291D5FCFF843A9', X'FE5FE75A1A326E4482802A29C764CDE1', X'FE5FE75A1A326E4482802A29C764CDE1', 0, NULL, NULL, 2, NULL, 0, 'For the Cake');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE915110D4C2824EB8675FF61706B189', X'9664643AA6B5A24FAACF6F085AAE6CD3', X'9664643AA6B5A24FAACF6F085AAE6CD3', 0, NULL, NULL, 2, NULL, 0, 'PREHEAT oven to 350° F. Spray whoopie pie pan with Bakers Joy or other Bakers Spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC6F72366182FD4C9F90FCE2981C8A2A', X'9664643AA6B5A24FAACF6F085AAE6CD3', X'9664643AA6B5A24FAACF6F085AAE6CD3', 1, NULL, NULL, 2, NULL, 0, 'COMBINE flour, baking soda, baking powder, and cinnamon and salt in medium bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A932C536C1E2F04896839D00D4FBE747', X'9664643AA6B5A24FAACF6F085AAE6CD3', X'9664643AA6B5A24FAACF6F085AAE6CD3', 2, NULL, NULL, 2, NULL, 0, 'Beat butter and sugar in large mixer bowl on medium speed for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4488A29F2630040B4D1977ED2B07D62', X'9664643AA6B5A24FAACF6F085AAE6CD3', X'9664643AA6B5A24FAACF6F085AAE6CD3', 3, NULL, NULL, 2, NULL, 0, 'Add eggs, one at a time, beating well after each addition.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CEF44DBC9D9B642B0CF2679B26DC806', X'9664643AA6B5A24FAACF6F085AAE6CD3', X'9664643AA6B5A24FAACF6F085AAE6CD3', 4, NULL, NULL, 2, NULL, 0, 'Add pumpkin and vanilla extract; beat until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F6B4E0843E32544B8CFF47DCFF83BA7', X'9664643AA6B5A24FAACF6F085AAE6CD3', X'9664643AA6B5A24FAACF6F085AAE6CD3', 5, NULL, NULL, 2, NULL, 0, 'Stir in flour mixture until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'639F1B5F485F1F4D93E7EF7871117D6D', X'9664643AA6B5A24FAACF6F085AAE6CD3', X'9664643AA6B5A24FAACF6F085AAE6CD3', 6, NULL, NULL, 2, NULL, 0, 'Drop by heaping measuring teaspoons into prepared whoopie pie pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D46EAAC663D07498252757CAAC6C230', X'9664643AA6B5A24FAACF6F085AAE6CD3', X'9664643AA6B5A24FAACF6F085AAE6CD3', 7, NULL, NULL, 2, NULL, 0, 'BAKE for 10 to 13 minutes or until springy to the touch.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAB2FCFE0417434E9D45295FA0F3FBD1', X'9664643AA6B5A24FAACF6F085AAE6CD3', X'9664643AA6B5A24FAACF6F085AAE6CD3', 8, NULL, NULL, 2, NULL, 0, 'Remove and cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E1F31CBD7BCC54D96834AC4135A9DB9', X'AEB651869A45014CAB90EF92024D6742', X'AEB651869A45014CAB90EF92024D6742', 0, NULL, NULL, 2, NULL, 0, 'BEAT cream cheese, butter and vanilla extract in small mixer on medium speed until combined. Gradually beat in powdered sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBFC130D0697934E8219BA50BE14CC26', X'AEB651869A45014CAB90EF92024D6742', X'AEB651869A45014CAB90EF92024D6742', 1, NULL, NULL, 2, NULL, 0, 'SCOOP a heaping tablespoon of filling onto flat side of one cookie; top with flat side of second cookie to make a sandwich.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06D7BAC23B3A72459807EFB80643FE8F', X'AEB651869A45014CAB90EF92024D6742', X'AEB651869A45014CAB90EF92024D6742', 2, NULL, NULL, 2, NULL, 0, 'Repeat with remaining cookies and filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'829E9D3DAFA17943B70DF9D81FB7B199', X'AEB651869A45014CAB90EF92024D6742', X'AEB651869A45014CAB90EF92024D6742', 3, NULL, NULL, 2, NULL, 0, 'Add fondant decorations.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F2252798AB6044D950CEFAFCAA8372C', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees. Butter the cavities in your molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FC208514C5C784B8245D9CBB878ACEC', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 1, NULL, NULL, 2, NULL, 0, 'Heat heavy whipping cream and chocolate on high in the microwave for 20-second intervals, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F0A2297E7500140B2CE562F35C2F0C9', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 2, NULL, NULL, 2, NULL, 0, 'Set aside to cool slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8F670A463B42D449CCFB4DF8A048940', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 3, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F2B6CCDBB276D43A1F44B4FD5CAB88A', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 4, NULL, NULL, 2, NULL, 0, 'Add sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D803639ECFA5146914B9219923DB463', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 5, NULL, NULL, 2, NULL, 0, 'Add Eggland''s Best Eggs, one at a time until well incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BAB34C64F18414C9EC271A8832BD0A6', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 6, NULL, NULL, 2, NULL, 0, 'Add the vanilla then pour in the chocolate and beat just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16ADCC1C7775BE4AA79CC2CDB39A8FBA', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 7, NULL, NULL, 2, NULL, 0, 'Set the silicone molds on a baking sheet. Pour the chocolate cheesecake filling into the molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52142E4703E9954EBC65FE80F0142488', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 8, NULL, NULL, 2, NULL, 0, 'Bake for 22 minutes, then turn the oven off, open the oven door, and allow the cheesecake to remain in the oven for 10 minutes or until they look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B7030CF96DD7941BAB162FC074D77FA', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 9, NULL, NULL, 2, NULL, 0, 'Remove them from the oven and allow them to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'486317B1D19A104FA20085D63312F983', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 10, NULL, NULL, 2, NULL, 0, 'Pop them in the freezer for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3A608A6CBDCF5418A95337F4525968A', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 11, NULL, NULL, 2, NULL, 0, 'Once frozen, un-mold them by pulling back on the silicone mold and pushing the cheesecake out.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7861ED72E3411429616B591436C2567', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 12, NULL, NULL, 2, NULL, 0, 'Put them on small individual serving dishes and allow them to thaw.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84D3749C7F5CD346B8111B098E0E72C2', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 13, NULL, NULL, 2, NULL, 0, 'At this point, you can store the cheesecakes in the refrigerator for up to five days or put them back in the freezer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C79AF6AC494FD74E9B8E017564E21C91', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 14, NULL, NULL, 2, NULL, 0, 'To make the turkey''s feathers, use a fluted biscuit cutter and simply press down onto the graham cracker and they will cut. Cut out about 3/4 of the circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62640761CD20064CAC50A9BF5C5E135E', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 15, NULL, NULL, 2, NULL, 0, 'When you are ready to decorate, press one tail feather onto the back of each cheesecake then add two candy eyes, one orange sunflower seed beak, and one red sunflower seed waddle. Do not decorate frozen cheesecakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A9D1B12CA8D1448B539B2EFEA5DA34F', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 16, NULL, NULL, 2, NULL, 0, 'You can decorate your cheesecake turkeys a few hours before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'512795B57D03D544BE5185527FC159F0', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 17, NULL, NULL, 2, NULL, 0, 'Keep refrigerated.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'400C627688C67D4FBF9853116D7C4B02', X'E64B179B920DD8499A50E05F6EA9E8FA', X'E64B179B920DD8499A50E05F6EA9E8FA', 18, NULL, NULL, 2, NULL, 0, 'Set them out at room temperature for at least 15 minutes before serving to intensify the chocolate flavor.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2EA75574FD058458F1C74A53DB03913', X'AE31C7219CB3D546859BB8D641B23F6D', X'AE31C7219CB3D546859BB8D641B23F6D', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E21DBB09B20E34D803FB86FFB1E2BC6', X'AE31C7219CB3D546859BB8D641B23F6D', X'AE31C7219CB3D546859BB8D641B23F6D', 1, NULL, NULL, 2, NULL, 0, 'Grease the leaf and pumpkin molds with shortening or butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'305E08CB518D4D4A84314C5E82E426BC', X'515ABAAF94B6CA4B85C56DBD82BC6491', X'515ABAAF94B6CA4B85C56DBD82BC6491', 0, NULL, NULL, 2, NULL, 0, 'Put 2 tablespoons of cheese into a zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F398A223F917264A96470D0CC42AB9E1', X'515ABAAF94B6CA4B85C56DBD82BC6491', X'515ABAAF94B6CA4B85C56DBD82BC6491', 1, NULL, NULL, 2, NULL, 0, 'Add a few drops of red coloring and shake to coat cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0AF2F8558BA8044DA4FDC86F7FCE6D93', X'515ABAAF94B6CA4B85C56DBD82BC6491', X'515ABAAF94B6CA4B85C56DBD82BC6491', 2, NULL, NULL, 2, NULL, 0, 'Repeat with yellow coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26821E2261E04B45B1F591AE62B09A43', X'515ABAAF94B6CA4B85C56DBD82BC6491', X'515ABAAF94B6CA4B85C56DBD82BC6491', 3, NULL, NULL, 2, NULL, 0, 'Lightly sprinkle some of the Parmesan Dipping Blend into the leaf cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA2500A48A9D1B4D841E08FD3363AF1D', X'515ABAAF94B6CA4B85C56DBD82BC6491', X'515ABAAF94B6CA4B85C56DBD82BC6491', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle the red, yellow, and orange colored cheese in the leaf cavities and just the orange cheese in the pumpkin cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B7D6596D912B94D87E81B2A983E39F8', X'102707630A6D4B4494DC4F255A92A2E6', X'102707630A6D4B4494DC4F255A92A2E6', 0, NULL, NULL, 2, NULL, 0, 'Combine flour, salt, baking powder, and Parmesan Bread Dipping Blend in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E2355CDB92AFC418605CAF3BCBB3391', X'102707630A6D4B4494DC4F255A92A2E6', X'102707630A6D4B4494DC4F255A92A2E6', 1, NULL, NULL, 2, NULL, 0, 'Add cheese and toss to coat using a fork.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'404D6E6EF33DB24CB9EF158614698363', X'102707630A6D4B4494DC4F255A92A2E6', X'102707630A6D4B4494DC4F255A92A2E6', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, mix heavy whipping cream, water, sour cream and egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0FDED7D58CFEDF439254F06D0A69D9B0', X'102707630A6D4B4494DC4F255A92A2E6', X'102707630A6D4B4494DC4F255A92A2E6', 3, NULL, NULL, 2, NULL, 0, 'Add it to the dry ingredients.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EFCD2A49827F344B80464E8DFC1E637E', X'102707630A6D4B4494DC4F255A92A2E6', X'102707630A6D4B4494DC4F255A92A2E6', 4, NULL, NULL, 2, NULL, 0, 'Fold a few times then add melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B3174851A6A0349911823EB099EC001', X'102707630A6D4B4494DC4F255A92A2E6', X'102707630A6D4B4494DC4F255A92A2E6', 5, NULL, NULL, 2, NULL, 0, 'Mix, just until blended. Batter with be lumpy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'001CC98CC52172428FCFA6608A58C75D', X'102707630A6D4B4494DC4F255A92A2E6', X'102707630A6D4B4494DC4F255A92A2E6', 6, NULL, NULL, 2, NULL, 0, 'Equally divide the batter among the 18 leaf and pumpkin cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0178881B11BE54FA4A98FC854E0AE44', X'102707630A6D4B4494DC4F255A92A2E6', X'102707630A6D4B4494DC4F255A92A2E6', 7, NULL, NULL, 2, NULL, 0, 'Grease your finger tips with some shortening or butter and flatten out the batter in the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26D85FCF13B26B4D93C21B47C6AA66EC', X'102707630A6D4B4494DC4F255A92A2E6', X'102707630A6D4B4494DC4F255A92A2E6', 8, NULL, NULL, 2, NULL, 0, 'Set leaf and pumpkin silicone molds on baking sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00FE4C7CE735434AAE74CB9EFBBE1D87', X'102707630A6D4B4494DC4F255A92A2E6', X'102707630A6D4B4494DC4F255A92A2E6', 9, NULL, NULL, 2, NULL, 0, 'Bake for 30-32 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA01B67093FF6745B1591309D9991494', X'102707630A6D4B4494DC4F255A92A2E6', X'102707630A6D4B4494DC4F255A92A2E6', 10, NULL, NULL, 2, NULL, 0, 'Remove rolls from oven, wearing oven mitts, pull on the mold, loosening the bread from the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6E1DF739AF8054688F4F57A11ABFC63', X'102707630A6D4B4494DC4F255A92A2E6', X'102707630A6D4B4494DC4F255A92A2E6', 11, NULL, NULL, 2, NULL, 0, 'Turn the molds upside down and pop out the bread.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C71012BF37AEFA4DA1F94FB119ABF2C3', X'102707630A6D4B4494DC4F255A92A2E6', X'102707630A6D4B4494DC4F255A92A2E6', 12, NULL, NULL, 2, NULL, 0, 'Serve warm or cold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72A93CD616AA7048BF419A7080F617DD', X'1F7631C9F533274F8F72BFA8D836E211', X'1F7631C9F533274F8F72BFA8D836E211', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'481153A0F98A5649A96DB3FAFDCBBEE9', X'1F7631C9F533274F8F72BFA8D836E211', X'1F7631C9F533274F8F72BFA8D836E211', 1, NULL, NULL, 2, NULL, 0, 'Butter leaf molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C37B52D120F22049B923D5075BE6F401', X'1F7631C9F533274F8F72BFA8D836E211', X'1F7631C9F533274F8F72BFA8D836E211', 2, NULL, NULL, 2, NULL, 0, 'Combine cream cheese, sugar, heavy whipping cream, eggs, and vanilla in the bowl of a food processor, pulse until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'955F879989B51045A0BF6CAD7DCAA7DE', X'1F7631C9F533274F8F72BFA8D836E211', X'1F7631C9F533274F8F72BFA8D836E211', 3, NULL, NULL, 2, NULL, 0, 'Divide cheesecake mixture into three or more bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A04115F76276A4781E45D3BA86CDD85', X'1F7631C9F533274F8F72BFA8D836E211', X'1F7631C9F533274F8F72BFA8D836E211', 4, NULL, NULL, 2, NULL, 0, 'Color one bowl orange, red, and yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D56E030326E6484889A2963B8B294649', X'1F7631C9F533274F8F72BFA8D836E211', X'1F7631C9F533274F8F72BFA8D836E211', 5, NULL, NULL, 2, NULL, 0, 'Fill each leaf mold with a swirl of orange, red, and yellow filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D62A6629640B64F9D8ED892C1AF2036', X'1F7631C9F533274F8F72BFA8D836E211', X'1F7631C9F533274F8F72BFA8D836E211', 6, NULL, NULL, 2, NULL, 0, 'Tap the mold several times to remove excess air bubbles after each scoop of cheesecake filling you add.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03640563D2054C4DADCAB88B498A5916', X'1F7631C9F533274F8F72BFA8D836E211', X'1F7631C9F533274F8F72BFA8D836E211', 7, NULL, NULL, 2, NULL, 0, 'Bake your cheesecakes for 20-24 minutes until the top forms a smooth film and the center is still giggly but not wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CF3BBD26E166B4780D8F93A167145E4', X'1F7631C9F533274F8F72BFA8D836E211', X'1F7631C9F533274F8F72BFA8D836E211', 8, NULL, NULL, 2, NULL, 0, 'Allow your cheesecakes to cool for an hour then freeze for at least 3 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99EE1307AD845A4995D52BDEE9E37ABD', X'1F7631C9F533274F8F72BFA8D836E211', X'1F7631C9F533274F8F72BFA8D836E211', 9, NULL, NULL, 2, NULL, 0, 'Un-mold cheesecakes directly onto a serving dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EE1CAF057027E4E80E6E4186BDE5340', X'1F7631C9F533274F8F72BFA8D836E211', X'1F7631C9F533274F8F72BFA8D836E211', 10, NULL, NULL, 2, NULL, 0, 'Allow to defrost in the refrigerator for a few hours before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CC98145B0EF4E4C99306309D264BF0C', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 0, NULL, NULL, 2, NULL, 0, 'Print a copy of the cupcake wrapper template, resizing it as needed to fit around your cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78DD6D8454B22048A1C2EB0BA5F0C1BB', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 1, NULL, NULL, 2, NULL, 0, 'Cut out around the template.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91423E0B352FAA4792A5796BD17C5D28', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 2, NULL, NULL, 2, NULL, 0, 'Wrap it around one of your cupcakes, to see if it is the right size. You need the template to overlap at the ends by at least 1/2 inch, so adjust the template as needed. Re-size then cut out a new template.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8181A6E7199F044980E9C63D0576AFC0', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 3, NULL, NULL, 2, NULL, 0, 'Roll out modeling chocolate on a non-stick baking mat using a fondant roller. Cut out around the template using a pastry cutter/pizza wheel. Check to make sure they fit around your cupcakes with enough overlap to seal the seam.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08AF3DA29ED19547B7FC1D7171F6A24B', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 4, NULL, NULL, 2, NULL, 0, 'Color some modeling chocolate orange and some black.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F2312741184C3449B9AFE02FC43D429', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 5, NULL, NULL, 2, NULL, 0, 'Roll them out on a non-stick mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B5521887F573B478A8639755D7B11F3', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 6, NULL, NULL, 2, NULL, 0, 'Use a teardrop cutter to cut out carrot noses and a small round plunger cutter to make eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07223337D826A24A83C386F4A08BE146', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 7, NULL, NULL, 2, NULL, 0, 'Brush water onto the backside of the eyes and nose and press them onto the modeling chocolate cupcake wrapper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8779A2866FD8EE43B7506D1348C4EA92', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 8, NULL, NULL, 2, NULL, 0, 'Press the pointed tip of a #5 pastry tip down into the dark modeling chocolate. Turn the tip upside down and tap it on your work surface, allowing the tiny black dot to fall out. You''ll need 7-9 dots per smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BCBC2326A0D2D4483E7D177FA2B05CE', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 9, NULL, NULL, 2, NULL, 0, 'Attach all but the top two dots.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AD4287386D055448E97A1602559946E', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 10, NULL, NULL, 2, NULL, 0, 'Brush on a small circle of pink luster dust, then attach the top two dots over the pink spots.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'922980FD21FED44DB6645E74621E9B66', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 11, NULL, NULL, 2, NULL, 0, 'Wrap the decorated modeling chocolate cupcake wrapper around a cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBEFC1D33AE24A4D84B80DDD8174190A', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 12, NULL, NULL, 2, NULL, 0, 'Brush water on the end pieces and press together. If your modeling chocolate has dried, rub the water on with your finger, making it a bit sticky.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'569F3DD83085334EB825AFCF8E6D30A4', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 13, NULL, NULL, 2, NULL, 0, 'Press together the seam.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B8DAB81B3B13D4586FD799396A9EDA9', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 14, NULL, NULL, 2, NULL, 0, 'Pipe on a swirl of frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54FBED680DC949479EC2DC6E5F8ADDB6', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 15, NULL, NULL, 2, NULL, 0, 'I suggest wrapping your cupcakes the day you will use them. Store in an airtight plastic container. Do NOT refrigerate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D277422C38D669468CBFE840B2A7247B', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', X'65A42F7EA4FDD74F9C76F7861CD4D7FD', 16, NULL, NULL, 2, NULL, 0, 'These cupcakes are meant to be eaten using a fork, and the entire thing is edible.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6480414D8CA5504FAA98097CE9890061', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B545EBC97515BE41A6A3F922CB6A5F3C', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 1, NULL, NULL, 2, NULL, 0, 'Either line a 9 inch by 13 inch baking pan with non-stick tin foil or spray it with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'427735021F88A8479111E0773F3E34F1', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 2, NULL, NULL, 2, NULL, 0, 'Combine cake mix, pudding mix, sour cream, milk, oil, and eggs in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA884927CB158B4CADB35E2CACFC4FDE', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 3, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on low speed for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D79322C88D7DD341894F5F8609C47B96', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 4, NULL, NULL, 2, NULL, 0, 'Increase speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FDD3C46F6773545A46606E06BD3B2AA', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 5, NULL, NULL, 2, NULL, 0, 'Pour into pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1FE77C89397E441A04A7D103B62764E', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 6, NULL, NULL, 2, NULL, 0, 'Bake for 28-34 minutes until a toothpick inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B0BFE0B354AB54984D3E76762E8ED51', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 7, NULL, NULL, 2, NULL, 0, 'Allow cake to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53FBA57953A9E341B23B26B4EFC39F9B', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 8, NULL, NULL, 2, NULL, 0, 'Crumble cake into a large bowl, preferable the bowl of a stand mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03F1D710A4E9334DAB0812CA9C048D8D', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 9, NULL, NULL, 2, NULL, 0, 'Add frosting and stir or beat until well blended and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B364FA81A133942B44FA0EC71404AD0', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 10, NULL, NULL, 2, NULL, 0, 'Scoop out 48 tablespoon size portions of the cake ball dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD6BC3FAFB354944B8041CD835F66573', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 11, NULL, NULL, 2, NULL, 0, 'Create an indentation in one cake ball and press a maraschino cherry into it.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23B1BCD61BCA0B4B81517F542941F7B3', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 12, NULL, NULL, 2, NULL, 0, 'Press the cake ball dough around the cherry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B312C2F665861438B13F5F5C5C55EA5', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 13, NULL, NULL, 2, NULL, 0, 'Roll into a ball with the stem sticking out.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F71B27D8EC066949A9E18900DC17878F', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 14, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77F2C98000A88245853118EB7B74D4D6', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 15, NULL, NULL, 2, NULL, 0, 'Let cake balls sit at room temperature for 1 hour to firm up a bit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1210443022936D45BD1A28CACAC8AEC7', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 16, NULL, NULL, 2, NULL, 0, 'Dip each cake ball into dark chocolate, allowing the excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25BDB474A5D4D545918ED25FE96867F1', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 17, NULL, NULL, 2, NULL, 0, 'Set on a parchment lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CC0CE912472134C9FC86EEC71CFE4F2', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 18, NULL, NULL, 2, NULL, 0, 'If using pure chocolate, chill the Chocolate Cherry Bombs in the refrigerator until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D217E67427294D4290BD71DE6A0CFD81', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 19, NULL, NULL, 2, NULL, 0, 'If using candy melts pop them in the freezer, instead.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64185752BFDDE4499F3AA3307EEDDCAE', X'36BD547D897CC34B90E6BBC9B9A22B7F', X'36BD547D897CC34B90E6BBC9B9A22B7F', 20, NULL, NULL, 2, NULL, 0, 'Let the cherry bombs sit at room temperature for at least 30 minutes before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A15A2E944478D54D8254CA4B48EAA0C6', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 0, NULL, NULL, 2, NULL, 0, 'Set your cream cheese, sour cream, and egg out on the counter to come to room temperature at least one hour, but preferably 2 hours, before you plan to bake your cheesecakes. You need to use room temperature ingredients!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2504D8C1FC59C4CA903C005B7295E54', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 1, NULL, NULL, 2, NULL, 0, 'Place a rimmed cookie sheet or baking pan filled with 1/2-inch to 1-inch of water on the lowest rack in your oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A5274BB5F66224597F1887F5B569F7B', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 2, NULL, NULL, 2, NULL, 0, 'Arrange another rack in the middle of the oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E301CB70800D548B5A17C2FC9D4A980', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 3, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 300 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76E8ADC7598339419F66AC92752148C3', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 4, NULL, NULL, 2, NULL, 0, 'Crush 12 OREO Cookies into fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'830419DC35A79B40A47A9BCB92DCCB81', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 5, NULL, NULL, 2, NULL, 0, 'Stir 2 tablespoons of melted butter into the cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86650618FA5AFB45A62A57B391B140C8', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 6, NULL, NULL, 2, NULL, 0, 'Scoop out about one tablespoon of the cookie crumbs and drop into a paper-lined cupcake pan. Repeat, filling 12 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1499DB96766ED140BC704FFFF1DE4D4B', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 7, NULL, NULL, 2, NULL, 0, 'Press the crumbs down into an even layer in the bottom of the cupcake wrapper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F4A86BD31962A449E2C52A95A6648C2', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 8, NULL, NULL, 2, NULL, 0, 'Place 8 ounces of softened cream cheese and 1/3 cup granulated sugar in a large mixing bowl and beat using an electric mixer on low speed just until it is creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'081EBCDFA867F34D9E4926C8EA59F65C', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 9, NULL, NULL, 2, NULL, 0, 'Add the room temperature sour cream and vanilla and beat just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C757DF3D213E0E4D81D2A6F6E92CE132', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 10, NULL, NULL, 2, NULL, 0, 'Stir in 4 ounces of melted semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D2DC1D140FD5D4E8E8CA897D472C3A1', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 11, NULL, NULL, 2, NULL, 0, 'Then pour a lightly beaten, room temperature, egg over the chocolate mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'325BC600791A9B42A7A5F5F58FEC166C', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 12, NULL, NULL, 2, NULL, 0, 'Stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E10AAD2AC9BC514F80171E596259549E', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 13, NULL, NULL, 2, NULL, 0, 'Scoop a little more than 2 tablespoons of the chocolate cheesecake filling over each cookie crust and spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C810CB81671CBE4B91D1D7BD7DC1D8AA', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 14, NULL, NULL, 2, NULL, 0, 'Tap the pan on the counter about 10 times to allow any air bubbles to rise to the surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9B6D8A846964E49A7BA3C741CB6C149', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 15, NULL, NULL, 2, NULL, 0, 'Place on the middle rack in the oven and bake for 24-28 minutes until the tops look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C29AD4DD211A054BA3A38EBA6C9BFFAE', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 16, NULL, NULL, 2, NULL, 0, 'The cheesecakes should still jiggle when the pan is jostled and will be glossy but should not look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55D8844DBA11474DA586AF3E14742F27', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 17, NULL, NULL, 2, NULL, 0, 'Turn the oven off, leave the cheesecakes inside, but crack the door open.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'713C25DA57879146A04EB77C68270BF6', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 18, NULL, NULL, 2, NULL, 0, 'Allow the cheesecakes to rest in the warm oven for 45 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACE04831396E2945BC62A9AA33FEE5B6', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 19, NULL, NULL, 2, NULL, 0, 'Remove and allow the cheesecakes to cool on the counter for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C574C72E7B799A46A1548D48A9E04C75', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 20, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B4BB9CE4E447943805A25D936F16760', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 21, NULL, NULL, 2, NULL, 0, 'Remove from the pan and peel off the paper before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F121902A509D5F45A5D27B4D8C4544DF', X'EB8210992F40CA42BFC8E6AE0FAE2E42', X'EB8210992F40CA42BFC8E6AE0FAE2E42', 22, NULL, NULL, 2, NULL, 0, 'Add toppings, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A93175669EE62D48BCB0E7D737D05659', X'A6AA71B63ECF464E836F2413B1CFCB4D', X'A6AA71B63ECF464E836F2413B1CFCB4D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE22BE7C3CE4774587E6BEBDAA32268A', X'A6AA71B63ECF464E836F2413B1CFCB4D', X'A6AA71B63ECF464E836F2413B1CFCB4D', 1, NULL, NULL, 2, NULL, 0, 'Brush some butter on the inside of a 10-inch skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'141A6CCC710CAA4B9CFF38EDB8E2AD4E', X'A6AA71B63ECF464E836F2413B1CFCB4D', X'A6AA71B63ECF464E836F2413B1CFCB4D', 2, NULL, NULL, 2, NULL, 0, 'Cut bread dough into 12 slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CBF34CE96B5544AB02A8D5F37C5A72A', X'A6AA71B63ECF464E836F2413B1CFCB4D', X'A6AA71B63ECF464E836F2413B1CFCB4D', 3, NULL, NULL, 2, NULL, 0, 'Roll each slice into balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'745EE7FC5EABD7498C377AF3C4C15867', X'A6AA71B63ECF464E836F2413B1CFCB4D', X'A6AA71B63ECF464E836F2413B1CFCB4D', 4, NULL, NULL, 2, NULL, 0, 'Arrange the balls around the edge of the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45FD5B499886B54BAC13D7CFCDD77167', X'A6AA71B63ECF464E836F2413B1CFCB4D', X'A6AA71B63ECF464E836F2413B1CFCB4D', 5, NULL, NULL, 2, NULL, 0, 'Brush with melted butter and sprinkle on some salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5BE6D57A9EF5E469E86DB08A0679957', X'AD98DFF19F34BE488AF61509237D5503', X'AD98DFF19F34BE488AF61509237D5503', 0, NULL, NULL, 2, NULL, 0, 'Combine the cream cheese, shredded chicken, Miracle Whip, lemon juice, and Worcestershire sauce in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'827EA6F23E64E44E8944BE645737CAE3', X'AD98DFF19F34BE488AF61509237D5503', X'AD98DFF19F34BE488AF61509237D5503', 1, NULL, NULL, 2, NULL, 0, 'Beat with an electric mixer until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24B53AFB8116B34FA3895EBF697CCFEE', X'AD98DFF19F34BE488AF61509237D5503', X'AD98DFF19F34BE488AF61509237D5503', 2, NULL, NULL, 2, NULL, 0, 'Spoon dip into the center of the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'199A8227ACA00646BD83C29BAAFBAF30', X'AD98DFF19F34BE488AF61509237D5503', X'AD98DFF19F34BE488AF61509237D5503', 3, NULL, NULL, 2, NULL, 0, 'Spread it into an even layer so that it touches the edge of the bread balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8044B4A5AC00F4449AECF60C7FE345B7', X'AD98DFF19F34BE488AF61509237D5503', X'AD98DFF19F34BE488AF61509237D5503', 4, NULL, NULL, 2, NULL, 0, 'Bake for 32-38 minutes until the bread is baked through and lightly brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5D7C7565A84884F93EB5FE158EADEAD', X'AD98DFF19F34BE488AF61509237D5503', X'AD98DFF19F34BE488AF61509237D5503', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, cut 7 round slices from 4 of the black olives.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA1B10DD64D1DF45B03BC19440F37FFA', X'AD98DFF19F34BE488AF61509237D5503', X'AD98DFF19F34BE488AF61509237D5503', 6, NULL, NULL, 2, NULL, 0, 'Cut the carrot into a 3-4 inch pointed piece.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E614172FBA2C594C90DC908BFD4724AA', X'AD98DFF19F34BE488AF61509237D5503', X'AD98DFF19F34BE488AF61509237D5503', 7, NULL, NULL, 2, NULL, 0, 'Remove the skillet from the oven and brush more butter over the bread pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57505A611E74414E87474C13E8344C25', X'AD98DFF19F34BE488AF61509237D5503', X'AD98DFF19F34BE488AF61509237D5503', 8, NULL, NULL, 2, NULL, 0, 'Decorate the dip with 2 whole black olive eyes, one carrot nose, and 7 black olive buttons in the smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'214F1E40E1727145AD8496753EBC5BF2', X'AD98DFF19F34BE488AF61509237D5503', X'AD98DFF19F34BE488AF61509237D5503', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57D7C9F04F5D7C4989009D3B594B4E2D', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', 0, NULL, NULL, 2, NULL, 0, 'If using a polycarbonate half-sphere mold, a single cavity silicone mold, or a plastic ornament mold, fill the mold with chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68FCC948818A28498A9916D366E79444', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', 1, NULL, NULL, 2, NULL, 0, 'Tap the mold a few times to allow air bubbles to come to the surface and pop.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A53F8CA3351B546BA7FCF4C7799B2C9', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', 2, NULL, NULL, 2, NULL, 0, 'Invert the mold over a bowl of chocolate and tap the side of the mold allowing the excess chocolate to drain out of the cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FE6364BFA35EA48BB176A627895972F', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', 3, NULL, NULL, 2, NULL, 0, 'Scrape off the excess chocolate using a metal spatula or bench scraper to clean the edges of the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B04281DA1C1A2C44AF0F18F5DADE4D2C', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', 4, NULL, NULL, 2, NULL, 0, 'If the chocolate is thick you can leave the molds upright to chill. If the chocolate is thin it''s best to turn the mold upside down and set it on a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2DA2D1C80EBC246AB9B5E2798441C5B', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', 5, NULL, NULL, 2, NULL, 0, 'If using pure chocolate and your room is cool you can allow your chocolate to air dry or you can chill it in the refrigerator until hardened, 5-10 minutes. If using compound chocolate (candy melts) place the mold in the freezer for 3-5 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1604AEBB56708438C660B5336EF6161', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', 6, NULL, NULL, 2, NULL, 0, 'If using a silicone mold with multiple cavities, fill about 1/3rd of a cavity with chocolate, and use a brush to paint the chocolate up the sides of the mold. Chill and if needed add a second coat of chocolate around the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'534175A69A3BC2438A72878D321D44CC', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', 7, NULL, NULL, 2, NULL, 0, 'Make a total of 12 chocolate half-spheres and reserve the remaining chocolate for drizzling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD0650FC63C988449E449698314F8265', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', 8, NULL, NULL, 2, NULL, 0, 'Fill 6 of the chocolate half-spheres with 2-4 tablespoons of hot cocoa mix or chocolate ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0422D41670057449CBACD686E60E94B', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle mini marshmallows over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E23A443A180CE47BB2FFB2C81B511E0', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', 10, NULL, NULL, 2, NULL, 0, 'Heat a skillet over low heat for 20-30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41A826BF642D094E8ED0972E4F0EE139', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', 11, NULL, NULL, 2, NULL, 0, 'Melt the top edge of one of the empty chocolates and place it over top of a filled chocolate. Press gently to seal the two pieces together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37627C5D7CA65D43A398ADD2E1324BA8', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', X'A1DCFA8DD257514D8DAE55F68D6E5CCF', 12, NULL, NULL, 2, NULL, 0, 'Drizzle chocolate over top of your hot chocolate bombs and decorate with sprinkles or other fun toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A29972822AC3A44BFE4F9A32B1E458B', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 0, NULL, NULL, 2, NULL, 0, 'Place the Green Candy Melts in a large, microwave-safe, mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'146380B0B3F142419A3FAAC6450506C8', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 1, NULL, NULL, 2, NULL, 0, 'Pour the sweetened condensed milk over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDC76E22BF4FC540B138D1F8F76890B5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 2, NULL, NULL, 2, NULL, 0, 'Stir just to coat the candy wafers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EF15D6B22A0B54B8967D26F1F68FED9', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 3, NULL, NULL, 2, NULL, 0, 'Heat in the microwave on high power for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EC8352C6095FF4CB280445817309B30', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 4, NULL, NULL, 2, NULL, 0, 'Allow the candy to sit in the microwave for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CFF93DAD9228D45825779F2892BB331', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 5, NULL, NULL, 2, NULL, 0, 'Remove and slowly stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CA8C35E8297D748B0C04D0C05E7A823', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 6, NULL, NULL, 2, NULL, 0, 'If needed, heat for additional 15-second bursts of 50% power, stirring slowly after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E30C6103291FEC45BC929AFD24F4E2CA', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 7, NULL, NULL, 2, NULL, 0, 'Stir in the Creme de Menthe Liquor or Cream de Methe syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1310038015BD7E4ABE0183A7B5767E9E', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 8, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap, and allow the fudge to cool and thicken, stirring every 5 minutes, until the fudge becomes the consistency of frosting. Keep an eye on this, as you don''t want the fudge to cool too much and get too thick.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A366A731FE3A44EB3EB43DB87520F38', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 9, NULL, NULL, 2, NULL, 0, 'Spoon the fudge into a pastry bag fitted with a large star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79106D2718C5DD4999E848758F146156', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 10, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of fudge onto a parchment or wax paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AAEF1AE867D53343AAF7C7EBBE8DBA82', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 11, NULL, NULL, 2, NULL, 0, 'Pull up while piping to get a nice pointed tip on each fudge swirl Christmas tree.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F8C0340CA9B3848BFF5AA81C5C13339', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 12, NULL, NULL, 2, NULL, 0, 'Immediately sprinkle on some colorful nonpareils.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6353F247F323BC4FAF3E830047C5D94A', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 13, NULL, NULL, 2, NULL, 0, 'Add a gold star sprinkle on top of the Fudge Christmas tree.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1B7D69B0A107B46AF8B83B7F3A5A4B4', X'9773E34A85D69A4DA4E5E11CF43FCCF5', X'9773E34A85D69A4DA4E5E11CF43FCCF5', 14, NULL, NULL, 2, NULL, 0, 'Repeat creating about 30 Fudge Christmas Trees.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2920581FB6A3343A497C115965F3897', X'50C9D46AF915C440AB46B5E5C970ED43', X'50C9D46AF915C440AB46B5E5C970ED43', 0, NULL, NULL, 2, NULL, 0, 'Melt 12 ounces white chocolate or white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5FE9EF9B5CCA746AB110F50ED8C0B33', X'50C9D46AF915C440AB46B5E5C970ED43', X'50C9D46AF915C440AB46B5E5C970ED43', 1, NULL, NULL, 2, NULL, 0, 'Stirring often, allow the candy melts to cool to 91 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E1CBB517C2DFF4A9B8B89008CBA45F9', X'50C9D46AF915C440AB46B5E5C970ED43', X'50C9D46AF915C440AB46B5E5C970ED43', 2, NULL, NULL, 2, NULL, 0, 'Pour in 1/3 cup of corn syrup and stir just until incorporated. Do not overmix.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F899BAA949B584984E252ADC4927B7E', X'50C9D46AF915C440AB46B5E5C970ED43', X'50C9D46AF915C440AB46B5E5C970ED43', 3, NULL, NULL, 2, NULL, 0, 'Place the modeling chocolate in a large zip-top bag, flatten it out into a disc, and set it aside for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1C3A82427C85B48B8DD9FA18CC2F84D', X'50C9D46AF915C440AB46B5E5C970ED43', X'50C9D46AF915C440AB46B5E5C970ED43', 4, NULL, NULL, 2, NULL, 0, 'Break off pieces of the modeling chocolate and knead until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CA6AA8478221C4A882FA002F86EEA91', X'50C9D46AF915C440AB46B5E5C970ED43', X'50C9D46AF915C440AB46B5E5C970ED43', 5, NULL, NULL, 2, NULL, 0, 'Color some white modeling chocolate using pink food coloring and some using black food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4637DB088AABC94E8A6BC30F50C69277', X'50C9D46AF915C440AB46B5E5C970ED43', X'50C9D46AF915C440AB46B5E5C970ED43', 6, NULL, NULL, 2, NULL, 0, 'Make the pink bright. Then break off about 1/3 of the pink and blend it with more white modeling chocolate to make pastel pink.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42DF789BE69C354EBBCE7D4127347E94', X'50C9D46AF915C440AB46B5E5C970ED43', X'50C9D46AF915C440AB46B5E5C970ED43', 7, NULL, NULL, 2, NULL, 0, 'Place the colored modeling chocolate in a zip-top bag and let it rest for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82688F2A5AFDA0479EDB7C90CB320CC4', X'DFFAADCDC948FB4EA5EFBBE38E629E3B', X'DFFAADCDC948FB4EA5EFBBE38E629E3B', 0, NULL, NULL, 2, NULL, 0, 'Use a silicone (fondant) rolling pin to roll out some white modeling chocolate to 1/8-inch thickness on a silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEEB77070DC28D4A9959C36C5EA42356', X'DFFAADCDC948FB4EA5EFBBE38E629E3B', X'DFFAADCDC948FB4EA5EFBBE38E629E3B', 1, NULL, NULL, 2, NULL, 0, 'Cut out two 7/8-inch circles for each mouse.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7AFF811819A7A4BB1EA766BAE29C998', X'DFFAADCDC948FB4EA5EFBBE38E629E3B', X'DFFAADCDC948FB4EA5EFBBE38E629E3B', 2, NULL, NULL, 2, NULL, 0, 'Roll out the light pink modeling chocolate really thin and cut out two 5/8-inch circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4D080012B2E3F4C92826120F6997CA8', X'DFFAADCDC948FB4EA5EFBBE38E629E3B', X'DFFAADCDC948FB4EA5EFBBE38E629E3B', 3, NULL, NULL, 2, NULL, 0, 'Brush one side of the pink circles lightly with water then press it onto a white circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CA5C7B487B3994C864D6720B39B3338', X'DFFAADCDC948FB4EA5EFBBE38E629E3B', X'DFFAADCDC948FB4EA5EFBBE38E629E3B', 4, NULL, NULL, 2, NULL, 0, 'Press down to flatten the edge of the pink circle into the white circle. This will make the entire ear about 1-inch in diameter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DA510BEAE46164580F66C8139338CAC', X'DFFAADCDC948FB4EA5EFBBE38E629E3B', X'DFFAADCDC948FB4EA5EFBBE38E629E3B', 5, NULL, NULL, 2, NULL, 0, 'Use the same 7/8-inch cutter to cut a small curve off of each ear. This will allow the ear to fit more closely to the edge of the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C5F7B913D98B94CB2FADF3C73CB4089', X'281E08E5476E254DBF98F4E77DC5F4D7', X'281E08E5476E254DBF98F4E77DC5F4D7', 0, NULL, NULL, 2, NULL, 0, 'Roll out the bright pink modeling chocolate to 1/16-inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A66288341A367847B664331565192AAA', X'281E08E5476E254DBF98F4E77DC5F4D7', X'281E08E5476E254DBF98F4E77DC5F4D7', 1, NULL, NULL, 2, NULL, 0, 'Cut out a 3/8 inch circle and flatten it into a bigger, rounded edge oval. Or use an oval cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91ACBFE22F6BB24894418EE8EEE03683', X'AD4BB7EF54A22542B529FF117E8CC58E', X'AD4BB7EF54A22542B529FF117E8CC58E', 0, NULL, NULL, 2, NULL, 0, 'Roll out black modeling chocolate thin and cut out ovals that are about 7/16-inch in length.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5C53D16ACC889499D33C3AF3601AB7C', X'AD4BB7EF54A22542B529FF117E8CC58E', X'AD4BB7EF54A22542B529FF117E8CC58E', 1, NULL, NULL, 2, NULL, 0, 'Reese''s Cup Mice');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5837A0B3E458EE428854986DC7138D62', X'AD4BB7EF54A22542B529FF117E8CC58E', X'AD4BB7EF54A22542B529FF117E8CC58E', 2, NULL, NULL, 2, NULL, 0, 'Brush melted white candy melts along the cut edge of one of the mouse ears and press it onto the White Chocolate Reese''s Cup along the rounded edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66ABC3115E450848848F20A800AA879A', X'AD4BB7EF54A22542B529FF117E8CC58E', X'AD4BB7EF54A22542B529FF117E8CC58E', 3, NULL, NULL, 2, NULL, 0, 'Attach the other ear about an inch away along the edge. You want them spaced so that they are at about 10:00 and 2:00 on a clock face.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47498500DBF17B4AA92FB6657BD50DCC', X'AD4BB7EF54A22542B529FF117E8CC58E', X'AD4BB7EF54A22542B529FF117E8CC58E', 4, NULL, NULL, 2, NULL, 0, 'Use white candy melts to attach a nose and two eyes to each mouse.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2471120CB415494A8E14DB27238B60D7', X'AD4BB7EF54A22542B529FF117E8CC58E', X'AD4BB7EF54A22542B529FF117E8CC58E', 5, NULL, NULL, 2, NULL, 0, 'Then add a dot of white candy melts to each eye, to create highlights.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3EBE26DF77E504DB4DC33F5AFB6DBA3', X'AD4BB7EF54A22542B529FF117E8CC58E', X'AD4BB7EF54A22542B529FF117E8CC58E', 6, NULL, NULL, 2, NULL, 0, 'Use a black food coloring marker to draw on whiskers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AF2B5A59FB98040870B13A8B5F82DB3', X'AD4BB7EF54A22542B529FF117E8CC58E', X'AD4BB7EF54A22542B529FF117E8CC58E', 7, NULL, NULL, 2, NULL, 0, 'Brush some white candy melts into a 2-inch long triangle (pick) then set a Reese''s Cup mouse over the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE353A83D923B0489F16362AD3F0A306', X'AD4BB7EF54A22542B529FF117E8CC58E', X'AD4BB7EF54A22542B529FF117E8CC58E', 8, NULL, NULL, 2, NULL, 0, 'Allow the candy melts to harden before picking up your mice.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBCE7CF926F8944BB4147EF013BC1094', X'5C55B3EB71250442894711D664EC08BE', X'5C55B3EB71250442894711D664EC08BE', 0, NULL, NULL, 2, NULL, 0, 'Place a cupcake in a teacup, coffee mug, or paper cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60C22852E5858D45AF9C4C5E8FE428C4', X'5C55B3EB71250442894711D664EC08BE', X'5C55B3EB71250442894711D664EC08BE', 1, NULL, NULL, 2, NULL, 0, 'Heat the Nutella or other chocolate hazelnut spread in the microwave just long enough to make it pourable.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1A84D8936AEAF41A289FFBF4CA8F7EA', X'5C55B3EB71250442894711D664EC08BE', X'5C55B3EB71250442894711D664EC08BE', 2, NULL, NULL, 2, NULL, 0, 'Pour it over the cupcakes and spread it out just to cover the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'082B8F0594B56D418A99FDCD9855CDE3', X'5C55B3EB71250442894711D664EC08BE', X'5C55B3EB71250442894711D664EC08BE', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle on some mini marshmallows.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BD5A3C72651344A9F54D8D80D5EFE4D', X'5C55B3EB71250442894711D664EC08BE', X'5C55B3EB71250442894711D664EC08BE', 4, NULL, NULL, 2, NULL, 0, 'Pick up a mouse and insert the white chocolate pick into the cupcake allowing the bottom edge of the Reese''s Cup to touch the Nutella spread.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81F403A31DE61E48A3662A9D926B8F11', X'5C55B3EB71250442894711D664EC08BE', X'5C55B3EB71250442894711D664EC08BE', 5, NULL, NULL, 2, NULL, 0, 'Pinch off pieces of white modeling chocolate and roll into a teardrop shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C814B0BE6CED4849B49149C8DCF06687', X'5C55B3EB71250442894711D664EC08BE', X'5C55B3EB71250442894711D664EC08BE', 6, NULL, NULL, 2, NULL, 0, 'Roll over the tip and insert it into the cup, so that it looks like one of the mouse''s paws is holding onto the cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02254FF1A3810248A1527B90E88A8D3E', X'5C55B3EB71250442894711D664EC08BE', X'5C55B3EB71250442894711D664EC08BE', 7, NULL, NULL, 2, NULL, 0, 'Repeat creating another paw.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4DEC8125F8B4245942425732C1979F0', X'5C55B3EB71250442894711D664EC08BE', X'5C55B3EB71250442894711D664EC08BE', 8, NULL, NULL, 2, NULL, 0, 'Decorate all 12 cupcakes with the Reese''s Cup Mice.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC2B51BAEDE35041AA09E6D56E902714', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC1A589BE6F1624AB76D2CABB6B56C83', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 1, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'835D5CD1A541B24B9549FF980FE8AF8E', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 2, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57A8E162236F814EB73F66DD6E7245BC', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 3, NULL, NULL, 2, NULL, 0, 'Stir in eggs, vanilla, and almond extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6A146169EAF1449BEAEF684AB21D105', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 4, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until it''s mixed in.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EBD5A86E1EB6F4C84CA915728729376', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 5, NULL, NULL, 2, NULL, 0, 'Roll out dough to 3/16 of an inch. Cut 28 snowman cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF1AAB4A8B343E4C8156B949A74DDD75', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 6, NULL, NULL, 2, NULL, 0, 'Cut circles out of 14 of those cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF03689042111E469757C8FEC8D54DCE', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 7, NULL, NULL, 2, NULL, 0, 'Roll remaining dough out to 1/16 of an inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F117E4CDFD0C44CA562233565F9059D', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 8, NULL, NULL, 2, NULL, 0, 'Cut out 14 more snowmen.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'085069922747FA4A933C9554343604B2', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 9, NULL, NULL, 2, NULL, 0, 'Freeze cookies for 30 minutes until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6197950FA53159448C32644899DFF02A', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 10, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'331AA7E027BA8A4CAA65EDF421A8DDFD', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 11, NULL, NULL, 2, NULL, 0, 'Set chilled cookies on a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75E40DB7AB1C7C4380DE7BAD5B93F2E8', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 12, NULL, NULL, 2, NULL, 0, 'Bake the whole cookies and 14 of the cookies with holes for 11-14 minutes until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69109B6CFF118845896616D0D311D459', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 13, NULL, NULL, 2, NULL, 0, 'Bake the remaining trays of 14 cookies with holes for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8E2E1E7AE05014FA203B4FD9C528EFF', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 14, NULL, NULL, 2, NULL, 0, 'Remove and spoon in the crushed candy filling the hole 3/4''s full.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29DBE0EDF54C344095C66B8C409B315B', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 15, NULL, NULL, 2, NULL, 0, 'Return to oven and melt for about 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CD83CBF5C2FB24888E407179F6C1B59', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 16, NULL, NULL, 2, NULL, 0, 'Allow all cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0574E001A7DAA74A86BC452991F50571', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 17, NULL, NULL, 2, NULL, 0, 'Decorate the candy belly cookies using white candy melts, sunflower seeds, and black sugar pearls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43D60E69784B51478EE9324EAAD42739', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 18, NULL, NULL, 2, NULL, 0, 'Freeze for a few minutes until the candy melts harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D56EC74DF4BF714DB836C17A8DCDF110', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 19, NULL, NULL, 2, NULL, 0, 'Attach one cookie with a hole to a whole cookie using candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCD0E1911CE01E48A9F6F6DA3B8042DC', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 20, NULL, NULL, 2, NULL, 0, 'Fill the hole with candy sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CBE8DB0FFD1504FB53EFCC758229CEC', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 21, NULL, NULL, 2, NULL, 0, 'Attach a decorated snowman cookie on top using candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54E947D158C9884D9E5C6B847E6A923E', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 22, NULL, NULL, 2, NULL, 0, 'Let dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'471586DFF05D9E45ABC3127FB9893D02', X'CF5748ABD703A0418169454C64226FD3', X'CF5748ABD703A0418169454C64226FD3', 23, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D99C6B06E05704FA591BF9BF3C0F412', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 0, NULL, NULL, 2, NULL, 0, 'Pour white chocolate into a bowl so that the chocolate is about 3 inches deep.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E01C70F88CE204DB5943235A5A513B9', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 1, NULL, NULL, 2, NULL, 0, 'Pour the red chocolate into a disposable pastry bag or zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A26601E7003444682388E85E8BA41BC', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 2, NULL, NULL, 2, NULL, 0, 'Cut the tip off the bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B12E874213FFF04A9F3C5CD0D5D1B2EF', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 3, NULL, NULL, 2, NULL, 0, 'Pipe 5 to 7 lines of red chocolate over the top surface of the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A342E197F069184F9C4177C3F1B9BDF9', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 4, NULL, NULL, 2, NULL, 0, 'Dip one balloon into the bowl of chocolate to cover about 2/3rd''s of the balloon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'137398CE3C6DFE44940259B5CE1067B4', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 5, NULL, NULL, 2, NULL, 0, 'Lift the balloon out of the chocolate and allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'651BDB01F4FAB246A502679D08AE9D70', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 6, NULL, NULL, 2, NULL, 0, 'Set the balloon on a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'063D04CFDC78D94D9CDC75FBC804E1A8', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 7, NULL, NULL, 2, NULL, 0, 'Repeat by piping the same number of red stripes over the bowl of white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D44178476FC7304DBF6ACBDE5F049A7B', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 8, NULL, NULL, 2, NULL, 0, 'Dip another balloon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E48C1CC65F7C6741804504236B9D2521', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 9, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D98554FF5F3983499545B615A5EC6C85', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 10, NULL, NULL, 2, NULL, 0, 'If using pure white chocolate, refrigerate the balloons until the chocolate sets.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61FD7109191AD64C98A9C2AE3DF33C94', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 11, NULL, NULL, 2, NULL, 0, 'If using Candy Melts, place the balloons in the freezer for about 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'372F20C9E61CF342A3DCF8070BCDFCCA', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 12, NULL, NULL, 2, NULL, 0, 'Remove and let sit for one minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A20B66115F8A33489FA8C5C96D07632C', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 13, NULL, NULL, 2, NULL, 0, 'Then, cut a small hole near the knot on the balloon to allow the air to escape.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F27AD4E7DC26FB46AFF091D2D11C0DEB', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 14, NULL, NULL, 2, NULL, 0, 'Tug and pull the balloons out of the chocolate cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51E5856373632D4B85CBDBA31929B9D7', X'96D33A252701664CADBA56CAB5751AE7', X'96D33A252701664CADBA56CAB5751AE7', 15, NULL, NULL, 2, NULL, 0, 'Fill with White Chocolate Peppermint Mousse , ice cream, pudding, candy or nuts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFEEE4CBE7DF66409FCE128F5315F2C7', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', 0, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of the softened butter over a parchment-paper-lined 9-inch by 13-inch pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31B59876DC8333438DF14EDDE4505ECD', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', 1, NULL, NULL, 2, NULL, 0, 'Pour marshmallows, butter, and red candy melts into a microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC058B4D12EE5942AF9F2D3BDE96B8BE', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power for three 30 second bursts of power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E27F4712D8C7FD41A5C483F3777B6217', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', 3, NULL, NULL, 2, NULL, 0, 'Stir in some red food coloring so that the marshmallow mixture becomes vibrantly red.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F270BC32BC91947BBFC1C7A9F4EC073', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', 4, NULL, NULL, 2, NULL, 0, 'Stir in the cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C733C5176D81B42B50B557685C10159', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', 5, NULL, NULL, 2, NULL, 0, 'Pour out onto the prepared pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C3A9CD503BBD747B1A4527FFA786111', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', X'5CF17C5DA8E94F4591C8C0ED72FAD7D6', 6, NULL, NULL, 2, NULL, 0, 'Grease your hands with butter then press the cereal mixture into an even layer in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B825008C0ADFC4F87224703C6CA8D7C', X'4D9E7E8B1204D949A86FEFC93E5586CE', X'4D9E7E8B1204D949A86FEFC93E5586CE', 0, NULL, NULL, 2, NULL, 0, 'Pour marshmallows, butter, and green candy melts into a microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CACD54779069A54DB241A1BCD7545468', X'4D9E7E8B1204D949A86FEFC93E5586CE', X'4D9E7E8B1204D949A86FEFC93E5586CE', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for three 30 second bursts of power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CDE5FCCA5F77349B58EFC56036D1760', X'4D9E7E8B1204D949A86FEFC93E5586CE', X'4D9E7E8B1204D949A86FEFC93E5586CE', 2, NULL, NULL, 2, NULL, 0, 'Stir in some green food coloring so that the marshmallows mixture becomes vibrantly green.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BFA1DCF0CC0974D92E8E17FC14FD335', X'4D9E7E8B1204D949A86FEFC93E5586CE', X'4D9E7E8B1204D949A86FEFC93E5586CE', 3, NULL, NULL, 2, NULL, 0, 'Stir in the cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30E6806B5C4CD2468A0461FF4CF2876E', X'4D9E7E8B1204D949A86FEFC93E5586CE', X'4D9E7E8B1204D949A86FEFC93E5586CE', 4, NULL, NULL, 2, NULL, 0, 'Use greased hands to press the green cereal into an even layer over top of the red cereal layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B99905E1228964EA11442E10D4182AC', X'4D9E7E8B1204D949A86FEFC93E5586CE', X'4D9E7E8B1204D949A86FEFC93E5586CE', 5, NULL, NULL, 2, NULL, 0, 'Turn the pan upside down and allow the cereal treat to fall out onto a silicone mat or a buttered piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E6EC313E2389244870ED381904FCE6E', X'4D9E7E8B1204D949A86FEFC93E5586CE', X'4D9E7E8B1204D949A86FEFC93E5586CE', 6, NULL, NULL, 2, NULL, 0, 'Starting at one of the shorter ends, roll the cereal treat up into a log, pinching together any gaps in the cereal treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E90C1B4BEBE2F4386E02A10A838D649', X'4D9E7E8B1204D949A86FEFC93E5586CE', X'4D9E7E8B1204D949A86FEFC93E5586CE', 7, NULL, NULL, 2, NULL, 0, 'Freeze for 5-10 minutes just until the cereal treat has cooled.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13AB306932BE354CA8241B15F2C0FC22', X'4D9E7E8B1204D949A86FEFC93E5586CE', X'4D9E7E8B1204D949A86FEFC93E5586CE', 8, NULL, NULL, 2, NULL, 0, 'Cut into 12-18 slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFC5E24305A7DA4BA50513BAE7E073C8', X'37B2A22997A84844B5387911505C3CFB', X'37B2A22997A84844B5387911505C3CFB', 0, NULL, NULL, 2, NULL, 0, 'Finely chop or shave your chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8B1A731EE65A44F9CF143E4FB10DE3E', X'37B2A22997A84844B5387911505C3CFB', X'37B2A22997A84844B5387911505C3CFB', 1, NULL, NULL, 2, NULL, 0, 'Heat the heavy whipping cream in a saucepan over medium heat just until bubbles begin to form around the edges of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'917679ACAC8BDC41A1C4CBB312B9AD26', X'A941477CD75D544BA998FD9FC5B4A616', X'A941477CD75D544BA998FD9FC5B4A616', 0, NULL, NULL, 2, NULL, 0, 'Pour chopped chocolate into a shallow bowl, creating a small well in the center of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12A16A7F9744704E94CAC5795D586484', X'A941477CD75D544BA998FD9FC5B4A616', X'A941477CD75D544BA998FD9FC5B4A616', 1, NULL, NULL, 2, NULL, 0, 'Pour the hot liquid over the chocolate allowing the cream to flow into the center of the bowl and underneath the chocolate. Make sure all the chocolate is submerged under the cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E1E2044CF209B41B4FCA9D07C13D3FA', X'A941477CD75D544BA998FD9FC5B4A616', X'A941477CD75D544BA998FD9FC5B4A616', 2, NULL, NULL, 2, NULL, 0, 'Let sit for 3 minutes allowing the chocolate to begin to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF4EB317F39A384D83BC9C0F2AF99AE0', X'A941477CD75D544BA998FD9FC5B4A616', X'A941477CD75D544BA998FD9FC5B4A616', 3, NULL, NULL, 2, NULL, 0, 'Begin stirring in a small circle in the center of the bowl to create an emulsion of the chocolate and cream then widen the circle slowly until all of the chocolate and cream blend together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B32D1327EB40CA469BE14DFA7180A647', X'A941477CD75D544BA998FD9FC5B4A616', X'A941477CD75D544BA998FD9FC5B4A616', 4, NULL, NULL, 2, NULL, 0, 'As soon as the chocolate and cream come together, and your chocolate ganache looks shiny and smooth, stop stirring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05049377B041EA40BDC2F709BD1D95C3', X'A941477CD75D544BA998FD9FC5B4A616', X'A941477CD75D544BA998FD9FC5B4A616', 5, NULL, NULL, 2, NULL, 0, 'Cover the ganache directly with plastic wrap or with a tight-fitting lid and set aside until it firms up, at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0CC521D3E284F54C9E188ECDBFE2EAEE', X'43583D3BFE5AE749AFF672CF1AC78143', X'43583D3BFE5AE749AFF672CF1AC78143', 0, NULL, NULL, 2, NULL, 0, 'Pour the hot cream over the chopped chocolate and close the lid.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'407E1CFE40B4B24898633C542B3F8AA3', X'43583D3BFE5AE749AFF672CF1AC78143', X'43583D3BFE5AE749AFF672CF1AC78143', 1, NULL, NULL, 2, NULL, 0, 'Pulse twice then remove the lid and scrape the sides of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B2EC04E1B5B6641896E55F3FB37101E', X'43583D3BFE5AE749AFF672CF1AC78143', X'43583D3BFE5AE749AFF672CF1AC78143', 2, NULL, NULL, 2, NULL, 0, 'Replace the lid and let the chocolate melt for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D1CE763BA28484E8BC24224D638916B', X'43583D3BFE5AE749AFF672CF1AC78143', X'43583D3BFE5AE749AFF672CF1AC78143', 3, NULL, NULL, 2, NULL, 0, 'Pulse 3-6 times just until the chocolate and cream come together and looks glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'781B2A2417B4F44281C7BCEFC06121F7', X'43583D3BFE5AE749AFF672CF1AC78143', X'43583D3BFE5AE749AFF672CF1AC78143', 4, NULL, NULL, 2, NULL, 0, 'Pour into a bowl, cover, and let rest at room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFF33BAE5C14214C80B0B07C02145621', X'D7E94D3B488BFA4290324AB5F23E1D70', X'D7E94D3B488BFA4290324AB5F23E1D70', 0, NULL, NULL, 2, NULL, 0, 'Pour 1/4 of the hot cream into a tall immersion blender cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1915D20074565047935C1FBA76A6679E', X'D7E94D3B488BFA4290324AB5F23E1D70', X'D7E94D3B488BFA4290324AB5F23E1D70', 1, NULL, NULL, 2, NULL, 0, 'Add the finely chopped chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39C37693AAE8954FB59CD0B08277D362', X'D7E94D3B488BFA4290324AB5F23E1D70', X'D7E94D3B488BFA4290324AB5F23E1D70', 2, NULL, NULL, 2, NULL, 0, 'Pour the remaining hot cream over top of the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'374DD608A70B564B80580EE35B31EA8F', X'D7E94D3B488BFA4290324AB5F23E1D70', X'D7E94D3B488BFA4290324AB5F23E1D70', 3, NULL, NULL, 2, NULL, 0, 'Set it aside for about 2 minutes to allow the chocolate to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE4A6C486F34D848B0592B2258BD73DB', X'D7E94D3B488BFA4290324AB5F23E1D70', X'D7E94D3B488BFA4290324AB5F23E1D70', 4, NULL, NULL, 2, NULL, 0, 'Use the immersion blender to combine the chocolate and cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'214983841E07F84E8618E88EC8FB1E57', X'D7E94D3B488BFA4290324AB5F23E1D70', X'D7E94D3B488BFA4290324AB5F23E1D70', 5, NULL, NULL, 2, NULL, 0, 'Scrape the sides of the cup and pulse again just until the ganache is smooth, glossy, and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F22C07F75083E46BA0D43E30CD0E00A', X'D7E94D3B488BFA4290324AB5F23E1D70', X'D7E94D3B488BFA4290324AB5F23E1D70', 6, NULL, NULL, 2, NULL, 0, 'Pour into a shallow bowl, cover, and set aside for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC5C18773775B5429AA7F83626B79BF1', X'8C38BA9CBEF0C345A36E30EC7C2396B8', X'8C38BA9CBEF0C345A36E30EC7C2396B8', 0, NULL, NULL, 2, NULL, 0, 'Using a small ice cream scoop or spoon, scoop the ganache onto a parchment paper-lined baking sheet and let rest for about 30 minutes until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C962DA40E437AC4EAFC6CB1179B23A90', X'8C38BA9CBEF0C345A36E30EC7C2396B8', X'8C38BA9CBEF0C345A36E30EC7C2396B8', 1, NULL, NULL, 2, NULL, 0, 'Roll a scoop of ganache into a ball then immediately roll into the toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EC1C22A01051F4AAB28B27EC6538195', X'8C38BA9CBEF0C345A36E30EC7C2396B8', X'8C38BA9CBEF0C345A36E30EC7C2396B8', 2, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32B057175EBE7A4EBBDC68A00DC41268', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350ºF.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C3434E6B002334C87B3E8C28E582275', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 1, NULL, NULL, 2, NULL, 0, 'Make one Betty Crocker SuperMoist™ white cake mix according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C16FE6057075F419657ABBA07CBB333', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 2, NULL, NULL, 2, NULL, 0, 'Scoop out 3/4 cup of batter and color it using gold or yellow food coloring. Equally divide the remaining batter and color one half red, the other green.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D51FFAF5B79774FB18E246968CF5D06', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 3, NULL, NULL, 2, NULL, 0, 'Spray one 6-inch square pan and three 4-inch round springform pans with baking spray. Pour gold batter into square pan and bake for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EED61CEB355B134A98FDE710BCD80793', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 4, NULL, NULL, 2, NULL, 0, 'Pour some red batter in one half of a 4-inch springform pan and green in the other half. Use a skewer or toothpick to swirl the colors slightly. Repeat in the other two pans. Go back and add layers of color, swirling, until you use up all the batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F17416B91580994CB7A1D01186F68759', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 5, NULL, NULL, 2, NULL, 0, 'Bake cakes for 26-30 minutes. The tops will look moist but should be baked through.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7628F7B8320ABA40A0F5A988E2FB1F08', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 6, NULL, NULL, 2, NULL, 0, 'Cool cakes for 10 minutes, then remove from pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6972D2A8469A6B4F880DBFF0A7DB4E26', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 7, NULL, NULL, 2, NULL, 0, 'Cut the gold cake into three 3/4-inch by 2-inch strips. Cut the domes off each of the red and green cakes, making them 2 inches thick. Reserve the cake scraps for another use.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B045262575FE3F47A71AFFBE264E03DF', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 8, NULL, NULL, 2, NULL, 0, 'Spray three 6-inch square baking pans with baking spray. Set one round cake in the bottom of each pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F58530B80736D34382B29AE05D588413', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 9, NULL, NULL, 2, NULL, 0, 'Make another Betty Crocker SuperMoist™ white cake mix.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8149B86835BC94C9756BBAB0561762E', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 10, NULL, NULL, 2, NULL, 0, 'Spread just a tiny bit of batter along one edge of a gold cake strip and press that up against the top of one of the round cakes, creating the top of the ornament. Repeat, then equally divide the batter among the three pans, pouring it around the ornaments.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'859CD15E681B6E4CAE1FA22E16A73654', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 11, NULL, NULL, 2, NULL, 0, 'Bake the cakes for 30-35 minutes until they are golden brown on top and when you press down around the edge with your finger, the cake bounces back.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15290856BDF9204990CFA4F78610E301', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 12, NULL, NULL, 2, NULL, 0, 'Allow cakes to cool completely, then freeze for one hour. Once the cakes are firm, carve off all the browned cake, making three level cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5518528D149254093EE1B6475CB1C17', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 13, NULL, NULL, 2, NULL, 0, 'Stack the cakes together, with the ornaments lined up. Carve off more cake if needed to make straight sides. Press the cakes to make sure they stick and freeze for another hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66C9BFFC58FFA141B848A4F95DF23CE1', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 14, NULL, NULL, 2, NULL, 0, 'Stand the cake upright, and frost. Smooth out the frosting to create the look of a box.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5986A368B74ED4EBD3DB5A8B5EE614F', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 15, NULL, NULL, 2, NULL, 0, 'Roll 2 ounces of red and 2 ounces of green fondant into logs, brush one side lightly with vodka, and stick the two ropes together. Twist, flatten, and roll out. Cut into two 1 1/2-inch by 15-inch pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96B78105302B08418AA2018DBE991401', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 16, NULL, NULL, 2, NULL, 0, 'Drape one piece from front to back over the center of the cake and the other from side to side. Cut the excess fondant at the bottom edges of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17446511DA804C4C9C75DA16B92187A2', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 17, NULL, NULL, 2, NULL, 0, 'Mix the remaining red fondant and green with a pinch of Tylose powder. Repeat above, cutting two 2-inch by 4-inch strips. Fold one strip over, into a loop, attaching the ends together with a bit of vodka. Roll up a small amount of paper towels and insert it into the loop. Pinch the ends and allow the loop to dry until it holds its shape. Repeat. Once dry, remove the paper towels. Cut the folded ends and stick the two loops together, forming a bow. Cut another small piece of fondant and wrap it around the center of the bow. Also cut two 5-inch long pieces for the ribbon. Attach pieces to cake using more vodka.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A513CB16EE90243ACC97D93F88FA875', X'32DE1A79F72904438ED825214B546FE6', X'32DE1A79F72904438ED825214B546FE6', 18, NULL, NULL, 2, NULL, 0, 'To serve the cake, cut across the cake to reveal the Christmas ornament hiding inside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D494301DEA42074B8D859B7536167DC0', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7CAF474DA478324898EF5E7AF7285659', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 1, NULL, NULL, 2, NULL, 0, 'Spread almonds out on a baking sheet, and toast for 5-6 minutes, until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'967C9BBCE60F8D44AACEC1894C66C4EE', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 2, NULL, NULL, 2, NULL, 0, 'Remove and pour nuts into the bowl of a food processor and let cool for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A3E5674A50BF84FB9574AFE3ACF7553', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 3, NULL, NULL, 2, NULL, 0, 'Add 1/4 cup of the sugar and pulse until the nuts become finely ground.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E7C2B7037EECB4198040191BCFD4308', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 4, NULL, NULL, 2, NULL, 0, 'Whisk the flour, salt, lemon zest, and cinnamon, if using, together then set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FBB27A6D3F2F5346B61328F32DE92366', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 5, NULL, NULL, 2, NULL, 0, 'Cream the butter and the remaining 1/2 cup sugar together in a large mixing bowl just until it becomes pale yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2BDA086DADEFD49B34EE224A530ABE8', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 6, NULL, NULL, 2, NULL, 0, 'Beat in vanilla and the egg yolks until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92297523CCC4FE4E99029AA0D156B05D', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 7, NULL, NULL, 2, NULL, 0, 'Mix in the ground almonds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A320B1F8A045D45A464E861226637BD', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 8, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients and mix just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1F4F623EA713B4BA2DDC9FE930299AB', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 9, NULL, NULL, 2, NULL, 0, 'Remove the dough from the bowl, divide in half, then flatten into two discs and place each half into a zip top bag or wrap in plastic wrap.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'532EA3062F8C5040A12A05E788CFD722', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 10, NULL, NULL, 2, NULL, 0, 'Refrigerate the dough for about 15-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B2D1BAE9E1DE846B2C61B3202293984', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 11, NULL, NULL, 2, NULL, 0, 'Dust a cutting board with flour or use a silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC23417DD692AE4E888ABEEE04D983D2', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 12, NULL, NULL, 2, NULL, 0, 'Roll 1 disc of chilled cookie dough out to 1/8 inch thickness onto the cutting board or silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBDEF9BB3B54F44F8C532C422F676388', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 13, NULL, NULL, 2, NULL, 0, 'Cut out as many shapes as you can using Linzer cutters or cookie cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4C464CD2B204F4698D88C9E2B0EB1C1', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 14, NULL, NULL, 2, NULL, 0, 'Remove excess dough, re-roll and continue to cut out shapes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B9669056BBE4740977801A4C48FDC12', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 15, NULL, NULL, 2, NULL, 0, 'Chill cut-outs in the freezer for 5-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03B4C1EB16113044BA28D8A2ED205BC4', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 16, NULL, NULL, 2, NULL, 0, 'Move cookies to a parchment lined baking sheet. (see tutorial for tips)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2112F72B1CC6204D8124D358F68FF41C', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 17, NULL, NULL, 2, NULL, 0, 'Bake for 8-12 minutes until lightly golden brown. The time will depend on the size of the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20462045F7A8B64CB28F6744763DAD2A', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 18, NULL, NULL, 2, NULL, 0, 'Roll the remaining disc of dough and cut out the same number and same shapes as before re-rolling as needed using the Linzer cutter fitted with the small cutter (which will cut the hole at the same time) or using traditional cookie cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BA7312197416F4AAF688436EE772383', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 19, NULL, NULL, 2, NULL, 0, 'If needed, cut a smaller hole in the center of each cookie using 1/2 inch to 1 inch cutters.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE3BFC5BE8847F439CF83B29683133A7', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 20, NULL, NULL, 2, NULL, 0, 'Remove excess dough re-roll and cut out more cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4302322854313F4AB565B63C47C4A61C', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 21, NULL, NULL, 2, NULL, 0, 'Freeze the cut-out dough for 5-10 minutes then move to parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D4E5A520C2E7B4DAE27BB2B1B79B0B4', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 22, NULL, NULL, 2, NULL, 0, 'Bake for about 8-12 minutes until lightly golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'550DDB430B6EE247AB191E86EC1DE676', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 23, NULL, NULL, 2, NULL, 0, 'Allow all the cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B70FF805261FEC459A3908EDF2D4D7D8', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 24, NULL, NULL, 2, NULL, 0, 'Dust the cookies with the holes in the middle with powdered sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E84AC00C939BDC48A38938E16EA2A4BD', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 25, NULL, NULL, 2, NULL, 0, 'Spread preserves, jam, curd, or chocolate spread on the whole cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1965520F73C8174D818C0FB60F188D83', X'9288426689030945820E1AA331154DDC', X'9288426689030945820E1AA331154DDC', 26, NULL, NULL, 2, NULL, 0, 'Place one powdered sugar dusted cut-out cookie on top of the jam and gently sandwich the cookies together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB2CFB511722464292E27887463BD5D8', X'B6C4297A058D2340BE4864FB3F3DEBDE', X'B6C4297A058D2340BE4864FB3F3DEBDE', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEFEE4A694FFC840B77635A17217BDA8', X'B6C4297A058D2340BE4864FB3F3DEBDE', X'B6C4297A058D2340BE4864FB3F3DEBDE', 1, NULL, NULL, 2, NULL, 0, 'Beat the butter until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D306CB5708B2C44B32B97DDB5FEEFAC', X'B6C4297A058D2340BE4864FB3F3DEBDE', X'B6C4297A058D2340BE4864FB3F3DEBDE', 2, NULL, NULL, 2, NULL, 0, 'Then add the peanut butter and beat until creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'649E478A67AF7A45BCCC4337A9F7E0AD', X'B6C4297A058D2340BE4864FB3F3DEBDE', X'B6C4297A058D2340BE4864FB3F3DEBDE', 3, NULL, NULL, 2, NULL, 0, 'Add the brown sugar and granulated sugar and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7762693D21C3374A9CD5EBE8F7F374D7', X'B6C4297A058D2340BE4864FB3F3DEBDE', X'B6C4297A058D2340BE4864FB3F3DEBDE', 4, NULL, NULL, 2, NULL, 0, 'Then stir in the eggs until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B67D5BE308B3934699FF80C404C0FE12', X'B6C4297A058D2340BE4864FB3F3DEBDE', X'B6C4297A058D2340BE4864FB3F3DEBDE', 5, NULL, NULL, 2, NULL, 0, 'In a separate bowl stir together the flour, baking soda, baking powder, and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C77657DC8698B4C84DE49C32331533A', X'B6C4297A058D2340BE4864FB3F3DEBDE', X'B6C4297A058D2340BE4864FB3F3DEBDE', 6, NULL, NULL, 2, NULL, 0, 'Combine the dry and wet ingredients and stir just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'107C98FFE216104FBBB3FBCED2A29A5B', X'B6C4297A058D2340BE4864FB3F3DEBDE', X'B6C4297A058D2340BE4864FB3F3DEBDE', 7, NULL, NULL, 2, NULL, 0, 'Scoop out 1-ounce portions of the cookie dough and arrange on a baking sheet with a few inches in between.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F4BE75FA0FD6F4993B8C3084EB98293', X'B6C4297A058D2340BE4864FB3F3DEBDE', X'B6C4297A058D2340BE4864FB3F3DEBDE', 8, NULL, NULL, 2, NULL, 0, 'Use a fork to flatten the cookies using a criss-cross pattern.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DC192084DC7874F9FAA93183465A891', X'B6C4297A058D2340BE4864FB3F3DEBDE', X'B6C4297A058D2340BE4864FB3F3DEBDE', 9, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until the edges are set and the tops of the cookies no longer look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'955088674DA34140B135D549B54AB243', X'B6C4297A058D2340BE4864FB3F3DEBDE', X'B6C4297A058D2340BE4864FB3F3DEBDE', 10, NULL, NULL, 2, NULL, 0, 'Allow cookies to cool for 10 minutes on the baking sheet then place them on a cooling rack until cooled completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'252B0886C43B18409DAFEDFC4CBE4B7B', X'A6A193502D4349458F72522B23FF2ACA', X'A6A193502D4349458F72522B23FF2ACA', 0, NULL, NULL, 2, NULL, 0, 'Pour finely chopped chocolate into a shallow bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'281AEA5A68ED31408EF00883F677BC5E', X'A6A193502D4349458F72522B23FF2ACA', X'A6A193502D4349458F72522B23FF2ACA', 1, NULL, NULL, 2, NULL, 0, 'Heat your cream over medium heat to a simmer on the stove, just until bubbles begin to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75F8B656206B614CAF099ED657A492DE', X'A6A193502D4349458F72522B23FF2ACA', X'A6A193502D4349458F72522B23FF2ACA', 2, NULL, NULL, 2, NULL, 0, 'Immediately pour the hot cream over the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9A636C5A5EBBD4185B0513AD05BD632', X'A6A193502D4349458F72522B23FF2ACA', X'A6A193502D4349458F72522B23FF2ACA', 3, NULL, NULL, 2, NULL, 0, 'Set aside undisturbed for 3 minutes to allow the chocolate to begin to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64E411FA4A09CF4EAD969511C64532E2', X'A6A193502D4349458F72522B23FF2ACA', X'A6A193502D4349458F72522B23FF2ACA', 4, NULL, NULL, 2, NULL, 0, 'Then begin stirring the mixture in small circles in the center of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D166A00FB299A248BD93247DFDFAB39B', X'A6A193502D4349458F72522B23FF2ACA', X'A6A193502D4349458F72522B23FF2ACA', 5, NULL, NULL, 2, NULL, 0, 'As the chocolate and cream start to come together you can begin to make your circle bigger, stirring until all the chocolate and cream emulsify.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44A83C76FBFCAA418FED499930870359', X'A6A193502D4349458F72522B23FF2ACA', X'A6A193502D4349458F72522B23FF2ACA', 6, NULL, NULL, 2, NULL, 0, 'Cover the bowl of ganache with a tight-fitting lid or press a piece of plastic wrap onto the top surface of the ganache and set it aside at room temperature until it thickens to a frosting-like texture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29EDE71A94036B4C8659CA3B73F8EADC', X'A6A193502D4349458F72522B23FF2ACA', X'A6A193502D4349458F72522B23FF2ACA', 7, NULL, NULL, 2, NULL, 0, 'Either fill a pastry bag with the chocolate ganache and pipe it onto half of the peanut butter cookies or simply spread it on using a knife or spatula.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FC1EE3D8164C94EA1506DBB77A1AFD7', X'A6A193502D4349458F72522B23FF2ACA', X'A6A193502D4349458F72522B23FF2ACA', 8, NULL, NULL, 2, NULL, 0, 'Gently press another peanut butter cookie on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'992E03F98C6F7C4FA024DEE5A7995B32', X'EED1F9BCBABB294DB6416AAEE3607C38', X'EED1F9BCBABB294DB6416AAEE3607C38', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A7A0A66BB834849B1C4E95A6CF00AE7', X'EED1F9BCBABB294DB6416AAEE3607C38', X'EED1F9BCBABB294DB6416AAEE3607C38', 1, NULL, NULL, 2, NULL, 0, 'Grease and flour a Mickey Mouse cake ring and set on a parchment paper-lined baking pan. (Or use a 6-inch pan and 2 jumbo muffin cups.)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'565AC1BD35CE86499BEDC57939E1DBDC', X'EED1F9BCBABB294DB6416AAEE3607C38', X'EED1F9BCBABB294DB6416AAEE3607C38', 2, NULL, NULL, 2, NULL, 0, 'Combine all the cake ingredients in a mixing bowl and beat, using an electric mixer, on low speed for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69AEE6939B372E41A965FA5CB4F7AC91', X'EED1F9BCBABB294DB6416AAEE3607C38', X'EED1F9BCBABB294DB6416AAEE3607C38', 3, NULL, NULL, 2, NULL, 0, 'Increase the speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'196452D74F21C245A96DBD87B749C0F6', X'EED1F9BCBABB294DB6416AAEE3607C38', X'EED1F9BCBABB294DB6416AAEE3607C38', 4, NULL, NULL, 2, NULL, 0, 'Pour batter into pan, filling it 2/3 full. Use the remaining batter to make cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF54FB148592044F9AF9206A4D91F8EE', X'EED1F9BCBABB294DB6416AAEE3607C38', X'EED1F9BCBABB294DB6416AAEE3607C38', 5, NULL, NULL, 2, NULL, 0, 'Bake for 26-34 minutes until a toothpick inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8976148AA4C26649971F5E90B325A091', X'EED1F9BCBABB294DB6416AAEE3607C38', X'EED1F9BCBABB294DB6416AAEE3607C38', 6, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9AB7B6DEB5CDD4C8A6F42D7E51EF229', X'EED1F9BCBABB294DB6416AAEE3607C38', X'EED1F9BCBABB294DB6416AAEE3607C38', 7, NULL, NULL, 2, NULL, 0, 'Carefully flip the cake upside down and unmold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0D63344EED6B347B6D3B1A4581C70C5', X'EED1F9BCBABB294DB6416AAEE3607C38', X'EED1F9BCBABB294DB6416AAEE3607C38', 8, NULL, NULL, 2, NULL, 0, 'Cut the cake in half creating two even layers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'311A94286F54944E8708012F3641C878', X'37EBF90229586C44B972573B1F5F1C86', X'37EBF90229586C44B972573B1F5F1C86', 0, NULL, NULL, 2, NULL, 0, 'Spread about 1 cup of white frosting in between the cake layers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA635DCE3EC9754989B5A3FD314A03E6', X'37EBF90229586C44B972573B1F5F1C86', X'37EBF90229586C44B972573B1F5F1C86', 1, NULL, NULL, 2, NULL, 0, 'Color about 2 tablespoons of white frosting using orange food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6800AB7EF720B4199ADAAB385C7CC7C', X'37EBF90229586C44B972573B1F5F1C86', X'37EBF90229586C44B972573B1F5F1C86', 2, NULL, NULL, 2, NULL, 0, 'Spoon into a pastry bag fitted with a small #14 or #15 star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26FD2D7FD4C4214FBC0A403E45937AC7', X'37EBF90229586C44B972573B1F5F1C86', X'37EBF90229586C44B972573B1F5F1C86', 3, NULL, NULL, 2, NULL, 0, 'Color the chocolate frosting using black food coloring and spoon into a pastry bag fitted with a coupler and a #3 round tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25643F07EF8D1C42A03A49D5078EB74A', X'9ED0D037552BED4E9F77C74EA5ED3FD6', X'9ED0D037552BED4E9F77C74EA5ED3FD6', 0, NULL, NULL, 2, NULL, 0, 'Fill a pastry bag fitted with a #3 round pastry tip with the clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B71B481840F594081CC0CAAE5C1F38C', X'9ED0D037552BED4E9F77C74EA5ED3FD6', X'9ED0D037552BED4E9F77C74EA5ED3FD6', 1, NULL, NULL, 2, NULL, 0, 'Set a piece of parchment paper over an image of Olaf (that has been printed in reverse) and pipe the gel over the outline of the mouth, eyes, carrot nose, and eyebrows.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36C2AE040BA3A2479E38C20D64AD9AF7', X'9ED0D037552BED4E9F77C74EA5ED3FD6', X'9ED0D037552BED4E9F77C74EA5ED3FD6', 2, NULL, NULL, 2, NULL, 0, 'Turn the template upside down and set it on the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C0AFB874282AA41B97F410509F631B7', X'9ED0D037552BED4E9F77C74EA5ED3FD6', X'9ED0D037552BED4E9F77C74EA5ED3FD6', 3, NULL, NULL, 2, NULL, 0, 'Gently press to adhere the piping gel to the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4FE8C3601EDEC46893D0F12EFD9CADC', X'9ED0D037552BED4E9F77C74EA5ED3FD6', X'9ED0D037552BED4E9F77C74EA5ED3FD6', 4, NULL, NULL, 2, NULL, 0, 'Remove the template.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31EF6718E34C4447A6435C2F21C32996', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', 0, NULL, NULL, 2, NULL, 0, 'Pipe black frosting directly over top of the piping gel outline.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF5FEE7B2E0E7F47899C292E20D1321E', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', 1, NULL, NULL, 2, NULL, 0, 'Remove the round tip and replace it with a (#18) star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE368862A9A6B54FB5B17283C7F9F198', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', 2, NULL, NULL, 2, NULL, 0, 'Pipe black frosting inside Olaf''s smile then add his pupils.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D98686108DB63946AFFC802C3A0021FA', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', 3, NULL, NULL, 2, NULL, 0, 'Pipe orange frosting stars into the carrot nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB443F42A046C84F9AA54E68B1B82DAA', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', 4, NULL, NULL, 2, NULL, 0, 'Spoon white frosting into a pastry bag fitted with a (#18) star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCBB09A86B6D374085EE6DABE0DA5244', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', 5, NULL, NULL, 2, NULL, 0, 'Pipe stars into the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79DCE97EEEEA674FBB71E66DD9417024', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', 6, NULL, NULL, 2, NULL, 0, 'Then pipe white stars all over the rest of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C5177E0C15C504AA3CEECBBF3EE22EC', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', X'FEE1FB1A5A5CBB48A8DAB894EC407A42', 7, NULL, NULL, 2, NULL, 0, 'If needed, replace the star tip with the round tip on the black frosting and pipe more black frosting over the pupils, eyebrows, and parts of the smile to make them show better.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9A35FF8D55A364EB2D3BC5BF4EF92BD', X'78D401ADC5BC6B4290710C91C8FBE69A', X'78D401ADC5BC6B4290710C91C8FBE69A', 0, NULL, NULL, 2, NULL, 0, 'Either use chocolate frosting to pipe some twigs onto your cake board or pipe twigs out of candy melts and allow them to harden before inserting them into the cake between the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB3C47E2B375064180A989E61A7827B2', X'1B7AF1B6D380B8448E11FA6E9A54921F', X'1B7AF1B6D380B8448E11FA6E9A54921F', 0, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA3F79F62987FF459022FAAA5FB7F1DC', X'1B7AF1B6D380B8448E11FA6E9A54921F', X'1B7AF1B6D380B8448E11FA6E9A54921F', 1, NULL, NULL, 2, NULL, 0, 'Pour popcorn into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E9B4257B74B2D47A1993CFF60A9027F', X'1B7AF1B6D380B8448E11FA6E9A54921F', X'1B7AF1B6D380B8448E11FA6E9A54921F', 2, NULL, NULL, 2, NULL, 0, 'Pour white chocolate over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DFC6AE4B62037743B9BB7BE2BAFC23A2', X'1B7AF1B6D380B8448E11FA6E9A54921F', X'1B7AF1B6D380B8448E11FA6E9A54921F', 3, NULL, NULL, 2, NULL, 0, 'Quickly stir it and toss it to coat the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84693A8CD3AB0E40B010177716A4AE4C', X'1B7AF1B6D380B8448E11FA6E9A54921F', X'1B7AF1B6D380B8448E11FA6E9A54921F', 4, NULL, NULL, 2, NULL, 0, 'Pour out onto the baking sheet and immediately toss the sprinkles and snowflakes over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9D5BEECEFD5BE4CA747FF7F66D228DD', X'1B7AF1B6D380B8448E11FA6E9A54921F', X'1B7AF1B6D380B8448E11FA6E9A54921F', 5, NULL, NULL, 2, NULL, 0, 'Wack the pan on the counter a few times. This helps get the candy to stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD6909D50C38934C85EFC1C5DD7547C3', X'1B7AF1B6D380B8448E11FA6E9A54921F', X'1B7AF1B6D380B8448E11FA6E9A54921F', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3770E6C4680BBD45909792D73D85008E', X'1B7AF1B6D380B8448E11FA6E9A54921F', X'1B7AF1B6D380B8448E11FA6E9A54921F', 7, NULL, NULL, 2, NULL, 0, 'Remove and break up into bite size chunks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A9C59B939CA9D49A985535C0BFF9BA9', X'D6ABFC789B47B84B80FC3236C55B26FF', X'D6ABFC789B47B84B80FC3236C55B26FF', 0, NULL, NULL, 2, NULL, 0, 'Pipe small triangle shaped orange carrot noses onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D021006A7D02924AB5D496C01F32A775', X'D6ABFC789B47B84B80FC3236C55B26FF', X'D6ABFC789B47B84B80FC3236C55B26FF', 1, NULL, NULL, 2, NULL, 0, 'Freeze for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33B25FBA084A5E4FB882B289B99D0B9C', X'D6ABFC789B47B84B80FC3236C55B26FF', X'D6ABFC789B47B84B80FC3236C55B26FF', 2, NULL, NULL, 2, NULL, 0, 'Remove from freezer and peel them off the paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'938FFD1446425E4EA95BEC94BFF344EB', X'D6ABFC789B47B84B80FC3236C55B26FF', X'D6ABFC789B47B84B80FC3236C55B26FF', 3, NULL, NULL, 2, NULL, 0, 'Dip one pretzel into white chocolate, leaving the top edge un-coated.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B551AD4BD3D004A9B3838C452285183', X'D6ABFC789B47B84B80FC3236C55B26FF', X'D6ABFC789B47B84B80FC3236C55B26FF', 4, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC172A9786F88B4A8D2E1F3E56AF5CB9', X'D6ABFC789B47B84B80FC3236C55B26FF', X'D6ABFC789B47B84B80FC3236C55B26FF', 5, NULL, NULL, 2, NULL, 0, 'Dip 5 more pretzels, setting them next to each other on the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7126091D47DD7444928E8E6A1C6624EF', X'D6ABFC789B47B84B80FC3236C55B26FF', X'D6ABFC789B47B84B80FC3236C55B26FF', 6, NULL, NULL, 2, NULL, 0, 'Freeze for 3-5 minutes until the chocolate sets.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B874C15C6FB624F94AD4DE23DCE4E3C', X'D6ABFC789B47B84B80FC3236C55B26FF', X'D6ABFC789B47B84B80FC3236C55B26FF', 7, NULL, NULL, 2, NULL, 0, 'Dip the un-coated part of the pretzels in dark chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64F39055892F204182AFA359F62FF7B9', X'D6ABFC789B47B84B80FC3236C55B26FF', X'D6ABFC789B47B84B80FC3236C55B26FF', 8, NULL, NULL, 2, NULL, 0, 'Set on baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DECD3C9420B5B14E9F2D82A1EF61EBA2', X'D6ABFC789B47B84B80FC3236C55B26FF', X'D6ABFC789B47B84B80FC3236C55B26FF', 9, NULL, NULL, 2, NULL, 0, 'Dip one whole pretzel in dark chocolate and set it across the snowman creating the brim of the hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F57FA3175E6DC944B301ECC2F2401943', X'D6ABFC789B47B84B80FC3236C55B26FF', X'D6ABFC789B47B84B80FC3236C55B26FF', 10, NULL, NULL, 2, NULL, 0, 'Freeze for a few minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C45BEA4F2C64794AB375D72F65E017FD', X'D6ABFC789B47B84B80FC3236C55B26FF', X'D6ABFC789B47B84B80FC3236C55B26FF', 11, NULL, NULL, 2, NULL, 0, 'Attach one orange carrot nose to the snowman''s face and a white snowflake on each hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F15AD5E5888F18488FFA8C86AA75BE3E', X'D6ABFC789B47B84B80FC3236C55B26FF', X'D6ABFC789B47B84B80FC3236C55B26FF', 12, NULL, NULL, 2, NULL, 0, 'Paint or pipe dark chocolate dots for the eyes and mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5A72FE2B2232845907FEB270395A982', X'DDD5EAA1552F6443AB09A43B798ABE1F', X'DDD5EAA1552F6443AB09A43B798ABE1F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A81E1539E075894DBE993DE7DC959728', X'DDD5EAA1552F6443AB09A43B798ABE1F', X'DDD5EAA1552F6443AB09A43B798ABE1F', 1, NULL, NULL, 2, NULL, 0, 'Grease and flour a 9x13 pan or line it with non-stick tin foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1CA514401582D4EA3B4ED6A4038B2BD', X'DDD5EAA1552F6443AB09A43B798ABE1F', X'DDD5EAA1552F6443AB09A43B798ABE1F', 2, NULL, NULL, 2, NULL, 0, 'Combine all the cake ingredients in a mixing bowl and beat, using an electric mixer, on low speed for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2D541D97E78864FB2B4D5EB54B6D773', X'DDD5EAA1552F6443AB09A43B798ABE1F', X'DDD5EAA1552F6443AB09A43B798ABE1F', 3, NULL, NULL, 2, NULL, 0, 'Increase the speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53455DF898E78044BD8CA73BC6483572', X'DDD5EAA1552F6443AB09A43B798ABE1F', X'DDD5EAA1552F6443AB09A43B798ABE1F', 4, NULL, NULL, 2, NULL, 0, 'Pour batter into pan and spread into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97C496000B3F9B4EA1B77147540A659E', X'DDD5EAA1552F6443AB09A43B798ABE1F', X'DDD5EAA1552F6443AB09A43B798ABE1F', 5, NULL, NULL, 2, NULL, 0, 'Bake for 24-30 minutes until a toothpick inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0CCDB1724A828446A873A675DE3BAE4F', X'DDD5EAA1552F6443AB09A43B798ABE1F', X'DDD5EAA1552F6443AB09A43B798ABE1F', 6, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'992F613734DCA64C9AD7081A4247A9A9', X'C4CCBF04BC5B244CBFC866AF1972E535', X'C4CCBF04BC5B244CBFC866AF1972E535', 0, NULL, NULL, 2, NULL, 0, 'Crumble the cake into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67AE8533E335844CA63C2E1C541FDCD8', X'C4CCBF04BC5B244CBFC866AF1972E535', X'C4CCBF04BC5B244CBFC866AF1972E535', 1, NULL, NULL, 2, NULL, 0, 'Stir in 1/4 cup of the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1597BFC8EB03A48BE1AD8ABE3EBE8DD', X'C4CCBF04BC5B244CBFC866AF1972E535', X'C4CCBF04BC5B244CBFC866AF1972E535', 2, NULL, NULL, 2, NULL, 0, 'Add more, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7276D1BA3C40B048ABDB31CC2DCD08E2', X'C4CCBF04BC5B244CBFC866AF1972E535', X'C4CCBF04BC5B244CBFC866AF1972E535', 3, NULL, NULL, 2, NULL, 0, 'Mix with your hands until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C836D778BD9B041A7DF488F7E4DE481', X'2B03BBC95784604F8511B628A034A3BD', X'2B03BBC95784604F8511B628A034A3BD', 0, NULL, NULL, 2, NULL, 0, 'Paint an orange nose, chocolate eyes and top hat, and a green or red scarf into each of the snowman popsicle mold cavities. See the blog post for detailed instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A844C086D3B7340A80C0951D85894C5', X'2B03BBC95784604F8511B628A034A3BD', X'2B03BBC95784604F8511B628A034A3BD', 1, NULL, NULL, 2, NULL, 0, 'Freeze until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AD7F7C27A0E574FA78C3BEB16644350', X'2B03BBC95784604F8511B628A034A3BD', X'2B03BBC95784604F8511B628A034A3BD', 2, NULL, NULL, 2, NULL, 0, 'Paint white chocolate over the buttons and facial features then spoon in more white chocolate and spread it around the bottom and sides of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'138DA96151A94444B289CB4E0A152521', X'2B03BBC95784604F8511B628A034A3BD', X'2B03BBC95784604F8511B628A034A3BD', 3, NULL, NULL, 2, NULL, 0, 'Freeze until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05EE7ED13C99FB4BA429C93B06C8E38B', X'2B03BBC95784604F8511B628A034A3BD', X'2B03BBC95784604F8511B628A034A3BD', 4, NULL, NULL, 2, NULL, 0, 'Fill the snowmen bellies with cake ball filling leaving 1/8 inch at the top of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAA7C6E5E945254C9345820B280F3045', X'2B03BBC95784604F8511B628A034A3BD', X'2B03BBC95784604F8511B628A034A3BD', 5, NULL, NULL, 2, NULL, 0, 'Spread white chocolate over top of the cake ball filling and scrape off the excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32551D6055FB294E9B9B6398B95534C6', X'2B03BBC95784604F8511B628A034A3BD', X'2B03BBC95784604F8511B628A034A3BD', 6, NULL, NULL, 2, NULL, 0, 'Freeze again then once the candy has hardened you can un-mold the snowmen cakesicles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F56A84AA68FC64296AB1DB203A900D6', X'7CC381751738814E8DAC9BDB3420A445', X'7CC381751738814E8DAC9BDB3420A445', 0, NULL, NULL, 2, NULL, 0, 'Wash and dry your oranges.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF7DBABBA5E0364A82E9AE271E6E7998', X'7CC381751738814E8DAC9BDB3420A445', X'7CC381751738814E8DAC9BDB3420A445', 1, NULL, NULL, 2, NULL, 0, 'Use a Microplane grater to zest the oranges.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37FD538D3516964EB7C9275D7AA6E08E', X'7CC381751738814E8DAC9BDB3420A445', X'7CC381751738814E8DAC9BDB3420A445', 2, NULL, NULL, 2, NULL, 0, 'Reserve the zest for the cookie dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'544F5AF413889E45ADCB1943BA25453D', X'7CC381751738814E8DAC9BDB3420A445', X'7CC381751738814E8DAC9BDB3420A445', 3, NULL, NULL, 2, NULL, 0, 'Cut the oranges in half and juice them into a small microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35B0A2DB7FB1544CADFB29829FB6FB29', X'7CC381751738814E8DAC9BDB3420A445', X'7CC381751738814E8DAC9BDB3420A445', 4, NULL, NULL, 2, NULL, 0, 'Heat the orange juice in the microwave for 40 seconds (or on the stove just until it begins to boil.)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7599C1BFA685444DB1240DCAD6B02D0F', X'7CC381751738814E8DAC9BDB3420A445', X'7CC381751738814E8DAC9BDB3420A445', 5, NULL, NULL, 2, NULL, 0, 'Pour the hot juice over the dried cranberries and let them soak for 20-30 minutes, stirring every five minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7856F46B320B5C4FB43E6E45026C5512', X'7CC381751738814E8DAC9BDB3420A445', X'7CC381751738814E8DAC9BDB3420A445', 6, NULL, NULL, 2, NULL, 0, 'Pour the soaked cranberries into a mesh sieve and press down on them to remove all the excess juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52E8980A5D06DC4DBD8FE2C80A3600E6', X'7CC381751738814E8DAC9BDB3420A445', X'7CC381751738814E8DAC9BDB3420A445', 7, NULL, NULL, 2, NULL, 0, 'Set aside until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20619B98530BDE47936174203017474B', X'E82A756C5C90614F87DFA92E26D92812', X'E82A756C5C90614F87DFA92E26D92812', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E302CDE93AD66B498109B24C3F906DC5', X'E82A756C5C90614F87DFA92E26D92812', X'E82A756C5C90614F87DFA92E26D92812', 1, NULL, NULL, 2, NULL, 0, 'Cream the butter and brown sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8635EB8E6758446A80E8CE6091BDE8E', X'E82A756C5C90614F87DFA92E26D92812', X'E82A756C5C90614F87DFA92E26D92812', 2, NULL, NULL, 2, NULL, 0, 'Stir in the Karo® Corn Syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14BDF24DFA216E40AACEE6753C1B535D', X'E82A756C5C90614F87DFA92E26D92812', X'E82A756C5C90614F87DFA92E26D92812', 3, NULL, NULL, 2, NULL, 0, 'Beat in the egg and egg yolk.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AFD3426F23D7D4D8F1D3CE0F36D2F69', X'E82A756C5C90614F87DFA92E26D92812', X'E82A756C5C90614F87DFA92E26D92812', 4, NULL, NULL, 2, NULL, 0, 'Add the orange zest.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60F7CB39971AC94A8443C85B2AE825BF', X'E82A756C5C90614F87DFA92E26D92812', X'E82A756C5C90614F87DFA92E26D92812', 5, NULL, NULL, 2, NULL, 0, 'Stir together the flour, baking soda, and salt and stir into the wet ingredients just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4392AFD4F53AD4CBEBA2FFB58CE637D', X'E82A756C5C90614F87DFA92E26D92812', X'E82A756C5C90614F87DFA92E26D92812', 6, NULL, NULL, 2, NULL, 0, 'Toss in the drained cranberries and chocolate chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D724C05E46967B4FBB51477569EF1836', X'E82A756C5C90614F87DFA92E26D92812', X'E82A756C5C90614F87DFA92E26D92812', 7, NULL, NULL, 2, NULL, 0, 'Scoop out 24 portions of the cookie dough, about 3 tablespoons each, onto four parchment paper-lined baking sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE97E58658439542A3645FFE805091AE', X'E82A756C5C90614F87DFA92E26D92812', X'E82A756C5C90614F87DFA92E26D92812', 8, NULL, NULL, 2, NULL, 0, 'Bake for 16-18 minutes, until the edges just begin to brown and tops of the cookies begin to look dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD4CFF435B2F3649A5EEF249337999EA', X'E82A756C5C90614F87DFA92E26D92812', X'E82A756C5C90614F87DFA92E26D92812', 9, NULL, NULL, 2, NULL, 0, 'Allow cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00648FC1BF8CBC418BCACA1F41525A73', X'C004810B2185B142B06B4BBE5CEC1F51', X'C004810B2185B142B06B4BBE5CEC1F51', 0, NULL, NULL, 2, NULL, 0, 'Crumble the cake and blend it together with the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EBF53AB10C7044EA703A14C3E85C1DE', X'C004810B2185B142B06B4BBE5CEC1F51', X'C004810B2185B142B06B4BBE5CEC1F51', 1, NULL, NULL, 2, NULL, 0, 'Add more frosting if desired until the cake ball filling has a nice chocolate truffle style texture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A527CC176DDD848A787E9982DE6EF99', X'CFF604B5DD403241AD0054CF114F7E1F', X'CFF604B5DD403241AD0054CF114F7E1F', 0, NULL, NULL, 2, NULL, 0, 'Spoon 1 tablespoonful of white chocolate into one popsicle cavity.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2ED0C8FAAA15A142AD85A4F6CB982849', X'CFF604B5DD403241AD0054CF114F7E1F', X'CFF604B5DD403241AD0054CF114F7E1F', 1, NULL, NULL, 2, NULL, 0, 'Paint it into a thin layer on the bottom and up the sides of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'663C5CC75A8A92429E88953619F6088A', X'CFF604B5DD403241AD0054CF114F7E1F', X'CFF604B5DD403241AD0054CF114F7E1F', 2, NULL, NULL, 2, NULL, 0, 'Immediately insert then remove a popsicle stick into the mold, to remove any white chocolate from that area.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6807B8C2C428F4A8A76609C412C636A', X'CFF604B5DD403241AD0054CF114F7E1F', X'CFF604B5DD403241AD0054CF114F7E1F', 3, NULL, NULL, 2, NULL, 0, 'Repeat creating 4 white chocolate shells in your popsicle mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F6778C260559A4BB8956A4CB69D8FFD', X'CFF604B5DD403241AD0054CF114F7E1F', X'CFF604B5DD403241AD0054CF114F7E1F', 4, NULL, NULL, 2, NULL, 0, 'Pop the mold into the refrigerator, if using pure white chocolate, and into the freezer if using confectionery coating (Candy Melts).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60C219E0ABF2F3499A0FC13422479C9A', X'CFF604B5DD403241AD0054CF114F7E1F', X'CFF604B5DD403241AD0054CF114F7E1F', 5, NULL, NULL, 2, NULL, 0, 'Chill for 3-5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E958938D8E36646A31BE01E52A07903', X'CFF604B5DD403241AD0054CF114F7E1F', X'CFF604B5DD403241AD0054CF114F7E1F', 6, NULL, NULL, 2, NULL, 0, 'Scoop out 1 tablespoonful of the cake ball filling and press it into the candy shell, leaving at least 1/16 of an inch of space at the top for the additional layer of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDFDE417BCBFB24691BDFEDDE48211A2', X'CFF604B5DD403241AD0054CF114F7E1F', X'CFF604B5DD403241AD0054CF114F7E1F', 7, NULL, NULL, 2, NULL, 0, 'Repeat filling all the popsicles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'655A2FA520B7FF46B734ADF4A2EA4ABB', X'CFF604B5DD403241AD0054CF114F7E1F', X'CFF604B5DD403241AD0054CF114F7E1F', 8, NULL, NULL, 2, NULL, 0, 'Spoon about a teaspoon of white chocolate over top and spread it out thin.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'620B59C4A5C4EB40BB2E8FD051135031', X'CFF604B5DD403241AD0054CF114F7E1F', X'CFF604B5DD403241AD0054CF114F7E1F', 9, NULL, NULL, 2, NULL, 0, 'Use an offset spatula to scrape off any excess white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D4957CBD1895546B1182D5417BF81F0', X'CFF604B5DD403241AD0054CF114F7E1F', X'CFF604B5DD403241AD0054CF114F7E1F', 10, NULL, NULL, 2, NULL, 0, 'Repeat covering all the cakesicles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB38BFD5C7C3614FB7C5BBE90EBAF3FA', X'CFF604B5DD403241AD0054CF114F7E1F', X'CFF604B5DD403241AD0054CF114F7E1F', 11, NULL, NULL, 2, NULL, 0, 'Chill for about 2 minutes until the candy layer hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D35BC5C7A5257B40A74070374B3597A5', X'CFF604B5DD403241AD0054CF114F7E1F', X'CFF604B5DD403241AD0054CF114F7E1F', 12, NULL, NULL, 2, NULL, 0, 'Unmold the cakesicles and they are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B8B710D55B80E74F9AF73CA44B8C7650', X'3925FDD60D479647B1FD8E157D1A5603', X'3925FDD60D479647B1FD8E157D1A5603', 0, NULL, NULL, 2, NULL, 0, 'Dip strawberries in melted and tempered pure white chocolate or in melted white confectionery coating (candy melts).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2447D736167FFA4AA344C9410BA5B856', X'3925FDD60D479647B1FD8E157D1A5603', X'3925FDD60D479647B1FD8E157D1A5603', 1, NULL, NULL, 2, NULL, 0, 'Allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4102B8CA77BFE45A714352450A4A783', X'3925FDD60D479647B1FD8E157D1A5603', X'3925FDD60D479647B1FD8E157D1A5603', 2, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'664CC44F6CC0024287EC9B68B025ABB7', X'3925FDD60D479647B1FD8E157D1A5603', X'3925FDD60D479647B1FD8E157D1A5603', 3, NULL, NULL, 2, NULL, 0, 'If desired, allow the white chocolate to puddle at the bottom of the strawberry for a few seconds, then lift up the berry and place it on a clean spot on the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'956ADAABF6D9734590C403FF5465A497', X'3925FDD60D479647B1FD8E157D1A5603', X'3925FDD60D479647B1FD8E157D1A5603', 4, NULL, NULL, 2, NULL, 0, 'Chill in the fridge (for 3-5 minutes) if using candy melts or let air dry (for about 10 minutes) if using pure white chocolate just until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF7D5DE77A67BA468A1FE8C6AA22D033', X'0166F9A7E6629B43BBDF259C292A73CA', X'0166F9A7E6629B43BBDF259C292A73CA', 0, NULL, NULL, 2, NULL, 0, 'EYES and NOSE: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two small circles and one 1/2 inch heart for each polar bear using a small plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'477322FC6CAB6C4E9B84672C5D7F5CF3', X'0166F9A7E6629B43BBDF259C292A73CA', X'0166F9A7E6629B43BBDF259C292A73CA', 1, NULL, NULL, 2, NULL, 0, 'EARS: Cut a curved sliver off of two white chocolate wafers for each polar bear or create ears using 1/4 inch thick white modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0BF93C5981125478EFC0ED09087B637', X'0166F9A7E6629B43BBDF259C292A73CA', X'0166F9A7E6629B43BBDF259C292A73CA', 2, NULL, NULL, 2, NULL, 0, 'BOW: Cut two small hearts and one small circle out of pink or red modeling chocolate. Set the hearts together with the points touching then attach the circle over top using water or clear piping gel to `glue` the bow together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C3DBBEC0A148E4CA9BE2E1357E6C0A5', X'0166F9A7E6629B43BBDF259C292A73CA', X'0166F9A7E6629B43BBDF259C292A73CA', 3, NULL, NULL, 2, NULL, 0, 'Dip the white chocolate coated strawberries into white chocolate again and press a fork up against the chocolate then pull away quickly. Do this all over the berry, over and over again, until spikes of fur start to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1FBBDC50F58AA468C19577A1F258254', X'0166F9A7E6629B43BBDF259C292A73CA', X'0166F9A7E6629B43BBDF259C292A73CA', 4, NULL, NULL, 2, NULL, 0, 'Attach two candy ears on the top edge of the strawberry on opposite sides of the green stem using melted white chocolate. Then add more white chocolate over the ears and create spikes of fur on them too.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'233D7456667F254DBF3675E979630389', X'0166F9A7E6629B43BBDF259C292A73CA', X'0166F9A7E6629B43BBDF259C292A73CA', 5, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add a nose to the tip of the strawberry and two eyes above it. Add a bow if making female polar bears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29B53D7B6927DB40883BF60FA382F483', X'DA78E03DC6845B4B902D3B146A9B79D0', X'DA78E03DC6845B4B902D3B146A9B79D0', 0, NULL, NULL, 2, NULL, 0, 'NOSE and FUR PATCHES: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two ovals using a plunger cutter and cut out a small 1/2 inch heart using a heart plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB976C1EFDEC9540B36E6382A760795D', X'DA78E03DC6845B4B902D3B146A9B79D0', X'DA78E03DC6845B4B902D3B146A9B79D0', 1, NULL, NULL, 2, NULL, 0, 'EARS: You can cut a small sliver off two dark chocolate wafers to create the ears or you can cut out black modeling chocolate circles (with a curved notch removed) to create the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD20FC0A76F0A146B7D57BFB1324E897', X'DA78E03DC6845B4B902D3B146A9B79D0', X'DA78E03DC6845B4B902D3B146A9B79D0', 2, NULL, NULL, 2, NULL, 0, 'BOW: Roll out red modeling chocolate to about 1/8th inch thickness and cut out two small hearts and one small circle. Set the points of the hearts together then use either water or clear piping gel to attach the circle over the intersection of the points creating a bow.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D1DC043309905419AB3A525E865E214', X'DA78E03DC6845B4B902D3B146A9B79D0', X'DA78E03DC6845B4B902D3B146A9B79D0', 3, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add two ears on the top edge of the strawberry, a nose to the tip of the strawberry and two fur patches above it off to the side. Add a bow in front of an ear to make panda bear girls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96CB3B535AD6B347BA18DA08AC3E9409', X'45C46504C156F24C8654E5F0743BBF48', X'45C46504C156F24C8654E5F0743BBF48', 0, NULL, NULL, 2, NULL, 0, 'Cut gumdrops into small pieces, toss in sugar, then pour into a colander to remove excess sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E00E30BC58B6C49A5853ADCCAF611BA', X'45C46504C156F24C8654E5F0743BBF48', X'45C46504C156F24C8654E5F0743BBF48', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EE24FBB931906849B706877B494DD0CC', X'45C46504C156F24C8654E5F0743BBF48', X'45C46504C156F24C8654E5F0743BBF48', 2, NULL, NULL, 2, NULL, 0, 'Cream Country Crock Baking Sticks and sugar together until fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69F81B1BA1FCCC4E9C2DDA5C08DECF4B', X'45C46504C156F24C8654E5F0743BBF48', X'45C46504C156F24C8654E5F0743BBF48', 3, NULL, NULL, 2, NULL, 0, 'Mix in eggs and vanilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34BE0B3DE8C3474096AE9EBE4DED3572', X'45C46504C156F24C8654E5F0743BBF48', X'45C46504C156F24C8654E5F0743BBF48', 4, NULL, NULL, 2, NULL, 0, 'In a separate bowl whisk together the flour, salt, baking powder and baking soda.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E32E591F7C7CD14DB9102733EB3A1D66', X'45C46504C156F24C8654E5F0743BBF48', X'45C46504C156F24C8654E5F0743BBF48', 5, NULL, NULL, 2, NULL, 0, 'Stir dry ingredients into the wet mixture just until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'763732DC6D635941A8E6F5E29C361BFC', X'45C46504C156F24C8654E5F0743BBF48', X'45C46504C156F24C8654E5F0743BBF48', 6, NULL, NULL, 2, NULL, 0, 'Mix in about half of the gumdrop pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'127831E870FED84D965BDD1B2D3E06BB', X'45C46504C156F24C8654E5F0743BBF48', X'45C46504C156F24C8654E5F0743BBF48', 7, NULL, NULL, 2, NULL, 0, 'Scoop out about 2 tablespoons of dough onto a parchment paper lined baking sheet. Repeat making 36 scoops.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27055784D7D8CF43B7D412F126354DB5', X'45C46504C156F24C8654E5F0743BBF48', X'45C46504C156F24C8654E5F0743BBF48', 8, NULL, NULL, 2, NULL, 0, 'Flatten cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'364AB4690BFC284288B4BB6258E84A74', X'45C46504C156F24C8654E5F0743BBF48', X'45C46504C156F24C8654E5F0743BBF48', 9, NULL, NULL, 2, NULL, 0, 'Top cookies with more gumdrops.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0CE8BF72A1CFB844A7592544864B0EDA', X'45C46504C156F24C8654E5F0743BBF48', X'45C46504C156F24C8654E5F0743BBF48', 10, NULL, NULL, 2, NULL, 0, 'Bake for 8-12 minutes, just until the tops of the cookies start to look dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77E3827E980E1E46B59D243A80FD2E3E', X'45C46504C156F24C8654E5F0743BBF48', X'45C46504C156F24C8654E5F0743BBF48', 11, NULL, NULL, 2, NULL, 0, 'Cool cookies on baking sheet for 2-3 minutes, then remove and place on a wire cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BB57C4EDF0F234DA2B0B17B9AE9BA1F', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 0, NULL, NULL, 2, NULL, 0, 'Stir together the OREO Cookie crumbs with the cream cheese until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AEB7BC15E0926428F0E06CB58486F78', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 1, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap and refrigerate for about 30 minutes until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CBDF8C69563F84B8A3982340802B8A5', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 2, NULL, NULL, 2, NULL, 0, 'Scoop out 36 tablespoon size portions of the dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6820695B6ED21F448709B05811179D03', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 3, NULL, NULL, 2, NULL, 0, 'Roll each into a ball then shape into a Christmas light bulb.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3656EF97A33C2D4DAAE1F01A89FDE756', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 4, NULL, NULL, 2, NULL, 0, 'Freeze for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD35139F701C284F83CEB3AC3DF7C793', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 5, NULL, NULL, 2, NULL, 0, 'Remove a few bulbs at a time and insert a toothpick into the fat end of the bulb.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7FAF4D0A451614EB7BA31ACBD4D7F79', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 6, NULL, NULL, 2, NULL, 0, 'Dip one bulb at a time into a colored confectionery coating leaving a small opening near the toothpick then lift out of the coating and allow the excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E492569447C4BF44BD99FF14F8E25F16', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 7, NULL, NULL, 2, NULL, 0, 'Immediately and quickly sprinkle the same colored sugar over the candy coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A8F661EAE4F704FA37CBDECE2D85EE4', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 8, NULL, NULL, 2, NULL, 0, 'Set the OREO Cookie Ball Christmas Light onto a parchment paper lined baking sheet and allow to dry at room temperature for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3ED0689F4351DA4A8ABD20669EB1A7A3', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 9, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A203B8DD282C594493B0466DD230C8DD', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 10, NULL, NULL, 2, NULL, 0, 'Once the cookie dough has oozed out from around the toothpick, remove the excess leaving a flat opening.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9612A54ECB2CA74EBBDFB96DC56B83B6', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 11, NULL, NULL, 2, NULL, 0, 'Cut the fudge rolls in half then use the knife to create grooves that look like the screw base of the light bulb.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8A151DA82091840A5AF9D31722E467F', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 12, NULL, NULL, 2, NULL, 0, 'Attach a fudge roll screw base to each light bulb using melted confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D37285BB1AC41419D2B942539CF2C0A', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 13, NULL, NULL, 2, NULL, 0, 'Let the candy coating harden for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4235A6BD1E9E2448B3FB50583D91BA47', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 14, NULL, NULL, 2, NULL, 0, 'Brush edible silver luster dust over the fudge roll if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7B671BDE8A18C4D91C31442312E402E', X'8826C2051E21224EB100D44CAAAA3FB4', X'8826C2051E21224EB100D44CAAAA3FB4', 15, NULL, NULL, 2, NULL, 0, 'Your OREO Cookie Ball Christmas Lights are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA907DFBBA65EE419F24EA7C70E7B30D', X'0AA191551922624791B73DAC021D4DAC', X'0AA191551922624791B73DAC021D4DAC', 0, NULL, NULL, 2, NULL, 0, 'Combine flour, baking powder, and salt in a mixing bowl and blend with a fork or whisk.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E798AD962B5D4D4C9AA085642C4ECC23', X'0AA191551922624791B73DAC021D4DAC', X'0AA191551922624791B73DAC021D4DAC', 1, NULL, NULL, 2, NULL, 0, 'Add half of the lard and use a pastry cutter to cut the lard into the flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DA33A4CC72A4C4F832FE2CC2854206B', X'0AA191551922624791B73DAC021D4DAC', X'0AA191551922624791B73DAC021D4DAC', 2, NULL, NULL, 2, NULL, 0, 'Add the remaining lard and continue to cut it into the flour until it resembles coarse crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43795D8526EFEB46B4C808EEDF3FD6F1', X'0AA191551922624791B73DAC021D4DAC', X'0AA191551922624791B73DAC021D4DAC', 3, NULL, NULL, 2, NULL, 0, 'Slowly pour in the water stirring as you go until the dough comes together. Add at least ¾ of a cup of the water then only add any remaining water as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'384682B1F80E324CB2984CEC834F3275', X'0AA191551922624791B73DAC021D4DAC', X'0AA191551922624791B73DAC021D4DAC', 4, NULL, NULL, 2, NULL, 0, 'Knead the dough on a flour dusted cutting board until the dough is no longer sticky and comes together in a cohesive ball then cover the dough and set aside for 10-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'357FE07F7F1B4943A01B1A73909292F1', X'946CEE2E9EB07F478A1D2CA00ACF6B05', X'946CEE2E9EB07F478A1D2CA00ACF6B05', 0, NULL, NULL, 2, NULL, 0, 'Melt butter in a non-stick 10 inch skillet over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30D8807D56FBBC428C2FF2CEF210784B', X'946CEE2E9EB07F478A1D2CA00ACF6B05', X'946CEE2E9EB07F478A1D2CA00ACF6B05', 1, NULL, NULL, 2, NULL, 0, 'Whisk together 5 whole eggs and 1 egg yolk with the heavy whipping cream, lemon juice, and salt and pepper. Set aside the remaining egg white for use later.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53F8A8D4A1C5804397B27E75BBCB0B6B', X'946CEE2E9EB07F478A1D2CA00ACF6B05', X'946CEE2E9EB07F478A1D2CA00ACF6B05', 2, NULL, NULL, 2, NULL, 0, 'Pour the egg mixture into the hot pan and continuously scrape the sides and bottom of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9F330AE694E7748ADF38E8014CF9D93', X'946CEE2E9EB07F478A1D2CA00ACF6B05', X'946CEE2E9EB07F478A1D2CA00ACF6B05', 3, NULL, NULL, 2, NULL, 0, 'Once the eggs begin to solidify, turn the heat down to low.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0869BF32886F4346BE910788A388CF32', X'946CEE2E9EB07F478A1D2CA00ACF6B05', X'946CEE2E9EB07F478A1D2CA00ACF6B05', 4, NULL, NULL, 2, NULL, 0, 'Cook just until the eggs set. Immediately remove from the pan and allow to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'441349D992E68F4DA627154656AF67FC', X'F2DA22CBF17F8C49B43F8245BCEF832A', X'F2DA22CBF17F8C49B43F8245BCEF832A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F37B82E23879944B009EDD8BDEBB311', X'F2DA22CBF17F8C49B43F8245BCEF832A', X'F2DA22CBF17F8C49B43F8245BCEF832A', 1, NULL, NULL, 2, NULL, 0, 'Cut the dough into 6 equal portions.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFB7CA18760A43429C31A8EE78ED7CD4', X'F2DA22CBF17F8C49B43F8245BCEF832A', X'F2DA22CBF17F8C49B43F8245BCEF832A', 2, NULL, NULL, 2, NULL, 0, 'Dust your work surface with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20D1F7E985E51349840BF6EFCD16FE08', X'F2DA22CBF17F8C49B43F8245BCEF832A', X'F2DA22CBF17F8C49B43F8245BCEF832A', 3, NULL, NULL, 2, NULL, 0, 'Roll out one ball of dough into a large 8-10 inch circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B245D4A78418944A3222AE1A7F20AA2', X'F2DA22CBF17F8C49B43F8245BCEF832A', X'F2DA22CBF17F8C49B43F8245BCEF832A', 4, NULL, NULL, 2, NULL, 0, 'Drape the dough over one snowflake cavity in the non-stick pan allowing the dough to form the snowflake shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'581DA42B1CB4B2419064BFC207E71D5D', X'F2DA22CBF17F8C49B43F8245BCEF832A', X'F2DA22CBF17F8C49B43F8245BCEF832A', 5, NULL, NULL, 2, NULL, 0, 'Fill with 1/6th of the eggs, sausage, cheese, and tater tots.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC056496B9237546B200F92485F99543', X'F2DA22CBF17F8C49B43F8245BCEF832A', X'F2DA22CBF17F8C49B43F8245BCEF832A', 6, NULL, NULL, 2, NULL, 0, 'Fold the dough over the filling, cutting off any excess dough and pinching the seams together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'913C140AA77F2C40A5D894659FCE809E', X'F2DA22CBF17F8C49B43F8245BCEF832A', X'F2DA22CBF17F8C49B43F8245BCEF832A', 7, NULL, NULL, 2, NULL, 0, 'Brush egg white over top of the dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'620F98D2CE59A4438CF45AF3B6C102CB', X'F2DA22CBF17F8C49B43F8245BCEF832A', X'F2DA22CBF17F8C49B43F8245BCEF832A', 8, NULL, NULL, 2, NULL, 0, 'Repeat filling the remaining 5 snowflakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBE8DD1E036F0B4CB7BE88FDC08F2B35', X'F2DA22CBF17F8C49B43F8245BCEF832A', X'F2DA22CBF17F8C49B43F8245BCEF832A', 9, NULL, NULL, 2, NULL, 0, 'Bake for 24-28 minutes until the tops of the dough turn lightly golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6ECDEC10792EA14386E86813FC448483', X'F2DA22CBF17F8C49B43F8245BCEF832A', X'F2DA22CBF17F8C49B43F8245BCEF832A', 10, NULL, NULL, 2, NULL, 0, 'Turn the snowflake burritos out onto a cooling rack and let cool for a few minutes before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A43A00CE254484F8AD4F954C8179685', X'F2DA22CBF17F8C49B43F8245BCEF832A', X'F2DA22CBF17F8C49B43F8245BCEF832A', 11, NULL, NULL, 2, NULL, 0, 'Serve with sour cream, guacamole, and or salsa if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'531ACB523D53074D962F9AFF7A1BCEE6', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 0, NULL, NULL, 2, NULL, 0, 'Beat cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84627F01B060C84198583A6251349A71', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 1, NULL, NULL, 2, NULL, 0, 'Stir in sharp cheddar cheese and taco seasoning.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'242901181AF0C646A75DAD8F5FCF0BFD', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 2, NULL, NULL, 2, NULL, 0, 'Add RO*TEL Diced Tomatoes and Green Chilies and stir just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F040A6BDD43DDA409D3B0A9BA3FD6A0D', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 3, NULL, NULL, 2, NULL, 0, 'Spoon onto plastic wrap and shape into an oval, reserving about 1 tablespoon of the cheese for later.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBC55859F2CA47429F1A0CE1115E5FBD', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 4, NULL, NULL, 2, NULL, 0, 'Chill for about one hour until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D3593A442F79241A888BCD928ED81C3', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 5, NULL, NULL, 2, NULL, 0, 'Unwrap cheese ball and cover in chopped bacon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38697E29C2033340AEACA4EF150F106F', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 6, NULL, NULL, 2, NULL, 0, 'Set on a serving platter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B4C853A8CA16B49A3649E5F92678F2E', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 7, NULL, NULL, 2, NULL, 0, 'Arrange two whole bacon slices coming out of the top of the cheese ball to begin forming the reindeer''s antlers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94427B243C8F884E9D09C2A502F42B86', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 8, NULL, NULL, 2, NULL, 0, 'Cut the remaining bacon slices into smaller pieces and arrange around the whole bacon slices creating smaller parts of the antlers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74B13B9DE7F64A4090210E032220888C', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 9, NULL, NULL, 2, NULL, 0, 'Cut two 1 1/2 inch circles out of a slice of white cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29A53198E041AC4496133292F0FC27D2', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 10, NULL, NULL, 2, NULL, 0, 'Draw on pupils using black food coloring and a food-use-only paint brush.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9ACFEA989EE7CA4D8A094FFB7159B0ED', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 11, NULL, NULL, 2, NULL, 0, 'Press an indentation at the bottom tip of the cheese ball and spread in a small amount of the reserved cheese ball mixture then attach the tomato nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2BA19BC9F0D0C47A4E611E2917D59EC', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 12, NULL, NULL, 2, NULL, 0, 'Use more of that cheese to attach the two eyes above the nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CC6F36C6659554DBDF75011F8225DA7', X'53E7748081AEC14EBB838C5A45D65C63', X'53E7748081AEC14EBB838C5A45D65C63', 13, NULL, NULL, 2, NULL, 0, 'Refrigerate until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'904667657B430345BD0F47BB70D7849F', X'8AA9555FFF51104B9D97A76A871C1926', X'8AA9555FFF51104B9D97A76A871C1926', 0, NULL, NULL, 2, NULL, 0, 'Cut fruit flavored gumdrops into small 1/4 inch pieces, toss in sugar, then pour into a colander to remove excess sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0C1D841259A5D41BABFF67EB4439252', X'8AA9555FFF51104B9D97A76A871C1926', X'8AA9555FFF51104B9D97A76A871C1926', 1, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7D9E2D577B3E544B4AED61F5767E99E', X'8AA9555FFF51104B9D97A76A871C1926', X'8AA9555FFF51104B9D97A76A871C1926', 2, NULL, NULL, 2, NULL, 0, 'Cream the Country Crock Unsalted Baking Sticks and sugar together until fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96E602CF7EB1EB4D9C89272647B752DE', X'8AA9555FFF51104B9D97A76A871C1926', X'8AA9555FFF51104B9D97A76A871C1926', 3, NULL, NULL, 2, NULL, 0, 'Mix in eggs and vanilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'076CDB1CA8301E4194C7E6103865B1AF', X'8AA9555FFF51104B9D97A76A871C1926', X'8AA9555FFF51104B9D97A76A871C1926', 4, NULL, NULL, 2, NULL, 0, 'In a separate bowl whisk together flour, salt, baking powder and baking soda.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'339DB1BE56751E4A971198AE1FD20249', X'8AA9555FFF51104B9D97A76A871C1926', X'8AA9555FFF51104B9D97A76A871C1926', 5, NULL, NULL, 2, NULL, 0, 'Stir the dry ingredients into the wet mixture just until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB4988237D18374AA9263C523505496A', X'8AA9555FFF51104B9D97A76A871C1926', X'8AA9555FFF51104B9D97A76A871C1926', 6, NULL, NULL, 2, NULL, 0, 'Mix in about half of the gumdrop pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D2C2335C4F1C74BB39E0B087DAEC453', X'8AA9555FFF51104B9D97A76A871C1926', X'8AA9555FFF51104B9D97A76A871C1926', 7, NULL, NULL, 2, NULL, 0, 'Scoop out about 2 tablespoons of dough onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B878D7C4F83AA346AB38922FD6BC3A17', X'8AA9555FFF51104B9D97A76A871C1926', X'8AA9555FFF51104B9D97A76A871C1926', 8, NULL, NULL, 2, NULL, 0, 'Flatten cookies and top them with more gumdrops.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6CF1B012EC21D4598BD9AC3705B78B9', X'8AA9555FFF51104B9D97A76A871C1926', X'8AA9555FFF51104B9D97A76A871C1926', 9, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes, just until the tops of the cookies start to look dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F5B969FD82F99479A822B69FE7E83FB', X'8AA9555FFF51104B9D97A76A871C1926', X'8AA9555FFF51104B9D97A76A871C1926', 10, NULL, NULL, 2, NULL, 0, 'Cool your cookies on baking sheet for 2-3 minutes, then remove and place on a wire cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67882A4E5887EF419CBB10266E3F724C', X'8A00A588F1D0C24EBD23DA2BC12F37FB', X'8A00A588F1D0C24EBD23DA2BC12F37FB', 0, NULL, NULL, 2, NULL, 0, 'Beat together the cream cheese, powdered sugar, vanilla and 3 tablespoons milk until smooth and creamy, adding more milk as needed until the icing is the desired consistency.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'677872B133B2DD45886E9DE52746E943', X'8A00A588F1D0C24EBD23DA2BC12F37FB', X'8A00A588F1D0C24EBD23DA2BC12F37FB', 1, NULL, NULL, 2, NULL, 0, 'Set aside until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D506A00B37E30D4E83EAB1F8EBB7224C', X'6185816C1F936B43B543802105EDF390', X'6185816C1F936B43B543802105EDF390', 0, NULL, NULL, 2, NULL, 0, 'Pour heavy whipping cream, sifted powdered sugar, vanilla, and a few drops of green food coloring into a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04BE74A80DF08648AEDDBF19A9882BB2', X'6185816C1F936B43B543802105EDF390', X'6185816C1F936B43B543802105EDF390', 1, NULL, NULL, 2, NULL, 0, 'Beat on low speed using an electric mixer until bubbles form then increase the speed to high and beat until the whipped cream holds stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AE7123B8DCE3D44AFE447FE7A216700', X'6185816C1F936B43B543802105EDF390', X'6185816C1F936B43B543802105EDF390', 2, NULL, NULL, 2, NULL, 0, 'Keep whipped cream refrigerated until needed up to 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9301E34F9C32BB409E4CC43E638C2F75', X'8B41C2E437BF054EB769F144F75FFADD', X'8B41C2E437BF054EB769F144F75FFADD', 0, NULL, NULL, 2, NULL, 0, 'In large bowl, whisk the flour, salt, baking powder, and baking soda.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F31C3EFC9860A9468EF07918EB413C7A', X'8B41C2E437BF054EB769F144F75FFADD', X'8B41C2E437BF054EB769F144F75FFADD', 1, NULL, NULL, 2, NULL, 0, 'Whisk together the egg and buttermilk.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'370EA1F32F199C45A251F8A434753751', X'8B41C2E437BF054EB769F144F75FFADD', X'8B41C2E437BF054EB769F144F75FFADD', 2, NULL, NULL, 2, NULL, 0, 'Pour into the flour mixture and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6A4B494CCEE6A49985B0E298C759D65', X'8B41C2E437BF054EB769F144F75FFADD', X'8B41C2E437BF054EB769F144F75FFADD', 3, NULL, NULL, 2, NULL, 0, 'Add sugar, melted butter, vanilla and green food coloring and mix until blended but with lumps remaining.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00F44BA313AB064892A7BD41D2161D68', X'8B41C2E437BF054EB769F144F75FFADD', X'8B41C2E437BF054EB769F144F75FFADD', 4, NULL, NULL, 2, NULL, 0, 'Drop a small amount of vegetable oil into a skillet or on a griddle and place over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B31BDD3ECBF01B48B8C08EDEB3F1403B', X'8B41C2E437BF054EB769F144F75FFADD', X'8B41C2E437BF054EB769F144F75FFADD', 5, NULL, NULL, 2, NULL, 0, 'Once the oil begins to separate and move around the pan, use a paper towel to rub it into the pan, leaving just a light coating on the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E543E88756F3A84AA13F374BBB3A7E27', X'8B41C2E437BF054EB769F144F75FFADD', X'8B41C2E437BF054EB769F144F75FFADD', 6, NULL, NULL, 2, NULL, 0, 'Ladle about 1/4 cup of the green pancake batter onto the pan. Cook until bubbles begin to form on surface and edges begin to set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A285FCF3345C04487BD74C8F97F7C9E', X'8B41C2E437BF054EB769F144F75FFADD', X'8B41C2E437BF054EB769F144F75FFADD', 7, NULL, NULL, 2, NULL, 0, 'Gently flip your pancakes over and cook until lightly browned on both sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B4E9EDCFB43174992E66B1548A1CD08', X'8B41C2E437BF054EB769F144F75FFADD', X'8B41C2E437BF054EB769F144F75FFADD', 8, NULL, NULL, 2, NULL, 0, 'Repeat with remaining batter making between 8 and 10 pancakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBFAC53A82F7D24F88F1EC58AF74CE33', X'2A7BF2EB83367B44B6874D096665B951', X'2A7BF2EB83367B44B6874D096665B951', 0, NULL, NULL, 2, NULL, 0, 'Stack warm pancakes on a plate then spread on some of the cream cheese icing and add a dollop or swirl of green whipped cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E82DF8CF90FD5647B4ABB9B100DCF1C8', X'2A7BF2EB83367B44B6874D096665B951', X'2A7BF2EB83367B44B6874D096665B951', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle on some candy hearts and serve immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03D67AD189562F429247C1E69BB9965F', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2602FEF9124BA74F8F96DF533AD5F8CE', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 1, NULL, NULL, 2, NULL, 0, 'Spray an 8 inch square pan and 4 small bowls with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D2F725D7BD51348A3C836F975908883', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 2, NULL, NULL, 2, NULL, 0, 'Separate eggs and pour 4 egg yolks into each of the small bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E99DC9FB3DD154DA7EAA4EA567D9902', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 3, NULL, NULL, 2, NULL, 0, 'Whisk egg whites to break up the thicker parts then pour into the 8 inch pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'239F3821DE3BD740841DC22BACFB6A48', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 4, NULL, NULL, 2, NULL, 0, 'Set pan in a 9x13 inch baking dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03AAB470D9B9D941B5AA0000CB047E4A', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 5, NULL, NULL, 2, NULL, 0, 'Pour about 2 cups of boiling water into the 9x13 baking dish, being careful no water spills into the egg whites.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDEEC48F41F691468B36193293731D98', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 6, NULL, NULL, 2, NULL, 0, 'Cover the pan tightly with tin foil and bake for about 25 minutes until the egg whites are firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'541EF5BD45B9BA4CB98C0462597FC6D1', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 7, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat about 1/2 of an inch of water in a 12 inch skillet set over high heat until boiling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AA45CE89EB82F4393484FA29011C053', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 8, NULL, NULL, 2, NULL, 0, 'Carefully place the 4 small bowls of egg yolks into the boiling water.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC76DC52C6C67248B7AA27FACA7F42A5', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 9, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low and cover the skillet with a tight fitting lid.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E82F3838DF1BF4F989DEAC21E9D0B21', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 10, NULL, NULL, 2, NULL, 0, 'Cook for about 25 minutes until the egg yolks are firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25D13D19C6EB3F4B93ED6E96BF98BC65', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 11, NULL, NULL, 2, NULL, 0, 'Remove the bowls from the water bath and let cool for about 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED1F0E5F8A7DD64D896E1FC5FCE16CA2', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 12, NULL, NULL, 2, NULL, 0, 'Remove the egg whites from the oven and it''s water bath and allow to cool for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FCBE420B6D80F43BDBB3DFCD5C05FD9', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 13, NULL, NULL, 2, NULL, 0, 'Use a small star shaped cookie cutter to cut out stars from the pan of egg whites.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FF0F25B9EC1ED48A6B722B20D48444E', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 14, NULL, NULL, 2, NULL, 0, 'Insert lollipop sticks into the star shaped egg whites.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70CDC85EEFBE4A4893B0E550CEAC176B', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 15, NULL, NULL, 2, NULL, 0, 'Place the egg yolks in a food process.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'418F7944DF999D43A39B3E95A927DAC0', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 16, NULL, NULL, 2, NULL, 0, 'Pulse until the eggs are finely chopped.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FE56F158EAC464CBCD5F0986257633E', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 17, NULL, NULL, 2, NULL, 0, 'Add Miracle Whip or Mayo, mustard, and relish and pulse until the mixture is really smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF4FE6EBD0D8A04C8145B3CED3599345', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 18, NULL, NULL, 2, NULL, 0, 'Taste, then season with salt, and pepper or paprika if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A11011D46341BD42B4EACEA591BD4F69', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 19, NULL, NULL, 2, NULL, 0, 'Spoon into a pastry bag fitted with a star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0251FC273C24543B85FF2A95FBD0856', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 20, NULL, NULL, 2, NULL, 0, 'Pipe five small stars on top of the egg white star, on each of the 5 tips, and one in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C333D8388F6DD94BB0D796787F1DF680', X'81363CEC3B50E24F8E07AF65B9DB0162', X'81363CEC3B50E24F8E07AF65B9DB0162', 21, NULL, NULL, 2, NULL, 0, 'Arrange on a serving platter and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E01DE9146A5FF4B9DF3AF5EC7CBB85D', X'A9E71E7935563245AC83449DB7FC1AF4', X'A9E71E7935563245AC83449DB7FC1AF4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FBF1719A209E074689673A80A3FF63F8', X'A9E71E7935563245AC83449DB7FC1AF4', X'A9E71E7935563245AC83449DB7FC1AF4', 1, NULL, NULL, 2, NULL, 0, 'Line a 9x13 baking pan with non-stick aluminum foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C935D9FEF70B6D43B3B71AC126DA21AE', X'A9E71E7935563245AC83449DB7FC1AF4', X'A9E71E7935563245AC83449DB7FC1AF4', 2, NULL, NULL, 2, NULL, 0, 'Mix crushed Original Oreo Cookies with melted butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02EC0E8F81ED1249A14456C6672C192D', X'A9E71E7935563245AC83449DB7FC1AF4', X'A9E71E7935563245AC83449DB7FC1AF4', 3, NULL, NULL, 2, NULL, 0, 'Press into and even layer in the 9x13 baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3311881A8B7110429DCCAD69BA1733C6', X'A9E71E7935563245AC83449DB7FC1AF4', X'A9E71E7935563245AC83449DB7FC1AF4', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle on toffee bits, peanuts, and pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A49137A2CD06C342834D5DCB523E711D', X'A9E71E7935563245AC83449DB7FC1AF4', X'A9E71E7935563245AC83449DB7FC1AF4', 5, NULL, NULL, 2, NULL, 0, 'Pour sweetened condensed milk over top and spread evenly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C64B44C08746FF4692889B66A2095952', X'A9E71E7935563245AC83449DB7FC1AF4', X'A9E71E7935563245AC83449DB7FC1AF4', 6, NULL, NULL, 2, NULL, 0, 'Add the pieces of red cream filled Oreos cookies, 1/2 cup of the red and green M&M''s and 1/2 of the bag of Holiday morsels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29809D438D72E54A8D613C7582B9058F', X'A9E71E7935563245AC83449DB7FC1AF4', X'A9E71E7935563245AC83449DB7FC1AF4', 7, NULL, NULL, 2, NULL, 0, 'Bake for 25-27 minutes until the sweetened condensed milk has caramelized. Remove from oven and sprinkle on the remaining red and green M&M''s and Holiday Chocolate Morsels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F916901FFA5A28459D65518601283845', X'A9E71E7935563245AC83449DB7FC1AF4', X'A9E71E7935563245AC83449DB7FC1AF4', 8, NULL, NULL, 2, NULL, 0, 'Return to oven for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03BD388ECAC2EF4DB9A9AA9915E32A14', X'A9E71E7935563245AC83449DB7FC1AF4', X'A9E71E7935563245AC83449DB7FC1AF4', 9, NULL, NULL, 2, NULL, 0, 'Remove pan from oven, and tap it on the counter a few times. This will help secure all the M&M''s.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26BFFF70A1E79F40B7C15614EA54A445', X'A9E71E7935563245AC83449DB7FC1AF4', X'A9E71E7935563245AC83449DB7FC1AF4', 10, NULL, NULL, 2, NULL, 0, 'Cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'500112E86829F144995CA1EB95D0FC70', X'A9E71E7935563245AC83449DB7FC1AF4', X'A9E71E7935563245AC83449DB7FC1AF4', 11, NULL, NULL, 2, NULL, 0, 'Peel off foil. Cut into 24-30 squares.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F4614290B6AC54E85DE364A02891312', X'A9E71E7935563245AC83449DB7FC1AF4', X'A9E71E7935563245AC83449DB7FC1AF4', 12, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for up to a week.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C96DE8B02A7AC44AB5CDA3882271264', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', 0, NULL, NULL, 2, NULL, 0, 'Fill the bottom trays of your gummy candy maker with ice.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D3C094291F9AE41A445EDC5EE33AEBE', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', 1, NULL, NULL, 2, NULL, 0, 'Set the silicone bear, worm, and fish molds over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0E67E27C762A44188ABFD230FA885A0', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', 2, NULL, NULL, 2, NULL, 0, 'Stir together the flavored and unflavored gelatin in the Candy Maker mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8CA0D621721EA4093EE2EA10D04798A', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', 3, NULL, NULL, 2, NULL, 0, 'Pour in the water, as you continue to stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05636699B583C24D9473CE096EBD5534', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', 4, NULL, NULL, 2, NULL, 0, 'Allow the mixture to melt and become warm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AAFC632DC1EFDA4E8447F292DBFA3052', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', 5, NULL, NULL, 2, NULL, 0, 'Pour into the molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFA4ABA88463C4459359CC410CD0FC1F', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', 6, NULL, NULL, 2, NULL, 0, 'Allow the candy to harden, about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1C0463AE3A2B94185557F579592E04B', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', X'99F9CE34A6FC3B46A639CD1AF23E4ACA', 7, NULL, NULL, 2, NULL, 0, 'Remove the molds from the trays and peel the candies out.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7880CA2897C984E97434CBF340238F3', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CAFCDE4FB8F31044A37896CA906F26DB', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 1, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BED92A08CABEE342A3F7E63A958FE87B', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 2, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3288064D15CA84E9A61B6CF07F458F0', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 3, NULL, NULL, 2, NULL, 0, 'Add the egg, vanilla, and almond extract, and yellow food coloring and beat until well combined. You want this to be bright yellow. The color will tone down once the flour is added.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9F22E05994B784BAC02F7AEC5FC0DB1', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 4, NULL, NULL, 2, NULL, 0, 'Stir in the flour mixture just until incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89679BBA90897040A62E26203E8115C5', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 5, NULL, NULL, 2, NULL, 0, 'Dust a silicone mat and non-stick rolling pin lightly with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E74FF6574C09C499B70C6BD377B30DD', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 6, NULL, NULL, 2, NULL, 0, 'Roll out some of the dough to between 1/4– 6/8 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67A8914E3ED9EF41B5CB9EC03E578592', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 7, NULL, NULL, 2, NULL, 0, 'Create the middle cookies with a hole in the middle using a 2 1/2 inch and a 3 1/2 inch long oblong cutter. Press the 2 1/2 inch cutter into the dough. Remove the cutter and the oblong shaped dough. Center the larger oblong cutter around the hole, press down to cut the dough. Leave that dough in place and continue to cut until you have a total of 9 oblong cookies with holes in them.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C25B4EDEBA602418667B4792F5DD939', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 8, NULL, NULL, 2, NULL, 0, 'Remove the excess dough around each oblong then place the silicone mat in the freezer. Do not move these cookies until they are chilled. Once firm, carefully peel the cookies up from the silicone mat and place the cookies on a parchment lined baking sheet or simply bake them on the silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'785717E70CE41441BD49D9F25DA6CCBD', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 9, NULL, NULL, 2, NULL, 0, 'Roll the remaining dough out to about 1/8th inch thickness and cut out 18 more oblong shaped cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B322DF5B0B2C5D43835B7A28677F757A', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 10, NULL, NULL, 2, NULL, 0, 'Roll the remaining dough out to 1/8th inch thickness and cut out 9 of the Minion’s legs and feet. I used a bell shaped cutter, but a mini gingerbread or teddy bear cutter will work too.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31054A37ECB4C64CB0E8B8D755230026', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 11, NULL, NULL, 2, NULL, 0, 'Use a tiny bit of water to attach the feet/legs to 9 of the oblong shaped cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7ED218CFAF637441AF7AF929A2AD8CB9', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 12, NULL, NULL, 2, NULL, 0, 'Freeze all the cookies for 15 minutes until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EEB5CDD28052A4A9AD736D6631FA866', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 13, NULL, NULL, 2, NULL, 0, 'Bake for 12 – 14 minutes until the bottom of the cookies just begin to brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F3483E443E54449A508CD97DC84272F', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 14, NULL, NULL, 2, NULL, 0, 'Remove and allow the cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1746AEF884B6E438FC9389B7BE3591C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 15, NULL, NULL, 2, NULL, 0, 'Use yellow candy melts to attach the middle cookie to the bottom cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0804FCCBF162F24583AE20596B46F3B2', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 16, NULL, NULL, 2, NULL, 0, 'Fill the middle cookie with candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27BF1B442464BC438A96CED70D0D3863', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 17, NULL, NULL, 2, NULL, 0, 'Roll out modeling chocolate, cut using a t-shirt or sweater cookie cutter, then decorate using more modeling chocolate and/or sprinkles. Attach the decorations to the sweaters using water or clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2B95F3A0ED4394CA706A1D730B224BD', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 18, NULL, NULL, 2, NULL, 0, 'Let the modeling chocolate sweaters dry until firm (15-30 minutes) then use yellow candy melts to attach them to the top cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F6B5358F052E04EA4473C819B9C0BF0', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 19, NULL, NULL, 2, NULL, 0, 'Roll out gray modeling chocolate, cut using a large round plunger cutter, then cut out a hole in the center using a smaller round plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D8D7B2DBA891C47902BF5E6385FBF20', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 20, NULL, NULL, 2, NULL, 0, 'Attach the gray goggles to jumbo candy eyes using clear piping gel or make modeling chocolate eyes and attach them under the goggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D116132C54E8304C845555F7473918F3', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 21, NULL, NULL, 2, NULL, 0, 'Glue the eyes and sweater to the top cookie using yellow candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41ED6C6297EE714B9846C7A1455BD7D8', X'7B9EBE0E06B23F42BA033EA5DFDA341C', X'7B9EBE0E06B23F42BA033EA5DFDA341C', 22, NULL, NULL, 2, NULL, 0, 'Attach the top cookie to the middle cookie using candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80FD0503A9D3D04B91E55EF463925777', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81BFFB2BB86DB341956F847F8ACC5F62', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 1, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, blend together ricotta, mozzarella, and Parmesan cheese, along with a pinch of salt and 1 tablespoon of egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41A7F4E5B585024A8B72DA460B892ABE', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 2, NULL, NULL, 2, NULL, 0, 'Spoon into a disposable pastry bag or zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBF40AC97121CB4489982210530BAEF4', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 3, NULL, NULL, 2, NULL, 0, 'Unroll dough onto a flour dusted cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A8C379BC84EF14B86154552402FF7DF', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 4, NULL, NULL, 2, NULL, 0, 'Cut each round of dough into 8 wedges.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'161D4EEEAC2D5E468CD95709F7BF7D1D', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 5, NULL, NULL, 2, NULL, 0, 'Cut a 1 inch trunk out of the curved edge of each wedge of dough, removing excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'181B9162F1FC294C9180EB4034028576', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 6, NULL, NULL, 2, NULL, 0, 'Set one of the trees on your work surface and brush egg over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88B902E6A9C59F44B5D4F267FC944489', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 7, NULL, NULL, 2, NULL, 0, 'Pipe or spread a thin layer of the cheese mixture onto the tree, leaving about 3/8 inch of dough uncovered around the border.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2926C987641D374799EC103438679ABE', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 8, NULL, NULL, 2, NULL, 0, 'Top with 1/8th of the MorningStar Farms® Grillers® Crumbles™, pizza sauce, roasted red peppers, marinated artichokes and tomatoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8513698BD119864FBB4E42F104344DEA', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 9, NULL, NULL, 2, NULL, 0, 'Top with another tree.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02A9FDC64E1E034AB1B3803D4A763EE7', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 10, NULL, NULL, 2, NULL, 0, 'Seal the edges then use a fork to crimp the edges together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A69FFA580FDEB749A9453FFD2377312A', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 11, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 8 calzone trees.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E1C0C3DDDC0464C94B2CA692A3CAD2F', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 12, NULL, NULL, 2, NULL, 0, 'Set trees on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED27743280F0324193D1B6B458FBD997', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 13, NULL, NULL, 2, NULL, 0, 'Brush egg over top then sprinkle on Italian seasoning.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B8A00F4B6700284A9E0C4B2B2B27B839', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 14, NULL, NULL, 2, NULL, 0, 'Bake for 10-14 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'812B2A4E03513D45839CE08E754CE66D', X'E3B82A9539DC664C9F312C0909AF0F1F', X'E3B82A9539DC664C9F312C0909AF0F1F', 15, NULL, NULL, 2, NULL, 0, 'Serve hot with more pizza sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0869E8A81C1F4C4A9B6306F340354F90', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 0, NULL, NULL, 2, NULL, 0, 'Carve an oblong shape out of the cake.The bottom edge can be a bit more flat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E09FC2CF694DC4D82E6A8D6750A600B', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 1, NULL, NULL, 2, NULL, 0, 'Cut one of the cake scraps into a Santa hat and attach it to the top of the cake using red frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F9608CB1E2FFF4FB15A3E562A4F3D31', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 2, NULL, NULL, 2, NULL, 0, 'Spoon all the frosting colors into individual pastry bags, each fitted with a star tip, reserving half of the black frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BD91FD8E9FE1B49B481B1903EDD40AB', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 3, NULL, NULL, 2, NULL, 0, 'Spoon the reserved black frosting into a pastry bag fitted with a #3 or #4 round tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B18FC125602AEB4DAC418DE71C0FDAF9', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 4, NULL, NULL, 2, NULL, 0, 'Decorate the cake by piping stars all over the surface and sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02C1E640E791384383660A664888474E', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 5, NULL, NULL, 2, NULL, 0, 'Start by making a red Santa Hat with a furry white rim. To make the white fur you can pipe stars or swirls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9D1DF2D5D27FF47832B05144F9A1E60', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 6, NULL, NULL, 2, NULL, 0, 'Pipe on two silver gray circles for the Minion''s goggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DB01F828596DD49AD5A24DB098A6A10', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 7, NULL, NULL, 2, NULL, 0, 'Pipe a small black circle in the middle of those circles then fill in around the black circle using white frosting to make the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DDAF3F21D7DA643A40EA0077F377AD4', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 8, NULL, NULL, 2, NULL, 0, 'Pipe a gray line one either side of the goggles then pipe a black band down the sides of the cake for the goggle straps.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65F74F92DE1F5041BD14B8AC25666979', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 9, NULL, NULL, 2, NULL, 0, 'Pipe on the red coat, black belt, and white fur trim.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0881946C1992F64B9C99097DE7A9E29F', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 10, NULL, NULL, 2, NULL, 0, 'Pipe on a gold belt buckle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD7E3B89CE26ED4F8141FE8EF4F3AED5', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 11, NULL, NULL, 2, NULL, 0, 'Pipe yellow above and just below the goggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED2A8DF6E77C9A4EBE0210DCBB1E0D93', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 12, NULL, NULL, 2, NULL, 0, 'Pipe red frosting onto the cake board to create arms then pipe on black gloves.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39C35E5303D22B498011D1A8DFBEE1D4', X'475330D7097E004E830B9454FC9FC359', X'475330D7097E004E830B9454FC9FC359', 13, NULL, NULL, 2, NULL, 0, 'Pipe red frosting onto the cake board below the Minion to create two legs then then pipe on black shoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40A3EB586987214CAD2B9471563F2576', X'F53C9113BC2C2D479CCBFA2247D3FA38', X'F53C9113BC2C2D479CCBFA2247D3FA38', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC41A2A31C44CA4887ED839AE160EBFC', X'F53C9113BC2C2D479CCBFA2247D3FA38', X'F53C9113BC2C2D479CCBFA2247D3FA38', 1, NULL, NULL, 2, NULL, 0, 'Stir together the flour and baking powder.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC09A1A4398A33428761DC9EBC1796DE', X'F53C9113BC2C2D479CCBFA2247D3FA38', X'F53C9113BC2C2D479CCBFA2247D3FA38', 2, NULL, NULL, 2, NULL, 0, 'Beat butter and sugar together until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9C5E8B315DC8147BF248CCC6182D80A', X'F53C9113BC2C2D479CCBFA2247D3FA38', X'F53C9113BC2C2D479CCBFA2247D3FA38', 3, NULL, NULL, 2, NULL, 0, 'Add egg, milk, vanilla, and almond or peppermint extract and beat until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D22FC906CDE0F840BD631586A0F3EFD6', X'F53C9113BC2C2D479CCBFA2247D3FA38', X'F53C9113BC2C2D479CCBFA2247D3FA38', 4, NULL, NULL, 2, NULL, 0, 'Add the flour mixture and mix just until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5D92E93FD5FA44C9827A49A347F0851', X'F53C9113BC2C2D479CCBFA2247D3FA38', X'F53C9113BC2C2D479CCBFA2247D3FA38', 5, NULL, NULL, 2, NULL, 0, 'Fill the cookie press with dough and press cookies out onto a cookie sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'372456DAD790D44998143493AF897402', X'F53C9113BC2C2D479CCBFA2247D3FA38', X'F53C9113BC2C2D479CCBFA2247D3FA38', 6, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until the bottoms of the cookies turn light golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86AD9B4456477947A6D2CC7676308076', X'F53C9113BC2C2D479CCBFA2247D3FA38', X'F53C9113BC2C2D479CCBFA2247D3FA38', 7, NULL, NULL, 2, NULL, 0, 'Remove and allow to cool on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78A1CBED9476534294BDD4EB1623E645', X'F53C9113BC2C2D479CCBFA2247D3FA38', X'F53C9113BC2C2D479CCBFA2247D3FA38', 8, NULL, NULL, 2, NULL, 0, 'Decorate with candy melts, sprinkles, and crushed candy canes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F69676EF37A0E44A49599DFED9BB107', X'24D88E31C944BA468536DF76EC334B6D', X'24D88E31C944BA468536DF76EC334B6D', 0, NULL, NULL, 2, NULL, 0, 'Unwrap the Reese''s Cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B3A784FA8429D45A16C5457014B3BFF', X'24D88E31C944BA468536DF76EC334B6D', X'24D88E31C944BA468536DF76EC334B6D', 1, NULL, NULL, 2, NULL, 0, 'Brush a small amount of melted White Candy Melts over the top (the wider end) of a Miniature Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01992C910F226745B4BCF13E75D3F740', X'24D88E31C944BA468536DF76EC334B6D', X'24D88E31C944BA468536DF76EC334B6D', 2, NULL, NULL, 2, NULL, 0, 'Turn it upside down and press it onto one of the 1.5 ounce White Reese''s Cup near one edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'408AEEE8E425D743BBB73AE9E771D3E4', X'24D88E31C944BA468536DF76EC334B6D', X'24D88E31C944BA468536DF76EC334B6D', 3, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes and one brown Reese''s Pieces nose using more White Candy Melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C044A85F7783B5429DE30448634213B8', X'24D88E31C944BA468536DF76EC334B6D', X'24D88E31C944BA468536DF76EC334B6D', 4, NULL, NULL, 2, NULL, 0, 'Set aside to allow the Candy Melts to dry for a few minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3432E65EA5FE5547BDF24782891D973E', X'24D88E31C944BA468536DF76EC334B6D', X'24D88E31C944BA468536DF76EC334B6D', 5, NULL, NULL, 2, NULL, 0, 'Then attach two Mini White Reese''s Cup ears to the edge of the 1.5 ounce Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5013CB611E60AE49B510E4837FA88C69', X'24D88E31C944BA468536DF76EC334B6D', X'24D88E31C944BA468536DF76EC334B6D', 6, NULL, NULL, 2, NULL, 0, 'Let dry for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC76FF3FBC49EF4CAA3555F5FE14A8FD', X'24D88E31C944BA468536DF76EC334B6D', X'24D88E31C944BA468536DF76EC334B6D', 7, NULL, NULL, 2, NULL, 0, 'Draw a pad and 5 toes onto the remaining 12 White Mini Reese''s Cups using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBB933A1F575354593EA828FF2AA9AA6', X'24D88E31C944BA468536DF76EC334B6D', X'24D88E31C944BA468536DF76EC334B6D', 8, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of white frosting on each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8A4B8424AABD048A33B75E6B6E5C63D', X'24D88E31C944BA468536DF76EC334B6D', X'24D88E31C944BA468536DF76EC334B6D', 9, NULL, NULL, 2, NULL, 0, 'Snuggle a Reese''s Cup Polar Bear onto the top part of the swirl adding a bit more frosting behind the Reese''s Cup if needed to keep the polar bear in place.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F12948439CF1134BA048F25ED21C22EA', X'24D88E31C944BA468536DF76EC334B6D', X'24D88E31C944BA468536DF76EC334B6D', 10, NULL, NULL, 2, NULL, 0, 'Press two paws into the frosting just above the edge of the cupcake wrapper on either side of the Polar Bear''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4289DD5E511C6042B0E10DAA5F1E5BE6', X'EACBD2F3A31F1343BC97BF47447C0D64', X'EACBD2F3A31F1343BC97BF47447C0D64', 0, NULL, NULL, 2, NULL, 0, 'Puree roasted red pepper, roasted garlic, roasted almonds, lemon juice, and Parmesan cheese in a food processor, keeping puree somewhat chunky.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C311A1389441774BAEDC40CFA49D10F3', X'EACBD2F3A31F1343BC97BF47447C0D64', X'EACBD2F3A31F1343BC97BF47447C0D64', 1, NULL, NULL, 2, NULL, 0, 'Add oil and puree just to incorporate the oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE9222A41F21164ABFEB2346F58CB8BF', X'EACBD2F3A31F1343BC97BF47447C0D64', X'EACBD2F3A31F1343BC97BF47447C0D64', 2, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper, to taste.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3CA573E7A8D0146A66415679FCE6D55', X'EACBD2F3A31F1343BC97BF47447C0D64', X'EACBD2F3A31F1343BC97BF47447C0D64', 3, NULL, NULL, 2, NULL, 0, 'Stir in chicken breast.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD59C1644CA70741B00F83B711D16169', X'EACBD2F3A31F1343BC97BF47447C0D64', X'EACBD2F3A31F1343BC97BF47447C0D64', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2EC6DC637315F64FBD412297D3031DFC', X'855F2DA566106F468EF9102854C4A1FA', X'855F2DA566106F468EF9102854C4A1FA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B28AE1FAD311B43ADEFCF284DA17BAE', X'855F2DA566106F468EF9102854C4A1FA', X'855F2DA566106F468EF9102854C4A1FA', 1, NULL, NULL, 2, NULL, 0, 'Cut twelve 4-inch round circles from tortillas.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D58C31069628BF44B7459623858CF53E', X'855F2DA566106F468EF9102854C4A1FA', X'855F2DA566106F468EF9102854C4A1FA', 2, NULL, NULL, 2, NULL, 0, 'Brush both sides of the rounds with olive oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08E325FC1F5E29469A072E6C4CACBBB5', X'855F2DA566106F468EF9102854C4A1FA', X'855F2DA566106F468EF9102854C4A1FA', 3, NULL, NULL, 2, NULL, 0, 'Press into the bottom and up the sides of a muffin tin.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E13FAFC8DB18C6418FB1515F537D2372', X'855F2DA566106F468EF9102854C4A1FA', X'855F2DA566106F468EF9102854C4A1FA', 4, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until the edges turn golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2E08E06A4D9FB499B1DDC194B3EFE2E', X'855F2DA566106F468EF9102854C4A1FA', X'855F2DA566106F468EF9102854C4A1FA', 5, NULL, NULL, 2, NULL, 0, 'Remove from muffin tin and allow to cool.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE162617484B0A438C62E59F519AED06', X'0A976D87CBFC5C448BA26FB8C24336D4', X'0A976D87CBFC5C448BA26FB8C24336D4', 0, NULL, NULL, 2, NULL, 0, 'Spoon whipped cream cheese into a pastry bag fitted with a large star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCCE128177E3804686E2354EEA2AE2AD', X'0A976D87CBFC5C448BA26FB8C24336D4', X'0A976D87CBFC5C448BA26FB8C24336D4', 1, NULL, NULL, 2, NULL, 0, 'Pipe about a teaspoon of cream cheese into the bottom of each tortilla cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD1BFC7823AF6543A9B75F533EB164EC', X'0A976D87CBFC5C448BA26FB8C24336D4', X'0A976D87CBFC5C448BA26FB8C24336D4', 2, NULL, NULL, 2, NULL, 0, 'Equally divide the chicken salad among the 12 tortilla cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23311B9A72BD91409B700768E79D6C34', X'0A976D87CBFC5C448BA26FB8C24336D4', X'0A976D87CBFC5C448BA26FB8C24336D4', 3, NULL, NULL, 2, NULL, 0, 'Pipe a swirl of cream cheese on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2339D92BEA60654580F034164048E0F8', X'0A976D87CBFC5C448BA26FB8C24336D4', X'0A976D87CBFC5C448BA26FB8C24336D4', 4, NULL, NULL, 2, NULL, 0, 'Tear basil into tiny pieces and sprinkle over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D5912A149ACDD49B4E25A7081484290', X'0A976D87CBFC5C448BA26FB8C24336D4', X'0A976D87CBFC5C448BA26FB8C24336D4', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A146057710ECD48BE3C28A64BB2A967', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCD7CECEE2C78C4F8D1B3559B2630638', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 1, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'368DC17A276F3F4A9448F564ABBF82C6', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 2, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5625BF1330A867448792F414EEB1E402', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 3, NULL, NULL, 2, NULL, 0, 'Add the vanilla, and almond extract and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'818C4AF2E570004CBB5102F72ECFBB01', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 4, NULL, NULL, 2, NULL, 0, 'Mix in the eggs until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A7C0EE67FA4374F8C9BB43061B9179E', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 5, NULL, NULL, 2, NULL, 0, 'Stir in the flour mixture just until incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC2B42E1583E584790A066083233A5C4', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 6, NULL, NULL, 2, NULL, 0, 'Roll out the dough to about 3/16th inch thickness on a silicone mat or a flour dusted cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1FD9AAA4A0CB346AFA456EC5E0166EF', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 7, NULL, NULL, 2, NULL, 0, 'Cut out dough using a 3 1/2 inch mason jar cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9958E6903E49F743BF1FCD4116965838', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 8, NULL, NULL, 2, NULL, 0, 'Remove excess dough leaving mason jars in place.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'637B53B7E23F6649BE6DE04F5DB6988A', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 9, NULL, NULL, 2, NULL, 0, 'Cut the centers out using an oblong cookie cutter or knife leaving about 1/2 - 3/4 inch of cookie dough around the outside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55A4A24F4CC4F34181F6B67A00445658', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 10, NULL, NULL, 2, NULL, 0, 'Remove the center pieces of dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'723F80E9590BB346A656321792549548', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 11, NULL, NULL, 2, NULL, 0, 'Place the cookies in the freezer for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98410F3F820CAA41970DDB7334DD615E', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 12, NULL, NULL, 2, NULL, 0, 'Re-roll dough and repeat creating about 60 cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2EE9AFAF4A4FD64786723CCACEBC4A60', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 13, NULL, NULL, 2, NULL, 0, 'Remove and peel the frozen cookies off the silicone mat or cutting board and place them on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB1468E6B4E6504C8ABB60D12EC4CD75', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 14, NULL, NULL, 2, NULL, 0, 'Bake for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41B328A6D907EF49A94B41D9EE31E20A', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 15, NULL, NULL, 2, NULL, 0, 'Meanwhile, crush the clear mint candies into very fine crumbs using a food processor or rolling pin.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EFA91FF42D011E409C60EC91E981D900', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 16, NULL, NULL, 2, NULL, 0, 'Remove hot cookies from oven and spoon candy crumbs into the center of each cookie, filling them about 1/2 full.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76E7AC57C033964A84D112B847A63F22', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 17, NULL, NULL, 2, NULL, 0, 'Return cookies to oven and allow the candy to melt for 3-4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3BDDBB1E6B8B64984DB2F8E9C55CED0', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 18, NULL, NULL, 2, NULL, 0, 'Remove and allow cookies to cool completely before removing them from the cookie sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B7136CABBFC883469519A9D99347F1C9', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 19, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of piping gel over about 3/4 of the candy glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B50A2042E220D439B2DFC1E6ECA5434', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 20, NULL, NULL, 2, NULL, 0, 'Sprinkle candies or sprinkles over the piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53F26324E8FE0E4E8E5EDFEFA38F1F14', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 21, NULL, NULL, 2, NULL, 0, 'Let the gel dry for about 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA0CD5E5DCB6364595321AE7434DE865', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 22, NULL, NULL, 2, NULL, 0, 'Paint black candy melts over the lid portion of the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1201D251D9B4DB4D8AF8561E81C512DF', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 23, NULL, NULL, 2, NULL, 0, 'Let the candy melts dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5D51C13F94FF44096C14428C279DB20', X'A353B6D5E0456D4196568FD7D682BB80', X'A353B6D5E0456D4196568FD7D682BB80', 24, NULL, NULL, 2, NULL, 0, 'Brush edible silver luster dust over the black candy lid to create a shine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D2B137EC1C77CE4FB00886C55A4E817F', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 0, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of creamy peanut butter on half of the RITZ Crackers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'008D898BFAEE1E4C936B7FF16CD96575', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 1, NULL, NULL, 2, NULL, 0, 'Sandwich two crackers together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'320F7BD82FEBBA479F60BA131368DF40', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 2, NULL, NULL, 2, NULL, 0, 'Set aside 10 of the cracker sandwiches to use for Rudolph''s antlers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1623A972F74F29499C763996DCE47F76', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 3, NULL, NULL, 2, NULL, 0, 'Dip the remaining cracker sandwiches in chocolate and chill until set. Use the refrigerator if using pure chocolate and the freezer if using candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A35962C7EE975343B166027DED53D1DD', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 4, NULL, NULL, 2, NULL, 0, 'Create a column of 6 chocolate dipped RITZ Crackers down the center of your serving platter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55F06AFC95390F4C927E9A0DE55C214B', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 5, NULL, NULL, 2, NULL, 0, 'Arrange 1 column of 6 chocolate dipped crackers on either side of the middle column.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A478366E8B9F364D9304DFA7FF95EEAD', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 6, NULL, NULL, 2, NULL, 0, 'Then arrange one column of 4 chocolate dipped crackers on either side of those so that the top and bottom rows have three chocolate dipped crackers in them.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'136ED53F661F51468F3E8DC0C8551F3F', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 7, NULL, NULL, 2, NULL, 0, 'Arrange three of the plain peanut butter crackers above column number 2 and again above column number 4 then arrange two crackers on either side to finish the antlers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77B25D1E920B8B4797B183AED266DA54', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 8, NULL, NULL, 2, NULL, 0, 'Roll out the three colors of candy clay to about 1/16th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4712E06289F1A478F140897297643CE', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 9, NULL, NULL, 2, NULL, 0, 'Cut two white circles using a 2 1/4 inch round cookie cutter or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6914614B5C6EB64196C058AD8226F0E8', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 10, NULL, NULL, 2, NULL, 0, 'Cut two black circles using a 1 1/4 inch round cookie cutter or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE9BA36B5B8DA94EB7391BEBD40B32C1', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 11, NULL, NULL, 2, NULL, 0, 'Cut one red circle using a 3 inch round cookie cutter or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0437AD891A36D2498DF66196342598B1', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 12, NULL, NULL, 2, NULL, 0, 'Brush a bit of water on the back side of a black circle and press it onto a white circle creating one eye. Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90917BB1E79F524C91B6E64D30676366', X'B6E87C89E519BB488F91F2F58E9E7A21', X'B6E87C89E519BB488F91F2F58E9E7A21', 13, NULL, NULL, 2, NULL, 0, 'Set the red nose and two eyes on the chocolate dipped crackers creating Rudolph''s eyes and nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE9AFB9EBD7E8B4E8A5380761BEDE1CB', X'8A231984395CED409E3345866F5F6041', X'8A231984395CED409E3345866F5F6041', 0, NULL, NULL, 2, NULL, 0, 'Set one Reese''s Cup on your work surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5BF8DA44D2BDB46BACFA7504A3FB5C2', X'8A231984395CED409E3345866F5F6041', X'8A231984395CED409E3345866F5F6041', 1, NULL, NULL, 2, NULL, 0, 'Brush a dab of melted chocolate in the center on top of the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'043E39ABC7EECF47BD0FE01801D0DB02', X'8A231984395CED409E3345866F5F6041', X'8A231984395CED409E3345866F5F6041', 2, NULL, NULL, 2, NULL, 0, 'Allow that to set up a bit then set another Reese''s Cup on it''s side down onto the melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECB2E6768E6E9945B1C6EFEEFDBF341D', X'8A231984395CED409E3345866F5F6041', X'8A231984395CED409E3345866F5F6041', 3, NULL, NULL, 2, NULL, 0, 'Hold the candy in place until the chocolate sets and the Reese''s Cup stays upright.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9894673958D4D048ACF40243A371D997', X'8A231984395CED409E3345866F5F6041', X'8A231984395CED409E3345866F5F6041', 4, NULL, NULL, 2, NULL, 0, 'Attach 2 candy eyes to the top, back edge, of the upright Reese''s Cup using dabs of chocolate as edible `glue.`');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A7F10D0B0537D44188D415DC1892841F', X'4028355A29392748A89C42C38D7E9488', X'4028355A29392748A89C42C38D7E9488', 0, NULL, NULL, 2, NULL, 0, 'Attach 1 red candy nose and 2 candy antlers to the upright Reese''s Cup using chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17AF7FFDFD7AD14C816A87BA47B4F43A', X'4028355A29392748A89C42C38D7E9488', X'4028355A29392748A89C42C38D7E9488', 1, NULL, NULL, 2, NULL, 0, 'Cut 3/8 inch wide strips from the Fruit Roll-Ups sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23D0A03C4AA90C478A5D6184B5D203CF', X'4028355A29392748A89C42C38D7E9488', X'4028355A29392748A89C42C38D7E9488', 2, NULL, NULL, 2, NULL, 0, 'Wrap a strip of Fruit Roll-Ups around the base of the upright Reese''s Cup forming a scarf.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECD9904D53BF9545B83F2727CB72333B', X'77E640CA4E7A014EA8ED798C632885A4', X'77E640CA4E7A014EA8ED798C632885A4', 0, NULL, NULL, 2, NULL, 0, 'Roll out brown candy clay to about 1/8th of an inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8F690B72FFA7141873BD500BD2CAE6E', X'77E640CA4E7A014EA8ED798C632885A4', X'77E640CA4E7A014EA8ED798C632885A4', 1, NULL, NULL, 2, NULL, 0, 'Cut out 24 small leaves (or antlers) using a 1 - 1/2 inch cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BBC69E1199EBB47BAD13C43636D46CF', X'77E640CA4E7A014EA8ED798C632885A4', X'77E640CA4E7A014EA8ED798C632885A4', 2, NULL, NULL, 2, NULL, 0, 'If using a leaf cutter you may need to cut off a bit to get an antler shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4498742E7C92BC43957847300B2D49BC', X'77E640CA4E7A014EA8ED798C632885A4', X'77E640CA4E7A014EA8ED798C632885A4', 3, NULL, NULL, 2, NULL, 0, 'Attach two antlers to the back side of each upright Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'247E3476C374074FAC166A94362BC18F', X'77E640CA4E7A014EA8ED798C632885A4', X'77E640CA4E7A014EA8ED798C632885A4', 4, NULL, NULL, 2, NULL, 0, 'Roll out the green candy clay to about 1/16th of an inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79C887884C2FEC49A47C157E8FC79782', X'77E640CA4E7A014EA8ED798C632885A4', X'77E640CA4E7A014EA8ED798C632885A4', 5, NULL, NULL, 2, NULL, 0, 'Cut twelve 3/8th inch wide by 6 or 7 inch long strips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24F3D182F0675243A62659A6EECF2B98', X'77E640CA4E7A014EA8ED798C632885A4', X'77E640CA4E7A014EA8ED798C632885A4', 6, NULL, NULL, 2, NULL, 0, 'Wrap one strip around the base of each upright Reese''s Cup forming a scarf.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B6A189C1F27C948B95F88006620C5B0', X'77E640CA4E7A014EA8ED798C632885A4', X'77E640CA4E7A014EA8ED798C632885A4', 7, NULL, NULL, 2, NULL, 0, 'Cut off the ends of the strip so you can a nice length of scarf on each Rudolph.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'823EADC6B767764F8461FB59A4D8EDB1', X'6BFCB62A5A33C141BBF4C3B61D3BF096', X'6BFCB62A5A33C141BBF4C3B61D3BF096', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'765BD78EB1968249A014F9BCA880BA7F', X'6BFCB62A5A33C141BBF4C3B61D3BF096', X'6BFCB62A5A33C141BBF4C3B61D3BF096', 1, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D801CA1D89466448AC6FB247FC3BCAB', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 0, NULL, NULL, 2, NULL, 0, 'Bring a pot of water to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4E264CE8B176E4C9F0ABABB04FC4558', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 1, NULL, NULL, 2, NULL, 0, 'Add salt and noodles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D7D90B5D65D094BB4671ACA7318FC62', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 2, NULL, NULL, 2, NULL, 0, 'Boil for 12 minutes until the noodles are soft. (They will crisp a bit in the oven.)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C913D5018814144E883078E23ACEDB6D', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 3, NULL, NULL, 2, NULL, 0, 'Brush two cookie sheets with olive oil or olive oil spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDB41BD5068DFF41A041D9D6FD114EF3', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 4, NULL, NULL, 2, NULL, 0, 'Set noodles in a single layer on the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4ADF833B20C8C43B31CFB1F06A78A94', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 5, NULL, NULL, 2, NULL, 0, 'Cut one 2 1/2 inch circle out of one noodle then cut that noodle into 2 even strips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E359F8BCC073C499106D6E77ECB6173', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 6, NULL, NULL, 2, NULL, 0, 'Repeat with 3 more noodles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EBBBCF520CF744D8235B286311B89FF', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 7, NULL, NULL, 2, NULL, 0, 'Cut five 2 1/2 inch circles out of the remaining four noodles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB251C2C7F68744985D81F37DE3CAACE', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 8, NULL, NULL, 2, NULL, 0, 'Set the cut pieces back on the oil covered pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95044E245F4EE34CB5B8AB966328990F', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 9, NULL, NULL, 2, NULL, 0, 'Brush olive oil or spray olive oil in the bottom and up the sides of eight 6 ounce ramekins.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79F1C00D2C001347AEBD94C1F3FC529F', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 10, NULL, NULL, 2, NULL, 0, 'Wrap a noodle strip around the inside of a ramekin allowing the noodle to overlap slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B41C97EA2DC14D47945B991E9DCC9F58', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 11, NULL, NULL, 2, NULL, 0, 'Set one round noodle in the bottom of the ramekin.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0FA513D35E48DB48B7BA4700E394CACF', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 12, NULL, NULL, 2, NULL, 0, 'Repeat lining all 8 ramekins.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FA68BD235FC1D468F2DEBCBEEE7A044', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', X'49AE89FD9ED4B94DAB5EA5DA391C3CCC', 13, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7CDEA03D2BDBFA40ACF1D8465B77599C', X'5016265C1F220348A8814C60FA84841E', X'5016265C1F220348A8814C60FA84841E', 0, NULL, NULL, 2, NULL, 0, 'Stir together the ricotta, mozzarella, parmesan, and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CF50F0FDD703A46BB3F8767B6AA8BF7', X'AD34377777EA7A42ADD071AAC8122F75', X'AD34377777EA7A42ADD071AAC8122F75', 0, NULL, NULL, 2, NULL, 0, 'Stir together the diced and thawed chicken fingers with the marinara sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D203A9A86418FD46840511283B7A12C9', X'AD34377777EA7A42ADD071AAC8122F75', X'AD34377777EA7A42ADD071AAC8122F75', 1, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30B543C6556C90488DD51141BCF9F839', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 0, NULL, NULL, 2, NULL, 0, 'Evenly divide the chicken filling among the 8 ramekins.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69042B278D99F54A9AC2FD5A79D4F749', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 1, NULL, NULL, 2, NULL, 0, 'Compact the filling into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9BB2C061863564F8283B8E60084706F', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 2, NULL, NULL, 2, NULL, 0, 'Set another round noodle on top of the chicken filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'368B63F325FAE24B820D68E66D54936E', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 3, NULL, NULL, 2, NULL, 0, 'Evenly divide the cheese filling among the 8 ramekins.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3A366117F90D342A38FC244A9A88FCA', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 4, NULL, NULL, 2, NULL, 0, 'Spread the filling in an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E84B8068C9B0D48BB81547DB19366B1', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 5, NULL, NULL, 2, NULL, 0, 'Set the final round noodle on top of the cheese filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'504DAEE35D4FD748AE3C6BF3D1011B13', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 6, NULL, NULL, 2, NULL, 0, 'Spread a teaspoon of Alfredo sauce over top then sprinkle with 1/2 ounce of mozzarella cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49A469539A214E41ABAD10A21C8BE9D1', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 7, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'045A1F0CC26C9C43B762615240DA2944', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 8, NULL, NULL, 2, NULL, 0, 'Cut olives in half. Use a bubble tea straw. a small round plunger or fondant cutter, or a large round pastry tip to cut 1/4 inch round circles from the olives.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BDB8BD6DDDEC3643BBBB27B22D5D64E9', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 9, NULL, NULL, 2, NULL, 0, 'Cut the mini carrots into 3/4 inch long pieces for the snowmen noses.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEA7AF6CABF8E94F87C1C2768C336DE5', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 10, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F4EB1A93EE6F846AF864B25206DFFF6', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 11, NULL, NULL, 2, NULL, 0, 'Arrange 2 olive eyes and one carrot nose on each lasagna.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B6ACEF8D0552E44AD94225ECC266906', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 12, NULL, NULL, 2, NULL, 0, 'Then arrange 5 olives to form a smile under the nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'488D756CA6932C4485E65F435C8738A6', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 13, NULL, NULL, 2, NULL, 0, 'Bake for 22-26 minutes until the cheese is bubbly and melted and the lasagnas are heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25CA4DB4825745409D4ED97D76DBA878', X'F603EF80D7324D458B0E15E951B0C628', X'F603EF80D7324D458B0E15E951B0C628', 14, NULL, NULL, 2, NULL, 0, 'Allow to cool for a few minutes before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'28582620F0C30744A6BD93CCCFAE7F36', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 0, NULL, NULL, 2, NULL, 0, 'Heat red candy melts in a microwave safe bowl for two 30 second increments, stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D03B7722FA93314FAFD7C8B2ED456DCE', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 1, NULL, NULL, 2, NULL, 0, 'Heat for another 30 seconds, then let the bowl rest in the microwave for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95FC24A639B7FC40A729A666F8F098B9', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir vigorously until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A925D0332D6E943ABD4A10640F75795', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 3, NULL, NULL, 2, NULL, 0, 'Cool, stirring often, to 91 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96A4CB72FBD4DC419CADC8FF7AE98880', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 4, NULL, NULL, 2, NULL, 0, 'Stir in the corn syrup and blend just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADF9BB8FAAB9E24FA82051EF3C267FB7', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 5, NULL, NULL, 2, NULL, 0, 'Pour out onto plastic wrap.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0BF81CC9D5858488B7EFBECB8EC757F', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 6, NULL, NULL, 2, NULL, 0, 'Wrap and set aside to firm up for several hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7544D88FD2D71C42A2628E9F359820DD', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 7, NULL, NULL, 2, NULL, 0, 'When ready, remove from plastic wrap and knead until the candy clay is pliable.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88F451BF0E14F7439F8B1766E69412EB', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 8, NULL, NULL, 2, NULL, 0, 'Roll out red candy clay really thin.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF93F081D250F649988EAEB70B6B3CC3', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 9, NULL, NULL, 2, NULL, 0, 'Cut 3/8 inch by 7-inch strips from the candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F075E7E0539EBB4285C8A20394C75EB5', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 10, NULL, NULL, 2, NULL, 0, 'Set one strip on a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6C129BB0B37814E972D93ADC369E078', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 11, NULL, NULL, 2, NULL, 0, 'Pour melted white candy melts into a disposable pastry bag or zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7229694053D1BB4B9F84D4D53C43F872', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 12, NULL, NULL, 2, NULL, 0, 'Snip off the tip of the bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'941FE8E59E623145B0B0ABD7DBF81996', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 13, NULL, NULL, 2, NULL, 0, 'Pipe a small mound of candy in the center of the red candy clay strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA61BB7C17E39342942B3189D2796812', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 14, NULL, NULL, 2, NULL, 0, 'Set two pretzel rings, touching, over the white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F69A666E78AD146ABAAA1B32111DDC1', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 15, NULL, NULL, 2, NULL, 0, 'Pipe a small amount of white candy where the pretzels come together just to help `glue'' them together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25F6AD412048214186A82837B2329A97', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 16, NULL, NULL, 2, NULL, 0, 'Pipe white candy into the center of both pretzel rings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D41D727ED1086F428FC659CCB7AF86E8', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 17, NULL, NULL, 2, NULL, 0, 'Set two mini chocolate chips and one orange sunflower seed in the top pretzel forming the snowman''s eyes and nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD92A60B0DAA96498939992A3E9386A6', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 18, NULL, NULL, 2, NULL, 0, 'Set three mini chocolate chips in the bottom pretzel forming 3 buttons.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BFB77FE1E7AFF741AF8C95A17E60AA14', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 19, NULL, NULL, 2, NULL, 0, 'Add another small amount of white candy melts in between the two pretzels then overlap the two ends of the red candy clay strip forming the snowman''s scarf.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EEE3B07780B5342A78702DBFF67C4A3', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 20, NULL, NULL, 2, NULL, 0, 'Freeze for 3-5 minutes just until the white candy melts harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6D55C84855A00429CDA4E68C90858D5', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 21, NULL, NULL, 2, NULL, 0, 'Remove and let sit at room temperature for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFC342E403F4EB4098535EA3BF13AE4D', X'82DA94485C12824CAE346E459DC6A16C', X'82DA94485C12824CAE346E459DC6A16C', 22, NULL, NULL, 2, NULL, 0, 'Use a black food coloring marker to create a smile formed by 5-7 dots.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89D034D91A398B4BBE3E3F6F0E4A170B', X'4FBF3DF45FEEAA4C932ECAB918B450E1', X'4FBF3DF45FEEAA4C932ECAB918B450E1', 0, NULL, NULL, 2, NULL, 0, 'Pull apart one Mini Oreo Cookie and scrape out the filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74614BC2B5D1CE45AFF35100D161DAFA', X'4FBF3DF45FEEAA4C932ECAB918B450E1', X'4FBF3DF45FEEAA4C932ECAB918B450E1', 1, NULL, NULL, 2, NULL, 0, 'Insert both cookie halves into one of the Oreo Mega Stuf Cookies creating Mickey Mouse ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52A7398F6A2B6D40B9CFD555ABE3E21C', X'4FBF3DF45FEEAA4C932ECAB918B450E1', X'4FBF3DF45FEEAA4C932ECAB918B450E1', 2, NULL, NULL, 2, NULL, 0, 'Repeat creating 12 Mickey Mouse Oreo Cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94F20EF1BC06A745AACD0760A4053165', X'4FBF3DF45FEEAA4C932ECAB918B450E1', X'4FBF3DF45FEEAA4C932ECAB918B450E1', 3, NULL, NULL, 2, NULL, 0, 'Insert two candy antlers in between Mickey''s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BD333BA12627549A1C1894C206D951B', X'4FBF3DF45FEEAA4C932ECAB918B450E1', X'4FBF3DF45FEEAA4C932ECAB918B450E1', 4, NULL, NULL, 2, NULL, 0, 'Dab a bit of melted chocolate on the back of a red nose and press it onto the Mega Stuf cookie opposite the antlers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2CDF529A5359E4A916F529DEF6EA89C', X'4FBF3DF45FEEAA4C932ECAB918B450E1', X'4FBF3DF45FEEAA4C932ECAB918B450E1', 5, NULL, NULL, 2, NULL, 0, 'Dip the tip of a wooden craft stick in chocolate and insert it into the cookie opposite the antlers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4BB2110CF711742BF4238D28C52DFE9', X'4FBF3DF45FEEAA4C932ECAB918B450E1', X'4FBF3DF45FEEAA4C932ECAB918B450E1', 6, NULL, NULL, 2, NULL, 0, 'Let chocolate dry before picking up your cookie pops.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B069280901988C43A927DE00265F85DC', X'78C1877CAE73CD46AFFB7D568557EC80', X'78C1877CAE73CD46AFFB7D568557EC80', 0, NULL, NULL, 2, NULL, 0, 'Split apart an Oreo Mega Stuf Cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD8C73DDCA6AAE4183A9DEB9D6FFF4A8', X'78C1877CAE73CD46AFFB7D568557EC80', X'78C1877CAE73CD46AFFB7D568557EC80', 1, NULL, NULL, 2, NULL, 0, 'Brush chocolate on the inside of the cookie and press the two halves back together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B699193E746FC14897A54AD095D95D53', X'78C1877CAE73CD46AFFB7D568557EC80', X'78C1877CAE73CD46AFFB7D568557EC80', 2, NULL, NULL, 2, NULL, 0, 'Split apart a Mini Oreo Cookie and scrape out the filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'324C512B97BB1241A76E173A804C638C', X'78C1877CAE73CD46AFFB7D568557EC80', X'78C1877CAE73CD46AFFB7D568557EC80', 3, NULL, NULL, 2, NULL, 0, 'Dip one edge of each mini cookie in chocolate and insert them into the Mega Stuf cookie creating Mickey''s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FFB31F4857951846B7BD044C876CFA3F', X'78C1877CAE73CD46AFFB7D568557EC80', X'78C1877CAE73CD46AFFB7D568557EC80', 4, NULL, NULL, 2, NULL, 0, 'Flatten one tip of a paper straw and dip the tip in chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C20C4A867EC2D4F9E6CC0D5FF82AAED', X'78C1877CAE73CD46AFFB7D568557EC80', X'78C1877CAE73CD46AFFB7D568557EC80', 5, NULL, NULL, 2, NULL, 0, 'Insert into the Mega Stuf cookie opposite the cookie ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04B059B06DEE9A4787D9FE081754B2B5', X'78C1877CAE73CD46AFFB7D568557EC80', X'78C1877CAE73CD46AFFB7D568557EC80', 6, NULL, NULL, 2, NULL, 0, 'Repeat creating 12 Mickey cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'764680154166D84992B693424EBC04DE', X'78C1877CAE73CD46AFFB7D568557EC80', X'78C1877CAE73CD46AFFB7D568557EC80', 7, NULL, NULL, 2, NULL, 0, 'Freeze cookie for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9371F8441F48B64F996E6B87646C2901', X'78C1877CAE73CD46AFFB7D568557EC80', X'78C1877CAE73CD46AFFB7D568557EC80', 8, NULL, NULL, 2, NULL, 0, 'Dip the cookies in chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D92DE7FC49C9049A4D0E6E06CF2F67F', X'78C1877CAE73CD46AFFB7D568557EC80', X'78C1877CAE73CD46AFFB7D568557EC80', 9, NULL, NULL, 2, NULL, 0, 'Set cookie on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B15D99F22DFF8240AF3EBC4242830988', X'78C1877CAE73CD46AFFB7D568557EC80', X'78C1877CAE73CD46AFFB7D568557EC80', 10, NULL, NULL, 2, NULL, 0, 'Press one red candy nose onto the cookie opposite the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02B93140BAE96546BC6A1EBC5C9DF6F4', X'78C1877CAE73CD46AFFB7D568557EC80', X'78C1877CAE73CD46AFFB7D568557EC80', 11, NULL, NULL, 2, NULL, 0, 'Freeze for 3-5 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0175CEBF8B570F449C94A0B708A285D8', X'E25A30ABED918C4DB37ABFBC7ED34344', X'E25A30ABED918C4DB37ABFBC7ED34344', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'674CB0F215992540BDB97664A710D4F0', X'E25A30ABED918C4DB37ABFBC7ED34344', X'E25A30ABED918C4DB37ABFBC7ED34344', 1, NULL, NULL, 2, NULL, 0, 'Use melted chocolate to attach 2 jumbo candy eyes and 2 candy antlers to each waffle cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19D8D4D1B2E794488D593DE5412E143A', X'E25A30ABED918C4DB37ABFBC7ED34344', X'E25A30ABED918C4DB37ABFBC7ED34344', 2, NULL, NULL, 2, NULL, 0, 'Then attach a big red candy nose to the tip of each waffle cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00D69A23ACED7F4CB9819A3E46C63306', X'E25A30ABED918C4DB37ABFBC7ED34344', X'E25A30ABED918C4DB37ABFBC7ED34344', 3, NULL, NULL, 2, NULL, 0, 'To keep the cones from rolling around while the chocolate dries, set two more candies on either side of the red nose. Once dry, remove the candies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6A11BA3A1B56948B113F60F47693FB0', X'E25A30ABED918C4DB37ABFBC7ED34344', X'E25A30ABED918C4DB37ABFBC7ED34344', 4, NULL, NULL, 2, NULL, 0, 'Fill with your favorite candy, popcorn, nuts, or ice cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E003B6E5EA16240A6A07426692326DF', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees (325 for dark non-stick pans)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D8CB7147D46254890011B38C33702B7', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 1, NULL, NULL, 2, NULL, 0, 'Spray at 9 inch by 5 inch loaf pan with non-stick baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8DC6F3BF8CCFC478C8B91E5CADE5A15', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 2, NULL, NULL, 2, NULL, 0, 'Combine cake mix with egg whites, water and coconut milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C0EC3B57A7A244F8C544E1BB4A460F6', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 3, NULL, NULL, 2, NULL, 0, 'Beat on low for 30 seconds, then beat on high for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73E2749ACE38DD438BF5190E9E3FDA02', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 4, NULL, NULL, 2, NULL, 0, 'Finely chop the coconut, using a mini food processor or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07203EC1A0EBFC4394C0E0EF9EF39E89', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 5, NULL, NULL, 2, NULL, 0, 'Fold coconut into cake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CD26EB221D9224A985A314DD42DE3A3', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 6, NULL, NULL, 2, NULL, 0, '_');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5AFA8EDF0C085747BD6042C1628D9AF1', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 7, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1BE6A5E8CAD7145B5B1319DD63E099C', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 8, NULL, NULL, 2, NULL, 0, 'Divide batter in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'391221A120EC2447A3D84C48856CDAF4', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 9, NULL, NULL, 2, NULL, 0, 'Divide one half of the batter into equal parts and pour into two bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00A7728FFF4C4B4FA826277EF14AED3A', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 10, NULL, NULL, 2, NULL, 0, 'Color one red and one green.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'510261CB109E064DA7BCE6D775DE3CC0', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 11, NULL, NULL, 2, NULL, 0, 'Pour red batter into a pastry bag and green into another bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F50801D70E34BE4D948719EDC5A0F9AF', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 12, NULL, NULL, 2, NULL, 0, 'Spoon 1/2 cup of the white batter into the loaf pan and brush it around the bottom and up the sides of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AE3D1FE8B0E0349AC7C858EAE2B1508', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 13, NULL, NULL, 2, NULL, 0, 'Pour the remaining white batter into a large pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9ACAB5A7F38AF4B87168C17D483A80C', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 14, NULL, NULL, 2, NULL, 0, 'Pipe a small amount of green batter in a squiggly pattern into pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F856005F4FA3B94AB233DE52BF4F0FB1', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 15, NULL, NULL, 2, NULL, 0, 'Pipe on some white squiggles then some red.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB194AECB041634D90272A6A519F33AF', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 16, NULL, NULL, 2, NULL, 0, 'Repeat creating layers of green, white, red, then white squiggles until all the red and green batter is gone.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F83EB7A8FA73F4394AF5C2FD3DE6AFE', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 17, NULL, NULL, 2, NULL, 0, 'Pipe on the remaining white batter and smooth out the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44CF1F839ED78343B68709F8CD86EA0F', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 18, NULL, NULL, 2, NULL, 0, 'Tap the loaf pan on the counter a few times to remove big air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC04EFFBD237EB47AE1F238AE0D073A9', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 19, NULL, NULL, 2, NULL, 0, '_');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1FC12456A3C1649BD2D98A577BA223A', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 20, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'955D7217B9FE4A489C53936919D9290F', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 21, NULL, NULL, 2, NULL, 0, 'Bake the pound cake for 50-60 minutes until a toothpick inserted in the center comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F7D259D5E83774598775D8FA6A51C47', X'21701B49AFEEFD4CB52883AC5F341690', X'21701B49AFEEFD4CB52883AC5F341690', 22, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool for 10 minutes in the pan, then turn it out onto a cooling rack and allow it to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3A7B12942E03846B8339A9741689145', X'30AD84B9ED4F854F9D9FF129C62A5F96', X'30AD84B9ED4F854F9D9FF129C62A5F96', 0, NULL, NULL, 2, NULL, 0, 'Stir the powdered sugar, 2 tablespoons coconut milk, and coconut extract together. Stir in more coconut milk, if needed, until you have a nice thick, yet pourable glaze.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58C13FE0E95BCE41ADF22309E7BC6923', X'83A5CDB2DDA7C841832EF01C32626A1F', X'83A5CDB2DDA7C841832EF01C32626A1F', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78F46AB18FB05E4A8B366555DD0149A5', X'83A5CDB2DDA7C841832EF01C32626A1F', X'83A5CDB2DDA7C841832EF01C32626A1F', 1, NULL, NULL, 2, NULL, 0, 'Pour white chocolate into a tall glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E89B65D6605D5E49B4549B5A6E5C0F83', X'83A5CDB2DDA7C841832EF01C32626A1F', X'83A5CDB2DDA7C841832EF01C32626A1F', 2, NULL, NULL, 2, NULL, 0, 'Dip one pretzel rod in the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD93897E4D1B1C4090F95F7CA4E5F94C', X'83A5CDB2DDA7C841832EF01C32626A1F', X'83A5CDB2DDA7C841832EF01C32626A1F', 3, NULL, NULL, 2, NULL, 0, 'Remove and allow the excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4B0BAB4CCB40B44A6FE4FBB7889B895', X'83A5CDB2DDA7C841832EF01C32626A1F', X'83A5CDB2DDA7C841832EF01C32626A1F', 4, NULL, NULL, 2, NULL, 0, 'Roll in the blue/white/snowflake sprinkles and the blue and white snowflake candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B62300EBF7CF404597C14AA9896D8DC5', X'83A5CDB2DDA7C841832EF01C32626A1F', X'83A5CDB2DDA7C841832EF01C32626A1F', 5, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper or wax paper lined baking sheet and allow to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44C3034CBA299441801660D5F411E0F5', X'35AA01AA5C373344AD6E70D58019AE6B', X'35AA01AA5C373344AD6E70D58019AE6B', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8D3617ECBC6514880AFF8FB292CE454', X'35AA01AA5C373344AD6E70D58019AE6B', X'35AA01AA5C373344AD6E70D58019AE6B', 1, NULL, NULL, 2, NULL, 0, 'Break pretzels into antlers, or just keep whole.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC2D79C9F0C8F84AB4DD2F982AA25D77', X'35AA01AA5C373344AD6E70D58019AE6B', X'35AA01AA5C373344AD6E70D58019AE6B', 2, NULL, NULL, 2, NULL, 0, 'Unwrap candy bars.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3F91E8963D67D458293574EF1111A66', X'35AA01AA5C373344AD6E70D58019AE6B', X'35AA01AA5C373344AD6E70D58019AE6B', 3, NULL, NULL, 2, NULL, 0, 'Use melted candy melts or chocolate to attach the two antlers, two candy eyes, and one red nose to each candy bar creating Rudolph.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'256B2E7787E6094BA447C9A22A34B2FE', X'480E0347FD303B42AAAE7F9D0E0108D1', X'480E0347FD303B42AAAE7F9D0E0108D1', 0, NULL, NULL, 2, NULL, 0, 'Pour the cream of coconut into a microwave safe bowl and stir until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A163CCB9A9C1824FB8B1CD67C7E498ED', X'480E0347FD303B42AAAE7F9D0E0108D1', X'480E0347FD303B42AAAE7F9D0E0108D1', 1, NULL, NULL, 2, NULL, 0, 'Stir in the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6DDF53FE94482B4C9A58A00B8E64BA7F', X'480E0347FD303B42AAAE7F9D0E0108D1', X'480E0347FD303B42AAAE7F9D0E0108D1', 2, NULL, NULL, 2, NULL, 0, 'Heat in the microwave on high for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'563CD5910FDFD74F8B0480AD74FA1F6B', X'480E0347FD303B42AAAE7F9D0E0108D1', X'480E0347FD303B42AAAE7F9D0E0108D1', 3, NULL, NULL, 2, NULL, 0, 'Or melt in a double boiler set over an inch of simmering water.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36448460BD821F4398F813DDE756A610', X'480E0347FD303B42AAAE7F9D0E0108D1', X'480E0347FD303B42AAAE7F9D0E0108D1', 4, NULL, NULL, 2, NULL, 0, 'Stir in unsweetened coconut.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54ECE48D1402884989C7560E7E507A07', X'480E0347FD303B42AAAE7F9D0E0108D1', X'480E0347FD303B42AAAE7F9D0E0108D1', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3E806E68DBB6D40B8CC5E13389C3A9A', X'480E0347FD303B42AAAE7F9D0E0108D1', X'480E0347FD303B42AAAE7F9D0E0108D1', 6, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FE29C408164E64A86BCAB68F30EB87C', X'480E0347FD303B42AAAE7F9D0E0108D1', X'480E0347FD303B42AAAE7F9D0E0108D1', 7, NULL, NULL, 2, NULL, 0, 'Remove and scoop out tablespoon sized balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAD9E06166C1A14C9BE1E6653ED54C8F', X'480E0347FD303B42AAAE7F9D0E0108D1', X'480E0347FD303B42AAAE7F9D0E0108D1', 8, NULL, NULL, 2, NULL, 0, 'Roll into cones and roll into sweetened coconut.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1C3376D9A5AA048B0219F24516F2CFA', X'480E0347FD303B42AAAE7F9D0E0108D1', X'480E0347FD303B42AAAE7F9D0E0108D1', 9, NULL, NULL, 2, NULL, 0, 'Use melted white chocolate as `glue` to attach gold dragee ornaments all around the coconut candy Christmas trees.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E091DED228A304D9C0A13AC97E6DA7A', X'480E0347FD303B42AAAE7F9D0E0108D1', X'480E0347FD303B42AAAE7F9D0E0108D1', 10, NULL, NULL, 2, NULL, 0, 'Attach one gold candy star to the top of each tree using the melted white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B628D3EC0D9704DA3845D59D50D727F', X'480E0347FD303B42AAAE7F9D0E0108D1', X'480E0347FD303B42AAAE7F9D0E0108D1', 11, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for up to several weeks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D031FADDA985C64D96BE7F57E93C7502', X'29AEBA8303678043B1BA91E76DC87FBA', X'29AEBA8303678043B1BA91E76DC87FBA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F (325 for dark cupcake pans)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D51D17C0B51534BA0E750995B2C28B6', X'29AEBA8303678043B1BA91E76DC87FBA', X'29AEBA8303678043B1BA91E76DC87FBA', 1, NULL, NULL, 2, NULL, 0, 'Line a cupcake pan with 12 wrappers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8F6F56BC8D1A242B23E2B5FAFAF0EEA', X'29AEBA8303678043B1BA91E76DC87FBA', X'29AEBA8303678043B1BA91E76DC87FBA', 2, NULL, NULL, 2, NULL, 0, 'Pour finely chopped chocolate and cocoa powder into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF8130859AD2AF47B0F261A8A26AB581', X'29AEBA8303678043B1BA91E76DC87FBA', X'29AEBA8303678043B1BA91E76DC87FBA', 3, NULL, NULL, 2, NULL, 0, 'Add hot coffee and stir until chocolate melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49132667C446D94B85105435CEFC02DB', X'29AEBA8303678043B1BA91E76DC87FBA', X'29AEBA8303678043B1BA91E76DC87FBA', 4, NULL, NULL, 2, NULL, 0, 'Place in the refrigerator to cool for about 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C577B96F14F0249A089FAF665B0D8E8', X'29AEBA8303678043B1BA91E76DC87FBA', X'29AEBA8303678043B1BA91E76DC87FBA', 5, NULL, NULL, 2, NULL, 0, 'Once cooled, whisk in oil, eggs, white vinegar, and vanilla extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E33AB6D853F2684A86EC24834224A54E', X'29AEBA8303678043B1BA91E76DC87FBA', X'29AEBA8303678043B1BA91E76DC87FBA', 6, NULL, NULL, 2, NULL, 0, 'In a separate bowl whisk together the sugar, flour, baking soda, and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C17F272F4A8CD478485A796BF2AD48B', X'29AEBA8303678043B1BA91E76DC87FBA', X'29AEBA8303678043B1BA91E76DC87FBA', 7, NULL, NULL, 2, NULL, 0, 'Add it to the chocolate bowl and whisk until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4825E10C579B841BAAAC205465A91E5', X'29AEBA8303678043B1BA91E76DC87FBA', X'29AEBA8303678043B1BA91E76DC87FBA', 8, NULL, NULL, 2, NULL, 0, 'Equally divide batter among 12 cupcake wrappers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2D70FD1E05FA4489C3A676781F7E6A9', X'29AEBA8303678043B1BA91E76DC87FBA', X'29AEBA8303678043B1BA91E76DC87FBA', 9, NULL, NULL, 2, NULL, 0, 'Bake the cupcakes for 20-22 minutes until they are set around the edges and puffed in the middle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2732C8B96D2B4C4A9C2D42ADDCA7E677', X'29AEBA8303678043B1BA91E76DC87FBA', X'29AEBA8303678043B1BA91E76DC87FBA', 10, NULL, NULL, 2, NULL, 0, 'Allow them to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B99D0212BE47A4D9E5F8F011A10F1DB', X'4DA32A4C9D2BC54488E60E9E0B8936A9', X'4DA32A4C9D2BC54488E60E9E0B8936A9', 0, NULL, NULL, 2, NULL, 0, 'Place chopped chocolate in the bowl of a food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EB3D95BDF5E8C45A2D87E4EB2320EAF', X'4DA32A4C9D2BC54488E60E9E0B8936A9', X'4DA32A4C9D2BC54488E60E9E0B8936A9', 1, NULL, NULL, 2, NULL, 0, 'Pulse until chocolate is fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'275A632CD70237459C20CAE669E244DC', X'4DA32A4C9D2BC54488E60E9E0B8936A9', X'4DA32A4C9D2BC54488E60E9E0B8936A9', 2, NULL, NULL, 2, NULL, 0, 'Heat heavy whipping cream in a small saucepan over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD69591313DD2743942138BF603086FC', X'4DA32A4C9D2BC54488E60E9E0B8936A9', X'4DA32A4C9D2BC54488E60E9E0B8936A9', 3, NULL, NULL, 2, NULL, 0, 'Stir often until it just comes to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C591E5762D3F246ABAA274572F5C44C', X'4DA32A4C9D2BC54488E60E9E0B8936A9', X'4DA32A4C9D2BC54488E60E9E0B8936A9', 4, NULL, NULL, 2, NULL, 0, 'Pour the hot cream over the chocolate, put lid on the food processor bowl, and let sit for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B884170AE170D347A687CB756EC2DBA7', X'4DA32A4C9D2BC54488E60E9E0B8936A9', X'4DA32A4C9D2BC54488E60E9E0B8936A9', 5, NULL, NULL, 2, NULL, 0, 'Pulse for 3 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B3BA28D0E19D8408B11A5A9A8BDEA92', X'4DA32A4C9D2BC54488E60E9E0B8936A9', X'4DA32A4C9D2BC54488E60E9E0B8936A9', 6, NULL, NULL, 2, NULL, 0, 'Remove lid and scrape down the sides and bottom of the food processor bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'118C67095642384A82E1D6AE4B82F13E', X'4DA32A4C9D2BC54488E60E9E0B8936A9', X'4DA32A4C9D2BC54488E60E9E0B8936A9', 7, NULL, NULL, 2, NULL, 0, 'Return lid and pulse for 5 more seconds until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4793B7324B96014092D1C265FB843306', X'4DA32A4C9D2BC54488E60E9E0B8936A9', X'4DA32A4C9D2BC54488E60E9E0B8936A9', 8, NULL, NULL, 2, NULL, 0, 'Pour into a mixing bowl and allow to cool for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93CDB7E31950774DB9326366C0D0071D', X'4DA32A4C9D2BC54488E60E9E0B8936A9', X'4DA32A4C9D2BC54488E60E9E0B8936A9', 9, NULL, NULL, 2, NULL, 0, 'Beat using an electric mixer until fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'314D975BAAA6A54DB41CE3CB9F129918', X'4DA32A4C9D2BC54488E60E9E0B8936A9', X'4DA32A4C9D2BC54488E60E9E0B8936A9', 10, NULL, NULL, 2, NULL, 0, 'Pipe in big swirls over cooled cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72227D8B1695E741B5979E7D3A213079', X'4CC32D8632A3AE478C13C2BCCE4C202A', X'4CC32D8632A3AE478C13C2BCCE4C202A', 0, NULL, NULL, 2, NULL, 0, 'Use melted chocolate to attach two jumbo candy eyes and a red nose to each peanut butter cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C92143550778EA49A38D0EC838228DEA', X'4CC32D8632A3AE478C13C2BCCE4C202A', X'4CC32D8632A3AE478C13C2BCCE4C202A', 1, NULL, NULL, 2, NULL, 0, 'Attach to the top of the frosted cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5FB7016348A0F42BA5CACAE106A2833', X'4CC32D8632A3AE478C13C2BCCE4C202A', X'4CC32D8632A3AE478C13C2BCCE4C202A', 2, NULL, NULL, 2, NULL, 0, 'Attach two small malted milk balls to the cupcakes just above the wrappers and on either side of the peanut butter cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66B549180A74AB4ABFBF918DCDBFD6D7', X'4CC32D8632A3AE478C13C2BCCE4C202A', X'4CC32D8632A3AE478C13C2BCCE4C202A', 3, NULL, NULL, 2, NULL, 0, 'Insert two candy canes into the frosting behind the peanut butter cups to create antlers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'824C9F11B692EB48A3D579C696BB08AA', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D2DC6DE170ED04085552BBCDBDF0DEB', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 1, NULL, NULL, 2, NULL, 0, 'Line two 9x13 baking pans with non-stick foil and spray with baking spray, or spray the pans, line with parchment paper, spray again, and dust with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F86318D97C0D1489311A887713A6245', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 2, NULL, NULL, 2, NULL, 0, 'Pour Coca-Cola into a medium saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D442E2017DFDBB4AB46B8435F5A756EB', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 3, NULL, NULL, 2, NULL, 0, 'Heat over medium heat, stirring often, until thickened and reduced to about 2 1/2 tablespoons, about 30 minutes. Watch it near the end so it doesn''t burn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D2D6DFE9C912C4FB1D381C788EF1F14', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 4, NULL, NULL, 2, NULL, 0, 'Reserve 1 teaspoon of the Coke syrup for the filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'145CD2CAFB415347B372628A71B6C3D9', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 5, NULL, NULL, 2, NULL, 0, 'Pour the remaining syrup in a microwave safe bowl along with the chocolate and butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'058064FB5848F4438915949E0D90CE8E', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 6, NULL, NULL, 2, NULL, 0, 'Heat on high in the microwave for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0798A26D77A2643AA8A5B85E1BBFC15', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 7, NULL, NULL, 2, NULL, 0, 'Stir then let rest for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BD19466B2053D4E8B1F46AAAFC5DB37', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 8, NULL, NULL, 2, NULL, 0, 'Stir again until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C0A0958898CC841BD515092E5D577F0', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 9, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'011B887F4A21704FBCE1FE4BD78DE86E', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 10, NULL, NULL, 2, NULL, 0, 'Beat egg yolks using an electric mixer set on medium high speed until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'443F672FE13B894C9DF8A18894384DB5', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 11, NULL, NULL, 2, NULL, 0, 'With the beaters running, sprinkle in about 3 tablespoons of the sugar and continue to beat until the eggs become pale yellow and thick enough that it falls in a thick ribbon when the beaters are lifted out of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F933F6259ED2674FB87A6F3F773AA346', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 12, NULL, NULL, 2, NULL, 0, 'Fold the egg yolks into the melted chocolate mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F2E53AC1F62C94FBDB1BFA37251060E', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 13, NULL, NULL, 2, NULL, 0, 'Sift together the flour, cocoa powder, and salt and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8E02647B0F4C74F828960CED081BBDC', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 14, NULL, NULL, 2, NULL, 0, 'In a clean bowl with clean beaters whip the egg whites and cream of tarter until foamy. Add 1 teaspoon of the sugar and continue to beat to soft peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5BBCE81D7A6634E9012DF9341FE01BC', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 15, NULL, NULL, 2, NULL, 0, 'Continue beating and sprinkle in the remaining sugar until the whites become glossy and hold stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'669559456843684A8C220D0A18AF3251', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 16, NULL, NULL, 2, NULL, 0, 'Don''t over beat until dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81FB6F43254E024DA0E8FE2F29446EDA', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 17, NULL, NULL, 2, NULL, 0, 'Fold 1/3rd of the egg whites into the chocolate mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA721693BC9EFF4AA068630C1BA5B003', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 18, NULL, NULL, 2, NULL, 0, 'Then gently fold in the remaining egg whites.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D80EB3792160B24BA8917FDDADA26F5B', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 19, NULL, NULL, 2, NULL, 0, 'Fold in the dry ingredients.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'147FC1B8C65B114CA8E26E6A62DECFE6', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 20, NULL, NULL, 2, NULL, 0, 'Equally divide the batter among the two cake pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03AE4CD563B7354E8A7DF3B1C5849D54', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 21, NULL, NULL, 2, NULL, 0, 'Bake for about 8 minutes, until the center of the cake springs back when touched.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5EC2D65A119EB4BB161B0719F5CC979', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 22, NULL, NULL, 2, NULL, 0, 'While the cakes are baking cut 8 strips of parchment paper that are at least 4 1/2 inches wide by 8 inches long.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEFF315F1A7B9347A5C1ECEF38EEB05C', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 23, NULL, NULL, 2, NULL, 0, 'Set them out on your counter and dust them with cocoa powder.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DD50E39E96D5F4E85B7C9DDF90A2130', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 24, NULL, NULL, 2, NULL, 0, 'Remove the cakes from the oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78FC1926D0E62D4886F88A451FC1F19F', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 25, NULL, NULL, 2, NULL, 0, 'Let them sit for 3 minutes, then remove one from the pan. Cut into 4 equal rectangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65FF7F20A5BD644296DB56C7ADEAC3D7', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 26, NULL, NULL, 2, NULL, 0, 'Immediately, set one cake rectangle on each parchment paper strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D42F01A11CB17E43B99B9BFEF8E36AC8', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 27, NULL, NULL, 2, NULL, 0, 'Roll up the cakes and parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E8F8992E5423B149BB140E65984E266D', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 28, NULL, NULL, 2, NULL, 0, 'Repeat with second cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC1C002909451245ADE61F2B0E190AC7', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 29, NULL, NULL, 2, NULL, 0, 'Allow cakes to cool for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA9C1756D84CAD449DE03FAAE801FA12', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 30, NULL, NULL, 2, NULL, 0, 'Meanwhile make your filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'643AC7E6FE1DC34493762656D716F8C7', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 31, NULL, NULL, 2, NULL, 0, 'Combine the shortening, powdered sugar, marshmallow fluff, and whipping cream in a large mixing bowl and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BCC1B36AA7F324D807A2EEB06F6FB56', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 32, NULL, NULL, 2, NULL, 0, 'Stir in the reserved Coca-Cola syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04E47267A4F142409F589E82633B6F46', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 33, NULL, NULL, 2, NULL, 0, 'When cakes are ready, carefully unroll them and remove the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD1288A600407A4CA3494F2A4B9B2B3E', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 34, NULL, NULL, 2, NULL, 0, 'Pipe or spread filling on each cake leaving a border around the edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2288FB3CAE437840B9CACB6480BB7DD6', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 35, NULL, NULL, 2, NULL, 0, 'Roll up cake and repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD56D8D507E29149A7B42D2005A292E0', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 36, NULL, NULL, 2, NULL, 0, 'Now they are ready to become penguins.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B193FB1FCDA41946AF0572E570BD0AE1', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 37, NULL, NULL, 2, NULL, 0, 'Color a small amount of the white modeling chocolate orange.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CEF09A439A84A4BB4BB19CFAB5BD989', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 38, NULL, NULL, 2, NULL, 0, 'Roll out the white, orange and dark modeling chocolate to about 1/16-1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'056CE1B43DCE3246A265FB032B6940A5', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 39, NULL, NULL, 2, NULL, 0, 'Cut oval bellies, two white eyes, two small dark pupils, two tear drop shaped wings, and 3 triangles for the beak and feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F882E82AF2AFA44BBA3D007D61BC5FD1', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 40, NULL, NULL, 2, NULL, 0, 'Lightly brush water on the back side of the pupils and attach to the whites of the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F467EB1036D4B4CAE3DF8A24E4452E6', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 41, NULL, NULL, 2, NULL, 0, 'Attach the eyes, along with a beak, two wings and a white belly using the candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4159BA79F38BE9459D33AF905141B9DD', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 42, NULL, NULL, 2, NULL, 0, 'Cut two small slits along one edge of the remaining triangles, creating feet and attach them to the cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55CD8B76990F73479FC5C93BC1A54513', X'A33E34C30CA6C1459D7831CAD1BACB84', X'A33E34C30CA6C1459D7831CAD1BACB84', 43, NULL, NULL, 2, NULL, 0, 'You can serve these standing up if you trim the bottoms as needed so they stand upright.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDFB9A36C34CA348929ABBAB528F7D8B', X'72F0A8AB78D3B241AF95CCD6A0881A5F', X'72F0A8AB78D3B241AF95CCD6A0881A5F', 0, NULL, NULL, 2, NULL, 0, 'Pour white chocolate and sweetened condensed milk into a saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B026A858FE0D1240A677C60293A917D1', X'72F0A8AB78D3B241AF95CCD6A0881A5F', X'72F0A8AB78D3B241AF95CCD6A0881A5F', 1, NULL, NULL, 2, NULL, 0, 'Set over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C1996AFFEC5EA44BAFA829A6EDB28D06', X'72F0A8AB78D3B241AF95CCD6A0881A5F', X'72F0A8AB78D3B241AF95CCD6A0881A5F', 2, NULL, NULL, 2, NULL, 0, 'Heat, stirring occasionally, until the chocolate is about half melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9356520FB9AFBE459022D8E009DDCD2A', X'72F0A8AB78D3B241AF95CCD6A0881A5F', X'72F0A8AB78D3B241AF95CCD6A0881A5F', 3, NULL, NULL, 2, NULL, 0, 'Remove from the heat and stir slowly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE07AFEDF26A4B4C920D42526F01C2D5', X'72F0A8AB78D3B241AF95CCD6A0881A5F', X'72F0A8AB78D3B241AF95CCD6A0881A5F', 4, NULL, NULL, 2, NULL, 0, 'Set aside for 5 minutes to allow the heat to melt the remaining chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDDCBB70E7CCB44190266543E25057F9', X'72F0A8AB78D3B241AF95CCD6A0881A5F', X'72F0A8AB78D3B241AF95CCD6A0881A5F', 5, NULL, NULL, 2, NULL, 0, 'Stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33197D8DC1A586459F8D867EAB888F40', X'72F0A8AB78D3B241AF95CCD6A0881A5F', X'72F0A8AB78D3B241AF95CCD6A0881A5F', 6, NULL, NULL, 2, NULL, 0, 'Spoon the fudge into the molds and pop them in the freezer for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'080F5E2FA275F344A40325F79C8DD732', X'4D537292D160A14F8184B2D9A2833C9F', X'4D537292D160A14F8184B2D9A2833C9F', 0, NULL, NULL, 2, NULL, 0, 'Heat the heavy whipping cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29E7345A410A7941BC4B27DB734F3C0D', X'4D537292D160A14F8184B2D9A2833C9F', X'4D537292D160A14F8184B2D9A2833C9F', 1, NULL, NULL, 2, NULL, 0, 'Pour 1/2 cup of the hot coffee along with the hot cream over the chopped chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83320C35B14F0744A8B91444A023C726', X'4D537292D160A14F8184B2D9A2833C9F', X'4D537292D160A14F8184B2D9A2833C9F', 2, NULL, NULL, 2, NULL, 0, 'Leave it sit for 2 minutes then stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DB94F34C4345344BCC42E1611CAF19F', X'4D537292D160A14F8184B2D9A2833C9F', X'4D537292D160A14F8184B2D9A2833C9F', 3, NULL, NULL, 2, NULL, 0, 'Let the ganache cool for 20-30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3DB2524B7AFA64B9DE6E0D8B144B867', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 0, NULL, NULL, 2, NULL, 0, 'Paint a thin layer of white chocolate into each egg shaped cavity in your candy molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5280C83B22C1184B842F2A229C737BB4', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 1, NULL, NULL, 2, NULL, 0, 'Chill for 5 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDAB8A530AD52448A8CD1F63CB42CA91', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 2, NULL, NULL, 2, NULL, 0, 'Pipe chocolate ganache into the chocolate shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0B7A8277AD3954983DAFD299A1877B6', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 3, NULL, NULL, 2, NULL, 0, 'Set aside for 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80710CB8DD17084497E46E84AA32F0F9', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 4, NULL, NULL, 2, NULL, 0, 'Pipe a layer of chocolate over top of the ganache and scrape off any excess chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6EF274E4B1441A45A71B3A19166E6405', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 5, NULL, NULL, 2, NULL, 0, 'Chill for 5 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5ECD9028BAF5248B6E7466B5A23A1A5', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 6, NULL, NULL, 2, NULL, 0, 'Un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F24FE8F30C0E1643A2246FCF3012FC6C', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 7, NULL, NULL, 2, NULL, 0, 'Pipe melted chocolate tree limbs onto parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0AC108E1B2A8C146AC877696B00C5ECC', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 8, NULL, NULL, 2, NULL, 0, 'Allow them to harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D1D33D798DC4344A09633C4D1476719', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 9, NULL, NULL, 2, NULL, 0, 'Attach two black pearl eyes and 1 orange candy coated sunflower seed nose using a bit of white chocolate to each egg shaped truffle. Draw on the dots for the smile using a food coloring marker. Then attach three black pearl buttons.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91EEE42FF4BFF94DBDB58362DB413761', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 10, NULL, NULL, 2, NULL, 0, 'Spoon about a teaspoon or so of white chocolate onto a piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'747200961B4CCA45936E5313CF1834EC', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 11, NULL, NULL, 2, NULL, 0, 'Allow it to start setting, then place one truffle in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0AC9BF3FF1892409A50B3F1417A177D', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 12, NULL, NULL, 2, NULL, 0, 'If using, sprinkle edible glitter over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'377D5AADA17F5C428B721A7A1551CCA3', X'6505D33D335A254FB24848AA76A8FEBD', X'6505D33D335A254FB24848AA76A8FEBD', 13, NULL, NULL, 2, NULL, 0, 'Use some white chocolate to attach two stick arms to each truffle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'087B13F70EE41342840DB7FFCACEFE02', X'BE0073B77FD314418B44D1435D9BAD7E', X'BE0073B77FD314418B44D1435D9BAD7E', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03BBBC27AFB4EC4DB0521CEF6FFB9D97', X'BE0073B77FD314418B44D1435D9BAD7E', X'BE0073B77FD314418B44D1435D9BAD7E', 1, NULL, NULL, 2, NULL, 0, 'Spray a 9 inch springform pan with non-stick baking spray. Wipe out with a paper towel, leaving just a thin coating on the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0CE99B8200FEBC488BFC4313E99EFA35', X'BE0073B77FD314418B44D1435D9BAD7E', X'BE0073B77FD314418B44D1435D9BAD7E', 2, NULL, NULL, 2, NULL, 0, 'Stir together brownie mix, vegetable oil, water and egg. Pour into the 9 inch springform pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26DB968B0EC9D347A4B662346D301534', X'BE0073B77FD314418B44D1435D9BAD7E', X'BE0073B77FD314418B44D1435D9BAD7E', 3, NULL, NULL, 2, NULL, 0, 'Bake for 48-52 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F20D0EF45E90AA42B2C29A834939D04F', X'BE0073B77FD314418B44D1435D9BAD7E', X'BE0073B77FD314418B44D1435D9BAD7E', 4, NULL, NULL, 2, NULL, 0, 'Cool completely in pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72CA1D0CB5749749A931ECDD19A6C1D6', X'BE0073B77FD314418B44D1435D9BAD7E', X'BE0073B77FD314418B44D1435D9BAD7E', 5, NULL, NULL, 2, NULL, 0, 'Remove ring, and loosen brownie from the bottom before you top it with mousse.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BE071B2B634BE499863E6296D85704B', X'D2CB3F0EAADA794DA4FD8184E8A513AC', X'D2CB3F0EAADA794DA4FD8184E8A513AC', 0, NULL, NULL, 2, NULL, 0, 'Whisk whipping cream to soft peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B22BD2E23086E4192A51E44914D3B90', X'D2CB3F0EAADA794DA4FD8184E8A513AC', X'D2CB3F0EAADA794DA4FD8184E8A513AC', 1, NULL, NULL, 2, NULL, 0, 'Melt the chocolate. Click here for detailed instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'838D8BC87424FD45ADD86F11BEF610AB', X'D2CB3F0EAADA794DA4FD8184E8A513AC', X'D2CB3F0EAADA794DA4FD8184E8A513AC', 2, NULL, NULL, 2, NULL, 0, 'Pour water and sugar in a small saucepan and bring it to a boil over medium heat, stirring occasionally until the sugar dissolves.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B8B41F548BAA34499767D7FCE54280A9', X'D2CB3F0EAADA794DA4FD8184E8A513AC', X'D2CB3F0EAADA794DA4FD8184E8A513AC', 3, NULL, NULL, 2, NULL, 0, 'Whisk egg yolks in a medium bowl and slowly drizzle in the hot sugar syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36DAFDF5A384234786AC340F193B99BB', X'D2CB3F0EAADA794DA4FD8184E8A513AC', X'D2CB3F0EAADA794DA4FD8184E8A513AC', 4, NULL, NULL, 2, NULL, 0, 'Combine eggs with melted chocolate, stirring until well incorporated. Heat in microwave on high power for 15 seconds. Stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BB385094039DC4F9DE181FC082A01E9', X'D2CB3F0EAADA794DA4FD8184E8A513AC', X'D2CB3F0EAADA794DA4FD8184E8A513AC', 5, NULL, NULL, 2, NULL, 0, 'Add one third of the whipped cream to the chocolate and whisk until well incorporated. Gently fold in the remaining whipped cream until fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'797AD228B8A0A648A06ACE59D5B1B496', X'D2CB3F0EAADA794DA4FD8184E8A513AC', X'D2CB3F0EAADA794DA4FD8184E8A513AC', 6, NULL, NULL, 2, NULL, 0, 'Pour mousse over the chocolate brownie layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F585034688D404F98A97940834B658E', X'D2CB3F0EAADA794DA4FD8184E8A513AC', X'D2CB3F0EAADA794DA4FD8184E8A513AC', 7, NULL, NULL, 2, NULL, 0, 'Spread evenly over top then refrigerate the cake for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5F56465CC542D4F97B88E11DFCD29F6', X'A4F10E214469E44CAD97EF088D5D833F', X'A4F10E214469E44CAD97EF088D5D833F', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle gelatin over water. Set aside for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6967FB43F65004BA2ADDDDC7C37C4E2', X'A4F10E214469E44CAD97EF088D5D833F', X'A4F10E214469E44CAD97EF088D5D833F', 1, NULL, NULL, 2, NULL, 0, 'Bring 1/2 cup heavy whipping cream just to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABFE86A0341E8B4990009C87DDE4D6BB', X'A4F10E214469E44CAD97EF088D5D833F', X'A4F10E214469E44CAD97EF088D5D833F', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in gelatin until dissolved.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF2C7EFCFDD13E4EBC1D2EA006D248DD', X'A4F10E214469E44CAD97EF088D5D833F', X'A4F10E214469E44CAD97EF088D5D833F', 3, NULL, NULL, 2, NULL, 0, 'Pour over white chocolate. Let sit one minute, then stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EED27A32C360774A8784D04DAE36199A', X'A4F10E214469E44CAD97EF088D5D833F', X'A4F10E214469E44CAD97EF088D5D833F', 4, NULL, NULL, 2, NULL, 0, 'Combine remaining 1 cups heavy whipping cream and peppermint extract and whisk to stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D2CA21B9F52444681FA58728E99996B', X'A4F10E214469E44CAD97EF088D5D833F', X'A4F10E214469E44CAD97EF088D5D833F', 5, NULL, NULL, 2, NULL, 0, 'Fold whipped cream into white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB6181EF82DDD7489395246FBEA3C865', X'A4F10E214469E44CAD97EF088D5D833F', X'A4F10E214469E44CAD97EF088D5D833F', 6, NULL, NULL, 2, NULL, 0, 'Spread evenly over chocolate mousse layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD01404CB8375C4CB4CB8D025244070C', X'A4F10E214469E44CAD97EF088D5D833F', X'A4F10E214469E44CAD97EF088D5D833F', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate cake for at least two hours and up to 3 days.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18F4AAA9C060E54881F2E8C26AE1ECD1', X'A4F10E214469E44CAD97EF088D5D833F', X'A4F10E214469E44CAD97EF088D5D833F', 8, NULL, NULL, 2, NULL, 0, 'Can be frozen for up to a month.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC1083AB7F6C7A46AB304E473D750836', X'7F82407B774ABF4295E2173710B5F14B', X'7F82407B774ABF4295E2173710B5F14B', 0, NULL, NULL, 2, NULL, 0, 'pipe white chocolate into candy cane shapes');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3EC8B5C32A2214DA5F24272E038852C', X'7F82407B774ABF4295E2173710B5F14B', X'7F82407B774ABF4295E2173710B5F14B', 1, NULL, NULL, 2, NULL, 0, 'sprinkle with crushed candy canes');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26A6F981065AB0468BB007A7C68B593B', X'7F82407B774ABF4295E2173710B5F14B', X'7F82407B774ABF4295E2173710B5F14B', 2, NULL, NULL, 2, NULL, 0, 'just before serving set cake on cake plate and press candy canes into the side of the cake');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F12AE091430E24CB8CED211A9555C7D', X'7F82407B774ABF4295E2173710B5F14B', X'7F82407B774ABF4295E2173710B5F14B', 3, NULL, NULL, 2, NULL, 0, 'sprinkle top of cake with crushed candy canes');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18D88E5CC57E0141A3933124ACD38339', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 0, NULL, NULL, 2, NULL, 0, 'Combine the raisins, Amaretto, and cinnamon in a small saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE5F1236DC34B843B3B572663CB0A903', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 1, NULL, NULL, 2, NULL, 0, 'Set over medium heat. Bring to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E37F8B2FE7383143B5CF1B6FF9D6B1B0', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 2, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium low and simmer, stirring occasionally, until most of the liquid evaporates and the raisins become coated in a thick glaze.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4F28A8B6490AF40A612E8D27F9A376A', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 3, NULL, NULL, 2, NULL, 0, 'Pour white chocolate into bowl of food processor and pulse until you have small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0AEAF394D776549BDE80E00137AA484', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 4, NULL, NULL, 2, NULL, 0, 'Add the hot raisins to the chocolate and pulse until it is well blended. It will be very thick and chunky at this point.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7726E00554700648BA609A58BFB19795', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 5, NULL, NULL, 2, NULL, 0, 'Heat heavy whipping cream in a saucepan over medium heat, just until bubbles begin to form around the edge of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD7EE97FE45AA249ADC6BC17E36BF75E', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 6, NULL, NULL, 2, NULL, 0, 'Pour the hot cream over the white chocolate. Place the lid on the food processor and let it sit for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A5865FE3FF6E8428221BB7FEE091FA9', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 7, NULL, NULL, 2, NULL, 0, 'Pulse until creamy and smooth. Add the remaining 1 1/2 teaspoons Amaretto and pulse to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5853F5F64431FB40BD37110FC1456937', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 8, NULL, NULL, 2, NULL, 0, 'Create chocolate shells in egg molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4F0599FF49C9249AD057E6A9C07C3E4', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 9, NULL, NULL, 2, NULL, 0, 'Allow chocolate to set at room temperature for 10 minutes then refrigerate for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0855097EEFFC7146B3178D4BAB251C47', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 10, NULL, NULL, 2, NULL, 0, 'Fill with Amaretto Raisin Ganache');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB391ACD62A23443974260DCD08A57DE', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 11, NULL, NULL, 2, NULL, 0, 'Let set for 4 hours at room temperature');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB5B57D5A8E54E43BFDBD0742C4E400F', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 12, NULL, NULL, 2, NULL, 0, 'Cover ganache with thin layer of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98ECEEAD4DD672489557AD95AA812FE6', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 13, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10 minutes until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0E90585B78B6F4AAEA23328603E0E05', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 14, NULL, NULL, 2, NULL, 0, 'Un-mold and decorate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16D6353E8F38B640A0138B96CC5CE72B', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 15, NULL, NULL, 2, NULL, 0, 'Brush on an oval of white luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F55B8179B583D419B4725D131E15611', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 16, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes, 1 orange candy coated sunflower seed beak and 2 feet using chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8D462AB40044840B4FB99F6E9051991', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 17, NULL, NULL, 2, NULL, 0, 'Spoon a heaping teaspoonful of chocolate onto a piece of parchment paper. Sprinkle white non-pareils around the edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'740119BA42E6A649AC1BA747A4D282BC', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 18, NULL, NULL, 2, NULL, 0, 'Set one penguin truffle in the center of the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6929AFCB349ADA4EB80A95DCE17106DF', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 19, NULL, NULL, 2, NULL, 0, 'Sprinkle on more non-pareils to cover entire surface of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17A076FCDF4E744095E16B6BA7F0B931', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 20, NULL, NULL, 2, NULL, 0, 'Hold penguin in place until it can stand upright on it''s own.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92C34326D0E6AE448BC004D9E8F2016B', X'4EB086883873754FA595AA553D5AD4CF', X'4EB086883873754FA595AA553D5AD4CF', 21, NULL, NULL, 2, NULL, 0, 'Alternatively, decorate penguins using modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3224D1A9451EA4E84797DF6F3766718', X'3922F9FF73B2AC4590A34BF09063D5FE', X'3922F9FF73B2AC4590A34BF09063D5FE', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'734B97C6D3FC414E812E0B3E1BEC8B73', X'3922F9FF73B2AC4590A34BF09063D5FE', X'3922F9FF73B2AC4590A34BF09063D5FE', 1, NULL, NULL, 2, NULL, 0, 'Chop almonds into small pieces and toss with flour and salt. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60AECB8C008F254D8EE034202EB8C92D', X'3922F9FF73B2AC4590A34BF09063D5FE', X'3922F9FF73B2AC4590A34BF09063D5FE', 2, NULL, NULL, 2, NULL, 0, 'Heat butter, corn syrup, and sugar in a small saucepan over medium high heat until butter melts. Stir gently. Let it come to a boil. Stir in floured almonds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3078A533FFDC7C468C5993EA135FA5EF', X'3922F9FF73B2AC4590A34BF09063D5FE', X'3922F9FF73B2AC4590A34BF09063D5FE', 3, NULL, NULL, 2, NULL, 0, 'Pour onto a parchment paper lined baking sheet and spread thin. Set another piece of paper on top and use a rolling pin to roll it out as thin as possible. Remove top piece of paper then place baking pan in oven.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A364DF7E8FAA64B9C0A2A51958094C2', X'3922F9FF73B2AC4590A34BF09063D5FE', X'3922F9FF73B2AC4590A34BF09063D5FE', 4, NULL, NULL, 2, NULL, 0, 'Bake for 10-14 minutes until the edges start to look set and the bubbling starts to subside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53128DCAE77C474E809D469EEF71A996', X'3922F9FF73B2AC4590A34BF09063D5FE', X'3922F9FF73B2AC4590A34BF09063D5FE', 5, NULL, NULL, 2, NULL, 0, 'Butter the cutting edge of a round metal cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'498E91CDB6942C41B527FD08D5F4CF75', X'3922F9FF73B2AC4590A34BF09063D5FE', X'3922F9FF73B2AC4590A34BF09063D5FE', 6, NULL, NULL, 2, NULL, 0, 'Remove from oven, and immediately use a buttered round metal cookie cutter to cut circles in the hot candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA003ABB053DCD498FBB5EF4193714B5', X'3922F9FF73B2AC4590A34BF09063D5FE', X'3922F9FF73B2AC4590A34BF09063D5FE', 7, NULL, NULL, 2, NULL, 0, 'If needed, pop the pan back in the oven for a minute to reheat the nougatine so you can cut 12 circles out.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'990004783D8A2B44B318CEB0E0197B4A', X'3922F9FF73B2AC4590A34BF09063D5FE', X'3922F9FF73B2AC4590A34BF09063D5FE', 8, NULL, NULL, 2, NULL, 0, 'Use a metal spatula or knife to pick up a cut piece of nougatine and place it over a mini tart mold, reheat in oven for a minute or two, then indent the center using a tart tamper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B080D55981308C41860F44DF84244FBF', X'F7E995B2B9D1CE419C8D2AB44BEA80F4', X'F7E995B2B9D1CE419C8D2AB44BEA80F4', 0, NULL, NULL, 2, NULL, 0, 'Heat 1/4 cup heavy whipping cream and white chocolate in microwave on high for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6327E5D58262334CA68D0B480E703CDB', X'F7E995B2B9D1CE419C8D2AB44BEA80F4', X'F7E995B2B9D1CE419C8D2AB44BEA80F4', 1, NULL, NULL, 2, NULL, 0, 'Whisk remaining 1 1/4 cups heavy whipping cream to stiff peaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5424794512D1C247822FC5A317E4F80D', X'F7E995B2B9D1CE419C8D2AB44BEA80F4', X'F7E995B2B9D1CE419C8D2AB44BEA80F4', 2, NULL, NULL, 2, NULL, 0, 'Fold cream into chocolate in 3 increments.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C16FA38B83DD9143B4B60F11664BFC5F', X'F7E995B2B9D1CE419C8D2AB44BEA80F4', X'F7E995B2B9D1CE419C8D2AB44BEA80F4', 3, NULL, NULL, 2, NULL, 0, 'Spoon or pipe into Cocoa Almond Nougatine cups. Top with a maraschino cherry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7446BA7F8562BA4AAEC84A1D504CA5BA', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 0, NULL, NULL, 2, NULL, 0, 'Roll some white and some black (or dark) modeling chocolate out on a non-stick mat using a fondant roller.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14D2D12F1D7DBF41AC62EC20B386A994', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 1, NULL, NULL, 2, NULL, 0, 'For each bear:');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2ABE0AA67B427242B80FEB7D848C9C5B', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 2, NULL, NULL, 2, NULL, 0, 'ears - cut out 2 white circles using the medium round plunger cutter');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE7249E4DCE905429C7DC998BC6CF082', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 3, NULL, NULL, 2, NULL, 0, 'inner Ears - cut 2 black circles using the small round plunger cutter');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C096E51D2ED4E48A4BB20DC2246FB44', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 4, NULL, NULL, 2, NULL, 0, 'nose - cut 2 black circles using the small round cutter and pinch it between your thumb and finger shaping it into a triangle with rounded edges');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF4530F376CDCD47B2F384979251AB43', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 5, NULL, NULL, 2, NULL, 0, 'eyes - pinch off 2 tiny pieces of black and roll into balls');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'906AFB782A3AE841AB4E386F13008AEB', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 6, NULL, NULL, 2, NULL, 0, 'snout - pinch off 1 pea size piece of white and roll into a ball');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98C6B3A29B72004DBE8E17651D851FF3', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 7, NULL, NULL, 2, NULL, 0, 'Pour melted white chocolate into a tall glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'541A63D64171304783307123A1A587EA', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 8, NULL, NULL, 2, NULL, 0, 'Dip pretzel into the chocolate, lift it out, shake it, and allow excess chocolate to drip back into the glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF4FD43A67D9A94EA1A393659478C784', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 9, NULL, NULL, 2, NULL, 0, 'If using pure tempered white chocolate, refrigerate the pretzels for about 4-5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91C65E761DC5AC4F9364E0B1731FA08A', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 10, NULL, NULL, 2, NULL, 0, 'If using confectionery coating (Candy Melts) place in the freezer for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57F9CB09F8EB2340ABBC751E63B56800', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 11, NULL, NULL, 2, NULL, 0, 'Use a bit of melted white chocolate to attach the round ball onto the pretzel, about an inch from the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E178747C72D00D4DBDEB43116A0595FF', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 12, NULL, NULL, 2, NULL, 0, 'Dip a pretzel back into the white chocolate, giving it a second coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBCACD9BBBDADB488D7E0D2E3ED8D2F1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 13, NULL, NULL, 2, NULL, 0, 'Allow the white chocolate to set until it thickens, holding the pretzel for about 10 seconds or so, then press a fork onto the pretzel, lift up, pulling away the chocolate, creating spikes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E23076E1C533454EA8189B021698971D', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 14, NULL, NULL, 2, NULL, 0, 'Continue doing this, adding white chocolate if needed, until the entire pretzel looks furry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE0D0196D281EB4A938FC0832B371F89', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 15, NULL, NULL, 2, NULL, 0, 'Attach the ears, eyes, and nose using white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10829D384B48EA4689D5DAA4D042475B', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', X'37BC2CFF9F3FE9498D2086B9D6AC92C1', 16, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for several weeks. Do not refrigerate or freeze.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72723F109F5E804990DF1C5FE0612060', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C4A02362092C44A883C161F90BB4226', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 1, NULL, NULL, 2, NULL, 0, 'Crack 6 Eggland''s Best eggs into a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F803B1A433909B49A6CD376F5B3A74F9', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 2, NULL, NULL, 2, NULL, 0, 'Beat 3-5 minutes, until doubled in volume, using a whisk, hand held mixer, or a stand mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB3BD211A07B5843B03A54FB82065DCA', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 3, NULL, NULL, 2, NULL, 0, 'Fill the bottom pot of a double boiler with 1 inch of water.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE5A160F771F014484B6DC9E3B38DD86', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 4, NULL, NULL, 2, NULL, 0, 'Add top bowl and place chopped chocolate and butter inside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC875531249C3046A33FDE3DC6699B65', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 5, NULL, NULL, 2, NULL, 0, 'Turn heat onto low, stirring the chocolate often, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B735F2F04DF17A428329595B7FFFA66F', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 6, NULL, NULL, 2, NULL, 0, 'Slowly drizzle melted chocolate into the eggs, stirring to incorporate it all. I poured it in with my mixer on low. If you add too much warm chocolate all at once, you can cook the eggs, so go slowly, at least at first.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E53ACA5D30F6F24FAB5D01345065F285', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 7, NULL, NULL, 2, NULL, 0, 'Bring a pot of water to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4B42691F4042D4B9AA0C68BBDFD1DE0', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 8, NULL, NULL, 2, NULL, 0, 'Set 18 small mason jars or ramekins on a rimmed baking sheet or in a roasting pan. Equally divide the flourless chocolate cake batter among the jars.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2EF7195D439E0748A7123F6B8575351D', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 9, NULL, NULL, 2, NULL, 0, 'Tap down the cake batter, and wipe the edges of the jar clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A78EDFFD4898E47AE1BD098AEC64790', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 10, NULL, NULL, 2, NULL, 0, 'Place baking sheet in oven and carefully pour the boiling water into the pan, allowing the water to go half way up around the cake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBD57AAE1A1D5543A074342E8FE29487', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 11, NULL, NULL, 2, NULL, 0, 'Bake for 18-22 minutes until an instant read thermometer inserted into one of the cakes registers 140 degrees Fahrenheit. If you don''t have a thermometer just bake until the cakes no longer look really wet. Do not overbake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D92A3F511696C5449F1228729C2B7C52', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 12, NULL, NULL, 2, NULL, 0, 'After the cakes are completely cooled, make the white chocolate mousse.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF5D3BCFEA47BE4FB919D9E2A97EB04A', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 13, NULL, NULL, 2, NULL, 0, 'Pour a tablespoon of water into a small bowl and sprinkle on the 1 teaspoon of gelatin.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FF634410FDC1540A8C020E38228606C', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 14, NULL, NULL, 2, NULL, 0, 'Allow it to bloom for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97EDAF0E6DB577448285FFAD67B19C80', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 15, NULL, NULL, 2, NULL, 0, 'Meanwhile, clean out your double boiler bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D646213F2F6214AA0F42E6678539726', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 16, NULL, NULL, 2, NULL, 0, 'Pour 3/4 cup heavy whipping cream into bowl and set over pot filled with 1 inch of water.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B606B35E90BA645946D3A6A85E485DA', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 17, NULL, NULL, 2, NULL, 0, 'Heat on low until warm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53EAFBD9E5D8FC40B198A2CEC8342D67', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 18, NULL, NULL, 2, NULL, 0, 'Add gelatin, and stir until dissolved.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF5B4B1C64EE7D4CA92A3D3B4999F022', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 19, NULL, NULL, 2, NULL, 0, 'Add white chocolate and stir. When half of the chocolate is melted, remove from the heat. Let it sit for about 2 minutes, then stir until melted. If it doesn''t all melt, heat for a minute or so longer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CCC4EE5E0BD9B43B75D50C0FB7B0A9B', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 20, NULL, NULL, 2, NULL, 0, 'Pour melted white chocolate into a large mixing bowl, preferably one with a spout. Allow it to cool for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9C98487D53D6846AC6FF7620298C11A', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 21, NULL, NULL, 2, NULL, 0, 'Meanwhile, whisk the remaining 1 1/4 cups of heavy whipping cream to soft peaks. Fold 1/3 of the whipped cream at a time into the melted white chocolate until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0455CAA1D354341B9C21E377A62A684', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 22, NULL, NULL, 2, NULL, 0, 'Crush the candy canes into small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E02EAE21D0F17D44B65637AA926C1D90', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 23, NULL, NULL, 2, NULL, 0, 'Pour white chocolate mousse over the cooled chocolate cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BF8AE2D1010DB43A469410BB8893BF5', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 24, NULL, NULL, 2, NULL, 0, 'Sprinkle on crushed candy canes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DF04356372EE047A3520A321E9437C9', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 25, NULL, NULL, 2, NULL, 0, 'Refrigerate for at least 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D650012069FFB24EA501B0E5EF30658E', X'37274649C8E7544785F2D48AD2BDD359', X'37274649C8E7544785F2D48AD2BDD359', 26, NULL, NULL, 2, NULL, 0, 'Allow to sit at room temperature for about 20 minutes before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A89639254F50C4DA8355A60A22C5947', X'7D05FEF3052CA74DA23CF7FC82832B41', X'7D05FEF3052CA74DA23CF7FC82832B41', 0, NULL, NULL, 2, NULL, 0, 'Place blueberries, brown sugar, and Balsamic vinegar in saucepan. Heat on medium-low for 10 minutes, stirring often.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B770697057EE034DBAED62874DBA0A61', X'7D05FEF3052CA74DA23CF7FC82832B41', X'7D05FEF3052CA74DA23CF7FC82832B41', 1, NULL, NULL, 2, NULL, 0, 'Remove from heat and pour into a food processor or blender. Puree. Pour through a fine-mesh sieve, twice, removing all the solids. Clean the saucepan, then pour in 3/4 cup of the puree.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F08F9718BE7B1448C1155EB7CBE40B5', X'7D05FEF3052CA74DA23CF7FC82832B41', X'7D05FEF3052CA74DA23CF7FC82832B41', 2, NULL, NULL, 2, NULL, 0, 'Add pectin and baking soda to the blueberry puree. It will bubble up. Bring to a boil over high heat. Turn off heat, stir in 1/2 teaspoon citric acid and set it aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A202DEB45E43A4DA111B2A984E4A3BB', X'7D05FEF3052CA74DA23CF7FC82832B41', X'7D05FEF3052CA74DA23CF7FC82832B41', 3, NULL, NULL, 2, NULL, 0, 'Grease a 1 1/2 to 2-quart stainless steel pot with butter. Pour corn syrup into the bottom. Sprinkle sugar in the center of the pan. Turn heat on medium-high and allow it to cook for one minute. Then stir gently to dissolve sugar. Be careful not to splash the sugar crystals onto the side of your saucepan. Bring to a boil, stirring constantly. This took me just a few minutes, but it can take up to 10 minutes. Clip your candy thermometer onto the side of your pot. Cook, without stirring, until the thermometer registers 260 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06DD908E4A3742418CBD49615F6EF822', X'7D05FEF3052CA74DA23CF7FC82832B41', X'7D05FEF3052CA74DA23CF7FC82832B41', 4, NULL, NULL, 2, NULL, 0, 'You will continue to cook your sugar (ultimately you''ll want it to reach 280 degrees which is the soft crack stage,) but at this point, you need to set your pan of blueberry mixture back on the stove and heat it over high heat until it comes to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'085342AAC1FE4448A6D2135CDDA75EBE', X'7D05FEF3052CA74DA23CF7FC82832B41', X'7D05FEF3052CA74DA23CF7FC82832B41', 5, NULL, NULL, 2, NULL, 0, 'Once your pot of boiling corn syrup/sugar reaches 280 degrees remove it from the heat or turn off your gas. Slowly and VERY CAREFULLY drizzle in the hot pectin, stirring slowly yet constantly. You DO NOT want to splash any of this hot boiling sugar on your hands. The mixture will really bubble up. Once all the pectin has been poured into the saucepan, return it to medium-high heat and cook it for one more minute, then remove from heat and pour into a glass bowl, with a spout, preferably. This stops it from continuing to cook.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFB5F3F3B597E744B5032D5583E2C8F8', X'7D05FEF3052CA74DA23CF7FC82832B41', X'7D05FEF3052CA74DA23CF7FC82832B41', 6, NULL, NULL, 2, NULL, 0, 'Carefully pour the hot mixture into silicone molds. Allow the candy to cool at room temperature for up to 8 hours. Press the backside of the silicone mold and pop the candy out.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E13C7BA918FB04EB7B4B711C267ACC0', X'7D05FEF3052CA74DA23CF7FC82832B41', X'7D05FEF3052CA74DA23CF7FC82832B41', 7, NULL, NULL, 2, NULL, 0, 'Pour colored sanding sugar into a dish or bowl. Stir in 3/4 teaspoon of citric acid. Taste the sugar or even coat one of the candies. If you''d like it more tart, add more citric acid.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A758D69285519409B97F54DE5C87890', X'7D05FEF3052CA74DA23CF7FC82832B41', X'7D05FEF3052CA74DA23CF7FC82832B41', 8, NULL, NULL, 2, NULL, 0, 'Drop your gum drops into the sugar and toss to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A98BBE3E4C93544DAF3936DAF47F9245', X'7D05FEF3052CA74DA23CF7FC82832B41', X'7D05FEF3052CA74DA23CF7FC82832B41', 9, NULL, NULL, 2, NULL, 0, 'Allow the candies to dry for an hour before packaging or stacking in a candy dish. Store in an airtight container for about a week.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'364B607B4F329A40935323E5BDB9E0A6', X'2667464C6304CE428D23B00B99E3A911', X'2667464C6304CE428D23B00B99E3A911', 0, NULL, NULL, 2, NULL, 0, 'Place white confectionery coating wafers and peanut butter chips in a microwave safe mixing bowl. Heat on high power for 45 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'83FA488318A7FA409472CFD8A9A7E4B6', X'2667464C6304CE428D23B00B99E3A911', X'2667464C6304CE428D23B00B99E3A911', 1, NULL, NULL, 2, NULL, 0, 'Remove and stir the chips around or they will burn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41E82EE6E16B6344ADC2F83B89FF3BF0', X'2667464C6304CE428D23B00B99E3A911', X'2667464C6304CE428D23B00B99E3A911', 2, NULL, NULL, 2, NULL, 0, 'Heat for 30 more seconds, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9410ADB6271D2942A0A6AD832741B938', X'2667464C6304CE428D23B00B99E3A911', X'2667464C6304CE428D23B00B99E3A911', 3, NULL, NULL, 2, NULL, 0, 'Heat for 25 seconds, then let it sit in the microwave for 2-3 minutes, then stir vigorously.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72024BA24346D14AA4379A035D5137E4', X'2667464C6304CE428D23B00B99E3A911', X'2667464C6304CE428D23B00B99E3A911', 4, NULL, NULL, 2, NULL, 0, 'If all of the chips are not melted, heat at ten second intervals, stirring in between each, until completely melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'663ACCF3E252D64099F4DD26FEC5F76D', X'2667464C6304CE428D23B00B99E3A911', X'2667464C6304CE428D23B00B99E3A911', 5, NULL, NULL, 2, NULL, 0, 'Stir in peanut butter and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8464E8EF4E726E4ABA685BE54E04C067', X'2667464C6304CE428D23B00B99E3A911', X'2667464C6304CE428D23B00B99E3A911', 6, NULL, NULL, 2, NULL, 0, 'NOTES: Make sure to mix really well, scraping the bottom of the bowl. If you don''t stir the peanut butter into the candy, you''ll end up with clumps of hardened candy coating in your filling.Allow it to cool a bit before piping it into your chocolate shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE72EA10B1673040B9291321077C6CC5', X'8602101B6DB68B4183FAA5E013CAE5C9', X'8602101B6DB68B4183FAA5E013CAE5C9', 0, NULL, NULL, 2, NULL, 0, 'Cut a thin slice from each round end of your marshmallows.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3ADEA1DE3E1AA418DA11924A5085EED', X'8602101B6DB68B4183FAA5E013CAE5C9', X'8602101B6DB68B4183FAA5E013CAE5C9', 1, NULL, NULL, 2, NULL, 0, 'Unwrap Tootsie Rolls and smash them flat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD1FD4EEA15A57438126B2403333F9F4', X'8602101B6DB68B4183FAA5E013CAE5C9', X'8602101B6DB68B4183FAA5E013CAE5C9', 2, NULL, NULL, 2, NULL, 0, 'Use kitchen shears or a knife to cut wings from the Tootsie Rolls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D25076EBC46EFF46BDA20F7D93E65FED', X'8602101B6DB68B4183FAA5E013CAE5C9', X'8602101B6DB68B4183FAA5E013CAE5C9', 3, NULL, NULL, 2, NULL, 0, 'Cut off small pieces of your Circus Peanut or other orange candy, and shape into triangular beaks, and tear drop shaped feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE2E71DCFA5EDD499C82F699806A14CD', X'8602101B6DB68B4183FAA5E013CAE5C9', X'8602101B6DB68B4183FAA5E013CAE5C9', 4, NULL, NULL, 2, NULL, 0, 'Dip one Nutter Butter Cookie in the melted and tempered dark chocolate or melted confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38495A484E4F7A4192A5BAEC2F30FD16', X'8602101B6DB68B4183FAA5E013CAE5C9', X'8602101B6DB68B4183FAA5E013CAE5C9', 5, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FCB2CD227625342B447E79BA7E4F535', X'8602101B6DB68B4183FAA5E013CAE5C9', X'8602101B6DB68B4183FAA5E013CAE5C9', 6, NULL, NULL, 2, NULL, 0, 'Immediately attach all your candy decorations while the chocolate is still wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5904E3FB852109439594B9073BDF6FE2', X'8602101B6DB68B4183FAA5E013CAE5C9', X'8602101B6DB68B4183FAA5E013CAE5C9', 7, NULL, NULL, 2, NULL, 0, 'Attach two Tootsie Roll wings, one candy beak, two candy feet, one marshmallow belly, and two candy eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99B25286B1D5554A994072898A5019E9', X'8602101B6DB68B4183FAA5E013CAE5C9', X'8602101B6DB68B4183FAA5E013CAE5C9', 8, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator if using pure chocolate and in the freezer if using candy melts for about 5 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'987B6133FDE2E845A12AABD20FFA3ED2', X'448586B0D86AA54ABAC2684B7BA019B6', X'448586B0D86AA54ABAC2684B7BA019B6', 0, NULL, NULL, 2, NULL, 0, 'Unwrap your Reese''s Cup Miniatures, removing the foil and the paper cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FADE978EED80B149B58A6241E3D897D1', X'448586B0D86AA54ABAC2684B7BA019B6', X'448586B0D86AA54ABAC2684B7BA019B6', 1, NULL, NULL, 2, NULL, 0, 'Set one Reese''s Cup, wide end down on your table.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE9EBC102418474EAEBDA5CE483432E2', X'448586B0D86AA54ABAC2684B7BA019B6', X'448586B0D86AA54ABAC2684B7BA019B6', 2, NULL, NULL, 2, NULL, 0, 'Put a small dab of edible glue (frosting or chocolate) on top of that Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F9AC060988A4146BFEF2CDB750A804E', X'448586B0D86AA54ABAC2684B7BA019B6', X'448586B0D86AA54ABAC2684B7BA019B6', 3, NULL, NULL, 2, NULL, 0, 'Set another Reese''s Cup on its side on top of the edible glue.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C26FA4D0D514F444857E7F992772AED1', X'448586B0D86AA54ABAC2684B7BA019B6', X'448586B0D86AA54ABAC2684B7BA019B6', 4, NULL, NULL, 2, NULL, 0, 'Cut thin strips from the green Fruit Roll-Ups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EAAF3A138293E40A40239B6578A4839', X'448586B0D86AA54ABAC2684B7BA019B6', X'448586B0D86AA54ABAC2684B7BA019B6', 5, NULL, NULL, 2, NULL, 0, 'Wrap one strip around the middle of the Reese''s Cup stack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CCC85E55109E945A72BAD26C02D95AE', X'448586B0D86AA54ABAC2684B7BA019B6', X'448586B0D86AA54ABAC2684B7BA019B6', 6, NULL, NULL, 2, NULL, 0, 'Use a small dab of edible glue to attach two eyes to the top of the Reese''s Cup stack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EDEDC65E85BD44BAEF6EBB54888F21E', X'448586B0D86AA54ABAC2684B7BA019B6', X'448586B0D86AA54ABAC2684B7BA019B6', 7, NULL, NULL, 2, NULL, 0, 'Unwrap Tootsie Rolls. Flatten them. You can use your fingers to flatten them, but a rolling pin works well too. If needed, you can heat them up in the microwave for a few seconds, just to soften them.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'597EC84E3D1AFF4B89D3ED4970450119', X'448586B0D86AA54ABAC2684B7BA019B6', X'448586B0D86AA54ABAC2684B7BA019B6', 8, NULL, NULL, 2, NULL, 0, 'Cut two antlers using kitchen shears, a knife, or a leaf shaped cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D0C718E1319F94782689939A94F14C1', X'448586B0D86AA54ABAC2684B7BA019B6', X'448586B0D86AA54ABAC2684B7BA019B6', 9, NULL, NULL, 2, NULL, 0, 'Trim if using a leaf cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'157ACB5CDAF9374E949C069D089272E3', X'448586B0D86AA54ABAC2684B7BA019B6', X'448586B0D86AA54ABAC2684B7BA019B6', 10, NULL, NULL, 2, NULL, 0, 'Cut a circle out of a Tootsie Roll using kitchen shears or a round cutter, making it the same size as the narrow end of a Reese''s Cup (7/8 inch.)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F2F17C7F3027B41A93925D1453D90AD', X'448586B0D86AA54ABAC2684B7BA019B6', X'448586B0D86AA54ABAC2684B7BA019B6', 11, NULL, NULL, 2, NULL, 0, 'Use edible glue (frosting or chocolate) to attach the round piece of Tootsie Roll to the narrow end of the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03A7777565B89E40BCDB83A3F2467628', X'448586B0D86AA54ABAC2684B7BA019B6', X'448586B0D86AA54ABAC2684B7BA019B6', 12, NULL, NULL, 2, NULL, 0, 'Attach the antlers to the back of the Reese''s Cup and add a red M&M nose on top of the Tootsie Roll round.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53D5A5A5C885CF4696655D67ABB1D929', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 0, NULL, NULL, 2, NULL, 0, 'Pour heavy whipping cream, molasses, cinnamon, ginger, cloves, and nutmeg into a small saucepan. Heat over medium heat, stirring often, until it just begins to boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22134D0C31C273498EE70A5A5C14DAB2', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 1, NULL, NULL, 2, NULL, 0, 'Remove it from the heat and allow it to sit for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7BB2C5A07921D43AB728E7ED963A7CC', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 2, NULL, NULL, 2, NULL, 0, 'Once again heat it over medium heat just until bubbles form around the edge of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'035EDE6EF0529D42A9EA30F178BAD760', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 3, NULL, NULL, 2, NULL, 0, 'Remove it from the heat and add the finely chopped milk chocolate and vanilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CDD68B661646A4A922490D2392EA4A5', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 4, NULL, NULL, 2, NULL, 0, 'Stir it just until the chocolate is completely submerged in the cream, then let it sit for 2-3 minutes. Stir until smooth and creamy. If you still have any chunks of chocolate remaining, you can gently heat it over low heat until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A0D6002992D044EBCD503FBE46D5056', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 5, NULL, NULL, 2, NULL, 0, 'NOTE: Do not over heat this ganache (the chocolate mixture) or the cocoa butter will rise to the surface and your truffles will be oily. If this happens, you can stir in a tablespoon of cold cream which will help bring the mixture back together, just be aware that your truffles will be softer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92FFA53117B9954289BAB637ED5D8C4C', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 6, NULL, NULL, 2, NULL, 0, 'Fill a disposable pastry bag or a zip top bag with the gingerbread ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C76B2D9987C4B419BF92BDC60615219', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 7, NULL, NULL, 2, NULL, 0, 'Pipe it into the gingerbread mold filling the cavities about 3/4 of the way full.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CE5F5D4A7455F46BEEADFC431E77016', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 8, NULL, NULL, 2, NULL, 0, 'Refrigerate for one hour, then freeze for another hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CEEF64CA56A524EAA4CED0AE6DCC62C', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 9, NULL, NULL, 2, NULL, 0, 'Un-mold the gingerbread men truffles. If they are sticking at all, freeze for a while longer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D948BF4329D604F84A5CDDAB44A22A9', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 10, NULL, NULL, 2, NULL, 0, 'Allow truffles to come to room temperature before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4116DB91899D414E8C00EE3AD941B583', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 11, NULL, NULL, 2, NULL, 0, 'If desired, decorate gingerbread men with frosting, melted chocolate, and candies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D412EB3D6C89F6448CE79322B5546AFD', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 12, NULL, NULL, 2, NULL, 0, 'Keep them stored in an airtight container.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41D291219930744F86E0C06566A1F441', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 13, NULL, NULL, 2, NULL, 0, 'If kept in a cool dry place they will keep up to two weeks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B90475990769C428176BD462667F387', X'ADEC790A4D005A449FB1E5FB58EBAA59', X'ADEC790A4D005A449FB1E5FB58EBAA59', 14, NULL, NULL, 2, NULL, 0, 'If you keep them refrigerated they will keep for about three weeks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1FC6D927442D2489AFC1A4D9F75A77F', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 0, NULL, NULL, 2, NULL, 0, 'Fill a small plastic cup with melted red candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA130DEF67FAE34B9B78F219510A8023', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 1, NULL, NULL, 2, NULL, 0, 'Let the candy filled cup sit at room temperature for about 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C915A185EA370845A397CBB80166C122', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 2, NULL, NULL, 2, NULL, 0, 'Pour wet candy out of the cup, leaving just the outer shell of candy melts in the cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C9D34C32E36D44C94A2B9B109FE32AF', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 3, NULL, NULL, 2, NULL, 0, 'Set cup, upside down, on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92A3F77079660C418640014C83740D5B', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 4, NULL, NULL, 2, NULL, 0, 'Freeze for 4-5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA1AC8579E8CBB4FBE80C9270B8C3BA4', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 5, NULL, NULL, 2, NULL, 0, 'Remove and pull candy cup out of plastic cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33E3882401F6F64DAD71E09BAC77599C', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 6, NULL, NULL, 2, NULL, 0, 'Allow white candy melts to cool slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B74AD13A5B7E6409A2D85E80E59F777', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 7, NULL, NULL, 2, NULL, 0, 'Brush white candy melts over the top edge of the candy cup so that it looks like fur.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69254B74C587AE4B9D4630FD7EAB98B6', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 8, NULL, NULL, 2, NULL, 0, 'Roll out black candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAA44A5488D3C84BADE7E39BEC5D7411', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 9, NULL, NULL, 2, NULL, 0, 'Cut into 3/8 inch by 6 inch long belts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'296A4077E469574884AB942C173D69EF', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 10, NULL, NULL, 2, NULL, 0, 'Roll out white candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5C066B11DE8D74398D53450D2D1426C', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 11, NULL, NULL, 2, NULL, 0, 'Cut out small square buckles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A62D48AA80221F45AE406F4504D177E3', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 12, NULL, NULL, 2, NULL, 0, 'Lightly brush water on the back of the candy clay buckle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEB1CA62E9D91A45B3C9B73E4770ADAC', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 13, NULL, NULL, 2, NULL, 0, 'Press the candy buckle onto a black candy clay belt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'923CD1156193484E9EAE6E75AE89636A', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 14, NULL, NULL, 2, NULL, 0, 'Brush candy melts onto the back of the candy belt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC263E8F4FB686409817F34A11E46339', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 15, NULL, NULL, 2, NULL, 0, 'Immediately wrap the candy belt around a candy cup and cut off the excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D983B4C5C9D8A46AF001CA0DF44B335', X'E85FFD35039370489DAFE26999FC6F36', X'E85FFD35039370489DAFE26999FC6F36', 16, NULL, NULL, 2, NULL, 0, 'Fill cups with candy, trail mix, dried fruit, mini cookies, chocolate mousse, pudding, or ice cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC527C7AA560584AA32E4554D0CF7BEF', X'E273A463D5019D4C9B6986A4D16E7954', X'E273A463D5019D4C9B6986A4D16E7954', 0, NULL, NULL, 2, NULL, 0, 'Unwrap your candy bars and caramels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D77DD5014D05BE4C8C9CA2E189BDEC05', X'E273A463D5019D4C9B6986A4D16E7954', X'E273A463D5019D4C9B6986A4D16E7954', 1, NULL, NULL, 2, NULL, 0, 'Flatten the caramels, by pressing on them with your fingers. If necessary, heat them in the microwave for a few seconds to soften them.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08F5C51763596449A56CFCBC183EFC33', X'E273A463D5019D4C9B6986A4D16E7954', X'E273A463D5019D4C9B6986A4D16E7954', 2, NULL, NULL, 2, NULL, 0, 'Use kitchen shears to cut an oval that is about 2/3rds of the width of your candy bars.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54A1F4D5E0106B49AB643865B906910E', X'E273A463D5019D4C9B6986A4D16E7954', X'E273A463D5019D4C9B6986A4D16E7954', 3, NULL, NULL, 2, NULL, 0, 'Press the oval near the bottom edge of your candy bar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77E947E7120E404A9626BBBF65D65B62', X'E273A463D5019D4C9B6986A4D16E7954', X'E273A463D5019D4C9B6986A4D16E7954', 4, NULL, NULL, 2, NULL, 0, 'Press one red Peanut M&M into the caramel oval.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A05146650C9F5545ACF0A909B2ED5A52', X'E273A463D5019D4C9B6986A4D16E7954', X'E273A463D5019D4C9B6986A4D16E7954', 5, NULL, NULL, 2, NULL, 0, 'Press one tiny piece of caramel onto the back of each candy eye then press the eyes onto the candy bars just above the caramel oval.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A91370FB8D7F84A8E2EC97A406FF54F', X'E273A463D5019D4C9B6986A4D16E7954', X'E273A463D5019D4C9B6986A4D16E7954', 6, NULL, NULL, 2, NULL, 0, 'Use a leave cookie cutter or kitchen shears to cut antlers. I cut out a leaf then cut it in half and it worked perfectly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5859F2F3C583CB4191DA07BEE0854CF5', X'E273A463D5019D4C9B6986A4D16E7954', X'E273A463D5019D4C9B6986A4D16E7954', 7, NULL, NULL, 2, NULL, 0, 'Press the caramel antlers onto the top half of the candy bar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8ECC8685114B034CB941F580CC07A1D5', X'E273A463D5019D4C9B6986A4D16E7954', X'E273A463D5019D4C9B6986A4D16E7954', 8, NULL, NULL, 2, NULL, 0, 'Serve them on a platter at your party, or wrap them in clear cellophane bags to give as Christmas treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'427EC6034CF6354586C76F387A1DD778', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 0, NULL, NULL, 2, NULL, 0, 'Heat one sugar cone at a time in the microwave for 10-20 seconds. Start at 10 seconds then you can always heat them for more time if needed. I used a 1200 watt microwave for 14 seconds and it worked great.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'19DCF418DD883344A7CA29F4C787BC2A', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 1, NULL, NULL, 2, NULL, 0, 'Remove the hot sugar cone and VERY CAREFULLY, bend the tip so that the ice cream cone looks like a flopped over Santa hat. Be sure the seam is to the back side when you bend your sugar cone or it may come apart at the seam.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'479D02D75973F447B6CE68FD8AAEC842', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 2, NULL, NULL, 2, NULL, 0, 'Pour melted red confectionery coating/candy melts into a tall glass or measuring cup, making sure your bent sugar cones can fit in the opening.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D68C5CC9883D3498E6D1B2C4893667F', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 3, NULL, NULL, 2, NULL, 0, 'Hold onto the open end of your sugar cone and dip it into the red coating, leaving about 1/2 inch around the base un-coated.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D34E790D97AB94EA5489E7BD44E6C20', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 4, NULL, NULL, 2, NULL, 0, 'Once you dip a few, put them in the freezer for 3-5 minutes, just until they set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BFC558FE901604D8670C5EA625D9FD0', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 5, NULL, NULL, 2, NULL, 0, 'TIP: If you dip a lot of Sugar Cone Santa Hats and leave them at room temperature, the candy coating on the first ones that you dipped will begin to harden and you will end up with white spots or streaks on those hats. Dip a few then place them in the freezer!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59D217CA93B63446BA09945D8FD2E190', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 6, NULL, NULL, 2, NULL, 0, 'TIP: Your dipped sugar cones will crack if left in the freezer for too long. Remove after 3-5 minutes - set your timer if necessary!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E66FD04CACBF74EB37EB022764571B2', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 7, NULL, NULL, 2, NULL, 0, 'Allow your melted white candy coating to cool and thicken slightly before proceeding, or you''ll melt too much of the red candy coating as you add the white to the hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'930664E5C47DE2499AE6D33E25E0EF50', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 8, NULL, NULL, 2, NULL, 0, 'Use a small spatula, spoon, or knife, to spread melted white candy coating around the bottom edge of each hat making it look fuzzy by pressing the spatula against the candy coating and pulling the spatula away quickly. Freeze for 1-2 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50EF56258901384EBD80110ECAC5FEE1', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 9, NULL, NULL, 2, NULL, 0, 'Spread some melted white candy coating all around the tip of each sugar cone Santa hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BC61EA87DB6084199A596FB05E25A99', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 10, NULL, NULL, 2, NULL, 0, 'Allow it to dry a minute or two, then put on another layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11DC01FAF96FA34285D8EA9A381E6D34', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 11, NULL, NULL, 2, NULL, 0, 'Put on as many layers as you''d like, then make it look furry by pressing the spatula against the candy coating and pulling the spatula away quickly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D1C9C0426A2E24493EEC8530FF361A1', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 12, NULL, NULL, 2, NULL, 0, 'Freeze for 1-2 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82DCA4B8EC7EF044B0135C5E82764DB5', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 13, NULL, NULL, 2, NULL, 0, 'Store your Sugar Cone Santa Hats in an airtight container for up to a few weeks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE6404F0F9278B4EAACFC9ADF06F5DE0', X'DDCA6A839AE0A044B6B8D244AA6017E2', X'DDCA6A839AE0A044B6B8D244AA6017E2', 14, NULL, NULL, 2, NULL, 0, 'Serve as is, or on top of a scoop of ice cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B350CA028115DF4C88B650EDB78294EB', X'B5834014DA2A744AB68FB4BED6236CEF', X'B5834014DA2A744AB68FB4BED6236CEF', 0, NULL, NULL, 2, NULL, 0, 'Cut the green Mike & Ike candies in half. I used a lighter to heat up my knife so I got really clean cuts in the Mike and Ikes. Just carefully hold the knife blade over the flame for a few seconds to heat it up then cut through the candies. If making these with kids, skip the flame and be safe!');
