INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E20767B390A0BA4CAC9E8451714B48EA', X'9FE6D960BD618449823A8721386A6E6C', X'9FE6D960BD618449823A8721386A6E6C', 1, NULL, NULL, 2, NULL, 0, 'Cut two feet and a beak.');
