INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1AD85D0E3FE7A4182FC2AB23A28FD0D', X'B30AF31A504A854ABB47B7DC97045758', X'B30AF31A504A854ABB47B7DC97045758', 0, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of frosting on the cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A182AE62BF538F4A83F43EAF8AF2D6C1', X'B30AF31A504A854ABB47B7DC97045758', X'B30AF31A504A854ABB47B7DC97045758', 1, NULL, NULL, 2, NULL, 0, 'Print and cut out cupcake wrapper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5AB219433042DE43897CA40D3D34C6E1', X'B30AF31A504A854ABB47B7DC97045758', X'B30AF31A504A854ABB47B7DC97045758', 2, NULL, NULL, 2, NULL, 0, 'Wrap it around the cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F27B045E4259A4192739C0400534B41', X'B30AF31A504A854ABB47B7DC97045758', X'B30AF31A504A854ABB47B7DC97045758', 3, NULL, NULL, 2, NULL, 0, 'Add modeling chocolate, frosting, or candy snowballs on top. (see tutorial for details)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F5B370B6D94EB468CF1F1169755E4C0', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', 0, NULL, NULL, 2, NULL, 0, 'Heat oil to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4BDE7FB3B790D43B848152DDA47A73E', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', 1, NULL, NULL, 2, NULL, 0, 'Un-roll crescent dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F234B34276F83C428CB9EAEDCDB40A85', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', 2, NULL, NULL, 2, NULL, 0, 'Use snowflake cookie cutters to cut out snowflake doughnuts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C74E63B6836E6E49B99FB6FCCE8D7951', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', 3, NULL, NULL, 2, NULL, 0, 'Fry, flipping as needed, until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87FB0EF1E0CB7A46979320C0377DD1E5', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', 4, NULL, NULL, 2, NULL, 0, 'Remove and allow oil to drain onto a paper towel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1178FD6425B9C47B006A337850E8E51', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle immediately with powdered sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F79FD773C2F91E4ABA03003D0B89B251', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', X'9CE7BEB9D6BED04FAFF6EC068147B9F0', 6, NULL, NULL, 2, NULL, 0, 'Let cool slightly before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9B39F696C0DDA459C196AD8A6558A5F', X'478B255AD83CA142A80CFB0B306C6ECA', X'478B255AD83CA142A80CFB0B306C6ECA', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD685B8B6BF0714694E3E2EF52F17B22', X'478B255AD83CA142A80CFB0B306C6ECA', X'478B255AD83CA142A80CFB0B306C6ECA', 1, NULL, NULL, 2, NULL, 0, 'Spread 1/3 cup pizza sauce in the middle of the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70CB783F185D654991D51D9E085A4D16', X'478B255AD83CA142A80CFB0B306C6ECA', X'478B255AD83CA142A80CFB0B306C6ECA', 2, NULL, NULL, 2, NULL, 0, 'Blend together 8 ounces cream cheese, 1/2 cup shredded Mozzarella Cheese, 1/2 cup shredded Parmesan cheese, 1/2 cup sour cream, and a pinch of salt, then spread half of it over the pizza sauce in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEFDD414FA77564EB3C47B4A9AD06730', X'478B255AD83CA142A80CFB0B306C6ECA', X'478B255AD83CA142A80CFB0B306C6ECA', 3, NULL, NULL, 2, NULL, 0, 'Top with 3/4 cup shredded Mozzarella cheese, then sprinkle on half of the pepperoni or your favorite pizza toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6569E62D280FD04A83CB11193C27D18F', X'478B255AD83CA142A80CFB0B306C6ECA', X'478B255AD83CA142A80CFB0B306C6ECA', 4, NULL, NULL, 2, NULL, 0, 'Layer on another 1/3 cup pizza sauce, the remaining cheese dip, 1/3 cup pizza sauce, another 3/4 cup Mozzarella cheese, and more pepperoni or pizza toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4A6E142E789E34D8C81369B999D75C6', X'478B255AD83CA142A80CFB0B306C6ECA', X'478B255AD83CA142A80CFB0B306C6ECA', 5, NULL, NULL, 2, NULL, 0, 'Bake the dip for 28-30 minutes until the cheese is melted and the pizza crust is golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B7E3C2A2ECAB84484558E20840092AF', X'478B255AD83CA142A80CFB0B306C6ECA', X'478B255AD83CA142A80CFB0B306C6ECA', 6, NULL, NULL, 2, NULL, 0, 'Serve while hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A7CF840C3FD084AAD6B8A058D5EA3AA', X'E5F610B79D514F498F3D8B93846325D6', X'E5F610B79D514F498F3D8B93846325D6', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16F0049CF2F51047A1AAF28AB7CAF2D5', X'E5F610B79D514F498F3D8B93846325D6', X'E5F610B79D514F498F3D8B93846325D6', 1, NULL, NULL, 2, NULL, 0, 'Heat a large skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'726757F63186014F9F2CECDA2ACBA647', X'E5F610B79D514F498F3D8B93846325D6', X'E5F610B79D514F498F3D8B93846325D6', 2, NULL, NULL, 2, NULL, 0, 'Add ground beef and cook until browned; drain.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DC3CD955A8C5548B868AFE7F1D99DB6', X'E5F610B79D514F498F3D8B93846325D6', X'E5F610B79D514F498F3D8B93846325D6', 3, NULL, NULL, 2, NULL, 0, 'Add taco seasoning and water and bring to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5A04FB3A490594DB716696A311E4063', X'E5F610B79D514F498F3D8B93846325D6', X'E5F610B79D514F498F3D8B93846325D6', 4, NULL, NULL, 2, NULL, 0, 'Lower heat and simmer until most of the liquid has evaporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6541544563AED84CAA3B0D7898B0967A', X'E5F610B79D514F498F3D8B93846325D6', X'E5F610B79D514F498F3D8B93846325D6', 5, NULL, NULL, 2, NULL, 0, 'Spread refried beans into bottom of a round or rectangle baking dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68D787BF555D2B47ADCB3D6B99F64B34', X'E5F610B79D514F498F3D8B93846325D6', X'E5F610B79D514F498F3D8B93846325D6', 6, NULL, NULL, 2, NULL, 0, 'Spread the taco meat over the layer of refried beans.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71AE91686A0F094A974C2984C5BBDEAB', X'E5F610B79D514F498F3D8B93846325D6', X'E5F610B79D514F498F3D8B93846325D6', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle on diced tomatoes, diced jalapenos, and chopped olives.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'201659D2961F204DB94636AF9211235F', X'E5F610B79D514F498F3D8B93846325D6', X'E5F610B79D514F498F3D8B93846325D6', 8, NULL, NULL, 2, NULL, 0, 'Top with shredded cheeses.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FACACC2D88C114CA1E5AB5D1F3D4F33', X'E5F610B79D514F498F3D8B93846325D6', X'E5F610B79D514F498F3D8B93846325D6', 9, NULL, NULL, 2, NULL, 0, 'Arrange black olive slices in the appropriate date (2015) over top of the cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F47727DAFAD21547976DE8B397AD5F06', X'E5F610B79D514F498F3D8B93846325D6', X'E5F610B79D514F498F3D8B93846325D6', 10, NULL, NULL, 2, NULL, 0, 'Bake for 25 minutes or until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D01EAE97BD32F4BA0F1EFF307CA4923', X'E5F610B79D514F498F3D8B93846325D6', X'E5F610B79D514F498F3D8B93846325D6', 11, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E375E87AC9376A4089EB8AA408812875', X'D84DA9613204284C948344A534495BB5', X'D84DA9613204284C948344A534495BB5', 0, NULL, NULL, 2, NULL, 0, 'Pour water into the bottom of a saucepan set over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1162D4CDC114394E8886B95FA8DD1801', X'D84DA9613204284C948344A534495BB5', X'D84DA9613204284C948344A534495BB5', 1, NULL, NULL, 2, NULL, 0, 'Add sugar and allow to come to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EDC6F2470FCCC4CAEFBAE37316EF13F', X'D84DA9613204284C948344A534495BB5', X'D84DA9613204284C948344A534495BB5', 2, NULL, NULL, 2, NULL, 0, 'Boil until all the sugar is dissolved, stirring if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A241FED97081A940AEBF7334801350D1', X'D84DA9613204284C948344A534495BB5', X'D84DA9613204284C948344A534495BB5', 3, NULL, NULL, 2, NULL, 0, 'Pour egg yolks into a mixing bowl and whisk to break up.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E089CE80E140BE49911AF00919349AE9', X'D84DA9613204284C948344A534495BB5', X'D84DA9613204284C948344A534495BB5', 4, NULL, NULL, 2, NULL, 0, 'Continue whisking while slowly drizzling in the sugar syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01D0C9B86D4D8C4FA06D4E01A09F1E0C', X'D84DA9613204284C948344A534495BB5', X'D84DA9613204284C948344A534495BB5', 5, NULL, NULL, 2, NULL, 0, 'Pour 1/2 cup of heavy whipping cream into the saucepan and set it back on the stove on low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACB3D3F9C80DC64797DE53CBFDB03E95', X'D84DA9613204284C948344A534495BB5', X'D84DA9613204284C948344A534495BB5', 6, NULL, NULL, 2, NULL, 0, 'Stir in the egg mixture and continue to stir for at least 3 minutes until heated through (and registers at least 140 degrees Fahrenheit).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70974D9EAE37D443BA0B5417ADFFD189', X'D84DA9613204284C948344A534495BB5', X'D84DA9613204284C948344A534495BB5', 7, NULL, NULL, 2, NULL, 0, 'Pour through a fine mesh sieve that is set over your bowl of melted semi-sweet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22A3FAE7C3EF5B438E19418985D1739C', X'D84DA9613204284C948344A534495BB5', X'D84DA9613204284C948344A534495BB5', 8, NULL, NULL, 2, NULL, 0, 'Discard anything that remains in the sieve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3057862F2C9774DB44351CE324C422B', X'D84DA9613204284C948344A534495BB5', X'D84DA9613204284C948344A534495BB5', 9, NULL, NULL, 2, NULL, 0, 'Stir the chocolate until glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9DE78B7CEA0914583E2680E4432D103', X'D84DA9613204284C948344A534495BB5', X'D84DA9613204284C948344A534495BB5', 10, NULL, NULL, 2, NULL, 0, 'Fold the whipped cream into the chocolate in two stages.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3658F5C55917674BA01ECB58A1FA070D', X'D84DA9613204284C948344A534495BB5', X'D84DA9613204284C948344A534495BB5', 11, NULL, NULL, 2, NULL, 0, 'Cover and chill until the mousse thickens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBBA974072222C46A5624DA96F327BA9', X'11FC805D85EF5B4C8D08CD519C7B9E86', X'11FC805D85EF5B4C8D08CD519C7B9E86', 0, NULL, NULL, 2, NULL, 0, 'Combine cream cheese and sugar in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65EDE948A4E47047B06C277D453312C4', X'11FC805D85EF5B4C8D08CD519C7B9E86', X'11FC805D85EF5B4C8D08CD519C7B9E86', 1, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on medium-high speed until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77DBC7181C75484D8D2B3C4F516C4B3D', X'11FC805D85EF5B4C8D08CD519C7B9E86', X'11FC805D85EF5B4C8D08CD519C7B9E86', 2, NULL, NULL, 2, NULL, 0, 'Add marshmallow fluff and vanilla bean paste or extract and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01336B874F5E8C419FD14005C41D73E9', X'11FC805D85EF5B4C8D08CD519C7B9E86', X'11FC805D85EF5B4C8D08CD519C7B9E86', 3, NULL, NULL, 2, NULL, 0, 'Pour in whipped topping and beat until creamy and smooth. If using real whipped cream, gently fold it into the chocolate mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6AC91FD7FE86574CAECFA09A9C8A73E4', X'E5A7F0080D29C94DB9E08576E9A66975', X'E5A7F0080D29C94DB9E08576E9A66975', 0, NULL, NULL, 2, NULL, 0, 'Fill one disposable pastry bag with about 1 1/2 cups of dark chocolate mousse and fill another back with about the same amount of the cheesecake mousse.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C331BCCD46F4645AB1C2A8CEBA5E60A', X'E5A7F0080D29C94DB9E08576E9A66975', X'E5A7F0080D29C94DB9E08576E9A66975', 1, NULL, NULL, 2, NULL, 0, 'Fit a large pastry bag with a large star pastry tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'941A73C4E94BDC488BBE86F6F8B115DF', X'E5A7F0080D29C94DB9E08576E9A66975', X'E5A7F0080D29C94DB9E08576E9A66975', 2, NULL, NULL, 2, NULL, 0, 'Cut off the tips of the filled bags then snuggle them down into the third bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C195BD325A08C4439A31CA3F54DED569', X'E5A7F0080D29C94DB9E08576E9A66975', X'E5A7F0080D29C94DB9E08576E9A66975', 3, NULL, NULL, 2, NULL, 0, 'Squeeze until the mousse comes of of the bag and you can see both colors of mousse.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C24F90EACA21CB4B95BF7F895B690C70', X'E5A7F0080D29C94DB9E08576E9A66975', X'E5A7F0080D29C94DB9E08576E9A66975', 4, NULL, NULL, 2, NULL, 0, 'Pipe between 2/3 and 3/4 cup of the swirled chocolate cheesecake mousse into small dessert cups or bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50066217B7AC4945BC4F5341D5FB8DD9', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350ºF.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F92C0111BB854A47979D1D0519A7C6ED', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 1, NULL, NULL, 2, NULL, 0, 'Grease a 10-inch oven-safe non-stick skillet (or cast iron skillet) with some butter or non-stick baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'056F31A4D8FE2C46BB18D7F80FE436AD', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 2, NULL, NULL, 2, NULL, 0, 'Brush egg wash over pieces of pizza dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1FBD0452D3BC0345B7F1918A4EB8B587', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 3, NULL, NULL, 2, NULL, 0, 'Set one piece of the string cheese on a square of dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DF4C849EAB24E499C8AE43C938D8FE0', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 4, NULL, NULL, 2, NULL, 0, 'Attach opposing corners of the dough, then pinch around the edges to seal the dough. Pinch and roll into a ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5687AE42CCB737458249D41DC8ED0E0B', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 5, NULL, NULL, 2, NULL, 0, 'Set balls of dough around the edge of the buttered skillet, creating one row of 12.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BD50EA9950B684D8D10D6D682904F0D', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 6, NULL, NULL, 2, NULL, 0, 'Combine cream cheese, Parmesan cheese, 1/2 cup mozzarella cheese, ricotta cheese, and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8410B2A6E4967D4C836AA8A1B1B41EEA', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 7, NULL, NULL, 2, NULL, 0, 'Spoon 1/4 cup pizza sauce in the bottom of the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'180540BAC58FC44D8CEA94BFE9BFFCD9', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 8, NULL, NULL, 2, NULL, 0, 'Spread the cream cheese mixture over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC5AD01AB8DE19499BC46FBE89C51696', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 9, NULL, NULL, 2, NULL, 0, 'Spread 1/2 cup pizza sauce over top and sprinkle on half of the bacon, half of the pepperoni, and half of the remaining mozzarella cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95B157984204304392B617F15540EC92', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 10, NULL, NULL, 2, NULL, 0, 'Bake for 25 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E15277C0BC647347A913504E3A7146D8', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 11, NULL, NULL, 2, NULL, 0, 'Remove and top with remaining sauce, bacon, pepperoni, and mozzarella cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50976815440D864FA7B984DDF536A923', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 12, NULL, NULL, 2, NULL, 0, 'Return to oven and bake for 7-10 minutes until the pizza puffs are deep golden brown and the dip is hot and bubbly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7ACDA07FF9B0640A77DDF2B340479BE', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 13, NULL, NULL, 2, NULL, 0, 'While the dip is baking, cut the block of mozzarella cheese into 1/4 inch thick slices. Cut slices using number cutters, creating 1 though 12. Cut a minute hand and an hour hand for the clock too.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E59B09D5BA32A408038C6E396AE51B5', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 14, NULL, NULL, 2, NULL, 0, 'When dip is ready, arrange the numbers on the pizza puffs and the hands in the center of the dip creating a clock.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16344D04EE097B41AF17022BFF0A6D2D', X'B4FEF4650C71BD4CB855DB4604AE4A28', X'B4FEF4650C71BD4CB855DB4604AE4A28', 15, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'507D8D442FA68947A609A9FE202E9410', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'776AA163AB15BE42912A83BE2AF87506', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 1, NULL, NULL, 2, NULL, 0, 'Spray the springform pan with non-stick spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3174C8C41E5E0246A36E636A3B238A86', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 2, NULL, NULL, 2, NULL, 0, 'Bring a pot of water to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'077C91A32CEEB84893A5EB60630125D7', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 3, NULL, NULL, 2, NULL, 0, 'Pour the Town House Pretzel Thins in the bowl of a food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCF8CA2127A0334AA6696BBB41380F1C', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 4, NULL, NULL, 2, NULL, 0, 'Pulse to fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D263B04A4576D4C900A41F3A0B591C8', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 5, NULL, NULL, 2, NULL, 0, 'Add Parmesan Cheese and butter and pulse until it resembles wet sand.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A930727934219846A901EC801A846773', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 6, NULL, NULL, 2, NULL, 0, 'Press crumb mixture into the bottom of the springform pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E659E9C051B4EF4C92EDC1D8071AD641', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 7, NULL, NULL, 2, NULL, 0, 'Bake for 8-10 minutes until the crust starts to brown around the edge of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9741E47128ADC14495FD870DFC9DD124', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 8, NULL, NULL, 2, NULL, 0, 'Cut two ounces of cream cheese off one block and reserve for later.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD74CC9BAA536B45BB91C573171EC10A', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 9, NULL, NULL, 2, NULL, 0, 'Clean food processor bowl, and place remaining 14 ounces of cream cheese inside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F27B279946CAB4DB7B040F352C01890', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 10, NULL, NULL, 2, NULL, 0, 'Pulse until creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAF5081BD4A6044BBDA7465651204205', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 11, NULL, NULL, 2, NULL, 0, 'Add the sour cream, Parmesan cheese, roasted garlic, egg, egg yolk, and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27C3AE492DA2C442B34269C779C68510', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 12, NULL, NULL, 2, NULL, 0, 'Pulse until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59265582BE7FDF49B09BFDD719B00D29', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 13, NULL, NULL, 2, NULL, 0, 'Add artichokes, and pulse until chunky.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45809FAACFDE81418667D48E932BBA97', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 14, NULL, NULL, 2, NULL, 0, 'Add more salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C20400316279D42AA2938BC70BB8B92', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 15, NULL, NULL, 2, NULL, 0, 'Pour cheesecake filling over crust and spread into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9205DCF4548E9341A9CE7511F6549F43', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 16, NULL, NULL, 2, NULL, 0, 'Wrap bottom of springform pan in tin foil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDB596B9A7BF274FAB194D4F2090BCFC', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 17, NULL, NULL, 2, NULL, 0, 'Set in the 9x13 baking pan, set it in the hot oven, pour hot water into the baking pan, being careful to not spill any into the cheesecake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4C573047AD8BB4D8D27583098F7164B', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 18, NULL, NULL, 2, NULL, 0, 'Bake for 40 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B31435B87B4EE3448672A79FB6340A0A', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 19, NULL, NULL, 2, NULL, 0, 'Turn off oven, open oven door, and allow cheesecake to remain in oven for 15 more minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39286747BB38D649A0D2E12EE5D59A26', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 20, NULL, NULL, 2, NULL, 0, 'Remove cheesecake from water bath and allow it to cool at room temperature for an hour. Refrigerate for at least 4 hours or up to several days.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDDA5AA6022EE44394AEC561C19788F3', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 21, NULL, NULL, 2, NULL, 0, 'Remove from springform pan and set on a serving platter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07C9891538B3B74AAD86DA8E994CBA38', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 22, NULL, NULL, 2, NULL, 0, 'Stir black food coloring into the reserved 2 ounces of cream cheese and put it in a pastry bag fitted with a #3 pastry tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58FA68F219A76C4EAFD839AE150F98DA', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 23, NULL, NULL, 2, NULL, 0, 'Pipe on 1 through 12 onto the round cheesecake in a clock sequence.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B44C2C8765F8D4ABCE30CABE127D5CC', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 24, NULL, NULL, 2, NULL, 0, 'Change the tips and put the #6 tip on the pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A26EAC29FE4D44FAD3D3DFF4954EA5A', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 25, NULL, NULL, 2, NULL, 0, 'Pipe on the hands of the clock.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65F3A910C9859440BB9D60BDA3B18037', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 26, NULL, NULL, 2, NULL, 0, 'Refrigerate until you are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6522AE721F21C4FB93902F138BDAF0B', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 27, NULL, NULL, 2, NULL, 0, 'I popped my cheesecake in the freezer and will thaw it on New Year''s Eve before the party.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF10AA4698CEC841914FB924ADF7CF8F', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 28, NULL, NULL, 2, NULL, 0, 'You can serve the cheesecake all on it''s own, or with more Town House Sea Salt Pretzel Thins or some Town House Sea Salt and Olive Oil Flatbread Crisps.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEFF12C756B859438C6BE0D4D1A31646', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 29, NULL, NULL, 2, NULL, 0, 'To make the chocolate ganache, heat 6 ounces of semi-sweet chocolate with 1/2 cup heavy whipping cream on high in the microwave for 30 seconds, then stir, and heat for 15 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67F02B66B76C994B907628F56F3083F8', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 30, NULL, NULL, 2, NULL, 0, 'Serve with Town House Sea Salt Pretzel Thins.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'081B2C448BE2EB4B9E750633A42BCD55', X'29AFE4026887154E80F49D2EF4B6DB42', X'29AFE4026887154E80F49D2EF4B6DB42', 31, NULL, NULL, 2, NULL, 0, 'To make my cheese balls, I crushed 10 Town House Sea Salt and Olive Oil Flatbread Crisps to fine crumbs then blended 4 ounces cream cheese with 4 ounces garlic and herb Boursin cheese, 1/4 cup Parmesan cheese, and 4 ounces Italian blend shredded cheese. I scooped out a tablespoonful and wrapped it around a black stir stick and sprinkled on the crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD8EB502FBBF9641B24A250F1BBEAFB6', X'8E3A3D86E2FBCD49B0D8541392D731D2', X'8E3A3D86E2FBCD49B0D8541392D731D2', 0, NULL, NULL, 2, NULL, 0, 'Draw the clock face onto 16 of the round won ton wrappers using a black edible ink marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2E565E2BF61114F9A68C28AB38CF09F', X'8E3A3D86E2FBCD49B0D8541392D731D2', X'8E3A3D86E2FBCD49B0D8541392D731D2', 1, NULL, NULL, 2, NULL, 0, 'Brush egg wash over and scoop out .05 ounces (a rounded teaspoonful) of veggie cream cheese onto the middle of the 16 remaining won ton wrappers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6EAF9C2DDE71BF459F47CCD403201D24', X'8E3A3D86E2FBCD49B0D8541392D731D2', X'8E3A3D86E2FBCD49B0D8541392D731D2', 2, NULL, NULL, 2, NULL, 0, 'Place one decorated won ton on top of the cheese and press to seal around the edges.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4536A29504FE7B489403C4D08D2F7B65', X'8E3A3D86E2FBCD49B0D8541392D731D2', X'8E3A3D86E2FBCD49B0D8541392D731D2', 3, NULL, NULL, 2, NULL, 0, 'Bring pot of oil to 375 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'549BB1BCF1A5CE42B2A0439DB868B28E', X'8E3A3D86E2FBCD49B0D8541392D731D2', X'8E3A3D86E2FBCD49B0D8541392D731D2', 4, NULL, NULL, 2, NULL, 0, 'Place filled won tons in fryer, clock face down (this will give them a nicer shape.)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C76C1073C11F343B1D15A8A6874D906', X'8E3A3D86E2FBCD49B0D8541392D731D2', X'8E3A3D86E2FBCD49B0D8541392D731D2', 5, NULL, NULL, 2, NULL, 0, 'Fry until golden brown on both sides, about a minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81B0768FAC76B749A12508002365BBC1', X'8E3A3D86E2FBCD49B0D8541392D731D2', X'8E3A3D86E2FBCD49B0D8541392D731D2', 6, NULL, NULL, 2, NULL, 0, 'Drain on a piece of paper towel. Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17A9D7CB845D5A49AA51AE1B49D63929', X'8E3A3D86E2FBCD49B0D8541392D731D2', X'8E3A3D86E2FBCD49B0D8541392D731D2', 7, NULL, NULL, 2, NULL, 0, 'You can make these before your party then just reheat them in a 350 degree Fahrenheit oven for 5-7 minutes until heated through, but they are best right out of the hot oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'071D4F0CD95BD444A9C04271AB465F6B', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 0, NULL, NULL, 2, NULL, 0, 'Unroll both tubes of dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'697DFFD08373C54AA6DDA17B5761F2F0', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 1, NULL, NULL, 2, NULL, 0, 'Spread half of the cream cheese on each dough sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5DC4A441E272E42BFA209CEB21E5A66', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle half of the artichokes, sun dried tomatoes, roasted red peppers, and feta cheese on each dough sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDBFB28FD275FB4988C3686693AEA6FE', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 3, NULL, NULL, 2, NULL, 0, 'Roll up starting at the long end.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12E30D513499974FA6512E18A2F6E705', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 4, NULL, NULL, 2, NULL, 0, 'Pinch seams together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E63273B8EBC8F44B3A09D0E4E4F9F1E', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 5, NULL, NULL, 2, NULL, 0, 'Freeze for 15-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C47C09E50BA92429CA969D030DA4CCB', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 6, NULL, NULL, 2, NULL, 0, 'Cut into 1/2 inch thick slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EB8ED7DC048F047A34BB22226939278', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 7, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with non-stick tin foil or spray a non-stick pan with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9DFFF266140A174E8C8B4719AD56F23F', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 8, NULL, NULL, 2, NULL, 0, 'Arrange the pinwheels so that they write out the year');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6263820C60C52479A2A32D16135AEDE', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 9, NULL, NULL, 2, NULL, 0, 'Brush egg wash in between the pieces, where they touch, to help them stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2881524D904964F83BB24D98ABC4A86', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 10, NULL, NULL, 2, NULL, 0, 'Refrigerate for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB18DF62804F834C8EDF2684DED7D2F4', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 11, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF4FCC4F9DD6F44FAF9A6CDDC1CF3B50', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 12, NULL, NULL, 2, NULL, 0, 'Bake pinwheels for 18-20 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACB21024E4CAC64CA9A244F24874B78B', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 13, NULL, NULL, 2, NULL, 0, 'Slide off the baking pan onto a large platter or cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66825E6EA705D9448DA2BE469AB2DD60', X'A665F67FAF69D249850D1F91954E3BB4', X'A665F67FAF69D249850D1F91954E3BB4', 14, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'198E7C5590149C419B5FAB2FBD813E85', X'E0865445A0C56D47B8CF855667084745', X'E0865445A0C56D47B8CF855667084745', 0, NULL, NULL, 2, NULL, 0, 'Pour melted and tempered white chocolate or melted confectionery coating/candy melts into a tall glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B34BCFFA59B9F34DA08DFA03E8A16CFF', X'E0865445A0C56D47B8CF855667084745', X'E0865445A0C56D47B8CF855667084745', 1, NULL, NULL, 2, NULL, 0, 'Hold onto the tip of a sugar cone and dip it into the glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4398A10D1C0E994481376524D4494B1A', X'E0865445A0C56D47B8CF855667084745', X'E0865445A0C56D47B8CF855667084745', 2, NULL, NULL, 2, NULL, 0, 'Coat all but the very tip of the sugar cone with white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31B532EB01EAD84C816BD0382B1AEE02', X'E0865445A0C56D47B8CF855667084745', X'E0865445A0C56D47B8CF855667084745', 3, NULL, NULL, 2, NULL, 0, 'Remove the cone from the glass and allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58B956C578518D45BAE1F58C43A21717', X'E0865445A0C56D47B8CF855667084745', X'E0865445A0C56D47B8CF855667084745', 4, NULL, NULL, 2, NULL, 0, 'While wet, sprinkle colorful sanding sugar all over the cone (inside too, if you''d like).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A7594CA25889D41B98E51FC4CEAEFF5', X'E0865445A0C56D47B8CF855667084745', X'E0865445A0C56D47B8CF855667084745', 5, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A150BB51692FB9459BC91FEABCE03138', X'E0865445A0C56D47B8CF855667084745', X'E0865445A0C56D47B8CF855667084745', 6, NULL, NULL, 2, NULL, 0, 'Place the dipped cones in the refrigerator for about 10 minutes until the white chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7D87D479D946444BFD85DC98EC98939', X'E0865445A0C56D47B8CF855667084745', X'E0865445A0C56D47B8CF855667084745', 7, NULL, NULL, 2, NULL, 0, 'Pinch off pieces of white modeling chocolate and roll them into 1 1/4`-1 1/2` long logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'449EE6FB31546D468B170A9E94E62B0A', X'E0865445A0C56D47B8CF855667084745', X'E0865445A0C56D47B8CF855667084745', 8, NULL, NULL, 2, NULL, 0, 'Insert a skewer into the log, creating a hole.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'86FFAA460C883742A6F45C846B56026C', X'E0865445A0C56D47B8CF855667084745', X'E0865445A0C56D47B8CF855667084745', 9, NULL, NULL, 2, NULL, 0, 'Move the skewer around making the hole a bit bigger.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37FBD51FB829A94DBFC3A1684A35E72F', X'E0865445A0C56D47B8CF855667084745', X'E0865445A0C56D47B8CF855667084745', 10, NULL, NULL, 2, NULL, 0, 'Remove the skewer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3289E439198592408C0532FBC9EBD88A', X'E0865445A0C56D47B8CF855667084745', X'E0865445A0C56D47B8CF855667084745', 11, NULL, NULL, 2, NULL, 0, 'Press one log onto each dipped cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'581BFB51A7035B4FAD9208F546C364FD', X'E0865445A0C56D47B8CF855667084745', X'E0865445A0C56D47B8CF855667084745', 12, NULL, NULL, 2, NULL, 0, 'Reshape the log, if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E72EE7D74078F409C0C4157C621A99C', X'5D7A52AB01FD5043BD0A0145A4462783', X'5D7A52AB01FD5043BD0A0145A4462783', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C5467D7F469E24387DA04ABABCB331B', X'5D7A52AB01FD5043BD0A0145A4462783', X'5D7A52AB01FD5043BD0A0145A4462783', 1, NULL, NULL, 2, NULL, 0, 'Spread 1/3 cup pizza sauce in the middle of the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD255F8BFC2CC9469897D2D7540205ED', X'5D7A52AB01FD5043BD0A0145A4462783', X'5D7A52AB01FD5043BD0A0145A4462783', 2, NULL, NULL, 2, NULL, 0, 'Blend together 8 ounces cream cheese, 1/2 cup shredded Mozzarella Cheese, 1/2 cup shredded Parmesan cheese, 1/2 cup sour cream, and a pinch of salt, then spread half of it over the pizza sauce in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7259DE3B07BE8444996D4F64DB58BAF3', X'5D7A52AB01FD5043BD0A0145A4462783', X'5D7A52AB01FD5043BD0A0145A4462783', 3, NULL, NULL, 2, NULL, 0, 'Top with 3/4 cup shredded Mozzarella cheese, then sprinkle on half of the pepperoni or your favorite pizza toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD1B8C65C934BD468CF910244E9583E7', X'5D7A52AB01FD5043BD0A0145A4462783', X'5D7A52AB01FD5043BD0A0145A4462783', 4, NULL, NULL, 2, NULL, 0, 'Layer on another 1/3 cup pizza sauce, the remaining cheese dip, 1/3 cup pizza sauce, another 3/4 cup Mozzarella cheese, and more pepperoni or pizza toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0D3CAB988926B41AE7670EF0382F866', X'5D7A52AB01FD5043BD0A0145A4462783', X'5D7A52AB01FD5043BD0A0145A4462783', 5, NULL, NULL, 2, NULL, 0, 'Bake the dip for 28-30 minutes until the cheese is melted and the pizza crust is golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'040A80941164654CAC309CEABFB7AB69', X'5D7A52AB01FD5043BD0A0145A4462783', X'5D7A52AB01FD5043BD0A0145A4462783', 6, NULL, NULL, 2, NULL, 0, 'Serve while hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E0198D8B37A1E4EB3E80E061C474E37', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 0, NULL, NULL, 2, NULL, 0, 'If baking, preheat oven to 400 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CB12D6EA5E5084FBED0A60DF44F782D', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 1, NULL, NULL, 2, NULL, 0, 'Shred the zucchini, carrot, and potato using a cheese grater or food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D733F62F4E442479CB4F90CC01AA45D', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 2, NULL, NULL, 2, NULL, 0, 'Finely slice and chop the onion.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'124B58B701E692439E6C22091D039EBD', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 3, NULL, NULL, 2, NULL, 0, 'Place the veggies in a salad spinner or strainer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'451ED0D91E96CD4EAD2551C9EE9033AA', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle with salt and toss to evenly coat the veggies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7BE762599F33240A717E04A90BE5DB4', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 5, NULL, NULL, 2, NULL, 0, 'Set aside for 10 minutes to allow the moister to drain from the vegetables.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'403E6496A722BF49A37A375C9C28D4B2', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 6, NULL, NULL, 2, NULL, 0, 'Spin, if using a salad spinner, or press down using paper towels if using a strainer to remove as much liquid as possible.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'659C0C0B76B4C1478B9FE25409FC3341', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 7, NULL, NULL, 2, NULL, 0, 'Stir then let sit another 5 minutes and repeat until the veggies are quite dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11E58136F4592443A45C448FC79DF546', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 8, NULL, NULL, 2, NULL, 0, 'Pour into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B204B400C257E48A0C0404F622B586E', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle on the flour and toss to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D812E50B9CF3F14690C634CD8B5C842D', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 10, NULL, NULL, 2, NULL, 0, 'Stir in the eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70813C806F8C304FB1328CA538543972', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 11, NULL, NULL, 2, NULL, 0, 'If baking, spray a non-stick baking pan with olive oil cooking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44562E6BC721E74F85499766D080B75A', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 12, NULL, NULL, 2, NULL, 0, 'Spoon the veggie mixture into a football shaped cookie cutter, creating a fritter that is about 1/4 inch thick.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47E544CD81143844BB487F263EE5B3B1', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 13, NULL, NULL, 2, NULL, 0, 'Remove cookie cutter and repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77F5AA9D3A809C46A2F04CFD0A78318F', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 14, NULL, NULL, 2, NULL, 0, 'Spray olive oil spray over top of the fritters.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A88C562A1F14C247B81C6B178E92DBA3', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 15, NULL, NULL, 2, NULL, 0, 'Bake for 12 minutes, then remove and flip the fritters over.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4DC9ED5296BAC946BF4C9AB5DAF0BBFF', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 16, NULL, NULL, 2, NULL, 0, 'Bake for another 12-15 minutes until evenly browned on both sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA22043FAC94B04B9541073A3DCA2EA8', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 17, NULL, NULL, 2, NULL, 0, 'If frying, follow instructions in the tutorial.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4B9E509C891A74F84FADACDFA185027', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 18, NULL, NULL, 2, NULL, 0, 'Spoon 2 tablespoons of the yogurt or sour cream into a pastry bag or zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA8FC5FA2675EC43AB6EFD519293198E', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 19, NULL, NULL, 2, NULL, 0, 'Pipe laces and stripes on each of the footballs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2A7C3E1E774364E82BD7FC648473126', X'E407E0399A3DA6488D321B0660F81B46', X'E407E0399A3DA6488D321B0660F81B46', 20, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CBCABF0E4A02C4DBFB3789E2CF5EE5A', X'902130825192A64596CE714F40C5D9CB', X'902130825192A64596CE714F40C5D9CB', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED0B8D525B75F54CA62E8404914E8957', X'902130825192A64596CE714F40C5D9CB', X'902130825192A64596CE714F40C5D9CB', 1, NULL, NULL, 2, NULL, 0, 'Combine 1 1/2 cups flour, undissolved yeast, sugar and salt in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A60BAE4A9E1AF347AAD4E2FAEB57811C', X'902130825192A64596CE714F40C5D9CB', X'902130825192A64596CE714F40C5D9CB', 2, NULL, NULL, 2, NULL, 0, 'Add the very warm water (between 120° to 130°F) and corn oil then mix until well blended for about 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78D7B935BAC9F940A0414591C34C9DB8', X'902130825192A64596CE714F40C5D9CB', X'902130825192A64596CE714F40C5D9CB', 3, NULL, NULL, 2, NULL, 0, 'Knead the dough on a floured surface, adding additional flour if necessary, until smooth and elastic, about 4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A35705D4A2FCE46AF1E71E01002A1B8', X'902130825192A64596CE714F40C5D9CB', X'902130825192A64596CE714F40C5D9CB', 4, NULL, NULL, 2, NULL, 0, 'Cover the dough with plastic wrap then let the dough rest for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AD340335A8C504BB8B742410B781982', X'902130825192A64596CE714F40C5D9CB', X'902130825192A64596CE714F40C5D9CB', 5, NULL, NULL, 2, NULL, 0, 'Grease a 12-inch round pizza pan then pat the dough into an even layer in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E50BD9037952354AB6F7DAC32D3B3E19', X'902130825192A64596CE714F40C5D9CB', X'902130825192A64596CE714F40C5D9CB', 6, NULL, NULL, 2, NULL, 0, 'Pinch the edges to create a thicker crust around the perimeter of the crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA16166E7DF499468E01653C8A292A10', X'902130825192A64596CE714F40C5D9CB', X'902130825192A64596CE714F40C5D9CB', 7, NULL, NULL, 2, NULL, 0, 'Brush the edges of the pizza crust lightly with corn oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E447E412F1FDF54C8E4C4FBFB4CE3A99', X'902130825192A64596CE714F40C5D9CB', X'902130825192A64596CE714F40C5D9CB', 8, NULL, NULL, 2, NULL, 0, 'Spread the pizza sauce over the center of the dough, leaving 1/2-inch to 1-inch of uncovered dough around the perimeter of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'704D94E19A3BCC41806C93AC7256B673', X'902130825192A64596CE714F40C5D9CB', X'902130825192A64596CE714F40C5D9CB', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle about 3/4 of the mozzarella cheese over the sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CAA6ACFF7CA8A54EAAB77717776E80A4', X'902130825192A64596CE714F40C5D9CB', X'902130825192A64596CE714F40C5D9CB', 10, NULL, NULL, 2, NULL, 0, 'Add the roasted vegetables on top then add the marinated artichokes, the remaining mozzarella cheese, and the feta cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3E3B97B4EA90F46A056EA0657FFABE4', X'902130825192A64596CE714F40C5D9CB', X'902130825192A64596CE714F40C5D9CB', 11, NULL, NULL, 2, NULL, 0, 'Bake on lowest oven rack for 12 to 15 minutes, until cheese is bubbly and crust is browned.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A07388E101514948B0398C43CD4E3B60', X'D868E4058776974392398DE7A9B0C2A8', X'D868E4058776974392398DE7A9B0C2A8', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB9A118D54E6D343B9EDB006480113F4', X'D868E4058776974392398DE7A9B0C2A8', X'D868E4058776974392398DE7A9B0C2A8', 1, NULL, NULL, 2, NULL, 0, 'Heat a large skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53C67CB0923D4C44BBCC7301325EE977', X'D868E4058776974392398DE7A9B0C2A8', X'D868E4058776974392398DE7A9B0C2A8', 2, NULL, NULL, 2, NULL, 0, 'Add ground beef and cook until browned; drain.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45D4F202F010984E97180F7987D61D67', X'D868E4058776974392398DE7A9B0C2A8', X'D868E4058776974392398DE7A9B0C2A8', 3, NULL, NULL, 2, NULL, 0, 'Add taco seasoning and water and bring to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F47A4B512385C48AE7C185745472123', X'D868E4058776974392398DE7A9B0C2A8', X'D868E4058776974392398DE7A9B0C2A8', 4, NULL, NULL, 2, NULL, 0, 'Lower heat and simmer until most of the liquid has evaporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAD1E0E0FA404E4293615E5F8E433334', X'D868E4058776974392398DE7A9B0C2A8', X'D868E4058776974392398DE7A9B0C2A8', 5, NULL, NULL, 2, NULL, 0, 'Spread refried beans into bottom of a round or rectangle baking dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A542C430DB99254C9CE40FC2B3A4D745', X'D868E4058776974392398DE7A9B0C2A8', X'D868E4058776974392398DE7A9B0C2A8', 6, NULL, NULL, 2, NULL, 0, 'Spread the taco meat over the layer of refried beans.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F87E23213D73C0418B4DD00DF0D39424', X'D868E4058776974392398DE7A9B0C2A8', X'D868E4058776974392398DE7A9B0C2A8', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle on diced tomatoes, diced jalapenos, and chopped olives.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E081DEA5FF3CA443A750A0B30E0EBDB1', X'D868E4058776974392398DE7A9B0C2A8', X'D868E4058776974392398DE7A9B0C2A8', 8, NULL, NULL, 2, NULL, 0, 'Top with shredded cheeses.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5AE77FB8F3796B459B9FFEAB5641792E', X'D868E4058776974392398DE7A9B0C2A8', X'D868E4058776974392398DE7A9B0C2A8', 9, NULL, NULL, 2, NULL, 0, 'Arrange black olive slices in the appropriate date (2015) over top of the cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63CBE652309CD04191479F2A13A04584', X'D868E4058776974392398DE7A9B0C2A8', X'D868E4058776974392398DE7A9B0C2A8', 10, NULL, NULL, 2, NULL, 0, 'Bake for 25 minutes or until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F362928DB7B59F4EBC2FDD10C74F665D', X'D868E4058776974392398DE7A9B0C2A8', X'D868E4058776974392398DE7A9B0C2A8', 11, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA312DF9A38F9D41BB7A1581844EE582', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 0, NULL, NULL, 2, NULL, 0, 'Place the bacon pieces in a large skillet and set over medium-high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'250F035A751DDF45B082518592A79537', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 1, NULL, NULL, 2, NULL, 0, 'Cook until crispy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F64FF39F5EF5545AAEB35E4F4056D66', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 2, NULL, NULL, 2, NULL, 0, 'Remove the bacon and all but 1 tablespoon of the fat from the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7A5667BA63D8945B04B6B7DA800701A', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 3, NULL, NULL, 2, NULL, 0, 'Season the ground sirloin with salt, garlic salt, and pepper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A057C93D8AB90341950E7F4FEEA8E720', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 4, NULL, NULL, 2, NULL, 0, 'Brown half of the ground sirloin in the hot skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B886CFBCC40FBB42B1A59CEB7A67C8D7', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 5, NULL, NULL, 2, NULL, 0, 'Remove the browned hamburger from the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CAEB3634EF04D84B9AEB32456A5CCD8E', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 6, NULL, NULL, 2, NULL, 0, 'If needed, add more bacon grease to the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBD0C87B3AE8CD49B0BC2C9469B085ED', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 7, NULL, NULL, 2, NULL, 0, 'Brown the remaining ground sirloin in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE360E80AA829C47940BFE9828FE1B47', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 8, NULL, NULL, 2, NULL, 0, 'Add the other hamburger back into the pan and reduce the heat to low.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8BE4B67BDF80D44B90D9F49BB24B64E', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 9, NULL, NULL, 2, NULL, 0, 'Stir in the ketchup, Worcestershire sauce, and yellow mustard.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71D14FD5B001F34485CCCAA35CB8FB1F', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 10, NULL, NULL, 2, NULL, 0, 'Then stir in 1/3 cup sour cream, the cream cheese, and cheddar cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B9271A5923A92428C49862EFE380176', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 11, NULL, NULL, 2, NULL, 0, 'Once all the cheese has melted toss in the diced tomatoes and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB56160A0746B8419FD3C2599C567D51', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 12, NULL, NULL, 2, NULL, 0, 'Spoon the hot cheeseburger dip into a ceramic football bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8EDCAB00159F0408D9134A46C7763E5', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 13, NULL, NULL, 2, NULL, 0, 'Spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0BF003D30C0904C8411DEDBF3D6920A', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 14, NULL, NULL, 2, NULL, 0, 'Top it with the bacon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09FC17B4CB347B439A4F2398B8E6D223', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 15, NULL, NULL, 2, NULL, 0, 'Pour the sour cream into a zip top bag and snip off a tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90AE2B34DC8C534EBD124E591CAC40D4', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 16, NULL, NULL, 2, NULL, 0, 'Pipe a line going across the football then add some laces going across that line.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10745067A4DD404DB6E7B0150DC4781B', X'DDC1C80559E3394FA52BBD9C415CAA9A', X'DDC1C80559E3394FA52BBD9C415CAA9A', 17, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55F80DC9F59F8F4CB643746114CE6206', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 425 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4BCC3EFAC6E9904FAE57ED407A646BD1', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 1, NULL, NULL, 2, NULL, 0, 'Butter the inside of 24 pig cavities in 2 silicone molds using melted butter, reserving 2 tablespoons of the melted butter to put into the pancake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'277FA898496A094FB010D28F5CCE718A', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 2, NULL, NULL, 2, NULL, 0, 'In large bowl, whisk together the flour, salt, baking powder, and baking soda.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93E981C8E08113469AC21890B1ED33FE', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 3, NULL, NULL, 2, NULL, 0, 'Whisk together the egg and buttermilk then add the sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71AA2CF7C53EAF4EAAB3D614C69DBEE4', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 4, NULL, NULL, 2, NULL, 0, 'Pour the liquid mixture into the flour mixture and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'349206F21AC6984CA5D28DC3C03E8B76', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 5, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoons melted butter and mix until blended but with lumps remaining.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4B3036D9DF2484083EB889A07979880', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 6, NULL, NULL, 2, NULL, 0, 'Pour batter into a squeeze bottle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'648A0DFE4D70354CA38F38B34DF2AC8F', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 7, NULL, NULL, 2, NULL, 0, 'Squeeze batter into a pig cavity in the silicone mold filling it about 3/4''s full.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01B3D8DA724DF5449234218E1D3B7AE6', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 8, NULL, NULL, 2, NULL, 0, 'Press a Little Smokie into the pancake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D4160380619A94F8EE5E2FB910C37A4', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 9, NULL, NULL, 2, NULL, 0, 'Cover the little sausage with more pancake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CA68AEF7E09884B9D5D4CF84AB09B64', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 10, NULL, NULL, 2, NULL, 0, 'Repeat, filling up all 24 pig cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DE4D70DF3B8DA48A28F6CFC2CB96EB4', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 11, NULL, NULL, 2, NULL, 0, 'Bake for 14-18 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6406D5AC726864B9354286969747DF4', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 12, NULL, NULL, 2, NULL, 0, 'Remove and allow the pigs to cool for a few minutes before removing from the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'157830145B1C0C41A741DE3AA79F8E38', X'3D2E2674A8434342B1D59269C856E65F', X'3D2E2674A8434342B1D59269C856E65F', 13, NULL, NULL, 2, NULL, 0, 'Serve with your favorite dipping sauces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DADAEEC80313F24D82C6154B1364E691', X'8B5F55D5FBE4F643A67309B9A560C0A2', X'8B5F55D5FBE4F643A67309B9A560C0A2', 0, NULL, NULL, 2, NULL, 0, 'Pour 3/4''s of the drained artichokes into the bowl of a food processor along with the cashews, Parmigiano Reggiano Cheese, lemon juice, and 1/3 cup of the bacon pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A0269F0043A61449D8A84C177E51255', X'8B5F55D5FBE4F643A67309B9A560C0A2', X'8B5F55D5FBE4F643A67309B9A560C0A2', 1, NULL, NULL, 2, NULL, 0, 'Pulse a few times, then with the food processor running, drizzle in the extra virgin olive oil until the mixture becomes finely chopped.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1FEA4FDE13D6D4897A18E54A89BF7A6', X'8B5F55D5FBE4F643A67309B9A560C0A2', X'8B5F55D5FBE4F643A67309B9A560C0A2', 2, NULL, NULL, 2, NULL, 0, 'Taste, then season with salt and pepper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BA4D64A63C6BA4A9EAA61DD7014EC0F', X'8B5F55D5FBE4F643A67309B9A560C0A2', X'8B5F55D5FBE4F643A67309B9A560C0A2', 3, NULL, NULL, 2, NULL, 0, 'Chop the remaining artichokes into small pieces and stir into the pesto.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADC2B1C109955C45B643601FB5A49B2C', X'8B5F55D5FBE4F643A67309B9A560C0A2', X'8B5F55D5FBE4F643A67309B9A560C0A2', 4, NULL, NULL, 2, NULL, 0, 'Id you want a creamy dip then stir in the whipped cream cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6C15C24E6B6484782235775D3C712B8', X'8B5F55D5FBE4F643A67309B9A560C0A2', X'8B5F55D5FBE4F643A67309B9A560C0A2', 5, NULL, NULL, 2, NULL, 0, 'Pour the pesto into a football shaped ceramic bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99AE13C78EC9D949A1E48755EAB6BFAF', X'8B5F55D5FBE4F643A67309B9A560C0A2', X'8B5F55D5FBE4F643A67309B9A560C0A2', 6, NULL, NULL, 2, NULL, 0, 'Crumble the remaining bacon over top of the dip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75CF09DE2B51904B875A69ABEBB99413', X'8B5F55D5FBE4F643A67309B9A560C0A2', X'8B5F55D5FBE4F643A67309B9A560C0A2', 7, NULL, NULL, 2, NULL, 0, 'If you want to serve the dip hot, cover it with tin foil and heat it in a 350 degree oven for about 15-20 minutes until heated through before decorating with sour cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF2E457AE9FDB141BEBC55724289F072', X'8B5F55D5FBE4F643A67309B9A560C0A2', X'8B5F55D5FBE4F643A67309B9A560C0A2', 8, NULL, NULL, 2, NULL, 0, 'Spoon the sour cream into a small zip top bag and snip off the tip then decorate the cold or hot dip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71E868D8FB9963498F3630AB3CC7222A', X'8B5F55D5FBE4F643A67309B9A560C0A2', X'8B5F55D5FBE4F643A67309B9A560C0A2', 9, NULL, NULL, 2, NULL, 0, 'Pipe laces down the center of the football and stripes on either end.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45A42D6888DFB34181BFCBF5EBB0E12B', X'8B5F55D5FBE4F643A67309B9A560C0A2', X'8B5F55D5FBE4F643A67309B9A560C0A2', 10, NULL, NULL, 2, NULL, 0, 'Serve the dip with Mission Strips Tortilla Chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24EAFD8D0DC2444693EA93961E8D8202', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 0, NULL, NULL, 2, NULL, 0, 'Heat taco meat, black beans, and peppers together in a skillet over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DECDA15BCE0C3E47A801981CAA215652', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 1, NULL, NULL, 2, NULL, 0, 'Stir in cheese just until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE1DD7544101C14696D23863E0F0A9BD', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 2, NULL, NULL, 2, NULL, 0, 'Set aside to cool slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F1CBF7C35DC6A49AC61BD329851F657', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 3, NULL, NULL, 2, NULL, 0, 'Cut 3 football shapes out of each egg roll wrapper using a cookie cutter or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8EDB9F158BA584590FBA7A7FADC0A5C', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 4, NULL, NULL, 2, NULL, 0, 'Whisk together 1 egg and water to create an egg wash.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DEEA7489928691499C5C847C250E058C', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 5, NULL, NULL, 2, NULL, 0, 'Brush egg wash over one football shaped egg roll wrapper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AA484E62ACA7146A4F130170B7170E5', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 6, NULL, NULL, 2, NULL, 0, 'Spoon one tablespoon of the taco mixture onto the football.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED0AA4198BA38D4098691247EC44A9D1', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 7, NULL, NULL, 2, NULL, 0, 'Set another football shaped egg roll wrapper over top and press and seal the edges.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAFD8729460F9D45902937548E721697', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 8, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 30 footballs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F08669FC12D4E44689D88350925BFE40', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 9, NULL, NULL, 2, NULL, 0, 'Heat oil to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A84B8EF1FC6E264491221D7956AA3ECC', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 10, NULL, NULL, 2, NULL, 0, 'Fry taco footballs until deep golden brown, about 1 minutes, flipping them over as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74892B9C79393841AAA67A4795959AA5', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 11, NULL, NULL, 2, NULL, 0, 'Remove from oil and set on paper towels to drain excess oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D40589FB3581BB42A3105338D0E5D645', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 12, NULL, NULL, 2, NULL, 0, 'Spoon sour cream into a pastry or zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7F0C93E909B754C97F28B92B4F369A1', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 13, NULL, NULL, 2, NULL, 0, 'Pipe sour cream laces over each football.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10DCB4F593DAAE43A76BD7063660A00D', X'16B2E6ED6779A640A74112123FF04B87', X'16B2E6ED6779A640A74112123FF04B87', 14, NULL, NULL, 2, NULL, 0, 'Serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FCA0E7A7BDC7C468A5CD885C8292C3F', X'481009BA361BC9449DF9547F7BA1654C', X'481009BA361BC9449DF9547F7BA1654C', 0, NULL, NULL, 2, NULL, 0, 'Coat one bag of popped popcorn with 18 ounces of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'132BD4163729C940A6F6A4B6E59AF63F', X'481009BA361BC9449DF9547F7BA1654C', X'481009BA361BC9449DF9547F7BA1654C', 1, NULL, NULL, 2, NULL, 0, 'Press into the bottom and up the sides of a large, plastic, football shaped bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'966290CE55A8DE4785BDA04BC56CE0E4', X'481009BA361BC9449DF9547F7BA1654C', X'481009BA361BC9449DF9547F7BA1654C', 2, NULL, NULL, 2, NULL, 0, 'Coat one more bag of popped popcorn with 18 ounces of chocolate and continue to press the popcorn up the sides of the football bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47181B559D0C9E43B9AB4AD22F534416', X'481009BA361BC9449DF9547F7BA1654C', X'481009BA361BC9449DF9547F7BA1654C', 3, NULL, NULL, 2, NULL, 0, 'Brush the inside of the football shaped popcorn bowl with 4 ounces of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65C50AC8CA1A3A499A104E9EF4FF0F60', X'481009BA361BC9449DF9547F7BA1654C', X'481009BA361BC9449DF9547F7BA1654C', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes then freeze for 10.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'124837A322D2A84AAF5CEC13689A9831', X'481009BA361BC9449DF9547F7BA1654C', X'481009BA361BC9449DF9547F7BA1654C', 5, NULL, NULL, 2, NULL, 0, 'Remove popcorn bowl from plastic bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F80CA7B79DED3C4CB9DA82D7362E9621', X'481009BA361BC9449DF9547F7BA1654C', X'481009BA361BC9449DF9547F7BA1654C', 6, NULL, NULL, 2, NULL, 0, 'Coat the remaining bag of popcorn, peanuts, and toffee bits with 18 ounces of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40CC34C83B6BC54CB523B4068BC28E67', X'481009BA361BC9449DF9547F7BA1654C', X'481009BA361BC9449DF9547F7BA1654C', 7, NULL, NULL, 2, NULL, 0, 'Spread onto a parchment paper lined baking pan and refrigerate for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F8B3970D4E3594A8D578ACE7CE53253', X'481009BA361BC9449DF9547F7BA1654C', X'481009BA361BC9449DF9547F7BA1654C', 8, NULL, NULL, 2, NULL, 0, 'Break the popcorn into pieces and place in the football shaped popcorn bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E997C7DC2908343AE2FB2C2E87519DD', X'481009BA361BC9449DF9547F7BA1654C', X'481009BA361BC9449DF9547F7BA1654C', 9, NULL, NULL, 2, NULL, 0, 'Decorate the football with white modeling chocolate laces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'602ED7AD76FBBA4E8ED2815C9A7BE685', X'481009BA361BC9449DF9547F7BA1654C', X'481009BA361BC9449DF9547F7BA1654C', 10, NULL, NULL, 2, NULL, 0, 'Roll out the white modeling chocolate to about 1/8th inch thickness. Cut one 5 inch by 1/4 inch strip and attach it to the popcorn bowl using clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBC6C9ACD6083842926DCEF1E70813F4', X'481009BA361BC9449DF9547F7BA1654C', X'481009BA361BC9449DF9547F7BA1654C', 11, NULL, NULL, 2, NULL, 0, 'Cut eight 3/4 inch by 1/8th inch strips and attach them using piping gel going vertically across the 5 inch strip creating the laces of the football.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68E83B0843CF67408401A2DDE991F352', X'C7CAA18D8A27A64F82B6620CC7DC6C22', X'C7CAA18D8A27A64F82B6620CC7DC6C22', 0, NULL, NULL, 2, NULL, 0, 'Pour the chopped chocolate into a mixing bowl or large glass measuring cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'532691B79CDBF14A802981BDBC106BF6', X'C7CAA18D8A27A64F82B6620CC7DC6C22', X'C7CAA18D8A27A64F82B6620CC7DC6C22', 1, NULL, NULL, 2, NULL, 0, 'Heat heavy whipping cream either in the microwave or on the stove, just until the cream forms small bubbles around the edge of the bowl or pan. (Do not bring to a boil.)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0CEA64BA0F553B428D428DB15195B652', X'C7CAA18D8A27A64F82B6620CC7DC6C22', X'C7CAA18D8A27A64F82B6620CC7DC6C22', 2, NULL, NULL, 2, NULL, 0, 'Pour over chocolate and let the bowl sit for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C93D4DBAA0266429E57D5E5CDC8D990', X'C7CAA18D8A27A64F82B6620CC7DC6C22', X'C7CAA18D8A27A64F82B6620CC7DC6C22', 3, NULL, NULL, 2, NULL, 0, 'Whisk, starting in the center of the bowl and moving out towards the edge of the bowl, until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'906973F4A2323B43B0FF9F95B1AEC1F3', X'C7CAA18D8A27A64F82B6620CC7DC6C22', X'C7CAA18D8A27A64F82B6620CC7DC6C22', 4, NULL, NULL, 2, NULL, 0, 'Stir in the vanilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98B78C888AEDFD458FC7E39DA55D94C4', X'C7CAA18D8A27A64F82B6620CC7DC6C22', X'C7CAA18D8A27A64F82B6620CC7DC6C22', 5, NULL, NULL, 2, NULL, 0, 'Pour into a football shaped bowl and let cool for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A19AA7595F11854D80461A298365C758', X'C7CAA18D8A27A64F82B6620CC7DC6C22', X'C7CAA18D8A27A64F82B6620CC7DC6C22', 6, NULL, NULL, 2, NULL, 0, 'Spoon vanilla frosting or other white cream into a small zip top bag and snip off the tip of the bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3263D8E75C46D943AB8B751859DCF1ED', X'C7CAA18D8A27A64F82B6620CC7DC6C22', X'C7CAA18D8A27A64F82B6620CC7DC6C22', 7, NULL, NULL, 2, NULL, 0, 'Pipe on some football laces and stripes to make the dip look like a football.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA86276063E8674787F6950F2A77F68B', X'C7CAA18D8A27A64F82B6620CC7DC6C22', X'C7CAA18D8A27A64F82B6620CC7DC6C22', 8, NULL, NULL, 2, NULL, 0, 'Serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3ADEDCA1E84E7F4BB439E292ADF8B41E', X'233AD42292AD6A4BA5BCC31B41DAAB47', X'233AD42292AD6A4BA5BCC31B41DAAB47', 0, NULL, NULL, 2, NULL, 0, 'Cut hard boiled eggs in half lengthwise.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2708FBDA7FDABE4A9B7B6B05BB1621D0', X'233AD42292AD6A4BA5BCC31B41DAAB47', X'233AD42292AD6A4BA5BCC31B41DAAB47', 1, NULL, NULL, 2, NULL, 0, 'Remove the yolks and crumble them in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A52CA5325AFA8A4EA6419903C657C284', X'233AD42292AD6A4BA5BCC31B41DAAB47', X'233AD42292AD6A4BA5BCC31B41DAAB47', 2, NULL, NULL, 2, NULL, 0, 'Add 1/3 cup Miracle Whip or mayonnaise, mustard, pickle juice, and salt and stir until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'171FEC1D0A3C874696D5281AD325748A', X'233AD42292AD6A4BA5BCC31B41DAAB47', X'233AD42292AD6A4BA5BCC31B41DAAB47', 3, NULL, NULL, 2, NULL, 0, 'Equally divide the filling among the 12 egg halves, spreading the filing in an even layer over top of the egg, leaving about an 1/8th of an inch border around the outer edge of the egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10D209A788475445A5F10511416FFA54', X'233AD42292AD6A4BA5BCC31B41DAAB47', X'233AD42292AD6A4BA5BCC31B41DAAB47', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle bacon over top and use your fingers to press the bacon into a football shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A756B58C69C5694B87FA4B5FBD0F3FD6', X'233AD42292AD6A4BA5BCC31B41DAAB47', X'233AD42292AD6A4BA5BCC31B41DAAB47', 5, NULL, NULL, 2, NULL, 0, 'Spoon the remaining 1 tablespoon of Miracle Whip or mayo into a small zip top bag. Snip off the tip leaving a small opening.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E4FDA6C026BBA4A96C7BD682E7D713E', X'233AD42292AD6A4BA5BCC31B41DAAB47', X'233AD42292AD6A4BA5BCC31B41DAAB47', 6, NULL, NULL, 2, NULL, 0, 'Pipe a straight line of mayo down the center, going lengthwise, of each egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29832179986447499932101067BC44ED', X'233AD42292AD6A4BA5BCC31B41DAAB47', X'233AD42292AD6A4BA5BCC31B41DAAB47', 7, NULL, NULL, 2, NULL, 0, 'Add 3-4 short lines of mayo going across the long line to create the football laces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'360B50B609F3344D8377983EEA5A96FB', X'233AD42292AD6A4BA5BCC31B41DAAB47', X'233AD42292AD6A4BA5BCC31B41DAAB47', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D44F264A6F57F44BB3E7ABB8A1C6D770', X'7D884648C30E11499619A1013BD3BC49', X'7D884648C30E11499619A1013BD3BC49', 0, NULL, NULL, 2, NULL, 0, 'Peel and dice potatoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91E785479CB2AD44BCAC58231A1BB59E', X'7D884648C30E11499619A1013BD3BC49', X'7D884648C30E11499619A1013BD3BC49', 1, NULL, NULL, 2, NULL, 0, 'Rinse with cold water.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E14045480AA5124991219C96F1805EA9', X'7D884648C30E11499619A1013BD3BC49', X'7D884648C30E11499619A1013BD3BC49', 2, NULL, NULL, 2, NULL, 0, 'Place in a small saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B98E99D3F85F94AA57EC2323F7ABAA3', X'7D884648C30E11499619A1013BD3BC49', X'7D884648C30E11499619A1013BD3BC49', 3, NULL, NULL, 2, NULL, 0, 'Pour water over potatoes so that the water goes 1 inch above the potatoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7474DAC2E5B3C04DA041A4D8545C6BE0', X'7D884648C30E11499619A1013BD3BC49', X'7D884648C30E11499619A1013BD3BC49', 4, NULL, NULL, 2, NULL, 0, 'Add the white vinegar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A70FBC4536B38845A36096FDBF8795F7', X'7D884648C30E11499619A1013BD3BC49', X'7D884648C30E11499619A1013BD3BC49', 5, NULL, NULL, 2, NULL, 0, 'Bring to a boil over high heat then lower heat to medium and continue to cook for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DE8AE5A90A3A54B9BD4CD54B85711C5', X'7D884648C30E11499619A1013BD3BC49', X'7D884648C30E11499619A1013BD3BC49', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and drain off water.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'592A5981FBF7DC45A229EEDD18707BCB', X'7D884648C30E11499619A1013BD3BC49', X'7D884648C30E11499619A1013BD3BC49', 7, NULL, NULL, 2, NULL, 0, 'Pour out onto paper towels and pat dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3F17814CCC33441AAAC9CFCD2FC9C97', X'7D884648C30E11499619A1013BD3BC49', X'7D884648C30E11499619A1013BD3BC49', 8, NULL, NULL, 2, NULL, 0, 'Heat oil in a 12 inch skillet over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60A41DF36515BB4B9FA0F1A6A3E9D2F9', X'7D884648C30E11499619A1013BD3BC49', X'7D884648C30E11499619A1013BD3BC49', 9, NULL, NULL, 2, NULL, 0, 'Once the oil begins to shimmer, toss in the potatoes and onions, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0C163FFF14F7F46A638C8211C7F6C56', X'7D884648C30E11499619A1013BD3BC49', X'7D884648C30E11499619A1013BD3BC49', 10, NULL, NULL, 2, NULL, 0, 'Cook until they begin to brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F125B9A74C8A34B825A7694F0C43D30', X'7D884648C30E11499619A1013BD3BC49', X'7D884648C30E11499619A1013BD3BC49', 11, NULL, NULL, 2, NULL, 0, 'Add the peppers, sausage, and a pinch of season salt and cook until heated through and lightly browned.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D96E0F6B58D554F9F40AE4D2D6284A6', X'7D884648C30E11499619A1013BD3BC49', X'7D884648C30E11499619A1013BD3BC49', 12, NULL, NULL, 2, NULL, 0, 'Spread onto flour tortillas, top with sour cream if using, roll up and enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1CB3B59552B3148A8FE7BB5BC9F30C5', X'F24B0D58C5E44E48A887F2762979962C', X'F24B0D58C5E44E48A887F2762979962C', 0, NULL, NULL, 2, NULL, 0, 'Line two 8 inch square pans with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE22AD5D9FDFDE4FBF2DC6E3C392287A', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 0, NULL, NULL, 2, NULL, 0, 'Pour the white candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20666F252C98EE4EACBC2C82D28EC25B', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 1, NULL, NULL, 2, NULL, 0, 'Pour the sweetened condensed milk over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'456DB0B285C0324E9EA9179F55880F7C', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power in the microwave for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'006C78D6FDC4D14AB953713B800F4D80', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 3, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69295629F09F444197E581F7BC4802D6', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 4, NULL, NULL, 2, NULL, 0, 'Heat on high power for another 30 seconds then let the bowl sit for 2-3 minutes in the microwave.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D603D4752C151F4FA4A1BDD67EB92A0D', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 5, NULL, NULL, 2, NULL, 0, 'Remove and stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96422D50A70F69488497406371F589EB', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 6, NULL, NULL, 2, NULL, 0, 'If needed let bowl sit another few minutes and stir again.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9E3E0CBF69ABE46A15CC67DE8CF1B30', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 7, NULL, NULL, 2, NULL, 0, 'If lumps of candy remain, heat for 10 seconds and stir again.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6532460308F90846B181AC1F0C16DDED', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 8, NULL, NULL, 2, NULL, 0, 'Pour fudge into the prepared pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C74E2B4ED4A1D4A951B379FD05C2D4B', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEF57B70D102AE4FAEFBC853F4209BC6', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 10, NULL, NULL, 2, NULL, 0, 'Remove and flip upside down onto a clean piece of parchment paper or a silicone mat set over a cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'143EC2CF899C354DA450F0A7A56FB9FD', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 11, NULL, NULL, 2, NULL, 0, 'Peel the parchment paper off the fudge and place the fudge in the freezer for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E60F0AC007B7E4EB87C849AD753D0D2', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 12, NULL, NULL, 2, NULL, 0, 'Remove and cut using t-shirt shaped cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E56D8AEF01EA34CB0BAEBE4A82E474C', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 13, NULL, NULL, 2, NULL, 0, 'If desired, you can re-roll any scraps of fudge and cut out more shirts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECC0F06C0CBBA647840157680462D2D6', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 14, NULL, NULL, 2, NULL, 0, 'Pour melted blue Candy Melts into a zip top bag and snip off one tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90538F353BFD3F4EAA406B822A282A59', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 15, NULL, NULL, 2, NULL, 0, 'Pipe numbers and stripes onto each of the fudge shirts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53314A3271F53348A46FFFDC77839782', X'279F4A3FC306ED4584115B2B72391CC6', X'279F4A3FC306ED4584115B2B72391CC6', 16, NULL, NULL, 2, NULL, 0, 'Let candy melts dry before packaging or serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0153567C3BA354AA56FC7E650933134', X'B61618527EBA234483F17A753730E007', X'B61618527EBA234483F17A753730E007', 0, NULL, NULL, 2, NULL, 0, 'Pour blue Candy Melts into microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62DC24489B175E4CAA5F4961E49FB199', X'B61618527EBA234483F17A753730E007', X'B61618527EBA234483F17A753730E007', 1, NULL, NULL, 2, NULL, 0, 'Pour remaining 7 ounces of sweetened condensed milk over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'889F647375321246B136DF7DB4377CDD', X'B61618527EBA234483F17A753730E007', X'B61618527EBA234483F17A753730E007', 2, NULL, NULL, 2, NULL, 0, 'Repeat process above.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FF793B745DC9C4B9D09FA1E598EC687', X'B44FFBBABFF8E743B4D758BB56C0D3DF', X'B44FFBBABFF8E743B4D758BB56C0D3DF', 0, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22617D651505CD4586837206C892F2C7', X'B44FFBBABFF8E743B4D758BB56C0D3DF', X'B44FFBBABFF8E743B4D758BB56C0D3DF', 1, NULL, NULL, 2, NULL, 0, 'Pour popcorn into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6628E0E82BF1D44A1D49111583492EB', X'B44FFBBABFF8E743B4D758BB56C0D3DF', X'B44FFBBABFF8E743B4D758BB56C0D3DF', 2, NULL, NULL, 2, NULL, 0, 'Pour white chocolate over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7809916B09CF2046B82C41AA3757031A', X'B44FFBBABFF8E743B4D758BB56C0D3DF', X'B44FFBBABFF8E743B4D758BB56C0D3DF', 3, NULL, NULL, 2, NULL, 0, 'Quickly stir it and toss it to coat the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE0E19E9B98F424FABC892A445D64723', X'B44FFBBABFF8E743B4D758BB56C0D3DF', X'B44FFBBABFF8E743B4D758BB56C0D3DF', 4, NULL, NULL, 2, NULL, 0, 'Pour out onto the baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87AC9FB6981F154599B49A0B5010B298', X'B44FFBBABFF8E743B4D758BB56C0D3DF', X'B44FFBBABFF8E743B4D758BB56C0D3DF', 5, NULL, NULL, 2, NULL, 0, 'Drizzle with colored confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80E19CE8CF92ED45A41ACB5E19C07CE4', X'B44FFBBABFF8E743B4D758BB56C0D3DF', X'B44FFBBABFF8E743B4D758BB56C0D3DF', 6, NULL, NULL, 2, NULL, 0, 'Place chocolate almond footballs all around popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E52750F71DF1C8418179E3BC3495595D', X'B44FFBBABFF8E743B4D758BB56C0D3DF', X'B44FFBBABFF8E743B4D758BB56C0D3DF', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3919F6C2D4862458537E7F11EB01883', X'B44FFBBABFF8E743B4D758BB56C0D3DF', X'B44FFBBABFF8E743B4D758BB56C0D3DF', 8, NULL, NULL, 2, NULL, 0, 'Remove and break up into bite size chunks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A781FC03CA3E94BA1ED6E89D4CBE42D', X'6375D2E9FE11F24ABEE78DF7001FE228', X'6375D2E9FE11F24ABEE78DF7001FE228', 0, NULL, NULL, 2, NULL, 0, 'Melt butter in a large microwave safe bowl on high power for 45-75 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04C5C0EA649615409E08E376AE9BD315', X'6375D2E9FE11F24ABEE78DF7001FE228', X'6375D2E9FE11F24ABEE78DF7001FE228', 1, NULL, NULL, 2, NULL, 0, 'Stir in dark brown sugar, corn syrup and sweetened condensed milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3313A71A3403124CA995CA95CC092B3A', X'6375D2E9FE11F24ABEE78DF7001FE228', X'6375D2E9FE11F24ABEE78DF7001FE228', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90303B205B98CD418F868956283A3D50', X'6375D2E9FE11F24ABEE78DF7001FE228', X'6375D2E9FE11F24ABEE78DF7001FE228', 3, NULL, NULL, 2, NULL, 0, 'Remove and whisk until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42523868A515DB4286F7EF50F7719C5B', X'6375D2E9FE11F24ABEE78DF7001FE228', X'6375D2E9FE11F24ABEE78DF7001FE228', 4, NULL, NULL, 2, NULL, 0, 'Heat for 5 more minutes, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9336157266BBC7448BCECA9B5ED7F7C4', X'6375D2E9FE11F24ABEE78DF7001FE228', X'6375D2E9FE11F24ABEE78DF7001FE228', 5, NULL, NULL, 2, NULL, 0, 'Heat for an additional 5 minutes, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9775C3F1C871A847BCEA0500546EBF4D', X'6375D2E9FE11F24ABEE78DF7001FE228', X'6375D2E9FE11F24ABEE78DF7001FE228', 6, NULL, NULL, 2, NULL, 0, 'Check temperature, if it''s between 245 - 250 degrees F, pour it into the oval shaped molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6899B3D7877DDC4793BEC6C552879418', X'6375D2E9FE11F24ABEE78DF7001FE228', X'6375D2E9FE11F24ABEE78DF7001FE228', 7, NULL, NULL, 2, NULL, 0, 'If needed, heat for 1 minute increments, stirring after each until desired temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1233CF3B29CCFE458D6638A4D3D8348C', X'6375D2E9FE11F24ABEE78DF7001FE228', X'6375D2E9FE11F24ABEE78DF7001FE228', 8, NULL, NULL, 2, NULL, 0, 'Let caramels harden for 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B90436025FD0D043995F4D36F24A4267', X'6375D2E9FE11F24ABEE78DF7001FE228', X'6375D2E9FE11F24ABEE78DF7001FE228', 9, NULL, NULL, 2, NULL, 0, 'Un-mold and set on a silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6206A2AAE53ED14E83D568A652823273', X'6375D2E9FE11F24ABEE78DF7001FE228', X'6375D2E9FE11F24ABEE78DF7001FE228', 10, NULL, NULL, 2, NULL, 0, 'Pipe white chocolate laces over each football.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1DD577D76493342AC6F20F9E322F3A9', X'830C9C05A4EAED4391799EE77AFDB207', X'830C9C05A4EAED4391799EE77AFDB207', 0, NULL, NULL, 2, NULL, 0, 'Pour finely chopped milk chocolate into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA99E0BDBE39F346BC48C97B73C46F9D', X'830C9C05A4EAED4391799EE77AFDB207', X'830C9C05A4EAED4391799EE77AFDB207', 1, NULL, NULL, 2, NULL, 0, 'Pour caramel flavored sweetened condensed milk over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CA574C9EB22074FBC320422FC93A8D2', X'830C9C05A4EAED4391799EE77AFDB207', X'830C9C05A4EAED4391799EE77AFDB207', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power for 45 seconds then remove and fold about 10 times. Let the bowl sit for 3 minutes, then fold a few more times.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E198221C1A8644196A7527084AE1924', X'830C9C05A4EAED4391799EE77AFDB207', X'830C9C05A4EAED4391799EE77AFDB207', 3, NULL, NULL, 2, NULL, 0, 'Heat for 30 more seconds, then let the bowl sit in the microwave for 3 minutes. Remove and fold 10 times. Let sit for 2 more minutes, then stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A0BC9E3B44A0E4AA9B5E9BCC814F83C', X'830C9C05A4EAED4391799EE77AFDB207', X'830C9C05A4EAED4391799EE77AFDB207', 4, NULL, NULL, 2, NULL, 0, 'If needed, you can heat for 10 second increments if not all melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'117FD8D4413F0141AC15A785872742EB', X'830C9C05A4EAED4391799EE77AFDB207', X'830C9C05A4EAED4391799EE77AFDB207', 5, NULL, NULL, 2, NULL, 0, 'Cover the fudge and let it rest for 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D4743AA94EDC94DB392BE059A742A25', X'830C9C05A4EAED4391799EE77AFDB207', X'830C9C05A4EAED4391799EE77AFDB207', 6, NULL, NULL, 2, NULL, 0, 'Scoop out tablespoon sized balls of fudge. Roll into smooth balls then shape into footballs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E694AB00E375D548B4782DBB3D9755A3', X'830C9C05A4EAED4391799EE77AFDB207', X'830C9C05A4EAED4391799EE77AFDB207', 7, NULL, NULL, 2, NULL, 0, 'Add white chocolate laces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'716160637845E944BED00302732C0AB1', X'DFA703035CD8F84A8504F3CF609D87AB', X'DFA703035CD8F84A8504F3CF609D87AB', 0, NULL, NULL, 2, NULL, 0, 'Peel and slice potato into 1/4 inch thick slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2119EE261EA334D99ECA197E3350241', X'DFA703035CD8F84A8504F3CF609D87AB', X'DFA703035CD8F84A8504F3CF609D87AB', 1, NULL, NULL, 2, NULL, 0, 'Cut into small 1 1/2 - 2 inch long footballs (ovals.)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24EF3EE7EC71F640A7E155B87EFD14C2', X'DFA703035CD8F84A8504F3CF609D87AB', X'DFA703035CD8F84A8504F3CF609D87AB', 2, NULL, NULL, 2, NULL, 0, 'Use a toothpick to carve the football laces in each potato.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7BE6239CBF90346879A205479AFC253', X'DFA703035CD8F84A8504F3CF609D87AB', X'DFA703035CD8F84A8504F3CF609D87AB', 3, NULL, NULL, 2, NULL, 0, 'Pour Swanson® Chicken Broth into 3 quart pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3761170BF142C6439CBB276E175FB615', X'DFA703035CD8F84A8504F3CF609D87AB', X'DFA703035CD8F84A8504F3CF609D87AB', 4, NULL, NULL, 2, NULL, 0, 'Add potatoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A83846F9B77964F9C16DCC340352C1A', X'DFA703035CD8F84A8504F3CF609D87AB', X'DFA703035CD8F84A8504F3CF609D87AB', 5, NULL, NULL, 2, NULL, 0, 'Heat on high until the broth comes to a boil then lower heat to low and simmer for 10-15 minutes until the potatoes are tender.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'083E6C2AE7EA594AB1AA9D5E2CC1FA50', X'DFA703035CD8F84A8504F3CF609D87AB', X'DFA703035CD8F84A8504F3CF609D87AB', 6, NULL, NULL, 2, NULL, 0, 'Remove and pat potatoes dry with a paper towel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B3041E057B0FF44AD62DA2E1CC55771', X'DFA703035CD8F84A8504F3CF609D87AB', X'DFA703035CD8F84A8504F3CF609D87AB', 7, NULL, NULL, 2, NULL, 0, 'Melt 1 tablespoon butter and 1 1/2 tablespoons oil in a 12 inch skillet set over medium heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9ABC2777892A5648889C2BD33D25AE9B', X'DFA703035CD8F84A8504F3CF609D87AB', X'DFA703035CD8F84A8504F3CF609D87AB', 8, NULL, NULL, 2, NULL, 0, 'Add a single layer of potatoes to the pan and cook for about 4 minutes on each side until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3224EF162D3C843ADEC14BCB2656B32', X'DFA703035CD8F84A8504F3CF609D87AB', X'DFA703035CD8F84A8504F3CF609D87AB', 9, NULL, NULL, 2, NULL, 0, 'Season with salt immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F200C67B33DF1498D50B3A5DD98EEA9', X'DFA703035CD8F84A8504F3CF609D87AB', X'DFA703035CD8F84A8504F3CF609D87AB', 10, NULL, NULL, 2, NULL, 0, 'Repeat, heating the remaining butter and oil and frying the remaining potatoes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F946C0B2D79054F8654E4D9709A6B02', X'DFA703035CD8F84A8504F3CF609D87AB', X'DFA703035CD8F84A8504F3CF609D87AB', 11, NULL, NULL, 2, NULL, 0, 'If needed, reheat in a 400 degree oven for 6-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B23FE5D82A1674B9458E3FB3CBB3E27', X'8DF976257DA75E4EBB89378DE01D10C4', X'8DF976257DA75E4EBB89378DE01D10C4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F14A8D921CD0148916DA01E5C934E9E', X'8DF976257DA75E4EBB89378DE01D10C4', X'8DF976257DA75E4EBB89378DE01D10C4', 1, NULL, NULL, 2, NULL, 0, 'Cook bacon in a skillet set over medium heat until crisp.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2823DE4B5C8A4F46B40118E45BD42DD1', X'8DF976257DA75E4EBB89378DE01D10C4', X'8DF976257DA75E4EBB89378DE01D10C4', 2, NULL, NULL, 2, NULL, 0, 'Remove bacon using a slotted spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7324398E561B634393AADCB408DAF97E', X'8DF976257DA75E4EBB89378DE01D10C4', X'8DF976257DA75E4EBB89378DE01D10C4', 3, NULL, NULL, 2, NULL, 0, 'Add the mushrooms to the pan and saute until nicely browned.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3014EC10051C040BE9812F68461673A', X'8DF976257DA75E4EBB89378DE01D10C4', X'8DF976257DA75E4EBB89378DE01D10C4', 4, NULL, NULL, 2, NULL, 0, 'Remove from pan and add the ground beef, salt, pepper, and Worcestershire sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'016756B0B7B05141B770A4B760837B74', X'8DF976257DA75E4EBB89378DE01D10C4', X'8DF976257DA75E4EBB89378DE01D10C4', 5, NULL, NULL, 2, NULL, 0, 'Cook until browned.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50531FFEEB4660439B5F4A57F0AF0813', X'8DF976257DA75E4EBB89378DE01D10C4', X'8DF976257DA75E4EBB89378DE01D10C4', 6, NULL, NULL, 2, NULL, 0, 'Remove pan from heat and stir in the mushrooms, cream cheese and sour cream. Stir in half of the cheddar cheese, Monterey Jack cheese, and bacon. Pour into a baking dish and sprinkle on the remaining cheese and bacon. Bake for 20-30 minutes until bubbly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'210C454C68363F4C9EA7399FB7079D3C', X'8DF976257DA75E4EBB89378DE01D10C4', X'8DF976257DA75E4EBB89378DE01D10C4', 7, NULL, NULL, 2, NULL, 0, 'Top with a goal post made out of the Cheddar Cheese Cheez-It Crunch''d.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1110C0F28E5B2949BE7532792F323B29', X'8DF976257DA75E4EBB89378DE01D10C4', X'8DF976257DA75E4EBB89378DE01D10C4', 8, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'994B761FDC36C7419CCF5AFE069746FE', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', 0, NULL, NULL, 2, NULL, 0, 'Stir the French Onion Dip and buttermilk together in a shallow baking dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8D5BC2676C2A3418AB68CD279614663', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', 1, NULL, NULL, 2, NULL, 0, 'Place the chicken tenders in the dish, making sure they are covered with the dip mixture. Cover and refrigerate for 30-60 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C88E31BCA3F74B43AE6C66F0A0EAE828', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', 2, NULL, NULL, 2, NULL, 0, 'Crush the potato chips into fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31684240E502874B9834BABB0F7AEA94', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', 3, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD1BE8FE876F1A43AB29958D923D8862', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', 4, NULL, NULL, 2, NULL, 0, 'Spray a cooling rack with non-stick spray. Set it over a tin foil lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'790202EE30480E4BAEF0E1C0BCA3AC3C', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', 5, NULL, NULL, 2, NULL, 0, 'Remove chicken tenders from the marinade. Allow excess liquid to drain off. Season with salt and pepper, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24B39F3FE1F3E540BC6C47B4314C6868', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', 6, NULL, NULL, 2, NULL, 0, 'Skewer the chicken tenders, then coat them in the crushed potato chips. Set on the wire cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8AAF513BDE91A4E9C4D27B64642D60B', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', 7, NULL, NULL, 2, NULL, 0, 'Spray the chicken tenders with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06F1412E85A6A14B8D2072E2D3FB73F1', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', 8, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD779B78D8FF6C49BFFD898DB9E3AB30', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', X'D4F9AAFA88FAF2448AAEB96F13B8FAB8', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately with the remaining French onion dip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E6BC840B1A76B4C897B9E0587F46979', X'9C63B86300D52E4E8790291D6BD553B3', X'9C63B86300D52E4E8790291D6BD553B3', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABB04FE7A6C4F142B1C9B9AE235EA7FC', X'9C63B86300D52E4E8790291D6BD553B3', X'9C63B86300D52E4E8790291D6BD553B3', 1, NULL, NULL, 2, NULL, 0, 'Stir together mashed potatoes, egg, cheddar cheese, and bacon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C44D9BB24EEEA44CAB700FA8C55E7FF2', X'9C63B86300D52E4E8790291D6BD553B3', X'9C63B86300D52E4E8790291D6BD553B3', 2, NULL, NULL, 2, NULL, 0, 'Grease the football cavities with butter. Sprinkle about a tablespoon of crushed Doritos into each football cavity.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64B1127099A77543B09F1F45EACBBFC5', X'9C63B86300D52E4E8790291D6BD553B3', X'9C63B86300D52E4E8790291D6BD553B3', 3, NULL, NULL, 2, NULL, 0, 'Spoon an even layer of mashed potatoes into each football (about 2 tablespoons.)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DEBDD760C7D6F344A40C5D082A27347E', X'9C63B86300D52E4E8790291D6BD553B3', X'9C63B86300D52E4E8790291D6BD553B3', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle more Doritos crumbs over the mashed potatoes. Press the crumbs into the potatoes using your fingers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94F4F10DF778B440BCFB297B49710B17', X'9C63B86300D52E4E8790291D6BD553B3', X'9C63B86300D52E4E8790291D6BD553B3', 5, NULL, NULL, 2, NULL, 0, 'Bake for 15 minutes. Remove footballs from mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68844975AFB9374F8BB98B62F6C7B3B9', X'9C63B86300D52E4E8790291D6BD553B3', X'9C63B86300D52E4E8790291D6BD553B3', 6, NULL, NULL, 2, NULL, 0, 'Heat a thin layer of oil over medium heat. Fry footballs until golden brown, about 30 seconds on each side. Drain on paper towels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E231FADBD5881D41B9D202EFDC9E8A7C', X'9C63B86300D52E4E8790291D6BD553B3', X'9C63B86300D52E4E8790291D6BD553B3', 7, NULL, NULL, 2, NULL, 0, 'Pipe sour cream laces and stripes onto footballs. Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'293D67B2966A2C42B54F80E4D5D913AF', X'40105763520AD146A5FAF88814CE8CA8', X'40105763520AD146A5FAF88814CE8CA8', 0, NULL, NULL, 2, NULL, 0, 'Pour candy melts into a microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E38863D402BA0442AC6FB3B88BFBF5F2', X'40105763520AD146A5FAF88814CE8CA8', X'40105763520AD146A5FAF88814CE8CA8', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 30 seconds, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0762A202FEED9642AE3FB23E9D535BB4', X'40105763520AD146A5FAF88814CE8CA8', X'40105763520AD146A5FAF88814CE8CA8', 2, NULL, NULL, 2, NULL, 0, 'Heat for another 30 seconds, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F438DCD0BF16E43B55FF95A7A54F844', X'40105763520AD146A5FAF88814CE8CA8', X'40105763520AD146A5FAF88814CE8CA8', 3, NULL, NULL, 2, NULL, 0, 'Then heat for 20 seconds, then let the candy coating sit in the microwave for 2 minutes, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5C9E6AF10A7DB4B913AE53FC0EDC26C', X'40105763520AD146A5FAF88814CE8CA8', X'40105763520AD146A5FAF88814CE8CA8', 4, NULL, NULL, 2, NULL, 0, 'If all the wafers have not melted, heat for 10-second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2E51DC256BC1A4FBE21F0FE8350CA25', X'40105763520AD146A5FAF88814CE8CA8', X'40105763520AD146A5FAF88814CE8CA8', 5, NULL, NULL, 2, NULL, 0, 'If using white candy melts, stir in some yellow candy coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE88A51065678C4595B642CA5010255C', X'40105763520AD146A5FAF88814CE8CA8', X'40105763520AD146A5FAF88814CE8CA8', 6, NULL, NULL, 2, NULL, 0, 'Set a piece of parchment paper on a baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A130E03E6AC5A24A865C4CB8CB7EEAD9', X'40105763520AD146A5FAF88814CE8CA8', X'40105763520AD146A5FAF88814CE8CA8', 7, NULL, NULL, 2, NULL, 0, 'Arrange the pretzel rounds on the baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F460D569C932F409DE836ACF3CE6739', X'40105763520AD146A5FAF88814CE8CA8', X'40105763520AD146A5FAF88814CE8CA8', 8, NULL, NULL, 2, NULL, 0, 'Pour a few tablespoons of the yellow candy coating into a zip-top bag, and snip off one tip making a small hole.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F5E7CE42B36D249A0080F5747060292', X'40105763520AD146A5FAF88814CE8CA8', X'40105763520AD146A5FAF88814CE8CA8', 9, NULL, NULL, 2, NULL, 0, 'Pipe the yellow candy coating into a few of the pretzels, then immediately, place two mini chocolate chips in the wet coating to create the smiley face’s eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'548C1BA0161C174AAEAB0133FA4AB403', X'40105763520AD146A5FAF88814CE8CA8', X'40105763520AD146A5FAF88814CE8CA8', 10, NULL, NULL, 2, NULL, 0, 'Continue filling pretzels and adding chocolate chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'120980C94613474F831A9442026D2EAA', X'40105763520AD146A5FAF88814CE8CA8', X'40105763520AD146A5FAF88814CE8CA8', 11, NULL, NULL, 2, NULL, 0, 'Pop the tray in the freezer for about 3 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6D8ED96E81FDC408A899091B5A6D910', X'40105763520AD146A5FAF88814CE8CA8', X'40105763520AD146A5FAF88814CE8CA8', 12, NULL, NULL, 2, NULL, 0, 'After you remove the pan from the freezer, allow the pretzels to come to room temperature for about 10 minutes, then use a black food coloring marker to draw on smiles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'888226A029280E47BD0656377D80D46F', X'96605257BE314E439940FF6B9819A366', X'96605257BE314E439940FF6B9819A366', 0, NULL, NULL, 2, NULL, 0, 'If using a polycarbonate half-sphere mold, a single cavity silicone mold, or a plastic ornament mold, fill the mold with chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71F217DA60F6154B89586998729FB605', X'96605257BE314E439940FF6B9819A366', X'96605257BE314E439940FF6B9819A366', 1, NULL, NULL, 2, NULL, 0, 'Tap the mold a few times to allow air bubbles to come to the surface and pop.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD67A40D215EE54EA4452C5FE13E0EF5', X'96605257BE314E439940FF6B9819A366', X'96605257BE314E439940FF6B9819A366', 2, NULL, NULL, 2, NULL, 0, 'Invert the mold over a bowl of chocolate and tap the side of the mold allowing the excess chocolate to drain out of the cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCEC60DF854A07449E18E8B5EBD79F07', X'96605257BE314E439940FF6B9819A366', X'96605257BE314E439940FF6B9819A366', 3, NULL, NULL, 2, NULL, 0, 'Scrape off the excess chocolate using a metal spatula or bench scraper to clean the edges of the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE1D499C5F459C4E9C09866EA5192DA9', X'96605257BE314E439940FF6B9819A366', X'96605257BE314E439940FF6B9819A366', 4, NULL, NULL, 2, NULL, 0, 'If the chocolate is thick you can leave the molds upright to chill. If the chocolate is thin it''s best to turn the mold upside down and set it on a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E56F9C1BDA91543ACFEE0E5719A2E06', X'96605257BE314E439940FF6B9819A366', X'96605257BE314E439940FF6B9819A366', 5, NULL, NULL, 2, NULL, 0, 'If using pure chocolate and your room is cool you can allow your chocolate to air dry or you can chill it in the refrigerator until hardened, 5-10 minutes. If using compound chocolate (candy melts) place the mold in the freezer for 3-5 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D9D1F738971A749AA2DC15D90DEAAC9', X'96605257BE314E439940FF6B9819A366', X'96605257BE314E439940FF6B9819A366', 6, NULL, NULL, 2, NULL, 0, 'If using a silicone mold with multiple cavities, fill about 1/3rd of a cavity with chocolate, and use a brush to paint the chocolate up the sides of the mold. Chill and if needed add a second coat of chocolate around the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CF11537049884468C8F3C88DFE58A61', X'96605257BE314E439940FF6B9819A366', X'96605257BE314E439940FF6B9819A366', 7, NULL, NULL, 2, NULL, 0, 'Make a total of 12 chocolate half-spheres and reserve the remaining chocolate for drizzling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'505E4878114F044487338F0EEBC6696F', X'96605257BE314E439940FF6B9819A366', X'96605257BE314E439940FF6B9819A366', 8, NULL, NULL, 2, NULL, 0, 'Fill 6 of the chocolate half-spheres with 2-4 tablespoons of hot cocoa mix or chocolate ganache.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F1507D54BF99A4A981CF5E87CE87B89', X'96605257BE314E439940FF6B9819A366', X'96605257BE314E439940FF6B9819A366', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle mini marshmallows over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'959AB790D0076C4BB5CFC0ECCB6B29FD', X'96605257BE314E439940FF6B9819A366', X'96605257BE314E439940FF6B9819A366', 10, NULL, NULL, 2, NULL, 0, 'Heat a skillet over low heat for 20-30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FFA4B46A30DC78449C2A25F826A1F25E', X'96605257BE314E439940FF6B9819A366', X'96605257BE314E439940FF6B9819A366', 11, NULL, NULL, 2, NULL, 0, 'Melt the top edge of one of the empty chocolates and place it over top of a filled chocolate. Press gently to seal the two pieces together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44C82756F5666243ADE9224EC08C9340', X'96605257BE314E439940FF6B9819A366', X'96605257BE314E439940FF6B9819A366', 12, NULL, NULL, 2, NULL, 0, 'Drizzle chocolate over top of your hot chocolate bombs and decorate with sprinkles or other fun toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95A92F084243334CB19F90ACE13FC418', X'B2FD98F9698120418314CAD2711252A7', X'B2FD98F9698120418314CAD2711252A7', 0, NULL, NULL, 2, NULL, 0, 'Finely chop or shave your chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D74C7BA27FE614C811FD291F8EB65B5', X'B2FD98F9698120418314CAD2711252A7', X'B2FD98F9698120418314CAD2711252A7', 1, NULL, NULL, 2, NULL, 0, 'Heat the heavy whipping cream in a saucepan over medium heat just until bubbles begin to form around the edges of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0661F3A4831BAC4E986B03C737BF571D', X'2FB09638494E9F409B74D21E2AFF8A2D', X'2FB09638494E9F409B74D21E2AFF8A2D', 0, NULL, NULL, 2, NULL, 0, 'Pour chopped chocolate into a shallow bowl, creating a small well in the center of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A28612D7AA80944DAD64D0C7CB9E0681', X'2FB09638494E9F409B74D21E2AFF8A2D', X'2FB09638494E9F409B74D21E2AFF8A2D', 1, NULL, NULL, 2, NULL, 0, 'Pour the hot liquid over the chocolate allowing the cream to flow into the center of the bowl and underneath the chocolate. Make sure all the chocolate is submerged under the cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B62E10378FDD514DBE5DD560ADDEB758', X'2FB09638494E9F409B74D21E2AFF8A2D', X'2FB09638494E9F409B74D21E2AFF8A2D', 2, NULL, NULL, 2, NULL, 0, 'Let sit for 3 minutes allowing the chocolate to begin to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34F4651DB7A06C4FB412DF74DEE2E505', X'2FB09638494E9F409B74D21E2AFF8A2D', X'2FB09638494E9F409B74D21E2AFF8A2D', 3, NULL, NULL, 2, NULL, 0, 'Begin stirring in a small circle in the center of the bowl to create an emulsion of the chocolate and cream then widen the circle slowly until all of the chocolate and cream blend together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB23ECBC540361409405D464F1101BB3', X'2FB09638494E9F409B74D21E2AFF8A2D', X'2FB09638494E9F409B74D21E2AFF8A2D', 4, NULL, NULL, 2, NULL, 0, 'As soon as the chocolate and cream come together, and your chocolate ganache looks shiny and smooth, stop stirring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'600E1782695C314ABC75F8EAD4B76DFC', X'2FB09638494E9F409B74D21E2AFF8A2D', X'2FB09638494E9F409B74D21E2AFF8A2D', 5, NULL, NULL, 2, NULL, 0, 'Cover the ganache directly with plastic wrap or with a tight-fitting lid and set aside until it firms up, at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5309410D7569EC468F007A0EE43DFA03', X'3B4159B50AFF064EA15E9053CC31511D', X'3B4159B50AFF064EA15E9053CC31511D', 0, NULL, NULL, 2, NULL, 0, 'Pour the hot cream over the chopped chocolate and close the lid.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEED11234D5B4B439EB6F28D84FD2615', X'3B4159B50AFF064EA15E9053CC31511D', X'3B4159B50AFF064EA15E9053CC31511D', 1, NULL, NULL, 2, NULL, 0, 'Pulse twice then remove the lid and scrape the sides of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A92AC889FE03AC4A8CE8C822EA69F442', X'3B4159B50AFF064EA15E9053CC31511D', X'3B4159B50AFF064EA15E9053CC31511D', 2, NULL, NULL, 2, NULL, 0, 'Replace the lid and let the chocolate melt for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE04AEB4FB29964491135E5D23986483', X'3B4159B50AFF064EA15E9053CC31511D', X'3B4159B50AFF064EA15E9053CC31511D', 3, NULL, NULL, 2, NULL, 0, 'Pulse 3-6 times just until the chocolate and cream come together and looks glossy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51A8A98110924849A5B23AA638B61762', X'3B4159B50AFF064EA15E9053CC31511D', X'3B4159B50AFF064EA15E9053CC31511D', 4, NULL, NULL, 2, NULL, 0, 'Pour into a bowl, cover, and let rest at room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43DD1294B1265947BAED86E18DD420F6', X'581ADD1C5D92E04D8C09CB65106058CA', X'581ADD1C5D92E04D8C09CB65106058CA', 0, NULL, NULL, 2, NULL, 0, 'Pour 1/4 of the hot cream into a tall immersion blender cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'664A3B033C06E3489A6C7E26D6B2A4E2', X'581ADD1C5D92E04D8C09CB65106058CA', X'581ADD1C5D92E04D8C09CB65106058CA', 1, NULL, NULL, 2, NULL, 0, 'Add the finely chopped chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4621B60E21FDCA48A04174291B4D6CD8', X'581ADD1C5D92E04D8C09CB65106058CA', X'581ADD1C5D92E04D8C09CB65106058CA', 2, NULL, NULL, 2, NULL, 0, 'Pour the remaining hot cream over top of the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE403C2AA12FFF4ABC2986BBC287A0B2', X'581ADD1C5D92E04D8C09CB65106058CA', X'581ADD1C5D92E04D8C09CB65106058CA', 3, NULL, NULL, 2, NULL, 0, 'Set it aside for about 2 minutes to allow the chocolate to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E55C005F75B9C34A8BE3CD1D0BACEC67', X'581ADD1C5D92E04D8C09CB65106058CA', X'581ADD1C5D92E04D8C09CB65106058CA', 4, NULL, NULL, 2, NULL, 0, 'Use the immersion blender to combine the chocolate and cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D39D81CBBE768B4580487169511762F5', X'581ADD1C5D92E04D8C09CB65106058CA', X'581ADD1C5D92E04D8C09CB65106058CA', 5, NULL, NULL, 2, NULL, 0, 'Scrape the sides of the cup and pulse again just until the ganache is smooth, glossy, and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B50F8982AF904469F2680A0F518DBC9', X'581ADD1C5D92E04D8C09CB65106058CA', X'581ADD1C5D92E04D8C09CB65106058CA', 6, NULL, NULL, 2, NULL, 0, 'Pour into a shallow bowl, cover, and set aside for at least 4 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA1385EF7DC5174984FEE91B05A0CFBE', X'F861721C4A804444A18AC5BBCF747DB1', X'F861721C4A804444A18AC5BBCF747DB1', 0, NULL, NULL, 2, NULL, 0, 'Using a small ice cream scoop or spoon, scoop the ganache onto a parchment paper-lined baking sheet and let rest for about 30 minutes until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAACB0763762394E9D1F3FD525AF54A1', X'F861721C4A804444A18AC5BBCF747DB1', X'F861721C4A804444A18AC5BBCF747DB1', 1, NULL, NULL, 2, NULL, 0, 'Roll a scoop of ganache into a ball then immediately roll into the toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'292217C3FADF554BB2BB1B838D11092F', X'F861721C4A804444A18AC5BBCF747DB1', X'F861721C4A804444A18AC5BBCF747DB1', 2, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6FA2A779F85504E993D157D7B6B5F2D', X'01459703A86494439F22F8FE9B4CE83A', X'01459703A86494439F22F8FE9B4CE83A', 0, NULL, NULL, 2, NULL, 0, 'Pour red candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'060EA952F4677F42950CA3FC4705C7C4', X'01459703A86494439F22F8FE9B4CE83A', X'01459703A86494439F22F8FE9B4CE83A', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power in the microwave for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7763F09D414AD645AB7412466F2B823B', X'01459703A86494439F22F8FE9B4CE83A', X'01459703A86494439F22F8FE9B4CE83A', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F190B119EC1144D8E82CB779ECC3785', X'01459703A86494439F22F8FE9B4CE83A', X'01459703A86494439F22F8FE9B4CE83A', 3, NULL, NULL, 2, NULL, 0, 'Heat for 15-20 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'575BD8F8CECF384CB1B3596479A29666', X'01459703A86494439F22F8FE9B4CE83A', X'01459703A86494439F22F8FE9B4CE83A', 4, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of red candy melts on the bottom and up the sides of a 2 ounce plastic shot glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16A5495EDC6CAD469673867C7BDBC4AB', X'01459703A86494439F22F8FE9B4CE83A', X'01459703A86494439F22F8FE9B4CE83A', 5, NULL, NULL, 2, NULL, 0, 'Wipe the top edge of the glass of any candy that has dripped over the edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04F6DD603885854E9A62BFB86E1EFFA4', X'01459703A86494439F22F8FE9B4CE83A', X'01459703A86494439F22F8FE9B4CE83A', 6, NULL, NULL, 2, NULL, 0, 'Freeze for 90 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73D168455BADDF4CB18F97EC66537A1E', X'01459703A86494439F22F8FE9B4CE83A', X'01459703A86494439F22F8FE9B4CE83A', 7, NULL, NULL, 2, NULL, 0, 'Remove from freezer and brush a second layer of red candy melts over the first layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B66B08332189F4FA80F16CDB536BB44', X'01459703A86494439F22F8FE9B4CE83A', X'01459703A86494439F22F8FE9B4CE83A', 8, NULL, NULL, 2, NULL, 0, 'Freeze for 2-3 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D83C1E53EDBFC1428FD87E4A229A6870', X'01459703A86494439F22F8FE9B4CE83A', X'01459703A86494439F22F8FE9B4CE83A', 9, NULL, NULL, 2, NULL, 0, 'Remove and slide the candy cup out of the plastic shot glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2935C585F9B92F46B4359F14697B47D5', X'01459703A86494439F22F8FE9B4CE83A', X'01459703A86494439F22F8FE9B4CE83A', 10, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 6 candy cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'161E5EEE1C99844EBB762A87F14F222D', X'01459703A86494439F22F8FE9B4CE83A', X'01459703A86494439F22F8FE9B4CE83A', 11, NULL, NULL, 2, NULL, 0, 'Roll white modeling chocolate into long thin logs, or use a clay extruder to create the logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4F82335B299444FB8A0B6F562EBFBD4', X'01459703A86494439F22F8FE9B4CE83A', X'01459703A86494439F22F8FE9B4CE83A', 12, NULL, NULL, 2, NULL, 0, 'Use clear piping gel (or remaining red candy melts) to attach a log of white modeling chocolate around the top edge of a candy cup. Cut off any excess and pinch the two seams together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'253A743EC1DB07479BEFDEF8BA27CFC9', X'B2CADED0978973439FD37EE964A55789', X'B2CADED0978973439FD37EE964A55789', 0, NULL, NULL, 2, NULL, 0, 'Pour finely chopped chocolate into a shallow bowl');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B860EA1125BB0409F812DF7D0B74344', X'B2CADED0978973439FD37EE964A55789', X'B2CADED0978973439FD37EE964A55789', 1, NULL, NULL, 2, NULL, 0, 'Heat your cream over medium heat to a simmer on the stove, just until bubbles begin to form then immediately pour the hot cream over the chocolate, making sure it covers all of the chocolate. Let the chocolate and cream sit, undisturbed for 3 minutes to allow the chocolate to begin to melt. Then begin stirring the mixture in small circles in the center of the bowl. As the chocolate and cream start to darken and come together you can begin to make your circle bigger. Continue stirring until the cream and chocolate come together then widen the circle again and continue until the entire bowl of cream comes together. Be sure to scrape the bottom and sides of the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FA33F77C84E824990F0EACE6676601D', X'B2CADED0978973439FD37EE964A55789', X'B2CADED0978973439FD37EE964A55789', 2, NULL, NULL, 2, NULL, 0, 'Add 16 drops of cola flavoring. Taste. Add more if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'663A671028EADD44B3DD4F776A1582B3', X'B2CADED0978973439FD37EE964A55789', X'B2CADED0978973439FD37EE964A55789', 3, NULL, NULL, 2, NULL, 0, 'Pour the chocolate cola ganache into the red candy cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'176B5681FAF0A244A5CF48AC826353A5', X'B2CADED0978973439FD37EE964A55789', X'B2CADED0978973439FD37EE964A55789', 4, NULL, NULL, 2, NULL, 0, 'Optional: Cut straws into shorter pieces and insert one into each cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9415001EBE516B449DCDE3C459608FAC', X'B2CADED0978973439FD37EE964A55789', X'B2CADED0978973439FD37EE964A55789', 5, NULL, NULL, 2, NULL, 0, 'Let sit at room temperature for at least an hour so the ganache can set up before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA146F6F4C982A4791C79F8F488F9755', X'D12A6F376648DB41A3BDCB86844739A9', X'D12A6F376648DB41A3BDCB86844739A9', 0, NULL, NULL, 2, NULL, 0, 'Pour white chocolate chips into a microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89E2A86CD787D24E9EB25DDD7A257CD4', X'D12A6F376648DB41A3BDCB86844739A9', X'D12A6F376648DB41A3BDCB86844739A9', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power, for 30 seconds, then remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC949942C3AAA54181DB80110205F687', X'D12A6F376648DB41A3BDCB86844739A9', X'D12A6F376648DB41A3BDCB86844739A9', 2, NULL, NULL, 2, NULL, 0, 'Heat for two more 30 second bursts on high power, stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'402C640A90485A4A9372AB71216491FF', X'D12A6F376648DB41A3BDCB86844739A9', X'D12A6F376648DB41A3BDCB86844739A9', 3, NULL, NULL, 2, NULL, 0, 'Let the white chocolate sit for 2 minutes then stir again.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'944D50F022B93F4F9EC9B5D41DBF4BE4', X'D12A6F376648DB41A3BDCB86844739A9', X'D12A6F376648DB41A3BDCB86844739A9', 4, NULL, NULL, 2, NULL, 0, 'If not completely melted, heat for 10-20 more seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'665BE0DD7A40514B93DDD077783F605A', X'D12A6F376648DB41A3BDCB86844739A9', X'D12A6F376648DB41A3BDCB86844739A9', 5, NULL, NULL, 2, NULL, 0, 'Stir in the Biscoff Cookie Spread.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65D638850ECBD1438527EA5B471F03FF', X'D12A6F376648DB41A3BDCB86844739A9', X'D12A6F376648DB41A3BDCB86844739A9', 6, NULL, NULL, 2, NULL, 0, 'Place a piece of plastic wrap directly on top of the mixture and set aside for several hours until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F51E80658695A4FA776D51E23127952', X'D12A6F376648DB41A3BDCB86844739A9', X'D12A6F376648DB41A3BDCB86844739A9', 7, NULL, NULL, 2, NULL, 0, 'Optionally, you can place the mixture in the refrigerator for about an hour then let sit on your counter until soft enough to scoop.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D506530796E1245AC3557EA800BCB19', X'D12A6F376648DB41A3BDCB86844739A9', X'D12A6F376648DB41A3BDCB86844739A9', 8, NULL, NULL, 2, NULL, 0, 'Meanwhile, crush 6 Biscoff Cookies into fine crumbs using a rolling pin or food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEBB9F0CC229D24A978B44C2E6BD24E3', X'D12A6F376648DB41A3BDCB86844739A9', X'D12A6F376648DB41A3BDCB86844739A9', 9, NULL, NULL, 2, NULL, 0, 'Scoop out twenty-six 2-teaspoon size scoops and set on a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'392F2C179624774C99F87A8DFBAF1101', X'D12A6F376648DB41A3BDCB86844739A9', X'D12A6F376648DB41A3BDCB86844739A9', 10, NULL, NULL, 2, NULL, 0, 'Roll one scoop into a ball then immediately roll it into the Cookie Crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4670230E34519B4FA683CD85FD4B9FFD', X'D12A6F376648DB41A3BDCB86844739A9', X'D12A6F376648DB41A3BDCB86844739A9', 11, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B07C2156A6B1B64194D0FFCBC1B1E090', X'0A705E494D483B458890C58F44AF02BE', X'0A705E494D483B458890C58F44AF02BE', 0, NULL, NULL, 2, NULL, 0, 'Melt the chocolate chips in the microwave at high power for 30-second burst of power, stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D05ADB66CFC11142A42770C20674208E', X'0A705E494D483B458890C58F44AF02BE', X'0A705E494D483B458890C58F44AF02BE', 1, NULL, NULL, 2, NULL, 0, 'Stir in the almond butter until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FCB807F92098B47AD01D76AAA34A66B', X'0A705E494D483B458890C58F44AF02BE', X'0A705E494D483B458890C58F44AF02BE', 2, NULL, NULL, 2, NULL, 0, 'Pour into the lined 8-inch baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA79272896D96B4FBC32A09ECFF3CB8B', X'0A705E494D483B458890C58F44AF02BE', X'0A705E494D483B458890C58F44AF02BE', 3, NULL, NULL, 2, NULL, 0, 'Cover and refrigerate for about 2 hours until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F61ACB3C265974EB248C8068F14194D', X'0A705E494D483B458890C58F44AF02BE', X'0A705E494D483B458890C58F44AF02BE', 4, NULL, NULL, 2, NULL, 0, 'Remove and let the fudge sit at room temperature for about 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34D1DD4CCBE3F0408B46127057E5551A', X'0A705E494D483B458890C58F44AF02BE', X'0A705E494D483B458890C58F44AF02BE', 5, NULL, NULL, 2, NULL, 0, 'Use a bunny-shaped cookie cutter to cut out fudge bunnies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED84D453A2407745AE165E2C3176E79A', X'0A705E494D483B458890C58F44AF02BE', X'0A705E494D483B458890C58F44AF02BE', 6, NULL, NULL, 2, NULL, 0, 'Remelt the chocolate fudge using 10-second bursts of power, stirring after each, then pour the remaining fudge into a smaller pan and repeat the process or pour the fudge directly into some cookie cutters and chill until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6128B99E448C3E4799337F01E57B8E8B', X'9DBAA68DEB97774392DDF66CC7558AD2', X'9DBAA68DEB97774392DDF66CC7558AD2', 0, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0153BBABE258E143BDEA322E9490A3FA', X'9DBAA68DEB97774392DDF66CC7558AD2', X'9DBAA68DEB97774392DDF66CC7558AD2', 1, NULL, NULL, 2, NULL, 0, 'Pour popcorn into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'575609C90377784D9A07B287F29BCD13', X'9DBAA68DEB97774392DDF66CC7558AD2', X'9DBAA68DEB97774392DDF66CC7558AD2', 2, NULL, NULL, 2, NULL, 0, 'Pour white chocolate over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC15F223B18B7340B55658D10D47E4D3', X'9DBAA68DEB97774392DDF66CC7558AD2', X'9DBAA68DEB97774392DDF66CC7558AD2', 3, NULL, NULL, 2, NULL, 0, 'Quickly stir it and toss it to coat the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F94A4CF9F1337D47889B0F196660BCAA', X'9DBAA68DEB97774392DDF66CC7558AD2', X'9DBAA68DEB97774392DDF66CC7558AD2', 4, NULL, NULL, 2, NULL, 0, 'Pour out onto the baking sheet and immediately toss the sprinkles and snowflakes over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'509883381790C447B1B276A3B708E264', X'9DBAA68DEB97774392DDF66CC7558AD2', X'9DBAA68DEB97774392DDF66CC7558AD2', 5, NULL, NULL, 2, NULL, 0, 'Wack the pan on the counter a few times. This helps get the candy to stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1364C49A26425940968E0837EBEE0EDC', X'9DBAA68DEB97774392DDF66CC7558AD2', X'9DBAA68DEB97774392DDF66CC7558AD2', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'299657ED4E73F542A08C9BF6CA8D153D', X'9DBAA68DEB97774392DDF66CC7558AD2', X'9DBAA68DEB97774392DDF66CC7558AD2', 7, NULL, NULL, 2, NULL, 0, 'Remove and break up into bite size chunks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'363C2946507A5A458FCD24760FBF12DE', X'117246ECEB41EA4CA60FC78DDB07130C', X'117246ECEB41EA4CA60FC78DDB07130C', 0, NULL, NULL, 2, NULL, 0, 'Pipe small triangle shaped orange carrot noses onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E39A79AEDBD7B40A954392FCC7D96A3', X'117246ECEB41EA4CA60FC78DDB07130C', X'117246ECEB41EA4CA60FC78DDB07130C', 1, NULL, NULL, 2, NULL, 0, 'Freeze for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0AAF7D226258CB40B0E56242951C8253', X'117246ECEB41EA4CA60FC78DDB07130C', X'117246ECEB41EA4CA60FC78DDB07130C', 2, NULL, NULL, 2, NULL, 0, 'Remove from freezer and peel them off the paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D87F8E8AFC4D264DB60C73109D9ED45E', X'117246ECEB41EA4CA60FC78DDB07130C', X'117246ECEB41EA4CA60FC78DDB07130C', 3, NULL, NULL, 2, NULL, 0, 'Dip one pretzel into white chocolate, leaving the top edge un-coated.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB4681FEE9AD8849B66D9BEC30E9C7BA', X'117246ECEB41EA4CA60FC78DDB07130C', X'117246ECEB41EA4CA60FC78DDB07130C', 4, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C423637A9D6A91439F0FD1EB87E622F3', X'117246ECEB41EA4CA60FC78DDB07130C', X'117246ECEB41EA4CA60FC78DDB07130C', 5, NULL, NULL, 2, NULL, 0, 'Dip 5 more pretzels, setting them next to each other on the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F234E6F4715FF46B8BF7EF403DFE5B6', X'117246ECEB41EA4CA60FC78DDB07130C', X'117246ECEB41EA4CA60FC78DDB07130C', 6, NULL, NULL, 2, NULL, 0, 'Freeze for 3-5 minutes until the chocolate sets.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'408DB537C27B254096B06EA3A931A43F', X'117246ECEB41EA4CA60FC78DDB07130C', X'117246ECEB41EA4CA60FC78DDB07130C', 7, NULL, NULL, 2, NULL, 0, 'Dip the un-coated part of the pretzels in dark chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B0F7C6AEB22A64E8212E5625527E43A', X'117246ECEB41EA4CA60FC78DDB07130C', X'117246ECEB41EA4CA60FC78DDB07130C', 8, NULL, NULL, 2, NULL, 0, 'Set on baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C8088F385527E44ADD1EC72CE83AA32', X'117246ECEB41EA4CA60FC78DDB07130C', X'117246ECEB41EA4CA60FC78DDB07130C', 9, NULL, NULL, 2, NULL, 0, 'Dip one whole pretzel in dark chocolate and set it across the snowman creating the brim of the hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3799F2924C486F47A09731974DB91A26', X'117246ECEB41EA4CA60FC78DDB07130C', X'117246ECEB41EA4CA60FC78DDB07130C', 10, NULL, NULL, 2, NULL, 0, 'Freeze for a few minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'148791512EA59240A5F277A68B1CA17B', X'117246ECEB41EA4CA60FC78DDB07130C', X'117246ECEB41EA4CA60FC78DDB07130C', 11, NULL, NULL, 2, NULL, 0, 'Attach one orange carrot nose to the snowman''s face and a white snowflake on each hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D38A0480E707E7438A7AE289800C1D9B', X'117246ECEB41EA4CA60FC78DDB07130C', X'117246ECEB41EA4CA60FC78DDB07130C', 12, NULL, NULL, 2, NULL, 0, 'Paint or pipe dark chocolate dots for the eyes and mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A248419F6D9FA499DF2FD4A7DC6679F', X'CBB1B69F5415384088CBBB3187D249D2', X'CBB1B69F5415384088CBBB3187D249D2', 0, NULL, NULL, 2, NULL, 0, 'Use a food-use-only paint brush to brush white chocolate in a thin layer over the top of a plastic spork.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7ABD53B54F09444E854B84659095B85D', X'CBB1B69F5415384088CBBB3187D249D2', X'CBB1B69F5415384088CBBB3187D249D2', 1, NULL, NULL, 2, NULL, 0, 'Wipe away any chocolate that drips down the the backside of the spork.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B438FD044E134499FBF54CEE0395615', X'CBB1B69F5415384088CBBB3187D249D2', X'CBB1B69F5415384088CBBB3187D249D2', 2, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator if using tempered pure white chocolate or in the freezer if using confectionery coating (candy melts) for about 3 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD09E646FE79A84E88ED5304CDCA7E9F', X'CBB1B69F5415384088CBBB3187D249D2', X'CBB1B69F5415384088CBBB3187D249D2', 3, NULL, NULL, 2, NULL, 0, 'Allow the spork to come to room temperature then brush on a second layer of white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50A08B6D9C135743850CFFE61AB8231A', X'CBB1B69F5415384088CBBB3187D249D2', X'CBB1B69F5415384088CBBB3187D249D2', 4, NULL, NULL, 2, NULL, 0, 'Chill until the white chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEEE5177E91D93458D0390F3AD5387BB', X'CBB1B69F5415384088CBBB3187D249D2', X'CBB1B69F5415384088CBBB3187D249D2', 5, NULL, NULL, 2, NULL, 0, 'The chocolate should retract from the spork so you can pop it off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'050AB710A60B664C997AB21E6B338AAF', X'CBB1B69F5415384088CBBB3187D249D2', X'CBB1B69F5415384088CBBB3187D249D2', 6, NULL, NULL, 2, NULL, 0, 'Attach one 1/2 inch candy eye and one 1/4 inch candy eye to each white chocolate spork.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D8B7D332F650A4D85CA468A2F8BF027', X'CBB1B69F5415384088CBBB3187D249D2', X'CBB1B69F5415384088CBBB3187D249D2', 7, NULL, NULL, 2, NULL, 0, 'Brush rosy red cheeks onto each Forky using edible red luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89D73E5209F8A148B04EB19A677A849E', X'CBB1B69F5415384088CBBB3187D249D2', X'CBB1B69F5415384088CBBB3187D249D2', 8, NULL, NULL, 2, NULL, 0, 'Roll red and blue modeling chocolate into thin ropes and create a mouth, eyebrow and arms for each Forky. See the blog post for detailed instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECBAC0997FC8274798F73459A7FE4FE6', X'CBB1B69F5415384088CBBB3187D249D2', X'CBB1B69F5415384088CBBB3187D249D2', 9, NULL, NULL, 2, NULL, 0, 'Attach the mouth and eyebrow to the white chocolate spork using water.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04B880B9FECC7546AE74E33DEDDC977F', X'52C643938A764949A5362562688C4DB4', X'52C643938A764949A5362562688C4DB4', 0, NULL, NULL, 2, NULL, 0, 'If desired, fill the bottom of each garbage can with candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25044B322D0C644B94B72C46486296B1', X'52C643938A764949A5362562688C4DB4', X'52C643938A764949A5362562688C4DB4', 1, NULL, NULL, 2, NULL, 0, 'Place one jumbo cupcake into each 9-ounce disposable cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C72B44E869D9094C934A3A957E5831F9', X'52C643938A764949A5362562688C4DB4', X'52C643938A764949A5362562688C4DB4', 2, NULL, NULL, 2, NULL, 0, 'Set the cupcakes down into the garbage cans.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A59DCAB2245D52488F7FB1F998BB55EB', X'52C643938A764949A5362562688C4DB4', X'52C643938A764949A5362562688C4DB4', 3, NULL, NULL, 2, NULL, 0, 'Add a scoop of frosting onto each cupcake and spread over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A846C51D34B9C94087C8A7613A63DFD3', X'52C643938A764949A5362562688C4DB4', X'52C643938A764949A5362562688C4DB4', 4, NULL, NULL, 2, NULL, 0, 'Break off about 1/3rd of each Pocky Stick and insert the longer piece into each cupcake about an inch from one edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E89F85FDDFD9FE44AAB7D7E082BF1634', X'52C643938A764949A5362562688C4DB4', X'52C643938A764949A5362562688C4DB4', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle dehydrated marshmallows over the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'647AC29AF1ACF548AC37F19BF789F4CA', X'52C643938A764949A5362562688C4DB4', X'52C643938A764949A5362562688C4DB4', 6, NULL, NULL, 2, NULL, 0, 'Set the trash can lid over the Pocky Stick allowing a small opening near the front of the trash can.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E058F951B8B5EC44A5E2DE3DB4671A0C', X'52C643938A764949A5362562688C4DB4', X'52C643938A764949A5362562688C4DB4', 7, NULL, NULL, 2, NULL, 0, 'Insert one white chocolate Forky into the cupcake at the very front edge of the garbage can.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE4197C551A94B4782F56DFC115B589A', X'52C643938A764949A5362562688C4DB4', X'52C643938A764949A5362562688C4DB4', 8, NULL, NULL, 2, NULL, 0, 'Wrap a red modeling chocolate arms/hands around the spork and press the hands onto the top edge of the garbage can.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4CA175DFA1004498A2E8C62C581E97C', X'88276EBE651AB84EABE673E0F1A759CE', X'88276EBE651AB84EABE673E0F1A759CE', 0, NULL, NULL, 2, NULL, 0, 'Tape a clear acetate sheet over top of a printed template to create each of the dragons.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'304D01C374342543AF87889E72991A14', X'88276EBE651AB84EABE673E0F1A759CE', X'88276EBE651AB84EABE673E0F1A759CE', 1, NULL, NULL, 2, NULL, 0, 'Use a food use only paint brush to paint a white highlight in the dragon''s eye.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92056A5892E1A543BF33E625A2D7CC2E', X'88276EBE651AB84EABE673E0F1A759CE', X'88276EBE651AB84EABE673E0F1A759CE', 2, NULL, NULL, 2, NULL, 0, 'Add coloring around the eye then once that is dried add a black pupil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A856C19CB1F6F4DB913648EF72FBC28', X'88276EBE651AB84EABE673E0F1A759CE', X'88276EBE651AB84EABE673E0F1A759CE', 3, NULL, NULL, 2, NULL, 0, 'Paint on any other details like mouths, chins, horns, nostrils, and spikes or bumps using the appropriate color of candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27C059C152580F4DACE3CB324D1582D0', X'88276EBE651AB84EABE673E0F1A759CE', X'88276EBE651AB84EABE673E0F1A759CE', 4, NULL, NULL, 2, NULL, 0, 'Pop the template in the freezer for about a minute until these candy melt details have hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C1228279A22AC4DA7CF06D1F3E8A5BB', X'88276EBE651AB84EABE673E0F1A759CE', X'88276EBE651AB84EABE673E0F1A759CE', 5, NULL, NULL, 2, NULL, 0, 'Pipe candy melts all over the entire surface of the template (using dark grey for Toothless, white for Light Fury, green for Meatlug, and light blue for Stormfly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B907EE39F9E72C43B3A03A151E6B3A44', X'88276EBE651AB84EABE673E0F1A759CE', X'88276EBE651AB84EABE673E0F1A759CE', 6, NULL, NULL, 2, NULL, 0, 'Lay a lollipop stick down the middle of the candy then pipe a second layer of candy coating over top of the wet candy coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C03BCEC6C2611A4FAF386C3B34A86EE1', X'88276EBE651AB84EABE673E0F1A759CE', X'88276EBE651AB84EABE673E0F1A759CE', 7, NULL, NULL, 2, NULL, 0, 'Freeze for about 5 minutes just until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBBB6B0CD3D39D4499BB0E7401579DC4', X'88276EBE651AB84EABE673E0F1A759CE', X'88276EBE651AB84EABE673E0F1A759CE', 8, NULL, NULL, 2, NULL, 0, 'Remove from the freezer and peel the lollipop off the acetate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F76CC8E3A604345B5E3BDFCA422E456', X'88276EBE651AB84EABE673E0F1A759CE', X'88276EBE651AB84EABE673E0F1A759CE', 9, NULL, NULL, 2, NULL, 0, 'See more detailed instructions at https://hungryhappenings/how-to-train-your-dragon-lollipops/');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECE4217B74D5E8438E371970F10FF699', X'AA44D6C1C9D3484EB9E7B5600BD3316F', X'AA44D6C1C9D3484EB9E7B5600BD3316F', 0, NULL, NULL, 2, NULL, 0, 'Dip strawberries in melted and tempered pure white chocolate or in melted white confectionery coating (candy melts).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27223D74AA7F744FB86C979418327131', X'AA44D6C1C9D3484EB9E7B5600BD3316F', X'AA44D6C1C9D3484EB9E7B5600BD3316F', 1, NULL, NULL, 2, NULL, 0, 'Allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17C037FBAC51BA458D1FBB0936FFE607', X'AA44D6C1C9D3484EB9E7B5600BD3316F', X'AA44D6C1C9D3484EB9E7B5600BD3316F', 2, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4F66BC2ED758041A285422CB552FC95', X'AA44D6C1C9D3484EB9E7B5600BD3316F', X'AA44D6C1C9D3484EB9E7B5600BD3316F', 3, NULL, NULL, 2, NULL, 0, 'If desired, allow the white chocolate to puddle at the bottom of the strawberry for a few seconds, then lift up the berry and place it on a clean spot on the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B3CC727FF8F414BAF4F781FD8E1C131', X'AA44D6C1C9D3484EB9E7B5600BD3316F', X'AA44D6C1C9D3484EB9E7B5600BD3316F', 4, NULL, NULL, 2, NULL, 0, 'Chill in the fridge (for 3-5 minutes) if using candy melts or let air dry (for about 10 minutes) if using pure white chocolate just until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'316F7CEFD5298A47A0CA6F9A781B02C1', X'50F855D13E1B5E4BB18B598B348BCB6E', X'50F855D13E1B5E4BB18B598B348BCB6E', 0, NULL, NULL, 2, NULL, 0, 'EYES and NOSE: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two small circles and one 1/2 inch heart for each polar bear using a small plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEB077A3D309764597F4530EF990CCCF', X'50F855D13E1B5E4BB18B598B348BCB6E', X'50F855D13E1B5E4BB18B598B348BCB6E', 1, NULL, NULL, 2, NULL, 0, 'EARS: Cut a curved sliver off of two white chocolate wafers for each polar bear or create ears using 1/4 inch thick white modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0768BD40146D084A964E1F8CE91F1286', X'50F855D13E1B5E4BB18B598B348BCB6E', X'50F855D13E1B5E4BB18B598B348BCB6E', 2, NULL, NULL, 2, NULL, 0, 'BOW: Cut two small hearts and one small circle out of pink or red modeling chocolate. Set the hearts together with the points touching then attach the circle over top using water or clear piping gel to `glue` the bow together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75EC7DD7C2422142B6AD37320E8CDA8F', X'50F855D13E1B5E4BB18B598B348BCB6E', X'50F855D13E1B5E4BB18B598B348BCB6E', 3, NULL, NULL, 2, NULL, 0, 'Dip the white chocolate coated strawberries into white chocolate again and press a fork up against the chocolate then pull away quickly. Do this all over the berry, over and over again, until spikes of fur start to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C71F247A43C654B9A65B5B6565A280F', X'50F855D13E1B5E4BB18B598B348BCB6E', X'50F855D13E1B5E4BB18B598B348BCB6E', 4, NULL, NULL, 2, NULL, 0, 'Attach two candy ears on the top edge of the strawberry on opposite sides of the green stem using melted white chocolate. Then add more white chocolate over the ears and create spikes of fur on them too.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'807150089280B24489B27D92C20F5289', X'50F855D13E1B5E4BB18B598B348BCB6E', X'50F855D13E1B5E4BB18B598B348BCB6E', 5, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add a nose to the tip of the strawberry and two eyes above it. Add a bow if making female polar bears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'596F36D96DD4CE4283E3C79824F89FAD', X'161C816A9CA56949AF55981A968597A8', X'161C816A9CA56949AF55981A968597A8', 0, NULL, NULL, 2, NULL, 0, 'NOSE and FUR PATCHES: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two ovals using a plunger cutter and cut out a small 1/2 inch heart using a heart plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E419D32A92FB854C8BC482064C22A429', X'161C816A9CA56949AF55981A968597A8', X'161C816A9CA56949AF55981A968597A8', 1, NULL, NULL, 2, NULL, 0, 'EARS: You can cut a small sliver off two dark chocolate wafers to create the ears or you can cut out black modeling chocolate circles (with a curved notch removed) to create the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BA259FBEC3A1F468171CB7084F94925', X'161C816A9CA56949AF55981A968597A8', X'161C816A9CA56949AF55981A968597A8', 2, NULL, NULL, 2, NULL, 0, 'BOW: Roll out red modeling chocolate to about 1/8th inch thickness and cut out two small hearts and one small circle. Set the points of the hearts together then use either water or clear piping gel to attach the circle over the intersection of the points creating a bow.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E52EB0588A941488A99D2C4521CA867', X'161C816A9CA56949AF55981A968597A8', X'161C816A9CA56949AF55981A968597A8', 3, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add two ears on the top edge of the strawberry, a nose to the tip of the strawberry and two fur patches above it off to the side. Add a bow in front of an ear to make panda bear girls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F62AB9536820544D85A3AFB9FA719336', X'421A0737AEB3554E9CA8A9FDE0FABD48', X'421A0737AEB3554E9CA8A9FDE0FABD48', 0, NULL, NULL, 2, NULL, 0, 'Dip strawberries in melted and tempered pure white chocolate or in melted white confectionery coating (candy melts).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4611AFCC1AB3A749A8155D982502ED77', X'421A0737AEB3554E9CA8A9FDE0FABD48', X'421A0737AEB3554E9CA8A9FDE0FABD48', 1, NULL, NULL, 2, NULL, 0, 'Allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BD3E4548DA1A14BA2F040248579EB3A', X'421A0737AEB3554E9CA8A9FDE0FABD48', X'421A0737AEB3554E9CA8A9FDE0FABD48', 2, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CFC100E89A54044904E3E7E3C76F25E', X'421A0737AEB3554E9CA8A9FDE0FABD48', X'421A0737AEB3554E9CA8A9FDE0FABD48', 3, NULL, NULL, 2, NULL, 0, 'If desired, allow the white chocolate to puddle at the bottom of the strawberry for a few seconds, then lift up the berry and place it on a clean spot on the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3E9B2C92744E54FBAC50CD786D768D2', X'421A0737AEB3554E9CA8A9FDE0FABD48', X'421A0737AEB3554E9CA8A9FDE0FABD48', 4, NULL, NULL, 2, NULL, 0, 'Chill in the fridge (for 3-5 minutes) if using candy melts or let air dry (for about 10 minutes) if using pure white chocolate just until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54271DDDB101254AB662A8D7C29A1160', X'F2844B7FF124B541959CE7E97892777D', X'F2844B7FF124B541959CE7E97892777D', 0, NULL, NULL, 2, NULL, 0, 'EYES and NOSE: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two small circles and one 1/2 inch heart for each polar bear using a small plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FFE9CBE64E893D43B22B2704BD012811', X'F2844B7FF124B541959CE7E97892777D', X'F2844B7FF124B541959CE7E97892777D', 1, NULL, NULL, 2, NULL, 0, 'EARS: Cut a curved sliver off of two white chocolate wafers for each polar bear or create ears using 1/4 inch thick white modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'133DBB5893580544A8103AAA94F486A7', X'F2844B7FF124B541959CE7E97892777D', X'F2844B7FF124B541959CE7E97892777D', 2, NULL, NULL, 2, NULL, 0, 'BOW: Cut two small hearts and one small circle out of pink or red modeling chocolate. Set the hearts together with the points touching then attach the circle over top using water or clear piping gel to `glue` the bow together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0ADFDF9AD666C42968964DDA159CBA6', X'F2844B7FF124B541959CE7E97892777D', X'F2844B7FF124B541959CE7E97892777D', 3, NULL, NULL, 2, NULL, 0, 'Dip the white chocolate coated strawberries into white chocolate again and press a fork up against the chocolate then pull away quickly. Do this all over the berry, over and over again, until spikes of fur start to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4211AFBBFDB80E469B15F8B27FAA919F', X'F2844B7FF124B541959CE7E97892777D', X'F2844B7FF124B541959CE7E97892777D', 4, NULL, NULL, 2, NULL, 0, 'Attach two candy ears on the top edge of the strawberry on opposite sides of the green stem using melted white chocolate. Then add more white chocolate over the ears and create spikes of fur on them too.');
