INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3BF152942029254D97B8B61233B8BC4F', X'18BF939A38EBB7448412B360EB872DA2', X'18BF939A38EBB7448412B360EB872DA2', 0, NULL, NULL, 2, NULL, 0, 'NOSE and FUR PATCHES: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two ovals using a plunger cutter and cut out a small 1/2 inch heart using a heart plunger cutter.');
