INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9366A1B77F15A4D9A479DBBE8613D31', X'18BF939A38EBB7448412B360EB872DA2', X'18BF939A38EBB7448412B360EB872DA2', 2, NULL, NULL, 2, NULL, 0, 'BOW: Roll out red modeling chocolate to about 1/8th inch thickness and cut out two small hearts and one small circle. Set the points of the hearts together then use either water or clear piping gel to attach the circle over the intersection of the points creating a bow.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9CA85ECDD66374F8660E9C2A5ACBF81', X'18BF939A38EBB7448412B360EB872DA2', X'18BF939A38EBB7448412B360EB872DA2', 3, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add two ears on the top edge of the strawberry, a nose to the tip of the strawberry and two fur patches above it off to the side. Add a bow in front of an ear to make panda bear girls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17A2CEB58631BB48B2543C96928A714B', X'FE1C3C96EC7C0546972C64A00B8142A3', X'FE1C3C96EC7C0546972C64A00B8142A3', 0, NULL, NULL, 2, NULL, 0, 'Pour the chocolate into a small bowl or glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55ACCC6125599745ABB975C707FC4038', X'FE1C3C96EC7C0546972C64A00B8142A3', X'FE1C3C96EC7C0546972C64A00B8142A3', 1, NULL, NULL, 2, NULL, 0, 'Dip one strawberry into the chocolate then set it on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'997054EFA96BFF449E61926541BD1F1A', X'FE1C3C96EC7C0546972C64A00B8142A3', X'FE1C3C96EC7C0546972C64A00B8142A3', 2, NULL, NULL, 2, NULL, 0, 'Take a fork and press it into the chocolate, then pull up and away quickly creating spikes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00E74207A9968247AD550969E0C3E5DA', X'FE1C3C96EC7C0546972C64A00B8142A3', X'FE1C3C96EC7C0546972C64A00B8142A3', 3, NULL, NULL, 2, NULL, 0, 'Repeat this process until the entire chocolate strawberry looks furry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F94D9F4940C1D440A71D419922616692', X'FE1C3C96EC7C0546972C64A00B8142A3', X'FE1C3C96EC7C0546972C64A00B8142A3', 4, NULL, NULL, 2, NULL, 0, 'Immediate press two candy eyes and an M&M nose onto the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06136FA8F6AAC3499FD47F899A2C291B', X'FE1C3C96EC7C0546972C64A00B8142A3', X'FE1C3C96EC7C0546972C64A00B8142A3', 5, NULL, NULL, 2, NULL, 0, 'Dip the cut edge of two chocolate wafers in chocolate and attach them to either side of the top of the strawberry creating the bear''s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'152F2E73D3F7B44C8661CEAE05C11073', X'FE1C3C96EC7C0546972C64A00B8142A3', X'FE1C3C96EC7C0546972C64A00B8142A3', 6, NULL, NULL, 2, NULL, 0, 'Let the strawberry air dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFF2540F6E8C2F4186B9A7E3A262D5B2', X'FE1C3C96EC7C0546972C64A00B8142A3', X'FE1C3C96EC7C0546972C64A00B8142A3', 7, NULL, NULL, 2, NULL, 0, 'Repeat dipping all 16 bears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BB4EEC70D6D9543BDE20D82C646FFCB', X'FE1C3C96EC7C0546972C64A00B8142A3', X'FE1C3C96EC7C0546972C64A00B8142A3', 8, NULL, NULL, 2, NULL, 0, 'If desired, brush a second layer of chocolate over the bottom of each strawberry to seal in the berry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2306D17CEB67D43BBD93FFFD5F14982', X'527F667572948D4D89A92A845493094D', X'527F667572948D4D89A92A845493094D', 0, NULL, NULL, 2, NULL, 0, 'Pour the popped popcorn into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57F175FE0C8FE14186CFA3EBC43FA2EB', X'527F667572948D4D89A92A845493094D', X'527F667572948D4D89A92A845493094D', 1, NULL, NULL, 2, NULL, 0, 'Stir in creamy peanut butter and a pinch of salt into the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD357E8EA105394AADA543EC941DD1C4', X'527F667572948D4D89A92A845493094D', X'527F667572948D4D89A92A845493094D', 2, NULL, NULL, 2, NULL, 0, 'Reserve 2 tablespoons of peanut butter chocolate mixture for later use.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87E2479DA6F89344B77AAC9CAF0037AD', X'527F667572948D4D89A92A845493094D', X'527F667572948D4D89A92A845493094D', 3, NULL, NULL, 2, NULL, 0, 'Pour the remaining chocolate over the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A284BFC5548F541BEB25BD9A311D0DB', X'527F667572948D4D89A92A845493094D', X'527F667572948D4D89A92A845493094D', 4, NULL, NULL, 2, NULL, 0, 'Toss to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'310936F3F8F04840965B7366F04EC5F6', X'527F667572948D4D89A92A845493094D', X'527F667572948D4D89A92A845493094D', 5, NULL, NULL, 2, NULL, 0, 'Spread into an even layer on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E12CCB04C418C74C9577D0468F5CCD06', X'527F667572948D4D89A92A845493094D', X'527F667572948D4D89A92A845493094D', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle with cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6940ADEECAF6A643825CADCF5709228A', X'527F667572948D4D89A92A845493094D', X'527F667572948D4D89A92A845493094D', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10-15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00DD1AF351F3A841A396D3FC097CC7DE', X'527F667572948D4D89A92A845493094D', X'527F667572948D4D89A92A845493094D', 8, NULL, NULL, 2, NULL, 0, 'Remove and break popcorn into chunks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'793C7674EB088C4F99A3E9049D5A3FA9', X'527F667572948D4D89A92A845493094D', X'527F667572948D4D89A92A845493094D', 9, NULL, NULL, 2, NULL, 0, 'Reheat the reserved peanut butter chocolate then use it as glue to attach chocolate shells to the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7650D748D30EE74E8599D61CF298C9EA', X'527F667572948D4D89A92A845493094D', X'527F667572948D4D89A92A845493094D', 10, NULL, NULL, 2, NULL, 0, 'Store in an airtight container until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EEC34EB57183649A3C373EE094F5DC9', X'527F667572948D4D89A92A845493094D', X'527F667572948D4D89A92A845493094D', 11, NULL, NULL, 2, NULL, 0, '*See melting instructions on Hungry Happenings'' Chocolate Making Tips Page');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0C00027912FA145AE9ED0A04FEBF0E8', X'0A847E20B3B7034B9EAE65684CFCFF90', X'0A847E20B3B7034B9EAE65684CFCFF90', 0, NULL, NULL, 2, NULL, 0, 'Pour white chocolate over popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A4B419E086BDB4AAB083AA84689ED70', X'0A847E20B3B7034B9EAE65684CFCFF90', X'0A847E20B3B7034B9EAE65684CFCFF90', 1, NULL, NULL, 2, NULL, 0, 'Stir just to coat the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DEED523676E9141B87DB8B25D362924', X'0A847E20B3B7034B9EAE65684CFCFF90', X'0A847E20B3B7034B9EAE65684CFCFF90', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle Hershey''s S''mores Baking Pieces over popcorn and stir just until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB2143905D38E64C91C3176E18FEBC64', X'0A847E20B3B7034B9EAE65684CFCFF90', X'0A847E20B3B7034B9EAE65684CFCFF90', 3, NULL, NULL, 2, NULL, 0, 'Pour out onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AA26D56B58EFA49B3B5B7E334184BEE', X'0A847E20B3B7034B9EAE65684CFCFF90', X'0A847E20B3B7034B9EAE65684CFCFF90', 4, NULL, NULL, 2, NULL, 0, 'Drizzle with dark chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'107F510BA7D72844A455C35ADAD8AB68', X'0A847E20B3B7034B9EAE65684CFCFF90', X'0A847E20B3B7034B9EAE65684CFCFF90', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1858C98E3E3F5345AC056C6A24AB0916', X'0A847E20B3B7034B9EAE65684CFCFF90', X'0A847E20B3B7034B9EAE65684CFCFF90', 6, NULL, NULL, 2, NULL, 0, 'Break into pieces and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9DF053511925546AE34C2B5C2D1CD99', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 0, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of chocolate inside the cavities in a silicone half sphere mold or a plastic peanut butter cup mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48D9ABEBD42D4043B443A283568E24FF', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 1, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to harden (chill in the refrigerator for a few minutes, if desired).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA02BD54757F294BAEC1F979643B9113', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 2, NULL, NULL, 2, NULL, 0, 'Combine the Nestle White Chips with the Reese''s Peanut Butter Chips in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF8F4879C909F64F95CF3CC3316CB5C7', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 3, NULL, NULL, 2, NULL, 0, 'Heat on high power for three 30 second increments, stirring after each until melted. If needed heat additional 10 second burst of power, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBD9438D03F26D45A372A5C10BE54207', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 4, NULL, NULL, 2, NULL, 0, 'Stir in the salt and creamy peanut butter until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C0676515869A24BBBBE29E051B55586', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 5, NULL, NULL, 2, NULL, 0, 'Allow to cool 10 minutes then pour into a disposable pastry bag or squeeze bottle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94C6E2E9C1EDA24FB16BDF056DB927D6', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 6, NULL, NULL, 2, NULL, 0, 'Pipe peanut butter fudge filling into the chocolate shells, leaving 1/16 - 1/8th of an inch of space at the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73472435594C8641AF0703356BD13B34', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 7, NULL, NULL, 2, NULL, 0, 'Allow fudge to firm up.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A0E4B071D90904D83F9493BF98D2C6A', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 8, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of chocolate over top of each candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DFD43CE6976AB14797A02C1331D71A78', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 9, NULL, NULL, 2, NULL, 0, 'Let harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F71B363833749348BDF96901EC60E982', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 10, NULL, NULL, 2, NULL, 0, 'Pop out of molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CC9CB12C644034E9BE53EC664B1564E', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 11, NULL, NULL, 2, NULL, 0, 'Spread remaining chocolate out to about 1/8th of an inch thickness on a silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'866382E07AFB9A4E89A8E716185AFD97', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 12, NULL, NULL, 2, NULL, 0, 'Let sit just until dry, but still flexible.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B0EF9F8D3C99C4FB0901B9CC797645D', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 13, NULL, NULL, 2, NULL, 0, 'Cut out fifteen 2 inch squares using a metal cookie cutter then press a sheet of parchment paper over top and cover with a heavy cookie sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EE55A2BCAD08443B0BE45DAF13C6A31', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 14, NULL, NULL, 2, NULL, 0, 'Allow to harden then break off excess chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1F3C726630F5D49A35262533EC46DBF', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 15, NULL, NULL, 2, NULL, 0, 'Re-melt the scraps of chocolate and use to attach one chocolate square to the tops of each peanut butter cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8DA64A463DF184093279258393513B4', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 16, NULL, NULL, 2, NULL, 0, 'Let dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67F317C484052D4E81AAD16AC517FE90', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 17, NULL, NULL, 2, NULL, 0, 'Press white modeling chocolate out through a clay extruder fitted with a multi hole disc. Cut off 1 1/2 inch groupings of the white modeling chocolate strands and pinch together one end.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A0BE654456A884395C5D8188511DCC1', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 18, NULL, NULL, 2, NULL, 0, 'Press the end into the center of a chocolate square forming the tassels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA24E9562FF6F14C8F95027BA5C3C30A', X'660A1D8821BF8F4F801280C1A716503F', X'660A1D8821BF8F4F801280C1A716503F', 19, NULL, NULL, 2, NULL, 0, 'Press a small ball of white modeling chocolate over the center of the chocolate graduation cap covering the tips of the tassels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6A3473B550943489B78F1037FBB3E73', X'FCBA30B7A9915A43AA05047423E53340', X'FCBA30B7A9915A43AA05047423E53340', 0, NULL, NULL, 2, NULL, 0, 'Fill the bottom trays of your gummy candy maker with ice.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CEA53CA15EE6CA4EA96F9F1D8166C53F', X'FCBA30B7A9915A43AA05047423E53340', X'FCBA30B7A9915A43AA05047423E53340', 1, NULL, NULL, 2, NULL, 0, 'Set the silicone bear, worm, and fish molds over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66BCBAA65E7C28448C7D6E98A287CD4D', X'FCBA30B7A9915A43AA05047423E53340', X'FCBA30B7A9915A43AA05047423E53340', 2, NULL, NULL, 2, NULL, 0, 'Stir together the flavored and unflavored gelatin in the Candy Maker mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC6BE5F42E2826498EEE89306230082D', X'FCBA30B7A9915A43AA05047423E53340', X'FCBA30B7A9915A43AA05047423E53340', 3, NULL, NULL, 2, NULL, 0, 'Pour in the water, as you continue to stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58C307C357A3204CB7F1B28DF6F62F24', X'FCBA30B7A9915A43AA05047423E53340', X'FCBA30B7A9915A43AA05047423E53340', 4, NULL, NULL, 2, NULL, 0, 'Allow the mixture to melt and become warm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D7A9F8A2B69BC4FB4B18391D2E24034', X'FCBA30B7A9915A43AA05047423E53340', X'FCBA30B7A9915A43AA05047423E53340', 5, NULL, NULL, 2, NULL, 0, 'Pour into the molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41B25196F795ED4093D482CA31922CC8', X'FCBA30B7A9915A43AA05047423E53340', X'FCBA30B7A9915A43AA05047423E53340', 6, NULL, NULL, 2, NULL, 0, 'Allow the candy to harden, about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'62FB7822EA9AEA43BA01ADCC3DEF8838', X'FCBA30B7A9915A43AA05047423E53340', X'FCBA30B7A9915A43AA05047423E53340', 7, NULL, NULL, 2, NULL, 0, 'Remove the molds from the trays and peel the candies out.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89201C1C1E74DB42AD74900194F2CB58', X'3D53F205F5FA9946A8B73E19BC9C7CA2', X'3D53F205F5FA9946A8B73E19BC9C7CA2', 0, NULL, NULL, 2, NULL, 0, 'Chop candy bars into small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD8E86F505B14C498957A9280D96BA41', X'3D53F205F5FA9946A8B73E19BC9C7CA2', X'3D53F205F5FA9946A8B73E19BC9C7CA2', 1, NULL, NULL, 2, NULL, 0, 'Pour popcorn into an extra large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA1A7C9981D8A747AE7B0B54F3CDB384', X'3D53F205F5FA9946A8B73E19BC9C7CA2', X'3D53F205F5FA9946A8B73E19BC9C7CA2', 2, NULL, NULL, 2, NULL, 0, 'Pour white chocolate over popcorn and toss to evenly coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51EE2EE38C0ED8479F8A90EEB569F2F6', X'3D53F205F5FA9946A8B73E19BC9C7CA2', X'3D53F205F5FA9946A8B73E19BC9C7CA2', 3, NULL, NULL, 2, NULL, 0, 'Stir in candy bar pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FF4365160210C4FB27519D2915C331B', X'3D53F205F5FA9946A8B73E19BC9C7CA2', X'3D53F205F5FA9946A8B73E19BC9C7CA2', 4, NULL, NULL, 2, NULL, 0, 'Pour popcorn onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'996E263EA881D14789D7613584AE184B', X'3D53F205F5FA9946A8B73E19BC9C7CA2', X'3D53F205F5FA9946A8B73E19BC9C7CA2', 5, NULL, NULL, 2, NULL, 0, 'Drizzle the milk chocolate over top of the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F91B526609AC14B87E03925AB995CAE', X'3D53F205F5FA9946A8B73E19BC9C7CA2', X'3D53F205F5FA9946A8B73E19BC9C7CA2', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10-15 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CA3F9B5094B884A8D9022AF65187113', X'3D53F205F5FA9946A8B73E19BC9C7CA2', X'3D53F205F5FA9946A8B73E19BC9C7CA2', 7, NULL, NULL, 2, NULL, 0, 'Break popcorn into chunks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BBB89061410214E8A4B57754813358A', X'3D53F205F5FA9946A8B73E19BC9C7CA2', X'3D53F205F5FA9946A8B73E19BC9C7CA2', 8, NULL, NULL, 2, NULL, 0, 'Enjoy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDE3249D66F2B04D831AFB9DC3770FE4', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 0, NULL, NULL, 2, NULL, 0, 'Fill all the cavities in one Day of the Dead sugar skull silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'493AB4F2CD9CA742B597E7D0249DA2AA', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 1, NULL, NULL, 2, NULL, 0, 'Turn the mold upside down over a piece of parchment paper allowing the excess chocolate to drip out.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2E6E26E8EDE8D45BA36F35CD3CAB166', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 2, NULL, NULL, 2, NULL, 0, 'Shake the mold until you just have a thin coating of chocolate around the side and bottom of the skulls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D3112053409A04691A138711B014DAF', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 3, NULL, NULL, 2, NULL, 0, 'Turn the mold over and use an offset spatula to scrape the chocolate off the top of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA3AB761968B2A419EA9E25F72395F61', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate until the chocolate hardens then remove and let sit at room temperature for at least 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AF895A1269DFB43A06CEA2A739BE8F4', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 5, NULL, NULL, 2, NULL, 0, 'Repeat using a second mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA93E22F128BBD4F9E2B373821A50C7B', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 6, NULL, NULL, 2, NULL, 0, 'In a small saucepan or in the microwave, heat the heavy whipping cream just until it begins to bubble around the edges. (Scald the cream).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC41ABD4D742A845B8CE241209C3EBDA', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle the cinnamon and cayenne pepper into the cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38D8F0CF7B426B4E9185EBFB123BDDFD', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 8, NULL, NULL, 2, NULL, 0, 'Stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A939939C6776F49B4BC6C5E4346BA5D', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 9, NULL, NULL, 2, NULL, 0, 'Set aside for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBF9141ED2C69042A4C05276E7BC31C8', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 10, NULL, NULL, 2, NULL, 0, 'Reheat just until hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'461993B60918584888C4E1B52DBFEE95', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 11, NULL, NULL, 2, NULL, 0, 'Strain through a fine mesh sieve that is set over the bowl of 6 ounces of melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EC17FA64C25D14ABC4EFED52BA00D2F', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 12, NULL, NULL, 2, NULL, 0, 'Begin stirring in the center of the bowl until the chocolate and cream start to blend together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15B603A2FC32B6458788AA5744C676CF', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 13, NULL, NULL, 2, NULL, 0, 'Continue to stir through the entire bowl until the chocolate ganache completely comes together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A7584F6DBD67D47B0968567853D20C6', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 14, NULL, NULL, 2, NULL, 0, 'Pour ganache into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E002BB98F0989649BB09EC08E329DD75', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 15, NULL, NULL, 2, NULL, 0, 'Pipe the ganache into all 18 skull shaped chocolate shells filling each so that you have between 1/8th and 1/16th of an inch of space between the ganache and the top edge of the chocolate shell.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7032B1EA392FE848885DBF39AB8C6187', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 16, NULL, NULL, 2, NULL, 0, 'Refrigerate until the ganache firms up.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'682843FF98A3704D8C76D01B37051545', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 17, NULL, NULL, 2, NULL, 0, 'Remove and let the filled mold sit at room temperature for at least 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A08C4DA6E171D42980C8AF628B37854', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 18, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of chocolate over top of each candy, scraping off any excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'007421B3E1FFAB41AF462BFD08AD1D15', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 19, NULL, NULL, 2, NULL, 0, 'Refrigerate for a few minutes until that chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B6FDAE37D141341AF9E9271A0F65C96', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 20, NULL, NULL, 2, NULL, 0, 'Remove the chocolates from the molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E768DC51486FAB478735F1F615E5BD88', X'C5F8F3E26559F747A0F64921D0E4E96E', X'C5F8F3E26559F747A0F64921D0E4E96E', 21, NULL, NULL, 2, NULL, 0, 'If desired, brush some silver luster dust over top of each candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B45EC9F32155F4D8E20EE5FEDE4C127', X'16C9CD45FD88AA42B02616D396619EB6', X'16C9CD45FD88AA42B02616D396619EB6', 0, NULL, NULL, 2, NULL, 0, 'Pour the melted green candy into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'663D6948CD7C7947BBD1A62FCE27AAC6', X'16C9CD45FD88AA42B02616D396619EB6', X'16C9CD45FD88AA42B02616D396619EB6', 1, NULL, NULL, 2, NULL, 0, 'Pipe the candy into 8 cavities of a silicone shot glass mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7FDBC66F0D0BC45BBF84B11C26A59E4', X'16C9CD45FD88AA42B02616D396619EB6', X'16C9CD45FD88AA42B02616D396619EB6', 2, NULL, NULL, 2, NULL, 0, 'Tap the mold to remove air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABDDDFA7A096084D91EB4159402D9C12', X'16C9CD45FD88AA42B02616D396619EB6', X'16C9CD45FD88AA42B02616D396619EB6', 3, NULL, NULL, 2, NULL, 0, 'Freeze for 10-12 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCC1D57C8D659049949189F1BAE5944C', X'16C9CD45FD88AA42B02616D396619EB6', X'16C9CD45FD88AA42B02616D396619EB6', 4, NULL, NULL, 2, NULL, 0, 'Remove and push snack cups out of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0FBC8483F949C244A434B595D9D644FA', X'16C9CD45FD88AA42B02616D396619EB6', X'16C9CD45FD88AA42B02616D396619EB6', 5, NULL, NULL, 2, NULL, 0, 'Set aside for 10 minutes so the candy comes to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21DB9D98E862904E9BE33D170F3F8CD9', X'16C9CD45FD88AA42B02616D396619EB6', X'16C9CD45FD88AA42B02616D396619EB6', 6, NULL, NULL, 2, NULL, 0, 'Pour black candy melts into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51DE23ADF7A09A46ADC82A804C44ED6F', X'16C9CD45FD88AA42B02616D396619EB6', X'16C9CD45FD88AA42B02616D396619EB6', 7, NULL, NULL, 2, NULL, 0, 'Pipe dribbles of candy over the top edge of the snack cups forming the monster''s black hair.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02EFEFCC4EAD22479D931D34EFEDF9DE', X'16C9CD45FD88AA42B02616D396619EB6', X'16C9CD45FD88AA42B02616D396619EB6', 8, NULL, NULL, 2, NULL, 0, 'Use the black candy to attach two candy eyes to each snack cup just under the black hair.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'637FC02164EEA448BE85083F34CE40DD', X'16C9CD45FD88AA42B02616D396619EB6', X'16C9CD45FD88AA42B02616D396619EB6', 9, NULL, NULL, 2, NULL, 0, 'Attach two chocolate fudge rolls on either side of the snack cups, placing them near the bottom edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0E6CF8BC72B724A8E85C27BA3088E2E', X'16C9CD45FD88AA42B02616D396619EB6', X'16C9CD45FD88AA42B02616D396619EB6', 10, NULL, NULL, 2, NULL, 0, 'Pipe on a scar using black candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6AC47DA9D47F3418A09DC749B0946E3', X'16C9CD45FD88AA42B02616D396619EB6', X'16C9CD45FD88AA42B02616D396619EB6', 11, NULL, NULL, 2, NULL, 0, 'Freeze for 3 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DCF4A93C56C3449A4ECFC9C83F1D9D1', X'16C9CD45FD88AA42B02616D396619EB6', X'16C9CD45FD88AA42B02616D396619EB6', 12, NULL, NULL, 2, NULL, 0, 'Fill with Welch''s Fruit Snacks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5C32CD7ECDE334C903E4602BC9FAD9C', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 0, NULL, NULL, 2, NULL, 0, 'Pour the vibrant green candy melts into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D15911D19FE694429992A5175D04B65B', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 1, NULL, NULL, 2, NULL, 0, 'Pipe the green candy melts into 8 shot glass cavities in your silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'484A487718CC874B9A4A1DBBB3D7B481', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 2, NULL, NULL, 2, NULL, 0, 'Tap the mold a few times to remove any air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4B28FC95A3656449ACB1DA3075D205C', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 3, NULL, NULL, 2, NULL, 0, 'Freeze for 12-15 minutes until the candy is hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF45AB70C5D4B7479DDF1F7DDF881FE5', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 4, NULL, NULL, 2, NULL, 0, 'Let sit at room temperature for 5 minutes then un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00D6F2E6AB535E44B8163670A49D5C38', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 5, NULL, NULL, 2, NULL, 0, 'Push the candy out of the silicone mold to remove.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC226F7FB811ED4ABD16E9315B87B82E', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 6, NULL, NULL, 2, NULL, 0, 'Set aside for 10 minutes before decorating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8081197DD53F9A43ADE43F11DEB3084A', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 7, NULL, NULL, 2, NULL, 0, 'Pour red candy melts into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADA5DF685C7AB240924D8FD6C7B1990D', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 8, NULL, NULL, 2, NULL, 0, 'Pipe some red candy melts onto the back of a candy eye and press the eye onto the candy cup being sure to smear some of the red candy around the edge of the candy eye.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6010A641C7C1124FB9AF2F9335D5412F', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 9, NULL, NULL, 2, NULL, 0, 'Add another eye, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DECA819E68CED4BACAC37894F40FBA6', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 10, NULL, NULL, 2, NULL, 0, 'Pipe drips of blood red candy melts over the top edge of the candy cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9735EF023AF5F84B8321D3AC5C5E055D', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 11, NULL, NULL, 2, NULL, 0, 'Pop the candy cup in the freezer for 2-3 minutes until the red candy melts harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B62D434E824E9D4AA796C513B1A0E8BD', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 12, NULL, NULL, 2, NULL, 0, 'Remove and allow the candy cup to sit at room temperature for a few minutes before piping on a black mouth, nose, and scars, as desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D427F232C4D1342BD858C5C9D718BCF', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 13, NULL, NULL, 2, NULL, 0, 'Let the black candy melts dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E844EAF06925C440A8038C4E649074AA', X'438B65F315A2B84A91CD416E3D70F1B3', X'438B65F315A2B84A91CD416E3D70F1B3', 14, NULL, NULL, 2, NULL, 0, 'Fill the candy cups with candy, pudding, or chocolate mousse.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50ED38698960554A84E7DF64DFF71EFF', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 0, NULL, NULL, 2, NULL, 0, 'Cut 6 of the StackerMallows in half lengthwise.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F5AA5E4A1845248A0BA130CA622A16B', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 1, NULL, NULL, 2, NULL, 0, 'Brush a very thin layer of the melted semi-sweet chocolate over the inside of a graham cracker.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEEE659AAF828A4292D30403A841CC31', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 2, NULL, NULL, 2, NULL, 0, 'Arrange one whole StackerMallow and two halves on top of the wet chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8447EC213B635540902D9269586A6ABF', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 3, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of chocolate on another graham cracker square and set it on top of the marshmallows creating a sandwich.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03B2D38C169E6648A1D9F538F6757B84', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 4, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 6 s''mores sandwiches.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C2C06208A97E145AF8ECBDC80943774', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 5, NULL, NULL, 2, NULL, 0, 'Pop in the freezer for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DED9F4F6335AA74F95AA65299BCF69F8', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 6, NULL, NULL, 2, NULL, 0, 'Dip one end of a paper straw or lollipop stick into melted yellow Candy Melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AA76F0C9593F346853478CE206BA06B', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 7, NULL, NULL, 2, NULL, 0, 'Insert that tip into the marshmallow center of a s''mores sandwich so that the straw goes up about 3/4 of the way through the sandwich.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACB71C39120C334796C568D824B1BD0C', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 8, NULL, NULL, 2, NULL, 0, 'Repeat, adding straws to all the s''mores, then allow the candy coating to dry for a few minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B15783D026BF2B41B091A07AF634C57C', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 9, NULL, NULL, 2, NULL, 0, 'Dip each of the s''mores into yellow candy melts, allowing the excess candy coating to drip off before setting them on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'299911D7720C824F9E9A1B69630ED7BB', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 10, NULL, NULL, 2, NULL, 0, 'Freeze for 5 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F293E032169ED04B9A93E5878F978691', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 11, NULL, NULL, 2, NULL, 0, 'Meanwhile, roll out the silver colored candy clay to about 1/8 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'946A7F0B3135A145AB7A77A87F2521CC', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 12, NULL, NULL, 2, NULL, 0, 'Cut out 1 inch circles (1 or 2 per Minion depending on how many eyes you want to add).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9743148240BA08408205769C58199209', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 13, NULL, NULL, 2, NULL, 0, 'Cut a 3/4 inch circle out of each of those 1 inch circles to create the goggles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C162BFDC6CDE9D488FD298981ED96144', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 14, NULL, NULL, 2, NULL, 0, 'Insert a jumbo candy eye in the center of each (or make 3/4 inch round candy clay eyes to insert).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC639141E2C41E4BAB6DAD7C4A076CDF', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 15, NULL, NULL, 2, NULL, 0, 'Roll out the black candy clay and cut into 1/4 inch wide by 2 inch long strips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAA17A0AC7038F4BACB5B38CE6AA08FB', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 16, NULL, NULL, 2, NULL, 0, 'Create smiles and hair using black candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D11084389E5BDC4381F7D58A8C597B87', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 17, NULL, NULL, 2, NULL, 0, 'Use yellow candy melts to attach one or two goggles (eyes) to each s''mores pop.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCE19373AABD4647826B8C6F28018324', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 18, NULL, NULL, 2, NULL, 0, 'Attach two black strips on either side of the goggles then add smiles and hair to some.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D46CA685D3F6C94B864014C2876AF2E0', X'D90B415AA6D22442BA2A3247D228AB30', X'D90B415AA6D22442BA2A3247D228AB30', 19, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for up to 2 weeks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59D7077403A02F46BB3D5B0122758F41', X'4C4321E23DCFAB479B581AFA7D6C971A', X'4C4321E23DCFAB479B581AFA7D6C971A', 0, NULL, NULL, 2, NULL, 0, 'Stir 2/3 cup caramel bits and cashews into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'788E45031235044A8F46E741F31A6646', X'4C4321E23DCFAB479B581AFA7D6C971A', X'4C4321E23DCFAB479B581AFA7D6C971A', 1, NULL, NULL, 2, NULL, 0, 'Quickly scoop out 24 spoonfuls onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'256944D9E222DB4D91206866EC53B7C2', X'4C4321E23DCFAB479B581AFA7D6C971A', X'4C4321E23DCFAB479B581AFA7D6C971A', 2, NULL, NULL, 2, NULL, 0, 'Immediately sprinkle on chopped cashews.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D638ABBECA10BB45A4EC1EC8BCDF0EEE', X'4C4321E23DCFAB479B581AFA7D6C971A', X'4C4321E23DCFAB479B581AFA7D6C971A', 3, NULL, NULL, 2, NULL, 0, 'Refrigerate for about 10 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55F5389CCB20794E830897DAD2503E60', X'4C4321E23DCFAB479B581AFA7D6C971A', X'4C4321E23DCFAB479B581AFA7D6C971A', 4, NULL, NULL, 2, NULL, 0, 'Remove and let sit at room temperature for at least 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A3B14396E83A0499C7D4A971D669FCD', X'4C4321E23DCFAB479B581AFA7D6C971A', X'4C4321E23DCFAB479B581AFA7D6C971A', 5, NULL, NULL, 2, NULL, 0, 'Combine the remaining 3 tablespoons of caramel bits with the heavy whipping cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CCF5D5E214A7E4DB85C6053F924EC67', X'4C4321E23DCFAB479B581AFA7D6C971A', X'4C4321E23DCFAB479B581AFA7D6C971A', 6, NULL, NULL, 2, NULL, 0, 'Heat on high in the microwave for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A100298C315BE747919498015557738C', X'4C4321E23DCFAB479B581AFA7D6C971A', X'4C4321E23DCFAB479B581AFA7D6C971A', 7, NULL, NULL, 2, NULL, 0, 'Stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CEF9984BEFA71C4CA02080E117FD7E12', X'4C4321E23DCFAB479B581AFA7D6C971A', X'4C4321E23DCFAB479B581AFA7D6C971A', 8, NULL, NULL, 2, NULL, 0, 'Drizzle over the chocolate clusters.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10417C76DE910F438FEA9680DFB7DCD5', X'4C4321E23DCFAB479B581AFA7D6C971A', X'4C4321E23DCFAB479B581AFA7D6C971A', 9, NULL, NULL, 2, NULL, 0, 'Allow the caramel to dry before packaging.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E0D05E24E92CA4F86DD9E30A8496489', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 0, NULL, NULL, 2, NULL, 0, 'Spread some peanut butter on 12 of the butterfly crackers then set another cracker over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FEFD51A4D8E97418E48F2764B60F7AF', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 1, NULL, NULL, 2, NULL, 0, 'Dip a peanut butter cracker into the melted candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93971192A0E4C949AC7E2BBA569B4743', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 2, NULL, NULL, 2, NULL, 0, 'Use a chocolate dipping fork to remove the butterfly and allow the excess candy melts to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'649A2D3E5723D346B76F20F401AA9023', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 3, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D27E8CEF88845459306424EDBB453F4', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 4, NULL, NULL, 2, NULL, 0, 'Immediately press two regular yellow or orange M&M''s on the upper portion of each wing.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67E6D4611E203545B0CBA7B37AE04835', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 5, NULL, NULL, 2, NULL, 0, 'Press two mini yellow or orange M&M''s on the lower portion of each wing.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7584156D0C25B489ECDE9D4441A8909', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 6, NULL, NULL, 2, NULL, 0, 'Press one regular green M&M at the top of the mid section creating the butterfly''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B49EF9F1D1140F499DB3E9388D806ED0', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 7, NULL, NULL, 2, NULL, 0, 'Arrange three mini green M&M''s in a row below the head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77AA41F8739647438BDCF08DD1A93411', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 8, NULL, NULL, 2, NULL, 0, 'Insert two candy licorice pieces into the top of each cracker creating antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C03020C4066CC442A34FA02ACDE74E4B', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 9, NULL, NULL, 2, NULL, 0, 'Pop the cracker into the freezer for 3-5 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5632A556CA689C46BEFDD7442B420FD6', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 10, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2859E74BEE1C1D45AEBD70196F6FE69D', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 11, NULL, NULL, 2, NULL, 0, 'Remove the crackers from the freezer and let sit at room temperature for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC28CBCDC0CE5E4188E72AFACB2BEC60', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 12, NULL, NULL, 2, NULL, 0, 'Draw two eyes and a smile on the green butterfly heads.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E762E4F6B5D0D4D955C76AEED604866', X'897B3807DA5E904AA8E3C4C49219FC19', X'897B3807DA5E904AA8E3C4C49219FC19', 13, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DCBDB0512013F47B4F14B60F77044C0', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 0, NULL, NULL, 2, NULL, 0, 'Line a baking pan with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E5A95FF9EA740459DA873431B713FAB', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 1, NULL, NULL, 2, NULL, 0, 'Set an 8 x 5.75 X 2 inch oval cake ring on the paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9A4353DD787624E996367F43D849E9A', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 2, NULL, NULL, 2, NULL, 0, 'Pour 6 ounces of the yellow chocolate into the cake ring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75ECB6C0295D0D4797C60A3BEDF1D11B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 3, NULL, NULL, 2, NULL, 0, 'Spread into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87BD8B05AA952742B0D70E2D21C05E1E', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 4, NULL, NULL, 2, NULL, 0, 'Tap the pan gently to remove any air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88ECDCAF87B05E4292F5B2E6D22D17AF', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 5, NULL, NULL, 2, NULL, 0, 'Chill in the fridge if using pure white chocolate and in the freezer if using Candy Melts for about 10 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15B7CC4AF725584FAE3E24388A41DCC7', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 6, NULL, NULL, 2, NULL, 0, 'Remove from cake ring and use a knife to trim any excess around the edges.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D27C63704AE214AB995D606C66A09AC', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 7, NULL, NULL, 2, NULL, 0, 'Repeat creating another yellow chocolate oval using 6 ounces of the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D62AC5188196E4488BA06F47203C7A63', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 8, NULL, NULL, 2, NULL, 0, 'Roll out white candy clay to 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA565F3A7E15F74397E2E6A9EC645BF5', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 9, NULL, NULL, 2, NULL, 0, 'Cut two circles using a 2 1/4 inch round cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1052631F87BBD4DA21CE14527A18D0D', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 10, NULL, NULL, 2, NULL, 0, 'Use the 1 5/8 inch round cutter to cut a hole out of the center of each large circle (for the Minion''s goggles).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A18DFD9682B4F841B691F14699169DDC', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 11, NULL, NULL, 2, NULL, 0, 'Flatten those smaller circles to about 1/8th of an inch thickness and cut using a 1 1/2 inch cutter (for the whites of the eyes).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'469730D82F2AA94DAF705067D48BD311', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 12, NULL, NULL, 2, NULL, 0, 'Roll out some black and white candy clay to 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9021A7BE75C8E49BF72017A86E1DA8B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 13, NULL, NULL, 2, NULL, 0, 'Cut a 7/8 x 1/2 rectangle out of each color.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECA66EFC8AB92444B71684E05F881CBE', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 14, NULL, NULL, 2, NULL, 0, 'Cut both in half lengthwise.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0168FF4C79D09E48896ACAAD6C349F59', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 15, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of piping gel along one long edge of a white piece and press it up against a long edge of a black piece.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF5501A8E2ED564DA995EB194755C5F1', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 16, NULL, NULL, 2, NULL, 0, 'Repeat creating two 7/8 X 1/2 inch black and white rectangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DD8FF01385CB6448A30E672D2BD2319', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 17, NULL, NULL, 2, NULL, 0, 'Cut a notch on the edge in the mid section of the black part of each.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0159403BB6109848A9276578BB6EDEF4', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 18, NULL, NULL, 2, NULL, 0, 'Use piping gel to attach them to the google pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C75DDCCB9F0094DA4B91AC710091EA0', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 19, NULL, NULL, 2, NULL, 0, 'Combine about a teaspoon of lemon extract with about a teaspoon of edible silver luster dust until you get a nice fluid yet thick mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BF19D3F8171044DACA699362FC27C3D', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 20, NULL, NULL, 2, NULL, 0, 'Paint the white candy clay goggles silver.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'975B9AF13546D74C9AE5EAA61639F970', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 21, NULL, NULL, 2, NULL, 0, 'Let dry for at least 30 minutes before touching.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'765F8E5B27C9B144807984E96714F503', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 22, NULL, NULL, 2, NULL, 0, 'Roll out some black candy clay to 1/16th of an inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18E01D945818964997FFD6923E904D88', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 23, NULL, NULL, 2, NULL, 0, 'Cut two circles using a 3/4 inch round plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C82138BF0992343A960C62878A85E08', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 24, NULL, NULL, 2, NULL, 0, 'Attach to the center of the white eye pieces (1 1/2 inch circles of candy clay).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FE576455064A94C8004EF25CE60A9CE', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 25, NULL, NULL, 2, NULL, 0, 'Roll out blue candy clay to 1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CB1306CA0F1E74EB317C16DE00FF3BD', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 26, NULL, NULL, 2, NULL, 0, 'Cut out the Minion Template (resized if needed to fit the size of your chocolate Minion).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A8E2D6552D1234684EA280141851B8A', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 27, NULL, NULL, 2, NULL, 0, 'Cut out the blue overalls, the straps, and the pocket and set on the blue candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3237A3D55A8524682BD2820E06954C7', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 28, NULL, NULL, 2, NULL, 0, 'Cut the candy clay out around the template.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B7ACBE1E3FF324F8D4C90B75AE76082', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 29, NULL, NULL, 2, NULL, 0, 'Use a fondant sticking wheel to create the stitching around the pockets and crotch area of the overalls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F95CE103B77EA4DA5F86DF744F88F7B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 30, NULL, NULL, 2, NULL, 0, 'Draw the `G` logo onto the half moon shaped pocked using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32DE477680F5604FA7BB1A92CDDCC82F', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 31, NULL, NULL, 2, NULL, 0, 'Use piping gel to attach all the pieces to one of the yellow chocolate ovals creating your Minion.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F178D1E6D4AA79458D67AD4DEFD25BC5', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 32, NULL, NULL, 2, NULL, 0, 'Draw on a smile using black food coloring marker or use a thin log of black candy clay to create one, if you prefer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC8512225835454BA86F456E8D8B7F5E', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 33, NULL, NULL, 2, NULL, 0, 'Roll out yellow candy clay into a 24 inch long log.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB92DE555592624388A0269F3C4BC62C', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 34, NULL, NULL, 2, NULL, 0, 'Flatten and roll out to 1/2 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'812D71421D929E44AF7667CB1E617819', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 35, NULL, NULL, 2, NULL, 0, 'Cut into a 1 1/2 inch (or 2 1/2 - 3 inch) wide by 24 inch long strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8E88329B5B1D0409BA1D34756B5007B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 36, NULL, NULL, 2, NULL, 0, 'Set the oval cake ring on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40D72C7781992E41A8F255CDBD64AAB5', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 37, NULL, NULL, 2, NULL, 0, 'Wrap the yellow strip of candy clay around the inside of the oval ring so that it creates the walls of your chocolate box.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08801046468E1C4C8C59829C834A055B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 38, NULL, NULL, 2, NULL, 0, 'Cut the ends as needed so they match up neatly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB8C6C6E9ADE934996F66CEC9C9A9583', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 39, NULL, NULL, 2, NULL, 0, 'Attach the seam using a small amount of piping gel. Make sure the seam does not stick to the inside of the cake ring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D986D3F6B3C4974B8A0A7E7A842F984A', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 40, NULL, NULL, 2, NULL, 0, 'Let dry for at least 30 minutes, preferably longer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB94EDC4D4731144B6A375AD91224D06', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 41, NULL, NULL, 2, NULL, 0, 'Remove the cake ring from the yellow candy clay walls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1BC298D9714464985852AD17C017C50', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 42, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of yellow chocolate on the top edge of the candy clay walls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECD26B2D6DDAC54CBE5382B20BB98E57', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 43, NULL, NULL, 2, NULL, 0, 'Set the plain yellow chocolate oval on top creating the base of the box.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71DD6CE700295F4084617C1A08D44DEB', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 44, NULL, NULL, 2, NULL, 0, 'Let dry for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'472F07E26663EC46BEC638AD4E5EF9B3', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 45, NULL, NULL, 2, NULL, 0, 'Carefully turn the box base right side up.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AA0BC7037BD51478C4540E61D6F72AE', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 46, NULL, NULL, 2, NULL, 0, 'If needed, brush the interior with melted yellow chocolate to reinforce the walls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED70FEA5365426419E6A0D3CBD4004A5', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 47, NULL, NULL, 2, NULL, 0, 'Let dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA6D6832751F16429DF3780D80562355', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', X'D263D0FADDAFBE44AB0C35ECAE1D9D5B', 48, NULL, NULL, 2, NULL, 0, 'Fill box with chocolate or candy and set the decorated chocolate Minion lid on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBE7F2C3B391BE4388E63F14948AC168', X'DEC4FB0DC89B2645BB074804341804CC', X'DEC4FB0DC89B2645BB074804341804CC', 0, NULL, NULL, 2, NULL, 0, 'Paint the pad and toes in each paw print mold with the colored candy melts, making 4 paws with each of the six colors.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06603D5174F5B64684564EAD9F242D31', X'DEC4FB0DC89B2645BB074804341804CC', X'DEC4FB0DC89B2645BB074804341804CC', 1, NULL, NULL, 2, NULL, 0, 'Pop the molds in the freezer for 2-3 minutes until the colored candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3CE677DC4B2B4429D90C320974C3448', X'DEC4FB0DC89B2645BB074804341804CC', X'DEC4FB0DC89B2645BB074804341804CC', 2, NULL, NULL, 2, NULL, 0, 'Remove and let the molds sit at room temperature for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F91CBA82764E06409776A41FA0ABA527', X'DEC4FB0DC89B2645BB074804341804CC', X'DEC4FB0DC89B2645BB074804341804CC', 3, NULL, NULL, 2, NULL, 0, 'Then fill one paw print about three quarters full of white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32049F45E4EC4C439B276298B4C4B769', X'DEC4FB0DC89B2645BB074804341804CC', X'DEC4FB0DC89B2645BB074804341804CC', 4, NULL, NULL, 2, NULL, 0, 'Immediately sprinkle on enough cookie pieces to cover the surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D19D7F3E4F29854DAEED4A39B632A492', X'DEC4FB0DC89B2645BB074804341804CC', X'DEC4FB0DC89B2645BB074804341804CC', 5, NULL, NULL, 2, NULL, 0, 'Repeat filling all the paw prints.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D04C6FB69871B438799654418EF30B7', X'DEC4FB0DC89B2645BB074804341804CC', X'DEC4FB0DC89B2645BB074804341804CC', 6, NULL, NULL, 2, NULL, 0, 'Freeze for 15-20 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EB04BDDBCC10B4CBB97D01EE683DC64', X'DEC4FB0DC89B2645BB074804341804CC', X'DEC4FB0DC89B2645BB074804341804CC', 7, NULL, NULL, 2, NULL, 0, 'Remove from the freezer and push the candy out of the molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B04957509A468249B23E07F2F27F619B', X'0924C5B281B5514AA63A2B40CD4A2ED7', X'0924C5B281B5514AA63A2B40CD4A2ED7', 0, NULL, NULL, 2, NULL, 0, 'Spoon 1/4-1/2 teaspoon of white candy melts onto a parchment paper lined baking sheet and spread it out into an oval shaped bunny foot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99169B0B8133534AAEABAAA93F9DCDE9', X'0924C5B281B5514AA63A2B40CD4A2ED7', X'0924C5B281B5514AA63A2B40CD4A2ED7', 1, NULL, NULL, 2, NULL, 0, 'Immediately add one pink heart sprinkle for the pad and three pink sugar pearls for the toes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5EFE0C5D2301B498BEF6ECC9E7F03A3', X'0924C5B281B5514AA63A2B40CD4A2ED7', X'0924C5B281B5514AA63A2B40CD4A2ED7', 2, NULL, NULL, 2, NULL, 0, 'Repeat creating more feet then freeze until hard, about 2 minutes.*');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E84688254AD02848AF0E02FCBA8417A7', X'0924C5B281B5514AA63A2B40CD4A2ED7', X'0924C5B281B5514AA63A2B40CD4A2ED7', 3, NULL, NULL, 2, NULL, 0, 'Roll mini marshmallows into small balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29ABA3ADB7B6E540BB45659E6861588A', X'0924C5B281B5514AA63A2B40CD4A2ED7', X'0924C5B281B5514AA63A2B40CD4A2ED7', 4, NULL, NULL, 2, NULL, 0, 'Arrange pretzels about 2 inches apart on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9FC42964E8D214397628741523D4A99', X'0924C5B281B5514AA63A2B40CD4A2ED7', X'0924C5B281B5514AA63A2B40CD4A2ED7', 5, NULL, NULL, 2, NULL, 0, 'Fill the center of one pretzel with white candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EF7F5FB4C703A44A2EEA0881C76A141', X'0924C5B281B5514AA63A2B40CD4A2ED7', X'0924C5B281B5514AA63A2B40CD4A2ED7', 6, NULL, NULL, 2, NULL, 0, 'Immediately add a marshmallow tail to the rounded top edge of the pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F888DF06732C0438644A6BE6EE5CF46', X'0924C5B281B5514AA63A2B40CD4A2ED7', X'0924C5B281B5514AA63A2B40CD4A2ED7', 7, NULL, NULL, 2, NULL, 0, 'Add two candy feet, one set over top of each of the smaller rounded edges of the pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FA1D327EAF0EA42901A77243E0A3128', X'0924C5B281B5514AA63A2B40CD4A2ED7', X'0924C5B281B5514AA63A2B40CD4A2ED7', 8, NULL, NULL, 2, NULL, 0, 'Repeat making more pretzels then pop them in the freezer for about 3-5 minutes until the candy coating hardens.*');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'591E97321F961E45BA04A9FEB7AEE37D', X'0924C5B281B5514AA63A2B40CD4A2ED7', X'0924C5B281B5514AA63A2B40CD4A2ED7', 9, NULL, NULL, 2, NULL, 0, '*If using pure white chocolate, refrigerate instead of freezing.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B539041C4805524EAC884D6110C0CC8E', X'8A3BA44BCC13B74F9ACFA8D3C03E4D0C', X'8A3BA44BCC13B74F9ACFA8D3C03E4D0C', 0, NULL, NULL, 2, NULL, 0, 'Stir the toffee bits into the chocolate and pour into a 5 inch heart cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B51B89AB56ECA147ABA6015A3ADCDD28', X'8A3BA44BCC13B74F9ACFA8D3C03E4D0C', X'8A3BA44BCC13B74F9ACFA8D3C03E4D0C', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle on M&M''s.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED062829310FA149B1A952AEDFF2AC0F', X'8A3BA44BCC13B74F9ACFA8D3C03E4D0C', X'8A3BA44BCC13B74F9ACFA8D3C03E4D0C', 2, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator if using pure chocolate and in the freezer if using Candy Melts until the chocolate hardens, about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'859B11FB3F31904190CC79E70FC6009D', X'8A3BA44BCC13B74F9ACFA8D3C03E4D0C', X'8A3BA44BCC13B74F9ACFA8D3C03E4D0C', 3, NULL, NULL, 2, NULL, 0, 'Remove and pop the chocolate out of the cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F922A4CA90B2248B1EA76CCCC32C598', X'8A3BA44BCC13B74F9ACFA8D3C03E4D0C', X'8A3BA44BCC13B74F9ACFA8D3C03E4D0C', 4, NULL, NULL, 2, NULL, 0, 'Set aside at room temperature for 20-30 minutes before packaging in clear cellophane bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90D2DDDF9704FF41A8C21E89D57A2A80', X'9AFCF96A2D5AB04693E8EA44942D2AA5', X'9AFCF96A2D5AB04693E8EA44942D2AA5', 0, NULL, NULL, 2, NULL, 0, 'Stir the crisp rice cereal into the chocolate and pour into a 5 inch heart cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11E0B5F927D03C4BAC851C196E616960', X'9AFCF96A2D5AB04693E8EA44942D2AA5', X'9AFCF96A2D5AB04693E8EA44942D2AA5', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle on M&M''s and sprinkle mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FBC3E03DC7A0046B7F10FCE6CA94951', X'9AFCF96A2D5AB04693E8EA44942D2AA5', X'9AFCF96A2D5AB04693E8EA44942D2AA5', 2, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator if using pure chocolate and in the freezer if using Candy Melts until the chocolate hardens, about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AA136A99392C04D9DDDA1240D9693DC', X'9AFCF96A2D5AB04693E8EA44942D2AA5', X'9AFCF96A2D5AB04693E8EA44942D2AA5', 3, NULL, NULL, 2, NULL, 0, 'Remove and pop the chocolate out of the cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C3DA0EAB74B7149A2B22D58F6ACBBE0', X'9AFCF96A2D5AB04693E8EA44942D2AA5', X'9AFCF96A2D5AB04693E8EA44942D2AA5', 4, NULL, NULL, 2, NULL, 0, 'Set aside at room temperature for 20-30 minutes before packaging in clear cellophane bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1647A66CF02B844FA51CFF5012119243', X'170C68B2BDEFE64E85CF80F1974E2B6F', X'170C68B2BDEFE64E85CF80F1974E2B6F', 0, NULL, NULL, 2, NULL, 0, 'Spoon Dulce de Leche into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2365D944D53BE489158512E30F8BC54', X'170C68B2BDEFE64E85CF80F1974E2B6F', X'170C68B2BDEFE64E85CF80F1974E2B6F', 1, NULL, NULL, 2, NULL, 0, 'Pour half of the chocolate into the heart shaped cookie cutter and spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA35A27F8FA2F440A511C80F0C9FA6C3', X'170C68B2BDEFE64E85CF80F1974E2B6F', X'170C68B2BDEFE64E85CF80F1974E2B6F', 2, NULL, NULL, 2, NULL, 0, 'Pipe a heart of the Dulce de Leche into the center of the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C389F94CA2F84459FB32ABBA0A628C9', X'170C68B2BDEFE64E85CF80F1974E2B6F', X'170C68B2BDEFE64E85CF80F1974E2B6F', 3, NULL, NULL, 2, NULL, 0, 'Chill for 3-5 minutes until the chocolate begins to set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B55DA82C7DF2F4BBBAF4165C3EEC5B6', X'170C68B2BDEFE64E85CF80F1974E2B6F', X'170C68B2BDEFE64E85CF80F1974E2B6F', 4, NULL, NULL, 2, NULL, 0, 'Pour the remaining chocolate over top and spread into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'238293B7D485A94E9491942A49625EF3', X'170C68B2BDEFE64E85CF80F1974E2B6F', X'170C68B2BDEFE64E85CF80F1974E2B6F', 5, NULL, NULL, 2, NULL, 0, 'Arrange the Mega M&M''s around the perimeter of the heart.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D45B0902C851B44B26E0C1A7E0E0CFF', X'170C68B2BDEFE64E85CF80F1974E2B6F', X'170C68B2BDEFE64E85CF80F1974E2B6F', 6, NULL, NULL, 2, NULL, 0, 'Chill until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DC74B855F7AC1459707AAA02228903F', X'170C68B2BDEFE64E85CF80F1974E2B6F', X'170C68B2BDEFE64E85CF80F1974E2B6F', 7, NULL, NULL, 2, NULL, 0, 'Set aside for 20-30 minutes before packaging.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A67AA142F28FD44DBDC1C7140EE32C4A', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 0, NULL, NULL, 2, NULL, 0, 'Combine the yellow Candy Melts and the sweetened condensed milk in a microwave safe mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34A92497C1FC4C41B4893A0F56BF9D54', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 1 minute then stir well until the candy begins to melt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E5246502DFBC343BAC46DDF06E4B11D', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 2, NULL, NULL, 2, NULL, 0, 'Heat for 30 more seconds on high power then let the bowl sit in the microwave for 2-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45CDAD167A2B9A48BC2010AFBBBAE600', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 3, NULL, NULL, 2, NULL, 0, 'Remove and stir thoroughly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76166E22DFF7B244BD891348F6D22201', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 4, NULL, NULL, 2, NULL, 0, 'Let rest for 2 more minutes then stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E7B4845D0D35A4CABF7B604DEF4B5DD', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 5, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments on high power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6ABD63E583E52499AEDDF894D3E8653', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 6, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap and set the bowl aside for several hours until the fudge firms up or refrigerate for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6880C84C70CD364CAE7FA994DA97C460', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 7, NULL, NULL, 2, NULL, 0, 'Meanwhile rub the black Candy Melts wafers in the palm of your hand. This will remove any white marks and soften the wafer slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0ED3D2A583B60B4B8B827C9950ACBBBD', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 8, NULL, NULL, 2, NULL, 0, 'Cut out one or two smiles from each wafer using a 1 1/4 inch round cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B094BA2909C2F4CB7B9EB11BDB83F81', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 9, NULL, NULL, 2, NULL, 0, 'Scoop out 1 1/2 tablespoons of fudge using an ice cream scoop or spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D003216D43BD5B4F94036AD9B0BFC652', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 10, NULL, NULL, 2, NULL, 0, 'Roll into a ball then flatten into a 2 1/4 inch round disc onto a non-stick silicone mat or piece of wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64FAC8E4C389B241A328922653A6BF08', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 11, NULL, NULL, 2, NULL, 0, 'Immediately press two heart sprinkles or two Sixlets into the fudge creating the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F63BDF456A1FB4EB502656A4DC11548', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 12, NULL, NULL, 2, NULL, 0, 'Press a black Candy Melts smile below the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5EE15CB20FFFB4B9220D643DBB77926', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 13, NULL, NULL, 2, NULL, 0, 'Repeat the process, creating a total of 30 emoji.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1021F29B58A954BA7ABDFC41E03770A', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 14, NULL, NULL, 2, NULL, 0, 'Pop the candy into the freezer for 10 minutes until they firm up and are easy to remove from the silicone mat or wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61BE8350F57DB04C82AC43E0060C611D', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 15, NULL, NULL, 2, NULL, 0, 'Let warm up for 15 minutes before packaging them in clear 3 inch x 5 inch cellophane bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6E74C61B279794FB87631DE8CF1AA4D', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 16, NULL, NULL, 2, NULL, 0, 'Fold over the top of the bag and tape it closed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AFC8EA620FA8C48A75CB28A48CA9FA8', X'D938BC02A319CB489A8BC29CB81D1752', X'D938BC02A319CB489A8BC29CB81D1752', 17, NULL, NULL, 2, NULL, 0, 'Print and cut the Valentine''s Day tags, fold them over, add your child''s name and the recipient''s name, then use double stick tape to attach them to the top of the bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'568C2E9B9F2B0D4D9093F789E2A53136', X'27D02F02238D154BBECBEA3BEBF5A2EA', X'27D02F02238D154BBECBEA3BEBF5A2EA', 0, NULL, NULL, 2, NULL, 0, 'Heat red candy melts in microwave on high power for 30 seconds then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91612573C3FDFB40B3A538E8811B322F', X'27D02F02238D154BBECBEA3BEBF5A2EA', X'27D02F02238D154BBECBEA3BEBF5A2EA', 1, NULL, NULL, 2, NULL, 0, 'Heat another 30 seconds then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3003CC5DBD256C48AE00813249508EEB', X'27D02F02238D154BBECBEA3BEBF5A2EA', X'27D02F02238D154BBECBEA3BEBF5A2EA', 2, NULL, NULL, 2, NULL, 0, 'Heat for 20 seconds then let sit in microwave for 2 minutes before stirring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38FE9964E2F7B243A797DADB9C40D567', X'27D02F02238D154BBECBEA3BEBF5A2EA', X'27D02F02238D154BBECBEA3BEBF5A2EA', 3, NULL, NULL, 2, NULL, 0, 'If not all melted, let sit for another 2 minutes then stir again.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'410821841E89324499F8053DEFCE23CA', X'27D02F02238D154BBECBEA3BEBF5A2EA', X'27D02F02238D154BBECBEA3BEBF5A2EA', 4, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'484A475550F8134DB9937F8D707F0420', X'27D02F02238D154BBECBEA3BEBF5A2EA', X'27D02F02238D154BBECBEA3BEBF5A2EA', 5, NULL, NULL, 2, NULL, 0, 'It''s best to keep the candy from getting over 95 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B13565398DE5BE4A841E56D0470F5E7A', X'27D02F02238D154BBECBEA3BEBF5A2EA', X'27D02F02238D154BBECBEA3BEBF5A2EA', 6, NULL, NULL, 2, NULL, 0, 'If the candy got really hot, let it cool, stirring often, until just warm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'943F188D5A96404A8577023FFE25CC4C', X'27D02F02238D154BBECBEA3BEBF5A2EA', X'27D02F02238D154BBECBEA3BEBF5A2EA', 7, NULL, NULL, 2, NULL, 0, 'Add the marshmallow creme and stir just until half of it is mixed in then add the food coloring, if using, and continue to stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A42ED6DDEEB2848BC0FA1E23206DD96', X'27D02F02238D154BBECBEA3BEBF5A2EA', X'27D02F02238D154BBECBEA3BEBF5A2EA', 8, NULL, NULL, 2, NULL, 0, 'The mixture will become thick quickly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00456A9F67028143B020C90ED9CA60A9', X'27D02F02238D154BBECBEA3BEBF5A2EA', X'27D02F02238D154BBECBEA3BEBF5A2EA', 9, NULL, NULL, 2, NULL, 0, 'Spread the red nougat into the bottom of a silicone loaf pan or into a metal loaf pan lined with strips of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51E131E81165444ABB76EA8163D2CF71', X'0B22F07D0F06454BBEF5477DA11FBF86', X'0B22F07D0F06454BBEF5477DA11FBF86', 0, NULL, NULL, 2, NULL, 0, 'Repeat the process above layering in the orange, yellow, green, blue, indigo, and purple nougat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D225240EE408D345AFEDDDE6E3D2FFA3', X'0B22F07D0F06454BBEF5477DA11FBF86', X'0B22F07D0F06454BBEF5477DA11FBF86', 1, NULL, NULL, 2, NULL, 0, 'Set aside for 30 minutes so that the candy firms up.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F562E1B63062D45BF479741AFD34C63', X'BA4A1DC2C4F9794E8D00ACD402F606F2', X'BA4A1DC2C4F9794E8D00ACD402F606F2', 0, NULL, NULL, 2, NULL, 0, 'Pour just enough chocolate to cover the top of the purple layer and spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D76828DC67AF648AEF4E6E5DBEAB78D', X'BA4A1DC2C4F9794E8D00ACD402F606F2', X'BA4A1DC2C4F9794E8D00ACD402F606F2', 1, NULL, NULL, 2, NULL, 0, 'Tap the mold on the counter a few times to remove any air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'421153F45619FF47BB9BE7AAA8012C2A', X'BA4A1DC2C4F9794E8D00ACD402F606F2', X'BA4A1DC2C4F9794E8D00ACD402F606F2', 2, NULL, NULL, 2, NULL, 0, 'If using pure chocolate refrigerate for 10-15 minutes until the chocolate hardens.If using Candy Melts freeze for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD7EADA0AD35684E84732588EAD25F23', X'BA4A1DC2C4F9794E8D00ACD402F606F2', X'BA4A1DC2C4F9794E8D00ACD402F606F2', 3, NULL, NULL, 2, NULL, 0, 'Remove and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BFC4A184E4CDE2439A111DA5D52F7E75', X'BA4A1DC2C4F9794E8D00ACD402F606F2', X'BA4A1DC2C4F9794E8D00ACD402F606F2', 4, NULL, NULL, 2, NULL, 0, 'Let the candy bar sit at room temperature for 15 minutes');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84215CC5C813544FBD39FB9E433453E1', X'BA4A1DC2C4F9794E8D00ACD402F606F2', X'BA4A1DC2C4F9794E8D00ACD402F606F2', 5, NULL, NULL, 2, NULL, 0, 'Set the candy bar up on two glasses that fit underneath.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'051714E75F1A4F45B55D66E57003EFB0', X'BA4A1DC2C4F9794E8D00ACD402F606F2', X'BA4A1DC2C4F9794E8D00ACD402F606F2', 6, NULL, NULL, 2, NULL, 0, 'Pour the remaining chocolate over top of the bar allowing it to run down over the sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'415CE76B2AA1DD46A2D876E9F5D83C12', X'BA4A1DC2C4F9794E8D00ACD402F606F2', X'BA4A1DC2C4F9794E8D00ACD402F606F2', 7, NULL, NULL, 2, NULL, 0, 'Clean up the drips of chocolate from the bottom edge of the bar using a spatula or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B7BF94C9EEDA1438453C92A80F577D7', X'BA4A1DC2C4F9794E8D00ACD402F606F2', X'BA4A1DC2C4F9794E8D00ACD402F606F2', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle on rainbow chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E9D581609EDA8468960CF1F99DD5E59', X'BA4A1DC2C4F9794E8D00ACD402F606F2', X'BA4A1DC2C4F9794E8D00ACD402F606F2', 9, NULL, NULL, 2, NULL, 0, 'Chill for 10-15 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D78920E84B0264E983BB43CB0CA01DF', X'57FF5E3A08A68B47A670FACAD72266D2', X'57FF5E3A08A68B47A670FACAD72266D2', 0, NULL, NULL, 2, NULL, 0, 'Pour candy melts into silicone shot glass mold reserving about a tablespoon of candy to use as `glue` to attach the sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'918B735854696F4DA18B1C977F3DD200', X'57FF5E3A08A68B47A670FACAD72266D2', X'57FF5E3A08A68B47A670FACAD72266D2', 1, NULL, NULL, 2, NULL, 0, 'Tap the mold to allow the air bubbles to rise to the surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5C95BC1DFF0934A87430C30A91B1FCE', X'57FF5E3A08A68B47A670FACAD72266D2', X'57FF5E3A08A68B47A670FACAD72266D2', 2, NULL, NULL, 2, NULL, 0, 'If needed, pop the air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C711AF2BAAB2B4AABF62A5B3EF56702', X'57FF5E3A08A68B47A670FACAD72266D2', X'57FF5E3A08A68B47A670FACAD72266D2', 3, NULL, NULL, 2, NULL, 0, 'Freeze the mold for about 15 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD9AB58E6EDA784A9E47511C55BC8073', X'57FF5E3A08A68B47A670FACAD72266D2', X'57FF5E3A08A68B47A670FACAD72266D2', 4, NULL, NULL, 2, NULL, 0, 'Remove and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B89982B46100D40A12A086707B5B494', X'57FF5E3A08A68B47A670FACAD72266D2', X'57FF5E3A08A68B47A670FACAD72266D2', 5, NULL, NULL, 2, NULL, 0, 'Use dabs of melted chocolate or candy melts to attach pink or red heart sprinkles around the outside of the chocolate heart cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F14DB9F43DB52A4BA1B9F36BB4A195AD', X'57FF5E3A08A68B47A670FACAD72266D2', X'57FF5E3A08A68B47A670FACAD72266D2', 6, NULL, NULL, 2, NULL, 0, 'Fill with candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC5FFCC4DE795D43847C270D5BD1A2F9', X'ABCE891A2DA3224AB2B31BD37F7D1C4C', X'ABCE891A2DA3224AB2B31BD37F7D1C4C', 0, NULL, NULL, 2, NULL, 0, 'Line an 8 inch square pan with non-stick tin foil or parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7382B0A540E25C44AE149647372DE480', X'ABCE891A2DA3224AB2B31BD37F7D1C4C', X'ABCE891A2DA3224AB2B31BD37F7D1C4C', 1, NULL, NULL, 2, NULL, 0, 'Combine the white confectionery coating wafers with the peanut butter chips in a large microwave safe mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E04102F1DFCED54E8C02544C373F7289', X'ABCE891A2DA3224AB2B31BD37F7D1C4C', X'ABCE891A2DA3224AB2B31BD37F7D1C4C', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C03FC846518BE248B1BD3E8BA0539F8D', X'ABCE891A2DA3224AB2B31BD37F7D1C4C', X'ABCE891A2DA3224AB2B31BD37F7D1C4C', 3, NULL, NULL, 2, NULL, 0, 'Add the peanut butter and salt and stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54D19A4E3FB370458521BB8782A91F13', X'ABCE891A2DA3224AB2B31BD37F7D1C4C', X'ABCE891A2DA3224AB2B31BD37F7D1C4C', 4, NULL, NULL, 2, NULL, 0, 'Pour into the lined pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA359E7608B86441AF540FB1F61E6431', X'ABCE891A2DA3224AB2B31BD37F7D1C4C', X'ABCE891A2DA3224AB2B31BD37F7D1C4C', 5, NULL, NULL, 2, NULL, 0, 'Let firm up at room temperature for at least 6 hours before cutting or freeze for 30-45 minutes, then remove and let thaw for one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14698483FDB83647AE43D734C802C1F1', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 0, NULL, NULL, 2, NULL, 0, 'Arrange sets of 5 Pretzel Dipping Sticks on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6DDE617C4C74484AAE18A95ADE4860EE', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 1, NULL, NULL, 2, NULL, 0, 'Pour melted green candy melts into a tall glass or mug.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FB1B2D1F8A5374483E50105A6DED57D', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 2, NULL, NULL, 2, NULL, 0, 'Dip one pretzel in the green candy melts so that all but about 1/2 inch at one end is covered.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FD383FDF2831844A4A2E80A11E948EE', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 3, NULL, NULL, 2, NULL, 0, 'Lift out of candy coating and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03CA8699971D554DA48F9CAFAFF38FDA', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 4, NULL, NULL, 2, NULL, 0, 'Set on the parchment lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E297200F2DF1249A53596E72A41A95E', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 5, NULL, NULL, 2, NULL, 0, 'Dip a second pretzel then set it so that it sticks to the first pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1FE8E510D17A724782571472089ECA43', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 6, NULL, NULL, 2, NULL, 0, 'Repeat until you have all five dipped pretzels stuck to each other forming a rectangle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A15B49EA77C45469B03ED706221DEC1', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 7, NULL, NULL, 2, NULL, 0, 'Freeze for 3-4 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95419497AD1BFB439BB9C8C60EE32F91', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 8, NULL, NULL, 2, NULL, 0, 'Let sit at room temperature for 5 minutes before decorating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70C2E44EC947164E8648AB5580F6EFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 9, NULL, NULL, 2, NULL, 0, 'Pour black candy melts into a zip top bag and snip the tip off of one end.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02C04EBE505454469610E2C7793359AE', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 10, NULL, NULL, 2, NULL, 0, 'Pipe black candy melts over the exposed edge of the pretzels making the candy look like hair.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9BEB75D77D882C40A48A6CC36938E509', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 11, NULL, NULL, 2, NULL, 0, 'Pipe a scar and a mouth onto each pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA3F9F1C8232B2499A5C10D2098DE11D', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 12, NULL, NULL, 2, NULL, 0, 'Pipe a dot of black candy melts on the back side of two candy eyes and attach them to each of the pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E043AA264DAD44AB24BE11630BC4EEF', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 13, NULL, NULL, 2, NULL, 0, 'Pop in the freezer for 1-2 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'726BE460F863E6469140DDA5EE73BCE2', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 14, NULL, NULL, 2, NULL, 0, 'Break or cut enough dipping pretzels to create 36 small pieces, approximately 1/2 inch in length.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CB14544B6153E47AC4F88B5C4511899', X'0321A5B09C10DA43A06F795EB0FDBFD6', X'0321A5B09C10DA43A06F795EB0FDBFD6', 15, NULL, NULL, 2, NULL, 0, 'Dip one end of two pretzel pieces in melted green candy melts and attach on either side of the pretzel monster to create it''s bolts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66145BE57217D7478A5FB10E914C28F6', X'2A0B6DA204339F429DC0E82440C94A16', X'2A0B6DA204339F429DC0E82440C94A16', 0, NULL, NULL, 2, NULL, 0, 'Dip a small balloon in white chocolate so that you cover about 3/4 of the balloon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D0EDED46690724B965009780CAC7A42', X'2A0B6DA204339F429DC0E82440C94A16', X'2A0B6DA204339F429DC0E82440C94A16', 1, NULL, NULL, 2, NULL, 0, 'Remove balloon from the white chocolate and allow the excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BC396E50015BF40A52891A74708E400', X'2A0B6DA204339F429DC0E82440C94A16', X'2A0B6DA204339F429DC0E82440C94A16', 2, NULL, NULL, 2, NULL, 0, 'Set dipped balloon on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2819F22AC0D5744A85C8AFFB8C502045', X'2A0B6DA204339F429DC0E82440C94A16', X'2A0B6DA204339F429DC0E82440C94A16', 3, NULL, NULL, 2, NULL, 0, 'Repeat dipping a total of 6 balloons.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E60620D43FAAF4CBABC9AEC86F096B1', X'2A0B6DA204339F429DC0E82440C94A16', X'2A0B6DA204339F429DC0E82440C94A16', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate for about 5 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52A9C3A2FF959149B73795465919355B', X'2A0B6DA204339F429DC0E82440C94A16', X'2A0B6DA204339F429DC0E82440C94A16', 5, NULL, NULL, 2, NULL, 0, 'Repeat, dipping 6 more balloons.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FE314C7A86D384BBF649CB41A16E289', X'2A0B6DA204339F429DC0E82440C94A16', X'2A0B6DA204339F429DC0E82440C94A16', 6, NULL, NULL, 2, NULL, 0, 'Remove balloons from fridge and immediately cut a small hole in the top of the balloon and allow the balloon to gently deflate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'28DE79B4FD011A43855BA436B759A952', X'2A0B6DA204339F429DC0E82440C94A16', X'2A0B6DA204339F429DC0E82440C94A16', 7, NULL, NULL, 2, NULL, 0, 'Pull the balloon out of the chocolate bowl and discard.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A129560F7813BD4699A728678C48AA18', X'2A0B6DA204339F429DC0E82440C94A16', X'2A0B6DA204339F429DC0E82440C94A16', 8, NULL, NULL, 2, NULL, 0, 'Pour the black candy melts or dark chocolate into a small zip top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9965C11FDD3ACF46B3802489A87E27F0', X'2A0B6DA204339F429DC0E82440C94A16', X'2A0B6DA204339F429DC0E82440C94A16', 9, NULL, NULL, 2, NULL, 0, 'Snip off one tip from the bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F23F1D6F86A3714B829D1D430D032938', X'2A0B6DA204339F429DC0E82440C94A16', X'2A0B6DA204339F429DC0E82440C94A16', 10, NULL, NULL, 2, NULL, 0, 'Pipe eyes, nostrils, and a mouth onto each bowl creating Jack''s face.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'075920AFC2F24547A95B4EC6398D4C7C', X'2A0B6DA204339F429DC0E82440C94A16', X'2A0B6DA204339F429DC0E82440C94A16', 11, NULL, NULL, 2, NULL, 0, 'Freeze for 2-3 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8ECCE7F3C4BCB14891D9FAD396A99BFA', X'8FE7C209C545184A82F1502EFBB0DAB1', X'8FE7C209C545184A82F1502EFBB0DAB1', 0, NULL, NULL, 2, NULL, 0, 'Combine cream cheese and sugar in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9FB156A492F2AB4E806DEA68E1832781', X'8FE7C209C545184A82F1502EFBB0DAB1', X'8FE7C209C545184A82F1502EFBB0DAB1', 1, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on medium-high speed until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B3F8DA457E8E14089ADCFFDE1551AA8', X'8FE7C209C545184A82F1502EFBB0DAB1', X'8FE7C209C545184A82F1502EFBB0DAB1', 2, NULL, NULL, 2, NULL, 0, 'Add marshmallow fluff and vanilla bean paste or extract and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FF333186E716A4F876834A60A2041B4', X'8FE7C209C545184A82F1502EFBB0DAB1', X'8FE7C209C545184A82F1502EFBB0DAB1', 3, NULL, NULL, 2, NULL, 0, 'Pour in whipped topping and beat until creamy and smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C844FD239298DD4F976B5DEAB4FE484F', X'8FE7C209C545184A82F1502EFBB0DAB1', X'8FE7C209C545184A82F1502EFBB0DAB1', 4, NULL, NULL, 2, NULL, 0, 'Spoon into a pastry bag fitted with a large star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1672ECEA65E5094EA4E8133CCEB333DF', X'8FE7C209C545184A82F1502EFBB0DAB1', X'8FE7C209C545184A82F1502EFBB0DAB1', 5, NULL, NULL, 2, NULL, 0, 'Pipe the cheesecake mousse into each chocolate bowl filling them just half way.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD0174FB9118924089B27D30CB5A1E6C', X'8FE7C209C545184A82F1502EFBB0DAB1', X'8FE7C209C545184A82F1502EFBB0DAB1', 6, NULL, NULL, 2, NULL, 0, 'Break up an Oreo and crumble it in each chocolate bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E76AC54D8556524ABF85C368BDB6D597', X'8FE7C209C545184A82F1502EFBB0DAB1', X'8FE7C209C545184A82F1502EFBB0DAB1', 7, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of cheesecake mousse over the cookies in each chocolate bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EB2CF62A314554FAFC8E702C99736F8', X'8FE7C209C545184A82F1502EFBB0DAB1', X'8FE7C209C545184A82F1502EFBB0DAB1', 8, NULL, NULL, 2, NULL, 0, 'Crush the remaining Oreo Cookies into fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'232B79D2566C8540A9474AF179FB4D8E', X'8FE7C209C545184A82F1502EFBB0DAB1', X'8FE7C209C545184A82F1502EFBB0DAB1', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle the crumbs over top of the mousse filled bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3FE951113ABD546B52FD7D3B72FFBEE', X'8FE7C209C545184A82F1502EFBB0DAB1', X'8FE7C209C545184A82F1502EFBB0DAB1', 10, NULL, NULL, 2, NULL, 0, 'Serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30AB2C6085B583479DF4D05BFAAAB441', X'8127C6CADDA74B4B9EB780FF00424D27', X'8127C6CADDA74B4B9EB780FF00424D27', 0, NULL, NULL, 2, NULL, 0, 'Pour white and pink candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D135CBBEE78D07419A5D11D58828D222', X'8127C6CADDA74B4B9EB780FF00424D27', X'8127C6CADDA74B4B9EB780FF00424D27', 1, NULL, NULL, 2, NULL, 0, 'Heat for three 30 second increments on high power, stirring well after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C7C835B400E59468C23B3F4779C3863', X'8127C6CADDA74B4B9EB780FF00424D27', X'8127C6CADDA74B4B9EB780FF00424D27', 2, NULL, NULL, 2, NULL, 0, 'If needed heat for 10 second increments on high power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75149D2B9E9AA54A9420066C82A59810', X'8127C6CADDA74B4B9EB780FF00424D27', X'8127C6CADDA74B4B9EB780FF00424D27', 3, NULL, NULL, 2, NULL, 0, 'Spoon 1 tablespoon of candy coating into each brain cavity reserving the remaining candy to top off the brains once filled.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'802FBB5CA15D32478F63576EFD57A103', X'8127C6CADDA74B4B9EB780FF00424D27', X'8127C6CADDA74B4B9EB780FF00424D27', 4, NULL, NULL, 2, NULL, 0, 'Brush the candy around the entire interior of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DCBE40EEFFF464BB6EE044534D46A1C', X'8127C6CADDA74B4B9EB780FF00424D27', X'8127C6CADDA74B4B9EB780FF00424D27', 5, NULL, NULL, 2, NULL, 0, 'Freeze for 3-5 minutes just until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F70426E903E00419F5250758A6AADBD', X'205C768B825266499FA3B8814AAE8DE4', X'205C768B825266499FA3B8814AAE8DE4', 0, NULL, NULL, 2, NULL, 0, 'Stir the preserves until very runny, adding a drop or so of water if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7029D074C973A4DBE097C77ABF360AD', X'205C768B825266499FA3B8814AAE8DE4', X'205C768B825266499FA3B8814AAE8DE4', 1, NULL, NULL, 2, NULL, 0, 'If needed, you can warm your jelly, just be sure it''s only slightly warm when you pour it into the brain.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61B73614E2595D40BB0B34109723DEDB', X'205C768B825266499FA3B8814AAE8DE4', X'205C768B825266499FA3B8814AAE8DE4', 2, NULL, NULL, 2, NULL, 0, 'Spoon 1 tablespoon into each brain.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6981ACFC606E94794E08F7B432C7458', X'8600FBD88CE5C84A974916F81012FE6B', X'8600FBD88CE5C84A974916F81012FE6B', 0, NULL, NULL, 2, NULL, 0, 'Combine the 4 ounces of white candy melts with the peanut butter chips in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D74D1A36F098646993574DA371B4AFB', X'8600FBD88CE5C84A974916F81012FE6B', X'8600FBD88CE5C84A974916F81012FE6B', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for two 30 second increments, stirring after each.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61FB5E81D49EA44683A1C7B173B61B75', X'8600FBD88CE5C84A974916F81012FE6B', X'8600FBD88CE5C84A974916F81012FE6B', 2, NULL, NULL, 2, NULL, 0, 'Heat for 15 second increments on high power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EDB0E828D828F45A0040545BCE069AB', X'8600FBD88CE5C84A974916F81012FE6B', X'8600FBD88CE5C84A974916F81012FE6B', 3, NULL, NULL, 2, NULL, 0, 'Stir in the peanut butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5242CA7136353744BED231D896D6D06B', X'8600FBD88CE5C84A974916F81012FE6B', X'8600FBD88CE5C84A974916F81012FE6B', 4, NULL, NULL, 2, NULL, 0, 'Allow the fudge to cool for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A11023FABF21549A583ED8207033875', X'8600FBD88CE5C84A974916F81012FE6B', X'8600FBD88CE5C84A974916F81012FE6B', 5, NULL, NULL, 2, NULL, 0, 'Pour a heaping tablespoonful into each brain on top of the jelly, filling to 1/8th of an inch from the edge of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C5E3FBD18E023468BC83EA51A9DF8B8', X'8600FBD88CE5C84A974916F81012FE6B', X'8600FBD88CE5C84A974916F81012FE6B', 6, NULL, NULL, 2, NULL, 0, 'Freeze the brains for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AF710C23234E7499A62ED4A6B8C216E', X'8600FBD88CE5C84A974916F81012FE6B', X'8600FBD88CE5C84A974916F81012FE6B', 7, NULL, NULL, 2, NULL, 0, 'Remove and let sit for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C76A648664E5F343A39A27BEE4BDD8C0', X'8600FBD88CE5C84A974916F81012FE6B', X'8600FBD88CE5C84A974916F81012FE6B', 8, NULL, NULL, 2, NULL, 0, 'Re-heat the pinkish candy coating in the microwave for 10 second bursts at high power, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'367EF719BDE67B4390FFB57FE31D8040', X'8600FBD88CE5C84A974916F81012FE6B', X'8600FBD88CE5C84A974916F81012FE6B', 9, NULL, NULL, 2, NULL, 0, 'Spoon about a teaspoon of the candy coating on top of each brain, over the peanut butter fudge layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'653C2190B3D9CB4FBAA3A1CB5CEA0B88', X'8600FBD88CE5C84A974916F81012FE6B', X'8600FBD88CE5C84A974916F81012FE6B', 10, NULL, NULL, 2, NULL, 0, 'Smooth it out by gently tapping the mold on the table.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B0411B55CA7E443A27333564BC72D37', X'8600FBD88CE5C84A974916F81012FE6B', X'8600FBD88CE5C84A974916F81012FE6B', 11, NULL, NULL, 2, NULL, 0, 'Wipe off any candy coating on the top edge of the brain mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6E7DFC70D28F646808BB6FD36FD5A15', X'8600FBD88CE5C84A974916F81012FE6B', X'8600FBD88CE5C84A974916F81012FE6B', 12, NULL, NULL, 2, NULL, 0, 'Freeze for about 5 minutes until the candy hardens.');
