INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7BBDA6DF5961443B83CB75726FC11EB', X'C5AD329AF8058B4B8B013809A212B53B', X'C5AD329AF8058B4B8B013809A212B53B', 3, NULL, NULL, 2, NULL, 0, 'Insert lollipop sticks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EBFE1E1144698449A64A0D86A66EB26', X'C5AD329AF8058B4B8B013809A212B53B', X'C5AD329AF8058B4B8B013809A212B53B', 4, NULL, NULL, 2, NULL, 0, 'Freeze for 8-10 minutes until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAC1F4FC14F8E24BB5935F9D4784E530', X'C5AD329AF8058B4B8B013809A212B53B', X'C5AD329AF8058B4B8B013809A212B53B', 5, NULL, NULL, 2, NULL, 0, 'Remove from freezer and pop candy out of mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89A0F8B0AC363C4196FCDDF6D84B6BC8', X'C5AD329AF8058B4B8B013809A212B53B', X'C5AD329AF8058B4B8B013809A212B53B', 6, NULL, NULL, 2, NULL, 0, 'Let sit at room temperature for 15-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FADDD1B8F58504FB9E09385C0FBCCC9', X'C5AD329AF8058B4B8B013809A212B53B', X'C5AD329AF8058B4B8B013809A212B53B', 7, NULL, NULL, 2, NULL, 0, 'Brush a light coating of corn syrup over front of lollipop.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA02CD77ACCEA24891193572F71CB67A', X'C5AD329AF8058B4B8B013809A212B53B', X'C5AD329AF8058B4B8B013809A212B53B', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle cookie crumbs over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01E68688C7844744B5C0A5FA865DF303', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 0, NULL, NULL, 2, NULL, 0, 'Butter the sides of a heavy 1-1/2 or 2 quart saucepan and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33F8667BD43D3D49AE0562A20A7015DD', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 1, NULL, NULL, 2, NULL, 0, 'In another 2 or 3 quart saucepan combine the water, pectin, and baking soda.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3E274552974C94CA9433A5152590D3C', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil over high heat, stirring constantly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6172071FBEA04B4F8B1A855DD77F697E', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat, cover, and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'524BB9F1F30E3C45879F59AE53666855', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 4, NULL, NULL, 2, NULL, 0, 'In the buttered saucepan add corn syrup and water.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36F646FE6867004BB2B710D5CB099757', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 5, NULL, NULL, 2, NULL, 0, 'Set over medium-high heat stirring until sugar disolves.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53C85868658CF84E9B9F192F11E354B4', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 6, NULL, NULL, 2, NULL, 0, 'Bring to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F70A00DEAC1B341B0814C5AA2EF0F2A', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 7, NULL, NULL, 2, NULL, 0, 'When candy reaches 260 degrees Fahrenheit, remove the lid and place the pot of pectin back on high heat and bring to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0916354F20907945BCCF04D4D6DAB07E', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 8, NULL, NULL, 2, NULL, 0, 'When candy reaches 280 degrees F, remove from heat and slowly pour in the hot pectin mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADEFA9B349C73F45A1B9DF18BF3D96C6', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 9, NULL, NULL, 2, NULL, 0, 'Return to medium-high heat and stir for one minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'104C101A605AA74690276D9F7B0F14AA', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 10, NULL, NULL, 2, NULL, 0, 'Remove from heat and stir in 1/4 - 1/2 teaspoon flavored oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9E75C627A201D45A7B625096CC24D92', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 11, NULL, NULL, 2, NULL, 0, 'Add coloring, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'74F4BDDD26EB544B9BBBE5DC14E3220E', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 12, NULL, NULL, 2, NULL, 0, 'Carefully pour into silicone candy mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3061B821C99C964792DEDFDE142163E9', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 13, NULL, NULL, 2, NULL, 0, 'Set molds aside for 6 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4012E2E64E2FEB45BBBA8EB2DD9A7A7B', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 14, NULL, NULL, 2, NULL, 0, 'Turn molds upside down and press on the back side of the mold to remove the candies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'962B577C72A16C41BFBF143B858FE8A4', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 15, NULL, NULL, 2, NULL, 0, 'Add details using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'320ABA34582AC3499EC71D3EA1971AC4', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 16, NULL, NULL, 2, NULL, 0, 'Roll in colored decorating sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDDC5C123CCB8A4AADD856F8A30E5232', X'82BD1B6F4662D046A3767BEC456315D8', X'82BD1B6F4662D046A3767BEC456315D8', 17, NULL, NULL, 2, NULL, 0, 'Set aside for 1 hour before packaging.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C8EA2CCAF8B1E4D9C8B1D658CDC5CB4', X'ADBAC23D6B938E469C3A44C736BAA5B6', X'ADBAC23D6B938E469C3A44C736BAA5B6', 0, NULL, NULL, 2, NULL, 0, 'Combine the butterscotch chips and white confectionery coating wafers in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F255C8E419C154285E7C04B3DB40659', X'ADBAC23D6B938E469C3A44C736BAA5B6', X'ADBAC23D6B938E469C3A44C736BAA5B6', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 15-30 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EFBA13531580F34F81BF9BE203B8BB3E', X'ADBAC23D6B938E469C3A44C736BAA5B6', X'ADBAC23D6B938E469C3A44C736BAA5B6', 2, NULL, NULL, 2, NULL, 0, 'Stir in cereal crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03F4DAF59D1BA24187A420B5AD52DFFE', X'ADBAC23D6B938E469C3A44C736BAA5B6', X'ADBAC23D6B938E469C3A44C736BAA5B6', 3, NULL, NULL, 2, NULL, 0, 'Spoon or pipe into starfish candy mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22DF6D90921B8E4F92B6E2CD857EB4E8', X'ADBAC23D6B938E469C3A44C736BAA5B6', X'ADBAC23D6B938E469C3A44C736BAA5B6', 4, NULL, NULL, 2, NULL, 0, 'Freeze for 8-12 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E5F0A1B622B9C4FA09F2B6DA8DBBD11', X'ADBAC23D6B938E469C3A44C736BAA5B6', X'ADBAC23D6B938E469C3A44C736BAA5B6', 5, NULL, NULL, 2, NULL, 0, 'Remove and un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3F1ED2EDD062F489AD1F3F91368174F', X'D105C727E5D08740B06682E63C7868A1', X'D105C727E5D08740B06682E63C7868A1', 0, NULL, NULL, 2, NULL, 0, 'Brush a thin line or two of the colored confectionery coating onto the sides of a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F5E31BF6FA5DE4EA2397C3BB8A399BD', X'D105C727E5D08740B06682E63C7868A1', X'D105C727E5D08740B06682E63C7868A1', 1, NULL, NULL, 2, NULL, 0, 'Fill bag with white confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D971E778A9CC54EAB122A84879239EE', X'D105C727E5D08740B06682E63C7868A1', X'D105C727E5D08740B06682E63C7868A1', 2, NULL, NULL, 2, NULL, 0, 'Snip tip off bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97E34936D8E47E4999232C2F6B411CE8', X'D105C727E5D08740B06682E63C7868A1', X'D105C727E5D08740B06682E63C7868A1', 3, NULL, NULL, 2, NULL, 0, 'Pipe into sea shell candy molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0FC0410857DBAF4FA1746426C8527CA4', X'D105C727E5D08740B06682E63C7868A1', X'D105C727E5D08740B06682E63C7868A1', 4, NULL, NULL, 2, NULL, 0, 'Freeze for 5-10 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F0C553621E2E24E871E423873ED30C1', X'D105C727E5D08740B06682E63C7868A1', X'D105C727E5D08740B06682E63C7868A1', 5, NULL, NULL, 2, NULL, 0, 'Remove and un-mold the candy shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC9958CC1520114898198F4F77150177', X'2583A1EB5DA8834D9C24D4E7C70941A1', X'2583A1EB5DA8834D9C24D4E7C70941A1', 0, NULL, NULL, 2, NULL, 0, 'Color the white chocolate using candy coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9404C7F12573574F949E0484E86BE006', X'2583A1EB5DA8834D9C24D4E7C70941A1', X'2583A1EB5DA8834D9C24D4E7C70941A1', 1, NULL, NULL, 2, NULL, 0, 'You''ll use approximately one ounce of chocolate per cup, so color accordingly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C169BB77FA739F44A9B475265F08B1E6', X'4CA6A0D6567F0447B917FB0767B0E389', X'4CA6A0D6567F0447B917FB0767B0E389', 0, NULL, NULL, 2, NULL, 0, 'Fill a small plastic shot glass full of colored white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12A43A73B20A0E4FA9BE028D33CFD595', X'4CA6A0D6567F0447B917FB0767B0E389', X'4CA6A0D6567F0447B917FB0767B0E389', 1, NULL, NULL, 2, NULL, 0, 'Let the cup sit at room temperature for 1-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8786CF22F8342649B8B38E116071B91E', X'4CA6A0D6567F0447B917FB0767B0E389', X'4CA6A0D6567F0447B917FB0767B0E389', 2, NULL, NULL, 2, NULL, 0, 'Turn shot glass upside down and allow the excess chocolate to drip out, leaving just a thin shell.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'597C1A1D24FDD749B00E35D81B2B883A', X'4CA6A0D6567F0447B917FB0767B0E389', X'4CA6A0D6567F0447B917FB0767B0E389', 3, NULL, NULL, 2, NULL, 0, 'Set aside for 5 minutes then refrigerate for 5-10 minutes if using pure chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B3DEDDB0292BA4992FB5BD698F9D934', X'4CA6A0D6567F0447B917FB0767B0E389', X'4CA6A0D6567F0447B917FB0767B0E389', 4, NULL, NULL, 2, NULL, 0, 'If using candy melts, freeze for 5-10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'254F320C775A9943A31546AF3382AF11', X'4CA6A0D6567F0447B917FB0767B0E389', X'4CA6A0D6567F0447B917FB0767B0E389', 5, NULL, NULL, 2, NULL, 0, 'Remove and gently pull the chocolate out of the shot glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71761EDE62A1BB45BBB460EB669C83A1', X'1C817E7726C9C34E85F369BC330B1C8C', X'1C817E7726C9C34E85F369BC330B1C8C', 0, NULL, NULL, 2, NULL, 0, 'Brush chocolate onto the bottom and up the sides of a plastic shot glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CD75362D4660844B0D1447BEBEA4D27', X'1C817E7726C9C34E85F369BC330B1C8C', X'1C817E7726C9C34E85F369BC330B1C8C', 1, NULL, NULL, 2, NULL, 0, 'Chill until hardened, then brush another layer over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9A039452C575446979B55A994F99D6B', X'1C817E7726C9C34E85F369BC330B1C8C', X'1C817E7726C9C34E85F369BC330B1C8C', 2, NULL, NULL, 2, NULL, 0, 'Chill until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09F790FADA361B47B79D87403EA1B92F', X'1C817E7726C9C34E85F369BC330B1C8C', X'1C817E7726C9C34E85F369BC330B1C8C', 3, NULL, NULL, 2, NULL, 0, 'Remove and gently pull the chocolate out of the shot glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68AADC78E9C50C44A43E062D16F2A8A8', X'6B9B21FF51A1764898A0FD286C5F16E8', X'6B9B21FF51A1764898A0FD286C5F16E8', 0, NULL, NULL, 2, NULL, 0, 'Cut thin 3/16th inch by 3 1/2 inch long strips of white modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'049BF5E3D5F4AF4B8D7FB7430BECBD1B', X'6B9B21FF51A1764898A0FD286C5F16E8', X'6B9B21FF51A1764898A0FD286C5F16E8', 1, NULL, NULL, 2, NULL, 0, 'Attach one end near the top edge of a chocolate pail.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C5A81B831CD5A4B813C92C94295FBE2', X'6B9B21FF51A1764898A0FD286C5F16E8', X'6B9B21FF51A1764898A0FD286C5F16E8', 2, NULL, NULL, 2, NULL, 0, 'Drape the strip around to the other side of the pail and press it on.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2193B979F4165247A6D252ADF6B1E53D', X'6B9B21FF51A1764898A0FD286C5F16E8', X'6B9B21FF51A1764898A0FD286C5F16E8', 3, NULL, NULL, 2, NULL, 0, 'If needed, use piping gel or melted chocolate as glue.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5DBB3A9A2AB854C84D92C36B5BC365C', X'E05505814C8D5147BF5360DF2E1593B4', X'E05505814C8D5147BF5360DF2E1593B4', 0, NULL, NULL, 2, NULL, 0, 'Just before serving, pipe about an ounce of mousse or pudding into each chocolate pail.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F50EFEB4496BE4E98385EEB8EA62EC9', X'E05505814C8D5147BF5360DF2E1593B4', X'E05505814C8D5147BF5360DF2E1593B4', 1, NULL, NULL, 2, NULL, 0, 'Spoon on about a teaspoon of toffee bits crumbs or cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6A7111A5C98F541B0D49947631DCB0D', X'E05505814C8D5147BF5360DF2E1593B4', X'E05505814C8D5147BF5360DF2E1593B4', 2, NULL, NULL, 2, NULL, 0, 'Set three chocolate shells on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'806342955D704E4995F763ABC32E624A', X'35615C41C99C1D469DAB0FA2B9AD3514', X'35615C41C99C1D469DAB0FA2B9AD3514', 0, NULL, NULL, 2, NULL, 0, 'Combine white confectionery coating and peanut butter chips in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD1495B21CD6DE498A1BDDDA0DC5C645', X'35615C41C99C1D469DAB0FA2B9AD3514', X'35615C41C99C1D469DAB0FA2B9AD3514', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC5BFF2CC1CBD9458ECF671363295DFB', X'35615C41C99C1D469DAB0FA2B9AD3514', X'35615C41C99C1D469DAB0FA2B9AD3514', 2, NULL, NULL, 2, NULL, 0, 'Stir in peanut butter and pinch of salt until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7EC213734C186148AF37CEB34C4CC37B', X'35615C41C99C1D469DAB0FA2B9AD3514', X'35615C41C99C1D469DAB0FA2B9AD3514', 3, NULL, NULL, 2, NULL, 0, 'Allow to cool slightly before pouring into your white chocolate shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CA5337962011244B4C100CCD28FF1CB', X'29108E6E01D2C346950379AE9F9B1CD1', X'29108E6E01D2C346950379AE9F9B1CD1', 0, NULL, NULL, 2, NULL, 0, 'Spoon about a tablespoon of white chocolate into each baseball mold and brush it up the sides.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1585D8FA917B04295E03390898F4D7E', X'29108E6E01D2C346950379AE9F9B1CD1', X'29108E6E01D2C346950379AE9F9B1CD1', 1, NULL, NULL, 2, NULL, 0, 'Refrigerate (or freeze if using candy melts) for about 5 minutes until it sets.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E87BAD444221C044AC456DE01A895AB3', X'29108E6E01D2C346950379AE9F9B1CD1', X'29108E6E01D2C346950379AE9F9B1CD1', 2, NULL, NULL, 2, NULL, 0, 'Fill each mold with fudge and chill until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0EA5DB41FBE6240B94E34F146386E67', X'29108E6E01D2C346950379AE9F9B1CD1', X'29108E6E01D2C346950379AE9F9B1CD1', 3, NULL, NULL, 2, NULL, 0, 'Top with a thin coating of white chocolate and chill until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61BF40255CD9A04B85AFB7FC1A086C57', X'29108E6E01D2C346950379AE9F9B1CD1', X'29108E6E01D2C346950379AE9F9B1CD1', 4, NULL, NULL, 2, NULL, 0, 'Draw on details using a red food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'28184DA939C012418265CB483B9A3627', X'D02FC77054319845ACDD87AB9310E938', X'D02FC77054319845ACDD87AB9310E938', 0, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D793EF880B3AD8468093A6690988A744', X'D02FC77054319845ACDD87AB9310E938', X'D02FC77054319845ACDD87AB9310E938', 1, NULL, NULL, 2, NULL, 0, 'Pour melted candy melts onto the lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E20115A8855B8F4F876AA39414D40139', X'D02FC77054319845ACDD87AB9310E938', X'D02FC77054319845ACDD87AB9310E938', 2, NULL, NULL, 2, NULL, 0, 'Spread into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4AE1BA9B5F9774DB05FAEB7FB64E053', X'D02FC77054319845ACDD87AB9310E938', X'D02FC77054319845ACDD87AB9310E938', 3, NULL, NULL, 2, NULL, 0, 'Arrange Minion icing decorations over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B33EC2EFBB4A39478ACB9EEDEFD3DD13', X'D02FC77054319845ACDD87AB9310E938', X'D02FC77054319845ACDD87AB9310E938', 4, NULL, NULL, 2, NULL, 0, 'Toss on blue sprinkles and add a few candy bananas.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C65CECEDAE3E14E9C63E950916E809B', X'D02FC77054319845ACDD87AB9310E938', X'D02FC77054319845ACDD87AB9310E938', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate until the chocolate sets but is still soft.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C1A9C5A01A7D042B9D76BF563B4457C', X'D02FC77054319845ACDD87AB9310E938', X'D02FC77054319845ACDD87AB9310E938', 6, NULL, NULL, 2, NULL, 0, 'Remove and cut into pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB96DF073C8E4840BAD62C40CFD737CF', X'D02FC77054319845ACDD87AB9310E938', X'D02FC77054319845ACDD87AB9310E938', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for 8-10 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6FADDE54B14A9478C1A1DCCD626B4F3', X'D02FC77054319845ACDD87AB9310E938', X'D02FC77054319845ACDD87AB9310E938', 8, NULL, NULL, 2, NULL, 0, 'Remove and break into pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A922B6E4A7DDC04CAED9215CC14D527B', X'662E199ABAEE20499F1C4F0B038F9018', X'662E199ABAEE20499F1C4F0B038F9018', 0, NULL, NULL, 2, NULL, 0, 'Combine heavy whipping cream and corn syrup in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC07E64DF2E74045BA451D69D9CDC0E9', X'662E199ABAEE20499F1C4F0B038F9018', X'662E199ABAEE20499F1C4F0B038F9018', 1, NULL, NULL, 2, NULL, 0, 'Heat for 1 minute then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EDC72243FBC8CF48B4314B58FF7F11A9', X'662E199ABAEE20499F1C4F0B038F9018', X'662E199ABAEE20499F1C4F0B038F9018', 2, NULL, NULL, 2, NULL, 0, 'Pour chopped chocolate into the bowl of a food processor or in a mixing bowl and pour hot cream over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'81F786F4C3F35F4A8390B47E1560B4EA', X'662E199ABAEE20499F1C4F0B038F9018', X'662E199ABAEE20499F1C4F0B038F9018', 3, NULL, NULL, 2, NULL, 0, 'Allow to sit for 3 minutes, then pulse or stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'21D9EB261A0E63478894F81F29B92391', X'662E199ABAEE20499F1C4F0B038F9018', X'662E199ABAEE20499F1C4F0B038F9018', 4, NULL, NULL, 2, NULL, 0, 'Let cool for one hour until thickened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C64F5BCB03326418C0E75A7099D66DF', X'38A7E7BD335BCD4BB1017FBA950126E0', X'38A7E7BD335BCD4BB1017FBA950126E0', 0, NULL, NULL, 2, NULL, 0, 'Color 2 ounces of white chocolate or confectionery coating using red candy coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E23329938034B468E2DCB97E82A6E81', X'38A7E7BD335BCD4BB1017FBA950126E0', X'38A7E7BD335BCD4BB1017FBA950126E0', 1, NULL, NULL, 2, NULL, 0, 'Use a food use only paint brush to paint polka dots on the inside of 24 paper candy cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4DCCEFFDFEA61C47999999B993E90388', X'38A7E7BD335BCD4BB1017FBA950126E0', X'38A7E7BD335BCD4BB1017FBA950126E0', 2, NULL, NULL, 2, NULL, 0, 'Refrigerate for 5 minutes until the dots are hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6F54B0A02D14FB45AE5DC977B1899782', X'38A7E7BD335BCD4BB1017FBA950126E0', X'38A7E7BD335BCD4BB1017FBA950126E0', 3, NULL, NULL, 2, NULL, 0, 'Spoon about 1/2 teaspoon of white chocolate into a cup and brush it up around the sides of the cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB33961ECA4CE34687364BF1E8E3EA00', X'38A7E7BD335BCD4BB1017FBA950126E0', X'38A7E7BD335BCD4BB1017FBA950126E0', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate for 5-8 minutes until hardened. Freeze if using confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7C8B97C5051F74B9D265846DA89594D', X'38A7E7BD335BCD4BB1017FBA950126E0', X'38A7E7BD335BCD4BB1017FBA950126E0', 5, NULL, NULL, 2, NULL, 0, 'Peel the paper off the white chocolate cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80BD73FDE2FB8F48B9311CB0FFA6BEB1', X'7D37D12ACAB391439202A5EC8361C1BF', X'7D37D12ACAB391439202A5EC8361C1BF', 0, NULL, NULL, 2, NULL, 0, 'Pour thickened chocolate ganache into a pastry bag fitted with a large star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B03D8ED1EBD1B148967C3DE92D826833', X'7D37D12ACAB391439202A5EC8361C1BF', X'7D37D12ACAB391439202A5EC8361C1BF', 1, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of ganache into each candy cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3CE66B9708B3A4AB0C78D27638FEAAB', X'7D37D12ACAB391439202A5EC8361C1BF', X'7D37D12ACAB391439202A5EC8361C1BF', 2, NULL, NULL, 2, NULL, 0, 'Immediately top with a chocolate coated candy ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7CC52298DDCC1409391309934CC5EA4', X'A77705590CB95242BF4C4071F7B384CF', X'A77705590CB95242BF4C4071F7B384CF', 0, NULL, NULL, 2, NULL, 0, 'Spoon a heaping tablespoonful of melted white chocolate into one cavity in a semi-sphere silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33075CD7BFF1154B8FC919201E6E256A', X'A77705590CB95242BF4C4071F7B384CF', X'A77705590CB95242BF4C4071F7B384CF', 1, NULL, NULL, 2, NULL, 0, 'Brush the white chocolate up the sides of the cavity.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15D487F0409B7F40AE12A91D37405A7D', X'A77705590CB95242BF4C4071F7B384CF', X'A77705590CB95242BF4C4071F7B384CF', 2, NULL, NULL, 2, NULL, 0, 'Refrigerate is using pure white chocolate or freeze if using confectionery coating for 8-14 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'183BCDC89C5A0E4F8E9151B4F419D365', X'A77705590CB95242BF4C4071F7B384CF', X'A77705590CB95242BF4C4071F7B384CF', 3, NULL, NULL, 2, NULL, 0, 'Un-mold and allow them to warm to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC8F52DC4420D1469985B450D3BAFCA0', X'A77705590CB95242BF4C4071F7B384CF', X'A77705590CB95242BF4C4071F7B384CF', 4, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of chocolate over the entire backside of the bowl or just around the outer and inner rim of the bowl then immediately roll in a bowl of sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E091A02E6A7F54CA2FA5346497B64EB', X'A77705590CB95242BF4C4071F7B384CF', X'A77705590CB95242BF4C4071F7B384CF', 5, NULL, NULL, 2, NULL, 0, 'Once dry you can fill the bowls with your favorite treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71655F0F68314F4198B7F0208CCB03BA', X'D5DFACF01E498F4FB9EBD6C97EF88E59', X'D5DFACF01E498F4FB9EBD6C97EF88E59', 0, NULL, NULL, 2, NULL, 0, 'Melt ice cream in microwave on high for 20 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE53BAF954FFEF418C2A7AD7415B687F', X'D5DFACF01E498F4FB9EBD6C97EF88E59', X'D5DFACF01E498F4FB9EBD6C97EF88E59', 1, NULL, NULL, 2, NULL, 0, 'Remove and stir, heat for 20-30 more seconds until completely melted and it just comes to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'427828D4782FC94393096B49895C05B5', X'D5DFACF01E498F4FB9EBD6C97EF88E59', X'D5DFACF01E498F4FB9EBD6C97EF88E59', 2, NULL, NULL, 2, NULL, 0, 'Measure out 1/2 cup of melted ice cream and stir into the finely chopped chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E93318530632AB4F988ABD0708FC7B05', X'D5DFACF01E498F4FB9EBD6C97EF88E59', X'D5DFACF01E498F4FB9EBD6C97EF88E59', 3, NULL, NULL, 2, NULL, 0, 'If all the chocolate doesn’t melt, heat at high power for 10 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97D01C4744311040826A04FCC2A98C53', X'D5DFACF01E498F4FB9EBD6C97EF88E59', X'D5DFACF01E498F4FB9EBD6C97EF88E59', 4, NULL, NULL, 2, NULL, 0, 'Let it sit in the microwave for a minute, then remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78C8C25BA2C4684296BA7DA214C954F7', X'D5DFACF01E498F4FB9EBD6C97EF88E59', X'D5DFACF01E498F4FB9EBD6C97EF88E59', 5, NULL, NULL, 2, NULL, 0, 'Repeat if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9061A35C718B5F43B65665A9339A5DE4', X'D5DFACF01E498F4FB9EBD6C97EF88E59', X'D5DFACF01E498F4FB9EBD6C97EF88E59', 6, NULL, NULL, 2, NULL, 0, 'Press a piece of plastic wrap directly on the surface of the chocolate ganache and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'833067A9D6FE024A869F082A5EAAF6F4', X'D5DFACF01E498F4FB9EBD6C97EF88E59', X'D5DFACF01E498F4FB9EBD6C97EF88E59', 7, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to cool and thicken for several hours or overnight.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76FECD2E1E6CEC499F204E7D868DDAB3', X'D5DFACF01E498F4FB9EBD6C97EF88E59', X'D5DFACF01E498F4FB9EBD6C97EF88E59', 8, NULL, NULL, 2, NULL, 0, 'When firm, scoop and roll into your favorite toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C015B89689D9B458A5BF45EB07905E6', X'4335BB14AD273C459B25331735336702', X'4335BB14AD273C459B25331735336702', 0, NULL, NULL, 2, NULL, 0, 'Line an 8 inch square pan with non-stick tin foil or parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1D5D1E45262DD4C9FF53ED9658BD871', X'4335BB14AD273C459B25331735336702', X'4335BB14AD273C459B25331735336702', 1, NULL, NULL, 2, NULL, 0, 'Cut Sugar Stamp sheet to fit in bottom of pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'04847F830747D549A89F490FDC4EE0AD', X'4335BB14AD273C459B25331735336702', X'4335BB14AD273C459B25331735336702', 2, NULL, NULL, 2, NULL, 0, 'Stir together finely chopped white chocolate and sweetened condensed milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3885C3B8C5BEB344A5D87A26421995AF', X'4335BB14AD273C459B25331735336702', X'4335BB14AD273C459B25331735336702', 3, NULL, NULL, 2, NULL, 0, 'Heat in microwave on high power for 45 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F81D3F067B80494B97942FF16B501A6D', X'4335BB14AD273C459B25331735336702', X'4335BB14AD273C459B25331735336702', 4, NULL, NULL, 2, NULL, 0, 'Remove and stir, then let sit for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'999CDB8E09713F49B7F5D0CF4517EF8B', X'4335BB14AD273C459B25331735336702', X'4335BB14AD273C459B25331735336702', 5, NULL, NULL, 2, NULL, 0, 'Stir again then place back in the microwave and heat for 20 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA0E7148387ED747916736A3C241ABA8', X'4335BB14AD273C459B25331735336702', X'4335BB14AD273C459B25331735336702', 6, NULL, NULL, 2, NULL, 0, 'Allow the bowl to sit in the microwave for 3 minutes then remove and stir slowly until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D89F437D0547949BB4A5802A99FF858', X'4335BB14AD273C459B25331735336702', X'4335BB14AD273C459B25331735336702', 7, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59A4D4FE0154A842997703BD9F87CC6C', X'4335BB14AD273C459B25331735336702', X'4335BB14AD273C459B25331735336702', 8, NULL, NULL, 2, NULL, 0, 'Pour into prepared pan and smooth into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6612BA9BBF12B942A2E6DB343F735536', X'4335BB14AD273C459B25331735336702', X'4335BB14AD273C459B25331735336702', 9, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap and let cool for 2 hours or refrigerate or freeze until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8498233A3D4734087B4A370B5C9FF1B', X'4335BB14AD273C459B25331735336702', X'4335BB14AD273C459B25331735336702', 10, NULL, NULL, 2, NULL, 0, 'Remove fudge from pan and peel off tin foil then peel off acetate from the sugar stamp.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CBE6A0581EBEC4A9AF90E5E49AD5175', X'4335BB14AD273C459B25331735336702', X'4335BB14AD273C459B25331735336702', 11, NULL, NULL, 2, NULL, 0, 'Cut into squares and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FD1D2A0C8EBB84AB579F5A2B5AEFEE2', X'8432C21B14D071499C079042BC7D8B90', X'8432C21B14D071499C079042BC7D8B90', 0, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02EFD7F2FFA877459355FEAD577B4329', X'8432C21B14D071499C079042BC7D8B90', X'8432C21B14D071499C079042BC7D8B90', 1, NULL, NULL, 2, NULL, 0, 'Pour popcorn into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A3CE6D8AF8EBC4DBBAADC83CE20020A', X'8432C21B14D071499C079042BC7D8B90', X'8432C21B14D071499C079042BC7D8B90', 2, NULL, NULL, 2, NULL, 0, 'Pour white chocolate over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'540F63E0D9F9F24CBB8DEF5E5EC38167', X'8432C21B14D071499C079042BC7D8B90', X'8432C21B14D071499C079042BC7D8B90', 3, NULL, NULL, 2, NULL, 0, 'Quickly stir it and toss it to coat the popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'432BDD35DA711843BF519AFF59D1D198', X'8432C21B14D071499C079042BC7D8B90', X'8432C21B14D071499C079042BC7D8B90', 4, NULL, NULL, 2, NULL, 0, 'Pour out onto the baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E95AF7A15BA0E44B38016D718132FB3', X'8432C21B14D071499C079042BC7D8B90', X'8432C21B14D071499C079042BC7D8B90', 5, NULL, NULL, 2, NULL, 0, 'Drizzle with colored confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'73E4496265161344B7344837E472C619', X'8432C21B14D071499C079042BC7D8B90', X'8432C21B14D071499C079042BC7D8B90', 6, NULL, NULL, 2, NULL, 0, 'Place chocolate almond footballs all around popcorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93C9ECA93D04BB4EAEF416BCB1AC807F', X'8432C21B14D071499C079042BC7D8B90', X'8432C21B14D071499C079042BC7D8B90', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'155E55D485B8E542A182BF0D6925A193', X'8432C21B14D071499C079042BC7D8B90', X'8432C21B14D071499C079042BC7D8B90', 8, NULL, NULL, 2, NULL, 0, 'Remove and break up into bite size chunks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5553D769BCA33F49B2A095F66514CC9E', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 0, NULL, NULL, 2, NULL, 0, 'Melt pink confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADA29167A8CAE04FB6BCCA842EBA3C36', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 1, NULL, NULL, 2, NULL, 0, 'Pour it into a squeeze bottle or pastry bag fitted with a small round pastry tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82C28E1AEE8CF140861C696C7941678A', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 2, NULL, NULL, 2, NULL, 0, 'Pipe inch size X''s and O''s onto a parchment lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCECAED7FB440F46BC233CBCEF6750F5', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 3, NULL, NULL, 2, NULL, 0, 'Refrigerate for about 3 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1FB0B4E1C3653A4B81AFBDF0DF0C4964', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 4, NULL, NULL, 2, NULL, 0, 'Remove from parchment paper and let sit to warm to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26CE46BF6AFE3A4D9F18860351E2D579', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 5, NULL, NULL, 2, NULL, 0, 'Melt and temper pure milk chocolate or melt light cocoa candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6462BE19B4E67A46B4BCA91C5018BC17', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 6, NULL, NULL, 2, NULL, 0, 'Spread onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBF5A02210C97644AFA30E0CB0A0392B', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 7, NULL, NULL, 2, NULL, 0, 'Immediately, drizzle with pink candy coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C20E241C6D78994F8196BDEBCF736EA3', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 8, NULL, NULL, 2, NULL, 0, 'Press X''s and O''s on top, leaving room to make cuts around each set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03DD33FFFCA03546A3EE04EB6563891F', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 9, NULL, NULL, 2, NULL, 0, 'Toss on heart sprinkles and nonpareils.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'589629A0F3924743B75EDD6B702284CB', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 10, NULL, NULL, 2, NULL, 0, 'If using pure chocolate, let sit until it sets but is still soft.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9199F1B70C1D0447B34E1DE28940AE2C', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 11, NULL, NULL, 2, NULL, 0, 'If using confectionery coating, refrigerate until set but still soft, 1-3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED65E9736D33F44F8F30DE328196FB65', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 12, NULL, NULL, 2, NULL, 0, 'Cut chocolate into pieces, then refrigerate for 10 minutes until set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD9469D35EF6A748B108B80D7D4A754E', X'C514F6CA1FF94247A12B4BE31A703530', X'C514F6CA1FF94247A12B4BE31A703530', 13, NULL, NULL, 2, NULL, 0, 'Break apart.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'967C4691C1F396458300B12707BE5109', X'BE86E9B78BBFA144BB35C00112B0AF68', X'BE86E9B78BBFA144BB35C00112B0AF68', 0, NULL, NULL, 2, NULL, 0, 'Melt butter in a large microwave safe bowl on high power for 45-75 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'753683254B50CD4C94A5A7F0C5ACCC45', X'BE86E9B78BBFA144BB35C00112B0AF68', X'BE86E9B78BBFA144BB35C00112B0AF68', 1, NULL, NULL, 2, NULL, 0, 'Stir in dark brown sugar, corn syrup and sweetened condensed milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4DAB4512331B14F91875F7E85BFC4E3', X'BE86E9B78BBFA144BB35C00112B0AF68', X'BE86E9B78BBFA144BB35C00112B0AF68', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F52B5BD56F43B4A85AC66CB2C7CCF42', X'BE86E9B78BBFA144BB35C00112B0AF68', X'BE86E9B78BBFA144BB35C00112B0AF68', 3, NULL, NULL, 2, NULL, 0, 'Remove and whisk until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D8A8C67A6B33E45A0D6122741FF5C8D', X'BE86E9B78BBFA144BB35C00112B0AF68', X'BE86E9B78BBFA144BB35C00112B0AF68', 4, NULL, NULL, 2, NULL, 0, 'Heat for 5 more minutes, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'355EBFC1FC4C1F47965F6DEB6D910E62', X'BE86E9B78BBFA144BB35C00112B0AF68', X'BE86E9B78BBFA144BB35C00112B0AF68', 5, NULL, NULL, 2, NULL, 0, 'Heat for an additional 5 minutes, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DB41E74D7A8F5478CA8FE5E1CFC6A84', X'BE86E9B78BBFA144BB35C00112B0AF68', X'BE86E9B78BBFA144BB35C00112B0AF68', 6, NULL, NULL, 2, NULL, 0, 'Check temperature, if it''s between 245 - 250 degrees F, pour it into the oval shaped molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C90491597B15F47B0C2E7B2B9F18677', X'BE86E9B78BBFA144BB35C00112B0AF68', X'BE86E9B78BBFA144BB35C00112B0AF68', 7, NULL, NULL, 2, NULL, 0, 'If needed, heat for 1 minute increments, stirring after each until desired temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44ABA4756DFDD648A1DB24B66126DA33', X'BE86E9B78BBFA144BB35C00112B0AF68', X'BE86E9B78BBFA144BB35C00112B0AF68', 8, NULL, NULL, 2, NULL, 0, 'Let caramels harden for 2 hours.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'288FD7FDCC933B43AA38B51180D90CA3', X'BE86E9B78BBFA144BB35C00112B0AF68', X'BE86E9B78BBFA144BB35C00112B0AF68', 9, NULL, NULL, 2, NULL, 0, 'Un-mold and set on a silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F5170597534E34283018E011585081C', X'BE86E9B78BBFA144BB35C00112B0AF68', X'BE86E9B78BBFA144BB35C00112B0AF68', 10, NULL, NULL, 2, NULL, 0, 'Pipe white chocolate laces over each football.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'362D37686A9E064C86AAFD9B7E611B9C', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', 0, NULL, NULL, 2, NULL, 0, 'Melt and temper pure white chocolate or melt white confectionery coating. See the Chocolate Making Tips page at HungryHappenings.com for detailed instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5ABE4003DE32F4489E193E3AF2EA0484', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', 1, NULL, NULL, 2, NULL, 0, 'Spread the white chocolate out onto a parchment paper lined baking tray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8550CA63C4B978498C08A26E3C15F777', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', 2, NULL, NULL, 2, NULL, 0, 'Immediately set the dark chocolate wafers all around the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5037173977E3644A81AF836AD2D28E79', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', 3, NULL, NULL, 2, NULL, 0, 'Add two chocolate chip ears next to each chocolate wafer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E409A100BA062949B9EA3536FDEFB85B', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', 4, NULL, NULL, 2, NULL, 0, 'Toss on sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1327A1987478944FA3654F66BEFD560F', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate for 2-5 minutes until the white chocolate begins to set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BD16A83ED615B4AA62C0663A414FEB4', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', 6, NULL, NULL, 2, NULL, 0, 'Remove and cut into pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1878B3692CA6648B8B158306C100A5F', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for 5 more minutes until the white chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99F0397B91261B4B9DD70FA021F425E6', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', X'FCD0EB0C41CBFC47ABF8ACA9DB52E781', 8, NULL, NULL, 2, NULL, 0, 'Store in an airtight container for up to 2 months.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A35AE242A32F9447B2962505E472EF2B', X'61EE1080F9E634489F4E55D5859DB8FA', X'61EE1080F9E634489F4E55D5859DB8FA', 0, NULL, NULL, 2, NULL, 0, 'Pour cherry gelatin and unflavored gelatin into a saucepan and stir together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AE9E42BF4FCFC459CDA0A42AFA81B68', X'61EE1080F9E634489F4E55D5859DB8FA', X'61EE1080F9E634489F4E55D5859DB8FA', 1, NULL, NULL, 2, NULL, 0, 'Pour in the cola and stir to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'139963CE09F43A45A913D8AC6992DA0B', X'61EE1080F9E634489F4E55D5859DB8FA', X'61EE1080F9E634489F4E55D5859DB8FA', 2, NULL, NULL, 2, NULL, 0, 'Turn on heat to medium low and heat until the mixture just starts to bubble around the edges.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACC46C668ACAB24FBA611115DF3C9B2D', X'61EE1080F9E634489F4E55D5859DB8FA', X'61EE1080F9E634489F4E55D5859DB8FA', 3, NULL, NULL, 2, NULL, 0, 'Add a few drops of black food coloring and stir until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'632710EC33E5DD4BBF30A6353E4BD2E3', X'61EE1080F9E634489F4E55D5859DB8FA', X'61EE1080F9E634489F4E55D5859DB8FA', 4, NULL, NULL, 2, NULL, 0, 'Pour into Darth Vader molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FF5A7F49A34D348932F0272993259A7', X'61EE1080F9E634489F4E55D5859DB8FA', X'61EE1080F9E634489F4E55D5859DB8FA', 5, NULL, NULL, 2, NULL, 0, 'Freeze for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCEB9AA49458904FB7A734053DA1E894', X'61EE1080F9E634489F4E55D5859DB8FA', X'61EE1080F9E634489F4E55D5859DB8FA', 6, NULL, NULL, 2, NULL, 0, 'Remove and un-mold the candies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D9D0B29785AAB4DAA2DC56FD1D9A1A0', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 0, NULL, NULL, 2, NULL, 0, 'Combine white confectionery coating wafers and peanut butter chips in a microwave safe bowl. Heat on high power for 15-30 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC5164C58729644793CF7EDE55AC01D1', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 1, NULL, NULL, 2, NULL, 0, 'Stir in peanut butter and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C4472E377544C42B315CA15FEDC31F7', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 2, NULL, NULL, 2, NULL, 0, 'Allow it to cool enough so that it no longer feels warm to the touch.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F78B256BE08C34CB570E32DD2CDB1C6', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 3, NULL, NULL, 2, NULL, 0, 'Paint a thin shell of melted chocolate into plastic egg molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC4EA322EF426B49A37242FF80824136', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA9E10BC242E6E4196BC1F5D4D45AFB2', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 5, NULL, NULL, 2, NULL, 0, 'Pour peanut butter fudge into chocolate shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'258FBFAB19276B4E821DEB5F52CFB0C6', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate until firm. Then allow to warm up to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0BB9E413078954DB5F6882CC970A4DE', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 7, NULL, NULL, 2, NULL, 0, 'Top with a thin layer of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAE88275549BAC4FB099689D3356644D', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 8, NULL, NULL, 2, NULL, 0, 'Refrigerate until firm then un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'314D379004EAFA4B9FDC2FC9D581019E', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 9, NULL, NULL, 2, NULL, 0, 'Pipe chocolate ears and paws onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B82B13C1EA53B4792EAA309A7E781D5', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 10, NULL, NULL, 2, NULL, 0, 'Add pink hearts and confetti sprinkles to paws.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C7709A76B38C741B269E3A881FFAC6B', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 11, NULL, NULL, 2, NULL, 0, 'Refrigerate until hard.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2C36840763F184E95F703EC5EA3606A', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 12, NULL, NULL, 2, NULL, 0, 'Remove and brush pink luster dust over ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'487198D34202B34386C920E38BD82E10', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 13, NULL, NULL, 2, NULL, 0, 'Spoon some chocolate onto a parchment paper lined baking sheet and set one egg on top, then sprinkle on green sprinkles and hold egg in place until it sticks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B48A3357025F243BB5BFA0B60FE7DBF', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 14, NULL, NULL, 2, NULL, 0, 'Decorate each egg by adding 2 candy eyes, a pink candy nose, 2 candy ears, and 2 paws using melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD73FC181258474FB0D424E61047A09E', X'96D63635596B0A4FB5479939D9F71140', X'96D63635596B0A4FB5479939D9F71140', 15, NULL, NULL, 2, NULL, 0, 'Draw on smile and whiskers using black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5F67AE061AFE941A723CF311A3E8F71', X'0203375419884C4A8581E313A9997386', X'0203375419884C4A8581E313A9997386', 0, NULL, NULL, 2, NULL, 0, 'Heat the Light Cocoa Candy Melts for 15-30 second increments stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'260CCA1BD7D0EF4EA127340D75275680', X'0203375419884C4A8581E313A9997386', X'0203375419884C4A8581E313A9997386', 1, NULL, NULL, 2, NULL, 0, 'Stir in the Reese''s Chocolate Peanut Butter Spread until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'51505DDA967C1E448AE5031DC926F402', X'0203375419884C4A8581E313A9997386', X'0203375419884C4A8581E313A9997386', 2, NULL, NULL, 2, NULL, 0, 'Reserve 1 tablespoon of the fudge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0F790828ACC9143990ADAB784560DC7', X'0203375419884C4A8581E313A9997386', X'0203375419884C4A8581E313A9997386', 3, NULL, NULL, 2, NULL, 0, 'Pour the remaining fudge into a disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA51DADE948CD04180DA293152801D40', X'0203375419884C4A8581E313A9997386', X'0203375419884C4A8581E313A9997386', 4, NULL, NULL, 2, NULL, 0, 'Snip off the tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F355638734D6945A90F6A297BB50319', X'0203375419884C4A8581E313A9997386', X'0203375419884C4A8581E313A9997386', 5, NULL, NULL, 2, NULL, 0, 'Pipe fudge into the 24 bunny cavities in the silicone mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'680102EF0BFDCB47B5E7CC348692BCDE', X'0203375419884C4A8581E313A9997386', X'0203375419884C4A8581E313A9997386', 6, NULL, NULL, 2, NULL, 0, 'Freeze for 15-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0929FFE28C871041BBB120AC93477FE9', X'0203375419884C4A8581E313A9997386', X'0203375419884C4A8581E313A9997386', 7, NULL, NULL, 2, NULL, 0, 'Un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1283286264B6414F86153E8ED24626D9', X'0203375419884C4A8581E313A9997386', X'0203375419884C4A8581E313A9997386', 8, NULL, NULL, 2, NULL, 0, 'Thaw for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61B7D86E5D4C134697446331FC51C86C', X'0203375419884C4A8581E313A9997386', X'0203375419884C4A8581E313A9997386', 9, NULL, NULL, 2, NULL, 0, 'Heat the reserved fudge in the microwave for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B77A764B733C54C8CDD1ECAB770B505', X'0203375419884C4A8581E313A9997386', X'0203375419884C4A8581E313A9997386', 10, NULL, NULL, 2, NULL, 0, 'Attach two white confetti sprinkles and one pink confetti sprinkle to each bunny.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0309E25F9E77C5459F687DFC5FB131AF', X'0203375419884C4A8581E313A9997386', X'0203375419884C4A8581E313A9997386', 11, NULL, NULL, 2, NULL, 0, 'Use a candy decorating pen to draw pupils onto each white sprinkle, creating eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'125B461E8866BD488856C4210DAEFC55', X'0203375419884C4A8581E313A9997386', X'0203375419884C4A8581E313A9997386', 12, NULL, NULL, 2, NULL, 0, 'Draw on a smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AB31E57C425A34EB6FE2355A975D129', X'7CAA122A436FE047A7CD23C14FD524D6', X'7CAA122A436FE047A7CD23C14FD524D6', 0, NULL, NULL, 2, NULL, 0, 'Heat the heavy whipping cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F445A28C24673C4D92B5504B920B3A71', X'7CAA122A436FE047A7CD23C14FD524D6', X'7CAA122A436FE047A7CD23C14FD524D6', 1, NULL, NULL, 2, NULL, 0, 'Pour 1/2 cup of the hot coffee along with the hot cream over the chopped chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC8F72FEE7AD2F4DA4206EF3F328B733', X'7CAA122A436FE047A7CD23C14FD524D6', X'7CAA122A436FE047A7CD23C14FD524D6', 2, NULL, NULL, 2, NULL, 0, 'Leave it sit for 2 minutes then stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4352795F818A1A4AAD64F1D5FE84153F', X'7CAA122A436FE047A7CD23C14FD524D6', X'7CAA122A436FE047A7CD23C14FD524D6', 3, NULL, NULL, 2, NULL, 0, 'Let the ganache cool for 20-30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00E53C9512C2E64C8B6D11448C1AE543', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 0, NULL, NULL, 2, NULL, 0, 'Paint a thin layer of white chocolate into each egg shaped cavity in your candy molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E6670984A12574BB8B7BCCB621265D6', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 1, NULL, NULL, 2, NULL, 0, 'Chill for 5 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'081FDEBEFE8D4D4B823D1A6D85A3BC92', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 2, NULL, NULL, 2, NULL, 0, 'Pipe chocolate ganache into the chocolate shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5ACF88E54E84E34281563BED12BD5313', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 3, NULL, NULL, 2, NULL, 0, 'Set aside for 1 hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18E46228D753FD4BBC3764C32C0F544B', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 4, NULL, NULL, 2, NULL, 0, 'Pipe a layer of chocolate over top of the ganache and scrape off any excess chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBE7D58FBDD35841A734DB6899C401B1', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 5, NULL, NULL, 2, NULL, 0, 'Chill for 5 minutes until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1850D072F6D7244A98135DE03E2CD34E', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 6, NULL, NULL, 2, NULL, 0, 'Un-mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36846141F787A44C9811265A8E1EE817', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 7, NULL, NULL, 2, NULL, 0, 'Pipe melted chocolate tree limbs onto parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A78F2DD83E35F47A1568489E058FDA8', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 8, NULL, NULL, 2, NULL, 0, 'Allow them to harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'42A915C58821654E8B391CCE4D16A02B', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 9, NULL, NULL, 2, NULL, 0, 'Attach two black pearl eyes and 1 orange candy coated sunflower seed nose using a bit of white chocolate to each egg shaped truffle. Draw on the dots for the smile using a food coloring marker. Then attach three black pearl buttons.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8FE6D6B7EBFA04088706D5EF270AFBF', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 10, NULL, NULL, 2, NULL, 0, 'Spoon about a teaspoon or so of white chocolate onto a piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9AB83746EC6AC4ABA74809F34C05F73', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 11, NULL, NULL, 2, NULL, 0, 'Allow it to start setting, then place one truffle in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3F8C4F3DBC95040B56DF6960ADF952B', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 12, NULL, NULL, 2, NULL, 0, 'If using, sprinkle edible glitter over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'283EC01A74B5924CAED73F4B1050E731', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', X'0A5AD4BEB5F7DB4ABA7B31C5D9C49016', 13, NULL, NULL, 2, NULL, 0, 'Use some white chocolate to attach two stick arms to each truffle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25C998D23063704BA0DB13E0B77A8E70', X'1AFEB729A61DF64AAE4068ACE29242E8', X'1AFEB729A61DF64AAE4068ACE29242E8', 0, NULL, NULL, 2, NULL, 0, 'Melt ice cream in microwave on high for 20 seconds. Remove and stir, heat for 20-30 more seconds until completely melted and it just comes to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'298379B6BE13E24EBC807A58FC85BC07', X'1AFEB729A61DF64AAE4068ACE29242E8', X'1AFEB729A61DF64AAE4068ACE29242E8', 1, NULL, NULL, 2, NULL, 0, 'Measure out 1/2 cup of melted ice cream and stir into the finely chopped chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC02EE291666AB438E418F3408395D29', X'1AFEB729A61DF64AAE4068ACE29242E8', X'1AFEB729A61DF64AAE4068ACE29242E8', 2, NULL, NULL, 2, NULL, 0, 'If all the chocolate doesn''t melt, heat at high power for 15 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5F1850C4FF0D34C99B9A4E88E7A12A6', X'1AFEB729A61DF64AAE4068ACE29242E8', X'1AFEB729A61DF64AAE4068ACE29242E8', 3, NULL, NULL, 2, NULL, 0, 'Let it sit in the microwave for a minute, then remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'134B6757C633B6409AC0325EBF6F9F10', X'1AFEB729A61DF64AAE4068ACE29242E8', X'1AFEB729A61DF64AAE4068ACE29242E8', 4, NULL, NULL, 2, NULL, 0, 'Heat again if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE36CE3CD650DA479480AAFE9EEBAEDF', X'1AFEB729A61DF64AAE4068ACE29242E8', X'1AFEB729A61DF64AAE4068ACE29242E8', 5, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to cool and thicken at room temperature for about 6 hours or in the refrigerator for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'15E35D02E3A99743ABE07EE88623399C', X'1AFEB729A61DF64AAE4068ACE29242E8', X'1AFEB729A61DF64AAE4068ACE29242E8', 6, NULL, NULL, 2, NULL, 0, 'Scoop out using a small ice cream scoop (2 teaspoon size) or a spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7ED9A48BFBE79A4EABA981F11107717D', X'1AFEB729A61DF64AAE4068ACE29242E8', X'1AFEB729A61DF64AAE4068ACE29242E8', 7, NULL, NULL, 2, NULL, 0, 'Roll each scoop into a ball and immediately roll in the chocolate shavings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AAA29DABB4B93A4D993A42AF3308B413', X'1AFEB729A61DF64AAE4068ACE29242E8', X'1AFEB729A61DF64AAE4068ACE29242E8', 8, NULL, NULL, 2, NULL, 0, 'Store in an airtight container at room temp for up to 2 weeks. Refrigerate up to 3 weeks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33BB3935B1DFB6488D348FABE94CFB22', X'3B901B1744C2E846873D3D8A7A6491E9', X'3B901B1744C2E846873D3D8A7A6491E9', 0, NULL, NULL, 2, NULL, 0, 'Place white confectionery coating wafers and peanut butter chips in a microwave safe mixing bowl. Heat on high power for 45 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CBAB8C6FB81874F88D5D3C5697F1878', X'3B901B1744C2E846873D3D8A7A6491E9', X'3B901B1744C2E846873D3D8A7A6491E9', 1, NULL, NULL, 2, NULL, 0, 'Remove and stir the chips around or they will burn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F6C81A481D56D4D8363126C29596462', X'3B901B1744C2E846873D3D8A7A6491E9', X'3B901B1744C2E846873D3D8A7A6491E9', 2, NULL, NULL, 2, NULL, 0, 'Heat for 30 more seconds, then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45EAFA542F40D8489D586E6AC9E0D94B', X'3B901B1744C2E846873D3D8A7A6491E9', X'3B901B1744C2E846873D3D8A7A6491E9', 3, NULL, NULL, 2, NULL, 0, 'Heat for 25 seconds, then let it sit in the microwave for 2-3 minutes, then stir vigorously.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6945CD8A87C3FF4EB8E4788E187DDE1C', X'3B901B1744C2E846873D3D8A7A6491E9', X'3B901B1744C2E846873D3D8A7A6491E9', 4, NULL, NULL, 2, NULL, 0, 'If all of the chips are not melted, heat at ten second intervals, stirring in between each, until completely melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC31DB98BAE598488DFF7463CE74DBD5', X'3B901B1744C2E846873D3D8A7A6491E9', X'3B901B1744C2E846873D3D8A7A6491E9', 5, NULL, NULL, 2, NULL, 0, 'Stir in peanut butter and salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A688F960BAD3A34F8B2FA638ACC05089', X'3B901B1744C2E846873D3D8A7A6491E9', X'3B901B1744C2E846873D3D8A7A6491E9', 6, NULL, NULL, 2, NULL, 0, 'NOTES: Make sure to mix really well, scraping the bottom of the bowl. If you don''t stir the peanut butter into the candy, you''ll end up with clumps of hardened candy coating in your filling.Allow it to cool a bit before piping it into your chocolate shells.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B6AFC4BF49C1349A471FD003A97C560', X'AA7EAF75248F024698979A674152178A', X'AA7EAF75248F024698979A674152178A', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EFDD8245F9CE724A9D43BC5D1CD82F05', X'AA7EAF75248F024698979A674152178A', X'AA7EAF75248F024698979A674152178A', 1, NULL, NULL, 2, NULL, 0, 'Spread 1/3 cup pizza sauce in the middle of the skillet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'582204ADD488BF48A77F45A690DB941C', X'AA7EAF75248F024698979A674152178A', X'AA7EAF75248F024698979A674152178A', 2, NULL, NULL, 2, NULL, 0, 'Blend together 8 ounces cream cheese, 1/2 cup shredded Mozzarella Cheese, 1/2 cup shredded Parmesan cheese, 1/2 cup sour cream, and a pinch of salt, then spread half of it over the pizza sauce in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B391DB3C91E4C428D13672B0EE256B5', X'AA7EAF75248F024698979A674152178A', X'AA7EAF75248F024698979A674152178A', 3, NULL, NULL, 2, NULL, 0, 'Top with 3/4 cup shredded Mozzarella cheese, then sprinkle on half of the pepperoni or your favorite pizza toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17F88C5E65BBB749907A5F8BDDF69282', X'AA7EAF75248F024698979A674152178A', X'AA7EAF75248F024698979A674152178A', 4, NULL, NULL, 2, NULL, 0, 'Layer on another 1/3 cup pizza sauce, the remaining cheese dip, 1/3 cup pizza sauce, another 3/4 cup Mozzarella cheese, and more pepperoni or pizza toppings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24C5377D04DB2E438083019E3635B3DD', X'AA7EAF75248F024698979A674152178A', X'AA7EAF75248F024698979A674152178A', 5, NULL, NULL, 2, NULL, 0, 'Bake the dip for 28-30 minutes until the cheese is melted and the pizza crust is golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F2032B535144E43B722C6BF81B4C2A1', X'AA7EAF75248F024698979A674152178A', X'AA7EAF75248F024698979A674152178A', 6, NULL, NULL, 2, NULL, 0, 'Serve while hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC37029EDC441743869A39F200280D88', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4AAD4024EC62074B906099E3E84112EA', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 1, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BA03B273D0D8A4098D72C60F2D2A6F8', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 2, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A699800B6806C40B816F74756226BF2', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 3, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C2A2CEE77A64394E9D692FA32D6D16FF', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 4, NULL, NULL, 2, NULL, 0, 'Stir in egg, vanilla, and almond extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A41B8DCFF9681644994C225600C49840', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 5, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and stir just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B794804EADBFA41B6C675CC75DFB156', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 6, NULL, NULL, 2, NULL, 0, 'If the dough is sticky, add a small amount of flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'808038F9FD867149982DEF4DB92DF146', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 7, NULL, NULL, 2, NULL, 0, 'Roll out cookie dough, on a floured surface, to about 1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B08F870DCDDE4448B799BAACEB7DF05D', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 8, NULL, NULL, 2, NULL, 0, 'Cut twelve 3 inch circles out of the cookie dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3981C9403ECAB34496E441E1F12B118C', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 9, NULL, NULL, 2, NULL, 0, 'Cut out twelve diamond-shaped grad caps out of the dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4D2901983CD3847AE323CC922E9782F', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 10, NULL, NULL, 2, NULL, 0, 'Use the same diamond-shaped cookie cutter to cut a triangle shaped notch out of the circle of dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52E1E05259388247A1B974E027ECDE5F', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 11, NULL, NULL, 2, NULL, 0, 'Then brush two long edges of the diamond-shaped dough and insert it into the notch in the circle of dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10E1757437D69345A7D6E8E3085EAE5A', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 12, NULL, NULL, 2, NULL, 0, 'Use your finger to press the seams together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F16B0362AE7B047A6F49F4FDCA3B378', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 13, NULL, NULL, 2, NULL, 0, 'Bake for 10-14 minutes until the edges look set and the tops no longer look wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E107C17132DC747AA3FAC21FE7F6C38', X'865BC20DA718004AA8DD5A3E3C85B0A7', X'865BC20DA718004AA8DD5A3E3C85B0A7', 14, NULL, NULL, 2, NULL, 0, 'Allow cookies to cool completely before decorating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0DE2A3FB3575F4F8D0E563129AC70BD', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 0, NULL, NULL, 2, NULL, 0, 'Reserve about 2 tablespoons of white modeling chocolate to color to make the tassels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1ECA66CF231E5A4A9B251C641791C8F4', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 1, NULL, NULL, 2, NULL, 0, 'Color about 2/3 of the white modeling chocolate using flesh tone food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6110E376DC10314A829CA20B8C8A1574', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 2, NULL, NULL, 2, NULL, 0, 'Color the rest using whatever color you want to make your grad caps.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C858AC8889ABBA47AA17D690F3F5599F', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 3, NULL, NULL, 2, NULL, 0, 'Let the colored modeling chocolate rest at room temperature for about 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A9536D23ADC6745B8A44D73E31D0CBE', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 4, NULL, NULL, 2, NULL, 0, 'Roll the flesh tone modeling chocolate out to about 1/16th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D8CA0E1AF69A143AEC42480D92745F2', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 5, NULL, NULL, 2, NULL, 0, 'Cut out twelve 3 inch circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CD4DC5E5B381A4BABC49901B67DA06F', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 6, NULL, NULL, 2, NULL, 0, 'Then roll out the blue modeling chocolate to the same thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C7296F5B856AE4CABF64C773A319D99', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 7, NULL, NULL, 2, NULL, 0, 'Cut out twelve diamond shaped grad caps.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E22326EF9B59C4A8BA6143726D85CA3', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 8, NULL, NULL, 2, NULL, 0, 'Use that same diamond cutter to cut a triangle shaped notch from each circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11308B5528EB994E90F15C4BCE36C4DF', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 9, NULL, NULL, 2, NULL, 0, 'Brush a light coating of corn syrup over a cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'966647B75234EF41A5A000A0A1A92B49', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 10, NULL, NULL, 2, NULL, 0, 'Place the circle and the diamond over the cookie pressing the seams together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAE2783AB443E44B837374DEDC644E63', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 11, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A153C38D4C5344AB8A6BE46042DFC93', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 12, NULL, NULL, 2, NULL, 0, 'Color your modeling chocolate appropriately for your tassels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0158754839C4834981AF9B8F25D462B7', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 13, NULL, NULL, 2, NULL, 0, 'Roll the modeling chocolate into a tube.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6026240CBB89B74797DEEDF538ABFAB8', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 14, NULL, NULL, 2, NULL, 0, 'Insert into the barrel of a clay extruder.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CFDA3EC8BAC5E4BA72B8DD8ABB77BF1', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 15, NULL, NULL, 2, NULL, 0, 'Attach a single hole disc and screw on the cap.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F67C87E6ABA8504D96428D33227B838C', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 16, NULL, NULL, 2, NULL, 0, 'Push the chocolate clay through the extruder to create a single strand then cut it into twelve 1-inch pieces for the top section of the tassel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FBABC7ACE0F1724096B5EF0B189EB1F0', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 17, NULL, NULL, 2, NULL, 0, 'Brush a dot of corn syrup in the center of each grad cap.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09BE78BC64AB7E4E8961CC7204C87724', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 18, NULL, NULL, 2, NULL, 0, 'Attach the single strand to the corn syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'84B027685062DD48BCF00E42D8FFE832', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 19, NULL, NULL, 2, NULL, 0, 'Then attach a multi-hole disc to the extruder.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AA620CF08844346970A19F9E1609CCB', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 20, NULL, NULL, 2, NULL, 0, 'Push the candy clay through the multi-hole disc to make lots of little strands.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E07E137842EB0A41B1182D424EAB04E8', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 21, NULL, NULL, 2, NULL, 0, 'Cut the strands at about 3/4 of an inch.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A482C52EC780524D8BE78E144B9F0C16', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 22, NULL, NULL, 2, NULL, 0, 'Pinch together the strands on one end and attach it to the single strand using corn syrup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB5426982E3D50428F5B48DAB38873B6', X'8449BA87C98ACA40922F8D79F7328D88', X'8449BA87C98ACA40922F8D79F7328D88', 23, NULL, NULL, 2, NULL, 0, 'Pinch off a small piece of modeling chocolate that matches your grad cap and roll it into a ball and flatten it in the center of the grad cap over the end of the single strand.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5B73BAAAA7402B48910497CFD7763C0F', X'AE67F246D8B60A46A691CC785630A070', X'AE67F246D8B60A46A691CC785630A070', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F04174C95BB0E4E82A03F04409B9C9A', X'AE67F246D8B60A46A691CC785630A070', X'AE67F246D8B60A46A691CC785630A070', 1, NULL, NULL, 2, NULL, 0, 'Heat a large skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E605F20BD13374A994172B8986C1621', X'AE67F246D8B60A46A691CC785630A070', X'AE67F246D8B60A46A691CC785630A070', 2, NULL, NULL, 2, NULL, 0, 'Add ground beef and cook until browned; drain.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79AAAC2E86288A4FA5A372523F9C4EC9', X'AE67F246D8B60A46A691CC785630A070', X'AE67F246D8B60A46A691CC785630A070', 3, NULL, NULL, 2, NULL, 0, 'Add taco seasoning and water and bring to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79BA611812377C4DB6400364FF98B851', X'AE67F246D8B60A46A691CC785630A070', X'AE67F246D8B60A46A691CC785630A070', 4, NULL, NULL, 2, NULL, 0, 'Lower heat and simmer until most of the liquid has evaporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8785DFEF2D030841838CACFBA410A813', X'AE67F246D8B60A46A691CC785630A070', X'AE67F246D8B60A46A691CC785630A070', 5, NULL, NULL, 2, NULL, 0, 'Spread refried beans into bottom of a round or rectangle baking dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3444A8BA40F7F4E8CA388010455389A', X'AE67F246D8B60A46A691CC785630A070', X'AE67F246D8B60A46A691CC785630A070', 6, NULL, NULL, 2, NULL, 0, 'Spread the taco meat over the layer of refried beans.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A9C6C3BF488A7459E849078FF8E49B1', X'AE67F246D8B60A46A691CC785630A070', X'AE67F246D8B60A46A691CC785630A070', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle on diced tomatoes, diced jalapenos, and chopped olives.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'805DDC5A31D17F4F9F0457B87DE09DD4', X'AE67F246D8B60A46A691CC785630A070', X'AE67F246D8B60A46A691CC785630A070', 8, NULL, NULL, 2, NULL, 0, 'Top with shredded cheeses.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31E3E4F5BFC71042B3B66F3443825D79', X'AE67F246D8B60A46A691CC785630A070', X'AE67F246D8B60A46A691CC785630A070', 9, NULL, NULL, 2, NULL, 0, 'Arrange black olive slices in the appropriate date (2015) over top of the cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'967D9B550F5A064D9D9FFCB4AEFAA13D', X'AE67F246D8B60A46A691CC785630A070', X'AE67F246D8B60A46A691CC785630A070', 10, NULL, NULL, 2, NULL, 0, 'Bake for 25 minutes or until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89B6601961CDDD4BA8DE5505B18610B0', X'AE67F246D8B60A46A691CC785630A070', X'AE67F246D8B60A46A691CC785630A070', 11, NULL, NULL, 2, NULL, 0, 'Serve hot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A93FAD43C511B9449F2A6981C9DD67A1', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 0, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of chocolate inside the cavities in a silicone half sphere mold or a plastic peanut butter cup mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C0653F15645264CA6181A414B6C2675', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 1, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to harden (chill in the refrigerator for a few minutes, if desired).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54514726E53C30498FA2634020E8F7F9', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 2, NULL, NULL, 2, NULL, 0, 'Combine the Nestle White Chips with the Reese''s Peanut Butter Chips in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBEC03646B8F7C489531DA911DCE4D19', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 3, NULL, NULL, 2, NULL, 0, 'Heat on high power for three 30 second increments, stirring after each until melted. If needed heat additional 10 second burst of power, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F0F8C172DA5E34C941E8F69155407B5', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 4, NULL, NULL, 2, NULL, 0, 'Stir in the salt and creamy peanut butter until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA87FEF787938347B3C080C9454396FA', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 5, NULL, NULL, 2, NULL, 0, 'Allow to cool 10 minutes then pour into a disposable pastry bag or squeeze bottle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F477C20BD2C88428B5E5FE32DB8004E', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 6, NULL, NULL, 2, NULL, 0, 'Pipe peanut butter fudge filling into the chocolate shells, leaving 1/16 - 1/8th of an inch of space at the top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8CEF1192EAFE944CA788CF317096AA2D', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 7, NULL, NULL, 2, NULL, 0, 'Allow fudge to firm up.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB526A1487923A42842F0FAB51E70453', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 8, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of chocolate over top of each candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD66E427C0A6BD4DA25DF29A6FBB9F28', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 9, NULL, NULL, 2, NULL, 0, 'Let harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60DEE949D88FBD4EAEF8DC0BB6A60173', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 10, NULL, NULL, 2, NULL, 0, 'Pop out of molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72B46AD01D133E40BECCC8E2C97B427A', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 11, NULL, NULL, 2, NULL, 0, 'Spread remaining chocolate out to about 1/8th of an inch thickness on a silicone mat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A16BABD1562D7C4699C0E8143335EFC8', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 12, NULL, NULL, 2, NULL, 0, 'Let sit just until dry, but still flexible.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC05F8014ECFFF4FB9E5D53ED5C125E1', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 13, NULL, NULL, 2, NULL, 0, 'Cut out fifteen 2 inch squares using a metal cookie cutter then press a sheet of parchment paper over top and cover with a heavy cookie sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7033FEEAFBF79A44A50CADC6536F3783', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 14, NULL, NULL, 2, NULL, 0, 'Allow to harden then break off excess chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BDA6BC0C99BBF4BB4245EA6AF37E631', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 15, NULL, NULL, 2, NULL, 0, 'Re-melt the scraps of chocolate and use to attach one chocolate square to the tops of each peanut butter cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5F06104AC18514EAB412E00C1487721', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 16, NULL, NULL, 2, NULL, 0, 'Let dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB68A889D07550489BE3C190433BB49F', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 17, NULL, NULL, 2, NULL, 0, 'Press white modeling chocolate out through a clay extruder fitted with a multi hole disc. Cut off 1 1/2 inch groupings of the white modeling chocolate strands and pinch together one end.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'807B9779869D614E8CF81A2039DE49DE', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 18, NULL, NULL, 2, NULL, 0, 'Press the end into the center of a chocolate square forming the tassels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'918093E3C2009F4BBF558651585B2F78', X'85924F55A907974E8F86AD68DA518695', X'85924F55A907974E8F86AD68DA518695', 19, NULL, NULL, 2, NULL, 0, 'Press a small ball of white modeling chocolate over the center of the chocolate graduation cap covering the tips of the tassels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34FA351FE2D4204F805800D4C79441AF', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 0, NULL, NULL, 2, NULL, 0, 'Cut off and discard the crust from one slice of bread.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1AFB5329D445F4DA212907019ADDFFB', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 1, NULL, NULL, 2, NULL, 0, 'Use a rolling pin to flatten the bread.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32DAFE7BA5F3EF4DB069162BC64F4860', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 2, NULL, NULL, 2, NULL, 0, 'Cut the bread into 3 equal size rectangles, trimming them to make them all the same width (about an inch).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53C1CDA01DBC8A41B3CDD9F2AEB8B6A1', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 3, NULL, NULL, 2, NULL, 0, 'Spread a thin layer of light veggie cream cheese over all three of the rectangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D364CAEA842FA74EABDB78CF98F2D54D', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 4, NULL, NULL, 2, NULL, 0, 'Overlap two of the short ends, sticking the two rectangles together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B772B6D61E158643A7ECD4116132641C', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 5, NULL, NULL, 2, NULL, 0, 'Cut turkey slices the same size as the rectangles and set them on top of the two attached rectangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6016704B246A804A910CD1AC79E7FE0E', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 6, NULL, NULL, 2, NULL, 0, 'Starting at one short end, roll up the turkey & cream cheese topped bread into a spiral.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'280C4210143A3449B4F037F74730E59B', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 7, NULL, NULL, 2, NULL, 0, 'Press the remaining rectangle over the seam.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7CC6D71D39871F4B80E45585CC017DBA', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 8, NULL, NULL, 2, NULL, 0, 'The bread will not completely wrap around the bread, but the seam will be covered and the spiral will stay in place.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6985FA3C4788D4BA03429168BAFC07D', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 9, NULL, NULL, 2, NULL, 0, 'Set the sandwich spiral on the counter so the spiral shows on top then spread a thick layer of veggie cream cheese over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C6717F778CE7A48A61EBE701FB36FCA', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 10, NULL, NULL, 2, NULL, 0, 'Cut a small 3/4 inch circle and a large 2 1/2 inch square out of the second slice of bread.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DF5577EE4BF2F43B72ACADD1CF0C6C0', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 11, NULL, NULL, 2, NULL, 0, 'Press the square on top of the spiral.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD32AF5535FF2B40B57278DA82B7DE64', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 12, NULL, NULL, 2, NULL, 0, 'Cut very thin strips of string cheese or white cheese and attach them to the middle of the square using a dab of the cream cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0EC1445C63AE9498A5ABEBB48606CEB', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 13, NULL, NULL, 2, NULL, 0, 'Allow the cheese to dangle over the edge of the square bread crating the grad cap''s tassels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'66412C763FA7314187E24A03A81E846D', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 14, NULL, NULL, 2, NULL, 0, 'Cut the end of the cheese tassels so they don''t touch the counter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05531767C0364D4E8E2BF0DD9BBF3702', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 15, NULL, NULL, 2, NULL, 0, 'Flatten the circle a bit so that it''s not too tall and spread some cream cheese on one side.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B5432D99A93B34BAE671E8AC6584C7C', X'47806A39C6B0904F80C2D11C1E762F04', X'47806A39C6B0904F80C2D11C1E762F04', 16, NULL, NULL, 2, NULL, 0, 'Flip it over and press it down over in the middle of the square so that it covers the ends of the cheese strips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BA7E13A4E13974180FB8C8C91A8F2C2', X'55B0DF1EB9734249A1F495A022B30F20', X'55B0DF1EB9734249A1F495A022B30F20', 0, NULL, NULL, 2, NULL, 0, 'Stir the French Onion Dip and buttermilk together in a shallow baking dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44E66EF5A5687D4A80C5C4794CB51260', X'55B0DF1EB9734249A1F495A022B30F20', X'55B0DF1EB9734249A1F495A022B30F20', 1, NULL, NULL, 2, NULL, 0, 'Place the chicken tenders in the dish, making sure they are covered with the dip mixture. Cover and refrigerate for 30-60 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9A4874E1159774E9BBC5DF6325FC9B2', X'55B0DF1EB9734249A1F495A022B30F20', X'55B0DF1EB9734249A1F495A022B30F20', 2, NULL, NULL, 2, NULL, 0, 'Crush the potato chips into fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5420888934F794FBE39BF1840263AD3', X'55B0DF1EB9734249A1F495A022B30F20', X'55B0DF1EB9734249A1F495A022B30F20', 3, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADDBC467A6BFB04C945D73324E5D068D', X'55B0DF1EB9734249A1F495A022B30F20', X'55B0DF1EB9734249A1F495A022B30F20', 4, NULL, NULL, 2, NULL, 0, 'Spray a cooling rack with non-stick spray. Set it over a tin foil lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEE1E6412506BE4DAC148EC349D3B52A', X'55B0DF1EB9734249A1F495A022B30F20', X'55B0DF1EB9734249A1F495A022B30F20', 5, NULL, NULL, 2, NULL, 0, 'Remove chicken tenders from the marinade. Allow excess liquid to drain off. Season with salt and pepper, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40EABF75FAC9FF428F21B4F5F0D85495', X'55B0DF1EB9734249A1F495A022B30F20', X'55B0DF1EB9734249A1F495A022B30F20', 6, NULL, NULL, 2, NULL, 0, 'Skewer the chicken tenders, then coat them in the crushed potato chips. Set on the wire cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C6F4E72A9C566419E444B3C5AB0864D', X'55B0DF1EB9734249A1F495A022B30F20', X'55B0DF1EB9734249A1F495A022B30F20', 7, NULL, NULL, 2, NULL, 0, 'Spray the chicken tenders with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C6AD85F3417944A8CA0E1121B23BDD9', X'55B0DF1EB9734249A1F495A022B30F20', X'55B0DF1EB9734249A1F495A022B30F20', 8, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'033F3B69A2AF234CB54378DACBB48C36', X'55B0DF1EB9734249A1F495A022B30F20', X'55B0DF1EB9734249A1F495A022B30F20', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately with the remaining French onion dip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4876B7036EC40F4B980AAE230283D677', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven and a roasting pan filled with enough water to go halfway up the sides of the silicone mold, at 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'157B2157F1048949B7EE016FD1141A06', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 1, NULL, NULL, 2, NULL, 0, 'Place chopped chocolate and butter in a microwave-safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBBA8F67C2CA774B8FC54B68D32ED2F3', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 2, NULL, NULL, 2, NULL, 0, 'Heat on high power for 25 seconds then stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FD62121849A074BA0DBAC1548F4CABE', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 3, NULL, NULL, 2, NULL, 0, 'Heat for 20 more seconds, stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34DA6481BBFA4C48BD910DCDC36C3A0F', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 4, NULL, NULL, 2, NULL, 0, 'Heat on 50% power for 15-20 second increments, stirring vigorously after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EDD7BA188E2204FAAEA215F0343ECF6', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 5, NULL, NULL, 2, NULL, 0, 'Alternatively, you can melt the chocolate and butter in a double boiler set over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB69E3353AC9EA46B75FC6AC16D80340', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 6, NULL, NULL, 2, NULL, 0, 'Whisk eggs until doubled in volume.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52E199A01B78D3459D2B285918813CB5', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 7, NULL, NULL, 2, NULL, 0, 'Stir one third of the eggs into the warm, melted chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6035EEE885C82741BABE9804BDF4ED88', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 8, NULL, NULL, 2, NULL, 0, 'Then fold in the remaining eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C6B308CD3910F46833E9AF190A9DA3A', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 9, NULL, NULL, 2, NULL, 0, 'Butter your silicone molds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5120FCD04D61994FB306CDCED11BD484', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 10, NULL, NULL, 2, NULL, 0, 'Equally, divide batter among 18 half-sphere silicone mold cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'353F05D3ABF3D54586BC2748D286385C', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 11, NULL, NULL, 2, NULL, 0, 'Bake for 10-14 minutes until the cakes still look moist but are set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D766718A1337D44CA041C5BD165E7AD4', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 12, NULL, NULL, 2, NULL, 0, 'Insert a digital thermometer into a cake. Once the cakes reach 140 degrees Fahrenheit, remove them from the oven and set on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F27D85337E833A46839DF59EB063BEEC', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 13, NULL, NULL, 2, NULL, 0, 'Allow the cakes to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44C7753A824564459013530DF61008BC', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 14, NULL, NULL, 2, NULL, 0, 'Wrap the molds in plastic wrap and refrigerate the cakes for at least 4 hours or overnight.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C3C182D131A954B9453C46653AA414E', X'BC530C91D0C5F648A6F0C6E8C636086D', X'BC530C91D0C5F648A6F0C6E8C636086D', 15, NULL, NULL, 2, NULL, 0, 'Un-mold the cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20EF76C76075E84EB6BDC4DA44E4D386', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 0, NULL, NULL, 2, NULL, 0, 'Turn a rimmed baking sheet upside down and set a piece of parchment paper or acetate paper on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7C8C683C01CDA49ADCE000E8947781F', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 1, NULL, NULL, 2, NULL, 0, 'Spread half of the chocolate into a thin layer on the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'376B8C465D135441BC99D9CCDB5EDAD0', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 2, NULL, NULL, 2, NULL, 0, 'Set it in the refrigerator for about 3 minutes, just until it loses it’s shine but isn’t completely set. It needs to be soft enough to cut through.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A443F400C42A964BB0F674E9D268116F', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 3, NULL, NULL, 2, NULL, 0, 'Cut into 3` squares using a straight edge and a pizza wheel or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF9AC3A86F3F3247A34607EDA415E18F', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 4, NULL, NULL, 2, NULL, 0, 'Set another piece of parchment paper on top of your chocolate and set a baking sheet on top of it.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18F9BDDC376F224C82F4C76785EEB2F7', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate the chocolate until set about 10 minutes. Reserve a small amount of melted chocolate to attach the tassels to the caps, and repeat with remaining chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A00F40B8F3F67743AA3F237565D8617A', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 6, NULL, NULL, 2, NULL, 0, 'Once the chocolate is set, remove from the refrigerator and peel from the parchment paper away from the chocolate squares.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EAA4579B04F90F4E95BC97CBC74CBBC8', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 7, NULL, NULL, 2, NULL, 0, 'Remove the scrap pieces, which can be used for another project or enjoyed as a snack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4F3510164165947ABC89BDA64EEEE19', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 8, NULL, NULL, 2, NULL, 0, 'Knead white modeling chocolate until soft.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8366D749F97A7F4F91EB1DD0814F38C6', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 9, NULL, NULL, 2, NULL, 0, 'Pinch off about a tablespoonful and roll it into a log and insert it into a clay extruder fitted with a multi-hole disc.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'100DDEF0467E22489385BFAD90C73036', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 10, NULL, NULL, 2, NULL, 0, 'Extrude a few inches of the clay then cut with a sharp knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34E258CC3D47564D85F19D7C604D1B80', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 11, NULL, NULL, 2, NULL, 0, 'If you don’t have a clay extruder, you can roll thin logs of modeling chocolate to make your tassels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61206090C2F2BB44AA3DF2BBE69AA8AB', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 12, NULL, NULL, 2, NULL, 0, 'Use some melted chocolate to attach the tassels to each chocolate square – place one end in the center of each square.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB4A5EFA5D90D94EBA27E72544B6BFFB', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 13, NULL, NULL, 2, NULL, 0, 'Pinch off a small piece of the modeling chocolate and roll into a ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33FB07157D4587459B45583495FFFDE8', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 14, NULL, NULL, 2, NULL, 0, 'Flatten it and use a dab of melted chocolate to adhere it in the center of the chocolate square covering the end of the tassel pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82D8135BCDA78B4998478A62B84059B9', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 15, NULL, NULL, 2, NULL, 0, 'Use a small dab of chocolate frosting or Nutella to adhere the chocolate squares to your cakes just before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'923A08D0E12FBE44BF93ED64CE7DFCA4', X'F7CEDB479BFD8F4299613832B5932C2D', X'F7CEDB479BFD8F4299613832B5932C2D', 16, NULL, NULL, 2, NULL, 0, 'Cut the ends of the tassels off if they are too long.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B76A15144951B469C31DD00A480FC72', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 0, NULL, NULL, 2, NULL, 0, 'Unwrap peanut butter cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A55E6CD5C0014B4BB879D2279665895D', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 1, NULL, NULL, 2, NULL, 0, 'Unwrap caramels and soften them by heating in the microwave on defrost for 5 second increments, kneading after each, until soft.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33C5F8B27D5DF346A16982F26D5BF79D', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 2, NULL, NULL, 2, NULL, 0, 'Flatten 6 of the caramels, and cut using a 1 - 1 1/2 inch heart cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FFC83B2D7360844A807792EE13C5B298', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 3, NULL, NULL, 2, NULL, 0, 'Cut 12 small circles using a small round plunger cutter or pinch off pieces and roll into small balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D0DF333AF0E1B48A2DC40830FA4AB18', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 4, NULL, NULL, 2, NULL, 0, 'Press one red heart sprinkle onto each caramel heart forming the lion''s nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'043A16E62682B34792A27B0F2B0A1FD3', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 5, NULL, NULL, 2, NULL, 0, 'Break the remaining 3 heart sprinkles in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'421D2853377CF94D916C9FB13CA01EA8', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 6, NULL, NULL, 2, NULL, 0, 'Attach one piece with the rounded side facing down, under the curved part of the caramel heart, creating the lion''s tongue.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41AB32E8F0102E468542BA7402E49620', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 7, NULL, NULL, 2, NULL, 0, 'Draw eyes, eyebrows, and freckles onto each caramel heart using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C58C9918F5DBB34595CAC7DE87368741', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 8, NULL, NULL, 2, NULL, 0, 'Attach one caramel heart lion''s face and two caramel ears to each peanut butter cup using melted confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07236216AC70034AAF00531928A0D55C', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 9, NULL, NULL, 2, NULL, 0, 'Cut the remaining caramel squares in half.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFDD148F27BA504B893A1F2D4E1C584E', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 10, NULL, NULL, 2, NULL, 0, 'Roll each into a ball then into a teardrop shaped paw.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E0999C590556945969AC2CEC044E0D2', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 11, NULL, NULL, 2, NULL, 0, 'Draw 5 claws onto each paw using a black food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AA6469A86DE044B831D57C2C2673725', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 12, NULL, NULL, 2, NULL, 0, 'Wrap each cupcake in a printable `You Make My Heart Roar` cupcake wrapper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A6E648E19C7C34696D1DF17436D38FF', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 13, NULL, NULL, 2, NULL, 0, 'Spoon frosting into a large pastry bag fitted with a large 1M star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'537853E36C2EF9459ED0A488A54AB58B', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 14, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of frosting on top of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A393E569A193444A332760E376FD05D', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 15, NULL, NULL, 2, NULL, 0, 'Press a Reese''s Cup lion onto the front at the top of each frosting swirl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBBA406F64C1364E97185DC9E802DC2E', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 16, NULL, NULL, 2, NULL, 0, 'Press a conversation heart onto the frosting just below the lion''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C238338A4600748B791B6FC8A269F53', X'F5E4DB6B1DD60149BB230C05C96E8583', X'F5E4DB6B1DD60149BB230C05C96E8583', 17, NULL, NULL, 2, NULL, 0, 'Attach one paw on either side of the candy heart.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F43EB04DEE1FA4EB4563A19FEC2FF99', X'3CAB0D843FAD3041A41D14F7A38D4F3A', X'3CAB0D843FAD3041A41D14F7A38D4F3A', 0, NULL, NULL, 2, NULL, 0, 'Carefully split each cookie and scrape the peanut butter filling out into a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5134138CF5DF14E9F06E0CB49FCD418', X'3CAB0D843FAD3041A41D14F7A38D4F3A', X'3CAB0D843FAD3041A41D14F7A38D4F3A', 1, NULL, NULL, 2, NULL, 0, 'Combine the creamy peanut butter, butter, cream and salt in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7892C405DE974048A390C796A5734CC8', X'3CAB0D843FAD3041A41D14F7A38D4F3A', X'3CAB0D843FAD3041A41D14F7A38D4F3A', 2, NULL, NULL, 2, NULL, 0, 'Heat in microwave on high for 30 seconds, stir, heat 20 more seconds, then stir and heat 10-second increments afterward, if needed until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50E5DF15AB2CB648B4AAA91B5A3540C9', X'3CAB0D843FAD3041A41D14F7A38D4F3A', X'3CAB0D843FAD3041A41D14F7A38D4F3A', 3, NULL, NULL, 2, NULL, 0, 'Stir in the powdered sugar then add the reserved peanut butter filling from the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FF309D2FE4A4D478E98D1879EE28116', X'3CAB0D843FAD3041A41D14F7A38D4F3A', X'3CAB0D843FAD3041A41D14F7A38D4F3A', 4, NULL, NULL, 2, NULL, 0, 'Scoop out 30 balls of fudge using a #70 ice cream scoop or spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC7A8C0D9883EC44B351AE94662E5B61', X'3CAB0D843FAD3041A41D14F7A38D4F3A', X'3CAB0D843FAD3041A41D14F7A38D4F3A', 5, NULL, NULL, 2, NULL, 0, 'Roll into balls and press into a slipper shape on one end of a Nutter Butter Cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65A7AAF6FE014C4FAFF477FFB1AF816C', X'3CAB0D843FAD3041A41D14F7A38D4F3A', X'3CAB0D843FAD3041A41D14F7A38D4F3A', 6, NULL, NULL, 2, NULL, 0, 'Create an indentation in each peanut butter fudge ball making it look like a slipper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98BB7B3231877C4ABCAA5C657DA0E74A', X'3CAB0D843FAD3041A41D14F7A38D4F3A', X'3CAB0D843FAD3041A41D14F7A38D4F3A', 7, NULL, NULL, 2, NULL, 0, 'Dip the top party of a cookie in melted pink candy melts and immediately sprinkle with grated pink candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3BEFD30B2B774346BADEC49B5C408637', X'3CAB0D843FAD3041A41D14F7A38D4F3A', X'3CAB0D843FAD3041A41D14F7A38D4F3A', 8, NULL, NULL, 2, NULL, 0, 'Set aside to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DBF0E151C2EBD43B19FC671F9B9C99E', X'3CAB0D843FAD3041A41D14F7A38D4F3A', X'3CAB0D843FAD3041A41D14F7A38D4F3A', 9, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'723622FEC1B58340B1EDA0282F0D3455', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D94AB4BB683764A99B85939FD8A1366', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 1, NULL, NULL, 2, NULL, 0, 'Spray a 9-inch by 13-inch cake pan with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29710A25C5377D4BBFA44103AF704170', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 2, NULL, NULL, 2, NULL, 0, 'Optionally, line the pan with parchment paper and spray the paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E61D73EC4269E743A462D9C59B12C629', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 3, NULL, NULL, 2, NULL, 0, 'Divide white cake batter into 6 bowls (about 3/4 cup in each).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F73A954360F9745AA9654EC8AC954AB', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 4, NULL, NULL, 2, NULL, 0, 'Color one bowl red, another orange, yellow, green, blue and purple.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64AE95E5E16F1B4599A86782D817A2CA', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 5, NULL, NULL, 2, NULL, 0, 'Pour batter into individual disposable pastry bags or large zip top bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'517211A2ED23174A8C76DD290B875281', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 6, NULL, NULL, 2, NULL, 0, 'Snip off the tip of the bag and pipe squiggles of one color batter into the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B100E70162635F4CB6BAFB0D74999F31', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 7, NULL, NULL, 2, NULL, 0, 'Use about 1/3rd of the batter for the first layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D870DA33F27194C9AFF8F4E1FEE360E', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 8, NULL, NULL, 2, NULL, 0, 'Continue piping squiggles of colors, layering the 6 colors a total of three times.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D321B80F535E545800857753AC8DE37', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 9, NULL, NULL, 2, NULL, 0, 'Tap the pan on the counter a few times to level the cake batter and to remove any air bubbles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7166BAED028DD14AB28C18E595867EB9', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 10, NULL, NULL, 2, NULL, 0, 'Bake according to instructions on the cake mix box.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1269AD3F0DAF84DAFE86566857D4F6E', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 11, NULL, NULL, 2, NULL, 0, 'Remove cake and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA35320DA3A3CE49AB7CC5FBCE46269D', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 12, NULL, NULL, 2, NULL, 0, 'Invert cake onto a cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B7CA178E3883CC49B2155FBE43786A97', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 13, NULL, NULL, 2, NULL, 0, 'Remove parchment paper if necessary.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9FF3046D5691D44BD91F47C5ED10F34', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 14, NULL, NULL, 2, NULL, 0, 'Cut cake in half so you have two 9 by 6 1/2-inch pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8505F4A6CDE26E4194249F4FCCB5736B', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 15, NULL, NULL, 2, NULL, 0, 'One piece will be the unicorn''s body.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF37DC85B8D474498FFE796A3AAEBD78', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 16, NULL, NULL, 2, NULL, 0, 'Cut the other piece in half so you have two 6 1/2 by 4 1/2-inch pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1227644DBDCF0C4B8AEA8D229460EE93', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 17, NULL, NULL, 2, NULL, 0, 'One piece will be the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79F74D0EF1DCDE459608C213EE69DB8E', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 18, NULL, NULL, 2, NULL, 0, 'Cut two 2 by 3-inch pieces for the unicorn''s legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37ADE11AB2119842A4CEA9E4DDC756E6', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 19, NULL, NULL, 2, NULL, 0, 'Discard or eat the remaining pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'814E286ED67E414C87565FC82E3BF4E8', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 20, NULL, NULL, 2, NULL, 0, 'Assemble the cake by placing the 9-inch by 6 1/2-inch cake in the lower half of your cake board forming the unicorn''s body.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'643175F0726213459F00853E40A33833', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 21, NULL, NULL, 2, NULL, 0, 'Set the 6 1/2-inch by 4 1/2-inch piece about 2 1/2 inches from the top edge of the larger cake forming the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D998A67238213E48A5F9CB2415DB68F0', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 22, NULL, NULL, 2, NULL, 0, 'Place the two leg pieces under the body piece about 3/4 of an inch from either edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'173C9018E0A65242A2B5ADF9CEA4ACC3', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 23, NULL, NULL, 2, NULL, 0, 'Spoon frosting into pastry bags fitted with #21 star tips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09E468F77064A848BC0019A47B7B7578', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 24, NULL, NULL, 2, NULL, 0, 'Pipe white stars over the large cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F89725175F7E094EAB2400DB2EACE75D', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 25, NULL, NULL, 2, NULL, 0, 'Pipe stars over 3/4''s of the head and legs, leaving room to pipe on pink hooves and a pink snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'044014C465EF2C488D4176AFBE1652F1', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 26, NULL, NULL, 2, NULL, 0, 'Pinch off about 3 ounces of white modeling chocolate and form into a twisted unicorn horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E4273E7EF6CA948BFE2C732771899A3', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 27, NULL, NULL, 2, NULL, 0, 'Brush with edible gold luster dust and attach to the cake near the right side of the top edge of the head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA070DBA1A71D949AF290C199D3784DD', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 28, NULL, NULL, 2, NULL, 0, 'Roll the remaining white candy clay to about 3/16-inch thickness. Cut out an ear using a 1 1/2-inch wide by 2 1/2-inch long teardrop cookie cutter or a knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0108A5F802D1D4CBBFF9AE6D68D361A', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 29, NULL, NULL, 2, NULL, 0, 'Roll out the pink candy clay to about 1/16th-inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FD3E0F2E1D35FD4E97AE749A2D6A44CE', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 30, NULL, NULL, 2, NULL, 0, 'Cut a smaller teardrop shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F86014493F01E44FA0781DA33EA18FA7', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 31, NULL, NULL, 2, NULL, 0, 'Use a small dab of water to attach the pink teardrop to the center near the lower edge of the white teardrop to form the unicorn''s ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B6D8BCBCBD5AB4F84798BDAD2D0ED9D', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 32, NULL, NULL, 2, NULL, 0, 'Set the ear aside to dry for about 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BF46522115E2E498843055AA0FA486F', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 33, NULL, NULL, 2, NULL, 0, 'Roll out the black candy clay and cut out a 2 1/4-inch circle then cut just a thin edge from around the circle to create a thin moon-shaped eye.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA3CF72D98465344BAF16A723B7E93D6', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 34, NULL, NULL, 2, NULL, 0, 'Cut a small 3/4-inch round out of the black and do the same creating a nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9880886718B6B44BA15653FB0A8AC01F', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 35, NULL, NULL, 2, NULL, 0, 'Cut four small rectangles then shape them into teardrop shaped eyelashes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1067C1604DC6A346A4BEDAFC8043EE01', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 36, NULL, NULL, 2, NULL, 0, 'Roll the remaining black candy clay into a long thin log for the unicorn''s mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4C719C9DDBB484FA922F902AAED46E3', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 37, NULL, NULL, 2, NULL, 0, 'Use a dab of frosting to attach the candy ear to the upper right corner of the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A90D1F68AB9994FB736C2EECD30A440', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 38, NULL, NULL, 2, NULL, 0, 'Press the eye onto the cake to the left below the ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BB734FE46127E498C6BA60F231E90C0', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 39, NULL, NULL, 2, NULL, 0, 'Then press the four eyelashes above the eye.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FCA5647559728499B396CCB2665470A', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 40, NULL, NULL, 2, NULL, 0, 'Press the black log onto the pink frosting creating the unicorn''s smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF394D23DF56B348AD749A5879FDD95D', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 41, NULL, NULL, 2, NULL, 0, 'Cut off any excess candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5212C76714999E4C816B2227528E48BF', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 42, NULL, NULL, 2, NULL, 0, 'Press the nose above the smile.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1FFCC8A1B1C17B44A8F8A88338CB40A7', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 43, NULL, NULL, 2, NULL, 0, 'Roll all the other colored candy clay pieces into 8 inch long logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14D1AED7C8B7E94693F64B319047C78D', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 44, NULL, NULL, 2, NULL, 0, 'Press them all together in rainbow order: purple, blue, green, yellow, orange, and red.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'359B999E63481442BE430347E9F78F82', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 45, NULL, NULL, 2, NULL, 0, 'Cut the logs into one 3 inch side and one 5 inch side.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A0BBA73A43B9247BCA114F6905480FA', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 46, NULL, NULL, 2, NULL, 0, 'Flatten the logs and roll them out to about 1/4 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E402C4F5BB42304DBADEFE9F3DCDFEF3', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 47, NULL, NULL, 2, NULL, 0, 'Shape the longer piece into a curved tail pinching together the left side.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6831336680F0104D8E819FD0EE975E94', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 48, NULL, NULL, 2, NULL, 0, 'On the right side, cut the ends so that each color has a nice pointed shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA9C519B7FF32E4D9BF67848BC36DD32', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 49, NULL, NULL, 2, NULL, 0, 'Cut the left edge of the other piece so that it''s straight then cut the opposite side so that each color has a nice pointed shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E12B1D56CF0C4479C0ED3A99BC967AD', X'615A8334293E6F42B455A6618ED856AA', X'615A8334293E6F42B455A6618ED856AA', 50, NULL, NULL, 2, NULL, 0, 'Attach the tail to the top corner on the right side of the unicorn''s body and the mane to the right edge of the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A8AE1C23E531A428D86A3BD16050587', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 0, NULL, NULL, 2, NULL, 0, 'Dip a clean, food-use-only, fine-tipped paintbrush into the pink candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80A00926E79DF64D8613D55BC58EB99B', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 1, NULL, NULL, 2, NULL, 0, 'Dab the candy over the rose cavity in the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BAF0DB8594AC4A4BAB1A8BF5F2BFD10F', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 2, NULL, NULL, 2, NULL, 0, 'Then dip another clean paintbrush into the light pink candy melts and paint on the inner triangle in both of the unicorn’s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'745640A6D3382D4AB899FFEDD8B3D862', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 3, NULL, NULL, 2, NULL, 0, 'Now begin to paint the mane by dabbing in dark blue or bright purple into the tuft of hair that is closest to the rose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3472B4E23DB524EAF31040DCFF9CEA8', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 4, NULL, NULL, 2, NULL, 0, 'Continue to paint the tufts of hair in this order: light blue, green, yellow, orange, and red.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0DE8385C6104764995156FABC19640AF', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 5, NULL, NULL, 2, NULL, 0, 'Use a very fine paintbrush to paint the eyelashes using black or dark cocoa candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E7B2BE344098243B5A74A793BEC182B', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 6, NULL, NULL, 2, NULL, 0, 'Pop the mold into the freezer for about 3 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A67BD9F2E686454D97026AF884EC3A86', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 7, NULL, NULL, 2, NULL, 0, 'Remove the mold and let it sit at room temperature for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24501A58F213A8488D2CB8A2278D2480', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 8, NULL, NULL, 2, NULL, 0, 'Your mold must not be cold when you add in the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00B872DA8991564F8CCD9C508B6042F5', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 9, NULL, NULL, 2, NULL, 0, 'Make sure your white chocolate is not hot to the touch, then fill the entire mold with white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BCE4209F7EBF644A21D1249F9E8068A', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 10, NULL, NULL, 2, NULL, 0, 'Carefully invert the mold over your bowl of white chocolate and allow the wet candy to drip out of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2D472C48D95B444A647CBFB3F0E8FD3', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 11, NULL, NULL, 2, NULL, 0, 'Shake the mold and allow more to drip out until you just have a nice thin coating around the interior of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97B8359F8DF7D349AB131AE2B2D67420', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 12, NULL, NULL, 2, NULL, 0, 'Use a metal scraper to scrape off the excess so that the edge is nice and clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B666DFCE3716C2409CD3840AEA52E97C', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 13, NULL, NULL, 2, NULL, 0, 'Insert a wooden popsicle stick into the wet white chocolate in the silicone unicorn mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB969EDB7CA3394ABFEF5F0AFF98C66D', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 14, NULL, NULL, 2, NULL, 0, 'Immediately insert a wood popsicle stick into the mold while the white chocolate is still wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B22B4DDEAC530845A4CBB88D7B7C96A3', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 15, NULL, NULL, 2, NULL, 0, 'Pop the mold in the freezer for about 5 minutes just until the white candy melts harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D56AAE743665043BD26EE2F57211402', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 16, NULL, NULL, 2, NULL, 0, 'Step-by-step images showing how to fill a white chocolate unicorn with cake ball filling before covering it in more white chocolate. Then brush edible gold luster dust over the horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BFE24A02F221BB4BBAAE497574B491DA', X'C977C1EE9A956F47AB312BCE27248BF3', X'C977C1EE9A956F47AB312BCE27248BF3', 17, NULL, NULL, 2, NULL, 0, 'Let the mold sit for about 10 minutes to come to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E18DD55AA777EA42AC6D401E9E222FEB', X'43EB2B6C39274D4AABE1E8774A33519B', X'43EB2B6C39274D4AABE1E8774A33519B', 0, NULL, NULL, 2, NULL, 0, 'Cut off the browned edges of the cake then crumble into really small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5D28BDD68B45A4AAF6D2165D1DD45D7', X'43EB2B6C39274D4AABE1E8774A33519B', X'43EB2B6C39274D4AABE1E8774A33519B', 1, NULL, NULL, 2, NULL, 0, 'Stir in at least 1/4 cup of frosting until the cake ball filling is smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C96F5A668B4C2B44AC775FECEC697CA2', X'43EB2B6C39274D4AABE1E8774A33519B', X'43EB2B6C39274D4AABE1E8774A33519B', 2, NULL, NULL, 2, NULL, 0, 'Press cake ball filling into the mold and, if necessary, under the popsicle stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'503C1FCAA9175344B0AFCEB8AADE3E68', X'43EB2B6C39274D4AABE1E8774A33519B', X'43EB2B6C39274D4AABE1E8774A33519B', 3, NULL, NULL, 2, NULL, 0, 'Fill the mold to about 1/8th inch from the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D80528891DC12B4AB148450C2CF6FF8C', X'43EB2B6C39274D4AABE1E8774A33519B', X'43EB2B6C39274D4AABE1E8774A33519B', 4, NULL, NULL, 2, NULL, 0, 'Spoon white chocolate over top of the cake ball filling and spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13F12CCD81DA2140BAC4C961A4684132', X'43EB2B6C39274D4AABE1E8774A33519B', X'43EB2B6C39274D4AABE1E8774A33519B', 5, NULL, NULL, 2, NULL, 0, 'Scrape over top using a metal scraper to remove all of the excess chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C65894BB8C9C3E4C94ED2CA7E8A46BDA', X'43EB2B6C39274D4AABE1E8774A33519B', X'43EB2B6C39274D4AABE1E8774A33519B', 6, NULL, NULL, 2, NULL, 0, 'Freeze for about 5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A499E6FA50DEF478DC43845DEE3DCD1', X'43EB2B6C39274D4AABE1E8774A33519B', X'43EB2B6C39274D4AABE1E8774A33519B', 7, NULL, NULL, 2, NULL, 0, 'Then unmold the unicorn and allow it to warm to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79EE60CA594C4448B9C0A3874ABA080D', X'43EB2B6C39274D4AABE1E8774A33519B', X'43EB2B6C39274D4AABE1E8774A33519B', 8, NULL, NULL, 2, NULL, 0, 'Brush a light dusting of edible gold luster dust over the unicorn’s horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'67A31E1F7E51E841A948F482F9792A51', X'96548F6FC730764E8932994ED05FBF8E', X'96548F6FC730764E8932994ED05FBF8E', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14925202A46F534489C12A709A963D55', X'96548F6FC730764E8932994ED05FBF8E', X'96548F6FC730764E8932994ED05FBF8E', 1, NULL, NULL, 2, NULL, 0, 'Grease and flour a Mickey Mouse cake ring and set on a parchment paper-lined baking pan. (Or use a 6-inch pan and 2 jumbo muffin cups.)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA54830BE882A6488A833B18F1997D43', X'96548F6FC730764E8932994ED05FBF8E', X'96548F6FC730764E8932994ED05FBF8E', 2, NULL, NULL, 2, NULL, 0, 'Combine all the cake ingredients in a mixing bowl and beat, using an electric mixer, on low speed for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'534F946B4E46A74F8E44D2D840A6ECF7', X'96548F6FC730764E8932994ED05FBF8E', X'96548F6FC730764E8932994ED05FBF8E', 3, NULL, NULL, 2, NULL, 0, 'Increase the speed to medium and beat for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB95E4C2CEA7A74D9BF7BD01F7A50B7A', X'96548F6FC730764E8932994ED05FBF8E', X'96548F6FC730764E8932994ED05FBF8E', 4, NULL, NULL, 2, NULL, 0, 'Pour batter into pan, filling it 2/3 full. Use the remaining batter to make cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE8B73875AEA2941B950588608386304', X'96548F6FC730764E8932994ED05FBF8E', X'96548F6FC730764E8932994ED05FBF8E', 5, NULL, NULL, 2, NULL, 0, 'Bake for 26-34 minutes until a toothpick inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D18FD646F396D4E829684F579B45F87', X'96548F6FC730764E8932994ED05FBF8E', X'96548F6FC730764E8932994ED05FBF8E', 6, NULL, NULL, 2, NULL, 0, 'Allow the cake to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA2E5DC956E8914E976151B389C27537', X'96548F6FC730764E8932994ED05FBF8E', X'96548F6FC730764E8932994ED05FBF8E', 7, NULL, NULL, 2, NULL, 0, 'Carefully flip the cake upside down and unmold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB644A8BAA90084CBEA2757D67E9AC03', X'96548F6FC730764E8932994ED05FBF8E', X'96548F6FC730764E8932994ED05FBF8E', 8, NULL, NULL, 2, NULL, 0, 'Cut the cake in half creating two even layers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6D10F77D97585478534CDEF66318E46', X'4695896A30365748B150C1B6A1D56BD5', X'4695896A30365748B150C1B6A1D56BD5', 0, NULL, NULL, 2, NULL, 0, 'Spread about 1 cup of white frosting in between the cake layers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61141E493D35A64B9FD0C30420D29533', X'4695896A30365748B150C1B6A1D56BD5', X'4695896A30365748B150C1B6A1D56BD5', 1, NULL, NULL, 2, NULL, 0, 'Color about 2 tablespoons of white frosting using orange food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3317CA2EFAD5CD48A7D93578C34B5C27', X'4695896A30365748B150C1B6A1D56BD5', X'4695896A30365748B150C1B6A1D56BD5', 2, NULL, NULL, 2, NULL, 0, 'Spoon into a pastry bag fitted with a small #14 or #15 star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB10D0A02E5FDE4C89D5D30D4E3ECBB4', X'4695896A30365748B150C1B6A1D56BD5', X'4695896A30365748B150C1B6A1D56BD5', 3, NULL, NULL, 2, NULL, 0, 'Color the chocolate frosting using black food coloring and spoon into a pastry bag fitted with a coupler and a #3 round tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1580E92E05EF048BF68F596EE37F5C3', X'5432431521A0F64DB22BB108335B2CA6', X'5432431521A0F64DB22BB108335B2CA6', 0, NULL, NULL, 2, NULL, 0, 'Fill a pastry bag fitted with a #3 round pastry tip with the clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0ED1D7981CAB5459A7AB444C8284C41', X'5432431521A0F64DB22BB108335B2CA6', X'5432431521A0F64DB22BB108335B2CA6', 1, NULL, NULL, 2, NULL, 0, 'Set a piece of parchment paper over an image of Olaf (that has been printed in reverse) and pipe the gel over the outline of the mouth, eyes, carrot nose, and eyebrows.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F20EDCCEA75384AAD31D21F318414AB', X'5432431521A0F64DB22BB108335B2CA6', X'5432431521A0F64DB22BB108335B2CA6', 2, NULL, NULL, 2, NULL, 0, 'Turn the template upside down and set it on the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C46F2A57EA73643AD0818CE6E055DD4', X'5432431521A0F64DB22BB108335B2CA6', X'5432431521A0F64DB22BB108335B2CA6', 3, NULL, NULL, 2, NULL, 0, 'Gently press to adhere the piping gel to the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'559F1618696CA442B647FD0B5C29AE57', X'5432431521A0F64DB22BB108335B2CA6', X'5432431521A0F64DB22BB108335B2CA6', 4, NULL, NULL, 2, NULL, 0, 'Remove the template.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9790FAD8EA2D2C48BE269C7873A9AEC8', X'2FB183FD6A6D1044AFDA79B19D315366', X'2FB183FD6A6D1044AFDA79B19D315366', 0, NULL, NULL, 2, NULL, 0, 'Pipe black frosting directly over top of the piping gel outline.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'929C88E434969F49BDCE8857D79EC2E9', X'2FB183FD6A6D1044AFDA79B19D315366', X'2FB183FD6A6D1044AFDA79B19D315366', 1, NULL, NULL, 2, NULL, 0, 'Remove the round tip and replace it with a (#18) star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03224B03BC0A874BB68E468097BC49EB', X'2FB183FD6A6D1044AFDA79B19D315366', X'2FB183FD6A6D1044AFDA79B19D315366', 2, NULL, NULL, 2, NULL, 0, 'Pipe black frosting inside Olaf''s smile then add his pupils.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20B1548209770A42A85D167A0FF7D15F', X'2FB183FD6A6D1044AFDA79B19D315366', X'2FB183FD6A6D1044AFDA79B19D315366', 3, NULL, NULL, 2, NULL, 0, 'Pipe orange frosting stars into the carrot nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'070DC96AFB61B441BF0B7CF1A543B5DA', X'2FB183FD6A6D1044AFDA79B19D315366', X'2FB183FD6A6D1044AFDA79B19D315366', 4, NULL, NULL, 2, NULL, 0, 'Spoon white frosting into a pastry bag fitted with a (#18) star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'705637404355FF4C9743991FAB792F82', X'2FB183FD6A6D1044AFDA79B19D315366', X'2FB183FD6A6D1044AFDA79B19D315366', 5, NULL, NULL, 2, NULL, 0, 'Pipe stars into the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF7A22D13500E8439A2443E88AAE7DE7', X'2FB183FD6A6D1044AFDA79B19D315366', X'2FB183FD6A6D1044AFDA79B19D315366', 6, NULL, NULL, 2, NULL, 0, 'Then pipe white stars all over the rest of the cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B178B9855E661F4AA762EBBF31050A12', X'2FB183FD6A6D1044AFDA79B19D315366', X'2FB183FD6A6D1044AFDA79B19D315366', 7, NULL, NULL, 2, NULL, 0, 'If needed, replace the star tip with the round tip on the black frosting and pipe more black frosting over the pupils, eyebrows, and parts of the smile to make them show better.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'599EA884A643834F94EE18B6BE75766F', X'5DD3E7DB47136E49A1A7E321FB0260D6', X'5DD3E7DB47136E49A1A7E321FB0260D6', 0, NULL, NULL, 2, NULL, 0, 'Either use chocolate frosting to pipe some twigs onto your cake board or pipe twigs out of candy melts and allow them to harden before inserting them into the cake between the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A635521CD266CC42A88D6AF0337C3BCB', X'6D4D4E477B35E44C811232E95F7F208A', X'6D4D4E477B35E44C811232E95F7F208A', 0, NULL, NULL, 2, NULL, 0, 'Crumble the cake and blend it together with the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DB1D0B3A9737E47991E29EEA6A372E8', X'6D4D4E477B35E44C811232E95F7F208A', X'6D4D4E477B35E44C811232E95F7F208A', 1, NULL, NULL, 2, NULL, 0, 'Add more frosting if desired until the cake ball filling has a nice chocolate truffle style texture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A88C5A214A69A49A7B6839D71976194', X'34FC4364CAEACA4EAF4C74B65BAB5C08', X'34FC4364CAEACA4EAF4C74B65BAB5C08', 0, NULL, NULL, 2, NULL, 0, 'Spoon 1 tablespoonful of white chocolate into one popsicle cavity.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1D425FBF726BE41B118B30A9A6ED18F', X'34FC4364CAEACA4EAF4C74B65BAB5C08', X'34FC4364CAEACA4EAF4C74B65BAB5C08', 1, NULL, NULL, 2, NULL, 0, 'Paint it into a thin layer on the bottom and up the sides of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9EFFB33D6E4BE84DA6428883F959B6E9', X'34FC4364CAEACA4EAF4C74B65BAB5C08', X'34FC4364CAEACA4EAF4C74B65BAB5C08', 2, NULL, NULL, 2, NULL, 0, 'Immediately insert then remove a popsicle stick into the mold, to remove any white chocolate from that area.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA94074F2D3D26469242027332091E7B', X'34FC4364CAEACA4EAF4C74B65BAB5C08', X'34FC4364CAEACA4EAF4C74B65BAB5C08', 3, NULL, NULL, 2, NULL, 0, 'Repeat creating 4 white chocolate shells in your popsicle mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F87A2DBD234214D9CB543CDE7EC0AFF', X'34FC4364CAEACA4EAF4C74B65BAB5C08', X'34FC4364CAEACA4EAF4C74B65BAB5C08', 4, NULL, NULL, 2, NULL, 0, 'Pop the mold into the refrigerator, if using pure white chocolate, and into the freezer if using confectionery coating (Candy Melts).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'08051B0D7CBAF74AB20BDD218D2EF5E0', X'34FC4364CAEACA4EAF4C74B65BAB5C08', X'34FC4364CAEACA4EAF4C74B65BAB5C08', 5, NULL, NULL, 2, NULL, 0, 'Chill for 3-5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'99CFEEB91B9A074EB5F6A4735C0F18A1', X'34FC4364CAEACA4EAF4C74B65BAB5C08', X'34FC4364CAEACA4EAF4C74B65BAB5C08', 6, NULL, NULL, 2, NULL, 0, 'Scoop out 1 tablespoonful of the cake ball filling and press it into the candy shell, leaving at least 1/16 of an inch of space at the top for the additional layer of chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E47DD633062BB347B2F12FD9A1F6E11F', X'34FC4364CAEACA4EAF4C74B65BAB5C08', X'34FC4364CAEACA4EAF4C74B65BAB5C08', 7, NULL, NULL, 2, NULL, 0, 'Repeat filling all the popsicles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C49294A434AA346876CF2E36F56F2F1', X'34FC4364CAEACA4EAF4C74B65BAB5C08', X'34FC4364CAEACA4EAF4C74B65BAB5C08', 8, NULL, NULL, 2, NULL, 0, 'Spoon about a teaspoon of white chocolate over top and spread it out thin.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36DD24309E0AF74AB3A43927CDA8B711', X'34FC4364CAEACA4EAF4C74B65BAB5C08', X'34FC4364CAEACA4EAF4C74B65BAB5C08', 9, NULL, NULL, 2, NULL, 0, 'Use an offset spatula to scrape off any excess white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E66059C31213FC43B3AC39D4DDC12452', X'34FC4364CAEACA4EAF4C74B65BAB5C08', X'34FC4364CAEACA4EAF4C74B65BAB5C08', 10, NULL, NULL, 2, NULL, 0, 'Repeat covering all the cakesicles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13EEFD53B8844E4A85912ED3805614CA', X'34FC4364CAEACA4EAF4C74B65BAB5C08', X'34FC4364CAEACA4EAF4C74B65BAB5C08', 11, NULL, NULL, 2, NULL, 0, 'Chill for about 2 minutes until the candy layer hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B355FE8A06DC64CBE87BDA5B3F91A53', X'34FC4364CAEACA4EAF4C74B65BAB5C08', X'34FC4364CAEACA4EAF4C74B65BAB5C08', 12, NULL, NULL, 2, NULL, 0, 'Unmold the cakesicles and they are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECF5795A3A46BC4B8106B625B792EED1', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', 0, NULL, NULL, 2, NULL, 0, 'Melt butter and marshmallows in a large saucepan set over low heat then stir in the cereal until evenly coated.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'812DF7D0DE768C4C95B9355D2FAAD851', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', 1, NULL, NULL, 2, NULL, 0, 'Gently spread into an even layer in a well buttered 9 x 13 inch pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CD487D53AFA7245A3D2DE02BC55CB6D', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', 2, NULL, NULL, 2, NULL, 0, 'Allow to cool for at least one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A9713904E6FBF24B89408FCE536E815B', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', 3, NULL, NULL, 2, NULL, 0, 'Cut 12 circles out of the treats using the 2-inch round cutter from the DIY-Lish Decorating Kit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'95583B04737E404DB65DA6005C0DA824', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', 4, NULL, NULL, 2, NULL, 0, 'Knead then roll out the fondant to about 1/16 of an inch thickness. Mix together, in a 1 to 6 ratio, a small amount of white and brown fondant to create tan fondant.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA4EF6EC9CC1034F968F7D9E0A5738E5', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', 5, NULL, NULL, 2, NULL, 0, 'For each bear cut out 1 black circle, 2 black teardrops, and 3 tan teardrops. Attach one tan teardrop to the black circle using water and attach the other tan teardrops to the black teardrops, cutting off the tips, creating the ears. Press two black candies on either side of the tan teardrop creating the eyes. Roll a tiny piece of black fondant into an oval and attach, using water, along the bottom edge of the tan teardrop creating the nose. Use a toothpick to create two nostrils.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CB03A96CA0B564EBB58900581367CAB', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', 6, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of melted candy melts on the top of a rice krispie treat and attach a fondant bear face on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABB0A378DBF6C940BA6E3FBB096436AB', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', 7, NULL, NULL, 2, NULL, 0, 'Brush a small amount of candy melts onto the edge of one of the ears and press it onto the rice krispie treat along the top edge of the bears head. Repeat with the other ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F93D0B2A4E20A4D960C58C3416E0DC0', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', X'8A2EFC3AF82C00478EB6CECCAB9B5E10', 8, NULL, NULL, 2, NULL, 0, 'Create more fondant animals using the instructions found in the DIY-Lish Cute Critters Decorating Kit and attach them to the remaining rice krispie treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98AD552891CFC1429D8658BA77675C19', X'A1402FBCA0B59141A8CC276EC45683D0', X'A1402FBCA0B59141A8CC276EC45683D0', 0, NULL, NULL, 2, NULL, 0, 'Grease a large mixing bowl, rubber spatula, and 9-inch x 13-inch pan with butter or non-stick spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A66B7073294E84AA012AFD96B497C9F', X'A1402FBCA0B59141A8CC276EC45683D0', X'A1402FBCA0B59141A8CC276EC45683D0', 1, NULL, NULL, 2, NULL, 0, 'Pour the cereal into the bowl and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'223414EDF76D1A4A81AE07C266464EE2', X'A1402FBCA0B59141A8CC276EC45683D0', X'A1402FBCA0B59141A8CC276EC45683D0', 2, NULL, NULL, 2, NULL, 0, 'Begin melting the butter in a medium-large saucepan set over low heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB011B19D2D5674DAA2EA0572343B521', X'A1402FBCA0B59141A8CC276EC45683D0', X'A1402FBCA0B59141A8CC276EC45683D0', 3, NULL, NULL, 2, NULL, 0, 'Add marshmallows and continue to heat, stirring often, until about 75% melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BEAB530FBCF6C54E86E30B3CA88EF8FE', X'A1402FBCA0B59141A8CC276EC45683D0', X'A1402FBCA0B59141A8CC276EC45683D0', 4, NULL, NULL, 2, NULL, 0, 'Remove from the heat and continue to stir vigorously until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D0AF622208D904EB76192A17BFD8C96', X'A1402FBCA0B59141A8CC276EC45683D0', X'A1402FBCA0B59141A8CC276EC45683D0', 5, NULL, NULL, 2, NULL, 0, 'Pour the melted marshmallows over the cereal and stir to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11E5B6757116924895C8D3A6541D3E3D', X'A1402FBCA0B59141A8CC276EC45683D0', X'A1402FBCA0B59141A8CC276EC45683D0', 6, NULL, NULL, 2, NULL, 0, 'Spread into an even layer in the pan and set aside to cool for at least one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D63FA4CBA6D934F937653A59A4F5DE1', X'A1402FBCA0B59141A8CC276EC45683D0', X'A1402FBCA0B59141A8CC276EC45683D0', 7, NULL, NULL, 2, NULL, 0, 'Cut into twelve 2-inch by 3-inch rectangles, saving the scraps to fill in any craters on the rectangle treats before enjoying as a snack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68FAE54DEF152E48A75BBC754369337D', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 0, NULL, NULL, 2, NULL, 0, 'Insert the tip of a lollipop stick into the melted and tempered white chocolate or melted confectionery coating.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'297BC0D8377A4A42A8986A5270E3B17B', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 1, NULL, NULL, 2, NULL, 0, 'Then insert the stick into a cereal treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8719BBEDDF1F1C4890DD94C4A6F577F1', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 2, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC6D7BC35304C6468AFDD3559DE67A4D', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 3, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to harden for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B667FB615C35C34C9667B79B9141BCFC', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 4, NULL, NULL, 2, NULL, 0, 'Fill a tall 3 inch diameter glass with white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01D7CF43A54B9A418CEABDD9D44EEE24', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 5, NULL, NULL, 2, NULL, 0, 'Dip a cereal treat into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5FE13725A527740AD5C043803AC936A', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 6, NULL, NULL, 2, NULL, 0, 'Lift it out and allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E003F895E33D4F4994E6B34E16041ADD', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 7, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27E4651112EC0645AB1890FAD0B5BEA8', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 8, NULL, NULL, 2, NULL, 0, 'If using pure white chocolate allow the chocolate to dry at room temperature or pop in the refrigerator for about 5 minutes. If using confectionery coating, pop in the freezer for 3-5 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'268232E2E37394468CC7C05AEF3988CF', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 9, NULL, NULL, 2, NULL, 0, 'Repeat, dipping all 12 rice krispie treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7086985A532A60428C071ACFB0DCF39B', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 10, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of the gold modeling chocolate and roll into pointed logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3367CFBD05950D41A4B07F287872CEFD', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 11, NULL, NULL, 2, NULL, 0, 'Twist two logs together to create a unicorn horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8FD5A70198CD674C88302CA12EB9C3C6', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 12, NULL, NULL, 2, NULL, 0, 'If desired, brush the horns with edible gold luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F01F1F131C8FD4C9801E74BF913C55F', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 13, NULL, NULL, 2, NULL, 0, 'Roll out the white modeling chocolate to about 1/4 inch thickness and cut out 24 small triangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6ECDF493858FE489E5FB8C3373E57D7', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 14, NULL, NULL, 2, NULL, 0, 'Roll out the pink modeling chocolate really thin and cut out 24 small triangles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'85B6F2D32A3995408C13DCA02A7FE604', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 15, NULL, NULL, 2, NULL, 0, 'Use water to attach the inner pink ears (triangles) to the outer white ears (triangles).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50235DBE0D2D6B46AC869F0D91203DF7', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 16, NULL, NULL, 2, NULL, 0, 'Dust a silicone rose mold with corn starch.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6FC3459950F4147918C55F9CAD18D6A', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 17, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of your colored modeling chocolate and press them into the mold creating various sized roses.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8AFB8E756A219343BFB1D03A30ED4BD6', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 18, NULL, NULL, 2, NULL, 0, 'Attach one horn to the top of each of the rice krispie treats and attach two ears on either side using white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B8B34E3D14DE443AACEB36D62A9A86C', X'DBA70C5A0086BD43BE4739D9E8D03215', X'DBA70C5A0086BD43BE4739D9E8D03215', 19, NULL, NULL, 2, NULL, 0, 'Then attach 5 flowers on the front, top edge, of each treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'885817F162C54B47B6A674F18D58A5A8', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1AF10C153337E4385AC8E215D4EB822', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', 1, NULL, NULL, 2, NULL, 0, 'Line two cupcake pans with 21 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24C018FB6F2EFA42BAD4416F866DFCAB', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', 2, NULL, NULL, 2, NULL, 0, 'Combine the white cake mix, instant pudding mix, sour cream, egg whites, vegetable oil, and water in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8E4302769FF244C938BE9D065202106', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', 3, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on low speed for 30 seconds to incorporate the ingredients then increase the speed to medium and beat for 1 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F827FB006B4E584C8458E99B601D2649', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', 4, NULL, NULL, 2, NULL, 0, 'Equally divide the cake batter among the 21 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0CB0517AA583614F8136BD825A2E49D7', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', 5, NULL, NULL, 2, NULL, 0, 'Bake for 18-24 minutes until the cupcake springs back when your press a finger on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4A638D2A9A5F73448FBDCE36E9605078', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', X'EEA2A5ECCCFCCC4BAF6A7EE2818EC39B', 6, NULL, NULL, 2, NULL, 0, 'Remove the cupcakes from the oven and allow them to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'489FCC7CEEE77247B1263B9CA1414EF3', X'DFBA8577A266294EB7CF52DF5C203510', X'DFBA8577A266294EB7CF52DF5C203510', 0, NULL, NULL, 2, NULL, 0, 'Pour the strawberry pie filing into a strainer set over a large bowl and press down on the strawberries to allow the red gel to fall into the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C757D5BF7D02049925200012B3DA2DD', X'DFBA8577A266294EB7CF52DF5C203510', X'DFBA8577A266294EB7CF52DF5C203510', 1, NULL, NULL, 2, NULL, 0, 'You will use the gel to fill the cupcakes and the strawberries to make the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F6A29253BA24A40925FCF954503DCD3', X'DFBA8577A266294EB7CF52DF5C203510', X'DFBA8577A266294EB7CF52DF5C203510', 2, NULL, NULL, 2, NULL, 0, 'Spoon the gel into a pastry bag to pipe into the cupcake or just use a small spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EA2EE17701A4614F80BF75132D48BA61', X'DFBA8577A266294EB7CF52DF5C203510', X'DFBA8577A266294EB7CF52DF5C203510', 3, NULL, NULL, 2, NULL, 0, 'Use a cupcake corer or knife to cut a well out of the center of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC65B0DBCE4B9945B13D8F238BBD7F6B', X'DFBA8577A266294EB7CF52DF5C203510', X'DFBA8577A266294EB7CF52DF5C203510', 4, NULL, NULL, 2, NULL, 0, 'Fill each well in the cupcake with the strawberry pie filling. Reserve any remaining pie filling for another use.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36034700EBCF5E45A9F986123F47FB18', X'DFBA8577A266294EB7CF52DF5C203510', X'DFBA8577A266294EB7CF52DF5C203510', 5, NULL, NULL, 2, NULL, 0, 'Place the softened cream cheese in a large mixing bowl and beat, using an electric mixer. until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'048F1D28430F5B48B51E7311E03C06B2', X'DFBA8577A266294EB7CF52DF5C203510', X'DFBA8577A266294EB7CF52DF5C203510', 6, NULL, NULL, 2, NULL, 0, 'Sift the powdered sugar into the bowl and add the reserved strawberries Beat well until the ingredients are fully combined and the strawberries are broken into small bits.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D750571A3691D4FB3ACB74D812732F9', X'DFBA8577A266294EB7CF52DF5C203510', X'DFBA8577A266294EB7CF52DF5C203510', 7, NULL, NULL, 2, NULL, 0, 'Add a teaspoon of lemon juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7765D353E0D7F340930FC88F468CF2E3', X'DFBA8577A266294EB7CF52DF5C203510', X'DFBA8577A266294EB7CF52DF5C203510', 8, NULL, NULL, 2, NULL, 0, 'Either add the whipped topping and beat until combined or fold in the freshly whipped whip cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52C660EB80ABC04D9651CCAE5AA5C317', X'DFBA8577A266294EB7CF52DF5C203510', X'DFBA8577A266294EB7CF52DF5C203510', 9, NULL, NULL, 2, NULL, 0, 'Spoon the frosting into a large pastry bag fitted with a large star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E777D05D0DAD047B6F49B8F4C1A6AB4', X'DFBA8577A266294EB7CF52DF5C203510', X'DFBA8577A266294EB7CF52DF5C203510', 10, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of frosting on top of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A024E45E16F7B249A965F3CBE4F8C321', X'33F13AA6E397874FB73298282B444F7F', X'33F13AA6E397874FB73298282B444F7F', 0, NULL, NULL, 2, NULL, 0, 'Dip strawberries in melted and tempered pure white chocolate or in melted white confectionery coating (candy melts).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EAE955608775547B42D6F669BC6DFDF', X'33F13AA6E397874FB73298282B444F7F', X'33F13AA6E397874FB73298282B444F7F', 1, NULL, NULL, 2, NULL, 0, 'Allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F487B4D5D6E2D4FB031D7DBD5F98F90', X'33F13AA6E397874FB73298282B444F7F', X'33F13AA6E397874FB73298282B444F7F', 2, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E10F3C334E58554E90E0AD98E2A52509', X'33F13AA6E397874FB73298282B444F7F', X'33F13AA6E397874FB73298282B444F7F', 3, NULL, NULL, 2, NULL, 0, 'If desired, allow the white chocolate to puddle at the bottom of the strawberry for a few seconds, then lift up the berry and place it on a clean spot on the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A869557FF72A5546A20FF7B98D052A12', X'33F13AA6E397874FB73298282B444F7F', X'33F13AA6E397874FB73298282B444F7F', 4, NULL, NULL, 2, NULL, 0, 'Chill in the fridge (for 3-5 minutes) if using candy melts or let air dry (for about 10 minutes) if using pure white chocolate just until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A55DBEA44E62E429D943CF41AED24E0', X'922B5915AC62FA4F98D6E5DFD846CAE8', X'922B5915AC62FA4F98D6E5DFD846CAE8', 0, NULL, NULL, 2, NULL, 0, 'EYES and NOSE: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two small circles and one 1/2 inch heart for each polar bear using a small plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'697053FA43DC0147A5286628A867B274', X'922B5915AC62FA4F98D6E5DFD846CAE8', X'922B5915AC62FA4F98D6E5DFD846CAE8', 1, NULL, NULL, 2, NULL, 0, 'EARS: Cut a curved sliver off of two white chocolate wafers for each polar bear or create ears using 1/4 inch thick white modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'814B73EC22C1B74E93AE73504937FC29', X'922B5915AC62FA4F98D6E5DFD846CAE8', X'922B5915AC62FA4F98D6E5DFD846CAE8', 2, NULL, NULL, 2, NULL, 0, 'BOW: Cut two small hearts and one small circle out of pink or red modeling chocolate. Set the hearts together with the points touching then attach the circle over top using water or clear piping gel to `glue` the bow together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3682741212277B42A47BBCDDD25569E6', X'922B5915AC62FA4F98D6E5DFD846CAE8', X'922B5915AC62FA4F98D6E5DFD846CAE8', 3, NULL, NULL, 2, NULL, 0, 'Dip the white chocolate coated strawberries into white chocolate again and press a fork up against the chocolate then pull away quickly. Do this all over the berry, over and over again, until spikes of fur start to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9CC36C5EB55BA4BAFF672BE941B7679', X'922B5915AC62FA4F98D6E5DFD846CAE8', X'922B5915AC62FA4F98D6E5DFD846CAE8', 4, NULL, NULL, 2, NULL, 0, 'Attach two candy ears on the top edge of the strawberry on opposite sides of the green stem using melted white chocolate. Then add more white chocolate over the ears and create spikes of fur on them too.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECE6B99DEBB7A14695D5F7F6CE01E265', X'922B5915AC62FA4F98D6E5DFD846CAE8', X'922B5915AC62FA4F98D6E5DFD846CAE8', 5, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add a nose to the tip of the strawberry and two eyes above it. Add a bow if making female polar bears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D28E285AEF12B34588EE6FE5E33D3C2C', X'EC84DEF1B57FBF4582787F7104AA5D8F', X'EC84DEF1B57FBF4582787F7104AA5D8F', 0, NULL, NULL, 2, NULL, 0, 'NOSE and FUR PATCHES: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two ovals using a plunger cutter and cut out a small 1/2 inch heart using a heart plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC1F5875148FF04BA471DFA71D376224', X'EC84DEF1B57FBF4582787F7104AA5D8F', X'EC84DEF1B57FBF4582787F7104AA5D8F', 1, NULL, NULL, 2, NULL, 0, 'EARS: You can cut a small sliver off two dark chocolate wafers to create the ears or you can cut out black modeling chocolate circles (with a curved notch removed) to create the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9ACA7AC06594C041988C18B87CEE9339', X'EC84DEF1B57FBF4582787F7104AA5D8F', X'EC84DEF1B57FBF4582787F7104AA5D8F', 2, NULL, NULL, 2, NULL, 0, 'BOW: Roll out red modeling chocolate to about 1/8th inch thickness and cut out two small hearts and one small circle. Set the points of the hearts together then use either water or clear piping gel to attach the circle over the intersection of the points creating a bow.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'661C35BEF75315429F7C22F189B7ECBE', X'EC84DEF1B57FBF4582787F7104AA5D8F', X'EC84DEF1B57FBF4582787F7104AA5D8F', 3, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add two ears on the top edge of the strawberry, a nose to the tip of the strawberry and two fur patches above it off to the side. Add a bow in front of an ear to make panda bear girls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F4B8998A1AB7D47AF8DACD0AB77E3F6', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 425 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53DA7D8F571B284BA11DFF36BEFC159C', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 1, NULL, NULL, 2, NULL, 0, 'Butter the inside of 24 pig cavities in 2 silicone molds using melted butter, reserving 2 tablespoons of the melted butter to put into the pancake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4CC2DD3B73C6AB4D8CE1BBD34BF51A24', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 2, NULL, NULL, 2, NULL, 0, 'In large bowl, whisk together the flour, salt, baking powder, and baking soda.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92D67DBA2AC33946A08D7AE19B64D51B', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 3, NULL, NULL, 2, NULL, 0, 'Whisk together the egg and buttermilk then add the sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2710AAD4A924FA4781478F3428B6AD33', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 4, NULL, NULL, 2, NULL, 0, 'Pour the liquid mixture into the flour mixture and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'018FAFEC57A7EB4899574CC5EE3CC279', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 5, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoons melted butter and mix until blended but with lumps remaining.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C67F84D32D3255439CED6FAE4C6F9B83', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 6, NULL, NULL, 2, NULL, 0, 'Pour batter into a squeeze bottle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3F0C82D8709C5469318F0B3C7F48A4E', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 7, NULL, NULL, 2, NULL, 0, 'Squeeze batter into a pig cavity in the silicone mold filling it about 3/4''s full.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F356BDAB919ED54A8358D0D05176FC50', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 8, NULL, NULL, 2, NULL, 0, 'Press a Little Smokie into the pancake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98EC0BB18FD5294C9A04AD8FF512C935', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 9, NULL, NULL, 2, NULL, 0, 'Cover the little sausage with more pancake batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D32BA1E83AF55D47BAA4F2940B6FD89F', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 10, NULL, NULL, 2, NULL, 0, 'Repeat, filling up all 24 pig cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63F4ED1C2F30304495AA5FDE518820B8', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 11, NULL, NULL, 2, NULL, 0, 'Bake for 14-18 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49E890AF89D0C0499F87E53BF244521D', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 12, NULL, NULL, 2, NULL, 0, 'Remove and allow the pigs to cool for a few minutes before removing from the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB9A72093A0A26489670DFE6D4EB5EAD', X'D314750D3BAEB54BBAF46920A93225D2', X'D314750D3BAEB54BBAF46920A93225D2', 13, NULL, NULL, 2, NULL, 0, 'Serve with your favorite dipping sauces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B6D129AD31E694A9C60360C3C4C10E6', X'263AA31468E2284FB8C1724C0F3C387A', X'263AA31468E2284FB8C1724C0F3C387A', 0, NULL, NULL, 2, NULL, 0, 'Allow the cake ingredients to sit at room temperature for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C99B3C9A80485446B94827AEC16C398D', X'263AA31468E2284FB8C1724C0F3C387A', X'263AA31468E2284FB8C1724C0F3C387A', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2213E8F0335AF46BB4C12C5B25036F2', X'263AA31468E2284FB8C1724C0F3C387A', X'263AA31468E2284FB8C1724C0F3C387A', 2, NULL, NULL, 2, NULL, 0, 'Brush two 6-inch cake pans with butter then dust with flour and line with a round of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6630D2712F501E4BA6BAF337CACBDEFA', X'263AA31468E2284FB8C1724C0F3C387A', X'263AA31468E2284FB8C1724C0F3C387A', 3, NULL, NULL, 2, NULL, 0, 'Brush 4 cupcake cavities in a muffin tin with butter then dust with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D23717A706ABA4ABDE591DA2642FC21', X'263AA31468E2284FB8C1724C0F3C387A', X'263AA31468E2284FB8C1724C0F3C387A', 4, NULL, NULL, 2, NULL, 0, 'Combine the cake mix, water, oil, egg whites, sour cream, and pudding mix together in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F136E73F9C06814F8D9E8B92BA599A6D', X'263AA31468E2284FB8C1724C0F3C387A', X'263AA31468E2284FB8C1724C0F3C387A', 5, NULL, NULL, 2, NULL, 0, 'Use an electric mixer to beat on low for 30 seconds then increase speed to medium and beat for 1 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B21C25E00A88C46AD9212613D14EC23', X'263AA31468E2284FB8C1724C0F3C387A', X'263AA31468E2284FB8C1724C0F3C387A', 6, NULL, NULL, 2, NULL, 0, 'Fill 4 cupcake cavities about 2/3''s full with batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE78550584FBB94FBC35258E39DE362B', X'263AA31468E2284FB8C1724C0F3C387A', X'263AA31468E2284FB8C1724C0F3C387A', 7, NULL, NULL, 2, NULL, 0, 'Divide the remaining batter among the two 6-inch cake pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11930B381000594DB62A65D838CAC07D', X'263AA31468E2284FB8C1724C0F3C387A', X'263AA31468E2284FB8C1724C0F3C387A', 8, NULL, NULL, 2, NULL, 0, 'Bake the cupcakes and cakes for 18-26 minutes removing the cupcakes as soon as they are done at around 18-20 minutes. Check the cakes at around the 22 minute mark.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BB89311C6A57941884ACD75BE033628', X'263AA31468E2284FB8C1724C0F3C387A', X'263AA31468E2284FB8C1724C0F3C387A', 9, NULL, NULL, 2, NULL, 0, 'A toothpick inserted into the cakes should come out with just a few moist crumbs. Or the cakes should spring back when you press a finger on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CEA8AD97843DA4BAA3A1B3F690A4269', X'263AA31468E2284FB8C1724C0F3C387A', X'263AA31468E2284FB8C1724C0F3C387A', 10, NULL, NULL, 2, NULL, 0, 'Allow the cakes and cupcakes to cool for 10 minutes in their pans then remove them and set them on a cooling rack. Peel off the parchment paper once the cakes are cooled completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3E210A8E72A054A91D1C0A33AD44827', X'263AA31468E2284FB8C1724C0F3C387A', X'263AA31468E2284FB8C1724C0F3C387A', 11, NULL, NULL, 2, NULL, 0, 'Place cakes forming two bears on a large cake board and cut each cupcake at a curve so that they fit up against the cake to form the panda bear''s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64FEC23E98B481449B3E1BC3B2BCADED', X'A7C6503C896D5346B8411A63EDA93F81', X'A7C6503C896D5346B8411A63EDA93F81', 0, NULL, NULL, 2, NULL, 0, 'Beat the shortening and butter together until well combined using an electric mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'295384779E3B304994056D6D732CEA0E', X'A7C6503C896D5346B8411A63EDA93F81', X'A7C6503C896D5346B8411A63EDA93F81', 1, NULL, NULL, 2, NULL, 0, 'Add the powdered sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'948CF7FE1B017C47BE96E4BB90CC6FB6', X'A7C6503C896D5346B8411A63EDA93F81', X'A7C6503C896D5346B8411A63EDA93F81', 2, NULL, NULL, 2, NULL, 0, 'Add the vanilla and 3 tablespoons of the milk and beat until fully incorporated and the frosting is smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90B1FFFAE4BD0D45A41BDF4556B569F7', X'A7C6503C896D5346B8411A63EDA93F81', X'A7C6503C896D5346B8411A63EDA93F81', 3, NULL, NULL, 2, NULL, 0, 'Add more milk as needed to make the frosting soft enough to pipe through a star tip yet hold it''s shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF8B897BAF6CCE4D82F0C84947AF5CCA', X'A7C6503C896D5346B8411A63EDA93F81', X'A7C6503C896D5346B8411A63EDA93F81', 4, NULL, NULL, 2, NULL, 0, 'Spoon 2/3 cup of the frosting into another bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E34CB25C641BF549AD9E7B86C9E6F8AD', X'A7C6503C896D5346B8411A63EDA93F81', X'A7C6503C896D5346B8411A63EDA93F81', 5, NULL, NULL, 2, NULL, 0, 'Stir the cocoa powder into that small bowl of frosting then color using black food coloring and add more milk, if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'41A75F81808A004398C1FD750FCBF40A', X'29BAEED67EFAEC4FB76F272FDAFE14A8', X'29BAEED67EFAEC4FB76F272FDAFE14A8', 0, NULL, NULL, 2, NULL, 0, 'Roll out the black, white, and pink modeling chocolate to about 1/8th inch thickness on a silicone mat using a silicone rolling pin.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98058F1C5AC5F24EA5021FD57E1A38AA', X'29BAEED67EFAEC4FB76F272FDAFE14A8', X'29BAEED67EFAEC4FB76F272FDAFE14A8', 1, NULL, NULL, 2, NULL, 0, 'Cut four 2 1/4 inch by 1 3/4 inch ovals from the black modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB6229D98C682C44BC6AF9B4A34A409D', X'29BAEED67EFAEC4FB76F272FDAFE14A8', X'29BAEED67EFAEC4FB76F272FDAFE14A8', 2, NULL, NULL, 2, NULL, 0, 'Cut four 1 1/2 inch circles from the white, four small 1/4 inch hearts from the white and four 1 inch circles from the black.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D5B971C083DE245A2D8BC8BC9ED1DE5', X'29BAEED67EFAEC4FB76F272FDAFE14A8', X'29BAEED67EFAEC4FB76F272FDAFE14A8', 3, NULL, NULL, 2, NULL, 0, 'Attach one white circle to one black oval using a thin layer of water or clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9368E2CD920DB94BA70135720F9DE3F9', X'29BAEED67EFAEC4FB76F272FDAFE14A8', X'29BAEED67EFAEC4FB76F272FDAFE14A8', 4, NULL, NULL, 2, NULL, 0, 'Attach one black circle on top of the white circle then attach one small white heart along one edge of the black circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D4B26D0DBD12A4E9E4DC4C8505B880B', X'29BAEED67EFAEC4FB76F272FDAFE14A8', X'29BAEED67EFAEC4FB76F272FDAFE14A8', 5, NULL, NULL, 2, NULL, 0, 'Repeat creating 4 eyes with all the hearts pointing up or down in one direction.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'681556567DEB3A4B95AF56A8FBE4E9A8', X'29BAEED67EFAEC4FB76F272FDAFE14A8', X'29BAEED67EFAEC4FB76F272FDAFE14A8', 6, NULL, NULL, 2, NULL, 0, 'Cut out two 1 1/2 inch long heart shaped noses from the black modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B738071F31452446ADEDF748CDFA4385', X'29BAEED67EFAEC4FB76F272FDAFE14A8', X'29BAEED67EFAEC4FB76F272FDAFE14A8', 7, NULL, NULL, 2, NULL, 0, 'Cut two 1 inch hearts and one 1/2 inch circle from the pink modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39148C2C8C3EC14CB0666F55967A9577', X'29BAEED67EFAEC4FB76F272FDAFE14A8', X'29BAEED67EFAEC4FB76F272FDAFE14A8', 8, NULL, NULL, 2, NULL, 0, 'Place the two points of the heart together then attach the circle over the top of the tips using water or piping gel to form a cute bow.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6075D23D98DE224E9DF704DDFE5F5615', X'845B1AB023F0B64BB306172A07FABB61', X'845B1AB023F0B64BB306172A07FABB61', 0, NULL, NULL, 2, NULL, 0, 'Attach two eyes and a nose using frosting to each round cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'144934C489713F4EBF48D498C84902F2', X'845B1AB023F0B64BB306172A07FABB61', X'845B1AB023F0B64BB306172A07FABB61', 1, NULL, NULL, 2, NULL, 0, 'Pipe white frosting using a star tip over the entire surface of the round cakes around the eyes and nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34476139D565774686AFB59FFFB4E0C5', X'845B1AB023F0B64BB306172A07FABB61', X'845B1AB023F0B64BB306172A07FABB61', 2, NULL, NULL, 2, NULL, 0, 'Pipe black frosting stars over the cupcake ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'357F2E22EEBFD84B8C165F2A81FB6611', X'845B1AB023F0B64BB306172A07FABB61', X'845B1AB023F0B64BB306172A07FABB61', 3, NULL, NULL, 2, NULL, 0, 'Press the pink modeling chocolate bow just below one ear of one of the panda cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94AD51524F995C4A8F22BF1D75CBDE62', X'845B1AB023F0B64BB306172A07FABB61', X'845B1AB023F0B64BB306172A07FABB61', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'026BF92A30F19549A3AF82A31F0941B3', X'475FEAB396668D48BA3EB039DBC74CA5', X'475FEAB396668D48BA3EB039DBC74CA5', 0, NULL, NULL, 2, NULL, 0, 'Pour the chocolate into a small bowl or glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E4FBDC7A9BCD24BA8AC5BDF3F3B22A2', X'475FEAB396668D48BA3EB039DBC74CA5', X'475FEAB396668D48BA3EB039DBC74CA5', 1, NULL, NULL, 2, NULL, 0, 'Dip one strawberry into the chocolate then set it on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BF58BAA24F1B64D892CDDE6306A7128', X'475FEAB396668D48BA3EB039DBC74CA5', X'475FEAB396668D48BA3EB039DBC74CA5', 2, NULL, NULL, 2, NULL, 0, 'Take a fork and press it into the chocolate, then pull up and away quickly creating spikes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9D51AD480220D418A5E053D302D7D27', X'475FEAB396668D48BA3EB039DBC74CA5', X'475FEAB396668D48BA3EB039DBC74CA5', 3, NULL, NULL, 2, NULL, 0, 'Repeat this process until the entire chocolate strawberry looks furry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71C558D911C7234DA138F46D8A989587', X'475FEAB396668D48BA3EB039DBC74CA5', X'475FEAB396668D48BA3EB039DBC74CA5', 4, NULL, NULL, 2, NULL, 0, 'Immediate press two candy eyes and an M&M nose onto the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7E6E4B0F2BC86B47B5948222D36F4FE4', X'475FEAB396668D48BA3EB039DBC74CA5', X'475FEAB396668D48BA3EB039DBC74CA5', 5, NULL, NULL, 2, NULL, 0, 'Dip the cut edge of two chocolate wafers in chocolate and attach them to either side of the top of the strawberry creating the bear''s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3BF14EA70660045B7E4DB655A02E407', X'475FEAB396668D48BA3EB039DBC74CA5', X'475FEAB396668D48BA3EB039DBC74CA5', 6, NULL, NULL, 2, NULL, 0, 'Let the strawberry air dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7353EB23E411C14E9D5DB630F6D349F6', X'475FEAB396668D48BA3EB039DBC74CA5', X'475FEAB396668D48BA3EB039DBC74CA5', 7, NULL, NULL, 2, NULL, 0, 'Repeat dipping all 16 bears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6102729650C4B7449C8828F5464304D6', X'475FEAB396668D48BA3EB039DBC74CA5', X'475FEAB396668D48BA3EB039DBC74CA5', 8, NULL, NULL, 2, NULL, 0, 'If desired, brush a second layer of chocolate over the bottom of each strawberry to seal in the berry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A5AD9C087885440BFACC72A6BB63042', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'49B17CA6E22E2F438B26945720F2D91D', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 1, NULL, NULL, 2, NULL, 0, 'Spray an 8 inch square pan and 4 small bowls with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E72CAE126CC7014F9B0E4669AC3FCD72', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 2, NULL, NULL, 2, NULL, 0, 'Separate eggs and pour 4 egg yolks into each of the small bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A350787922EDD4C8F5A30C799DACE68', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 3, NULL, NULL, 2, NULL, 0, 'Whisk egg whites to break up the thicker parts then pour into the 8 inch pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FB2CA59C7B5CCD45A1E1E23359EB98B5', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 4, NULL, NULL, 2, NULL, 0, 'Set pan in a 9x13 inch baking dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E10B398E5F1981479ECB0C261DDA4F7D', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 5, NULL, NULL, 2, NULL, 0, 'Pour about 2 cups of boiling water into the 9x13 baking dish, being careful no water spills into the egg whites.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFA748D565285548835F704E1F9E9ABF', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 6, NULL, NULL, 2, NULL, 0, 'Cover the pan tightly with tin foil and bake for about 25 minutes until the egg whites are firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'91A636A7FC8C3A4EA7BC2D023F52C657', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 7, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat about 1/2 of an inch of water in a 12 inch skillet set over high heat until boiling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'548E05AC8843004A8AD8202286C498E7', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 8, NULL, NULL, 2, NULL, 0, 'Carefully place the 4 small bowls of egg yolks into the boiling water.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27D750246987944BB481C0067900E404', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 9, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low and cover the skillet with a tight fitting lid.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B8BD8ED69E0D5C4DB003248F05E115EC', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 10, NULL, NULL, 2, NULL, 0, 'Cook for about 25 minutes until the egg yolks are firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'743CAB3EED1C9747AF929D21FEA96867', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 11, NULL, NULL, 2, NULL, 0, 'Remove the bowls from the water bath and let cool for about 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6CBADA01057988488FA6E7CA33A43D1D', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 12, NULL, NULL, 2, NULL, 0, 'Remove the egg whites from the oven and it''s water bath and allow to cool for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1841F055AC51B445AD546FA3502C2D93', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 13, NULL, NULL, 2, NULL, 0, 'Use a small star shaped cookie cutter to cut out stars from the pan of egg whites.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'20499941FD05634EA4519F292A2770EF', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 14, NULL, NULL, 2, NULL, 0, 'Insert lollipop sticks into the star shaped egg whites.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D42CCE3E6319A48AC0A8E79D510957E', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 15, NULL, NULL, 2, NULL, 0, 'Place the egg yolks in a food process.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EABB39B8D514414D935864F010291934', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 16, NULL, NULL, 2, NULL, 0, 'Pulse until the eggs are finely chopped.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'33B42C925B849444AB56A9492E870584', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 17, NULL, NULL, 2, NULL, 0, 'Add Miracle Whip or Mayo, mustard, and relish and pulse until the mixture is really smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE3DB4D3402AF34996F3ABE868CCFCD5', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 18, NULL, NULL, 2, NULL, 0, 'Taste, then season with salt, and pepper or paprika if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD2510CBD71C5F4B82B15D36AE596B27', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 19, NULL, NULL, 2, NULL, 0, 'Spoon into a pastry bag fitted with a star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D885DE7CD78F814AB94E5A9E05507179', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 20, NULL, NULL, 2, NULL, 0, 'Pipe five small stars on top of the egg white star, on each of the 5 tips, and one in the center.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B4201A45B71D4479B76B60BE5CB8857', X'503597A407F5984B816EEC8CB509FBF3', X'503597A407F5984B816EEC8CB509FBF3', 21, NULL, NULL, 2, NULL, 0, 'Arrange on a serving platter and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC9A2BB49F57A8488281F73205DD49C9', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7EB12A83EF27E4AA273542A7C9D0DEA', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 1, NULL, NULL, 2, NULL, 0, 'Spread almonds out on a baking sheet, and toast for about 5 minutes, until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77A5609530DB784FAD957E03A1A445EB', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 2, NULL, NULL, 2, NULL, 0, 'Remove and pour nuts into the bowl of a food processor and let cool for at least 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'391434B8287DDB4490E7C9461AA4DDBE', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 3, NULL, NULL, 2, NULL, 0, 'Add 1/4 cup of the sugar and pulse until the nuts become finely ground.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B2445217422CA4688287D6D23A5F5C9', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 4, NULL, NULL, 2, NULL, 0, 'Whisk flour, salt, and lemon zest together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C0C1AEE8A443A439BC45BE60ED0E5EB', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 5, NULL, NULL, 2, NULL, 0, 'Cream butter and 1/2 cup sugar together just until it becomes pale yellow.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'404AD13ACD01BC44A56EE6FA57817841', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 6, NULL, NULL, 2, NULL, 0, 'Stir in vanilla then egg yolks and mix until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'039B47062224794FA5C5B85C49B22333', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 7, NULL, NULL, 2, NULL, 0, 'Mix in the ground almonds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A9B9293C39C6D47B659ED92F65F2C8D', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 8, NULL, NULL, 2, NULL, 0, 'Add dry ingredients and mix just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0809043F44515D40AE8A8A63417106BE', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 9, NULL, NULL, 2, NULL, 0, 'Remove dough from bowl, divide in half, then flatten into two discs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA5B0ACF8A014E49A4DB97E1C47EB3D9', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 10, NULL, NULL, 2, NULL, 0, 'Refrigerate for 10-15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87074B407DBAF14D87C023BD5C6009F8', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 11, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF52179365B5D040866E39B1A142B15E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 12, NULL, NULL, 2, NULL, 0, 'Dust a silicone mat or cutting board with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0325E45520D36B418C322D572F507E90', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 13, NULL, NULL, 2, NULL, 0, 'Roll 1 disc of cookie dough out to 1/8 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79C79323F0C92146BCCD6BB201122428', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 14, NULL, NULL, 2, NULL, 0, 'Cut 12 daisies using a 4 inch daisy shaped cookie cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F8AEFF4ABC9FE044A39C7ABD5A2C1601', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 15, NULL, NULL, 2, NULL, 0, 'Remove excess dough and freeze daisies for 5 minutes until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48B7C4BCB0094740A90686F71BEF61B5', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 16, NULL, NULL, 2, NULL, 0, 'Move cookies to a parchment lined baking sheet and bake for 12-16 minutes until lightly golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C0D7E7BFF38CF747AF1B965FE4DF6DB5', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 17, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'772177A92233D440B096FB66F49F9B4F', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 18, NULL, NULL, 2, NULL, 0, 'Roll the remaining dough and cut 9 or 10 daisies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C794BA46945C5A459C57BF7407590C48', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 19, NULL, NULL, 2, NULL, 0, 'Use 1 1/4 inch tear drop cookie cutter to cut out holes in each petal, leaving a small border around the edge of the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2ABEFD2A50EBB4C9A5E41C1C754A0E6', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 20, NULL, NULL, 2, NULL, 0, 'Remove excess dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'692DF3BB2E10AC44B885456E9839A22F', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 21, NULL, NULL, 2, NULL, 0, 'Re-roll dough until you get 3 more daisies with holes in the petals.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'233A759277377A4E95D3596BF708EF92', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 22, NULL, NULL, 2, NULL, 0, 'Freeze the 12 cut-out daisies for 10 minutes then move to parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54CAD6020E52724B8BDC91D2838B1997', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 23, NULL, NULL, 2, NULL, 0, 'Bake for about 12-14 minutes until lightly golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C51F1B0296086429E33084840A654C3', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 24, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E0D809B8FC92C40A937463A82C5729F', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 25, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool completely, then dust with powdered sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11AC9686C6509045BCFADA26214A1663', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 26, NULL, NULL, 2, NULL, 0, 'Spread raspberry jam or lemon curd on the whole daisy cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D8A561A635D8842A860876D3C08A60B', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', X'C1A22EF52A8FEF478CC42FB3C9BD8D2E', 27, NULL, NULL, 2, NULL, 0, 'Place one cut-­out cookie on top of the jam and gently sandwich the cookies together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CED065EEC0E6C14D880FC8FD4BC60BE8', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 0, NULL, NULL, 2, NULL, 0, 'Pinch off pieces of the white modeling chocolate and roll into a thin rope.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBAD86ABAB286B4589AE0406AC615329', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 1, NULL, NULL, 2, NULL, 0, 'Cut off 1/2 inch pieces and roll into a cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9ED2BEF1FDDE514D86F2290CBAFC73CC', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 2, NULL, NULL, 2, NULL, 0, 'Twist two of those small pieces together to form a unicorn horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD756327E79BB0438386DDC89A539701', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 3, NULL, NULL, 2, NULL, 0, 'Brush with edible gold luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9FEF64E922DE424BB215A45E15E1699B', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 4, NULL, NULL, 2, NULL, 0, 'Set aside to dry for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1D330E8A3B0EF140BA8ADE93FF72DC97', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 5, NULL, NULL, 2, NULL, 0, 'Attach one small white heart to one jumbo pink heart using white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4F1DF041383AE42AE74F762494531E7', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 6, NULL, NULL, 2, NULL, 0, 'Spoon about 1/2 teaspoon of white chocolate onto a piece of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B01C3AADDBEBE4AA1A1F206A9C4E3D5', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 7, NULL, NULL, 2, NULL, 0, 'Dip one mini pretzel twist in white chocolate then remove and allow excess to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB38045AFB722E4090BED611057E39B8', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 8, NULL, NULL, 2, NULL, 0, 'Set over top of the spoonful of white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00FF32E0A6787E4385A3736C1ECC5462', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 9, NULL, NULL, 2, NULL, 0, 'Immediately press one unicorn horn in between the two rounded parts of the pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9E07572F9C7094B9D9A09E39AE7D5D9', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 10, NULL, NULL, 2, NULL, 0, 'Attach two pink heart ears on either side of the horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A63E1D8351EDCA4FB762723619B33A2B', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 11, NULL, NULL, 2, NULL, 0, 'Sprinkle 5 rainbow heart sprinkles on the pretzel in between the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'80BE3F1764510342A97F6DCA8461EEC0', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 12, NULL, NULL, 2, NULL, 0, 'Chill in the refrigerator if using pure white chocolate and in the freezer is using Candy Melts for 3-4 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32E89AC5B816CC4387DD73B5FF1DDF68', X'02B75C595AD09E4EB22A2C9C15A9E3F1', X'02B75C595AD09E4EB22A2C9C15A9E3F1', 13, NULL, NULL, 2, NULL, 0, 'Repeat dipping and decorating a total of 24 pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCF082C7666D2A47B956BDE30C5D80B6', X'853441E3478F9C43B00392A7E6E4487C', X'853441E3478F9C43B00392A7E6E4487C', 0, NULL, NULL, 2, NULL, 0, 'Attach one white peanut butter cup mini to the milk chocolate peanut butter cup using clear piping gel creating Paddington''s snout.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55604672CD37E54E848942596F825530', X'853441E3478F9C43B00392A7E6E4487C', X'853441E3478F9C43B00392A7E6E4487C', 1, NULL, NULL, 2, NULL, 0, 'Pinch off a tiny piece of black modeling chocolate, roll it into a ball, then use clear piping gel to attach it to the top edge of the white peanut butter cup creating a nose. Insert a skewer to create two nostrils.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C07D7D1CA1174F4DA9A63415B2D5DD5D', X'853441E3478F9C43B00392A7E6E4487C', X'853441E3478F9C43B00392A7E6E4487C', 2, NULL, NULL, 2, NULL, 0, 'Draw on a smile using the food coloring marker.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11CD6AE6A41A94489A3D03B06395922A', X'853441E3478F9C43B00392A7E6E4487C', X'853441E3478F9C43B00392A7E6E4487C', 3, NULL, NULL, 2, NULL, 0, 'Pinch off two more small pieces of black modeling chocolate, roll them into balls, and attach them above the white peanut butter cup for his eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7474419D40B7FF4FA63BD9648C484B08', X'853441E3478F9C43B00392A7E6E4487C', X'853441E3478F9C43B00392A7E6E4487C', 4, NULL, NULL, 2, NULL, 0, 'Add highlights to the eyes using white food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68A7FE845F0C7D48B4E014A9D2BEC4C4', X'853441E3478F9C43B00392A7E6E4487C', X'853441E3478F9C43B00392A7E6E4487C', 5, NULL, NULL, 2, NULL, 0, 'Set aside and allow to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5EA3037A4E67D43A3B91508758A3E73', X'3DADBE5BE2CA9440AD1AB752BC3A3904', X'3DADBE5BE2CA9440AD1AB752BC3A3904', 0, NULL, NULL, 2, NULL, 0, 'Roll out the red modeling chocolate and cut out one 2 inch circle, then cut a 1 1/8 inch circle from the center of it.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC5BD74121D4E945B7051BD9876C71AE', X'3DADBE5BE2CA9440AD1AB752BC3A3904', X'3DADBE5BE2CA9440AD1AB752BC3A3904', 1, NULL, NULL, 2, NULL, 0, 'Cut a 1/2 inch wide by 3 1/2 inch long strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'06E7099954A75B4293873A00374777CC', X'3DADBE5BE2CA9440AD1AB752BC3A3904', X'3DADBE5BE2CA9440AD1AB752BC3A3904', 2, NULL, NULL, 2, NULL, 0, 'Brush the inside of the cut circle with clear piping gel and insert the strip along the inner edge, creating the tube shaped crown of the hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'276EC85570224F4C9A117FD2B0C16EF2', X'3DADBE5BE2CA9440AD1AB752BC3A3904', X'3DADBE5BE2CA9440AD1AB752BC3A3904', 3, NULL, NULL, 2, NULL, 0, 'Brush clear piping gel all around the top edge of the crown piece.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'03B04FDD4E0D4347A6600098343AFF62', X'3DADBE5BE2CA9440AD1AB752BC3A3904', X'3DADBE5BE2CA9440AD1AB752BC3A3904', 4, NULL, NULL, 2, NULL, 0, 'Flatten out the outer edge of the smaller circle then attach it on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1FD64DD6AF10A42989989DCECDC51AC', X'3DADBE5BE2CA9440AD1AB752BC3A3904', X'3DADBE5BE2CA9440AD1AB752BC3A3904', 5, NULL, NULL, 2, NULL, 0, 'Let this hat dry for just a few minutes then brush some clear piping gel inside the hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E9D75285B9E8043A76F8BD4A242AE2F', X'3DADBE5BE2CA9440AD1AB752BC3A3904', X'3DADBE5BE2CA9440AD1AB752BC3A3904', 6, NULL, NULL, 2, NULL, 0, 'Press the hat over your peanut butter cup bear, shaping it to look like Paddington''s hat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D881EB65BB96DD4AB56C15E7AD07E13C', X'AB88B4C3BC9C3443AEB22810B13FA172', X'AB88B4C3BC9C3443AEB22810B13FA172', 0, NULL, NULL, 2, NULL, 0, 'Cut thin slices from a frozen white cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A80465F3E55EA549BA9198AF0963784C', X'AB88B4C3BC9C3443AEB22810B13FA172', X'AB88B4C3BC9C3443AEB22810B13FA172', 1, NULL, NULL, 2, NULL, 0, 'Cut out twelve 2''s using a #2 fondant cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E944817B2A491642A6D3BB645C92D7A3', X'AB88B4C3BC9C3443AEB22810B13FA172', X'AB88B4C3BC9C3443AEB22810B13FA172', 2, NULL, NULL, 2, NULL, 0, 'Pipe orange gel on six of them then top each with another.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7C568CFE15469A4B92FA54DB563027D5', X'88D8A1E18B681747B41E33EB4D25F255', X'88D8A1E18B681747B41E33EB4D25F255', 0, NULL, NULL, 2, NULL, 0, 'Insert one cookie stick into the center of a chocolate cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B702392FC6F6D429FAEEEAF7DB89E19', X'88D8A1E18B681747B41E33EB4D25F255', X'88D8A1E18B681747B41E33EB4D25F255', 1, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of chocolate frosting around the stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE9749B124B1194E83A21E28EB11AC09', X'88D8A1E18B681747B41E33EB4D25F255', X'88D8A1E18B681747B41E33EB4D25F255', 2, NULL, NULL, 2, NULL, 0, 'Attach two malt ball paws holding one #2 marmalade sandwich.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E37456B4819338469A3741D88222949D', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 0, NULL, NULL, 2, NULL, 0, 'Run a line of butter around the inside edges of a 12 x 18 inch baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F5399B95369C5409A33E8D56AB4A6F8', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 1, NULL, NULL, 2, NULL, 0, 'Set a piece of parchment or wax paper down on the pan then rub more butter over it.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17B5E899111E484A99D0980631EDA720', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 2, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C55E6A37C81B254DA48000497ACF637B', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 3, NULL, NULL, 2, NULL, 0, 'Equally divide the marshmallows (a heaping cup of each) into 6 microwave safe bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E89AFD2726EF43428CE4353C9CF12C9F', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 4, NULL, NULL, 2, NULL, 0, 'Cut the butter into 1/2 tablespoon pieces and set one piece in each bowl of marshmallows.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5F4F0472B744A4B93FD56A65156AABA', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 5, NULL, NULL, 2, NULL, 0, 'Heat each bowl on high power for 45-75 seconds until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9913886E257EDD43A8FD9B22AA02E6C0', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 6, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C64FC3CB24F4B747B2DE13B23C64623F', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 7, NULL, NULL, 2, NULL, 0, 'Repeat, melting all the marshmallows.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'468D737DE68D084CA2098A605E3BCA03', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 8, NULL, NULL, 2, NULL, 0, 'Add purple coloring to one bowl and stir until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FFFB20BFE772C4BBBB1BE8B44B12175', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 9, NULL, NULL, 2, NULL, 0, 'Add one of each of the remaining colors to the remaining bowls of marshmallow.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE3CCC07E7AC734BA5641D79755DF138', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 10, NULL, NULL, 2, NULL, 0, 'Stir 1 cup of Rice Krispies Cereal into each bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3767EB1D470E734EB5203CA90718CEC6', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 11, NULL, NULL, 2, NULL, 0, 'Scoop the purple cereal treat mixture out of the bowl and stretch it out over the long edge of the prepared pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F82B85795EEE8049A64B0B089549BD30', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 12, NULL, NULL, 2, NULL, 0, 'Flatten it into a 2 inch wide strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C43E3AF11BF80643AB3DB4914CB0A604', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 13, NULL, NULL, 2, NULL, 0, 'Add a blue strip next to the purple one then add a green, then yellow, orange, and pink strip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00086613C7B3464EB43823BA6FE3F9B4', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 14, NULL, NULL, 2, NULL, 0, 'Press the seams together so the colored strips are well sealed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F177DC6449ECE84AAA339886BAB987B4', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 15, NULL, NULL, 2, NULL, 0, 'Run a knife or plastic scraper around the edge of the pan loosening the cereal treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D306A310C98624695B588C015894D78', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 16, NULL, NULL, 2, NULL, 0, 'Carefully flip the pan over allowing the rainbow rice krispie treat to fall out onto your work surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F86F8E6EA91ED446A13D7334C793ECBD', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 17, NULL, NULL, 2, NULL, 0, 'Peel the paper off the treat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C072DC4353BF08419A88AD313B025239', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 18, NULL, NULL, 2, NULL, 0, 'Begin rolling the treat along the long edge (either the purple or pink strip).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5335282281BEC0488EE66B4467EE1F8A', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 19, NULL, NULL, 2, NULL, 0, 'Roll it, pinching together any cracks, until you have an 18 inch long log.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96A420B85EB3E547BE9213728ED435C8', X'E46F15D2D1AD2C468971A4B473D80C05', X'E46F15D2D1AD2C468971A4B473D80C05', 20, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, cut the roll into 12-18 slices and they are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0C30F947A3DEF488AD2681B1EC1373B', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 0, NULL, NULL, 2, NULL, 0, 'Unwrap Reese''s Cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'160F042EA2CADE4DA15835440DDCF28B', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 1, NULL, NULL, 2, NULL, 0, 'Roll out white candy clay and cut out 2 tear drop shaped ears per unicorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C80697D761C0334894F034E73F7E8737', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 2, NULL, NULL, 2, NULL, 0, 'Pinch off a small piece of white candy clay and roll into a unicorn horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC3E759C12A30F4C923444153E46BE0B', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 3, NULL, NULL, 2, NULL, 0, 'Lightly score across the horn using the back of a knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6698A5B50E4CD2419C5F96CDE6290BBF', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 4, NULL, NULL, 2, NULL, 0, 'Brush gold luster dust over horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE2CAC81CC26FA4C96F405D06E2D41CC', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 5, NULL, NULL, 2, NULL, 0, 'Roll out pink candy clay and cut out 1 oval shaped nose for each unicorn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B1F767AEA898744A59506B3FB2CB5F6', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 6, NULL, NULL, 2, NULL, 0, 'Brush the ovals with pink luster dust.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6EE3044A0D3F6C4FA2F309B3B8138E3A', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 7, NULL, NULL, 2, NULL, 0, 'Make two indentations in the pink oval to create the unicorn''s nostrils using the flat end of a wood skewer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0AADA8E92397A43A3B8DE12BB921E2A', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 8, NULL, NULL, 2, NULL, 0, 'Attach one pink oval along one edge of a White Reese''s Cup using Candy Melts as edible glue.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B4C2DAABF9412D48B46053DA37CD3D5A', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 9, NULL, NULL, 2, NULL, 0, 'Let the candy melts dry for a few minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38AD9B61A3C4214992DADE46B30F7BC2', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 10, NULL, NULL, 2, NULL, 0, 'Use your finger to smooth out the edge of the oval so that the top edge of the oval slopes down to the edge of the Reese''s Cup.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9FF15C075E6D6043BEB8D85A0CD2F409', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 11, NULL, NULL, 2, NULL, 0, 'Brush more pink luster dust over the oval.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA33ED37B344204D973F38EA361CF439', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 12, NULL, NULL, 2, NULL, 0, 'Attach two candy clay ears and the horn opposite the pink nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E04AD163AB486D4BBF5ECA07D51E8B66', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 13, NULL, NULL, 2, NULL, 0, 'Roll pink, purple, yellow and blue candy clay into 1/4 thick logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BE0760EFA0BDE43A3BB8F004356C2CE', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 14, NULL, NULL, 2, NULL, 0, 'Arrange the four logs into one thick log and squeeze together into a log that will fit into the clay extruder.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CD1BF231659DD40993AD98896A6F28D', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 15, NULL, NULL, 2, NULL, 0, 'Fit the clay extruder with a multi hole disc.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02D5FABB77AB3247BF7EBFB1DEFAB591', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 16, NULL, NULL, 2, NULL, 0, 'Extrude the colorful candy clay through the extruder creating long strands of rainbow colored hair.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6071DFEB4B30F6479C1E7264DCEB0C23', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 17, NULL, NULL, 2, NULL, 0, 'Cut off a 1 1/2 inch long section of the hair.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3463B0DCE595CC4D85F56380932AD7D7', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 18, NULL, NULL, 2, NULL, 0, 'Pinch together one end and drape it over the Reese''s Cup in between one ear and the horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1F974A3527CC34B83C231700832E134', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 19, NULL, NULL, 2, NULL, 0, 'Press the candy clay hair onto the back edge of the Reese''s Cup. It should stick all on it''s own, but if it doesn''t, brush some candy melts over it to hold it in place.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A4066B7BC889664B9F7C12157B6A07D1', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 20, NULL, NULL, 2, NULL, 0, 'Cut off the hair on the front of the Reese''s Cup if needed so that the unicorn''s mane hangs just below the bottom edge of the horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14DD0651B331724C813DC89B2AB60BD0', X'EFA2C1155A073647B6107D0BB03C909F', X'EFA2C1155A073647B6107D0BB03C909F', 21, NULL, NULL, 2, NULL, 0, 'Repeat adding hair in between the other ear and horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79F7C05B65EDB3469CFBF9FABE103146', X'BBA9E6AFF40BD445A4A783C12B8CDB48', X'BBA9E6AFF40BD445A4A783C12B8CDB48', 0, NULL, NULL, 2, NULL, 0, 'Roll yellow candy clay into a log and insert it into a clay extruder that is fitted with a small single hole plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E7530BA91B3484CB464E5916CD61FA6', X'BBA9E6AFF40BD445A4A783C12B8CDB48', X'BBA9E6AFF40BD445A4A783C12B8CDB48', 1, NULL, NULL, 2, NULL, 0, 'Extrude a long strand of yellow onto your work surface.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE77C98F238D6A4A87EA13EA7990FD16', X'BBA9E6AFF40BD445A4A783C12B8CDB48', X'BBA9E6AFF40BD445A4A783C12B8CDB48', 2, NULL, NULL, 2, NULL, 0, 'Do the same creating a long strand of green, blue, and purple candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55FC3F18CFF26E4C85D07A4270637F56', X'BBA9E6AFF40BD445A4A783C12B8CDB48', X'BBA9E6AFF40BD445A4A783C12B8CDB48', 3, NULL, NULL, 2, NULL, 0, 'Press the four strands together then cut off 2 inch long sections and form them into rainbows cutting off the excess as needed to make a nice straight bottom edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64A9ED444CFA2E42A9A0E866C1010C5A', X'BBA9E6AFF40BD445A4A783C12B8CDB48', X'BBA9E6AFF40BD445A4A783C12B8CDB48', 4, NULL, NULL, 2, NULL, 0, 'Brush white candy melts over the rainbow and allow that to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC5006849337944D8EBD0ACAB0BE921B', X'BBA9E6AFF40BD445A4A783C12B8CDB48', X'BBA9E6AFF40BD445A4A783C12B8CDB48', 5, NULL, NULL, 2, NULL, 0, 'Turn the rainbows over so that the colorful side is facing up.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9894A1FB235DC74FA7F67BEEFE25B979', X'9FE9F586881FC54B9736553A009EFC3D', X'9FE9F586881FC54B9736553A009EFC3D', 0, NULL, NULL, 2, NULL, 0, 'Pipe a big tall swirl of frosting onto each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDCB2493F7A07C42B33EB88045A431DE', X'9FE9F586881FC54B9736553A009EFC3D', X'9FE9F586881FC54B9736553A009EFC3D', 1, NULL, NULL, 2, NULL, 0, 'Snuggle a Reese''s Cup Unicorn into the frosting near the top portion of the swirl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF7797FB57523546B4FC19CAF0D77CD4', X'9FE9F586881FC54B9736553A009EFC3D', X'9FE9F586881FC54B9736553A009EFC3D', 2, NULL, NULL, 2, NULL, 0, 'If needed pipe a bit more frosting behind the unicorn in order to get the Reese''s Cup to stay upright.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93BBBF7C711D7C48851BD0412BCF7488', X'9FE9F586881FC54B9736553A009EFC3D', X'9FE9F586881FC54B9736553A009EFC3D', 3, NULL, NULL, 2, NULL, 0, 'Press two Vanilla Whoppers below the unicorn creating two hooves.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2EC7538721DC34C8F076166543F13AE', X'9FE9F586881FC54B9736553A009EFC3D', X'9FE9F586881FC54B9736553A009EFC3D', 4, NULL, NULL, 2, NULL, 0, 'Set a candy clay rainbow in the frosting in between the hooves to make it look like the unicorn is holding the rainbow.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE684756B843DD4E85B64ED65E495E72', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 0, NULL, NULL, 2, NULL, 0, 'Pour caramel bits into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BBE83165BEABEF48AF752795BA72A49B', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 1, NULL, NULL, 2, NULL, 0, 'Heat on medium power for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02A3C8087884C04B9F8EC5F656553A66', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D256F2891A06334589DB4FA3A1F2E37F', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 3, NULL, NULL, 2, NULL, 0, 'Heat on medium power for 20 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA4721C83E99D448B80FD9EEB05E8B33', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 4, NULL, NULL, 2, NULL, 0, 'Let sit in microwave for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E6C97F3A6961848A5AEC69C71D7FE74', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 5, NULL, NULL, 2, NULL, 0, 'Remove and stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B172A3A63E46424181ED58BB194E920E', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 6, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10 second increments, stirring after each until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF1BE47CD7303F48BE29A59E77149566', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 7, NULL, NULL, 2, NULL, 0, 'Note: Don''t allow the caramel to get too hot because it will harden like hard candy. You just want to warm it until it''s spreadable.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22EB734587B472498B473E45A1FB4995', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 8, NULL, NULL, 2, NULL, 0, 'Spread caramel over three quarters of a pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BF9E779E72EA044B3938D6BA7489A6B', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 9, NULL, NULL, 2, NULL, 0, 'Immediately roll caramel coated pretzel in chopped nuts or toffee bits.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7FBC91844E420409BE8DEDBC97FE189', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 10, NULL, NULL, 2, NULL, 0, 'Repeat, with 5 more pretzels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FCE8456050219348B13527177B650394', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 11, NULL, NULL, 2, NULL, 0, 'Freeze pretzels for 5 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF523396C65272488B6CFBE79E8363A6', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 12, NULL, NULL, 2, NULL, 0, 'Spoon candy melts over caramel coated pretzel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DF5149750941046BE51D865513ADA0A', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 13, NULL, NULL, 2, NULL, 0, 'Immediately decorate using candy eyes, candies and sprinkles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1447FB32DA56B40ACCE9E15922D5C73', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 14, NULL, NULL, 2, NULL, 0, 'Freeze for 3 minutes until candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC0118B36C1AD64491F5269D6B2E9CF5', X'33563F7014CB1347BAD984069D3768A3', X'33563F7014CB1347BAD984069D3768A3', 15, NULL, NULL, 2, NULL, 0, 'Allow pretzels to sit at room temperature for a while before adding details with food coloring markers and then packaging.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52CCAD1E392B1941A8E309C4ED7F92B2', X'DC442DA7576972479539F9F0C6D7FCE4', X'DC442DA7576972479539F9F0C6D7FCE4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DD3BF5E865957A448C4360F2C37277FF', X'DC442DA7576972479539F9F0C6D7FCE4', X'DC442DA7576972479539F9F0C6D7FCE4', 1, NULL, NULL, 2, NULL, 0, 'Line 60 cavities in mini cupcake pans with paper cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A4C50288A1A2144A554D64D8B1063AB', X'DC442DA7576972479539F9F0C6D7FCE4', X'DC442DA7576972479539F9F0C6D7FCE4', 2, NULL, NULL, 2, NULL, 0, 'Make cake mix according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'39649A33D960444BB1F12693549C5CC6', X'DC442DA7576972479539F9F0C6D7FCE4', X'DC442DA7576972479539F9F0C6D7FCE4', 3, NULL, NULL, 2, NULL, 0, 'Equally divide batter into 6 bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F4317AE77B3B1448A8D44701C7E3D6B7', X'DC442DA7576972479539F9F0C6D7FCE4', X'DC442DA7576972479539F9F0C6D7FCE4', 4, NULL, NULL, 2, NULL, 0, 'Color batter using food coloring.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BB36371AA904348A9DCC1F04013B086', X'DC442DA7576972479539F9F0C6D7FCE4', X'DC442DA7576972479539F9F0C6D7FCE4', 5, NULL, NULL, 2, NULL, 0, 'Pipe or spoon batter into mini cupcake pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27634B6958E1554BBBE242A9AB48494E', X'DC442DA7576972479539F9F0C6D7FCE4', X'DC442DA7576972479539F9F0C6D7FCE4', 6, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until the cakes spring back when touched.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'59E23F3317769B40A3C848C41B722549', X'DC442DA7576972479539F9F0C6D7FCE4', X'DC442DA7576972479539F9F0C6D7FCE4', 7, NULL, NULL, 2, NULL, 0, 'Remove and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6A90772CD31A64E93708884787532AC', X'DC442DA7576972479539F9F0C6D7FCE4', X'DC442DA7576972479539F9F0C6D7FCE4', 8, NULL, NULL, 2, NULL, 0, 'Pipe or spread white frosting on top of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7708C7DF6067EB41A1A1623D5C4E3FA9', X'DC442DA7576972479539F9F0C6D7FCE4', X'DC442DA7576972479539F9F0C6D7FCE4', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle on a bit of rainbow nonpareils.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7DAB515CFABB2946AD4D14129B14E3F6', X'DC442DA7576972479539F9F0C6D7FCE4', X'DC442DA7576972479539F9F0C6D7FCE4', 10, NULL, NULL, 2, NULL, 0, 'Spoon about a tablespoon of candy into a plastic 1 ounce shot glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A07D0ABDE8388B42A4F8C97BD3AF6900', X'DC442DA7576972479539F9F0C6D7FCE4', X'DC442DA7576972479539F9F0C6D7FCE4', 11, NULL, NULL, 2, NULL, 0, 'Set a mini cupcake on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AEC29B2C5FBFCA4C8ABC8BAF44748360', X'DC442DA7576972479539F9F0C6D7FCE4', X'DC442DA7576972479539F9F0C6D7FCE4', 12, NULL, NULL, 2, NULL, 0, 'Repeat, creating a total of 60 mini cupcakes in candy filled shot glasses.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97FB312EFBF1C448B33F20735341853D', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', 0, NULL, NULL, 2, NULL, 0, 'Pour melted and tempered white chocolate or melted confectionery coating/candy melts into a tall glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB46A8071411A8499D827BB0A90CC3AB', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', 1, NULL, NULL, 2, NULL, 0, 'Hold onto the tip of a sugar cone and dip it into the glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'230B2B534AEDA5448CBFD4EFE3AF795E', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', 2, NULL, NULL, 2, NULL, 0, 'Coat all but the very tip of the sugar cone with white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'883CFB99EC4A3D4BB8359A57E6778B82', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', 3, NULL, NULL, 2, NULL, 0, 'Remove the cone from the glass and allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8DB4DAAA92D834DB9C1B9591E358C97', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', 4, NULL, NULL, 2, NULL, 0, 'While wet, sprinkle colorful sanding sugar all over the cone (inside too, if you''d like).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FDDBF61E7D0C1D4EA208A5A36925D2BB', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', 5, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CB31E08720CE048B9E07ABA44FD5FDF', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', 6, NULL, NULL, 2, NULL, 0, 'Place the dipped cones in the refrigerator for about 10 minutes until the white chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77F09B947B580B42A7622A554474C637', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', 7, NULL, NULL, 2, NULL, 0, 'Pinch off pieces of white modeling chocolate and roll them into 1 1/4`-1 1/2` long logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ABEAFEAE78B1214FA539F85A693E730E', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', 8, NULL, NULL, 2, NULL, 0, 'Insert a skewer into the log, creating a hole.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'166758FCB3E9064292A76F1E98C90A6E', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', 9, NULL, NULL, 2, NULL, 0, 'Move the skewer around making the hole a bit bigger.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'78C12AD7D3DF904F95E07795F3225DDA', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', 10, NULL, NULL, 2, NULL, 0, 'Remove the skewer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4F8FBFE97364DE49A080E83D8EE8DB18', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', 11, NULL, NULL, 2, NULL, 0, 'Press one log onto each dipped cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8571D575CE740847A87F0E86130B2F67', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', X'158EC9E7D0CF5B4ABF3778D4C8D3C013', 12, NULL, NULL, 2, NULL, 0, 'Reshape the log, if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FD1E007CFA0234CAEC9302FF65F5A2C', X'DC7372BF70F3CB46AE06785C59916063', X'DC7372BF70F3CB46AE06785C59916063', 0, NULL, NULL, 2, NULL, 0, 'Measure three tablespoons of refried beans into a fine mesh strainer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD217177E7308445BF29AD87D44A42FB', X'DC7372BF70F3CB46AE06785C59916063', X'DC7372BF70F3CB46AE06785C59916063', 1, NULL, NULL, 2, NULL, 0, 'Use a rubber spatula or spoon to press the beans through the strainer. Scrape mashed beans from the back of the strainer into a small bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'017A809B8F599646ACFE22116D559C3C', X'DC7372BF70F3CB46AE06785C59916063', X'DC7372BF70F3CB46AE06785C59916063', 2, NULL, NULL, 2, NULL, 0, 'Add 4 drops of black food coloring and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEB4B85C873F114B8DDBAC1D7D644697', X'DC7372BF70F3CB46AE06785C59916063', X'DC7372BF70F3CB46AE06785C59916063', 3, NULL, NULL, 2, NULL, 0, 'Add 1-2 more drops until you reach the desired shade of black.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65C12D4B67643B45A57D3B83A9E6EC49', X'DC7372BF70F3CB46AE06785C59916063', X'DC7372BF70F3CB46AE06785C59916063', 4, NULL, NULL, 2, NULL, 0, 'Spoon into a disposable pastry bag or zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3AD11A5308F50E478155DCF1786F4E95', X'DC7372BF70F3CB46AE06785C59916063', X'DC7372BF70F3CB46AE06785C59916063', 5, NULL, NULL, 2, NULL, 0, 'Pour remaining refried beans into a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC40D1A29253E14C9BB08E8016EAC0C7', X'DC7372BF70F3CB46AE06785C59916063', X'DC7372BF70F3CB46AE06785C59916063', 6, NULL, NULL, 2, NULL, 0, 'Add cheese cubes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EBFACA29E5C0FC4085913D3E22EFAEE5', X'DC7372BF70F3CB46AE06785C59916063', X'DC7372BF70F3CB46AE06785C59916063', 7, NULL, NULL, 2, NULL, 0, 'Heat in the microwave for 3-4 minutes, stirring every 45-60 seconds, until the cheese melts. Spread evenly in a 9` pie plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E228B7DD547D4947AEFFE05078533CC7', X'DC7372BF70F3CB46AE06785C59916063', X'DC7372BF70F3CB46AE06785C59916063', 8, NULL, NULL, 2, NULL, 0, 'Cut the tip off the pastry bag or zip-top bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4EE91D34C2224A4D90D27C719522DA8B', X'DC7372BF70F3CB46AE06785C59916063', X'DC7372BF70F3CB46AE06785C59916063', 9, NULL, NULL, 2, NULL, 0, 'Pipe the black colored beans onto the dip creating the seams of a basketball.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1723505291863A4B82F76355F7AAFC53', X'5378C57952482B489CFC2C03576F682B', X'5378C57952482B489CFC2C03576F682B', 0, NULL, NULL, 2, NULL, 0, 'Set flour tortilla on a cutting board. Use a 2 1/2` round cookie cutter to cut circles from the tortilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6732762F681124AB0CBBDEBEFE9A91A', X'5378C57952482B489CFC2C03576F682B', X'5378C57952482B489CFC2C03576F682B', 1, NULL, NULL, 2, NULL, 0, 'Cut as many tortilla rounds as you would like. (I made 72 chips for the picture.)');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18790D11F0EA0C409E23C79A8845949A', X'5378C57952482B489CFC2C03576F682B', X'5378C57952482B489CFC2C03576F682B', 2, NULL, NULL, 2, NULL, 0, 'Set tortilla rounds on a sheet of aluminum foil, parchment paper, or wax paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'98D0154A5A8A024AA17656A5A9B9321F', X'5378C57952482B489CFC2C03576F682B', X'5378C57952482B489CFC2C03576F682B', 3, NULL, NULL, 2, NULL, 0, 'Spray each round with Wilton''s Orange Color Mist. Use the black food marker to draw the basketball''s seam lines onto each tortilla round.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3284E4C0C685C489947C0E2580E7C29', X'5378C57952482B489CFC2C03576F682B', X'5378C57952482B489CFC2C03576F682B', 4, NULL, NULL, 2, NULL, 0, 'Heat oil in fryer or stockpot to 350 degrees. Fry about 10 tortilla rounds at a time, flipping them over midway through, until golden brown, about 30 seconds per side.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'127A5140B78E4E4A8BC1148175CF14F4', X'5378C57952482B489CFC2C03576F682B', X'5378C57952482B489CFC2C03576F682B', 5, NULL, NULL, 2, NULL, 0, 'Set hot chips on paper towels and immediately sprinkle with salt.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3B9F72EA3AE9D45AA35CF83D5E625B9', X'5378C57952482B489CFC2C03576F682B', X'5378C57952482B489CFC2C03576F682B', 6, NULL, NULL, 2, NULL, 0, 'Allow the oil to come back up to temperature before continuing.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C86CFEA28633644A9A1DF61F657193ED', X'5378C57952482B489CFC2C03576F682B', X'5378C57952482B489CFC2C03576F682B', 7, NULL, NULL, 2, NULL, 0, 'You can also choose to bake these chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'139EE21D08E0C047AFD41081751BA134', X'5378C57952482B489CFC2C03576F682B', X'5378C57952482B489CFC2C03576F682B', 8, NULL, NULL, 2, NULL, 0, 'Arrange them in a single layer on a baking sheet, spray them with cooking spray or brush them with oil, and bake at 350 degrees until golden brown, about 10-15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'449068D4E42A214EB3FDE450FFFED041', X'5378C57952482B489CFC2C03576F682B', X'5378C57952482B489CFC2C03576F682B', 9, NULL, NULL, 2, NULL, 0, 'Serve basketball tortilla chips with basketball bean dip or your favorite dip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE97EEC63DA2D2479E6EB20FADFEF566', X'2E978B40623B3945A6BF33A9DC2AEE7A', X'2E978B40623B3945A6BF33A9DC2AEE7A', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'282814396F9A5244A84D492B677B4CAB', X'2E978B40623B3945A6BF33A9DC2AEE7A', X'2E978B40623B3945A6BF33A9DC2AEE7A', 1, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D78E7D032A9047438D465FAC097530ED', X'2E978B40623B3945A6BF33A9DC2AEE7A', X'2E978B40623B3945A6BF33A9DC2AEE7A', 2, NULL, NULL, 2, NULL, 0, 'Stir in the egg, vanilla, almond extract, and enough orange food coloring to make the mixture really bright.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1399E4DACF430B418CCA415E561132CA', X'2E978B40623B3945A6BF33A9DC2AEE7A', X'2E978B40623B3945A6BF33A9DC2AEE7A', 3, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients and stir just until combined. If the dough is sticky, add a small amount of flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5DC2C464C265C9419B9626F417A9ECE0', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', 0, NULL, NULL, 2, NULL, 0, 'Roll some dough out to 3/8 inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'219A225D416E124AB9EAFC7BC1D5B316', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', 1, NULL, NULL, 2, NULL, 0, 'Cut 6 rounds using the 3 1/4 inch cutter. Remove excess dough, set on a parchment paper-lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D4573C2526E93439F84D13FC1BC52E5', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', 2, NULL, NULL, 2, NULL, 0, 'Put the large cutter around one cookie, then cut a smaller hole using the 2 3/4 inch cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87CF2C70BA16E249A436CCD1ECB7E000', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', 3, NULL, NULL, 2, NULL, 0, 'Remove the center dough round, then remove the small cookie cutter, then the large cookie cutter. This will help keep your cookies in a nice round shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6ECFA62090470844A653815141DAC59A', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', 4, NULL, NULL, 2, NULL, 0, 'Place the baking sheet in the refrigerator for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2A897006FDD32E41BEA2918B395F0214', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', 5, NULL, NULL, 2, NULL, 0, 'Roll the remaining dough out to 3/16th inch thickness, and cut 12 circles using the 3 1/4 inch cutter, re-rolling as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0D317DCBBE74145B6C31CB90AD5A933', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', 6, NULL, NULL, 2, NULL, 0, 'Set cookies on parchment paper-lined cookie sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4680B841C116C140908080D805C4430F', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', X'F3CEB6A43F9D2F41BE2B3F6D696F665E', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F2EEC08C593BB345A1E2CB4D6A646C4B', X'D894611E86464B4EB31716E1E7183F1A', X'D894611E86464B4EB31716E1E7183F1A', 0, NULL, NULL, 2, NULL, 0, 'Bake cookies for 10-12 minutes until the edges look set and the center of the cookie no longer looks wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52C77FBEF0287B47975647F6D901E13C', X'D894611E86464B4EB31716E1E7183F1A', X'D894611E86464B4EB31716E1E7183F1A', 1, NULL, NULL, 2, NULL, 0, 'Allow cookies to cool completely on the baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B10590E39C69A942830051039471AB9E', X'D894611E86464B4EB31716E1E7183F1A', X'D894611E86464B4EB31716E1E7183F1A', 2, NULL, NULL, 2, NULL, 0, 'Decorate and Assemble Cookies');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1AF07087BD6A446B53930E6982C9762', X'D894611E86464B4EB31716E1E7183F1A', X'D894611E86464B4EB31716E1E7183F1A', 3, NULL, NULL, 2, NULL, 0, 'Heat orange candy melts on high in the microwave for 20-second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D53E7EB6C2390F4BA2E760EBBCF8288C', X'D894611E86464B4EB31716E1E7183F1A', X'D894611E86464B4EB31716E1E7183F1A', 4, NULL, NULL, 2, NULL, 0, 'Spread a thin layer over 6 of the whole round cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF0D05C202C22E458F06A76EC57A184E', X'D894611E86464B4EB31716E1E7183F1A', X'D894611E86464B4EB31716E1E7183F1A', 5, NULL, NULL, 2, NULL, 0, 'Place those 6 cookies in the freezer for about 4 minutes, just until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'237614F280CCC641B052D93BFE567ABE', X'D894611E86464B4EB31716E1E7183F1A', X'D894611E86464B4EB31716E1E7183F1A', 6, NULL, NULL, 2, NULL, 0, 'Pour the remaining orange candy melts into a small zip-top bag and snip off the tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC7AE31F516B8D40BD7CF6C755AA8AE3', X'D894611E86464B4EB31716E1E7183F1A', X'D894611E86464B4EB31716E1E7183F1A', 7, NULL, NULL, 2, NULL, 0, 'Pipe candy melts around the bottom edge of a cookie with a hole in it.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE5C015896DB3F42A164CB2A4E141D6E', X'D894611E86464B4EB31716E1E7183F1A', X'D894611E86464B4EB31716E1E7183F1A', 8, NULL, NULL, 2, NULL, 0, 'Set that cookie on top of a solid round cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3E10CBFB1591AC4BA0E9FF0CAE95257D', X'D894611E86464B4EB31716E1E7183F1A', X'D894611E86464B4EB31716E1E7183F1A', 9, NULL, NULL, 2, NULL, 0, 'Fill the holes with Skittles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'840364CF61B3334AA8F5564128AF4994', X'D894611E86464B4EB31716E1E7183F1A', X'D894611E86464B4EB31716E1E7183F1A', 10, NULL, NULL, 2, NULL, 0, 'Pipe a thin band of orange coating around the top edge of the cookies with holes in them then set a candy-coated cookie on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'107EE9A5025C214C870C9DE80CF9CDED', X'D894611E86464B4EB31716E1E7183F1A', X'D894611E86464B4EB31716E1E7183F1A', 11, NULL, NULL, 2, NULL, 0, 'Press down gently to seal the two cookies together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C216FD8F3128F14DAB3B48D84F098E9C', X'D894611E86464B4EB31716E1E7183F1A', X'D894611E86464B4EB31716E1E7183F1A', 12, NULL, NULL, 2, NULL, 0, 'Use a black food coloring marker to draw on the seam lines.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F65BE4B5227584C802E15506A158865', X'56A1E10BFCFCA1459A42E07EB31854AC', X'56A1E10BFCFCA1459A42E07EB31854AC', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425°F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'609E7337C3744A44951E4CC86A619A3A', X'56A1E10BFCFCA1459A42E07EB31854AC', X'56A1E10BFCFCA1459A42E07EB31854AC', 1, NULL, NULL, 2, NULL, 0, 'Combine 1 1/2 cups flour, undissolved yeast, sugar and salt in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5EBFDE75136E244EA33AC964788063D8', X'56A1E10BFCFCA1459A42E07EB31854AC', X'56A1E10BFCFCA1459A42E07EB31854AC', 2, NULL, NULL, 2, NULL, 0, 'Add the very warm water (between 120° to 130°F) and corn oil then mix until well blended for about 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'905583CC6FE85443822EB04A3A6BB041', X'56A1E10BFCFCA1459A42E07EB31854AC', X'56A1E10BFCFCA1459A42E07EB31854AC', 3, NULL, NULL, 2, NULL, 0, 'Knead the dough on a floured surface, adding additional flour if necessary, until smooth and elastic, about 4 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7328A14348FFAC4E9530D6B1C7B92AB8', X'56A1E10BFCFCA1459A42E07EB31854AC', X'56A1E10BFCFCA1459A42E07EB31854AC', 4, NULL, NULL, 2, NULL, 0, 'Cover the dough with plastic wrap then let the dough rest for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCBDBF982B03124AACDAB318DDEB9ED6', X'56A1E10BFCFCA1459A42E07EB31854AC', X'56A1E10BFCFCA1459A42E07EB31854AC', 5, NULL, NULL, 2, NULL, 0, 'Grease a 12-inch round pizza pan then pat the dough into an even layer in the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E49AAFDC9D135D4A8AA3B7D9C62811EB', X'56A1E10BFCFCA1459A42E07EB31854AC', X'56A1E10BFCFCA1459A42E07EB31854AC', 6, NULL, NULL, 2, NULL, 0, 'Pinch the edges to create a thicker crust around the perimeter of the crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C4D2ED1A629E554C994428717DADB26D', X'56A1E10BFCFCA1459A42E07EB31854AC', X'56A1E10BFCFCA1459A42E07EB31854AC', 7, NULL, NULL, 2, NULL, 0, 'Brush the edges of the pizza crust lightly with corn oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B90850E0E817A4E9B5E1BFA1E44723E', X'56A1E10BFCFCA1459A42E07EB31854AC', X'56A1E10BFCFCA1459A42E07EB31854AC', 8, NULL, NULL, 2, NULL, 0, 'Spread the pizza sauce over the center of the dough, leaving 1/2-inch to 1-inch of uncovered dough around the perimeter of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18AC0404C9326E4190AD631B0F8C0A0F', X'56A1E10BFCFCA1459A42E07EB31854AC', X'56A1E10BFCFCA1459A42E07EB31854AC', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle about 3/4 of the mozzarella cheese over the sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B356516DAA44274EB0FBB6DAE9E7762F', X'56A1E10BFCFCA1459A42E07EB31854AC', X'56A1E10BFCFCA1459A42E07EB31854AC', 10, NULL, NULL, 2, NULL, 0, 'Add the roasted vegetables on top then add the marinated artichokes, the remaining mozzarella cheese, and the feta cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F979D83437969145BB54DE010095A4E9', X'56A1E10BFCFCA1459A42E07EB31854AC', X'56A1E10BFCFCA1459A42E07EB31854AC', 11, NULL, NULL, 2, NULL, 0, 'Bake on lowest oven rack for 12 to 15 minutes, until cheese is bubbly and crust is browned.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCE9CB7C7158A941BEDE628776F44B0D', X'034B946D073F6749B18DFFBB1AB58F1A', X'034B946D073F6749B18DFFBB1AB58F1A', 0, NULL, NULL, 2, NULL, 0, 'Combine Biscoff Spread and Butterscotch Chips together in a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2BFC974F548D684E998016D6E15A5944', X'034B946D073F6749B18DFFBB1AB58F1A', X'034B946D073F6749B18DFFBB1AB58F1A', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power, for 15-30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F332C16783715D4E8901D3A53BA2D623', X'034B946D073F6749B18DFFBB1AB58F1A', X'034B946D073F6749B18DFFBB1AB58F1A', 2, NULL, NULL, 2, NULL, 0, 'Stir in orange candy coloring to get a nice basketball color.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A167E17E04BFCC449EBB00CCFFAB8635', X'034B946D073F6749B18DFFBB1AB58F1A', X'034B946D073F6749B18DFFBB1AB58F1A', 3, NULL, NULL, 2, NULL, 0, 'Pour into basketball molds equally dividing it among 12 basketball cavities.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'117D70FDBE41EC4C915DE27EF7E52C39', X'034B946D073F6749B18DFFBB1AB58F1A', X'034B946D073F6749B18DFFBB1AB58F1A', 4, NULL, NULL, 2, NULL, 0, 'Freeze for 30 minutes until solid.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'064DFC45919A2448A96C0BAEA0ABC441', X'034B946D073F6749B18DFFBB1AB58F1A', X'034B946D073F6749B18DFFBB1AB58F1A', 5, NULL, NULL, 2, NULL, 0, 'Pop candies out of mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7041F11E262EE44915052310CC02CCA', X'034B946D073F6749B18DFFBB1AB58F1A', X'034B946D073F6749B18DFFBB1AB58F1A', 6, NULL, NULL, 2, NULL, 0, 'Allow basketballs to thaw and warm to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D5E4ED5FCC0C4C45B2BFB9CF36736C2C', X'034B946D073F6749B18DFFBB1AB58F1A', X'034B946D073F6749B18DFFBB1AB58F1A', 7, NULL, NULL, 2, NULL, 0, 'Draw on lines using a black food coloring marker, if desired.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'26E9530E5ACA1B43AF9E48864C801A5F', X'3242DBE702F0D84FABE356094F269236', X'3242DBE702F0D84FABE356094F269236', 0, NULL, NULL, 2, NULL, 0, 'Draw basketball seam lines on the flat side of each orange candy melt wafer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8A74E40A6F02DA40B8BBCAD3B88AE4BD', X'3242DBE702F0D84FABE356094F269236', X'3242DBE702F0D84FABE356094F269236', 1, NULL, NULL, 2, NULL, 0, 'Stir the peanuts, pretzels, and Pringles into the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0E52E5BCB21804F980A15B2BDC75693', X'3242DBE702F0D84FABE356094F269236', X'3242DBE702F0D84FABE356094F269236', 2, NULL, NULL, 2, NULL, 0, 'Spread out into an even layer on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'22C995AB98E61040A15AF5066812656D', X'3242DBE702F0D84FABE356094F269236', X'3242DBE702F0D84FABE356094F269236', 3, NULL, NULL, 2, NULL, 0, 'Starting around the edge and moving into the center, press the orange candy melt basketballs into the chocolate, leaving enough space to cut around them.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93C980317994EE449631A4F3CB07B81B', X'3242DBE702F0D84FABE356094F269236', X'3242DBE702F0D84FABE356094F269236', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate for 3-5 minutes just until the chocolate dries but is not set hard.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1782E2A43812694C90B2656584F6DFE1', X'3242DBE702F0D84FABE356094F269236', X'3242DBE702F0D84FABE356094F269236', 5, NULL, NULL, 2, NULL, 0, 'Remove and cut the chocolate into pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F18B46C8D4236B408C7FEFBB78110B57', X'3242DBE702F0D84FABE356094F269236', X'3242DBE702F0D84FABE356094F269236', 6, NULL, NULL, 2, NULL, 0, 'Return to the refrigerator for another 5-10 minutes, until the chocolate hardens completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1FA50E347A5E1A46A91F7C35EC4D1502', X'3242DBE702F0D84FABE356094F269236', X'3242DBE702F0D84FABE356094F269236', 7, NULL, NULL, 2, NULL, 0, 'Break apart and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C909502DE52A64A9C083C3D6E38677C', X'31975EA71A11604397EB39B971CA5C6E', X'31975EA71A11604397EB39B971CA5C6E', 0, NULL, NULL, 2, NULL, 0, 'Stir the French Onion Dip and buttermilk together in a shallow baking dish.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0339BF1F3B20C74E9DEF9D2E67D53B2C', X'31975EA71A11604397EB39B971CA5C6E', X'31975EA71A11604397EB39B971CA5C6E', 1, NULL, NULL, 2, NULL, 0, 'Place the chicken tenders in the dish, making sure they are covered with the dip mixture. Cover and refrigerate for 30-60 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C90854C6BDB0F74D858A9BED1F7615F1', X'31975EA71A11604397EB39B971CA5C6E', X'31975EA71A11604397EB39B971CA5C6E', 2, NULL, NULL, 2, NULL, 0, 'Crush the potato chips into fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'987B1C0E18FE6C489197CD769162D4A5', X'31975EA71A11604397EB39B971CA5C6E', X'31975EA71A11604397EB39B971CA5C6E', 3, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01FA50AE90DA4E439959273590CF64FB', X'31975EA71A11604397EB39B971CA5C6E', X'31975EA71A11604397EB39B971CA5C6E', 4, NULL, NULL, 2, NULL, 0, 'Spray a cooling rack with non-stick spray. Set it over a tin foil lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1E5A17E04E0E6418C38B72CD1A4689E', X'31975EA71A11604397EB39B971CA5C6E', X'31975EA71A11604397EB39B971CA5C6E', 5, NULL, NULL, 2, NULL, 0, 'Remove chicken tenders from the marinade. Allow excess liquid to drain off. Season with salt and pepper, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9B433465EF27144F9837072CCA1696B6', X'31975EA71A11604397EB39B971CA5C6E', X'31975EA71A11604397EB39B971CA5C6E', 6, NULL, NULL, 2, NULL, 0, 'Skewer the chicken tenders, then coat them in the crushed potato chips. Set on the wire cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'00D948EE2566BB4EAC72083CEEC2EA29', X'31975EA71A11604397EB39B971CA5C6E', X'31975EA71A11604397EB39B971CA5C6E', 7, NULL, NULL, 2, NULL, 0, 'Spray the chicken tenders with baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FF5BC12A58E054E9D8788C3D6D40169', X'31975EA71A11604397EB39B971CA5C6E', X'31975EA71A11604397EB39B971CA5C6E', 8, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9429BE5D17BFA942966F71530F47A0BE', X'31975EA71A11604397EB39B971CA5C6E', X'31975EA71A11604397EB39B971CA5C6E', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately with the remaining French onion dip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2AA906089A01F34692F5B4AA2FE9A39F', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 0, NULL, NULL, 2, NULL, 0, 'In a mixing bowl cream together butter and sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47E1546F4EE92540A426178AB84BB14E', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 1, NULL, NULL, 2, NULL, 0, 'Add the egg and mix until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47D14DDD8442B541BEE96E4FA83695D7', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 2, NULL, NULL, 2, NULL, 0, 'Add the vanilla and almond extract and mix until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'042D51D8F80B134A87151D2E1082C49B', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 3, NULL, NULL, 2, NULL, 0, 'Mix flour and salt together and add it to the mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53DA6C07A2B7104EAEE404E9CE942A04', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 4, NULL, NULL, 2, NULL, 0, 'Mix just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A2BA0D243F92DE428045B48C9CD6C72A', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 5, NULL, NULL, 2, NULL, 0, 'Place a piece of parchment paper on top of your cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9D16D9036927A4AB41F48F2A7AAA79D', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 6, NULL, NULL, 2, NULL, 0, 'Roll your dough out to 3/8` thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AB847673FB45564F8F7EB7CA14DC8E54', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 7, NULL, NULL, 2, NULL, 0, 'Lay the butterfly template over the dough and cut out around it.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1AFC3E026E19F34FAFE0BE92F4B81246', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 8, NULL, NULL, 2, NULL, 0, 'Remove excess dough and save for another use.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E88907F99819804595891F82E8208FBA', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 9, NULL, NULL, 2, NULL, 0, 'Refrigerate butterfly cookie for at least 30 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BCABF1F6E91D1C4ABEA46A9A4BFCA8BD', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 10, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat your oven to 375 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'18D07AAAEEC03142AD95646755D5D008', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 11, NULL, NULL, 2, NULL, 0, 'Bake for 16-22 minutes until golden brown around the edges.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EA9C4181232CC42A1CA7E3223C732AD', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 12, NULL, NULL, 2, NULL, 0, 'Cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A407B092DD92945AF7781C707A53BF4', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 13, NULL, NULL, 2, NULL, 0, 'Spread strawberry cream cheese over the cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'596FB62B67632B4A912192B1CB020664', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 14, NULL, NULL, 2, NULL, 0, 'Top the cookie with diced fruit using green grapes');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A77766FB786136479DDD7949821CAC98', X'E4BFCB9457BDC4409ABC8364039C9D05', X'E4BFCB9457BDC4409ABC8364039C9D05', 15, NULL, NULL, 2, NULL, 0, 'for the body, bananas for its eyes, pineapple, strawberries, peaches, and kiwi slices for the wings.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'14CD1DFAD09E2649AFEA899C219DA046', X'E5B2F1935173A3459BCDE6A348499B76', X'E5B2F1935173A3459BCDE6A348499B76', 0, NULL, NULL, 2, NULL, 0, 'Bake mini corn dogs on baking sheet according to package instructions.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EC6AF18B74C2A40B610EDCAAAB5AFFE', X'1E7285D39550D8459F0A00BB0D03A521', X'1E7285D39550D8459F0A00BB0D03A521', 0, NULL, NULL, 2, NULL, 0, 'Line up 9 corn dogs widthwise into a squiggly line for a centipede.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E31EFC4AAD4EA4F9038C3197301186B', X'1E7285D39550D8459F0A00BB0D03A521', X'1E7285D39550D8459F0A00BB0D03A521', 1, NULL, NULL, 2, NULL, 0, 'Press two long chow mein noodles (or potato sticks) into the first corn dog to create antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A67D5359A2768240A4195ADACFDB56BD', X'1E7285D39550D8459F0A00BB0D03A521', X'1E7285D39550D8459F0A00BB0D03A521', 2, NULL, NULL, 2, NULL, 0, 'Press two long, but shorter chow mein noodles (or potato sticks) into the last corn dog to create the back legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72803D66969AA448898906A91CA31960', X'1E7285D39550D8459F0A00BB0D03A521', X'1E7285D39550D8459F0A00BB0D03A521', 3, NULL, NULL, 2, NULL, 0, 'Then on all remaining corn dogs, press one chow mein noodle (or potato stick) on either side, creating legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A769AFA981434C418D90A3AE4E6D853C', X'1E7285D39550D8459F0A00BB0D03A521', X'1E7285D39550D8459F0A00BB0D03A521', 4, NULL, NULL, 2, NULL, 0, 'If needed, place in a 200-degree oven to keep warm until you are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90025CB07F5C93428F50020B3F088DD0', X'1E7285D39550D8459F0A00BB0D03A521', X'1E7285D39550D8459F0A00BB0D03A521', 5, NULL, NULL, 2, NULL, 0, 'Before serving, add two ketchup eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'936BFC00897FEE42A5AC415F3067E77F', X'56B24BDF33F18041922FD9C019B937C6', X'56B24BDF33F18041922FD9C019B937C6', 0, NULL, NULL, 2, NULL, 0, 'Line up 4 or 5 corn dogs lengthwise so that each section is longer rather than wider.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F85359A78EDEC24A9079068B695909AC', X'56B24BDF33F18041922FD9C019B937C6', X'56B24BDF33F18041922FD9C019B937C6', 1, NULL, NULL, 2, NULL, 0, 'Press two long chow mein noodles (or potato sticks) into the first corn dog to create antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D1C104DF67FE44596687185C092DF6C', X'56B24BDF33F18041922FD9C019B937C6', X'56B24BDF33F18041922FD9C019B937C6', 2, NULL, NULL, 2, NULL, 0, 'Attach three chow mein noodle legs for each body segment and have them curved toward the ground.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32BEF55E18843C4385391FB82995CF52', X'56B24BDF33F18041922FD9C019B937C6', X'56B24BDF33F18041922FD9C019B937C6', 3, NULL, NULL, 2, NULL, 0, 'If needed, place in a 200-degree oven to keep warm until you are ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05E4303D851EA04A8BC4A973C785A4AD', X'56B24BDF33F18041922FD9C019B937C6', X'56B24BDF33F18041922FD9C019B937C6', 4, NULL, NULL, 2, NULL, 0, 'Just before serving add ketchup eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0A7691280A29442A1886C42CF572049', X'596BBF90E5217448BBD9F423948F4D9E', X'596BBF90E5217448BBD9F423948F4D9E', 0, NULL, NULL, 2, NULL, 0, 'Bake your chicken tenders according to the instructions on the package.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6B2332A28B32C243A0D512315C7F5FC9', X'596BBF90E5217448BBD9F423948F4D9E', X'596BBF90E5217448BBD9F423948F4D9E', 1, NULL, NULL, 2, NULL, 0, 'You can also make homemade chicken tenders if you prefer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D7B599F860702441A45638F11B311F6F', X'7FBFC9701FEAE144A085E98FAD38599E', X'7FBFC9701FEAE144A085E98FAD38599E', 0, NULL, NULL, 2, NULL, 0, 'Arrange 4 Doritos Nacho Cheese Tortilla Chips like butterfly wings on a plate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5673FE66A4F964ABF8ECFF898AFA0DB', X'7FBFC9701FEAE144A085E98FAD38599E', X'7FBFC9701FEAE144A085E98FAD38599E', 1, NULL, NULL, 2, NULL, 0, 'Add a dab of mustard or dipping sauce in the center, where the points of the chips meet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6E817D69584B944F886C6DE9C7990F7C', X'7FBFC9701FEAE144A085E98FAD38599E', X'7FBFC9701FEAE144A085E98FAD38599E', 2, NULL, NULL, 2, NULL, 0, 'This will help to secure the chicken tender in place.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7BE80B15D3D8D54DBE48DA3730D81A7D', X'7FBFC9701FEAE144A085E98FAD38599E', X'7FBFC9701FEAE144A085E98FAD38599E', 3, NULL, NULL, 2, NULL, 0, 'Poke two chow mein noodles into one end of the cooked chicken tender forming the antennas.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEB68FD26A5F024FAE789F49686FBAE3', X'7FBFC9701FEAE144A085E98FAD38599E', X'7FBFC9701FEAE144A085E98FAD38599E', 4, NULL, NULL, 2, NULL, 0, 'Use the black food coloring marker to draw eyes and a smile on the chicken tender.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEC8F2415E472E4FAD7C4DCB15BA6A86', X'7FBFC9701FEAE144A085E98FAD38599E', X'7FBFC9701FEAE144A085E98FAD38599E', 5, NULL, NULL, 2, NULL, 0, 'Set the chicken tender over the center of the wings with the antennas pointing up.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8C5110D9FC9A31429E574E8E8A50273D', X'7FBFC9701FEAE144A085E98FAD38599E', X'7FBFC9701FEAE144A085E98FAD38599E', 6, NULL, NULL, 2, NULL, 0, 'Serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8DA296521EB2044B155C09B73DE3DE1', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat oven to 325 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1E7C5E045F7CF24F90AC6282E6CA6AB0', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 1, NULL, NULL, 2, NULL, 0, 'Melt butter in a mixing bowl. Allow it to cool slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEFBF8E20727F34AA8BD8233A4013A50', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 2, NULL, NULL, 2, NULL, 0, 'Add brown sugar and granulated sugar and beat on medium speed for about a minute until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DA586DB4D35E1247AFD0B4DE8CB82744', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 3, NULL, NULL, 2, NULL, 0, 'Add egg, egg yolk, and vanilla and beat until fully incorporated.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5566402EB92DA34783D7DB7FB3204786', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 4, NULL, NULL, 2, NULL, 0, 'Mix flour, salt, and baking soda together then incorporate it into the wet ingredients.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'504018B1A4E5204C850FD4C09103F8F6', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 5, NULL, NULL, 2, NULL, 0, 'Fold about 9 ounces (1 1/2 cups) of chips into the batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1FC187D3F270D469156F0077DDAE577', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 6, NULL, NULL, 2, NULL, 0, 'Scoop out 18 cookies, about 1/4 cup of dough for each.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E38A69F1844E324790C8D05C5F593C92', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 7, NULL, NULL, 2, NULL, 0, 'Place some of the reserved chocolate chips on top of each cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA5A025EBD50604AAC25BC0454A1C2F6', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 8, NULL, NULL, 2, NULL, 0, 'Remember to keep about 2 ounces (1/3 cup) of the chips for the spiders.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D8F9769B8F492489C03E3BEC1F15369', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 9, NULL, NULL, 2, NULL, 0, 'Bake cookies one tray at a time, for 14-18 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE05E163D345C44681CB7FA5630C2C14', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 10, NULL, NULL, 2, NULL, 0, 'Allow cookies to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B561ACBDBA646445B11635664D06B263', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 11, NULL, NULL, 2, NULL, 0, 'Place the remaining 2 ounces of chocolate chips in a small microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8AA227770F8F2458C084B0775FF2F4F', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 12, NULL, NULL, 2, NULL, 0, 'Heat for 20 seconds, stir, then for 15 seconds, stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9AE467E948ADE046A38E3D8D0875C79F', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 13, NULL, NULL, 2, NULL, 0, 'Allow the chocolate to sit for 1 minute, then stir again.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8971EED024FD004F876C1CCB879D7EFC', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 14, NULL, NULL, 2, NULL, 0, 'If you need to, heat it for 10 more seconds, then repeat until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6BF0C6FCED42A43A789E11A95183157', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 15, NULL, NULL, 2, NULL, 0, 'Pour into a squeeze-it mold painter bottle fitted with a #2 round pastry tip, or a disposable pastry bag or zip top bag with the tip snipped off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2BE477E7D5C7E47998275D5909D7DE4', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 16, NULL, NULL, 2, NULL, 0, 'Pipe on spider heads and legs around some of the larger chocolate chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C9BE7902E7719B408A866A1786952460', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 17, NULL, NULL, 2, NULL, 0, 'Allow to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D3F2A0C742FA9B499D5B36C5B3946739', X'41265F3D9644A9458FA663B24E71F454', X'41265F3D9644A9458FA663B24E71F454', 18, NULL, NULL, 2, NULL, 0, 'Serve with a glass of milk.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0D39245AC893924BA22AF2E72A5617E1', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 0, NULL, NULL, 2, NULL, 0, 'Whisk the flour, salt, and baking powder together in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A79F97BA2F8FF34A943A6B38FA3377BB', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 1, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AAE6FD1D8200074D8053814FFFF8D410', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 2, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugar together until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DADB45F2EE8B58429F5FD1471E312694', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 3, NULL, NULL, 2, NULL, 0, 'Stir in the egg, vanilla, and almond or lemon extract.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4E24F046468F2C489E86CBE21F6E2B50', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 4, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients and stir just until combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D33464B0E2A74E46BD2BC2CE98345E28', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 5, NULL, NULL, 2, NULL, 0, 'Flatten dough into a disc and wrap in plastic wrap or a large zip-top bag and refrigerate for an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0A2E6190E2C640469B423C5371BD5DDE', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 6, NULL, NULL, 2, NULL, 0, 'When the dough has chilled, dust a cutting board or lightly dust a silicone baking mat and place 1/2 of the dough on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FAAD128855CCAF4591DC0B63FB958C64', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 7, NULL, NULL, 2, NULL, 0, 'Roll out to 1/8th inch thickness.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE2A67CA07500D449003A8C9E50E601D', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 8, NULL, NULL, 2, NULL, 0, 'Cut out 5 petals using the 3 1/2 long tear drop cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADF05D506E3036458318DEB5031FC8CB', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 9, NULL, NULL, 2, NULL, 0, 'Cut out one 1 1/2 inch circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'543D5403A85E7D4CA5B4CA3CC78CFF2B', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 10, NULL, NULL, 2, NULL, 0, 'Then arrange the petals in a flower shape, so that their tips almost touch, and use the round cookie cutter to cut a circle in the middle of the tips to create room for the center to fit inside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'208E60FA5B4FF94ABB185D34277CD52D', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 11, NULL, NULL, 2, NULL, 0, 'Cut out two leaves and one 6 1/2 inch long stem.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87F5658EAA18964BAF0AB534FE903482', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 12, NULL, NULL, 2, NULL, 0, 'Move all the pieces onto a parchment paper or silicone mat lined baking sheet, leaving an inch in between, and place in the refrigerator for at least 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A7F9CE5FDD578428D4E39A430D03F06', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 13, NULL, NULL, 2, NULL, 0, 'Roll out the other chilled piece of dough and cut out the same shapes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C101D59087BE1745B09836A9E4475F60', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 14, NULL, NULL, 2, NULL, 0, 'Blend the remaining dough together and roll it out. Cut out a hillside on which to place your flowers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C733155603D6E847918B4774AE05DB39', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 15, NULL, NULL, 2, NULL, 0, 'Use the excess dough to cut out another daisy or save for another use.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'963F3E703B1BA24CB5887085306CEC81', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 16, NULL, NULL, 2, NULL, 0, 'While the cut-outs are chilling, preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1034B24F0586F84681A1B45A6EB82737', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 17, NULL, NULL, 2, NULL, 0, 'Bake 1 tray of cookies at a time for 8 minutes then rotate the pan and continue to bake for 3-4 minutes just until the tops of the cookies look dry and the edges look set.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC45798289F2AE4CBBC8C5FF423ABA26', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 18, NULL, NULL, 2, NULL, 0, 'Remove and set the pan on a cooling rack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2ECED54E55686C45BF7C206CB075A0B0', X'3100C9075FD1F840902EA450DEBD278E', X'3100C9075FD1F840902EA450DEBD278E', 19, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool for 5 minutes then remove them using a metal spatula and place them directly on the cooling rack to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E444525E04955743927F3A2F09F7B542', X'335296C10C1A5E48BD2D9897FFAE9BB8', X'335296C10C1A5E48BD2D9897FFAE9BB8', 0, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese and 1/4 cup of the powdered sugar together until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF9DEA0CA8C08E4784A271A25F072DD3', X'335296C10C1A5E48BD2D9897FFAE9BB8', X'335296C10C1A5E48BD2D9897FFAE9BB8', 1, NULL, NULL, 2, NULL, 0, 'In a clean bowl with clean beaters, beat the heavy whipping cream, the remaining 1/2 cup of powdered sugar, and the vanilla extract until you get stiff peaks. Do not over whip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9C0622EC84B9DC4291A26F096377FA1A', X'335296C10C1A5E48BD2D9897FFAE9BB8', X'335296C10C1A5E48BD2D9897FFAE9BB8', 2, NULL, NULL, 2, NULL, 0, 'Stir 1/3 of the whipped cream into the cream cheese until the cream cheese is lighter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'440AB236794798449EE332A1E2F7337D', X'335296C10C1A5E48BD2D9897FFAE9BB8', X'335296C10C1A5E48BD2D9897FFAE9BB8', 3, NULL, NULL, 2, NULL, 0, 'Fold in another 1/3 of the whipped cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E761432E2527F34B88143EB43DF35E4E', X'335296C10C1A5E48BD2D9897FFAE9BB8', X'335296C10C1A5E48BD2D9897FFAE9BB8', 4, NULL, NULL, 2, NULL, 0, 'Then fold in the remaining 1/3 of the whipped cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E15885D63DAC4048B4E1AFAC4A4F2BB3', X'335296C10C1A5E48BD2D9897FFAE9BB8', X'335296C10C1A5E48BD2D9897FFAE9BB8', 5, NULL, NULL, 2, NULL, 0, 'Chill the cheesecake frosting for at least 30 minutes');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6DEA701F5851EA4FBCF87CF22065FEBC', X'CC06717B1C5678488B0F1F09E831A2A8', X'CC06717B1C5678488B0F1F09E831A2A8', 0, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5149A8675427524B8CCADF7A13ED03E6', X'CC06717B1C5678488B0F1F09E831A2A8', X'CC06717B1C5678488B0F1F09E831A2A8', 1, NULL, NULL, 2, NULL, 0, 'Add the marshmallow fluff and beat to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'143BC8F49F8BA1499C0D7DB4B6C40CD2', X'CC06717B1C5678488B0F1F09E831A2A8', X'CC06717B1C5678488B0F1F09E831A2A8', 2, NULL, NULL, 2, NULL, 0, 'Then add the whipped topping and beat until nice and light.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69FC57D541CA034193024D550110135C', X'AEF326F60A3C9042B807C408F57D476E', X'AEF326F60A3C9042B807C408F57D476E', 0, NULL, NULL, 2, NULL, 0, 'Spread or pipe a 1/4 inch layer of cheesecake filling onto each individual cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E866F4A4B073F0408F8CA3038213C963', X'AEF326F60A3C9042B807C408F57D476E', X'AEF326F60A3C9042B807C408F57D476E', 1, NULL, NULL, 2, NULL, 0, 'Arrange the cookies on a platter or cake board.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE53A307F8AA1249BBBF89ED546F50C0', X'AEF326F60A3C9042B807C408F57D476E', X'AEF326F60A3C9042B807C408F57D476E', 2, NULL, NULL, 2, NULL, 0, 'Then arrange raspberries, blueberries, or strawberry pieces over the petals of your daisies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'695178CE8A5A584AAD3FA9A1D2850E61', X'AEF326F60A3C9042B807C408F57D476E', X'AEF326F60A3C9042B807C408F57D476E', 3, NULL, NULL, 2, NULL, 0, 'Top the small round cookie with pineapple, mango, or peach pieces');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82250096BE273145AD617550DAE7FC5B', X'AEF326F60A3C9042B807C408F57D476E', X'AEF326F60A3C9042B807C408F57D476E', 4, NULL, NULL, 2, NULL, 0, 'Top the stem and leaves with green grapes and/or kiwi slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'239F6EF9220BDE4CAE3FEA839AE74C8C', X'AEF326F60A3C9042B807C408F57D476E', X'AEF326F60A3C9042B807C408F57D476E', 5, NULL, NULL, 2, NULL, 0, 'Lay the hillside cookie over the bottom of the flower stems and top with kiwi slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3BBA8C400B2DB64BBB26B2302D6BF7CA', X'FDF009C13C79034A9BBD90731A5C2747', X'FDF009C13C79034A9BBD90731A5C2747', 0, NULL, NULL, 2, NULL, 0, 'Melt butter and marshmallows in a large saucepan set over low heat then stir in the cereal until evenly coated.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FC790440E6D0E4EA5EBBB5819A00745', X'FDF009C13C79034A9BBD90731A5C2747', X'FDF009C13C79034A9BBD90731A5C2747', 1, NULL, NULL, 2, NULL, 0, 'Gently spread into an even layer in a well buttered 9 x 13 inch pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1177F48DC918241BBDD4A7AB31EE650', X'FDF009C13C79034A9BBD90731A5C2747', X'FDF009C13C79034A9BBD90731A5C2747', 2, NULL, NULL, 2, NULL, 0, 'Allow to cool for at least one hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5FFE4EB4EBE8404288ED1D3AE4B33782', X'FDF009C13C79034A9BBD90731A5C2747', X'FDF009C13C79034A9BBD90731A5C2747', 3, NULL, NULL, 2, NULL, 0, 'Cut 12 circles out of the treats using the 2-inch round cutter from the DIY-Lish Decorating Kit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FC91E3EB47057458701BF23D409C189', X'FDF009C13C79034A9BBD90731A5C2747', X'FDF009C13C79034A9BBD90731A5C2747', 4, NULL, NULL, 2, NULL, 0, 'Knead then roll out the fondant to about 1/16 of an inch thickness. Mix together, in a 1 to 6 ratio, a small amount of white and brown fondant to create tan fondant.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A53554EC67BE7F4DA4290F3517A10B1D', X'FDF009C13C79034A9BBD90731A5C2747', X'FDF009C13C79034A9BBD90731A5C2747', 5, NULL, NULL, 2, NULL, 0, 'For each bear cut out 1 black circle, 2 black teardrops, and 3 tan teardrops. Attach one tan teardrop to the black circle using water and attach the other tan teardrops to the black teardrops, cutting off the tips, creating the ears. Press two black candies on either side of the tan teardrop creating the eyes. Roll a tiny piece of black fondant into an oval and attach, using water, along the bottom edge of the tan teardrop creating the nose. Use a toothpick to create two nostrils.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43304DC529669D478B3602794CFDC984', X'FDF009C13C79034A9BBD90731A5C2747', X'FDF009C13C79034A9BBD90731A5C2747', 6, NULL, NULL, 2, NULL, 0, 'Brush a thin layer of melted candy melts on the top of a rice krispie treat and attach a fondant bear face on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'297A6A95CC9F52478BCCFDA865C7A23B', X'FDF009C13C79034A9BBD90731A5C2747', X'FDF009C13C79034A9BBD90731A5C2747', 7, NULL, NULL, 2, NULL, 0, 'Brush a small amount of candy melts onto the edge of one of the ears and press it onto the rice krispie treat along the top edge of the bears head. Repeat with the other ear.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E01C1874BAEBE4C881DAA797AFAC5E3', X'FDF009C13C79034A9BBD90731A5C2747', X'FDF009C13C79034A9BBD90731A5C2747', 8, NULL, NULL, 2, NULL, 0, 'Create more fondant animals using the instructions found in the DIY-Lish Cute Critters Decorating Kit and attach them to the remaining rice krispie treats.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FF0E2CB9EAD3348BB229FE8ED7FA496', X'67B423FC7AF8A84DBDE428F922229E71', X'67B423FC7AF8A84DBDE428F922229E71', 0, NULL, NULL, 2, NULL, 0, 'Melt and temper pure milk chocolate or melt the candy melts. (See tutorial for instructions).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5000088ECF179548BE7F813BEA4CC93B', X'67B423FC7AF8A84DBDE428F922229E71', X'67B423FC7AF8A84DBDE428F922229E71', 1, NULL, NULL, 2, NULL, 0, 'Dip one vanilla wafer in the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E77AF757489664AACB31A473EED56C7', X'67B423FC7AF8A84DBDE428F922229E71', X'67B423FC7AF8A84DBDE428F922229E71', 2, NULL, NULL, 2, NULL, 0, 'Remove using a fork and allow excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6036EDB01100064DB3D211E64DAF19D8', X'67B423FC7AF8A84DBDE428F922229E71', X'67B423FC7AF8A84DBDE428F922229E71', 3, NULL, NULL, 2, NULL, 0, 'Set chocolate dipped cookie on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5486C0C97418A54496E262E95BE20D7F', X'67B423FC7AF8A84DBDE428F922229E71', X'67B423FC7AF8A84DBDE428F922229E71', 4, NULL, NULL, 2, NULL, 0, 'Immediately pipe a round head and two pointed ears next to the chocolate dipped cookie then pipe a long triangle shaped pick opposite the head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90A759C4E309E2459A9B45021C61F3CD', X'67B423FC7AF8A84DBDE428F922229E71', X'67B423FC7AF8A84DBDE428F922229E71', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate, if using pure chocolate, and freeze, if using candy melts, for about 5 minutes just until the chocolate hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F02EB39EC1B72409D795CBE7794D905', X'67B423FC7AF8A84DBDE428F922229E71', X'67B423FC7AF8A84DBDE428F922229E71', 6, NULL, NULL, 2, NULL, 0, 'Repeat creating 21 bunnies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'79F4A911922EC14A8621CDF9293FC726', X'67B423FC7AF8A84DBDE428F922229E71', X'67B423FC7AF8A84DBDE428F922229E71', 7, NULL, NULL, 2, NULL, 0, 'Pipe a small dab of chocolate on the bottom edge of the bunny, just above the chocolate pick, and attach one white Sixlet candy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F22166439545684A800EA6DD45E324FA', X'67B423FC7AF8A84DBDE428F922229E71', X'67B423FC7AF8A84DBDE428F922229E71', 8, NULL, NULL, 2, NULL, 0, 'Optionally, roll a mini marshmallow in the palms of your hand until it looks like a fluffy bunny tail. Attach that to the bunny instead of the white Sixlet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7705AEEB4AB5C441BC35332C9396854B', X'421D9BBEB9E9FB498A0BD132002BA872', X'421D9BBEB9E9FB498A0BD132002BA872', 0, NULL, NULL, 2, NULL, 0, 'Preheat your oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'366F963EE374E84A9A29C9B009DCA87C', X'421D9BBEB9E9FB498A0BD132002BA872', X'421D9BBEB9E9FB498A0BD132002BA872', 1, NULL, NULL, 2, NULL, 0, 'Line 21 muffin tin cavities with paper cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E26F3BA0A662D348B632FCAED6A310B3', X'421D9BBEB9E9FB498A0BD132002BA872', X'421D9BBEB9E9FB498A0BD132002BA872', 2, NULL, NULL, 2, NULL, 0, 'Combine the white cake mix, vanilla pudding mix, sour cream, water, oil, and egg whites in a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A71465569E6C7B4A9F44DEA8C619DFAF', X'421D9BBEB9E9FB498A0BD132002BA872', X'421D9BBEB9E9FB498A0BD132002BA872', 3, NULL, NULL, 2, NULL, 0, 'Beat on low speed for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D5984013B2AA4418FEF7197F9B181CC', X'421D9BBEB9E9FB498A0BD132002BA872', X'421D9BBEB9E9FB498A0BD132002BA872', 4, NULL, NULL, 2, NULL, 0, 'Beat on medium-high speed for another two minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE73229BD8818440B898186FA0CDD4B2', X'421D9BBEB9E9FB498A0BD132002BA872', X'421D9BBEB9E9FB498A0BD132002BA872', 5, NULL, NULL, 2, NULL, 0, 'Equally divide the cake batter among 21 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'58B93F59574E694BBDB2ACF99CE27C3F', X'421D9BBEB9E9FB498A0BD132002BA872', X'421D9BBEB9E9FB498A0BD132002BA872', 6, NULL, NULL, 2, NULL, 0, 'Bake for 18-24 minutes then remove and cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DB2F561F45085744807CB2F6BEEF64E7', X'90E533C53A56AC4B91AF6CFDC6889814', X'90E533C53A56AC4B91AF6CFDC6889814', 0, NULL, NULL, 2, NULL, 0, 'Beat the shortening and butter until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A317D09797F34B4AA6750690D26889A9', X'90E533C53A56AC4B91AF6CFDC6889814', X'90E533C53A56AC4B91AF6CFDC6889814', 1, NULL, NULL, 2, NULL, 0, 'Add the powdered sugar and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FF72334D5C2E84E8F4DDC7F58FE86F1', X'90E533C53A56AC4B91AF6CFDC6889814', X'90E533C53A56AC4B91AF6CFDC6889814', 2, NULL, NULL, 2, NULL, 0, 'Pour in 3 tablespoons of the milk and green food coloring and beat until smooth and creamy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C562C07900087A4993E962B81DA471AB', X'90E533C53A56AC4B91AF6CFDC6889814', X'90E533C53A56AC4B91AF6CFDC6889814', 3, NULL, NULL, 2, NULL, 0, 'If needed, add more milk, a teaspoon at a time, until the frosting is of piping consistency.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C24B1CC63403C04FBEEEE932B01D7507', X'90E533C53A56AC4B91AF6CFDC6889814', X'90E533C53A56AC4B91AF6CFDC6889814', 4, NULL, NULL, 2, NULL, 0, 'Spoon into a pastry bag that has a multi-hole grass tip attached.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A8742FCC897B245954402B1F02DC099', X'CFD4A863269F374AA23293726A64BF09', X'CFD4A863269F374AA23293726A64BF09', 0, NULL, NULL, 2, NULL, 0, 'Pipe green grass over top of each of the cooled cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C38F7A7A2C20A340ADFF7B40A4DB69EA', X'CFD4A863269F374AA23293726A64BF09', X'CFD4A863269F374AA23293726A64BF09', 1, NULL, NULL, 2, NULL, 0, 'Insert a chocolate bunny in the center of each frosted cupcake before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'720B5CCC57CE5D4F8146110D599ED96C', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', 0, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese using an electric mixer or food processor until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5967003A76E2D744BD146FD1806E9032', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', 1, NULL, NULL, 2, NULL, 0, 'Scoop out about 2 1/2 tablespoons of the cream cheese and place it in a disposable pastry bag or a sandwich size zip top bag and set aside until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ECF7CF792FC53549B1C53A7C07B7F784', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', 2, NULL, NULL, 2, NULL, 0, 'To the remaining cream cheese, add the Worcestershire sauce, seasoned salt, garlic powder, and 1 cup of the shredded white cheddar cheese and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61BB5FAFE9C92F478E3ED46E01309079', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', 3, NULL, NULL, 2, NULL, 0, 'Toss in the finely chopped bacon and stir well.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24C89B32BC99014DB73E8FC7AC49B5D6', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', 4, NULL, NULL, 2, NULL, 0, 'Scoop out 18 level tablespoons of the cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4900210565FB234182FF9FA2BD8242B0', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', 5, NULL, NULL, 2, NULL, 0, 'Drop each of the scoops onto a pile of the remaining shredded cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0ED2C592A9A1B241A4D875D5FCCE8D75', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', 6, NULL, NULL, 2, NULL, 0, 'Roll into a ball coated in the cheese then set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A321CE133D8DC4CB8D253FE56045275', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', 7, NULL, NULL, 2, NULL, 0, 'Use the reserved cream cheese to pipe a small round tail onto the top of each mini cheese ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3925DF4A72CF0942BF4758E0582FEC89', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', 8, NULL, NULL, 2, NULL, 0, 'Cut 36 small (about 3/4 inch long) ovals out of the white cheese slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B3F2E9F48A35C448AF375837EB6DF48F', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', 9, NULL, NULL, 2, NULL, 0, 'Use a pink food coloring marker to draw four toes and one pad onto each of the ovals creating 36 bunny feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E11007AEAE933C419C430EB8779E0CA0', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', 10, NULL, NULL, 2, NULL, 0, 'Use a dab of cream cheese to attach two bunny feet to each of the mini cheese balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1FE287AA3105F64B9F8BAB2A21A46478', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', X'BEC53BE13A7C9B47BF77C8E17C36A5DE', 11, NULL, NULL, 2, NULL, 0, 'If desired, use some cream cheese to attach the Mini Cheese Ball Bunnies to round crackers just before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43AB263AE98D82429909F4C248698A7C', X'BC47F958BE11624F9DFA23708693A9A0', X'BC47F958BE11624F9DFA23708693A9A0', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'702AF9ECFB3D1947B2A51D56DEF61A0E', X'BC47F958BE11624F9DFA23708693A9A0', X'BC47F958BE11624F9DFA23708693A9A0', 1, NULL, NULL, 2, NULL, 0, 'Beat butter and sugar until fully combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C311C97B8FF7A045A02ACA97BA1E82F3', X'BC47F958BE11624F9DFA23708693A9A0', X'BC47F958BE11624F9DFA23708693A9A0', 2, NULL, NULL, 2, NULL, 0, 'Add melted chocolate and beat until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01F036875D0C9945ACAE5CD3CFDF7F86', X'BC47F958BE11624F9DFA23708693A9A0', X'BC47F958BE11624F9DFA23708693A9A0', 3, NULL, NULL, 2, NULL, 0, 'Mix in eggs and egg yolks one at a time until just combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CF3E818242C4F4B87A62C7774077AE5', X'BC47F958BE11624F9DFA23708693A9A0', X'BC47F958BE11624F9DFA23708693A9A0', 4, NULL, NULL, 2, NULL, 0, 'Mix in oil, vanilla, and sour cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87F767DB04B7B0438605BF33FE8BCF64', X'BC47F958BE11624F9DFA23708693A9A0', X'BC47F958BE11624F9DFA23708693A9A0', 5, NULL, NULL, 2, NULL, 0, 'Sift together flour, salt, baking soda, and cocoa powder together then mix into wet ingredients in three increments.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0353D7B55CA30442832971A84FB240AD', X'BC47F958BE11624F9DFA23708693A9A0', X'BC47F958BE11624F9DFA23708693A9A0', 6, NULL, NULL, 2, NULL, 0, 'Stir in water until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1DCA7C715DD3654AB44DEBB31CF8B1EC', X'BC47F958BE11624F9DFA23708693A9A0', X'BC47F958BE11624F9DFA23708693A9A0', 7, NULL, NULL, 2, NULL, 0, 'Fill 12 silicone terra cotta pot molds with 5 tablespoons of batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0173B629A6CE844FB370385F2A8F6035', X'BC47F958BE11624F9DFA23708693A9A0', X'BC47F958BE11624F9DFA23708693A9A0', 8, NULL, NULL, 2, NULL, 0, 'Equally divide remaining batter in 4 standard cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D033B037D4BC744591AEFA757C0D65B1', X'BC47F958BE11624F9DFA23708693A9A0', X'BC47F958BE11624F9DFA23708693A9A0', 9, NULL, NULL, 2, NULL, 0, 'Bake for 18-24 minutes until a toothpick inserted into the center of a cupcake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09E1EC7301DF74488D5F45047425174C', X'BC47F958BE11624F9DFA23708693A9A0', X'BC47F958BE11624F9DFA23708693A9A0', 10, NULL, NULL, 2, NULL, 0, 'Remove and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76145ED191B8C147962A6A99F206FCCD', X'BC47F958BE11624F9DFA23708693A9A0', X'BC47F958BE11624F9DFA23708693A9A0', 11, NULL, NULL, 2, NULL, 0, 'Remove about an inch or so of cake from the center of each terra cotta cupcake by using a cupcake corer or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1957BB4936B1924AB7D48BF16FCDED4C', X'BC47F958BE11624F9DFA23708693A9A0', X'BC47F958BE11624F9DFA23708693A9A0', 12, NULL, NULL, 2, NULL, 0, 'Crumble the removed cake pieces along with the 1 of the cupcakes. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40E8F3C0F7AAFF48812542F1EA718985', X'034C66A5F6D16943A2A9B1D918B7D0A6', X'034C66A5F6D16943A2A9B1D918B7D0A6', 0, NULL, NULL, 2, NULL, 0, 'Combine chocolate with 3/4 cup heavy whipping cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'007C4154E079AB478A32FA8019D9656F', X'034C66A5F6D16943A2A9B1D918B7D0A6', X'034C66A5F6D16943A2A9B1D918B7D0A6', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power in the microwave for 1 minute 15 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F6229682C4281F418C34DA2E46201FA3', X'034C66A5F6D16943A2A9B1D918B7D0A6', X'034C66A5F6D16943A2A9B1D918B7D0A6', 2, NULL, NULL, 2, NULL, 0, 'Let chocolate rest in microwave for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'94666DD947632C49A474953ED607571A', X'034C66A5F6D16943A2A9B1D918B7D0A6', X'034C66A5F6D16943A2A9B1D918B7D0A6', 3, NULL, NULL, 2, NULL, 0, 'Remove and stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EC9ACECAF42074A85146F88A1F1B98A', X'034C66A5F6D16943A2A9B1D918B7D0A6', X'034C66A5F6D16943A2A9B1D918B7D0A6', 4, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10-15 more seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27E5466A184E3C48A2D6696282B10F64', X'034C66A5F6D16943A2A9B1D918B7D0A6', X'034C66A5F6D16943A2A9B1D918B7D0A6', 5, NULL, NULL, 2, NULL, 0, 'Pour in remaining whipping cream and stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2455B1859701B449331D0F535A1C19D', X'034C66A5F6D16943A2A9B1D918B7D0A6', X'034C66A5F6D16943A2A9B1D918B7D0A6', 6, NULL, NULL, 2, NULL, 0, 'Pour warm ganache over top of one of the terra cotta cakes then sprinkle cake crumbs over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AD56BE647B33764F873914D8EC90AD05', X'034C66A5F6D16943A2A9B1D918B7D0A6', X'034C66A5F6D16943A2A9B1D918B7D0A6', 7, NULL, NULL, 2, NULL, 0, 'Repeat covering all the terra cotta cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6838432DC0B41E41B7D2A43256645416', X'034C66A5F6D16943A2A9B1D918B7D0A6', X'034C66A5F6D16943A2A9B1D918B7D0A6', 8, NULL, NULL, 2, NULL, 0, 'Use the remaining ganache to frost the plain cupcakes and enjoy a snack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25E02F97F410964BA4892142558CC79A', X'5256A44F474CE844B927FCF3F55048AE', X'5256A44F474CE844B927FCF3F55048AE', 0, NULL, NULL, 2, NULL, 0, 'Scoop about 2 tablespoons of cold white frosting using an ice cream scoop and set it in the center of one of the terra cotta pot cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F40A28E37460CD4A8FE70D9D8BE50023', X'5256A44F474CE844B927FCF3F55048AE', X'5256A44F474CE844B927FCF3F55048AE', 1, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'93D6D2181ED0E54DA0D5AEE410E05349', X'5256A44F474CE844B927FCF3F55048AE', X'5256A44F474CE844B927FCF3F55048AE', 2, NULL, NULL, 2, NULL, 0, 'Attach two candy feet to each scoop of frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60C8012037C87641848CA394C34E5874', X'5256A44F474CE844B927FCF3F55048AE', X'5256A44F474CE844B927FCF3F55048AE', 3, NULL, NULL, 2, NULL, 0, 'Scoop out about 1/2 teaspoon of cold frosting and roll it into a small ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF215CABC400D348B02A6B470FEA9E87', X'5256A44F474CE844B927FCF3F55048AE', X'5256A44F474CE844B927FCF3F55048AE', 4, NULL, NULL, 2, NULL, 0, 'Set it on a scoop of frosting to create the bunny''s tail.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'76E811A9E40B4B4FB783660D28D418F3', X'5256A44F474CE844B927FCF3F55048AE', X'5256A44F474CE844B927FCF3F55048AE', 5, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05E7F5E494A4704786E18EE67C09B0C7', X'5256A44F474CE844B927FCF3F55048AE', X'5256A44F474CE844B927FCF3F55048AE', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'88A9A91F3C49CC40B0C183230069E402', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 0, NULL, NULL, 2, NULL, 0, 'Dip Nutter Butter Cookies in Yellow Candy Melts and set them on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'220A241C141605458E39CBF6813861D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 1, NULL, NULL, 2, NULL, 0, 'Freeze for 3-4 minutes until hardened.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9E73A5BA88E0B5469E2D12323AB5DC26', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 2, NULL, NULL, 2, NULL, 0, 'Fit a clay extruder with a ribbon disc (small rectangle whole).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDE098AA5C1A7145BB8BFB196C2654FA', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 3, NULL, NULL, 2, NULL, 0, 'Insert black modeling chocolate in the clay extruder and press out ribbons of the candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1A8E6ADBBECB954DBF506118CADA30C8', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 4, NULL, NULL, 2, NULL, 0, 'Brush clear piping gel in a thin line over the upper middle part of the cookie and lay a strip of black modeling chocolate over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD43C1D0F61AC243982DEAA761061488', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 5, NULL, NULL, 2, NULL, 0, 'Cut off the excess modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC95838D148032459F56A99856EE2853', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 6, NULL, NULL, 2, NULL, 0, 'Repeat creating four black, curved stripes across each cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57FA8F271410D24C8DDFB68D6CF05929', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 7, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of black modeling chocolate and roll into a cone.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4FA214CEFB27714BB1437BE8BC271854', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 8, NULL, NULL, 2, NULL, 0, 'Attach along the bottom edge of the cookie to create the bee''s stinger.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A8DCBE07044204297996B4C2C788007', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 9, NULL, NULL, 2, NULL, 0, 'Press more black modeling chocolate through the clay extruder fitted with a round hole disc.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E5E2BB25D1FD614485F9A848EB9A1501', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 10, NULL, NULL, 2, NULL, 0, 'Cut tiny pieces of the modeling chocolate to create a smile then create two antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB6A8F622674B84AAC7B0AFBBF5E0D46', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 11, NULL, NULL, 2, NULL, 0, 'Attach the smile using clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F63E93951396AF41BF4FFA2059060F22', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 12, NULL, NULL, 2, NULL, 0, 'Attach the antennae using yellow candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1E8A8E17270BF4FABABAE995772D30F', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 13, NULL, NULL, 2, NULL, 0, 'Attach two candy eyes using yellow candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C3D4B7A765026940876E7A19D5289210', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 14, NULL, NULL, 2, NULL, 0, 'Pipe two wings and set a cookie on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'249F32DBA086A24AB16F2CA1093D6BDF', X'B71C71CC500A3F4A95BB1DB1646C12D0', X'B71C71CC500A3F4A95BB1DB1646C12D0', 15, NULL, NULL, 2, NULL, 0, 'Allow the wings and antennae to harden before moving the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'750956D242B33E46B11E13E2EBCCDE0B', X'E22450725E5FBA4B9206833276425527', X'E22450725E5FBA4B9206833276425527', 0, NULL, NULL, 2, NULL, 0, 'Place Oreo Cookies in the bowl of a food processor.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0BD6FC13ECB65A42BB88050ABA7EA84C', X'E22450725E5FBA4B9206833276425527', X'E22450725E5FBA4B9206833276425527', 1, NULL, NULL, 2, NULL, 0, 'Pulse on high until you have fine crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45B71BE1471D6447BC09171E1657F582', X'E22450725E5FBA4B9206833276425527', X'E22450725E5FBA4B9206833276425527', 2, NULL, NULL, 2, NULL, 0, 'Scoop out 2/3 cup crumbs for later.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D20D3A83EB2C7F45AA28D958A5ECD9C4', X'E22450725E5FBA4B9206833276425527', X'E22450725E5FBA4B9206833276425527', 3, NULL, NULL, 2, NULL, 0, 'Add cream cheese to the food processor bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55AFE9EFAFA5F942AD1B469C53E44EB5', X'E22450725E5FBA4B9206833276425527', X'E22450725E5FBA4B9206833276425527', 4, NULL, NULL, 2, NULL, 0, 'Pulse until the mixture is well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'546EC35FE854C94984A9688B71C1A6E1', X'E22450725E5FBA4B9206833276425527', X'E22450725E5FBA4B9206833276425527', 5, NULL, NULL, 2, NULL, 0, 'Scoop out 28 tablespoon size balls of Oreo dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B0143E066A896044A7A934884A045795', X'E22450725E5FBA4B9206833276425527', X'E22450725E5FBA4B9206833276425527', 6, NULL, NULL, 2, NULL, 0, 'Roll each scoop into a ball then roll in Oreo Cookie crumbs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B53FC2F2A4BA9B47B5B89579F6BCD31B', X'E22450725E5FBA4B9206833276425527', X'E22450725E5FBA4B9206833276425527', 7, NULL, NULL, 2, NULL, 0, 'Pipe `L` shaped spider legs onto parchment paper lined baking sheets.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED87D546188AFB4789B280BCC3F8DBB3', X'E22450725E5FBA4B9206833276425527', X'E22450725E5FBA4B9206833276425527', 8, NULL, NULL, 2, NULL, 0, 'You''ll need 8 legs per spider but you''ll want extras in case of breakage.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'64E2435F631F764AA74B9A9C23E7AF1E', X'E22450725E5FBA4B9206833276425527', X'E22450725E5FBA4B9206833276425527', 9, NULL, NULL, 2, NULL, 0, 'Let the candy harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC1AEB38B2C61F41B06D540AE1250531', X'E22450725E5FBA4B9206833276425527', X'E22450725E5FBA4B9206833276425527', 10, NULL, NULL, 2, NULL, 0, 'Insert 8 legs into each Oreo ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0B247DA9F220B04199452D30519054EC', X'E22450725E5FBA4B9206833276425527', X'E22450725E5FBA4B9206833276425527', 11, NULL, NULL, 2, NULL, 0, 'Attach 2 candy eyes using candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17230A48666899428D33142BFBD8737E', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 0, NULL, NULL, 2, NULL, 0, 'Spread some peanut butter on 12 of the butterfly crackers then set another cracker over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2FBDA2E9A274534BB4B4E3E6B7EDA2E0', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 1, NULL, NULL, 2, NULL, 0, 'Dip a peanut butter cracker into the melted candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A27671001ADCE74F825E3E6461C3508B', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 2, NULL, NULL, 2, NULL, 0, 'Use a chocolate dipping fork to remove the butterfly and allow the excess candy melts to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EFB530254D22A9458AB19DF77F9EA9B0', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 3, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2C94FFA56DEB82439630290583A9C4CE', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 4, NULL, NULL, 2, NULL, 0, 'Immediately press two regular yellow or orange M&M''s on the upper portion of each wing.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF19B3E98118A04D82C922528F1342A8', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 5, NULL, NULL, 2, NULL, 0, 'Press two mini yellow or orange M&M''s on the lower portion of each wing.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CF1D5BBC0A773458A8ACA6B33FFB0BC', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 6, NULL, NULL, 2, NULL, 0, 'Press one regular green M&M at the top of the mid section creating the butterfly''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCA94F74223B7546B0471AF5EB9C7E11', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 7, NULL, NULL, 2, NULL, 0, 'Arrange three mini green M&M''s in a row below the head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A42B85900BA5248A0A578F134546559', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 8, NULL, NULL, 2, NULL, 0, 'Insert two candy licorice pieces into the top of each cracker creating antennae.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CAFB3F28C99DD4BA33022530B02B4E7', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 9, NULL, NULL, 2, NULL, 0, 'Pop the cracker into the freezer for 3-5 minutes until the candy coating hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0F0D8CD797F96842A8379B100C75926F', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 10, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3C5E5330C7D5D4A970E09144C05E37A', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 11, NULL, NULL, 2, NULL, 0, 'Remove the crackers from the freezer and let sit at room temperature for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C828C6B588CD634985A9D0BE8720DA65', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 12, NULL, NULL, 2, NULL, 0, 'Draw two eyes and a smile on the green butterfly heads.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'683C8184B28B8F46B9B40099CFBFF65B', X'2AF434014595944D97FE937816FF0162', X'2AF434014595944D97FE937816FF0162', 13, NULL, NULL, 2, NULL, 0, 'Serve!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'47093D6A2540F54FA6E6FDEAFC287F55', X'EA77FC059C0CFD42892A2D6E97F060AF', X'EA77FC059C0CFD42892A2D6E97F060AF', 0, NULL, NULL, 2, NULL, 0, 'Combine white chocolate with sweetened condensed milk in a saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8447037E5A4784D90DAFB92793DFBAC', X'EA77FC059C0CFD42892A2D6E97F060AF', X'EA77FC059C0CFD42892A2D6E97F060AF', 1, NULL, NULL, 2, NULL, 0, 'Heat over low heat until half of the chocolate is melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8AF3A9AEC589B449DFC1BD94675AA08', X'EA77FC059C0CFD42892A2D6E97F060AF', X'EA77FC059C0CFD42892A2D6E97F060AF', 2, NULL, NULL, 2, NULL, 0, 'Remove from the heat and let sit for 3 minutes then stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0CEC2C57A17014D94F0B769A2894EF9', X'EA77FC059C0CFD42892A2D6E97F060AF', X'EA77FC059C0CFD42892A2D6E97F060AF', 3, NULL, NULL, 2, NULL, 0, 'Add the Oreo Cookie crumbs and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EB0B18C6CD1921438429C17F544951D6', X'EA77FC059C0CFD42892A2D6E97F060AF', X'EA77FC059C0CFD42892A2D6E97F060AF', 4, NULL, NULL, 2, NULL, 0, 'Pour into a tin foil pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0471354B3CA1D4EA07424F5D2E15773', X'EA77FC059C0CFD42892A2D6E97F060AF', X'EA77FC059C0CFD42892A2D6E97F060AF', 5, NULL, NULL, 2, NULL, 0, 'Follow the tutorial to decorate your fudge garden stone.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1169D62AFA96C240A439AF97AE41DCC4', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 0, NULL, NULL, 2, NULL, 0, 'Combine marshmallows, 6 ounces yellow candy melts, and butter in a large microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'373CBEDA9433B440A2A3AAB90812451A', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 1, NULL, NULL, 2, NULL, 0, 'Heat on high for 1 minute.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92D205438C244848945294701DDE48DB', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 2, NULL, NULL, 2, NULL, 0, 'Remove and stir.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F1DFA6CDA02E604EB7BC55F032FF21DC', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 3, NULL, NULL, 2, NULL, 0, 'Heat for another 45-60 seconds until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'395A811569C65941A7E82572BB3D2D54', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 4, NULL, NULL, 2, NULL, 0, 'Add a few drops of yellow food coloring and stir until well blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF094DF781B3684DB4BD4E98CEA71666', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 5, NULL, NULL, 2, NULL, 0, 'Stir in the Rice Krispies Cereal.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DAAFE225998F946B389879C96BFF833', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 6, NULL, NULL, 2, NULL, 0, 'Allow to cool slightly.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'30EF8AC88E14464D8B405AF4A519F290', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 7, NULL, NULL, 2, NULL, 0, 'Grease hands with butter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'023993D35E8FB945A8C6BB899EC01DE7', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 8, NULL, NULL, 2, NULL, 0, 'Scoop and roll 9 balls using about 1/2 cup cereal treat in each ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3284121B38ABC644BADBF78C8372C1C5', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 9, NULL, NULL, 2, NULL, 0, 'Reserve about 3/4 cup for the wings and feathers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'428E9C508262C94B9CC2396AB0B20B3F', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 10, NULL, NULL, 2, NULL, 0, 'Pinch off teaspoon size pieces of the cereal treat and shape into tear drop shaped wings and immediately attach two, one on either side of a ball. If they don''t stick well, then use melted candy melts to attach them.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D62475EA876D148963D891363E54149', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 11, NULL, NULL, 2, NULL, 0, 'Roll the remaining cereal treat mixture into thin logs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C457410DC110E4F8466B13DD8F558C5', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 12, NULL, NULL, 2, NULL, 0, 'Pinch off three short pieces and stick them to the tops of each rice krispie treat chick forming 3 feathers.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AFF4DE254F79424892BEFC4CEBF62057', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 13, NULL, NULL, 2, NULL, 0, 'Cut the circus peanuts into 8 equal size slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D52C0E01CB38141A8FA962EB0494234', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 14, NULL, NULL, 2, NULL, 0, 'Cut 18 slices into triangles to form the beaks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7D6B3989D72BDB429079A2F129F2855B', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 15, NULL, NULL, 2, NULL, 0, 'Cut 18 slices into chicken feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'966883E12A6AD74990E763026601556B', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 16, NULL, NULL, 2, NULL, 0, 'Melt the remaining 2 ounces of yellow candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'419F9BF6A8EFE145A1F3817D6B6E79AB', X'EBA2F183E2B71A40ABE80448734AA053', X'EBA2F183E2B71A40ABE80448734AA053', 17, NULL, NULL, 2, NULL, 0, 'Use the candy melts to attach two candy eyes, two circus peanut feet and beak to each chick.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2E839A584898974BB0260386B6CEA447', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 325 F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'05BC05AF439A1D4891BC70AF7F1DE27D', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', 1, NULL, NULL, 2, NULL, 0, 'In a medium-sized mixing bowl or the bowl of a stand mixer, mix butter and sugar until fully combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'383D7E2D09C07D4B9A79A918FED76311', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', 2, NULL, NULL, 2, NULL, 0, 'Add melted chocolate and beat until well mixed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50FFB24EFA0C534FA7AEEC46DA038837', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', 3, NULL, NULL, 2, NULL, 0, 'Mix in eggs and egg yolks one at a time until just combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3751CDC565E13141884ECDC5AB651DE8', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', 4, NULL, NULL, 2, NULL, 0, 'Mix in oil, vanilla, and sour cream until just combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E47AFB4CAD70B44AA31C2FA05C6EAB12', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', 5, NULL, NULL, 2, NULL, 0, 'Sift together flour, salt, baking soda, and cocoa powder together then mix into wet ingredients in three increments.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF922DA0D9F96048BF6833FBBA488D33', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', 6, NULL, NULL, 2, NULL, 0, 'Mix in water until just combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'004ABB363238E94BBFBBE45411EF271D', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', 7, NULL, NULL, 2, NULL, 0, 'Fill silicone mold with 5 tablespoons of batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF29719C4FFE664D841C86511A9ECB40', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', 8, NULL, NULL, 2, NULL, 0, 'Equally divide remaining batter in 4 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F0C43361E5CCB34E824189E1CD61E221', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', 9, NULL, NULL, 2, NULL, 0, 'Bake for 18-24 minutes until a toothpick inserted into the center of a cupcake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'46B1F79D34F82843823442D1BF12C4BF', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', 10, NULL, NULL, 2, NULL, 0, 'Remove and allow to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'052960860FFEEE458F4DEEB85B8C528E', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', 11, NULL, NULL, 2, NULL, 0, 'Remove about an inch or so of cake from the center of each terra cotta cupcake by using a cupcake corer or knife.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B30AEA9A4A4FD146A90B688266B07504', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', X'F4A0EEF69FA7BF48B75DDA40C400B4A4', 12, NULL, NULL, 2, NULL, 0, 'Crumble the removed cake pieces along with one of the cupcakes. Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'168F90D68EFDFB4B9CF07FCCA9BEBA48', X'9210C603A8004140B456011EB51587A2', X'9210C603A8004140B456011EB51587A2', 0, NULL, NULL, 2, NULL, 0, 'Combine chocolate with 3/4 cup heavy whipping cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7F4EADF48B9076439B85CF82943DFC8C', X'9210C603A8004140B456011EB51587A2', X'9210C603A8004140B456011EB51587A2', 1, NULL, NULL, 2, NULL, 0, 'Heat on high power in the microwave for 1 minute 15 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'52D57B35761488449D44ABE9126D4563', X'9210C603A8004140B456011EB51587A2', X'9210C603A8004140B456011EB51587A2', 2, NULL, NULL, 2, NULL, 0, 'Let chocolate rest in microwave for 2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60F40D34CEE6A84C8984552D15B6CE39', X'9210C603A8004140B456011EB51587A2', X'9210C603A8004140B456011EB51587A2', 3, NULL, NULL, 2, NULL, 0, 'Remove and stir until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'63F77B47029E0F4C8C13926E33320CE9', X'9210C603A8004140B456011EB51587A2', X'9210C603A8004140B456011EB51587A2', 4, NULL, NULL, 2, NULL, 0, 'If needed, heat for 10-15 more seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'418B54927BB4F34E80B4499852C4C0CA', X'9210C603A8004140B456011EB51587A2', X'9210C603A8004140B456011EB51587A2', 5, NULL, NULL, 2, NULL, 0, 'Pour in remaining whipping cream and stir until smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FE93FFB0C82AA4DB31312CE46EF15E6', X'9210C603A8004140B456011EB51587A2', X'9210C603A8004140B456011EB51587A2', 6, NULL, NULL, 2, NULL, 0, 'Pour warm ganache over top of one of the terra cotta cakes then sprinkle cake crumbs over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8350E5EB1ACF54B8D06BC09BF78D5F6', X'9210C603A8004140B456011EB51587A2', X'9210C603A8004140B456011EB51587A2', 7, NULL, NULL, 2, NULL, 0, 'Repeat covering all the terra cotta cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32F75720FDD8C641AD2BEED4979090AE', X'9210C603A8004140B456011EB51587A2', X'9210C603A8004140B456011EB51587A2', 8, NULL, NULL, 2, NULL, 0, 'Use the remaining ganache to frost the plain cupcakes and enjoy a snack.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53A4A53CDC49B144B1C6B000C6055256', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 0, NULL, NULL, 2, NULL, 0, 'Unwrap White Reese''s Cups and set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FC1DDE0DE143B4C8C180C49C4B68045', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 1, NULL, NULL, 2, NULL, 0, 'Roll out creamy white, pink, bright white and black candy clay.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F5EA7407277C1A4A92B960A52ECDEA0E', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 2, NULL, NULL, 2, NULL, 0, 'Cut out twenty four 2 1/2 inch long tear drop shaped creamy white ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1BD6F844E140114BA9BE36857845DD48', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 3, NULL, NULL, 2, NULL, 0, 'Cut out twenty four 1 3/4 inch long tear drop shaped pink ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5056B215755F7943B9F7FD04EE17E4EB', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 4, NULL, NULL, 2, NULL, 0, 'Brush a dab of water on a pink ear and set the wet side on the white ear, centering the pink part.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A1492AA8A5ADC6488CA8C51A317B1033', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 5, NULL, NULL, 2, NULL, 0, 'Repeat creating 24 ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F39DE7185B49849AAE88B9395970FC0', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 6, NULL, NULL, 2, NULL, 0, 'Cut out twenty four 1/2 inch bright white circles and 3/8 inch black circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E29830923B62844598A79BCD93BF6D64', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 7, NULL, NULL, 2, NULL, 0, 'Brush one side of a white circle with water and set the wet side down on the Reese''s Cup situated where you''d want your Bunny''s eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE9924824FFD16449CEB808ED8326EFF', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 8, NULL, NULL, 2, NULL, 0, 'Press down on the white candy clay smoothing it out and making it a bit larger.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5969796BBD9F29489F5534F3CFAC8FAA', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 9, NULL, NULL, 2, NULL, 0, 'Repeat, adding the second white circle right next to the first.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F559FC2182D0A4F811D6228EDA52514', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 10, NULL, NULL, 2, NULL, 0, 'Attach two black pupils to the whites using a very small amount of water.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF868B804636264D97466EFA52CD0B79', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 11, NULL, NULL, 2, NULL, 0, 'Continue adding eyes to all your Reese''s Cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45CB65E81019754CB1E5724FA73397F6', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 12, NULL, NULL, 2, NULL, 0, 'Pinch off a small piece of pink candy clay and shape it into a nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1DCC4CC5594504D8CC1B89C7719FFD7', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 13, NULL, NULL, 2, NULL, 0, 'Attach under the eyes using a dab of water.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C6AE974380D6B244BB71A578A8C90F54', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 14, NULL, NULL, 2, NULL, 0, 'Set the points of the bunny ears together on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C91618C05A56F24484F607323B1D2D2F', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 15, NULL, NULL, 2, NULL, 0, 'Pour a small amount of white candy melts over the tips of the ears then continue adding a 2 1/2 inch line of candy melts straight down from the ear forming a pick that will go into the cupcakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C8F2960CD5353E4BAF027318C5CBDD18', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 16, NULL, NULL, 2, NULL, 0, 'Set the Reese''s cup on the wet candy so that the bunny faces the correct direction.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD9CCEC34BCC4A41B7800A65D3CF8AAB', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 17, NULL, NULL, 2, NULL, 0, 'Freeze for 5 minutes until the candy melts harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2A0EB43369A104FA17CD48FDFEAFF9E', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 18, NULL, NULL, 2, NULL, 0, 'Remove from freezer and let sit at room temperature for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65EE719D6A7095458AEB3C295CB35501', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 19, NULL, NULL, 2, NULL, 0, 'Bend one ear a little bit then flip the bunny over.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D4FCD4414C4C9498E2FF9A836AFD035', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 20, NULL, NULL, 2, NULL, 0, 'Brush candy melts over the back of the bunny''s ears, head, and pick to give it more stability.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D4CF21F55287AA4491CD00683A8D8FF9', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 21, NULL, NULL, 2, NULL, 0, 'Let the candy melts dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF505BC1FF902540A7284CF68E1DC7D7', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 22, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of orange candy clay and shape them into carrots.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'411EECF7F6DA084684BB5CD81A0AFF45', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 23, NULL, NULL, 2, NULL, 0, 'Poke a hole in the larger end of the carrot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3EC8656FE3BA1045BB0FB9044CCA297D', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 24, NULL, NULL, 2, NULL, 0, 'Roll tiny pieces of green candy clay into carrot shapes as well.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'436B4EE7DC0BCA4D90BC7E91ABB30B25', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 25, NULL, NULL, 2, NULL, 0, 'Brush some water on the pointed end and insert it into the hole in the orange carrot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DEBD5793FD54D546958479BF57D6ADCE', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 26, NULL, NULL, 2, NULL, 0, 'Flatten out the green candy melts, then make indentations using a sharp knife so that the green candy looks like carrot greens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1CA960B5E2855489C0162A89F49C644', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 27, NULL, NULL, 2, NULL, 0, 'Use the back of the knife to create grooves around the carrot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EB493380CB84C4BB9A9FFDC5CFD522A', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 28, NULL, NULL, 2, NULL, 0, 'Pinch off small pieces of creamy white candy clay and roll them into 24 balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'349DD56F83C49A4FBA9F26E19720A75A', X'262A9F985B4E674091DA9F059A4DEC68', X'262A9F985B4E674091DA9F059A4DEC68', 29, NULL, NULL, 2, NULL, 0, 'Cut a slit halfway through each ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CA581C9D9081AF468FB0EFA94D44E5BF', X'10D145724D4374469661CDAC81BF56F4', X'10D145724D4374469661CDAC81BF56F4', 0, NULL, NULL, 2, NULL, 0, 'Insert a Reese''s Cup Bunny into a terra cotta pot cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'90294CC4C7C5A34AAB9D330C9562C549', X'10D145724D4374469661CDAC81BF56F4', X'10D145724D4374469661CDAC81BF56F4', 1, NULL, NULL, 2, NULL, 0, 'Place a white ball of candy clay so that the slit goes over the edge of the silicone terra cotta pot forming one bunny paw. Add a second paw.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1CEFB04BDC0786469420A330C05FC11E', X'10D145724D4374469661CDAC81BF56F4', X'10D145724D4374469661CDAC81BF56F4', 2, NULL, NULL, 2, NULL, 0, 'Poke a hole in the cupcake and insert the tip of a carrot in front of the bunny.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3B8FE1CE836EA14C8B5E55CD0244D860', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 0, NULL, NULL, 2, NULL, 0, 'Dust a cutting board and rolling pin with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1B154BE8BBEAFF48AFA50C16F0E8832C', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 1, NULL, NULL, 2, NULL, 0, 'Un-fold the puff pastry sheet on the cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E501B9C05C99264B8F06B683D4AA3C0C', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 2, NULL, NULL, 2, NULL, 0, 'Roll out the dough to about 12 inches by 14 inches.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E45D78897EC0F340878A2C19DAB3036F', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 3, NULL, NULL, 2, NULL, 0, 'Cut the dough sheet into six squares.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0AA87D41079CDE46B1DCB589BB0EF77E', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 4, NULL, NULL, 2, NULL, 0, 'Drape one square over the pumpkin cavity in the baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D004B7FEBD63E0478CDC6000586CBB40', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 5, NULL, NULL, 2, NULL, 0, 'Wiggle the dough into the cavity leaving the excess to hang over the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E654235D1D34CE49A56F381F2B25C271', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 6, NULL, NULL, 2, NULL, 0, 'Spoon in about 1/4 cup of pie filling.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9880FC1C2A5B884A93677C0AF645CE7E', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 7, NULL, NULL, 2, NULL, 0, 'Whisk together egg white and water to make an egg wash.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71A72658B584EA4CB1DCD09913D0FEFF', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 8, NULL, NULL, 2, NULL, 0, 'Fold the excess dough over the pie filling brushing it with egg wash as needed for the pieces to stick together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'257E2D32D8D7C64E8C71DCF80F6CA8E2', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 9, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'387B84045CDF02438661E14EF66B829B', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 10, NULL, NULL, 2, NULL, 0, 'Brush egg wash over the tops of the dough and sprinkle sugar over top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57A85D3AA44A5648AF0CD7013CB9E16F', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 11, NULL, NULL, 2, NULL, 0, 'Freeze for 15-20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3594D05E9BEA4F4791CCF6253EA55236', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 12, NULL, NULL, 2, NULL, 0, 'Meanwhile, preheat oven to 400 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD06AE5ABDE52746878C2CA95E991F40', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 13, NULL, NULL, 2, NULL, 0, 'Bake pies for 20-26 minutes until the tops are deep golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E32D9B199F792547A261E56E4E612429', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 14, NULL, NULL, 2, NULL, 0, 'Remove pies from pan immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA8184AF1893BF4CA0FB8D21BB91EB70', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', X'D3975038ABD72D4ABF1AA4ECB8A9EB42', 15, NULL, NULL, 2, NULL, 0, 'Let cool slightly before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9F1E6C36F7EE0409BFBFA91DDF1A4D3', X'4650AF32485B2441864A4A8C1D75883E', X'4650AF32485B2441864A4A8C1D75883E', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'35466A6BD4BDD045B9EB924B00C34940', X'4650AF32485B2441864A4A8C1D75883E', X'4650AF32485B2441864A4A8C1D75883E', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'16742CE3D53F1C46AA0EA1BEA71D8F85', X'4650AF32485B2441864A4A8C1D75883E', X'4650AF32485B2441864A4A8C1D75883E', 2, NULL, NULL, 2, NULL, 0, 'Stir flour, sugar, baking powder, and salt together in a mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'435D592BC9DC37409467177AB94A5046', X'4650AF32485B2441864A4A8C1D75883E', X'4650AF32485B2441864A4A8C1D75883E', 3, NULL, NULL, 2, NULL, 0, 'Pour in whipping cream and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE6296CB1659774DA25F9E8A05EDD642', X'4650AF32485B2441864A4A8C1D75883E', X'4650AF32485B2441864A4A8C1D75883E', 4, NULL, NULL, 2, NULL, 0, 'Pour out onto a flour dusted cutting board.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F97424D6E488BD4FA813EC6C60D5F8FF', X'4650AF32485B2441864A4A8C1D75883E', X'4650AF32485B2441864A4A8C1D75883E', 5, NULL, NULL, 2, NULL, 0, 'Knead for 30 seconds until the dough is smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'376C5FF730A7E343A0DC94E19E426903', X'4650AF32485B2441864A4A8C1D75883E', X'4650AF32485B2441864A4A8C1D75883E', 6, NULL, NULL, 2, NULL, 0, 'Roll out to 3/4 inch thickness and cut out daisies using a 4 or 5 inch cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF45D8702331A842BAA1715D0976DC62', X'4650AF32485B2441864A4A8C1D75883E', X'4650AF32485B2441864A4A8C1D75883E', 7, NULL, NULL, 2, NULL, 0, 'Re-roll once and cut out more daisies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37237CE4AEEE6E499AC064A48B7BB055', X'4650AF32485B2441864A4A8C1D75883E', X'4650AF32485B2441864A4A8C1D75883E', 8, NULL, NULL, 2, NULL, 0, 'Set daisies on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5300E8390E1DB849B725E21D6C52D66D', X'4650AF32485B2441864A4A8C1D75883E', X'4650AF32485B2441864A4A8C1D75883E', 9, NULL, NULL, 2, NULL, 0, 'Brush a green circle in the middle of each daisy then brush on yellow petals.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9FD0EE8BB16E9D40ACE1AB93B6B61D4D', X'4650AF32485B2441864A4A8C1D75883E', X'4650AF32485B2441864A4A8C1D75883E', 10, NULL, NULL, 2, NULL, 0, 'Or, press a small round cookie cutter in the center of each daisy then press a small tear drop cutter in the center of each petal.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8D2CDAC04EBD4447850EED5108377107', X'4650AF32485B2441864A4A8C1D75883E', X'4650AF32485B2441864A4A8C1D75883E', 11, NULL, NULL, 2, NULL, 0, 'Bake for 14-18 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5852427A582A6D4DB4B03E22DB2147E0', X'4650AF32485B2441864A4A8C1D75883E', X'4650AF32485B2441864A4A8C1D75883E', 12, NULL, NULL, 2, NULL, 0, 'Cut open and fill with Smokehouse BBQ.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3CFBE878364D04458FC26A8E28EFF9CA', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FC85CC1A7D3B4E4A9675ABD4B0A7E4FB', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 1, NULL, NULL, 2, NULL, 0, 'Unroll pizza dough onto a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32E0181939377149AE1101A49AA68A2A', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 2, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B2AA3E134930E644BB72CF83AA1DBB2A', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 3, NULL, NULL, 2, NULL, 0, 'Allow crust to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B27A0A6A5C76DA458C66B0B7AC9DE574', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 4, NULL, NULL, 2, NULL, 0, 'Cut out using the Fish Template.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E33BC56845E9304190C6BCA9E49B9FC4', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 5, NULL, NULL, 2, NULL, 0, 'Spread veggie cream cheese over fish shaped crust.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4225707C171A2408427664AA8563AE6', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 6, NULL, NULL, 2, NULL, 0, 'Finely slice carrots.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B5945940985B7644B6D53D94859E714A', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 7, NULL, NULL, 2, NULL, 0, 'Arrange carrots to form scales on the body of the fish.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEAB6091AD789643B96A487AF0FBA7F3', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 8, NULL, NULL, 2, NULL, 0, 'Cut red peppers into thin strips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B208687EBB3D749A1FA0E2EEDA229AD', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 9, NULL, NULL, 2, NULL, 0, 'Arrange peppers over fins and cut off excess using kitchen shears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'772AECE63CA9904A81FC3C67863B813C', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 10, NULL, NULL, 2, NULL, 0, 'Finely chop yellow pepper and sprinkle over face of fish.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA31F63549B4494E839220F526D812AD', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 11, NULL, NULL, 2, NULL, 0, 'Cut 2 large ovals from the white cheese or cream cheese. You can also pipe the cream cheese on, if you prefer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8BAD034C95277E4EB72B21EF6121210C', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 12, NULL, NULL, 2, NULL, 0, 'Cut one jumbo olive in half and set one piece on each white oval creating eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E4F35E68B51CA64DAFFCD5AD9769F5B1', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 13, NULL, NULL, 2, NULL, 0, 'Set eyes on fish''s face.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8B7E45D602F23D499390FA60B69E75F4', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 14, NULL, NULL, 2, NULL, 0, 'Cut two thin slivers from the other black olive to create eyebrows over the eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F240A31179BC904B8E01AED97D980946', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 15, NULL, NULL, 2, NULL, 0, 'Cut the stem from the spinach leaf or cut a green onion into a thin strip and create a mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54D82F52DC1E0641B84749D7A114710F', X'834CEA54C57122488E87371A2DB0BBD0', X'834CEA54C57122488E87371A2DB0BBD0', 16, NULL, NULL, 2, NULL, 0, 'Refrigerate until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45AB4CEEC85F1049A024251CD6467C6F', X'008BA55CF9365649960BB0A878EA5418', X'008BA55CF9365649960BB0A878EA5418', 0, NULL, NULL, 2, NULL, 0, 'Bring one inch of water in a large pot to a boil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B015046F381FE44A82C674D3AF8D2567', X'008BA55CF9365649960BB0A878EA5418', X'008BA55CF9365649960BB0A878EA5418', 1, NULL, NULL, 2, NULL, 0, 'Set eggs in a steamer basket and lower into pot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F414BC2ADBB87E48AAB5D4DA0CC56116', X'008BA55CF9365649960BB0A878EA5418', X'008BA55CF9365649960BB0A878EA5418', 2, NULL, NULL, 2, NULL, 0, 'Cover pot with lid and set timer for 13 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44450D3B73BD724791D657B16D424141', X'008BA55CF9365649960BB0A878EA5418', X'008BA55CF9365649960BB0A878EA5418', 3, NULL, NULL, 2, NULL, 0, 'Remove lid and take steamer basket out of the pot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'68FCB68C74D7E347AE63B7DF1231EC61', X'008BA55CF9365649960BB0A878EA5418', X'008BA55CF9365649960BB0A878EA5418', 4, NULL, NULL, 2, NULL, 0, 'Immediately place eggs in a bowl of ice water.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2ABEACF1786808498A63440A17684E4D', X'008BA55CF9365649960BB0A878EA5418', X'008BA55CF9365649960BB0A878EA5418', 5, NULL, NULL, 2, NULL, 0, 'Allow to cool for at least 20 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9FED3B262BF08D4C81403C7CF53383E4', X'008BA55CF9365649960BB0A878EA5418', X'008BA55CF9365649960BB0A878EA5418', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile cut carrots into slices and sticks.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'12AF9BAFC7FE5B49B21E17715D9D8D02', X'008BA55CF9365649960BB0A878EA5418', X'008BA55CF9365649960BB0A878EA5418', 7, NULL, NULL, 2, NULL, 0, 'Cut the slices using a small `E` cookie cutter (2 per egg).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F32F978C7EF74B458D0C6E174D23DF5A', X'008BA55CF9365649960BB0A878EA5418', X'008BA55CF9365649960BB0A878EA5418', 8, NULL, NULL, 2, NULL, 0, 'Cut the sticks so that each end is pointed ( 2 per egg).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AD4C5D0E28BEF4A86AC2B02ED967502', X'008BA55CF9365649960BB0A878EA5418', X'008BA55CF9365649960BB0A878EA5418', 9, NULL, NULL, 2, NULL, 0, 'Push a skewer into the center of each `E` creating a hole.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CB4FF73FF5DA26429744D6BECE56299B', X'008BA55CF9365649960BB0A878EA5418', X'008BA55CF9365649960BB0A878EA5418', 10, NULL, NULL, 2, NULL, 0, 'Poke a carrot stick into each `E` creating a chicken leg and foot.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5D735274FCBB0640AF1C2B65C4BE211A', X'008BA55CF9365649960BB0A878EA5418', X'008BA55CF9365649960BB0A878EA5418', 11, NULL, NULL, 2, NULL, 0, 'Remove eggs from ice water.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'44A4340EECF9A849B46E69C2ABA7C80A', X'008BA55CF9365649960BB0A878EA5418', X'008BA55CF9365649960BB0A878EA5418', 12, NULL, NULL, 2, NULL, 0, 'Poke two carrot chicken legs into each egg.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA69E3BC193BF545AED3913F6F36541F', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 0, NULL, NULL, 2, NULL, 0, 'Select 16 large plump and juicy raisins for the spider bodies and 16 very small raisins for the spider heads.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D8B3EF1B2E079744B7199D78B3E29708', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 1, NULL, NULL, 2, NULL, 0, 'Flatten the remaining raisins as thin as you can.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'23BE0C598B9E44489B76F29A46AA1912', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 2, NULL, NULL, 2, NULL, 0, 'Using a sharp knife, cut thin long strips out of the raisins for the spider legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'97D9E53EB0ADD74BB423118D963C1A0C', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 3, NULL, NULL, 2, NULL, 0, 'Create enough legs for all 16 of your spiders.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'57E58BF964D18640B65BF8287802F16B', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 4, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350°F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3C0D914208A06941A44048B5E4FF0BEA', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 5, NULL, NULL, 2, NULL, 0, 'Beat butter, brown sugar, and granulated sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9330D78F0D66A742882326E1668134E2', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 6, NULL, NULL, 2, NULL, 0, 'Add one egg at a time mixing to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'929C9104C8C673469A0F54E8239A8BA6', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 7, NULL, NULL, 2, NULL, 0, 'Add vanilla.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC8ED9D72FBA2D4594279735E18329A2', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 8, NULL, NULL, 2, NULL, 0, 'Whisk together the flour, nutmeg, cinnamon, salt, and baking powder.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BABA46D9F75D334DA302302891285CC3', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 9, NULL, NULL, 2, NULL, 0, 'Add to the wet ingredients and stir just to combine.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A3A3AA45D7470498F8D7AE184A355B1', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 10, NULL, NULL, 2, NULL, 0, 'Stir in the oats and 1 1/2 cups of raisins.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7C87339F2A6B44A90A54E8E85A6757F', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 11, NULL, NULL, 2, NULL, 0, 'Scoop out two thirds of a cup of cookie dough, roll into a ball, and place on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3342589A6DF69146AA5AE6AF43284185', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 12, NULL, NULL, 2, NULL, 0, 'Repeat creating a total of 16 cookies. Flatten the cookies slightly. These cookies are really big, which helps keep them moist and chewy on the inside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6A4C95257D16B94B8083F6AD32723716', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 13, NULL, NULL, 2, NULL, 0, 'Arrange your raisin spiders on top. Start by placing the eight legs on the cookie then put the large raisin body over top and add a head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'051A98C5A4ADC343B4CE9141BF9B76BD', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 14, NULL, NULL, 2, NULL, 0, 'Bake for 18 to 22 min. until the edges turn golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D91B71BFF75A4D4EA7761D3967A3F62D', X'7114C8A8611E2B43BCE5E9EBD99F08B0', X'7114C8A8611E2B43BCE5E9EBD99F08B0', 15, NULL, NULL, 2, NULL, 0, 'Allow the cookies to cool and then go have fun creeping out your friends and family.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6ABC9999A2E9DB4E8FCED553B01BD337', X'623259E1609FCC4586A686A4535C3260', X'623259E1609FCC4586A686A4535C3260', 0, NULL, NULL, 2, NULL, 0, 'Stir together the cream cheese and cheddar cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CDE60FDB359FD345B002F577EE4D70B7', X'623259E1609FCC4586A686A4535C3260', X'623259E1609FCC4586A686A4535C3260', 1, NULL, NULL, 2, NULL, 0, 'Reserve 2 tablespoons of this mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4D4EAB1923B2E440AB1EB2F3430DB06F', X'623259E1609FCC4586A686A4535C3260', X'623259E1609FCC4586A686A4535C3260', 2, NULL, NULL, 2, NULL, 0, 'Stir the bacon into the remaining cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B6839D84CA96744A0EC9AE980872B46', X'E3DED26EB0CD584EA6F3793F7464E72E', X'E3DED26EB0CD584EA6F3793F7464E72E', 0, NULL, NULL, 2, NULL, 0, 'Scoop out 1/2 tablespoon size scoops of the cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'059013A942B9FF4694D7F01F52BE14DE', X'E3DED26EB0CD584EA6F3793F7464E72E', X'E3DED26EB0CD584EA6F3793F7464E72E', 1, NULL, NULL, 2, NULL, 0, 'Roll into the small pieces of finely shredded cheddar cheese shaping into balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02556B161C548B44ADED1F63D42A052B', X'E3DED26EB0CD584EA6F3793F7464E72E', X'E3DED26EB0CD584EA6F3793F7464E72E', 2, NULL, NULL, 2, NULL, 0, 'Cut 2 tear drop shapes, 2 flower shapes, and one tiny triangle from the cheddar cheese for each cheese ball chick you have made.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8736520DF2948F4A97D1C5CBD6B98B86', X'E3DED26EB0CD584EA6F3793F7464E72E', X'E3DED26EB0CD584EA6F3793F7464E72E', 3, NULL, NULL, 2, NULL, 0, 'Brush orange food coloring over the triangles and flowers (or brush the coloring over the slice of cheese, allow to dry, then cut out the shapes).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FE4CC0252B51934598FEE5D728A34E29', X'E3DED26EB0CD584EA6F3793F7464E72E', X'E3DED26EB0CD584EA6F3793F7464E72E', 4, NULL, NULL, 2, NULL, 0, 'Cut thin slivers off the edges of the black olives and cut into tiny circles.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B38A386F87389746ADF39D3A0C9C74FB', X'E3DED26EB0CD584EA6F3793F7464E72E', X'E3DED26EB0CD584EA6F3793F7464E72E', 5, NULL, NULL, 2, NULL, 0, 'Attach two tear drop shaped wings to the sides of each cheese ball using the reserved cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'362C4D6A702F634388A1D8B483E0CDB8', X'E3DED26EB0CD584EA6F3793F7464E72E', X'E3DED26EB0CD584EA6F3793F7464E72E', 6, NULL, NULL, 2, NULL, 0, 'If needed, warm the reserved cheese ball mixture in the microwave on the defrost setting for about 10 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'65BA611E8290B94E9846AF535B252BD1', X'E3DED26EB0CD584EA6F3793F7464E72E', X'E3DED26EB0CD584EA6F3793F7464E72E', 7, NULL, NULL, 2, NULL, 0, 'Attach a triangle beak and two flower shaped feet to each cheese ball then attach two black olive eyes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'38FA93612350084B9177C34906905D15', X'E3DED26EB0CD584EA6F3793F7464E72E', X'E3DED26EB0CD584EA6F3793F7464E72E', 8, NULL, NULL, 2, NULL, 0, 'Store in the refrigerator in an airtight container until ready to serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'11F9865C3135E94EAEFA88A631FBA5AE', X'E3DED26EB0CD584EA6F3793F7464E72E', X'E3DED26EB0CD584EA6F3793F7464E72E', 9, NULL, NULL, 2, NULL, 0, 'Set cheese balls on crackers just before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'45A4A9BA2B826B4B8E627162326BA467', X'E6D77AE1513F564195B8B7718C3666CA', X'E6D77AE1513F564195B8B7718C3666CA', 0, NULL, NULL, 2, NULL, 0, 'Pour oil into a medium saucepan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8E8E72D42CBA44449F51ACFA3CCE2B70', X'E6D77AE1513F564195B8B7718C3666CA', X'E6D77AE1513F564195B8B7718C3666CA', 1, NULL, NULL, 2, NULL, 0, 'Heat over medium high heat to 350 degrees, adjusting the heat as needed to maintain the temperature of the oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A831448E34DC49438CFE0D0549469990', X'E6D77AE1513F564195B8B7718C3666CA', X'E6D77AE1513F564195B8B7718C3666CA', 2, NULL, NULL, 2, NULL, 0, 'Prick the bottoms of the mini tortilla boats using a fork.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'964FE509CB01194FBBE056597E215468', X'E6D77AE1513F564195B8B7718C3666CA', X'E6D77AE1513F564195B8B7718C3666CA', 3, NULL, NULL, 2, NULL, 0, 'Fry a few at a time, flipping over often, until crispy and golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6BEF48F11AF2F947B906F8EFEF7AE78B', X'E6D77AE1513F564195B8B7718C3666CA', X'E6D77AE1513F564195B8B7718C3666CA', 4, NULL, NULL, 2, NULL, 0, 'Remove from oil and set on a few layers of paper towels to allow the excess oil to drain off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AF14D5E210987144BD42EA597AFDE890', X'E6D77AE1513F564195B8B7718C3666CA', X'E6D77AE1513F564195B8B7718C3666CA', 5, NULL, NULL, 2, NULL, 0, 'Combine the cinnamon and sugar in a bowl that is large enough for one tortilla boat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F35376BE2F777944A3A6A33DBDA6C163', X'E6D77AE1513F564195B8B7718C3666CA', X'E6D77AE1513F564195B8B7718C3666CA', 6, NULL, NULL, 2, NULL, 0, 'Toss the tortilla boats in cinnamon sugar then set them aside to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'71F2CF66A8D9574782EED1CE6FEAB283', X'E6D77AE1513F564195B8B7718C3666CA', X'E6D77AE1513F564195B8B7718C3666CA', 7, NULL, NULL, 2, NULL, 0, 'Break up one of the tortilla boats into small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B409ACA3D8B26D419AF1BE5B677B183A', X'E6D77AE1513F564195B8B7718C3666CA', X'E6D77AE1513F564195B8B7718C3666CA', 8, NULL, NULL, 2, NULL, 0, 'Scoop ice cream into the fried tortilla boats.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'50E4D83B8F6C8C4CB831A4703972D938', X'E6D77AE1513F564195B8B7718C3666CA', X'E6D77AE1513F564195B8B7718C3666CA', 9, NULL, NULL, 2, NULL, 0, 'Top with some dulce de leche sauce or caramel sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC21F358957C36468AC66DE1943E4DB3', X'E6D77AE1513F564195B8B7718C3666CA', X'E6D77AE1513F564195B8B7718C3666CA', 10, NULL, NULL, 2, NULL, 0, 'Pipe or dollop on some whipped cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'962E4CFC005E2349895685EF15612CDF', X'E6D77AE1513F564195B8B7718C3666CA', X'E6D77AE1513F564195B8B7718C3666CA', 11, NULL, NULL, 2, NULL, 0, 'Sprinkle bits of cinnamon sugar tortilla onto each sundae.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'026F347257DFBE48BD8D74081FD8DD68', X'E6D77AE1513F564195B8B7718C3666CA', X'E6D77AE1513F564195B8B7718C3666CA', 12, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EEE64A20BDE5B48A8940FEA89FEC058', X'F6217C6FA0FEF24391638649D21A51DD', X'F6217C6FA0FEF24391638649D21A51DD', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'984D151EC871F646B2AEC88D016FCACD', X'F6217C6FA0FEF24391638649D21A51DD', X'F6217C6FA0FEF24391638649D21A51DD', 1, NULL, NULL, 2, NULL, 0, 'Spray 8 glass custard cups with non-stick spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8ADDC70DFDFE5E4BBB120D92D0D185F5', X'F022571C2F94FC4EB694FCB243372CDB', X'F022571C2F94FC4EB694FCB243372CDB', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F3DD02584815BD429ECD5108A835DF8F', X'F022571C2F94FC4EB694FCB243372CDB', X'F022571C2F94FC4EB694FCB243372CDB', 1, NULL, NULL, 2, NULL, 0, 'Wet 2 paper towels with water then ring out excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CFF5F1C909725945ABFB8D038C8CD7DB', X'F022571C2F94FC4EB694FCB243372CDB', X'F022571C2F94FC4EB694FCB243372CDB', 2, NULL, NULL, 2, NULL, 0, 'Set 8 corn tortillas in between the two paper towels.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6D31585510E9C144BE3209CB1A748F52', X'F022571C2F94FC4EB694FCB243372CDB', X'F022571C2F94FC4EB694FCB243372CDB', 3, NULL, NULL, 2, NULL, 0, 'Heat in the microwave on high for 30-45 seconds, just to soften the tortillas.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0B7ADF234B1114E9FE9521B4223B34D', X'F022571C2F94FC4EB694FCB243372CDB', X'F022571C2F94FC4EB694FCB243372CDB', 4, NULL, NULL, 2, NULL, 0, 'Brush both sides of the corn tortillas with oil.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3920090B76F98A4DA9C370BACA620C2A', X'F022571C2F94FC4EB694FCB243372CDB', X'F022571C2F94FC4EB694FCB243372CDB', 5, NULL, NULL, 2, NULL, 0, 'Press into a bowl shape in the custard cups, crimping as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'445AA70B56C622479BA1A3E77963EDA0', X'F022571C2F94FC4EB694FCB243372CDB', X'F022571C2F94FC4EB694FCB243372CDB', 6, NULL, NULL, 2, NULL, 0, 'Bake for 6 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'336220949F89C843AA3B3D3F23342B8E', X'F022571C2F94FC4EB694FCB243372CDB', X'F022571C2F94FC4EB694FCB243372CDB', 7, NULL, NULL, 2, NULL, 0, 'Remove tortillas from custard cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D1910C3571F2F84381248DF531DF4ACF', X'F022571C2F94FC4EB694FCB243372CDB', X'F022571C2F94FC4EB694FCB243372CDB', 8, NULL, NULL, 2, NULL, 0, 'Turn them upside down on a baking sheet and bake for 2-4 more minutes until the bottoms lightly brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C93780E44D3F743A759E1628DBA0963', X'F022571C2F94FC4EB694FCB243372CDB', X'F022571C2F94FC4EB694FCB243372CDB', 9, NULL, NULL, 2, NULL, 0, 'Remove and set right side up on baking pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C957C3EB5850A40A54CD40F74345572', X'F022571C2F94FC4EB694FCB243372CDB', X'F022571C2F94FC4EB694FCB243372CDB', 10, NULL, NULL, 2, NULL, 0, 'Reduce oven to 350 degrees.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60B00287A23D534687015237EE930F9B', X'8F00684AB61BDD4088C78F32898DC8A7', X'8F00684AB61BDD4088C78F32898DC8A7', 0, NULL, NULL, 2, NULL, 0, 'Heat oil in a skillet set over medium high heat just until shimmering.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC76D33ED85050459D17B708712753D5', X'8F00684AB61BDD4088C78F32898DC8A7', X'8F00684AB61BDD4088C78F32898DC8A7', 1, NULL, NULL, 2, NULL, 0, 'Add shallot and stir occasionally until light golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'96726BAE5C5033498668F720803054AD', X'8F00684AB61BDD4088C78F32898DC8A7', X'8F00684AB61BDD4088C78F32898DC8A7', 2, NULL, NULL, 2, NULL, 0, 'Stir together garlic, chili powder, coriander, cumin, salt, and sugar.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A0834C6B88165B4AA2EDEDF1167EBA13', X'8F00684AB61BDD4088C78F32898DC8A7', X'8F00684AB61BDD4088C78F32898DC8A7', 3, NULL, NULL, 2, NULL, 0, 'Add to cooked shallots and stir constantly for about 30 seconds, until fragrant.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E783D8EF30D9E04FB185585752C969D9', X'8F00684AB61BDD4088C78F32898DC8A7', X'8F00684AB61BDD4088C78F32898DC8A7', 4, NULL, NULL, 2, NULL, 0, 'Add chicken pieces and toss to coat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5216F106758CD54DBEADAB6FFC2F2D96', X'8F00684AB61BDD4088C78F32898DC8A7', X'8F00684AB61BDD4088C78F32898DC8A7', 5, NULL, NULL, 2, NULL, 0, 'Let cook, stirring occasionally, for 1 minute, then pour in the tomato sauce.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB455088D510D744BEF9B1CBCDB2398E', X'8F00684AB61BDD4088C78F32898DC8A7', X'8F00684AB61BDD4088C78F32898DC8A7', 6, NULL, NULL, 2, NULL, 0, 'Bring to a boil, then reduce heat to low and cook for 14 minutes, stirring as needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4598BCC128EDAA4CA53026671F1B0C1E', X'8F00684AB61BDD4088C78F32898DC8A7', X'8F00684AB61BDD4088C78F32898DC8A7', 7, NULL, NULL, 2, NULL, 0, 'Remove from heat and set aside until ready to assemble enchilada bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'524058825C8C6740B0365364E03F1D55', X'BF727766446FD54C80DE2B3A9B666204', X'BF727766446FD54C80DE2B3A9B666204', 0, NULL, NULL, 2, NULL, 0, 'Heat oil in skillet set over medium high heat until shimmering.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DDF7E91165CE774096B41C355FB178FB', X'BF727766446FD54C80DE2B3A9B666204', X'BF727766446FD54C80DE2B3A9B666204', 1, NULL, NULL, 2, NULL, 0, 'Add chopped poblano pepper and shallot and cook until peppers begin to blister and soften.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9A0C4F69B993E349969BF1E8AD223ADC', X'BF727766446FD54C80DE2B3A9B666204', X'BF727766446FD54C80DE2B3A9B666204', 2, NULL, NULL, 2, NULL, 0, 'Toss in garlic and corn and cook for 3 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'482AF10A2AAFA64D93651E0CFC48EE18', X'BF727766446FD54C80DE2B3A9B666204', X'BF727766446FD54C80DE2B3A9B666204', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E0620A44B6389C48B0DB79B8E18B60A3', X'E94DEF67624016448C4549D5A62025AC', X'E94DEF67624016448C4549D5A62025AC', 0, NULL, NULL, 2, NULL, 0, '');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'822C66CFB269B84CA659F00D65A5AB98', X'E94DEF67624016448C4549D5A62025AC', X'E94DEF67624016448C4549D5A62025AC', 1, NULL, NULL, 2, NULL, 0, 'Fill each corn tortilla bowl with about a tablespoon of the cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF4DD2F06928D34EB09F90D860DD19D9', X'E94DEF67624016448C4549D5A62025AC', X'E94DEF67624016448C4549D5A62025AC', 2, NULL, NULL, 2, NULL, 0, 'Stir about 2/3 cup of the cheese into the chicken then equally divide it among the corn tortilla bowls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'40E35171A5D4A94D8CF5AB0ACF877ADE', X'E94DEF67624016448C4549D5A62025AC', X'E94DEF67624016448C4549D5A62025AC', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle on Poblano pepper and corn mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCD662D76C1A8F45A6790EF051996F56', X'E94DEF67624016448C4549D5A62025AC', X'E94DEF67624016448C4549D5A62025AC', 4, NULL, NULL, 2, NULL, 0, 'Top with the remaining cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7A098506120DB84D9BF3AE8D9CD60EBA', X'E94DEF67624016448C4549D5A62025AC', X'E94DEF67624016448C4549D5A62025AC', 5, NULL, NULL, 2, NULL, 0, 'Bake for 8 minutes until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C2732C907C7DC40AFC73F168F21053A', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 0, NULL, NULL, 2, NULL, 0, 'Mix the flour, salt, and baking powder together in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2DBB1C2983DB26419DE9907F8769862D', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 1, NULL, NULL, 2, NULL, 0, 'Set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'02BC5DA0D40A944EAFDB8DDEC563A095', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 2, NULL, NULL, 2, NULL, 0, 'Cream butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2ADD7B1B55A55343809DF0685257A165', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 3, NULL, NULL, 2, NULL, 0, 'Stir in vanilla bean paste, and almond extract then mix in the eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F315CC3A21F671468BBA671B73BA6960', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 4, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients and stir just until blended.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ADD49AFCF4077F4981894BDD49D90DFD', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 5, NULL, NULL, 2, NULL, 0, 'Divide the dough into 4 equal parts (about 1 cup of dough per color).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0966E94BD6DC5A458E7264DA5F4B437F', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 6, NULL, NULL, 2, NULL, 0, 'Scoop out tablespoon size spoonfuls of dough and press into a whoopie pie pan creating a total of 8 of each color.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3DD8FA47EBCE7A41967CF51A204C000E', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 7, NULL, NULL, 2, NULL, 0, 'Refrigerate for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5C5B6649B7EE3046B3B1B48FA073FBED', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 8, NULL, NULL, 2, NULL, 0, 'Bake for 10-12 minutes until the edges of the cookies turn light brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'986DEE8F803D8C45936B4019CB9E3799', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 9, NULL, NULL, 2, NULL, 0, 'Allow to cool then remove from pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'785ABC7D1FD87E469C48228AD6916731', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 10, NULL, NULL, 2, NULL, 0, 'Scoop out tablespoons size spoonfuls of dough and press into individual cavities in brownie pop silicone molds, making 8 hat crowns of each color.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'24DA85374F9B9942863B55724F78AFFF', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 11, NULL, NULL, 2, NULL, 0, 'Use your finger to create a well in the center of each dough ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B35B88E4AB56CB47B615539A5CD3DC27', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 12, NULL, NULL, 2, NULL, 0, 'Press the dough up the sides of the mold, up to the fill line, about 1/4 inch from the top of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'430668A112EBD449B611FF51CBD408A8', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 13, NULL, NULL, 2, NULL, 0, 'Freeze molds for 15 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D6A7FA6342F70844843081FB7EFA5A1B', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 14, NULL, NULL, 2, NULL, 0, 'Bake for 14 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E6D0915C6745704FB25F173DF785E3EE', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 15, NULL, NULL, 2, NULL, 0, 'Remove from oven and use the handle of a wooden spoon to reshape the well in the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1754AD38A6A014479D92695D91E91657', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 16, NULL, NULL, 2, NULL, 0, 'Return to the oven for 6-8 minutes until cookies are baked through.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F08E70A1777CD49B24396260ACD0730', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 17, NULL, NULL, 2, NULL, 0, 'Reshape the well in each cookie if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48AF72BC69A3AC4083C470533F09EA58', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 18, NULL, NULL, 2, NULL, 0, 'Allow to cool completely, then remove from mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FABCF005BD88EC4FB22ED86C57F7A223', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 19, NULL, NULL, 2, NULL, 0, 'Pour melted white Candy Melts into a squeeze bottle or disposable pastry bag.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F72BCF47DC188F4491959C04AA1845DD', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 20, NULL, NULL, 2, NULL, 0, 'Turn a hat crown upside down and fill it with M&M Minis.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6C0CD3536B1A27459480993C1F98FE5D', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 21, NULL, NULL, 2, NULL, 0, 'Pipe a line of white Candy Melts around the rim of the hat crown cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5E4C346111C8F1488895B58A24CE2882', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 22, NULL, NULL, 2, NULL, 0, 'Immediately turn one of the round cookies upside down and center it over the hat crown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF5DEF16E812044BAA196CA854112F3D', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 23, NULL, NULL, 2, NULL, 0, 'Press the two cookies together then carefully, holding the top of the hat crown and the bottom of the round cookies, flip the whole thing over.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CBA1FC026CE5443924C0DD1742D207B', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 24, NULL, NULL, 2, NULL, 0, 'Set it on your work surface and pipe a line of white Candy Melts around the base of the hat crown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1EBF24A6DABC7E448A50F50E8021ED74', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 25, NULL, NULL, 2, NULL, 0, 'Pipe decorations on the hat brim.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A1C847BF454884DAFF7874077369BF5', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 26, NULL, NULL, 2, NULL, 0, 'Set aside to allow the candy to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'546950DD3E80CF43BBF54FF187405D0C', X'5435C6816DC9F3419FEAD36A7435A8C4', X'5435C6816DC9F3419FEAD36A7435A8C4', 27, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'31CA932895FD924DBD9BE249B7DC4E99', X'25C0CDDE4013F042BF3E28844A14DCC4', X'25C0CDDE4013F042BF3E28844A14DCC4', 0, NULL, NULL, 2, NULL, 0, 'Carefully split each cookie and scrape the peanut butter filling out into a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37ED7EC0B73C434F93DB421D71465261', X'25C0CDDE4013F042BF3E28844A14DCC4', X'25C0CDDE4013F042BF3E28844A14DCC4', 1, NULL, NULL, 2, NULL, 0, 'Combine the creamy peanut butter, butter, cream and salt in a bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8DC001FE99C4514CB4C3612F31508929', X'25C0CDDE4013F042BF3E28844A14DCC4', X'25C0CDDE4013F042BF3E28844A14DCC4', 2, NULL, NULL, 2, NULL, 0, 'Heat in microwave on high for 30 seconds, stir, heat 20 more seconds, then stir and heat 10-second increments afterward, if needed until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34EF54FEF56D764BA08A1220016E2656', X'25C0CDDE4013F042BF3E28844A14DCC4', X'25C0CDDE4013F042BF3E28844A14DCC4', 3, NULL, NULL, 2, NULL, 0, 'Stir in the powdered sugar then add the reserved peanut butter filling from the cookies.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7FF6F4BA3FE610488335A7BE10CB02C6', X'25C0CDDE4013F042BF3E28844A14DCC4', X'25C0CDDE4013F042BF3E28844A14DCC4', 4, NULL, NULL, 2, NULL, 0, 'Scoop out 30 balls of fudge using a #70 ice cream scoop or spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'227D0A5ABF2D6E49B9256720DD932ED0', X'25C0CDDE4013F042BF3E28844A14DCC4', X'25C0CDDE4013F042BF3E28844A14DCC4', 5, NULL, NULL, 2, NULL, 0, 'Roll into balls and press into a slipper shape on one end of a Nutter Butter Cookie.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'32C79704856A2340B64222E41EFD0E74', X'25C0CDDE4013F042BF3E28844A14DCC4', X'25C0CDDE4013F042BF3E28844A14DCC4', 6, NULL, NULL, 2, NULL, 0, 'Create an indentation in each peanut butter fudge ball making it look like a slipper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AE5F24A484F0074FB686D9076AE1CF5C', X'25C0CDDE4013F042BF3E28844A14DCC4', X'25C0CDDE4013F042BF3E28844A14DCC4', 7, NULL, NULL, 2, NULL, 0, 'Dip the top party of a cookie in melted pink candy melts and immediately sprinkle with grated pink candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DEF77106D6F4B143B706E69C8E19BABA', X'25C0CDDE4013F042BF3E28844A14DCC4', X'25C0CDDE4013F042BF3E28844A14DCC4', 8, NULL, NULL, 2, NULL, 0, 'Set aside to dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9D84702E8258454EA3843EDDA04A58B0', X'25C0CDDE4013F042BF3E28844A14DCC4', X'25C0CDDE4013F042BF3E28844A14DCC4', 9, NULL, NULL, 2, NULL, 0, 'Repeat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'838BEC7CC408ED46889FD1A0074D7525', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 0, NULL, NULL, 2, NULL, 0, 'Dip a clean, food-use-only, fine-tipped paintbrush into the pink candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E102ADABB766B1418EFBB6ADD6867003', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 1, NULL, NULL, 2, NULL, 0, 'Dab the candy over the rose cavity in the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BBB76F8E4635E4D819FA48A5002F4A6', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 2, NULL, NULL, 2, NULL, 0, 'Then dip another clean paintbrush into the light pink candy melts and paint on the inner triangle in both of the unicorn’s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'01DD906E3561844994458692039A69FE', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 3, NULL, NULL, 2, NULL, 0, 'Now begin to paint the mane by dabbing in dark blue or bright purple into the tuft of hair that is closest to the rose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C82CFAB0E25A13428424A72070E35631', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 4, NULL, NULL, 2, NULL, 0, 'Continue to paint the tufts of hair in this order: light blue, green, yellow, orange, and red.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5BC249328D588D49ADD978AD6A166288', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 5, NULL, NULL, 2, NULL, 0, 'Use a very fine paintbrush to paint the eyelashes using black or dark cocoa candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B9A855F16F102B4D93E3AF43844D3989', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 6, NULL, NULL, 2, NULL, 0, 'Pop the mold into the freezer for about 3 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55E419E09F6F81468D01DF587FDA15F9', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 7, NULL, NULL, 2, NULL, 0, 'Remove the mold and let it sit at room temperature for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E65D8452904E32429B016737D0CA5DCC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 8, NULL, NULL, 2, NULL, 0, 'Your mold must not be cold when you add in the white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'200141563895CA408BBEE24E638BB457', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 9, NULL, NULL, 2, NULL, 0, 'Make sure your white chocolate is not hot to the touch, then fill the entire mold with white chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BC2C514B9380F441A532ECE7D4C6CD8E', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 10, NULL, NULL, 2, NULL, 0, 'Carefully invert the mold over your bowl of white chocolate and allow the wet candy to drip out of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E4B033E677B5C4087BA744DFD00EE6A', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 11, NULL, NULL, 2, NULL, 0, 'Shake the mold and allow more to drip out until you just have a nice thin coating around the interior of the mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'48DFFBB191B01A4A8EA784FC9D297C78', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 12, NULL, NULL, 2, NULL, 0, 'Use a metal scraper to scrape off the excess so that the edge is nice and clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'446B041CD3E02443971D07136DE3175A', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 13, NULL, NULL, 2, NULL, 0, 'Insert a wooden popsicle stick into the wet white chocolate in the silicone unicorn mold.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E550E39FC3A5D24DA9B90C9B5D7C7908', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 14, NULL, NULL, 2, NULL, 0, 'Immediately insert a wood popsicle stick into the mold while the white chocolate is still wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'249AF9EB4AE64D49929377F712B446FE', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 15, NULL, NULL, 2, NULL, 0, 'Pop the mold in the freezer for about 5 minutes just until the white candy melts harden.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9FAD2A533094246A1499284789FE1EC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 16, NULL, NULL, 2, NULL, 0, 'Step-by-step images showing how to fill a white chocolate unicorn with cake ball filling before covering it in more white chocolate. Then brush edible gold luster dust over the horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C7FBD004E68E944BACBBF555ACCF602A', X'A995ABCB5B4B5E4899AE113ED79CA9AC', X'A995ABCB5B4B5E4899AE113ED79CA9AC', 17, NULL, NULL, 2, NULL, 0, 'Let the mold sit for about 10 minutes to come to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DAA04DD9B2EAEA4199A1E988AB18BB10', X'F94CFD0AC3047D47A014705C2D772633', X'F94CFD0AC3047D47A014705C2D772633', 0, NULL, NULL, 2, NULL, 0, 'Cut off the browned edges of the cake then crumble into really small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0EDF2680C066AD40840D287C8894D29B', X'F94CFD0AC3047D47A014705C2D772633', X'F94CFD0AC3047D47A014705C2D772633', 1, NULL, NULL, 2, NULL, 0, 'Stir in at least 1/4 cup of frosting until the cake ball filling is smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'808DEF55A77740468766B00C408103BB', X'F94CFD0AC3047D47A014705C2D772633', X'F94CFD0AC3047D47A014705C2D772633', 2, NULL, NULL, 2, NULL, 0, 'Press cake ball filling into the mold and, if necessary, under the popsicle stick.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29D5539D612F9041AF1C23A54005EF40', X'F94CFD0AC3047D47A014705C2D772633', X'F94CFD0AC3047D47A014705C2D772633', 3, NULL, NULL, 2, NULL, 0, 'Fill the mold to about 1/8th inch from the top edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8F3FAD9FB6BAA045B4D956F12D333C77', X'F94CFD0AC3047D47A014705C2D772633', X'F94CFD0AC3047D47A014705C2D772633', 4, NULL, NULL, 2, NULL, 0, 'Spoon white chocolate over top of the cake ball filling and spread it into an even layer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'87D8E8010624854EA5329AC125601475', X'F94CFD0AC3047D47A014705C2D772633', X'F94CFD0AC3047D47A014705C2D772633', 5, NULL, NULL, 2, NULL, 0, 'Scrape over top using a metal scraper to remove all of the excess chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF251050BA238D4081F8638DCA8AC661', X'F94CFD0AC3047D47A014705C2D772633', X'F94CFD0AC3047D47A014705C2D772633', 6, NULL, NULL, 2, NULL, 0, 'Freeze for about 5 minutes until the candy hardens.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C633E7BC64FBDF41A7D4793D648CBFBA', X'F94CFD0AC3047D47A014705C2D772633', X'F94CFD0AC3047D47A014705C2D772633', 7, NULL, NULL, 2, NULL, 0, 'Then unmold the unicorn and allow it to warm to room temperature.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CE68E1A2282B6545AC7A4E52CD050492', X'F94CFD0AC3047D47A014705C2D772633', X'F94CFD0AC3047D47A014705C2D772633', 8, NULL, NULL, 2, NULL, 0, 'Brush a light dusting of edible gold luster dust over the unicorn’s horn.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'92ECA1A8C7E8A64AB1398C4BDC7A7A17', X'8046819E29F0E74B894F7D6A8CB5F8BC', X'8046819E29F0E74B894F7D6A8CB5F8BC', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4347B14455D98C48A8BB6C62931B672A', X'8046819E29F0E74B894F7D6A8CB5F8BC', X'8046819E29F0E74B894F7D6A8CB5F8BC', 1, NULL, NULL, 2, NULL, 0, 'Line two cupcake pans with 21 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D0FEDAFF05BEF5488194CB2F4410FEF1', X'8046819E29F0E74B894F7D6A8CB5F8BC', X'8046819E29F0E74B894F7D6A8CB5F8BC', 2, NULL, NULL, 2, NULL, 0, 'Combine the white cake mix, instant pudding mix, sour cream, egg whites, vegetable oil, and water in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'626CBB8BEAB35347A7D5F333B4BFC21B', X'8046819E29F0E74B894F7D6A8CB5F8BC', X'8046819E29F0E74B894F7D6A8CB5F8BC', 3, NULL, NULL, 2, NULL, 0, 'Using an electric mixer, beat on low speed for 30 seconds to incorporate the ingredients then increase the speed to medium and beat for 1 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'887969A003F1CD47AF43153305C5736E', X'8046819E29F0E74B894F7D6A8CB5F8BC', X'8046819E29F0E74B894F7D6A8CB5F8BC', 4, NULL, NULL, 2, NULL, 0, 'Equally divide the cake batter among the 21 cupcake liners.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5A3BE31AB9CB1540872BB72D46608DF7', X'8046819E29F0E74B894F7D6A8CB5F8BC', X'8046819E29F0E74B894F7D6A8CB5F8BC', 5, NULL, NULL, 2, NULL, 0, 'Bake for 18-24 minutes until the cupcake springs back when your press a finger on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9494BDEEC9CBD14DAC15F44CC2A79EF4', X'8046819E29F0E74B894F7D6A8CB5F8BC', X'8046819E29F0E74B894F7D6A8CB5F8BC', 6, NULL, NULL, 2, NULL, 0, 'Remove the cupcakes from the oven and allow them to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'07BB4DF14D582648A29FFB045F2C707B', X'79855AE7E265AF4D9E7560A65A338BE5', X'79855AE7E265AF4D9E7560A65A338BE5', 0, NULL, NULL, 2, NULL, 0, 'Pour the strawberry pie filing into a strainer set over a large bowl and press down on the strawberries to allow the red gel to fall into the bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A8E1C14082348D40B24F2059966154A6', X'79855AE7E265AF4D9E7560A65A338BE5', X'79855AE7E265AF4D9E7560A65A338BE5', 1, NULL, NULL, 2, NULL, 0, 'You will use the gel to fill the cupcakes and the strawberries to make the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'82AF8C56E411E9419F6BF59E58692A49', X'79855AE7E265AF4D9E7560A65A338BE5', X'79855AE7E265AF4D9E7560A65A338BE5', 2, NULL, NULL, 2, NULL, 0, 'Spoon the gel into a pastry bag to pipe into the cupcake or just use a small spoon.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9F5FAB32C5D90347BF2913821070D90C', X'79855AE7E265AF4D9E7560A65A338BE5', X'79855AE7E265AF4D9E7560A65A338BE5', 3, NULL, NULL, 2, NULL, 0, 'Use a cupcake corer or knife to cut a well out of the center of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'54BCF71B785DB54CB9EFB074FB889CF0', X'79855AE7E265AF4D9E7560A65A338BE5', X'79855AE7E265AF4D9E7560A65A338BE5', 4, NULL, NULL, 2, NULL, 0, 'Fill each well in the cupcake with the strawberry pie filling. Reserve any remaining pie filling for another use.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F131F71368EBE54EB5601161AB0B213E', X'79855AE7E265AF4D9E7560A65A338BE5', X'79855AE7E265AF4D9E7560A65A338BE5', 5, NULL, NULL, 2, NULL, 0, 'Place the softened cream cheese in a large mixing bowl and beat, using an electric mixer. until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69FB4574868B054A86DED9BDC9C63A30', X'79855AE7E265AF4D9E7560A65A338BE5', X'79855AE7E265AF4D9E7560A65A338BE5', 6, NULL, NULL, 2, NULL, 0, 'Sift the powdered sugar into the bowl and add the reserved strawberries Beat well until the ingredients are fully combined and the strawberries are broken into small bits.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E1AC676957552D4C9D1008941090FBF6', X'79855AE7E265AF4D9E7560A65A338BE5', X'79855AE7E265AF4D9E7560A65A338BE5', 7, NULL, NULL, 2, NULL, 0, 'Add a teaspoon of lemon juice.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FF621C4C983FFE4DB2B77B601350EDEF', X'79855AE7E265AF4D9E7560A65A338BE5', X'79855AE7E265AF4D9E7560A65A338BE5', 8, NULL, NULL, 2, NULL, 0, 'Either add the whipped topping and beat until combined or fold in the freshly whipped whip cream.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3938F3F6B4152D4393EAEC6CD9CD581C', X'79855AE7E265AF4D9E7560A65A338BE5', X'79855AE7E265AF4D9E7560A65A338BE5', 9, NULL, NULL, 2, NULL, 0, 'Spoon the frosting into a large pastry bag fitted with a large star tip.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A24527C7B5E92C488FA7E1BDD44E24EE', X'79855AE7E265AF4D9E7560A65A338BE5', X'79855AE7E265AF4D9E7560A65A338BE5', 10, NULL, NULL, 2, NULL, 0, 'Pipe a big swirl of frosting on top of each cupcake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EC49984FC3DFA144B6B613C3523C8B2E', X'8BD883B9CFA51148A92F1F5078290294', X'8BD883B9CFA51148A92F1F5078290294', 0, NULL, NULL, 2, NULL, 0, 'Beat the cream cheese using an electric mixer or food processor until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'255D56B0A5C52349BCC255FA468BADAE', X'8BD883B9CFA51148A92F1F5078290294', X'8BD883B9CFA51148A92F1F5078290294', 1, NULL, NULL, 2, NULL, 0, 'Scoop out about 2 1/2 tablespoons of the cream cheese and place it in a disposable pastry bag or a sandwich size zip top bag and set aside until needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E72A40A31F0068429833CE933A49FBBE', X'8BD883B9CFA51148A92F1F5078290294', X'8BD883B9CFA51148A92F1F5078290294', 2, NULL, NULL, 2, NULL, 0, 'To the remaining cream cheese, add the Worcestershire sauce, seasoned salt, garlic powder, and 1 cup of the shredded white cheddar cheese and beat until well combined.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3800EEF1C7EA4B47AA5EF70709C671FC', X'8BD883B9CFA51148A92F1F5078290294', X'8BD883B9CFA51148A92F1F5078290294', 3, NULL, NULL, 2, NULL, 0, 'Toss in the finely chopped bacon and stir well.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DC0AE861BDDC544AB9FDD4924F67CDB4', X'8BD883B9CFA51148A92F1F5078290294', X'8BD883B9CFA51148A92F1F5078290294', 4, NULL, NULL, 2, NULL, 0, 'Scoop out 18 level tablespoons of the cheese ball mixture.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'27BE82A424AD2B4E8FD368FF26308023', X'8BD883B9CFA51148A92F1F5078290294', X'8BD883B9CFA51148A92F1F5078290294', 5, NULL, NULL, 2, NULL, 0, 'Drop each of the scoops onto a pile of the remaining shredded cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'766A975D796B3346A2C5C64BF035D677', X'8BD883B9CFA51148A92F1F5078290294', X'8BD883B9CFA51148A92F1F5078290294', 6, NULL, NULL, 2, NULL, 0, 'Roll into a ball coated in the cheese then set aside.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5CB2BC774A2C24499A9A10D36D790F24', X'8BD883B9CFA51148A92F1F5078290294', X'8BD883B9CFA51148A92F1F5078290294', 7, NULL, NULL, 2, NULL, 0, 'Use the reserved cream cheese to pipe a small round tail onto the top of each mini cheese ball.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'43973035BB6E37478AAE1890AFF0B0FA', X'8BD883B9CFA51148A92F1F5078290294', X'8BD883B9CFA51148A92F1F5078290294', 8, NULL, NULL, 2, NULL, 0, 'Cut 36 small (about 3/4 inch long) ovals out of the white cheese slices.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5EEDBA0BD6AA5439C5E58B6BBC9C54D', X'8BD883B9CFA51148A92F1F5078290294', X'8BD883B9CFA51148A92F1F5078290294', 9, NULL, NULL, 2, NULL, 0, 'Use a pink food coloring marker to draw four toes and one pad onto each of the ovals creating 36 bunny feet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF0EAAB76D29D24BB0C1424AA8CBD7E7', X'8BD883B9CFA51148A92F1F5078290294', X'8BD883B9CFA51148A92F1F5078290294', 10, NULL, NULL, 2, NULL, 0, 'Use a dab of cream cheese to attach two bunny feet to each of the mini cheese balls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0E5D7E57D6E59F42A8E004D1CDDD22E6', X'8BD883B9CFA51148A92F1F5078290294', X'8BD883B9CFA51148A92F1F5078290294', 11, NULL, NULL, 2, NULL, 0, 'If desired, use some cream cheese to attach the Mini Cheese Ball Bunnies to round crackers just before serving.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'386C339E5F8A67459A6458AB2D9DB7A5', X'14C4C148B0159F4589A5E7C44CD76BCC', X'14C4C148B0159F4589A5E7C44CD76BCC', 0, NULL, NULL, 2, NULL, 0, 'Dip strawberries in melted and tempered pure white chocolate or in melted white confectionery coating (candy melts).');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BF35C9E715823045A126E68E78D411BA', X'14C4C148B0159F4589A5E7C44CD76BCC', X'14C4C148B0159F4589A5E7C44CD76BCC', 1, NULL, NULL, 2, NULL, 0, 'Allow the excess chocolate to drip off.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B90D79AF323DE44FAE58A7701EB09991', X'14C4C148B0159F4589A5E7C44CD76BCC', X'14C4C148B0159F4589A5E7C44CD76BCC', 2, NULL, NULL, 2, NULL, 0, 'Set on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FA66E711EF9CD349BA6FA385EE021BE4', X'14C4C148B0159F4589A5E7C44CD76BCC', X'14C4C148B0159F4589A5E7C44CD76BCC', 3, NULL, NULL, 2, NULL, 0, 'If desired, allow the white chocolate to puddle at the bottom of the strawberry for a few seconds, then lift up the berry and place it on a clean spot on the parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'966B54F4A996B5419E1D6840D5E2FBF5', X'14C4C148B0159F4589A5E7C44CD76BCC', X'14C4C148B0159F4589A5E7C44CD76BCC', 4, NULL, NULL, 2, NULL, 0, 'Chill in the fridge (for 3-5 minutes) if using candy melts or let air dry (for about 10 minutes) if using pure white chocolate just until firm.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6328174E4DE6BF4CB9820A2F30462C71', X'4235016F5DD94F42AA0FA0C8B9E9DEA0', X'4235016F5DD94F42AA0FA0C8B9E9DEA0', 0, NULL, NULL, 2, NULL, 0, 'EYES and NOSE: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two small circles and one 1/2 inch heart for each polar bear using a small plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0683027B4AC99548A111A37C2F97B9F4', X'4235016F5DD94F42AA0FA0C8B9E9DEA0', X'4235016F5DD94F42AA0FA0C8B9E9DEA0', 1, NULL, NULL, 2, NULL, 0, 'EARS: Cut a curved sliver off of two white chocolate wafers for each polar bear or create ears using 1/4 inch thick white modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'53FCCD34F1947845898F91DC9C00C4B6', X'4235016F5DD94F42AA0FA0C8B9E9DEA0', X'4235016F5DD94F42AA0FA0C8B9E9DEA0', 2, NULL, NULL, 2, NULL, 0, 'BOW: Cut two small hearts and one small circle out of pink or red modeling chocolate. Set the hearts together with the points touching then attach the circle over top using water or clear piping gel to `glue` the bow together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'17268D7313AB3D4DA0AE98B62EB92D24', X'4235016F5DD94F42AA0FA0C8B9E9DEA0', X'4235016F5DD94F42AA0FA0C8B9E9DEA0', 3, NULL, NULL, 2, NULL, 0, 'Dip the white chocolate coated strawberries into white chocolate again and press a fork up against the chocolate then pull away quickly. Do this all over the berry, over and over again, until spikes of fur start to form.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2796B6D85770FE47B942B34ACB25F930', X'4235016F5DD94F42AA0FA0C8B9E9DEA0', X'4235016F5DD94F42AA0FA0C8B9E9DEA0', 4, NULL, NULL, 2, NULL, 0, 'Attach two candy ears on the top edge of the strawberry on opposite sides of the green stem using melted white chocolate. Then add more white chocolate over the ears and create spikes of fur on them too.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE899A2CD4B43E46BE0CFA1DD313C50D', X'4235016F5DD94F42AA0FA0C8B9E9DEA0', X'4235016F5DD94F42AA0FA0C8B9E9DEA0', 5, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add a nose to the tip of the strawberry and two eyes above it. Add a bow if making female polar bears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2148A6637938224389A2697FA2E3BF60', X'003FDBED7F5D234F80F3AFD5AD4492A9', X'003FDBED7F5D234F80F3AFD5AD4492A9', 0, NULL, NULL, 2, NULL, 0, 'NOSE and FUR PATCHES: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two ovals using a plunger cutter and cut out a small 1/2 inch heart using a heart plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB7DE16E34B51346B6A92BEA86FE5EB5', X'003FDBED7F5D234F80F3AFD5AD4492A9', X'003FDBED7F5D234F80F3AFD5AD4492A9', 1, NULL, NULL, 2, NULL, 0, 'EARS: You can cut a small sliver off two dark chocolate wafers to create the ears or you can cut out black modeling chocolate circles (with a curved notch removed) to create the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60C7AE21275FDD4492E1EC1D18EF7A20', X'003FDBED7F5D234F80F3AFD5AD4492A9', X'003FDBED7F5D234F80F3AFD5AD4492A9', 2, NULL, NULL, 2, NULL, 0, 'BOW: Roll out red modeling chocolate to about 1/8th inch thickness and cut out two small hearts and one small circle. Set the points of the hearts together then use either water or clear piping gel to attach the circle over the intersection of the points creating a bow.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2952B598E68D5B418C2EC6836F4A9703', X'003FDBED7F5D234F80F3AFD5AD4492A9', X'003FDBED7F5D234F80F3AFD5AD4492A9', 3, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add two ears on the top edge of the strawberry, a nose to the tip of the strawberry and two fur patches above it off to the side. Add a bow in front of an ear to make panda bear girls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'455CAEB3C7E4F74394E2697738FACDAC', X'9F1A982597EBB144BAE17282108A6B6B', X'9F1A982597EBB144BAE17282108A6B6B', 0, NULL, NULL, 2, NULL, 0, 'Dip strawberries in melted and tempered pure white chocolate or in melted white confectionery coating (candy melts).');
