INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC837B29FC765D42808006716C5EC144', X'BC81599BA29D4248AB6595A8DA98F5A9', X'BC81599BA29D4248AB6595A8DA98F5A9', 4, NULL, NULL, 2, NULL, 0, 'Attach two candy ears on the top edge of the strawberry on opposite sides of the green stem using melted white chocolate. Then add more white chocolate over the ears and create spikes of fur on them too.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'60F52121030A8D47B4A82D1D606E86F0', X'BC81599BA29D4248AB6595A8DA98F5A9', X'BC81599BA29D4248AB6595A8DA98F5A9', 5, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add a nose to the tip of the strawberry and two eyes above it. Add a bow if making female polar bears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3F7DA3C103B5CE40B5879AB0E33EABB4', X'32BB3D5A0F3BE640BB67B23FE71FBF28', X'32BB3D5A0F3BE640BB67B23FE71FBF28', 0, NULL, NULL, 2, NULL, 0, 'NOSE and FUR PATCHES: Roll out some black modeling chocolate to about 1/8th inch thickness and cut out two ovals using a plunger cutter and cut out a small 1/2 inch heart using a heart plunger cutter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2B5CC74798528749986B9CB22DB00DBE', X'32BB3D5A0F3BE640BB67B23FE71FBF28', X'32BB3D5A0F3BE640BB67B23FE71FBF28', 1, NULL, NULL, 2, NULL, 0, 'EARS: You can cut a small sliver off two dark chocolate wafers to create the ears or you can cut out black modeling chocolate circles (with a curved notch removed) to create the ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A5E69EF90B770A418DEE860E1E552968', X'32BB3D5A0F3BE640BB67B23FE71FBF28', X'32BB3D5A0F3BE640BB67B23FE71FBF28', 2, NULL, NULL, 2, NULL, 0, 'BOW: Roll out red modeling chocolate to about 1/8th inch thickness and cut out two small hearts and one small circle. Set the points of the hearts together then use either water or clear piping gel to attach the circle over the intersection of the points creating a bow.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0217AFD65494F44E9C2E37BEBAEAFE92', X'32BB3D5A0F3BE640BB67B23FE71FBF28', X'32BB3D5A0F3BE640BB67B23FE71FBF28', 3, NULL, NULL, 2, NULL, 0, 'Attach the modeling chocolate decorations to the strawberry using the remaining, melted, white chocolate. Add two ears on the top edge of the strawberry, a nose to the tip of the strawberry and two fur patches above it off to the side. Add a bow in front of an ear to make panda bear girls.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EEB9755D91CF4C44B818A61EBF2D3D08', X'1B72197FFDD3E04197C3146C7D40F607', X'1B72197FFDD3E04197C3146C7D40F607', 0, NULL, NULL, 2, NULL, 0, 'Allow the cake ingredients to sit at room temperature for about an hour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'75D84BF8B46EB542A680813AA1155B81', X'1B72197FFDD3E04197C3146C7D40F607', X'1B72197FFDD3E04197C3146C7D40F607', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees Fahrenheit.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'078F51045693A84DABA9279C9AC0FBBA', X'1B72197FFDD3E04197C3146C7D40F607', X'1B72197FFDD3E04197C3146C7D40F607', 2, NULL, NULL, 2, NULL, 0, 'Brush two 6-inch cake pans with butter then dust with flour and line with a round of parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'FEA483C8C6389B4BB01882B6268B05B0', X'1B72197FFDD3E04197C3146C7D40F607', X'1B72197FFDD3E04197C3146C7D40F607', 3, NULL, NULL, 2, NULL, 0, 'Brush 4 cupcake cavities in a muffin tin with butter then dust with flour.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CD637A6A964E744392258F4997C22C93', X'1B72197FFDD3E04197C3146C7D40F607', X'1B72197FFDD3E04197C3146C7D40F607', 4, NULL, NULL, 2, NULL, 0, 'Combine the cake mix, water, oil, egg whites, sour cream, and pudding mix together in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6C5962A9152CB49B52C14EFCFC72DD8', X'1B72197FFDD3E04197C3146C7D40F607', X'1B72197FFDD3E04197C3146C7D40F607', 5, NULL, NULL, 2, NULL, 0, 'Use an electric mixer to beat on low for 30 seconds then increase speed to medium and beat for 1 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'140D5EDBC9E26543B6DCFC7E63E68F26', X'1B72197FFDD3E04197C3146C7D40F607', X'1B72197FFDD3E04197C3146C7D40F607', 6, NULL, NULL, 2, NULL, 0, 'Fill 4 cupcake cavities about 2/3''s full with batter.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BE296E835616F9489816E1347CA589FF', X'1B72197FFDD3E04197C3146C7D40F607', X'1B72197FFDD3E04197C3146C7D40F607', 7, NULL, NULL, 2, NULL, 0, 'Divide the remaining batter among the two 6-inch cake pans.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A6E76CA66C765E4ABEF090184A725CA6', X'1B72197FFDD3E04197C3146C7D40F607', X'1B72197FFDD3E04197C3146C7D40F607', 8, NULL, NULL, 2, NULL, 0, 'Bake the cupcakes and cakes for 18-26 minutes removing the cupcakes as soon as they are done at around 18-20 minutes. Check the cakes at around the 22 minute mark.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5470340ED36C814C93294B2D4C55228E', X'1B72197FFDD3E04197C3146C7D40F607', X'1B72197FFDD3E04197C3146C7D40F607', 9, NULL, NULL, 2, NULL, 0, 'A toothpick inserted into the cakes should come out with just a few moist crumbs. Or the cakes should spring back when you press a finger on top.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F088FE2DD5527C478C6F8FA16AE2B39D', X'1B72197FFDD3E04197C3146C7D40F607', X'1B72197FFDD3E04197C3146C7D40F607', 10, NULL, NULL, 2, NULL, 0, 'Allow the cakes and cupcakes to cool for 10 minutes in their pans then remove them and set them on a cooling rack. Peel off the parchment paper once the cakes are cooled completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4B4014F403FE5947B20D9D28F8FC3FBD', X'1B72197FFDD3E04197C3146C7D40F607', X'1B72197FFDD3E04197C3146C7D40F607', 11, NULL, NULL, 2, NULL, 0, 'Place cakes forming two bears on a large cake board and cut each cupcake at a curve so that they fit up against the cake to form the panda bear''s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1C4C18F1D97DEF46B6ADBCC5C056D7F2', X'563AF1F1D90C0E46B6C32693B0657417', X'563AF1F1D90C0E46B6C32693B0657417', 0, NULL, NULL, 2, NULL, 0, 'Beat the shortening and butter together until well combined using an electric mixer.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B669B74BB3624E43A217A01959319989', X'563AF1F1D90C0E46B6C32693B0657417', X'563AF1F1D90C0E46B6C32693B0657417', 1, NULL, NULL, 2, NULL, 0, 'Add the powdered sugar and beat until light and fluffy.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'77E0445699A28B4EA1848F4E80A3751C', X'563AF1F1D90C0E46B6C32693B0657417', X'563AF1F1D90C0E46B6C32693B0657417', 2, NULL, NULL, 2, NULL, 0, 'Add the vanilla and 3 tablespoons of the milk and beat until fully incorporated and the frosting is smooth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A54910C9C6066D4F97A3FAD9BC424F87', X'563AF1F1D90C0E46B6C32693B0657417', X'563AF1F1D90C0E46B6C32693B0657417', 3, NULL, NULL, 2, NULL, 0, 'Add more milk as needed to make the frosting soft enough to pipe through a star tip yet hold it''s shape.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70F5973E3B2BF14C8696C7A40917B7A9', X'563AF1F1D90C0E46B6C32693B0657417', X'563AF1F1D90C0E46B6C32693B0657417', 4, NULL, NULL, 2, NULL, 0, 'Spoon 2/3 cup of the frosting into another bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A3E381C993617F469CCE45F774A4C90A', X'563AF1F1D90C0E46B6C32693B0657417', X'563AF1F1D90C0E46B6C32693B0657417', 5, NULL, NULL, 2, NULL, 0, 'Stir the cocoa powder into that small bowl of frosting then color using black food coloring and add more milk, if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E8E5847FFD0A044BAD48AF84ED1D2A66', X'94E16D8F2E4F8B41885F1AADDDCC915A', X'94E16D8F2E4F8B41885F1AADDDCC915A', 0, NULL, NULL, 2, NULL, 0, 'Roll out the black, white, and pink modeling chocolate to about 1/8th inch thickness on a silicone mat using a silicone rolling pin.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CCB0B93E7360DC4DABD52C54F3FA1211', X'94E16D8F2E4F8B41885F1AADDDCC915A', X'94E16D8F2E4F8B41885F1AADDDCC915A', 1, NULL, NULL, 2, NULL, 0, 'Cut four 2 1/4 inch by 1 3/4 inch ovals from the black modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0160E773AA62594E94FC57D5DB48CCFB', X'94E16D8F2E4F8B41885F1AADDDCC915A', X'94E16D8F2E4F8B41885F1AADDDCC915A', 2, NULL, NULL, 2, NULL, 0, 'Cut four 1 1/2 inch circles from the white, four small 1/4 inch hearts from the white and four 1 inch circles from the black.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'550C9DAEED1CA74E9CB76F0581235EA3', X'94E16D8F2E4F8B41885F1AADDDCC915A', X'94E16D8F2E4F8B41885F1AADDDCC915A', 3, NULL, NULL, 2, NULL, 0, 'Attach one white circle to one black oval using a thin layer of water or clear piping gel.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2048FACF63377445AF34B0D9D97ED2D7', X'94E16D8F2E4F8B41885F1AADDDCC915A', X'94E16D8F2E4F8B41885F1AADDDCC915A', 4, NULL, NULL, 2, NULL, 0, 'Attach one black circle on top of the white circle then attach one small white heart along one edge of the black circle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DBCB672BF7EE494EB6ABEC33FE103F0A', X'94E16D8F2E4F8B41885F1AADDDCC915A', X'94E16D8F2E4F8B41885F1AADDDCC915A', 5, NULL, NULL, 2, NULL, 0, 'Repeat creating 4 eyes with all the hearts pointing up or down in one direction.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'61F33103A1134F499D42DA812BDCA643', X'94E16D8F2E4F8B41885F1AADDDCC915A', X'94E16D8F2E4F8B41885F1AADDDCC915A', 6, NULL, NULL, 2, NULL, 0, 'Cut out two 1 1/2 inch long heart shaped noses from the black modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'89ED6D7B9D3E0A4A8BD3B070214D223E', X'94E16D8F2E4F8B41885F1AADDDCC915A', X'94E16D8F2E4F8B41885F1AADDDCC915A', 7, NULL, NULL, 2, NULL, 0, 'Cut two 1 inch hearts and one 1/2 inch circle from the pink modeling chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'8EA381EC55C5534E97B7A2D405CD4EE5', X'94E16D8F2E4F8B41885F1AADDDCC915A', X'94E16D8F2E4F8B41885F1AADDDCC915A', 8, NULL, NULL, 2, NULL, 0, 'Place the two points of the heart together then attach the circle over the top of the tips using water or piping gel to form a cute bow.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1CE181E4A77274E973D4FECF8D500D0', X'CBD5A03B48C3CB449DF78C07690694CD', X'CBD5A03B48C3CB449DF78C07690694CD', 0, NULL, NULL, 2, NULL, 0, 'Attach two eyes and a nose using frosting to each round cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'368CE39370A1BD4F92E392ABEAA4E847', X'CBD5A03B48C3CB449DF78C07690694CD', X'CBD5A03B48C3CB449DF78C07690694CD', 1, NULL, NULL, 2, NULL, 0, 'Pipe white frosting using a star tip over the entire surface of the round cakes around the eyes and nose.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'34FE6E5B6E116F4FAD78C89DC3AEB251', X'CBD5A03B48C3CB449DF78C07690694CD', X'CBD5A03B48C3CB449DF78C07690694CD', 2, NULL, NULL, 2, NULL, 0, 'Pipe black frosting stars over the cupcake ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'263F71D6BD1CE141B255614EF1CFBB8E', X'CBD5A03B48C3CB449DF78C07690694CD', X'CBD5A03B48C3CB449DF78C07690694CD', 3, NULL, NULL, 2, NULL, 0, 'Press the pink modeling chocolate bow just below one ear of one of the panda cakes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'950A08B8C5539D40BF753F24D4BD6889', X'CBD5A03B48C3CB449DF78C07690694CD', X'CBD5A03B48C3CB449DF78C07690694CD', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2F22335AB5994A449D1755682DA414A6', X'EA668DDDD5CEE2439A3068F86E2137FF', X'EA668DDDD5CEE2439A3068F86E2137FF', 0, NULL, NULL, 2, NULL, 0, 'Pour the chocolate into a small bowl or glass.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BB3163F97E74484AA2B2FC7E85AC2089', X'EA668DDDD5CEE2439A3068F86E2137FF', X'EA668DDDD5CEE2439A3068F86E2137FF', 1, NULL, NULL, 2, NULL, 0, 'Dip one strawberry into the chocolate then set it on a parchment paper lined baking sheet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6752362420BD454CA1AD87C11E79168E', X'EA668DDDD5CEE2439A3068F86E2137FF', X'EA668DDDD5CEE2439A3068F86E2137FF', 2, NULL, NULL, 2, NULL, 0, 'Take a fork and press it into the chocolate, then pull up and away quickly creating spikes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'C5ECFC1797EC5249A8166D3BFB65E12B', X'EA668DDDD5CEE2439A3068F86E2137FF', X'EA668DDDD5CEE2439A3068F86E2137FF', 3, NULL, NULL, 2, NULL, 0, 'Repeat this process until the entire chocolate strawberry looks furry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'938EBBF6E0CF5D48B5AEE5D47698255B', X'EA668DDDD5CEE2439A3068F86E2137FF', X'EA668DDDD5CEE2439A3068F86E2137FF', 4, NULL, NULL, 2, NULL, 0, 'Immediate press two candy eyes and an M&M nose onto the chocolate.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A47DC18F299F8F419772DA6CE78AB06D', X'EA668DDDD5CEE2439A3068F86E2137FF', X'EA668DDDD5CEE2439A3068F86E2137FF', 5, NULL, NULL, 2, NULL, 0, 'Dip the cut edge of two chocolate wafers in chocolate and attach them to either side of the top of the strawberry creating the bear''s ears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'060805900F15204A8E26D716BF8D8328', X'EA668DDDD5CEE2439A3068F86E2137FF', X'EA668DDDD5CEE2439A3068F86E2137FF', 6, NULL, NULL, 2, NULL, 0, 'Let the strawberry air dry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E9EF0A85C4F25E45BB314137E26082F7', X'EA668DDDD5CEE2439A3068F86E2137FF', X'EA668DDDD5CEE2439A3068F86E2137FF', 7, NULL, NULL, 2, NULL, 0, 'Repeat dipping all 16 bears.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CEB5E54E02FBEB47838A91ED0F37970C', X'EA668DDDD5CEE2439A3068F86E2137FF', X'EA668DDDD5CEE2439A3068F86E2137FF', 8, NULL, NULL, 2, NULL, 0, 'If desired, brush a second layer of chocolate over the bottom of each strawberry to seal in the berry.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'10507FB2DD993942B5F1201FD2112924', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BD50BE00F07D074BBA764C92532E670C', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 1, NULL, NULL, 2, NULL, 0, 'Line a 9x13 inch baking pan with non-stick tin foil, parchment paper, or baking spray.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C348EFF0A567C4CBC293CEAD8507DDC', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 2, NULL, NULL, 2, NULL, 0, 'Combine the cake mix, graham cracker crumbs, water, sour cream, oil, and eggs in a large mixing bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3D8F5762C8E2EC42B7BA05757587BAC4', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 3, NULL, NULL, 2, NULL, 0, 'Beat on low speed using an electric mixer for 30 seconds.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A83249A5A84347458A0AFD5030D4507F', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 4, NULL, NULL, 2, NULL, 0, 'Increase the speed to medium and beat for 1 1/2 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ACDF424E18834041A32E212ACE8AA0BF', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 5, NULL, NULL, 2, NULL, 0, 'Fold in the chocolate chips.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CC996473C2E2934AB1B9BB966E0E0058', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 6, NULL, NULL, 2, NULL, 0, 'Pour batter into prepared pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E62DC3EC8AFEAF4F8A5BCF92159D965B', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 7, NULL, NULL, 2, NULL, 0, 'Bake for 28-32 minutes until a toothpick inserted into the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F8B2D82B6E0CF4F9A5B7B4C3F82DFBB', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 8, NULL, NULL, 2, NULL, 0, 'Remove from oven, and set pan on a cooling rack and allow cake to cool completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DF3044ACE435D047870D76AB6E66617F', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 9, NULL, NULL, 2, NULL, 0, 'Remove cake from pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'70955381AC679A479EBEF13A59F1C805', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 10, NULL, NULL, 2, NULL, 0, 'Cut cake in half so you have two 9 inch by 6 1/2 inch pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D58EE590BD81A841AD5C3980CF09F6E4', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 11, NULL, NULL, 2, NULL, 0, 'One piece will be the unicorn''s body.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'303EDB6E614B8A4AB060B7A10AD39184', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 12, NULL, NULL, 2, NULL, 0, 'Cut the other piece in half so you have two 6 1/2 by 4 1/2 inch pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CF984513E1E96442BED7FDE0181F438C', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 13, NULL, NULL, 2, NULL, 0, 'One piece will be the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2D773266E06C56459CD53F705F145FC7', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 14, NULL, NULL, 2, NULL, 0, 'Cut two 2 inch by 3 inch pieces for the unicorn''s legs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36F296934245A84EABC649296F23A0C9', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 15, NULL, NULL, 2, NULL, 0, 'Discard or eat the remaining pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'EF847B0238E28D478F90AD568803058F', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 16, NULL, NULL, 2, NULL, 0, 'Assemble the cake by placing the 9 inch by 6 1/2 inch cake in the lower half of your cake board forming the unicorn''s body.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0783E76CF73B3B4587B8C2B7C85C6898', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 17, NULL, NULL, 2, NULL, 0, 'Set the 6 1/2 inch by 4 1/2 inch piece about 2 1/2 inches from the top edge of the larger cake forming the unicorn''s head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C9C41DD75D3D3499932A9F1388152C8', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 18, NULL, NULL, 2, NULL, 0, 'Place the two leg pieces under the body piece about 3/4 of an inch from either edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F7187C56E6697A4A8B7203510975A727', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 19, NULL, NULL, 2, NULL, 0, 'Mix together the marshmallow frosting ingredients.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E2B76F09783B6B4D9C2D64E3A5759C59', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 20, NULL, NULL, 2, NULL, 0, 'Spread the white frosting over the body cake.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'162A85331D520840B359B51522867DAA', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 21, NULL, NULL, 2, NULL, 0, 'Mix together the black marshmallow frosting ingredients, adding a tiny bit of water, as needed to thin out the frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'9CDB203CEFA81F47B8EBCD939E7D33F5', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 22, NULL, NULL, 2, NULL, 0, 'Frost the legs using the black frosting.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'09331A1E319BA2469915E06A3B86BF4F', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 23, NULL, NULL, 2, NULL, 0, 'For the head, brush black frosting onto about 3/4 of the head piece, curving the back edge.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1F5F6434E99BA548AD48285CCDA662D7', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 24, NULL, NULL, 2, NULL, 0, 'Add white frosting on the back part of the head.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'846CA94E9343A640900A120D288061F9', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 25, NULL, NULL, 2, NULL, 0, 'Pour the remaining white and black frosting into individual pastry or zip top bags.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'5F34F564F48FB7478F13612CF0A869B4', X'C181C11C5C5326439EB1951BC12AE69A', X'C181C11C5C5326439EB1951BC12AE69A', 26, NULL, NULL, 2, NULL, 0, 'Pipe on a black ear, a white tail, a white eyelid with eye lashes, and a nose and mouth.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'BA26831293399440AE58CC3A0FDAFF8C', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 0, NULL, NULL, 2, NULL, 0, 'Cut black olives in half, discarding the two halves with the big hole in the middle.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'4C3668A9866FB249AEA72A5BB6F15FF4', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 1, NULL, NULL, 2, NULL, 0, 'Cut the orange pepper into one beak and two feet for the chick pizza.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'69A2EB03FBA38746A5F57898210C0524', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 2, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 degrees F.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B1FF460A7D3E3C4EA77FE266B0509EBA', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 3, NULL, NULL, 2, NULL, 0, 'Line a baking pan with non-stick tin foil or parchment paper.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'753C2CD5485214498759EA96E9E41697', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 4, NULL, NULL, 2, NULL, 0, 'Unroll pizza dough onto the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D17D66149534F844888CCAA6B2683564', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 5, NULL, NULL, 2, NULL, 0, 'Cut out two 1/2 inch strips from one long edge of the dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DE5551DA5E990F44B29D77086F3F5B1C', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 6, NULL, NULL, 2, NULL, 0, 'Then cut a large egg shape from the dough, using a parchment paper template if needed.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E3DDC92766F6574291AAE0B24DDFAF5C', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 7, NULL, NULL, 2, NULL, 0, 'Separate one egg and brush some of the egg white over the egg shaped pizza dough.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'13D766171714874484CBDE93920080DA', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 8, NULL, NULL, 2, NULL, 0, 'Discard remaining egg white.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3A6DA07E0512CE49BD3D7FA8BFBF6EE8', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 9, NULL, NULL, 2, NULL, 0, 'Line the edge of the egg shaped dough with the two strips of dough, cutting off the excess.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'36CBA78FF679304C8976AC0AF3CF6E3E', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 10, NULL, NULL, 2, NULL, 0, 'Bake for 8-10 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'72E09DEEEF65DD42B098D03403586399', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 11, NULL, NULL, 2, NULL, 0, 'Meanwhile, combine the remaining 7 eggs and 1 egg yolk with the half-and-half, salt, and pepper, if using.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'6FF0E47EEBF64F4C9B7E43B62191D334', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 12, NULL, NULL, 2, NULL, 0, 'Put butter in a 10 or 12 inch non-stick skillet and set over medium high heat.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'0C641FA467890540B067A3E2F5722FFA', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 13, NULL, NULL, 2, NULL, 0, 'As the butter begins to melt, swirl it around the bottom of the pan.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'CBA381E5E058E544BA8C625E46D0D6C9', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 14, NULL, NULL, 2, NULL, 0, 'Once the butter has completely melted, add the eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7B971BFD871ED54491CFDCDDE800699A', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 15, NULL, NULL, 2, NULL, 0, 'Use a rubber spatula to continuously scrape the bottom and sides of the pan until the eggs begin to clump together.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'1699C82612FC0C4080533BDCE609EF3F', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 16, NULL, NULL, 2, NULL, 0, 'Once the eggs are cooked about half way, turn the heat down to low and continue to fold the eggs until clumped and slightly wet.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'37963372272AE44DBA0DEF92FB40E0A5', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 17, NULL, NULL, 2, NULL, 0, 'Remove from heat. Do not overcook the eggs.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F270395918530A40B149C5FFC6B92355', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 18, NULL, NULL, 2, NULL, 0, 'Take pizza crust from oven and top with pizza sauce, ham, and shredded cheese.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F02B8C5DD04A9C49AAF3E3793CE8844B', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 19, NULL, NULL, 2, NULL, 0, 'Pop back in the oven for 3-4 minutes until the cheese melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'E7D3C70716C97946B415DF65378B00F6', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 20, NULL, NULL, 2, NULL, 0, 'Break up the scrambled eggs into small pieces.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'25C21EF3EF596240B138DFD2BF5C8B08', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 21, NULL, NULL, 2, NULL, 0, 'Spread the eggs over the pizza and pop in the oven for 2-3 minutes just until heated through.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F61044C4789E2344A3F02D691431ED54', X'4AD2A25B2ACF8D4FA3075B137164FA04', X'4AD2A25B2ACF8D4FA3075B137164FA04', 22, NULL, NULL, 2, NULL, 0, 'Move the pizza to a cutting board or serving platter, add the olive eyes, the orange pepper beak and feet and serve.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'7AC25EE92D7D8E46BB3D5358A1325297', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 0, NULL, NULL, 2, NULL, 0, 'Pour bright pink candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'2CA534E87D9DC245A250A2C6834ECE34', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 1, NULL, NULL, 2, NULL, 0, 'Heat for 20 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B6C68946BA27C042AE5F7A11C6D2841F', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 2, NULL, NULL, 2, NULL, 0, 'Use a food use only paint brush to paint pink polka dots on the inside of 6 cups in a muffin tin.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'3FCB4B2A824B294E9912602E367BAF39', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 3, NULL, NULL, 2, NULL, 0, 'Pour bright white candy melts into a microwave safe bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'ED9CC1666670744A92C55093CE804DA5', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 4, NULL, NULL, 2, NULL, 0, 'Heat for 30 second increments, stirring after each, until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'A23539703762EB4799FD3C7EF285B6EB', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 5, NULL, NULL, 2, NULL, 0, 'Stir in the remaining pink candy melts.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AA68C458FC5AC34A9D76A562D435B0C9', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 6, NULL, NULL, 2, NULL, 0, 'Spoon about 2 tablespoons of the light pink candy into one muffin cup. Use a spoon to push the candy coating up and around the sides covering the pink dots completely.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'DCCFF835B3EB824E8D3233995BBE54D8', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 7, NULL, NULL, 2, NULL, 0, 'Repeat, covering all 6 polka dotted cups.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'B871681AF281FF44BBDDA70E380C7779', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 8, NULL, NULL, 2, NULL, 0, 'Pop in the freezer for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'AC33971CB62A5245AA0F02A7E1D27B78', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 9, NULL, NULL, 2, NULL, 0, 'Remove and gently tug on the candy cups, removing them from the muffin tin.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'29BA9C2A439D6C4ABC71D014DDCA0ADD', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 10, NULL, NULL, 2, NULL, 0, 'Allow the candy bowls to sit at room temperature for 10 minutes.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'F9C1921328CC8943A7626DAF92BE0D1D', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 11, NULL, NULL, 2, NULL, 0, 'Pour the pink rock candy into a shallow bowl.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'55405D673AE70849B227F44092520FF2', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 12, NULL, NULL, 2, NULL, 0, 'Reheat the remaining pink candy coating for 10-20 seconds until melted.');
INSERT INTO `VWR INTERNAL Hungryhappenings2 howToStep` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, item) VALUES (X'D9A26164362C6A40B3B7E68F3086118D', X'BF2C24FAF703F443AE95EB89EB08CDE1', X'BF2C24FAF703F443AE95EB89EB08CDE1', 13, NULL, NULL, 2, NULL, 0, 'Dip the top edge of each candy bowl in the melted candy then immediately dip it in the pink rock candy, pressing the rocks along the edge of the candy bowl.');
